INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 13, 1000, "2030-04-01 14:00:00", "2020-02-15 15:00:00", 190801, 1000001, "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 13, 1000, "2030-04-01 14:00:00", "2020-02-15 15:00:00", 190801, 1000002, "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 13, 1001, "2030-04-01 14:00:00", "2020-02-15 15:00:00", 190801, 1001001, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 12, 1002, "2030-04-01 14:00:00", "2020-02-15 15:00:00", 190801, 1002001, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 11, 1003, "2030-04-01 14:00:00", "2020-02-15 15:00:00", 190801, 1003001, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 3, 1004, "2030-04-01 14:00:00", "2020-02-15 15:00:00", 190801, 1004001, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 7, 1005, "2030-04-01 14:00:00", "2020-02-15 15:00:00", 190801, 1005001, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 2, 1006, "2030-04-01 14:00:00", "2020-05-31 12:00:00", 190801, 1006001, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 4, 1007, "2030-04-01 14:00:00", "2020-08-15 15:00:00", 190801, 1007001, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 6, 1008, "2030-04-01 14:00:00", "2021-08-31 12:00:00", 190801, 1008001, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 13, 1009, "2030-04-01 14:00:00", "2021-12-18 15:00:00", 190801, 1009001, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 4, 1010, "2030-04-01 14:00:00", "2022-02-15 15:00:00", 190801, 1010001, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 6, 1011, "2030-04-01 14:00:00", "2022-07-18 15:00:00", 190801, 1011001, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 6, 1012, "2030-04-01 14:00:00", "2023-09-18 15:00:00", 190801, 1012001, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (0, 6, 1013, "2030-04-01 14:00:00", "2024-04-18 15:00:00", 190801, 1013001, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪");
