INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (1003, "2020-02-15 15:00:00", 1003001, 11, "2030-04-01 14:00:00", 0, 190801, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
