INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (100, 0.0, 1, 0.0, 100, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 10.85, 129901, 10142, 7, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (101, 0.0, 1, 0.0, 101, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 11.15, 129901, 10142, 2, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (102, 0.0, 7, 0.0, 102, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 6.4, 129901, 10142, 5, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (103, 0.0, 3, 0.0, 103, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 0.0, 129901, 10142, 3, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (104, 0.0, 2, 0.0, 104, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 2.43, 129901, 10142, 8, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (105, 0.0, 4, 0.0, 105, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 9.08, 129901, 10142, 3, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (106, 0.0, 6, 0.0, 106, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 0.0, 129901, 10142, 6, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (107, 0.0, 3, 0.0, 107, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 10.6, 129901, 10142, 8, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (108, 0.0, 6, 0.0, 108, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 7.54, 129901, 10142, 4, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (109, 0.0, 2, 0.0, 109, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 11.5, 129901, 10142, 7, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (110, 0.0, 2, 0.0, 110, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 8.83, 129901, 10142, 1, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (111, 0.0, 1, 0.0, 111, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 6.68, 129901, 10142, 2, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (112, 0.0, 6, 0.0, 112, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 8.8, 129901, 10142, 2, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (113, 0.0, 2, 0.0, 113, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 8.9, 129901, 10142, 1, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (114, 0.0, 2, 0.0, 114, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "エリコ", 3.7, 129901, 10142, 5, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (200, 0.0, 1, 0.0, 200, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 7.6, 129801, 10142, 2, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (201, 0.0, 2, 0.0, 201, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 2.25, 129801, 10142, 1, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (202, 0.0, 2, 0.0, 202, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 3.28, 129801, 10142, 1, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (203, 0.0, 4, 0.0, 203, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 2.98, 129801, 10142, 1, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (204, 0.0, 4, 0.0, 204, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 3.0, 129801, 10142, 1, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (205, 0.0, 1, 0.0, 205, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 10.3, 129801, 10142, 2, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (206, 0.0, 6, 0.0, 206, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 2.93, 129801, 10142, 1, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (207, 0.0, 3, 0.0, 207, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 9.3, 129801, 10142, 1, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (208, 0.0, 4, 0.0, 208, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 6.9, 129801, 10142, 1, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (209, 0.0, 2, 0.0, 209, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 0.0, 129801, 10142, 2, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (210, 0.0, 1, 0.0, 210, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 5.95, 129801, 10142, 2, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (211, 0.0, 2, 0.0, 211, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 6.73, 129801, 10142, 1, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (212, 0.0, 2, 0.0, 212, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 0.0, 129801, 10142, 2, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (213, 0.0, 1, 0.0, 213, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 6.65, 129801, 10142, 2, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ");
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (214, 0.0, 6, 0.0, 214, "2024/03/31 12:00:00", "2030/12/17 14:59:59", 24, "マコト", 3.05, 129801, 10142, 1, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！");
