INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (100, "2024/03/31 12:00:00", "エリコ", 0.0, 7, 10.85, 1, 0.0, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901, 100, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (101, "2024/03/31 12:00:00", "エリコ", 0.0, 2, 11.15, 1, 0.0, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901, 101, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (102, "2024/03/31 12:00:00", "エリコ", 0.0, 5, 6.4, 7, 0.0, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901, 102, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (103, "2024/03/31 12:00:00", "エリコ", 0.0, 3, 0.0, 3, 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901, 103, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (104, "2024/03/31 12:00:00", "エリコ", 0.0, 8, 2.43, 2, 0.0, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901, 104, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (105, "2024/03/31 12:00:00", "エリコ", 0.0, 3, 9.08, 4, 0.0, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901, 105, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (106, "2024/03/31 12:00:00", "エリコ", 0.0, 6, 0.0, 6, 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901, 106, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (107, "2024/03/31 12:00:00", "エリコ", 0.0, 8, 10.6, 3, 0.0, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901, 107, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (108, "2024/03/31 12:00:00", "エリコ", 0.0, 4, 7.54, 6, 0.0, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901, 108, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (109, "2024/03/31 12:00:00", "エリコ", 0.0, 7, 11.5, 2, 0.0, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901, 109, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (110, "2024/03/31 12:00:00", "エリコ", 0.0, 1, 8.83, 2, 0.0, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901, 110, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (111, "2024/03/31 12:00:00", "エリコ", 0.0, 2, 6.68, 1, 0.0, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901, 111, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (112, "2024/03/31 12:00:00", "エリコ", 0.0, 2, 8.8, 6, 0.0, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901, 112, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (113, "2024/03/31 12:00:00", "エリコ", 0.0, 1, 8.9, 2, 0.0, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901, 113, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (114, "2024/03/31 12:00:00", "エリコ", 0.0, 5, 3.7, 2, 0.0, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901, 114, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (200, "2024/03/31 12:00:00", "マコト", 0.0, 2, 7.6, 1, 0.0, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801, 200, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (201, "2024/03/31 12:00:00", "マコト", 0.0, 1, 2.25, 2, 0.0, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801, 201, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (202, "2024/03/31 12:00:00", "マコト", 0.0, 1, 3.28, 2, 0.0, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801, 202, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (203, "2024/03/31 12:00:00", "マコト", 0.0, 1, 2.98, 4, 0.0, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801, 203, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (204, "2024/03/31 12:00:00", "マコト", 0.0, 1, 3.0, 4, 0.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801, 204, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (205, "2024/03/31 12:00:00", "マコト", 0.0, 2, 10.3, 1, 0.0, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801, 205, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (206, "2024/03/31 12:00:00", "マコト", 0.0, 1, 2.93, 6, 0.0, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801, 206, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (207, "2024/03/31 12:00:00", "マコト", 0.0, 1, 9.3, 3, 0.0, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801, 207, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (208, "2024/03/31 12:00:00", "マコト", 0.0, 1, 6.9, 4, 0.0, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801, 208, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (209, "2024/03/31 12:00:00", "マコト", 0.0, 2, 0.0, 2, 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801, 209, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (210, "2024/03/31 12:00:00", "マコト", 0.0, 2, 5.95, 1, 0.0, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801, 210, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (211, "2024/03/31 12:00:00", "マコト", 0.0, 1, 6.73, 2, 0.0, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801, 211, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (212, "2024/03/31 12:00:00", "マコト", 0.0, 2, 0.0, 2, 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801, 212, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (213, "2024/03/31 12:00:00", "マコト", 0.0, 2, 6.65, 1, 0.0, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801, 213, "2030/12/17 14:59:59", 24, 10142);
INSERT INTO `v1_58e861055177dc9dde06f69061ec1a04e328d05671798dc05a49992a61c6d6c8` VALUES (214, "2024/03/31 12:00:00", "マコト", 0.0, 1, 3.05, 6, 0.0, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801, 214, "2030/12/17 14:59:59", 24, 10142);
