INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (2, 1000004, -416.5, 63, "2030/01/01 14:59:59", 0.0, 0.0, 190803, "カリン", 2, 4, "2025/01/31 12:00:00", 1, "報酬がもらえますよ！\nチェックしてみてください");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (2, 1000005, -363.0, 64, "2030/01/01 14:59:59", 0.0, 2.12, 190803, "カリン", 1, 5, "2025/01/31 12:00:00", 1, "ギルド管理協会です！\nイベント報酬券をお持ちでしたら、様々なアイテムと交換できますよ");
