INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (2, 1000007, -363.0, 64, "2030/01/01 14:59:59", 0.0, 2.33, 190803, "カリン", 1, 7, "2025/01/31 12:00:00", 1, "いつもありがとうございます！\n報酬交換ですか？\nでは、イベント報酬券とお引き換えいたしますね");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (3, 1020101, -285.0, 61, "2030/01/01 14:59:59", -6.0, 5.18, 133701, "ラビリスタ", 7, 101, "2025/01/31 12:00:00", 10201, "今度の敵は、アタシにとっても\n放ってはおけない存在だ\n少年、アタシも全力で行くからさ\n一緒に守ろう、この世界を");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020102, -285.0, 61, "2030/01/01 14:59:59", -2.0, 8.77, 133901, "クリスティーナ", 2, 201, "2025/01/31 12:00:00", 10201, "退屈の吹き飛ぶような\n戦いの気配がするなぁ坊や\nさっさとワタシをそこへ連れてゆけ\n世界も守ってやるさ、ついでにな");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (6, 1020103, -285.0, 61, "2030/01/01 14:59:59", 0.0, 15.5, 133801, "ネネカ", 1, 301, "2025/01/31 12:00:00", 10201, "おや、私の力が必要……と？\nグレゴールが手伝うそうですよ\n私は戦わないのか、という顔ですね\n……考えておきます");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (2, 1020201, -285.0, 61, "2030/01/01 14:59:59", 0.0, 9.061, 134301, "ランファ", 5, 101, "2025/02/28 12:00:00", 10202, "わたしだけの歌…\n一緒に、探してくれる…？\nあなたがいれば…\nもっとがんばれるから…");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020202, -285.0, 61, "2030/01/01 14:59:59", 0.0, 7.699, 134401, "チカ", 2, 201, "2025/02/28 12:00:00", 10202, "同じ唱喚士として、仲間として\n私にできることをします\n…これも、あなたが\n背中を押してくれたおかげですよ");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020301, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.595, 134601, "アオイ", 2, 101, "2025/03/31 12:00:00", 10203, "世界の平和が私とクローチェさんに\nかかってるんですよね？\nぼっちには過大すぎる期待ですけど\nふ、二人でなら背負ってみせます！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020302, -285.0, 61, "2030/01/01 14:59:59", 0.0, 7.758, 134501, "クローチェ", 2, 201, "2025/03/31 12:00:00", 10203, "新しく手に入れたこの翼で…\nナイトさんがどこにいたって\nわたし、駆けつけちゃいますよっ！\nクローチェ・エアリアル発進ですっ");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (2, 1020401, -285.0, 61, "2030/01/01 14:59:59", 0.0, 7.0, 132302, "ルイズマリー", 6, 101, "2025/04/30 12:00:00", 10204, "今日はダーリンとの初デート♪\n二人でショッピングして、その後は\n…って私、指名手配されてる！？\nちょ、た…助けてダーリン～！！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020402, -285.0, 61, "2030/01/01 14:59:59", 0.0, 7.904, 132402, "イロハ", 2, 201, "2025/04/30 12:00:00", 10204, "ルイちゃんのおばあちゃん優しくて\nいつもにこにこで、イロハ大好き♪\nでも一番好きなのは、角がすっごく\n大きくてかっこいいとこなんだ～♪");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (4, 1020403, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.487, 132502, "ソノ", 2, 301, "2025/04/30 12:00:00", 10204, "ノゾミちゃんのおかげで売上が安定\nしてきたのに、臨時休業、痛すぎる\n…ま、ルイちゃんのために\n一肌脱ぐか。行くよイロハ～");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020502, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.763, 134901, "カノン", 2, 101, "2025/05/31 12:00:00", 10205, "ヲ～ッホッホ♪\nあたくし様と同じお空気を吸える\nお幸運を噛みしめなさいですわ～♪\nヲ～ッホッホッホ～♪");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020503, -285.0, 61, "2030/01/01 14:59:59", 0.0, 6.417, 134801, "ツムギ", 3, 201, "2025/05/31 12:00:00", 10205, "いきますよ騎士さん！\nカノンさんと力を合わせて…力を…\n…本当にあの人なんなんです？\nこれまでになく不安なんですけど！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (3, 1020601, -285.0, 61, "2030/01/01 14:59:59", 0.0, 10.335, 135001, "エリス", 7, 101, "2025/06/30 12:00:00", 10206, "『願いが叶う海岸』か…怪しい\nだというのに、阿賀斗紫布菜たちは\n呑気に戯れている…見ていられない\n騎士クンは、わたしが守ってみせる");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (2, 1020602, -285.0, 61, "2030/01/01 14:59:59", 0.0, 6.281, 135101, "シェフィ", 4, 201, "2025/06/30 12:00:00", 10206, "せっかくの夏休みなんだし\nたまにはビーチでゆっくりしましょ\n…え、もしかしてこの夏もあんまり\n休めなかったりする感じなの？");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020603, -285.0, 61, "2030/01/01 14:59:59", 0.0, 12.244, 135201, "プレシア", 2, 301, "2025/06/30 12:00:00", 10206, "いっぱい。たべて\nおおきく。なると\nせーぎ。まもれる。はず\nお兄。いっしょに。スイカ。たべよ");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (2, 1020701, -285.0, 61, "2030/01/01 14:59:59", 0.0, 6.339, 135301, "クリア", 8, 101, "2025/07/31 12:00:00", 10207, "ひと夏の冒険のはじまりです！\nアネモネさんも楽しんでくれたら\n…できれば、もう少し\n仲良くなれたらうれしいな…");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (7, 1020702, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.184, 135401, "アネモネ", 9, 201, "2025/07/31 12:00:00", 10207, "─幻の花…\n本当にあるのか謎─ですけど\n探すくらいは、\n付き合ってもいいです");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (9, 1020801, -285.0, 61, "2030/01/01 14:59:59", 0.0, 3.906, 129101, "アイラ", 2, 101, "2025/08/31 12:00:00", 10208, "あははっ！　笑う門には福来る！\n笑ってればきっと、複製世界にも\n未来はやってくるって信じてる！\nそうだよね、シナツちゃん！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020802, -285.0, 61, "2030/01/01 14:59:59", 0.0, 14.465, 129201, "シナツ", 2, 201, "2025/08/31 12:00:00", 10208, "繰り返されるリセットの先の未来\n何度願っても手に入らなかった未来\n…でも、私は諦めない。絶対にだ\nキミも同じだろ…アイラ？");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (4, 1020803, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.662, 100101, "ヒヨリ", 3, 301, "2025/08/31 12:00:00", 10208, "アイラの世界のみんなも、\n一生懸命生きてるんだ…！\n…なのに、世界の危機だなんて！\nあたし、絶対、絶対力になるよ！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020804, -285.0, 61, "2030/01/01 14:59:59", 0.0, 10.915, 100301, "レイ", 3, 401, "2025/08/31 12:00:00", 10208, "シナツ…もう一人の私\n彼女が大切にしている世界を\n絶対に終わらせはしない…！\n彼女と笑い合える未来のために！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1020901, -285.0, 61, "2030/01/01 14:59:59", 0.0, 6.042, 135802, "ユキノ", 7, 101, "2025/09/30 12:00:00", 10209, "あ、ど、ども…\nあ、あんま見ねぇでくれっと\nその、恥ずかしくて…\n体が熱くなって、溶けちまう…");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (4, 1020902, -285.0, 61, "2030/01/01 14:59:59", 0.0, 9.402, 135903, "サツキ", 1, 201, "2025/09/30 12:00:00", 10209, "なんで俺が幽霊に…意味不明だ\nこれじゃ死んでも死にきれねェ\n悪ィがこれも何かの縁だ、\nよろしく頼むぜ騎士公");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (8, 1020903, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.16, 136902, "ルルィ", 1, 301, "2025/09/30 12:00:00", 10209, "う、うじゅる…\nサツキ姉しゃまを助けるためにも\nまずは、腹ごしらえでしゅね…！\nタコ焼き、召し上がってくだしゃい");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (3, 1021001, -285.0, 61, "2030/01/01 14:59:59", 0.0, 5.0, 137401, "カスミ", 2, 101, "2025/10/31 12:00:00", 10210, "ひさしぶりのジオ・ゲヘナ\n歓迎パーティーで起きた殺人事件！\nランドソルの名探偵の名にかけて\n解き明かしてみせようじゃないか！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (4, 1021002, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.12, 136001, "アラクネ", 1, 201, "2025/10/31 12:00:00", 10210, "…わたしはどこの誰なのでしょう\nどこから来てどこへ行くのでしょう\nこの事件の真相が分かれば、\n何かが掴めるような気がいたします");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (4, 1021003, -285.0, 61, "2030/01/01 14:59:59", 0.0, 6.386, 136202, "ナーナ", 2, 301, "2025/10/31 12:00:00", 10210, "ナーナ、すいりとかろんぱとか、\nむずかしくてわかんないけど…\nお友だちとドーナツのためならっ\nせーいっぱいがんばるよっ！");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (3, 1021101, -285.0, 61, "2030/01/01 14:59:59", 0.0, 7.438, 136402, "ユイ", 4, 101, "2025/11/30 12:00:00", 10211, "騎士クンに想いを伝えたい…だから\nミソラちゃん、わたしがんばるね！\n…あれ？　わたし、なんでこんなに\n必死になってるんだろう…？");
INSERT INTO `v1_59f86ba18e651a773180a0bf7f38a7caf37cc1cbda0efbf5714a2a31557da82c` VALUES (1, 1021102, -285.0, 61, "2030/01/01 14:59:59", 0.0, 8.35, 136302, "ミソラ", 5, 201, "2025/11/30 12:00:00", 10211, "騎士さんとのデートはわたしに\nお任せです★　なのでユイさんは\n安心して計画に専念して…たくさん\nドキドキしちゃってくださいね");
