INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (1800, 1, "再生ノ腕輪", 15005, 10, 1, 0, 15005, "迷宮内で見つかった腕輪。\n装備者に再生の力を与える。", "継続ＨＰ回復（毎秒【守備】数×500回復）", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "流烈の耳飾り", 21001, 10, 2, 0, 21001, "迷宮内部で発見された耳飾りの形\nをした遺物。流水の激しさのよう\nに、活力加速を増幅させる。", "ＴＰ上昇＋10", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "星涙の輝盾", 21002, 10, 2, 0, 21002, "迷宮内部で発見された円盾の形を\nした遺物。装備者に星の癒しを与\nえ攻撃をエネルギーに変換する。", "ダメージを受けるたびＴＰ100回復（最大２回）", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "蒼珠の腕輪", 21003, 10, 2, 0, 21003, "迷宮内部で発⾒された腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。", "バトル開始時ＴＰ200回復", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "星明りの儀杖", 21004, 10, 2, 0, 21004, "迷宮内部で発⾒された杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。", "ＴＰ消費軽減＋4", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "疾風の翼靴", 21005, 10, 2, 0, 21005, "迷宮内部で発見された、翼の生え\nた靴形の遺物。装備した者に疾風\nの力を施し、行動を速める。", "重複可能の行動速度＋20％", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "闇至宝アノマリーラ", 22001, 10, 2, 0, 22001, "迷宮内部で発⾒された、⾸飾りの\n遺物。宝⽯の輝きで敵を虜にし、\n怪異をもたらすという。", "状態異常命中＋40", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "邪眼杖ドゥーム", 22002, 10, 2, 0, 22002, "迷宮内部で発見された、杖の形を\nした遺物。杖先のコアから流れる\n邪気が敵に呪いを付与する。", "敵全体に呪い付与（毎秒【弱体】数×1000ダメージ）", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "影刃剣プラール", 22003, 10, 2, 0, 22003, "迷宮内部で発見された、短剣の形\nをした遺物。刃に仕込まれた毒が\n気化し、接敵と共に毒を与える。", "敵全体に毒付与（毎秒【弱体】数×1000ダメージ）", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "薔薇飾帽イレース", 22004, 10, 2, 0, 22004, "迷宮内部で発見された、帽子の形\nをした遺物。絡みつく茨の様に、\n敵の攻撃力を阻害する。", "敵の物理/魔法攻撃力－20％", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "斬赤刃ディバイド", 22005, 10, 2, 0, 22005, "迷宮内部で発⾒された、⼑の遺物\n血で⾚く染まった⼑⾝で、敵の装\n甲をたやすく断ち割る。", "敵の物理/魔法防御力－20％", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "フェイタリティ", 23001, 10, 2, 0, 23001, "迷宮内部で発見された、槍の遺物\n特殊な形の穂先が、敵により深い\n傷を刻み込む。", "敵の物理/魔法被クリダメ＋20％", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "クレナイノタチ", 23002, 10, 2, 0, 23002, "迷宮内部で発見された、刀の形を\nした遺物。秋の紅を映す美しき刃\nは致命の攻撃をさらに強化する。", "物理/魔法クリダメ＋20％", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "ラプチャーガントレット", 23003, 10, 2, 0, 23003, "迷宮内部で発⾒された籠⼿の遺\n物。装備者は研ぎ澄まされた強\n力な一撃を放つことができる。", "通常攻撃強化＋60", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "スカイストライク", 23004, 10, 2, 0, 23004, "迷宮内部で発⾒された、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。", "物理/魔法クリティカル＋20％", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "インヘイルアックス", 23005, 10, 2, 0, 23005, "迷宮内部で発⾒された斧の遺物。\n繰り出される⼀撃は、掠めるだけ\nで獲物の魂を吸い取る。", "ＨＰ吸収＋20", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "進化のイヤリング", 24001, 10, 2, 0, 24001, "迷宮内部で発見された、耳飾りの\n形をした遺物。魔石に込められた\n魔力が強化の力を増幅させる。", "バフ強化＋60", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "紅反鋼のアーマー", 24002, 10, 2, 0, 24002, "迷宮内部で発⾒された、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。", "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "聖減術のテラドレス", 24003, 10, 2, 0, 24003, "迷宮内部で発⾒された、ドレスの\n遺物。精霊の加護が、防魔結界か\nら力を集めてくれる。", "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×10）", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "炎王のグレートソード", 24004, 10, 2, 0, 24004, "迷宮内部で発見された、大剣の形\nをした遺物。湧き出る炎の力が、\n攻撃の能力を大きく高める。", "物理/魔法攻撃力＋40％", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "桜堅牢のコート", 24005, 10, 2, 0, 24005, "迷宮内部で発⾒された鎧の遺物。\n千年咲く桜の如く、盤石な堅牢さ\nを装備者に与える。", "物理/魔法防御力＋20％", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "創世緑杖", 25001, 10, 2, 0, 25001, "迷宮内部で発⾒された杖の遺物。\n装備者に⼤樹の恵みを施し、治癒\n術の才覚を授けてくれる。", "回復強化＋20", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "空命護珠", 25002, 10, 2, 0, 25002, "迷宮内部で発見された、指輪の形\nをした遺物。装備者を天空の護り\nで包み込み生命力を増幅させる。", "ＨＰ＋20％", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "鋼装堅鎧", 25003, 10, 2, 0, 25003, "迷宮内部で発見された、鎧の形を\nした遺物。高度な技術で精錬され\nた鋼は、物理攻撃から身を護る。", "20秒ごとに物理無効バリア展開（【守備】数×1000無効）", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "魔護霊装", 25004, 10, 2, 0, 25004, "迷宮内部で発⾒された、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。", "20秒ごとに魔法無効バリア展開（【守備】数×1000無効）", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (2800, 2, "緑護ノ盾輪", 25005, 10, 2, 0, 25005, "迷宮内部で発⾒された、腕輪の遺\n物。溢れ出す⾃然の⼒は装備者に\n生命の息吹をもたらす。", "継続ＨＰ回復（毎秒【守備】数×1000回復）", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "エレメントサージオーブ", 30001, 10, 0, 0, 30001, "迷宮中枢部に隠された黎明の秘宝\nデータ保管のため属性の⼒の断⽚\nが封じられており、内なるアスト\nラルを著しく上昇させる。", "属性ボーナス＋100％", 0);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "瞬閃蝶の月飾り", 31001, 10, 3, 0, 31001, "迷宮の奥深くに眠っていた月を象\nる耳飾り。内部から発する魔力は\n装備者に蝶のような瞬麗さを授け\n活力加速を大きく上昇させる。", "ＴＰ上昇＋15", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "海宝華の煌盾", 31002, 10, 3, 0, 31002, "迷宮の奥深くに眠る宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。", "ダメージを受けるたびＴＰ100回復（最大３回）", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "碧貴石の聖輪", 31003, 10, 3, 0, 31003, "迷宮の奥深くに眠る聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。", "バトル開始時ＴＰ300回復", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "永天鏡の王笏", 31004, 10, 3, 0, 31004, "迷宮の奥深くに眠る、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。", "ＴＰ消費軽減＋6", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "熾天使の翔靴", 31005, 10, 3, 0, 31005, "迷宮の奥深くに眠る、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。", "重複可能の行動速度＋30％", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (9999, 3, "風神の護風恩寵", 31006, 10, 5, 0, 31006, "迷宮最奥部に鎮祀されし神業の遺\n物。風を司る神々の恩寵が宿され\nており、装備者は無尽蔵の活力が\n疾風の如き速さで体を駆け巡る。", "ＴＰ上昇＋100", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "幻獣飾テンタシオン", 32001, 10, 3, 0, 32001, "迷宮の奥深くに眠る、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。", "状態異常命中＋60", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "深淵蝕杖マリディクト", 32002, 10, 3, 1, 32002, "迷宮の奥深くに眠る、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。", "敵全体に呪い付与（毎秒【弱体】数×1500ダメージ）", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "滅刻凶刃ヴェノマス", 32003, 10, 3, 0, 32003, "迷宮の奥深くに眠る、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。", "敵全体に毒付与（毎秒【弱体】数×1500ダメージ）", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "燦然御纏アノラ・ベイラ", 32004, 10, 3, 0, 32004, "迷宮の奥深くに眠る、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。", "敵の物理/魔法攻撃力－30％", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "断壁鎖刃フラグメント", 32005, 10, 3, 0, 32005, "迷宮の奥深くに眠る、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。", "敵の物理/魔法防御力－30％", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (9999, 3, "霜影虚装アビスモナーク", 32006, 10, 5, 0, 32006, "迷宮深層部に静眠せし虚影の装衣\n深淵から出でし蝕影は、存在全て\nを霜雪の如く凍てつかせ、抗う敵\nの剣盾両翼を虚壊させる。", "敵の物理/魔法与ダメ－25％\n敵の物理/魔法被ダメ＋25％", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "リーサルハルバード", 33001, 10, 3, 0, 33001, "迷宮の奥深くに眠る、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。", "敵の物理/魔法被クリダメ＋30％", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "オオトリノカタナ", 33002, 10, 3, 0, 33002, "迷宮の奥深くに眠る、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。", "物理/魔法クリダメ＋30％", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "アースデストラクション", 33003, 10, 3, 0, 33003, "迷宮の奥深くに眠る、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。", "通常攻撃強化＋90", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "ジャッジメントアロー", 33004, 10, 3, 0, 33004, "迷宮の奥深くに眠る、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。", "物理/魔法クリティカル＋30％", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "ザ・ハーヴェスト", 33005, 10, 3, 0, 33005, "迷宮の奥深くに眠る、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。", "ＨＰ吸収＋30", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (9999, 3, "セレスティアルエイジ", 33006, 10, 5, 0, 33006, "迷宮最深部に残された久遠の遺物\n神代の大戦より聖騎士に脈々と受\nけ継がれ、装備者へ戦いの記憶と\nともに不滅の力を分け与える。", "通常攻撃強化＋500\nＨＰ吸収＋50", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "極醒のアミュレット", 34001, 10, 3, 0, 34001, "迷宮の奥深くに眠る、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。", "バフ強化＋90", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "断刃拒絶のアルギュロス", 34002, 10, 3, 0, 34002, "迷宮の奥深くに眠る、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。", "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "緋華栄華のグラナート", 34003, 10, 3, 0, 34003, "迷宮の奥深くに眠る、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。", "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "覇光帝龍のグローリア", 34004, 10, 3, 0, 34004, "迷宮の奥深くに眠る、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。", "物理/魔法攻撃力＋60％", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "華舞玲瓏のハルナデシコ", 34005, 10, 3, 0, 34005, "迷宮の奥深くに眠る、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。", "物理/魔法防御力＋30％", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (9999, 3, "天戴冠のゼニスブレード", 34006, 10, 5, 0, 34006, "迷宮深淵部に佇む歴戦無敗の⼤剣\n台座から剣を⼿にした者は天その\nものを冠すかの如き全能感を得て\n千軍を容易く退ける剛⼒を得る。", "バトル開始時、物理/魔法攻撃力アップフィールド展開\n（現在値＋50％）", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "妖精霊杖・癒光", 35001, 10, 3, 0, 35001, "迷宮の奥深くに眠る、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。", "回復強化＋30", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "泉湧命環・豊穣", 35002, 10, 3, 0, 35002, "迷宮の奥深くに眠る、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。", "ＨＰ＋30％", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "白光輝鎧・断壁", 35003, 10, 3, 0, 35003, "迷宮の奥深くに眠る、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。", "20秒ごとに物理無効バリア展開（【守備】数×1500無効）", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "幽玄煌装・絶魔", 35004, 10, 3, 0, 35004, "迷宮の奥深くに眠る、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。", "20秒ごとに魔法無効バリア展開（【守備】数×1500無効）", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3800, 3, "輪廻ノ翡翠珠輪", 35005, 10, 3, 0, 35005, "迷宮の奥深くに眠る、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。", "継続ＨＰ回復（毎秒【守備】数×1500回復）", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (9999, 3, "天命授環・伊邪那", 35006, 10, 5, 0, 35006, "迷宮最下層に供奉された伝承の環\n選ばれし者がひとたび⼿にすれば\n創造神話の如く、千万無量の⽣命\n⼒を齎すと云われる。", "ＨＰ＋100％", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "プリンセスフォース", 40001, 10, 1, 0, 40001, "【美⾷殿】と共に迷宮を冒険する\nための遺物。王家の装備を精巧に\n模しており、味方の⾝体能⼒を⼤\nきく向上させてくれる。", "ＨＰ＋15％\n物理/魔法与ダメ＋15％", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "アストラルブースト", 40002, 10, 1, 0, 40002, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。\n３人のアストラルに呼応して、属\n性の力を増幅させてくれる。", "属性ボーナス＋50％", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "サレンディアエンゲージ", 40003, 10, 1, 0, 40003, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人が\n築いた信頼関係のおかげで、商品\nの価格を値引きしてもらえる。", "ショップの販売価格－10％", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "ナイツオブフェイス", 40004, 10, 1, 0, 40004, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。団長の愛⽤品を\n模し会⼼率と引き換えに、会⼼時\nの威⼒を⾼めてくれる。", "物理/魔法クリダメ＋50％\n物理/魔法クリティカル－25％", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "ラビリンスオーソリティ", 40005, 10, 1, 0, 40005, "【ラビリンス】と共に迷宮を冒険\nするための遺物。迷宮女王はその\n権能でナイトがどこに居ようとも\n即座に参戦してくれる。", "挑戦開始時にラビリスタが加入", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "イルミネイトカルミナ", 40006, 10, 1, 0, 40006, "【カルミナ】と共に迷宮を冒険す\nるための遺物。これを振るう者の\n熱き声援は、ステージに立つ者の\n能力をより一層高めてくれる。", "物理/魔法攻撃力＋15％\nバフ強化＋15", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "ブルートナハト", 40007, 10, 1, 0, 40007, "【悪魔偽王国軍】と共に迷宮を冒\n険するための遺物。紅き両刃から\n禍々しく迸る魔力の前では、いか\nなる敵だろうと戦意を失う。", "敵のＴＰ上昇－15", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "エリザベスガーディアン", 40008, 10, 1, 0, 40008, "【牧場】と共に迷宮を冒険するた\nめの遺物。被ったらたちまちその\n場で最も目立つ存在となり、すべ\nての攻撃を引きつけてしまう。", "バトル開始時に最もＨＰが多いキャラが敵を挑発", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "ノブレスウィスタリア", 40009, 10, 1, 0, 40009, "【メルクリウス財団】と共に迷宮\nを冒険するための遺物。人から決\nして多く受け取らず、生まれ持っ\nた財のみで戦いぬく高潔さの証。", "初期アルファルピ+3000\nアルファルピ獲得量－30%", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "兵法二海一流", 40010, 10, 1, 0, 40010, "【トワイライトキャラバン】と共\nに迷宮を冒険するための遺物。二\n本の刀が見せる煌めきは、悪党ど\nもの気炎を容易く挫く。", "デバフ強化＋20", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "プリンセスエンチャント", 50001, 10, 1, 0, 50001, "【美⾷殿】と共に迷宮を冒険する\nための遺物。冒険の道中で絆を結\nべば結ぶほどに、味⽅の攻撃⼒を\n⾼めてくれる。", "物理/魔法攻撃力アップ（加入キャラ数×4％、最大100％）", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "トゥインクルブレス", 50002, 10, 1, 1, 50002, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。ギ\nルドマスターの諦めない心が、味\n方を一度だけ敗北から守る。", "ボスバトルを除いて一度だけバトル敗北を免除", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "サレンディアノーブル", 50003, 10, 1, 0, 50003, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人の\n気品を漂わせる剣は、築いた富を\n⼒に変転させる効果を持つ。", "通常攻撃強化アップ（アルファルピ所持数×0.01、最大100）", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "レジーナガーラ", 50004, 10, 1, 0, 50004, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。副団長の催す闘\n争の宴は、敵味⽅が受けるダメー\nジをみな悉く増加させる。", "敵の物理/魔法被ダメ＋25％\n味方の物理/魔法被ダメ＋25％", 2);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "マスターオブラビリンス", 50005, 10, 1, 0, 50005, "【ラビリンス】と共に迷宮を冒険\nするための遺物。マスターの⼒を\n冠した剣は、開発者権限の⼀部を\n解放し、遺物を即座に獲得する。", "挑戦開始時にランダムな★1の迷宮遺物を１個獲得", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "ヴォイスフォーユー", 50006, 10, 1, 0, 50006, "【カルミナ】と共に迷宮を冒険す\nるための遺物。ハンドマイクを通\nじて拡がっていく歌声は、味方の\n疲労をふき飛ばす。", "30秒ごとに物理/魔法攻撃力ダウン解除", 4);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "ディアボロスフィアー", 50007, 10, 1, 0, 50007, "【悪魔偽王国軍】と共に迷宮を冒\n険するための遺物。重く振りかざ\nす一撃は、斬り裂いた獲物の生気\nを我が物とする。", "15秒ごとにＴＰ固定量100回復\n敵のＴＰ100ダウン", 1);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "ドングリースピリット", 50008, 10, 1, 0, 50008, "【牧場】と共に迷宮を冒険するた\nめの遺物。持ち主のやる気を呼び\n起こし、ぐうたらさを１回だけ無\n効化できる。あくまで１回だけ。", "行動不能無効１回", 5);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "リヴァイアサンコール", 50009, 10, 1, 0, 50009, "【メルクリウス財団】と共に迷宮\nを冒険するための遺物。富を積め\nば積むほど精神を集中させ、必殺\nの一突きを敵に見舞う。", "物理/魔法クリダメアップ（アルファルピ所持数×0.01%、最大100%）", 3);
INSERT INTO `v1_5a571a5104a53bd9e006794987654e736ed8c9d233a589c9e460e7449f9c98c0` VALUES (3200, 3, "バインディングローズ", 50010, 10, 1, 0, 50010, "【トワイライトキャラバン】と共\nに迷宮を冒険するための遺物。さ\nまざまな者たちの個性を学習し、\n敵を苦しめる術に転化する。", "敵への継続ダメージアップ（加入キャラ数×10％、最大50万ダメージ）", 2);
