INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (44, 2.09, 0, 2, 540, 450, 216400, 0, 0, 0, "プレシア（フォールン）の姿をした特殊な魔物", "プレシア（フォールン）のシャドウ", 216400);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (3, 2.09, 0, 1, 464, 450, 216501, 0, 0, 0, "リリ（フォールン）の姿をした特殊な魔物\n\n・【召喚】目の前の敵１キャラに物理大ダメージ\n　を与える。このスキルのダメージは自分のシャ\n　ドウの数に応じて増加する。さらにユニオン\n　バーストを使用しない、自分のシャドウを２体\n　生成する。\n　生成されたシャドウは自分を飛行状態にする。\n・自分を中心とした範囲内の味方すべての物理攻\n　撃力を特大アップさせる。\n・目の前の敵１キャラに物理大ダメージを与え、\n　物理防御力を小ダウンさせる。", "リリ（フォールン）のシャドウ", 216501);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (0, 2.09, 0, 1, 406, 450, 216601, 0, 0, 0, "クリア（フォールン）の姿をした特殊な魔物\n\n・目の前の敵１キャラに物理中ダメージを与え、\n　自分のＨＰを中回復させる。自分を除く味方の\n　残りＨＰに応じて、このスキルのダメージが\n　アップする。\n・目の前の敵１キャラに物理中ダメージを与え、\n　物理防御力を中ダウンさせる。自分を除く味方\n　の残りＨＰに応じて、このスキルのダメージが\n　アップする。\n・自分以外の最もＨＰの低い味方１キャラに継続\n　ＨＰ回復状態と【堕天使の加護】を付与する。\n　【堕天使の加護】が付与されている間、その\n　キャラへの敵の攻撃をすべて代わりに受け、自\n　分への物理ダメージと魔法ダメージをカットし、\n　自分が敵から受ける行動不能を無効化する。", "クリア（フォールン）のシャドウ", 216601);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (0, 1.9, 0, 1, 400, 270, 216700, 0, 0, 0, "【物理】ダメージカット\nシャドウと共に聖域を守護する、記憶の番人。\n真なる力を欲するものに、相応の試練を与える。\n\n・味方全体に物理魔法ダメージカット効果を付与\n　する。さらに、自身以外のダメージを受けてい\n　る味方に無敵状態を付与し、物理攻撃力と魔法\n　攻撃力をアップさせる。", "シールド・レリック", 216700);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (0, 0.9, 0, 2, 1200, 270, 216800, 0, 0, 0, "【魔法】遠距離攻撃\nシャドウと共に聖域を守護する、記憶の番人。\n真なる力を欲するものに、相応の試練を与える。\n\n・敵の挑発を無視して、最も遠い敵１キャラに\n　５万の魔法固定ダメージを与える。このスキ\n　ルを使用するたび、次のスキルを使用するま\n　での時間が短くなる。", "アタック・レリック", 216800);
