INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (11, 1.3, 0, 1, 410, 270, 250500, 0, 0, 0, "【物理】【遠距離】\n砂地の地中で獲物を待ち、\n一気に大顎で仕留めるムカデの魔物\n\n・後ろから１番目に移動して近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", "サンドワーム", 250500);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (0, 0.5, 0, 2, 120, 100, 250600, 0, 0, 0, "【魔法】【中距離】【防御】\n幼いながらも巧みな変化で\n攻守を必死にこなす豆狸の魔物。\n\n・後ろから３番目攻撃\n・一定時間無敵\n・与えるダメージによるＴＰ回復量80％ダウン", "ワッパダヌキ", 250600);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 5.0, 0, 1, 20, 270, 250700, 0, 0, 0, "【物理】【物理防御】\n頑丈な身体で、攻守を備えた岩の魔物。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", "ゴーレム", 250700);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 5.0, 0, 1, 25, 270, 250800, 0, 0, 0, "【物理】【魔法防御】\n魔力を秘めた岩から生まれた、\n魔法耐性のあるゴーレム。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", "ブルーゴーレム", 250800);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 4.0, 0, 1, 100, 270, 250900, 0, 0, 0, "【物理】【防御】\nあらゆる攻撃を防ぐ盾をもった、命ある石像。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", "動く戦士像", 250900);
