INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 5.5, 0, 1, 10, 270, 251100, 0, 0, 0, "【物理】【防御】\nあらゆる攻撃を甲羅ではじく、\n巨大ガメの魔物。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", "タートルタイラント", 251100);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (15, 4.0, 0, 1, 85, 270, 251200, 0, 0, 0, "【物理】【物理防御】\n身の丈ほどもある、巨大な盾で\n仲間を護る見習いゴブリン兵。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", "ゴブリンガード", 251200);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (15, 4.0, 0, 1, 90, 270, 251300, 0, 0, 0, "【物理】【魔法防御】\n顔にも見える大きな盾で敵を威嚇し\n侵入者を阻む、ジャングルの護り人。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", "ジャングルガード", 251300);
