INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (13, 3.5, 0, 1, 1000, 270, 303100, 0, 0, 0, "【物理】氷穴最奥に引き篭り\n生涯冬眠すると決めた巨大熊。\n長い歳月を夢うつつの中で過ごし\n獲物を狩るその一瞬、覚醒する。\n\n・敵全体に魔法大ダメージを与え、\n　物理攻撃力を下げる。\n\n・前方3キャラに物理小ダメージを\n　与える。さらに、前方1キャラを\n　大きく吹き飛ばし、氷結状態\n　にして移動速度を下げる。", "トウミングマ", 303100);
