INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (13, 1.7, 0, 1, 1000, 270, 303600, 0, 0, 0, "【物理】強靭な脚と鋭利なツメで\n獲物を一瞬で狩る、獰猛な恐竜。\n滅びた大地でマグマを喰らい、\n生存の道を見つけた孤高の存在。\n\n・敵全体に物理中ダメージを与え、\n　自分の行動速度を上げる。\n\n・前方3キャラに物理中ダメージ\n　を与え、行動速度を下げる。", "バーンサウルス", 303600);
