INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 1000, 270, 305400, 0, 0, 0, "【物理】強力な氷の頭突きで\n堅牢な盾も貫き穿つ一角獣。\n氷丘押し寄せる北の果てで、\n冒険者を氷獄へ突き落す。\n\n・敵全体に魔法大ダメージを与え、\n　魔法防御力を下げる。\n　さらに、味方全体のＴＰ上昇率を\n　上げる。\n\n・前方小範囲内に魔法小ダメージを\n　与え、氷結状態にする。さらに、\n　全体に魔法小ダメージを与え、\n　前方3キャラを小さく\n　吹き飛ばす。", "アイスホーン", 305400);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 3.0, 0, 2, 2160, 270, 305500, 0, 0, 0, "【魔法】幾多の伝承に名を残す、記憶の大精霊。\n霧の如く実態を持たず、全て覆い隠してしまう。\n\n・ユニオンバーストは敵全体に\n　魔法大ダメージを与え、\n　さらに敵が呪い状態の場合、\n　ダメージがアップする。\n・敵全体に魔法中ダメージを与える。\n　さらに呪い状態にする。\n・分身体（紫）（赤）（青）のうち、\n　いずれか1体を召喚する。\n　召喚する際は敵全体をノックバックさせる。\n・【物理】分身体（紫）は、自身に物理/魔法\n　吸収バリアを展開する。\n・【物理】分身体（赤）は、敵全体に\n　魔法中ダメージを与える。\n・【物理】分身体（青）は、敵全体に\n　魔法中ダメージ。\n　さらに、呪い状態にする。\n　分身体（青）のＨＰが30％以下になった時、\n　フォギーのＴＰを300回復させる。\n・フォギー自身のＨＰが\n　50％を切ると、さらに攻撃が激しく\n　凶暴になる。", "フォギー", 305500);
