INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 2, 3000, 270, 308400, 0, 0, 0, "【ＭＯＤＥ１】【リトルリリカル】の\nシャドウを召喚。\n無垢な少女達が手招く先は、亡者が蠢く常夜の国。\n\n・前方１キャラに魔法ダメージを与える。", "ファントムバロン", 308404);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 2, 3000, 270, 308400, 0, 0, 0, "【ＭＯＤＥ２】冥府の門が開かれ、\n生者の魂は奈落に墜ちる。既に還る術もなく、\n思い出を標に闇を漂う。\n\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与え、\n　さらに魔法防御力をダウンさせる。\n・一番遠くの敵１キャラに物理中ダメージを\n　与え、さらに一番前にいるキャラの位置まで\n　引き寄せてスタンさせる。\n・前方３キャラに魔法中ダメージを与えて\n　ＴＰを中ダウンさせ、ファントムバロン\n　のＴＰを中回復させる。\n　さらに、物理攻撃をするキャラのうち、\n　前から３キャラのステータスアップ効果を\n　解除し、魔法攻撃をするキャラのうち、\n　前から３キャラに追加の魔法大ダメージを\n　与える。", "ファントムバロン", 308405);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 2, 3000, 270, 308400, 0, 0, 0, "【ＭＯＤＥ３】最愛との別離が刻々と近づく。\n悲しき宿命に抗うべく、\n怨霊は寂滅の慟哭をあげる。\n\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与え、\n　さらに魔法防御力をダウンさせる。\n・最も近い敵１キャラに魔法大ダメージを与える。\n　さらに、前方３キャラにキャラの最大ＨＰに\n　応じた魔法割合ダメージを与え、\n　大きく吹き飛ばす。\n・一番遠くの敵１キャラに物理中ダメージを\n　与え、さらに一番前にいるキャラの位置まで\n　引き寄せてスタンさせる。\n・バトル開始直後に、自身の魔法攻撃力と\n　行動速度をアップさせる。", "ファントムバロン", 308406);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 2, 3000, 270, 308408, 0, 0, 0, "【魔法】塔の魔力に群がる怨魂が姿を成した亡霊。\n邪悪な手が生者を呼び込み、冥府へ引き摺り込む。\n\n・敵全体に魔法大ダメージを与え、\n　さらに魔法防御力をダウンさせる。\n・最も近い敵１キャラに魔法大ダメージを与える。\n　さらに、前方３キャラに魔法中ダメージを与え、\n　大きく吹き飛ばす。\n・一番遠くの敵１キャラに物理中ダメージを\n　与え、さらに一番前にいるキャラの位置まで\n　引き寄せてスタンさせる。", "マッドファントム", 308408);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 2, 3000, 270, 308400, 0, 0, 0, "【魔法】我が子への妄執に囚われた亡親の霊。\n娘と偽りの日々を享受すべく、\n立ち塞がる者を容赦なく屠る。\n\n・ファントムバロンから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与え、さらに\n　魔法防御力を中ダウンさせる。このスキルによ\n　るダメージでは、ＴＰが回復しない。このスキ\n　ルの効果はダメージを無効化しても付与される。\n・最も遠い敵１キャラに物理小ダメージを与え、\n　さらに一番前にいるキャラの位置まで引き寄せ\n　て、スタン状態を付与する。このスキルの状態\n　異常はダメージを無効化しても付与される。\n・最も近い敵１キャラに魔法大ダメージを与え、\n　さらに最も近い敵３キャラに魔法小ダメージを\n　与え、大きくノックバックする。\n・自身のＨＰが50％を切ると、さらに攻撃が激\n　しく凶暴になる。", "ファントムバロン", 308409);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 2.8, 0, 1, 1000, 270, 308500, 0, 0, 0, "【物理】ルナの魔力に魅入られ荒れ狂う水の神獣。\n自らの役目を忘れ、万物を飲み込む狂獣と化す。\n\n・敵全体に魔法大ダメージを与え、畏怖を与える。\n　畏怖状態のキャラは、一定時間攻撃および\n　ＨＰ回復のユニオンバーストの効果が半減する。\n・前方範囲内に魔法中ダメージを与え、\n　ヒットした数に応じて自身の物理防御力を\n　小アップ。さらに、魔法ダメージを与える\n　フィールドを展開する。\n・前方１キャラに魔法大ダメージを与え、\n　自身のＨＰを消費し、敵全体に追加の\n　魔法中ダメージを与える。\n　さらに、自身の物理攻撃力と魔法攻撃力を\n　小アップ。", "水瑚麟", 308500);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 1000, 270, 308600, 0, 0, 0, "", "ドラゴンもどき", 308600);
