INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310000, 0, 0, 0, "【ＭＯＤＥ１】冒険者の力を試す、内なる影を\n召喚。寒風吹き荒ぶ天下の険、自身の脆さが\n試される。\n\n・一番前のキャラに物理ダメージを与える。", "山神様", 310004);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310000, 0, 0, 0, "【ＭＯＤＥ２】益荒男の疾駆に大地が揺れる。\nその様を祝賀するが如く、目出鯛は巌山を舞う。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。\n　先頭のキャラにはさらに追加で物理特大\n　ダメージを与え、大きく吹き飛ばして\n　スタンさせる。\n　山神様がユニオンバーストを発動するたび\n　自身の物理防御力がダウンする。\n　この効果は累積する。\n・魔法攻撃力の最も高い２キャラに、キャラの\n　最大ＨＰに応じた魔法割合ダメージを与え、\n　ＴＰを大きくダウンさせる。\n・後方に物理防御力ダウンフィールドを展開し、\n　さらに敵全体に物理中ダメージを与える。\n　目出鯛が背中を離れている間は、山神様の\n　魔法防御力が特大ダウン。", "山神様", 310005);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310000, 0, 0, 0, "【ＭＯＤＥ３】神と競いし強者への、終の試煉。\n互いの全てを以て、想い人へ駆け抜ける。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。\n　先頭のキャラにはさらに追加で物理特大\n　ダメージを与え、大きく吹き飛ばして\n　スタンさせる。\n　山神様がユニオンバーストを発動するたび\n　自身の物理防御力がダウンする。\n　この効果は累積する。\n・魔法攻撃力の最も高い１キャラに、キャラの\n　最大ＨＰに応じた魔法割合ダメージを与え、\n　ＴＰを大きくダウンさせる。\n・自身のＴＰと行動速度をアップさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる代わりに、自身の物理防御力が\n　ダウンする。", "山神様", 310006);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (14, 2.6, 0, 1, 1000, 270, 310100, 0, 0, 0, "【物理】忘却の秘境に、住まう若き巨竜。\n禁忌に踏み入る冒険者を灼熱の業火で焼き払う。\n\n・ユニオンバーストは敵全体に物理割合ダメージ\n　を与え、さらにＴＰをダウンさせる。\n・バトル開始時に、自身のＴＰを特大ダウン。\n・前方1キャラを中心に一定範囲内のキャラに\n　魔法ダメージを与え、さらに物理防御力を\n　ダウンさせる。\n・敵全体に物理ダメージを与え、さらに火傷\n　状態にする。\n・敵全体に物理ダメージを与え、ノックバック\n　させる、さらに、ステータスアップ効果を\n　打ち消す。\n\n・全ターゲットがブレイクされると、自身のＴＰ\n　を特大ダウン。\n・ラースドラゴン自身の残りＨＰが10％を切る\n　とさらに攻撃が激しく強暴になり、ブレイク\n　されなくなる。\n・バトルがタイムアップすると、敵全体を戦闘\n　不能にする。\n", "ラースドラゴン", 310100);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (14, 2.6, 0, 1, 1000, 270, 310101, 0, 0, 0, "【物理】忘却の秘境に、住まう若き巨竜。\n禁忌に踏み入る冒険者を灼熱の業火で焼き払う。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ラースドラゴンから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体のバリアを解除\n　し、敵全体にキャラの最大ＨＰに応じた物理割\n　合ダメージを与え、さらにＴＰを小ダウンさせ\n　る。\n・最も近い敵を中心にした範囲内の敵すべてに\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、さらに物理防御力を大ダウンさせる。\n　このスキルの効果はダメージを無効化しても付\n　与される。\n・敵全体に物理中ダメージを与え、さらに火傷状\n　態にする。\n　このスキルにより付与された火傷のダメージで\n　は、ＴＰが回復しない。\n・敵全体のバリアを解除し、敵全体に物理中ダ\n　メージを与え、ノックバックさせる、さらに、\n　ステータスアップ効果を打ち消す。\n　このスキルの状態異常と効果はダメージを無効\n　化しても付与される。\n・向かって左側のターゲットのブレイク時、自身\n　の物理防御力と魔法防御力が大ダウン。\n・中央のターゲットのブレイク時、自身の物理防\n　御力と魔法防御力が大ダウン。\n・向かって右側のターゲットのブレイク時、自身\n　の物理防御力と魔法防御力が大ダウン。\n・全ターゲットがブレイクされると、自身のＴＰ\n　を特大ダウン。\n・ラースドラゴン自身の残りＨＰが30％を切る\n　とさらに攻撃が激しく強暴になり、ブレイク\n　されなくなるが、自身の物理防御力と魔法防御\n　力が大ダウンする。\n・最も近い敵１キャラに物理大ダメージを与える。", "ラースドラゴン", 310101);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 1000, 270, 310200, 0, 0, 0, "【物理】海蝕洞の支配者として\n君臨する、サメエイの魔物。凶猛な\n欲望を全て捕食に注ぐ海の大悪党。\n\n・敵全体に物理特大ダメージを与え\n　物理防御力と魔法攻撃力を\n　ダウンさせる。\n\n・前方範囲内の敵すべてに\n　魔法小ダメージを与え、ＴＰと\n　ＴＰ上昇率をダウンさせる。", "シャークヘッドレイ", 310200);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 1000, 270, 310201, 0, 0, 0, "【物理】塔の魔力を吸収した、暴食の巨大鮫鱏。\n音波で感覚を狂わせ、鈍った獲物を喰らい尽くす。\n\n・敵全体に物理特大ダメージを与え、物理防御力\n　と魔法攻撃力をダウンさせる。\n・最もＴＰの高い敵１キャラに魔法中ダメージを\n　与え、麻痺させる。\n　さらに挑発状態にする。\n・挑発を無視して、最もＨＰの高い敵１キャラに\n　毎秒増加する継続ダメージを与える。", "シャークグリード", 310201);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 3000, 270, 310300, 0, 0, 0, "", "アンチビースト", 310300);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 3000, 270, 310300, 0, 0, 0, "【物理】愛の力を求め顕現した、負の魔法生物。\n皆の心の中の愛の力を歪め世界に災いをもたらす。\n\n・ユニオンバーストは、異次元より取り出した\n　アイテムで攻撃を仕掛ける。アイテムによって\n　効果が変化する。\n　ハンマーの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、物理攻撃力を\n　大幅にダウンさせる。\n　シンバルの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、魔法攻撃力を\n　大幅にダウンさせる。\n　ボムの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、\n　呪い状態にする。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・一番前の敵を中心に、範囲内の敵すべてに\n　物理中ダメージを与え、さらにノックバック\n　させる。\n・一定範囲内の敵すべてに物理大ダメージを\n　与える。", "アンチビースト", 310301);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 3000, 270, 310300, 0, 0, 0, "【物理】愛の力を求め顕現した、負の魔法生物。\n皆の心の中の愛の力を歪め世界に災いをもたらす。\n\n・ユニオンバーストは、異次元より取り出した\n　アイテムで攻撃を仕掛ける。アイテムによって\n　効果が変化する。\n　ハンマーの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、物理攻撃力を\n　大幅にダウンさせる。\n　シンバルの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、魔法攻撃力を\n　大幅にダウンさせる。\n　ボムの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、\n　呪い状態にする。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・一番前の敵を中心に、範囲内の敵すべてに\n　物理中ダメージを与え、さらにノックバック\n　させる。\n・一定範囲内の敵すべてに物理大ダメージを\n　与える。", "アンチビースト", 310302);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 3000, 270, 310300, 0, 0, 0, "【物理】愛の力を求め顕現した、負の魔法生物。\n皆の心の中の愛の力を歪め世界に災いをもたらす。\n\n・ユニオンバーストは、異次元より取り出した\n　アイテムで攻撃を仕掛ける。アイテムによって\n　効果が変化する。\n　ハンマーの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、物理攻撃力を\n　大幅にダウンさせる。\n　シンバルの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、魔法攻撃力を\n　大幅にダウンさせる。\n　ボムの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、\n　呪い状態にする。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・一番前の敵を中心に、範囲内の敵すべてに\n　物理中ダメージを与え、さらにノックバック\n　させる。\n・一定範囲内の敵すべてに物理大ダメージを\n　与える。\n・アンチビースト自身のＨＰが50％を切ると、\n　さらに攻撃が激しく凶暴になる。", "アンチビースト", 310303);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 4.2, 0, 1, 3000, 270, 310304, 0, 0, 0, "【ＭＯＤＥ１】闇の力によって染め上げられた\n愛と欲望が形となった負の魔法生物。世界を\n無慈悲にただ歪めはじめる。\n\n・ユニオンバーストは、異次元より取り出した\n　アイテムで攻撃を仕掛ける。アイテムによって\n　効果が変化する。\n　ハンマーの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、物理攻撃力を\n　大幅にダウンさせる。\n　シンバルの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、魔法攻撃力を\n　大幅にダウンさせる。\n　ボムの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、\n　呪い状態にする。\n・一番前の敵を中心に、範囲内の敵すべてに\n　物理中ダメージを与え、さらにノックバック\n　させる。\n・一定範囲内の敵すべてに物理大ダメージを\n　与える。", "アンチビースト", 310304);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 3000, 270, 310300, 0, 0, 0, "【ＭＯＤＥ２】魔法少女のシャドウを召喚。\n歪んだ負の絶望影が、魔法少女たちの愛と\n希望の力を試す。\n\n・シャドウの戦いを静かに見守る。", "アンチビースト", 310305);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.8, 0, 1, 3000, 270, 310300, 0, 0, 0, "【ＭＯＤＥ３】憤怒する負の魔法生物はとうとう\n真の力を解放する。魔法少女の心を打ち砕かん\nと、歪んだ愛の力ですべてを捻じ伏せる。\n\n・ユニオンバーストは、異次元より取り出した\n　アイテムで攻撃を仕掛ける。アイテムによって\n　効果が変化する。\n　ハンマーの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、物理攻撃力を\n　大幅にダウンさせる。\n　シンバルの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、魔法攻撃力を\n　大幅にダウンさせる。\n　ボムの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、\n　呪い状態にする。\n・一番前の敵を中心に、範囲内の敵すべてに\n　物理中ダメージを与え、さらにノックバック\n　させる。\n・一定範囲内の敵すべてに物理大ダメージを\n　与える。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", "アンチビースト", 310306);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 2.0, 0, 1, 3000, 270, 310307, 0, 0, 0, "【物理】塔の魔力に導かれた無邪気な奇術師。\n披露する手品は楽しめたものではなく凶悪な兵器。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・イノセントマジシャンから受けるすべての\n　ダメージで発生する、パーティキャラのＴＰ\n　回復量を中ダウンさせる。\n・ユニオンバーストは、異次元より取り出した\n　アイテムで攻撃を仕掛ける。アイテムによって\n　効果が変化する。\n　ハンマーの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、物理攻撃力を\n　大幅にダウンさせる。\n　シンバルの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、魔法攻撃力を\n　大幅にダウンさせる。\n　ボムの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、\n　スタン状態にする。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・一番前の敵を中心に、範囲内の敵すべてに\n　物理中ダメージを与え、さらにノックバック\n　させる。\n・一定範囲内の敵すべてに物理中ダメージを\n　与える。\n・イノセントマジシャン自身のＨＰが50％を\n　切ると、さらに攻撃が激しく凶暴になる。", "イノセントマジシャン", 310307);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (11, 2.4, 0, 1, 1000, 270, 310400, 0, 0, 0, "【物理】湿林に潜み獲物を付け狙う\nカメレオンの魔物。臆病で狡猾な気\n性だが、怒るとツノを向け猛突進。\n\n・ユニオンバーストは、敵全体に物\n　理大ダメージを与え、物理防御力\n　を大ダウンさせる。\n\n・ランダムな１キャラに\n　物理中ダメージを与える。\n\n・カメレオプスが一定時間背景に擬\n　態し、回避を極大アップ、行動速\n　度を特大アップ、物理防御力が大\n　ダウンする。\n\n・前方３キャラに物理中ダメージを\n　与える。", "カメレオプス", 310400);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (11, 2.4, 0, 1, 1000, 270, 310401, 0, 0, 0, "【物理】湿林に潜み獲物を付け狙う\nカメレオンの魔物。臆病で狡猾な気\n性だが、怒るとツノを向け猛突進。\n\n・ユニオンバーストは、敵全体に物\n　理大ダメージを与え、物理防御力\n　を大ダウンさせる。\n\n・ランダムな１キャラに\n　物理中ダメージを与える。\n\n・カメレオプスが一定時間背景に擬\n　態し、回避を極大アップ、行動速\n　度を特大アップ、物理防御力が大\n　ダウンする。\n\n・前方３キャラに物理中ダメージを\n　与える。", "カメレオプス", 310401);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310500, 0, 0, 0, "", "ラウドラパーン", 310500);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310500, 0, 0, 0, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。", "ラウドラパーン", 310501);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310500, 0, 0, 0, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。", "ラウドラパーン", 310502);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310500, 0, 0, 0, "【物理】ポップな体色をした大ウサギの魔物。\n見た目とは裏腹に迷惑極まりないその歌声は、\n大地を震わせ冒険者の心をも折る。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・ラウドラパーン自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。", "ラウドラパーン", 310503);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310500, 0, 0, 0, "【ＭＯＤＥ１】ニュージェネレーションズの\nシャドウを召喚。森で出会った大ウサギ。\n３人は夢をあきらめない心を貫き通せるか、\n大ウサギは各個の弱さを見せつける。\n\n・一番前のキャラに物理ダメージを与える。", "ラウドラパーン", 310504);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310500, 0, 0, 0, "【ＭＯＤＥ２】森の人気者の歌声に魔物たちが\n大挙する。血沸き肉躍る野生の宴会が今、\n開幕する。\n\n・ブルーゴーレム、スリーピィオウル、\n　ツキツキを倒すと次のＭＯＤＥに移行する。\n\n・ラウドラパーンは、一番前のキャラに\n　物理ダメージを与える。", "ラウドラパーン", 310505);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310500, 0, 0, 0, "【ＭＯＤＥ３】宴も終焉、大兎が最後の力を振り\n絞る。咆哮にも似た壊震の歌声が、３人の夢道を\n阻む。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", "ラウドラパーン", 310506);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 310507, 0, 0, 0, "【物理】世にも奇妙な歌う大ウサギ\nの魔獣。その歌声はとても褒められ\nたものではなく、甚だ迷惑である。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた物理割\n　合ダメージを与え、ノックバック\n　させる。その後、目を回している\n　間、自身の物理防御力と魔法防御\n　力を特大ダウン。\n\n・一番前のキャラを中心として、\n　一定範囲に物理中ダメージを与\n　え、さらに行動速度を特大ダウン\n　とスタンさせる。\n\n・前から３キャラに物理小ダメージ\n　を与え、さらに物理防御力を大ダ\n　ウンさせる。", "バニフォルテ", 310507);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 310600, 1, 3000, 270, 310600, 0, 310600, 0, "", "葛藤のうえきちゃん", 310600);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 310600, 1, 3000, 270, 310600, 0, 310600, 0, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。", "葛藤のうえきちゃん", 310601);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 310600, 1, 3000, 270, 310600, 0, 310600, 0, "【魔法】魔物かはたまた精霊なのか、謎多き巨躯\nのいきもの。如何ともし難いその表情のまま謎の\n花粉を噴霧し、市中を混乱へと誘い落とす。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、物理防御力と魔法防御力をダウンさせ\n　る。\n・前方範囲内の敵に物理ダメージを与え、ノック\n　バックさせる。\n・きゅうこんちゃんを召喚。\n　きゅうこんちゃんは攻撃の対象にならず、一定\n　時間が経つと自爆して前方範囲内のキャラに魔\n　法ダメージを与える。\n・左右の葉っぱは、ブレイクすると一定時間消滅\n　する。\n・向かって左側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。\n・本体のブレイク時、物理防御力と魔法防御力が\n　大ダウン。\n・向かって右側の葉っぱのブレイク時、物理防御\n　力と魔法防御力が小ダウン。", "葛藤のうえきちゃん", 310602);
