INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 2.0, 0, 2, 3000, 270, 313705, 0, 0, 0, "【ＭＯＤＥ３】戦いを止められるのは黒い天使\nのみ。大切なものを守るため、冒険者として踏み\n出すため…その迷いなき翼で巨悪を薙ぎ払え！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ミソラから受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与える。\n・前から１番目、２番目、３番目の敵に魔法小ダ\n　メージを与える。\n・敵全体の魔法防御力を中ダウンさせ、火傷状態\n　を付与する。敵のＨＰが50％未満の場合、混\n　乱状態を付与する。このスキルにより付与され\n　た火傷のダメージでは、ＴＰが回復しない。", "ミソラ", 313707);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.5, 0, 1, 3000, 270, 313800, 0, 0, 0, "常に幾つもの奥の手を隠し持つ、大食の紳士。\n万能人形を駆使し、知略戦略で敵を追い詰める。\n\n・ユニオンバーストは、敵全体にアゾールドの物\n　理攻撃力に応じた物理割合ダメージを与える。\n　さらに、アゾールドの物理防御力と魔法防御力\n　を大ダウン。\n・万能人形を召喚し敵全体をノックバックさせる。\n　万能人形は前から１番目のキャラに物理小ダメー\n　ジを与える。\n　万能人形のＨＰが一定値以下になった時、アゾー\n　ルドの物理防御力と魔法防御力を大ダウンさせ\n　る。\n・前から１番目のキャラに物理小ダメージ。", "アゾールド", 313800);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.5, 0, 1, 3000, 270, 313801, 0, 0, 0, "常に幾つもの奥の手を隠し持つ、大食の紳士。\n万能人形を駆使し、知略戦略で敵を追い詰める。\n\n・ユニオンバーストは、敵全体にアゾールドの物\n　理攻撃力に応じた物理割合ダメージを与える。\n　さらに、アゾールドの物理防御力と魔法防御力\n　を大ダウン。\n・万能人形を召喚し敵全体をノックバックさせる。\n　万能人形は前から１番目のキャラに物理小ダメー\n　ジを与える。\n　万能人形のＨＰが一定値以下になった時、アゾー\n　ルドの物理防御力と魔法防御力を大ダウンさせ\n　る。\n・前から１番目のキャラに物理小ダメージ。", "アゾールド", 313801);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 313900, 1, 3000, 270, 313900, 0, 313900, 0, "一騎当千、万夫不当の実力を揮う寡黙な剣士。\n覚醒することで、龍は更なる高みへと昇る。\n\n・ユニオンバーストは、敵全体にゼーンの物理\n　攻撃力と残りＨＰに応じた物理割合ダメージ\n　を与える。\n　このスキルによるダメージでは、ＴＰ回復量\n　は減少する。\n・敵全体に物理中ダメージを与え、スタンさせ\n　る。\n・ゼーンのＴＰ上昇、行動速度、物理攻撃力を\n　大アップ。\n　このスキルの効果は発動するたびにアップす\n　る。\n　３回目の発動時は、さらにＴＰ1000アップ。\n・前から１番目のキャラに物理中ダメージを与\n　え、小さく吹き飛ばす。", "ゼーン", 313900);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 313900, 1, 3000, 270, 313901, 0, 313900, 0, "", "ゼーン", 313901);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 313900, 1, 3000, 270, 313902, 0, 313900, 0, "【物理】一騎当千、万夫不当の実力を揮う寡黙な\n剣士。覚醒することで、龍は更なる高みへと昇る。\n\n・ユニオンバーストは、飛行状態のキャラを含む\n　敵全体にゼーンの物理攻撃力とキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理攻\n　撃力と魔法攻撃力をバトル終了まで小ダウンさ\n　せる。このスキルによるダメージでは、ＴＰが\n　回復しない。このスキルの効果はダメージを無\n　効化しても付与される。\n・敵全体に物理小ダメージを与え、スタン状態を\n　付与する。このスキルによるダメージでは、\n　ＴＰが回復しない。このスキルの効果はダメー\n　ジを無効化しても付与される。\n・自身のＴＰ上昇、行動速度、物理攻撃力を大\n　アップさせる。このスキルの効果は発動するた\n　びにアップする。３回目の発動時は、ＴＰ上昇\n　をアップさせる代わりに自身のＴＰを特大回復\n　する。\n・最も近い敵１キャラに物理特大ダメージを与え、\n　ノックバックさせる。このスキルによるダメー\n　ジでは、ＴＰ回復量が特大ダウンする。", "ゼーン", 313902);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 0, 1, 3000, 270, 314000, 0, 0, 0, "", "宿題", 314000);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 0, 1, 3000, 270, 314001, 0, 0, 0, "まずは手始めに得意な宿題を終わらせよう。\nここでつまづいてはいられない。夏休みの宿題は\nほかにもまだまだあるぞ！！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", "得意な宿題", 314001);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 0, 1, 3000, 270, 314002, 0, 0, 0, "誰にでもある苦手科目の宿題。ついつい後回しに\nしがちで、溜まってしまうとなかなか大変。\nここで立ち止まるわけにはいかないぞ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", "ちょっと苦手な宿題", 314002);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 0, 1, 3000, 270, 314003, 0, 0, 0, "眺めているだけで眩暈のするいや～な宿題。\nペンを置いて外に飛び出したくなる衝動を耐え、\nコツコツ進めていくことが攻略のカギ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", "すごく苦手な宿題", 314003);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.0, 0, 1, 3000, 270, 314004, 0, 0, 0, "SP MODE1", "夏休み最後まで残りし宿題", 314004);
