INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314500, 0, 0, 0, "【ＭＯＤＥ１】妖精がお菓子で形作ったミヤコた\nちの甘い猛攻と妖精の悪戯が押し寄せる。聖夜に\n育んだ輝く絆でニセモノたちを打ち砕け！\n\n・ミヤコ、マヒル、リノのシャドウを召喚。\n・前から３キャラに魔法中ダメージを与え、物理\n　防御力を特大ダウン。このスキルの効果はダ\n　メージを無効化しても付与される。", "ひとりぼっちの聖夜の妖精", 314504);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 0.0, 0, 1, 3000, 270, 314500, 0, 0, 0, "【ＭＯＤＥ２】妖精が袋から取り出したるは魅惑\nのお菓子。一瞬でも気を許したが最期、甘味な罠\nに堕ちていく。プリンパワーで甘い幻想を打ち砕\nけ！\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・敵全体にキャラの最大ＨＰに応じた物理割合ダ\n　メージを与え、キャラのＨＰが60%を切って\n　いる場合、誘惑状態にする。このスキルの状態\n　異常はダメージを無効化しても付与される。", "ひとりぼっちの聖夜の妖精", 314505);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314500, 0, 0, 0, "【ＭＯＤＥ３】聖夜に独り妖精の哀叫が鳴り響く。\n傍若無人に暴れる妖精をなだめ、プリンの絆で皆\nの幸せのクリスマスと妖精の笑顔を取り戻せ！\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・ひとりぼっちの聖夜の妖精自身の物理/魔法防\n　御力を極大アップ。\n・ひとりぼっちの聖夜の妖精自身のＨＰが30％\n　を切ると、さらに攻撃が激しく凶暴になる。", "ひとりぼっちの聖夜の妖精", 314506);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314600, 0, 0, 0, "", "邪牛神モーモン", 314600);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314600, 0, 0, 0, "【物理】縁起物を取り込み、願いの力を吸収して\n猛り荒ぶる牛の魔物。怒りに身を落とした猛牛が\n周囲の願いを打ち砕く。\n\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・最も魔法攻撃力の高い敵１キャラにキャラの最\n　大ＨＰに応じた物理割合ダメージを与え、ノッ\n　クバックさせると同時にスタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。", "邪牛神モーモン", 314601);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314600, 0, 0, 0, "【物理】縁起物を取り込み、願いの力を吸収して\n猛り荒ぶる牛の魔物。怒りに身を落とした猛牛が\n周囲の願いを打ち砕く。\n\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・最も魔法攻撃力の高い敵１キャラにキャラの最\n　大ＨＰに応じた物理割合ダメージを与え、ノッ\n　クバックさせると同時にスタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。", "邪牛神モーモン", 314602);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314600, 0, 0, 0, "【物理】縁起物を取り込み、願いの力を吸収して\n猛り荒ぶる牛の魔物。怒りに身を落とした猛牛が\n周囲の願いを打ち砕く。\n\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・通常攻撃は、目の前の敵１キャラに邪牛神モー\n　モンの物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n・最も魔法攻撃力の高い敵１キャラにキャラの最\n　大ＨＰに応じた物理割合ダメージを与え、ノッ\n　クバックさせると同時にスタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。\n・残り時間が30秒を切ると、さらに攻撃が激し\n　く凶暴になる。", "邪牛神モーモン", 314603);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314600, 0, 0, 0, "【ＭＯＤＥ１】力への渇望から魔物は子分を率\nい、再び略奪へ踏みだす。迫り来るならず者た\nちを打倒し、おせちの具材を守り抜け。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・邪牛神モーモンおよびハイオークから受けるす\n　べてのダメージで発生する、パーティキャラの\n　ＴＰ回復量を中ダウンさせる。\n・バトル開始時、ハイオークを３体召喚する。\n・ハイオークを１体倒すたびに、残りハイオーク\n　数が１減少する。その後、ハイオークが再度\n　召喚される。\n・目の前の敵１キャラに魔法小ダメージを与え、　物理防御力を大ダウンさせる。このスキルの状\n　態異常はダメージを無効化しても付与される。", "邪牛神モーモン", 314604);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314605, 0, 0, 0, "【ＭＯＤＥ２】猛牛の強靭な肉体が爆ぜる。\n怪腕から繰り出される殴打をまともに受けると\n肉体と、そこに宿る全ての煩悩をも吹き飛ばす。\n\n・敵からの攻撃による麻痺、スタン、束縛、凍結、\n　時間停止、行動速度低下効果への耐性を持たな\n　い。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・邪牛神モーモンから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・通常攻撃は、目の前の敵１キャラに邪牛神モー\n　モンの物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。\n・敵全体のステータスアップ効果を解除し、一定\n　時間バトルフィールドを展開する。\n【バトルフィールド効果】\n・敵全体の物理攻撃力と魔法攻撃力を100％\n　ダウンした後、物理攻撃力と魔法攻撃力を極大\n　アップ。\n・邪牛神モーモンの物理攻撃力を100％ダウン\n　した後、物理攻撃力を極大アップ。", "邪牛神モーモン", 314605);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314605, 0, 0, 0, "【ＭＯＤＥ３】猛牛は痛手を負おうとも、鼻息\nを荒げ立ちはだかる。妄執に取りつかれた迷惑\nな魔物を打ち倒し、謹賀新年を取り戻せ！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・邪牛神モーモンから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体に邪牛神モーモン\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与え、火傷状態にする。\n・最も魔法攻撃力の高い敵１キャラに物理割合\n　ダメージを与え、ノックバックさせると同時に\n　スタンさせる。\n・前から１番目と２番目のキャラに物理大ダメー\n　ジを与える。スキル発動後、自身の物理攻撃力\n　を大アップさせ、物理/魔法防御力が大ダウン。\n・残り時間が30秒を切ると、さらに攻撃が激し\n　く凶暴になる。", "邪牛神モーモン", 314606);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314700, 0, 0, 0, "", "グレーターゴーレム", 314700);
INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (16, 1.6, 0, 1, 3000, 270, 314700, 0, 0, 0, "【物理】同族のゴーレムを次々に呼び寄せる\n岩石の魔物。大地を揺さぶる巨腕が冒険者たちに\n襲い掛かる。\n\n・主人公が目覚めていないため、バトル開始時\n　に全キャラクターのユニオンバーストが発動\n　不能状態になる。\n・ぺコリーヌが目覚めていないため、本バトルで\n　はペコリーヌ（同名キャラ含む）が編成不可。\n・ユニオンバーストは、前から4キャラにグレー\n　ターゴーレムの物理攻撃力とキャラの最大ＨＰ\n　に応じた物理割合ダメージを与える。\n・敵全体をノックバックさせ、ゴーレムを召喚す\n　る。さらに自分の物理攻撃力を中アップさせ\n　る。\n・前から１番目のキャラに物理大ダメージ。", "グレーターゴーレム", 314701);
