INSERT INTO `v1_5a82a79b9b579f5557273db3190e4b5c42c2819321b138fdee39f0c244667ccc` VALUES (7, 1.965, 0, 2, 740, 450, 126501, 0, 0, 1, "ライラエルの姿をした特殊な魔物\n\n・特殊スキルは、環境効果【界雷】を発動する。\n　このスキルの効果は敵、味方すべてに適用され\n　る。魔法攻撃のダメージが小アップし、物理攻\n　撃を行うキャラが行動する度にそのキャラの最\n　大ＨＰに応じて最大99999の割合ダメージ\n　を与える。", "ライラエルのシャドウ", 626501);
