INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (1, "やばいですね☆なの！", 5, 100, 3, "ペコリーヌのプリン", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (2, "主さま、なの！", 5, 101, 3, "コッコロのプリン", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (3, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 5, 102, 3, "キャルのプリン", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (4, "おにーたん♪なの！", 5, 103, 3, "シェフィのプリン", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (5, "ハーハッハッハ！\n世界征服なのじゃなのー！", 5, 104, 3, "イリヤのプリン", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (6, "お兄ちゃんのエッチ！　なの！", 5, 105, 3, "アカリのプリン", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (7, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 5, 106, 3, "ヨリのプリン", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (8, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 5, 107, 3, "シノブのプリン", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (9, "インフェルノシールド！なの！", 5, 108, 3, "ジュンのプリン", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (10, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 5, 109, 3, "トモのプリン", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (11, "タイガーヒーローボンバー！\nなの！", 5, 110, 3, "マツリのプリン", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (13, "ま、いいんじゃん？\nしらんけどなの", 5, 111, 3, "クロエのプリン", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (14, "ぷりーん★なの！", 5, 112, 3, "チエルのプリン", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (15, "端的に換言すれば\n青春の味なの？", 5, 113, 3, "ユニのプリン", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (16, "幼なじみはあんた…なの？", 5, 114, 3, "サレンのプリン", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (17, "ひゃああんなの～！", 5, 115, 3, "スズメのプリン", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (18, "サルキデバレヤ！なの！", 5, 116, 3, "クルミのプリン", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (19, "プリンフルスイングなの～！", 5, 117, 3, "アヤネのプリン", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (20, "みらくるみやりん\nくるりんぱ～☆なの！", 5, 118, 3, "マホのプリン", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (21, "ウルフェンバイトなの！", 5, 119, 3, "マコトのプリン", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (22, "なんくるないさ～！なの！", 5, 120, 3, "カオリのプリン", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (23, "ふふん。簡単な推理なの", 5, 121, 3, "カスミのプリン", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (24, "エリザベス～！なの～！", 5, 122, 3, "マヒルのプリン", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (25, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 5, 123, 3, "シオリのプリン", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (26, "もふもふにしてあげるなの！", 5, 124, 3, "リマのプリン", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (27, "めんどくさいな～なの…", 5, 125, 3, "リンのプリン", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (28, "プリンの権能を解放するの～！", 5, 126, 3, "ラビリスタのプリン", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (29, "お姉ちゃんパワー全開！なの！", 5, 127, 3, "シズルのプリン", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (30, "お兄ちゃん、プリンをよこすの！", 5, 128, 3, "リノのプリン", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (31, "困った時はお互いさまさま！\nなの！", 5, 129, 3, "ヒヨリのプリン", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (32, "フラワリングヒールなの～♪", 5, 130, 3, "ユイのプリン", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (33, "スラッシュテンペスト！なの。", 5, 131, 3, "レイのプリン", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (34, "おーほっほほほ！なの！", 5, 132, 3, "アキノのプリン", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (35, "プリしゅわ最高～！なの！", 5, 133, 3, "ユカリのプリン", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (36, "にゃにゃにゃ！なの！", 5, 134, 3, "タマキのプリン", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (37, "効率全開！なの！", 5, 135, 3, "ミフユのプリン", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (38, "あらあら～なの～♪", 5, 136, 3, "ミサトのプリン", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (39, "スヤスヤ～…なの…", 5, 137, 3, "ハツネのプリン", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (40, "ぼっちは卒業なの～！", 5, 138, 3, "アオイのプリン", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (41, "リズムあげるのー！", 5, 139, 3, "ノゾミのプリン", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (42, "風の加護を！なの！", 5, 140, 3, "チカのプリン", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (43, "騎士さんのために\nしたんじゃありません！なの！", 5, 141, 3, "ツムギのプリン", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (12, "宴の始まりなの！", 5, 142, 3, "クリスティーナのプリン", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (44, "一刀両断、覚悟しな！なの！", 5, 143, 3, "ルカのプリン", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (45, "きゅぴーん！キタコレ！なの！", 5, 144, 3, "ナナカのプリン", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (46, "人呼んで、疾風のミヤコなの！", 5, 145, 3, "アンナのプリン", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (47, "クスクス…クスクスクス…なの", 5, 146, 3, "エリコのプリン", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (48, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 5, 147, 3, "ミツキのプリン", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (49, "私は子どもではない！なの！", 5, 148, 3, "モニカのプリン", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (50, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 5, 149, 3, "ニノンのプリン", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (51, "…ぐふふ、じゅるり～なの", 5, 150, 3, "クウカのプリン", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (52, "あぁ、ミヤコはなんて美しいの～", 5, 151, 3, "ユキのプリン", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (53, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 5, 152, 3, "アユミのプリン", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (54, "にひひ…イタズラの時間なの！", 5, 153, 3, "ミソギのプリン", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (55, "ぴょんぴょこぴょーん、なの♪", 5, 154, 3, "ミミのプリン", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (56, "へんたいふしんしゃさんなの！", 5, 155, 3, "キョウカのプリン", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (57, "ハート狙い撃ち！なの！", 5, 156, 3, "スズナのプリン", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (58, "授業を始めます♪なの！", 5, 157, 3, "イオのプリン", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (59, "立派なレディ、なの！", 5, 158, 3, "ミサキのプリン", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (60, "プリンを持ってこないと\nお仕置きだよ～☆なの！", 5, 159, 3, "ホマレのプリン", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (61, "たぎってきたの！", 5, 160, 3, "カヤのプリン", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (62, "下剋上、やってやるですなの！", 5, 161, 3, "イノリのプリン", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (63, "どけどけ～！なの～！", 5, 162, 3, "ムイミのプリン", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (64, "ミラーミラー！なの！", 5, 163, 3, "ネネカのプリン", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (65, "プリンに忠誠を誓いますわ！\nなの！", 5, 164, 3, "クレジッタのプリン", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (66, "ギルド管理協会へようこそ！\nなの！", 5, 165, 3, "カリンのプリン", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 1);
