INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "やばいですね☆なの！", 1, "ペコリーヌのプリン", 3, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 100, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (2, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "主さま、なの！", 1, "コッコロのプリン", 3, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 101, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 0, "キャルのプリン", 3, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 102, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (4, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "おにーたん♪なの！", 1, "シェフィのプリン", 3, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 103, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (5, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "ハーハッハッハ！\n世界征服なのじゃなのー！", 1, "イリヤのプリン", 3, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 104, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (6, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "お兄ちゃんのエッチ！　なの！", 1, "アカリのプリン", 3, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 105, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (7, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 1, "ヨリのプリン", 3, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 106, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (8, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 0, "シノブのプリン", 3, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 107, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (9, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "インフェルノシールド！なの！", 1, "ジュンのプリン", 3, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 108, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (10, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 1, "トモのプリン", 3, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 109, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (11, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "タイガーヒーローボンバー！\nなの！", 1, "マツリのプリン", 3, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 110, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (13, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "ま、いいんじゃん？\nしらんけどなの", 1, "クロエのプリン", 3, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 111, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (14, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "ぷりーん★なの！", 1, "チエルのプリン", 3, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 112, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (15, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "端的に換言すれば\n青春の味なの？", 1, "ユニのプリン", 3, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 113, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (16, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "幼なじみはあんた…なの？", 1, "サレンのプリン", 3, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 114, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (17, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "ひゃああんなの～！", 1, "スズメのプリン", 3, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 115, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (18, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "サルキデバレヤ！なの！", 0, "クルミのプリン", 3, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 116, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (19, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "プリンフルスイングなの～！", 1, "アヤネのプリン", 3, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 117, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (20, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "みらくるみやりん\nくるりんぱ～☆なの！", 1, "マホのプリン", 3, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 118, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (21, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "ウルフェンバイトなの！", 1, "マコトのプリン", 3, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 119, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (22, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "なんくるないさ～！なの！", 1, "カオリのプリン", 3, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 120, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (23, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "ふふん。簡単な推理なの", 1, "カスミのプリン", 3, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 121, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (24, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "エリザベス～！なの～！", 1, "マヒルのプリン", 3, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 122, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (25, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 1, "シオリのプリン", 3, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 123, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (26, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "もふもふにしてあげるなの！", 1, "リマのプリン", 3, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 124, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (27, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "めんどくさいな～なの…", 0, "リンのプリン", 3, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 125, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (28, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "プリンの権能を解放するの～！", 1, "ラビリスタのプリン", 3, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 126, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (29, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "お姉ちゃんパワー全開！なの！", 0, "シズルのプリン", 3, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 127, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (30, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "お兄ちゃん、プリンをよこすの！", 1, "リノのプリン", 3, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 128, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (31, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "困った時はお互いさまさま！\nなの！", 1, "ヒヨリのプリン", 3, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 129, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (32, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "フラワリングヒールなの～♪", 1, "ユイのプリン", 3, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 130, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (33, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "スラッシュテンペスト！なの。", 1, "レイのプリン", 3, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 131, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (34, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "おーほっほほほ！なの！", 1, "アキノのプリン", 3, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 132, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (35, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "プリしゅわ最高～！なの！", 1, "ユカリのプリン", 3, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 133, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (36, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "にゃにゃにゃ！なの！", 1, "タマキのプリン", 3, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 134, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (37, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "効率全開！なの！", 0, "ミフユのプリン", 3, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 135, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (38, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "あらあら～なの～♪", 1, "ミサトのプリン", 3, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 136, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (39, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "スヤスヤ～…なの…", 1, "ハツネのプリン", 3, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 137, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (40, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "ぼっちは卒業なの～！", 1, "アオイのプリン", 3, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 138, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (41, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "リズムあげるのー！", 1, "ノゾミのプリン", 3, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 139, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (42, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "風の加護を！なの！", 1, "チカのプリン", 3, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 140, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (43, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "騎士さんのために\nしたんじゃありません！なの！", 1, "ツムギのプリン", 3, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 141, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (12, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "宴の始まりなの！", 0, "クリスティーナのプリン", 3, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 142, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (44, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "一刀両断、覚悟しな！なの！", 1, "ルカのプリン", 3, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 143, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (45, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "きゅぴーん！キタコレ！なの！", 1, "ナナカのプリン", 3, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 144, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (46, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "人呼んで、疾風のミヤコなの！", 1, "アンナのプリン", 3, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 145, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (47, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "クスクス…クスクスクス…なの", 1, "エリコのプリン", 3, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 146, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (48, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 1, "ミツキのプリン", 3, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 147, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (49, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "私は子どもではない！なの！", 1, "モニカのプリン", 3, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 148, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (50, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 1, "ニノンのプリン", 3, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 149, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (51, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "…ぐふふ、じゅるり～なの", 0, "クウカのプリン", 3, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 150, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (52, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "あぁ、ミヤコはなんて美しいの～", 1, "ユキのプリン", 3, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 151, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (53, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 0, "アユミのプリン", 3, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 152, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (54, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "にひひ…イタズラの時間なの！", 0, "ミソギのプリン", 3, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 153, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (55, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "ぴょんぴょこぴょーん、なの♪", 0, "ミミのプリン", 3, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 154, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (56, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "へんたいふしんしゃさんなの！", 0, "キョウカのプリン", 3, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 155, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (57, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "ハート狙い撃ち！なの！", 1, "スズナのプリン", 3, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 156, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (58, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "授業を始めます♪なの！", 0, "イオのプリン", 3, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 157, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (59, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "立派なレディ、なの！", 1, "ミサキのプリン", 3, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 158, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (60, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 1, "ホマレのプリン", 3, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 159, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (61, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "たぎってきたの！", 1, "カヤのプリン", 3, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 160, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (62, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "下剋上、やってやるですなの！", 0, "イノリのプリン", 3, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 161, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (63, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "どけどけ～！なの～！", 1, "ムイミのプリン", 3, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 162, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (64, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "ミラーミラー！なの！", 1, "ネネカのプリン", 3, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 163, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (65, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "プリンに忠誠を誓いますわ！\nなの！", 1, "クレジッタのプリン", 3, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 164, 5);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (66, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "ギルド管理協会へようこそ！\nなの！", 1, "カリンのプリン", 3, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 165, 5);
