INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (1, 3, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 100, 5, "やばいですね☆なの！", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", "ペコリーヌのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (2, 3, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 101, 5, "主さま、なの！", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", "コッコロのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (3, 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 102, 5, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", "キャルのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (4, 3, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 103, 5, "おにーたん♪なの！", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", "シェフィのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (5, 3, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 104, 5, "ハーハッハッハ！\n世界征服なのじゃなのー！", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", "イリヤのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (6, 3, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 105, 5, "お兄ちゃんのエッチ！　なの！", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", "アカリのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (7, 3, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 106, 5, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", "ヨリのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (8, 3, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 107, 5, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", "シノブのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (9, 3, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 108, 5, "インフェルノシールド！なの！", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", "ジュンのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (10, 3, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 109, 5, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", "トモのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (11, 3, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 110, 5, "タイガーヒーローボンバー！\nなの！", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", "マツリのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (13, 3, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 111, 5, "ま、いいんじゃん？\nしらんけどなの", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", "クロエのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (14, 3, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 112, 5, "ぷりーん★なの！", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", "チエルのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (15, 3, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 113, 5, "端的に換言すれば\n青春の味なの？", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", "ユニのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (16, 3, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 114, 5, "幼なじみはあんた…なの？", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", "サレンのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (17, 3, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 115, 5, "ひゃああんなの～！", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", "スズメのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (18, 3, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 116, 5, "サルキデバレヤ！なの！", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", "クルミのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (19, 3, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 117, 5, "プリンフルスイングなの～！", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", "アヤネのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (20, 3, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 118, 5, "みらくるみやりん\nくるりんぱ～☆なの！", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", "マホのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (21, 3, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 119, 5, "ウルフェンバイトなの！", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", "マコトのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (22, 3, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 120, 5, "なんくるないさ～！なの！", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", "カオリのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (23, 3, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 121, 5, "ふふん。簡単な推理なの", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", "カスミのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (24, 3, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 122, 5, "エリザベス～！なの～！", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", "マヒルのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (25, 3, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 123, 5, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", "シオリのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (26, 3, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 124, 5, "もふもふにしてあげるなの！", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", "リマのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (27, 3, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 125, 5, "めんどくさいな～なの…", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", "リンのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (28, 3, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 126, 5, "プリンの権能を解放するの～！", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", "ラビリスタのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (29, 3, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 127, 5, "お姉ちゃんパワー全開！なの！", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", "シズルのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (30, 3, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 128, 5, "お兄ちゃん、プリンをよこすの！", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", "リノのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (31, 3, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 129, 5, "困った時はお互いさまさま！\nなの！", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", "ヒヨリのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (32, 3, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 130, 5, "フラワリングヒールなの～♪", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", "ユイのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (33, 3, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 131, 5, "スラッシュテンペスト！なの。", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", "レイのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (34, 3, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 132, 5, "おーほっほほほ！なの！", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", "アキノのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (35, 3, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 133, 5, "プリしゅわ最高～！なの！", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", "ユカリのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (36, 3, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 134, 5, "にゃにゃにゃ！なの！", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", "タマキのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (37, 3, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 135, 5, "効率全開！なの！", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", "ミフユのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (38, 3, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 136, 5, "あらあら～なの～♪", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", "ミサトのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (39, 3, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 137, 5, "スヤスヤ～…なの…", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", "ハツネのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (40, 3, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 138, 5, "ぼっちは卒業なの～！", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", "アオイのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (41, 3, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 139, 5, "リズムあげるのー！", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", "ノゾミのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (42, 3, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 140, 5, "風の加護を！なの！", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", "チカのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (43, 3, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 141, 5, "騎士さんのために\nしたんじゃありません！なの！", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", "ツムギのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (12, 3, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 142, 5, "宴の始まりなの！", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", "クリスティーナのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (44, 3, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 143, 5, "一刀両断、覚悟しな！なの！", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", "ルカのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (45, 3, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 144, 5, "きゅぴーん！キタコレ！なの！", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", "ナナカのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (46, 3, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 145, 5, "人呼んで、疾風のミヤコなの！", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", "アンナのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (47, 3, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 146, 5, "クスクス…クスクスクス…なの", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", "エリコのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (48, 3, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 147, 5, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", "ミツキのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (49, 3, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 148, 5, "私は子どもではない！なの！", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", "モニカのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (50, 3, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 149, 5, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", "ニノンのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (51, 3, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 150, 5, "…ぐふふ、じゅるり～なの", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", "クウカのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (52, 3, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 151, 5, "あぁ、ミヤコはなんて美しいの～", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", "ユキのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (53, 3, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 152, 5, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", "アユミのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (54, 3, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 153, 5, "にひひ…イタズラの時間なの！", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", "ミソギのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (55, 3, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 154, 5, "ぴょんぴょこぴょーん、なの♪", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", "ミミのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (56, 3, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 155, 5, "へんたいふしんしゃさんなの！", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", "キョウカのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (57, 3, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 156, 5, "ハート狙い撃ち！なの！", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", "スズナのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (58, 3, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 157, 5, "授業を始めます♪なの！", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", "イオのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (59, 3, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 158, 5, "立派なレディ、なの！", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", "ミサキのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (60, 3, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 159, 5, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", "ホマレのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (61, 3, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 160, 5, "たぎってきたの！", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", "カヤのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (62, 3, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 161, 5, "下剋上、やってやるですなの！", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", "イノリのプリン", 0);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (63, 3, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 162, 5, "どけどけ～！なの～！", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", "ムイミのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (64, 3, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 163, 5, "ミラーミラー！なの！", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", "ネネカのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (65, 3, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 164, 5, "プリンに忠誠を誓いますわ！\nなの！", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", "クレジッタのプリン", 1);
INSERT INTO `v1_5d087b64cc2f86177e8d2a84944c09af7e6b157bbd7e70f20aaf9a48166a1025` VALUES (66, 3, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 165, 5, "ギルド管理協会へようこそ！\nなの！", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", "カリンのプリン", 1);
