INSERT INTO `v1_5f027e0bcd084fc1b8fa6613d2303202b096dd338060a8987d302f9bd267090a` VALUES (/*7331a07a24d8865e8629f27d83a634b3d8056a18796fba19cbd9ce75091cdfc8*/1010808, /*c395d90d2c3cbf0f1330287a8dffc4307eeacd56c9875a4a5ca8948959289c28*/10108, /*236483d5f48493411cb37ee77b2bd0bdf88c4491f7e4fcc1d65e6dcdd36f3e52*/"心と体に爽やかな癒しを♪　スースーの湯", /*3678e1c49387be8f658230dcdf51a465face0fba31ba814449a8757ea7c2f382*/"2023/01/07", /*29797e31c043765a6660cb67645135ca3db7046e68e82ecfebe8863f5482591f*/0, /*8ae157844e3f164e2813a15619dc0dbbddf14417dee0f1b63907b6838717dad7*/8, /*2af5fb6c0bb3d1b1c1b5eed2b5e1b594ff869f09fa0a8a09ed9d28d7171f77f0*/91002, /*ccc86546a6e16a668544b129965b7ea5bbbe539bcbca9d1c2f852b0f6c256499*/40, /*9f87dd231c6350facc2b115d913427f4099c64009782311cfa9d9034cee004e9*/101501, /*7f4ff99303c5fe75dd835c8e1ca581960aa2b5a7450b18875058ef47c452e9d7*/102101, /*8984b0cad01fefefaba4e2a67f1a9d35d83924423cbea0e750d940aa945fcfbd*/8, /*b823c6c35c001943ee00681a80cdee092c39cf037cb07a0f2573b01d34ebdf02*/"エルフの森産　スースー草のシードオイル", /*9d53cf1e3cf558237135f78e8de230b821070c7ecbd001f90b09195292d5835e*/"清涼効果、のど・鼻の保湿効果\n※鼻歌が旅館内に届く可能性があります", /*6cb015dc6bdcca36c97c54b3eb59f099433feac4fbd7b9a90f5964b11303747d*/"清涼効果のある薬草類を調合した薬湯。\n湯気を吸うことでのどや鼻が保湿され、\n声の通りが格段によくなると好評。\n湯上がり後のスッキリ感も相まって\nリピーターが多い。歌手やアイドルたちも\nお忍びでこっそり訪れているとか。");
INSERT INTO `v1_5f027e0bcd084fc1b8fa6613d2303202b096dd338060a8987d302f9bd267090a` VALUES (/*7331a07a24d8865e8629f27d83a634b3d8056a18796fba19cbd9ce75091cdfc8*/1010809, /*c395d90d2c3cbf0f1330287a8dffc4307eeacd56c9875a4a5ca8948959289c28*/10108, /*236483d5f48493411cb37ee77b2bd0bdf88c4491f7e4fcc1d65e6dcdd36f3e52*/"ああバラ色の人生　美肌の湯", /*3678e1c49387be8f658230dcdf51a465face0fba31ba814449a8757ea7c2f382*/"2023/01/08", /*29797e31c043765a6660cb67645135ca3db7046e68e82ecfebe8863f5482591f*/0, /*8ae157844e3f164e2813a15619dc0dbbddf14417dee0f1b63907b6838717dad7*/8, /*2af5fb6c0bb3d1b1c1b5eed2b5e1b594ff869f09fa0a8a09ed9d28d7171f77f0*/91002, /*ccc86546a6e16a668544b129965b7ea5bbbe539bcbca9d1c2f852b0f6c256499*/40, /*9f87dd231c6350facc2b115d913427f4099c64009782311cfa9d9034cee004e9*/105101, /*7f4ff99303c5fe75dd835c8e1ca581960aa2b5a7450b18875058ef47c452e9d7*/100801, /*8984b0cad01fefefaba4e2a67f1a9d35d83924423cbea0e750d940aa945fcfbd*/9, /*b823c6c35c001943ee00681a80cdee092c39cf037cb07a0f2573b01d34ebdf02*/"仲居特製の超美肌薬、大輪のバラ", /*9d53cf1e3cf558237135f78e8de230b821070c7ecbd001f90b09195292d5835e*/"美肌効果、保湿効果、保温効果\nバラの香りによるリラックス効果", /*6cb015dc6bdcca36c97c54b3eb59f099433feac4fbd7b9a90f5964b11303747d*/"とにもかくにも肌を美しくする効果を追求\nした珠玉の温泉。お湯を軽く浴びただけで\n違いが分かるとウワサになり、このお湯を\n利用した化粧水の発売も模索されている\nとか。ちなみにバラはあくまで香りづけの\nものであり、美肌効果はないとのこと。");
