INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (1, 1, "やばいですね☆なの！", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "ペコリーヌのプリン", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 5, 3, 100);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (2, 1, "主さま、なの！", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "コッコロのプリン", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 5, 3, 101);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (3, 0, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "キャルのプリン", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 5, 3, 102);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (4, 1, "おにーたん♪なの！", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "シェフィのプリン", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 5, 3, 103);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (5, 1, "ハーハッハッハ！\n世界征服なのじゃなのー！", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "イリヤのプリン", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 5, 3, 104);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (6, 1, "お兄ちゃんのエッチ！　なの！", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "アカリのプリン", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 5, 3, 105);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (7, 1, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "ヨリのプリン", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 5, 3, 106);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (8, 0, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "シノブのプリン", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 5, 3, 107);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (9, 1, "インフェルノシールド！なの！", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "ジュンのプリン", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 5, 3, 108);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (10, 1, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "トモのプリン", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 5, 3, 109);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (11, 1, "タイガーヒーローボンバー！\nなの！", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "マツリのプリン", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 5, 3, 110);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (13, 1, "ま、いいんじゃん？\nしらんけどなの", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "クロエのプリン", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 5, 3, 111);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (14, 1, "ぷりーん★なの！", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "チエルのプリン", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 5, 3, 112);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (15, 1, "端的に換言すれば\n青春の味なの？", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "ユニのプリン", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 5, 3, 113);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (16, 1, "幼なじみはあんた…なの？", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "サレンのプリン", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 5, 3, 114);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (17, 1, "ひゃああんなの～！", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "スズメのプリン", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 5, 3, 115);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (18, 0, "サルキデバレヤ！なの！", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "クルミのプリン", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 5, 3, 116);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (19, 1, "プリンフルスイングなの～！", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "アヤネのプリン", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 5, 3, 117);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (20, 1, "みらくるみやりん\nくるりんぱ～☆なの！", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "マホのプリン", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 5, 3, 118);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (21, 1, "ウルフェンバイトなの！", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "マコトのプリン", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 5, 3, 119);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (22, 1, "なんくるないさ～！なの！", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "カオリのプリン", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 5, 3, 120);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (23, 1, "ふふん。簡単な推理なの", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "カスミのプリン", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 5, 3, 121);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (24, 1, "エリザベス～！なの～！", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "マヒルのプリン", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 5, 3, 122);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (25, 1, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "シオリのプリン", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 5, 3, 123);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (26, 1, "もふもふにしてあげるなの！", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "リマのプリン", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 5, 3, 124);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (27, 0, "めんどくさいな～なの…", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "リンのプリン", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 5, 3, 125);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (28, 1, "プリンの権能を解放するの～！", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "ラビリスタのプリン", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 5, 3, 126);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (29, 0, "お姉ちゃんパワー全開！なの！", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "シズルのプリン", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 5, 3, 127);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (30, 1, "お兄ちゃん、プリンをよこすの！", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "リノのプリン", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 5, 3, 128);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (31, 1, "困った時はお互いさまさま！\nなの！", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "ヒヨリのプリン", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 5, 3, 129);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (32, 1, "フラワリングヒールなの～♪", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "ユイのプリン", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 5, 3, 130);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (33, 1, "スラッシュテンペスト！なの。", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "レイのプリン", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 5, 3, 131);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (34, 1, "おーほっほほほ！なの！", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "アキノのプリン", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 5, 3, 132);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (35, 1, "プリしゅわ最高～！なの！", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "ユカリのプリン", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 5, 3, 133);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (36, 1, "にゃにゃにゃ！なの！", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "タマキのプリン", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 5, 3, 134);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (37, 0, "効率全開！なの！", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "ミフユのプリン", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 5, 3, 135);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (38, 1, "あらあら～なの～♪", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "ミサトのプリン", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 5, 3, 136);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (39, 1, "スヤスヤ～…なの…", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "ハツネのプリン", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 5, 3, 137);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (40, 1, "ぼっちは卒業なの～！", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "アオイのプリン", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 5, 3, 138);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (41, 1, "リズムあげるのー！", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "ノゾミのプリン", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 5, 3, 139);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (42, 1, "風の加護を！なの！", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "チカのプリン", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 5, 3, 140);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (43, 1, "騎士さんのために\nしたんじゃありません！なの！", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "ツムギのプリン", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 5, 3, 141);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (12, 0, "宴の始まりなの！", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "クリスティーナのプリン", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 5, 3, 142);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (44, 1, "一刀両断、覚悟しな！なの！", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "ルカのプリン", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 5, 3, 143);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (45, 1, "きゅぴーん！キタコレ！なの！", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "ナナカのプリン", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 5, 3, 144);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (46, 1, "人呼んで、疾風のミヤコなの！", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "アンナのプリン", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 5, 3, 145);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (47, 1, "クスクス…クスクスクス…なの", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "エリコのプリン", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 5, 3, 146);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (48, 1, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "ミツキのプリン", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 5, 3, 147);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (49, 1, "私は子どもではない！なの！", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "モニカのプリン", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 5, 3, 148);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (50, 1, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "ニノンのプリン", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 5, 3, 149);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (51, 0, "…ぐふふ、じゅるり～なの", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "クウカのプリン", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 5, 3, 150);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (52, 1, "あぁ、ミヤコはなんて美しいの～", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "ユキのプリン", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 5, 3, 151);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (53, 0, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "アユミのプリン", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 5, 3, 152);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (54, 0, "にひひ…イタズラの時間なの！", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "ミソギのプリン", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 5, 3, 153);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (55, 0, "ぴょんぴょこぴょーん、なの♪", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "ミミのプリン", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 5, 3, 154);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (56, 0, "へんたいふしんしゃさんなの！", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "キョウカのプリン", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 5, 3, 155);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (57, 1, "ハート狙い撃ち！なの！", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "スズナのプリン", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 5, 3, 156);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (58, 0, "授業を始めます♪なの！", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "イオのプリン", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 5, 3, 157);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (59, 1, "立派なレディ、なの！", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "ミサキのプリン", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 5, 3, 158);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (60, 1, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "ホマレのプリン", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 5, 3, 159);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (61, 1, "たぎってきたの！", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "カヤのプリン", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 5, 3, 160);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (62, 0, "下剋上、やってやるですなの！", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "イノリのプリン", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 5, 3, 161);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (63, 1, "どけどけ～！なの～！", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "ムイミのプリン", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 5, 3, 162);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (64, 1, "ミラーミラー！なの！", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "ネネカのプリン", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 5, 3, 163);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (65, 1, "プリンに忠誠を誓いますわ！\nなの！", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "クレジッタのプリン", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 5, 3, 164);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (66, 1, "ギルド管理協会へようこそ！\nなの！", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "カリンのプリン", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 5, 3, 165);
