INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (100, 1, "やばいですね☆なの！", 3, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 5, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "ペコリーヌのプリン", 1);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (101, 1, "主さま、なの！", 3, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 5, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "コッコロのプリン", 2);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (102, 0, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 5, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "キャルのプリン", 3);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (103, 1, "おにーたん♪なの！", 3, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 5, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "シェフィのプリン", 4);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (104, 1, "ハーハッハッハ！\n世界征服なのじゃなのー！", 3, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 5, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "イリヤのプリン", 5);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (105, 1, "お兄ちゃんのエッチ！　なの！", 3, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 5, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "アカリのプリン", 6);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (106, 1, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 3, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 5, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "ヨリのプリン", 7);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (107, 0, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 3, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 5, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "シノブのプリン", 8);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (108, 1, "インフェルノシールド！なの！", 3, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 5, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "ジュンのプリン", 9);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (109, 1, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 3, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 5, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "トモのプリン", 10);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (110, 1, "タイガーヒーローボンバー！\nなの！", 3, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 5, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "マツリのプリン", 11);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (111, 1, "ま、いいんじゃん？\nしらんけどなの", 3, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 5, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "クロエのプリン", 13);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (112, 1, "ぷりーん★なの！", 3, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 5, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "チエルのプリン", 14);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (113, 1, "端的に換言すれば\n青春の味なの？", 3, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 5, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "ユニのプリン", 15);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (114, 1, "幼なじみはあんた…なの？", 3, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 5, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "サレンのプリン", 16);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (115, 1, "ひゃああんなの～！", 3, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 5, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "スズメのプリン", 17);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (116, 0, "サルキデバレヤ！なの！", 3, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 5, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "クルミのプリン", 18);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (117, 1, "プリンフルスイングなの～！", 3, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 5, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "アヤネのプリン", 19);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (118, 1, "みらくるみやりん\nくるりんぱ～☆なの！", 3, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 5, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "マホのプリン", 20);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (119, 1, "ウルフェンバイトなの！", 3, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 5, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "マコトのプリン", 21);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (120, 1, "なんくるないさ～！なの！", 3, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 5, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "カオリのプリン", 22);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (121, 1, "ふふん。簡単な推理なの", 3, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 5, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "カスミのプリン", 23);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (122, 1, "エリザベス～！なの～！", 3, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 5, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "マヒルのプリン", 24);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (123, 1, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 3, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 5, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "シオリのプリン", 25);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (124, 1, "もふもふにしてあげるなの！", 3, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 5, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "リマのプリン", 26);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (125, 0, "めんどくさいな～なの…", 3, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 5, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "リンのプリン", 27);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (126, 1, "プリンの権能を解放するの～！", 3, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 5, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "ラビリスタのプリン", 28);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (127, 0, "お姉ちゃんパワー全開！なの！", 3, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 5, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "シズルのプリン", 29);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (128, 1, "お兄ちゃん、プリンをよこすの！", 3, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 5, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "リノのプリン", 30);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (129, 1, "困った時はお互いさまさま！\nなの！", 3, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 5, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "ヒヨリのプリン", 31);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (130, 1, "フラワリングヒールなの～♪", 3, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 5, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "ユイのプリン", 32);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (131, 1, "スラッシュテンペスト！なの。", 3, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 5, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "レイのプリン", 33);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (132, 1, "おーほっほほほ！なの！", 3, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 5, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "アキノのプリン", 34);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (133, 1, "プリしゅわ最高～！なの！", 3, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 5, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "ユカリのプリン", 35);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (134, 1, "にゃにゃにゃ！なの！", 3, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 5, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "タマキのプリン", 36);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (135, 0, "効率全開！なの！", 3, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 5, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "ミフユのプリン", 37);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (136, 1, "あらあら～なの～♪", 3, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 5, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "ミサトのプリン", 38);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (137, 1, "スヤスヤ～…なの…", 3, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 5, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "ハツネのプリン", 39);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (138, 1, "ぼっちは卒業なの～！", 3, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 5, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "アオイのプリン", 40);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (139, 1, "リズムあげるのー！", 3, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 5, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "ノゾミのプリン", 41);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (140, 1, "風の加護を！なの！", 3, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 5, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "チカのプリン", 42);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (141, 1, "騎士さんのために\nしたんじゃありません！なの！", 3, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 5, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "ツムギのプリン", 43);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (142, 0, "宴の始まりなの！", 3, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 5, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "クリスティーナのプリン", 12);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (143, 1, "一刀両断、覚悟しな！なの！", 3, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 5, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "ルカのプリン", 44);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (144, 1, "きゅぴーん！キタコレ！なの！", 3, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 5, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "ナナカのプリン", 45);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (145, 1, "人呼んで、疾風のミヤコなの！", 3, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 5, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "アンナのプリン", 46);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (146, 1, "クスクス…クスクスクス…なの", 3, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 5, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "エリコのプリン", 47);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (147, 1, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 3, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 5, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "ミツキのプリン", 48);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (148, 1, "私は子どもではない！なの！", 3, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 5, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "モニカのプリン", 49);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (149, 1, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 3, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 5, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "ニノンのプリン", 50);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (150, 0, "…ぐふふ、じゅるり～なの", 3, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 5, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "クウカのプリン", 51);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (151, 1, "あぁ、ミヤコはなんて美しいの～", 3, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 5, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "ユキのプリン", 52);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (152, 0, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 3, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 5, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "アユミのプリン", 53);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (153, 0, "にひひ…イタズラの時間なの！", 3, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 5, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "ミソギのプリン", 54);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (154, 0, "ぴょんぴょこぴょーん、なの♪", 3, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 5, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "ミミのプリン", 55);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (155, 0, "へんたいふしんしゃさんなの！", 3, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 5, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "キョウカのプリン", 56);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (156, 1, "ハート狙い撃ち！なの！", 3, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 5, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "スズナのプリン", 57);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (157, 0, "授業を始めます♪なの！", 3, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 5, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "イオのプリン", 58);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (158, 1, "立派なレディ、なの！", 3, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 5, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "ミサキのプリン", 59);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (159, 1, "プリンを持ってこないと\nお仕置きだよ～☆なの！", 3, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 5, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "ホマレのプリン", 60);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (160, 1, "たぎってきたの！", 3, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 5, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "カヤのプリン", 61);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (161, 0, "下剋上、やってやるですなの！", 3, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 5, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "イノリのプリン", 62);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (162, 1, "どけどけ～！なの～！", 3, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 5, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "ムイミのプリン", 63);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (163, 1, "ミラーミラー！なの！", 3, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 5, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "ネネカのプリン", 64);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (164, 1, "プリンに忠誠を誓いますわ！\nなの！", 3, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 5, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "クレジッタのプリン", 65);
INSERT INTO `v1_6152319514a7c787e089d1beeb56f46e1e54fb58996810c2f2542dd38d1f642a` VALUES (165, 1, "ギルド管理協会へようこそ！\nなの！", 3, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 5, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "カリンのプリン", 66);
