INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 13, 190801, 1000, 1000001, "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？", "2030-04-01 14:00:00", "2020-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 13, 190801, 1000, 1000002, "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？", "2030-04-01 14:00:00", "2020-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 13, 190801, 1001, 1001001, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！", "2030-04-01 14:00:00", "2020-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 12, 190801, 1002, 1002001, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", "2030-04-01 14:00:00", "2020-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 11, 190801, 1003, 1003001, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", "2030-04-01 14:00:00", "2020-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 3, 190801, 1004, 1004001, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", "2030-04-01 14:00:00", "2020-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 7, 190801, 1005, 1005001, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", "2030-04-01 14:00:00", "2020-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 2, 190801, 1006, 1006001, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", "2030-04-01 14:00:00", "2020-05-31 12:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 4, 190801, 1007, 1007001, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", "2030-04-01 14:00:00", "2020-08-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 6, 190801, 1008, 1008001, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n", "2030-04-01 14:00:00", "2021-08-31 12:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 13, 190801, 1009, 1009001, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。", "2030-04-01 14:00:00", "2021-12-18 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 4, 190801, 1010, 1010001, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", "2030-04-01 14:00:00", "2022-02-15 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 6, 190801, 1011, 1011001, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", "2030-04-01 14:00:00", "2022-07-18 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 6, 190801, 1012, 1012001, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪", "2030-04-01 14:00:00", "2023-09-18 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 6, 190801, 1013, 1013001, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪", "2030-04-01 14:00:00", "2024-04-18 15:00:00");
INSERT INTO `v1_720b1b892de7fdd66bc56f3f3948d27349dbb846755e41cece03e6a365b14f0e` VALUES (0, 6, 190801, 1014, 1014001, "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪", "2030-04-01 14:00:00", "2024-11-18 15:00:00");
