INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。", 3800, 10, 31002, 0, 3, 31002, 3, "海宝華の煌盾", "ダメージを受けるたびＴＰ100回復（最大３回）", 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。", 3800, 10, 31003, 0, 3, 31003, 3, "碧貴石の聖輪", "バトル開始時ＴＰ300回復", 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。", 3800, 10, 31004, 0, 3, 31004, 3, "永天鏡の王笏", "ＴＰ消費軽減＋6", 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。", 3800, 10, 31005, 0, 3, 31005, 3, "熾天使の翔靴", "重複可能の行動速度＋30％", 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮最奥部に鎮祀されし神業の遺\n物。風を司る神々の恩寵が宿され\nており、装備者は無尽蔵の活力が\n疾風の如き速さで体を駆け巡る。", 9999, 10, 31006, 0, 5, 31006, 3, "風神の護風恩寵", "ＴＰ上昇＋100", 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。", 3800, 10, 32001, 0, 3, 32001, 3, "幻獣飾テンタシオン", "状態異常命中＋60", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。", 3800, 10, 32002, 1, 3, 32002, 3, "深淵蝕杖マリディクト", "敵全体に呪い付与（毎秒【弱体】数×1500ダメージ）", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。", 3800, 10, 32003, 0, 3, 32003, 3, "滅刻凶刃ヴェノマス", "敵全体に毒付与（毎秒【弱体】数×1500ダメージ）", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。", 3800, 10, 32004, 0, 3, 32004, 3, "燦然御纏アノラ・ベイラ", "敵の物理/魔法攻撃力－30％", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。", 3800, 10, 32005, 0, 3, 32005, 3, "断壁鎖刃フラグメント", "敵の物理/魔法防御力－30％", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮深層部に静眠せし虚影の装衣\n深淵から出でし蝕影は、存在全て\nを霜雪の如く凍てつかせ、抗う敵\nの剣盾両翼を虚壊させる。", 9999, 10, 32006, 0, 5, 32006, 3, "霜影虚装アビスモナーク", "敵の物理/魔法与ダメ－25％\n敵の物理/魔法被ダメ＋25％", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。", 3800, 10, 33001, 0, 3, 33001, 3, "リーサルハルバード", "敵の物理/魔法被クリダメ＋30％", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。", 3800, 10, 33002, 0, 3, 33002, 3, "オオトリノカタナ", "物理/魔法クリダメ＋30％", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。", 3800, 10, 33003, 0, 3, 33003, 3, "アースデストラクション", "通常攻撃強化＋90", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。", 3800, 10, 33004, 0, 3, 33004, 3, "ジャッジメントアロー", "物理/魔法クリティカル＋30％", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。", 3800, 10, 33005, 0, 3, 33005, 3, "ザ・ハーヴェスト", "ＨＰ吸収＋30", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮最深部に残された久遠の遺物\n神代の大戦より聖騎士に脈々と受\nけ継がれ、装備者へ戦いの記憶と\nともに不滅の力を分け与える。", 9999, 10, 33006, 0, 5, 33006, 3, "セレスティアルエイジ", "通常攻撃強化＋500\nＨＰ吸収＋50", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。", 3800, 10, 34001, 0, 3, 34001, 3, "極醒のアミュレット", "バフ強化＋90", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。", 3800, 10, 34002, 0, 3, 34002, 3, "断刃拒絶のアルギュロス", "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。", 3800, 10, 34003, 0, 3, 34003, 3, "緋華栄華のグラナート", "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。", 3800, 10, 34004, 0, 3, 34004, 3, "覇光帝龍のグローリア", "物理/魔法攻撃力＋60％", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。", 3800, 10, 34005, 0, 3, 34005, 3, "華舞玲瓏のハルナデシコ", "物理/魔法防御力＋30％", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮深淵部に佇む歴戦無敗の⼤剣\n台座から剣を⼿にした者は天その\nものを冠すかの如き全能感を得て\n千軍を容易く退ける剛⼒を得る。", 9999, 10, 34006, 0, 5, 34006, 3, "天戴冠のゼニスブレード", "バトル開始時、物理/魔法攻撃力アップフィールド展開\n（現在値＋50％）", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。", 3800, 10, 35001, 0, 3, 35001, 3, "妖精霊杖・癒光", "回復強化＋30", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。", 3800, 10, 35002, 0, 3, 35002, 3, "泉湧命環・豊穣", "ＨＰ＋30％", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。", 3800, 10, 35003, 0, 3, 35003, 3, "白光輝鎧・断壁", "20秒ごとに物理無効バリア展開（【守備】数×1500無効）", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。", 3800, 10, 35004, 0, 3, 35004, 3, "幽玄煌装・絶魔", "20秒ごとに魔法無効バリア展開（【守備】数×1500無効）", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮の奥深くに眠る、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。", 3800, 10, 35005, 0, 3, 35005, 3, "輪廻ノ翡翠珠輪", "継続ＨＰ回復（毎秒【守備】数×1500回復）", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("迷宮最下層に供奉された伝承の環\n選ばれし者がひとたび⼿にすれば\n創造神話の如く、千万無量の⽣命\n⼒を齎すと云われる。", 9999, 10, 35006, 0, 5, 35006, 3, "天命授環・伊邪那", "ＨＰ＋100％", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【美⾷殿】と共に迷宮を冒険する\nための遺物。王家の装備を精巧に\n模しており、味方の⾝体能⼒を⼤\nきく向上させてくれる。", 3200, 10, 40001, 0, 1, 40001, 3, "プリンセスフォース", "ＨＰ＋15％\n物理/魔法与ダメ＋15％", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。\n３人のアストラルに呼応して、属\n性の力を増幅させてくれる。", 3200, 10, 40002, 0, 1, 40002, 3, "アストラルブースト", "属性ボーナス＋50％", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人が\n築いた信頼関係のおかげで、商品\nの価格を値引きしてもらえる。", 3200, 10, 40003, 0, 1, 40003, 3, "サレンディアエンゲージ", "ショップの販売価格－10％", 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【王宮騎士団】と共に迷宮を冒険\nするための遺物。団長の愛⽤品を\n模し会⼼率と引き換えに、会⼼時\nの威⼒を⾼めてくれる。", 3200, 10, 40004, 0, 1, 40004, 3, "ナイツオブフェイス", "物理/魔法クリダメ＋50％\n物理/魔法クリティカル－25％", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【ラビリンス】と共に迷宮を冒険\nするための遺物。迷宮女王はその\n権能でナイトがどこに居ようとも\n即座に参戦してくれる。", 3200, 10, 40005, 0, 1, 40005, 3, "ラビリンスオーソリティ", "挑戦開始時にラビリスタが加入", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【美⾷殿】と共に迷宮を冒険する\nための遺物。冒険の道中で絆を結\nべば結ぶほどに、味⽅の攻撃⼒を\n⾼めてくれる。", 3200, 10, 50001, 0, 1, 50001, 3, "プリンセスエンチャント", "物理/魔法攻撃力アップ（加入キャラ数×4％、最大100％）", 4);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。ギ\nルドマスターの諦めない心が、味\n方を一度だけ敗北から守る。", 3200, 10, 50002, 1, 1, 50002, 3, "トゥインクルブレス", "ボスバトルを除いて一度だけバトル敗北を免除", 5);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人の\n気品を漂わせる剣は、築いた富を\n⼒に変転させる効果を持つ。", 3200, 10, 50003, 0, 1, 50003, 3, "サレンディアノーブル", "通常攻撃強化アップ（アルファルピ所持数×0.01、最大100）", 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【王宮騎士団】と共に迷宮を冒険\nするための遺物。副団長の催す闘\n争の宴は、敵味⽅が受けるダメー\nジをみな悉く増加させる。", 3200, 10, 50004, 0, 1, 50004, 3, "レジーナガーラ", "敵の物理/魔法被ダメ＋25％\n味方の物理/魔法被ダメ＋25％", 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("【ラビリンス】と共に迷宮を冒険\nするための遺物。マスターの⼒を\n冠した剣は、開発者権限の⼀部を\n解放し、遺物を即座に獲得する。", 3200, 10, 50005, 0, 1, 50005, 3, "マスターオブラビリンス", "挑戦開始時にランダムな★1の迷宮遺物を１個獲得", 1);
