INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("キラースピア", 13001, 13001, 3, 1, 1800, "敵の物理/魔法被クリダメ＋10％", 10, "迷宮内で見つかった槍。鋭い一撃\nで敵に深い傷を負わせる。", 0, 1);
