INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("クリーヴフィスト", 13003, 13003, 3, 1, 1800, "通常攻撃強化＋30", 10, "迷宮内で見つかった籠手。強化\nされた一撃を放つことができる。", 0, 1);
