INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("抗鉄のプレート", 14002, 14002, 4, 1, 1800, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×5）", 10, "迷宮内で⾒つかった鎧。同朋の\n強靭を、自身の攻撃に変換する。", 0, 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("抗魔のドレス", 14003, 14003, 4, 1, 1800, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×5）", 10, "迷宮内で⾒つかったドレス。装備\n者の抗魔の才を攻撃⼒に換える。", 0, 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("蛇紋のツルギ", 14004, 14004, 4, 1, 1800, "物理/魔法攻撃力＋20％", 10, "迷宮内で見つかった大剣。装備者\nの攻撃力を強化する。", 0, 1);
