INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("回生杖", 15001, 15001, 5, 1, 1800, "回復強化＋10", 10, "迷宮内で⾒つかった杖。\n癒しの⼒を高めてくれる。", 0, 1);
