INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("再生ノ腕輪", 15005, 15005, 5, 1, 1800, "継続ＨＰ回復（毎秒【守備】数×500回復）", 10, "迷宮内で見つかった腕輪。\n装備者に再生の力を与える。", 0, 1);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("流烈の耳飾り", 21001, 21001, 1, 2, 2800, "ＴＰ上昇＋10", 10, "迷宮内部で発見された耳飾りの形\nをした遺物。流水の激しさのよう\nに、活力加速を増幅させる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("星涙の輝盾", 21002, 21002, 1, 2, 2800, "ダメージを受けるたびＴＰ100回復（最大２回）", 10, "迷宮内部で発見された円盾の形を\nした遺物。装備者に星の癒しを与\nえ攻撃をエネルギーに変換する。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("蒼珠の腕輪", 21003, 21003, 1, 2, 2800, "バトル開始時ＴＰ200回復", 10, "迷宮内部で発⾒された腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("星明りの儀杖", 21004, 21004, 1, 2, 2800, "ＴＰ消費軽減＋4", 10, "迷宮内部で発⾒された杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("疾風の翼靴", 21005, 21005, 1, 2, 2800, "重複可能の行動速度＋20％", 10, "迷宮内部で発見された、翼の生え\nた靴形の遺物。装備した者に疾風\nの力を施し、行動を速める。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("闇至宝アノマリーラ", 22001, 22001, 2, 2, 2800, "状態異常命中＋40", 10, "迷宮内部で発⾒された、⾸飾りの\n遺物。宝⽯の輝きで敵を虜にし、\n怪異をもたらすという。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("邪眼杖ドゥーム", 22002, 22002, 2, 2, 2800, "敵全体に呪い付与（毎秒【弱体】数×1000ダメージ）", 10, "迷宮内部で発見された、杖の形を\nした遺物。杖先のコアから流れる\n邪気が敵に呪いを付与する。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("影刃剣プラール", 22003, 22003, 2, 2, 2800, "敵全体に毒付与（毎秒【弱体】数×1000ダメージ）", 10, "迷宮内部で発見された、短剣の形\nをした遺物。刃に仕込まれた毒が\n気化し、接敵と共に毒を与える。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("薔薇飾帽イレース", 22004, 22004, 2, 2, 2800, "敵の物理/魔法攻撃力－20％", 10, "迷宮内部で発見された、帽子の形\nをした遺物。絡みつく茨の様に、\n敵の攻撃力を阻害する。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("斬赤刃ディバイド", 22005, 22005, 2, 2, 2800, "敵の物理/魔法防御力－20％", 10, "迷宮内部で発⾒された、⼑の遺物\n血で⾚く染まった⼑⾝で、敵の装\n甲をたやすく断ち割る。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("フェイタリティ", 23001, 23001, 3, 2, 2800, "敵の物理/魔法被クリダメ＋20％", 10, "迷宮内部で発見された、槍の遺物\n特殊な形の穂先が、敵により深い\n傷を刻み込む。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("クレナイノタチ", 23002, 23002, 3, 2, 2800, "物理/魔法クリダメ＋20％", 10, "迷宮内部で発見された、刀の形を\nした遺物。秋の紅を映す美しき刃\nは致命の攻撃をさらに強化する。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("ラプチャーガントレット", 23003, 23003, 3, 2, 2800, "通常攻撃強化＋60", 10, "迷宮内部で発⾒された籠⼿の遺\n物。装備者は研ぎ澄まされた強\n力な一撃を放つことができる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("スカイストライク", 23004, 23004, 3, 2, 2800, "物理/魔法クリティカル＋20％", 10, "迷宮内部で発⾒された、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("インヘイルアックス", 23005, 23005, 3, 2, 2800, "ＨＰ吸収＋20", 10, "迷宮内部で発⾒された斧の遺物。\n繰り出される⼀撃は、掠めるだけ\nで獲物の魂を吸い取る。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("進化のイヤリング", 24001, 24001, 4, 2, 2800, "バフ強化＋60", 10, "迷宮内部で発見された、耳飾りの\n形をした遺物。魔石に込められた\n魔力が強化の力を増幅させる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("紅反鋼のアーマー", 24002, 24002, 4, 2, 2800, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", 10, "迷宮内部で発⾒された、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("聖減術のテラドレス", 24003, 24003, 4, 2, 2800, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×10）", 10, "迷宮内部で発⾒された、ドレスの\n遺物。精霊の加護が、防魔結界か\nら力を集めてくれる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("炎王のグレートソード", 24004, 24004, 4, 2, 2800, "物理/魔法攻撃力＋40％", 10, "迷宮内部で発見された、大剣の形\nをした遺物。湧き出る炎の力が、\n攻撃の能力を大きく高める。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("桜堅牢のコート", 24005, 24005, 4, 2, 2800, "物理/魔法防御力＋20％", 10, "迷宮内部で発⾒された鎧の遺物。\n千年咲く桜の如く、盤石な堅牢さ\nを装備者に与える。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("創世緑杖", 25001, 25001, 5, 2, 2800, "回復強化＋20", 10, "迷宮内部で発⾒された杖の遺物。\n装備者に⼤樹の恵みを施し、治癒\n術の才覚を授けてくれる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("空命護珠", 25002, 25002, 5, 2, 2800, "ＨＰ＋20％", 10, "迷宮内部で発見された、指輪の形\nをした遺物。装備者を天空の護り\nで包み込み生命力を増幅させる。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("鋼装堅鎧", 25003, 25003, 5, 2, 2800, "20秒ごとに物理無効バリア展開（【守備】数×1000無効）", 10, "迷宮内部で発見された、鎧の形を\nした遺物。高度な技術で精錬され\nた鋼は、物理攻撃から身を護る。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("魔護霊装", 25004, 25004, 5, 2, 2800, "20秒ごとに魔法無効バリア展開（【守備】数×1000無効）", 10, "迷宮内部で発⾒された、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("緑護ノ盾輪", 25005, 25005, 5, 2, 2800, "継続ＨＰ回復（毎秒【守備】数×1000回復）", 10, "迷宮内部で発⾒された、腕輪の遺\n物。溢れ出す⾃然の⼒は装備者に\n生命の息吹をもたらす。", 0, 2);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("エレメントサージオーブ", 30001, 30001, 0, 0, 3800, "属性ボーナス＋100％", 10, "迷宮中枢部に隠された黎明の秘宝\nデータ保管のため属性の⼒の断⽚\nが封じられており、内なるアスト\nラルを著しく上昇させる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("瞬閃蝶の月飾り", 31001, 31001, 1, 3, 3800, "ＴＰ上昇＋15", 10, "迷宮の奥深くに眠っていた月を象\nる耳飾り。内部から発する魔力は\n装備者に蝶のような瞬麗さを授け\n活力加速を大きく上昇させる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("海宝華の煌盾", 31002, 31002, 1, 3, 3800, "ダメージを受けるたびＴＰ100回復（最大３回）", 10, "迷宮の奥深くに眠る宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("碧貴石の聖輪", 31003, 31003, 1, 3, 3800, "バトル開始時ＴＰ300回復", 10, "迷宮の奥深くに眠る聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("永天鏡の王笏", 31004, 31004, 1, 3, 3800, "ＴＰ消費軽減＋6", 10, "迷宮の奥深くに眠る、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("熾天使の翔靴", 31005, 31005, 1, 3, 3800, "重複可能の行動速度＋30％", 10, "迷宮の奥深くに眠る、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("風神の護風恩寵", 31006, 31006, 1, 5, 9999, "ＴＰ上昇＋100", 10, "迷宮最奥部に鎮祀されし神業の遺\n物。風を司る神々の恩寵が宿され\nており、装備者は無尽蔵の活力が\n疾風の如き速さで体を駆け巡る。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("幻獣飾テンタシオン", 32001, 32001, 2, 3, 3800, "状態異常命中＋60", 10, "迷宮の奥深くに眠る、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("深淵蝕杖マリディクト", 32002, 32002, 2, 3, 3800, "敵全体に呪い付与（毎秒【弱体】数×1500ダメージ）", 10, "迷宮の奥深くに眠る、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。", 1, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("滅刻凶刃ヴェノマス", 32003, 32003, 2, 3, 3800, "敵全体に毒付与（毎秒【弱体】数×1500ダメージ）", 10, "迷宮の奥深くに眠る、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("燦然御纏アノラ・ベイラ", 32004, 32004, 2, 3, 3800, "敵の物理/魔法攻撃力－30％", 10, "迷宮の奥深くに眠る、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("断壁鎖刃フラグメント", 32005, 32005, 2, 3, 3800, "敵の物理/魔法防御力－30％", 10, "迷宮の奥深くに眠る、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("霜影虚装アビスモナーク", 32006, 32006, 2, 5, 9999, "敵の物理/魔法与ダメ－25％\n敵の物理/魔法被ダメ＋25％", 10, "迷宮深層部に静眠せし虚影の装衣\n深淵から出でし蝕影は、存在全て\nを霜雪の如く凍てつかせ、抗う敵\nの剣盾両翼を虚壊させる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("リーサルハルバード", 33001, 33001, 3, 3, 3800, "敵の物理/魔法被クリダメ＋30％", 10, "迷宮の奥深くに眠る、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("オオトリノカタナ", 33002, 33002, 3, 3, 3800, "物理/魔法クリダメ＋30％", 10, "迷宮の奥深くに眠る、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("アースデストラクション", 33003, 33003, 3, 3, 3800, "通常攻撃強化＋90", 10, "迷宮の奥深くに眠る、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("ジャッジメントアロー", 33004, 33004, 3, 3, 3800, "物理/魔法クリティカル＋30％", 10, "迷宮の奥深くに眠る、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("ザ・ハーヴェスト", 33005, 33005, 3, 3, 3800, "ＨＰ吸収＋30", 10, "迷宮の奥深くに眠る、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("セレスティアルエイジ", 33006, 33006, 3, 5, 9999, "通常攻撃強化＋500\nＨＰ吸収＋50", 10, "迷宮最深部に残された久遠の遺物\n神代の大戦より聖騎士に脈々と受\nけ継がれ、装備者へ戦いの記憶と\nともに不滅の力を分け与える。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("極醒のアミュレット", 34001, 34001, 4, 3, 3800, "バフ強化＋90", 10, "迷宮の奥深くに眠る、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("断刃拒絶のアルギュロス", 34002, 34002, 4, 3, 3800, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", 10, "迷宮の奥深くに眠る、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("緋華栄華のグラナート", 34003, 34003, 4, 3, 3800, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", 10, "迷宮の奥深くに眠る、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("覇光帝龍のグローリア", 34004, 34004, 4, 3, 3800, "物理/魔法攻撃力＋60％", 10, "迷宮の奥深くに眠る、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("華舞玲瓏のハルナデシコ", 34005, 34005, 4, 3, 3800, "物理/魔法防御力＋30％", 10, "迷宮の奥深くに眠る、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("天戴冠のゼニスブレード", 34006, 34006, 4, 5, 9999, "バトル開始時、物理/魔法攻撃力アップフィールド展開\n（現在値＋50％）", 10, "迷宮深淵部に佇む歴戦無敗の⼤剣\n台座から剣を⼿にした者は天その\nものを冠すかの如き全能感を得て\n千軍を容易く退ける剛⼒を得る。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("妖精霊杖・癒光", 35001, 35001, 5, 3, 3800, "回復強化＋30", 10, "迷宮の奥深くに眠る、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("泉湧命環・豊穣", 35002, 35002, 5, 3, 3800, "ＨＰ＋30％", 10, "迷宮の奥深くに眠る、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("白光輝鎧・断壁", 35003, 35003, 5, 3, 3800, "20秒ごとに物理無効バリア展開（【守備】数×1500無効）", 10, "迷宮の奥深くに眠る、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("幽玄煌装・絶魔", 35004, 35004, 5, 3, 3800, "20秒ごとに魔法無効バリア展開（【守備】数×1500無効）", 10, "迷宮の奥深くに眠る、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("輪廻ノ翡翠珠輪", 35005, 35005, 5, 3, 3800, "継続ＨＰ回復（毎秒【守備】数×1500回復）", 10, "迷宮の奥深くに眠る、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("天命授環・伊邪那", 35006, 35006, 5, 5, 9999, "ＨＰ＋100％", 10, "迷宮最下層に供奉された伝承の環\n選ばれし者がひとたび⼿にすれば\n創造神話の如く、千万無量の⽣命\n⼒を齎すと云われる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("プリンセスフォース", 40001, 40001, 5, 1, 3200, "ＨＰ＋15％\n物理/魔法与ダメ＋15％", 10, "【美⾷殿】と共に迷宮を冒険する\nための遺物。王家の装備を精巧に\n模しており、味方の⾝体能⼒を⼤\nきく向上させてくれる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("アストラルブースト", 40002, 40002, 4, 1, 3200, "属性ボーナス＋50％", 10, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。\n３人のアストラルに呼応して、属\n性の力を増幅させてくれる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("サレンディアエンゲージ", 40003, 40003, 1, 1, 3200, "ショップの販売価格－10％", 10, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人が\n築いた信頼関係のおかげで、商品\nの価格を値引きしてもらえる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("ナイツオブフェイス", 40004, 40004, 3, 1, 3200, "物理/魔法クリダメ＋50％\n物理/魔法クリティカル－25％", 10, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。団長の愛⽤品を\n模し会⼼率と引き換えに、会⼼時\nの威⼒を⾼めてくれる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("ラビリンスオーソリティ", 40005, 40005, 2, 1, 3200, "挑戦開始時にラビリスタが加入", 10, "【ラビリンス】と共に迷宮を冒険\nするための遺物。迷宮女王はその\n権能でナイトがどこに居ようとも\n即座に参戦してくれる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("プリンセスエンチャント", 50001, 50001, 4, 1, 3200, "物理/魔法攻撃力アップ（加入キャラ数×4％、最大100％）", 10, "【美⾷殿】と共に迷宮を冒険する\nための遺物。冒険の道中で絆を結\nべば結ぶほどに、味⽅の攻撃⼒を\n⾼めてくれる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("トゥインクルブレス", 50002, 50002, 5, 1, 3200, "ボスバトルを除いて一度だけバトル敗北を免除", 10, "【トゥインクルウィッシュ】と共\nに迷宮を冒険するための遺物。ギ\nルドマスターの諦めない心が、味\n方を一度だけ敗北から守る。", 1, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("サレンディアノーブル", 50003, 50003, 3, 1, 3200, "通常攻撃強化アップ（アルファルピ所持数×0.01、最大100）", 10, "【サレンディア救護院】と共に迷\n宮を冒険するための遺物。主人の\n気品を漂わせる剣は、築いた富を\n⼒に変転させる効果を持つ。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("レジーナガーラ", 50004, 50004, 2, 1, 3200, "敵の物理/魔法被ダメ＋25％\n味方の物理/魔法被ダメ＋25％", 10, "【王宮騎士団】と共に迷宮を冒険\nするための遺物。副団長の催す闘\n争の宴は、敵味⽅が受けるダメー\nジをみな悉く増加させる。", 0, 3);
INSERT INTO `v1_7591f6432f7a73205ccb01906562a54791338d6ba733830ce738fe4a158f69d0` VALUES ("マスターオブラビリンス", 50005, 50005, 1, 1, 3200, "挑戦開始時にランダムな★1の迷宮遺物を１個獲得", 10, "【ラビリンス】と共に迷宮を冒険\nするための遺物。マスターの⼒を\n冠した剣は、開発者権限の⼀部を\n解放し、遺物を即座に獲得する。", 0, 3);
