INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51001, 2, 2, "ペコリーヌの\n満腹ツインおにぎり", 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 0, "500マイルを獲得する。", 10, 1, "ペコリーヌの満腹ツインおにぎり", "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 1, 500, 0, 52001);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51002, 2, 30001, "コッコロの\nゆるりと休憩サラダ", 3, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 1, 0, "500マイルを獲得する。", 10, 2, "コッコロのゆるりと休憩サラダ", "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", 1, 500, 0, 52002);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51003, 1, 20004, "キャルの\n絶品五つ星バーガー", 2, "このターンに振るダイスの目が必ず「5」になる。", 1, 0, "平凡な未鑑定トレジャーを1個獲得する。", 10, 1, "キャルの絶品五つ星バーガー", "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", 1, 500, 0, 52003);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51004, 4, 20001, "リリのコツコツ\nまい進かしわ餅", 2, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 1, 0, "800マイルを獲得する。", 10, 3, "リリのコツコツまい進かしわ餅", "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", 3, 500, 0, 52004);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51005, 1, 1, "クリアの彩り華やか\nバラエティ弁当", 4, "ランダムなイベントを発生させる。", 3, 0, "次にダイスを振った際、1マス多く進める。", 11, 3, "クリアの彩り華やかバラエティ弁当", "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", 0, 500, 0, 52005);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51006, 4, 20002, "プレシアの食いしん坊\nぶーぶー肉まん", 2, "このターンに振るダイスの目が「4・5・6」のみになる。", 1, 0, "800マイルを獲得する。", 10, 1, "プレシアの食いしん坊ぶーぶー肉まん", "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", 1, 500, 0, 52006);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51007, 3, 1, "カヤとイノリの\n食欲倍増ケバブサンド", 7, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", 1, 0, "福引券を1枚獲得する。", 10, 1, "カヤとイノリの食欲倍増ケバブサンド", "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", 2, 500, 0, 52007);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51008, 2, 200, "タマキの金運招く\nレインボーたい焼き", 5, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 2, 1, "500マイルを獲得する。", 10, 2, "タマキの金運招くレインボーたい焼き", "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", 0, 500, 0, 52008);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51009, 1, 1, "クローチェの\nフルパワーポテトサラダ", 7, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", 1, 0, "平凡な未鑑定トレジャーを1個獲得する。", 10, 2, "クローチェのフルパワーポテトサラダ", "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", 2, 500, 1, 52009);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51010, 2, 1, "アヤネとクルミの\n青空ピクニックセット", 6, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 2, 2, "500マイルを獲得する。", 10, 3, "アヤネとクルミの青空ピクニックセット", "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", 0, 500, 0, 52010);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51011, 3, 1, "リマの不思議な\nスイートアップル", 7, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", 1, 0, "福引券を1枚獲得する。", 10, 3, "リマの不思議なスイートアップル", "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", 2, 500, 0, 52011);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51012, 1, 30002, "ニノンのオリジナル\n一二三寿司", 3, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 1, 0, "平凡な未鑑定トレジャーを1個獲得する。", 10, 2, "ニノンのオリジナル一二三寿司", "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", 1, 500, 0, 52012);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51013, 4, 2, "クレジッタの開運\nお菓子盛り合わせ", 8, "RANK2のマイルショップを開く。", 3, 0, "800マイルを獲得する。", 10, 3, "クレジッタの開運お菓子盛り合わせ", "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", 0, 500, 0, 52013);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51014, 1, 1, "リノの\n「時は金時」かき氷", 9, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, 0, "次にダイスを振った際、1マス多く進める。", 11, 2, "リノの「時は金時」かき氷", "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 2, 500, 0, 52014);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51015, 1, 2, "シズルの愛情\n盛り盛りハンバーグ", 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 0, "次にダイスを振った際、1マス多く進める。", 11, 1, "シズルの愛情盛り盛りハンバーグ", "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", 1, 500, 0, 52015);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51016, 0, 20005, "アオイの疾走\nサンドイッチ", 2, "このターンに振るダイスの目が必ず「6」になる。", 1, 0, "なし", 0, 3, "アオイの疾走サンドイッチ", "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 1, 500, 0, 52016);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51017, 5, 1, "クウカの緊縛\n触手活け造り", 2, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 1, 0, "なし", 10, 3, "クウカの緊縛触手活け造り", "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 2, 500, 1, 52017);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51018, 6, 20006, "ランファの世話焼き三昧肉じゃが", 2, "このターンに振るダイスの目が必ず「3」になる。", 1, 0, "1000マイルを獲得する。", 10, 1, "ランファの世話焼き三昧肉じゃが", "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 1, 500, 0, 52018);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (0, 0, 20007, "ワカナの節約腹八分目カレー", 2, "このターンに振るダイスの目が必ず「8」になる。", 1, 0, "なし", 0, 1, "ワカナの節約腹八分目カレー", "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 1, 500, 0, 52019);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51014, 1, 1, "リノの\n「時は金時」かき氷", 9, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 0, "次にダイスを振った際、1マス多く進める。", 11, 2, "リノの「時は金時」かき氷", "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 2, 500, 0, 52020);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51021, 7, 2, "カスミの真実見抜く\nボンボン・ショコラ", 9, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, 0, "	平凡な未鑑定トレジャーを2個獲得する。", 10, 3, "カスミの真実見抜くボンボン・ショコラ", "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", 3, 500, 0, 52021);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51022, 0, 20008, "アキノのゲヘニアン\nチキンプレート", 2, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 1, 0, "なし", 0, 1, "アキノのゲヘニアンチキンプレート", "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", 3, 500, 0, 52022);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51023, 1, 1, "ミミのコロコロ\nお手玉おにぎり", 7, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", 1, 0, "平凡な未鑑定トレジャーを1個獲得する。", 10, 1, "ミミのコロコロお手玉おにぎり", "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 2, 500, 1, 52023);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51024, 3, 2, "アユミの隠密\nガトーインビジブル", 8, "RANK2のマイルショップを開く。", 3, 0, "次にダイスを振った際、3マス多く進める。", 11, 3, "アユミの隠密ガトーインビジブル", "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 0, 500, 0, 52024);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51025, 0, 3, "アネモネの\n花爛漫ステーキサラダ", 9, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 2, 0, "なし", 0, 3, "アネモネの花爛漫ステーキサラダ", "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 1, 500, 0, 52025);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51026, 8, 1, "アンナの\n煉獄蕃茄煮混沌卵", 4, "ランダムなイベントを発生させる。", 3, 0, "福引券を2枚獲得する。", 10, 1, "アンナの煉獄蕃茄煮混沌卵", "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 0, 500, 0, 52026);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51027, 0, 20004, "ユイの\n栄養満点まごころ弁当", 2, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 1, 0, "なし", 0, 3, "ユイの栄養満点まごころ弁当", "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", 2, 500, 0, 52027);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51028, 9, 30003, "リンの\nナッツ入りぐうたらあんパン", 2, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 1, 0, "なし", 10, 3, "リンのナッツ入りぐうたらあんパン", "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 1, 500, 0, 52028);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51029, 0, 30004, "シェフィの\n氷晶煌めく手作りアイス", 3, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 1, 0, "なし", 0, 2, "シェフィの氷晶煌めく手作りアイス", "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 1, 500, 0, 52029);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51030, 0, 1, "ミソラの\n気まぐれ味噌ラーメン★", 12, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 3, 0, "なし", 0, 1, "ミソラの気まぐれ味噌ラーメン★", "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 3, 500, 0, 52030);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51031, 0, 20009, "イロハの\n最強クワガタゼリー（人間用）", 2, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 1, 0, "なし", 0, 3, "イロハの最強クワガタゼリー（人間用）", "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 1, 500, 0, 52031);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51032, 3, 4, "グレイスの\n目ん玉飛び出るカップケーキ", 9, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 2, 0, "次にダイスを振った際、3マス多く進める。", 11, 3, "グレイスの目ん玉飛び出るカップケーキ", "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 2, 500, 0, 52032);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51033, 0, 3, "ソノの今日は特別ご褒美オムライス", 8, "RANK3のマイルショップを開く。", 3, 0, "なし", 0, 1, "ソノの今日は特別ご褒美オムライス", "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", 0, 500, 0, 52033);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51034, 1, 1, "ジータの\nグリルド・グラッジチャンク", 9, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 0, "平凡な未鑑定トレジャーを1個獲得する。", 10, 1, "ジータのグリルド・グラッジチャンク", "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", 2, 500, 0, 52034);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51035, 10, 1, "ルイズマリーの\n成功しすぎてるフライ", 4, "ランダムなイベントを発生させる。", 3, 0, "貴重な未鑑定トレジャーを1個獲得する。", 10, 1, "ルイズマリーの成功しすぎてるフライ", "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", 0, 500, 0, 52035);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51036, 0, 1, "ホウオウの\n食べる権利をあげます！饅頭", 7, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", 1, 0, "なし", 0, 3, "ホウオウの食べる権利をあげます！饅頭", "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。", 3, 500, 1, 52036);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51037, 11, 1, "魔法少女\nナナカちゃんウエハース", 6, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", 2, 1, "平凡な未鑑定トレジャーを3個獲得する。", 10, 3, "魔法少女ナナカちゃんウエハース", "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。", 0, 500, 0, 52037);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51038, 3, 2, "アリサがお届け♪\nリノセウスの包み焼き", 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 0, "福引券を1枚獲得する。", 10, 1, "アリサがお届け♪リノセウスの包み焼き", "アリサ特製の包み焼き。伝承の怪物とよく似た\n魔物を材料にしているため、いつしかその怪物と\n同じ名で呼ばれるようになったという。鋭い鎌と\n火傷に気を付けながら、熱々を召し上がれ。", 1, 500, 0, 52038);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51039, 0, 2, "イリヤの†ブラッディ†\nトマトジュース", 12, "2ターンの間、すべての「マイルマス」をランク3の「ショップマス」に変化させる。", 3, 0, "なし", 0, 2, "イリヤの†ブラッディ†トマトジュース", "イリヤの愛飲する赤く鮮やかなトマトジュース。\n伝説の吸血鬼に相応しいクオリティを、と求めた\n末に辿り着いた究極の逸品。しかしお値段も相応\nに張るため、特別な時にしか飲めないらしい。", 2, 500, 0, 52039);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51040, 2, 2, "マツリの活力源\n～騎士団遠征プレート～", 9, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, 0, "次にダイスを振った際、2マス多く進める。", 11, 1, "マツリの活力源～騎士団遠征プレート～", "【王宮騎士団】遠征時の定番メニューにマツリが\nアレンジを加え調理したプレート。苦手な野菜を\nなくしてお肉倍増を目論んだのが配膳前にバレ、\nジュンの指導できちんと野菜も入れさせられた。", 3, 500, 0, 52040);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51041, 1, 1, "ヴァンピィちゃんの\nさいきょースープ", 7, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「7」に、次のターンに振るダイスの目を必ず「2」にする。", 1, 0, "平凡な未鑑定トレジャーを1個獲得する。", 10, 1, "ヴァンピィちゃんのさいきょースープ", "ヴァンピィがよく振舞ってくれる特製スープ。\nどうやら家庭料理のようで、素朴な味わいが\n人々を郷愁へと誘う。昔誰かと並んで一緒に\n肉団子を作ったような…とはヴァンピィの言葉。", 2, 500, 1, 52041);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51042, 0, 20002, "アイラのみんなで\n飲みたい赤麦しゅわ", 2, "ダイスを2回振るまでの間、出る目が「4・5・6」のみになる", 1, 0, "なし", 0, 2, "アイラのみんなで飲みたい赤麦しゅわ", "アイラが愛する赤麦しゅわ。彼女の熱い要望で\n酩酊感ゼロの子供向けも生産開始した。曰くこの\n美味しさを広めるためとのことだが、本当は単に\n老若男女皆で楽しく飲みたいだけかもしれない。", 1, 500, 0, 52042);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (51043, 12, 1, "レイの釣りたて\n鮮魚七輪塩焼き", 2, "【上書き不可】次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で貴重な未鑑定トレジャーを3個獲得する。", 1, 0, "なし", 10, 1, "レイの釣りたて鮮魚七輪塩焼き", "レイが釣った魚の塩焼き。試行錯誤の末に理想の\n塩と火の加減にたどり着いた。調理は真剣勝負で\n釣りと同様、己の心との戦いだという。レイも\n時々、集中しすぎて仲間に心配されているとか。", 2, 500, 1, 52043);
