INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES ("500マイルを獲得する。", "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", 2, 500, 2, 0, 5, 200, 51008, 10, 52008, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 0, "タマキの金運招く\nレインボーたい焼き", 2, "タマキの金運招くレインボーたい焼き", 1);
