INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (1, "なし", "リンのナッツ入りぐうたらあんパン", 2, "リンの\nナッツ入りぐうたらあんパン", 1, 30003, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 10, 9, 0, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 500, 52028, 3, 0, 51028);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (1, "なし", "シェフィの氷晶煌めく手作りアイス", 3, "シェフィの\n氷晶煌めく手作りアイス", 1, 30004, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 0, 0, 0, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 500, 52029, 2, 0, 51029);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (3, "なし", "ミソラの気まぐれ味噌ラーメン★", 12, "ミソラの\n気まぐれ味噌ラーメン★", 3, 1, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 0, 0, 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 500, 52030, 1, 0, 51030);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (1, "なし", "イロハの最強クワガタゼリー（人間用）", 2, "イロハの\n最強クワガタゼリー（人間用）", 1, 20009, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 0, 0, 0, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 500, 52031, 3, 0, 51031);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (2, "次にダイスを振った際、3マス多く進める。", "グレイスの目ん玉飛び出るカップケーキ", 9, "グレイスの\n目ん玉飛び出るカップケーキ", 2, 4, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 11, 3, 0, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 500, 52032, 3, 0, 51032);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (0, "なし", "ソノの今日は特別ご褒美オムライス", 8, "ソノの今日は特別ご褒美オムライス", 3, 3, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", 0, 0, 0, "RANK3のマイルショップを開く。", 500, 52033, 1, 0, 51033);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (2, "平凡な未鑑定トレジャーを1個獲得する。", "ジータのグリルド・グラッジチャンク", 9, "ジータの\nグリルド・グラッジチャンク", 2, 1, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", 10, 1, 0, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 500, 52034, 1, 0, 51034);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (0, "貴重な未鑑定トレジャーを1個獲得する。", "ルイズマリーの成功しすぎてるフライ", 4, "ルイズマリーの\n成功しすぎてるフライ", 3, 1, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", 10, 10, 0, "ランダムなイベントを発生させる。", 500, 52035, 1, 0, 51035);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (3, "なし", "ホウオウの食べる権利をあげます！饅頭", 7, "ホウオウの\n食べる権利をあげます！饅頭", 1, 1, "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。", 0, 0, 0, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", 500, 52036, 3, 1, 51036);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (0, "平凡な未鑑定トレジャーを3個獲得する。", "魔法少女ナナカちゃんウエハース", 6, "魔法少女\nナナカちゃんウエハース", 2, 1, "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。", 10, 11, 1, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", 500, 52037, 3, 0, 51037);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (1, "福引券を1枚獲得する。", "アリサがお届け♪リノセウスの包み焼き", 1, "アリサがお届け♪\nリノセウスの包み焼き", 1, 2, "アリサ特製の包み焼き。伝承の怪物とよく似た\n魔物を材料にしているため、いつしかその怪物と\n同じ名で呼ばれるようになったという。鋭い鎌と\n火傷に気を付けながら、熱々を召し上がれ。", 10, 3, 0, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 500, 52038, 1, 0, 51038);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (2, "なし", "イリヤの†ブラッディ†トマトジュース", 12, "イリヤの†ブラッディ†\nトマトジュース", 3, 2, "イリヤの愛飲する赤く鮮やかなトマトジュース。\n伝説の吸血鬼に相応しいクオリティを、と求めた\n末に辿り着いた究極の逸品。しかしお値段も相応\nに張るため、特別な時にしか飲めないらしい。", 0, 0, 0, "2ターンの間、すべての「マイルマス」をランク3の「ショップマス」に変化させる。", 500, 52039, 2, 0, 51039);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (3, "次にダイスを振った際、2マス多く進める。", "マツリの活力源～騎士団遠征プレート～", 9, "マツリの活力源\n～騎士団遠征プレート～", 2, 2, "【王宮騎士団】遠征時の定番メニューにマツリが\nアレンジを加え調理したプレート。苦手な野菜を\nなくしてお肉倍増を目論んだのが配膳前にバレ、\nジュンの指導できちんと野菜も入れさせられた。", 11, 2, 0, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 500, 52040, 1, 0, 51040);
INSERT INTO `v1_76cdaef23056c852d67c54b3c416773ae4bd64658c32b492f05a519e2b27be03` VALUES (2, "平凡な未鑑定トレジャーを1個獲得する。", "ヴァンピィちゃんのさいきょースープ", 7, "ヴァンピィちゃんの\nさいきょースープ", 1, 1, "ヴァンピィがよく振舞ってくれる特製スープ。\nどうやら家庭料理のようで、素朴な味わいが\n人々を郷愁へと誘う。昔誰かと並んで一緒に\n肉団子を作ったような…とはヴァンピィの言葉。", 10, 1, 0, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「7」に、次のターンに振るダイスの目を必ず「2」にする。", 500, 52041, 1, 1, 51041);
