INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.029, 1, 122601, 160, 110, 1, 1, 101, 0, 0, 1.5, 105, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.014, 0, 122401, 120, 105, 2, 2, 102, 1, 0, 2.5, 111, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.017, 0, 122501, 350, 110, 3, 3, 103, 0, 1, 2.5, 105, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.016, 0, 107501, 130, 110, 4, 4, 104, 1, 0, 1.5, 105, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.014, 1, 107601, 160, 110, 5, 5, 105, 0, 0, 2.5, 105, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.029, 0, 107801, 255, 120, 6, 6, 106, 0, 1, 3.5, 103, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.013, 1, 110601, 160, 120, 7, 7, 107, 0, 0, 3.5, 103, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.014, 0, 110401, 350, 110, 8, 8, 108, 0, 1, 2.5, 103, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.012, 0, 110501, 130, 105, 9, 9, 109, 1, 0, 1.5, 100, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.014, 1, 115701, 200, 105, 10, 10, 110, 0, 0, 2.5, 103, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.071, 0, 113601, 350, 105, 11, 11, 111, 0, 1, 2.5, 103, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.014, 0, 107901, 130, 120, 12, 12, 112, 1, 0, 1.5, 103, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.022, 1, 108001, 200, 110, 13, 13, 113, 0, 0, 1.5, 105, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.031, 1, 110301, 160, 110, 14, 14, 114, 0, 0, 2.5, 108, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.036, 0, 107701, 130, 110, 15, 15, 115, 1, 0, 0.5, 105, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.048, 0, 113101, 350, 110, 16, 16, 116, 0, 1, 3.5, 95, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.043, 0, 113301, 130, 105, 17, 17, 117, 1, 0, 1.5, 105, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.018, 0, 113201, 130, 105, 18, 18, 118, 1, 0, 1.5, 95, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.021, 1, 117001, 160, 110, 19, 19, 119, 0, 0, 2.5, 108, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.036, 0, 113501, 255, 110, 20, 20, 120, 0, 1, 2.5, 105, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.02, 0, 113401, 350, 105, 21, 21, 121, 0, 1, 2.5, 105, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.018, 0, 110101, 130, 110, 22, 22, 122, 1, 0, 2.5, 105, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.03, 1, 110001, 160, 105, 23, 23, 123, 0, 0, 3.5, 111, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.027, 0, 117201, 130, 110, 24, 24, 124, 1, 0, 1.5, 103, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.02, 1, 117301, 160, 105, 25, 25, 125, 0, 0, 3.5, 108, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.04, 0, 117401, 350, 110, 26, 26, 126, 0, 1, 2.5, 105, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.009, 0, 117101, 350, 110, 27, 27, 127, 0, 1, 3.5, 105, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (1, 0.022, 0, 122901, 255, 120, 28, 28, 128, 0, 1, 3.5, 105, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (3, 0.028, 0, 122801, 130, 110, 29, 29, 129, 1, 0, 2.5, 105, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？");
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES (2, 0.023, 1, 122701, 130, 105, 30, 30, 130, 0, 0, 3.5, 108, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。");
