INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 160, 122601, 105, 0.029, 101, 0, 1, 1.5, 110, 1, 1, 0, 1);
