INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 350, 122501, 105, 0.017, 103, 1, 3, 2.5, 110, 3, 0, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 130, 107501, 105, 0.016, 104, 0, 4, 1.5, 110, 4, 0, 1, 3);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 160, 107601, 105, 0.014, 105, 0, 5, 2.5, 110, 5, 1, 0, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 255, 107801, 103, 0.029, 106, 1, 6, 3.5, 120, 6, 0, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 160, 110601, 103, 0.013, 107, 0, 7, 3.5, 120, 7, 1, 0, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 350, 110401, 103, 0.014, 108, 1, 8, 2.5, 110, 8, 0, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 130, 110501, 100, 0.012, 109, 0, 9, 1.5, 105, 9, 0, 1, 3);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 200, 115701, 103, 0.014, 110, 0, 10, 2.5, 105, 10, 1, 0, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 350, 113601, 103, 0.071, 111, 1, 11, 2.5, 105, 11, 0, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 130, 107901, 103, 0.014, 112, 0, 12, 1.5, 120, 12, 0, 1, 3);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 200, 108001, 105, 0.022, 113, 0, 13, 1.5, 110, 13, 1, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 160, 110301, 108, 0.031, 114, 0, 14, 2.5, 110, 14, 1, 0, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 130, 107701, 105, 0.036, 115, 0, 15, 0.5, 110, 15, 0, 1, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 350, 113101, 95, 0.048, 116, 1, 16, 3.5, 110, 16, 0, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 130, 113301, 105, 0.043, 117, 0, 17, 1.5, 105, 17, 0, 1, 3);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 130, 113201, 95, 0.018, 118, 0, 18, 1.5, 105, 18, 0, 1, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 160, 117001, 108, 0.021, 119, 0, 19, 2.5, 110, 19, 1, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 255, 113501, 105, 0.036, 120, 1, 20, 2.5, 110, 20, 0, 0, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 350, 113401, 105, 0.02, 121, 1, 21, 2.5, 105, 21, 0, 0, 3);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 130, 110101, 105, 0.018, 122, 0, 22, 2.5, 110, 22, 0, 1, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 160, 110001, 111, 0.03, 123, 0, 23, 3.5, 105, 23, 1, 0, 3);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 130, 117201, 103, 0.027, 124, 0, 24, 1.5, 110, 24, 0, 1, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 160, 117301, 108, 0.02, 125, 0, 25, 3.5, 105, 25, 1, 0, 2);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 350, 117401, 105, 0.04, 126, 1, 26, 2.5, 110, 26, 0, 0, 3);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 350, 117101, 105, 0.009, 127, 1, 27, 3.5, 110, 27, 0, 0, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 255, 122901, 105, 0.022, 128, 1, 28, 3.5, 120, 28, 0, 0, 1);
INSERT INTO `v1_77030360d327711acfa55ae647052fcc086c9421a2ead63c2fcae45a9ff837bf` VALUES ("無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 130, 122801, 105, 0.028, 129, 0, 29, 2.5, 110, 29, 0, 1, 3);
