INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110001, 1, 1, 1, "「プラチナ」とも呼ばれる金属は「金」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110002, 1, 1, 1, "ローマ数字で「1」を表すときに使うアルファベットは「I」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110003, 1, 1, 1, "英語で「トルネード」とは「台風」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110004, 1, 1, 1, "「未確認飛行物体」の略称は「USO」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110005, 1, 1, 1, "風邪薬として使われる漢方薬の「葛根湯」の読みは「かっこんとう」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110006, 1, 1, 1, "「知能指数」はアルファベット2文字で「ID」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110007, 1, 1, 1, "「横断歩道」は英語で「ストライプゾーン」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110008, 1, 1, 1, "三日月の形をしていることから名前がついたパンは「クロワッサン」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110009, 1, 1, 1, "英語でアクアリウムといえば、「美術館」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110011, 1, 1, 1, "和歌の数え方は「一句、二句」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110014, 1, 1, 1, "「きみ」や「あなた」など、相手のことを表す言葉を「一人称」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110015, 1, 1, 1, "仕事の途中で無駄話などをしてサボることを「油を売る」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110016, 1, 1, 1, "エレベーターで「B」といえば「駐車場」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110017, 1, 1, 1, "同じことの繰り返しでらちがあかないことを「いたちごっこ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110019, 1, 1, 1, "「おかんむり」とは「寝ている」状態を表す言葉である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110020, 1, 1, 1, "実際には役に立たないもののたとえを、「絵に描いた団子」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110021, 1, 1, 1, "人目につかないところで力を尽くす人を、「縁の下の力持ち」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110022, 1, 1, 1, "自分の悪行が自分に返ってくることを、「身から出たぼろ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110024, 1, 1, 1, "俳句の季語で、「月見」といえば季節は「秋」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110026, 1, 1, 1, "「慎重」の対義語は「軽率」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110027, 1, 1, 1, "とある色の名前や、「憂鬱な」という意味を持つ英単語は「blue」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110028, 1, 1, 1, "とある色の名前や、「未熟な」という意味を持つ英単語は「red」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110029, 1, 1, 1, "「日」「月」「木」など、物の形を模って作られた漢字を「象形文字」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110030, 1, 1, 1, "ことわざで、溺れる者がつかむといわれるのは「糸」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110031, 1, 1, 1, "「のれんに腕押し」とは、「効果がないこと」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110033, 1, 1, 1, "性格がはっきりしていることを「腹を割ったよう」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110034, 1, 1, 1, "我慢が無駄になることを、ことわざで「石の上にも三年」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110036, 1, 1, 1, "お酒に酔ってふらふら歩くことを、鳥にたとえて「すずめ足」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110037, 1, 1, 1, "英語のアルファベットは、全部で「26文字」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110039, 1, 1, 1, "もともとはヘビなどの抜け殻に由来する、追うべき人物が消えて誰もいない様子を「もぬけの殻」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110040, 1, 1, 1, "権力を持つ親の恩恵を受けることを、俗に「親の黒光り」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110042, 1, 1, 1, "もともと優れていたものは、多少傷んでも本来の価値を失わないという意味のことわざは「腐っても鯛」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110043, 1, 1, 1, "危険だとわかっていることを行うことは「危ない橋を渡る」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110044, 1, 1, 1, "「春」を英語で言うと「オータム」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110045, 1, 1, 1, "「虎」を英語で言うと「タイガー」である ");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110046, 1, 1, 1, "「山」を英語で言うと「リバー」である ");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110049, 1, 1, 1, "恥ずかしいことのたとえは「穴があったら入りたい」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110050, 1, 1, 1, "生き生きとしていることのたとえは「餌を得た魚」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110051, 1, 1, 1, "急ぐときほど無難な方法がよいことは「急がば回れ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110054, 1, 1, 1, "元気をなくして沈み込む様子を意味する四字熟語は「意気消沈」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110055, 1, 1, 1, "お互いに気持ちが通じ合ってしっくりくる様子を意味する四字熟語は「意気消沈」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110056, 1, 1, 1, "俗に、非常に忙しい時に「借りたい」といわれるものは「猫の手」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110058, 1, 1, 1, "「くにくのさく」を漢字で書くと「狗肉の策」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110059, 1, 1, 1, "「湿布」。この漢字の読み方は「しっぷ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1110060, 1, 1, 1, "英語で「ウォーターメロン」といえば、「スイカ」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1111001, 1, 1, 1, "獣人族の読みは「ビーストぞく」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1111002, 1, 1, 1, "ペコリーヌがギルドマスターを務める【美食殿】の読みは「ビストロクラン」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1111003, 1, 1, 1, "七冠の読みは「セブンクラウンズ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1111004, 1, 1, 1, "イベント名「リトル・サマー・メモリーズ　渚でみつけた小さな幸せ」の「リトル・サマー・メモリーズ」を和訳すると「小さな夏の思い出」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1111005, 1, 1, 1, "プリンセスを和訳すると「女王」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120003, 2, 1, 1, "二字熟語で1文字目が音読み、2文字目が訓読みであることを「重箱読み」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120004, 2, 1, 1, "「役職を利用して私服を肥やす」この文中に間違った熟語はない");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120006, 2, 1, 1, "四字熟語の「いくどうおん」。漢字にすると「異句同音」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120007, 2, 1, 1, "四字熟語の「おんこちしん」。漢字にすると「温固知新」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120008, 2, 1, 1, "「提灯につりがね」とは、「似ているようで別物」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120010, 2, 1, 1, "四字熟語の「海千山千」とは、「長生きな人」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120011, 2, 1, 1, "日本刀の数え方は「一振、二振」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120013, 2, 1, 1, "かな文字の中で、濁音がつくものは「15個」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120014, 2, 1, 1, "漢字で「鯖」と書く魚は「さば」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120016, 2, 1, 1, "本性を隠しておとなしく見せかけることを「化けの皮をかぶる」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120017, 2, 1, 1, "「睦月」とは「旧暦一月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120019, 2, 1, 1, "「弥生」とは「旧暦三月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120020, 2, 1, 1, "「卯月」とは「旧暦二月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120021, 2, 1, 1, "「皐月」とは「旧暦五月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120022, 2, 1, 1, "「水無月」とは「旧暦七月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120024, 2, 1, 1, "「葉月」とは「旧暦八月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120026, 2, 1, 1, "「神無月」とは「旧暦六月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120027, 2, 1, 1, "「霜月」とは「旧暦十一月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120030, 2, 1, 1, "「かぶとを脱ぐ」とは、「敗北を認めて降参すること」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120031, 2, 1, 1, "四字熟語の「四面楚歌」とは、「仲の悪い者同士が協力すること」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120032, 2, 1, 1, "起きてしまったことは取り返しがつかないということを、「覆水盆に返らず」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120033, 2, 1, 1, "「あくび」は漢字で「欠伸」と書く");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120034, 2, 1, 1, "漢字で「案山子」と書いて「やじろべえ」と読む");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120035, 2, 1, 1, "漢字で「胡桃」と書いて「くるみ」と読む");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120036, 2, 1, 1, "漢字で「土筆」と書く植物は「つくし」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120037, 2, 1, 1, "漢字では「鳳梨」と書く果物は「ラフランス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120038, 2, 1, 1, "病院のベッドの数え方は「一台、二台」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120039, 2, 1, 1, "「末端」の対義語は「根本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120040, 2, 1, 1, "とても嬉しい気持ちのたとえを、「夢にものぼる心地」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120041, 2, 1, 1, "「一」「上」「末」など、筆画の位置関係により表した漢字を「象形文字」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120043, 2, 1, 1, "息がピタリと合うことを「阿吽の呼吸」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120044, 2, 1, 1, "旅行添乗員とオーケストラの指揮者に共通する英語は「コンダクター」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120046, 2, 1, 1, "物事の理解が非常に早いことを、「目から口に抜ける」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120047, 2, 1, 1, "「煮え湯を飲まされる」とは、「敵からひどい目にあわされる」ことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120048, 2, 1, 1, "英語の月の略称で、「Jan.」は「1月」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120049, 2, 1, 1, "英語の月の略称で、「Jun.」は「7月」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120051, 2, 1, 1, "思い通りにならずにもどかしいことを、ことわざで「二階から目薬」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120052, 2, 1, 1, "「ムカデ」を漢字で書くときに使用する漢数字は「十」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120054, 2, 1, 1, "一つのことが巡り巡って意外なところに影響を及ぼすことを「風が吹けば医者が儲かる」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120055, 2, 1, 1, "四字熟語「いちねんほっき」を漢字にすると「一年発起」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120057, 2, 1, 1, "「お酒を飲めない人」を意味する言葉は「上戸」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120058, 2, 1, 1, "「うっかり間違い」は、和製英語で「ケアレスミス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120060, 2, 1, 1, "目をかけていた者に裏切られることは、「飼い犬に腕をかまれる」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1120061, 2, 1, 1, "「拝啓」に続く結語は「敬具」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1121001, 2, 1, 1, "ラビリスタがギルドマスターを務める【ラビリンス】を英訳すると「愛の巣」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1121002, 2, 1, 1, "儀装束の読み方は「ぎしょうぞく」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1121003, 2, 1, 1, "変貌大妃の読み方は「メタモルレグナント」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1121004, 2, 1, 1, "跳躍王の読み方は「キングワープ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1121005, 2, 1, 1, "覇瞳皇帝の読み方は「エンペラーインサイト」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130002, 3, 1, 1, "冬に欠かせない「こたつ」を漢字で書いたとき、2文字に共通する部首は「ひへん」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130003, 3, 1, 1, "「立て板に水」の反対語は「横板に風」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130005, 3, 1, 1, "俳句で「小春日和」といえば、「春」の季語である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130007, 3, 1, 1, "俳句で「ラグビー」といえば、「冬」の季語である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130008, 3, 1, 1, "俳句で「納豆」といえば、「夏」の季語である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130010, 3, 1, 1, "プールの数え方は「一面、二面」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130011, 3, 1, 1, "灯台の数え方は「一灯、二灯」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130012, 3, 1, 1, "正しい道理を説いても効き目のないことを「泣く子と地頭には勝てない」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130013, 3, 1, 1, "「峠」「辻」などのような、日本で作られた漢字を「国字」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130014, 3, 1, 1, "商売は長く続けるのがよいということを「商いは牛のなみだ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130015, 3, 1, 1, "英語で「Adam's Apple」という体の部分は「鼻の頭」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130016, 3, 1, 1, "調子に乗って他人に便乗することを「尻馬に乗る」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130018, 3, 1, 1, "前の人の失敗を繰り返すことを「轍を踏む」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130020, 3, 1, 1, "「食指が動く」というときの食指とはもともと「親指」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130021, 3, 1, 1, "秘密を暴く「すっぱ抜く」、この「すっぱ」とは「飛脚」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130022, 3, 1, 1, "「わかさぎ」は漢字で「公魚」と書く");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130023, 3, 1, 1, "ロシア語で使われるアルファベットは全部で「26文字」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130024, 3, 1, 1, "無理に押し通すことをたとえて、「横紙を破る」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130028, 3, 1, 1, "五七五の定型に縛られずに作られる俳句を「自然律俳句」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130029, 3, 1, 1, "「出納」の読みは「でのう」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130030, 3, 1, 1, "「アヒル」を漢字で書くと「家鴨」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130031, 3, 1, 1, "いったん悪に手を染めたのなら、最後まで貫くという意味の言葉は、「毒を食らわば骨まで」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130032, 3, 1, 1, "「腹が据わる」とは、「物事に動じないこと」を示す慣用句である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130033, 3, 1, 1, "古語の「をかし」とは、「趣がある」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130034, 3, 1, 1, "漢字の床・庄・序に共通する部首は「まだれ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130036, 3, 1, 1, "故事成語の「蛇足」とは「無駄」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130037, 3, 1, 1, "「最初から最後まで筋が通っている」という意味の四字熟語は「清廉潔白」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130038, 3, 1, 1, "古文に出て来る「十六夜」の読みは「いざよい」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130039, 3, 1, 1, "英語で「クレセントムーン」といえば、「満月」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130041, 3, 1, 1, "ことわざで、相手に誘いをかける時には向け、邪魔をするときには差すものは「水」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130043, 3, 1, 1, "英語で「フィンガープリント」といえば、「指紋」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130045, 3, 1, 1, "英語では「ファイヤーワークス」といえば、「消防士」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130046, 3, 1, 1, "四字熟語の「しくはっく」を漢字で書いたとき、出てくる数字を全部足すと「17」になる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130048, 3, 1, 1, "まるで相手にされない様子を示す慣用句は、「取り付くひまもない」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130049, 3, 1, 1, "「寓話」とは、「人間にとって大切なことを何かにたとえて教えるお話」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130050, 3, 1, 1, "非常に狭い場所のことを「ねこの額」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130051, 3, 1, 1, "「頭打ち」とは、「これ以上は成長できないこと」を示すことばである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130052, 3, 1, 1, "「ウーロン茶」を漢字で書いたときに出てくる鳥は、「からす」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130053, 3, 1, 1, "「マージャン」を漢字で書いたときに出てくる鳥は、「ひばり」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130054, 3, 1, 1, "出世魚ボラの成長しきった時の名前に由来する言葉は「とどのつまり」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130055, 3, 1, 1, "「猫に小判」と同じ意味のことわざは「豚に念仏」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130056, 3, 1, 1, "「へそで茶を沸かす」とは、「ありえないこと」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130058, 3, 1, 1, "「二束三文」とは、「数が多くても売値が非常に安いこと」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130059, 3, 1, 1, "「満を持す」「てぐすねを引く」という言葉は、「弓矢」に関係する動作から生まれたものである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1130060, 3, 1, 1, "漢字で「南風」と書いたとき、「なんぷう」以外に「はえ」とも読む");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1131001, 3, 1, 1, "イベント名「Re:member　僕の願いが紡ぐ未来」の読み方は「リメンバー　ぼくのねがいがつむぐみらい」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1131002, 3, 1, 1, "イベント名「カルミナサマーライブ　ジャスト ア モーメント！」の「ジャスト ア モーメント」を和訳すると「一瞬」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1131003, 3, 1, 1, "「オーロラ・サンクチュアリ」の「サンクチュアリ」を和訳すると「聖域」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1131004, 3, 1, 1, "「フィオレ・フェリチータ」の「フェリチータ」を和訳すると「幸福」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1131005, 3, 1, 1, "「ヴァーミリオンエンプレス」の「ヴァーミリオン」を和訳すると「統べるもの」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210001, 1, 2, 1, "他人の血を血管にとりいれることを「採血」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210002, 1, 2, 1, "鳴き声が「コケコッコー」と聞こえる鳥は「ウグイス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210003, 1, 2, 1, "ある生物種の全てが死に絶えてしまうことを「絶滅」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210004, 1, 2, 1, "カズノコは「タラ」の卵である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210005, 1, 2, 1, "大豆はその栄養価から、「畑の肉」とも呼ばれる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210006, 1, 2, 1, "煎って粉にすると「きな粉」に、発芽させると「もやし」になる食材は「小豆」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210009, 1, 2, 1, "十二支で、「亥」が表す動物は「いのしし」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210011, 1, 2, 1, "畜産で「養けい」といえば、「ぶた」を育てることである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210012, 1, 2, 1, "襟もとに浮き出ている、胸骨と肩甲骨とをつなぐ左右二対の骨を「肩甲骨」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210013, 1, 2, 1, "動物の耳の中にある、平衡感覚を司るための器官は「三半規管」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210014, 1, 2, 1, "「絹」は「植物の種子」から採取される");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210015, 1, 2, 1, "ABO式、Rh式といえば「血液型」の種類のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210016, 1, 2, 1, "人間は、「哺乳類」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210018, 1, 2, 1, "チューリップが咲く季節は「夏」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210019, 1, 2, 1, "「果物の王様」とも呼ばれる、強烈なにおいで有名な果物は「マンゴー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210020, 1, 2, 1, "じゃがいもの芽には「ソラニン」という有毒物質が含まれる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210021, 1, 2, 1, "胴体が太い、ヘビのような姿と伝えられる日本の未確認生物は「ツチノコ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210022, 1, 2, 1, "ウマやライオンの頭や首に生えている長い毛を「うしろがみ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210024, 1, 2, 1, "栄養物質のリコピンが多く含まれる、ジュースやケチャップでおなじみの野菜は「トマト」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210026, 1, 2, 1, "ネコやイヌなどの動物の足の裏についている、少し盛り上がっていて柔らかい部分を「肉球」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210027, 1, 2, 1, "石の下などでよく見かける、刺激を受けると丸くなる性質で知られる昆虫は「ワラジムシ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210028, 1, 2, 1, "もぐらは土の中にトンネルを掘る性質があり、漢字では「土流」と書く");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210029, 1, 2, 1, "音を感じ取る感覚は「嗅覚」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210031, 1, 2, 1, "俗に赤ちゃんを運んでくるといわれる鳥は「コウノトリ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210033, 1, 2, 1, "人間の五本指のうち、真ん中の指は「中指」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210034, 1, 2, 1, "ホエールウォッチングといえば、「イルカ」を観察することである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210035, 1, 2, 1, "人間の体の中で一番硬い部分は「歯」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210036, 1, 2, 1, "俗に「百獣の王」と呼ばれる動物は「虎」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210037, 1, 2, 1, "昔からの習慣で、冬至の日にお風呂に入れるものは「かぼちゃ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210039, 1, 2, 1, "昔から「眠れない夜に数える」と言われる動物といえば「羊」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210040, 1, 2, 1, "弁慶の泣き所とは「ふくらはぎ」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210041, 1, 2, 1, "内臓の一つ「すいぞう」は、漢字で「水臓」と書く");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210042, 1, 2, 1, "木登りをするものもいる、漢字で「山羊」と書く動物は「ひつじ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210043, 1, 2, 1, "胃袋に続く小腸の部分を「十二指腸」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210045, 1, 2, 1, "熱いものが苦手な舌を、「イヌ舌」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210047, 1, 2, 1, "「ソメイヨシノ」といえば「ウメ」の一種である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210048, 1, 2, 1, "野菜のレンコンは、「ハス」の根っこである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210049, 1, 2, 1, "昆虫の足は「6本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210050, 1, 2, 1, "一般的に魚は「ひれ」で呼吸している");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210052, 1, 2, 1, "普通、「大学いも」に使われるいもは「さつまいも」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210054, 1, 2, 1, "十二支の6番目の動物は「うさぎ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210055, 1, 2, 1, "十二支のうち、唯一の想像上の生き物は「たつ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210057, 1, 2, 1, "太陽の日を浴びて日焼けする原因は「赤外線」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210058, 1, 2, 1, "ハ虫類の成長過程で、古い皮を脱ぎ捨てることを「変態」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1210060, 1, 2, 1, "犬の感覚で、人よりも100万倍以上すぐれているとされるものは「嗅覚」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1211001, 1, 2, 1, "コッコロの主人公に対する呼び方は「主どの」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (1, 1, 1211002, 1, 2, 1, "このイラストにて、\nペコリーヌが持って\nいるのは「ごはん茶碗\n」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1211003, 1, 2, 1, "アオイが自作したブリキの人形の名前は「だいじょぶマイフレンドくん１号」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1211004, 1, 2, 1, "ギルド「ヴァイスフリューゲル　ランドソル支部」のメンバーは「5人」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1211005, 1, 2, 1, "ユニ、チエル、クロエのうち一番年上なのは「チエル」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220001, 2, 2, 1, "ヒマワリは「キク科」の植物である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220003, 2, 2, 1, "傷口に集まって傷をふさぐ働きをする血液の成分は「赤血球」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220004, 2, 2, 1, "アオノリ、コンブ、ワカメは「藻類」に分類される");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220006, 2, 2, 1, "湧き水のあるきれいな水に自生する、日本原産の香草野菜は「サンショウ」である");
