INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220009, 2, 2, 1, "二日酔いに効くとされる、「ターメリック」とも呼ばれる植物は「コリアンダー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220010, 2, 2, 1, "「アキザクラ」とも呼ばれる、桃色や赤などの花を咲かせる植物は「コスモス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220012, 2, 2, 1, "血液中で、体内に侵入したウイルスなどを排除する働きをする物質は「白血球」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220013, 2, 2, 1, "モンシロチョウの紋の色は「白」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220015, 2, 2, 1, "別名を「春告鳥」という鳥は「ウグイス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220016, 2, 2, 1, "入口が噴門、出口が幽門と呼ばれる内臓は「胃」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220017, 2, 2, 1, "冬至に食べると風邪をひかないといわれるものは「ゆず」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220018, 2, 2, 1, "世界的に「平和のシンボル」とされる鳥は、「鳩」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220019, 2, 2, 1, "パイソン柄のパイソンは「牛」の一種である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220020, 2, 2, 1, "アリジゴクは「ウスバカゲロウ」の幼虫である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220021, 2, 2, 1, "寝ているときに気道が狭くなることで起こる現象は「あくび」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220023, 2, 2, 1, "世界最大の両生類である生物は「オオサンショウウオ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220024, 2, 2, 1, "別名を「マツバウド」という、グリーン、ホワイトなどの種類がある野菜は「ブロッコリー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220025, 2, 2, 1, "人間の胃袋は1つだが、ウマには胃袋が「2つ」ある");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220026, 2, 2, 1, "人の臓器で、主にアルコールを分解するのは「十二指腸」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220027, 2, 2, 1, "イノシシの子は、背中がうりに似ていることから「うりぼう」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220028, 2, 2, 1, "ネコの品種で、「アメショー」の「ショー」は「ショーグン」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220029, 2, 2, 1, "横隔膜のけいれんによって引き起こされる生理現象は「げっぷ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220032, 2, 2, 1, "英語では「チャイニーズキャベジ」という、鍋料理によく使われる冬の代表的な野菜は「チンゲンサイ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220034, 2, 2, 1, "ギンナンの実は「マツ」の木から採れる");
