INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220037, 2, 2, 1, "人間の脳の大部分を占めるのは「大脳」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220038, 2, 2, 1, "植物が水と二酸化炭素から有機物を合成し、その過程で酸素を放出することを「呼吸」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220039, 2, 2, 1, "アメーバなど、体が一個の細胞だけからできている生物を「多細胞生物」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220041, 2, 2, 1, "約120日間絶食して子どもを育てるという、最も体の大きいペンギンは「コウテイペンギン」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220043, 2, 2, 1, "小学校の理科の授業によく登場する、漢字で「糸瓜」と書く植物は「ゴーヤー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220044, 2, 2, 1, "きんぴらなどに使われる、漢字で「牛蒡」と書く野菜は「ゴボウ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220045, 2, 2, 1, "昆虫が身を守るため、植物など他の物に成りすますことを「変態」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220046, 2, 2, 1, "英語で「star fish」と呼ばれる海の生き物は「クリオネ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220047, 2, 2, 1, "魚の体にある、浮き沈みのための袋状の器官は「うきぶくろ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220048, 2, 2, 1, "樹木の年輪が一つ増えるのに必要な期間は「およそ10年」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220049, 2, 2, 1, "ゾウの長い鼻は「毛」が変化したものである");
