INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220052, 2, 2, 1, "しっぽの先についている抜け殻を振って音を出すことから名がついたヘビは「ガラガラヘビ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220054, 2, 2, 1, "全身がトゲだらけのハリセンボンは「タイ」の仲間である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220057, 2, 2, 1, "お尻や太ももにつきやすい、デコボコした見た目の脂肪組織を「セルライト」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220059, 2, 2, 1, "「ウルメ」や「カタクチ」といえば、この魚は「サバ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220060, 2, 2, 1, "「アオダイショウ」という種類がいる生物は「ヘビ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1220061, 2, 2, 1, "ふくらはぎの筋肉とかかとの骨を結ぶ部分を「アキレス腱」という");
