INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1221002, 2, 2, 1, "トモが魔法少女に変身したときの名前は「魔法剣士フェアリー★トモ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1221003, 2, 2, 1, "アンナの本名は「アンネローゼ・フォン・シュテッヒパルム」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1221004, 2, 2, 1, "リンの大好物は「カレーパン」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (2, 1, 1221005, 2, 2, 1, "このキャラクターの\n名前は「チュウザ」\nである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230002, 3, 2, 1, "ゴリラの血液型は、すべて「A型」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230003, 3, 2, 1, "「ファイヤーフォックス」という別名がある動物は「キタキツネ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230004, 3, 2, 1, "サメの頭にある、微弱な電流を察知する器官のことを「ピット器官」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230006, 3, 2, 1, "日本最大の蛾は「ヨナグニサン」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230007, 3, 2, 1, "釣ったばかりのタコやイカの血液は「赤い」");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230008, 3, 2, 1, "カツオノエボシは、刺されたときの痛みから「電気クラゲ」とも呼ばれる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230009, 3, 2, 1, "サルが他のサルの体にまたがって、上下関係を示す行動を「マウンティング」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230011, 3, 2, 1, "悪夢を食べる聖獣と同じ名前を持つ、鼻の長い動物は「アリクイ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230012, 3, 2, 1, "世界で最も体の大きな鳥は「コンドル」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230013, 3, 2, 1, "日本一大きなトンボの種類は「オニヤンマ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230014, 3, 2, 1, "ヒトの心臓から全身に血液を流す血管を「大静脈」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230017, 3, 2, 1, "サメなど大型の魚にくっついて生活する習性で知られる魚は「コバンザメ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230018, 3, 2, 1, "人間の喉にある「扁桃腺」の「扁桃」は「スモモ」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230019, 3, 2, 1, "シロクマの地肌の色は「黒」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230020, 3, 2, 1, "カバの身体から出る、汗のような液体の色は「白」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230021, 3, 2, 1, "ゴリラが胸を叩くときの手の形は「グー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230022, 3, 2, 1, "キリンの群れのことを英語で「タワー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230023, 3, 2, 1, "ライオンの群れのことを英語で「バンド」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230024, 3, 2, 1, "トンボの眼などに見られる、小さな目がたくさん集まった眼のことを「多眼」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230025, 3, 2, 1, "学名を「ニッポニア・ニッポン」という鳥は「トキ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230027, 3, 2, 1, "人が声を出すときに振動させている、のどにある膜状の器官は「横隔膜」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230029, 3, 2, 1, "イチゴの花の色は「赤」のみである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230030, 3, 2, 1, "植物の光合成が行われる細胞小器官は「ミトコンドリア」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230033, 3, 2, 1, "ヘビは、「両生類」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230036, 3, 2, 1, "身、皮、肝、エラなどが「七つ道具」と呼ばれ、すべて鍋に入れられる魚は「アンコウ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230037, 3, 2, 1, "シシャモは市場に出回っているもののほとんどが「カペリン」という別の魚である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230038, 3, 2, 1, "しゃっくりは、「腹直筋」のけいれんによって起こる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230039, 3, 2, 1, "子どものときにはエラ呼吸、大人になると肺呼吸する生物を「は虫類」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230040, 3, 2, 1, "ブリやスズキに代表される、成長過程で呼び名が変わる魚を「出世魚」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230041, 3, 2, 1, "トリカブトは「テトロドトキシン」という猛毒を持つ");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230043, 3, 2, 1, "光るものを口に入れる習性がある牛。その胃を守るため、牧場などでは「磁石」を飲ませている");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230044, 3, 2, 1, "ジャイアントパンダが主に食べるのは「ユーカリ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230046, 3, 2, 1, "日本に生息するヤマネコは2種類。「ツシマヤマネコ」と「イリオモテヤマネコ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230047, 3, 2, 1, "漢字では「桃花心木」、高級木材として知られる、センダン科の植物は「マホガニー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230050, 3, 2, 1, "ワラビ、ゼンマイは「コケ類」に分類される");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230051, 3, 2, 1, "タイ料理のトムヤンクンにも用いるハーブ、レモングラスは「バラ科」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230052, 3, 2, 1, "十二指腸に続く、人間の胃にある出口の部分を「鬼門」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230053, 3, 2, 1, "英語で「ヒポポタマス」といえば、「カバ」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230054, 3, 2, 1, "和紙の原料になるジンチョウゲ科の植物は、枝が分かれていることから「フタマタ」という名がついた");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230055, 3, 2, 1, "サボテンやアロエのような、茎や葉の厚い植物を「多肉植物」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230056, 3, 2, 1, "漢字では「山茶花」。秋の終わりから冬にかけて咲くツバキ科の花は「さんざし」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230057, 3, 2, 1, "リャマとアルパカは、どちらも「ラクダ科」の動物である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230058, 3, 2, 1, "人の体内で、食べたものが最後に通過する臓器は「盲腸」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230059, 3, 2, 1, "人体において半月板がある部位は「ひじ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1230061, 3, 2, 1, "アリとの共生関係で知られる生物は「キリギリス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1231001, 3, 2, 1, "クロエは「にょぴゃあああああ！！」と叫んだことがある");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1231002, 3, 2, 1, "「ミサキ」の身長は「ミソギ」よりも高い");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1231003, 3, 2, 1, "「カリザ」の年齢は「ミミ」と同じである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1231004, 3, 2, 1, "ホマレと一緒にいる黄色いドラゴンの名前は「ラーゴ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1231005, 3, 2, 1, "テンタパスの足の本数は「10本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310001, 1, 3, 1, "摂氏温度を表すのに使われるアルファベットといえば「F」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310002, 1, 3, 1, "数学で、90°未満の角を「鋭角」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310004, 1, 3, 1, "一般的なサイコロの頂点の数は「6つ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310005, 1, 3, 1, "化学式「CO2」といえば「一酸化炭素」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310006, 1, 3, 1, "化学式「H2O」といえば「水」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310008, 1, 3, 1, "地球内部のマグマが、噴火によって地表に出てきたものを「マントル」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310009, 1, 3, 1, "月の満ち欠けで、ほぼ見えない状態を「新月」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310010, 1, 3, 1, "磁界・電流・力の向きを指先で表した法則はフレミングの「右手」の法則である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310012, 1, 3, 1, "1辺が2の正方形の面積は「4」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310014, 1, 3, 1, "救急車のサイレンなどの音が、遠くと近くでは高さが違って聞こえるのは「ドップラー効果」のためである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310015, 1, 3, 1, "四角形に対角線は「4本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310016, 1, 3, 1, "石灰水に二酸化炭素を加えても、「何も変化しない」");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310017, 1, 3, 1, "豆電球を差し込む受け口を「ポケット」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310018, 1, 3, 1, "物質を原子・分子レベルで扱う技術を「ナノテクノロジー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310019, 1, 3, 1, "「有機物」「無機物」などといえば、「炭素」の有無をいったものである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310020, 1, 3, 1, "一般的に、百葉箱の色は「緑」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310022, 1, 3, 1, "時計の短針は1日に「1回だけ」回転する");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310024, 1, 3, 1, "地震波が最初に発生した場所を「震源」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310025, 1, 3, 1, "地図上で、同じ高度の地点を結ぶ線を「水平線」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310026, 1, 3, 1, "乾電池にある2つの極といえば「プラス」と「マイナス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310029, 1, 3, 1, "地球表面の半分以上は「陸地」が占めている");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310030, 1, 3, 1, "理科の実験で使うアルコールランプ。正しい火の消し方は「息を吹きかける」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310031, 1, 3, 1, "台風の中心にある、風がおだやかな部分を「目」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310034, 1, 3, 1, "午前2時の3時間前は「午後11時」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310036, 1, 3, 1, "正方形だけで囲まれた箱の形を「四角錐」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310037, 1, 3, 1, "火山などで、地下のマグマが地表にふき出すことを「噴火」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310038, 1, 3, 1, "雷を避ける金属の棒を「ほういじしん」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310040, 1, 3, 1, "すべての図形は、「360度」回転すると元通りになる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310042, 1, 3, 1, "ジルコニウムなど、天然に存在する量が少ない、希少金属のことを「レアメタル」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1310043, 1, 3, 1, "1日の最高気温が30℃以上の日を「猛暑日」という");
