INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520009, 2, 5, 1, "トランプのカードは、ジョーカーを除くと「52枚」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520011, 2, 5, 1, "一般的なギターの弦の数は「4本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520013, 2, 5, 1, "正式名をメカニカルペンシルという文房具は「万年筆」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520015, 2, 5, 1, "病気になっても症状が表れにくいことから「沈黙の臓器」とも呼ばれるのは「肝臓」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520016, 2, 5, 1, "バイオリンの弦を弾く道具は「弓」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520017, 2, 5, 1, "落語家が高座にあがる時にかかる曲のことを「出囃子」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520018, 2, 5, 1, "ピアノの鍵盤で、1オクターブの中にある黒い鍵盤の数は「7つ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520019, 2, 5, 1, "映画で見る「字幕スーパー」の「スーパー」とは「スーパーバイザー」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520020, 2, 5, 1, "大規模な多人数同時参加型オンラインRPGのことを「WWORPG」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520021, 2, 5, 1, "同じ局面を繰り返すと「千日手」となるのは「囲碁」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520023, 2, 5, 1, "初めて使う化粧品が、肌にあうかどうかを試すテストを「パッチノート」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520024, 2, 5, 1, "将棋で、縦と横に何マスでも進める攻めに重要な駒は「角行」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520025, 2, 5, 1, "チェスの駒で、馬の頭の形をしているのは「ナイト」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520026, 2, 5, 1, "楽譜で使われるト音記号はアルファベットの「G」を図案化したものである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520027, 2, 5, 1, "麻雀で「リーチ」を宣言したときに、場に出す点数は「100点」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520029, 2, 5, 1, "タイヤ交換のために使用される、車を持ち上げる道具を「ジャッキ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520031, 2, 5, 1, "一般的に、カジノのルーレットに書かれている数字は「1」から「36」までである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520032, 2, 5, 1, "俗称で「痩せこけた」という意味がある、足のラインにぴったりフィットしたデニムに使われる言葉は「スキニー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520034, 2, 5, 1, "化粧品売り場の美容部員を指す「BA」は「ベストアドバイザー」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520035, 2, 5, 1, "持っている服を意味する「ワードローブ」という言葉はもともと「仕立て屋」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520037, 2, 5, 1, "ピアス穴を開けずに、耳にはさんだりひっかけたりして使う耳飾りを「イヤーカフ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520038, 2, 5, 1, "将棋を始めるときに、王将の両端に並べられる駒は「銀将」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520039, 2, 5, 1, "結婚式で、指輪交換の直前まで指輪を置いておくクッションを「リングピロー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520041, 2, 5, 1, "オセロで使う石の種類は「1つ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520042, 2, 5, 1, "オセロの盤面のマス目の数は「36」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520043, 2, 5, 1, "木版画で、インクをこすって写すための道具を「バラン」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520044, 2, 5, 1, "囲碁や将棋をする人のことを「棋士」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520045, 2, 5, 1, "絵を書くときに、絵の具をまぜたりする板のことを「パレット」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520047, 2, 5, 1, "六曜のなかで、もっとも縁起が良いとされている日は「先勝」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520048, 2, 5, 1, "「ピカイチ」や「シカト」という言葉の由来となったゲームは「麻雀」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520049, 2, 5, 1, "「まな板」の「まな」は、もともと「愛」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520050, 2, 5, 1, "出産時に用いられる「ひっひっふー」という呼吸法を「ラマーズ法」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520052, 2, 5, 1, "恐竜の名前によく使われる「サウルス」。もともと「恐ろしい」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520053, 2, 5, 1, "「キウイフルーツ」の「キウイ」とは、もともと「似ている鳥」の名前である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520054, 2, 5, 1, "占星術などで使われる用語で、4つの惑星が十字型に並ぶことを「グランドクロス」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520055, 2, 5, 1, "最後にお茶を怖がってオチがつく落語の演目といえば「まんじゅうこわい」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520057, 2, 5, 1, "オーケストラで指揮者が手にする指揮棒のことをドイツ語で「タクト」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520058, 2, 5, 1, "ジャズなどで、歌詞の代わりに「シュビドゥバ」などと意味のない言葉で歌うことを「シャウト」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1520059, 2, 5, 1, "劇場などでの最初の興業を「千秋楽」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1521001, 2, 5, 1, "プリンセスナイト傘下のギルドは、【王宮騎士団（NIGHTMARE）】、【サレンディア救護院】、【メルクリウス財団】である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1521002, 2, 5, 1, "キャラバンで一緒に冒険する仲間の名前は「オムスビちゃん」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1521003, 2, 5, 1, "「隻眼の悪魔（ワンアイドデビル）」の二つ名を持つのは「アンナ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1521004, 2, 5, 1, "キャラバンで収集するトレジャーには、「平凡」「貴重」「至高」の3段階のレアリティがある");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1521005, 2, 5, 1, "ギルドハウス家具には、「ルピ」をストックすることができる「コッコロのおさいふ」がある");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530001, 3, 5, 1, "髪を細かく分けて三つ編みにして垂らしたヘアスタイルを「ドレッドヘア」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530002, 3, 5, 1, "囲碁の碁石は、「黒石」の方が大きい");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530003, 3, 5, 1, "「枕缶」という缶に入れられている食べ物といえば「コンビーフ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530004, 3, 5, 1, "楽器の「尺八」の名前は、「考案者の名前」に由来している");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530006, 3, 5, 1, "トランプゲームの「大富豪」で、革命時でない状態で最も強いのは、ジョーカーを除くと「K」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530007, 3, 5, 1, "プラモデルで、リアル感を出すためわざと汚れをつけることを「ウェザリング」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530008, 3, 5, 1, "一般的に海の塩分濃度は約「6%」といわれる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530009, 3, 5, 1, "音楽のジャンル、R＆B。Rはリズム、Bは「ベース」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530010, 3, 5, 1, "落語で、本題の前に噺家が語る導入部分のことを「まくら」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530011, 3, 5, 1, "ざるそばは「一枚、二枚」と数える");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530012, 3, 5, 1, "「グラシエ」といえば、「チョコレート」専門の職人である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530013, 3, 5, 1, "宝石の重さを表す単位、「1カラット」は「0.1グラム」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530014, 3, 5, 1, "箸使いのタブーで、箸をつけたのに食べずに戻すことを「ねぶり箸」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530018, 3, 5, 1, "車のエンジンに異常があった場合、金属性の打撃音が生じる現象を「キッキング」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530019, 3, 5, 1, "釣りで、魚を集めるための寄せエサのことを「コマセ」と呼ぶ");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530020, 3, 5, 1, "占いの易者さんが使う細い棒を「ぜいちく」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530021, 3, 5, 1, "付け爪の一種・ジェルネイルは「紫外線」で樹脂を固定する");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530022, 3, 5, 1, "ファッションショーでモデルが歩く、客席に向かって長く突き出した「ランウェイ」。俗称は「ドッグウォーク」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530024, 3, 5, 1, "ダイヤモンドで理想のカットといわれる58面のカットを「ブリリアントカット」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530025, 3, 5, 1, "靴と爪先の間にある空間を一般的に「遊び寸」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530026, 3, 5, 1, "タクシー業界の隠語で、酔っ払いのことを「ワカメ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530028, 3, 5, 1, "香水を吹き付けるのに使う霧吹きは、英語で「アトマイザー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530029, 3, 5, 1, "大きさにして0.2カラット以下の小粒なダイヤモンドのことを「メレ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530030, 3, 5, 1, "ダッフルコートにボタンの代わりについている留め具を「タックル」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530031, 3, 5, 1, "襟の先をボタンで留めるタイプの服を「ボタンダウン」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530032, 3, 5, 1, "日本語では「鳥打帽」という帽子は「シルクハット」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530034, 3, 5, 1, "ファンデーションの色の種類で「オークル」とは、フランス語で「黄土色」という意味である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530035, 3, 5, 1, "茶道において、茶室に付属する庭園を「露地」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530036, 3, 5, 1, "「エメリーボード」といえば、「爪」の手入れに使う道具である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530037, 3, 5, 1, "鉛筆1ダースといえば12本。1グロスといえば「120本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530038, 3, 5, 1, "トランプゲームの「ばば抜き」は、もともとジョーカーを使わず、「K」を1枚抜いてプレイしていた");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530040, 3, 5, 1, "三味線の弦を弾く道具を「爪」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530041, 3, 5, 1, "落語家が使う小道具で、おそばを食べる動作での箸代わりになるものは「手ぬぐい」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530042, 3, 5, 1, "温度差による音程の狂いが少なく、オーケストラの音合わせに用いられる木管楽器は「フルート」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530043, 3, 5, 1, "陶器の底の部分にある、出っ張った箇所を「糸底」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530045, 3, 5, 1, "多くの落語で登場する、間抜けな者の定番の名前は「八兵衛」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530047, 3, 5, 1, "演劇などで、本番同様の条件で行う最終リハーサルを「ゲネプロ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530049, 3, 5, 1, "「穴熊」という戦法が使われるゲームといえば「囲碁」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530050, 3, 5, 1, "標高の高い山などで、雪が一年中残っているところを「万年雪」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530051, 3, 5, 1, "「ステルス機」といえば、「レーダーに探知されにくい飛行機」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530052, 3, 5, 1, "裁縫で、表に縫い目が目立たないようにする縫い方といえば、「なみ縫い」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530054, 3, 5, 1, "裁縫を行うとき、布に直接印を付けるペンを「チャコペン」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530058, 3, 5, 1, "「布石」「定石」「死活」といえば、もともとは「将棋」に由来する用語である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530059, 3, 5, 1, "麻雀の役満で、東・南・西・北をそれぞれ3枚ずつ集めたものといえば「国士無双」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1530060, 3, 5, 1, "勝負を続行するときの宣言が由来の、花札の一般的なゲームは「いけいけ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1531001, 3, 5, 1, "リマの淑女の五箇条のうち一箇条は、「夢と希望が、淑女へのとびらをひらく第一歩」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1531002, 3, 5, 1, "ミヤコはプリン以外口にしたことがない");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1531003, 3, 5, 1, "アドベンチャーの思い出イベント「船頭酔っ払いて船滝を下る」でユカリと同じ船に乗っているのは「タマキ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1531004, 3, 5, 1, "「激走！　ランドソルギルドレース」のギルドレースでリタイアしたチームは【メルクリウス財団】と【リトルリリカル】である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1531005, 3, 5, 1, "「鋼の聖女と聖なる学舎の異端児」で行われた騎馬戦でMVPを獲得したのは「クロエ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610001, 1, 6, 1, "サッカーで次のプレーにつなげるためにボールを受け止める動きを「トラップ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610002, 1, 6, 1, "釣り用語で、魚が一匹も釣れなかったことを「ボウズ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610003, 1, 6, 1, "陸上の「障害走」で飛び越えるのは「バーベル」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610004, 1, 6, 1, "野球で、最終回の裏の攻撃で点を奪って勝敗が決まった試合を「オツカレ勝ち」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610005, 1, 6, 1, "野球で、打者の手前で急激に落ちる球を「スプーンボール」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610006, 1, 6, 1, "ボクシングのトレーニングの時に叩く、中に砂が入った大きな袋の名前は「サンドバッグ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610007, 1, 6, 1, "「アメフト」の「フト」とは「フトモモ」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610009, 1, 6, 1, "バスケットボールで、ゴールの真下からショットを放ち得点すると、「3点」になる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610010, 1, 6, 1, "バスケットボールのゴールは「リング」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610013, 1, 6, 1, "卓球を英語にすると「テーブルテニス」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610014, 1, 6, 1, "ボウリングに使われるピンはふつう「20本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610015, 1, 6, 1, "テニスで、2人1チームで行うものは「ダブルス」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610018, 1, 6, 1, "柔道で、「一本」判定を獲得すると勝利になる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610021, 1, 6, 1, "バドミントンで打ち合う、コルクに鳥の羽根をつけたものは「シャトル」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610022, 1, 6, 1, "試合放棄の合図にタオルを投げるスポーツは「レスリング」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610023, 1, 6, 1, "野球で満塁ホームランを打ったとき、入る点数は「3点」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610024, 1, 6, 1, "野球の投手の投げたボールが打者に当たってしまうことを「デッドボール」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610025, 1, 6, 1, "ラグビーで、ボールを持っている相手に組みついて倒す行動を「タックル」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610026, 1, 6, 1, "大相撲で、取組の前に関取が土俵にまいているのは「小麦粉」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610027, 1, 6, 1, "サッカーの試合で、非紳士的プレーに対して審判が警告を出すときに提示するカードは「イエローカード」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610028, 1, 6, 1, "スキーをする人はスキーヤーと呼ぶのに対して、スケートをする人は「スケーティスト」と呼ぶ");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610029, 1, 6, 1, "スポーツのときに履く、ピンのついた靴を「レシーブ」と呼ぶ");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610031, 1, 6, 1, "競泳や陸上などで、トップの記録に並ぶ記録を「タイ記録」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610032, 1, 6, 1, "スポーツの審判で用いる「ホイッスル」とは、「笛」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610033, 1, 6, 1, "サッカーで「守護神」と呼ばれる人のポジションは「フォワード」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610035, 1, 6, 1, "大相撲で、横綱が敗れるなどの波乱があった場合に、よく土俵に投げ込まれるものは「座布団」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610036, 1, 6, 1, "プロレスやボクシングの試合で鳴らされる鐘を「ゴング」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610037, 1, 6, 1, "素人の仲間どうしで行う野球の試合を「泥野球」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610039, 1, 6, 1, "相撲部屋において、力士たちを指導する立場の人は「親方」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610040, 1, 6, 1, "野球で、試合開始に先立って選手以外の人物が一球投げるセレモニーを「始球式」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610041, 1, 6, 1, "野球用語「スタメン」の「スタ」とは「スタンディング」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610042, 1, 6, 1, "スポーツ用の靴で、「バッシュ」といえば主に「バスケットボール」で履くものである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610044, 1, 6, 1, "ボクシングの試合で、出場した選手に支払われる報酬を「ファイトマネー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610046, 1, 6, 1, "野球で「ゲッツー」といったら「ストライク」を2つ取ることである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610047, 1, 6, 1, "スポーツのトーナメント戦で、序盤の回戦を免除される権利を「シード」と呼ぶ");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610048, 1, 6, 1, "ボクシング用語の「KO」は「ノックアウト」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610049, 1, 6, 1, "アームレスリングとは「指相撲」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610050, 1, 6, 1, "スポーツの大会で「インターハイ」には「中学生」「高校生」が出場できる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610051, 1, 6, 1, "サッカーで「スコアレスドロー」といえば、両チームの得点の合計は「0点」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610053, 1, 6, 1, "プロボクシングのダウンは、「10カウント」以内に立ち上がれないと負けとなる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610054, 1, 6, 1, "柔道の国際大会にて、選手が着る柔道着の色は「白」と「赤」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610056, 1, 6, 1, "サッカーの試合で唯一手を使ってもよいのは「ミッドフィルダー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610058, 1, 6, 1, "剣道に使う、手につける防具は「小手」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610059, 1, 6, 1, "ゴルフで、ボールを打つための道具を「クラブ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1610060, 1, 6, 1, "サッカーで、「レッドカード」を出された選手は一発退場となる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1611001, 1, 6, 1, "パーティ内のキャラの位置付けは、「前衛」「中衛」「後衛」の3種類である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1611002, 1, 6, 1, "クリティカルダメージが発生すると、ダメージが「3倍」になる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1611003, 1, 6, 1, "プリンセスアリーナでは、3パーティ目が戦うまで勝敗が決まることはない");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1611004, 1, 6, 1, "イベント「トゥインクルサマーゲーム　夏海にきらめく三つの想い」のビーチバレーにて、チームトゥインクルは勝利した。");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1611005, 1, 6, 1, "専用装備1を装備することで、そのキャラの「ユニオンバースト」の効果が変化する");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620001, 2, 6, 1, "野球において、審判役を務める人を「アンパイア」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620002, 2, 6, 1, "相手を想定して一人で行うボクシングの練習は「シャドーパンチング」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620003, 2, 6, 1, "登山で、靴の底に付ける金属の爪を「アイゼン」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620004, 2, 6, 1, "格闘技で「スープレックス」といえば「投げ技」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620005, 2, 6, 1, "野球で、三振を表すアルファベット1文字は「S」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620006, 2, 6, 1, "野球の最終回は9回なのに対して、ソフトボールの最終回は「7回」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620008, 2, 6, 1, "「バスケットボール」を日本語で表すと「籠球」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620009, 2, 6, 1, "通常、剣道の審判は「2人」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620010, 2, 6, 1, "ポケット・ビリヤードで使用される、的球をセットするための枠を「ラック」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620011, 2, 6, 1, "テニスで3ポイント取ったとき、点数は「45」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620012, 2, 6, 1, "野球で、投手がランナーの盗塁を防ぐために塁に投球することを「牽制球」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620013, 2, 6, 1, "野球で「盗塁」を意味する英語は「スクイズ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620014, 2, 6, 1, "ボクシングで、防具を付けて行う実践さながらの練習を「スパークリング」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620015, 2, 6, 1, "バスケットボールのポジションで、「PG」とは「ポイントガード」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620017, 2, 6, 1, "ボウリングで「ターキー」といったら、ストライクを「3投」続けて出すことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620018, 2, 6, 1, "ダーツで、1ラウンドに投げる矢は「2本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620019, 2, 6, 1, "テニスのクレーコートの材料は「芝」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620020, 2, 6, 1, "剣道の防具をまとう際、普通最後に身につけるものは「胴」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620022, 2, 6, 1, "反復横跳びをする際に、地面に引かれる線は「2本」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620023, 2, 6, 1, "大相撲の番付で、横綱の次に来るのは「大関」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620025, 2, 6, 1, "テニスで「フォアハンド」と反対の打ち方は「バックハンド」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620026, 2, 6, 1, "サッカーのゴールの横棒はクロスバーであるのに対して、縦の棒は「ゴールポスト」と呼ばれる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620028, 2, 6, 1, "ボウリングの球の重さの単位は「ポンド」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620029, 2, 6, 1, "ボクシングで「チン」といえば、「あご」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620032, 2, 6, 1, "テニスで、「0点」のことを「ラブ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620033, 2, 6, 1, "バスケットボールで最初に行うプレーは「ジャンプボール」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620034, 2, 6, 1, "ビリヤードで、台にあいた穴を「カップ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620035, 2, 6, 1, "プロレス技の「ニースタンプ」「ニードロップ」の「ニー」とは、「ひじ」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620036, 2, 6, 1, "握手をするように握る、卓球のラケットの持ち方は「シェークハンド」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620039, 2, 6, 1, "競馬で、馬に乗る人を「馬主」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620040, 2, 6, 1, "競馬の「トレセン」。この「トレ」とは「トレーニング」の略である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620041, 2, 6, 1, "「バックストローク」と呼ばれる泳ぎ方は「平泳ぎ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620042, 2, 6, 1, "卓球で、ボールを打ち合うことを「リレー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620044, 2, 6, 1, "野球の昼間の試合を「ナイター」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620047, 2, 6, 1, "プールの「短水路」とは、「25m」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620048, 2, 6, 1, "角界といえば「相撲」の世界のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620049, 2, 6, 1, "バスケットボールでトラベリングの反則となるのは、ボールを持ってから「2歩」以上歩いたときである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620050, 2, 6, 1, "競馬場で、馬の下見をする場所を「パドック」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620051, 2, 6, 1, "「ルチャ・リブレ」といえば「ボクシング」のことである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620052, 2, 6, 1, "陸上選手のフォームで、大股で走る走法を「ストライド走法」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620054, 2, 6, 1, "サーフィンで、波が作るトンネルを「ホール」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620057, 2, 6, 1, "スポーツのポロで使われる動物は「馬」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620058, 2, 6, 1, "バレーボールで、主にトスを上げる役割の選手を「レシーバー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620059, 2, 6, 1, "ボクシングにおいて、腕の長さを意味するのは「リーチ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1620060, 2, 6, 1, "プロレスラーやボクサーが本名とは別に用いる名前を「リングネーム」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1621001, 2, 6, 1, "ミヤコ（クリスマス）の持っている武器は「スプーン」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1621002, 2, 6, 1, "ネネカ（ニューイヤー）のパーティ内の位置付けは「後衛」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1621003, 2, 6, 1, "トモ（マジカル）のスキル「シャイニーブレード」で与えるダメージは「魔法ダメージ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1621004, 2, 6, 1, "ルナの塔の100階では「エレクトロ・ギガス」が出現する");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1621005, 2, 6, 1, "エリコ（バレンタイン）の持っている武器は「ホイッパー型の斧」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630001, 3, 6, 1, "「水上の格闘技」と言われるのは「水球」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630003, 3, 6, 1, "ボウリングで、マイボールに対して、ボウリング場に準備されているボールのことを「ハウスボール」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630004, 3, 6, 1, "日本のプロ野球にて、公式球の縫い目の数は「108」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630006, 3, 6, 1, "大相撲本場所で毎年取組終了後に土俵上で行われる儀式は「矢取り式」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630007, 3, 6, 1, "フェンシングで使われる用語は、基本的に「英語」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630010, 3, 6, 1, "ダーツで、一投で獲得できる最高点は「60点」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630011, 3, 6, 1, "野球で1試合に2本以上ヒットを打つことを「マルチヒット」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630012, 3, 6, 1, "プロレスで、善玉のレスラーを英語で「ヒール」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630014, 3, 6, 1, "ダーツの的の中心にあるのは「ホークアイ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630015, 3, 6, 1, "アーチェリーの的の中央は「赤色」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630016, 3, 6, 1, "相撲の土俵の中央付近にある2本の白線のことを「仕切り線」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630018, 3, 6, 1, "日本語では「鎧球」と訳されるスポーツは「ラグビー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630020, 3, 6, 1, "日本語では「送球」と訳されるスポーツは「ハンドボール」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630021, 3, 6, 1, "相撲で、相手を追い詰めるも自分の足が先に土俵の外に出てしまうことを「急ぎ足」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630022, 3, 6, 1, "ボクシングで、相手の後頭部を故意にねらったパンチを「ラビットパンチ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630023, 3, 6, 1, "スノーボードで、進行方向に対して左足が前になることはレギュラー。右足が前になるスタンスを「グーフィー」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630026, 3, 6, 1, "野球で、3ストライク目のボールをキャッチャーが捕球できず、打者が一塁に走れることを「打ち逃げ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630027, 3, 6, 1, "野球の投手が滑り止めに使う白い袋の名前は「ロージンバッグ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630028, 3, 6, 1, "野球で、ピッチャーがキャッチャーの捕れないようなところに悪投球してしまうことを「ワイルドピッチ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630029, 3, 6, 1, "ボウリングで、ストライクとスペアを交互に出すと1ゲーム「100点」になる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630030, 3, 6, 1, "ダーツボードに書かれている点数を示す数字で、一番大きいのは「20」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630031, 3, 6, 1, "ダーツで、1ラウンドの合計得点が100以上になることを「メガ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630033, 3, 6, 1, "水泳のメドレーリレーにて、最初の泳法は「バタフライ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630034, 3, 6, 1, "ゴルフで、コースの形状が途中で折れ曲がっていることを「ドッグレッグ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630035, 3, 6, 1, "綱引きの国際ルールで使う綱の素材は「ナイロン」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630036, 3, 6, 1, "スキージャンプの飛型点は、審判1人につき「100点満点」でつけられる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630038, 3, 6, 1, "相撲の技で、相手の目の前でパチンと手を叩く戦法を「猫だまし」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630039, 3, 6, 1, "ゴルフで、一般的にバンカーからの打球で使われるクラブは「ドライバー」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630040, 3, 6, 1, "サッカーで、ボールを保持している選手を、後方の選手が追い越していくプレーを「オーバーラップ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630041, 3, 6, 1, "ラグビーは、「サッカーに熱中するあまりボールを手で持ってしまった」ことから生まれたスポーツである");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630043, 3, 6, 1, "雪合戦の国際ルールでは、1チームの人数は「7人」で行われる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630044, 3, 6, 1, "テニスで、ラケットに張る弦を「ガット」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630045, 3, 6, 1, "テニスで、自分のサービスゲームをとることを「ブレーク」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630047, 3, 6, 1, "バレーボールで、後衛の選手が行う攻撃を「ビハインドアタック」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630048, 3, 6, 1, "バレーボールで、守備専門の選手を「リベロ」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630051, 3, 6, 1, "柔道にて、技ありを「3回」取ると一本と同等になる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630052, 3, 6, 1, "剣道にて、打突をした後の気構え、身構えを「残心」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630053, 3, 6, 1, "フットサルのルールで、ゴレイロからボールを投げてゲームを再開することを「ゴールキック」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630054, 3, 6, 1, "ゲートボールのコート上の、ゲートの数は「3つ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630055, 3, 6, 1, "ゲートボールの試合は、1チームあたり「4人」で行われる");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630057, 3, 6, 1, "漢字では「長刀」と書くこともある、剣道に似たスポーツは「なぎなた」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630058, 3, 6, 1, "卓球で「タオリング」といえば、「タオルで汗を拭くこと」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630059, 3, 6, 1, "卓球のダブルスでは、2人のうち打ちやすい方が打てばよい");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630060, 3, 6, 1, "野球で、投手の代わりに守備につかない選手が打席に入る制度を「DH制」という");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 1630061, 3, 6, 1, "「図星をつく」という言葉の元となったスポーツは「弓道」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1631001, 3, 6, 1, "ミニゲーム「駆けろ常夏！　アルティメットビーチフラッグス」のぺコリーヌのＵＢ効果は「スピードアップ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1631002, 3, 6, 1, "ミニゲーム「駆けろ常夏！　アルティメットビーチフラッグス」のコッコロのＵＢ効果は「スピードアップ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1631003, 3, 6, 1, "ヨリ（エンジェル）の専用装備1は「ハッピーエンジェルアロー」、アカリ（エンジェル）の専用装備1は「ハッピーエンジェルボウ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1631004, 3, 6, 1, "ミニゲーム「猛訓打撃道！　ミサトのホームランドリル」のミサトの球種は「ストレートのみ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 1631005, 3, 6, 1, "ミニゲーム「猛訓打撃道！　ミサトのホームランドリル」のバッターの中で、ミートがSなのは「アヤネ」である");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110001, 1, 1, 2, "コンピュータのプログラム上の誤りを「虫」という意味の言葉で何という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110003, 1, 1, 2, "「ハイテク」「ナノテク」の「テク」は何の略？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110005, 1, 1, 2, "漢字で「雪崩」と書く雪山で起きる現象はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110006, 1, 1, 2, "生活の様々な場面でも応用される「マグネット」といえばどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110007, 1, 1, 2, "英語で海は「シー」、では大洋は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110008, 1, 1, 2, "「＠マーク」、読み方はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110010, 1, 1, 2, "「日なた」とは、どんな場所のこと？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110011, 1, 1, 2, "データを最新のものに更新することは？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110012, 1, 1, 2, "猛吹雪を伴う強風を何という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110014, 1, 1, 2, "「おひさま」「おてんとさま」といえば何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110016, 1, 1, 2, "空と地面の境界線は？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110018, 1, 1, 2, "無かったことにしたい過去のことを、ある色を使って「何歴史」という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110019, 1, 1, 2, "英語で「ソイソース」と呼ばれる調味料は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110021, 1, 1, 2, "「こうら干し」とは、主に体のどの部位に日光を浴びること？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110022, 1, 1, 2, "お笑いやギャンブルの世界で「間違いないこと」を意味する言葉はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110023, 1, 1, 2, "パーティーなど、その場にふさわしい服装の基準のことを何という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110024, 1, 1, 2, "新しくできた劇場の、初興行を何という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110025, 1, 1, 2, "次のうち、硬貨をさすものはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110026, 1, 1, 2, "広く「服飾」の意味で使われる用語は、次のうちどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110028, 1, 1, 2, "英語では「ウォーターマーク」という、偽造を防ぐため紙幣に施されるものは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110031, 1, 1, 2, "思うように行かないこと。「二階から何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110032, 1, 1, 2, "手応えがないこと。「何に釘」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110034, 1, 1, 2, "強い者がさらに強くなること。「鬼に何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110035, 1, 1, 2, "わずかな努力は意味がないこと。「焼け石に何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110036, 1, 1, 2, "ガマンの限界！「仏の顔も何度まで」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110037, 1, 1, 2, "目障りなこと。「目の上の何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110038, 1, 1, 2, "先行きはわからないこと。「一寸先は何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110039, 1, 1, 2, "予想外の幸運がやってくること。「棚から何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110040, 1, 1, 2, "悪い事態が重なること。「泣き面に何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110041, 1, 1, 2, "意気投合すること。「何が合う」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110042, 1, 1, 2, "漢字の部首「さんずい」が表すものは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110043, 1, 1, 2, "次のうち、部首が「にすい」の文字はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110044, 1, 1, 2, "「十月十日」この4文字を組み合わせてできる漢字1文字は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110045, 1, 1, 2, "漢字の読み。「な」で始まらない言葉はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110046, 1, 1, 2, "「運動会の選手センセイ」。センセイを漢字で書くとどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110047, 1, 1, 2, "「朝6時にキショウする」。キショウを漢字で書くとどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110049, 1, 1, 2, "「勉強」「平等」「人気」の前に共通してつく漢字はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110050, 1, 1, 2, "「公式」「常識」「現実」の前に共通してつく漢字はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110051, 1, 1, 2, "「神経」「意識」「鉄砲」の前に共通してつく漢字はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110052, 1, 1, 2, "「成年」「完成」「発表」の前に共通してつく漢字はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110053, 1, 1, 2, "すぐに飽きて長続きしないこと。四字熟語で言うと何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110054, 1, 1, 2, "ことわざ。「とらぬ○の皮算用」。○に入る動物は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110056, 1, 1, 2, "「足袋」。この漢字の読み方は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110058, 1, 1, 2, "四字熟語「○発○中」。○に共通して入る数字は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2110060, 1, 1, 2, "四字熟語「○人○色」。○に共通して入る数字は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2111001, 1, 1, 2, "「君と出逢い、君と往く○○○○○○○のその先で」");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2111002, 1, 1, 2, "ミソラを★5に才能開花した際に入手できる称号は？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2111003, 1, 1, 2, "「エルフ族：アストルムに住む四種族の一つ。○○○が特徴。」○の中に入るのは次のうちどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2111004, 1, 1, 2, "マホがギルドマスターを務める【自警団】の読み方は次のうちどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2111005, 1, 1, 2, "「グロウスフィア」の「グロウ」の意味は次のうちどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120002, 2, 1, 2, "金属の和名で「しろがね」とは何のこと？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120004, 2, 1, 2, "「肝臓」を意味する言葉はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120005, 2, 1, 2, "日本語で「心的外傷」と呼ばれるのは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120006, 2, 1, 2, "秋の終わりから冬の始めによく降る、にわか雨をさす言葉はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120009, 2, 1, 2, "金属の和名で「くろがね」とは何のこと？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120011, 2, 1, 2, "家の中心にある、最も太い柱は「何柱」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120012, 2, 1, 2, "「祝電」「弔電」の「電」は何のこと？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120013, 2, 1, 2, "海外では「スイートサケ」と呼ばれる調味料は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120014, 2, 1, 2, "お茶を何度も煎じ出したりして、味や香りが薄くなった物を何という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120016, 2, 1, 2, "舞台の右側を表す「上手」は何と読む？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120017, 2, 1, 2, "「寄席」は何と読む？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120018, 2, 1, 2, "「高速道路」をさす言葉はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120021, 2, 1, 2, "ききめがまったく無いこと。「豆腐に何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120022, 2, 1, 2, "けちんぼな人が、俗に「火を灯す」といわれる身体の一部はどこ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120023, 2, 1, 2, "何を言っても無駄なこと。「犬に何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120025, 2, 1, 2, "ありえないことが現実に…。「何から駒」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120026, 2, 1, 2, "量がほんのわずかなこと。「何の涙」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120027, 2, 1, 2, "四字熟語の「いっちょういっせき」。漢字で書くとどうなる？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120028, 2, 1, 2, "雨のときに使う「かさ」を漢字で書いたとき、「人」はいくつ出てくる？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120029, 2, 1, 2, "英語のあいさつ「ハロー」。正しいつづりはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120031, 2, 1, 2, "ぐずぐずして決断できないこと。四字熟語で言うと何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120033, 2, 1, 2, "一週間の曜日の英語、「F」で始まるのは何曜日？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120034, 2, 1, 2, "一週間の曜日の英語、「W」で始まるのは何曜日？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120035, 2, 1, 2, "推理小説で、殺人事件の被害者が死の間際に残す何らかの伝言を何という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120036, 2, 1, 2, "相手に攻め入るときの言葉、「将を射んとせば先ず“何”を射よ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120037, 2, 1, 2, "昔の言葉で、「かわや」といえば家のどこ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120038, 2, 1, 2, "「鑑みる」。この漢字の読み方はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120039, 2, 1, 2, "「やおよろずの神」の「やおよろず」、漢字では何と書く？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120042, 2, 1, 2, "余計なことをしてさらに酷い目に遭うこと、「やぶをつついて何を出す」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120045, 2, 1, 2, "「瀕死の状態から一死報いる」この文中の間違った熟語はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120046, 2, 1, 2, "「不信な人物を追跡する」この文中の間違った熟語はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120047, 2, 1, 2, "「紙幣を硬化に両替する」この文中の間違った熟語はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120048, 2, 1, 2, "次の四字熟語を漢字で書いたとき、漢数字の合計が10になるのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120049, 2, 1, 2, "次の四字熟語を漢字で書いたとき、漢数字の合計が最も大きいのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120050, 2, 1, 2, "「便宜をハカる」、この「ハカる」の漢字で正しいのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120051, 2, 1, 2, "「国王の位にツく」、この「ツく」の漢字で正しいのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120053, 2, 1, 2, "「○は藍より出でて藍より○し」○に共通して入る色は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120054, 2, 1, 2, "弱い者にも意地がある、という意味の言葉、「一寸の虫にも何分の魂」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120055, 2, 1, 2, "「呉越同舟」で、同じ舟に乗り合わせているのはどんな人同士？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120056, 2, 1, 2, "似たようなものが次々出てくること、「雨後の何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120057, 2, 1, 2, "俗に「天下の回りもの」と言われるのは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120058, 2, 1, 2, "じわじわと攻めるたとえ、「何で首を絞める」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120059, 2, 1, 2, "怪しい話を聞くときなどに、俗に「つばをつける」と言われる身体の一部はどこ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120060, 2, 1, 2, "ことわざ。「○の甲より年の功」。○に入る漢字は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2120061, 2, 1, 2, "慣用句。「虎の威をかる○」。○に入る漢字は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2121001, 2, 1, 2, "アオイが結成した「BB団」の正式名称はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2121002, 2, 1, 2, "「熾炎戦鬼煉獄血盟暗黒団」の正しい読み方はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2121003, 2, 1, 2, "クリスティーナを★6に才能開花した際に入手できる称号は？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2121004, 2, 1, 2, "「疾風の冥姫」の正しい読み方はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2121005, 2, 1, 2, "「ミリオンフリッピング」の「ミリオン」の意味は次のうちどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130004, 3, 1, 2, "「紅差し指」とも呼ばれる指は何指？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130005, 3, 1, 2, "「高々指」「丈高指」とも呼ばれる指は何指？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130006, 3, 1, 2, "「塩嘗め指」とも呼ばれる指は何指？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130007, 3, 1, 2, "俗に「百薬の長」とも呼ばれるものは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130008, 3, 1, 2, "「芋の煮えたもご存じない」とはどんな人？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130009, 3, 1, 2, "次のうち、部首が「まだれ」の文字はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130011, 3, 1, 2, "次のうち、画数が最も多い漢字はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130014, 3, 1, 2, "昔の言葉で、「くりや」といえば何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130015, 3, 1, 2, "四字熟語「○牛一毛」。○に入る漢字は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130016, 3, 1, 2, "相手を惑わせる発言をすること、「何を弾く」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130018, 3, 1, 2, "次の四字熟語で、間違っているものはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130019, 3, 1, 2, "次のうち、重箱読みの熟語はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130020, 3, 1, 2, "次のうち、湯桶読みの熟語はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130021, 3, 1, 2, "次の四字熟語で、間違っているものはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130022, 3, 1, 2, "次の漢字で、魚偏をつけると「かじか」と読むのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130023, 3, 1, 2, "訓読みで「ともなう」「なかば」「そる」この漢字に共通する音読みは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130024, 3, 1, 2, "訓読みで「たたえる」「なげく」「ひとえ」この漢字に共通する音読みは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130025, 3, 1, 2, "読みで「はり」「おみ」「くちびる」この漢字に共通する音読みは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130026, 3, 1, 2, "「ミイラ」を漢字で書くと正しいのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130027, 3, 1, 2, "「微温湯」、この熟語は何と読む？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130028, 3, 1, 2, "次のうち、「オルゴール」を表すのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130029, 3, 1, 2, "次のうち、「デッサン」を表す熟語はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130030, 3, 1, 2, "「あつものに懲りてなますを吹く」この言葉はどんな意味？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130031, 3, 1, 2, "第三者が一番わかっていることを表す「傍目八目」とは、もともとどんな遊びに関する言葉？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130033, 3, 1, 2, "「太公望」とは、何が好きな人のこと？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130034, 3, 1, 2, "リーダーに立った方が良い、という意味の言葉「鯛の尾よりも何の頭」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130035, 3, 1, 2, "「月に叢雲花に風」この言葉の意味は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130036, 3, 1, 2, "汚れにそまっていないこと、「泥中の何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130037, 3, 1, 2, "時期はずれのこと、「六日の菖蒲、十日の何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130039, 3, 1, 2, "わずかな希望を意味する「一縷の望み」。「一縷」の読み方はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130041, 3, 1, 2, "「思う壺」という言葉の壺とは何に使う壺？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130044, 3, 1, 2, "ことわざ「虻蜂取らず」で、虻と蜂を取ろうとした生き物は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130045, 3, 1, 2, "子供の機嫌の変わりやすさをいう言葉。「いま泣いた何がもう笑う」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130046, 3, 1, 2, "ことわざで「五月の鯉の吹き流し」や「宵越しの銭は持たぬ」と言われる人は？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130047, 3, 1, 2, "余計な発言はつつしむべきという言葉。「何も鳴かずば撃たれまい」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130048, 3, 1, 2, "ことわざで「転がる石には生えない」と言われるものは何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130049, 3, 1, 2, "文章の表現を練り直すという意味の熟語「推敲」の読み方は何？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130050, 3, 1, 2, "「鬼に金棒」と同じ意味のことわざ。「虎に何」？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130052, 3, 1, 2, "失敗してしまうことを指す言葉はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130053, 3, 1, 2, "「筆不精」とは反対に、よく手紙を書く人を何という？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130054, 3, 1, 2, "「漸く」の正しい読みはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130055, 3, 1, 2, "英単語「poet」の正しい意味はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130058, 3, 1, 2, "漢字で「無花果」と書く植物はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130059, 3, 1, 2, "漢字で「独活」と書く、山菜としても食べられる植物はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2130061, 3, 1, 2, "漢字で「燕子花」や「杜若」と書くアヤメ科の植物はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2131001, 3, 1, 2, "「深淵悠久＝翠翔裂空斬」の正しい読み方はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2131002, 3, 1, 2, "「盟約外典術式第666章」の正しい読み方はどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2131003, 3, 1, 2, "リノがキャラストーリー3話で「ここで会ったが百年目！」の「百年目」をなんと言い間違えた？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2131004, 3, 1, 2, "イベント名「ファボ！ラブ！大集合！　至高の祭典と少女の秘めた○○○」○の中に入るのは次のうちどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 1, 2131005, 3, 1, 2, "イベント名「エンジョイ＆リフレッシュ　でこぼこガールズ・○○○○」○の中に入るのは次のうちどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2210001, 1, 2, 2, "カエルの子どもはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2210003, 1, 2, 2, "普通のウマには無くてユニコーンにはあるのは？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2210004, 1, 2, 2, "次のうち、バッタの仲間は？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2210005, 1, 2, 2, "「ハシブト」「ハシボソ」などの種類がいる、頭がいいことで知られる鳥は？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2210006, 1, 2, 2, "食材の「ショウガ」をさすのはどれ？");
INSERT INTO `v1_7c5daa4e7a7508131df82aa9d1c821c347a26bd4ea52db45cf40933b758529b4` VALUES (0, 2, 2210007, 1, 2, 2, "わさび醤油ともよく合う、「森のバター」とも呼ばれる果実は何？");
