INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 160, 122601, 101, 0.029, 1, 110, 0, 1, 1, 1.5, 1, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 111, 120, 122401, 102, 0.014, 0, 105, 0, 2, 2, 2.5, 3, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 350, 122501, 103, 0.017, 0, 110, 1, 3, 3, 2.5, 2, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 105, 130, 107501, 104, 0.016, 0, 110, 0, 4, 4, 1.5, 3, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 160, 107601, 105, 0.014, 1, 110, 0, 5, 5, 2.5, 1, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 103, 255, 107801, 106, 0.029, 0, 120, 1, 6, 6, 3.5, 2, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 103, 160, 110601, 107, 0.013, 1, 120, 0, 7, 7, 3.5, 1, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 103, 350, 110401, 108, 0.014, 0, 110, 1, 8, 8, 2.5, 2, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 100, 130, 110501, 109, 0.012, 0, 105, 0, 9, 9, 1.5, 3, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 103, 200, 115701, 110, 0.014, 1, 105, 0, 10, 10, 2.5, 1, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 103, 350, 113601, 111, 0.071, 0, 105, 1, 11, 11, 2.5, 2, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 103, 130, 107901, 112, 0.014, 0, 120, 0, 12, 12, 1.5, 3, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 200, 108001, 113, 0.022, 1, 110, 0, 13, 13, 1.5, 2, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 108, 160, 110301, 114, 0.031, 1, 110, 0, 14, 14, 2.5, 1, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 105, 130, 107701, 115, 0.036, 0, 110, 0, 15, 15, 0.5, 1, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 95, 350, 113101, 116, 0.048, 0, 110, 1, 16, 16, 3.5, 2, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 105, 130, 113301, 117, 0.043, 0, 105, 0, 17, 17, 1.5, 3, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 95, 130, 113201, 118, 0.018, 0, 105, 0, 18, 18, 1.5, 2, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 108, 160, 117001, 119, 0.021, 1, 110, 0, 19, 19, 2.5, 2, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 255, 113501, 120, 0.036, 0, 110, 1, 20, 20, 2.5, 1, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 350, 113401, 121, 0.02, 0, 105, 1, 21, 21, 2.5, 3, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 105, 130, 110101, 122, 0.018, 0, 110, 0, 22, 22, 2.5, 1, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 111, 160, 110001, 123, 0.03, 1, 105, 0, 23, 23, 3.5, 3, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 103, 130, 117201, 124, 0.027, 0, 110, 0, 24, 24, 1.5, 1, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 108, 160, 117301, 125, 0.02, 1, 105, 0, 25, 25, 3.5, 2, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 350, 117401, 126, 0.04, 0, 110, 1, 26, 26, 2.5, 3, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 350, 117101, 127, 0.009, 0, 110, 1, 27, 27, 3.5, 1, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 105, 255, 122901, 128, 0.022, 0, 120, 1, 28, 28, 3.5, 1, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 105, 130, 122801, 129, 0.028, 0, 110, 0, 29, 29, 2.5, 3, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？");
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 108, 130, 122701, 130, 0.023, 1, 105, 0, 30, 30, 3.5, 2, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。");
