INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (122601, 110, 1, 1, 1.5, 0, 101, 1, 160, 0, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 105, 1, 0.029);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (122401, 105, 2, 3, 2.5, 0, 102, 2, 120, 1, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 111, 0, 0.014);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (122501, 110, 3, 2, 2.5, 1, 103, 3, 350, 0, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 105, 0, 0.017);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (107501, 110, 4, 3, 1.5, 0, 104, 4, 130, 1, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 105, 0, 0.016);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (107601, 110, 5, 1, 2.5, 0, 105, 5, 160, 0, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 105, 1, 0.014);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (107801, 120, 6, 2, 3.5, 1, 106, 6, 255, 0, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 103, 0, 0.029);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (110601, 120, 7, 1, 3.5, 0, 107, 7, 160, 0, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 103, 1, 0.013);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (110401, 110, 8, 2, 2.5, 1, 108, 8, 350, 0, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 103, 0, 0.014);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (110501, 105, 9, 3, 1.5, 0, 109, 9, 130, 1, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 100, 0, 0.012);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (115701, 105, 10, 1, 2.5, 0, 110, 10, 200, 0, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 103, 1, 0.014);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (113601, 105, 11, 2, 2.5, 1, 111, 11, 350, 0, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 103, 0, 0.071);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (107901, 120, 12, 3, 1.5, 0, 112, 12, 130, 1, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 103, 0, 0.014);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (108001, 110, 13, 2, 1.5, 0, 113, 13, 200, 0, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 105, 1, 0.022);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (110301, 110, 14, 1, 2.5, 0, 114, 14, 160, 0, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 108, 1, 0.031);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (107701, 110, 15, 1, 0.5, 0, 115, 15, 130, 1, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 105, 0, 0.036);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (113101, 110, 16, 2, 3.5, 1, 116, 16, 350, 0, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 95, 0, 0.048);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (113301, 105, 17, 3, 1.5, 0, 117, 17, 130, 1, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 105, 0, 0.043);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (113201, 105, 18, 2, 1.5, 0, 118, 18, 130, 1, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 95, 0, 0.018);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (117001, 110, 19, 2, 2.5, 0, 119, 19, 160, 0, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 108, 1, 0.021);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (113501, 110, 20, 1, 2.5, 1, 120, 20, 255, 0, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 105, 0, 0.036);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (113401, 105, 21, 3, 2.5, 1, 121, 21, 350, 0, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 105, 0, 0.02);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (110101, 110, 22, 1, 2.5, 0, 122, 22, 130, 1, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 105, 0, 0.018);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (110001, 105, 23, 3, 3.5, 0, 123, 23, 160, 0, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 111, 1, 0.03);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (117201, 110, 24, 1, 1.5, 0, 124, 24, 130, 1, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 103, 0, 0.027);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (117301, 105, 25, 2, 3.5, 0, 125, 25, 160, 0, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 108, 1, 0.02);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (117401, 110, 26, 3, 2.5, 1, 126, 26, 350, 0, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 105, 0, 0.04);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (117101, 110, 27, 1, 3.5, 1, 127, 27, 350, 0, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 105, 0, 0.009);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (122901, 120, 28, 1, 3.5, 1, 128, 28, 255, 0, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 105, 0, 0.022);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (122801, 110, 29, 3, 2.5, 0, 129, 29, 130, 1, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 105, 0, 0.028);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (122701, 105, 30, 2, 3.5, 0, 130, 30, 130, 0, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 108, 1, 0.023);
