INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 1.5, 160, 101, 122601, 110, 0.029, 1, 1, 1, 105, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 2.5, 120, 102, 122401, 105, 0.014, 3, 2, 0, 111, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 2);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 2.5, 350, 103, 122501, 110, 0.017, 2, 3, 0, 105, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 3);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 1.5, 130, 104, 107501, 110, 0.016, 3, 4, 0, 105, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 4);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 2.5, 160, 105, 107601, 110, 0.014, 1, 5, 1, 105, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 5);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 3.5, 255, 106, 107801, 120, 0.029, 2, 6, 0, 103, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 6);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 3.5, 160, 107, 110601, 120, 0.013, 1, 7, 1, 103, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 7);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 2.5, 350, 108, 110401, 110, 0.014, 2, 8, 0, 103, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 8);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 1.5, 130, 109, 110501, 105, 0.012, 3, 9, 0, 100, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 9);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 2.5, 200, 110, 115701, 105, 0.014, 1, 10, 1, 103, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 10);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 2.5, 350, 111, 113601, 105, 0.071, 2, 11, 0, 103, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 11);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 1.5, 130, 112, 107901, 120, 0.014, 3, 12, 0, 103, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 12);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 1.5, 200, 113, 108001, 110, 0.022, 2, 13, 1, 105, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 13);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 2.5, 160, 114, 110301, 110, 0.031, 1, 14, 1, 108, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 14);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 0.5, 130, 115, 107701, 110, 0.036, 1, 15, 0, 105, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 15);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 3.5, 350, 116, 113101, 110, 0.048, 2, 16, 0, 95, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 16);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 1.5, 130, 117, 113301, 105, 0.043, 3, 17, 0, 105, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 17);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 1.5, 130, 118, 113201, 105, 0.018, 2, 18, 0, 95, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 18);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 2.5, 160, 119, 117001, 110, 0.021, 2, 19, 1, 108, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 19);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 2.5, 255, 120, 113501, 110, 0.036, 1, 20, 0, 105, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 20);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 2.5, 350, 121, 113401, 105, 0.02, 3, 21, 0, 105, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 21);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 2.5, 130, 122, 110101, 110, 0.018, 1, 22, 0, 105, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 22);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 3.5, 160, 123, 110001, 105, 0.03, 3, 23, 1, 111, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 23);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 1.5, 130, 124, 117201, 110, 0.027, 1, 24, 0, 103, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 24);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 3.5, 160, 125, 117301, 105, 0.02, 2, 25, 1, 108, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 25);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 2.5, 350, 126, 117401, 110, 0.04, 3, 26, 0, 105, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 26);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 3.5, 350, 127, 117101, 110, 0.009, 1, 27, 0, 105, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 27);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 1, 3.5, 255, 128, 122901, 120, 0.022, 1, 28, 0, 105, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 28);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 0, 2.5, 130, 129, 122801, 110, 0.028, 3, 29, 0, 105, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 29);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (0, 0, 3.5, 130, 130, 122701, 105, 0.023, 2, 30, 1, 108, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 30);
