INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (1, 105, 1, 1, 1.5, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1, 122601, 110, 160, 0.029, 0, 0, 101);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (2, 111, 2, 0, 2.5, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 3, 122401, 105, 120, 0.014, 0, 1, 102);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (3, 105, 3, 0, 2.5, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 2, 122501, 110, 350, 0.017, 1, 0, 103);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (4, 105, 4, 0, 1.5, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 3, 107501, 110, 130, 0.016, 0, 1, 104);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (5, 105, 5, 1, 2.5, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 1, 107601, 110, 160, 0.014, 0, 0, 105);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (6, 103, 6, 0, 3.5, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 2, 107801, 120, 255, 0.029, 1, 0, 106);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (7, 103, 7, 1, 3.5, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 1, 110601, 120, 160, 0.013, 0, 0, 107);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (8, 103, 8, 0, 2.5, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 2, 110401, 110, 350, 0.014, 1, 0, 108);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (9, 100, 9, 0, 1.5, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 3, 110501, 105, 130, 0.012, 0, 1, 109);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (10, 103, 10, 1, 2.5, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 1, 115701, 105, 200, 0.014, 0, 0, 110);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (11, 103, 11, 0, 2.5, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 2, 113601, 105, 350, 0.071, 1, 0, 111);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (12, 103, 12, 0, 1.5, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 3, 107901, 120, 130, 0.014, 0, 1, 112);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (13, 105, 13, 1, 1.5, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 2, 108001, 110, 200, 0.022, 0, 0, 113);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (14, 108, 14, 1, 2.5, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 1, 110301, 110, 160, 0.031, 0, 0, 114);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (15, 105, 15, 0, 0.5, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 1, 107701, 110, 130, 0.036, 0, 1, 115);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (16, 95, 16, 0, 3.5, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 2, 113101, 110, 350, 0.048, 1, 0, 116);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (17, 105, 17, 0, 1.5, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 3, 113301, 105, 130, 0.043, 0, 1, 117);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (18, 95, 18, 0, 1.5, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 2, 113201, 105, 130, 0.018, 0, 1, 118);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (19, 108, 19, 1, 2.5, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 2, 117001, 110, 160, 0.021, 0, 0, 119);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (20, 105, 20, 0, 2.5, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 1, 113501, 110, 255, 0.036, 1, 0, 120);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (21, 105, 21, 0, 2.5, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 3, 113401, 105, 350, 0.02, 1, 0, 121);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (22, 105, 22, 0, 2.5, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 1, 110101, 110, 130, 0.018, 0, 1, 122);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (23, 111, 23, 1, 3.5, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 3, 110001, 105, 160, 0.03, 0, 0, 123);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (24, 103, 24, 0, 1.5, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 1, 117201, 110, 130, 0.027, 0, 1, 124);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (25, 108, 25, 1, 3.5, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 2, 117301, 105, 160, 0.02, 0, 0, 125);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (26, 105, 26, 0, 2.5, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 3, 117401, 110, 350, 0.04, 1, 0, 126);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (27, 105, 27, 0, 3.5, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 1, 117101, 110, 350, 0.009, 1, 0, 127);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (28, 105, 28, 0, 3.5, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 1, 122901, 120, 255, 0.022, 1, 0, 128);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (29, 105, 29, 0, 2.5, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 3, 122801, 110, 130, 0.028, 0, 1, 129);
INSERT INTO `v1_7d7e9b83e071f3c832b07d227314ad4b838f9f0366dfca21456d5a998888b656` VALUES (30, 108, 30, 1, 3.5, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 2, 122701, 105, 130, 0.023, 0, 0, 130);
