INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100101, 0.0, 2, 0, 1, 0, 4.0, 0, 0, 1, 1, 1001001, 0, "みんなが笑顔でいてくれたら\nあたしも笑顔でいられるんだ！\nだからほら、スマイルスマイル！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100101, 0.0, 2, 0, 2, 0, 3.0, 0, 0, 1, 1, 1001002, 0, "困った時はお互いさまさま！\n何かあったら相談してね！\nできること、あるはずだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100101, 0.0, 2, 0, 3, 0, 7.5, 0, 0, 1, 1, 1001003, 0, "いつでも元気なのが一番っ！\n疲れた時はちゃんと休んでね？\nあたしは…まだ元気だもんっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100101, 0.0, 2, 0, 4, 0, 6.0, 0, 0, 1, 5, 1001004, 0, "実はね…？　一緒にいると\nすっごく楽しいんだ！\nえへへ…ありがとね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100101, 0.0, 6, 0, 5, 0, 6.0, 0, 0, 1, 4, 1001005, 0, "なんだろ…時々\n胸の真ん中が熱くなるんだ\nもしかして風邪引いちゃった！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1001006, 0, "強くなるんだ…！\nあたしはみんなと…\nずっと笑顔でいたい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1001007, 0, "傷だらけになっても大丈夫！\n支えてくれる人がいるから…\n何度でも立ち上がってみせる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1001008, 0, "騎士くんが近くにいてくれたら\n元気100万倍って感じ！\n…ちょっと言い過ぎかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1001009, 0, "聞こえるよ…騎士くんの声\nどんなに離れてても、\nあたしには分かる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1001010, 0, "たとえどんな人が相手でも、\n今は負ける気がしない…！\n支えてくれるあの人がいるから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100101, 0.0, 2, 0, 6, 0, 11.3, 0, 0, 2, 1, 1001011, 0, "ハッピーバースデー、騎士くん！\nプレゼントは何がいい？\nごはん？　装備？　それとも…\nそうだ！　修行に付き合うね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1001012, 0, "お誕生日おめでとー！\nはいこれ！　猫のお守り！\nあたしがそばにいない時は、\nきっと代わりに守ってくれるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100161, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1001013, 0, "うにゃにゃにゃにゃー！\n騎士くんが一緒なら\nあたしは無敵だもんね！\nいっくぞぉー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100161, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1001014, 0, "どんなピンチだって、今日まで\n騎士くんと乗り越えてきた！\nこれからも二人でいれば\nあたしは負けないよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100161, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1001015, 0, "守りたい笑顔があるから\nあたしは戦える！\nだから騎士くん\nあたしのそばにいて…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100161, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1001016, 0, "もっとドキドキを感じたい！\nよくわかんないけど、\nそうすれば騎士くんとなんだって\nできそうな気がするから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100161, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1001017, 0, "あたし、こんなに強かったっけ？\nきっと騎士くんのおかげだね！\nだからずっとそばにいてくれる？\nだ、だって、強くなりたくて…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100201, 0.0, 5, 0, 1, 0, 6.0, 0, 0, 1, 4, 1002001, 0, "あ、あの…\nわたしが話し相手で、\n本当にいいの？\n…そっか。ありがとう…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100201, 0.0, 5, 0, 2, 0, 4.0, 0, 0, 1, 2, 1002002, 0, "戦うなら、\n治療と援護はまかせて！\n攻撃はその、一緒に\n頑張って欲しい…かな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100201, 0.0, 8, 0, 3, 0, 4.0, 0, 0, 1, 4, 1002003, 0, "お願い…\nそんなにじっと見ないで…\nは、恥ずかしいよ…あうぅ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100201, 0.0, 2, 0, 4, 0, 4.0, 0, 0, 1, 1, 1002004, 0, "お腹空いてない？　ご飯が\n食べたくなったら言ってね\n軽い物ならすぐ作れるから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100201, 0.0, 5, 0, 5, 0, 3.0, 0, 0, 1, 1, 1002005, 0, "何か手伝ったほうがいい？\nどこかへ行くなら、わたしも\n連れて行って欲しいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1002006, 0, "わたしに出来ることがあったら、\n何でも言ってね\nちょっとした怪我なら、\nすぐに治せるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1002007, 0, "騎士クンのためなら\nわたし…もっともっと\nがんばろうって思える！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1002008, 0, "癒しの輝きを…！\n見てるだけで癒されるって、\nそんな…なんか、\n恥ずかしくなってきちゃった…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1002009, 0, "どんな魔法を勉強しよう…\n恋が成就する魔法…\nそ、そんなのあるわけないよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1002010, 0, "新しく覚えた魔法はね、\n防炎、防寒、耐熱…あとは、\nのどに魚の骨が刺さらない\n魔法…内緒だよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100201, 0.0, 5, 0, 6, 0, 7.9, 0, 0, 2, 1, 1002011, 0, "騎士クン。お誕生日おめでとう\nあの…これ、お祝いのケーキ！\nその…良かったら食べてみて！\nえっと…これからも、よろしくね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1002012, 0, "誕生日おめでとう、騎士クン\n今日はケーキの他にも\nいろんな料理を用意したから\nみんなでお祝いしようね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100261, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1002013, 0, "騎士クンが、大切な人が\n傷ついた時は癒してあげたい…\nそれが今の、わたしの願い");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100261, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1002014, 0, "騎士クン、痛いところはない？\n傷はちゃんと治ってる？\n…よかった。もう大丈夫だね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100261, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1002015, 0, "お願い騎士クン。これからも\nわたしに、勇気をください…\nもっと強く、優しくなるために！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100261, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1002016, 0, "思いの強さなら、わたしだって…\nでも、気持ちだけじゃダメだよね\nちゃんと行動で示さないと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100261, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1002017, 0, "騎士クンを好きな気持ちが\nいつもわたしに勇気をくれる…\n騎士クン！？　今の聞いて…！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100301, 0.0, 2, 0, 1, 0, 4.0, 0, 0, 1, 1, 1003001, 0, "目的を果たすためなら、\nどんな努力でもする。キミにも\nそういう気持ちでいてほしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100301, 0.0, 6, 0, 2, 0, 4.5, 0, 0, 1, 2, 1003002, 0, "次の修行の予定について\n相談したいんだけど、いい？\n場所はどこにしようか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100301, 0.0, 2, 0, 3, 0, 4.5, 0, 0, 1, 1, 1003003, 0, "強くなりたい…\nその気持ちさえ失わなければ\n必ずキミも強くなれるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100301, 0.0, 2, 0, 4, 0, 2.0, 0, 0, 1, 6, 1003004, 0, "何故だろう…\nキミと剣を交えていると\n踊っているみたいだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100301, 0.0, 2, 0, 5, 0, 4.5, 0, 0, 1, 1, 1003005, 0, "今度一緒に釣りへ行かない？\n一人でも退屈はしないけど\nキミと行きたいんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1003006, 0, "さあ構えて！\n私から一本取るんだろう？\nのんびりしている暇はないよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1003007, 0, "決して諦めないその目…\nやっぱりキミはすごいな\n私の見込みは正しかった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1003008, 0, "キミはどんどん強くなる\nこんなものが限界じゃないはず\n…それを証明してみせて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1003009, 0, "（不思議な気分…\nずっと続けていたいけど、\nそういうわけにもいかないか）");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1003010, 0, "何でもいうことを聞くとは\nいったけど…破廉恥なのは\nなしだからね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100301, 0.0, 5, 0, 6, 0, 2.6, 0, 0, 2, 2, 1003011, 0, "誕生日おめでとう\n実は何を渡せば喜んでくれるか\n分からなくて…良ければ\n一緒に選びにいかない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1003012, 0, "キミの誕生日か。おめでとう\nせっかくだし今日の修業は\nなしにしよう。…あれ？\nどうして不満そうなの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1003013, 0, "キミの思いが、私を\n強くしてくれているんだね…\n私もその思いに応えよう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1003014, 0, "驚くほど体が軽い…\nこれなら剣技も、いつも以上に\n冴え渡る！　いくよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1003015, 0, "この力は、私とキミの絆の力…\nこれからも二人一緒に、\nどこまでも強くなりたい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1003016, 0, "私にとってキミは、\nかけがえのない存在だよ\n今までも、この先も…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1003017, 0, "私が自分を信じられるのは\nキミが私を信じてくれるから…\nお願い…ずっと私を信じていて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100401, 0.0, 2, 0, 1, 0, 2.5, 0, 0, 1, 1, 1004001, 0, "あっ、にいちゃんだー！\nねえねえ、\nミソギとなにして遊ぶ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100401, 0.0, 4, 0, 2, 0, 4.0, 0, 0, 1, 4, 1004002, 0, "う～イタズラ用の\n激辛キャンディー、\n自分で食べちゃったよぉ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100401, 0.0, 6, 0, 3, 0, 6.0, 0, 0, 1, 1, 1004003, 0, "よーし、イタズラの設置完了！\nにいちゃんをひっかけて…\nあっ…もしかして、\n見ちゃった？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100401, 0.0, 2, 0, 4, 0, 4.5, 0, 0, 1, 1, 1004004, 0, "ミソギがイタズラを用意する…\nにいちゃんが引っかかる…\nうんっ、最高のコンビだね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100401, 0.0, 2, 0, 5, 0, 4.0, 0, 0, 1, 5, 1004005, 0, "にいちゃん、もっともっと\n遊ぼうね。約束だよ！\nほら、指切りげんまん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1004006, 0, "ミソギ探検隊、全員集合！\nミソギとにいちゃんの二人だけ\nなんだけどね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1004007, 0, "これからも、いっぱい\nにいちゃんで遊べるよね！\n…あ、にいちゃんと遊べる、\nって言いたかったんだよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1004008, 0, "にいちゃーん！\n早く早くっ探検に行こっ！\nにいちゃんのこと\n待ってたんだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1004009, 0, "大好きなにいちゃんのために、\nたっくさんイタズラ\n用意しなくっちゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1004010, 0, "ミソギねぇ、本当の\nにいちゃんが出来たみたいで、\nすっごくうれしいんだー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100401, 0.0, 2, 0, 6, 0, 7.6, 0, 0, 2, 1, 1004011, 0, "誕生日おめでとう、にいちゃん！\nはい、バースデープレゼント！\nそれから、バースデーケーキ！\n早く開けて！　食べて！　わくわく");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1004012, 0, "誕生日おめでと！　ミソギ、\nにいちゃん大好き！　本当だよ！\nんーイタズラばっかりしてるから\n信じてもらえないや…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100461, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1004013, 0, "にいちゃん！\nパワーアップしたミソギの\nスペシャルなイタズラ\nぜったい見逃さないでね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100461, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1004014, 0, "にいちゃんとミソギなら\nどんな魔物もあっという間に\nやっつけられちゃうよね！\nえへへ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100461, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1004015, 0, "にいちゃんと一緒に居ると\nどんどん力が湧いてくる！\nこの力でにいちゃんに\nすっごいイタズラしちゃお♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100461, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1004016, 0, "どんどん新しいイタズラが\n浮かんでくるよ！\nあ～早く全部にいちゃんで\n試したいな～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100461, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1004017, 0, "にいちゃんも\nミソギのイタズラからは\n逃げられないよ！\n覚悟してね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100501, 0.0, 2, 0, 1, 0, 4.6, 0, 0, 1, 1, 1005001, 0, "あんたが困ったときは、是非とも\n自分を呼んでほしいッス！\n見習いでも、\n心はヒーローッスから！　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100501, 0.0, 2, 0, 2, 0, 3.2, 0, 0, 1, 1, 1005002, 0, "一緒に悪の組織を退治しに\n行くッス！　半人前でも\n二人で行けば一人前ッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100501, 0.0, 4, 0, 3, 0, 8.8, 0, 0, 1, 1, 1005003, 0, "お父さんやお母さんが、やたらと\n可愛い恰好させようとするッス\nリボンとかレースとか…でも\n自分は飾りの少ない服が好きッス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100501, 0.0, 5, 0, 4, 0, 4.6, 0, 0, 1, 1, 1005004, 0, "たまには二人で\n静かなところに行きたいッス\nヒーローにも、休息は\n必要ッスから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100501, 0.0, 5, 0, 5, 0, 9.8, 0, 0, 1, 4, 1005005, 0, "あんたと一緒にもっともっと\n遊びたいッスけど…これ以上\n仲良くなってもし秘密が…\nい、いやその何でもないッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1005006, 0, "とぉーうっ！\n悪に立ち向かう勇気がある限り\n正義は必ず勝つッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1005007, 0, "うおおおっ！　燃えてきたー！\n真のスーパーヒーロー目指して\n突撃ッスー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1005008, 0, "自分、世界平和のためなら\nどんな敵にも負けないッス！\nヒーローに二言は無いッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1005009, 0, "あんたがくれた勇気で\n最強のヒーローに\nバッチリ変身ッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1005010, 0, "あんたと一緒なら勇気100倍、\nいや100万倍ッス！\nこれからも一緒に頑張るッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100501, 0.0, 2, 0, 6, 0, 5.3, 0, 0, 2, 1, 1005011, 0, "えっ、今日誕生日なんスか！？\nじゃあ、お祝いにとっておきの\nトラタイガーグッズあげるッス！\nお近づきの印ッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1005012, 0, "ハッピーバースデー♪\nいつもお世話になってるアンタに\nトラタイガーのカップケーキッス！\n自分、手作りに挑戦したッスよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100561, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1005013, 0, "自分たちは正義のヒーローッス！\n種族なんて関係ない…\nみんなを守るために戦う！\n騎士団は、変わったんスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100561, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1005014, 0, "あんたも困ったことがあったら\n遠慮なく自分たちに相談するッス\nいつだって、力になるッスよ\nそれが、ヒーローッスから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100561, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1005015, 0, "みんなで力を合わせて\n大切なものを守る…\n一人だけじゃ難しいことも\n仲間がいたらなんとかなるッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100561, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1005016, 0, "なんの遠慮もなく、自分が\nこうしてお仕事できるのは\nあんたのおかげッス\nこれからも、ずっと一緒ッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100561, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1005017, 0, "自分の活躍、見ててほしいッス\nいろんな人のお世話になったッス\nけど、一番辛いとき、あんたは\nそばにいてくれた人ッスから…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100601, 0.0, 2, 0, 1, 0, 3.5, 0, 0, 1, 5, 1006001, 0, "お兄ちゃん、\nまたあとでサックスの練習、\n付き合ってくれますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100601, 0.0, 5, 0, 2, 0, 5.0, 0, 0, 1, 4, 1006002, 0, "はぁ、はぁ…\nもう練習できません…\nお兄ちゃん激しすぎます…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100601, 0.0, 4, 0, 3, 0, 3.0, 0, 0, 1, 6, 1006003, 0, "え？　もっとするの…？\n今日はもうヘトヘト…\n私、限界です、お兄ちゃん…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100601, 0.0, 2, 0, 4, 0, 5.5, 0, 0, 1, 5, 1006004, 0, "お兄ちゃんったら\nいつも激しすぎて、私…\nドキドキが止まらないんです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100601, 0.0, 4, 0, 5, 0, 4.5, 0, 0, 1, 3, 1006005, 0, "あのね、お兄ちゃんは\n私とお姉ちゃんとどっちが…\nううん、何でもないです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1006006, 0, "ふふ、お兄ちゃん見てください！\nジャズのリズムに乗って\nアカリ流ポールダンスです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1006007, 0, "お兄ちゃん、そんなに\nアカリのが見たいんですか…\nそ、それじゃ…恥ずかしいけど\n行きます！　アカリ大車輪―！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1006008, 0, "はあ、はあ…もう、らめぇ…\nお兄ちゃんが上手だって\n褒めてくれるから、つい\n調子に乗って回りすぎちゃった♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1006009, 0, "め、目が回っちゃいました～\nお兄ちゃん抱っこしてください♪\nアカリをこんなにした責任…\nとってくださいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1006010, 0, "こんなアカリの姿、誰にでも\n見せるわけじゃないですよ？\nお兄ちゃんは特別だから、\nアカリの全てを見てほしいの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100601, 0.0, 5, 0, 6, 0, 5.9, 0, 0, 2, 1, 1006011, 0, "あ！　お兄ちゃんったら、\nまた大きくなっちゃいましたね\nなんだかアカリ、\nドキドキしてきちゃいました～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1006012, 0, "お兄ちゃん、お誕生日ですよね\nおめでとうございます！\nアカリと一緒に、\n大人の階段、上りましょうね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1006013, 0, "感じます、お兄ちゃんの力…\nあぁ、ドキドキが止まりません！\nいまのアカリなら、きっと\nなんでもできちゃいます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1006014, 0, "お兄ちゃんのおかげで、アカリ、\n魔法だってうまくなったんです！\nかわいくて強い、最高の\nアカリになれちゃいました♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1006015, 0, "見ててくださいね、お兄ちゃん！\n一緒に特訓してきた成果を\n見せちゃいますから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1006016, 0, "お兄ちゃん、今日のアカリも\nかわいいですか？\nもう、恥ずかしがらないで、\nかわいいって褒めてほしいです～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1006017, 0, "お兄ちゃんとの特訓はいつも\n『ハジメテ』だらけで楽しいです！\nこれからも二人で『ハジメテ』を\nいっぱい体験しましょうね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100701, 0.0, 2, 0, 1, 0, 4.5, 0, 0, 1, 1, 1007001, 0, "１にプリン、２にプリン、\n３、４もプリンで、\n５も、もちろんプリンなの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100701, 0.0, 2, 0, 2, 0, 6.0, 0, 0, 1, 1, 1007002, 0, "お空を飛ぶのが好きなの～\nイタズラするのも好きなの～\nでもプリンは\nもーっと好きなの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100701, 0.0, 6, 0, 3, 0, 5.0, 0, 0, 1, 7, 1007003, 0, "魔物に追いかけられて、\n死ぬかと思ったの～\n…って、よく考えたら、\nもう死んでたの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100701, 0.0, 6, 0, 4, 0, 7.0, 0, 0, 1, 5, 1007004, 0, "ミヤコは幽霊なの～\nだから、オマエが死んでも\nずっと一緒なの～♪\nん？　うれしくないの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100701, 0.0, 5, 0, 5, 0, 3.0, 0, 0, 1, 2, 1007005, 0, "今日は一緒にお出かけするのー\nだから今日はずっと、\nオマエに憑いてるの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1007006, 0, "もっともっともっともっと\nたくさんプリンを買って来るの！\nそうじゃないとミヤコ、\n成仏しちゃうかもしれないの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1007007, 0, "ミヤコのプリンは、\n絶対にあげないの～！\nもし一口でも盗ったら、\n夢枕に立ち続けてやるの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1007008, 0, "幽霊は人を怖がらせるものなの～\nオマエも、もっと怖がれなのー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1007009, 0, "ムー！　オマエはミヤコとプリン\nどっちが大事なの！？\n…どっちも大事？\nそれならいいの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1007010, 0, "ミヤコばかり幽霊でずるいの！\nオマエも死んで幽霊になるの！\nそうすれば、二人でずっと\nいっしょにいられるの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100701, 0.0, 2, 0, 6, 0, 5.8, 0, 0, 2, 1, 1007011, 0, "ミヤコは死んでるけど、誕生日の\nお祝いは大事にしてるの！\nだから早くバースデープリン\nパーティーを始めるの～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1007012, 0, "おめでとうなの！　誕生日は、\n仲良しとごちそうを食べるの\nだから今日は、ミヤコと一緒に\nプリンケーキを食べるの～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1007013, 0, "ふっふっふ……\nおもしろいことするヤツらなの\nだからお返しに、もっとも～っと\nおもしろいことをしてやるの…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1007014, 0, "冥土のおみやげに\nオマエらをプリンにしてやるの\nプリンはおみやげに\nもってこいなの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1007015, 0, "オマエの恨みはミヤコが晴らすの\nだからスプーン二本なの\n二人分の恨み、\nミヤコが思い知らせてやるの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1007016, 0, "ミヤコのものはミヤコのものなの\nミヤコがほしいと思ったから\nオマエはもうミヤコのものなの！\nじょーしきなの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1007017, 0, "オマエが幽霊でも\n幽霊じゃなくても、関係ないの\nオマエがオマエなら\nミヤコはそれでいいの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100801, 0.0, 2, 0, 1, 0, 7.0, 0, 0, 1, 1, 1008001, 0, "あ、良いところに来てくれた！\nより可愛く見える角度を、\nさっき発見したんだよ！\nキミも、見て見て！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100801, 0.0, 2, 0, 2, 0, 6.5, 0, 0, 1, 5, 1008002, 0, "このボクの美しさ…\nいつか、「歩く観光名所」って\n呼ばれちゃったりするのかな？\nガイドブックを作らないと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100801, 0.0, 1, 0, 3, 0, 7.0, 0, 0, 1, 6, 1008003, 0, "あっ、キミ！　いいところに！\nボクの美しさがとどまることを\n知らないんだ！\n止めて！　いや、止めないで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100801, 0.0, 2, 0, 4, 0, 4.5, 0, 0, 1, 5, 1008004, 0, "こんなに可愛いボクと\n一緒にいられること、喜んでね？\nキミは、世界一の\n幸せ者なんだから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100801, 0.0, 2, 0, 5, 0, 4.0, 0, 0, 1, 5, 1008005, 0, "ボクは、キミといたいから\nここにいるんだよ\n引き立て役ってわけじゃ\nないから、安心してね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1008006, 0, "どんな宝石や宝物も…\nボクの美しさと輝きには\n勝てないんだなあ…\n罪作りな、ボク♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1008007, 0, "ああ、本当にボクは美しい♪\nボクの美しさを表現するには…\nいや、言葉じゃ表現できないよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1008008, 0, "ボクほど美しくない、なんて\n恥ずかしがることじゃないよね\nだって改めて言うまでもなく\n当たり前じゃない、そんなの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1008009, 0, "いつか、どんな宝石よりも\n価値のあるこのボクを…\nキミにプレゼントするって\n言ったら、キミは喜ぶかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1008010, 0, "このボクがここにいるという\nプレシャス…キミと\n二人で作っていくときめき…\n世界は希望に満ちているんだね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100801, 0.0, 2, 0, 6, 0, 9.9, 0, 0, 2, 1, 1008011, 0, "誕生日おめでとう♪\n今日のキミは輝いて見えるね\n綺麗で美麗で華麗で壮麗だ\nきっと世界で二番目に美しいよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1008012, 0, "ハッピーバースデー♪\n今日はボクの美しさを、\n独り占めにしてもいいよ♪\n全部受け止めきれるかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1008013, 0, "ボクの美しさはもはや神秘の領域\n奇跡や魔法に等しいよ\nボクと同じ時代を生きてることを\n誉れに思ってよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1008014, 0, "笑顔も泣き顔も、怒った顔だって\n究極にかわいいボク♪\nねえ、キミはどのボクが\n一番好きなのかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1008015, 0, "もし美しさが罪になるのなら\nボクは世界一の大罪人に\nなっちゃうね！　ああ、自分の\n美しさが恐ろしいよ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1008016, 0, "ここまでボクについてこられる\nなんて、キミは想像以上だよ\nそんなにボクの隣がいいのかい？\nならこれからもいるといいよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1008017, 0, "常に更新し続ける美の最高到達点\nそれを見続ける権利を\nキミにあげるよ！　ふふ、これで\nもうボクから離れられないね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100901, 0.0, 2, 0, 1, 0, 3.5, 0, 0, 1, 1, 1009001, 0, "貴様も早く前世の記憶を\n取り戻すのだ！　我が宿敵にして\n盟友、閃光のシグルドよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100901, 0.0, 2, 0, 2, 0, 2.5, 0, 0, 1, 1, 1009002, 0, "我が真名はちゃんと覚えたか？\nアンネローゼ・フォン・\nシュテッヒパルムだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100901, 0.0, 3, 0, 3, 0, 5.0, 0, 0, 1, 4, 1009003, 0, "くっ…！　魔力を封じた\n「瞳」が疼く…！\n目薬？　いらんわっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100901, 0.0, 2, 0, 4, 0, 5.5, 0, 0, 1, 1, 1009004, 0, "シグルドよ、現世においても\n我ら盟友の絆は不滅だ！\nはーっはっはっはっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100901, 0.0, 5, 0, 5, 0, 5.0, 0, 0, 1, 1, 1009005, 0, "我ら二人で手を取り合い\n『機関』の野望を打ち砕こう！\nそしてその暁には、前世と同じく\nめ、夫婦の誓いを…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1009006, 0, "その目に、魂に刻めっ…！\nこれぞ我が禁断の\n最上究極殲滅魔法だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1009007, 0, "貴様も技を持っているなら\nかっこよく放つべきだ！\n例えばだな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1009008, 0, "魔力を込め続けた\n私の瞳が輝きを放つ時\n敵の命運は尽きる…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1009009, 0, "防御を捨て、攻撃に特化した\n『撃滅形態』をとくと見よ！\n…ど、どこを見ているのだ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1009010, 0, "シグルドよ…貴様のおかげで\nいつも私は自分を貫けている\nだから…ありがとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100901, 0.0, 2, 0, 6, 0, 6.8, 0, 0, 2, 1, 1009011, 0, "今日は貴様が転生した日か\n記念に私からこれを授けよう！\n見よ！　暗黒騎士の紋章を！\n…どうだ。かっこいいだろう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1009012, 0, "シグルドよ、誕生日おめでとう！\n私からは日記をプレゼントだ！\n我らの活動を書き残すがいい！\n…字は書ける、よな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100961, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1009013, 0, "慄くがいい！\n何人たりとも\n我が魔瞳の魔力には\n抗えはしない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100961, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1009014, 0, "さあ、始めようか！\n破壊と再生…\nあらたな神話を\n目に焼き付けるがいい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100961, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1009015, 0, "世界が終末を迎えようとも…\n我らの絆は滅びはしない！\nそうだろう？　シグルドよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100961, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1009016, 0, "シグルド、失われし我が半身よ…\nって、聞こえてたのか？\nいや、その…\nな、なんでもないってばぁ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 100961, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1009017, 0, "我が封印は完全に解かれた！\n魂の盟友と共にある限り、\n疾風の冥姫は止められん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101001, 0.0, 2, 0, 1, 0, 5.5, 0, 0, 1, 1, 1010001, 0, "王子はん、王子はん\nおぶういかがどすかぁ？\nおぶういうんはお茶どす！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101001, 0.0, 2, 0, 2, 0, 6.0, 0, 0, 1, 1, 1010002, 0, "女の子は夢の国で暮らすことに、\n夢を見るんどすなぁ\n運命の王子はんと夢の国～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101001, 0.0, 2, 0, 3, 0, 5.5, 0, 0, 1, 5, 1010003, 0, "古今東西の絵本ではなぁ、\nお姫はんを守るのは、\n王子はんの役目なんよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101001, 0.0, 2, 0, 4, 0, 4.5, 0, 0, 1, 1, 1010004, 0, "怪我したときは、\nうちを頼ってほしいわぁ\n特別な呪文があるんどす\n痛いの痛いの飛んでけ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101001, 0.0, 2, 0, 5, 0, 5.5, 0, 0, 1, 1, 1010005, 0, "朝、目を覚ましたら、\nまずは小鳥はんたちと、\nおはよーって挨拶するんどす～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1010006, 0, "『くるりんぱっ☆　』あららぁ、\nここ洞窟やってんなあ\nでもうちの魔法で明るうなったし\nそれにとってもきれいですやろ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1010007, 0, "え、手ぇ引いてくれはるん？　\nおおきに、嬉しおす…\nほなうちは王子はんの足元明るう\n照らして、一緒に歩きまひょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1010008, 0, "王子はんの道筋はうちが\n照らします～\nせやから、王子はんは安心して\n一緒にいておくれやす～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1010009, 0, "王子はんとならどこにいても\nお伽噺の世界になるわ～\nさあ王子はん、マホ姫を連れて\n探検に参りまひょ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1010010, 0, "ずーっと一緒に歩きまひょ\nたまぁに足踏み外したかて\n二人でいてたら心配あらへんよ\nせやろ？　マホ姫の王子はん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101001, 0.0, 5, 0, 6, 0, 4.0, 0, 0, 2, 2, 1010011, 0, "誕生日、おめでとうさん♪\n王子はんの誕生日を\n一緒にお祝いできて幸せやわぁ♪\nこれからもほっこりしようなぁ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1010012, 0, "今日が誕生日なんどすか？\nそれは、ほんにめでたいなあ～\n今日をマホマホ王国の\n記念日にしたいぐらいやわあ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101061, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1010013, 0, "さ～て、みんなを\nマホマホ王国にご招待や～\nみらくるまほりんくるりんぱ♪\nも一つおまけにくるりんぱ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101061, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1010014, 0, "マホマホ王国のパレード開始や～\n王子はんも、\nマホマホ王国の王子として、\nみんなと一緒に楽しんでな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101061, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1010015, 0, "これがうちと王子はんの力…\nこないなことができるなんて、\nやっぱり王子はんとうちは\n運命の相手なんやね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101061, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1010016, 0, "王子はんがそばにおってくれたら\nうち、なんでもできる気がする…\n王子はん、\nこれからもずっとそばにいてな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101061, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1010017, 0, "王子と姫がそろうとき、\nマホマホ王国への扉が開かれる…\nさあ、王子はん\n二人でマホマホ王国に凱旋や～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101101, 0.0, 6, 0, 1, 0, 4.0, 0, 0, 1, 5, 1011001, 0, "お兄ちゃんと私は、\n血は繋がってないけど\nきょうだいなの！\nえ？　よくわかんない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101101, 0.0, 6, 0, 2, 0, 3.0, 0, 0, 1, 1, 1011002, 0, "私の弓は百発八中！\nん？　これじゃあ、ほとんど\n当たってないことに…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101101, 0.0, 1, 0, 3, 0, 4.0, 0, 0, 1, 2, 1011003, 0, "今度お兄ちゃんと、\nスイーツ巡りしたいなぁ\nつきあってくれますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101101, 0.0, 2, 0, 4, 0, 4.0, 0, 0, 1, 1, 1011004, 0, "お兄ちゃんが一緒なら、\nなんだってできる気がします！\nよーし、どんどん行きましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101101, 0.0, 2, 0, 5, 0, 6.0, 0, 0, 1, 5, 1011005, 0, "私たちの絆、どんどん深まってる\nお兄ちゃんも…\nそう思ってくれてたらいいなぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1011006, 0, "なんか、最近は\n「ケーキがいい」んですって！\n早速、食べに行きましょうよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1011007, 0, "弓も上達しなくっちゃ…\nお兄ちゃんの前で\nカッコ悪いところ\n見せられないもん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1011008, 0, "「習うより離れろ」って、\nお兄ちゃんに勉強\n教わってちゃダメってこと？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1011009, 0, "えへへ、この調子だと…\nお兄ちゃんのハートを射抜ける\n日も、近いかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1011010, 0, "お兄ちゃんを傷つける相手は\n私がこの弓で、ぜーんぶ\nやっつけちゃうんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101101, 0.0, 2, 0, 6, 0, 7.2, 0, 0, 2, 1, 1011011, 0, "お誕生日おめでとうございます！\nお兄ちゃん、やっぱり誕生日は\n家族と過ごすべきですよ\n私とパーティーしましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1011012, 0, "ハッピーバースデー！\nお兄ちゃんが大きくなっても、\n私が歳を取っても\n二人は兄妹のままですからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101161, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1011013, 0, "妹さえいれば兄に金棒ですよ！\n私の活躍しっかり見てて下さいね\nお兄ちゃん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101161, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1011014, 0, "お兄ちゃんといっしょですから\n今の私は無敵ですよ！\nイヤでも鉄砲でもどんとこいです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101161, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1011015, 0, "例えお兄ちゃんになにがあっても\n私はお兄ちゃんの妹ですからね\n絶対に見つけて、再会して…\nそして、ずっと一緒にいるんです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101161, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1011016, 0, "任せてください、お兄ちゃん\n私がいる限り、お兄ちゃんには\n指一本触れさせませんからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101161, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1011017, 0, "お兄ちゃんのおかげで、\nとってもいい調子なんです\nこのまま恋も受験も\nバッチリいただいちゃいますよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101201, 0.0, 1, 0, 1, 0, 4.0, 0, 0, 1, 2, 1012001, 0, "何かあったら、\n私にどーんと頼っていいんだよ！\nハツネちゃんが、\n全力で助けるのだー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101201, 0.0, 1, 0, 2, 0, 3.0, 0, 0, 1, 2, 1012002, 0, "ぐっすり眠って元気いっぱい！\nほらほらぼーっとしてると\n置いてっちゃうぞー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101201, 0.0, 5, 0, 3, 0, 5.0, 0, 0, 1, 4, 1012003, 0, "ん～、ちょっと眠たいけど…\nじょぶじょぶ大丈夫！\nきら～ん☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101201, 0.0, 6, 0, 4, 0, 9.0, 0, 0, 1, 2, 1012004, 0, "スヤスヤ…\nシオリン…柔らか～い…\nんん？　あれ？　キミだったの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101201, 0.0, 2, 0, 5, 0, 6.0, 0, 0, 1, 5, 1012005, 0, "いぇ～い、ピースピース！\n魔物なんか、\n超能力で一発だよ！\n私は強いのだぁ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1012006, 0, "ねえねえ、キミも一緒に\n寝てみない？　太陽さんを\nいーっぱいあびた草原のお布団！\nとっても気持ちいいよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1012007, 0, "あのね、超能力をたくさん\n使っちゃっても、こうやって\n大地のパワーでチャージ完了！\nバッチリだよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1012008, 0, "えへへ\nキミの側にいると、ポカポカ\nしてきて、なんだか…眠く\n…ふあぁ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1012009, 0, "スヤスヤ…\nキミがいると、気持ちいい…\nいい夢見られそう…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1012010, 0, "なんだか、いい夢\n見てた気がする\nキミと一緒に…\nえへへ、予知夢だったらいいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101201, 0.0, 2, 0, 6, 0, 7.2, 0, 0, 2, 1, 1012011, 0, "お誕生日のお祝いに、\nハツネ膝枕をプレゼント！\n寝心地いいよ～☆\nん～なんだか私も眠く…すやぁ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1012012, 0, "お誕生日だね。おめでとうー！\nお祝いに超能力で…\nあっで、でも眠くなっちゃうし～\nじゃあ……………………カプッ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101261, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1012013, 0, "いっくよー☆　きらーん☆\nきっらきらのお星さま、\nみ～んなにプレゼントだよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101261, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1012014, 0, "今日のハツネちゃんは\n絶好調なのだ！\nキミと一緒なら、\n何でもできちゃう気がするよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101261, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1012015, 0, "それー！　ハツネちゃんの\nスターライト☆ショーだよ～！\nキミには一番の特等席で\n見せてあげちゃうね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101261, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1012016, 0, "それ！　きらーん☆\nキレイだからって、\n星ばかり見てちゃダメだからね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101261, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1012017, 0, "見て見て☆\nハツネちゃんのとっておき\nキミだけに見せちゃうぞ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101301, 0.0, 2, 0, 1, 0, 7.3, 0, 0, 1, 1, 1013001, 0, "日課の定期巡回！\nレアな魔導書なんかあった日には\nwktkが止まりませんぞー！\nさあ、いざ君もこっちの世界へ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101301, 0.0, 5, 0, 2, 0, 1.5, 0, 0, 1, 2, 1013002, 0, "ヒャッハー！\n推しキャラの抱き枕に顔を埋めて、\n魔力を充電せねばー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101301, 0.0, 2, 0, 3, 0, 7.4, 0, 0, 1, 1, 1013003, 0, "私はたくさんの人を守る魔法少女\n…老若男女、全ての命を平等に\n…もちろん、大きなお友達もね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101301, 0.0, 2, 0, 4, 0, 5.2, 0, 0, 1, 1, 1013004, 0, "君にはお世話になってるし、\nたまにはナナカちゃんが\nなにか奢ってあげよー！\nよーし、支払いはまかせろー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101301, 0.0, 2, 0, 5, 0, 4.5, 0, 0, 1, 1, 1013005, 0, "私は魔法少女としてみんなを\n笑顔にしたいのだ！\nもちろん、君のこともね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1013006, 0, "絶大な力の前に絶望するナナカ\nそんな中、新たな魔導書の力が\nナナカに奇跡をもたらす！\n次回、ナナカ死す！　お楽しみに");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1013007, 0, "新しい魔法を使う時って\nすごくドキドキするんだー！\n思った通りに成功すると、\nもうテンションマックス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1013008, 0, "この魔法は、私自身の命を糧に\n放つ最後の必殺技…えへへ\n君との毎日、楽しかったな\n…っていう展開よくない！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1013009, 0, "君と私のバディタッグで挑めば、\nどんな敵だって怖くないよ！\n私たちは同志であり相棒！\n二人で一人の魔法少女なのだー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1013010, 0, "私の魔法少女物語は、\n君っていう相棒がいてこそ！\n私たち二人で、みんなを守れる\n魔法少女になろうね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101301, 0.0, 1, 0, 6, 0, 2.8, 0, 0, 2, 2, 1013011, 0, "ハッピーバースデイッ！！\nキミへのプレゼントはこれだっ！\n私のおすすめ作品全185巻！\n…持って帰れるかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1013012, 0, "お誕生日おめでとーっ！\n眼鏡をプレゼントだー！\nつけてみて！　…うん！\nパーペキ！　似合ってるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1013013, 0, "ふおおー！　力がみなぎる！\nこれが君と私の最強奥義…\n二人で一人の魔法少女\nサイツヨの合体攻撃だーーッ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1013014, 0, "これは…、君の力が私の中に\n流れ込んでる、ってコト？\nえへへ、今の私は真のサイツヨ！\nなんでもできる気がするよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1013015, 0, "私が！　私たちが！\nサイカワサイツヨ\n魔法少女なのだ！\nいくぞ相棒！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1013016, 0, "今ここに！　ナナカちゃんの\n真の力が解放されたのだ！\nなーんちゃってね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1013017, 0, "ふっふっふ、君と私の絆の力が\n最強の魔法なのだ～っ！\nって、真顔でうなずかれると\nちょっと恥ずかしいんだけどぉ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101401, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1014001, 0, "私の好きな推理小説の\n新作が出るんだ\nよければ君もどうだ？\n全巻貸すよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101401, 0.0, 2, 0, 2, 0, 5.4, 0, 0, 1, 1, 1014002, 0, "たまには、探偵と助手として\nではなく、オフの状態で\n出かけないかい？\n頭にも休憩が必要だからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101401, 0.0, 1, 0, 3, 0, 6.2, 0, 0, 1, 4, 1014003, 0, "昼夜逆転になりがちな仕事だし\nとにかく歩き回るし根気がいるし\n…の割に、君はよくやって\nくれているね。助かっているよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101401, 0.0, 2, 0, 4, 0, 4.8, 0, 0, 1, 5, 1014004, 0, "助手くんとなら\nどんな難しそうな事件も\n一発解決間違いナシだねっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101401, 0.0, 5, 0, 5, 0, 6.5, 0, 0, 1, 1, 1014005, 0, "今までは一人で\n探偵をしていたけれど、\nあの頃に戻りたいとは思わない…\n君と出会ってしまったからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1014006, 0, "そぉーれ～っ！\nふふっ【自警団（カォン）】が誇る\n名探偵の力を\n見くびってもらっては困るよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1014007, 0, "はやての速度で事件を暴き\n犯人確保は雷撃のごとし！\n名探偵・カスミに\nお任せあれ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1014008, 0, "荒事は好みじゃないけど…\n事件解決のためなら\nおとなしくしているつもりは\nないんだよっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1014009, 0, "探偵と助手という関係の\n私たちだけど、今はそれを\n抜きにしても君のことを大事な\nパートナーだと思っているよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1014010, 0, "的確にフォローしてくれる\n君がいるから、私は存分に\n力を振るえるんだよ\nまさに名探偵名助手、だねっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101401, 0.0, 5, 0, 6, 0, 6.7, 0, 0, 2, 1, 1014011, 0, "今日は助手くんの誕生日\nなんだって？　おめでとう！\nなら、捜査は早めに切り上げて\nお祝いをしようじゃないか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1014012, 0, "誕生日の余興に、今日は\n助手くんが探偵役をしてみよう\n…ねぇ先生、今日はどんな謎に\n挑まれるんですか？　ふふっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101461, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1014013, 0, "この手にあるのは真実の輝き\nずっと、私が求めていたものさ\n今度はちゃんと笑顔を贈るよ\nキーリ…大切な、もう一人の私");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101461, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1014014, 0, "迷い惑う人々に救いの光を与える\n知的で格好良い名探偵、か\n…そう在り続けたいものだね\n『私自身』への、誓いとして");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101461, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1014015, 0, "名探偵としての私\n儚く弱々しい私\nどちらも紛れもなく私だよ\n全部見せられるのは、君だけさ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101461, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1014016, 0, "『大好きです、先輩♪』\nわぁ違う！　今のは私じゃなくて\nキーリが言ったんだよ！\nもうっ、正直すぎるよキーリは！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101461, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1014017, 0, "…ねぇ、助手くん。私たちは\n探偵と助手としてなかなかの\n名コンビになったと思うんだ\nだから、ね…次は仕事以外でも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101501, 0.0, 2, 0, 1, 0, 3.0, 0, 0, 1, 1, 1015001, 0, "あらあら、先生に\n会いに来てくれたの？\nふふふ、嬉しいわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101501, 0.0, 2, 0, 2, 0, 3.0, 0, 0, 1, 1, 1015002, 0, "今日はどんなご本を\n読もうかしら…\nあなたは、どれが好き？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101501, 0.0, 5, 0, 3, 0, 1.0, 0, 0, 1, 2, 1015003, 0, "うふふ♪\n先生のことは、\nお母さんだと思っていいからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101501, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1015004, 0, "いいこいいこ♪\n先生がずっと\nそばにいるからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101501, 0.0, 5, 0, 5, 0, 4.0, 0, 0, 1, 1, 1015005, 0, "先生はいつでも\nあなたの味方\nたくさん、甘えていいのよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1015006, 0, "もしもなにか辛いことがあったら\n遠慮しないで先生に言ってね？\nなにがあっても、私があなたの\n力になってあげるから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1015007, 0, "いつもお疲れ様♪\nあなたが頑張っているところ、\n先生はちゃーんと見ているからね\nいい子いい子");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1015008, 0, "あらあら、怪我しちゃったの？\nこっちに来て見せてごらん？\n先生が治してあげるわね～\nほーら、痛いの痛いの飛んでけ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1015009, 0, "今日の私は、あなただけの\nミサト先生ですよ～\n子どもたちには秘密よ？\nやきもちやかれちゃうもの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1015010, 0, "私と一緒にいて安心するって\n思ってくれたらすごく嬉しいわ\n私も、あなたといると胸が\nとってもあったかくなるの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101501, 0.0, 2, 0, 6, 0, 10.4, 0, 0, 2, 5, 1015011, 0, "今日は、一年に一度の特別な日\nあなたの誕生日をお祝いできて\n先生とっても嬉しいわ\n本当におめでとう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1015012, 0, "お誕生日おめでとう！\n今日はあなたが主役だから、\nなんでも言うこと聞いちゃうわ～\nいっぱい甘えていいのよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101561, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1015013, 0, "私があなたを癒してあげる…♪\n心も体も、全部先生に任せてね\n今だけは何も考えずに\n私のぬくもりを感じてていいのよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101561, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1015014, 0, "体の奥から、温かい力が\nあふれてくるの\n私もまだ成長できるのね\nあなたと一緒に…うふふ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101561, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1015015, 0, "そろそろ子ども扱いは\nやめた方がいいのかしら…\nえ？　まだいいの？\nあらあら、甘えん坊さん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101561, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1015016, 0, "先生にしてほしいことはある？\n…あらあら、そんなことを？\nうふふ。いいわよ、いらっしゃい\nあなたのお願いだものね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101561, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1015017, 0, "先生の役目はあなたを見守ること\nあなたさえよければ、\nずっと私をそばにいさせてね\n成長するあなたを、見ていたいの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101601, 0.0, 1, 0, 1, 0, 2.0, 0, 0, 1, 2, 1016001, 0, "ヒデサイ、ちょす～！\nまた計算手伝ってほしいんだけど\nお願いしてもいいかなー？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101601, 0.0, 6, 0, 2, 0, 5.0, 0, 0, 1, 1, 1016002, 0, "ねーねー、ヒデサイってさ\n九九どの段まで覚えてるのー？\nうっそー？　マジ？　超天才ー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101601, 0.0, 1, 0, 3, 0, 4.5, 0, 0, 1, 2, 1016003, 0, "今日はねー、このあとイベントで\nモデルの仕事やるんだー\n良かったら見に来てよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101601, 0.0, 2, 0, 4, 0, 7.0, 0, 0, 1, 5, 1016004, 0, "ヒデサイと話してると、なんか\nほっこりする～…あったか～い\nえ？　ウソじゃないよ、マジマジ！\nガチバナだって～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101601, 0.0, 1, 0, 5, 0, 4.0, 0, 0, 1, 2, 1016005, 0, "ヒデサイ～、ちょす～！\n一緒にごはんしよ！\n最近みつけた美味しいお店、\nうちが教えてあげる～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1016006, 0, "うちの愛の矢で\nファンのみんなを\nメロメロにしちゃうよ～\nもちろんヒデサイもね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1016007, 0, "今度の撮影で使う\n新しいポーズ考えてたんだ～\nこんなのどう？\nツヨカワイイ感じでよくない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1016008, 0, "うわぁ、海ちょ～キレー！\n海なんて何回も見たことあるけど\nこれまでで一番キレイかも！\n一緒に見てる人のおかげかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1016009, 0, "モデル仲間に、ヒデサイのこと\nカレシだと思われちゃったんだ\nマジうける～！　けど、うちも\n少しは…んん…何でもない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1016010, 0, "うちのこと、モデルだからって\n特別扱いしなくていいよ！\nうちから見ればヒデサイのほうが\nよっぽどすごいんだからさ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101601, 0.0, 1, 0, 6, 0, 4.0, 0, 0, 2, 2, 1016011, 0, "ハッピーバースデー！\nうちがお祝いしてあげる☆\nん～誕生日に一緒なんて\nカップルみたいかも？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1016012, 0, "今日はヒデサイの誕生日だよね！\n学院のみんなでパーティーしよ？\nあ、でも、二人きりのパーティー\nってのも楽しいかも♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1016013, 0, "うちが楽しいって思うこと\nみんなにも楽しいって\n思ってもらいたいんだよね！\nもち、ヒデサイにもね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1016014, 0, "ランウェイの上から、絶対に\nヒデサイを見つけちゃうし！\nだからヒデサイも、うちのこと\n見ててね？　絶対約束だかんね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1016015, 0, "うちのこと、これからもずーっと\n夢中にさせちゃうよ！\nヒデサイの視線は、うちから\n絶対に外させないんでよろ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1016016, 0, "ヒデサイが応援して\nくれてるってだけで\nうちすっごく楽しいんだよ？\n人生楽しんだもん勝ちっしょー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1016017, 0, "ばっきゅ～ん♪\nどうどう？　うちのランウェイ！\nもにカワっしょ？　あ、ヒデサイ\n顔赤くなっててわろ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101701, 0.0, 2, 0, 1, 0, 1.5, 0, 0, 1, 1, 1017001, 0, "ハイターイ！\n元気にあいさつすると、\n気分がいいさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101701, 0.0, 2, 0, 2, 0, 3.0, 0, 0, 1, 1, 1017002, 0, "焦らずのんびりやっていくさ～\n急いでも、いいことなんて\nなんにもないさ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101701, 0.0, 2, 0, 3, 0, 4.0, 0, 0, 1, 5, 1017003, 0, "ゴーヤーにソーキ…\nサーターアンダギー！\n故郷の食べ物が恋しいさ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101701, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 1, 1017004, 0, "キミと一緒なら、\nどんな冒険もなんくるないさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101701, 0.0, 2, 0, 5, 0, 4.0, 0, 0, 1, 5, 1017005, 0, "いつかキミに、\n故郷を見せてあげたいさー\n青い海に白い砂浜！\n絶対に感動するさー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1017006, 0, "ハイターイ！\n今日も楽しくなりそうさー\nワクワクして、\n踊らずにはいられないさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1017007, 0, "友達いっぱいで、\n毎日楽しいさー\n楽しすぎて、\nついつい踊り出しちゃうよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1017008, 0, "一緒に海に行くさ！\n水の中は、\n最高に気持ちいいさ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1017009, 0, "水浴びは気持ちいいさー！\nキミも一緒に入ろうよ～！\n転ばないように\n手つないであげるよー ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1017010, 0, "みんなで踊ると楽しいさ～！\nやさやさ、\nキミも一緒に踊るさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101701, 0.0, 2, 0, 6, 0, 4.2, 0, 0, 2, 1, 1017011, 0, "おお～！\n今日はキミの誕生日なの～？\nそうと聞いたら\n全力でお祝いするさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1017012, 0, "誕生日おめでとうさ～！\nお祝いに私が、\n24時間連続で祝いの踊りを\n踊り続けてあげるさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1017013, 0, "キミ、見て見て～\nお月さまと夜のダンスさ～！\nキラキラして、綺麗でしょー？\n…あは～、見つめすぎだよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1017014, 0, "なんくるないさー！\nはい、言ってみて～？\n…うんうん、いいね～♪\nいつでも私の故郷に住めるよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1017015, 0, "キミと一緒にいると、いつだって\n胸の奥のリズムが高鳴るね～♪\nどうしてかな～？\nねぇ、どうしてだと思う～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1017016, 0, "海辺で踊ってると\n私の故郷を思い出すよ～\nいつかキミを連れて\nあの海辺を歩いてみたいさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1017017, 0, "ちょっと手を出して～？\n…よしっ、つかまえたよ～♪\nもう逃げられないからね～？\n夜が明けるまで、踊ろうね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101801, 0.0, 2, 0, 1, 0, 9.0, 0, 0, 1, 1, 1018001, 0, "私、こう見えても新米教師なの\nもし何か国語でわからないことが\nあったら質問してね\n先生が教えてあ・げ・る♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101801, 0.0, 5, 0, 2, 0, 7.5, 0, 0, 1, 1, 1018002, 0, "私、担当は国語なんだけど\n少女漫画とか恋愛小説のほうが\nもっと得意よ\nそういう科目があればいいのに～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101801, 0.0, 2, 0, 3, 0, 5.5, 0, 0, 1, 1, 1018003, 0, "魔族の女子校時代…\n楽しい思い出がいっぱいあるわ\n君もこの学院で\nすてきな時間を過ごしてね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101801, 0.0, 6, 0, 4, 0, 10.0, 0, 0, 1, 1, 1018004, 0, "宿題、手こずってるの？\n先生がじっくり教えてあげる♪\nまず…この文章の意味は…\n…え？　くっつきすぎ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101801, 0.0, 5, 0, 5, 0, 8.0, 0, 0, 1, 1, 1018005, 0, "二人で歩いてるとよくカップルに\n間違われちゃうわね\n恋人同士に見えるのかな？\n私はイヤじゃないけど…　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1018006, 0, "こ、ころんじゃったぁ～\n教師なんだし、もっとしっかり\nしなくちゃって思うんだけど\nまだまだなのよねぇ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1018007, 0, "私、天然キャラじゃないのよ？\nたま～にうっかりすることが\nあるだけで…ほんとよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1018008, 0, "魔物さんに襲われたんだけど、\n私の顔を見たら、\nまっ赤になって倒れちゃって…\nどうしたのかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1018009, 0, "あのね、もしまた転んじゃったら\n助けてくれる？\nえへへ、私すっかり君の事頼りに\nしちゃってる。先生なのにね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1018010, 0, "初めての彼氏はどんな人かな\n転んでもバカにせず助けてくれる\n君みたいに優しい人がいいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101801, 0.0, 2, 0, 6, 0, 6.2, 0, 0, 2, 1, 1018011, 0, "お誕生日おめでとう～♪\n昨日より一つ大人になったのね\nお祝いに先生が、\nナデナデしてあげるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1018012, 0, "今日は君の誕生日ね～！\n年下の男の子が、結婚できる\n年齢になってプロポーズ…なんて\n小説みたいであこがれちゃうな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1018013, 0, "君にだけ\n特別な授業をしてあげる\nじっくり教える予定だから\nサボっちゃダメよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1018014, 0, "いつも優しくて頼れる君の姿から\n色んなことを学んでるのよ\n例えばどんなことかって？\nふふ、いいわよ。教えてあげる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1018015, 0, "たまには愛の鞭も必要かしら！\nあれ？　どうしてうれしそうな\n顔をするの？\nしっかり反省して欲しいのに～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1018016, 0, "君のおかげで私も前に進めたわ\n特大の花丸よりもすごいご褒美を\nあげなきゃ！\nどんなことかは、まだ内緒よ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 101861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1018017, 0, "どうしていつも彼氏じゃないって\n否定しないの？　先生のこと\nあんまりからかっちゃだめよ\n勘違いしちゃうじゃない");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102001, 0.0, 2, 0, 1, 0, 8.0, 0, 0, 1, 5, 1020001, 0, "あか、しろ、きいろ～\nきれいに咲いたお花さん～♪\n虫さんブーン\nちょうちょさんヒラヒラ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102001, 0.0, 2, 0, 2, 0, 3.5, 0, 0, 1, 5, 1020002, 0, "うさぎさん～♪\nネコさん♪　イヌさん♪\nみんな、みんな、おともだち～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102001, 0.0, 2, 0, 3, 0, 4.0, 0, 0, 1, 1, 1020003, 0, "おにいちゃん、\nミミと遊んでくれるの？\nやったぁ！　うれしいな～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102001, 0.0, 2, 0, 4, 0, 4.0, 0, 0, 1, 5, 1020004, 0, "ミミね、おにいちゃんのこと、\nだいすき～！\nうさぎさんも、お花さんも、\nみんなすき～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102001, 0.0, 5, 0, 5, 0, 2.5, 0, 0, 1, 2, 1020005, 0, "おにいちゃんすき～！\nギュー！\nおにいちゃんも、ギューして～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1020006, 0, "わぁ、プチグリフォンさん、\nはやーい！\nおにいちゃん、はやくはやく～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1020007, 0, "ミミね、お花のかんむりが\n作りたいな♪　できたら\nおにいちゃんにつけてあげるね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1020008, 0, "パパとママとおともだちと\n動物さんとお花さんと\nあと、おにいちゃん！\nぜんぶミミのだいじなものだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1020009, 0, "ここ、ないしょのお花畑なんだ！\nでもね、おにいちゃんには\nとくべつにおしえてあげる\nだってだいすきなんだもん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1020010, 0, "ミミもプチグリフォンさんも\nおにいちゃんのことだ～いすき♪\n一緒にいられてうれしいな～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102001, 0.0, 5, 0, 6, 0, 2.7, 0, 0, 2, 2, 1020011, 0, "お誕生日おめでとう～♪\nミミも早く大きくなって、\nおにいちゃんの、\nおねえちゃんになりたいな～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1020012, 0, "ミミ、お祝いのお歌をうたうね\nるんたったるんたった～♪\nお誕生日おめでと～♪\n大好き、大好き♪　おにいちゃん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102061, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1020013, 0, "えへへ、ミミ、とんでるよ♪\n行こう、プチグリフォンさん\nお空のむこうまで\nご～ご～ご～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102061, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1020014, 0, "ミミとプチグリフォンさんは\nさいこうのおともだち♪\nずっとず～っと\nいっしょだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102061, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1020015, 0, "おにいちゃんどう？\nミミ、カッコいいかな\nおねえさんできてるかな？\nほんとに？　やったやった～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102061, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1020016, 0, "ミミがおとなになったら\nおにいちゃんといっしょに\n遠くまでいきたいな～\nはやくおっきくな～れ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102061, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1020017, 0, "これまでおにいちゃんに\n助けてもらってばかりだったけど\n今度はミミがおにいちゃんを\n守ってあげるね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102101, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 1, 1021001, 0, "お兄ちゃんのそばにいると\n何だかすごく落ち着きますぅ\nえへ、つい甘えちゃいました…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102101, 0.0, 1, 0, 2, 0, 7.0, 0, 0, 1, 2, 1021002, 0, "あ、あの…\nお兄ちゃんは、こんな私にも\nすごく優しいですよね\nどうしてなんですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102101, 0.0, 5, 0, 3, 0, 7.0, 0, 0, 1, 1, 1021003, 0, "あえいうえおあお…\nも、もっとみんなと、\nお話しできるように、\n練習です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102101, 0.0, 2, 0, 4, 0, 4.5, 0, 0, 1, 1, 1021004, 0, "あ、あの、\nおままごとしませんか？\n私、お母さん役で、\nお兄ちゃんは、お父さん役です♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102101, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1021005, 0, "えへ、\n今日も遊びに来てくれた…！\nうれしいですぅ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1021006, 0, "あ、あのぅ…\nご、ごめんなさい！\nやっぱりなんでもないですぅ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1021007, 0, "はぅ！？\nな、なにか私の顔についてます？\nあ、あんまりじっと見ないでぇ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1021008, 0, "もう少しこの森で\nお稽古していきたいな…\nうまくなるまで\n人に見せるの恥ずかしいもん… ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1021009, 0, "うぅ…あまり見つめないでぇ\nあ、えっと…でも、\n見られたくないわけじゃ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1021010, 0, "えっと…あのぅ…\n私、お芝居が好きで…\nお兄ちゃんに見てほしい…な");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102101, 0.0, 2, 0, 6, 0, 5.6, 0, 0, 2, 5, 1021011, 0, "おお、お誕生日…\nおめでとうございますぅ…！\nわわ、私からのプレゼント…\nもらって…くれますか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1021012, 0, "お兄ちゃん、お誕生日…\nお、おめでとうございますっ！\nきょ、今日はその…\nい、一緒にお祝いしたいですぅ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102161, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1021013, 0, "『小さな勇気、小さな一歩！\nみんなの希望の光…\n魔法少女べルルちゃん参上☆』");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102161, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1021014, 0, "えへへ…\nお兄ちゃんのおかげで\nお芝居うまくなりました…！\nまた練習に付き合ってくださいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102161, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1021015, 0, "ひゃぁ！？　お、お兄ちゃん…！\n急に触っちゃ嫌ぁ…！\n…で、でも…嫌いって言ってる\nわけじゃないですからね…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102161, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1021016, 0, "ステージの上ってこんなに\nキラキラしてるんですね…！\n私、もっとお芝居上手になって\nいろんな人を笑顔にしたいですぅ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102161, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1021017, 0, "私の夢はまだ始まったばかり…\nすごく怖いし、自信はないけど\nお兄ちゃんが応援してくれる…\nだから私、がんばりますぅ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102201, 0.0, 1, 0, 1, 0, 5.5, 0, 0, 1, 2, 1022001, 0, "ね、ねえ。ちょっと、\nアンタに話したいことが…\nや、やっぱりなんでもない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102201, 0.0, 1, 0, 2, 0, 9.0, 0, 0, 1, 5, 1022002, 0, "今日は新作ゲームの\n発売日なんだけど…\nきょ、興味があれば、\n一緒に…や、やっぱりいい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102201, 0.0, 1, 0, 3, 0, 4.5, 0, 0, 1, 2, 1022003, 0, "…アンタが相手だと、\n少しだけまともに話せるけど、\nもっとうまく喋れるように\nなりたいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102201, 0.0, 5, 0, 4, 0, 4.5, 0, 0, 1, 2, 1022004, 0, "アンタと一緒にいるのが、\n普通になってきたかな\n特別じゃないって、\nけっこう特別なことかも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102201, 0.0, 6, 0, 5, 0, 5.5, 0, 0, 1, 5, 1022005, 0, "今度、一緒に買い物に行かない？\nうん、練習は完璧\n…って、アンタもしかして、\n今の聞いてた！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1022006, 0, "こ、この花は、べ、別に\nアンタに見せようと思って\n摘んだわけじゃないわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1022007, 0, "きれいな花畑よね…\nわ、私みたいなのには\n似合わないかもだけど\n…な、何でもないわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1022008, 0, "私だって、こういう場所は\n嫌いじゃないわよ\nい…一応、女の子、だし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1022009, 0, "せっかく花畑に来たんだし…\n好き、嫌い、好き、嫌い…す、\nわああ！　な、なんでもないわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1022010, 0, "ねぇ、こういうところに\n連れてきてくれるってことは、\n私のこと、少しは女の子扱い\nしてくれてるってこと…かな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102201, 0.0, 4, 0, 6, 0, 5.5, 0, 0, 2, 5, 1022011, 0, "お、お誕生日お、おめ、おめで…\nや、やっぱり無理！\nおめでとうなんて言えないー！\nプレゼントなんて渡せないよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1022012, 0, "た、誕生日、おめでとう！\nプレゼントは、アンタが私に\nゲームで勝ったらあげるわよ\n…アンタ、このゲーム得意よね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102261, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1022013, 0, "進化した私の力…\n甘く見ないでほしいわね！\nどんな相手もみんなまとめて\nやっつけちゃうんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102261, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1022014, 0, "アンタと私のコンビなら\nなんだって乗り越えていける\n気がするの…不思議とね！\n限界だって振り切ってみせるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102261, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1022015, 0, "どんどん流れ込んでくる…\nアンタの想い、そして力が…\nこれなら誰にだって負けないわ！\n私の本気、見せてあげる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102261, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1022016, 0, "アンタが見ててくれるから\n私も安心して戦えるっていうか…\nだから…その…こ、これからも\n私のそばにいなさいよねっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102261, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1022017, 0, "アンタが一緒にいてくれて\nその…私もちょっとずつ\n変われたっていうか…\n…な、なんでもない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102301, 0.0, 5, 0, 1, 0, 4.2, 0, 0, 1, 1, 1023001, 0, "ぷうきちは私の大切な\nお友達なんだよ\nお兄ちゃんも、ぷうきちと\n仲良くしてね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102301, 0.0, 1, 0, 2, 0, 4.1, 0, 0, 1, 2, 1023002, 0, "私が先に救護院に来たんだから\nお姉ちゃんだよ！\nお兄ちゃんは弟ね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102301, 0.0, 2, 0, 3, 0, 3.7, 0, 0, 1, 5, 1023003, 0, "私とぷうきちと、お兄ちゃん！\n三人そろえば、\n怖いものなしだね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102301, 0.0, 1, 0, 4, 0, 2.6, 0, 0, 1, 3, 1023004, 0, "『俺たちともっと遊べ』って\nぷうきちが怒ってたよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102301, 0.0, 2, 0, 5, 0, 2.9, 0, 0, 1, 6, 1023005, 0, "お兄ちゃんはぷうきち好き？\n私は大好き！\nお兄ちゃんと同じくらい好き！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1023006, 0, "お、お兄ちゃん！\nあんまりうろうろすると\nぷうきちが\n当たっちゃうよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1023007, 0, "えいっ！　やあっ！　魔物めぇ！\nこ、こっち来るなったらぁ！\nあっ、そっちもダメ！\nお兄ちゃんの方に行くなー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1023008, 0, "見ててお兄ちゃん、\nぷうきちフルスイングの練習の\n成果、見せてあげる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1023009, 0, "お兄ちゃん！\n大丈夫？　けがはない？\nもう平気だよ、安心して\n私とぷうきちが来たからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1023010, 0, "お兄ちゃんに悪さする魔物めー！\n許さないんだから！\nやっちゃおう、ぷうきち！\n『合点だぜアヤネ！』");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102301, 0.0, 1, 0, 6, 0, 1.9, 0, 0, 2, 2, 1023011, 0, "ハッピーバースデー♪\nぷうきちも、\nお兄ちゃんのお祝いしたいって！\n３人でパーティーしよっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1023012, 0, "お兄ちゃん、お誕生日おめでと♪\nぷうきちからも\nプレゼントがあるみたいだよ！\nいっぱいもらえて、いいな～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1023013, 0, "見て見てお兄ちゃん、\nぷうきちがいっぱい！\nえへへ、み～んな私の、\n新しい家族だよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1023014, 0, "新しい家族だ～♪\nよ～し、私がお姉ちゃんとして、\nみんなの場所を\n作ってあげるからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1023015, 0, "こうやってお兄ちゃんと一緒に\n居ると、本当の兄妹みたいだね\nえへへ♪　今日は妹として、\n思いっきり甘えちゃおうかな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1023016, 0, "この子たちは私とお兄ちゃん\nにとって家族…それって…\n『子どもみたいなもんだな』\nち、ちち、違うからぁ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1023017, 0, "『お、俺の家族がこんなに！？』\n大家族のお兄ちゃんとして、\n頑張らないとね、ぷうきち♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102501, 0.0, 6, 0, 1, 0, 6.0, 0, 0, 1, 4, 1025001, 0, "いつも失敗してすみません…\nは、腹を切ってお詫びをっ！\n…え、そっちの方が迷惑？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102501, 0.0, 6, 0, 2, 0, 2.5, 0, 0, 1, 1, 1025002, 0, "身の回りのお世話は私が─\nわぁ！\nあ、頭ぶつけちゃいました…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102501, 0.0, 4, 0, 3, 0, 4.0, 0, 0, 1, 1, 1025003, 0, "ごはん作って、お洗濯して、\nお部屋の掃除も…\nや、やることがいっぱいで\n目が回っちゃいます～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102501, 0.0, 2, 0, 4, 0, 2.5, 0, 0, 1, 5, 1025004, 0, "精一杯、ご奉仕します！\nなんでも言ってくださいね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102501, 0.0, 5, 0, 5, 0, 7.0, 0, 0, 1, 4, 1025005, 0, "あの…お掃除頑張るので、\n終わったら、その…\n褒めてもらえませんか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1025006, 0, "このゴーレムさん、\n道端に捨てられていたんですよ\n可哀想に…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1025007, 0, "ゴーレムさんを綺麗にするの\n手伝ってくれるんですか？\nえへへ、ありがとうございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1025008, 0, "怪我とかしていません？\n回復魔法なら得意なので♪\nそれ以外の魔法は…それなりに");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1025009, 0, "あっ！　またやっちゃいました～\nあなたの前では失敗しないように\n気を付けていたのに～っ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1025010, 0, "お帰りなさいませ\nご主人さま\nな、な～んて、えへへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102501, 0.0, 1, 0, 6, 0, 5.7, 0, 0, 2, 2, 1025011, 0, "お誕生日、おめでとうございます\nケーキをご用意しました！\nこ、転ばないように、\nそーーーっと持っていきますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1025012, 0, "お誕生日おめでとうございます♪\n今日だけは普通の女の子として\nあなたのお祝いをしちゃ、\nダメ…ですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102561, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1025013, 0, "ひゃわあんっ！？\nえ、えへへ…転んじゃいました\nでも私、くじけませんから！\n一人前のメイドになる日までっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102561, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1025014, 0, "あ…あなたもお片付け\n手伝ってくれるんですか？\nありがとうございます♪\nこのご恩は必ず、お返ししますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102561, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1025015, 0, "なんだか最近、ドジが\nパワーアップしてる気がします～\nいえ、前向きに考えましょう！\nこれもまた成長なんですよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102561, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1025016, 0, "こ、こっち見ないでください～！\nはい？　どうしてって…？\n失敗したところを見られたくない\n乙女心…ううん、メイド心です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102561, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1025017, 0, "また大失敗しちゃいましたけど\nあなたがすぐに手を差し伸べて\nくれて…なんだか私、それが\nすごくうれしかったり…えへへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102601, 0.0, 1, 0, 1, 0, 5.4, 0, 0, 1, 5, 1026001, 0, "できればずーっと\nゴロゴロしてたい…\nねえ、あんたもそう思わない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102601, 0.0, 5, 0, 2, 0, 6.8, 0, 0, 1, 2, 1026002, 0, "ねーねー、あたしと一緒に\nのんびりゲームでもしようよ～\nたまには休むのも大事だよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102601, 0.0, 5, 0, 3, 0, 6.5, 0, 0, 1, 4, 1026003, 0, "あたし、お腹ぺこぺこだよ\nあんた何か持ってない？\n美味しいあんパンでもあれば\n最高なんだけどね～…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102601, 0.0, 2, 0, 4, 0, 3.8, 0, 0, 1, 1, 1026004, 0, "一人でだらだらしてることが\n多かったからさ、\nあんたっていう遊び相手が\nできて結構嬉しいんだー");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102601, 0.0, 2, 0, 5, 0, 9.5, 0, 0, 1, 4, 1026005, 0, "…あたしみたいなタイプってさ、\n自分の領域に人が入ってくるの\n少し苦手だったりするんだけど、\nあんたは不思議と嫌じゃないや");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1026006, 0, "大好きなドングリの中で\nごろごろできるなんて…\nなんだい？\nここは天国かい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1026007, 0, "ああ、まさにこの空間は、\nあたしのために\nあるような場所だね…\n戻ってこれなくなりそうだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1026008, 0, "ふかふかのベッドもいいけど\n自然に包まれてだらけるのも\nまた乙なんだよねぇ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1026009, 0, "ほらほら、あんたもこっちに来て\n一緒にこのパラダイスを\n堪能しようじゃないか…\nあたしの隣、あいてるよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1026010, 0, "こんな素敵な場所に\n連れてきてくれてありがとね\nこのためならいくらでも\n外にでるし、また連れてきておくれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102601, 0.0, 2, 0, 6, 0, 6.2, 0, 0, 2, 1, 1026011, 0, "誕生日おめでと～\nプレゼントは買い忘れてないよ～\n通販の時間指定を使ってるから、\nもうすぐ届くはずだよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1026012, 0, "誕生日おめでと～\n特別な日だからこそ、\nぐうたら過ごすに限るよね\nあたしも一緒に付き合うよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1026013, 0, "飲み物よし、食べ物よし！\nあたしはもう一歩も動かないよ！\nほら、あんたもこっちきな\n一緒にゲームするよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1026014, 0, "ぐうたらバンザ～～イ！\nはぁ…幸せだねぇ\nずっとこうしていられたら\nいいのになぁ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1026015, 0, "靴下を脱ぎ散らかしちゃダメ？\nいいじゃん、もうオフなんだし\n文句があるならあんたが\n片付けといて～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1026016, 0, "休憩って大切だよね？\n大切なんだからたくさん\nとらなきゃだよね？\nほら、あんたも早く休む休む！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1026017, 0, "あんたがいると心の底から\nくつろげるねぇ…どうせだったら\nあんたも牧場に住んじゃえば？\nなんてね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102701, 0.0, 2, 0, 1, 0, 5.0, 0, 0, 1, 1, 1027001, 0, "あなた様のことを思って\nまごころをこめて\n媚薬づくりをしていますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102701, 0.0, 2, 0, 2, 0, 6.0, 0, 0, 1, 1, 1027002, 0, "私の本業は花屋ですわ\nブーケなども作っています\n最近は毒草アレンジが人気です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102701, 0.0, 6, 0, 3, 0, 9.0, 0, 0, 1, 1, 1027003, 0, "花は嫌いではありません…\n生活の糧ですし薬の材料にも\nなりますから。…美しさ？\nそれは考えたこともありません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102701, 0.0, 5, 0, 4, 0, 5.5, 0, 0, 1, 2, 1027004, 0, "こうして研究をしていると\n心が弾みます…\n薬が完成すればあなた様を\n自由にできると思うと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102701, 0.0, 5, 0, 5, 0, 3.5, 0, 0, 1, 2, 1027005, 0, "花占いでもしましょうか\nあなた様は私のことが、好き…\n大好き…好き…大好き…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1027006, 0, "どのような女性が好みですか？\n明るい私？　クールな私？\nもの静かな私？　…もちろん、\n私以外の方ではありませんよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1027007, 0, "森の魔物…人の心を読み取り\n好みの幻を見せると聞きますわ\nあなた様がその魔物に遭ったら\nどのような幻を見るのでしょうね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1027008, 0, "私だけを見てください\n今目の前にいる私だけを\nたとえ別の私でも、\n目移りするのは許しませんよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1027009, 0, "もしもあなた様の偽物が現れたら\nいっそ縛り上げて観賞用に…\nいえ、偽物は所詮偽物ですわ\n私に必要なのはあなた様だけ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1027010, 0, "どんな私をお望みですか？\nあなた様が望むなら、\nその通りに私は変わります\n愛していますから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102701, 0.0, 5, 0, 6, 0, 7.2, 0, 0, 2, 1, 1027011, 0, "お誕生日を祝して、バースデー\nケーキをつくりましたわ\n隠し味は、睡眠薬としびれ薬と\n惚れ薬と媚薬と…私の愛情です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1027012, 0, "お誕生日おめでとうございます\nプレゼントを用意しましたわ\n婚姻届と、結婚指輪と、\n二人の新居です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1027013, 0, "クスクスクス…♪\nこのブーケのように\n私たちの未来は\nバラ色間違いなしですわ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1027014, 0, "私を狂わせるのも\n私を満たすのも、あなた様\nそう…私の全ては\nあなた様次第なのですわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1027015, 0, "優しいあなた様と私を\n無限に祝福する優しい世界…\n必ずや実現してみせますわ…！\nクスクスクスクス…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1027016, 0, "あなた様を私だけの物にしたい\nそれも本心のひとつですわ\nですが…もうその想いだけに\n囚われる私ではありません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1027017, 0, "バージンロード…\n指輪交換…\nそして誓いの…あぁっ！\n恥ずかしいですわっ…！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102801, 0.0, 2, 0, 1, 0, 5.0, 0, 0, 1, 1, 1028001, 0, "クレヨンと絵本と積み木が\nいっぺんに買えて\nしかも安い店を見つけたの！\n選ぶのを手伝ってくれない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102801, 0.0, 2, 0, 2, 0, 4.5, 0, 0, 1, 1, 1028002, 0, "料理をする時は、\n食材の隅から隅まで\n使い切らないとね\nもったいないじゃない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102801, 0.0, 2, 0, 3, 0, 7.0, 0, 0, 1, 1, 1028003, 0, "お金も大事だけど、あたしはただ\nみんなの笑顔が見たいの\nもちろん、その中には\nあんたも入ってるんだからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102801, 0.0, 5, 0, 4, 0, 3.5, 0, 0, 1, 2, 1028004, 0, "あんたと今度、\nどこかに遊びに行きたいわね\nあ、もちろん、\nお金のかからないところによ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102801, 0.0, 2, 0, 5, 0, 2.0, 0, 0, 1, 1, 1028005, 0, "お茶会を開こうと思うの\nもちろん、\nあんたも招待してあげるから\n楽しみにしてなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1028006, 0, "限定アイテムの列はこちらが\n最後尾です…あら、奇遇ね\nあんたも寄ってかない？\nあたしたちが主催するバザー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1028007, 0, "あたしがお客さん呼び込むから、\nあんたはこの看板持って\n誘導してくれる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1028008, 0, "予想以上にお客さんが来てくれて\nビックリだわ！　せっかくだし\nあんたも寄ってきなさいよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1028009, 0, "整列手伝ってくれてありがと♪\nあんたと一緒だといつもより\n何倍も仕事が楽しいわ♪\nまたお願いね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1028010, 0, "バザーは大盛況よ♪\nこのあとパーッとご飯でも\n行きましょ！　あんたがいれば\n疲れなんか吹っ飛んじゃう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102801, 0.0, 2, 0, 6, 0, 5.1, 0, 0, 2, 1, 1028011, 0, "ハッピーバースデー♪\n救護院のみんながプレゼントを\n選んだわ。もちろんあたしもね\nありがたく受け取りなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1028012, 0, "誕生日だったわね？\nお祝いに遊びに行くわよ\nこんな日くらいはしっかり休んで\nたまには楽しみましょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1028013, 0, "みんなが楽しそうに笑ってるのが\n何よりもうれしいの\nそんな場所であんたと過ごせるの\n本当に幸せよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1028014, 0, "帰れる場所があるって、本当に\n素敵よね。あんたも疲れたら\nいつでもここに来なさい\n待ってるから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1028015, 0, "見慣れた何気ない光景だけど\nこういう当たり前の日々を\n大切にしていきたいわよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1028016, 0, "あんたもだいぶここに\nなじんでるわよね\nこのままずっと\n居てくれてもいいのよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1028017, 0, "あんたの前でくらい、肩の力を\n抜いてもいいわよね\nママ・サレンはちょっとお休み\n…なんてね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102901, 0.0, 2, 0, 1, 0, 3.6, 0, 0, 1, 1, 1029001, 0, "私、今のポジションには\n満足してないんだ！\nトップアイドルめざして\n歌も踊りも磨かないとね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102901, 0.0, 5, 0, 2, 0, 6.0, 0, 0, 1, 6, 1029002, 0, "…私の写真集みてるの？　この\n派手な水着は撮影用だってば！\nプライベートで着るやつは\nもっと可愛いもん。…見たい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102901, 0.0, 2, 0, 3, 0, 3.6, 0, 0, 1, 1, 1029003, 0, "トップアイドルへの道は\nまだまだ遠いなあ…\nダンスと歌のレッスン、\nもうちょっと頑張ろうっと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102901, 0.0, 2, 0, 4, 0, 3.4, 0, 0, 1, 1, 1029004, 0, "歌も踊りも\nもっともっと上手になりたいな！\nキミには、一番輝いてる私を\n見てほしいから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102901, 0.0, 5, 0, 5, 0, 2.3, 0, 0, 1, 1, 1029005, 0, "アイドルのお勉強は進んでる？\nでも…私以外のアイドルの\nことはあまり勉強しなくても\nいいからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1029006, 0, "トップアイドルになれたら\n世界はどんな風に見えるんだろ？\nもしそうなった時も\nキミは隣にいてくれるかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1029007, 0, "ステージに立つからには\nチャレンジする気持ちを\n忘れたらダメだよね！\nこれからも前向きにいくよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1029008, 0, "私、がんばってトップアイドルの\n高みを目指すから！\n応援、よろしくね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1029009, 0, "キミが望むなら\nアイドルやめてもいいかなって\n思ったこともあったんだよ？\n…ちょっとだけね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1029010, 0, "この歌、片思いの女の子の心が\nよく出てるねって？\n…ふふふ、キミって鈍感♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102901, 0.0, 1, 0, 6, 0, 1.2, 0, 0, 2, 2, 1029011, 0, "おめでとう♪　キミの誕生日を\nお祝いできるように、\nずっと前からスケジュールを\n調整してたんだ！　うれしい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1029012, 0, "ハッピーバースデー、トゥユー♪\nハッピーバースデー、トゥユー♪\nどう？　キミだけのための\nプライベートライブだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102961, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1029013, 0, "今の私にできる最高の歌を\nキミに贈るよ。キミを想って\nキミのために歌うから…\nどうか最後まで聴いてね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102961, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1029014, 0, "この歌には私の色んな想いを\n込めたんだ。え、どんな\n想いかって？　ふふ、それは秘密\nキミ自身で見つけてね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102961, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1029015, 0, "みんなの応援がトップアイドルを\n目指す私の力になるんだ\nだからこれまで以上にいーっぱい\n応援してくれなきゃダメだよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102961, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1029016, 0, "私がトップアイドルになっても\nキミには今まで通りで\nいてほしいな。そういうのが\n一番うれしいんだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 102961, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1029017, 0, "アイドルは恋愛禁止だけど\nいつかちゃんとキミに…ううん\nまずはトップアイドルになるんだ\n勇気を出すのはその後だよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103001, 0.0, 2, 0, 1, 0, 5.5, 0, 0, 1, 1, 1030001, 0, "デケデケデン！\nこのニノン、ショーグンのために\n忠義を尽くすデース！\n見ててクダサイ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103001, 0.0, 2, 0, 2, 0, 4.0, 0, 0, 1, 3, 1030002, 0, "一大事デス！\nアクダイカンが現れたデス！\n一緒に成敗するでゴザルデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103001, 0.0, 1, 0, 3, 0, 4.0, 0, 0, 1, 6, 1030003, 0, "あ、ショーグン！\nそこでなにを…ハッ！？\nもしや忍法の修行デスか！？\nニノンもやるデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103001, 0.0, 1, 0, 4, 0, 4.5, 0, 0, 1, 2, 1030004, 0, "ショーグンのためなら、\nたとえ火の中、水の中デス！\nなぜなら…ワタシの命は、\nショーグンのものだからデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103001, 0.0, 5, 0, 5, 0, 5.5, 0, 0, 1, 1, 1030005, 0, "ヤマトナデシコ…\nニノンの国にも伝わる言葉デス\nワタシがそうなったら、\nショーグンもメロメロデスよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1030006, 0, "いつか…\nショーグンと共に\n天下統一するデス！\nそれがニノンの夢なのデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1030007, 0, "ニノンに切れぬものはないデス！\nショーグンはヤカタブネに\n乗ったつもりで\n安心していてクダサーイ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1030008, 0, "ニノンが、ショーグンのゾーリ\nふところで\nあっためておいたデス！\n一緒にでかけマショ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1030009, 0, "ショーグン、手をつなぐデース！\nえへへ…\nこれでニノンとショーグンは、\n固い絆で結ばれたデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1030010, 0, "ショーグン！　ご飯にするデス？\nお風呂にするデス？\nそれとも…ワ・タ・シ…？\nは、恥ずかしいデス…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103001, 0.0, 2, 0, 6, 0, 6.2, 0, 0, 2, 1, 1030011, 0, "誕生日おめでとうデス！\nお祝いの紅白マンジェウを\n用意したデス！　二人羽織で\n食べさせてあげるデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1030012, 0, "ショーグンのお祝いするデス！\n誕生日は、おジャシキ遊びで\nシュラシュシュシュデース！\nどんちゃん騒ぎデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103061, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1030013, 0, "チューシン・ニノン、呼ばれて\n飛び出てシュバババッー！\n命燃やしてショーグンを\nお守りいたしマス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103061, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1030014, 0, "シンリャクすること火の如くデス\nささ、ショーグン、ご命令を！\n最高のイクサバタラキを\nご披露いたソウロウデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103061, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1030015, 0, "ショーグン、見てクダサイ！\nメラメラ燃える究極忍法デス！\nこれでゾーリもホカホカに…\nアアー！　も、燃えてるデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103061, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1030016, 0, "シュババババッ！\nこの印さばき、どうデスか？\nえ、ゆ、指がきれい？　うぅ…\n不意打ちはズルいデ～ス…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103061, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1030017, 0, "ショーグンのおそばにいるだけで\nワタシは笑顔になりマス！\nそれこそがワタシの一番の\nゴホウビデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103101, 0.0, 2, 0, 1, 0, 6.5, 0, 0, 1, 1, 1031001, 0, "今日の運勢は最悪です\nえっと、気にしないでください\nちなみに…私の占いは、\nよく当たります");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103101, 0.0, 4, 0, 2, 0, 4.5, 0, 0, 1, 6, 1031002, 0, "あなたの後ろの人、\n誰ですか…？\nえ？　見えない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103101, 0.0, 2, 0, 3, 0, 7.0, 0, 0, 1, 1, 1031003, 0, "タロットカード、星占い、\n四柱推命…いろいろ\nありますが、私のおススメは、\n父を使った水晶占いです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103101, 0.0, 5, 0, 4, 0, 4.0, 0, 0, 1, 4, 1031004, 0, "父はあなたを\n誤解しているようです\nとてもいい人なのに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103101, 0.0, 5, 0, 5, 0, 5.0, 0, 0, 1, 1, 1031005, 0, "今日のあなたには、\n女難の相がでていますね\nあまり私以外の女性とは、\n話をしない方が…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1031006, 0, "舞がきれい…ですか\nうれしい\n修練した甲斐がありました");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1031007, 0, "これは祓魔の舞です\nまだ練習中なので\nあまり見ないでください\n…恥ずかしいですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1031008, 0, "父がこの舞を見るたびに\n成仏しそうだと褒めてくれます\n父に成仏されては\n困るのですが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1031009, 0, "あなたに褒めてもらえると\nいつもより舞がうまくできます\nプラスの気は\n霊を祓うのに最適なんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1031010, 0, "生きている人で舞を見せるのは\nあなたが初めてです\n少し恥ずかしいですけど\n…あなたに見てほしいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103101, 0.0, 5, 0, 6, 0, 10.3, 0, 0, 2, 2, 1031011, 0, "あなたが歳を取って、\nおじいさんになったら、一緒に\n私もおばあさんになりますね\nもちろん、幽霊になったら…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1031012, 0, "お誕生日おめでとうございます\n今日は、とてもいい運勢ですよ\n金運とか健康運とか…\nその…れ、恋愛運とか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103161, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1031013, 0, "あなたがそばにいれば\n何も恐れるものはありません\n降りかかる不安や恐怖は\n私が全て刈りとります");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103161, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1031014, 0, "たとえ誰からも恐れられようとも\nあなたを守るためなら\n私はためらうことなく\nこの刃を振るいます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103161, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1031015, 0, "キレイな鬼火…\n妖しくもはかなげで\nそして私たちの行く末を照らす\n希望の灯火なんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103161, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1031016, 0, "よければ力をお貸しください\nあなたとは共に助け合い\n守り合う仲でいたいですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103161, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1031017, 0, "あなたのそばにいると\n胸が高鳴ってしまいます…\nこれは魂の共鳴？\nあるいはもっと別の…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103201, 0.0, 1, 0, 1, 0, 4.0, 0, 0, 1, 2, 1032001, 0, "困ったことがあれば、\n何でも申してくださいな！\n私がなんとかして\n差し上げますわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103201, 0.0, 1, 0, 2, 0, 2.0, 0, 0, 1, 2, 1032002, 0, "ごきげんよう\n私が一人前の商人になるため、\nご指導ご鞭撻のほど\nよろしくお願いしますわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103201, 0.0, 5, 0, 3, 0, 6.5, 0, 0, 1, 4, 1032003, 0, "私は確かに方向音痴ですが、\nそこまで酷くありませんわ\n…ところで、私の家は\nどちらかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103201, 0.0, 2, 0, 4, 0, 6.0, 0, 0, 1, 1, 1032004, 0, "あなたさまが私の騎士になる日を\n私はいつまでも待っていますわ\nその時には、二人で\n色々な場所に行きますわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103201, 0.0, 2, 0, 5, 0, 3.5, 0, 0, 1, 1, 1032005, 0, "お金で買えないものも\nあるのですわね…\n例えば、あなたさまの\n心…ですわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1032006, 0, "『一人前の商人とは\n一人前の武人でもある！』\nウィスタリア家の教えを\n今こそ守りますわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1032007, 0, "この程度の相手、\n私の敵ではありません\n手早く片付けて、\n勝利の祝宴と参りますわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1032008, 0, "私の商売を邪魔するやからは\nなんぴとたりとも許しません！\n成敗してさしあげます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1032009, 0, "あなたさまが\nそばにいてくださることが、\nどれほど私を強くしているか、\nお気づきかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1032010, 0, "あなたさまと一緒なら、\n私は迷わずに、\nどこまでもまっすぐ進みますわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103201, 0.0, 2, 0, 6, 0, 5.2, 0, 0, 2, 1, 1032011, 0, "今日はあなたさまの誕生日？\nまあ、おめでとうございます！\nすぐにパーティーを\n開きましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1032012, 0, "誕生日、おめでとうございます\nあなたさまの誕生日の\n価値の前では、どんな宝石も\nかすんで見えてしまいますわね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103261, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1032013, 0, "おーほっほっほ！\n情熱の炎が燃え滾りますわ！\n今の私は完全無欠！　遅れずに\nついてきてくださいませ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103261, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1032014, 0, "やはり私には赤が似合いますわ\nこの全てを焼き尽くす炎でさえ\n私にとっては艶やかに彩る装飾の\n一つでしかありませんわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103261, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1032015, 0, "あなたさまがいてくれれば\n私は不死鳥の如く蘇りますわ！\nさあ、一気呵成に\n決めてしまいましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103261, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1032016, 0, "あなたさまと一緒にいると\n知らない世界を知ることが\nできますわ。さあ、次は何を\n見せてくださるの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103261, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1032017, 0, "私が背中を任せられるのは\nあなたさまだけですわ\nそして肩を並べ共に歩むのも\n私はあなたさまがいいですわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103301, 0.0, 2, 0, 1, 0, 3.0, 0, 0, 1, 1, 1033001, 0, "お笑いの道は奥が深けぇべ！\nオラと一緒に、\n笑いの道、極めるべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103301, 0.0, 4, 0, 2, 0, 4.0, 0, 0, 1, 3, 1033002, 0, "オラが一番おっかねぇのは、\n敵に負けることじゃねえ\nネタがすべることだ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103301, 0.0, 1, 0, 3, 0, 3.0, 0, 0, 1, 2, 1033003, 0, "あんちゃん、この牛乳ウマいべ？\nお乳を出したのは、ウマじゃなくて\n牛だけどな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103301, 0.0, 2, 0, 4, 0, 5.0, 0, 0, 1, 1, 1033004, 0, "あんちゃん、オラと一緒に、\nお笑い界の頂点めざすべ！\nめざせ、お笑いチャンピオン！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103301, 0.0, 2, 0, 5, 0, 4.0, 0, 0, 1, 1, 1033005, 0, "あんちゃん、オラと一緒に\nネタ、作るべ！\nもう二時間だけ「寝た」後に…\nなんちゃって！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1033006, 0, "ほーい、集まってけれ～！\nエリザベスパーク印の\n牛乳、ヨーグルト、チーズ！\n直売だべよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1033007, 0, "街の衆、味見してけろ！\n出来立てだぁ、ほんとに旨ぇぞ～\nオラ嘘はつかねえよ、\n冗談は言うけどな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1033008, 0, "街のみんながこんなに\n応援してくれてんだ！\n絶対に爆笑をかっさらうべよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1033009, 0, "オラのギャグ、おもしろいべか？\nなんつってもあんちゃんは、\nオラの初めてのファンだ\nきっとオラたち波長が合うだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1033010, 0, "な、なあ…あんちゃんのこと\n相方って呼んでもいいべか？\nそれがダメなら婿さんに…\nあ、逆だ！　こっぱずかしー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103301, 0.0, 2, 0, 6, 0, 4.7, 0, 0, 2, 1, 1033011, 0, "ひょうたん、常備してっか？\nなにキョトンとしてんだべ？\n今日誕生日のあんちゃんのための\nギャグだべ！　おめでとう～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1033012, 0, "ハッピーバースデーだべ！\n今日はあんちゃんのためだけに\n渾身ネタを、飛ばーすでー！\nいっぱい笑ってほしいべよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1033013, 0, "オラの手料理をあんちゃんに\n食べてもらうの、なんかドキドキ\nするべな…味には自信あっから\n楽しみにしててけろ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1033014, 0, "あんちゃんと二人でキャンプ\nなんてのもいいかもしんねぇな\n朝までみっちりネタ合わせ…\n最高の時間になりそうだべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1033015, 0, "牧場がこんなににぎやかに\nなるなんて…それもこれも\nあんちゃんが励ましてくれた\nおかげだべ。あんがとな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1033016, 0, "みんなの笑顔が行商人としても\n牧場主としても一番の幸せだべ\nこの景色をずっと、あんちゃんと\n一緒に見ていきてぇなぁ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1033017, 0, "オラの相方はエリザベスだけって\n思ってきたけど、あんちゃんも\n加えてトリオってのもいいなぁ…\nまじめに考えてくれねぇべか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103401, 0.0, 2, 0, 1, 0, 5.5, 0, 0, 1, 1, 1034001, 0, "何か困ったことがあったら\n相談してね\nいつでも力になるから\nお姉さんとの約束よ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103401, 0.0, 2, 0, 2, 0, 6.5, 0, 0, 1, 1, 1034002, 0, "キミ、いま暇だったりしない？\nだったら私と一緒に、\nどこか飲みにいきましょ？\nもちろんキミはジュースで、ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103401, 0.0, 5, 0, 3, 0, 5.0, 0, 0, 1, 6, 1034003, 0, "仕事持って帰ってきたけど\n終わりそうにないよ～！\nうぅ…キミ、\n少し手伝ってくれない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103401, 0.0, 2, 0, 4, 0, 7.5, 0, 0, 1, 2, 1034004, 0, "えへへ～…\nキミって暖かいんらね～\nちょっと飲みすぎらから、\nキミで暖を取らせて～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103401, 0.0, 2, 0, 5, 0, 4.0, 0, 0, 1, 1, 1034005, 0, "キミ、ご飯はちゃんと食べてる？\nあ、良かったらお姉さんが、\n作ってあげようか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1034006, 0, "はぁ…このベリー、いい香り♪\n飲み物にしたらさぞ美味しい…\nいけないいけない！\n仕事中なのに！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1034007, 0, "だめよユカリ…！\nこのままじゃいつもみたいに\nダメなところを見せちゃうわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1034008, 0, "な～んか、ちょっとだけ\nいい気分になってきたかも…\n気のせい…かなぁ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1034009, 0, "キミが大人になった時のために\n飲み物、一本作っておこうか？\nその日がきたら、二人で一緒に\nお祝いしながら飲みましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1034010, 0, "ねぇ…今度、一緒に\nデートしない？\nそれとも、私みたいなのが\n相手じゃ、いやかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103401, 0.0, 1, 0, 6, 0, 2.0, 0, 0, 2, 2, 1034011, 0, "誕生日おめでとう！\n今日はいっぱい\nお祝いしてあげるね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1034012, 0, "キミの誕生日というわけで\nまずは…かんぱーい！\nねえ、今日はずっと\n一緒にいてもいいかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103461, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1034013, 0, "んふふ～気持ちいい～♪\nほら、キミも飲も飲も！\nもちろんキミはジュースだからね！\nはい、かんぱーいっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103461, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1034014, 0, "あぁ～染み渡るわぁ～\n仕事の後はこれに限るわよね～！\nそうだキミ、肩揉んでくれない？\nんっ、あ～っ、上手ね、キミ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103461, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1034015, 0, "今日はお姉さんのおごりよぉ\nたくさん食べていいわよぉ～\n本当は手料理とかご馳走して\nあげたいんだけど…今度ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103461, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1034016, 0, "私がこんな風にリラックス\nできるのはキミの前だけよ～\n迷惑かけちゃうかもしれないけど\nキミは許してくれるわよね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103461, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1034017, 0, "よぉ～っし言っちゃおうかな～！\n私ねぇ～、キミのことが～…！\nふふ、や～っぱ秘密～！\n…あ、危なかったわ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103601, 0.0, 5, 0, 1, 0, 5.9, 0, 0, 1, 1, 1036001, 0, "今は、勉強の時間です\n話しかけないでくださ…\nもう、そんな顔しないでください\n遊んであげますから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103601, 0.0, 4, 0, 2, 0, 4.8, 0, 0, 1, 5, 1036002, 0, "ピーマンとにんじんを食べないと\n大きくなれない…？\nううっ…大きくはなりたい\nですけれどぉ～っ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103601, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1036003, 0, "聞いてください\n身長が伸びていました！\n一ミリも！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103601, 0.0, 5, 0, 4, 0, 5.2, 0, 0, 1, 1, 1036004, 0, "今は子供ですけど、\nすぐに大きくなりますから\nその時には…その…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103601, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1036005, 0, "ふふっ。とても頑張りましたね\n今日はほめてあげます\n頭をナデナデしてあげましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1036006, 0, "私を守ってあげる？　\n余計なお世話です！\nで、ですが…\nありがとうございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1036007, 0, "こ、腰が抜けちゃいました…\nは、恥ずかしい…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1036008, 0, "うぅ…どうして\nこの人がいる時に限って、\nかっこ悪くなっちゃうのかな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1036009, 0, "ま、魔物なんてこわくないです！\nで、でもその…守ってくれて\n助かりました…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1036010, 0, "街の外は魔物が出て危ないです\nですから、私が守ってあげますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103601, 0.0, 2, 0, 6, 0, 3.0, 0, 0, 2, 5, 1036011, 0, "今日、お誕生日なんですよね\nおこづかいを貯めて、\nカップケーキを買ってきたので、\n召し上がってください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1036012, 0, "お誕生日おめでとうございます\nどんどん大人になっちゃう…\n私も早く追いつかないと…\nな、なんでもないです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1036013, 0, "私、もう子供じゃないんですから\nひとりでも戦えますっ！\n今度は私があなたを守れるように\nなるんですからっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1036014, 0, "あなたの力を感じます…\n不思議です。今ならなんだって\n出来る気がする…！\nそばで見ていてくださいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1036015, 0, "たくさん勉強してきた\n成果を見せますから！\n失敗しちゃっても、\n笑わないでくださいねっ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1036016, 0, "私の魔法…どうですか？\n別に、褒めて欲しいとかじゃ…\nあ…頭ナデナデしないでください！\nもうっ…！　もう…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1036017, 0, "私、あなたの隣でちゃんと\n戦えてますよね？\nこれからはもっと、あなたと\n一緒にいられますよね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103701, 0.0, 1, 0, 1, 0, 8.2, 0, 0, 1, 3, 1037001, 0, "おや、何をしてるのかな？\n不審な行動をしたら、\nあなたでも逮捕するよ？\nなんて、冗談だけど♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103701, 0.0, 2, 0, 2, 0, 6.6, 0, 0, 1, 1, 1037002, 0, "稽古に付き合ってほしいな\n大丈夫大丈夫！\nきちんと手加減するから！\nふふ、多分だけどね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103701, 0.0, 2, 0, 3, 0, 5.6, 0, 0, 1, 1, 1037003, 0, "さて、今日も町の見回りに\n行かないと。あなたも来る？\n何があっても、私が守って\nあげるから安心していいよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103701, 0.0, 4, 0, 4, 0, 6.4, 0, 0, 1, 1, 1037004, 0, "うーん、自分では器用な方だと\n思ってたんだけどなぁ…\nあなたの前だと上手くいかない\n…どうしちゃったのかなぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103701, 0.0, 5, 0, 5, 0, 9.1, 0, 0, 1, 4, 1037005, 0, "からかうつもりなのに、あなたには\nいつも逆にやられちゃう\nまったく、調子が狂って困る\nほんと…困るよ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1037006, 0, "相手の動きを見て、正確に急所へ\n必殺の一撃を叩きこむ！\nそれが剣術の基本だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1037007, 0, "戦いの後ってすごく疲れるし、\nお腹が空くんだ…あなたも\n一緒にご飯なんてどうかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1037008, 0, "無駄な動きを削っていけば、\n剣技は磨き上げられる\n単純だけど難しいんだよね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1037009, 0, "あなたを守るためにも、もっと\n強くなりたい。最近は\nそう思うようになったよ\n…剣をやっていてよかった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1037010, 0, "一緒にいるだけで力が\n湧いてくる。あなたの存在が\n私を強くしてくれるんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103701, 0.0, 2, 0, 6, 0, 9.3, 0, 0, 2, 1, 1037011, 0, "ふ～ん、今日が誕生日なんだ？\nじゃあ、大人になったあなたに\nご飯でもおごってもらおうかな？\nな～んて、冗談だよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1037012, 0, "誕生日おめでとう！　ん、急に\n押しかけて迷惑だった？　今日は\nあなたの部屋を片付けようと思って\nまるでお嫁さんみたいじゃない？　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1037013, 0, "心が鈍れば、剣も鈍る\n逆もまた然りだ。けれど今は…\n一点の曇りもなく澄み渡ってる\n…ふふ、あなたへの気持ちもね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1037014, 0, "今の私なら、どんな相手にだって\n負ける気がしないよ…ふふ、\nあなたがそばにいるからだろうね\nこれからもずっと頼りにさせて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1037015, 0, "誰のために剣を振るうのか\n力よりも技よりも\nその想いこそが大切なんだね\n…あなたが教えてくれたことだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1037016, 0, "どんなに苦しくて大変なときでも\nどうか忘れないでいて\nあなたにはミクマの剣が…\nこの私が、そばについてることを");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1037017, 0, "私、あなたに伝えたいことがある\n今はまだ…うまく言葉に\nできないけど、そのときが来たら\n必ずこの想いを伝えるから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103801, 0.0, 5, 0, 1, 0, 2.5, 0, 0, 1, 2, 1038001, 0, "おすすめの本があるんです\nお互いに感想をいいあえたら\n楽しいだろうなって…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103801, 0.0, 1, 0, 2, 0, 2.0, 0, 0, 1, 4, 1038002, 0, "ケホッ、ケホッ…\nあの、背中を\nさすってもらえませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103801, 0.0, 5, 0, 3, 0, 7.0, 0, 0, 1, 2, 1038003, 0, "もう少し身体がよくなったら、\n一緒に、どこか遠くへ…\n…なんて。えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103801, 0.0, 5, 0, 4, 0, 4.5, 0, 0, 1, 4, 1038004, 0, "体力がないから\nすぐに疲れちゃって…\n私、迷惑かけてませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103801, 0.0, 4, 0, 5, 0, 5.5, 0, 0, 1, 1, 1038005, 0, "頼ってばかりではいけませんね\n私も自分のことは自分で…\nごほごほっ…！\nすみません…お水をください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1038006, 0, "この子、ニャットちゃんって\nいいます\nとっても聞き分けの\nいい子なんですよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1038007, 0, "体が大きいけどニャットちゃん\nとっても甘えん坊なんですよ\nニャットちゃん、\nいい子でちゅね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1038008, 0, "子どもだったニャットちゃんも\nすっかり立派になりました\n私の、大事なお友達です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1038009, 0, "疲れていませんか？\nよかったら…\n私と一緒に休憩しません？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1038010, 0, "ニャットちゃんに抱きつくと、\nモフモフして気持ちいいです\nあなたの傍も…気持ちいいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103801, 0.0, 5, 0, 6, 0, 8.2, 0, 0, 2, 2, 1038011, 0, "お誕生日おめでとうございます\nあの…お姉ちゃんからも、\nお祝いされましたか？\nす、少し気になっただけで…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1038012, 0, "この本、お誕生日プレゼントです\n恋愛小説なんですけど、たまたま\nヒロインの名前がシオリで、\nしゅ、主人公の名前が…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1038013, 0, "ふふ、心強いですね\nあなたと力を合わせれば\nなんだって射抜ける気がします\n…さあ、いきますよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1038014, 0, "ずっと諦めていたことにも\n向き合っていきます\n生きているかぎり、可能性は\n私の中にあるはずですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1038015, 0, "すぅ…はぁ…すぅ…はぁ…\n呼吸を合わすって不思議ですね\nあなたと一つになったみたいで、\n力が湧いてきます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1038016, 0, "命中したらデートしてください！\nなんて台詞、あこがれちゃいます\n…あ、恋愛小説の話ですよ！\nえ、いいんですか？　うれしい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 103861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1038017, 0, "あなたと一緒だと、どんなことも\nドキドキで彩られて、楽しくて…\nそんな毎日を過ごしたいです\nこれからも、ずっとずっと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104001, 0.0, 2, 0, 1, 0, 4.5, 0, 0, 1, 1, 1040001, 0, "友達を作るためのチーム、\nそれがＢＢ団！\nえへへ…これからも、\nよろしくお願いします！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104001, 0.0, 5, 0, 2, 0, 6.5, 0, 0, 1, 1, 1040002, 0, "友達作りの道は、\n誰でも一歩目から\n踏み出してるんですよね\n私も…踏み出したいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104001, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1040003, 0, "今日も、お花に水あげて、\n動物さんにご飯をあげて～♪\nえへへ、ぼっちでも、\n楽しみはそれなりにあります");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104001, 0.0, 7, 0, 4, 0, 9.0, 0, 0, 1, 5, 1040004, 0, "私、たまに思っちゃうんです\nあなたがいてくれるなら、\n友達ができなくても…\nはっ、な、なんでもないです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104001, 0.0, 6, 0, 5, 0, 3.5, 0, 0, 1, 5, 1040005, 0, "あの人は、私のことを…はっ、\nいつからそこに！？　ええと、\nこれは、花占いです。ど、どんな\n占いかって…内緒ですっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1040006, 0, "いまは微力な私だけど、きっと\nみなさんのお役に立ちます\nそうすれば…いつか友達、\n増えるかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1040007, 0, "ぼっちだとしても…\n私にもきっとできることが\nあるはずだって思ったんです\n精一杯がんばりますね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1040008, 0, "守りたいものを守る…\nそのためにがんばります\nＢＢ団は私に、色々なことを\n教えてくれました！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1040009, 0, "ＢＢ団の活動のたびに\nあなたに会えるのが楽しみで…\n毎日、早く明日がこないかなって\n思っちゃいます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1040010, 0, "えへへ…私にこの先、\nどれだけ友達が増えても、\n一番はあなただから…\n安心してくださいねっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104001, 0.0, 2, 0, 6, 0, 9.2, 0, 0, 2, 5, 1040011, 0, "お、おたたん、おたんこなすー！\nすみません、ハッピーなお祝いの\n言葉を口にしたことがなくて…\n誕生日おめでとう五郎丸ー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1040012, 0, "お誕生日おめでとうございます！\n森にいる小鳥さんたちと\n合唱団を結成して、\nお祝いの歌を作ってきました！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104061, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1040013, 0, "ＢＢ団の絆の強さ、\nこの一撃で証明します！\n見ていてくださいね、団長さん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104061, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1040014, 0, "困っている友達を助けるのは\n当然！　ですよね、団長さん！\nあなたがピンチの時には\n誰より早く駆け付けますからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104061, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1040015, 0, "団長さんと一緒なら、\nどんな困難でも乗り越えられる\n気がします。だって私たち、\n友達ですからっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104061, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1040016, 0, "もしこの先、私に友達がたくさん\nできたとしても、団長さんは私の\n特別な人です。だってあなたは\n私の最初の友達なんですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104061, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1040017, 0, "時々、団長さんと一緒にいると\nなんだかドキドキしちゃいます\nこの気持ちは…友情が深まった\n証なのでしょうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104201, 0.0, 2, 0, 1, 0, 4.5, 0, 0, 1, 1, 1042001, 0, "私の唱喚術は、人々を\n守るためのもの…この力を、\nもっと役立てたいんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104201, 0.0, 2, 0, 2, 0, 3.5, 0, 0, 1, 1, 1042002, 0, "あの…良かったらまた、\nアイドルの練習に\n付き合ってもらえませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104201, 0.0, 6, 0, 3, 0, 3.5, 0, 0, 1, 7, 1042003, 0, "～～…♪\nい、いつからそこに！？\n…今の、聞いてました？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104201, 0.0, 5, 0, 4, 0, 4.0, 0, 0, 1, 1, 1042004, 0, "あなたの前だと、\n緊張せずに歌えるんです…\nどうしてでしょう…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104201, 0.0, 5, 0, 5, 0, 3.5, 0, 0, 1, 2, 1042005, 0, "歌にはいろんな可能性がある…\nそれを教えてくれたのは仲間と\n…あなたです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1042006, 0, "…もう少しだけ、近くに…\nそばにいるだけで、不思議と\nメロディが浮かんでくるんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1042007, 0, "目が覚めました？　向こうの街で\nお祭りが始まりましたよ\n後で一緒に行ってみませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1042008, 0, "…書きとめられないくらい\n心の中から言葉が溢れてくる…\n私、こんなに歌いたかったんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1042009, 0, "いつか…聞いてくださいね\nあなたへの思いを綴った歌を…\n恥ずかしがらずに、歌いますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1042010, 0, "波の音が心地いいですね\nずっと一緒に聞いていたいくらい\n…私、今なにか言いました？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104201, 0.0, 5, 0, 6, 0, 6.6, 0, 0, 2, 1, 1042011, 0, "お誕生日おめでとうございます\n歌以外にいいプレゼントが\n思いつかなくて…\nよかったら聞いてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1042012, 0, "お誕生日おめでとうございます\n短いものですが、お祝いに\n曲を書いてみました\nここで歌ってもいいですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104261, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1042013, 0, "あなたと共に歩んだ日々が\n作り上げたこの歌を\nあなたに聞いてもらえて…\n私、とても幸せです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104261, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1042014, 0, "自分で言うのもなんですが\nこの歌は特に自信作なんです\nあなたの心に響かせてみせます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104261, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1042015, 0, "歌っていると心が軽くなって\nつい体が動いてしまうんです\nよければあなたもどうですか？\nさあ、お手をどうぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104261, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1042016, 0, "いつかあなたにこの思いが\n届くと信じて歌詞を綴り…\nそして歌い続けることを\nここに誓います");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104261, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1042017, 0, "あなたが望むなら唱喚士でも\nアイドルでもない\nあなただけの歌い手として\nいつでも曲を捧げます…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104301, 0.0, 2, 0, 1, 0, 3.3, 0, 0, 1, 1, 1043001, 0, "さて、パトロールにでも行くか\n一緒に行きたいなら、\nついてきてもいいぜ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104301, 0.0, 2, 0, 2, 0, 3.5, 0, 0, 1, 1, 1043002, 0, "おい、飯はちゃんと\n食ってんだろうな？\n食ってないなら、\nあたしが用意してやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104301, 0.0, 5, 0, 3, 0, 6.2, 0, 0, 1, 6, 1043003, 0, "あたしみたいな奴、\n普通の女の子扱いするのなんて、\nおまえぐらいだぜ\nわ、わりとうれしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104301, 0.0, 2, 0, 4, 0, 4.6, 0, 0, 1, 1, 1043004, 0, "あたしの親友の恩人なら、\nあたしの恩人みたいなもんだ\nお互い仲良くしていこうぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104301, 0.0, 5, 0, 5, 0, 5.1, 0, 0, 1, 1, 1043005, 0, "お前って、けっこう男気もあるし\n肝っ玉もすわってるよな…\n一緒にいて、あたしは楽しいよ\nお前はどうだ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1043006, 0, "あたしから離れんなよな！\nどんなやつが来ようが、\nぶった切ってやる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1043007, 0, "弱い奴らばっかで、\n暴れ足りねぇ\n歯ごたえのある奴は、\nいねぇのか！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1043008, 0, "お前と一緒なら、\nどんな敵も楽勝だぜ！\nお前はあたしが守るからよ、\nだからあたしのことも、頼むぜ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1043009, 0, "よっしゃー！\n敵が全部片付いたら、\n二人でパーッと飯だ飯！\nお前の好物たくさん作ってやる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1043010, 0, "力がみなぎってくるぜぇ！\nあたしから離れんなよ！\n何があっても絶対に\nお前を守ってやんよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104301, 0.0, 2, 0, 6, 0, 3.2, 0, 0, 2, 1, 1043011, 0, "へぇ、今日が誕生日だったのか\nおめでとさん！\nんじゃ、せっかくだし\nなんかおごってやるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1043012, 0, "今日ってお前の誕生日だよな？\nぐ、偶然、たまたま作った\nケーキがあるんだけど…\n食わねぇか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1043013, 0, "あたしの本能が目覚めちまったぜ\n…こっから先は手加減なしだ\nこの研ぎ澄まされた爪と牙で\n刻みつけてやる…あたしの力を！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1043014, 0, "なぁ…その、大丈夫だよな？\n全力のあたしを見て…\n引いたりしてないよな？\nっ！　そっか、よかった…へへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1043015, 0, "力がありあまってるからか\n体がうずきやがる…えっ？\nおまえが相手をしてくれる？\nいいね、じゃあぶつかりあうか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1043016, 0, "へへっ、すげぇ力だろ？\nおまえのために強くなったんだぜ\nだから…その、なんだ\n…あたしから目をそらすなよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1043017, 0, "あたしがおまえを守る\nおまえがあたしを守る\nそういう関係が続けばいいよな\nずっと…ず～っと、なっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104401, 0.0, 1, 0, 1, 0, 5.0, 0, 0, 1, 6, 1044001, 0, "お主…わらわと何処かで\n会ったことはないか？\nお主を見ていると、何故か\nなつかしさを覚えるのじゃが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104401, 0.0, 1, 0, 2, 0, 6.4, 0, 0, 1, 4, 1044002, 0, "むう…どういうわけか\n無性にトマトジュースが\n飲みたい気分じゃの…\nお主、どこぞで求めてまいれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104401, 0.0, 2, 0, 3, 0, 6.9, 0, 0, 1, 4, 1044003, 0, "昼間は思うように動けぬ…\n夜になるまで、じっとしておこう\nそれまでお主は、わらわの\n話し相手になるのじゃぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104401, 0.0, 4, 0, 4, 0, 4.2, 0, 0, 1, 6, 1044004, 0, "お主はちっとも、わらわを\n恐れないのじゃな？\nな？　可愛らしいじゃと？\n眷属の分際で何を申すー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104401, 0.0, 5, 0, 5, 0, 9.0, 0, 0, 1, 2, 1044005, 0, "お主は、特別な眷属じゃ\n今より「昼を総べる者」を\n名乗るが良いぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1044006, 0, "わらわの真の姿は美しかろう？\nなに？　見慣れてないから\nいつもの方が良い？　なんと\n張り合いのないことを…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1044007, 0, "わらわの野望を聞きたいのか？\nもちろん世界征服じゃ！\n女と生まれたからには\n一度は目指したいものよの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1044008, 0, "わらわは伝説の吸血鬼\nお主の命を奪うなど\nたやすいことじゃが…まあ、\n眷属ゆえ生かしておいてやろうぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1044009, 0, "世界を征服した暁には\nその半分をお主に分け与えよう\nど、どういう意味かわかるな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1044010, 0, "昼間にお主と会うために、\n最近は昼夜逆転してしまった…\n昼ふかしは、美容によくないの\nじゃが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104401, 0.0, 6, 0, 6, 0, 12.9, 0, 0, 2, 2, 1044011, 0, "今日は誕生日か？　めでたいな！\nいくつになったのじゃ？\nそうか。わらわがその歳の頃は…\nはて？　何をしていたかのう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1044012, 0, "眷属の誕生日を祝して、\nわらわの膝枕を贈呈しようぞ\nどうじゃ、心地よいじゃろう？\n腕枕でも胸枕でも良いのじゃぞ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104461, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1044013, 0, "そうじゃ、この威厳ある姿こそ\nわらわの本当の姿\nお主だけは特別じゃ\n存分に見惚れるがよいぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104461, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1044014, 0, "わらわこそが夜を統べる者\nさあ、夜の散歩へゆくぞ\nついてくるがよい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104461, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1044015, 0, "はーっはっはっは！　見よ！\n月までもがわらわたちを\n祝福しておるようじゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104461, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1044016, 0, "くっくっく……\nこれぞわらわの真の力じゃ\n眷属として誇りに思うがよい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104461, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1044017, 0, "なんじゃ？\nそのように見つめられると\nうっかり魅了して\nしまいそうじゃのう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104501, 0.0, 2, 0, 1, 0, 4.0, 0, 0, 1, 5, 1045001, 0, "ぐふふふ…\nえ？　な、何でもないですよ？\nあなたを見て、妄想なんて…\nはぁはぁ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104501, 0.0, 6, 0, 2, 0, 5.0, 0, 0, 1, 5, 1045002, 0, "「ドＳさんは、クウカを\n追いつめるとムリヤリ…」\nでゅふふふふ…あっ！\nいいいつの間に居たのですか！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104501, 0.0, 6, 0, 3, 0, 3.3, 0, 0, 1, 2, 1045003, 0, "この間読んだ本が、\nとっても素敵で…え？\n内容ですか？　そ、それは…\n聞かないほうがいいですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104501, 0.0, 5, 0, 4, 0, 4.0, 0, 0, 1, 2, 1045004, 0, "夢の中にまでドＳさんが\n出てきてくれました…\n寝ても覚めても、クウカは\n支配され続けてしまうのですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104501, 0.0, 5, 0, 5, 0, 3.9, 0, 0, 1, 2, 1045005, 0, "ドＳさんと二人っきり…\nこ、これは…！？\nクウカはどんな辱めを\n受けてしまうのでしょう…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1045006, 0, "こんな汚れたクウカを見る\nドSさんの冷ややかな視線\n…ふひっ、ふひひ！\nああ、また昂ぶってしまいます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1045007, 0, "こんな醜態をさらしながらも\nこの後さらにドSさんに\nめちゃくちゃにされることを\nクウカは望んでいますぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1045008, 0, "「魔物どもにこんなにされて\n嬉しいんだろええおい！」なんて\n言葉責めするつもりですね！？\nさすがはドＳさん、上級者…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1045009, 0, "もうお嫁にいけません…\nだから、一生ドSさんの\n支配下に置いてください！\nもっと体に教え込んでください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1045010, 0, "クウカはもうドＳさんなしでは\n生きられない体に…\nこ、この責任はちゃんと\n取ってくれるのですよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104501, 0.0, 2, 0, 6, 0, 8.5, 0, 0, 2, 1, 1045011, 0, "誕生日おめでとうございます\nケーキに深く刺さるロウソクが、\nとろっとろに溶けていますよ\nフゥー…って消してください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1045012, 0, "ドＳさんの誕生日ですね…\nクウカの体に巻いたリボンを\n乱暴にほどいて、包装紙を破る\n子供のように服も…ぐふふふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104561, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1045013, 0, "気持ちいいです～っ♪\nクウカの体を昂らせるこの力…\n全て、ドＳさんに捧げます！\nどうぞお好きなように…ぐふふ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104561, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1045014, 0, "はっ！？　ドＳさんの目が\nクウカに告げています…！\n「どこが成長したか見せてみろ」\n分かりました…み、見てください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104561, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1045015, 0, "ドＳさんの激しい攻めを\n受け止めるためなら、クウカは\nどこまでも強くなれるんですっ！\nねっ、ドＳさん♪　…じゅるり♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104561, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1045016, 0, "ドＳさんとクウカは以心伝心…\n言葉がなくとも通じ合っています\n今は…クウカを弄びたいのですね\n…違うのですか！？　そんな～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104561, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1045017, 0, "ドＳさん…いえ、あなたといると\nクウカはいつも心地良くて…\n胸の奥が、温かくなるのです\nだからいつまでも…ふふっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104601, 0.0, 2, 0, 1, 0, 3.0, 0, 0, 1, 1, 1046001, 0, "野良猫たちに、\nたい焼きを買ってあげてるにゃ！\nみんな喜んでくれるんだにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104601, 0.0, 2, 0, 2, 0, 2.3, 0, 0, 1, 1, 1046002, 0, "早く準備して出かけるにゃ！\n「時はルピなり」だにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104601, 0.0, 2, 0, 3, 0, 5.8, 0, 0, 1, 1, 1046003, 0, "お客さ～ん♪\nここだけのオイシイ話が\nあるんだにゃ～\n一口乗ってみないかにゃ～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104601, 0.0, 2, 0, 4, 0, 4.9, 0, 0, 1, 5, 1046004, 0, "あたしは気まぐれだから、\nちゃーんとつかまえて\nほしいにゃ？\nでないと逃げちゃうにゃ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104601, 0.0, 2, 0, 5, 0, 6.0, 0, 0, 1, 5, 1046005, 0, "ふふふ\nキミが怪我したら…\nぺろぺろ舐めてあげにゃくも…\nないかにゃ～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1046006, 0, "これでも、困ってる人がいたら\n助けずにはいられないのにゃ！\n今夜は正義のため、猫と一緒に\n飛び回るのにゃー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1046007, 0, "今度、ギルドにも\n遊びに来るといいにゃ\nにゃふふ、これで人手が増え…\n…にゃ、にゃんでもないにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1046008, 0, "あたしは気まぐれだけど、\nやる時はやる猫なのにゃ\n今日はやるかにゃ？\nだったら…全力にゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1046009, 0, "えへへ。キミといられるなら…\n家猫になっちゃっても\nいいかにゃ～\nにゃんてにゃ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1046010, 0, "あとで、キミと二人で\n食べようと思って\nたい焼きを取ってあるのにゃ\n半分こして食べようにゃ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104601, 0.0, 2, 0, 6, 0, 4.5, 0, 0, 2, 1, 1046011, 0, "今日はキミの誕生日みたいにゃ！\nだから今日だけ特別に\nたい焼き食べ放題に\nしてあげるにゃ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1046012, 0, "お誕生日、おめでとうにゃ！\n気まぐれなあたしでも、\n今日ぐらいは…キミだけの\n猫になってあげてもいいにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1046013, 0, "キミとあたしがそろえば、どこに\nお宝が隠されてようと楽勝にゃ！\nファントムキャッツに\n不可能はないのにゃ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1046014, 0, "す、すっごいお宝にゃ…！\nこんなにいっぱいあるなら、\nちょっとくらいネコババしても…\nな、なんでもないですにゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1046015, 0, "このお宝があれば、\n苦しんでる人たちを\nいっぱい助けられるにゃ～♪\nさっそく一緒に配りに行くにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1046016, 0, "キミが支えてくれるから、\nあたしはファントムキャッツで\nいられるにゃ…いつも\n一緒に居てくれてありがとにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1046017, 0, "い、いつもは猫とあたしでお宝を\n頂戴してるけど、誰かと一緒も\n悪くにゃいというか…その、\nまた一緒に来てくれるかにゃ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104701, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1047001, 0, "こう見えて私は女なんだ\nわけあって鎧は外せないが\nよろしく頼むよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104701, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1047002, 0, "鎧に太陽光を浴びせると\n表面が恐ろしく熱くなるが\n中の私は意外と平気なんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104701, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1047003, 0, "体を動かすのは好きだが\n水泳は苦手なんだ\n鎧が重くてね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104701, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1047004, 0, "顔を見せられない分\n今なにをどう思っているか\n伝えるよう努力するよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104701, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1047005, 0, "私の表情は見えないかも\nしれないが、君の表情は\nしっかり見えているよ、いつもね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1047006, 0, "この剣を振らずに済む\n世の中にするためにも、\n私は剣を振らねばならないんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1047007, 0, "どんな強敵が相手であろうと\n私は恐れずに立ち向かうよ\n子供たちの笑顔のために");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1047008, 0, "たとえ炎に包まれた戦場でも\n助けを呼ぶ声がある限り\n私はその地へと赴こう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1047009, 0, "私が戦っている時\n君の視線を背中に感じると\n心が安らぐんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1047010, 0, "私は団長として人々の安息は\n守らねばならないが、君だけは\n優先して守ってしまいそうだ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104701, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1047011, 0, "今日は君の誕生日なんだってね\n一つ大人になったんだから、今年は\nあまり無茶をしないでくれよ？\n君のことが心配なんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1047012, 0, "誕生日おめでとう！　何か\nお願い事はないかい？　そ、それは\nダメだ少年！　兜を脱ぐなんて…\n恥ずかしくて爆発してしまうよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1047013, 0, "君がそばにいるから…\n限界のその先に手が届く\nこの力は私だけのものじゃない\n君と私…二人の力だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1047014, 0, "みんなが何事もなく\n平和な日常を送れたらうれしい\nそのために私は剣を振るい\n戦っているんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1047015, 0, "戦いで臆病になって退くな\n私たちの後ろには\n守るべき者がいると思え\n安心しろ、私がついている");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1047016, 0, "君はまた何かに巻き込まれたのか\n…どうして分かるのかって？\nふふっ、顔を見ればすぐに分かる\n私で良ければ力になろう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1047017, 0, "君は私を特別扱いしない\nだから、一緒にいると\nとても居心地が良いんだ\nこれからも変わらずに頼むよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104801, 0.0, 2, 0, 1, 0, 3.9, 0, 0, 1, 1, 1048001, 0, "素材を稼ぐか、ルピを稼ぐか…\nどっちの戦略も練ってあるから\n必要なら相談してね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104801, 0.0, 2, 0, 2, 0, 2.9, 0, 0, 1, 2, 1048002, 0, "今回用意した計画もバッチリよ\nもし街の外に行くのなら\n早めに出かけましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104801, 0.0, 1, 0, 3, 0, 4.5, 0, 0, 1, 2, 1048003, 0, "効率的な動きに大事なのは、\n観察する力よ\n普段からちゃんと鍛えてる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104801, 0.0, 5, 0, 4, 0, 4.1, 0, 0, 1, 2, 1048004, 0, "かけ持ちしすぎちゃったから\nバイトを手伝ってくれない？\nほら、キミなら頼りになるし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104801, 0.0, 2, 0, 5, 0, 5.8, 0, 0, 1, 2, 1048005, 0, "キミのスケジュール管理、\n私がしてあげようか？\n24時間つきっきりで\nもちろん管理費はいただくけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1048006, 0, "よければキミのお財布、\n私が管理してあげよっか？\nえっ？　盗らないわよっ！\n見損なわないでっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1048007, 0, "この町に泥棒が現れたの！？\nまじめに働いてる人への\n冒涜じゃない！\nゆ、許せないわっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1048008, 0, "今日は特売セールの日！\nもちろんキミも、一緒にきてね！\n油断したら命に関わるわよ！\nそれじゃあ…とっつげき～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1048009, 0, "あの…キミって、\n好きなタイプっているのかしら？\nえ？　ちょ、ちょっと気になった\nだけよ。それだけだってばっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1048010, 0, "キミを傷つけるやつがいたら…\nこうしてやっつけてあげるわ！\n私の大切な弟子だもの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104801, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1048011, 0, "ケーキの準備よし！\nプレゼントもよし！\nさあ、効率的な\n誕生日パーティーを始めるわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1048012, 0, "誕生日おめでとう\nお祝いの言葉なんて\n非効率的かもしれないけど…\nキミにはちゃんと言いたかったの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1048013, 0, "キミの力と私の力が合わされば、\n相手が誰であろうと\n負けるはずがないわ！\n最大効率で一気に片付けるわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1048014, 0, "攻撃に大事なのは、１に効率、\n２に効率！　３も４も５も効率！\n究極の効率を体現したこの技で、\n纏めてぶっ飛ばしてやるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1048015, 0, "こんな討伐依頼は\nさっさと終わらせて、\nバーゲンセールに行くわよ！\n…もちろん、キミと二人でね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1048016, 0, "敵に包囲されたか…\n前の敵は私がやるから、\nキミは後ろから援護をお願い！\n…私の背中、預けたからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1048017, 0, "この力…キミの優しい心が、\n私に力を与えてくれてるのね…\nありがとう…私、キミのためなら\nどこまでも強くなってみせるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104901, 0.0, 2, 0, 1, 0, 4.8, 0, 0, 1, 5, 1049001, 0, "なんでもかんでもどんなことでも\nお姉ちゃんに言ってね！\nもっともっと頼って甘えて～\n遠慮しないでいいんだぞっ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104901, 0.0, 2, 0, 2, 0, 3.2, 0, 0, 1, 5, 1049002, 0, "いつでもお姉ちゃんに\n甘えてくれていいんだぞっ☆\nナデナデだって、ギューだって、\nいっぱいしてあげちゃうからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104901, 0.0, 2, 0, 3, 0, 4.3, 0, 0, 1, 5, 1049003, 0, "お姉ちゃん、うれしくって、\nついチョップが出ちゃいそう～\nこれはお姉ちゃんの愛情だから、\nしっかり受け止めてね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104901, 0.0, 2, 0, 4, 0, 2.8, 0, 0, 1, 5, 1049004, 0, "色んなこと、全部やってあげる♪\nだって、弟くんのお世話をするのは\nお姉ちゃんの役目だもん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104901, 0.0, 2, 0, 5, 0, 2.1, 0, 0, 1, 6, 1049005, 0, "なになに？　用事？\nいつでも、どこでも、\nどんなことでも、\nお姉ちゃん、大歓迎だよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1049006, 0, "可愛い弟くんを困らせる人は、\nお姉ちゃんが\nぜーんぶ成敗してあげる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1049007, 0, "どんなことがあっても、\n弟くんはお姉ちゃんが\n絶対守るからね！\n弟くんには指一本触れさせない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1049008, 0, "お姉ちゃんは、弟くんを\n守るために強くなったんだもん\nお姉ちゃんは、弟くんの盾\n弟くんの剣。弟くんの鎧だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1049009, 0, "弟くんとのひとときを邪魔する\n野暮な奴は許さないよ！\nチョップくらいじゃ\n済まさないんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1049010, 0, "弟くんをいじめるなんて、\n神さまが認めても、\n天が見逃しても、\nお姉ちゃんが許さない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104901, 0.0, 2, 0, 6, 0, 7.9, 0, 0, 2, 5, 1049011, 0, "キミが一つ歳をとっても、\nお姉ちゃんも歳をとってるから、\n私はずっと弟くんのお姉ちゃんの\nまんまだよ♪　安心した？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1049012, 0, "お誕生日おめでとう！\n弟くんがどれだけ大人になったか\nお姉ちゃんに、しっかり見せて！\nじーーーーーーーーーーーーっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104961, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1049013, 0, "弟くんっ、お姉ちゃんだよっ☆\nえへへ、ビックリした？\nじゃあ今度は驚かないように\n耳元で優しく…お姉ちゃんだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104961, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1049014, 0, "お姉ちゃん、強くなっちゃった！\nこの力で弟くんを守っちゃうね♪\nそのためには～、もっとも～っと\n一緒にいなくちゃね？　ねっ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104961, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1049015, 0, "ねぇ弟くん、お姉ちゃんの悩みを\n聞いてくれるかなぁ？　あのね、\nもっと弟くんに甘えてほしいんだ\n…どうしたらいいと思う？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104961, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1049016, 0, "ぎゅう～っ♪\nお姉ちゃんハグ、温かいでしょ？\nあっ…ちょっと離れてもいい…？\n…ドキドキ、伝わっちゃったかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 104961, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1049017, 0, "ぜ～んぶお姉ちゃんに任せてね♪\nん？　全部ってどれくらいって？\n弟くんが望むことすべて、だよっ\n…私の幸せは、キミの幸せだもん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105001, 0.0, 2, 0, 1, 0, 2.5, 0, 0, 1, 1, 1050001, 0, "ちょっとこの雑誌見てー！\n右の服と左の服、\nどっちがアタシに似合うと思う？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105001, 0.0, 6, 0, 2, 0, 6.9, 0, 0, 1, 1, 1050002, 0, "ね、ちょっと聞いていい？\nアンタって３桁の掛け算とかも\n出来たりするわけ？\nま、まさか割り算とかも…！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105001, 0.0, 5, 0, 3, 0, 3.5, 0, 0, 1, 4, 1050003, 0, "大人のレディには教養も大事？\nきょ、教養ってあれよね、\n成績とは関係ないのよね！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105001, 0.0, 2, 0, 4, 0, 5.5, 0, 0, 1, 1, 1050004, 0, "同年代の子と話しても、みんな\n子どもっぽくって退屈なのよね\nだからアンタと遊んであげるわよ\n感謝しなさいよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105001, 0.0, 5, 0, 5, 0, 3.4, 0, 0, 1, 2, 1050005, 0, "まずカフェに行って、次に…\nアンタはこうゆうの、\n詳しくなさそうだから\nアタシがエスコートしないとね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1050006, 0, "大人なレディだもん、\n魔物退治くらい簡単よ！\nまぁ、どうしても気になるなら、\n付いてきてもいいけど？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1050007, 0, "この前、魔物を蹴散らして\nがっつりお宝ゲットしたのよ！\n「子どもに大金は必要ない」って\n親にとりあげられたけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1050008, 0, "どきなさーい魔物ども♪\nレディ・ミサキのお通りよ！\nアンタはちゃんと、\nエスコートしなさいよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1050009, 0, "なんか最近、アンタと出かける\n機会、多いわよね\nアタシはけっこう楽しんでるわよ\nアンタはどう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1050010, 0, "二人で魔物退治して、\nそのお金で豪華にディナー！\nそれってすっごく大人よねぇ♪\nで、デートじゃないわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105001, 0.0, 6, 0, 6, 0, 9.8, 0, 0, 2, 2, 1050011, 0, "誕生日、おめでと！\nプレゼントは…ア・タ・シ・よ♪\n…あれ？　引いてる？\n雑誌に書いてあったのに…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1050012, 0, "ハッピーバースデー！\nアタシが、大人なバースデーを\nプロデュースしてあげるわ\n楽しみにしてなさい♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105061, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1050013, 0, "うふ♪\n今日のアタシは一味違うわよ\nアタシがよそ見しないように\nしっかりエスコートしてよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105061, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1050014, 0, "ねぇねぇ、アタシキレイでしょ♪\n…むぅ。かわいいじゃなくて、\nキレイって言いなさいよ\n言うまで帰さないんだからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105061, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1050015, 0, "え？　いつもより大人っぽい？\nほんと！？　…じゃなくて…\n当然のことよ。だってアタシは\n正真正銘のレディなんだから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105061, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1050016, 0, "大人のレディなら微笑むだけで\nみんなを虜にしちゃうのよ♪\nねぇ、アンタはどう？\nアタシにドキドキする！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105061, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1050017, 0, "アタシ、これからもっと\nキレイになると思うわ\nだからその日まで、アタシから\n目を離さない方がいいわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105101, 0.0, 2, 0, 1, 0, 3.0, 0, 0, 1, 1, 1051001, 0, "ねえ君、今時間ある？\n怖がらなくても大丈夫\n危ないことはしないわ\n私を信じて…ね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105101, 0.0, 5, 0, 2, 0, 2.3, 0, 0, 1, 1, 1051002, 0, "良かったら一緒に実験しない？\n新しい発見があった時の\n興奮は計り知れないわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105101, 0.0, 2, 0, 3, 0, 2.5, 0, 0, 1, 1, 1051003, 0, "科学の発展はみんなのため\nこう見えても、病気や怪我を\n治すのが目標なの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105101, 0.0, 2, 0, 4, 0, 5.4, 0, 0, 1, 5, 1051004, 0, "君の体を調べている内に、\n君自身に興味が出て来たわ\nうふふっ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105101, 0.0, 5, 0, 5, 0, 1.7, 0, 0, 1, 2, 1051005, 0, "いつも実験に協力してくれて\nありがとう。お礼に今度、\nとっておきの秘薬をあげるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1051006, 0, "あーら、私におしおきされたいの？\nそう…目覚めちゃったのね\nたっぷりと可愛がってあげる♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1051007, 0, "ねぇ、どうかしら…？\n私を見て、何か感じない？\n恐怖？　…おかしいわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1051008, 0, "痛みが快楽に変わる瞬間は、\n必ず訪れるものよ…\n君も体験してみない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1051009, 0, "君を虜にするための香水を\nつけてきたはずなのに、\nどうして魔物ばっかり…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1051010, 0, "君といると、不思議だけど…\n鼓動がぐっと早まるの\n…確かめてみる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105101, 0.0, 2, 0, 6, 0, 12.8, 0, 0, 2, 5, 1051011, 0, "君、今日が誕生日でしょう？\nはいこれ。私からのプレゼント\n特別に調合した薬なの。効能？\n…飲んでからのお楽しみ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1051012, 0, "ハッピーバースデー！\n私からの贈り物は…これよ\n元気が出る特製ドリンク♪\n一気に飲んじゃだめよ…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105161, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1051013, 0, "探求心は人間の限界を超えていく\n全ての不可能はやがて可能になる\n…君と実験を続けていけば\nそれを証明できると信じているわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105161, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1051014, 0, "新しい薬の効果は絶好調ね\nこれも君がずーっと実験台を\n務めてくれたおかげよ♪\n…あら、何か言いたそうな顔ね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105161, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1051015, 0, "新しい研究のアイデアが\n次から次へと溢れてくるのよ\nどんな実験をするつもりかって？\nふふっ、すぐに体験させてあげる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105161, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1051016, 0, "研究に行き詰ったときでも\n君の顔を思い出すと、もう少し\nがんばろうって気になるのよ\n私にとっては、栄養剤ね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105161, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1051017, 0, "どれだけ研究を重ねても、結婚の\n正解だけは分からないわね\nでも、君と検証していけば\n答えも出るかしら…ふふっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105201, 0.0, 5, 0, 1, 0, 5.8, 0, 0, 1, 1, 1052001, 0, "私、力持ちだからそれを活かして\nお仕事もいっぱい頑張ってるの♪\nだけど、これって女子的には\nちょっとどうなのかしら～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105201, 0.0, 9, 0, 2, 0, 7.0, 0, 0, 1, 1, 1052002, 0, "どう？　すてきな鎧でしょ？\nデザインもおしゃれだし\nボディにジャストフィットなの！\n私のお気に入りよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105201, 0.0, 5, 0, 3, 0, 0.0, 0, 0, 1, 5, 1052003, 0, "も～、あなたったらさっきから\nモフモフしすぎじゃない？\nせっかくブラッシングして\n毛並みを整えたばかりなのに～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105201, 0.0, 1, 0, 4, 0, 4.8, 0, 0, 1, 2, 1052004, 0, "少しでも女の子らしく\n見えるように、私、がんばるわ！\nこのままあなたのそばに\n居られるように…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105201, 0.0, 5, 0, 5, 0, 0.0, 0, 0, 1, 5, 1052005, 0, "はぁ～…憧れちゃう…\n普通の人間の女の子みたいに\nおしゃれしたり食べ歩きしたり\nあなたとデートしたいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1052006, 0, "こんな見た目だからって\n最初からあきらめちゃダメよね！\nあなたに嫌われたくないし\n身だしなみには気を付けるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1052007, 0, "なあに？　またモフモフするの？\nまあいいけどね、あなただから\nい～こい～こしてあげるわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1052008, 0, "魔物に間違われることも多いけど\n私、運命なんかに負けないわ！\nあなたと会ってから、ますます\nそう思えるようになったの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1052009, 0, "あなたは私のこの姿を見ても\n女の子として接してくれた！\n人の内側をきちんと見てくれる\nそんなあなたが大好き！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1052010, 0, "こんなに綺麗な空の下を\n大好きな人と一緒に歩けること\n普通の女の子になったみたいで\nすっごく嬉しいの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105201, 0.0, 9, 0, 6, 0, 7.2, 0, 0, 2, 1, 1052011, 0, "お誕生日おめでとう～！\nあなたのためにお祝いのプランを\nいっぱい考えたの♪\nやだっ、私ったらはしゃぎすぎ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1052012, 0, "ハッピーバースデートゥーユー♪\n人間の男の子の誕生日をこうして\nお祝いできるなんてね…\n私がプレゼントをもらった気分♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105261, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1052013, 0, "いつかこの素敵なドレスを着て\nあなたと踊れたらいいな…\n踊りの練習だけでも\n始めておこうかしら♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105261, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1052014, 0, "舞台の幕が上がったら\n私はお姫様…まさかこんな形で\nお姫様になれるなんて\n思わなかったけど、うれしいわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105261, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1052015, 0, "私がお姫様になったら\n何をしようかしら…\n王子様と二人きりの舞踏会とか\nしてみたいわね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105261, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1052016, 0, "私、あなたと出会ってから毎日が\nどんどん楽しくなっていくの！\n次はいつ会えるんだろうって\n考えてるだけで幸せよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105261, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1052017, 0, "あなたが王子様だったら、\n誰のどんなピンチにでも\n駆けつけてくれるんでしょうね！\n優しい王子様に憧れちゃう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105301, 0.0, 2, 0, 1, 0, 4.1, 0, 0, 1, 1, 1053001, 0, "貴公と私、共に\nヴァイスフリューゲルの\n興隆のために\n力を尽くそうではないか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105301, 0.0, 5, 0, 2, 0, 2.7, 0, 0, 1, 3, 1053002, 0, "こ、子供ではない！\nた、確かに少々背は低いが…\nまだ成長期に違いない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105301, 0.0, 2, 0, 3, 0, 4.5, 0, 0, 1, 1, 1053003, 0, "ん？　どうした？\n私と鍛錬がしたいのか？\n殊勝な心がけだな、貴公！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105301, 0.0, 2, 0, 4, 0, 4.5, 0, 0, 1, 1, 1053004, 0, "軍人は、受けた恩は返すものだ\nだから…私にできることが\nあれば、なんでも言ってくれ\n頼りにしてくれよな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105301, 0.0, 5, 0, 5, 0, 3.0, 0, 0, 1, 6, 1053005, 0, "と、ところでだな…\n今度、貴公と二人で、\n我が本国を旅行するとか…\nど、どうだろうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1053006, 0, "何度言えばわかるのだ！\nだから、私は子供ではないっ！\nば、バカにするな～！\nこ、こら、頭をなでるな～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1053007, 0, "天空より舞い降りし\nヴァイスフリューゲルの剣技！\nその華麗なる姿を、\n見せてやる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1053008, 0, "貴公！\n気がたるんではいないか？\n今すぐ準備をして平原に来い！\n私と共に鍛錬だ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1053009, 0, "私は子供ではない、が…\n貴公から見たら、やはり子供に\n見えてしまうのだろうか？\nそうでないと、いいのだが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1053010, 0, "貴公に私の命は預けた\nこの身が尽きるまで…\n軍人の誇りを持って、\n二人で戦いぬくぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105301, 0.0, 2, 0, 6, 0, 8.1, 0, 0, 2, 1, 1053011, 0, "今日は貴公の誕生日らしいな\n大切な一日を共に祝えて、\n私も嬉しく思うぞ！\n心から祝おうじゃないか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1053012, 0, "誕生日おめでとう\nプレゼント代わりに…\n今日は私が共に過ごしてやろう\nじ、時間を無駄にするでないぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1053013, 0, "待たせたな！\nこれが私の新しい奥義だ！\n友よ、見届けてくれ！\n我らの勝利の瞬間を！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1053014, 0, "驚くのはまだ早いぞ！\nなんといっても私は成長期！\n修行すれば更に大きく…\n背の話ではなく奥義の話だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1053015, 0, "よし！　また成功だ！\n貴公、見てたか！？\n…『努力の天才』？\nて、照れるではないか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1053016, 0, "私の勝利が貴公の勝利となる！\nだからこそ負けられん！\n我が名誉よりも、友への想いで\n私はこの勝負に勝つのだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1053017, 0, "どれほどこの身が傷つこうとも\n敵は誰一人通さんっ！\n我が友を…\n貴公を守るのは、この私だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105401, 0.0, 5, 0, 1, 0, 1.9, 0, 0, 1, 1, 1054001, 0, "いいですか騎士さん\nレイ様に変なことしたら\nただじゃおきませんよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105401, 0.0, 2, 0, 2, 0, 2.8, 0, 0, 1, 1, 1054002, 0, "騎士さんがレイ様に\n変なことしないように\n私がしっかり見張ります！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105401, 0.0, 1, 0, 3, 0, 2.0, 0, 0, 1, 4, 1054003, 0, "ああ、また服が…\nちょっとこっちに来てください\nすぐに直してあげますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105401, 0.0, 6, 0, 4, 0, 4.8, 0, 0, 1, 4, 1054004, 0, "あの…騎士さんって\nやっぱりレイ様のこと…\nな、なんでもないです！\n忘れてください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105401, 0.0, 6, 0, 5, 0, 6.5, 0, 0, 1, 5, 1054005, 0, "どうして騎士さんは\nひどいことした私にここまで\n優しくしてくれるんですか？\nまさか…また縛られたいとか！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1054006, 0, "何匹来ても平気です！\n騎士さんがそばにいてくれるから！\n…変な意味じゃありませんよ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1054007, 0, "そこはさすがに近すぎます\nもう少し下がってください\nさもないと、日頃の鬱憤\nぶつけちゃいますよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1054008, 0, "素材が余ったら騎士さんにも\n何か作ってあげましょうか\nそうですね…何がいいですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1054009, 0, "最近も…相変わらずレイ様と\n仲いいんですか…？\nき、聞いてみただけですっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1054010, 0, "騎士さんは私に何か\n手伝ってほしいことあります？\n報酬は特別になしでいいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105401, 0.0, 5, 0, 6, 0, 5.3, 0, 0, 2, 1, 1054011, 0, "た、誕生日なんですよね？\n一応おめでとうございます！\n…どこで知ったかなんて\nどうでもいいじゃないですか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1054012, 0, "あの、おめでとうございます…\n騎士さんの誕生日ですよ！\nこれ…ハンカチです…え？\nプレゼントですっ！　バカっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105461, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1054013, 0, "騎士さんとの絆が強くなるほどに\n私の力も強くなります！\n…って、いいですけど\nちょっと近くないですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105461, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1054014, 0, "一人一人が輝く服を作るのが\n私の夢なんですっ！\nこの力があれば、\nそれを叶えられますよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105461, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1054015, 0, "見ていてください！\nアイドルとしてだけじゃなく\n仕立て屋としても\nみんなを笑顔にしてみせますっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105461, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1054016, 0, "いつもお世話になってますから\n今度は私がお手伝いしますね？\nべ、別に騎士さんと一緒に\n居たいわけじゃないです…からっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105461, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1054017, 0, "もしかして、私の運命の赤い糸は\nレイ様じゃなくて騎士さんと\nつながってたり…して…？\nやだ、私なにを考えてるんだか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105501, 0.0, 5, 0, 1, 0, 2.5, 0, 0, 1, 1, 1055001, 0, "ジー…っは！\nせ、先輩！？　えっとあのその…\nな、なんでもないですぅ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105501, 0.0, 5, 0, 2, 0, 4.7, 0, 0, 1, 6, 1055002, 0, "はわわ、しぇんぴゃい！？\nほほ、ほんじちゅはお日柄もよく\nか、噛んじゃった…恥ずかしいよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105501, 0.0, 4, 0, 3, 0, 3.6, 0, 0, 1, 6, 1055003, 0, "あ、せ、先輩！？\nつ、つけてたわけじゃ…\nこ、これはその…観察で…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105501, 0.0, 5, 0, 4, 0, 5.7, 0, 0, 1, 6, 1055004, 0, "あ、あ、あの…せせ、先輩！\nよかったりゃ今度、一緒に…\nご飯…や、やっぱむり～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105501, 0.0, 5, 0, 5, 0, 5.4, 0, 0, 1, 2, 1055005, 0, "せ、先輩…い、いつかちゃんと\nお礼するので…\nま、待っててください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1055006, 0, "魔物退治のお手伝いを\n私なんかにさせていただいて\nありがとうございます！\nが、頑張りますから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1055007, 0, "戦うのは怖いけど、\n今は先輩がいるから\nとっても心強いです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1055008, 0, "せ、先輩は下がっててください！\nわた、私が…魔物を…\nうぅ、やっぱり手伝ってくださーい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1055009, 0, "いつも助けられてばっかりだから\n…今度は私が先輩を助けます！\nお、恩返しがしたいんです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1055010, 0, "せ、先輩と私のコンビネーション\n…バ、バッチリですね！\nえへへへ、共同作業だぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105501, 0.0, 5, 0, 6, 0, 8.8, 0, 0, 2, 2, 1055011, 0, "あっあの、先輩っ！\nお誕生日おめでとうございます！\nお祝いさせてくだひゃい！\nちゃ、ちゃんと言えましたっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1055012, 0, "お誕生日おめでとうございます！\nプ、プレ、プレゼントどうぞっ！\n先輩に似合う服を見つけました\nそれから次は、先輩が好きな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105561, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1055013, 0, "せ、先輩に助けてもらって\nばっかりじゃありません…！\nこれからは、私だって…\n先輩と一緒に戦います！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105561, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1055014, 0, "ど、どうですか…？\n前より、ちょっとくらいは\n頼りがいのある感じに\nなってますか？　私…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105561, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1055015, 0, "キ、キリっとした顔で、\n先輩に成長したところを\nちゃんと見せなきゃ…！\n眉毛をあげればいいのかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105561, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1055016, 0, "先輩と一緒にいろんなことを\n経験して、自信がついてきました\nだ、だからこれからも…\nよろしくお願いしましゅ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105561, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1055017, 0, "怖いことや危ないことがあっても\nずっと先輩のそばにいます\nきっとそれだけが、私に\nできることですから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105601, 0.0, 2, 0, 1, 0, 5.2, 0, 0, 1, 1, 1056001, 0, "お節介がすぎるのか、よく\n面倒ごとに巻き込まれるんだ\nお前さんも困ったことがあれば\n遠慮なく声かけておくれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105601, 0.0, 1, 0, 2, 0, 4.4, 0, 0, 1, 2, 1056002, 0, "ひと仕事終えたし\n釣りにでも行くとするか…\nお前さんもどうだい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105601, 0.0, 5, 0, 3, 0, 8.3, 0, 0, 1, 2, 1056003, 0, "お前さん、いい所に来たね！\n悪さしてたヤツらをちょいと\n懲らしめたら謝礼がもらえてね\nちょいと一杯付き合っとくれよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105601, 0.0, 1, 0, 4, 0, 2.7, 0, 0, 1, 6, 1056004, 0, "抱き締めるのはよせって？\n分かっちゃいるんだけどね\nお前さんがいい男なのが\n悪いんだよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105601, 0.0, 2, 0, 5, 0, 5.0, 0, 0, 1, 1, 1056005, 0, "かわいい見た目してるのに\n意外と肝が据わってるね\nまた厄介事があった時は\n頼らせておくれよ、お前さん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1056006, 0, "お前さんのためなら\nコイツを抜くのも悪くない…\n全力でいくとしようか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1056007, 0, "アタシにもう一本使わせたこと\nたっぷり後悔させてやるよ\nその身でとくと味わいな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1056008, 0, "何人相手にすりゃいいんだ？\nこっちは早いとこ終わらせて\nのんびりしたいってのに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1056009, 0, "手伝ってくれるのかい？\nお前さんがいると心強いね！\nとっとと片付けちまおう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1056010, 0, "誰かに背中を預けるなんて\nお前さんくらいなもんさ\nこれからも、頼りにしてるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105601, 0.0, 2, 0, 6, 0, 3.3, 0, 0, 2, 1, 1056011, 0, "お前さん、今日が誕生日なのか？\nそうか…めでたい日にはパーッと\n祝うに限る。そうと決まれば\n今日の飯はアタシのおごりだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1056012, 0, "誕生日おめでとさん！\nいい釣竿が手に入ったんだ\nお前さんにあげるよ。今度二人で\n一緒にのんびり釣りに行こう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1056013, 0, "惚れたからにゃ命懸けさ\nさぁ、アタシの恋路を\n邪魔するのはどいつだい！\n斬り刻んでやるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1056014, 0, "お前さんがアタシを女にしたのさ\n惚れるってのはね、心に彫りモン\n入れるようなもんなんだ\n取り返しはつかないよ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1056015, 0, "万一の時はお前さんが\n介錯してくれるんだろう？\nならどっちに転んでも\nアタシは果報者さ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1056016, 0, "刀と鞘、竿と針、椿と一輪挿し\nそんな関係でいられたら…なんて\nも、もちろん\nお前さん次第だけどさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1056017, 0, "確かにアタシは男勝りの剣客さ\nだけど、思っちゃだめかい？\nお前さんの前でだけは\nかわいい女でいたいって");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105701, 0.0, 2, 0, 1, 0, 3.2, 0, 0, 1, 1, 1057001, 0, "同じ騎空士として\n一緒に頑張ろうね！\n何か困ったことがあったら\nいつでも相談して！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105701, 0.0, 2, 0, 2, 0, 3.0, 0, 0, 1, 1, 1057002, 0, "良かったら一緒に修行しない？\n一人よりも二人でやったほうが\nもっと強くなれると思わない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105701, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1057003, 0, "私たちの騎空艇ができたら\nまずはアストライア大陸を\n一周したいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105701, 0.0, 5, 0, 4, 0, 3.7, 0, 0, 1, 2, 1057004, 0, "あなたが一緒なら、\n何があっても怖くないよ！\n…本当だよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105701, 0.0, 6, 0, 5, 0, 4.0, 0, 0, 1, 1, 1057005, 0, "ねぇ、副団長！　…おーい！\nやっぱり名前で呼んだほうが\nいいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1057006, 0, "今までの修行の成果\n見せてあげる！\nいくよっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1057007, 0, "大切なものを\n守るためなら、\n私は戦うよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1057008, 0, "冒険のために、\n毎日特訓してるんだ…！\n絶対負けないんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1057009, 0, "団長として、仲間には\n指一本触れさせない！\n私が相手になります！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1057010, 0, "あなたと一緒にいると、\nどんな困難だって\n乗り越えられる気がする！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105701, 0.0, 1, 0, 6, 0, 4.0, 0, 0, 2, 2, 1057011, 0, "今日はあなたの誕生日だよね、\nおめでとう！\nいっぱいお祝いしないとね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1057012, 0, "誕生日おめでとう！　お祝いに\nパーティーしようよ！　それと何か\n欲しいものがあったらプレゼント\nしてあげるから言ってね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1057013, 0, "あなたとこの蒼い空を守る…！\n私たちならきっとできるよ！\nさあ立って！　一緒に戦おう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1057014, 0, "新しい力と、新しい姿…！\nあなたがいてくれたから\n私、また強くなれたよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1057015, 0, "空に剣の光が輝き続ける限り\n私は負けない！　だって私には\nあなたとの冒険が待ってるから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1057016, 0, "一人じゃないって\nこんなにも安心できるんだ…\nあなたが不安で寂しい時は\n私が支えてあげるね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1057017, 0, "私ね、これからもずっと\nずーっと二人で冒険して…\nいつか父さんに会いたいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105801, 0.0, 2, 0, 1, 0, 3.0, 0, 0, 1, 1, 1058001, 0, "えへへ、ペコリーヌってあだ名、\nわたし結構気に入ってるんですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105801, 0.0, 2, 0, 2, 0, 3.2, 0, 0, 1, 6, 1058002, 0, "もしかして魔物料理、\n苦手でしたか？\nわたしは好きなんですけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105801, 0.0, 7, 0, 3, 0, 2.5, 0, 0, 1, 4, 1058003, 0, "ぐるるる～…\nあ、お腹が鳴っちゃいました\nわ、笑っちゃダメですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105801, 0.0, 1, 0, 4, 0, 2.1, 0, 0, 1, 2, 1058004, 0, "ごはん食べにいきましょう！\n一緒に食卓を囲むと仲良くなれ\nますよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105801, 0.0, 5, 0, 5, 0, 2.2, 0, 0, 1, 1, 1058005, 0, "食べ物くれるんですか？\nわあ、恋してもいいですかね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1058006, 0, "お腹空きませんか？\n良かったらわたしと一緒に、\nご飯でも食べに行きません？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1058007, 0, "食べものは大事にしましょう\nまあ、わたしがいれば食べ残し\nなんてありえませんけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1058008, 0, "好きな料理ですか？\nそうですね～\n「食べ放題」とか\n「飲み放題」とか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1058009, 0, "男の人とふたりで食事って\nちょっとドキドキしますね！\nやばいですね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1058010, 0, "好みの男性のタイプ？\n「食べものを粗末にしない人」\nです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105801, 0.0, 1, 0, 6, 0, 4.5, 0, 0, 2, 2, 1058011, 0, "今日、誕生日なんですよね！\nおめでとうございます！\n特別な日の特製特大ケーキ、\n一緒に食べませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1058012, 0, "おめでとうございます☆\n来年のあなたのお誕生日も\n必ずお祝いしますね♪\n絶対絶対ぜーったいです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1058013, 0, "あなたが側にいるだけで\n体の奥から\n勇気が溢れちゃいます！\nやばいですね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1058014, 0, "ごはんの力！\nそしてあなたとの絆！\n今のわたしは止められませんよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1058015, 0, "あなたのためなら\nわたしはどこまでも\n強くなれます！\n一緒に頑張りましょう☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1058016, 0, "あなたと食卓を囲むと\nごはんがとっても美味しいんです\nわたしたちって\n相性ばっちりですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1058017, 0, "わたし、もっともっと強くなって\nあなたを守り続けてみせます！\nずっとあなたといたいですから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105901, 0.0, 2, 0, 1, 0, 2.0, 0, 0, 1, 1, 1059001, 0, "ガイド役のわたくしが、\nしっかりサポートさせて\nいただきます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105901, 0.0, 2, 0, 2, 0, 3.7, 0, 0, 1, 1, 1059002, 0, "主さま、\nお腹はすいていませんか？\n何事もお腹を満たしてからで\nございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105901, 0.0, 2, 0, 3, 0, 2.3, 0, 0, 1, 6, 1059003, 0, "お疲れのようですね\n体調管理もわたくしの役目です\n何かあれば、遠慮せず\nおっしゃってください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105901, 0.0, 5, 0, 4, 0, 0.0, 0, 0, 1, 5, 1059004, 0, "主さまのような優しい方に\nお仕えできて、わたくしは\n幸せ者でございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105901, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1059005, 0, "主さまの奥さんになったつもりで\n尽くさせていただきますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1059006, 0, "主さまと出会うべき日に備え、\n幼少期から精霊と\n語り合ってきました\n存分にお使いくださいませ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1059007, 0, "精霊たちも、主さまが見ていると\n調子が良いみたいです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1059008, 0, "わたくしは自然と共に、\n主さまのお力となります");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1059009, 0, "主さまのお世話をすることを\n幼い頃から、楽しみにして\nおりました");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1059010, 0, "何かあれば、わたくしを\nお呼びください\n全身全霊で主さまの要求に\nお応えいたします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105901, 0.0, 2, 0, 6, 0, 11.3, 0, 0, 2, 5, 1059011, 0, "お誕生日おめでとうございます♪\nお誕生日おめでとうございます♪\n何度口にしても言い足りません…\n主さま、おめでとうございます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1059012, 0, "主さまの生誕された、この日は、\nわたくしには一年で最も尊い日\n心から祝福させていただきます\nお誕生日おめでとうございます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105961, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1059013, 0, "主さまとの心のつながりが、\nこの力を生み出したのですね\n主さま…これからもずっと、\nおそばに居させてくださいまし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105961, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1059014, 0, "わたくしの想いに、\n精霊たちが応えてくれている…\nこの力があれば、\nもっと主さまにご奉仕できます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105961, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1059015, 0, "なんて優しい風…\nふふっ…まるで精霊たちが、\nわたくしと主さまを\n祝福してくれているようです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105961, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1059016, 0, "花びらが花嫁のブーケのように…\nわたくしと主さまも…はっ！\nわ、わたくしとしたことが、\nなんて大それたことを…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 105961, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1059017, 0, "この力があれば、\nもっと主さまのお世話ができます\nおはようからおやすみまで、\n全てわたくしにお任せください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106001, 0.0, 2, 0, 1, 0, 3.5, 0, 0, 1, 1, 1060001, 0, "あんた、あたしを\n助けてくれたんだって？\nまぁ、一応だけど\nお礼をいっておくわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106001, 0.0, 5, 0, 2, 0, 0.0, 0, 0, 1, 5, 1060002, 0, "あんた、あんまり気安く\n話しかけないでよね！\n友だちでも\n何でもないんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106001, 0.0, 5, 0, 3, 0, 6.5, 0, 0, 1, 3, 1060003, 0, "気配を消して、周りをよく見て、\nあいつに気付かれないように…\nきゃっ！　なんでいっつも\n尾行がバレちゃうのよ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106001, 0.0, 2, 0, 4, 0, 6.0, 0, 0, 1, 4, 1060004, 0, "あんた、記憶喪失だし\nたいへんだろうから、\nあたしが面倒みてあげるわ\n感謝しなさい♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106001, 0.0, 2, 0, 5, 0, 5.0, 0, 0, 1, 5, 1060005, 0, "本当は、あんたと仲良くしてちゃ\nダメなんだけどね…\nまぁ、楽しいからいっか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1060006, 0, "み、見たわね、秘密の魔法特訓\nこうなったらあんたにも、\n付き合ってもらうわよ！　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1060007, 0, "あたしの魔法、\nけっこうすごいでしょ？\nふふ～ん♪\nもっと褒めて褒めて♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1060008, 0, "完璧完璧！　ちゃーんと\n上達してるじゃない♪\nやっぱり、あたしって\nやればできる子ね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1060009, 0, "ふふん、華麗なもんでしょ\nあんたにだけ教えてやるけど\n実は最近まで初心者だったのよ\nほら、もっと驚きなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1060010, 0, "回復魔法もそこそこ使えるのよ\n万一のことがあったら、まあ\n助けてあげなくもないわ\n万一なんて無い方がいいけどね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106001, 0.0, 5, 0, 6, 0, 8.9, 0, 0, 2, 1, 1060011, 0, "へ～、あんた、誕生日なんだ～\nあ、たまたまケーキ持ってるから\n一緒に食べましょ？\n…たまたまよ！　本当だからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1060012, 0, "はい、バースデープレゼント\n感謝しなさいよ！\nこんなものあげてるって、\n本当はバレたらマズいんだから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106061, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1060013, 0, "あたしの魔力とあんたの力が\n一つになっていく…！\nさあ、かかってきなさい！\n今日のあたしは一味違うわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106061, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1060014, 0, "これがあんたとあたしの\n真の力なのね…！\nあんたと一緒なら\n負ける気がしないわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106061, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1060015, 0, "ふっふっふっ…どうよこの力！\nもっと褒めても…うひゃあ！\nきゅ、急に頭なでんな！\nぶ、ぶっ殺すぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106061, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1060016, 0, "あいつとの心のつながりが力に…\nこういうのも悪くないわね\nなっ！　あんた聞いてたの！？\nい、今すぐ忘れろばかぁぁ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106061, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1060017, 0, "この新しい力であんたを\n守ってあげるから…その…\nあ、あたしのそばから\n離れるんじゃないわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106101, 0.0, 2, 0, 1, 0, 2.2, 0, 0, 1, 6, 1061001, 0, "ん？　ああ、オマエか！\nアタシになんか用か？\n困ったことがあったらちゃんと\n言うんだぞ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106101, 0.0, 2, 0, 2, 0, 5.8, 0, 0, 1, 1, 1061002, 0, "オマエが記憶喪失か…\n早くよくなるといいな！\n効きそうな薬、探してやるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106101, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1061003, 0, "オマエのプリンセスナイトの力\nアタシにも使ってくれよな！\n敵だった時にさ、見てて\nいっつもわくわくしたんだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106101, 0.0, 2, 0, 4, 0, 2.9, 0, 0, 1, 1, 1061004, 0, "ちょっと前までは色々と\n不安だったけど、今はもう\n大丈夫だぞ！　オマエやみんなと\n助け合えるからな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106101, 0.0, 3, 0, 5, 0, 5.1, 0, 0, 1, 1, 1061005, 0, "敵だったオマエとアタシが\n今は味方同士になれたんだ…\nだからきっと、オクトーだって…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1061006, 0, "オマエら全員道を開けろ！\n邪魔するヤツは、みんな\nアタシがぶった斬るぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1061007, 0, "新しい天楼覇断剣か…どんな\nヤツが相手でも、コイツがあれば\nアタシは無敵だ！　いくぞぉぉぉ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1061008, 0, "もう逃げも隠れもしないぞっ！\nどんなに傷ついても最後まで\n戦ってやる！　そう決めたんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1061009, 0, "はっはっはーっ！\nどけどけえっ！！\nノウェム様のお通りだぁ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1061010, 0, "やっとオマエと味方になれたんだ\n早いとこオクトーの目を覚まして\nアイツとも一緒に戦いたいな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106101, 0.0, 5, 0, 6, 0, 2.1, 0, 0, 2, 2, 1061011, 0, "誕生日おめでとう！\nあんまり人に物あげたこと\nなくてさ…何が欲しいんだ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1061012, 0, "ハッピーバースデー！\nよーし！　ケーキ買いに行くぞ！\n今日はアタシのおごりだ！\nたぶん明日は雪が降るぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106161, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1061013, 0, "王様だろうが神様だろうが\n誰かを苦しめる悪いヤツは\nアタシと天楼覇断剣が\nキレイにバッサリぶった斬るぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106161, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1061014, 0, "いつだってどんな世界にいたって\nアタシたちでみんなを救うぞ！\n立ち止まるな！　前を向け！\nもう二度と負けないために！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106161, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1061015, 0, "どうだ！　カッコいいだろ！\nもっと近くでよく見ていいぞっ！\nおっと危ない…！　間違って\n真っ二つにするところだった…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106161, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1061016, 0, "助け合う仲間がいるって\nやっぱりいいな！　オマエと\nアタシが一緒に立ち向かえば\n何だって誰だって敵じゃないぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106161, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1061017, 0, "オマエともいろんなことを\n乗り越えてきたな！\n天楼覇断剣と同じくらい\n頼もしくなったぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106301, 0.0, 1, 0, 1, 0, 5.8, 0, 0, 1, 6, 1063001, 0, "ランドソルってすごいなぁ…\n色々あって、大きいし…\n私の里とは全然違うや");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106301, 0.0, 1, 0, 2, 0, 5.3, 0, 0, 1, 2, 1063002, 0, "私の里もいいところなんだよ\n…なーんもないけど、\nでも、気に入ってくれると思うな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106301, 0.0, 2, 0, 3, 0, 6.1, 0, 0, 1, 6, 1063003, 0, "あなたとロザリアって、\nちょっと似てるかも？\n例えば…\n一緒にいて安心するところ、とか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106301, 0.0, 2, 0, 4, 0, 5.7, 0, 0, 1, 5, 1063004, 0, "あなたといると、\nドキドキしたり、苦しくなったり…\nでも、なんか…嬉しいんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106301, 0.0, 2, 0, 5, 0, 4.1, 0, 0, 1, 5, 1063005, 0, "私、あなたと一緒にいると…\n安心するし、ドキドキするんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1063006, 0, "いつか必ず、森番になるんだ\nもちろん、ロザリアと一緒にね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1063007, 0, "ランドソルは広いけど…\n風の匂いは、里と同じかも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1063008, 0, "私の里にはね、\n妖精さんがいっぱいいるんだよ！\nあなたにも見せてあげたいなぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1063009, 0, "ドキドキするのに、落ち着くし…\n安心するのに、たまに苦しくて…\nあなたの側にいると、\n不思議な気持ちになっちゃうな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1063010, 0, "森にいた頃よりも、\n今の方が楽しく思えちゃうのは…\nきっと、あなたのおかげかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106301, 0.0, 2, 0, 6, 0, 4.9, 0, 0, 2, 5, 1063011, 0, "ちょっと自信ないけど…\nこれ！　作ったんだ！\n今日は特別な日だから…\nあーんってしてあげよっか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1063012, 0, "このままどんどん成長したら\nあなたはどうなっちゃうんだろ？\n…なんか、ずっと楽しみだね\nえへへ…お誕生日おめでと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1063013, 0, "力と、想い…大切なこと\n少しだけ、分かった気がするよ\n私、ちょっとは成長できたかな？\nえへへ…あなたのおかげだね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1063014, 0, "ランドソルに来てよかったなぁ\nいろんな思い出が出来て、\nいろんな人と話せて…\n何より、あなたと出会えたから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1063015, 0, "森番の試練…大変だったな\nだけどおかげで、一歩ずつでも\n前に進めてる気がするんだ\n大人になるってこういう事かな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1063016, 0, "ロザリアみたいに、じゃない。\n私は私らしく、頑張るんだ\nこの想いも、いつか私らしく…\n…ひゃあ！？　き、聞いてた！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1063017, 0, "最初に会った時から、\nあなたはずっと優しいね\nそういうところが…す、す…\nす、素敵だと思うな！　うん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106401, 0.0, 2, 0, 1, 0, 7.2, 0, 0, 1, 1, 1064001, 0, "自分のなくした記憶もだけど…\nあなたが忘れた過去も気になるわ\n二人で一緒に\n見つけられたらいいわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106401, 0.0, 2, 0, 2, 0, 6.6, 0, 0, 1, 6, 1064002, 0, "アイスクリームって、どうして\nこうもおいしく感じるのかしら…\n『前の私』もひょっとしたら\nアイスが好きだったりしてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106401, 0.0, 1, 0, 3, 0, 4.7, 0, 0, 1, 6, 1064003, 0, "そんなに真面目っぽい性格に、\n見えるかしら？\n冗談とか言うのは、確かに苦手ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106401, 0.0, 5, 0, 4, 0, 4.1, 0, 0, 1, 4, 1064004, 0, "…ふとした弾みで、思い出すの\nコッコロさんに甘えてたこととか\nキャルのしっぽの味とか…\nもう…顔から火が出そうよ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106401, 0.0, 1, 0, 5, 0, 6.7, 0, 0, 1, 2, 1064005, 0, "【美食殿】での生活は\n毎日が充実していて、楽しいわ\nこの時間が、少しでも長く続く\nように…精一杯努力するつもりよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1064006, 0, "必ず、みんなを守ってみせる！\nだって、みんなの隣が\n『今の私』の居場所だから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1064007, 0, "氷を滑る、この動き─\n身体が覚えてる？\nううん、今はそんなこと\n考えてる場合じゃない…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1064008, 0, "っくしゅっ…！\n氷の魔法は使えても、そこまで\n寒さに強いわけじゃないみたい\n後で、温かいスープが欲しいわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1064009, 0, "私を迎えてくれた\n【美食殿】のみんな、\nそれにあなたも…\n絶対に、守ってみせるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1064010, 0, "…不思議ね\nあなたから力をもらうと、\n胸が熱くなって…\nどこまでも跳べそうな気がするわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106401, 0.0, 4, 0, 6, 0, 6.5, 0, 0, 2, 2, 1064011, 0, "お誕生日、おめでとう\nケーキ、一緒に食べましょう\nでも…魔物とか昆虫とか\nケーキに入ってないわよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1064012, 0, "私、自分の誕生日が分からなくて\n…だから、お祝いとかできないの\nその分、あなたの誕生日を\nお祝いさせて貰っていいかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106461, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1064013, 0, "願いと引き換えに\n手に入れたこの『力』…\n私の愛する人たち…\n家族やあなたは私が守るわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106461, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1064014, 0, "私は【美食殿】のシェフィ…\n今なら胸を張ってそう言える\nだからあなたのそばにいたいって\n気持ちも、もう隠したりしない");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106461, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1064015, 0, "記憶がなかった頃\nあなたは大丈夫だよって笑って\n迷子のような私を支えてくれた…\n今度は私があなたを助ける番よ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106461, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1064016, 0, "みんなと過ごす時間は、\n私を幸せな気持ちにしてくれた\nでも、あなたと二人で過ごす\nひと時も…キライじゃなかったわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106461, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1064017, 0, "過去を知った時は辛かった…\nでも、知らないままだったら\nなにも気づけなかったと思うの\nきっと、あなたへの想いにも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106501, 0.0, 2, 0, 1, 0, 7.7, 0, 0, 1, 1, 1065001, 0, "キミと一緒にいれば\nなんか面白いことが起きるだろ？\nだから期待させてもらってるよ\nさ、どっか遊びにいこうぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106501, 0.0, 3, 0, 2, 0, 0.0, 0, 0, 1, 3, 1065002, 0, "オレはこの世界に\nなにが起きたのか知りたい…\nそのためにもオレは\nもっと強くなる必要があるんだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106501, 0.0, 1, 0, 3, 0, 8.9, 0, 0, 1, 6, 1065003, 0, "なあ、仕事の依頼はないのか？\nだからケンカだって。ケンカの\n仕事。オレがたぎるようなやつを\n持ってきてくれよな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106501, 0.0, 2, 0, 4, 0, 7.6, 0, 0, 1, 1, 1065004, 0, "そう心配すんなって\nそこらのヤツにやられるほど\nオレは弱くはないさ\nなんなら、試してみるか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106501, 0.0, 1, 0, 5, 0, 5.8, 0, 0, 1, 6, 1065005, 0, "ドラゴン族以外のやつと\nここまで仲良くなるなんて\n思わなかったぜ\nキミ、一体何者なんだ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1065006, 0, "喧嘩屋の本領、見せてやるよ！\nダチが一緒じゃ負けられねぇ！\n誰だろうとぶっ倒して、オレ様が\n最強だって証明してやるぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1065007, 0, "すげえぜ、この力！\n体の奥から\nどんどん溢れてきやがる！\nオレたちに壊せないものはねえ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1065008, 0, "これがドラゴン族の力…！\nオレ様が使いこなしてやるよ！\nダチが見てるんだ。情けねえ\nカッコは見せらんねぇよな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1065009, 0, "ダチと一緒に戦うのが\nこんなに楽しいなんてな…！\n知らないこの世界で知った\n最高にたぎる感情だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1065010, 0, "いいぜいいぜ、たぎってきた！\nキミが一緒なら、これっぽっちも\n負ける気がしねえ！\n派手に暴れてやろうぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106501, 0.0, 2, 0, 6, 0, 4.7, 0, 0, 2, 1, 1065011, 0, "よっ！　おめでとう！\n今日はキミの誕生日だろ？\nパーッとなんか食べに行こうぜ\n今日はオレがおごってやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1065012, 0, "誕生日おめでとう！\nダチにとって良い日ってのは\nこっちまでうれしくなるんだな\nこんな気持ち、初めてだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106561, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1065013, 0, "くはははっ！　すげぇ…！\nコイツが本当の覚醒か！\n最高に気持ちいいぜ！\n力がたぎって止まらねぇ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106561, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1065014, 0, "制御しようなんて考えるな\nどうせ抑えられないんだ…\n…だったらもう、ただ欲して\n焼き尽くせばいいんだよな…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106561, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1065015, 0, "ああ、ちくしょう…なんでだ…！\n喉が渇いて仕方ねぇ…！\n早く全部ぶっ壊さないと\nオレ様がおかしくなっちまう…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106561, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1065016, 0, "頼むから止めないでくれよ？\nもしそんな真似しやがったら\nオレ様はキミを…！\n…違う…オレは…アタシは…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106561, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1065017, 0, "近づくんじゃねぇ！　今はダメだ\n炎でキミまで傷つけちまう…！\nやめろって！　おい！　ちょっ！\n分かったから抱き締めんなって！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106601, 0.0, 3, 0, 1, 0, 4.2, 0, 0, 1, 6, 1066001, 0, "ぴゃっ！　って、お兄さんですか\n何じろじろ見てるです、えっち！\n煩悩まみれ！　不審者だったら\n叩き潰してるところですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106601, 0.0, 4, 0, 2, 0, 5.4, 0, 0, 1, 1, 1066002, 0, "あたしがサボってここにいるのは\nボスには内緒にするですよ？\nもしあのババアに見つかったら\n何されるか分からないです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106601, 0.0, 1, 0, 3, 0, 6.9, 0, 0, 1, 6, 1066003, 0, "は～…ほんとうに\nあたしたち以外にも\nドラゴン族っているんですかね？\n…そこらへんに歩いてないです？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106601, 0.0, 6, 0, 4, 0, 5.0, 0, 0, 1, 2, 1066004, 0, "今日の仕事はここまでです！\n縄張りを見回ってたですよ！\nべ、別にサボってブラブラしてた\nわけじゃないですからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106601, 0.0, 3, 0, 5, 0, 6.5, 0, 0, 1, 4, 1066005, 0, "あ～あ、どこかにぽろっと\n大金が落ちてないですかね？\n早く自由の身になって\nボスを見返してやりたいですよ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1066006, 0, "どうですかお兄さん！\nこれがあたしの…\nドラゴン族の力ですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1066007, 0, "ふっふっふ…心技体ともに\n覚醒した今のあたしには\n死角はないです…ってぴゃあ！\nあ、足場が崩れたですよぉ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1066008, 0, "力がわきあがってくるです！\n知性派のあたしですけど\nたまには本能のおもむくまま\nってのも、悪くないです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1066009, 0, "この力があれば…\nお兄さんがどれだけ\nへっぽこナイトでも\n助けてあげられるですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1066010, 0, "もう守ってもらうばかりの\nあたしじゃないですよ！\nこの力でなりあがってやるです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106601, 0.0, 2, 0, 6, 0, 6.3, 0, 0, 2, 6, 1066011, 0, "え？　なんです？　今日は\nお兄さんの誕生日ですか？\nじゃあ特別に、あたしに\n何かおごる権利をやるですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1066012, 0, "お誕生日おめでとうです！\nこのあたしに祝われるなんて\nそうそうない名誉ですからね！\n感謝して何かおごりやがれです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1066013, 0, "力が溢れてくるです！\nこれが完全な覚醒…\nこうなったあたしは、\n誰にも止められねーですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1066014, 0, "どうですか！　これこそが\nあたしの真のパワーですよ！\nこの姿、その目にしっかりと\n焼き付けるがいいです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1066015, 0, "あたし一人だけだったら、きっと\n手に入れられなかった力です…\nお兄さんだけには、特別に\n手を貸してやるですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1066016, 0, "あたしが覚醒するには\nお兄さんが必要ですからね\nふふーん。頼まれたって\n離れてやんねーですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1066017, 0, "そんな顔してどうしたです？\n覚醒してもあたしはあたしです\nどっかいったりしねーですから\n安心しやがれです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106701, 0.0, 2, 0, 1, 0, 9.3, 0, 0, 1, 1, 1067001, 0, "揉め事に巻き込まれたら\n私の名前、出しちゃっていいよ\n…もっと悪いことが起きる？\nだって、そのほうが楽しいし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106701, 0.0, 1, 0, 2, 0, 6.6, 0, 0, 1, 6, 1067002, 0, "ギャングの心得その一はね…\nファミリーに入れる気はないよ～\n知ってるだけでいいから♪\n…何かあった時のために、ね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106701, 0.0, 2, 0, 3, 0, 7.0, 0, 0, 1, 1, 1067003, 0, "カヤちゃんとイノリちゃんと\nこれからも仲良くしてあげてね\n…私？　…君とはずっ～と前から\n仲良しだし、大丈夫♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106701, 0.0, 2, 0, 4, 0, 6.5, 0, 0, 1, 1, 1067004, 0, "君の頑張りが世界を\nより楽しく変えてくれるなら\nなんだって協力するよ\n遠慮せずに頼ってきてね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106701, 0.0, 5, 0, 5, 0, 7.8, 0, 0, 1, 1, 1067005, 0, "君がどんな色に変わっていくのか\n私はそれを最後まで見ていたいの\n…本当は私の選んだ色を\n使ってほしいけど、まだ早いかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1067006, 0, "ふふっ…あははっ♪\n戦うのって、本っ当に面白い…\n命を、魂を懸けた勝負…\nとってもワクワクするよね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1067007, 0, "私の目に狙われてる意味も\n理解できない相手なんて\n敵としての存在価値もないし…\nすぐに消してあげないと♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1067008, 0, "私たちの生きていく未来は\n誰かに決められるものじゃない…\nものすご～く大変だったとしても\nみんなで一緒に奪い返そうね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1067009, 0, "ねえ…君の目に、今の私は\nどんな風に見えてるのかな？\n怖い？　かっこいい？\n私はね…心の底から楽しいよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1067010, 0, "ねえ、君を苦しませるものを\n世界ごと焼き尽くしちゃおうか…\n実はそれだけのリスクがあるって\nじっくりと教えてあげたいし…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106701, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1067011, 0, "誕生日おめでとう～☆\nプレゼントは、一日私を\n好きにできちゃう券で～す\nいつ使いたい？　今でもいいよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1067012, 0, "ハッピーバースデー☆\n君もドラゴン族になれる\nアイテムを作ってきました～☆\nドラゴン族…なってみたくない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106801, 0.0, 1, 0, 1, 0, 8.1, 0, 0, 1, 2, 1068001, 0, "やあ少年。元気にしてる？\nたまには店に寄っていってよ\n美味しいクレープ作るからさ\n味は普通？　…そうかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106801, 0.0, 6, 0, 2, 0, 8.5, 0, 0, 1, 2, 1068002, 0, "おや、今日は人助けはお休み？\n一人きりでのんびりしたいなら\n迷宮を作ってあげようか\nあれ？　なんで逃げるの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106801, 0.0, 6, 0, 3, 0, 8.5, 0, 0, 1, 4, 1068003, 0, "実はちょっと困ってるんだよね…\n予約してた新作ゲームが\nこっちの世界だと遊べない！\nこれは由々しき問題だよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106801, 0.0, 2, 0, 4, 0, 7.7, 0, 0, 1, 1, 1068004, 0, "少年と一緒にいると\n世界も自分も、いい方向に\n向かう予感がするんだ\nキミはずっと、そのままでいてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106801, 0.0, 2, 0, 5, 0, 8.4, 0, 0, 1, 1, 1068005, 0, "…記憶がなくても、キミの本質は\n変わらない。それを知った時は\n本当にうれしかったよ\nこれからもよろしくね、少年");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1068006, 0, "迷宮女王とプリンセスナイトが\n力を合わせれば、どんな敵が\n相手でも勝てるさ\nさあ少年、一緒に迎え撃とう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1068007, 0, "ああ…やっとキミのそばで戦える\nうれしいよ、少年。七冠として、\n一人の人間としてキミを\n守り抜くと、今ここに誓うよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1068008, 0, "おいで、憐れな挑戦者たち\nアタシの迷宮から、果たして\n生きて帰れるかな…？\nその足掻き、見せてもらうよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1068009, 0, "近頃少年を近くに感じていると\n力が溢れて止まらないんだ\nこれってもしかして…恋？\n…いや、愛だ！　だよね、少年！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1068010, 0, "アタシは人間が大好きだ\nでもそれ以上に、少年が好きだよ\n…どうかな、ドキッとした？\n真剣だよ？　それがどうかした？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106801, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1068011, 0, "少年、誕生日おめでとう～！\n今日くらいはクレープじゃなくて\nケーキをプレゼントしちゃおう！\nでもクレープもあげちゃう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1068012, 0, "少年、誕生日おめでとう\nキミがこの世に生を受けた日は\n盛大に祝うべきだ。今から一緒に\nプレゼントを選びに行こう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106901, 0.0, 1, 0, 1, 0, 7.0, 0, 0, 1, 6, 1069001, 0, "あなた、私と一緒に戦うなんて\nおかしな未来を選んだのね\n後悔しても、もう遅いわよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106901, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1069002, 0, "剣の練習に付き合ってあげるわ\n一刻も早く成長して\n私にそれを突き立てなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106901, 0.0, 4, 0, 3, 0, 5.9, 0, 0, 1, 6, 1069003, 0, "私に勉強を教わりたい？\n何を言うかと思えば…そもそも\n私の言葉を正しく理解できて？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106901, 0.0, 3, 0, 4, 0, 3.1, 0, 0, 1, 6, 1069004, 0, "食事を持ってきた…？\nなによこれ…魔物じゃないの\n見た目のなってない料理は嫌いよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106901, 0.0, 6, 0, 5, 0, 4.5, 0, 0, 1, 4, 1069005, 0, "今日もくだらない一日を\n過ごしてきたみたいね…\nどうして満足気なのかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1069006, 0, "私を否定する世界はいらないの\n全て作り変えるわ、全てね\nだから何もかも終わらせるの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1069007, 0, "私に全てを奪われたあなたには\n私の全てを奪う資格があるわ\n互いの全てをかけて戦いましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1069008, 0, "仲間に頼らないと何もできない…\nそんなあなたには負けられないの\n私の強さを証明させてもらうわよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1069009, 0, "守る価値のない未来のために\nどうして必死になれるのかしら\n本当にあなたは…理解できないわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1069010, 0, "私を倒しても、その先には\n今以上の苦しみがあるのに…\n理解してもなおあなたは進むのね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106901, 0.0, 1, 0, 6, 0, 3.9, 0, 0, 2, 3, 1069011, 0, "誕生日？　どうでもいいじゃない\nいつ世界に生まれたかじゃなくて\n何を成すかが大事なのだから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 106931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1069012, 0, "あなた、今日が誕生日らしいけど\nいつまで祝えるかしらね…？\n来年まで生き延びてたら\nその時は祝福してあげるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107001, 0.0, 2, 0, 1, 0, 5.8, 0, 0, 1, 1, 1070001, 0, "敵の敵は味方…\n今回は貴方に協力しましょう\nよろしく頼みます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107001, 0.0, 1, 0, 2, 0, 5.6, 0, 0, 1, 6, 1070002, 0, "プリンセスナイトとしてではなく\n貴方だけが持つ可能性\nそれに賭けさせてもらいます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107001, 0.0, 2, 0, 3, 0, 2.0, 0, 0, 1, 6, 1070003, 0, "今の私？　ええ、私本人ですよ\n今の貴方に自分を偽っても\n意味がありませんから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107001, 0.0, 6, 0, 4, 0, 5.3, 0, 0, 1, 1, 1070004, 0, "貴方といると、犬を\nもう一匹飼い始めた気分です\n飼ったこと？　ありませんよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107001, 0.0, 2, 0, 5, 0, 5.3, 0, 0, 1, 1, 1070005, 0, "貴方の好みのタイプを\n教えなさい。再現してあげます\n妹系？　姉系？　幼馴染？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1070006, 0, "自分の目に映った私が\n本当の私だと思いますか…？\n私は変貌大妃\n千変万化の女ですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1070007, 0, "私の『冠』が偽物ではないと\nその身に刻み込んであげます\nもっとも全ての敵は私に模倣され\n消えて無くなりますが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1070008, 0, "鏡よ鏡…世界で最も\n賢く強いのは誰ですか？\n…ええその通り。当然私です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1070009, 0, "私に仕えたくなりましたか？\n私の中にデータさえあれば\n本人は不要なのです\n…彼以外は");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1070010, 0, "こそこそ隠れるのは終わりです\nこれから先は打って出ます\n貴方とその仲間たち…\n戦力として当てにしていますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107001, 0.0, 6, 0, 6, 0, 5.8, 0, 0, 2, 1, 1070011, 0, "今日が誕生日ですか\nおめでとうございます\n…これ以上何か必要ですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1070012, 0, "誕生日おめでとうございます\n贈り物は何にしましょう…\n計算問題集ではどうですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107101, 0.0, 2, 0, 1, 0, 5.8, 0, 0, 1, 1, 1071001, 0, "ややこしい話し合いは面倒だ！\n物事はシンプルに解決しよう\nそう、生きるか死ぬかの\n本気の殺り合いでな☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107101, 0.0, 1, 0, 2, 0, 2.5, 0, 0, 1, 4, 1071002, 0, "息苦しい世の中だ\n自分の欲求に従って\n好き勝手に生きた者の勝ちだとは\n思わんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107101, 0.0, 4, 0, 3, 0, 3.1, 0, 0, 1, 1, 1071003, 0, "強すぎるというのも考えものだ\n歯ごたえのない連中ばかりで\n命の遣り取りをする愉しい戦いに\nそうそう出会えないからな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107101, 0.0, 2, 0, 4, 0, 8.5, 0, 0, 1, 1, 1071004, 0, "ワタシの前に散っていった\n男たちは、この胸を触ったオマエを\n心底憎く思うだろうなあ\n安心しろ、全員墓の中だ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107101, 0.0, 5, 0, 5, 0, 6.8, 0, 0, 1, 1, 1071005, 0, "オマエが幸せな結婚生活を\n送れるように、ワタシだって\n努力に手を抜かない\n意外と可愛いところもあるだろ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1071006, 0, "イかれてなどいないさ\n人間の命は儚く脆い…\nその刹那の時もワタシは\n楽しみ切ってやるのさ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1071007, 0, "戦場を満たす血だまりの上で\n一緒に陽気なステップを\n踏もうじゃないか\nさあ、お手をどうぞ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1071008, 0, "誓約で仕方なく…ではないぞ？\n少なくともワタシは納得している\nこのワタシに触れた男だ\n結婚相手として認めているよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1071009, 0, "この胸の高ぶるような\n愉しい出来事はないか？\nないならばワタシが\n火種をつけてやろう☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1071010, 0, "ワタシの幼女時代の写真が\nもっと見たいだと？　もしや\n第二次性徴期前の幼子にしか\n興奮しない性癖だったのか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107101, 0.0, 2, 0, 6, 0, 9.1, 0, 0, 2, 1, 1071011, 0, "オマエ、今日が誕生日らしいな？\nほら、一つ歳をとった哀れな子羊に\nプレゼントだ\n今年もワタシを楽しませてくれよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1071012, 0, "誕生日おめでとう♪\nオマエの記念すべき日に\n立ち会えてうれしいよ！\n記念の誓約は…まだ早いかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107161, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1071013, 0, "気をつけろ坊や\nこの「結婚」という伏魔殿…\nあらゆる所に落とし穴がある\nどんな牙城よりも手ごわいぞ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107161, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1071014, 0, "一人で千万の敵に挑むよりも\n二人で普通に生きることの方が\n難しいとはな…\nふふ、結婚…実に面白い！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107161, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1071015, 0, "このワタシが共同作業とはな…\nしかし、共に乗り越えた先でしか\n見られない景色があるらしい\n坊や…見に行くぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107161, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1071016, 0, "ワタシと共に生きるのは\n想像以上にハードだろう？\nだがまだこんなものじゃあないぞ\n坊や─死ぬなよっ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107161, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1071017, 0, "上り坂下り坂まさかの連続とは…\nふふっ、結婚もとんだ戦場だな\n何があっても手を離すなよ？\n病める時も健やかなる時もな☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107501, 0.0, 2, 0, 1, 0, 2.2, 0, 0, 1, 1, 1075001, 0, "水着買っちゃいました～！\nどうですどうです？\n似合ってますか～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107501, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1075002, 0, "この水着、結構動きやすいので\n素潜りとかして\n海の幸をゲットできそうです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107501, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1075003, 0, "マリンスポーツしましょう！\nビーチボールとか、\nビーチフラッグとか！\n負けませんよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107501, 0.0, 2, 0, 4, 0, 4.3, 0, 0, 1, 5, 1075004, 0, "私の水着姿で、\nあなたを誘惑しちゃいます♪\nなんちゃってー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107501, 0.0, 2, 0, 5, 0, 5.0, 0, 0, 1, 1, 1075005, 0, "せっかくの水着ですし、\nたくさん泳いで、\nたくさんはしゃいで…\n思い出いっぱい作りましょうね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1075006, 0, "わぁっ、やりましたね～！\n塩水しょっぱいぃ～\nこうなったらわたしも本気です！\n覚悟してくださいよぉ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1075007, 0, "青い海、白い砂浜、輝く太陽！\nそしておいしいお魚！\nここは天国ですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1075008, 0, "水が気持ちいいですね～\nこんなに充実した夏、\n生まれて初めてです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1075009, 0, "えへへ♪\nあなたと海で遊ぶなんて、\nとっても新鮮な感じです！\n青春ですね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1075010, 0, "はしゃぎすぎて水着が\n流されちゃったりしたら、\nあなたに抱きついて\n隠しますね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107501, 0.0, 5, 0, 6, 0, 2.6, 0, 0, 2, 2, 1075011, 0, "お誕生日おめでとうございます！\nお魚、貝、タコ、エビ、カニ…\n海の幸満載のバースデー\nパーティーにしましょうね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1075012, 0, "水着で過ごす、あなたの誕生日！\n開放的でわくわくしちゃいます！\nさぁ、今日は二人で\nおもいっきりはしゃぎましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107561, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1075013, 0, "夏の海に二人きり…\nなんだかドキドキしちゃいます！\nせっかくなので、雰囲気を出して\nダーリンって呼んでいいですか☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107561, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1075014, 0, "不思議ですね…\n毎日見ているはずなのに\nあなたと眺める夕日は、なんだか\nロマンチックな感じがします♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107561, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1075015, 0, "周りはカップルだらけ…\n郷に入っては郷に従え！\nということで、その…\n二人で手を繋いでみませんか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107561, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1075016, 0, "海、楽しかったですね！\nなので、今日が終わることが\nすごくさみしいです。あの…\nまたあなたを誘っていいですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107561, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1075017, 0, "あなたと過ごした夏は\nわたしにとってキラキラした\n青春で、大切な宝物です！\n…ずっと、忘れませんから☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107601, 0.0, 5, 0, 1, 0, 4.8, 0, 0, 1, 1, 1076001, 0, "救護院の皆さまに、水着を\n選んでいただきました…\n似合っていますでしょうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107601, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1076002, 0, "主さま、よろしければ\n一緒に泳ぎの\n練習をしましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107601, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1076003, 0, "主さま。わたくし、\n砂のお城というものを\n作ってみたいのですが、\nお手伝いいただけますでしょうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107601, 0.0, 5, 0, 4, 0, 0.0, 0, 0, 1, 5, 1076004, 0, "このような\n過度な肌の露出は、\nやはり恥ずかしいです…\n主さまにしかお見せできません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107601, 0.0, 2, 0, 5, 0, 4.2, 0, 0, 1, 1, 1076005, 0, "日焼け止めを塗っていただいても\nよいでしょうか？\n主さまにでしたら、\n肌を許すことができます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1076006, 0, "主さま。どんな場所でも、\nわたくしがついております\nこの手を離さないでくださいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1076007, 0, "見てください、主さま\nおさかながこんなに近くに…\nとても神秘的でございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1076008, 0, "このまま流れに身を任せ、\n二人でどこまでも進む\n…それも悪くありませんね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1076009, 0, "これからもずっと、\n主さまと同じ景色を見て、\n主さまと同じ感動を抱き、\nともに歩んでいきたいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1076010, 0, "この広い海の中で、\n主さまと二人っきり…\nいまだけは、主さまを\n独り占めです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107601, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1076011, 0, "海で、主さまのお誕生日を\nお祝いするなどと、故郷にいた\n時には考えもしませんでした\nわたくし、幸せでございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1076012, 0, "空も海も輝いて…この世の\n全てが、主さまのお誕生日を\n祝福しているようです\nわたくしも心よりお祝いします♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107661, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1076013, 0, "山奥の里で育ったわたくしですが\nもう井の中の蛙ではございません\n主さまのおかげで、このように\n大海を知ることができました♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107661, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1076014, 0, "主さま、ご覧くださいまし…\nわたくしたちの冒険の行く先を\n星々の光ように、お魚さんたちが\n照らしておりますよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107661, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1076015, 0, "主さまと過ごす夏は\nなんと刺激的なのでしょう…\nこれからも、どうか夏のガイドを\n務めさせてくださいまし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107661, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1076016, 0, "主さまのお手手を引いて\nこれからもまだ見ぬ神秘の地へと\nガイドを続けてまいります\nどうぞお付き合いくださいまし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107661, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1076017, 0, "『星々の海』…この素晴らしい\n光景を、主さまとともに\n見て、感じて、味わうことができ\nたいへんうれしく思います♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107701, 0.0, 5, 0, 1, 0, 2.8, 0, 0, 1, 1, 1077001, 0, "働くのって楽しいですね！\nあなたと一緒だと、もっと\n楽しく感じちゃいます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107701, 0.0, 2, 0, 2, 0, 2.9, 0, 0, 1, 1, 1077002, 0, "休憩時間に海へ\n行きたいんですか？\nもちろん、いいですよ～\n一緒に泳ぎましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107701, 0.0, 2, 0, 3, 0, 6.2, 0, 0, 1, 4, 1077003, 0, "私ドジだから、砂のお城って\n完成させたことないんです～\n…一緒に作ってくれるんですか？\nありがとうございます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107701, 0.0, 2, 0, 4, 0, 4.2, 0, 0, 1, 1, 1077004, 0, "水着姿を褒めて下さるのは\n嬉しいですが…あ、あまり\n見つめられると恥ずかしいです～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107701, 0.0, 5, 0, 5, 0, 4.7, 0, 0, 1, 1, 1077005, 0, "御用はありますか？\nもしもなくても…\nお、おそばにいても\nいいですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1077006, 0, "まさか足元に花火があるなんて～！\n子供たちは危険ですから、\n離れてください！\nひゃう！　あつい～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1077007, 0, "だ、大惨事です～！\nきれいとか言ってる場合じゃ\nありませんから～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1077008, 0, "みんなで花火をするのって\nとっても楽しいですね\n今度は両手持ちで\n行きますよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1077009, 0, "二人で花火をしてると\n恋人同士に見えるんでしょうか？\nなんて…えへへ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1077010, 0, "あ、あなたにも、お仕えさせて\nもらえませんか？\nや、やっぱり、今の言葉は\n忘れてください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107701, 0.0, 6, 0, 6, 0, 8.6, 0, 0, 2, 1, 1077011, 0, "お誕生日おめでとうございます♪\nクリームとおっきな苺を乗せた\nかき氷ケーキを召し上がれっ\nあわわ、溶けかけてる～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1077012, 0, "お誕生日おめでとうございます♪\n今日は特別に、あなただけに\nお仕えさせてください\nえへへ、お嬢さまには内緒ですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107801, 0.0, 5, 0, 1, 0, 5.8, 0, 0, 1, 1, 1078001, 0, "水着が似合ってるって？\nあんたに褒められてもねぇ～\nで、でも…ありがと");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107801, 0.0, 2, 0, 2, 0, 2.0, 0, 0, 1, 1, 1078002, 0, "この日を待ってたわ！\n思いっきり遊ぶから、\n覚悟しなさいよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107801, 0.0, 2, 0, 3, 0, 4.3, 0, 0, 1, 1, 1078003, 0, "この浮輪、かわいいでしょ\n乗り心地もいいんだから\nどうしてもって言うなら、\n乗せてあげるけど？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107801, 0.0, 5, 0, 4, 0, 5.6, 0, 0, 1, 1, 1078004, 0, "あんたといると退屈しないわ\n…どういうことかって？\nそれは、えっと…\n一緒にいると楽しいってこと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107801, 0.0, 2, 0, 5, 0, 3.7, 0, 0, 1, 1, 1078005, 0, "たまには二人っきりで\n出かけてあげてもいいわよ\n言っておくけど、\nあんたに拒否権はないから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1078006, 0, "ね、ねえ、浮輪に穴あいてない？\n空いてるわよね！？　っていうか\n魔物もいるわよね！？\nぎゃー！　こっち来んなぁ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1078007, 0, "何よ、物欲しそうな顔して\nこのジュースが飲みたいの？\nしょうがないわね、\n一口だけよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1078008, 0, "汗をかいた後の飲み物って\n最高よね\nはぁー、おいし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1078009, 0, "疲れてるみたいね\n一緒に浮輪に乗って、\nゆっくりする？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1078010, 0, "なんか、幸せーって感じ\nこれもあんたのおかげ、かな？\n…その、あ、ありが…\nな、なんでもないわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107801, 0.0, 1, 0, 6, 0, 5.9, 0, 0, 2, 2, 1078011, 0, "誕生日、おめでと！\nこんな日くらい、パーッと\n羽目を外して楽しみましょ☆\nさ～海、海！　遊ぶわよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1078012, 0, "海の上で誕生日パーティーって\nいうのも、オシャレでしょ？\nさ、アンタの誕生日に乾杯よ！\nハッピーバースデー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1078013, 0, "こんな楽園みたいな所に\n来れるなんて夢みたいだわ！\nあーもう最っ高！\nさあ、楽しみつくすわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1078014, 0, "せっかくの穏やかな時間なのよ？\nあくせくしないで波に身を任せて\nのんびり過ごすの。こういうのも\nたまにはいいでしょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1078015, 0, "キレイな海もまばゆい太陽も\n別に初めてじゃないのに\n何倍も心地よく感じるわ\nこれもあんたのおかげかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1078016, 0, "なに、あんたもこの浮き輪\n使いたいの？　仕方ないわね\nこっち来なさ…ちょ、近い！\n顔が近いってもう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1078017, 0, "このままあんたとどこかに\n流れていっちゃうのも悪くな…\nや、やっぱ今のなし！\n忘れろばかぁ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107901, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 6, 1079001, 0, "どうしたにゃ？\nあたしの水着姿に\nドキドキしたかにゃ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107901, 0.0, 2, 0, 2, 0, 4.3, 0, 0, 1, 1, 1079002, 0, "何してるのって、\n見てのとおり屋台の準備にゃ！\nいっぱい稼ぐにゃ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107901, 0.0, 2, 0, 3, 0, 6.2, 0, 0, 1, 1, 1079003, 0, "手伝ってくれるのかにゃ？\nありがとにゃ！\nお礼はあたしで…\nなんて冗談にゃ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107901, 0.0, 6, 0, 4, 0, 3.4, 0, 0, 1, 1, 1079004, 0, "水は嫌いだから\n砂浜で遊ぶにゃ～！\nにゃ！？　波が来るにゃ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107901, 0.0, 4, 0, 5, 0, 4.1, 0, 0, 1, 1, 1079005, 0, "海には美味しいものが\nいっぱいあるから嬉しいにゃ！\nうぅ…でも、水は嫌いにゃ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1079006, 0, "焼きそば、かき氷、たい焼き…\n海の家には美味しいものが\nたくさんあるにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1079007, 0, "よってらっしゃい\n見てらっしゃいにゃ！\nここにしかないスイーツが\n看板メニューにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1079008, 0, "このスイーツを海で食べなきゃ\n夏は始まらないにゃ！\n誰も知らない\n未知の味を約束するにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1079009, 0, "一口食べるにゃ？\nほら、あーんするにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1079010, 0, "今年の夏もキミにはいつも\n助けてもらって感謝してるにゃ\nこれからもよろしくにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107901, 0.0, 1, 0, 6, 0, 2.3, 0, 0, 2, 2, 1079011, 0, "今日はキミの誕生日にゃ！\nキャットコインを献上してくれたら\nデートくらいは\n考えてやらなくもないにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 107931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1079012, 0, "ハッピーバースデー！\n今日は特別にキミのお願い事を\n聞いてあげるにゃ！　たとえば、\n無人島で二人っきり…なんてにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108001, 0.0, 2, 0, 1, 0, 9.2, 0, 0, 1, 1, 1080001, 0, "有能な弟子がいてくれて助かるわ\nえ？　お給料がほしい？\nそうねぇ…あぁ、あのさ\nこの水着姿がお代じゃダメ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108001, 0.0, 2, 0, 2, 0, 3.2, 0, 0, 1, 1, 1080002, 0, "忘れ物はない？\n装備は万全？\nさぁ、冒険に出発するわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108001, 0.0, 2, 0, 3, 0, 2.3, 0, 0, 1, 6, 1080003, 0, "どうして水着なのかって？\n夏の海で過ごすのに\nとっても効率的だからよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108001, 0.0, 5, 0, 4, 0, 3.2, 0, 0, 1, 1, 1080004, 0, "せっかくこんな格好を\nしてるんだもの…\nキミにも見てもらわないと\nもったいないわよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108001, 0.0, 2, 0, 5, 0, 3.6, 0, 0, 1, 1, 1080005, 0, "海のバイトって割がいいから\n助かっちゃうわ！\nキミと一緒にいられるのも\nポイント高いわねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1080006, 0, "キミの前だと\nなんだか落ち着くわ…\nあ、でもジロジロ見るのは\nダメよ？　恥ずかしいもの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1080007, 0, "海ってのんびりできていいわね\n戦いかたを忘れちゃいそう…\nふふ、なーんてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1080008, 0, "こういう海でのすごし方って\n経験がなかったけど…\n楽しいものね\nキミといるからかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1080009, 0, "バイトばっかりな私だけど\n最近はキミとの時間も\n大事にしたいなって\n思ってるんだからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1080010, 0, "師匠と弟子の関係だったのに\n気がついたらキミに\n頼りっぱなしになってたわね…\nふふ、恥ずかしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108001, 0.0, 2, 0, 6, 0, 4.6, 0, 0, 2, 6, 1080011, 0, "今日がキミの誕生日だったのね\nだったらお祝いをしないと！\nさあ、海でパーティーの食材を\nゲットしてくるわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1080012, 0, "誕生日おめでとう\n今日は二人で海を見ながら\n散歩したり…たまには非効率的に\n過ごしてみない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108101, 0.0, 5, 0, 1, 0, 3.9, 0, 0, 1, 2, 1081001, 0, "は、ハッピーハロウィン…\nうう、恥ずかしいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108101, 0.0, 1, 0, 2, 0, 6.7, 0, 0, 1, 2, 1081002, 0, "トリックオアトリート…\nお菓子をくれないと呪いを…\nではなくて、いたずらをします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108101, 0.0, 4, 0, 3, 0, 4.8, 0, 0, 1, 1, 1081003, 0, "ハロウィンの時期は\nあなたの周りも賑やかなので…\n肩が重く感じませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108101, 0.0, 2, 0, 4, 0, 6.3, 0, 0, 1, 5, 1081004, 0, "この格好、ハロウィンが好きな人\nみたいで恥ずかしいですけど…\n気に入ってもらえたら\n嬉しいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108101, 0.0, 5, 0, 5, 0, 6.9, 0, 0, 1, 2, 1081005, 0, "霊のみなさん、楽しそうです…\nハロウィンですからね\nわ、私も…楽しいです\nあ、あなたと、いられるので…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1081006, 0, "ハロウィンのダンスパーティーが\n始まりますよ\nあなたも来てください…\n素敵な時間が待っていますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1081007, 0, "ハロウィンパーティーに、大勢の\n霊のみなさんが来てくれました…\n霊感の高まる時期ですから、\nあなたにも見えるでしょうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1081008, 0, "お父さん\n素敵なハロウィンなんだから\n女の子へのセクハラで\n台無しにしちゃダメだよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1081009, 0, "恥ずかしいですけど…\n一緒に踊ってもらえますか？\n霊のみなさんと\n踊り明かしたいんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1081010, 0, "こんな幸せなハロウィンは\n初めてなので…\nつい、はしゃいでしまいます\n…あなたのおかげです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108101, 0.0, 6, 0, 6, 0, 5.3, 0, 0, 2, 2, 1081011, 0, "父もあなたのお誕生日を\n祝福しているみたいですね\nあ…お父さん！　\nこの人を噛んじゃダメ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1081012, 0, "ハッピーハロウィン＆\nハッピーバースデー♪\n今日はお菓子ではなく、\nケーキをお渡ししますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108201, 0.0, 1, 0, 1, 0, 6.3, 0, 0, 1, 1, 1082001, 0, "いまのミヤコは、血も涙もない\n獰猛な狼女なの！\n食べられたくなければ、\nプリンをよこすの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108201, 0.0, 2, 0, 2, 0, 8.4, 0, 0, 1, 3, 1082002, 0, "カボチャプリン？\nそんなの邪道なの、認めないの！\n…ぱくっ…お、美味しいの～♪\nさっきの言葉は取り消すの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108201, 0.0, 6, 0, 3, 0, 7.2, 0, 0, 1, 6, 1082003, 0, "子どもたちの仮装にまじって、\nお菓子をもらいに来てる\n幽霊がたくさんいるの～\nほら、オマエの後ろにも！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108201, 0.0, 1, 0, 4, 0, 2.4, 0, 0, 1, 1, 1082004, 0, "トリックオアプリンなの～\nプリンをくれなきゃ、\n死ぬまで取り憑くの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108201, 0.0, 1, 0, 5, 0, 5.1, 0, 0, 1, 2, 1082005, 0, "ミヤコのハロウィンパーティーに\nオマエも招待してやるの！\n参加費はプリン10個で\n手を打ってやるの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1082006, 0, "幽霊大集合なの～\nみんなでハロウィンを\n盛り上げるの！\nパーティーのはじまりなの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1082007, 0, "生きてても死んでても、\nハロウィンはみんなが楽しい\n最高のイベントなの！\nミヤコ、ハロウィン大好きなの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1082008, 0, "トリックオアプリーン！\nプリンがあるところに\nミヤコありなの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1082009, 0, "オマエと一緒にハロウィンが\nできて、とっても嬉しいの！\nプリンが食べられてオマエもいて、\nミヤコの幽霊人生はバラ色なの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1082010, 0, "今夜のミヤコは、きっと\n生きていた時より輝いてるの！\nオマエとのハロウィン、\nミヤコの大切な思い出にするの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108201, 0.0, 2, 0, 6, 0, 5.2, 0, 0, 2, 1, 1082011, 0, "ひゅ～どろろろ～\nお誕生日～\nおめでとうなの～～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1082012, 0, "オマエの誕生日パーティーに、\nミヤコの友達の幽霊さんを\nたくさんつれてきたの～！\nどろろろ～おめでとうなの～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108301, 0.0, 5, 0, 1, 0, 8.5, 0, 0, 1, 2, 1083001, 0, "うふん♪　この仮装、素敵よね\n大人の色気が最高潮って感じ！\nメロメロにしちゃうわよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108301, 0.0, 2, 0, 2, 0, 9.8, 0, 0, 1, 4, 1083002, 0, "はぁ…困ったわ…\nこんなセクシーな仮装してたら、\nモテモテになっちゃうじゃない…\nアタシって罪な女…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108301, 0.0, 3, 0, 3, 0, 5.6, 0, 0, 1, 2, 1083003, 0, "えー！　お菓子くれるの！？\nやったやったー！\n…って、喜んでないわよ！\n子ども扱いしないでよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108301, 0.0, 5, 0, 4, 0, 6.0, 0, 0, 1, 2, 1083004, 0, "アタシと一緒にハロウィンを\n過ごせるなんて光栄なことよ？\nほら、お菓子をもらいに行くから\nエスコートしなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108301, 0.0, 5, 0, 5, 0, 6.0, 0, 0, 1, 2, 1083005, 0, "いまのアタシなら、\n箒で空も飛べちゃうかも～♪\nそのときは、アンタもちゃんと\n乗せてあげるわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1083006, 0, "トリックオアトリートー♪\n今日のパーティーの主役は、\nセクシー魔女のミサキちゃんよ！\nさぁ、お菓子をよこしなさーい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1083007, 0, "おほほほほ♪　どう！？\nこのエレガントで優雅な仮装！\nまさに魔性の女って感じよね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1083008, 0, "アタシの溢れ出る大人の魅力に、\nみんな見とれちゃってるのね～\n仕方ないわ…だってアタシ、\nいま最高に輝いてるし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1083009, 0, "今日はアンタをパーティーに\n招待してあげるわ！\n会場まで、ちゃーんと\nエスコートしてよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1083010, 0, "今年のハロウィンは最高ね！\nパーティーにお菓子に仮装！\nそれになにより…\nアンタがいるんだもん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108301, 0.0, 2, 0, 6, 0, 11.2, 0, 0, 2, 1, 1083011, 0, "ハッピーバースデー＆\nハッピーハロウィン♪\nというわけで、アンタへの\nプレゼントは…イタズラよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1083012, 0, "お誕生日おめでと！\nプレゼントは何がいい？\n…え？　\nこのアイちゃんが欲しいの！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108401, 0.0, 1, 0, 1, 0, 1.8, 0, 0, 1, 2, 1084001, 0, "メリークリスマス！\nどうかあなたの周りが\n幸せな歌で包まれますように…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108401, 0.0, 2, 0, 2, 0, 2.9, 0, 0, 1, 1, 1084002, 0, "あなたと一緒に過ごす\nクリスマス…\nとても楽しみです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108401, 0.0, 5, 0, 3, 0, 5.3, 0, 0, 1, 1, 1084003, 0, "唱喚士として、もっともっと\n頼られる存在になりたい…\nどうか、そばで見ていてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108401, 0.0, 2, 0, 4, 0, 5.7, 0, 0, 1, 5, 1084004, 0, "この服、結構目立つので\n恥ずかしいですけど…\nあなたに見てもらえるのなら\n好きになれそうです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108401, 0.0, 5, 0, 5, 0, 5.7, 0, 0, 1, 1, 1084005, 0, "あなたがそばにいてくれるから\n私は歌に集中できるんです…\nそんな気持ちも、いつか歌にして\nあなたに届けたいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1084006, 0, "メリークリスマス！\nありったけの気持ちを込めて\n歌いますから…聴いてくださいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1084007, 0, "クリスマスを\n幸せな歌で彩ることができたら…\n唱喚士としてこの上ない幸せです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1084008, 0, "平和を守るため…そして\n人々にささやかな幸せを\n届けるために、私は歌い続けます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1084009, 0, "私の歌声の可能性…\n今は信じています\nだって、あなたが\n信じてくれているんですから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1084010, 0, "どんな些細な感情でも\nあなたと分かち合いたいんです\nもう、あなたがいてくれないと\nダメみたいですね、私…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108401, 0.0, 5, 0, 6, 0, 4.1, 0, 0, 2, 2, 1084011, 0, "今日は誕生日ですよね、\nおめでとうございます\nプレゼントの他に、私の\n歌も贈らせてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1084012, 0, "誕生日、おめでとうございます\n子どもたちや精霊も一緒に\nお祝いしてくれるそうです\n素敵な一日にしましょうね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108501, 0.0, 2, 0, 1, 0, 7.6, 0, 0, 1, 6, 1085001, 0, "【サレンディア救護院】のみんな\nだけじゃなく、お兄ちゃんとも\nクリスマスを過ごせるなんて…\n今年は最高のクリスマスです…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108501, 0.0, 2, 0, 2, 0, 4.8, 0, 0, 1, 1, 1085002, 0, "この服なら、サンタさんの役\nだってできちゃうかも…♪\nえへへ…子供たちに\nプレゼントを配っちゃいますよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108501, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1085003, 0, "サンタさんをイメージした\n新しいお洋服です…♪\nスズメお姉ちゃんに\n作ってもらったんです…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108501, 0.0, 2, 0, 4, 0, 4.1, 0, 0, 1, 1, 1085004, 0, "お兄ちゃんの手…あったかい…\nお兄ちゃん…来年も…再来年も…\nずーっと一緒に居てくださいね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108501, 0.0, 5, 0, 5, 0, 6.4, 0, 0, 1, 1, 1085005, 0, "あの…お兄ちゃん…\nこの服似合って…ひゃぅぅ…！\nきゅ、急に頭を撫でないでぇぇ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1085006, 0, "うう…ツリーのてっぺんに\n手が届かないよぉ…\nお行儀悪いけど、\n椅子を使って…ひゃわぁ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1085007, 0, "ツリーの飾り付けが終わったら、\nお買い物と、お料理の準備と…\nうう…アヤネちゃん、雪だるま\n作ってないで手伝ってよぉ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1085008, 0, "あ…このお人形かわいい…♪\n作り方を教えてもらって、\nお兄ちゃん人形を\n作ってみようかなぁ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1085009, 0, "お、お兄ちゃん…\nクリスマスは恋人たちの夜って\nいいますよね…だ、だからその…\nあうぅぅ…何でもないですぅ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1085010, 0, "…お兄ちゃんは、私の両親みたい\nに居なくなったり…ひゃわっ！\nい、いきなり抱きしめるなんて…\nでも…あったかいです…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108501, 0.0, 2, 0, 6, 0, 7.6, 0, 0, 2, 5, 1085011, 0, "お兄ちゃん、お誕生日…\nお、おめでとうございますぅ…！\n私なりに一生懸命お祝いするから\nよ、喜んでくれると嬉しいです…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1085012, 0, "お誕生日おめでとうございますぅ\nいい子にしてたお兄ちゃんに\nプ、プレゼントを用意しました…\n靴下の中を見てみてください…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108601, 0.0, 2, 0, 1, 0, 4.0, 0, 0, 1, 1, 1086001, 0, "メリークリスマース♪\nお兄ちゃん見て見て！\nぷうきちも私みたいに、\nクリスマス衣装なんだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108601, 0.0, 5, 0, 2, 0, 2.7, 0, 0, 1, 2, 1086002, 0, "あったか～い♪\nクリスマスのお洋服で、\nぷうきちがぬっくぬくだよ～♪\n『俺のハートが燃えてるぜ』");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108601, 0.0, 5, 0, 3, 0, 9.6, 0, 0, 1, 1, 1086003, 0, "サンタさんからのプレゼント…\nデッカい七面鳥に甘ぁいケーキ…\nやっぱり、\nクリスマスは最高だよぉ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108601, 0.0, 3, 0, 4, 0, 3.1, 0, 0, 1, 1, 1086004, 0, "クリスマスのごちそう楽しみ～♪\n…なんか、お兄ちゃんから食べて\nばかりと思われている気がする！\n私だってお料理できるんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108601, 0.0, 5, 0, 5, 0, 5.8, 0, 0, 1, 1, 1086005, 0, "今年のクリスマスは、\nぷうきちだけじゃなくて、\nお兄ちゃんも居る…えへへ♪\nなんだかとっても幸せかも♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1086006, 0, "クリスマスのお洋服で、\nぷうきちもさらに\nかわいくなったよねぇ♪\n『ぷりてぃーだろ？』");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1086007, 0, "ふっふっふっ…見よこの王冠！\nクリスマスの私は王様なのだぁ♪\nだからお兄ちゃんは、\n私を思いっきり甘やかすように♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1086008, 0, "ぷうきち大家族完成～♪\nこっちは、ぷうすけとぷうきち…\nあっちは、ぷうままとぷう兄…\nじゃなくて、お兄ちゃん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1086009, 0, "雪だるまで手がつめたいからぁ…\nお兄ちゃんにたーっち♪\nえへへ…やっぱり\nお兄ちゃんはあったかいね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1086010, 0, "あったかくなったら、\n雪だるまはなくなっちゃうけど…\n私たちはずーっと一緒だよね、\nお兄ちゃん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108601, 0.0, 6, 0, 6, 0, 5.8, 0, 0, 2, 2, 1086011, 0, "お誕生日おめでとう！\nバースデーケーキは、\nぷうきちの形で作ってみたよ！\nダメっ、そっちは本物だよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1086012, 0, "お兄ちゃん、誕生日おめでとー！\n大切な日を家族みたいに\n一緒に過ごせてうれしいな！\nぷうきちも喜んでるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108701, 0.0, 1, 0, 1, 0, 3.6, 0, 0, 1, 6, 1087001, 0, "年末年始はみんな忙しそう！\n騎士くん。あたしの手なら\nいくらでも貸しちゃうよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108701, 0.0, 2, 0, 2, 0, 4.5, 0, 0, 1, 1, 1087002, 0, "動きやすい着物だから\n力仕事もオッケーだよ！\nねぇねぇ、なにすればいい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108701, 0.0, 2, 0, 3, 0, 6.1, 0, 0, 1, 1, 1087003, 0, "お正月の空気って好きだなー\n新しい年を迎えるのって\n気分がピーンってなるよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108701, 0.0, 5, 0, 4, 0, 5.4, 0, 0, 1, 1, 1087004, 0, "騎士くんだー！\nおーい、ここだよー！\n騎士くんなら、どこにいても\nすぐに分かるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108701, 0.0, 5, 0, 5, 0, 7.8, 0, 0, 1, 1, 1087005, 0, "初詣のおみくじ？　大吉だよ！\nいいことばっかり書いてたなー\n恋愛運！？　…内緒っ！　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1087006, 0, "次はあっちの屋台に行こうよ！\n持ちきれない分は早く食べちゃお！\n騎士くんもほら、食べて食べて！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1087007, 0, "ユイちゃんのおせちに\n屋台の食べ物…！\n最高のお正月だよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1087008, 0, "大晦日にたくさん頑張ったし\n今日くらいは、もうちょっと\n食べちゃってもいいよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1087009, 0, "あっ…そのお菓子\nあたしが口付けちゃった…\n気にしない？　そ、そっか！\nならオッケー！　うん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1087010, 0, "騎士くん…手、つないでもいい？\nあっ、これ食べちゃわないとだね！\nはい騎士くん！　あーんっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108701, 0.0, 1, 0, 6, 0, 4.4, 0, 0, 2, 2, 1087011, 0, "騎士くん、誕生日おめでとー！\n騎士くんにとって\n新しい一年のはじまりだね！\n一緒にお祝いしよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1087012, 0, "ハッピーバースデー！\n騎士くんがこれからの一年間も\n幸せでありますように！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1087013, 0, "あはっ、すっごくきもちいい！\n騎士くんもおいでよ！\n一緒に風になろ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1087014, 0, "おっとっとぉ…\nうん、だいぶ慣れてきた♪\nよーし、もっとスピードだすぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1087015, 0, "やっほー騎士くーん！\nニューイヤートリック！\nなんちゃって♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1087016, 0, "騎士くんが見ててくれるなら…\nどんな大技でも\nきめられちゃうかも♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1087017, 0, "大丈夫だよ？\nだって何かあっても、騎士くんが\n抱っこして助けてくれるもんね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108801, 0.0, 1, 0, 1, 0, 4.7, 0, 0, 1, 2, 1088001, 0, "あけましておめでとう！\n騎士クン。今年もよろしくね\nおみくじはもう引いた？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108801, 0.0, 1, 0, 2, 0, 5.1, 0, 0, 1, 3, 1088002, 0, "お正月でも気を抜かずに\n魔法の修行をしないと…\n今年はもっと上達したいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108801, 0.0, 2, 0, 3, 0, 3.1, 0, 0, 1, 1, 1088003, 0, "騎士クン、寒くない？\n防寒の魔法が必要なら\nいつでも言ってね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108801, 0.0, 8, 0, 4, 0, 7.4, 0, 0, 1, 5, 1088004, 0, "いいな…恋人同士でお参り\nわたしもいつか…\nな、なんでもないよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108801, 0.0, 5, 0, 5, 0, 7.8, 0, 0, 1, 1, 1088005, 0, "今年の恋愛運は…\n「相手の胃袋を掴むべし」…\n…騎士クン、お腹空いてない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1088006, 0, "騎士クン、去年はありがとう！\n今年もよろしくね！\n来年も…その先も！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1088007, 0, "…こっちに来て欲しいな\nか、風が強くて寒いから！\n…そばにいてくれる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1088008, 0, "初日の出、キレイだね…\n…騎士クン？　どうしたの？\n太陽、まぶしくない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1088009, 0, "あのね？　今日みたいに\n毎年騎士クンと…\n…太陽が見たいの！\n…なに言ってるのわたし～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1088010, 0, "騎士クン。わたしね…\n騎士クンといるどんな時間も\n幸せに思ってるから…！\n…今、言えた…！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108801, 0.0, 2, 0, 6, 0, 11.3, 0, 0, 2, 5, 1088011, 0, "あ、あけましておめでとう！\nああっ、間違えちゃった…\nえっと…その…\nお誕生日おめでとう、騎士クン！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1088012, 0, "騎士クン、お誕生日おめでとう！\nこれからも騎士クンが\n幸せな日々を過ごせますように…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1088013, 0, "騎士クン…？　あ、あの…\nおみくじ、結んじゃわないと…\nわ、わたし…わたし…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1088014, 0, "はぁ…あったかいな…あっ！\nおみくじが飛んでいっちゃう！\nで、でも、もう少しだけ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1088015, 0, "おみくじに書いてあることも\nお参りして願ったことも、\n叶えるのはわたし自身…だよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1088016, 0, "騎士クン…もう大丈夫だよ？\n…手がやわらかくて気持ちいい？\nじゃあ…あと少し、このままで…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1088017, 0, "わたし、騎士クンと離れたくない\n…今だけじゃ、ないよ…？\n今年も、来年も…ずっと…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108901, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1089001, 0, "着物を着ると、いつもより\n歩き方や所作に注意して\n動くようになるんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108901, 0.0, 2, 0, 2, 0, 7.1, 0, 0, 1, 3, 1089002, 0, "正月といえど、羽目を外して\n暴れる輩は、この刀で成敗！\n…冗談だよ。本気にした？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108901, 0.0, 2, 0, 3, 0, 2.9, 0, 0, 1, 1, 1089003, 0, "キミは正月をどう過ごすの？\n参考にしたいから、良ければ\n教えてくれない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108901, 0.0, 5, 0, 4, 0, 6.8, 0, 0, 1, 6, 1089004, 0, "いつもと雰囲気が違う？\nまぁ、着物を着ているし、\nキミの隣にいるから…かな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108901, 0.0, 2, 0, 5, 0, 5.2, 0, 0, 1, 1, 1089005, 0, "おみくじを引いたら\n恋愛運が良かったんだけど…\n…さっそく運が向いたみたいだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1089006, 0, "新年早々なんて邪な…！\n人の幸せを邪魔する者は\n私が全力で斬る！　いくぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1089007, 0, "やっぱり今年も、私にはキミが\n必要だ。…お願い。これからも、\nキミの力を貸して欲しい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1089008, 0, "冷気を帯びたこの剣技…\nキミのおかげで、更に磨きが\nかかったよ。ありがとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1089009, 0, "…私は、自分の進む道は\n自らの手で切り開きたいんだ\nそしてその道は、大切な仲間と\nキミと一緒に歩んでいきたい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1089010, 0, "…煩悩は去年に置いてきたはず\n気にしないで！　なんでもない！\nなんでもない、はずだから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108901, 0.0, 2, 0, 6, 0, 2.1, 0, 0, 2, 1, 1089011, 0, "誕生日おめでとう！\nキミと一緒に過ごす日々は\nこれからも私にとって\n大切な宝物だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1089012, 0, "今日はキミがこの世に生まれた\n特別な日だ。特別なお祝いを\nあげよう。なんでもいいよ\n…キスのフリ、以外なら…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108961, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1089013, 0, "私が私らしく生きるには\n乗り越えなければならない壁が\nいくつもある。でもキミが隣に\nいてくれるなら、何も怖くないよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108961, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1089014, 0, "この先の人生で新年を祝うことは\n何回だってあるだろうけど…\nあの瞬間、目に焼き付いた光景は\n決して色あせないだろうね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108961, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1089015, 0, "家の束縛から逃れるために、\nキミと一緒にいるわけじゃない\n大切な人だから傍にいたいと\n心から願っている。それだけだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108961, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1089016, 0, "新たな一歩を踏み出せば、\nきっと何かを変えられる\nそして必ず掴めるはずだよ\nキミと生きる、輝く未来を…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 108961, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1089017, 0, "愛の力は偉大だ…なんて\n大真面目に言うことじゃないと、\n以前の私なら考えていただろうね\nでも、確かにその通りだったんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109001, 0.0, 2, 0, 1, 0, 12.7, 0, 0, 1, 1, 1090001, 0, "あなた様。手作りのチョコです\n受け取ってくださいますか…？\n愛情がいっぱい入っていますわ\nさぁ、お口を開けてください…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109001, 0.0, 2, 0, 2, 0, 10.8, 0, 0, 1, 1, 1090002, 0, "いい香りでしょう…？\nハーブを混ぜてあるんです\nどんな効能か？\nクスクス…なんでしょうね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109001, 0.0, 2, 0, 3, 0, 3.7, 0, 0, 1, 6, 1090003, 0, "周りのビンが気になりますか？\nご安心を、すべて調味料です\n料理するときは手になじむものを\n使いたいという、乙女心ですわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109001, 0.0, 5, 0, 4, 0, 3.2, 0, 0, 1, 1, 1090004, 0, "不思議なものですね\nチョコを作っているとき、\nあなた様の喜ぶ顔が見たくて\n一生懸命になってしまいました");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109001, 0.0, 2, 0, 5, 0, 11.2, 0, 0, 1, 5, 1090005, 0, "こうして料理をしていると、\n本当の伴侶のようですわね\nもちろん、いずれは本当に\nなるのですけれど…クスクス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1090006, 0, "バレンタインデーとは\n愛する方にチョコレートを\nお贈りする日…私の甘い愛で\nお腹を満たして差し上げましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1090007, 0, "運命で結ばれたあなた様への想い\nチョコだけでは表現しきれません\n三日三晩かけて想いを綴った\nお手紙も一緒にお付けしますわ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1090008, 0, "エプロン、似合っていますか？\nクスクス…うれしいですわ\nでしたら毎日お見せしますね\nどんな時間、どんな場所でも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1090009, 0, "バレンタインデーでは、チョコと\n一緒に告白する方もいるみたい\nですが…特別な日じゃなくても\n私は毎日愛をお伝えしますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1090010, 0, "調合した薬を混ぜるのもいいです\nけれど、やっぱりチョコレートを\n甘くするのは、込めた想いですわ\n愛しています…あなた様");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109001, 0.0, 2, 0, 6, 0, 11.2, 0, 0, 2, 5, 1090011, 0, "あなた様が生まれた日…つまり\n私たちの運命が始まった日ですわ\n祝杯をあげましょう\nさぁ、これをお飲みになって…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1090012, 0, "誕生日おめでとうございます\nふたりきりでお祝いする準備は\nできていますわ\nそのあとは…クスクス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109101, 0.0, 2, 0, 1, 0, 6.7, 0, 0, 1, 1, 1091001, 0, "弟くん、バレンタインは\n知ってる？　大好きな人に\nチョコレートをあげる日なんだよ\nお姉ちゃんの愛、受け止めてね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109101, 0.0, 5, 0, 2, 0, 7.5, 0, 0, 1, 1, 1091002, 0, "お姉ちゃんのよしよしみたいに\n甘いチョコか、お姉ちゃんの\nナデナデみたいに大人なチョコ\n弟くんはどっちがいいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109101, 0.0, 1, 0, 3, 0, 2.0, 0, 0, 1, 6, 1091003, 0, "虫歯になっちゃいそう？\n大丈夫だよっ、お姉ちゃんが\n弟くんの歯を綺麗に磨いてあげる\nあーんしてみて♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109101, 0.0, 2, 0, 4, 0, 5.5, 0, 0, 1, 5, 1091004, 0, "ナデナデしてあげようか？\nそれとも肩もみ？　膝枕？\nチョコとセットで、お姉ちゃんの\nあま～いお世話はいかがっ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109101, 0.0, 2, 0, 5, 0, 7.1, 0, 0, 1, 5, 1091005, 0, "ほら、口元にチョコが付いてるよ\nお姉ちゃんがとって…あむ\nうん、とっても甘い♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1091006, 0, "バレンタインは甘いチョコと\n一緒に愛を伝える日♪\nお姉ちゃんの愛はチョコよりも\n甘いから、覚悟しててね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1091007, 0, "チョコはいくつもらえたのかな？\nお姉ちゃんに教えてごらん？\n弟くんのすべてを知りたい\n純粋な姉心だよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1091008, 0, "そわそわしちゃってどうしたの？\n弟くんは何を待ってるのかな？\nふふっ、焦らされる弟くんも\nかわいいなぁ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1091009, 0, "えっ？　この衣装、似合ってる？\n弟くんったら、女の子の褒め方が\n上手になったね♪\nお姉ちゃん、ドキッとしちゃった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1091010, 0, "弟くん、大好きだよ♪\nチョコはまだまだあるから\nあげるたびに言ってあげるね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109101, 0.0, 2, 0, 6, 0, 2.8, 0, 0, 2, 1, 1091011, 0, "今日は弟くんの誕生日！\n大きなチョコケーキを作ったから\n一緒に食べようね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1091012, 0, "お誕生日だね！　お姉ちゃんは\n今日という記念日のために、\nプレゼントを考え続けてたよ！\nはい、お姉ちゃんをあげるね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109201, 0.0, 2, 0, 1, 0, 5.0, 0, 0, 1, 1, 1092001, 0, "せっかく別の世界に来たんだし、\nあなたとも仲良くなりたいな！\nよろしくねっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109201, 0.0, 1, 0, 2, 0, 4.0, 0, 0, 1, 6, 1092002, 0, "確かに、マナリアの第一王女では\nあるけど…あまり気にしないで、\n気軽に話してくれるとうれしいな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109201, 0.0, 2, 0, 3, 0, 3.7, 0, 0, 1, 1, 1092003, 0, "普段はグレアと二人でいることが\n多いかな！　グレアの隣は\nとっても落ち着くし、安心するの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109201, 0.0, 1, 0, 4, 0, 3.0, 0, 0, 1, 2, 1092004, 0, "あなたのお話、すっごく面白い！\nもっとも～っと聞かせてほしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109201, 0.0, 2, 0, 5, 0, 3.2, 0, 0, 1, 5, 1092005, 0, "良かったら、あとで一緒に\nお茶しない？　紅茶を淹れるの\n実は、ちょっと得意なんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1092006, 0, "英霊よ、我が声に応えよ！\nみんなを護るために\nあなたの力を貸して！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1092007, 0, "グレアには絶対に\n悲しい顔をさせたりしない！\n私、頑張るよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1092008, 0, "大切な人たちとの絆が、\n私に無限の力を\n与えてくれるんだ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1092009, 0, "私の魔法は、みんなを…\nあなたを守るためにあるんだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1092010, 0, "あなたのことは絶対に\n傷つけさせないから！\nここは私に任せてね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109201, 0.0, 2, 0, 6, 0, 1.8, 0, 0, 2, 1, 1092011, 0, "お誕生日おめでとうっ！\n今日は思いっきり幸せな\n一日になるといいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1092012, 0, "ハッピーバースデー！\n一緒に楽しい一日にしようねっ！\nまず何からしよっか！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109301, 0.0, 2, 0, 1, 0, 3.1, 0, 0, 1, 1, 1093001, 0, "お友だちが増えるのは\nうれしいですぅ！\n一緒にせいしゅんしたいですぅ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109301, 0.0, 1, 0, 2, 0, 3.0, 0, 0, 1, 4, 1093002, 0, "追試と赤点の毎日ですけど、\nルゥはマナリア魔法学院が\nだぁーい好きですぅ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109301, 0.0, 2, 0, 3, 0, 2.7, 0, 0, 1, 1, 1093003, 0, "せいしゅん、してるですか？\nルゥは毎日してるですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109301, 0.0, 2, 0, 4, 0, 1.2, 0, 0, 1, 1, 1093004, 0, "ルゥと一緒に、もっともーっと\n遊んでほしいですぅ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109301, 0.0, 1, 0, 5, 0, 3.4, 0, 0, 1, 6, 1093005, 0, "オメメちゃんのことが\n気になるですか？\nつんつんってしてみます？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1093006, 0, "ルゥの華麗なオメメちゃんさばき\n見せてあげるですよ！\nそれそれですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1093007, 0, "ルゥとオメメちゃんの\nこんびねーしょんで\nやっつけちゃうですよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1093008, 0, "別の世界での思い出も、\nせいしゅんの１ページです！\nルゥ、とっても楽しいですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1093009, 0, "え、オメメちゃんの正体ですか？\nさぁ…？　でも、ルゥとずーっと\n一緒のお友だちですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1093010, 0, "追試の女帝、落第の女王…\n数々のいみょうを持つルゥの実力\n見せてやるですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109301, 0.0, 2, 0, 6, 0, 2.8, 0, 0, 2, 6, 1093011, 0, "今日はあなたの誕生日です？\nじゃあ、ルゥもお祝いするです！\nおめでとうですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1093012, 0, "ルゥ、暗記は苦手ですが…\nお友だちの誕生日は\nばっちり覚えてるですぅ！\nお誕生日おめでとうですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109401, 0.0, 4, 0, 1, 0, 4.4, 0, 0, 1, 1, 1094001, 0, "私のこの姿や、竜の力…\nあなたは、\n怖がらないでいてくれる…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109401, 0.0, 2, 0, 2, 0, 3.6, 0, 0, 1, 5, 1094002, 0, "私はみんなみたいに\nはしゃげないけど…\nこれでも楽しんでるつもり");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109401, 0.0, 2, 0, 3, 0, 5.3, 0, 0, 1, 1, 1094003, 0, "アンは、ずっと一人だった私の\nそばにいてくれて…\n私の世界を照らしてくれたんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109401, 0.0, 2, 0, 4, 0, 3.7, 0, 0, 1, 6, 1094004, 0, "違う世界に来てしまうなんて…\nでも、あなたたちみたいな\n優しい人に出会えて、よかった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109401, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1094005, 0, "あなたには本当に感謝してる…\nもし私に手伝えることがあれば\n何でもいってね、頑張るから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1094006, 0, "皆を護るためなら、\nいくらでも力が出せるよ…！\n竜の炎を見せてあげる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1094007, 0, "皆のことは…\n絶対に傷つけさせない\n私の炎は、そのためにあるの…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1094008, 0, "私に足りなかったのは、\n自分に負けない強さ…\nでも今は、もう大丈夫！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1094009, 0, "こんな力、いらないって\n思ったこともあるけれど…\n今は少し誇れる気がするんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1094010, 0, "私、結構丈夫なんだ\nだから…任せて！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109401, 0.0, 2, 0, 6, 0, 4.3, 0, 0, 2, 1, 1094011, 0, "今日はあなたの誕生日なんだね\nその…おめでとう\nなにか欲しいものとかある？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1094012, 0, "ハッピーバースデー\nあなたのお祝いの日なのに、\n私までうれしいんだ。不思議だね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109501, 0.0, 2, 0, 1, 0, 2.6, 0, 0, 1, 1, 1095001, 0, "これが噂の着物ですね…\n見てよし、\n脱がせてよし、\n回してよしの魔性の服…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109501, 0.0, 6, 0, 2, 0, 2.9, 0, 0, 1, 5, 1095002, 0, "あ～れ～～～～～！\nドＳさん、見ていたのですか！？\nクウカの回る姿を楽しんでいたの\nですね…やはり人格破綻者…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109501, 0.0, 5, 0, 3, 0, 4.8, 0, 0, 1, 1, 1095003, 0, "噂によると、着物では\n履かないものなのだとか…\nドッ、ドＳさん…確かめさせろ\nだなんていけませんよぉ…ぐふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109501, 0.0, 5, 0, 4, 0, 7.4, 0, 0, 1, 6, 1095004, 0, "あっ、帯が緩んでしまいました…\nドＳさん、締めなおしてくれるの\nですか？　…じゅるり。いい…\nいいですっ！　もっときつく～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109501, 0.0, 5, 0, 5, 0, 5.6, 0, 0, 1, 2, 1095005, 0, "ドＳさん…いえ、今はドS様と\n呼んだ方がいいでしょうか…\nクウカはドＳ様にお仕えする\n従順な…じゅるり");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1095006, 0, "この着物、露出が多くて\n少し恥ずかしいです…でも、\nドＳさんに見られていると思うと\nなんだか気持ちが…じゅるり");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1095007, 0, "あ～～～れ～～～～～！\nこの回転がクセになりそうです\nもっと、もっと回ります～！\nぐふ、ぐふふふ～～～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1095008, 0, "この衣装で踊ると、いつもより\nきれいに踊れている気がします…\n蝶のように舞うクウカを、\n蜘蛛のようにドＳさんが…ぐふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1095009, 0, "「クウカの着物を剥ぎ取り、\nドＳさんは野獣のように…」\nぐふふ…はっ！　ドSさんに\n見られている気配がします…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1095010, 0, "この帯が気になるんですか？\nつ、つまりクウカを回したいと…！\nそして着物のはだけたクウカを\nそのまま美味しく…ぐふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109501, 0.0, 5, 0, 6, 0, 5.7, 0, 0, 2, 1, 1095011, 0, "今日は大切な日ですね…\nドＳさんが生まれた日…じゅるり\nつまりクウカの体でお祝いしろと\nいうのですね！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1095012, 0, "お誕生日おめでとうございます\nところでドＳさん、和服の帯って\nラッピングしてるみたいですよね\nプレゼントは私、なんて…ぐふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109601, 0.0, 2, 0, 1, 0, 7.9, 0, 0, 1, 1, 1096001, 0, "オシノビでお出かけするなら\nいいところを知ってマス\n今度ワタシが案内するデス！\nきっと楽しいデスよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109601, 0.0, 6, 0, 2, 0, 4.9, 0, 0, 1, 1, 1096002, 0, "もみじも好きデスけど\nもみじマンジュウも好きデスよ\n考えてたらお腹が空きまシタ…\nハナヨリダンゴデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109601, 0.0, 2, 0, 3, 0, 8.6, 0, 0, 1, 3, 1096003, 0, "ヒトツ投げては天下統一のため…\nフタツ投げても天下統一のため…\nショーグンも手裏剣投げの特訓を\nしまセンか？　投げ放題デス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109601, 0.0, 2, 0, 4, 0, 11.0, 0, 0, 1, 1, 1096004, 0, "このキツネのお面はオーエド町の\n縁日で買いまシタ。歩くと鈴が\n鳴って、とてもかわいいデス\nコンコンデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109601, 0.0, 1, 0, 5, 0, 4.0, 0, 0, 1, 6, 1096005, 0, "忍法・氷結地獄を思いつきまシタ\n冷たいカキ氷を\n敵に無理やり食べさせて\n頭をキーーーンとさせる技デス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1096006, 0, "ショーグンに仇名す敵に\n天誅デス！　天下統一するまで\n立ち止まりまセン！\n必殺忍法で勝ち進みマス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1096007, 0, "フッフッフ…とっておきの\n新忍法の見せどころデース！\nムム、まだ使っちゃダメデスか？\nショーグンのイケズデス～…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1096008, 0, "ショーグンは天下泰平の世を\n築いてくれるでショウ！\nワタシはそんな城下町を\nずっとお守りいたソウロウ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1096009, 0, "ワタシのカチュヤク、しっかり\n見ていてクダサイ！　いつか\nチューシンと呼ばれるその日まで\nシロクジチュウおそばにいマス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1096010, 0, "ショーグンのためならどんな\n敵とだって戦うデス！\nチューセーシンこそ天下統一の\n切り札だと信じていマス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109601, 0.0, 1, 0, 6, 0, 7.4, 0, 0, 2, 2, 1096011, 0, "誕生日おめでとうデス！\nプレゼントはショーグン専用の\nマイ手裏剣デース！\nいっぱい投げ込んでクダサーイ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1096012, 0, "今日はおめでとうゴザルデス！\n乾杯の音頭はワタシがとりマス！\nお手をハイシャクデース！\n朝までワッショイデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109701, 0.0, 2, 0, 1, 0, 4.9, 0, 0, 1, 1, 1097001, 0, "家事全般は得意ですので、\nなにかお役に\n立てることがありましたら、\nこのレムにお申し付けください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109701, 0.0, 2, 0, 2, 0, 5.8, 0, 0, 1, 6, 1097002, 0, "姉様がメイドの仕事を\nしないで休憩している…ですか？\nさすが姉様！\nお休みする姿もすてきです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109701, 0.0, 2, 0, 3, 0, 4.0, 0, 0, 1, 1, 1097003, 0, "鬼といっても、\n人間と大きく変わりませんよ\n魔獣を薙ぎ払ったり、\n鉄球を振り回せるぐらいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109701, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1097004, 0, "身の回りの世話は\nこのレムにお任せください\n懸命にご奉仕\nさせていただきます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109701, 0.0, 2, 0, 5, 0, 5.1, 0, 0, 1, 6, 1097005, 0, "こちらの世界では、\n角があっても\n珍しくはないのですね\nなんだか、少しうれしいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1097006, 0, "この鉄球ですか？\nこれは携帯用で、\n短くなっているから、\n普通の鉄球より軽いんですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1097007, 0, "巨木であろうと、\n鬼であるレムを\n阻むことは不可能です\n全て粉砕させていただきます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1097008, 0, "この程度の巨木ならば、\nレムの力でどけられると思います\nあ、あの…上手くいったら\n褒めてくれてもいいんですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1097009, 0, "女性にこの倒木を\n破壊するのは無理、ですか？\nご託は結構ですので、\nそこで見ていてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1097010, 0, "強いって…\n女の子にその言葉は\nどうかと思いますよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109701, 0.0, 1, 0, 6, 0, 5.4, 0, 0, 2, 2, 1097011, 0, "お誕生日おめでとうございます\nお祝いにケーキを焼きますね\nより豪華にするため、まずは\n飛竜の卵を調達してこなくては");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1097012, 0, "お誕生日おめでとうございます！\n今日のために色々と\n用意させていただきましたので、\n精いっぱいお祝いいたしますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109761, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1097013, 0, "お掃除はレムの得意分野です\nそれが食材調達も兼ねるのなら\nまさに好都合というものです\n綺麗さっぱりお片付けします！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109761, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1097014, 0, "魔物であろうと魔獣であろうと\nこちらの世界の皆様に\n害をなすというのであれば…\nレムがお相手いたします！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109761, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1097015, 0, "皆様に喜んでいただけるよう\n心を込めて調理しますね\n材料が魔物だろうと野菜だろうと\nご心配には及びません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109761, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1097016, 0, "少し離れていてください\n大丈夫です。レムが皆様を\nお守りします\n簡単にやられたりはしませんよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109761, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1097017, 0, "お任せください\n忠実なるメイドとして冷静に\n姉様のように堂々と\n完璧にお掃除を遂行します！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109801, 0.0, 3, 0, 1, 0, 5.0, 0, 0, 1, 6, 1098001, 0, "ここが異世界。正直、\n信じられるものでもないけど…\nバルスの軽口よりは\nいくらかましかもしれないわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109801, 0.0, 3, 0, 2, 0, 2.4, 0, 0, 1, 4, 1098002, 0, "面倒なことになったものね\nまぁ、危険人物はいないようだし\n楽できる分には問題ないわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109801, 0.0, 2, 0, 3, 0, 1.9, 0, 0, 1, 3, 1098003, 0, "仕事ならしているわよ\n紅茶を優雅に飲むラムの姿が、\n周りの目を楽しませているわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109801, 0.0, 4, 0, 4, 0, 5.4, 0, 0, 1, 3, 1098004, 0, "これは風の系統魔法よ\nその気になれば、\n何でも細切れにできるわ\n疲れるから、やらないけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109801, 0.0, 2, 0, 5, 0, 5.9, 0, 0, 1, 3, 1098005, 0, "蒸かし芋を食らうがいいわ\nおいしかったでしょう\n出来たて…いえ、\nふかしたてよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1098006, 0, "千里眼をもつラムからは\n逃げられないわよ\n死にたくなければ、せいぜい\n知恵と勇気を振り絞ることね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1098007, 0, "覚えておきなさい\n角をなくそうと、\n天才であるラムには\n遠くおよばないということを");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1098008, 0, "戦闘では本当に役立たずね\n失望を隠すのが大変だわ\n…せいぜいラムの側から\n離れないことね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1098009, 0, "一気に決めさせてもらうわ\nなぜって？\nラムが…いえ、\n早く帰りたいからよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1098010, 0, "風の魔法は便利よ。敵を\n切り裂くだけでなく、面倒な男を\nねじ切ることだってできるわ\nどこを？　ご想像にお任せするわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109801, 0.0, 2, 0, 6, 0, 6.5, 0, 0, 2, 3, 1098011, 0, "誕生日をラムに祝ってもらえる\nなんて光栄に思うことね\n誕生日プレゼント？　もちろん\n出来たての蒸かし芋よ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1098012, 0, "何が欲しいの？\n言ってごらんなさい\nなんでって、誕生日なんでしょ？\nご褒美くらいくれてやるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109861, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1098013, 0, "ラムが味方にいることを\n幸運に思いなさい\n何を隠そう、\nラムの得意料理は蒸かし芋よ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109861, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1098014, 0, "ラムの助けになりたいだなんて\n思い上がりも甚だしいけど\n殊勝な態度は認めてあげるわ\nさっさとラムを強化しなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109861, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1098015, 0, "ラムがあなたに恩を感じるなんて\nこれっぽっちもないのだけど\n偶然、蒸かしたての芋があるわ\n熱いうちに貪りなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109861, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1098016, 0, "あなたが信用に値するかは\nラムがこの目で判断するわ\n僕はラムを信用してる…？\nそう、ならせいぜい尽くしなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109861, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1098017, 0, "レムやエミリア様に\nいやらしい目を向けないことね\n……ラムに？\n浮気者は死になさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109901, 0.0, 1, 0, 1, 0, 7.9, 0, 0, 1, 6, 1099001, 0, "不思議なものね\nマナもあって精霊もいるのに\n見たこともないものがいっぱい\nここが異世界なんだ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109901, 0.0, 2, 0, 2, 0, 6.3, 0, 0, 1, 1, 1099002, 0, "軽くお散歩でもする？\n精霊の子たちとお話ししにいくの\nそれがこの子たちとの\n契約条件の一つだから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109901, 0.0, 6, 0, 3, 0, 5.0, 0, 0, 1, 1, 1099003, 0, "さっき街に行ったら\nエルフの人をみかけたわ\nもしかしてこの世界にも\nハーフエルフっているのかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109901, 0.0, 6, 0, 4, 0, 5.2, 0, 0, 1, 5, 1099004, 0, "コッコロちゃんって賢くて\nすごーくかわいいわよね\n…本当に私の妹だったら\nよかったのにな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109901, 0.0, 6, 0, 5, 0, 5.4, 0, 0, 1, 1, 1099005, 0, "こっちの世界に来てから\nパックってあまり出てこないの\nマナを使いすぎないように\n気を使ってるのかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1099006, 0, "あなたも少し座らない？\nランドソルの花もすごーくきれい\n微精霊たちもよろこんでるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1099007, 0, "少しお話しましょう？\nこの世界のことや\n私の世界のこと…\n話したいことがたくさんあるの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1099008, 0, "そっか、コッコロちゃんも\n精霊たちとお話しできるのね\nふふ、やっぱり他人とは\n思えないな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1099009, 0, "あら、花びらが髪についてる\nとってあげるから\n頭をこっちに向けて？\nそうそう…はい、とれたよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1099010, 0, "ねえ…またこのお花畑を\n一緒に見に来てくれる？\n…精霊使いにとって約束は\n何よりも大切なんだからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109901, 0.0, 2, 0, 6, 0, 6.0, 0, 0, 2, 1, 1099011, 0, "こっちの世界だと\nはっぴーばーすでー\nでいいのかな？\nお誕生日おめでとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1099012, 0, "お誕生日なんだね、おめでとう\nこうやってお祝いできて\nすごーくうれしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109961, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1099013, 0, "最近、ランドソルの人たちに\n名前で呼ばれるようになったの\n受け入れてもらえた気がして、\nすごーく嬉しいわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109961, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1099014, 0, "散歩や旅行、もちろん冒険も、\nいろんなことを通じて、みんなと\n絆を深めていきたいわ\n魔物料理を食べるのもいいわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109961, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1099015, 0, "雪が降ってるのに、\nなんだかすごーくあったかいの\n体を動かしたからかもだけど\nそれだけじゃないような…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109961, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1099016, 0, "思えば、この世界では素敵な\n思い出ばかりもらっちゃってる\n私も何かお返ししたいんだけど…\nえ？　もうもらってる…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 109961, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1099017, 0, "私、お友だちって呼べる人は\nあんまり多くないの\nだから、ここで出会ったみんなは\nすごーく大事な宝物なの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110001, 0.0, 2, 0, 1, 0, 4.9, 0, 0, 1, 1, 1100001, 0, "ちょす～♪\n夏の海ってキラキラしてて\nマジヤバいよね！\nめいっぱい楽しんじゃお～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110001, 0.0, 6, 0, 2, 0, 6.9, 0, 0, 1, 1, 1100002, 0, "ヒデサイ、知ってた？\n海の水ってしょっぱいんだよ！\nうち物知りっしょ～♪\nでぃすいずあんだすたんど？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110001, 0.0, 1, 0, 3, 0, 3.4, 0, 0, 1, 2, 1100003, 0, "撮影が終わったら\n一緒に海で遊ぼうよ！\nすぐ終わるから\nちょいまっててね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110001, 0.0, 5, 0, 4, 0, 2.5, 0, 0, 1, 2, 1100004, 0, "この水着、イケてるっしょ！\nファンのみんなに\n喜んでもらえるといいな～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110001, 0.0, 1, 0, 5, 0, 7.7, 0, 0, 1, 2, 1100005, 0, "んー！　海って最高ー！\nヒデサイといっぱい遊べるし\nマジで来てよかったよー♪\n今は夏の宿題の話はナシでよろ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1100006, 0, "見て見て、うちのモデル姿！\nどう？　いい感じっしょ♪\nこんなに近くで見ていいのは\nヒデサイだけだかんね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1100007, 0, "夏の海って楽しいことが\nマジでいっぱいあるけど…\nヒデサイといるだけで、めっちゃ\nバイブス上がるんだよね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1100008, 0, "セクシーってよく分かんないけど\nいまのうち、セクシーかな？\nヒデサイがドキドキ\nしてたら、うれしいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1100009, 0, "夏って気分アガるよね♪\nヒデサイも応援してくれるし、\nもう怖いもんなしって感じだよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1100010, 0, "日ざしも砂浜もあっついねー！\n今日はめっちゃ\nフィーリングがいいんだ～♪\n超かわいい写真が撮れそう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110001, 0.0, 2, 0, 6, 0, 3.3, 0, 0, 2, 1, 1100011, 0, "ちょすー！\nヒデサイ、誕生日なんだよね？\nおめでとー！\n今日はパーティーだね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1100012, 0, "ちょす！　ヒデサイ、ハピバー☆\nお祝いしなきゃだよね！\nうちにしてほしいことがあったら\nなんでも言っていいよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110101, 0.0, 2, 0, 1, 0, 7.4, 0, 0, 1, 1, 1101001, 0, "海に入る前には\n準備運動をしなくちゃ！\n手伝ってあげるから力を抜いて？\nそう、とっても上手よ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110101, 0.0, 1, 0, 2, 0, 7.1, 0, 0, 1, 6, 1101002, 0, "海って楽しいところだけど\n危ないこともあるんだから\n気を付けて遊びましょうね？\n先生がついていてあげる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110101, 0.0, 4, 0, 3, 0, 4.8, 0, 0, 1, 6, 1101003, 0, "なんだかやたら男の人が\n話しかけてくるの。もしかして\n水着がずれちゃってるのかな\n君、ちょっと見てくれる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110101, 0.0, 4, 0, 4, 0, 7.0, 0, 0, 1, 5, 1101004, 0, "ああ～ん、\n砂が水着の中に入ってきちゃって\nくすぐったいわ～\nお願い、なんとかして～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110101, 0.0, 6, 0, 5, 0, 8.0, 0, 0, 1, 5, 1101005, 0, "久しぶりに水着を着るから\n今日のために新調したのよ\n似合ってるかな？　ってどうして\n顔をそむけちゃうのよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1101006, 0, "くしゅんっ…\nちょっと冷えてきちゃった\n君にくっついて\n暖まってもいいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1101007, 0, "君があまりにも頼もしいから\n私たちが先生と生徒って事を\n忘れちゃいそうなの…\nそれでも…ううん、なんでもない");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1101008, 0, "こうして男の子と海にくるなんて\n初めてのことだから\nなんだか照れちゃうわ\nねえ、君はいまどんな気持ち？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1101009, 0, "夏休みの一番の思い出は\nこのきれいな夜の海かな\n君と一緒に見たんだから\n一生忘れないわ、きっと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1101010, 0, "君、もしかして少し背が伸びた？\n気のせいじゃないと思うよ\n君の成長を感じると、なんだか\nうれしくて胸がドキドキするから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110101, 0.0, 2, 0, 6, 0, 9.2, 0, 0, 2, 1, 1101011, 0, "今日は君のお誕生日ね\nふふ、もちろん忘れてないわ\n一つ大人になったお祝いに\n海の家でパーティーしましょ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1101012, 0, "お誕生日おめでとう～\n浜辺で見る君の顔が\nすっかり大人にみえるわ\nふふ、夏のせいかしらね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110301, 0.0, 1, 0, 1, 0, 4.9, 0, 0, 1, 2, 1103001, 0, "あははっ\n日が暮れるまで浜辺でゆっくり…\nこんな日があってもいいわよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110301, 0.0, 6, 0, 2, 0, 6.0, 0, 0, 1, 1, 1103002, 0, "貝殻を集めてアクセサリー作り\nなんてのも悪くないわね\n思い出作りよ？　売ろうなんて\n考えてないわよ？　たぶんね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110301, 0.0, 2, 0, 3, 0, 4.1, 0, 0, 1, 1, 1103003, 0, "海の砂ってサラサラしてて気持ち\nいいわよね。持ち帰って救護院に\n砂場を作ったりできないかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110301, 0.0, 5, 0, 4, 0, 4.9, 0, 0, 1, 1, 1103004, 0, "子どもたちと賑やかに\n過ごすのはもちろん楽しいけど\nあんたと二人きりで過ごすのも\nなかなかいいわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110301, 0.0, 2, 0, 5, 0, 3.1, 0, 0, 1, 1, 1103005, 0, "あたしの水着をこんな間近で\n見られるなんて、あんた本当に\n得してるわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1103006, 0, "エレガントじゃないかもだけど\n今だけは許してね\nあたし今、とびっきり\n舞い上がっちゃってる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1103007, 0, "夏の日差しって、開放的な\n気分にさせるわよね！\nあたしももう少し\n冒険してみようかしら♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1103008, 0, "海に来るのなんて久しぶりね～\nせっかくのバカンスなんだから\n楽しまなきゃ損よ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1103009, 0, "本当にきれいな海よね\nもし誰もいない浜辺であんたと\n二人っきりだったら…って！\nなんであたしそんなことを！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1103010, 0, "捕まえてみなさ～い♪\nな、なんてことあたしが\nできるわけないじゃない！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110301, 0.0, 1, 0, 6, 0, 1.9, 0, 0, 2, 2, 1103011, 0, "誕生日おめでとう♪\n救護院のみんなもあんたの\n誕生日を祝いたがってるから\n楽しみにしてなさいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1103012, 0, "誕生日おめでとう！\n二人きりでバースデーパーティー\nしましょうか♪　なんてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110401, 0.0, 6, 0, 1, 0, 2.9, 0, 0, 1, 2, 1104001, 0, "へへっ、海だー！\nな、なんだよ、あたしが\nはしゃいじゃおかしいかよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110401, 0.0, 2, 0, 2, 0, 2.6, 0, 0, 1, 1, 1104002, 0, "ビーチで焼きそばもいいよな\nよし！　材料持っていって\n昼飯は作ってやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110401, 0.0, 1, 0, 3, 0, 3.2, 0, 0, 1, 2, 1104003, 0, "暑い日に泳ぐのって最高だよな！\nそうだ、浮き輪引っ張って\n泳いでやろうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110401, 0.0, 5, 0, 4, 0, 3.3, 0, 0, 1, 1, 1104004, 0, "おまえと一緒だと、なんか楽…\nなっ、なんでもねぇ！\n…海だからって、開放的に\nなりすぎるのもよくないな、うん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110401, 0.0, 6, 0, 5, 0, 8.0, 0, 0, 1, 1, 1104005, 0, "ちょっと休憩しようぜ\nお、なんかおまえの手、\nひんやりして冷たいな！\nって…わりぃ、触りすぎたな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1104006, 0, "うぉわっ！？　み、水着がっ！？\nだ、誰も見てねぇ、よな…って\nなんでそこにいやがんだよ！？\n見てんじゃねぇ！　…ったく");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1104007, 0, "ん～、風が気持ちいいな！\nへへっ、おまえと二人で海なんて、\nくすぐったい感じだぜ\nさぁて、まずは何をする？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1104008, 0, "なんか、おまえには恥ずかしいとこ\nばかり見られてる気がするな…\nイヤってわけじゃねぇけどよ\n…って何言ってんだあたしは！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1104009, 0, "不思議なんだけどさ\nおまえといると、時間が\nあっという間に過ぎちまう…\nおまえもか？　…そ、そっか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1104010, 0, "か、かわいい？\nあたしの水着姿が？\nう、うれしくねぇっての！\n尻尾？　振ってねぇ！　見んな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110401, 0.0, 2, 0, 6, 0, 8.7, 0, 0, 2, 1, 1104011, 0, "誕生日なんだってな。そうだな…\n砂浜特訓メニューのプレゼント、\nってのは冗談で、\nあとでカキ氷でもおごってやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1104012, 0, "今日、誕生日なんだろ？\n食材の買い出し、付き合えよ\nあとでなんか作ってやるからさ\nおまえの好きなもの…教えろよな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110501, 0.0, 1, 0, 1, 0, 1.9, 0, 0, 1, 6, 1105001, 0, "元気してるさー？\n私はいつでも元気だよー！\nさーさ、一緒に何するさー？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110501, 0.0, 1, 0, 2, 0, 3.2, 0, 0, 1, 2, 1105002, 0, "なんくるないさー！\n海を見ると叫びたくならないー？\nほらキミも、なんくるないさー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110501, 0.0, 2, 0, 3, 0, 5.7, 0, 0, 1, 1, 1105003, 0, "海は楽しいことでいっぱいよー！\n走って、泳いで、歌って、踊って\n思う存分楽しむさー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110501, 0.0, 1, 0, 4, 0, 8.2, 0, 0, 1, 6, 1105004, 0, "ん～？　どこを見てるのー？\nこの水着が気になるさー？\nじゃあ触ってみるー？　\nほらほら、遠慮はいらないさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110501, 0.0, 5, 0, 5, 0, 3.1, 0, 0, 1, 2, 1105005, 0, "波の音は心地いいさ～\nキミと一緒に聞いてるとなんだか\n胸がドキドキするね～\nキミも？　聞いてみていい～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1105006, 0, "夏の楽しみ方は色々あるけど～\nこうして演奏するのは格別さ～\n聞こえる波のリズムに乗って\n心のままに音楽を楽しむさー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1105007, 0, "あは～♪　楽しいねぇ～！\n夏は体がウズウズするさ～！\nほら、キミも一緒に歌うさ～\n恥ずかしがってちゃダメだよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1105008, 0, "思う存分歌ったあとは\n浜辺を走って、海で泳ぐよー！\n夏は待っててくれないからね～\n今日は私と限界を超えるさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1105009, 0, "今度、キミをテーマに\n一曲作ってみたいさ～\nきっと楽しい曲になるさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1105010, 0, "キミのことを考えて演奏すると\n音がいつもと違う感じになるさー\nなんて言えばいいのかな～…\nとにかく聴いてほしいさー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110501, 0.0, 1, 0, 6, 0, 2.8, 0, 0, 2, 2, 1105011, 0, "ばっちり準備万端さー！\n何って、キミの誕生日だよー！\nさぁ手と手を繋いで、\nレッツダンスさー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1105012, 0, "誕生日、めでたいさ～！\n体が勝手に踊りだすさ～、あっ！\nぶつかってごめんねぇ、でも\nやわいとこだから平気だよねー？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110601, 0.0, 2, 0, 1, 0, 4.2, 0, 0, 1, 1, 1106001, 0, "夏の海は不思議の国どすえ\nうちと一緒に\n楽しく過ごしまひょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110601, 0.0, 1, 0, 2, 0, 5.5, 0, 0, 1, 6, 1106002, 0, "ふう、日差しが暑くて\nかなわんわぁ～\n王子はん、あっちの日陰で\n涼みまへんか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110601, 0.0, 2, 0, 3, 0, 4.1, 0, 0, 1, 1, 1106003, 0, "冷たいスイカ、切ってきましたぇ\nぬるならへんうちに一緒に\n食べまひょ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110601, 0.0, 5, 0, 4, 0, 7.9, 0, 0, 1, 1, 1106004, 0, "夏と言えば、花火どす\n夜空に咲く大輪の花…\nはぁ、王子はんと二人で\n見に行きたいわぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110601, 0.0, 2, 0, 5, 0, 5.0, 0, 0, 1, 6, 1106005, 0, "この水着、\nうちに似合うてますやろか？\n王子はんに見せたくて\n一生懸命選んだんどす♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1106006, 0, "いやぁ、気持ちええわぁ～♪\nこうして海辺で寝転がってると\n夏を全身で感じられそうやわぁ\n王子はんも一緒に、どうやろか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1106007, 0, "ぬいぐるみのみんなも\nお日さま浴びて気持ちよさそうや\n王子はんもこっち来て触ります？\nふかふか、もふもふですよ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1106008, 0, "うちの水着、\nヒラヒラしててかいらしやろ？\nじっくり見てもええんよ？\nもっと近くで、ほら、な♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1106009, 0, "夏の海辺はロマンチックやねぇ\nこんな場所に王子はんと二人きり\nゆったり暮らしたいどすなぁ\n…お城はどこに建てまひょか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1106010, 0, "王子はん、一緒に踊りまへんか？\nマホ姫と王子はん、二人だけの\n海辺の舞踏会や♪\nさぁ、一緒に…くるりんぱ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110601, 0.0, 2, 0, 6, 0, 6.2, 0, 0, 2, 1, 1106011, 0, "お誕生日おめでとうなぁ～\nまだまだ暑いさかい\n今日は、和菓子と冷やし抹茶で\nお祝いしまひょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1106012, 0, "今日は王子はんの誕生日やねぇ\nほんま、めでたい日やわぁ\n魔法でお祝いの花火\nあげるからしっかり見といてな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110701, 0.0, 4, 0, 1, 0, 3.0, 0, 0, 1, 6, 1107001, 0, "制服って、スカートなんですね…\nうう…足元がすーすーして\n落ち着かないです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110701, 0.0, 2, 0, 2, 0, 3.7, 0, 0, 1, 6, 1107002, 0, "私が学院に行くのが心配ですか？\nご安心ください！\nいざとなったら、\n保健室登校しますから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110701, 0.0, 2, 0, 3, 0, 2.7, 0, 0, 1, 6, 1107003, 0, "学院にも植物が…！\nぼっちのままでも、\nお花さんたちに\n話しかければ寂しくないです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110701, 0.0, 2, 0, 4, 0, 6.3, 0, 0, 1, 1, 1107004, 0, "ＢＢ団の特訓が無かったら、\n学院に近づくだけで\n失神していたかも…えへへ♪\nあなたには、とっても感謝です♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110701, 0.0, 5, 0, 5, 0, 7.2, 0, 0, 1, 4, 1107005, 0, "あ、あの…\n私の制服姿はどうでしょうか？\nえ…か、かわいいって…\nあわ、あわわわわ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1107006, 0, "とにかく目立たない！\nそして、常に隅っこに！\nこうすれば、ぼっちの私でも\n平和な学院生活が…ひぃ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1107007, 0, "あ、あの人と一緒の学院…\nなんだかドキドキして、\n授業に集中できな…はうぅ！？\nちゃんと授業も頑張ります～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1107008, 0, "つ、次の授業、団体行動がある…\n保健室に逃げ込むしか…ひぃぃ！\nう、嘘です嘘です！\nそんなこと考えてませんから～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1107009, 0, "きょ、今日は友達運が最高！？\n誰かに話しかけられるかもって…\n早速話しかけられる練習を\n始めないと…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1107010, 0, "きょ、今日こそ、\nあの人と下校を…はわぁ！\nご、ごめんなさい！\n私には大それた願いでした！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110701, 0.0, 2, 0, 6, 0, 6.8, 0, 0, 2, 5, 1107011, 0, "お、おお、お誕生日\nおめでとうございます！\nああ…他の誰かの誕生日を\n祝えるだけでも幸せです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1107012, 0, "お誕生日おめでとうございます！\nこんな日に\nあなたと一緒に居られるなんて…\n友達ができるよりうれしいです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110801, 0.0, 2, 0, 1, 0, 6.1, 0, 0, 1, 1, 1108001, 0, "いいけど、\nなんかガッコーとかだと\nうち謎に恐がられてンだよね\nあんたは違うみたいだけどさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110801, 0.0, 1, 0, 2, 0, 5.1, 0, 0, 1, 6, 1108002, 0, "ちょっと小耳に挟んだんだけど、\nあんたお金食べようとしたこと\nあんだって？　ウケる\nアレあんま美味しくないよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110801, 0.0, 4, 0, 3, 0, 3.0, 0, 0, 1, 2, 1108003, 0, "いらっしゃいませぇ～！\n…や、なんかバイト先で愛想よく\nしろとか言われて。はーマジだる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110801, 0.0, 2, 0, 4, 0, 3.4, 0, 0, 1, 6, 1108004, 0, "なんてーか、あんたのバイブス\nわりと悪くないんじゃない\n知んないけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110801, 0.0, 5, 0, 5, 0, 3.1, 0, 0, 1, 6, 1108005, 0, "うわ、きも…\n何カッコつけてんの\n不覚にも一瞬キュンって\n来ちゃったじゃん…うち、きもっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1108006, 0, "夜っていいよね。なんてゆか…\n色々見えすぎないカンジとか\n言えないこと言えちゃうノリとか\nあと…なんかカッコいいじゃん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1108007, 0, "何。あんたまた道に迷ってんの\nしゃーないなー、送ってくよ\nいや、礼とかいいって、べつに\nなんかそんな気分なんだよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1108008, 0, "夜に佇むミステリアスな少女…\nどーよコレ。カンジ出てない？\nただの夜勤休憩中なんだけどさ\nいいの、こういうのは気分だから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1108009, 0, "あのさ。あんたのことだから\nべつに意味とか無いンだろうけど\n無言で人のカオ見んのやめて\nなんか謎にモジついて来るから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1108010, 0, "あんたが？　送ってくれんの？\n何ソレ、誰が誰に言ってんのよ\n夜道は危ないって？　ウケる\nちょっとカッコいいじゃん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110801, 0.0, 5, 0, 6, 0, 9.3, 0, 0, 2, 1, 1108011, 0, "あのさ。弟たちに作ったケーキ、\n余ってるから食べてくんない\nべつに無理にとは言わないけど\nあー…あと、おたおめ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1108012, 0, "いいけど、あんた、\n今日が誕生日なんだって？\nおめでと。よかったね\nキラーペンシルあげるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110901, 0.0, 1, 0, 1, 0, 2.8, 0, 0, 1, 4, 1109001, 0, "なんか楽しいことないです？\n楽しいだけで生きていたい！\n楽しい為なら苦労もできる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110901, 0.0, 6, 0, 2, 0, 8.1, 0, 0, 1, 1, 1109002, 0, "せんぱーい！\n違うな…せ～んぱいっ！\nよし、こっちの方がかわいい\nあ、呼んだだけで用はないです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110901, 0.0, 1, 0, 3, 0, 6.7, 0, 0, 1, 6, 1109003, 0, "え、先輩こんな簡単なことすら\nできないんですか？\nすごいな…すご…\nえっ、大丈夫ですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110901, 0.0, 4, 0, 4, 0, 4.9, 0, 0, 1, 6, 1109004, 0, "今更なんですけど先輩って\n見た目パッとしないですよね\nうーん…もうちょっと\n内面が滲み出るといいですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110901, 0.0, 3, 0, 5, 0, 11.6, 0, 0, 1, 5, 1109005, 0, "ちょっと聞きたいんですけど\nえっと、もしかしてアレです？\n先輩、私に気があったりします？\n違います？　違う？　違うのかよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1109006, 0, "かわわなパフェと無敵の美少女！\nうーん映えるなー絵になるなー\nあ、先輩、悪いんですけど少し\n見切れてもらってていいです？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1109007, 0, "この後カラオケでも行きます？\nチエルが歌うんで、先輩には\nちーえるフゥ！　ちーえるフゥ！\nって跳ね散らかす係をあげますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1109008, 0, "ちぇる～ん♪　ちぇる　ちぇぱら\nちぇらるれろ　ちぇるぽぱっぴ？\nなーんて、嘘ですよー。えへっ☆\nいやあアオハルって感じですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1109009, 0, "ハイ、あーん？　…うわキモ\nなに本当に口開けてるんです、\n冗談に決まってるじゃないですか\n…もう、一口だけですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1109010, 0, "先輩って、こんな顔のいい後輩と\n二人きりで何も思わないんです？\nいえ、それが先輩のいいとこです\nけど…え、まじ全然ないです？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110901, 0.0, 1, 0, 6, 0, 7.5, 0, 0, 2, 2, 1109011, 0, "お誕生日おめでとうございます！\n可愛い後輩から可愛い笑顔を\nプレゼント！　ちぇる～ん☆\n良かったですね、一生ものですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 110931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1109012, 0, "先輩、ハピバ！\nプレゼントは何がいいです？\nちなみにチエルは可愛いアクセ、\n９月１５日生まれちゃんでーす！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111001, 0.0, 2, 0, 1, 0, 10.2, 0, 0, 1, 1, 1110001, 0, "気付き、学び、悟り\n先人の遺した書にも\n日々の生活にもそれはある\n共に高め合おうじゃないか、同志");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111001, 0.0, 4, 0, 2, 0, 7.0, 0, 0, 1, 6, 1110002, 0, "この制服…袖や丈の余剰が\n気にならないと言えば嘘になる\n入学当時の成長見込みから、\n多少の下方修正が必要なようだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111001, 0.0, 1, 0, 3, 0, 6.7, 0, 0, 1, 2, 1110003, 0, "ぼくは本質的に文系なのでね、\n余白や行間を愛すのだよ\nゼロかイチかの理系思考に\nあまり浪漫は感じないのさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111001, 0.0, 2, 0, 4, 0, 5.4, 0, 0, 1, 6, 1110004, 0, "ふむ…そろそろ睡眠を\n取ろうと思っていたのだが、\n君が話相手になってくれるなら\n起きているのもやぶさかではない");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111001, 0.0, 6, 0, 5, 0, 7.8, 0, 0, 1, 2, 1110005, 0, "学びは最高の快楽だよ\nそれは脳を耕し、心を潤す\n差しあたっては…そうだな、\n君のことをもっと知りたい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1110006, 0, "行儀が悪い？　なに、寝転がって\n読もうが背筋を正して読もうが、\n頭に入れてしまえば同じ知識さ\n君も隣りで横になりたまえよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1110007, 0, "読書は孤独なスポーツだからな\n存外と体力を消耗するのだよ\nそこで君に腓腹筋のマッサージを\n依頼する。ほれほれ、揉みたまえ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1110008, 0, "あまりここから出たくはないが…\n信頼する君が請うなら仕方ない\nそこに学びがあると云うならば、\nぼくは書を閉じ、扉を開けよう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1110009, 0, "ぼくにとって失いがたい学びは、\n君たちとの青春だ。世界の真理\nより大切なのさ。ことに生まれて\n初めて友達になってくれた君がね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111001, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1110011, 0, "生命とは実に尊い\n存在自体が億分の一の奇跡だ\nつまり端的に換言すれば、\n誕生日おめでとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1110012, 0, "ハッピーバースデー\n今日は君がこの世に\n生まれ落ちた記念日らしいな\nぼくの情報は正しいんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111101, 0.0, 6, 0, 1, 0, 4.9, 0, 0, 1, 2, 1111001, 0, "ハロウィンですね…\nみんな仮装して、楽しそうです\n…怖いわけじゃありませんよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111101, 0.0, 6, 0, 2, 0, 6.0, 0, 0, 1, 4, 1111002, 0, "カボチャがいっぱいです…\nみんな顔があって、うぅ…\nな、なんで被ってるんですか！\nビックリさせないでください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111101, 0.0, 5, 0, 3, 0, 8.7, 0, 0, 1, 1, 1111003, 0, "ど、どうでしょうか、この衣装…\n私は子供っぽいと思うんですけど\n…似合ってる？　そ、そうですか\nだったら…よかったです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111101, 0.0, 6, 0, 4, 0, 11.5, 0, 0, 1, 1, 1111004, 0, "あなたは仮装しないんですか？\nどんな衣装が似合うか、ですか？\nえっと…う～ん…吸血鬼さん？\nあ、血は吸わないでくださいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111101, 0.0, 5, 0, 5, 0, 6.0, 0, 0, 1, 6, 1111005, 0, "さっきからどうして\n頭を撫でてるんですか！\nかわいいから、って…\nもう！　もう～～～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1111006, 0, "お菓子、ありますか？\nない？　だったらイタズラですね\nか、噛みついちゃいますから！\nえ…えーいっ！　かぷっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1111007, 0, "私は子供じゃないので言いません\n絶対に言いませんから！　え？\nお菓子、おいしいんですか？\n…トリック・オア・トリート…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1111008, 0, "私、カップケーキ作ったんです\n食べますか？　え？\nイタズラの方がいい？\nへんた～い！　もうあげません！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1111009, 0, "ハロウィン、\n一緒に…回りませんか？\n怖いんじゃありません！　もう、\nあなたはしょうがない人ですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1111010, 0, "え？　お菓子、くれるんですか？\n……甘くて、おいしいです\nあ、手についちゃった…ぺろっ\nなっ、なに見てるんですかぁっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111101, 0.0, 6, 0, 6, 0, 7.1, 0, 0, 2, 2, 1111011, 0, "あなたのお誕生日…これ、どうぞ\n私が作ったカップケーキです\nあっ、そんなに急いで食べたら\nポロポロこぼしちゃいますよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1111012, 0, "お誕生日おめでとうございます\nこれ、パンプキンケーキです\nカボチャは少し苦手ですけど、\n頑張って味見もしたので…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111201, 0.0, 6, 0, 1, 0, 5.5, 0, 0, 1, 2, 1112001, 0, "ハロウィンって\nいくらいたずらしても\n怒られないから、最高だよね！\nえ？　違うの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111201, 0.0, 1, 0, 2, 0, 1.6, 0, 0, 1, 2, 1112002, 0, "ハッピーハロウィン！\nにいちゃんにいたずらできて\nお菓子ももらえるなんて、ミソギ\n本当にハッピーだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111201, 0.0, 2, 0, 3, 0, 4.8, 0, 0, 1, 1, 1112003, 0, "トリックオアトリート！\nお菓子をくれなきゃいたずら\nするぞ！　ミソギはお菓子\nもらってもするけどね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111201, 0.0, 1, 0, 4, 0, 3.1, 0, 0, 1, 6, 1112004, 0, "にいちゃんはハロウィンに\nいたずらしないの？　しないなら\nミソギがにいちゃんのかわりに\nいたずらしておくね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111201, 0.0, 1, 0, 5, 0, 5.1, 0, 0, 1, 2, 1112005, 0, "にいちゃん見て見て！\nミソギ、こんなにたくさん\nお菓子もらっちゃった！\n一緒に食べよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1112006, 0, "ハッピーハロウィン！\nお菓子のお礼にとびっきりの\nいたずらをプレゼントだよ！\nちゃんと受け取ってね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1112007, 0, "あはははは！\nみんな驚いてる驚いてる！\nいたずらって本当に楽しいね！\nえ？　少し手加減しなきゃだめ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1112008, 0, "走り回ってたら\nお腹空いてきちゃった…\nもらったお菓子、少しだけ\nつまみ食いしちゃおうかな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1112009, 0, "にいちゃんはいっつも\nミソギと一緒にいてくれるよね！\nにいちゃんがいるからミソギは\n安心していたずらできるんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1112010, 0, "いろんな人にいたずらするのも\n楽しいけど\nにいちゃんにいたずらするのが\n一番楽しいなー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111201, 0.0, 1, 0, 6, 0, 2.7, 0, 0, 2, 2, 1112011, 0, "誕生日おめでとう、にいちゃん！\nミソギからのお祝いはー、んー\nやっぱりいたずらかな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1112012, 0, "ハッピーバースデーにいちゃん！\nいっつもミソギと遊んでくれて\nありがとう！\nこれからも一緒に遊ぼうね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111301, 0.0, 1, 0, 1, 0, 4.2, 0, 0, 1, 3, 1113001, 0, "お菓子をくれなきゃ\nいたずらしちゃうよ～！\nえっと…どんないたずらをすれば\nいいのかな～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111301, 0.0, 1, 0, 2, 0, 1.4, 0, 0, 1, 2, 1113002, 0, "おにいちゃん♪\nハロウィンのパレードで\nぴょんぴょんってしよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111301, 0.0, 2, 0, 3, 0, 5.2, 0, 0, 1, 1, 1113003, 0, "ハロウィンって楽しいね～\nミミも、みんなが楽しそうだと\nもっとも～っと\n楽しくなってきちゃうんだ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111301, 0.0, 5, 0, 4, 0, 3.1, 0, 0, 1, 2, 1113004, 0, "トリックオアトリート～！\nえへへ、おにいちゃん\nお菓子ちょうだい♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111301, 0.0, 2, 0, 5, 0, 6.0, 0, 0, 1, 1, 1113005, 0, "こちょこちょこちょ…えへへ～\nハロウィンのいたずらだよ～♪\nトリックオアトリート！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1113006, 0, "にこにこ笑顔でおさんぽすれば\nハロウィンの魔法がうまれるの～\nえへへ、ハッピーハロウィン♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1113007, 0, "ふぁぁ…ハロウィンって\nとっても楽しいね～！\nねえねえ、おにいちゃんも\nいっしょにパレードしよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1113008, 0, "ハッピーハロウィン♪\nおにいちゃん\nミミのハロウィンのおようふく\nにあってるかな～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1113009, 0, "おにいちゃんと\nいっしょのハロウィン\nミミず～っとおぼえてるからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1113010, 0, "毎日がハロウィンならいいのに…\nだってだって\nそしたらミミ、おにいちゃんと\nず～っといっしょに遊べるもん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111301, 0.0, 2, 0, 6, 0, 4.1, 0, 0, 2, 6, 1113011, 0, "ハッピーハロ…じゃなくて\nハッピーバースデイ！\nおにいちゃん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1113012, 0, "おにいちゃん\nお誕生日おめでとう～！\nミミね、おにいちゃんのこと\nだーいすきだよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111401, 0.0, 4, 0, 1, 0, 2.7, 0, 0, 1, 1, 1114001, 0, "ルナ、おともだちが欲しいんだ\nさみしいのは嫌いだもん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111401, 0.0, 2, 0, 2, 0, 2.9, 0, 0, 1, 1, 1114002, 0, "ルナ、優しい人好きだなー\nおにいさんは優しいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111401, 0.0, 4, 0, 3, 0, 2.9, 0, 0, 1, 1, 1114003, 0, "パパとママはここにいるよ\nでも、喋ってくれないんだ\nさみしいなぁ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111401, 0.0, 6, 0, 4, 0, 3.0, 0, 0, 1, 2, 1114004, 0, "おにいさんの手ってあったかい\nルナの手、冷たい？\nもっとぎゅってしたら、\nあったかくなるかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111401, 0.0, 5, 0, 5, 0, 6.7, 0, 0, 1, 2, 1114005, 0, "ルナ、もっとおっきくなるよ\nお料理もできるようになるよ\nえへへ…ルナ、いいこ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1114006, 0, "ルナね、サンドイッチ好きなんだ\nおにいさんは好き？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1114007, 0, "ルナ、お外は好きじゃないんだ\nみんな、一人じゃなくて…\nルナだけ一人だから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1114008, 0, "おにいさんは何が好き？\nルナはお人形さんと、\nサンドイッチと…\nあと、おにいさん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1114009, 0, "パパとママみたいになれたら、\nきっと楽しいと思うんだ\nだから待っててね、おにいさん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1114010, 0, "おにいさんはルナのこと好き？\nルナはおにいさんのこと好き！\nだから嫌いにならないでね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111401, 0.0, 2, 0, 6, 0, 1.7, 0, 0, 2, 6, 1114011, 0, "お誕生日だね！\nおにいさんが一番！\nふふっ、うれしいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1114012, 0, "お誕生日なの？　すごいすごい！\nルナ、お誕生日大好き！\nだからいっぱいお祝いしようね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111501, 0.0, 1, 0, 1, 0, 6.1, 0, 0, 1, 2, 1115001, 0, "メリークリスマス☆\nクリスティーナさんと一緒に\n愉しい聖夜を過ごそうじゃないか\n問答無用だ、こっちへ来い♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111501, 0.0, 4, 0, 2, 0, 6.4, 0, 0, 1, 6, 1115002, 0, "この飲み物が気になるのか？\n飲ませてやってもいいが、\n後悔はするなよ？\nなんだ、やめるのか…残念だ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111501, 0.0, 1, 0, 3, 0, 8.4, 0, 0, 1, 3, 1115003, 0, "さぁ坊や、ワタシを愉しませろ！\n特別な日だ、くだらんことを\nしたら命はないと思え！\nまぁ、優しめに採点してやるさ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111501, 0.0, 2, 0, 4, 0, 6.1, 0, 0, 1, 6, 1115004, 0, "おや、震えているじゃないか\n寒いだと？　この軟弱者め\nだったらこのコートに入るか？\nふっ、遠慮しなくていいんだぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111501, 0.0, 2, 0, 5, 0, 3.1, 0, 0, 1, 1, 1115005, 0, "クリスマスをオマエと一緒に\n過ごすとはな…\nまぁ悪い気分じゃないさ\n坊やは見ていて愉しいからな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1115006, 0, "どうだい、坊や\n聖夜に相応しい絶景だろう？\n感謝するがいいよ。ここに連れて\n来たのはオマエが初めてだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1115007, 0, "こちらを見つめてどうした？\nいいや言わんでも分かるさ\n景色よりワタシの方が綺麗だと\n言いたいんだろう？　あっはは☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1115008, 0, "体が冷えてきたな…\nよし坊や、剣を抜け！\nこういう時は、剣と剣をぶつけて\n温まろうじゃないか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1115009, 0, "クリスマスを誰かと過ごすのも\n悪くはないな…\nそれがワタシを愉しませてくれる\n男なら、なおさらだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1115010, 0, "愉快なクリスマスだったな\nまた次も、よろしく頼むぞ？\nふふっ、忘れるなよ？\nワタシと交わす誓いは絶対だ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111501, 0.0, 1, 0, 6, 0, 6.5, 0, 0, 2, 6, 1115011, 0, "ふぅん。今日が誕生日なのか\nなんだそのもの欲しそうな顔は？\nあはは、プレゼントが欲しいなら\n贈るに相応しいか示して見せろ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1115012, 0, "オマエが生まれた日を祝し\nクリスティーナさんがプレゼント\nを用意してあげたよ♪\nい～い反応を期待しているぞ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111601, 0.0, 1, 0, 1, 0, 1.7, 0, 0, 1, 2, 1116001, 0, "メリークリスマス！\n私のライブがキミにとって\n最高のプレゼントに\nなったらうれしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111601, 0.0, 6, 0, 2, 0, 4.6, 0, 0, 1, 2, 1116002, 0, "チキンにケーキ…クリスマスの\nごちそうって大好き！\n今日だけは\n食べ過ぎちゃってもいいよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111601, 0.0, 2, 0, 3, 0, 3.0, 0, 0, 1, 1, 1116003, 0, "いつものクリスマスより\n楽しく感じるのは\nキミと一緒だからかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111601, 0.0, 5, 0, 4, 0, 4.1, 0, 0, 1, 6, 1116004, 0, "はぁ～\n今日はすごく寒いね\n…ねえ、もう少しだけ\nそっちに行ってもいいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111601, 0.0, 2, 0, 5, 0, 3.7, 0, 0, 1, 1, 1116005, 0, "どうかな？\nこの服いい感じでしょ♪\nキミには一番かわいい私を\n見てほしいんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1116006, 0, "みんなが笑顔になるクリスマスが\n大好きだよ！\nこの思いは最高の\nパフォーマンスでお返しするね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1116007, 0, "私のライブでクリスマスを\n盛り上げちゃうよー♪\nサンタもアイドルも夢を届ける\nお仕事だもんね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1116008, 0, "メリークリスマス！\nイルミネーションよりも\n輝いてみせるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1116009, 0, "クリスマスソングの別バージョン\nキミをイメージして作ったんだ\nちょっと恥ずかしいけど\n聴いてほしいな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1116010, 0, "これまでの人生で一番の\nクリスマスだよ！\nキミと過ごした最高の一日を\n絶対に忘れないからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111601, 0.0, 1, 0, 6, 0, 1.6, 0, 0, 2, 2, 1116011, 0, "ハッピーバースデー！\nはい、プレゼント\n私のサイン入りだよ\n大事にしてね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1116012, 0, "誕生日おめでとう！\nバースデーソングでお祝いするよ！\nキミだけのためのライブ、\n楽しんでってね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111701, 0.0, 1, 0, 1, 0, 3.1, 0, 0, 1, 4, 1117001, 0, "『聖なる夜』というのは\n気に入らんが、\nせっかくの夜の宴じゃ\n楽しませてもらおうかのう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111701, 0.0, 5, 0, 2, 0, 4.1, 0, 0, 1, 2, 1117002, 0, "わらわの美貌を\n引き立てる良い衣装じゃな\n…これで、あやつを\n誘惑するのもよいかもしれぬ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111701, 0.0, 2, 0, 3, 0, 3.7, 0, 0, 1, 1, 1117003, 0, "今のわらわは\nサンタクロースじゃからな\n聖夜を盛り上げるために、\n協力してやろうではないか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111701, 0.0, 4, 0, 4, 0, 5.2, 0, 0, 1, 2, 1117004, 0, "聖夜の宴ならば、\nチキンとケーキを食べねばな♪\n…な、なんじゃその目は？\nべ、別に浮かれてなどおらぬわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111701, 0.0, 5, 0, 5, 0, 5.0, 0, 0, 1, 1, 1117005, 0, "この格好でプレゼントを\n用意せぬのは無粋というもの…\nし、仕方ないから、あやつにも\nプレゼントを用意してやるか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1117006, 0, "ク、クリスマスはサンタが\nプレゼントを贈る日で、\n今のわらわはサンタだから…\nと、とにかく受け取れ馬鹿者～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1117007, 0, "アカリに借りた本によると…\nイリヤサンタが、キミに\nプレゼントをあげちゃうぞ～☆\n…できるかこんなの～～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1117008, 0, "夜を統べる者たる、このわらわが\nプレゼントをくれてやろう！\nありがたく受け取るがよい！\nうむ、これなら完璧じゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1117009, 0, "このプレゼント、あやつは\n喜んでくれるじゃろうか…\nうう…なんだか\nドキドキしてきたのじゃ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1117010, 0, "…じ、時間じゃな！\nプレゼントの準備…よし！\nこ、ここ、心の準備…よ、よし！\nあとはあやつに渡すだけじゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111701, 0.0, 2, 0, 6, 0, 4.8, 0, 0, 2, 6, 1117011, 0, "たしか、今日はお主の\n誕生日であったな\nならばこのわらわが、\n祝ってやろうではないか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1117012, 0, "今日はあやつの誕生日…！\nプレゼントを渡しつつお礼を…\nえ…お主、いつからそこに…\nい、今すぐ忘れろ馬鹿者ぉ～！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111801, 0.0, 1, 0, 1, 0, 5.9, 0, 0, 1, 2, 1118001, 0, "あけましておめでとうございます\n今年もたくさんおいしいものを\nいっぱい食べましょう！\n新年最初の約束です☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111801, 0.0, 2, 0, 2, 0, 3.6, 0, 0, 1, 1, 1118002, 0, "あなたはもう今年の抱負を\n決めていますか？\nわたしは…みんなが幸せに\n暮らせるような世界を、です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111801, 0.0, 2, 0, 3, 0, 3.0, 0, 0, 1, 6, 1118003, 0, "わたしの着物、似合ってますか？\n髪もいつもと違うんです\nもっと近くで見てください\n特別ですよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111801, 0.0, 2, 0, 4, 0, 3.7, 0, 0, 1, 6, 1118004, 0, "あっ！　あっちで餅つきを\nやってるみたいですね！\n行きましょう！　自分でついた\nお餅は格別ですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111801, 0.0, 7, 0, 5, 0, 5.4, 0, 0, 1, 6, 1118005, 0, "広場で凧揚げしてますよ！\n凧揚げ…たこあげ…タコ揚げ…\nなんだかお腹が空いて\nきちゃいましたね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1118006, 0, "おせち、心を込めて作りました♪\n見た目はちょっとアレかも\nしれませんが、味は保証します！\nはい、どうぞ召し上がれ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1118007, 0, "おせちには魔除けの意味も\nあるみたいなんですけど\n素材に魔物を使うのは…\n魔を倒してるからありですよね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1118008, 0, "今年もこうやってみんなで\nおいしいものをたくさん\n食べて行きたいですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1118009, 0, "こうして料理を食べるのは\nいつものことですけど、お正月を\n一緒に過ごしてるって思うと\nなんだか照れちゃいますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1118010, 0, "毎年こうやってあなたに\n手作りおせちを食べさせたいって\n思うのは…欲張りでしょうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111801, 0.0, 1, 0, 6, 0, 7.1, 0, 0, 2, 2, 1118011, 0, "誕生日おめでとうございます！\n縁起のいい食べ物をたくさん\n揃えたので一緒に食べましょう！\n魔物は…ヒミツです☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1118012, 0, "お誕生日おめでとうございます\n今年もこうしてあなたと過ごせて\n本当に幸せです☆\n来年もお祝いさせてくださいね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111901, 0.0, 2, 0, 1, 0, 5.2, 0, 0, 1, 1, 1119001, 0, "主さま、新年あけまして\nおめでとうございます\n今年も主さまと一緒に\n歩めることを幸せに思います");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111901, 0.0, 1, 0, 2, 0, 3.9, 0, 0, 1, 4, 1119002, 0, "まだまだ寒い日が続きますね\n主さま、お手手を繋ぎ\n暖めあって歩くといたしましょう\nぎゅっと握ってくださいまし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111901, 0.0, 5, 0, 3, 0, 8.0, 0, 0, 1, 4, 1119003, 0, "たまの里帰りもいいものですが\n主さまと離れるのは心細いです\n次の里帰りは一緒に、というのは\n欲張りでしょうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111901, 0.0, 1, 0, 4, 0, 4.9, 0, 0, 1, 6, 1119004, 0, "甘酒には疲労回復の効果が\nあるとのことです。お疲れの際は\nわたくしがいつでもフーフーして\n飲ませて差し上げます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111901, 0.0, 5, 0, 5, 0, 6.7, 0, 0, 1, 1, 1119005, 0, "主さまにお仕えするという至高の\n初夢を見ることができました\n主さまの初夢にも、わたくしが\n出ていたならいいのですが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1119006, 0, "一つ一つの動きに、主さまへの\n想いを込めております\n拙い舞ではございますが\n見守っていてくださいまし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1119007, 0, "わたくしの想いに反応して\n精霊たちも楽しそうに\n舞っておりますね。主さまも\nお楽しみいただけておりますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1119008, 0, "風情のある舞台で主さまと\n二人きりで過ごせる…\nわたくしにとって最高の\nお年玉でございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1119009, 0, "今年の抱負でございますか？\nそれはもちろんこれまで通り…\nいえ、これまで以上に主さまを\nお導きすることでございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1119010, 0, "主さまを想って舞っていると\n心も体も暖かくなります\nこの熱を主さまと分け合うことが\nできればいいのに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111901, 0.0, 5, 0, 6, 0, 5.9, 0, 0, 2, 2, 1119011, 0, "お誕生日おめでとうございます\nもし…もし主さまがよろしければ\n二人きりで過ごすことを\nお許しいただきたく…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 111931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1119012, 0, "今日は主さまにとって\nおめでたい一日。祝いの舞を\n奉納させてくださいまし\n心からの祝福をお贈りいたします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112001, 0.0, 2, 0, 1, 0, 4.0, 0, 0, 1, 1, 1120001, 0, "なに？　あたしと羽根つきで\n勝負したいの？\n手加減しないからね！\n墨まみれにしてあげるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112001, 0.0, 1, 0, 2, 0, 4.6, 0, 0, 1, 5, 1120002, 0, "あけましておめでとう\nまぁ今年もよろしくね\nまた騒がしい一年に\nなりそうだけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112001, 0.0, 6, 0, 3, 0, 7.8, 0, 0, 1, 4, 1120003, 0, "みんな年の始めから元気ね\nあたしは家で静かに過ごしたい…\nい、いや、別にあんたと居るのが\n嫌ってワケじゃないから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112001, 0.0, 2, 0, 4, 0, 5.5, 0, 0, 1, 3, 1120004, 0, "着物が似合ってる？\nいちいち言わなくていいわよ！\nけど、見る目あるじゃない\nふふん。いい気分だわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112001, 0.0, 5, 0, 5, 0, 5.9, 0, 0, 1, 1, 1120005, 0, "おみくじ引いたんだけど、\n待ち人はすぐそばに、だって\nまさかあんたのこと…？\nあ、あっちいきなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1120006, 0, "せっかくだからいろいろ願い事\nしたいわよね。あんたは何を\n願うの？　あたしのこと？\nふ、ふーん。そ、そう…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1120007, 0, "夜の参道ってのも悪くないわね\n一緒なら怖くないし…って！\n暗いの平気だから！\n勘違いしないでよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1120008, 0, "ほら、行くわよ\n疲れたっていうなら\n手繋いでやっても…て、本当に\n繋ぐ気？　べ、別にいいけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1120009, 0, "神殿にお参りするだけなのに\n特別な気がするのは\n着物のおかげ？　それとも…\nあんたと一緒だから？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1120010, 0, "すごく静かね…まさか道間違えて\nないわよね？　まぁ、のんびり\n行くのも悪くないかもね。二人で\nゆっくり歩きましょ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112001, 0.0, 4, 0, 6, 0, 5.7, 0, 0, 2, 1, 1120011, 0, "ハッピーバースデー\nケーキ買ってきたから\n一緒に食べましょう\n変なモノは入ってないわよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1120012, 0, "お誕生日おめでとう\nい、一応プレゼントも用意して\nあるのよ？　\nありがたく受け取りなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112101, 0.0, 1, 0, 1, 0, 5.9, 0, 0, 1, 2, 1121001, 0, "あなたもお参りに来たんですね\n私は見ての通り、巫女さんです！\n年末から新年にかけて、神殿の\nお仕事をお手伝いしてるんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112101, 0.0, 1, 0, 2, 0, 2.7, 0, 0, 1, 2, 1121002, 0, "この服、かわいいですよね♪\n巫女さんの服を着る機会なんて\n普段はありませんから\n新鮮で楽しいんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112101, 0.0, 2, 0, 3, 0, 5.2, 0, 0, 1, 1, 1121003, 0, "神殿には色んなお守りがあるので\nぜひ見ていってくださいね\n私は家内安全のお守りを\n買っておきました～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112101, 0.0, 5, 0, 4, 0, 4.5, 0, 0, 1, 4, 1121004, 0, "はぁ～…寒くて手が冷えますね\n…はわっ！？　あ、あのぉ…\nい、いきなり手をギュって\n握られると、恥ずかしいです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112101, 0.0, 6, 0, 5, 0, 3.4, 0, 0, 1, 1, 1121005, 0, "もしよければ、甘酒を飲みます？\n…え、私と分け合いっこ！？\nあ、ああ、あの、仕事中なので！\nわわ、私は、だ、大丈夫れしゅ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1121006, 0, "おはようございますっ！\n今日は参拝に来たんですか？\n私は神殿のお掃除中です\nほら、きれいになりましたよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1121007, 0, "もうすぐ休憩時間なので\n私もご一緒していいですか？\nこの神殿の見どころを\n色々教えて頂いたんです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1121008, 0, "それじゃあ、張り切って続きを…\nえ、危ない？　何がですか？\n…はわっ！　崩れちゃう！？\nわ、ちょ、まっ、きゃー！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1121009, 0, "恋愛運に書かれていたんですけど\n今年は「身近なところに縁あり」\nらしくて…あっ！？　い、いえ！\nべ、別に深い意味はないですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1121010, 0, "あなたの恋愛運はどうでした？\n…え？　失敗から始まる縁？\nそれってもしかして、私と…\nはっ！　な、なんでもないです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112101, 0.0, 6, 0, 6, 0, 5.1, 0, 0, 2, 2, 1121011, 0, "お誕生日おめでとうございます！\n私もあなたにプレゼントを…\nって、あれ…？　ない！？\nわ、忘れちゃいましたぁ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1121012, 0, "今日はあなたのお誕生日ですよね\nおめでとうございます！\nこれから良い一年を過ごせる\nおまじないをかけちゃいますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112201, 0.0, 4, 0, 1, 0, 6.4, 0, 0, 1, 6, 1122001, 0, "へ、変身したら、\n髪型まで変わってる！？\n一体どういう理論で…はあ\nもう、考えないようにしよう…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112201, 0.0, 5, 0, 2, 0, 5.9, 0, 0, 1, 1, 1122002, 0, "魔法探偵ミスティ★カスミか…\nちょっとだけ楽しいかも♪\n…って、助手くん！？　\nい、今のは忘れてくれたまえ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112201, 0.0, 1, 0, 3, 0, 5.1, 0, 0, 1, 4, 1122003, 0, "うぅ、周りの視線が気になる…\nん…助手くん、前に立ってくれる\nのかい？　ありがたいよ\nふふ。お姫様気分だね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112201, 0.0, 4, 0, 4, 0, 3.0, 0, 0, 1, 1, 1122004, 0, "ミスティ、セオリー、\nトゥルーリィ！\n…はぁ。だんだんと\n慣れてきているのが複雑だね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112201, 0.0, 1, 0, 5, 0, 6.3, 0, 0, 1, 5, 1122005, 0, "歩いているだけで子供に囲まれる\nなんて…魔法少女って人気なんだ\n助手くん、君も魔法少女のこと\n好きだったりするのかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1122006, 0, "ミスティセオリートゥルーリィ！\nマジカルリボン、スパイラル！\n魔法探偵ミスティ★カスミ\n魔法の力で捜査開始！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1122007, 0, "…や、やっぱり恥ずかしいね\nあまりジロジロ見てはダメだよ？\nこ、こら！　近い、近いったら！\nもう、これは現行犯で確保だね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1122008, 0, "体の奥からすごい力を感じるよ\nけれど、この格好で動き回ると\nその、スカートとかヒラヒラして\n落ち着かないんだよね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1122009, 0, "助手くん、どうかな？\n魔法少女らしく見えるかね？\nこんなはじけた姿、君の前でしか\n見せられないよ。まったく…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1122010, 0, "魔法少女と、パートナー\n探偵と、助手\n私たちはどうやら、離れられない\n運命のようだね。…ふふっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112201, 0.0, 2, 0, 6, 0, 5.9, 0, 0, 2, 1, 1122011, 0, "今日は助手くんの誕生日だよね\n魔法少女の役目を\n早く終わらせてくるから、二人で\n一緒にお祝いしようじゃないか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1122012, 0, "…魔法の力で捜査開始！\nた、誕生日のお祝いとして、\n君のためだけに変身して\nみたんだけど…ど、どうかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112301, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1123001, 0, "私、魔法少女になったんです\n私もお姉ちゃんみたいに\nたくさん人助けできたらいいな\nなんて…えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112301, 0.0, 2, 0, 2, 0, 5.4, 0, 0, 1, 1, 1123002, 0, "魔法少女になってから\nあまり体力のことで\n悩まなくなったんです\nなんだか出来すぎた物語みたい…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112301, 0.0, 5, 0, 3, 0, 2.8, 0, 0, 1, 2, 1123003, 0, "魔法少女って憧れだったんです\n見た目は、その…\nちょっと恥ずかしいですけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112301, 0.0, 4, 0, 4, 0, 5.0, 0, 0, 1, 1, 1123004, 0, "早く一人前の魔法少女に\nならなきゃって思うんですけど…\n読書の時間がなくなっちゃう…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112301, 0.0, 2, 0, 5, 0, 5.0, 0, 0, 1, 6, 1123005, 0, "ランドソルの街を歩いてたら\n子供たちに囲まれちゃいました\nあの子たちを守るためにも\nもっともっと頑張らなくちゃ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1123006, 0, "ピュアなハートにロックオン\nマジカルアロー、クリティカル！\n魔法狩人ピュアリー★シオリ\nあなたの心に愛の矢を！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1123007, 0, "ランドソルの平和は私が守ります\n体からあふれる愛と希望の力…\nこの一射に愛をこめて！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1123008, 0, "ここは私に任せてください！\nアンチビーストを倒して\nみなさんに笑顔を取り戻します！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1123009, 0, "魔法少女になったおかげで、少し\n自信がついたかもしれません\nこの街を守る覚悟ができました\nあなたのことも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1123010, 0, "アンチビーストが街を襲うかぎり\n魔法少女は戦い続けます\nそばにいてくださいね…あなたの\n存在が、私の力になりますから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112301, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1123011, 0, "今日、お誕生日ですよね\nふふ、ちゃんと覚えてますよ\n魔法で盛大にお祝いしなきゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1123012, 0, "お誕生日おめでとうございます\n今後もお祝いさせてくださいね\nこれからも、ずっと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112401, 0.0, 2, 0, 1, 0, 3.8, 0, 0, 1, 1, 1124001, 0, "千里の道も一歩から、ですよね\n素敵なアイドルを目指して\nレッスン頑張ります！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112401, 0.0, 4, 0, 2, 0, 4.6, 0, 0, 1, 6, 1124002, 0, "冒険者としての実力、ですか？\n実はあんまり自信がないんです\n運動神経は普通ですし\nそこまで活躍は…うぅ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112401, 0.0, 1, 0, 3, 0, 2.6, 0, 0, 1, 2, 1124003, 0, "今からダンスの練習なんです！\nどこかおかしいところがないか\n見てもらってもいいですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112401, 0.0, 2, 0, 4, 0, 9.6, 0, 0, 1, 5, 1124004, 0, "鼻歌を口ずさむ時、たまに…\nすごく懐かしい、大切な曲を\n歌ってる気持ちになるんです\n不思議ですよね、えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112401, 0.0, 4, 0, 5, 0, 3.0, 0, 0, 1, 3, 1124005, 0, "やぁー！　とぉー！\nう～ん、剣を振る時って\nもっと頭から叩くように\nする方がいいんでしょうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1124006, 0, "これ、夢じゃないんですよね…？\n私にこんな力があったなんて…\nまるで、魔法をかけられた\nおとぎ話の主人公みたいです…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1124007, 0, "魔法でお姫様に変身…じゃなくて\nなんだか勇者になったみたいです\nでも、自分の力で切り開くって\n意味では、ピッタリかもですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1124008, 0, "私は、普通の女の子ですけど…\n自分らしい、私だけのキラキラが\nあるんだって胸を張って言えます\nだって私は、アイドルですから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1124009, 0, "これ、伝説の剣を召喚！\nみたいにできたりしませんかね？\n念じたらもしかして…う～ん…！\n…で、できないみたいです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1124010, 0, "そうだ！　私もリンちゃんみたいな\n必殺技を使うのはどうでしょう？\nそうですね～…勇気！　元気！\nピンキー・ライブリィ！　…とか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112401, 0.0, 2, 0, 6, 0, 4.7, 0, 0, 2, 1, 1124011, 0, "みなさんから、今日があなたの\nお誕生日だって聞きました\nおめでとうございます！\nきっといい一年になりますよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1124012, 0, "お誕生日おめでとうございます！\n色んなことでお世話になったので\nそのお礼も兼ねたプレゼントです\nどうぞ、受け取ってください♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112501, 0.0, 1, 0, 1, 0, 5.4, 0, 0, 1, 6, 1125001, 0, "アイドルに向いてるのかどうか\n自分じゃよく分からないけど…\nやると決めたからには頑張るよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112501, 0.0, 6, 0, 2, 0, 4.9, 0, 0, 1, 1, 1125002, 0, "冒険者としての腕なら\n結構自信があるんだけどね\nアイドルって全然違うことだし\n今後どうなるか想像できないな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112501, 0.0, 2, 0, 3, 0, 2.8, 0, 0, 1, 1, 1125003, 0, "アイドルは体力も必要…\nなら、剣の素振りでもすれば\n冒険者としての修行にもなるよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112501, 0.0, 3, 0, 4, 0, 11.0, 0, 0, 1, 6, 1125004, 0, "ラズール・レオ！　…違うかな？\nもっとこう、相手を貫くように…\nもう一回！　ラズール・レオ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112501, 0.0, 2, 0, 5, 0, 6.7, 0, 0, 1, 1, 1125005, 0, "ちょっと疲れたし休もうかな…\nキミもチョコレート食べる？\n甘いものを食べると\n元気が出てくるよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1125006, 0, "誰が相手だろうと退かないよ\n私は立ち止まらないって決めた…\nだから、倒して先に進むだけ！\n蒼の剣を受けよ！　はぁーっ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1125007, 0, "私と戦ったのが失敗だったね\n見た目で油断したんだろうけど…\nこう見えても私、結構強いんだ\n自分の甘い判断を恨むんだね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1125008, 0, "…アイドルを目指してるのに\nこんなことしてて大丈夫かな？\nえ、それはそれで需要がある？\n本当かな、だといいんだけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1125009, 0, "そろそろ、何か新しい魔法を\n考えてみるのもアリかもね\n名前は…ラピス、ターコイズ…\nうーん、どういうのがいいかな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1125010, 0, "呪文を唱えてる理由が知りたい？\nなんていうんだろう、魔法の形を\nイメージしやすくなるんだよね\n…それにほら、カッコいいでしょ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112501, 0.0, 1, 0, 6, 0, 7.5, 0, 0, 2, 2, 1125011, 0, "今日、キミの誕生日なんだって？\nみんなから聞いたよ、おめでとう\n今からお祝いするって言ってたし\n一緒に行こうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1125012, 0, "今日はキミの誕生日だよね\nはいこれ、プレゼント\nあんまり大した物じゃないけど…\nお世話になったお礼ってことで");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112601, 0.0, 2, 0, 1, 0, 4.8, 0, 0, 1, 1, 1126001, 0, "冒険とアイドル、どっちも\n全力投球で頑張っちゃうよ！\nミオちゃんの応援よろしく～☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112601, 0.0, 1, 0, 2, 0, 5.9, 0, 0, 1, 2, 1126002, 0, "ほらほら、早く出かけようよ！\nワクワクの冒険が待ってるぞ～！\nあ、その後はレッスンするから\nちゃーんと手伝ってね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112601, 0.0, 1, 0, 3, 0, 2.6, 0, 0, 1, 6, 1126003, 0, "よっ、ほっ…！\nえへへ、自主練やってるんだー\n最高のアイドルに少しでも\n近づけるように、頑張らなきゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112601, 0.0, 6, 0, 4, 0, 9.0, 0, 0, 1, 1, 1126004, 0, "大きな声で挨拶するのは\nアイドルの基本中の基本！\nせーの、おいっす！\nこらこら声が小さいぞ～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112601, 0.0, 4, 0, 5, 0, 6.6, 0, 0, 1, 6, 1126005, 0, "魔物料理って結構おいしいのに\nあんまり一般的じゃないよね～\n確かに最初は勇気がいるけど\n食べたらアリなのになぁ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1126006, 0, "さぁ、歌って踊れるアイドル\nミオちゃんのショーが始まるよ！\nキラキラ輝くこの魔法で、\nみんなをファンにしちゃうから☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1126007, 0, "踊りながら魔法を使ってると\nテンションが上がって楽しいの！\nこういうところでもアイドルの\n経験は活かさなくっちゃね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1126008, 0, "この魔法陣、キレイでしょ？\nたくさん描くと星も増えていって\n流星がきらめく感じになるの\n私も気に入ってるんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1126009, 0, "そうだ！　ミオちゃんがキミの\nリクエストに答えてあげよう！\nどんな魔法陣を描いてほしい？\nパパっとやってみちゃうよ～☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1126010, 0, "笑顔はパワーのみなもと☆\n大変なことはたくさんあるけど\nいつでも笑顔を忘れない\nそれがミオちゃんなのだー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112601, 0.0, 1, 0, 6, 0, 5.7, 0, 0, 2, 2, 1126011, 0, "パンパカパーン、おめでとー！\n今日はキミの誕生日なんでしょ？\nほら、おめでたい日なんだし\nみんなでレッツパーリーだよっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1126012, 0, "誕生日、おっめでと～！\nまた一つ大人になったキミへ\nミオちゃんからのプレゼント！\nちゃーんと大事にしてよね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112701, 0.0, 4, 0, 1, 0, 7.4, 0, 0, 1, 1, 1127001, 0, "何の因果か労働に勤しむあたし\nま、ほどほどにがんばるよ\nあ～帰りたい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112701, 0.0, 5, 0, 2, 0, 3.5, 0, 0, 1, 2, 1127002, 0, "働いた後のあんパンは美味しいね\nまあ、働いてなくても\n美味しいんだけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112701, 0.0, 4, 0, 3, 0, 3.6, 0, 0, 1, 3, 1127003, 0, "ねえ、次の休みはいつ？\nげぇ～、マジで…\n働き過ぎて死んじゃうよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112701, 0.0, 2, 0, 4, 0, 5.9, 0, 0, 1, 1, 1127004, 0, "働き出してからもあんたには\nなにかと助けられっぱなしだね\n気付いてないかもだけど\n結構頼りにしてるんだよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112701, 0.0, 5, 0, 5, 0, 5.1, 0, 0, 1, 2, 1127005, 0, "仕事も遊びも、気付けば\nいつもあんたと一緒だね\nそばに誰かいてくれるって\n案外いいもんだね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1127006, 0, "森の中はあたしの独壇場！\nリスの怖さを見せてあげるよ\nさあ、疲れる前に終わらすよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1127007, 0, "ん～？　魔物退治のお仕事？\nはいはい、さくっとやっちゃうよ\nあんたは勝利のあんパンの\n用意でもしといてね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1127008, 0, "あたし、なかなかキマってない？\nカッコよくない？\nまた人気出ちゃうよ～\n困るな～マジで～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1127009, 0, "戦いに慣れてきたとはいえ\nやっぱりあんたが隣にいると\n心強いし、がんばれるよ\n…あんがとね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1127010, 0, "色々あってあたしの日常は\n変わっちゃったけど、あんたは\n変わらずにいてくれるんだね\nそういうの、結構うれしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112701, 0.0, 7, 0, 6, 0, 6.6, 0, 0, 2, 6, 1127011, 0, "え、あんた今日誕生日なの？\n忙しくて用意できてないよ…\nと、とりあえず今はこの\nキレイなドングリで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1127012, 0, "誕生日おめでと～。ゲームと\nあんパンでお祝いするよ～\nさあ、ゴロゴロしながら思い切り\n楽しもうね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112801, 0.0, 3, 0, 1, 0, 3.0, 0, 0, 1, 1, 1128001, 0, "今のオラは牧場の守護神だべ！\nみんなには指一本ふれさせねぇ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112801, 0.0, 1, 0, 2, 0, 3.3, 0, 0, 1, 2, 1128002, 0, "強くなると農作業もはかどるべ\n戦いは好きじゃねぇけど\n力も使い方次第で人を\n幸せにできるべさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112801, 0.0, 2, 0, 3, 0, 6.3, 0, 0, 1, 1, 1128003, 0, "この格好、子供たちに\nウケがいいべ！　ネタでとった\n笑いじゃなくても\nやっぱ笑顔はうれしいべさ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112801, 0.0, 5, 0, 4, 0, 5.4, 0, 0, 1, 6, 1128004, 0, "よ、鎧姿が珍しいからって\nそんなにじっと見られると\nは、恥ずかしいべ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112801, 0.0, 6, 0, 5, 0, 7.4, 0, 0, 1, 2, 1128005, 0, "オラたちお笑いだけじゃなくて\n戦いも息ピッタリだべ！\nもういっそ婿に来て…\nな、なんでもねぇべさ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1128006, 0, "見た目で判断すると\nいてぇ目にあうど？\n体はちっこいけど\n心に秘めた愛は無限大だべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1128007, 0, "今だけは笑い無しだべ！\n牧場の平和を乱すヤツは\nオラの突きで月まで\n吹っ飛ばすべよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1128008, 0, "日頃の労働のおかげか\n戦いでも案外動けるもんだべな\nよーし、このまま一気に\n終わらせちまうべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1128009, 0, "本当は戦いなんて怖くて\n仕方ねぇべ。でも隣に\nあんちゃんがいてくれんなら\nオラはいくらでも強くなれんべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1128010, 0, "あんま強くなると、嫁の貰い手が\nなくなっちまうな\nそ、そん時はあんちゃんに…\nや、やっぱり何でもねぇべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112801, 0.0, 2, 0, 6, 0, 5.2, 0, 0, 2, 1, 1128011, 0, "お誕生日おめでとうだべ！\nケーキも料理も用意してっけど\nまずは搾りたてのミルクで\n乾杯だべさ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1128012, 0, "よく来たなあんちゃん！\n今日は牧場を貸し切りで\nパーティーだべ！\nいっぱい楽しんでくんろ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112901, 0.0, 6, 0, 1, 0, 8.7, 0, 0, 1, 2, 1129001, 0, "この服を着てると、自分が\n物語の主人公になったみたいです\n馬子も大爆笑、ってやつですね！\n…え？　何か間違えてましたか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112901, 0.0, 4, 0, 2, 0, 9.5, 0, 0, 1, 1, 1129002, 0, "いいなぁ～、このお洋服…\n見れば見るほどよくできてる\nちょっと脱いで仕立ての確認を～\nってお兄ちゃん、見ちゃダメ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112901, 0.0, 6, 0, 3, 0, 7.7, 0, 0, 1, 1, 1129003, 0, "おっとと、危ない危ない\nいつもみたいにぴょんぴょん\n動き回るとスカートがふわ～って\n…誰にも見られてませんよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112901, 0.0, 5, 0, 4, 0, 5.6, 0, 0, 1, 1, 1129004, 0, "いつもと違うお洋服…\nお兄ちゃん、褒めてくれるかな？\nかわいいね、なんて言われたら\n私…ドキドキしちゃいますよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112901, 0.0, 2, 0, 5, 0, 4.7, 0, 0, 1, 6, 1129005, 0, "ちょっと動かないでくださいね～\n何してるのって？　えへへ、\nお兄ちゃんのサイズを測って\nおそろいの衣装を作るんですよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1129006, 0, "私、物語のヒロインみたいです♪\nもちろんヒーローはお兄ちゃん！\n私がピンチの時は、\nかっこよく助けてくださいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1129007, 0, "いつもお兄ちゃんの後ろに隠れて\nばかりだった私はもういません！\nこれからはお兄ちゃんの隣で…\n一緒に、歩いていきますからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1129008, 0, "お兄ちゃん見てくださいよ！\nこの服、とってもかわいいです！\nえ？　そうだね、って…\nて、照れちゃいますよ…えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1129009, 0, "この服のおかげなのか、\nとびきり調子がいい気がします！\nバッチリ狙いをつけますよ～\n目標はお兄ちゃんのハートです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1129010, 0, "こ～んなかわいい格好で\nアプローチしたら、お兄ちゃんも\nドキドキしますかね…よぉしっ！\n勇気を出してっ、お兄ちゃ～ん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112901, 0.0, 2, 0, 6, 0, 4.9, 0, 0, 2, 1, 1129011, 0, "今日はお兄ちゃんの誕生日！\nおめでとうございます！\nえへへ、またお兄ちゃんが\n格好よくなっちゃいましたね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 112931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1129012, 0, "ハッピーバースデーお兄ちゃん！\nお誕生日ケーキもバッチリです！\nあとは…えっと、その…うんっ！\n私が食べさせてあげちゃいます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113001, 0.0, 5, 0, 1, 0, 3.7, 0, 0, 1, 6, 1130001, 0, "この服…\n本物のウサギさんみたいで、\n私にはかわいすぎだよぉ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113001, 0.0, 6, 0, 2, 0, 5.0, 0, 0, 1, 2, 1130002, 0, "…ウ、ウサギさんだぴょん♪\nなんちゃって…え？\nせ、先輩、いつからそこに…\n今のは忘れてくださいぃぃ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113001, 0.0, 5, 0, 3, 0, 9.3, 0, 0, 1, 2, 1130003, 0, "ウ、ウサギさんは幸運の象徴\nですので、私が先輩のそばに\nずっと居れば…その…や、\nやっぱりなんでもないですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113001, 0.0, 2, 0, 4, 0, 4.1, 0, 0, 1, 1, 1130004, 0, "なんだかいつもより体が\n軽いような…これも、\nアリスちゃんの力が宿った、\nこの服のおかげかな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113001, 0.0, 5, 0, 5, 0, 11.7, 0, 0, 1, 3, 1130005, 0, "アリスちゃんの力で強くなった\n今の私なら、先輩を誘えるかも！\nせ、先輩！　一緒にご飯でも…\nや、やっぱりまだ無理ぃぃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1130006, 0, "ま、魔物と戦うのは怖いけど、\n私だって、\nアリスちゃんの力を受けた一人！\nやるときはやるんですぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1130007, 0, "体が軽い…まるで、本物の\nウサギさんになったみたい…\nえへへ、今の私なら、\n先輩に褒めてもらえるかも♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1130008, 0, "せ、先輩には\n指一本触れさせません！\n私が守りまひゅ…！？\nうう…またかんじゃったよぉ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1130009, 0, "せ、先輩たちに\n頼ってばっかりじゃダメ！\n私だって頑張らないと！\nファイトよアユミぃぃぃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1130010, 0, "時間を操作する力…\nこの力で、ずっと先輩を後ろから\n見て…じゃ、じゃなくてぇ！\nみんなの役に立ってみせますぅ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113001, 0.0, 4, 0, 6, 0, 8.0, 0, 0, 2, 2, 1130011, 0, "せ、先輩！！\nえっと…その、お、お誕生日、\nおめでとうごじゃいまひゅ！\nうう、またかんじゃったよぉ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1130012, 0, "お誕生日おめでとうございます！\nこれ、プレゼントですぅ！\n…つ、ついに、\nかまないでちゃんと言えました♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113101, 0.0, 2, 0, 1, 0, 9.2, 0, 0, 1, 1, 1131001, 0, "風が通り抜けて風鈴が鳴ると\nああ、今年も夏が来たなぁ…\nって感じたりするじゃないか\nあの空気が、好きなんだよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113101, 0.0, 5, 0, 2, 0, 5.6, 0, 0, 1, 6, 1131002, 0, "こういうヒラヒラした格好は\nあんまりしないんだよねぇ…\nいつもと勝手が違うから\nちょっと気恥ずかしいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113101, 0.0, 2, 0, 3, 0, 5.9, 0, 0, 1, 6, 1131003, 0, "お前さん、スイカを食べるとき\n何かをかけたりする派かい？\nアタシはそのまま食べるのが\n好きなんだよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113101, 0.0, 2, 0, 4, 0, 3.7, 0, 0, 1, 1, 1131004, 0, "お前さん、夏の支度は万全かい？\nアタシはこの通りさ\n一緒にバカンスってやつを\n楽しむとしようか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113101, 0.0, 1, 0, 5, 0, 4.2, 0, 0, 1, 4, 1131005, 0, "こう暑いと、かき氷が食べたいね\nお前さんも一緒にどうだい？\n色んな種類があるみたいだし\n別々の味を買おうじゃないか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1131006, 0, "潮風を感じたり波音を聞いたり…\n普段は忙しくて、こんな時間\n取れたりしないからね\n夏を満喫しようじゃないか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1131007, 0, "もし急ぎの用事がないんなら\nここでゆっくりしていきなよ\n何をするわけでもないけど\nたまにはこういうのも、ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1131008, 0, "おや、お前さん。散歩かい？\nアタシはのんびりしてたとこだよ\nきれいな海を眺めながら\nゆっくりするのは一番の贅沢さ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1131009, 0, "お前さんと一緒にいるとさ\nなんていうか安心できるんだよね\n自然体の自分になれるというか\n肩肘を張らなくていいんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1131010, 0, "これからもお前さんと\n一緒に居られたらうれしいよ\n…ふふ、なんだいその顔は\n相変わらず読めないね、まったく");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113101, 0.0, 2, 0, 6, 0, 7.7, 0, 0, 2, 1, 1131011, 0, "誕生日おめでとう\nあんまり大したものじゃないけど\nお祝いの品を用意しといたよ\nほら、受け取っておくれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1131012, 0, "今日はお前さんの誕生日だよね\nお祝いとして、アタシの手料理を\nたくさん用意しておいたんだよ\nさ、向こうで一緒に食べようか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113201, 0.0, 3, 0, 1, 0, 5.4, 0, 0, 1, 4, 1132001, 0, "我が真名を勝手に晒しているとは\nなんという装束なのだ…\nこ、こら、シグルドよ！\nあまり胸元を見るんじゃない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113201, 0.0, 2, 0, 2, 0, 0.0, 0, 0, 1, 2, 1132002, 0, "はーっはっはっは！\n我ら盟友の絆、この海でも\n変わることはないぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113201, 0.0, 1, 0, 3, 0, 2.4, 0, 0, 1, 6, 1132003, 0, "マフラーをしている理由？\nふっ、カッコいいからに\n決まっているではないか！\n他の理由などあるわけがない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113201, 0.0, 3, 0, 4, 0, 13.1, 0, 0, 1, 4, 1132004, 0, "な、なぁ、シグルドよ\n貴様は私の水着を見てどう思う？\n…似合ってる？　いや、もっとこう\nかわいいとか…な、なんでもない");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113201, 0.0, 1, 0, 5, 0, 2.3, 0, 0, 1, 6, 1132005, 0, "日焼け止めはちゃんと塗ったか？\nもしまだ塗っていないなら\n私が貴様に塗ってやろう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1132006, 0, "シグルドよ、何をしているのだ？\n今こそ日光の力を集める機会\n我らの力を高めるためにも\nバカンスを満喫するのだ～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1132007, 0, "ふふふ。海に来たことで私は\n新たな力を得ることができた！\nこの強さを目に焼き付けるがいい\n行くぞ！　アターック！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1132008, 0, "暑さが辛くないのか、だと？\nふっ。私を誰だと思っている…\n我が闇の力をもってすれば\n夏の日差しなど敵ではない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1132009, 0, "最初は、この水着も\n恥ずかしいと思っていたのだが…\n貴様との絆の証だからな\n別の水着など考えられんな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1132010, 0, "シグルド、貴様と過ごす夏は\n最高に愉快だ！\n来年もまた来よう！\nその次もまた一緒だ、いいな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113201, 0.0, 1, 0, 6, 0, 5.8, 0, 0, 2, 2, 1132011, 0, "誕生日、おめでとう！\n今日は私から闇の祝福を与えよう\nこの力があれば貴様には様々な\n幸運が訪れる。感謝するがよい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1132012, 0, "今日は貴様の誕生日か…\nよし、これをプレゼントしよう！\n私が考えた貴様の名乗り口上だ\nさぁ、今から練習をしてみるぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113301, 0.0, 2, 0, 1, 0, 6.4, 0, 0, 1, 1, 1133001, 0, "どうどう？　この水着！\nいつもの魔法少女とは違う\n新たなナナカちゃんの魅力、\n君に教えてあげようじゃないか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113301, 0.0, 2, 0, 2, 0, 3.1, 0, 0, 1, 1, 1133002, 0, "こういうダンサー風の\n魔法少女もいいでしょ？\nぐふふ、妄想が捗りますな～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113301, 0.0, 1, 0, 3, 0, 6.0, 0, 0, 1, 3, 1133003, 0, "ミステリアスな水着美少女\n彼女の正体は、敵か味方か…？\nみたいな登場しそうじゃない！？\n物語中盤で仲間になる系だよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113301, 0.0, 6, 0, 4, 0, 6.2, 0, 0, 1, 5, 1133004, 0, "この格好、君もちょっとくらい\nドキッとしちゃうんじゃな～い？\n…ここまでアピールしてるのに\n顔が普通なの、逆にすごいわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113301, 0.0, 5, 0, 5, 0, 8.0, 0, 0, 1, 4, 1133005, 0, "も～、ちょっとはナナカちゃんを\n意識したっていいのにな～\nえ？　どうしてなのかって…\nさぁ、どうしてだろうね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1133006, 0, "え！？　そのビンなにって…\nここ、これは君との夏の思い出を\n海に流してみようかなって…\nなにを書いたのかは絶対に秘密！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1133007, 0, "もし、無人島に流されちゃっても\n君と一緒だったら\n助けなんか来なくてもいいな…\nって、ナシナシ！　今のナシ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1133008, 0, "一度しか言わないよ？\n…ナナカちゃんの水着姿を\nじっくり見ていいのは\n君だけだからね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1133009, 0, "ちょ、ちょっと～、そんな真顔に\nなられるとさ…えと、その…\nドキっとしちゃうよ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1133010, 0, "ギャルゲーなら照れた反応も\n王道のリアクションだけど…\n君が相手だと、なんでだろ…\n上手にできないね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113301, 0.0, 1, 0, 6, 0, 6.3, 0, 0, 2, 2, 1133011, 0, "おたおめ～♪\nというわけで、君にはこの本を\nプレゼントしちゃいまーす！\n私のオススメ、選んでおいたぞ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1133012, 0, "誕生日おめでとーっ！\nさぁ、ナナカちゃんお手製の\n変身グッズをプレゼントだよん☆\nこれで君も魔法少女になるのだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113401, 0.0, 1, 0, 1, 0, 2.2, 0, 0, 1, 7, 1134001, 0, "ファイトファイト～♪\nキミが元気いっぱい\nがんばれるように\n私がいっぱい応援するよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113401, 0.0, 1, 0, 2, 0, 2.4, 0, 0, 1, 2, 1134002, 0, "今日もお疲れさま！\nいっぱいがんばった\n後は、ゆっくり休憩して\n疲れを取らなきゃだよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113401, 0.0, 6, 0, 3, 0, 6.5, 0, 0, 1, 2, 1134003, 0, "動きやすいしかわいいし\nこの水着、と～っても\nいい感じだね！\nえ？　キミも着てみたいの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113401, 0.0, 2, 0, 4, 0, 5.1, 0, 0, 1, 1, 1134004, 0, "キミのこと、他の誰よりも\nおっきな声で応援するね！\nどんな時でも、ちゃんとキミに\n聞こえるように！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113401, 0.0, 5, 0, 5, 0, 6.2, 0, 0, 1, 6, 1134005, 0, "全力でがんばってるキミを\n見てたら、なんだかドキドキ\nしちゃった…張り切って\n応援しすぎたからかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1134006, 0, "きらーん☆\nこれがハツネちゃんの本気だよ！\nキミが元気にがんばれるように\n私もいっぱい応援するからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1134007, 0, "潮風を感じながら\n体を動かすのって、\nすっごく気持ちいいね！\nキミも一緒に、やってみようよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1134008, 0, "超能力を使っちゃったから、\nちょっと眠くなってきちゃった…\nけど…がんばるキミのために…！\n私もがんばるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1134009, 0, "諦めないでがんばるキミって\nすっごくかっこいいかも…\nあぁ～っ、ごめんなんでもない！\nひとりごとだから気にしないで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1134010, 0, "キミががんばり続ける限り\n私もずっと応援してるよ！\nいつでもそばにいるからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113401, 0.0, 7, 0, 6, 0, 7.5, 0, 0, 2, 2, 1134011, 0, "今日ってキミのお誕生日だよね！\nえへへ、忘れるわけないよ～！\nパーティの準備もばっちりだし、\nいっぱい楽しく過ごそうね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1134012, 0, "ハッピーバースデー☆\nお祝いに、今日は朝から晩まで、\nキミをずっと応援するのだ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113501, 0.0, 6, 0, 1, 0, 3.8, 0, 0, 1, 2, 1135001, 0, "よしよし。今日も頑張ったわね♪\nあら、顔が真っ赤ね？\nいつもより恥ずかしい？\nん～？　どうしてかしら…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113501, 0.0, 2, 0, 2, 0, 9.9, 0, 0, 1, 1, 1135002, 0, "水分補給はちゃんとしてる？\n海はとっても楽しいけれど\n体調には気をつけないとね\nはい、先生特製のドリンクよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113501, 0.0, 6, 0, 3, 0, 8.9, 0, 0, 1, 1, 1135003, 0, "さ、次はお洗濯しなきゃ…\nえ？　手伝ってくれるの？\nありがとう、助かるわ♪\nあっ！　そっちの洗濯物は私の…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113501, 0.0, 2, 0, 4, 0, 11.6, 0, 0, 1, 5, 1135004, 0, "あなたの体、引き締まってるわね\n男の子って感じで格好いいわ♪\nもう子ども扱いできないかも…\nなんて、ね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113501, 0.0, 2, 0, 5, 0, 8.3, 0, 0, 1, 5, 1135005, 0, "二人で波打ち際を歩いてみない？\n…ロマンチックな雰囲気を\n味わってみたくて…\nふふ、子どもっぽかったかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1135006, 0, "みんなー、ファイトよーっ！\nふふ、先生の応援どうかしら？\n元気になったのなら、うれしいわ\nさぁ、もうひと頑張りしましょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1135007, 0, "夏の海で体を動かすのって\nとても気持ちがいいのね\n応援だけじゃなくて、先生も\n一緒に参加しちゃおうかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1135008, 0, "水着なんて森では着ないから\nちょっとだけ恥ずかしいわ\nやっぱり肌を出し過ぎてる気が…\n…もう、あまり見つめちゃダメよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1135009, 0, "ね、海の楽しみ方…\n先生に教えてもらえないかしら？\nできれば、あなたと一緒に\n夏を満喫したいなって…ふふ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1135010, 0, "うふふ、青春っていいわね～\nあら、先生も青春しようって？\nそうね、あなたとなら…\n甘酸っぱい青春、できそうね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113501, 0.0, 2, 0, 6, 0, 8.7, 0, 0, 2, 1, 1135011, 0, "なんだかソワソワしてるわね？\nそれもそうよね、今日はあなたの\nお誕生日だもの。先生と一緒に\nいっぱいお祝いしちゃいましょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1135012, 0, "今日はあなたのお誕生日ね！\nずっと頑張ってきたいい子には\n先生から特別なお祝いよ♪\nほら、いい子いい子…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113601, 0.0, 5, 0, 1, 0, 1.9, 0, 0, 1, 6, 1136001, 0, "どうしたんだい？\n…そんなにじっくりと見られると\n恥ずかしいじゃないか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113601, 0.0, 2, 0, 2, 0, 5.7, 0, 0, 1, 6, 1136002, 0, "直接日差しを浴びるのは\n鎧越しとは違った暑さだね…\nこれはこれでいいものだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113601, 0.0, 2, 0, 3, 0, 3.8, 0, 0, 1, 1, 1136003, 0, "思い切り身体を動かせるのも\nいいものだね…\nこれなら存分に泳げるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113601, 0.0, 2, 0, 4, 0, 3.0, 0, 0, 1, 6, 1136004, 0, "いつもより無防備な格好だが…\n君がそばにいてくれるおかげで\n心強いよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113601, 0.0, 2, 0, 5, 0, 1.7, 0, 0, 1, 1, 1136005, 0, "こうしていると\nいつもより君を近くに\n感じられる気がするよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1136006, 0, "どうしたんだい少年\nそんなにじっと見て…\nみ、見ないでくれとは言わないが\nいつもとは違った恥ずかしさだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1136007, 0, "日差しが強くなってきたね\n熱中症になると危ないし…\nき、君さえよければだが、\n一緒にパラソルに入らないかい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1136008, 0, "君の前でならありのままの\n自分でいられる気がするよ\n…どうしてだろうね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1136009, 0, "もう少しゆっくりしていこうか\nたまにはこういうのもいいだろう\nここには君と私しか\nいないのだから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1136010, 0, "夏は人を大胆にする、なんて\n信じてはいなかったのだけれど…\n今なら分かる気がするよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113601, 0.0, 2, 0, 6, 0, 7.1, 0, 0, 2, 1, 1136011, 0, "今日は君の誕生日だったね\nこの格好では大した祝福も\nできないが…祝えてうれしいよ\n誕生日おめでとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1136012, 0, "誕生日おめでとう、少年\n…そんなにじっと顔を見られると\nどうにも照れてしまうが…\n君が望むなら、受け入れるさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113701, 0.0, 5, 0, 1, 0, 10.6, 0, 0, 1, 1, 1137001, 0, "お兄ちゃん、見てください！\nエンジェルの服、どうですか？\nアカリ…かわいいですか？\nやんっ、見つめすぎですよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113701, 0.0, 5, 0, 2, 0, 6.5, 0, 0, 1, 2, 1137002, 0, "このたくさんのハートの飾り\nアカリの想いが詰まってるんです\nどんな想いか分かりますか？\nねぇ、お兄ちゃん…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113701, 0.0, 1, 0, 3, 0, 4.5, 0, 0, 1, 2, 1137003, 0, "アカリ、エンジェルみたいに\nみんなを幸せにしたいです！\nお兄ちゃん、特訓してください！\nビシバシ、しごいてくださいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113701, 0.0, 1, 0, 4, 0, 7.2, 0, 0, 1, 6, 1137004, 0, "エンジェルの力で、サックスが\n上手になったりは…しませんよね\nううん、自分で努力してこそです\nアカリ…もっと頑張ります！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113701, 0.0, 5, 0, 5, 0, 6.1, 0, 0, 1, 4, 1137005, 0, "お兄ちゃん、アカリなんだか\nドキドキしてます…\nお兄ちゃんの愛の力を\n感じてるから、ですかね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1137006, 0, "お兄ちゃん、アカリの愛の力\n受け止めてくださいね！\nアカリしか見えなくなるくらい\nメロメロにしちゃいますからっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1137007, 0, "フワフワな羽が気になりますか？\nどうぞ…触ってもいいですよ\nでも、優しくしてくださいね…？\n敏感なんですから…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1137008, 0, "この格好、お姉ちゃんとお揃いで\nとってもかわいいですよね？\nアカリ、お気に入りなんです\nお姉ちゃんは照れてますけど…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1137009, 0, "お兄ちゃん、元気ないんですか？\nだったらアカリに任せてください\n頭をナデナデしてあげますね…\nよしよし、元気になぁれ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1137010, 0, "えへへ、アカリの大切な想い\nお兄ちゃんに届きましたか？\nそれとも、もう一回…しますか？\nもっとすごいのあげちゃいます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113701, 0.0, 5, 0, 6, 0, 4.2, 0, 0, 2, 2, 1137011, 0, "お兄ちゃん、お誕生日\nおめでとうございますっ！\nなんだかアカリもうれしくて\nウズウズしてきちゃいます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1137012, 0, "お兄ちゃん、お誕生日の\nお祝いしちゃいますよ！\nプレゼントはア・カ・リ♪\nな～んて、ドキドキしましたか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113801, 0.0, 1, 0, 1, 0, 5.5, 0, 0, 1, 2, 1138001, 0, "さて、今日もエンジェルとして\n困っている人を助けに行かないと\nもちろん、アンタにも\n付き合ってもらうわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113801, 0.0, 2, 0, 2, 0, 2.5, 0, 0, 1, 6, 1138002, 0, "エンジェルらしさ…\nあなたの願いを\nかなえて差し上げます☆\nこ、こんな感じかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113801, 0.0, 6, 0, 3, 0, 6.4, 0, 0, 1, 4, 1138003, 0, "うう…やっぱりこういう格好は、\n私には似合わな…え？\nか、かわいいって…ば、ばかぁ！\nそういう恥ずかしいこと言うな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113801, 0.0, 6, 0, 4, 0, 8.2, 0, 0, 1, 4, 1138004, 0, "困ってる人に話しかける…\n人見知りの私には難しいかも\nま、まあ、アンタも\n手伝ってくれるし、頑張るけどね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113801, 0.0, 1, 0, 5, 0, 4.6, 0, 0, 1, 6, 1138005, 0, "エンジェルとして困ってる人を\n救うかぁ…これがきっかけで、\nちゃんと人と\n話せるようになれるといいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1138006, 0, "こ、こういうかわいいのって\nアカリの得意分野なのに…\nでも、今なら私だって…\nし、しっかり見てなさいよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1138007, 0, "手の位置とかこのへんで\n姿勢ももう少しこう…\nな、何よ、やると決めたからには\n完璧を目指すわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1138008, 0, "何か子供たちがこのポーズの\nまねしてくれてるのよね\nちょっと気恥ずかしいけど\n悪くない気分ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1138009, 0, "みんなの役に立つのって\n想像以上に大変なことだわ\nアンタはそれをずっとしてたのね\n…ちょっと見直したわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1138010, 0, "ア、アンタにはいつも助けられて\nばかりだから、アンタだけに\n特別なポーズ、見せてあげるわ\nこ、光栄に思いなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113801, 0.0, 6, 0, 6, 0, 8.0, 0, 0, 2, 1, 1138011, 0, "ア、アンタの誕生日とかどうでも\nいいんだけど、一応お祝いは\nしてあげるわ\nエ、エンジェルとしてね！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1138012, 0, "た、誕生日おめでとう。今日は\nたまたまエンジェルの仕事が\n休みだから、私が祝ってあげるわ\nか、感謝しなさいよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113901, 0.0, 3, 0, 1, 0, 5.7, 0, 0, 1, 1, 1139001, 0, "騎士さん、ハロウィンですね…\n少し付き合ってほしいんですけど\nかっ、勘違いしないでください！\n仕事の手伝いですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113901, 0.0, 1, 0, 2, 0, 6.7, 0, 0, 1, 6, 1139002, 0, "なんですか、ジロジロ見て…\nもしかして騎士さんも仮装\nしたいんですか？　なら…\n仕方ないので作ってあげますよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113901, 0.0, 2, 0, 3, 0, 8.4, 0, 0, 1, 1, 1139003, 0, "それっ！　えーいっ！\n何してるのって、糸で縛る練習に\n決まってるじゃないですか\n標的はもちろん…分かりますね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113901, 0.0, 5, 0, 4, 0, 9.1, 0, 0, 1, 1, 1139004, 0, "ハロウィンと言えば仮装ですよね\nどうですか、私の衣装は？\n力作なのでどうぞ褒めてください\nかわいい？　…で、でしょうっ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113901, 0.0, 1, 0, 5, 0, 3.4, 0, 0, 1, 2, 1139005, 0, "よし、お菓子の準備はオーケー♪\n騎士さんのことだから、お菓子を\n欲しがるに違いありませんからね\n手作りだけど…それは秘密です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1139006, 0, "えっと、騎士さん\nどうせ暇ですよね？\nなら私と一緒にハロウィンを…\nもうっ、いいから行きますよっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1139007, 0, "ふふ、きれいでしょこの飾り付け♪\nん？　どこを見てるんです？\n…わ、私じゃなくて飾り付けを\n見てください！　もうっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1139008, 0, "騎士さん、覚悟してください！\n今の私はこわ～い吸血鬼です！\nガブッと噛みついて、えっと…\nやっぱいいです！　今のナシで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1139009, 0, "色んな仮装を見てると\n自分でも作りたくなってきますね\n騎士さんはどんなのが好きです？\n…参考に聞いてるだけですからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1139010, 0, "騎士さんと二人でハロウィン…\n周りからどう見えるんだろう…？\nまぁ、どうだっていいですけど！\n私は別に、気にしませんよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113901, 0.0, 5, 0, 6, 0, 9.1, 0, 0, 2, 6, 1139011, 0, "…ついさっき思い出しましたけど\n今日は騎士さんの誕生日でしたね\nはいこれ、誕生日プレゼントです\nあっ…これは今買ったんですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 113931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1139012, 0, "えっと、騎士さん\n誕生日おめでとうございます！\n吸血鬼からの誕生日プレゼント\n存分にありがたがってください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114001, 0.0, 2, 0, 1, 0, 6.2, 0, 0, 1, 6, 1140001, 0, "ト、トリックオアトリート…\n実はこういうことには不慣れでね\nよければ、キミが私の\n師匠になってくれるかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114001, 0.0, 2, 0, 2, 0, 8.8, 0, 0, 1, 1, 1140002, 0, "子供から大人までみんな\nハロウィンを楽しんでいるね…\n私はちゃんと馴染めているかな？\nばっちり？　ならよかった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114001, 0.0, 4, 0, 3, 0, 9.7, 0, 0, 1, 6, 1140003, 0, "ハロウィンのお菓子のやりとり…\nええと、ハッピーハロウィン…\nお菓子をどうぞ…って\nうまくできてる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114001, 0.0, 2, 0, 4, 0, 5.6, 0, 0, 1, 1, 1140004, 0, "賑やかなハロウィンの街並み…\n眺めているだけで\n楽しいものだね\nさあ、私たちも出かけよう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114001, 0.0, 2, 0, 5, 0, 5.3, 0, 0, 1, 5, 1140005, 0, "キミと一緒に\nハロウィンを過ごすのは\n気恥ずかしいんだけど…\nうれしいよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1140006, 0, "トリックオアトリート\nお菓子をくれないと…\nとり憑いてしまうよ？\nふふ、なんてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1140007, 0, "さあ、手を出して？\n幽霊たちと一緒に\nハロウィンのダンスを踊ろうよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1140008, 0, "ツムギが作ってくれたこの衣装\nとても素敵だね…ふふ、\n幽霊たちも、似合ってるって\n言ってくれているのかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1140009, 0, "キミ、休んでいる暇はないよ？\nハロウィンを\n遊び尽くさなくちゃね\nほら、一緒に行こう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1140010, 0, "ハロウィンの祭りで賑わう\nこんな時だからこそ、かな？\nキミと過ごせる時間を\nとても幸せに感じるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114001, 0.0, 2, 0, 6, 0, 5.6, 0, 0, 2, 1, 1140011, 0, "誕生日おめでとう。キミの成長が\n感じられて私もうれしいよ\n来年のキミが今から楽しみだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1140012, 0, "キミの誕生日は私にとっても\nかけがえのない大切な日だよ\nキミと巡り会えた運命に\n感謝しなくちゃね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114101, 0.0, 2, 0, 1, 0, 2.6, 0, 0, 1, 3, 1141001, 0, "ホワイトタイガー出動ッス！\nさあ、今日も平和を守るため\n一緒にパトロールにいくッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114101, 0.0, 2, 0, 2, 0, 3.2, 0, 0, 1, 1, 1141002, 0, "今日も張り切って\n任務に出発ッス！\n遅れないように\nついてくるッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114101, 0.0, 1, 0, 3, 0, 4.2, 0, 0, 1, 3, 1141003, 0, "極秘任務に備えて\n秘密の特訓をするッス！\n付き合ってもらっていいッスか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114101, 0.0, 2, 0, 4, 0, 2.3, 0, 0, 1, 6, 1141004, 0, "…今、ヒマッスよね？\nだったら二人で\nキメポーズの研究をするッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114101, 0.0, 1, 0, 5, 0, 3.5, 0, 0, 1, 2, 1141005, 0, "ヒーローにも休息は必要ッス！\nあんたさえよければ\n今日は二人でごろごろする\nっていうのもいいッスよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1141006, 0, "どうッスか、この勇姿！\nどんな悪も\nホワイトタイガーからは\n逃げられないッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1141007, 0, "あんたと一緒につくった\nこのカッコイイキメポーズで、\n今日も平和を守るッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1141008, 0, "いつもと姿は変わっても\n正義の心は変わらないッス！\n一緒にがんばってもらうッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1141009, 0, "あんたも自分と一緒におそろいの\nカッコイイポーズをキメるッス！\nせーの…\nトリックオアジャスティスッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1141010, 0, "あんたが背中を守ってくれるから\n自分は正義に突き進めるッス！\nこれからもよろしく頼むッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114101, 0.0, 2, 0, 6, 0, 7.8, 0, 0, 2, 1, 1141011, 0, "今日はあんたの誕生日ッスね！\nお祝いに今日のヒーローごっこの\nトラタイガー役を譲って…\nえ？　自分がやっていいッスか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1141012, 0, "お誕生日おめでとうッス！\n今日のトラタイガーごっこは、\n自分とあんたで\nダブルタイガーッスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114201, 0.0, 2, 0, 1, 0, 6.8, 0, 0, 1, 1, 1142001, 0, "魔法少女と提督\nそれらが一つになった私こそ\n愛と希望の戦士\n魔法提督ラブリー★モニカだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114201, 0.0, 1, 0, 2, 0, 4.2, 0, 0, 1, 6, 1142002, 0, "この格好にもすっかり\n慣れてしまったな\n子どもっぽいかと思っていたが\nこういうのも案外…悪くない");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114201, 0.0, 1, 0, 3, 0, 5.6, 0, 0, 1, 4, 1142003, 0, "ずっと剣を扱ってきたからか\n魔法にはまだ慣れないな…\nしかし剣先から光線が出るのは\nなぜかワクワクするな…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114201, 0.0, 5, 0, 4, 0, 3.5, 0, 0, 1, 2, 1142004, 0, "輝け、愛の力！\nちょっとかわいすぎるか？\n…今からでも全砲門、発射！\nとかにした方がいいだろうか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114201, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1142005, 0, "困ったことがあれば\nなんでも私に言ってくれ\n魔法提督は困っている者を\n決して見捨てないのだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1142006, 0, "ラブリーキュアリーブレイブリー\nマジカルレベルインフィニット！\n魔法提督ラブリー★モニカ\n空に代わって指揮を執る！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1142007, 0, "戦いの指揮は私に任せろ！\n愛と希望に満ちた戦略で\n敵を討ち果たして見せよう\n最後の決めポーズまで完璧だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1142008, 0, "魔法少女は子どもたちとの\n触れ合いも大切にせねばな！\n今日も一緒に提督ごっこだ！\nなんだ、貴公も一緒にやるか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1142009, 0, "魔法提督の指揮を見せてやろう！\n貴公、今すぐ街中のお菓子を\n買い占めてくるのだ！\n…すまん、冗談だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1142010, 0, "人々の平和を守るという使命\n不安がないかと言えばウソになる\nだが、貴公がいてくれるなら\n私はどんな苦難にも立ち向かえる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114201, 0.0, 6, 0, 6, 0, 8.5, 0, 0, 2, 2, 1142011, 0, "貴公、今日は誕生日らしいな\nみなまで言うな、お祝いに\nお菓子を用意してある。決して\n私が食べたいからではないぞ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1142012, 0, "誕生日おめでとう。貴公のほしい\nものを言うがいい…え、私と\n一緒に過ごしたい？　そ、そうか\n欲がないな、貴公は");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114301, 0.0, 2, 0, 1, 0, 3.7, 0, 0, 1, 1, 1143001, 0, "このキラキラした衣装、\nキュートな武器…\nえへへ、やっぱり\n魔法少女っていいなぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114301, 0.0, 1, 0, 2, 0, 3.7, 0, 0, 1, 6, 1143002, 0, "まさか、私が\n魔法少女になれるなんて！\n…おっと、このことは\nみんなに内緒にしないとね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114301, 0.0, 1, 0, 3, 0, 4.6, 0, 0, 1, 2, 1143003, 0, "魔法少女はみんなに\n夢を与える存在…\nその名に恥じぬように、\n私も精進しないとね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114301, 0.0, 2, 0, 4, 0, 5.8, 0, 0, 1, 1, 1143004, 0, "シャイニー、\nフェザリー、グローリー！\nマジカルソード、シャープネス！\nよし、今のポーズは完璧だったね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114301, 0.0, 5, 0, 5, 0, 10.1, 0, 0, 1, 6, 1143005, 0, "変身したら髪型まで変わるとはね\nさすがに長いから結んで…え？　\n似合ってるって…じゃ、じゃあ、\nこのままにしとこうかな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1143006, 0, "シャイニーフェザリーグローリー\nマジカルソード、シャープネス！\n魔法剣士シャイニー★トモ！\n煌めく剣技で、悪を断つ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1143007, 0, "魔法少女になったとしても、\nミクマ流剣術に死角はない\n…なんなら、\nその身でためしてみる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1143008, 0, "半人前の私にどこまでできるか\n分からないけど、諦めることは\n絶対にしない。それが、\n私なりの魔法少女の在り方だよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1143009, 0, "私は魔法少女であると同時に、\nミクマ流の剣士…悪いけど、\n真剣勝負というならば、\n手加減はできないからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1143010, 0, "変身できるのは、あなたのおかげ\nでもあるんだよね…だったら、\nこれからはず～っと一緒に居て\nもらわないと…な～んてね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114301, 0.0, 2, 0, 6, 0, 6.4, 0, 0, 2, 1, 1143011, 0, "今日はあなたの誕生日だよね\nだったら、私がケーキを焼いて\nあげよっか？　こう見えて、\n料理には自信があるんだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1143012, 0, "誕生日おめでとう！\n魔法少女の役目が\n終わったあとで、\n二人でお祝いしようね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114401, 0.0, 1, 0, 1, 0, 4.1, 0, 0, 1, 2, 1144001, 0, "おーっほっほっほ！\n聖夜を彩るパーティーも\nこのアキノ・ウィスタリアに\nお任せあれ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114401, 0.0, 1, 0, 2, 0, 3.1, 0, 0, 1, 2, 1144002, 0, "買い出しなら私の出番ですわね\n料理にツリーにプレゼントに…\n価格交渉ならお手のものですわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114401, 0.0, 2, 0, 3, 0, 2.8, 0, 0, 1, 1, 1144003, 0, "クリスマスには\nプレゼントが付き物！\nもちろん準備は万端ですわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114401, 0.0, 5, 0, 4, 0, 5.0, 0, 0, 1, 1, 1144004, 0, "この衣装、新しく\n仕立てたのですがいかがですか？　\nえ、似合ってる…ほめて\nいただけてよかったですわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114401, 0.0, 2, 0, 5, 0, 4.3, 0, 0, 1, 1, 1144005, 0, "あなたさまとクリスマスを共に\n過ごせてうれしいですわ！\nさあ、さっそくパーティーの\n準備をいたしましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1144006, 0, "さあ、楽しい聖夜の晩餐会を\n始めるといたしましょう！\nほら、あなたさまも\n席にお座りくださいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1144007, 0, "おーっほっほっほ！　この私に\nかかれば、料理など造作も\nありませんわ！　さあ、遠慮せず\n召し上がってくださいませ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1144008, 0, "料理とは面白いものですわね\nあなたさまには、別の手料理も\n召し上がっていただきたいですし\n今後も続けていきたいですわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1144009, 0, "せっかくのお料理ですもの\n温かいうちにいただきましょう？\nふふ、安心なさってくださいまし\nお代わりはたーんとありますわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1144010, 0, "みなさまと過ごすクリスマスも\nよいものですけれど…\nあなたさまと二人でいる\nこの時間も…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114401, 0.0, 1, 0, 6, 0, 5.8, 0, 0, 2, 2, 1144011, 0, "今日はあなたさまの\n誕生日ですもの\nささやかに、とは言いません\n盛大にお祝いいたしましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1144012, 0, "誕生日のお祝いに手料理を\nご用意してみましたわ！\n是非とも\n召し上がってくださいませ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114501, 0.0, 1, 0, 1, 0, 4.0, 0, 0, 1, 1, 1145001, 0, "お金をかけなくても素敵な\nクリスマスが過ごせるってこと\n見せてあげるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114501, 0.0, 2, 0, 2, 0, 6.8, 0, 0, 1, 1, 1145002, 0, "子どもってクリスマスが好きよね\nまあ、あたしも人のこと\n言えないけど…何だか\n楽しい気分になれるのよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114501, 0.0, 2, 0, 3, 0, 4.3, 0, 0, 1, 1, 1145003, 0, "子どもたちが笑顔で\nクリスマスを\n過ごせること\nそれが一番の幸せよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114501, 0.0, 2, 0, 4, 0, 3.4, 0, 0, 1, 6, 1145004, 0, "せっかくのクリスマスなのに\n暇そうね。仕方ない、少しだけ\n付き合ってあげるわ。何か\n温かい物でも飲みに行きましょ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114501, 0.0, 5, 0, 5, 0, 3.3, 0, 0, 1, 6, 1145005, 0, "え、ドレスが似合ってる？\nそ、そう…ありがと…\nあんたに褒めてもらうのって…\nうれしいわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1145006, 0, "さあ、熱く楽しいクリスマスを\n始めるわよ！\n寒がってる暇なんて\nないんだからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1145007, 0, "少しエレガントじゃないかも\nしれないけど、許してね？\nあたし、目いっぱいクリスマスを\n楽しんじゃうんだから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1145008, 0, "あんたと一緒のクリスマス…\n結構いい感じね♪\n気の許せる相手と\n過ごせるのって…幸せよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1145009, 0, "外にいるのに何だか暑いって\n思うのは、もしかして…\nあんたが隣にいるから\nなのかしら…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1145010, 0, "高級レストランで豪華なディナー\nなんてのは柄じゃないし\nあんたがいてくれれば、それが…\nその…さ、最高のクリスマスよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114501, 0.0, 5, 0, 6, 0, 5.4, 0, 0, 2, 2, 1145011, 0, "ハッピーバースデー！\n子どもたちからあんたに\nプレゼントがあるみたい\nあたしも…一応用意してあるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1145012, 0, "ええと…誕生日おめでとう\nこれから一緒にプレゼントを\n見に行かない？\nデ、デートじゃないわよ、もう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114601, 0.0, 1, 0, 1, 0, 2.1, 0, 0, 1, 2, 1146001, 0, "お姉さんに任せなさい！\nキミのクリスマスを\n最高のものにしてあげるわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114601, 0.0, 6, 0, 2, 0, 2.8, 0, 0, 1, 5, 1146002, 0, "キミと二人のクリスマスかぁ…\nき、緊張なんかしてないわよ？\n私がリードしてあげるんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114601, 0.0, 5, 0, 3, 0, 3.8, 0, 0, 1, 4, 1146003, 0, "ちょっと派手じゃないかしら\nこの格好…ま、まあ、\nキミが気に入ってくれてるなら\nいいんだけどね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114601, 0.0, 1, 0, 4, 0, 5.7, 0, 0, 1, 4, 1146004, 0, "一人のクリスマスに\n慣れちゃってたから\nキミと過ごすクリスマス…\nちょっとドキドキしちゃうかも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114601, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1146005, 0, "メリークリスマース！\nえへへ、大丈夫よ～\n酔ってなんかないから～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1146006, 0, "さあ、最っ高の\nクリスマスが始まるわよ♪\nキミも早くおいで～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1146007, 0, "キミも飲み物どうかしら？\n一緒に乾杯しましょ♪\nはーい、メリークリスマース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1146008, 0, "おかわりはいかが？\n遠慮しなくてもいいわよ\nクリスマスパーティーなんだもの\n思いっきり楽しまなくちゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1146009, 0, "どう？　楽しんでるかしら？\n…もしよかったらなんだけど\nこの後二人で…\nや、やっぱりなんでもないわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1146010, 0, "キミと過ごすクリスマス…\nとっても素敵ね\nこんな幸せな時間が\nずっと続けばいいのに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114601, 0.0, 2, 0, 6, 0, 4.8, 0, 0, 2, 1, 1146011, 0, "今日はキミの誕生日だもの\nお祝いしないともったいないわ！\nそれじゃあ、かんぱ～い☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1146012, 0, "お誕生日、おめでとう！\n後で二人きりでパーティー\nしちゃおっか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114701, 0.0, 1, 0, 1, 0, 5.9, 0, 0, 1, 2, 1147001, 0, "はーっはっはっは！\n新年の初笑いは悪党らしく\n盛大にだ！　オマエも一緒に\nやらないか？　気持ちいいぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114701, 0.0, 3, 0, 2, 0, 5.3, 0, 0, 1, 2, 1147002, 0, "悪党に休みなしだ！\n正月だろうとアタシは戦うぞ！\nさあ、文句があるヤツは\n全員かかってこい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114701, 0.0, 8, 0, 3, 0, 7.8, 0, 0, 1, 1, 1147003, 0, "一年の計はなんとかっていうだろ\nだからアタシは、悪党らしく\n正月から悪いことをするぞ！\nま、年中悪さしてるけどな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114701, 0.0, 6, 0, 4, 0, 8.3, 0, 0, 1, 2, 1147004, 0, "出店がいっぱいだな！　これじゃ\nお年玉がいくらあっても\n足りないぞ！　なあ、オマエのは\nまだ…な、なんでもない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114701, 0.0, 2, 0, 5, 0, 3.9, 0, 0, 1, 1, 1147005, 0, "まさかオマエと新年を\n過ごすことになるなんて…\nでも悪い気はしないな！\nなんか美味いもんでも食うぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1147006, 0, "アタシが天楼覇断剣で\n全部真っ二つにしてやる！\n空だろうが海だろうが\n知ったことか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1147007, 0, "飾りをつけた天楼覇断剣も\n意外と悪くないぞ！\nぶった切れればなんだって\nいいんだけどな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1147008, 0, "気持ちのいい景色だな！\nどこかでアイツも…\nオクトーも、こんな風に\n日の出を見てるのかな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1147009, 0, "今年もオマエにはいい事あるぞ！\nなんてったってアタシと\nこの天楼覇断剣が一緒だからな！\nどんな未来も切り拓いてやる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1147010, 0, "プリンセスナイトの力って\n初日の出に似てると思わないか？\n温かくて、柔らかくて…それに\nなんか力がみなぎってくるだろ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114701, 0.0, 2, 0, 6, 0, 5.4, 0, 0, 2, 1, 1147011, 0, "誕生日おめでとう！\nオマエのお祝いができて\nアタシもうれしいぞ！\nもっともっと祝わせろ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 114731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1147012, 0, "ハッピーバースデー！\n今日はオマエの言う事を\nなんでも聞いてやるぞ！\nアタシに遠慮はするなよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115001, 0.0, 2, 0, 1, 0, 6.1, 0, 0, 1, 3, 1150001, 0, "この私を待たせるとは\n良い度胸をしていますね\n…冗談ですよ\n待ち合わせ時刻、丁度です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115001, 0.0, 1, 0, 2, 0, 4.0, 0, 0, 1, 6, 1150002, 0, "正月の街は騒々しいですね…\nですが、これはこれで\n観測するデータには\n事欠かないとも言えますか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115001, 0.0, 2, 0, 3, 0, 8.7, 0, 0, 1, 1, 1150003, 0, "せっかくの正月です\n貴方も人の事ばかりにかまけず\n自分のための時間を持ちなさい\nお姉さんからのアドバイスですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115001, 0.0, 6, 0, 4, 0, 5.1, 0, 0, 1, 1, 1150004, 0, "これを飲みなさい\nそこで売っていた甘酒です\n貴方に体調を崩されては\n私としても困りますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115001, 0.0, 2, 0, 5, 0, 9.8, 0, 0, 1, 1, 1150005, 0, "年下に見えるお姉さんは\n好きですか？\nええ、貴方の好みを聞いていますが\n…そう。それなら良いのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1150006, 0, "あけましておめでとうございます\n今年も私の様々な姿を\n貴方に見せてあげますよ…\nそのままがいい？　…そうですか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1150007, 0, "雪…こうして触れてみると\n儚いものなのですね\nあっという間に形が変わる…\n少し、親近感を覚えます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1150008, 0, "舞い散る雪を眺めながら\n温かい甘酒を飲むというのも\nなかなか乙なものですね\n貴方も付き合いなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1150009, 0, "外出など普段はしないのですが…\n気が向いたので初詣に行きますよ\n…ええ、他意など一切ない\n単なる気まぐれです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1150010, 0, "女性の晴れ姿を見た際には、まず\n言うべき事があると思いますが？\n…ふふ、よくできました\nもっと褒めてもいいのですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115001, 0.0, 2, 0, 6, 0, 7.7, 0, 0, 2, 1, 1150011, 0, "今日は貴方の誕生日ですね\n私の独断で贈り物を用意しました\nありがたく受け取りなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1150012, 0, "誕生日おめでとうございます\nまた一つ成長した貴方を\n見せてくれることを\n期待していますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115501, 0.0, 2, 0, 1, 0, 8.2, 0, 0, 1, 1, 1155001, 0, "どんな困難が行先を阻もうとも\nわたくしはこの手で明日を\n掴んでみせます。わたくしの\n決意を見ていてくださいまし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115501, 0.0, 2, 0, 2, 0, 0.0, 0, 0, 1, 2, 1155002, 0, "主さまのおそばにいられる幸せは\n当たり前のものではなく\nとても特別なことなのだと\n感じておりますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115501, 0.0, 2, 0, 3, 0, 4.7, 0, 0, 1, 6, 1155003, 0, "主さま、なにかお困りのことは\nございませんか？　これからも\n変わらず、わたくしになんなりと\nお申し付けくださいまし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115501, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1155004, 0, "この身に新たな力を宿せたのは\n全て主さまのおかげでございます\n恩返しできるよう、一生をかけて\nガイドさせていただきます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115501, 0.0, 5, 0, 5, 0, 8.8, 0, 0, 1, 2, 1155005, 0, "主さまをお慕いする気持ちには\nこの真っ白な装束と同じく\n一切の曇りもございません\nわたくしの全ては、主さまに…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1155006, 0, "わたくしは独りではございません\nかけがえのない仲間のため…\n絆を紡いだ主さまのために\nこの身の全てを捧げます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1155007, 0, "精霊たちが、わたくしたちの絆を\n祝福してくれているようです\n笑顔あふれる素晴らしい仲間に\n心からの加護を！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1155008, 0, "猛き風の精霊よ…\n未来に向かって歩まんとする\nわたくしたちの背中に\n祝福の追い風を！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1155009, 0, "絆を繋ぎとめてくださった\n主さまの優しいお手手…\nその温もりは、この胸に灯る\n希望の明かりでございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1155010, 0, "主さまの優しくて\n耳をくすぐるそのお声で\nどうかこれからもわたくしの名を\n呼んでくださいまし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115501, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1155011, 0, "今日は主さまがお生まれになった\nとてもおめでたい日ですね\n精霊たちとともに、心を込めて\nお祝いをさせてくださいまし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1155012, 0, "お誕生日おめでとうございます\nまた一つ大人になられましたね\n未来に向かって歩むその隣で\nこれからもわたくしはずっと…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115601, 0.0, 7, 0, 1, 0, 9.0, 0, 0, 1, 6, 1156001, 0, "精霊の意思を感じ取る…\nやっぱり難しいなぁ…\n騎士クンもやってみる？\n…あれ？　寝ちゃった！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115601, 0.0, 7, 0, 2, 0, 8.4, 0, 0, 1, 1, 1156002, 0, "精霊って、色んな子がいるんだね\n優しい子だけじゃなくて、\nいたずら好きな子も…ふにゅ！\nうぅ～…ほっぺ突っつかないで～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115601, 0.0, 8, 0, 3, 0, 9.6, 0, 0, 1, 7, 1156003, 0, "この服、刺激が強くないかな…？\nだってほら、こんなに布が\n少なくて…はっ！\nあんまり近くで見ないで～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115601, 0.0, 6, 0, 4, 0, 13.3, 0, 0, 1, 1, 1156004, 0, "精霊を使役してる時って\n感覚が普段より冴えるの\n今も風や光の変化を感じるよ…\n騎士クンは…お腹減ってきた？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115601, 0.0, 7, 0, 5, 0, 7.4, 0, 0, 1, 4, 1156005, 0, "…うん、騎士クンとの距離が\n中々縮まらな…え、騎士クン！？\nこ、これはその、精霊と話を\nしてて…い、今すぐ忘れて～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1156006, 0, "騎士クン…この子を一緒に\n育ててくれて、ありがとう\nわたしね、とってもうれしかった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1156007, 0, "大丈夫…伝わってるよ\nあなたの思いも、あなたの力も…\nずっと元気でいてね。約束だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1156008, 0, "騎士クンのためなら、\nいつでも呼んで…だって\nこの子もわたしも、\n同じ気持ちだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1156009, 0, "お父さん…ちがっ！　違うの！\nこの子が今、騎士クンのこと\nそう呼んでて…もぉ～！\nさっきのは忘れて～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1156010, 0, "人と精霊の気持ちが通じ合うなら\nこの気持ちも、騎士クンに\n通じたらいいのにな…あれ…？\n今、何も言ってない…よね…！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115601, 0.0, 2, 0, 6, 0, 6.4, 0, 0, 2, 1, 1156011, 0, "お誕生日おめでとう、騎士クン！\n精霊もおめでとうだって\nふふっ、やっぱり騎士クンは\nみんなに慕われてるんだね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1156012, 0, "お誕生日おめでとう！\nあ、あのね？　精霊から\n祝いの舞を教わったの\n踊ってみても…いい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115701, 0.0, 6, 0, 1, 0, 8.3, 0, 0, 1, 1, 1157001, 0, "助手くん、準備はできたかい？\n今度の事件現場は海だよ！\n二人で一緒にバカンス\n…じゃなかった、調査に出発だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115701, 0.0, 6, 0, 2, 0, 9.1, 0, 0, 1, 1, 1157002, 0, "海に持っていくものといえば\n手帳にルーペ、望遠鏡…\nあとは浮き輪にサーフボード…\nあれ…なにをしに行くんだっけ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115701, 0.0, 2, 0, 3, 0, 5.2, 0, 0, 1, 1, 1157003, 0, "海か…みんなに聞かれたら\nついていくって言いだしそうだ…\nだから、内緒にしておこうね？\n私と君だけの秘密、だよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115701, 0.0, 5, 0, 4, 0, 6.4, 0, 0, 1, 1, 1157004, 0, "この髪型、動きやすくていいね\n…助手くんも気に入ってくれた？\nじゃ、じゃあしばらくはこれで…\n深い意味はないんだよっ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115701, 0.0, 5, 0, 5, 0, 6.9, 0, 0, 1, 1, 1157005, 0, "事件、すぐに解決できるといいね\nえっ？　なんでって…それは…\n…助手くんと遊びたいからだよ！\nそれくらい見抜きたまえ、もう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1157006, 0, "助手くんっ、見たまえ、海だよ！\nなんだい？　その驚いた眼差しは\n私だってはしゃぎたいときもある\n…助手くんと海に来たときとかね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1157007, 0, "波打ち際の名探偵…\nう～ん、いい響きだ♪\nさぁ助手くん、青き水平線に\n隠されし真実、見つけに行こう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1157008, 0, "助手くん、君の観察力を試そう\n私は今、普段と違う格好だろう？\nなにか気づいたこととか…えっ？\n水着、似合ってる？　えへへ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1157009, 0, "世界にはまだまだ謎があふれてる\nこうして色々なところを巡って\n真実を見つけるのも…いいかもね\nもちろん助手くんと一緒に、だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1157010, 0, "いつも私は君のことを助手くんと\n呼んでいるだろう？　でも今は、\n遊びに来ているわけだから…\nその…やっぱりなんでもないっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115701, 0.0, 2, 0, 6, 0, 3.7, 0, 0, 2, 1, 1157011, 0, "さて助手くん、\n呼ばれた理由は分かるかな？\nふふ、私にはお見通しなのさ…！\n誕生日、おめでとうっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1157012, 0, "助手くん、誕生日だよね\nこれを受け取って欲しい\nこのプレゼントに込められた意味\n…解き明かしてみたまえ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115801, 0.0, 1, 0, 1, 0, 4.6, 0, 0, 1, 2, 1158001, 0, "まさか私が貴族様の舞踏会に\n出られるなんて…きちんと\nレッスンがんばらなきゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115801, 0.0, 2, 0, 2, 0, 4.2, 0, 0, 1, 1, 1158002, 0, "人間の姿になれるだけでも\nすっごく幸せなのに、こんなに\nすてきなドレスを着られるなんて\n本当に夢みたいだわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115801, 0.0, 1, 0, 3, 0, 4.4, 0, 0, 1, 4, 1158003, 0, "淑女ってどういう人のことを\nいうのかしら…森で木を切る\n淑女がいてもいいわよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115801, 0.0, 2, 0, 4, 0, 6.6, 0, 0, 1, 1, 1158004, 0, "牧場での暮らしは本当に幸せで\nこれ以上を望む必要なんて\nないけど、やっぱり華やかな\n世界も憧れちゃうわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115801, 0.0, 2, 0, 5, 0, 2.7, 0, 0, 1, 6, 1158005, 0, "ドレス姿が似合ってる？\nふふ、ありがとう。あなたに\nそう言ってもらえれば\n安心だわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1158006, 0, "アン・ドゥ・トロワ♪\nまさか私がきれいなドレスで\n踊れるなんて…キラキラした\n夢の中にいるみたいだわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1158007, 0, "こんなに素敵な場所で\nあなたと踊れるなんて…本当に\nお話の中のお姫様になったみたい\nあなたも王子様みたいで素敵よ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1158008, 0, "素敵なドレスを着て優雅に踊る…\nすっごく楽しいけど、あなたや\n牧場のみんなと居る時間も\n負けないくらい素敵な時間よね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1158009, 0, "あなたがいてくれたから\n私は明るく前向きでいられたの\nこれからは私が誰かのそんな人に\nなれたらいいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1158010, 0, "魔法はいつか終わっちゃう…\nでも魔法みたいに素敵な時間は\n自分の力で作っていけるのよね\n私、これからもがんばるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115801, 0.0, 1, 0, 6, 0, 2.3, 0, 0, 2, 2, 1158011, 0, "お誕生日おめでとう！\nあなたのために森で花を摘んで\nきたんだけど、受け取って\nもらえるかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1158012, 0, "あなたの特別な日をこうして\n一緒に迎えられるのはとっても\nうれしいわ♪　また来年も一緒に\n過ごしたいな…なんて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115901, 0.0, 5, 0, 1, 0, 0.0, 0, 0, 1, 6, 1159001, 0, "いつもズボンだから、\nスカートってのは落ち着かねえな\nま、まあ、こういう系の服も\nきらいじゃねえけどさ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115901, 0.0, 5, 0, 2, 0, 0.0, 0, 0, 1, 4, 1159002, 0, "パトロールに行きてえけど、\nこの服で行ったら目立つよなぁ\nえ、似合ってるから大丈夫って…\nそ、そういう問題じゃねえよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115901, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 1, 1159003, 0, "この服、結構動きやすいんだな\nへへっ、これなら、\n思いっきり暴れられそうだぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115901, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 1, 1159004, 0, "お嬢さまみたいな格好してる\nわけだし、しゃれた料理でも\n作ってみるか…もちろん、\nおまえの分も作ってやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115901, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 2, 1159005, 0, "どんな格好だろうと、\nあたしの剣の腕は変わらねえぜ\nなんなら、\n今から稽古つけてやろうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1159006, 0, "こんな格好してるやつが、\n戦えるわけないって？\n…だったら、\nその身で確かめてみるんだな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1159007, 0, "優雅に舞い…そして、\n狼のように敵をかみ砕く！\nあたしなりの華麗なる\n剣術ってやつを見せてやるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1159008, 0, "この服だと、お嬢さまになった\n気分だな…華麗に舞いますわ♪\nな～んて…！？　おまえいつから\nそこに…み、見るんじゃねぇ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1159009, 0, "なんだよ、急にじーと見てきて…\nそ、そういう服も似合うねって…\n急になに言ってんだよ！\nまあでも、あ、ありがとな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1159010, 0, "ここなら、思いっきり体を\n動かせそうだし、訓練してくか\nおまえも一緒にどうだ？\n二人ならもっと楽しめると思うぜ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115901, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 1, 1159011, 0, "今日はおまえの誕生日だよな\nせっかくだし、\nなんか飯でも作ってやるよ\nリクエストがあったら言ってくれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 115931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1159012, 0, "きょ、今日って誕生日だよな？\nおまえには世話になってるし、\nその…ケ、ケーキ焼いてきた\nから、さっさと受け取れ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116001, 0.0, 2, 0, 1, 0, 8.2, 0, 0, 1, 1, 1160001, 0, "マホマホ王国のお茶会へようこそ\nマホ姫が王子はんのために、\n愛を込めておもてなししますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116001, 0.0, 2, 0, 2, 0, 3.5, 0, 0, 1, 6, 1160002, 0, "日差しは苦手どすけど、\n春のひなたはぽかぽかしとって、\n心地ええなぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116001, 0.0, 2, 0, 3, 0, 10.1, 0, 0, 1, 1, 1160003, 0, "花の香り、小鳥のさえずり、\nそして王子はんと過ごす時間\nふふっ、ここは夢の国やね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116001, 0.0, 5, 0, 4, 0, 6.5, 0, 0, 1, 1, 1160004, 0, "王子はんに見てほしくて、\n衣装を新調したんよ\n王子はん、うちはきれいどすか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116001, 0.0, 2, 0, 5, 0, 4.8, 0, 0, 1, 1, 1160005, 0, "礼儀作法のことならうちに任し\nなんならうち、王子はんのために\nつきっきりで教えまひょか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1160006, 0, "王子はんに喜んでほしくて\nたくさんお菓子作ったんや\nああ、お礼なんていいんよ\n王子はんの笑顔だけで充分や♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1160007, 0, "テーブルマナーはお互いが\n仲よく過ごすための心配りなんよ\nでも一番大切なのは\n一緒に楽しい時間を過ごすことや");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1160008, 0, "王子はんと一緒におると\nうちの知らない世界が見えて\n楽しいわぁ。王子はんの世界も\nきっとワンダーランドなんやねぇ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1160009, 0, "童話の幸せな結末\nその続きはだれも分からへんけど\nうちと王子はんならずっとずっと\n幸せに暮らせそうやねぇ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1160010, 0, "今日はマホマホ王国のお茶会や\nマホ姫の特別なおもてなしで\n王子はんをたくさんたくさん\n甘やかしますわぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116001, 0.0, 1, 0, 6, 0, 4.4, 0, 0, 2, 2, 1160011, 0, "お誕生日おめでとう～\nうち王子はんのために\nケーキを用意したんよ\nおいしく召し上がってなぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1160012, 0, "今日は王子はんのお誕生日やね！\nそういうわけで…じゃーん！\nお花のシャワーや！\nふふっ、きれいやろう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116201, 0.0, 4, 0, 1, 0, 9.0, 0, 0, 1, 1, 1162001, 0, "テレフェスっていうか、\nまあ体育とかスポーツ的なのは\n得意な部類だよね、個人競技は\n団体競技は…協調性が課題だわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116201, 0.0, 6, 0, 2, 0, 0.0, 0, 0, 1, 6, 1162002, 0, "いいけどユニ先輩のフェス服、\nアレ何なの。似合ってるけど\nチエルのも無駄にデコってて、\nアレ何なの。似合ってるけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116201, 0.0, 6, 0, 3, 0, 6.1, 0, 0, 1, 1, 1162003, 0, "いんだけどさ、最近テレ女の空気\n全体的にちょっとアレじゃない？\nや、なんか校風崩れっていうの？\nルーセンティズム入ってきてね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116201, 0.0, 6, 0, 4, 0, 10.1, 0, 0, 1, 8, 1162004, 0, "そういやあんたもどっかの学校で\nスポーツ経験あるらしいね\nそうなんだ…へー、いいじゃん\nえ、海辺で野球？　何それ冗談？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116201, 0.0, 1, 0, 5, 0, 8.5, 0, 0, 1, 5, 1162005, 0, "え、ちょ何…ガン見やめてよ\nわりかし肌出てンの思いだして、\n急にモジついて来ちゃうじゃん…\nあ？　寒そう？　いいえ、別に");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1162006, 0, "よっ、と楽勝…！\nつーかさ、障害物競走なんて、\nフツー子供のやるもんでしょ…\nマジでこの学校アタオカだわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1162007, 0, "ちょー、いつもの外野うるさい\nハズめの声援やめてくんない\nあと、うち不良ではないから\nいや毒婦でもねーわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1162008, 0, "そんな、うちの応援とかいいから\nあんたも自分の競技がんばりー？\n次なに出んの。あ？　二人三脚？\nえ、誰と。別にいんだけど、誰と");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1162009, 0, "なんか悪いね、あんたに弟の\n面倒まで見てもらっちゃって…\nそだ、このあと家族とおべんと\n食べんだけど良ければ一緒にど？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1162010, 0, "ハ？　いや、格好いいとかそんな\n真顔で言うフツー？　はっず\nや、べつに言うなってわけじゃ…\n言いたきゃそれは…言いなよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116201, 0.0, 1, 0, 6, 0, 7.7, 0, 0, 2, 6, 1162011, 0, "あんた今日、誕生日だって？\nなんで言わんの、そーゆーこと…\nなんも用意してなかったじゃん\nじゃあ飴ちゃん多めにあげるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1162012, 0, "あんた今日、誕生日だよね\nああいや、さっきチエルに聞いて\nそだ、偶然ケーキ作ってきたから\nコレあげるわ。いや偶然だけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116301, 0.0, 3, 0, 1, 0, 4.1, 0, 0, 1, 6, 1163001, 0, "先輩、大変です！\n若者のちぇる離れが深刻です！\nちぇる語普及委員の一人として\nどうお考えなんですか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116301, 0.0, 1, 0, 2, 0, 6.8, 0, 0, 1, 7, 1163002, 0, "先輩、大変です！　今ならなんと\nこの「幸せになれるちぇる」が\n１万ルピでお手元に。どうです？\nちぇるは肉眼では見えませんけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116301, 0.0, 4, 0, 3, 0, 8.3, 0, 0, 1, 6, 1163003, 0, "クロエ先輩がチエルのフェス姿\n可愛いって言ってくれないんです\n…え、先輩の前では言ってた？\nやだー！　直接言ってほしいー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116301, 0.0, 6, 0, 4, 0, 10.4, 0, 0, 1, 1, 1163004, 0, "そういえば先輩もどっかの学校で\nスポーツ経験あるらしいですね\nそうなんだ…ふうん意外な一面…\nえ、海辺で野球？　何それ冗談？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116301, 0.0, 2, 0, 5, 0, 5.4, 0, 0, 1, 7, 1163005, 0, "ちぇる～ん☆　どうですか、この\nチエル仕様のフェス用ウェア！\nえっ？　そんなに似合ってます？\nですよね、知ってます。えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1163006, 0, "ちぇる～ん！　ちぇる　ちぇろぱ\nちぇるぴぴぱ　ちぇぴらぺぽー？\nうんうん、だよねーおっけー！\nじゃあリズムを上げて行くよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1163007, 0, "みんなー！\nちえるん単推しなのは分かるけど\n後ろの地味な二人にも\n少しは歓声送ってあげてねー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1163008, 0, "やっぱりフェスの最後はお約束、\nヒロインたちのライブだよねー！\n会場のみんなも配信のみんなも…\nえ、これ魔石配信してないの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1163009, 0, "ちぇるミナすこすこ侍の先輩も\nファンサで釣ってあげますよー！\nそうれ、投げキッＣＨＵ～☆\nって、何よそ見してるんですか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1163010, 0, "歌って踊れるスーパーアイドル、\nこれが世界の美少女ちえるんです\nどうです先輩、惚れ直しました？\nえ、真顔で頷かれても…困る…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116301, 0.0, 1, 0, 6, 0, 7.3, 0, 0, 2, 2, 1163011, 0, "お誕生日おめでとうございます！\n可愛い後輩から可愛いちぇるを\nプレゼント！　ちぇる～ん☆\n良かったですね、一生ものですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1163012, 0, "先輩、ハピバ～！\nじゃあ今日は特別なんでチエルが\n一緒にケーキ食べてあげますね！\nお祝いだから割り勘でいいですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116401, 0.0, 1, 0, 1, 0, 8.7, 0, 0, 1, 4, 1164001, 0, "聖学祭か…気が重いな…\n学芸発表はともかく運動競技は…\nうむ、人にはそれぞれ適性がある\n荒事は荒くれ後輩たちに任せよう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116401, 0.0, 1, 0, 2, 0, 6.8, 0, 0, 1, 6, 1164002, 0, "聖学祭に提出したぼくの芸術は、\nあまり評価を得なかったらしい\nぼくは印象派だからな、\n心得のない者には分からぬ領域だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116401, 0.0, 2, 0, 3, 0, 10.6, 0, 0, 1, 1, 1164003, 0, "高機動万能靴『神速くん』\nこれはからくり工学の成果物を、\nぼくが魔法で再解釈したものだ\n理系不要論の物証なのだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116401, 0.0, 4, 0, 4, 0, 10.7, 0, 0, 1, 1, 1164004, 0, "ときに後輩、君はどこぞの学院で\nスポーツを学んできたらしいな\nそれはどういった類の…\nなに、海で野球？　難しいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116401, 0.0, 6, 0, 5, 0, 10.5, 0, 0, 1, 1, 1164005, 0, "君と二人で撮った写真だが、\n塔の書庫の深くに保管してある\n他人に見せるのは躊躇われてな\n何なのだろうか、この情動は…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1164006, 0, "年に一度の文武の祭典…\n皆が祭囃子に浮足立つ日こそ、\n賢者はあえて日常を貫くのだよ\nではロゼッタ、後はよしなに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1164007, 0, "これは決してゴロ寝ではない\n体力温存と精神集中を兼ねた\n革新的なトレーニング法なのだ\n端的に換言すれば、ゴロ寝だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1164008, 0, "ぽーん！　子守唄を再生します！\n♪ユニちゃん良い子だ眠りゃんせ\n♪夢の扉で真理に接続\n♪多次元宇宙で神を見な～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1164009, 0, "なに、ぼくの運動不足が心配？\nそうか、よくぞ言ってくれた\nでは君に下肢部のストレッチを\n依頼する。ほれほれ、解したまえ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1164010, 0, "やあ同志、君も午睡に来たのか\n遠慮せず横になりたまえよ\nロゼッタの膝枕は売約済みだが、\nぼくので良ければ貸してやろう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116401, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1164011, 0, "おい同志、ぼくのメモによれば\n今日は君の誕生日じゃあないか\n祝いにぼくの講義を授けよう\n席につきたまえ、夜は長いぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1164012, 0, "ちぇる～ん♪　後輩ハピバ！\nぼくは都の風習に疎いゆえ、\nチエル君に祝い方を聞いてきた\nどうだ愛くるしいだろう。にこり☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116501, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1165001, 0, "はーっはっはっは！\nビッグボス・イノリ様の登場です\nお兄さんは今日から側近として\nあたしをサポートするですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116501, 0.0, 3, 0, 2, 0, 10.4, 0, 0, 1, 2, 1165002, 0, "あたしの知名度が広まるよう\nお兄さんはあたしを担いで…え？\n肩車すればいいのかって…\nそういう意味じゃねーですよ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116501, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1165003, 0, "あたしがランドソルで一番の\nボスになったら、カルミナを\n呼んでパーティーです！\nお兄さんも呼んであげますから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116501, 0.0, 3, 0, 4, 0, 11.0, 0, 0, 1, 6, 1165004, 0, "このポーズとこのポーズ\nどっちがかっこいいですか！？\n…どっちもかわいい？\nガ、ガキ扱いするなですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116501, 0.0, 3, 0, 5, 0, 7.9, 0, 0, 1, 5, 1165005, 0, "お兄さんの仕事は\nそのお人好し力を\nあたしのために使うことです！\nよそ見は禁止ですよ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1165006, 0, "どうですか、このポーズ！\n…え？　台の上はあぶない？\nお兄さんは分かってないですね\n高い所に登るのがボスの鉄則です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1165007, 0, "お兄さんには見えますか！\n空に輝くあの一番星が！\nあれこそギャングスターです！\n今は昼？　心の目で見るですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1165008, 0, "あたしがボスになったら\nイノリのお菓子工場を作らせます\nお小遣いとお菓子の両取りですよ\nくぅ〜！　夢が広がるです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1165009, 0, "あたしの計画ではお兄さんは側近\nカヤぴぃは右腕にして、ふふっ\nボスは雑用係にしてやるですよ！\nぴっ！？　今寒気がしたような…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1165010, 0, "ふっ、この服どうですか？\nかっこいいって言っても…\nはぁ！？　か、かわいいって\n言うのは、やめやがれですー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116501, 0.0, 2, 0, 6, 0, 2.9, 0, 0, 2, 6, 1165011, 0, "今日はお兄さんの誕生日？\n一億年前から知ってましたよ！\nタコスおごるので、お兄さんは\nあたしにタコスおごるですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1165012, 0, "お誕生日おめでとうです！\nあたしは部下想いのボスですから\nわがままを聞いてあげなくも…\nってなんですその本気の目は！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116601, 0.0, 1, 0, 1, 0, 6.4, 0, 0, 1, 6, 1166001, 0, "全く、喧嘩屋のオレに\nこんな格好までさせやがって\nまあ、やるからにはキッチリ\nやるさ。半端なのは嫌いだからな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116601, 0.0, 6, 0, 2, 0, 7.5, 0, 0, 1, 1, 1166002, 0, "この格好、\n案外動きやすいんだよな\nこれならケンカも…\nや、やらねえよ！　…多分");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116601, 0.0, 6, 0, 3, 0, 4.9, 0, 0, 1, 2, 1166003, 0, "本日はご利用いただき\nありがとうございまーす！\n…自分で言うのもなんだけど\n全然似合わねーな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116601, 0.0, 6, 0, 4, 0, 13.5, 0, 0, 1, 4, 1166004, 0, "この仕事は笑顔が大事\n分かっちゃいるけど難しいぜ…\nに、にこぉ…って\n何見てんだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116601, 0.0, 2, 0, 5, 0, 6.0, 0, 0, 1, 1, 1166005, 0, "キミがついてきてくれてると\nなんだか一緒に旅してる\nみたいな気がする\n結構楽しいもんだな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1166006, 0, "よく来たな。人気スポットから\n危険地帯まで、どんなトコでも\nガイドしてやるよ\nさあ、たぎるような旅に出発だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1166007, 0, "現地でトラブルがあったら\n迷わずオレを呼びな！\nランドソルで一番荒事に強い\nガイドだからな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1166008, 0, "キミの頼みならどんな危険な\n場所だって連れてってやるよ\nドラゴン族渾身のガイドで\n存分に楽しませてやるからな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1166009, 0, "キミがいなけりゃ、この仕事も\nこんなに上手くいかなかった\nだろうな。やっぱダチってヤツは\n最高だぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1166010, 0, "なあ、いつか二人で\n旅に行こうぜ！　仕事とか\n関係なく、好きなとこ行くんだ\nキミとならきっと楽しいだろうな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116601, 0.0, 2, 0, 6, 0, 2.0, 0, 0, 2, 1, 1166011, 0, "今日誕生日だろ？\nほら、プレゼントだ\nこの前土産物屋に行った時に\nいいなって思ってさ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1166012, 0, "よう、誕生日おめでとう！\n今日はオレが厳選した店に\n連れてってやるよ。年に一度の\n大事な日だ、パーッと祝おうぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116701, 0.0, 2, 0, 1, 0, 5.4, 0, 0, 1, 3, 1167001, 0, "…よし！　…よし！　…よし！\nうん、発声練習終わり…！\n今日も一日、ベルトコンベアと\nにらめっこです…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116701, 0.0, 2, 0, 2, 0, 0.0, 0, 0, 1, 2, 1167002, 0, "日が昇る前から\n工場の前に立ってますよ…！\n無遅刻無欠勤を目指します！\nえっへん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116701, 0.0, 2, 0, 3, 0, 5.7, 0, 0, 1, 1, 1167003, 0, "ランチは工場の先輩から\n誘われるまで倉庫の隅っこで…\nあなたも一緒にどうですか…？\n静かで過ごしやすいですよ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116701, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1167004, 0, "工作の技術は\nマイフレンドくん作りで\n身に付けました…！\n工場でも通用するといいなあ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116701, 0.0, 6, 0, 5, 0, 6.8, 0, 0, 1, 4, 1167005, 0, "うう…人生初めてのアルバイト…\n不安で消えてなくなりそうです…\nで、でも…あなたも一緒だから\nきっと大丈夫ですよね…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1167006, 0, "『だいじょぶマイフレンドくん\nＶ４』のカスタマイズも…\nバイト仲間を作ってぼっち卒業も\nこのバイトで叶えてみせます…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1167007, 0, "ぼっちの私に、誰かと一緒に\n作業する日が来るなんて…！\nまさか夢…！？\n絶対そうに決まってます〜！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1167008, 0, "バイト先でもＢＢ団の使命は\n忘れませんよ…！\nぼっち卒業を目指して\nバイト仲間作りにも励みます…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1167009, 0, "工具や機械の使い方も\n慣れてきました…！\nけど、バイト先の人との会話は\n全然慣れません…ううっ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1167010, 0, "ＢＢ団でそろって\n一緒にバイトができるなんて…！\nおもちゃも、バイト仲間も\n作りましょうね…えへへ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116701, 0.0, 2, 0, 6, 0, 7.9, 0, 0, 2, 6, 1167011, 0, "あなたのお誕生日を祝う言葉を\nわ、私が…！？　し、幸せすぎて\nちょっと怖いですが…せーのっ\nお誕生日おめでとうございます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1167012, 0, "今日はあなたのお誕生日をお祝い\nさせてください！　歌に、花束の\n贈呈…それと100通の手紙です\n…では心を込めて歌います！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116801, 0.0, 2, 0, 1, 0, 5.4, 0, 0, 1, 1, 1168001, 0, "たい焼きいかがかにゃ～！\n…あ、キミいいところに！\n手伝ってくれたら特別に\nたい焼きの賄いをあげるにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116801, 0.0, 1, 0, 2, 0, 5.8, 0, 0, 1, 2, 1168002, 0, "にゃはは♪\nこのたい焼きメーカーがあれば\n売り上げ爆増だにゃ！\n貯金箱が貯まっていくにゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116801, 0.0, 2, 0, 3, 0, 3.8, 0, 0, 1, 1, 1168003, 0, "お客さんが美味しそうに\nたい焼きを食べてるにゃ！\nやっぱりたい焼きは\nみんなを幸せにするにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116801, 0.0, 1, 0, 4, 0, 6.0, 0, 0, 1, 6, 1168004, 0, "キミ、じっとあたしを見てるけど\nこの服装気に入ったにゃ？\nなんなら…特別価格で\nポーズしてあげるにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116801, 0.0, 2, 0, 5, 0, 8.7, 0, 0, 1, 1, 1168005, 0, "忙しくても笑顔は忘れにゃい！\n商売人の鉄則にゃ！\nほら、キミも笑顔にゃ！\n…うん、その方が男前にゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1168006, 0, "忙しくても大丈夫にゃ♪\n猫の手に代わって\n自動たい焼きメーカーが\nあたしをサポートしてくれるにゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1168007, 0, "たい焼きを買ってくれたお礼に\nスマイルもサービスするにゃ！\nウェイトレスのバイトも\n出来そうな気がするにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1168008, 0, "たい焼き屋の商売が忙しくても\n義賊活動でピンチになっても\n背中を預ける人がいれば平気にゃ\nこれからもよろしくにゃ、相棒！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1168009, 0, "キミへ賄いのたい焼きにゃ！\nサイズが大きいのは\nいつも助けてもらってるお礼って\nことで、受け取るにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1168010, 0, "お客さんの笑顔が\nあたしの夢の源にゃ！\nそれでキミの笑顔は…\nあたしの元気の源にゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116801, 0.0, 1, 0, 6, 0, 5.6, 0, 0, 2, 2, 1168011, 0, "誕生日おめでとうにゃ！\n特別にたい焼きケーキを\n作ってきたにゃ！\n食べさせてあげようかにゃ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1168012, 0, "今日はキミの誕生日にゃ！\n特別に0ルピで今日はあたしを\n貸し出ししてあげるにゃ！\nなんならデートするかにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116901, 0.0, 2, 0, 1, 0, 4.6, 0, 0, 1, 1, 1169001, 0, "私たちが作ったおもちゃで\n子どもたちが喜んでくれる…\nふふ、やりがいのある\n仕事って素敵よね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116901, 0.0, 1, 0, 2, 0, 1.7, 0, 0, 1, 6, 1169002, 0, "こういう作業服って、\n普段着とは違って\nちょっと新鮮な感じが\nしていいわね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116901, 0.0, 1, 0, 3, 0, 3.9, 0, 0, 1, 6, 1169003, 0, "機械や工具のメンテも\n定期的にやらないと…\nみんなが使うものほど\n丁寧に扱わないとね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116901, 0.0, 1, 0, 4, 0, 4.6, 0, 0, 1, 2, 1169004, 0, "キミを一人前に鍛えるのも\nバイトリーダーのお仕事だもの\nわからないことがあれば\nすぐに聞いてちょうだいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116901, 0.0, 2, 0, 5, 0, 7.3, 0, 0, 1, 5, 1169005, 0, "キミさえよければ、このまま\n私の右腕としてずっと\n一緒にいてくれないかしら…\nなんてね。さ、仕事するわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1169006, 0, "リーダーとして\nキミを導いてみせるわ！\nもちろん、効率全開で！\nさあ、準備はいい！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1169007, 0, "今日の製造ノルマも\n順調に消化中…いい感じね！\nさあ、このまま最大効率で\n走り抜けるわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1169008, 0, "アオイちゃんもタマキさんも\nよく働いてくれて助かるわ\nキミも一緒なら鬼に金棒よ！\nどんなノルマもどんとこーい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1169009, 0, "お仕事が終わったら\n夕飯でも一緒にどう？\n近場の安くておいしい\nお店、見つけたのよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1169010, 0, "キミといるとそれだけで\nいつも以上に能力を\n発揮できる気がするのよね…\nなんでなのかしら…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116901, 0.0, 2, 0, 6, 0, 4.5, 0, 0, 2, 1, 1169011, 0, "誕生日おめでとう！\nまた一歩大人になったわね\nキミの成長をそばで見てると\n私も師匠として鼻が高いわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 116931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1169012, 0, "今日はキミの誕生日よね？\nお祝いのケーキを作ってみたの\n自分で作ると節約できるし、\n味にもけっこう自信あるのよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117001, 0.0, 5, 0, 1, 0, 6.1, 0, 0, 1, 1, 1170001, 0, "真夏の日差しのもと、あなた様の\nすべてを手に入れます…\n運命の伴侶として…\n永遠に…離しませんわ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117001, 0.0, 2, 0, 2, 0, 5.5, 0, 0, 1, 1, 1170002, 0, "私たちの間を引き裂くものは\nすべて破壊しつくしますわ…\nそう、海の藻屑と消えるまで\n細かく細かく…念入りに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117001, 0.0, 2, 0, 3, 0, 3.4, 0, 0, 1, 6, 1170003, 0, "この水着、似合いますか？\n…えぇ、いいお返事…\nあなた様との最高の夏のために\n最高のものを選びましたわ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117001, 0.0, 2, 0, 4, 0, 9.9, 0, 0, 1, 1, 1170004, 0, "海で水着くらい当然ですわ…\nでもあなた様に褒めていただける\nのは純粋にうれしいもの\nクスクス…もっと褒めてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117001, 0.0, 2, 0, 5, 0, 15.4, 0, 0, 1, 5, 1170005, 0, "クスクス…真夏の海で二人きり…\n私だけを…見てくださいね…\nよそ見をしたらどうなるかは\n…秘密です…クスクス…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1170006, 0, "夏の夜空に花を咲かせましょう\nあなた様が私以外全て\n忘れてしまうくらい…めくるめく\n夢のような花火を…クスクスクス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1170007, 0, "私とあなた様が結ばれるべき\n運命だということを…この夏に\n証明して差し上げますわ\nどんな手段を使ってでも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1170008, 0, "この水着、似合いますか？\nあなた様を想って選びました…\n生地もこだわって…ほら、もっと\n近くで見てください…クスクス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1170009, 0, "昼も夜もあなた様のことを\n考えてしまいます…あなた様も\nずっと私のことを考えて\nくださいね…絶対に…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1170010, 0, "寄せては返す波のように\n空に瞬く花火のように\nずっとあなた様といたいのです…\n愛していますわ、あなた様…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117001, 0.0, 5, 0, 6, 0, 8.4, 0, 0, 2, 1, 1170011, 0, "お誕生日おめでとうございます\nあなた様…特製の薬草茶で\n乾杯しましょう…そして薬草\n料理のフルコース…クスクス…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1170012, 0, "あなた様の誕生日…この日を\n共に迎えることができて私は\n幸せですわ…これからも毎年\n私とだけお祝いいたしましょう…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117101, 0.0, 1, 0, 1, 0, 5.0, 0, 0, 1, 2, 1171001, 0, "二人で一緒に海デート♪\n仲良しきょうだいだもん\nこれくらい普通だよねっ！\nあ、腕組んで歩こっ、弟くんっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117101, 0.0, 2, 0, 2, 0, 3.2, 0, 0, 1, 1, 1171002, 0, "熱い日差しにキラキラ輝く海！\nそして水着のお姉ちゃん♪\n弟くん、ワクワクしてこないっ？\nねぇねぇ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117101, 0.0, 5, 0, 3, 0, 6.3, 0, 0, 1, 6, 1171003, 0, "この水着、過激すぎないよね？\n弟くんを誘惑しすぎないよう\n気をつけなくちゃ…だけど…\nもう少しだけ、大胆にしちゃお♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117101, 0.0, 2, 0, 4, 0, 4.6, 0, 0, 1, 1, 1171004, 0, "弟くんが海で危ない目にあっても\nお姉ちゃんがいるから大丈夫！\n絶対にキミのそばを離れたり\nしないんだからねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117101, 0.0, 2, 0, 5, 0, 7.5, 0, 0, 1, 5, 1171005, 0, "ねぇ、もっとくっついて歩こう？\n周りの人たちに見せつけたいんだ\nお姉ちゃんの自慢の弟を♪\nえへへ、照れない照れない♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1171006, 0, "弟くん、休憩中かな\nお姉ちゃんも一緒に休んでいい？\nふふっ、あんまり見つめられたら\nお姉ちゃん、照れちゃうな…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1171007, 0, "ねっ、次はなにしてあそぼっか！\n弟くんが楽しめること\nぜ～んぶやっちゃおう♪\nほら、立って立って♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1171008, 0, "この水着ね\n弟くんが好きそうなのを\n一生懸命選んだんだよ？\nじ～っくり、堪能してねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1171009, 0, "弟くん、暑いでしょ？\nお姉ちゃんが冷やしてあげよっか\n…ふ～っ♪\nえへへ、涼しいでしょ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1171010, 0, "弟くんと一緒に海水浴なんて\nお姉ちゃん、ドキドキしちゃうな\n…ねぇ、弟くん？　私たち…\n周りからはどう見えるのかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117101, 0.0, 2, 0, 6, 0, 7.1, 0, 0, 2, 1, 1171011, 0, "弟くんのお誕生日だから\n水着でお祝いしてあげるねっ♪\nえ？　なんで水着なのかって？\nもうっ、うれしいくせに～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1171012, 0, "弟くん、欲しいものはある？\nまた一つ大人になった弟くんには\nなんでもあげちゃうよっ♪\nお姉ちゃんでも…いいんだぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117201, 0.0, 7, 0, 1, 0, 4.6, 0, 0, 1, 1, 1172001, 0, "夏といえばアイドルの季節！\n歌って、踊って、キラキラして！\nみーんなまとめて\n笑顔にしちゃうから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117201, 0.0, 1, 0, 2, 0, 4.3, 0, 0, 1, 6, 1172002, 0, "アイドルって結構、\n体力勝負なとこあるんだよね\n今度、砂浜で走りこもうっと！\nキミも一緒に走る？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117201, 0.0, 1, 0, 3, 0, 4.7, 0, 0, 1, 2, 1172003, 0, "ねえねえ！　せっかくの夏だし、\nお化け屋敷にでも行こう！\n怖くなっちゃったら、\n私の手を握ってもいいよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117201, 0.0, 2, 0, 4, 0, 7.3, 0, 0, 1, 6, 1172004, 0, "水着のために、\nダイエット頑張ったんだよ！\n誰か褒めてくれないかなー？\n…えへへ、ありがと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117201, 0.0, 2, 0, 5, 0, 6.4, 0, 0, 1, 4, 1172005, 0, "夏の紫外線はすっごく強烈なの！\nキミも気をつけた方がいいよ？\n…というわけで\n私の日傘で相合い傘しよっか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1172006, 0, "水着はアイドルを輝かせる\n特別な衣装なの！\nいつもより更にキラッキラな私に\n夢中になっちゃってもいいよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1172007, 0, "海も空もきれいだけど、\nノゾミの方がもっときれいだよ…\n的なこと言ってもいいんだよ？\nなーんてね、ふふっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1172008, 0, "今度、いつキミと海に来られるか\n分かんないし…\n今日は徹底的に遊んで\nいーっぱい思い出、作っちゃお♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1172009, 0, "私って人気アイドルなんだよ？\nそんな私の水着を見たのに、\n普段通りの反応？　ふふふ、\nキミのそういうとこ、好きだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1172010, 0, "きゃっ！　…もー、やったな～！\n私も水かけちゃうから！　えい！\n…なんかバカップルっぽいね！\nあはは！　さ、もっと楽しもう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117201, 0.0, 5, 0, 6, 0, 7.0, 0, 0, 2, 2, 1172011, 0, "誕生日おめでとー！\nキミの好きそうなアイスケーキ、\n人気のお店で買ってきたよ♪\n後で、一緒に食べようね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1172012, 0, "ハッピーバースデー！\nキミへのプレゼントは…\n私を今日だけ独り占めできる券！\n…なんて、どうかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117301, 0.0, 2, 0, 1, 0, 9.8, 0, 0, 1, 1, 1173001, 0, "きらめく太陽に美しい海…\n夏まっさかり、ですね\nあなたと過ごす夏がどんなものに\nなるのか…楽しみです。ふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117301, 0.0, 2, 0, 2, 0, 7.1, 0, 0, 1, 1, 1173002, 0, "この衣装、ツムギが\n作ってくれたんです\n少し恥ずかしいですけど…\nでも、大好きな衣装です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117301, 0.0, 6, 0, 3, 0, 6.6, 0, 0, 1, 2, 1173003, 0, "ビーチバレーにバーベキュー…\n夏にやってみたいこと、\nたくさんあるんです！\n…少し浮かれ過ぎでしょうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117301, 0.0, 4, 0, 4, 0, 8.2, 0, 0, 1, 6, 1173004, 0, "日焼けには気をつけないと\n…え？　日焼け止めを塗るの\n手伝おうかって…え、えっと\nでは…お、お願い…します…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117301, 0.0, 5, 0, 5, 0, 6.5, 0, 0, 1, 1, 1173005, 0, "波の音が好きなんです\nまるで心が洗われるようで…\n…よければ、ですけど\n一緒に、海を見に行きませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1173006, 0, "夏は開放的な気持ちになりますね\n今なら素直に\n自分の想いを歌に込められそう…\n私の夏の歌、聴いてもらえますか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1173007, 0, "海で歌うのがこんなにも\n楽しいなんて知りませんでした\nあなたも一緒に歌いませんか？\n夏の思い出を共に作りたいんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1173008, 0, "唱喚士として、アイドルとして…\nみんなの期待に応えるためにも\nこの夏、もっと成長したいです\nどうか見守っていてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1173009, 0, "まるで人魚みたい、ですか…？\nなら、おとぎ話のように、\n歌であなたを魅了しちゃいます\n…なんて、ふふ。冗談です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1173010, 0, "今までの人生の中で\nこの夏が一番ドキドキして\n一番、幸せでした\n…あなたのおかげです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117301, 0.0, 1, 0, 6, 0, 2.2, 0, 0, 2, 2, 1173011, 0, "ハッピーバースデー！\nあなたと過ごした夏は、\n私にとって大切な思い出です\n今後ともよろしくお願いしますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1173012, 0, "お誕生日、おめでとうございます\n夏の太陽のようなあなたに贈る\nバースデーソングを作りました\n気に入ってくれるといいのですが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117401, 0.0, 1, 0, 1, 0, 7.6, 0, 0, 1, 6, 1174001, 0, "いちにーさんしー…\nえ？　何してるのかって\nライブの練習ですよ\n騎士さんも一緒にやります？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117401, 0.0, 1, 0, 2, 0, 7.7, 0, 0, 1, 4, 1174002, 0, "レイ様と海へ来たかったな…\nあ、別に騎士さんと一緒が\n不満ってわけじゃないですよ！？\nむしろ結構楽しんでますから！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117401, 0.0, 1, 0, 3, 0, 5.8, 0, 0, 1, 6, 1174003, 0, "騎士さんもう疲れたんですか？\nもう、だらしがないですねぇ\n夏バテなら大変なんで\nこっちで一緒に涼みましょうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117401, 0.0, 6, 0, 4, 0, 3.6, 0, 0, 1, 2, 1174004, 0, "今日は仕事のことを忘れて\n遊びますよ！\n…あれ、あの子の水着かわいい\n次のデザインの参考にしよう…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117401, 0.0, 3, 0, 5, 0, 5.3, 0, 0, 1, 6, 1174005, 0, "さっきから何見てるんですか？\n言いたいことはハッキリと…\nあ、でも変なこと言ったら\n縛りますからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1174006, 0, "ん～！　いい天気ですね！\n風も気持ちい～！\n今日は思いっきり遊びますよ！\n騎士さんも付き合ってください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1174007, 0, "どうですか、この水着！\nサマーライブのために\n私が作った衣装なんですよ！\nかわいいでしょう！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1174008, 0, "どうですか？\nアイドルと一緒に過ごす夏は？\n…すごく楽しいって\nほんと騎士さんは素直ですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1174009, 0, "騎士さん、泳げないんですか？\nそれなら浅瀬へ行きましょう\n…一人で泳いでたら\n騎士さんと来た意味ないですし…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1174010, 0, "あれ？　この浜辺よく見たら\nカップルだらけじゃないですか…\nということは私たちも…\nだとしても私は気にしませんよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117401, 0.0, 5, 0, 6, 0, 5.1, 0, 0, 2, 3, 1174011, 0, "レイ様があげるというので\n私からもプレゼントです\nべ、別に仕方なくじゃないです\nちゃんと心は込めてるので！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1174012, 0, "大したものじゃないですけど\n作ってみたのでどーぞ\nどうして僕にって…\nわ、私も誕生日ぐらい祝います！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117501, 0.0, 1, 0, 1, 0, 4.5, 0, 0, 1, 2, 1175001, 0, "さあ、診察を始めましょう\n最初の患者は…もちろん君よ\n私の助手を務めてもらうからには\n健康でいてもらわないと♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117501, 0.0, 5, 0, 2, 0, 4.4, 0, 0, 1, 1, 1175002, 0, "特別にあつらえた\nこの服、どう？\nふふっ、君には少しだけ\n刺激が強いかしら♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117501, 0.0, 2, 0, 3, 0, 7.2, 0, 0, 1, 1, 1175003, 0, "せっかくオーエドまで来たんだし\nこの機会にカンポーを使って\n新しい薬を作りたいわね\n君も実験に付き合ってね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117501, 0.0, 1, 0, 4, 0, 4.8, 0, 0, 1, 6, 1175004, 0, "私は闇医者だから、善意だけで\n仕事はしないわ。でも…\n人助けもたまには悪くないわね\nふふっ、誰の影響かしら♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117501, 0.0, 2, 0, 5, 0, 8.4, 0, 0, 1, 1, 1175005, 0, "どれほど優秀な医者でも、\n一人ではできることに限りがある\nそれは闇医者も同じよ…だから\n君のサポート、頼りにしてるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1175006, 0, "滋養薬に強壮薬、回復薬に劇薬も\n私はどんな薬だって作れるのよ\nこれが薬か毒薬か\n君の体で試してみる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1175007, 0, "こう見えて薬で治すだけじゃなく\n手術の腕もなかなかなのよ\n痛みなんて感じさせないわ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1175008, 0, "医者として、科学者として…\n君は本当に興味深い研究対象だと\n思っているの。これからも\n実験や観察、させてもらうわね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1175009, 0, "少し疲れちゃったわ…\n肩を貸してもらえるかしら？\n君にもたれて仮眠をすると\nとても安心できるのよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1175010, 0, "いいところに来たわね\n新薬の実験に付き合ってくれる？\n私に服従する薬よ\nあら、遠慮しなくてもいいのに♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117501, 0.0, 5, 0, 6, 0, 8.3, 0, 0, 2, 2, 1175011, 0, "お誕生日おめでとう\nこれからも健やかでいられるよう\nお祝いに薬膳鍋を用意したわ\nほら、口をあけて…あ～ん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1175012, 0, "ハッピーバースデー\nお祝いに特製の薬を調合したの\nひと口で全身に力がみなぎるわ\nまだ、実験段階のものだけどね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117601, 0.0, 1, 0, 1, 0, 6.4, 0, 0, 1, 2, 1176001, 0, "うーん、さすがボク、\nどんな服を着ててもかわいいね♪\nほらほら、もっと近くで見ないと\n美しさを堪能できないよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117601, 0.0, 1, 0, 2, 0, 6.1, 0, 0, 1, 2, 1176002, 0, "ふふっ！　どう？\nいつもと違うボクの美しさ！\nしっかり目に焼き付けて\nおくといいよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117601, 0.0, 1, 0, 3, 0, 6.9, 0, 0, 1, 6, 1176003, 0, "なんだい？　もしかして\nボクに看護してほしいのかな？\nだからって、仮病はだめだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117601, 0.0, 6, 0, 4, 0, 5.4, 0, 0, 1, 1, 1176004, 0, "ただでさえ美しいボクが\n白衣の天使だなんて…\nもしかして病気の人には\n刺激が強すぎるかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117601, 0.0, 5, 0, 5, 0, 6.7, 0, 0, 1, 1, 1176005, 0, "天使なボクの姿を\nもっと見たいのかい？\nキミにならいいよ\n心ゆくまでじっくりと見てて♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1176006, 0, "美しいボクはこうしているだけで\nみんなを癒しちゃうんだよね\n白衣の天使ってボクの\n天職かもしれないね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1176007, 0, "もしキミが疲れちゃった時は\nこのボクの最高のかわいさで\nたっぷりと癒してあげるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1176008, 0, "ボクのこのかわいらしさ…\n美と医学の進歩のための\n何かのヒントになる気がするよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1176009, 0, "ちょっと待って！\n動かないでよく見せて…\nよし、キミの瞳の中のボクは\n今日もとても美しいね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1176010, 0, "いつも一番近くでボクの美しさを\n見てるキミがすごいってことは\nボクの美しさがすごいってことに\nなるんじゃないかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117601, 0.0, 5, 0, 6, 0, 5.6, 0, 0, 2, 2, 1176011, 0, "どう？　ボクの美しさが\n誕生日プレゼントだよ！\nいつも以上にじっくりと\n見てもいいからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1176012, 0, "今日のボクは特別に美しい？\nキミの誕生日だもの\n最高に美しいボクを\nプレゼントしてあげないとね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117701, 0.0, 2, 0, 1, 0, 5.1, 0, 0, 1, 1, 1177001, 0, "ランドソルのハロウィンって\nでーじ盛り上がるんだね～♪\n私たちもとことん\nハロウィンを楽しみまくるさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117701, 0.0, 2, 0, 2, 0, 4.2, 0, 0, 1, 1, 1177002, 0, "ハロウィンの平和は\n私たちが守るさ～！\nもし幽霊が出てきても\nなんとかしてみせるさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117701, 0.0, 1, 0, 3, 0, 3.6, 0, 0, 1, 6, 1177003, 0, "私も仮装してみたいさ～\nゴーヤーの仮装はどうかな～？\nきっと子どもたちの\n人気者になれるさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117701, 0.0, 1, 0, 4, 0, 3.0, 0, 0, 1, 2, 1177004, 0, "トリックオアトリート！\n…あれ、お菓子持ってないの？\nならイタズラするしかないね～\n脇腹くすぐるから覚悟するさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117701, 0.0, 5, 0, 5, 0, 6.5, 0, 0, 1, 6, 1177005, 0, "ハロウィンセイバーズの制服、\n似合っててかわいいねって？\nあは～、ありがとね～♪\n…あれ？　なんか顔が熱いさ～…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1177006, 0, "こうやって踊れば、\n幽霊とだって友達になれるさ～♪\nほら、キミも一緒に\n自由に楽しく踊るさー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1177007, 0, "これは私の故郷の踊りさ～♪\nうれしい時とかに、\nこうして踊るんだよ～\n踊り方、教えてあげよっか～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1177008, 0, "花火って夏のイメージだけど…\n秋に見る花火も最高さ～！\nやっぱりランドソルの\nハロウィンはすごいね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1177009, 0, "花火、でーじきれいで\nロマンチックだね～♪\nキミと一緒に見れて、\n私、すっごく幸せさ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1177010, 0, "キミと過ごすハロウィンは\nとびきり特別さ～♪\n一生忘れられないくらい\n楽しい夜にしようね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117701, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1177011, 0, "誕生日おめでと～！\nカボチャのサーターアンダギーを\nいーっぱい作ってきたんだ～\n遠慮せずたくさん食べるさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1177012, 0, "あは～、幽霊たちも\nおめでと～って言ってるさ～♪\n誕生日祝いに、\nみんなで一緒に踊るさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117801, 0.0, 4, 0, 1, 0, 9.3, 0, 0, 1, 6, 1178001, 0, "怪しい雰囲気が漂っていマスね…\nハっ！？　まさか、あの世から\n悪霊が蘇っているデスか！？\nなんともオソロシヤデ～ス…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117801, 0.0, 1, 0, 2, 0, 6.9, 0, 0, 1, 2, 1178002, 0, "妖魔や幽霊の能力を参考に\n新しい忍法を考えてみまショウ！\n変装したり…姿を消したり…\nいい忍法が開発できそうデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117801, 0.0, 2, 0, 3, 0, 5.1, 0, 0, 1, 1, 1178003, 0, "せっかくのお祭りデスから\n髪型も変えてみまシタ！\nどうデスか？　似合ってマスか？\nショーグンに褒めてほしいデス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117801, 0.0, 3, 0, 4, 0, 6.0, 0, 0, 1, 1, 1178004, 0, "ハロウィンセイバーズの制服は\n大きなマントがカッコイイデス！\nシュバっとひるがえして\n闇夜を駆けまショウ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117801, 0.0, 2, 0, 5, 0, 5.0, 0, 0, 1, 1, 1178005, 0, "あの世でも、ワタシは\nショーグンの家来でいマスよ\n永遠にお仕えするデース！\n忠誠心を燃やし続けマス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1178006, 0, "ヒャクマンゴクの\nヒャッキヤコウデース！\n夜通し踊るハロウィンの宴に\n集まるデス！　ピーヒャララー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1178007, 0, "人間も妖魔も幽霊も\nそんなの関係ないデース！\nみんな笑顔でヒャッキヤコウを\n楽しみまショウー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1178008, 0, "夜は短し騒げよ乙女デース！\n朝まで踊り明かして\nハロウィンを満喫しまショウ！\nソイヤソイヤ、ソレソレデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1178009, 0, "ショーグンもハロウィンを\nお楽しみデスか？\n今夜はブレイコウで\nはしゃぎまくるデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1178010, 0, "ショーグンと過ごすお祭りは\n格別デス！　…ショーグンは\nどうデスか？　ワタシと過ごせて\nうれしいデスか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117801, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1178011, 0, "ショーグンのお誕生日を\nお祝いするデス！　バースデー\nソングをこの笛で演奏しマス！\nピッピヒョーロロープープーー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1178012, 0, "お誕生日アッパレデース！\nショーグンのためにパレードを\n用意しまシタよー！\n盛大にお祝いするデース！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117901, 0.0, 1, 0, 1, 0, 4.3, 0, 0, 1, 2, 1179001, 0, "ハロウィンのお出かけすんの\nちょー楽しみにしてたんだ～♪\nうち見たいとこいっぱいあるし\nヒデサイも付き合ってよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117901, 0.0, 1, 0, 2, 0, 2.7, 0, 0, 1, 2, 1179002, 0, "カボチャ、カボチャ～♪\nヒデサイってカボチャ好き？\n今度料理作ったげよっか\nうち、腕によりをかけちゃうし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117901, 0.0, 7, 0, 3, 0, 3.9, 0, 0, 1, 1, 1179003, 0, "ハロウィンセイバーズの\n制服、もにカッコカワで\nうち的にもけっこー\nお気に入りなんだ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117901, 0.0, 1, 0, 4, 0, 4.9, 0, 0, 1, 2, 1179004, 0, "きゃはーっ♪　みんな仮装に\n気合入りまくりじゃーん！\nヒデサイが仮装するときは\nうちがコーデしたげるし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117901, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1179005, 0, "ヒデサイと一緒だと\nマジ楽しすぎてヤバいかも！\nテンション上がりすぎて\nやばばばーって感じ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1179006, 0, "こんなに楽しいハロウィン\nうち初めてかも！\nヒデサイも楽しんでる～？\nもっとテンション上げてこ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1179007, 0, "キャンディーにクッキーに…\nみんなに配るお菓子の\nチョイスってこんな感じでおけ？\nいっぱい配ろうね、ヒデサイ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1179008, 0, "ハロウィンのお菓子って\nもにカワすぎてヤバくない？\n食べるのもったいないし…\nうー、ヒデサイどーしよ～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1179009, 0, "ハロウィンのうちも\n超キマってるっしょ♪\nヒデサイをうちの\nトリコにしちゃうし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1179010, 0, "はぐれたらヤバいし\nうちと手つないどこ？\nきゃははっ♪　そんな\n照れなくていいじゃん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117901, 0.0, 2, 0, 6, 0, 7.6, 0, 0, 2, 1, 1179011, 0, "ヒデサイ誕生日おめ～♪\nプレゼントなにがいい？\nあ、一緒に選びに行く？\nりょ！　そんじゃ行こー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 117931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1179012, 0, "今日、ヒデサイの誕生日とか\nまじエモくない？\n最高の日じゃん！\nうちと一緒にお祝いしよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118001, 0.0, 3, 0, 1, 0, 0.0, 0, 0, 1, 3, 1180001, 0, "まったく、あの甘っちょろ\nお姫さまったら！\n公務をなんだと思ってるのかしら");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118001, 0.0, 2, 0, 2, 0, 2.8, 0, 0, 1, 1, 1180002, 0, "所詮、この世はお金です！\n金さえあれば、\nなんでもできますわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118001, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1180003, 0, "【リッチモンド商工会】は\nわたくしが一から作り上げた\n才能と努力の結晶なのですわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118001, 0.0, 1, 0, 4, 0, 4.1, 0, 0, 1, 6, 1180004, 0, "貴方、あの甘ちょろ陛下の\n騎士なのですって？\nずいぶん酔狂なものですわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118001, 0.0, 1, 0, 5, 0, 4.5, 0, 0, 1, 2, 1180005, 0, "貴方が望むなら、わたくしが\n雇ってさしあげましてよ？\nまずは見習いからですけれどね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1180006, 0, "金銀財宝！\n宝石に美術品！\nこうして囲まれていると…ああ！\n心が潤うようですわ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1180007, 0, "所詮、この世はお金です！\n金さえあれば、\nなんでもできますわ♪\n…そう思っておりましたけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1180008, 0, "この世には、金銀財宝にも劣らず\n輝くものがありますのね\nたとえば…そう！\nわたくしの忠義の心とかっ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1180009, 0, "ちょっとちょっと秘書1号？\n定時だからって\n上がれませんわよ？\nまだ仕事は残っておりましてよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1180010, 0, "仕事はまだまだ山積み…\nこうなったら少年、貴方にも\n手伝わせて差し上げますわ！\nお礼の方は、応相談でしてよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118001, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 1, 1180011, 0, "貴方、誕生日ですって？\nそれはおめでとうございます\n何か食べたいものはありまして？\nご馳走くらいしてあげますわよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118031, 0.0, 1, 0, 6, 0, 7.5, 0, 0, 2, 1, 1180012, 0, "お誕生日、おめでとうございます\n陛下の騎士として、\n今後とも一層励んでくださいな\nわたくしも手助けいたしますゆえ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118101, 0.0, 1, 0, 1, 0, 5.6, 0, 0, 1, 2, 1181001, 0, "歌っている時は…落ち着くの…\nほかのうるさい音も、\n自分の声で、紛れるから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118101, 0.0, 5, 0, 2, 0, 7.8, 0, 0, 1, 6, 1181002, 0, "えっ？\nまた、膝枕…してほしいの？\nま、待って…\n心の、準備がっ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118101, 0.0, 5, 0, 3, 0, 11.5, 0, 0, 1, 2, 1181003, 0, "ときどき…詩を書いたりもするの\n…あなたの詩も書いたけれど…\nダ、ダメ…見せられないわ\n恥ずかしいもの…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118101, 0.0, 2, 0, 4, 0, 7.4, 0, 0, 1, 1, 1181004, 0, "敵だったわたしに\n手を差し伸べてくれた\nあなたの気持ちに…\n少しでも、応えたいの…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118101, 0.0, 7, 0, 5, 0, 12.2, 0, 0, 1, 2, 1181005, 0, "話すのは苦手だけど、\nあなたと話すのは…好き…\nあっ…あなたが嫌だったら、\n無理、しないで…いいけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1181006, 0, "…あなたに救われてばかりの\n自分では、いたくないの…\n次は、わたしがあなたを\n助ける番…。…だからっ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1181007, 0, "人を傷つけるためではなく…\n守るために、歌います。\nもう二度と、大切な人を\n失わないように…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1181008, 0, "うるさい声に、耳を塞いできた…\nでもずっとそうしていたら、\nやさしい声も聞こえなくなるわ…\n今は…そのほうが嫌なの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1181009, 0, "あなたを見てると…甘やかして…\nぎゅっとして、あげたくなるの…\nこの気持ちは、母性…？\n…それとも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1181010, 0, "『こちら』に来る前は…\nわたしの居場所なんて\nどこにもなかった…でも、今は\n違うって。そう、思いたいの…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118101, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1181011, 0, "お誕生日、おめでとう…\nケーキ作り、失敗してしまって\n歌しかないのだけれど…\n心を込めて歌うわね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1181012, 0, "ハッピーバースデートゥーユー…\n…ふふ。あなたをお祝いする曲を\n歌えることがこんなにうれしい…\n来年もまた、聴いてね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118201, 0.0, 2, 0, 1, 0, 7.6, 0, 0, 1, 1, 1182001, 0, "騎士さん、お出かけしましょ♪\n場所はー…どこでもいいですよ？\n騎士さんと一緒なら…★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118201, 0.0, 2, 0, 2, 0, 10.7, 0, 0, 1, 4, 1182002, 0, "騎士さんは人気者ですよねー\nこんな人を好きになって\nそして想いが叶わなかったら…\nどれだけ傷つくんでしょうねー★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118201, 0.0, 1, 0, 3, 0, 2.2, 0, 0, 1, 6, 1182003, 0, "過去のこと？\n責めちゃっていいですよ？\nやったことは変わりませんし\n恨まれても仕方ないです★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118201, 0.0, 1, 0, 4, 0, 7.5, 0, 0, 1, 4, 1182004, 0, "んー、もっと騎士さんと\n二人きりになりたいんですよねー\n駆け落ちとかしちゃいます？\n…なーんて★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118201, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1182005, 0, "騎士さんって、\nとっても優しいですよねー\nでもでもー、わたしには\n優しくしなくて、いいですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1182006, 0, "どるるるる～んっ！　…あはっ★\nガトリングってサイコーですね\n狙うのヘタでも関係ないですしー\nたくさん壊せて快感♪　です♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1182007, 0, "ぺんぽこりん、ごーごー★\nえ？　中の人？\nんもー★　そんなの\nいるわけないじゃないですかぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1182008, 0, "ライクからラブになるには\n「キュン★」が大切ですからー\nだからもっともーっと…\n「キュン★」な展開希望です★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1182009, 0, "騎士さんがピンチの時は\n言ってくださいねー\nラブ＆テレポーテーションで\n駆けつけちゃいますから★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1182010, 0, "誰かをスキになるのって\nこんなに不思議なんですね\nまるでわたしが、違うわたしに\nなっちゃうみたいです…★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118201, 0.0, 1, 0, 6, 0, 10.0, 0, 0, 2, 2, 1182011, 0, "ハッピーバースデー★\nケーキのロウソクって、\n導火線みたいじゃないですか？\n消した瞬間に…どかーん♪　とかっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1182012, 0, "騎士さんのお誕生日★　なら、\n盛大にお祝いしなくちゃですね！\nそれじゃわたし、盛大な祝砲を…\nえっ、だめです？　…はーい★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118501, 0.0, 2, 0, 1, 0, 5.3, 0, 0, 1, 1, 1185001, 0, "私たちの仕事は地味かもしれない\nですけど、とても大切なんです！\nこれからも全身全霊でサポート\nさせていただきますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118501, 0.0, 2, 0, 2, 0, 4.5, 0, 0, 1, 1, 1185002, 0, "冒険と同じくらい書類仕事も\n楽しいものですよ？\nどうでしょう。今度一緒に\n働いてみませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118501, 0.0, 1, 0, 3, 0, 7.5, 0, 0, 1, 6, 1185003, 0, "この書類はあの部署に提出して…\nこの申請はあの人にして…\nこの討伐依頼は…あっ！\nちょうどいいところに！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118501, 0.0, 1, 0, 4, 0, 4.6, 0, 0, 1, 4, 1185004, 0, "お休みの日って何をすれば\nいいのか分からないんですよね…\nもしよければ一緒に休日の計画を\n立ててくれませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118501, 0.0, 1, 0, 5, 0, 5.9, 0, 0, 1, 2, 1185005, 0, "楽しそうに依頼達成の報告を\nしにきてくれる瞬間が\n一番好きなんです。あなたの\n報告もお待ちしてますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1185006, 0, "何事も初動が肝心ですからね\n書類を作るときは迅速丁寧に！\n皆さんに安心して冒険して\nもらえるようにがんばります♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1185007, 0, "空中で書類を書くコツ、ですか？\nう～ん…紙が落ちるよりも早く\n筆を動かす、ですかね？\n一緒に練習してみましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1185008, 0, "一度にたくさんの書類を\n片付けるのは楽しいんですけど\nこまめに提出していただけると\nと～っても助かります！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1185009, 0, "このペンと同じくらい武器を\n上手に扱えれば一緒に冒険\nできたんですけど事務仕事以外は\n苦手でして…ほんとですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1185010, 0, "困ったときは相談してください\n冒険者さんの…あなたのお力に\nなれることは、私にとって\nとても幸せなことですから　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118501, 0.0, 1, 0, 6, 0, 7.1, 0, 0, 2, 2, 1185011, 0, "お誕生日おめでとうございます\nあなたの成長をお祝いできて\nとてもうれしいです。これからも\nよろしくお願いしますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1185012, 0, "ハッピーバースデー！\nこちらギルド管理協会から…\nではなく、私からの贈り物です\n受け取ってくれますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118601, 0.0, 2, 0, 1, 0, 6.7, 0, 0, 1, 1, 1186001, 0, "あんたが今見てる私は本物？\n私が見せてる幻かもよ？\n…なーんてね♪　ねえねえ、\n疑っちゃった？　きゃははっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118601, 0.0, 2, 0, 2, 0, 5.7, 0, 0, 1, 6, 1186002, 0, "ふ～ん？　靴が片っぽだけ\n隠されて大変だったって？\nそれは災難ね～？　ふふっ、\n誰がそんなことしたのかしら！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118601, 0.0, 4, 0, 3, 0, 7.9, 0, 0, 1, 2, 1186003, 0, "ねえねえ、いい事思いついたの！\nキャルのご飯に激辛ソースを…\n…やめたほうがいい？\nちぇっ、つまんないの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118601, 0.0, 2, 0, 4, 0, 6.8, 0, 0, 1, 4, 1186004, 0, "あー、悪戯し疲れた！\nあんたの肩で、少し休ませて？\n…ふふっ、さ、キリキリ歩いて！\nガイドはしてあげる、多分ね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118601, 0.0, 2, 0, 5, 0, 9.2, 0, 0, 1, 1, 1186005, 0, "このギルドハウスのこと、\n今はだいぶ気に入ってるわ\n色んな思い出があるし…そうね\n慣れ親しんだ我が家、ってやつ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1186006, 0, "きゃははっ！　ねぇ、見た見た？\nキャルのあの慌てっぷり！\n毎回いいリアクションするから\n悪戯のしがいがあるわ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1186007, 0, "なんにもないと退屈しちゃって\n仕方ないのよね～…あら？\nふふっ、ターゲットはっけ～ん♪\nちょっと驚かせてやろっと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1186008, 0, "さて、次はなにをしようかしら？\n…悪戯道具でも仕入れに、\n繫華街にでも行こうっと！\n爆竹とかビックリ箱はあるかしら");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1186009, 0, "…あんたってどんな悪戯も\n平然と受け入れちゃうわよね\nそういうの良くないわよ！\nちゃんと怒らなくっちゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1186010, 0, "…私の力は、上手く利用すれば\nあんたの助けになれるわ\nだから…困った時は頼って？\nいい、わかったわね！　約束よ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118601, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1186011, 0, "へぇ…誕生日なんだ？\nならそれにふさわしい、\n特別な悪戯をしなくちゃ！\n期待してくれていいわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 118631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1186012, 0, "ハッピーバースデー！\nさ、誕生日プレゼントをどーぞ\nああ、そうそう…開ける前に\n心の準備はしっかりしてよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119001, 0.0, 2, 0, 1, 0, 9.6, 0, 0, 1, 1, 1190001, 0, "昼は教鞭を振るい…夜は愛と\n希望を胸に秘め、闇の力で戦う！\nしかしてその正体は！　ふふっ♪\nどの私もイオちゃんって呼んでね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119001, 0.0, 2, 0, 2, 0, 7.8, 0, 0, 1, 5, 1190002, 0, "『秘密の学び舎』に\n今は二人っきり…\nどういうことか分かる？\nマンツーマンで教えてあげる♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119001, 0.0, 6, 0, 3, 0, 1.6, 0, 0, 1, 2, 1190003, 0, "…ぐふふっ♪\nやだ！　まだ闇のドMの影響が…\nこんなところ生徒に見られたら…\nでも、それはそれで…じゅるり♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119001, 0.0, 3, 0, 4, 0, 6.4, 0, 0, 1, 4, 1190004, 0, "闇の力があふれないように\n私のこと、満たし続けてほしいの\n中途半端じゃダメだからね？\n本気でお願い～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119001, 0.0, 6, 0, 5, 0, 4.7, 0, 0, 1, 3, 1190005, 0, "オイタする子には\nしっかりお仕置きしますからね！\nえ？　どうしてうれしそうなの？\nお仕置き…されたいってこと…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1190006, 0, "愛と希望と闇の先生よ♪\nいつもだったら教えないことも\n今日だけは特別に…\nだって教室に二人っきりだから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1190007, 0, "先生の授業、受けたいの？\nふふ…背伸びしたい年頃だもんね\nいいわ。ちょっぴり早いかも\nしれないけど、教えてあげる♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1190008, 0, "あん、どぅ、とろわ〜♪\n先生の言うことを\nなんでも聞くようにな〜る♪\nまずは目をつぶってちょうだい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1190009, 0, "あら、先生の愛のムチがほしくて\nオイタしちゃったの？\nふふっ…イケナイ子ね…\nじゃあ反省してもらおうかしら♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1190010, 0, "もうすっかり夜なのに\nまだ補習を続けたいなんて…\nけど、勉強熱心な生徒って好きよ\nたっくさんかわいがってあげる♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119001, 0.0, 1, 0, 6, 0, 6.0, 0, 0, 2, 2, 1190011, 0, "お誕生日おめでとう！\n大人の階段をひとつ登ったわね\nじゃあ授業の内容も\n君の成長に合わせなくちゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1190012, 0, "ハッピーバースデー♪\n誕生日プレゼントとして\n学校じゃ習わないようなことも\n今日は特別に教えてあげる♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119101, 0.0, 2, 0, 1, 0, 6.1, 0, 0, 1, 1, 1191001, 0, "闇のドM魔法少女クウカです～♪\n困っている人はいませんか～？\nクウカがお手伝いしますよ～♪\nドMになれば何でも解決です～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119101, 0.0, 5, 0, 2, 0, 11.1, 0, 0, 1, 2, 1191002, 0, "愛と希望の妄想が捗ります～！\n「おいクウカ、お前の決めポーズ\nはこれだ」そう言ってドSさんは\nクウカを縛り上げ…ぐふふ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119101, 0.0, 2, 0, 3, 0, 7.9, 0, 0, 1, 6, 1191003, 0, "変身するときの台詞、ですか？\nいえ、クウカにはありませんね…\n興奮が頂点に達すると、勝手に\n変態、いえ変身してしまうので…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119101, 0.0, 5, 0, 4, 0, 7.7, 0, 0, 1, 2, 1191004, 0, "ドSさんとクウカのアジト…\nこ、ここでクウカに何をしようと\nいうのですかっ！？　ぐふふ…\n何があってもご褒美です～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119101, 0.0, 4, 0, 5, 0, 7.8, 0, 0, 1, 6, 1191005, 0, "はっ！？　正義の味方になって\nしまったらドSさんに攻められる\nチャンスが減るのでは…？\nクウカ、それだけは困ります～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1191006, 0, "クウカ、ドMビーーーーーーム！\nぐふふ、今日も街の平和を\n守ってしまいました～♪\nみなさん、幸せそうです～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1191007, 0, "体からあふれる愛と希望の力…！\n今なら、どんな攻めにも\n耐えられます！\nドSさん、遠慮なくどうぞ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1191008, 0, "「魔法少女め、俺がその体に\n敗北を刻んでやるよ…」\nああっ、いけませんドSさん！\nクウカ闇に染まっちゃいます～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1191009, 0, "じゅるり…この力があれば、\nすべての人々をドMに…\nはっ！　闇の力に負けそうです！\nクウカにお仕置きを、ドSさん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1191010, 0, "愛と希望の力…言葉にするのは\nちょっと気恥ずかしいですけど\nクウカの中にもあるんですよね…\nドSさん…この胸の高鳴りが…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119101, 0.0, 1, 0, 6, 0, 2.5, 0, 0, 2, 5, 1191011, 0, "クウカを受け取ってください！\nあっ、すみません、先走りました\nドSさんの誕生日ですので\nつい、興奮してしまい…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1191012, 0, "ドSさんのお誕生日をお祝いする\nために…クウカ、愛と希望と、\n闇の力を溜め込んできました…！\n受け取ってくださいドMビーム！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119201, 0.0, 1, 0, 1, 0, 5.8, 0, 0, 1, 3, 1192001, 0, "施設の修繕に雪かきに…\n冬だからって休んでられねぇど\n今日も気張って働くべや！\nあんちゃんもよろしく頼むべ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119201, 0.0, 2, 0, 2, 0, 4.9, 0, 0, 1, 1, 1192002, 0, "夏には夏の、冬には冬の\nおいしい食べ物があるべよ！\nうちで採れた旬の味を\nあんちゃんにも食べてほしいべ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119201, 0.0, 3, 0, 3, 0, 4.0, 0, 0, 1, 1, 1192003, 0, "オラたちのお笑いで\n冬の寒さを吹っ飛ばすべよ！\nあんちゃん、準備はいいだか？\nまずはネタ合わせから始めるべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119201, 0.0, 1, 0, 4, 0, 7.3, 0, 0, 1, 2, 1192004, 0, "うちの乳製品を使った\nクリスマスケーキに特製プリン…\nどれも自信作だべ！\nあんちゃんも味見してみてけろ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119201, 0.0, 2, 0, 5, 0, 7.4, 0, 0, 1, 1, 1192005, 0, "相方のあんちゃんがいるから\n牧場の仕事もお笑いも、\nオラは全力投球できるべさ\nいつもあんがとな、あんちゃん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1192006, 0, "やっぱここに飾んねぇとだな\nこのクリスマスリースが\nあんちゃんにも幸せを\n運んでくれますように♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1192007, 0, "クリスマスの賑やかさ…\n場の空気があったまってると\n笑いも取りやすいべな\nさ、ドッカンと笑わせにいくべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1192008, 0, "あんちゃん、この雪だるま…\n目元がなまらめんこいべ？\nオラがエリザベスに似せて作った\n会心の出来映えだべ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1192009, 0, "そんなとこ突っ立ってたら\nあんちゃん、風邪ひいちまうぞ？\nあったけぇお茶でも淹れっから\n中に入って待っててくんろ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1192010, 0, "大事な家族のみんなと、\nそんであんちゃんと…\n一緒にいれば冬でも\nポカポカあったけぇべさ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119201, 0.0, 2, 0, 6, 0, 2.8, 0, 0, 2, 1, 1192011, 0, "今日あんちゃんの誕生日だべ？\nこの日のためにとっておきの\n新ネタを用意してきただよ！\n楽しんでくれたらうれしいべさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1192012, 0, "よい子にしてたあんちゃんには\nマヒルサンタから誕生日の\nプレゼントをお届けするべ♪\n誕生日おめでとう、あんちゃん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119301, 0.0, 2, 0, 1, 0, 4.4, 0, 0, 1, 1, 1193001, 0, "お洋服よし！　ケーキよし！\nプレゼントよーし！　ふふふ、\n準備満タンですよ、お兄ちゃん！\n…じゃなかった、満杯ですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119301, 0.0, 2, 0, 2, 0, 3.6, 0, 0, 1, 1, 1193002, 0, "クリスマスケーキ？　もちろん\n作れますよ！　だてにマスターの\nお店で山ほどクレープ\n焼いてませんからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119301, 0.0, 5, 0, 3, 0, 7.1, 0, 0, 1, 2, 1193003, 0, "雪遊びしましょう、お兄ちゃん！\n雪だるま作って、雪合戦して、\nそれからそれから…\nデ、デートもしたいです～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119301, 0.0, 3, 0, 4, 0, 3.9, 0, 0, 1, 1, 1193004, 0, "今日のために練りに練った\nクリスマスデートのプラン…\nあっ、お兄ちゃんは見ちゃだめ！\nこのメモはきぎょー秘密です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119301, 0.0, 1, 0, 5, 0, 5.0, 0, 0, 1, 6, 1193005, 0, "押してダメならもっと押せ…\nお兄ちゃんを振り向かせるには、\nさらに大胆無敵なアプローチが\n必要ってことですね！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1193006, 0, "このお洋服、お兄ちゃんに見て\nもらいたくて作ったんですよ～\nつまりクリスマスコーデの私は\nお兄ちゃん専用です！　えへへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1193007, 0, "手袋にマフラー、セーターも…\n冬の防寒対策は、妹にまるっと\nお任せです♪　お兄ちゃんの\n健康は私が守っちゃいますよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1193008, 0, "なんでずっと見つめてるかって？\nお兄ちゃんと一緒に過ごす\nクリスマスを、いつまでも\n覚えていたいから！　ですよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1193009, 0, "お兄ちゃん、寒くないですか？\n寒いですよね？　私は寒いです！\nなので私とぎゅーってして\nあたたまっちゃいましょう…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1193010, 0, "クリスマスのお兄ちゃんは\n妹の私が独り占めです！\nお兄ちゃんも私のこと\n独り占めしちゃってください♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119301, 0.0, 2, 0, 6, 0, 7.0, 0, 0, 2, 5, 1193011, 0, "今日はお兄ちゃんの生まれた日…\n私にとってはどんな記念日より\n大切な日です♪　全力で\nお祝いしちゃいますよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1193012, 0, "ハッピーバースデーお兄ちゃん！\n気になる誕生日プレゼントは～…\nそう、お兄ちゃんの大好きな妹！\n私も大好きです、えへへ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119901, 0.0, 1, 0, 1, 0, 5.2, 0, 0, 1, 2, 1199001, 0, "オマエのクリスマスプレゼントは\nミヤコが用意してやるの！\nさあ、ほしいものを言えなの！\nプリン以外ならくれてやるの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119901, 0.0, 6, 0, 2, 0, 5.2, 0, 0, 1, 1, 1199002, 0, "サンタは空を飛んで\nどこへでも行けるって聞いたの\n…もしかしてサンタは幽霊なの？\nミヤコと同じなの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119901, 0.0, 2, 0, 3, 0, 4.4, 0, 0, 1, 1, 1199003, 0, "ミヤコのほしいクリスマス\nプレゼントは～、まずプリン！\n次にプリン、それとプリンなの！\nちゃんと覚えとくの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119901, 0.0, 1, 0, 4, 0, 3.6, 0, 0, 1, 4, 1199004, 0, "幽霊でも寒いものは寒いの\nオマエの服の中に入れろなの♪\nむふふ、抵抗してもむだなの！\nすり抜けて入ってやるの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119901, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1199005, 0, "クリスマスはミヤコとずっと\nくっついててもらうの～！\n嫌がっても憑いてくの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1199006, 0, "ミヤコサンタのお通りなの～！\nプリンをくれたら、お返しに\nプレゼントをあげるの！\nうぃんうぃんってやつなの～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1199007, 0, "サンタはトナカイとソリがないと\n飛べないらしいの～、でもでも！\nミヤコは一人で飛べちゃうの！\nミヤコサンタはすごいの～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1199008, 0, "プリンは全部ミヤコのだけど…\nたまにはミヤコが誰かに\nプレゼントするのも悪くないの♪\nなんだかあったかい気分になるの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1199009, 0, "ミヤコは優しいから、オマエに\n内緒でプレゼントを用意したの♪\n…あっ、言っちゃったの～！\nもう内緒じゃなくなったの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1199010, 0, "オマエと一緒にいると、毎日が\nクリスマスみたいな気分なの♪\nとーってもワクワクするの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119901, 0.0, 3, 0, 6, 0, 8.3, 0, 0, 2, 6, 1199011, 0, "今日はオマエの誕生日なの！\n特別にプリンを一口だけ\n分けてあげるの～！\n…それ以上食べたら許さないの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 119931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1199012, 0, "ハッピーバースデーなの～！\nお祝いのプリンケーキなの！\nほら、早くローソク吹き消すの！\nミヤコも食べたいの～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120001, 0.0, 1, 0, 1, 0, 7.5, 0, 0, 1, 3, 1200001, 0, "むむっ、闇の気配がするね！\n弟くんの平和を守ることが\n魔法少女なお姉ちゃんの使命！\nあ、街の平和ももちろん守るよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120001, 0.0, 5, 0, 2, 0, 8.1, 0, 0, 1, 2, 1200002, 0, "ふふっ、黒っぽい衣装って\nなんだか新鮮だよ～\nお姉ちゃんって清純派だからね♪\n…えへへ、ワルイコトしちゃう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120001, 0.0, 1, 0, 3, 0, 1.5, 0, 0, 1, 2, 1200003, 0, "へんし～んっ！\n…あっ、弟くん、見てたの？\n変身シーンに興味津々かな？\nじゃあもう一回…見せてあげるね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120001, 0.0, 2, 0, 4, 0, 4.7, 0, 0, 1, 1, 1200004, 0, "つよ～い愛と希望が\n魔法少女の力の源なんだって\nだったらお姉ちゃんはいつだって\n最強無敵の魔法少女だね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120001, 0.0, 5, 0, 5, 0, 5.3, 0, 0, 1, 6, 1200005, 0, "あっ、なんだか闇の力が\nあふれて暴走しちゃいそう…！\n今日は弟くんをひとりじめして\nたくさん愛でないと…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1200006, 0, "お姉ちゃんの愛の結晶！\n今、ここに合体！\nおいでっ、キング弟くんっ！\nふふっ、どかーんとやっちゃえ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1200007, 0, "じゃ～んっ♪\n魔法少女のお姉ちゃんだよっ！\nね、どうかな？　うれしい？\n変身、もう一回見せてあげよっか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1200008, 0, "弟くんっ♪\n決めポーズのリクエストはある？\nお姉ちゃんがどんなポーズでも\nバッチリ決めてあげちゃうぞ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1200009, 0, "ねっ、すっごく大きいでしょ♪\nこれはね、愛の大きさなんだよ？\nお姉ちゃんがどれだけ弟くんを\n想ってるか…伝わったかな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1200010, 0, "弟くんを守れるのなら\n愛でも、希望でも\n…闇の力でも構わないよ\n全部、お姉ちゃん力なんだから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120001, 0.0, 2, 0, 6, 0, 3.5, 0, 0, 2, 5, 1200011, 0, "今日はお姉ちゃんにとって\n何よりも大事な日…♪\nそう、弟くんの誕生日！\n魔法の力で、お祝いしちゃうぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1200012, 0, "お誕生日おめでとうっ♪\n魔法少女のお姉ちゃんから\n弟くんへのプレゼント♪\n愛と希望の…魔法だよっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120701, 0.0, 4, 0, 1, 0, 7.4, 0, 0, 1, 6, 1207001, 0, "ど、どうかしら…？\nコッコロさんと一緒に選んだの…\n着付けも慣れてないし…\nその…変なところはない…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120701, 0.0, 7, 0, 2, 0, 3.4, 0, 0, 1, 2, 1207002, 0, "せっかくの正月だものね\n私もたまには息抜きして\nあなたと新年の思い出を\n作ろうと思うわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120701, 0.0, 2, 0, 3, 0, 10.7, 0, 0, 1, 5, 1207003, 0, "『記念に写真を撮ろう』って…\nイ、イヤじゃないけど\n私一人だけじゃなくて…\nあなたとも一緒に撮りたいわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120701, 0.0, 7, 0, 4, 0, 5.2, 0, 0, 1, 2, 1207004, 0, "…ん。絶好のお参り日和ね\nさぁ、早く行きましょう\nあなたと過ごす貴重な\n休日を無駄にしたくないもの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120701, 0.0, 4, 0, 5, 0, 8.2, 0, 0, 1, 2, 1207005, 0, "【美食殿】の一員として\n正月の名物料理は\n全て堪能してみせるわ\n魔物の肉でも…が、がんばる…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1207006, 0, "待たせてごめんなさい\n着付けに時間がかかっちゃって…\n…え？　似合ってる？\nあ、ありがとう…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1207007, 0, "おせち料理…食べすぎちゃった…\n…でも。食べないで後悔するより\n食べて後悔する日があっても\nいいわよね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1207008, 0, "『今の私』にとって\n正月を過ごすのは初めてだから\nあなたの行きたい場所があれば\n私も一緒に連れて行ってくれる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1207009, 0, "正月ってすごいわね…\nまるでお祭りみたい…\nええと…迷子にならないように\n裾…掴んでてもいいかしら…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1207010, 0, "あなたはお参りで何を願ったの？\n私は…『今の私』にとって\n大切にしたいことを…\n絶対…叶えてみせるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120701, 0.0, 2, 0, 6, 0, 10.8, 0, 0, 2, 7, 1207011, 0, "誕生日、おめでとう\n正月もそうだったけれど\n一緒にお祝いできてうれしい…\nこれからもよろしくね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1207012, 0, "誕生日ケーキを作ってみたの…\n作るのはたぶん、初めてで…\nあまりキレイじゃないけど…\nでも、心は込めて…作ったから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120801, 0.0, 2, 0, 1, 0, 5.5, 0, 0, 1, 1, 1208001, 0, "正月ってのはいくつになっても\n心が躍るもんだね\nこの時ばかりは一緒に浮かれ\n楽しむのが粋ってもんさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120801, 0.0, 5, 0, 2, 0, 6.0, 0, 0, 1, 2, 1208002, 0, "どうだいお前さん、アタシの\n晴れ着姿は。キマってるだろ？\nえ、キレイだって？\nあ、ありがとな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120801, 0.0, 1, 0, 3, 0, 7.4, 0, 0, 1, 2, 1208003, 0, "羽根つき、たこあげ、福笑い\n正月の遊びってのは素朴で\n楽しいもんさ。どうだい？\nアタシと一勝負しないか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120801, 0.0, 2, 0, 4, 0, 6.1, 0, 0, 1, 6, 1208004, 0, "正月休みを利用して遠出する\nって人も結構いるみたいだね\nお前さんとならそういう\n過ごし方も悪くないかもね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120801, 0.0, 1, 0, 5, 0, 4.8, 0, 0, 1, 6, 1208005, 0, "おせちってのは作るのに\n手間がかかるんだよね\nまあ、食べたいって言うなら\n作るのもやぶさかじゃないさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1208006, 0, "アタシの剣は見せ物じゃないけど\n今日だけは特別さ\nとっておきの舞いを\nとくとご覧あれ、ってな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1208007, 0, "冷たい空気に触れると身も心も\n研ぎ澄まされていくね\n今なら新しいアタシを\nお披露目できそうだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1208008, 0, "おや、お前さんもこの剣舞に\n興味があるのかい？\nなら手取り足取りキッチリと\n仕込んでやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1208009, 0, "お前さんに降りかかる災いは\nアタシが全部斬り伏せる\n大船に乗ったつもりで\nいておくれよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1208010, 0, "どうしたんだいお前さん\nぼうっとしてさ？\nえ、アタシに見惚れてた？\nて、照れるじゃないかまったく…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120801, 0.0, 1, 0, 6, 0, 3.5, 0, 0, 2, 2, 1208011, 0, "誕生日おめでとう、お前さん\n今日はお前さんの成長を祝って\nとっておきの釣りスポットに\n連れてってやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1208012, 0, "お前さん、今日誕生日だって？\nふーん…いや、この一年で\n随分と見違えたなと思ってね\nなんだかうれしくなっちまうよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120901, 0.0, 2, 0, 1, 0, 8.7, 0, 0, 1, 1, 1209001, 0, "見事な晴れ着姿じゃろう？\n特にこの羽織りものは、自分でも\n気に入っておる。いかにも支配者\nらしい風格があるからのう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120901, 0.0, 2, 0, 2, 0, 12.0, 0, 0, 1, 1, 1209002, 0, "わらわは年長者じゃからのう\nすべての者にお年玉をやる権利が\nある…ということにほかなるまい\n景気よくばらまいてやるのじゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120901, 0.0, 2, 0, 3, 0, 6.4, 0, 0, 1, 1, 1209003, 0, "眷属のコウモリたちにも\n正月休みをやるとするか…\nおっと、お主には年中無休で\nわらわのそばにいてもらうぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120901, 0.0, 2, 0, 4, 0, 10.5, 0, 0, 1, 6, 1209004, 0, "この姿を民草に披露するか\nお主にじっくり堪能させてやるか\n…今は後者の気分じゃな。お主、\n年明けから運に恵まれたのう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120901, 0.0, 4, 0, 5, 0, 4.4, 0, 0, 1, 6, 1209005, 0, "子供の姿の晴れ着もかわい\nかった？　いくらおだててもあの\n姿には戻らぬぞ。断じて戻らぬ！\nまあ褒められて悪い気はせぬが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1209006, 0, "正月を制覇するには\nまず形から…この華々しい\n艶姿でもって、民草の信望を\n欲しいままにするのじゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1209007, 0, "後光が差して見えるじゃと？\nふふ、忌々しい太陽もわらわの\n美貌に見惚れるあまり、\n引き立て役に回ったようじゃの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1209008, 0, "晴れ着に合わせて髪も\n編んでみたのじゃが…ふふ\nなかなか見違えたじゃろう？\n何度でも惚れ直すがよいぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1209009, 0, "拝むがよい、称えるがよい！\n今のわらわはいくら褒められても\n足らぬ気分じゃ。お主からの\n賛辞であれば、なおさらのう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1209010, 0, "ほれ、もっと近う寄るのじゃ\nわらわの晴れ着姿、とくと\nその目に焼きつけるがよい\n…お主にだけ特別、じゃぞ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120901, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1209011, 0, "ハッピーバースデーなのじゃ！\nわらわほどの支配者ともなれば\n眷属の誕生日は当然把握しておる\nさあ、盛大に祝われるがよい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 120931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1209012, 0, "今日はお主の誕生日じゃな！\nふふ、すでに準備は済んでおるぞ\n正月並みに豪勢な宴で\n祝ってやろうではないか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121001, 0.0, 1, 0, 1, 0, 4.1, 0, 0, 1, 6, 1210001, 0, "これが、あなたのあふれる想い…\nとてつもない力ですが\n全力を超えて、受け止めます！\nあなたがつなげた絆のために！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121001, 0.0, 2, 0, 2, 0, 5.1, 0, 0, 1, 1, 1210002, 0, "わたしのそばから離れないで\nくださいね？　王女命令であり\nただのペコリーヌからの\n一生のお願いです☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121001, 0.0, 1, 0, 3, 0, 7.0, 0, 0, 1, 7, 1210003, 0, "あなたの想い、あなたの力…\n全部受け取って、前に進みます！\nあなたを感じているわたしは\nもう誰にも止められませんよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121001, 0.0, 1, 0, 4, 0, 4.8, 0, 0, 1, 2, 1210004, 0, "みんなに元気よく\nおいっす〜☆ って\n挨拶したいです\nそのために頑張りましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121001, 0.0, 2, 0, 5, 0, 4.5, 0, 0, 1, 1, 1210005, 0, "あなたと一緒なら、どんな場所に\n行ってもへっちゃらです！\nこれからもずっとずっと冒険を\n続けましょうね！　絶対ですよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1210006, 0, "【美食殿】の温かい食卓が\nわたしたちの帰る場所です！\nみんなで手を合わせて\nいただきますって言いましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1210007, 0, "あなたがつないだ絆を\n全力全開でつなぎとめて\n今日までの思い出と\n明日からの希望を守ります！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1210008, 0, "今だけはお腹空いたって泣き言は\n言いません！　\nみんなで食卓を囲むために\nわたしは剣を振るいます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1210009, 0, "あなたの強い想いを力に変えて…\n全力を超越しちゃいます！\nあなたがつないだ絆を\nこの体と剣に宿します！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1210010, 0, "あなたにどんなことがあっても\nずっとわたしが全力全開で\n守ってみせますよ！　だって…\n恋しちゃってるんですから☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121001, 0.0, 5, 0, 6, 0, 7.1, 0, 0, 2, 2, 1210011, 0, "ひゅうひゅう♪　お誕生日\nおめでとうございます！\n全力全開でお祝いしちゃいます！\n今日はあなたを独り占めです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1210012, 0, "大切な人の誕生日を祝えるって\nすごく幸せなことなんですよね\nこれからもずっと、おめでとうを\n言わせてくださいね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121101, 0.0, 1, 0, 1, 0, 6.2, 0, 0, 1, 2, 1211001, 0, "あんたとのつながりを感じる…\n温かくて優しい想いを…\n…今のあたしは、\n誰にも負ける気がしないわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121101, 0.0, 1, 0, 2, 0, 6.9, 0, 0, 1, 4, 1211002, 0, "気を抜くと魔力が暴走しそう…\nこの力、とんだじゃじゃ馬ね…\n…けど、使いこなしてみせるわ\nあいつのためにも…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121101, 0.0, 2, 0, 3, 0, 4.2, 0, 0, 1, 1, 1211003, 0, "あたしたちの冒険は、\nまだまだこれから、でしょ？\n眠ってるヒマなんてないわよ！\n次なる美食を探しに行きましょ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121101, 0.0, 2, 0, 4, 0, 4.7, 0, 0, 1, 1, 1211004, 0, "…あんたの背負ってるもの、\nあたしにも背負わせなさいよ\nここまで来たら、\n運命共同体ってヤツでしょうが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121101, 0.0, 2, 0, 5, 0, 7.3, 0, 0, 1, 1, 1211005, 0, "夢の中だろうがどこだろうが、\nあんたが大変な時は、\nあたしが必ず助けに行くから\nこの手は…絶対に離さないわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1211006, 0, "このつながりがある限り、\nどんな運命にだって負けないわ！\nさあ、とっとと片付けて、\nおいしいご飯を食べにいくわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1211007, 0, "【美食殿】があたしたちの\n帰る場所でしょ！\nあんたがどこにいたって、\nあたしが迎えに行ってやるから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1211008, 0, "あんたは一人じゃない\nどんな時だって、\nあたしたちは心でつながってる\nそのことを…忘れないで");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1211009, 0, "あんたと食べるご飯が、\n一番おいしいの\nだから…あたしのそばに\nずっといてもらわないと困るわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1211010, 0, "どうしようもないあたしを、\nあんたは受け入れてくれた\nだからあたしも…\nあんたの全部を受け入れるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121101, 0.0, 1, 0, 6, 0, 5.9, 0, 0, 2, 2, 1211011, 0, "誕生日、おめでと！\n今日はあんたのやりたいことに\n付き合ってあげるわ！\nさ、まずはなにをしましょっか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1211012, 0, "その…バースデーケーキ、\n作ってみたの。あんたみたいに、\n上手にできなかったけど…\nと、とにかく食べなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121201, 0.0, 6, 0, 1, 0, 7.6, 0, 0, 1, 1, 1212001, 0, "さあ少年、そこに座って！\n今回の事象についてもっと\n詳しく解析させてもらうね？\nあっ！　もう、逃げないでよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121201, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1212002, 0, "さて少年、ここで問題です！\nこの炎みたいなやつを使って\nクレープは作れるでしょうか？\n正解は…今から実験だー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121201, 0.0, 1, 0, 3, 0, 2.5, 0, 0, 1, 4, 1212003, 0, "力の制御？　ああ、大丈夫だよ\nもう慣れたし。少年、もしかして\nアタシが天才なの忘れてた？\n…ありがとう。心配してくれて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121201, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1212004, 0, "えへへ…いやあ、まさか\n少年からこんな贈り物を\n貰えるなんて思ってなくてさ\nついうれしくなっちゃった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121201, 0.0, 1, 0, 5, 0, 5.5, 0, 0, 1, 7, 1212005, 0, "やっぱり少年は、アタシすら\n変えてしまえるんだね…\nキミという存在の可能性\nこれからも見守らせてもらうよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1212006, 0, "少年を取り戻すためなら\n未知の事象も解き明かすさ\nキミを選んだ者として\n責任を取らないとね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1212007, 0, "少年。キミと繋がってる\nアタシの心も魂も、\n熱く燃えてるのが分かる？\nこの見た目だけじゃないよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1212008, 0, "実はさ、キミを待ってる人が\nこの世界には大勢いるんだよね\nその人たちのためにもアタシは\nキミを救うよ、少年！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1212009, 0, "だいたいさ、アタシから少年を\n取り上げようなんてふざけた話だ\nそんな真似、たとえ世界にだって\nさせてたまるもんか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1212010, 0, "なんでアタシ、こうなったんだろ\n少年と強く繋がり過ぎたかな？\nありえるね…愛ゆえの行動が\n二人を結びつけちゃったか～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121201, 0.0, 6, 0, 6, 0, 8.7, 0, 0, 2, 1, 1212011, 0, "ハッピーバースデー！\nキミへの心を込めた贈り物\n受け取ってくれるかな？\n…なんでクレープって分かった？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1212012, 0, "誕生日おめでとう、少年！\nキミの成長はアタシにとって\n何よりも喜ばしいよ。今日は\nアタシも一緒に祝っていいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121301, 0.0, 2, 0, 1, 0, 5.5, 0, 0, 1, 1, 1213001, 0, "お芝居のことなら\nちょっとだけ自信があります\nいつもとは一味違う私を\n見ていてくださいね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121301, 0.0, 1, 0, 2, 0, 8.1, 0, 0, 1, 2, 1213002, 0, "舞台裏でも客席でも\nお兄ちゃんが見てくれてると\nちゃんとわかるんです\nなんだかぽかぽかするので…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121301, 0.0, 2, 0, 3, 0, 3.8, 0, 0, 1, 1, 1213003, 0, "ミサキちゃんに勇気を\nわけてもらったから\n私は舞台に立つ楽しさを\n教えてあげたいな…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121301, 0.0, 1, 0, 4, 0, 7.9, 0, 0, 1, 6, 1213004, 0, "いまのお稽古、どうでしたか？\nひゃわ…！？\n頭は撫でないでくださいぃ…！\nよかったのは分かったので…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121301, 0.0, 2, 0, 5, 0, 7.1, 0, 0, 1, 1, 1213005, 0, "お芝居を見てくれたみんなに\n褒めてもらえるのもうれしいです\nでも…お兄ちゃんによろこんで\nもらえるのも…うれしいな…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1213006, 0, "今はまだ、つぼみのような\n未熟な私だけど…\nいつかこの花のように美しく\n舞台の上で咲いてみせます…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1213007, 0, "『言葉はいりません\nただ、なにも言わず\nこの花を受け取ってください』\nえへへ、どうですか…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1213008, 0, "お芝居をしているときの私は\n誰にでもなれますけど…\n一番なりたい役を演じるために\nいっぱい頑張りますね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1213009, 0, "私の舞台、よろこんでもらえた\nみたいでよかったですぅ…♪\n…今日は頭を撫でないんですか？\nいえ、なんでもないですよぅ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1213010, 0, "照れちゃって言えない\nお兄ちゃんへのこの気持ち…\n舞台の上から、この花に乗せて\n伝えられたらうれしいな…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121301, 0.0, 2, 0, 6, 0, 8.8, 0, 0, 2, 1, 1213011, 0, "観客はお兄ちゃんだけだけど…\nお誕生日祝いのお芝居、\n見てくれますか…？\nえへへ、心を込めて演じますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1213012, 0, "お祝いの気持ちを込めて\nささやかだけどこの花を贈ります\nお兄ちゃん、お誕生日\nおめでとうございます…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121401, 0.0, 5, 0, 1, 0, 2.4, 0, 0, 1, 1, 1214001, 0, "どうかしら？　この衣装♪\n大人のレディなら\nどんな服でもばっちり\n着こなしちゃうんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121401, 0.0, 2, 0, 2, 0, 7.6, 0, 0, 1, 1, 1214002, 0, "衣装に合わせて髪型も\n変えてみたんだけど、どう？\n…似合ってる？　ふふん♪\nま、聞くまでもなかったわね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121401, 0.0, 1, 0, 3, 0, 5.2, 0, 0, 1, 2, 1214003, 0, "アタシのエレガントな魅力で\n物産展を盛り上げちゃうわ♪\nアンタも、見とれてないで\nしっかり働きなさいよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121401, 0.0, 3, 0, 4, 0, 6.3, 0, 0, 1, 6, 1214004, 0, "ねえ、衣装の後ろの方\nちょっと直してくれない？\n…ひゃぁっ！？　バカバカ！\nど、どこ触ってんのよ～！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121401, 0.0, 4, 0, 5, 0, 5.6, 0, 0, 1, 6, 1214005, 0, "スタイリッシュなレディの魅力に\nメロメロなのはわかるけど…\nそ、そんなに見つめられると\nさすがに恥ずかしいわ…もうっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1214006, 0, "お客さんを笑顔にするためには\nアタシたちも笑ってないとね！\n物産展のお仕事は大変だけど\n楽しく働きましょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1214007, 0, "歌にダンスにお芝居だって…\nどんなことも完璧にできてこそ\n大人のレディよね？\n挑戦のしがいがあるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1214008, 0, "ねえ、このファンレター書いたの\nアンタでしょ？　アタシの魅力が\nやっと分かってきたようね！\n大事に読ませてもらうわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1214009, 0, "いらっしゃいませ～♪\n大人のフェロモンを振りまいて\nお客さんを呼び込んでみるわ！\n商売繁盛、間違いなしよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1214010, 0, "本当はね、舞台もお仕事も\n不安でいっぱいだったけど…\nアンタがいたら大丈夫になるの\nだから、その…いつもありがと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121401, 0.0, 2, 0, 6, 0, 3.9, 0, 0, 2, 1, 1214011, 0, "誕生日おめでとう！\nいつもお世話になってるから\n今日はアタシが特別に\nエスコートしてあげるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1214012, 0, "誕生日プレゼントはア・タ・シ♪\nもー！　リアクション薄いわよ！\nこのアタシが祝ってるんだから\nもっと素直に喜びなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121501, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1215001, 0, "この怪盗の衣装…\n私にはもったいないくらい\nかっこよくて、着るたびに\nちょっとドキドキしちゃいます…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121501, 0.0, 4, 0, 2, 0, 6.5, 0, 0, 1, 6, 1215002, 0, "せせせ、先輩…！　私の仮面\nどこかで見ませんでしたか？　\n…顔に着けてる？　…あっ\nほ、ほんとですね。あははは…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121501, 0.0, 4, 0, 3, 0, 0.0, 0, 0, 1, 4, 1215003, 0, "この間、怪盗活動に役立つかなと\n思って変装をしてみたんですけど\nそもそもいることに気づいて\nもらえませんでした…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121501, 0.0, 2, 0, 4, 0, 4.1, 0, 0, 1, 1, 1215004, 0, "先輩、私が見えてますか？\n…ふふ、それなら安心です\n先輩に見ててもらえれば\n私、勇気が出せるので…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121501, 0.0, 5, 0, 5, 0, 6.1, 0, 0, 1, 1, 1215005, 0, "誰にも気づかれないっていうのも\n悪いことばかりじゃないですね\n…先輩とゆっくり過ごせますし…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1215006, 0, "夜空を駆ける怪盗\nここに参上でしゅっ！\n…あわわ、大事なところで\n噛んじゃったぁ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1215007, 0, "この仮面を着けてると、ほんの\nちょっと大胆になれるんです…\nで、でもっ…空から潜入は\n大胆すぎたかもです～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1215008, 0, "ふえぇっ、高い、高いですっ！\nせせせ、先輩っ！　私に掴まって…\nいえ、掴まらせてくださーい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1215009, 0, "大丈夫、大丈夫…\n秘密道具は全部持ってきたはず…\nま、万一忘れ物があっても、\n先輩がいてくれれば大丈夫です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1215010, 0, "怪盗活動は怖いこと\nばかりですけど…\nでもこのドキドキする気持ちは\nなんだか嫌じゃないです…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121501, 0.0, 2, 0, 6, 0, 5.9, 0, 0, 2, 1, 1215011, 0, "先輩…お誕生日…\nおめでとう…ござい、ます…！\nや、やった…ゆっくり喋ったら\n噛まずに言えましちゃ！　…あっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1215012, 0, "今日は先輩のお誕生日…なので\nいろんな人にお誕生会の予告状を\n送っておきました！　えへへ…\n盛り上がるといいんですけど…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121601, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1216001, 0, "ふっふっふ…！\nあたしこそは神出鬼没の大怪盗！\n狙ったものは必ずいただくです！\n金銀財宝からボスの座まで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121601, 0.0, 6, 0, 2, 0, 7.1, 0, 0, 1, 2, 1216002, 0, "下克上のためなら\nどんな手でも使うですよ！\nなーに、バレなきゃいいんです！\n…バレたらもっと大変ですし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121601, 0.0, 3, 0, 3, 0, 6.7, 0, 0, 1, 2, 1216003, 0, "どうですか、このマフラー！\nいかにも大怪盗って感じで\nテンション上がるです…って、\n引っ張るのはダメですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121601, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1216004, 0, "あたしの前でボーっとするなんて\nいい度胸ですね、お兄さん！\n今日のぶんのオヤツが\nなくなっても知らないですよ～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121601, 0.0, 6, 0, 5, 0, 7.5, 0, 0, 1, 1, 1216005, 0, "べ、別になにもしてないですよ？\n気配を消して、お兄さんの話を\n盗み聞きしてたとかじゃないです\nホントホント、ただの未遂ですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1216006, 0, "ぴぇええええ！！！\n大怪盗のかっこいい逃走劇が\nはじまるはずだったのに！\nなんで…こうなるですかぁ～！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1216007, 0, "こ…こここ、こういうときこそ\n冷静になるですよ、あたし！\n落ちついて状況を見て…\nあーうん、落ち続けてるです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1216008, 0, "ぴぃっ！？　そ、そうだ！\nこういうときこそ覚醒です！\nドラゴン族の力、目覚めるです！\n真夜中だけど目覚めるです～！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1216009, 0, "ポーズを決めてるんじゃなくて！\nバランス！　崩して！　落下中！\nピンチなんですよ、お兄さん！\nボーっとしてる場合ですかっ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1216010, 0, "絶対、絶対受けとめるですよっ？\n前フリじゃないですからねっ？\nしっかりがっちりキャッチです！\n信じてるですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121601, 0.0, 2, 0, 6, 0, 2.5, 0, 0, 2, 1, 1216011, 0, "お兄さんの誕生日？\n覚えてるですよ、今日ですよね？\nほーら、どんなもんです！\nうたがったおわびをするですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1216012, 0, "お誕生日おめでとうです！\nしょうがないからプレゼントを\n用意してあげてもいいですよ\nさあ、どーんと言ってみるです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121901, 0.0, 2, 0, 1, 0, 7.4, 0, 0, 1, 1, 1219001, 0, "海賊衣装に新たな魔剣、\nと来れば次に必要なのは…\nふふ、あとは分かるな？\n…そう！　新しい必殺技だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121901, 0.0, 6, 0, 2, 0, 9.6, 0, 0, 1, 2, 1219002, 0, "ふふっ、新しい海賊の掟を\n思いついたぞ。記念すべき\n66個目の掟がな！\n…えっ、多すぎる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121901, 0.0, 4, 0, 3, 0, 7.1, 0, 0, 1, 6, 1219003, 0, "この格好をしていれば\n冥風戦記の続編の構想が次々に\n浮かんでくる…と思ったのだが\n現実はそう甘くなかったな…うん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121901, 0.0, 6, 0, 4, 0, 7.4, 0, 0, 1, 1, 1219004, 0, "新たに覚醒した深淵の力を\n制御すべく、眼帯も新調したぞ！\n…なに？　貴様も着けたい？\nわ、私のでよければ貸そうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121901, 0.0, 2, 0, 5, 0, 3.7, 0, 0, 1, 6, 1219005, 0, "その格好で泳げるのか、だと？\nふっ、侮るなよ…港町の皆さんに\n泳ぎ方を教わっているからな\nいずれは泳げるようになる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1219006, 0, "やはり船首に立つと胸が躍るな！\n…マストの上？　あ、あそこは\nちょっと高すぎるから…いや、\nあの場所は貴様に譲ってやろう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1219007, 0, "この船に主砲はまだないが\n私の最上究極殲滅魔法が、\nいわば主砲のようなものだ！\n船長にして主砲…かっこいいな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1219008, 0, "いい風が吹いているな…\nほどよくコートがはためくし\nかといって帽子が飛んでいかない\nうむ、絶妙にいい風だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1219009, 0, "ふっ、こうしてると前世で海原を\n冒険した記憶がよみがえってくる\n私と魂で結ばれた貴様も、\nいずれ思い出すはずだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1219010, 0, "航海士、操舵士…いや、やはり\n相棒というのがしっくりくるな\n大海賊の唯一無二の相棒…\nそれが貴様だ、シグルド！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121901, 0.0, 5, 0, 6, 0, 7.2, 0, 0, 2, 2, 1219011, 0, "シグルド、誕生日おめでとう！\nこの日のために財宝も用意した！\nわ、私の手作りなのだが…\nよければ受け取ってほしい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 121931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1219012, 0, "今日は貴様の生誕祭だ、盟友よ！\nふふ、すでに饗宴の準備は\n整えてある…海賊流の宴で\n盛大に祝ってやろう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122001, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1220001, 0, "船で旅するのも楽しそうですね\n今度、私たちもしてみますか？\n呪われた魔道具探索ツアーとか\nランドソル霊場巡りとか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122001, 0.0, 6, 0, 2, 0, 10.5, 0, 0, 1, 2, 1220002, 0, "波が穏やかでいい天気ですね\nあの空に飛んでる海鳥たちも\n気持ちよさそう…え、見えない？\nではあれは…霊鳥でしょうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122001, 0.0, 5, 0, 3, 0, 10.7, 0, 0, 1, 6, 1220003, 0, "この衣装…海賊なのか\n水兵なのかどちらなんでしょう\n…似合ってるから問題ない？\nそうではなくて…えっと…うぅ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122001, 0.0, 2, 0, 4, 0, 11.2, 0, 0, 1, 1, 1220004, 0, "幽霊になってでも叶えたい夢…\nそれを持てたら素敵ですね\n私にはまだ分かりませんが…\nあなたと一緒に見つけたいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122001, 0.0, 6, 0, 5, 0, 3.5, 0, 0, 1, 2, 1220005, 0, "潮風が気持ちいいですね\nでも少し風が強くて…\nスカートが…きゃっ！\nみ、見えましたか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1220006, 0, "星が降るような夜ですね…\nこのまま時が止まってしまえば\nいいのに…なんて\nそんなことを思ってしまいます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1220007, 0, "剣では救えない\n彷徨える魂たちも\n私と…あなたの力があれば\n救うことができます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1220008, 0, "あなたが迷った時は\n私にも力にならせてください\n暗夜の灯火となるように\nあなたの進む道を占いますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1220009, 0, "あなたと一緒なら\nどこまでも遠くへ\n行けそうな気がします\nこの海の果てまででも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1220010, 0, "月明かりと星の光…\nそれにどこまでも続く\n海だけが広がっていて…\n世界に二人きりみたいですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122001, 0.0, 5, 0, 6, 0, 10.0, 0, 0, 2, 2, 1220011, 0, "お誕生日おめでとうございます\n知り合いの霊から海賊に伝わる\n祝い唄を教えてもらったんです\n喜んでもらえるといいのですが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1220012, 0, "あなたのお誕生日のお祝いに\n私からこの舞を捧げます\nこの一年の幸せを祈願して\n心を込めて舞いますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122101, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1221001, 0, "やっぱり…自然はいいですね～\n小鳥さんや草花さんたちの\nおしゃべりも盛り上がっていて…\n心が落ち着きます…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122101, 0.0, 3, 0, 2, 0, 4.4, 0, 0, 1, 2, 1221002, 0, "自然はいつだって、私のことを\n受け入れてくれるんです…！\nそれはもう今か今かと\n待ち受けている勢いでっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122101, 0.0, 2, 0, 3, 0, 5.8, 0, 0, 1, 6, 1221003, 0, "都会では山籠もりのことを\nキャンプって言うんですよね？\nわぁ〜、キャンプかぁ〜…\nなんだかハイセンスな響き…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122101, 0.0, 2, 0, 4, 0, 4.4, 0, 0, 1, 1, 1221004, 0, "この服は、昔から山籠もりの時に\nいつも着てるんです！\n自然と向き合うための\n正装のようなものですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122101, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1221005, 0, "今度からは…ぼっち空間も\nＢＢ団の活動拠点にしましょう！\n私の大事な場所ですが…\nあなたならいつでも大歓迎です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1221006, 0, "今夜は語り明かしましょうね？\nＢＢ団の今後について…！\n私たちの壮大なぼっち卒業計画を\n大自然や星々に聞かせましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1221007, 0, "風のウワサで聞いたんですけど…\nBBQというものがあるそうです\n親近感がわく名前ですよね！\n多分ぼっちに優しいもののはず♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1221008, 0, "ふーふー…心も体も\nあったまります♪　なんでもない\nスープなのに、特別な味がします\nきっと二人だから…ですよね…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1221009, 0, "二人で力を合わせて\n設営をしてお料理をして…\nキャンプの共同作業は\n非ぼっちの気分が味わえます…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1221010, 0, "夜のキャンプ場は…\n本当に静か…ですね…\nまるで私とあなただけの…\nふたりぼっちの世界みたい…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122101, 0.0, 4, 0, 6, 0, 2.9, 0, 0, 2, 7, 1221011, 0, "お誕生日おめでとうございます！\n…あはは、やっぱり緊張しますね\n私の挨拶、大丈夫でしたか？\n声とか裏返ってませんでした？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1221012, 0, "たんおめ～♪　…すみません！\nどう挨拶しようか考えすぎて\nフランクになりすぎました…！\n改めて、おめでとうございます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122201, 0.0, 2, 0, 1, 0, 6.3, 0, 0, 1, 1, 1222001, 0, "どう？　この服、素敵でしょ？\n見た目だけじゃなく機能性も\n抜群なの。キャンプ用品店で\n一目惚れして買っちゃった♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122201, 0.0, 2, 0, 2, 0, 3.1, 0, 0, 1, 1, 1222002, 0, "またまたキャンプ道具を\n買い足しちゃった…\n元を取れるくらい使いたいから\nキミと何度も来なくっちゃね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122201, 0.0, 1, 0, 3, 0, 6.8, 0, 0, 1, 2, 1222003, 0, "んー！　なんて開放感なのー！\nもうこの眺めだけで\n麦しゅわが飲めるわ！\nさあ、キャンプを始めましょ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122201, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1222004, 0, "えっへへへ〜♪\nとっことんキャンプ飯で\n飲んじゃうわよ〜♪\n最高の一杯、さいこーっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122201, 0.0, 1, 0, 5, 0, 4.0, 0, 0, 1, 6, 1222005, 0, "キャンプって楽しいけど\n不便なこともあるわよね…\nなにか困ったことがあったら\nお姉さんに頼っていいからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1222006, 0, "すぅ〜…うん、空気がおいしい…\nもうこれだけで永遠に飲めそう…\n自然そのものが最高のおつまみ…\n朝から乾杯…しちゃおっかしら…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1222007, 0, "わ、私だって麦しゅわ以外の\n飲み物も飲むんだからっ！\nそれに、コーヒーなら\nキミと一緒に楽しめるでしょ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1222008, 0, "朝日って本当にきれい…\nランドソルじゃこんな景色は\nなかなか見られないわよね\n存分に味わいましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1222009, 0, "おはよう。コーヒー飲むでしょ？\nふふ、普段とは大違いね…\nだって、私がキミを起こして\n飲み物も二人分あるんだもん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1222010, 0, "キャンプ場で迎える朝って新鮮ね\n当たり前のことが\nなんだか当たり前じゃないみたい\nそれともキミと一緒だからかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122201, 0.0, 1, 0, 6, 0, 7.5, 0, 0, 2, 2, 1222011, 0, "ふふ、ハッピーバースデー！\nキャンプ場でケーキだなんて\n他には体験できないでしょ？\nほらほら、早速食べてみて！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1222012, 0, "お誕生日おめでと～！\nとっておきのキャンプ飯を\n振る舞ってあげるね？\n特別なキャンプにしてあげる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122301, 0.0, 7, 0, 1, 0, 4.9, 0, 0, 1, 2, 1223001, 0, "けんぞくー！\nヴァンピィちゃんの\nとっておきの場所、気に入った？\nさんにんだけの秘密だからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122301, 0.0, 2, 0, 2, 0, 5.8, 0, 0, 1, 7, 1223002, 0, "んー！　いい天気ー！\n絶好のお散歩日和かも！\nほらほら！\nけんぞくも早くこっちおいで♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122301, 0.0, 2, 0, 3, 0, 3.1, 0, 0, 1, 1, 1223003, 0, "けんぞく、もう疲れちゃったの？\nしょーがないなぁ…\nヴァンピィちゃんが\nひっぱってあげる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122301, 0.0, 7, 0, 4, 0, 2.7, 0, 0, 1, 6, 1223004, 0, "ここにも魔物がいるんだねー\nでも、大丈夫だよ！\nけんぞくはヴァンピィちゃんが\n守ってあげるから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122301, 0.0, 7, 0, 5, 0, 5.7, 0, 0, 1, 2, 1223005, 0, "けんぞくにね\nヴァンピィちゃんの好きな景色を\n見せてあげたかったんだ\n今度はけんぞくも教えてね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1223006, 0, "見て、けんぞく！　ちょうちょ！\nベスちゃんったらもう夢中だね！\nけんぞくは楽しい？\nヴァンピィちゃんも楽しーよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1223007, 0, "けんぞくがまた疲れちゃったら\nヴァンピィちゃんが助けるね！\nだってヴァンピィちゃんは\nアンタのご主人様ですから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1223008, 0, "お散歩ルールその１！\nけんぞく、いいことー？\nヴァンピィちゃんのそばを\n離れたらダメだからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1223009, 0, "ヴァンピィちゃんね\n見せたい場所、たくさんあるの！\nだからまた一緒にお散歩しよう！\n約束だよ、けんぞくぅ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1223010, 0, "ベスちゃんとけんぞくは違うけど\nおんなじぐらい大切なの！\n…つまり、ヴァンピィちゃんは\nどっちも大好きってこと♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122301, 0.0, 2, 0, 6, 0, 4.2, 0, 0, 2, 7, 1223011, 0, "けんぞくぅ～！\nお誕生日おめでとー♪\nケーキは作れないけど\nおいしいスープを作ってあげる♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1223012, 0, "ヴァンピィちゃん知ってるよ～？\n今日はけんぞくのお誕生日！\nこれからもけんぞくのこと\n大切にするからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122361, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1223013, 0, "ヴァンピィちゃんってば\nさいこーにぜっこーちょー！\n夜はまだまだこれから！\nいーっぱい遊ぼーね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122361, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1223014, 0, "今のヴァンピィちゃんなら\nあのお月様にも手がとどきそう！\nけんぞく、ベスちゃん、\n夜空のお散歩にしゅっぱーつ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122361, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1223015, 0, "夜遅くて眠くなっちゃった？\nもし寝ちゃっても\nヴァンピィちゃんがそばに\nついててあげるから安心してね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122361, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1223016, 0, "けんぞく、かぷっとしていーい？\nそうしたら、ヴァンピィちゃん\nもーっと強くなれるし…\nいつまでも、一緒にいられるよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122361, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1223017, 0, "いつもそばにいてくれてありがと\nけんぞく♪　この幸せがずっと\n続くよーに、ヴァンピィちゃんは\nずっとさいきょーでいるからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122401, 0.0, 7, 0, 1, 0, 3.8, 0, 0, 1, 1, 1224001, 0, "見て見て！　すっごくいい天気！\n騎士くんも一緒に\n夏を満喫しようよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122401, 0.0, 2, 0, 2, 0, 9.1, 0, 0, 1, 1, 1224002, 0, "騎士くん騎士くん！\nこの水着…どう…かな？\n…似合ってる？\nえへへ、うれしいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122401, 0.0, 1, 0, 3, 0, 1.7, 0, 0, 1, 6, 1224003, 0, "ふう…汗かいちゃったから\n水分はしっかり取らないとね♪\n騎士くんは大丈夫？\n熱中症には気を付けようね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122401, 0.0, 2, 0, 4, 0, 3.1, 0, 0, 1, 5, 1224004, 0, "騎士くんと過ごす夏…\nこんなに楽しいんだね！\nもっともーっとめいっぱい\n楽しんじゃうぞー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122401, 0.0, 1, 0, 5, 0, 5.8, 0, 0, 1, 2, 1224005, 0, "暑くても元気いっぱいだよ！\nだって騎士くんと一緒だもん！\nもしも元気がないなら\nあたしのをあげちゃう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1224006, 0, "あ、騎士くん！　こっちだよ！\nえへへ、今日は一日\n思いっきり楽しもうね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1224007, 0, "見て見て騎士くん！\n夏の日差しのおかげで、\nみ～んなキラキラ輝いてるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1224008, 0, "あははっ！\n冷たくて気持ちいー♪\n騎士くんもこっちおいでよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1224009, 0, "騎士くんのそばにいるとね？\nあたしには世界が全部\nまぶしく見えるんだ…\nだからありがとね！　騎士くん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1224010, 0, "騎士くん…大好きだよ！\nこの夏も、一緒に作った思い出も\nそれに…騎士くんのことも！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122401, 0.0, 2, 0, 6, 0, 3.2, 0, 0, 2, 1, 1224011, 0, "お誕生日おめでとう騎士くん！\nえへへ、こんなカッコでごめんね\n早くお祝いしたくて\n急いで来ちゃった♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1224012, 0, "騎士くん、お誕生日おめでとう♪\n今日はこのプール貸し切りだから\n思いっきりお祝いしちゃうよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122501, 0.0, 1, 0, 1, 0, 8.5, 0, 0, 1, 2, 1225001, 0, "こうして広がる海を見ていると\n晴れやかな気分になるよ\n足取りまで軽くなるみたいで…\nそうだ、向こうまで競争しない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122501, 0.0, 1, 0, 2, 0, 7.8, 0, 0, 1, 2, 1225002, 0, "潮風が気持ちいいね\nこんなに穏やかな時間は\nいつぶりだろう…キミもこっちで\n一緒に海を眺めようよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122501, 0.0, 2, 0, 3, 0, 8.1, 0, 0, 1, 1, 1225003, 0, "陽射しが強くなってきたな…\nあっちで涼みながら\nかき氷でも食べるのはどう？\n…決まりだね。行こうか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122501, 0.0, 5, 0, 4, 0, 5.5, 0, 0, 1, 6, 1225004, 0, "右を見ても左を見ても\nカップルばかりだけど…\nみんなの目に、私たちは…\nどう…映っているのかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122501, 0.0, 2, 0, 5, 0, 5.1, 0, 0, 1, 1, 1225005, 0, "時間がどれだけあっても\n足りなく感じてしまうね\nキミとこんな日々をずっと…\nそう夢見るくらいは許されるかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1225006, 0, "青い海に照り付ける太陽…\nこれぞバカンスって感じだね\n楽しむ準備はできてる？\n夏は待ってくれないよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1225007, 0, "冷たくて気持ちいいね…\nせっかくの水着なんだから\n海に入らないともったいないよ？\nほら、キミもこっちにおいで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1225008, 0, "ここの生き物はあまり\n人を警戒しないのかな\nこんなに近くでも逃げないで…\nふふっ、小さくてかわいいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1225009, 0, "この空に羽ばたく\n海鳥のようにいつか私も…\n限りのない自由を\n掴み取ってみせる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1225010, 0, "ずっと胸がドキドキして\nどうにも抑えきれないんだ\n…夏の雰囲気に\n私も当てられたのかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122501, 0.0, 2, 0, 6, 0, 8.1, 0, 0, 2, 1, 1225011, 0, "誕生日おめでとう\nその、ささやかなものだけど…\nプレゼントを用意してみたんだ\nぜひ受け取ってくれるとうれしい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1225012, 0, "今日はキミの誕生日だよね\n実はリゾートでおすすめの店を\n予約してるんだけど…少しの間\n私に付き合ってくれるかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122601, 0.0, 6, 0, 1, 0, 5.6, 0, 0, 1, 1, 1226001, 0, "浮き輪、借りてきたよ\nこれなら騎士クンも泳げるよね？\n…あれ？　これ、二人用！？\n…一緒に使う？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122601, 0.0, 7, 0, 2, 0, 7.4, 0, 0, 1, 1, 1226002, 0, "暑い日は水分を欠かさないでね\n喉が渇いてなくてもこまめに…\nダ、ダメだよ騎士クン！\nわたしのはあげられないの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122601, 0.0, 6, 0, 3, 0, 9.6, 0, 0, 1, 1, 1226003, 0, "泳いだ後って眠くなるよね\n弟たちと海に行った時は、\nいつもあの子たちも…\n…騎士クン？　寝ちゃった？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122601, 0.0, 5, 0, 4, 0, 10.8, 0, 0, 1, 6, 1226004, 0, "日焼け止めを塗って欲しい？\nわ、わたしでよければ…！\n…じゃ、じゃあ、失礼します！\n騎士クンの背中…おっきいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122601, 0.0, 5, 0, 5, 0, 7.3, 0, 0, 1, 1, 1226005, 0, "波の音を聞いてるの？\n心が落ち着くよね…\n…ひょっとして、今のわたしたち\nすごくいい雰囲気…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1226006, 0, "すごい星の数…\nこれだけのきらめきに\n照らされたら\n自分の心にうそなんてつけないよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1226007, 0, "この景色を見たこと…\nわたし、忘れないよ\n静けさも、光も、自分の気持ちも\n騎士クンが隣にいてくれたことも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1226008, 0, "な、泣いてなんかないよ…！\n―あ、ううん、ちがうよね\nわたし…泣いちゃった。ふふっ\nでも泣けたから…もう大丈夫だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1226009, 0, "…騎士クンと出会った時にね\nわたしの中にわたしとは違う\nもう１人の自分が生まれたの\nそのわたしも、騎士クンが…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1226010, 0, "すぐに焦って、暴走しちゃって…\nこんな私で、いつもごめんね\n…どうしてそうなるのか、って？\nそ、それは…大好きだから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122601, 0.0, 1, 0, 6, 0, 9.1, 0, 0, 2, 6, 1226011, 0, "誕生日プレゼントに\n特製のかき氷が食べたいの？\nわたしの魔法で作れるかな…\nちょっと試してみてもいい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1226012, 0, "騎士クン、お誕生日おめでとう！\n実はね、花火を買ってきたの\nふ、ふたりで一緒に遊びたくて…\n火の扱いには気をつけようね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122701, 0.0, 2, 0, 1, 0, 4.1, 0, 0, 1, 3, 1227001, 0, "私は海に来たからって\nはしゃいだりしませんよ\n大人はこういう時、ゆっくりと\n過ごすものなんです！　えへへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122701, 0.0, 2, 0, 2, 0, 9.1, 0, 0, 1, 1, 1227002, 0, "日差しには気をつけてくださいね\nこうやって帽子をかぶるとか…\nえ？　持ってない？\n…私の、少しだけ貸してあげます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122701, 0.0, 6, 0, 3, 0, 6.0, 0, 0, 1, 2, 1227003, 0, "夏の海はおいしいものがたくさん\nあって、食べすぎちゃいますね…\nあ、今おなかは見ちゃダメです！\nダ、ダメなのに…へんた～いっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122701, 0.0, 3, 0, 4, 0, 4.2, 0, 0, 1, 1, 1227004, 0, "夏はへんたいふしんしゃにも\n気をつけないといけません\n…私が見張ってますからね！\n絶対に離れちゃダメですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122701, 0.0, 2, 0, 5, 0, 7.4, 0, 0, 1, 4, 1227005, 0, "私がもし溺れちゃったりしたら…\nその…すぐに助けてくださいね？\nその代わり、あなたが危ない時は\n私が助けてあげますからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1227006, 0, "きゃっ！　なんで～！？\n魔法、失敗しちゃいました…\nビショビショになっちゃったけど\n水着だから平気ですっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1227007, 0, "夏の海では\n私の魔法も絶好調なんです！\nえへへ、冷たいお水…\nかけてあげましょうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1227008, 0, "涼しくて気持ちいいですね♪\nえ？　いつもより楽しそう？\n…子どもっぽい、ですかね…？\n今日は許します、夏の海なので！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1227009, 0, "も～、水をかけないでください！\nひゃっ、つめた～いっ！\n言うことを聞かない人はこうです\n弱めのコスモブルーフラッシュ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1227010, 0, "こんなに海が楽しいなんて\n思いませんでした…あ、あなたの\nおかげかもしれません…あの…\n…大人になったら、また二人で…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122701, 0.0, 5, 0, 6, 0, 7.1, 0, 0, 2, 1, 1227011, 0, "お誕生日おめでとうございます\nえ？　どうして水着なのかって？\nべ、別に理由はありませんけど\n嬉しく、ありませんでしたか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1227012, 0, "海でお誕生日をお祝いするのって\n特別感があっていいですよね\n今回はあなたの番ですけど\n私の時もお願いしますねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122801, 0.0, 1, 0, 1, 0, 5.7, 0, 0, 1, 2, 1228001, 0, "えへ、かわいい水着でしょー！\n動きやすくてお気に入りなんだ♪\nこの水着で海辺を探検したいな！\nもちろんにいちゃんも一緒だよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122801, 0.0, 2, 0, 2, 0, 8.6, 0, 0, 1, 1, 1228002, 0, "水鉄砲に水風船に熱湯風呂に～\n今日はずぶぬれ系のイタズラを\nにいちゃんに仕掛けちゃうよ！\n水着だからぬれても平気だよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122801, 0.0, 2, 0, 3, 0, 5.4, 0, 0, 1, 1, 1228003, 0, "この水中メガネを使ったら、\n海の中でもきれいに見えるよ！\nねえにいちゃん！　海のお魚と\nにらめっこ勝負しようよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122801, 0.0, 2, 0, 4, 0, 6.3, 0, 0, 1, 1, 1228004, 0, "ハリセンボンのトゲトゲに～\nクラゲのチクチク、タコのスミ～\n海の生き物ってイタズラ好きな子\nいっぱいだね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122801, 0.0, 5, 0, 5, 0, 3.3, 0, 0, 1, 2, 1228005, 0, "やったーにいちゃんと海だー♪\n今日は最高の思い出つくろうよ！\nねっ！　にいちゃん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1228006, 0, "ホントは派手な花火が好きだけど\n線香花火もきれいだね～\nずっとずっと見てたいから\nまだ消えちゃわないでほしいなぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1228007, 0, "線香花火ってきれいなだけじゃ\nなくって、音もかわいいな～\nなんだか線香花火のこと\nもっともっと好きになってくよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1228008, 0, "今日は海でにいちゃんと遊べて\nとっても楽しかったな～♪\n来年の夏も、その次の夏も\nミソギと一緒に遊ぼうね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1228009, 0, "夜の風が気持ちいいけど、\n線香花火が消えちゃうよ～！\nねえ、にいちゃん\n風よけに、ミソギの横きて～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1228010, 0, "わかってるよ、ちゃんとゴミは\n持って帰るってば！　\nきれいな海のほうが探検する時、\n気持ちいいもんね♪　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122801, 0.0, 2, 0, 6, 0, 4.0, 0, 0, 2, 1, 1228011, 0, "にいちゃんの誕生日を\n今日は海でお祝いしよう！\nプレゼントにミソギがいーっぱい\n遊んであげるね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1228012, 0, "誕生ケーキはろうそくの代わりに\n花火を使っちゃおっか♪　…あ、\nケーキ用意するの忘れちゃった！\n仕方ないから花火しようよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122901, 0.0, 2, 0, 1, 0, 5.2, 0, 0, 1, 5, 1229001, 0, "ねえねえ、おにいちゃん！\n海でなにしてあそぼっか！\nおにいちゃんとなら、なにしても\nぜったいたのしいと思うな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122901, 0.0, 5, 0, 2, 0, 7.2, 0, 0, 1, 2, 1229002, 0, "わぁ、おさかなさんいっぱい！\nヒトデさんもかわいいな～♪\nおにいちゃん、海まで\nつれてきてくれて、ありがとう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122901, 0.0, 6, 0, 3, 0, 7.3, 0, 0, 1, 1, 1229003, 0, "キョウカちゃんってたまに、\nへんたいふしんしゃさんって\nおにいちゃんのこと呼ぶけど…\nそれって、なあに？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122901, 0.0, 2, 0, 4, 0, 11.0, 0, 0, 1, 5, 1229004, 0, "おにいちゃんの食べてるかき氷、\nメロン味？　おいしそうだね♪\n…一口くれるの？　ありがと♪\nあ～ん♪　…えへへ、おいし～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122901, 0.0, 5, 0, 5, 0, 5.8, 0, 0, 1, 1, 1229005, 0, "おかあさんがね日焼けしないよう\n日傘さした方がいいよって。\nおにいちゃん、相合い傘して、\n砂浜をさんぽしよう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1229006, 0, "わぁ！　イルカさん、すごいね！\nぴょ～んって飛んでる！\nミミも海を飛んでみたいな♪\nぴょんぴょ～ん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1229007, 0, "えへへ♪　イルカさんと、\nおともだちになっちゃった！\nいっしょにいっぱい夏の思い出\nつくろうね、イルカさん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1229008, 0, "太陽サンサン、波がざぷ～んっ♪\n気持ちいいね、おにいちゃん♪\nぷかぷか浮かんでるだけでも\nミミ、す～っごく幸せ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1229009, 0, "おにいちゃんとの夏休みって\n今までとはちがう感じがする！\nドキドキワクワクな、\nとくべつな夏になりそう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1229010, 0, "お昼の海ってきれいだけど、\n夜の海ってどんな感じなんだろ？\nいつかミミが大人になったら、\n夜にあそびにこようね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122901, 0.0, 2, 0, 6, 0, 8.7, 0, 0, 2, 5, 1229011, 0, "お誕生日、おめでと～！\nあのね、ミミね、砂浜に\nおにいちゃんの絵をかいたの！\nよろこんでくれるとうれしいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 122931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1229012, 0, "ハッピーバースデー！\nイルカさんもね、おにいちゃんの\nおいわいに来てくれたよ♪\nみんなでいっしょにあそぼう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123001, 0.0, 2, 0, 1, 0, 4.5, 0, 0, 1, 1, 1230001, 0, "あんたが今見てるのは\n夢じゃなくて、本物のあたしよ\nじっくり拝んじゃいなさい♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123001, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1230002, 0, "暇だしあんた相手しなさいよ\n何をしたいか？　そうね…\nあんたが相手ならなんでもいいわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123001, 0.0, 1, 0, 3, 0, 3.5, 0, 0, 1, 6, 1230003, 0, "背中のこれが羽かどうかって？\nいつの間にかこうだったのよ\nあたしもあんたとおんなじね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123001, 0.0, 2, 0, 4, 0, 6.2, 0, 0, 1, 6, 1230004, 0, "あーあー…ちょっと待ちなさい\n髪が変になってる…これでよし\n相変わらずおっちょこちょいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123001, 0.0, 1, 0, 5, 0, 3.7, 0, 0, 1, 6, 1230005, 0, "一人でどっか行かないのかって？\nあたしはね、あんたを放って何か\nしようって気にならないのよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1230006, 0, "ほら、あたしを連れていきなさい\nまたあんたのそばにいてあげるわ\n今度こそ、最後の最後までね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1230007, 0, "飛べるようにもなったし\nあんたに会いに来てやったわ\nうれしい？　もっと喜んでいいわよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1230008, 0, "失くしたはずの羽…\n大事にするわ。これであんたの\nピンチに駆けつけてあげるから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1230009, 0, "あんたもあたしも色々あったけど\n過去よりも今、今よりも未来を\nあたしたちでより良くしましょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1230010, 0, "あたしが願う、行きたいところに\nあんたを連れてってあげられる…\n一緒に来てくれるわよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123001, 0.0, 2, 0, 6, 0, 6.7, 0, 0, 2, 6, 1230011, 0, "あんた今日誕生日じゃない\nプレゼント？　…忘れてた！\nな、何か買いに行きましょ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1230012, 0, "ハッピーバースデー。おめでとう\n覚えてない？　大丈夫。あたしが\nずっと忘れないでいてあげるわよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123101, 0.0, 1, 0, 1, 0, 6.0, 0, 0, 1, 2, 1231001, 0, "都会を離れてこうやって\n自然に触れるのもええなぁ\n危険がいっぱいのジャングルも\n王子はんとなら大丈夫や♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123101, 0.0, 6, 0, 2, 0, 8.0, 0, 0, 1, 4, 1231002, 0, "この辺りは虫さんが多くて\nうち、ちょっとかなんわぁ…\nちょうちょさんとかは\n平気なんやけどなぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123101, 0.0, 1, 0, 3, 0, 7.7, 0, 0, 1, 6, 1231003, 0, "森でのサバイバルやと\n泊まるとこの準備するのも\n一苦労やね…でもみんなで\n一緒に準備するの楽しいわぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123101, 0.0, 1, 0, 4, 0, 3.4, 0, 0, 1, 2, 1231004, 0, "お疲れさんどす、王子はん\nちょうどお茶の準備が\nできましたさかいに\n少し休憩しはったらどうどす？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123101, 0.0, 2, 0, 5, 0, 6.9, 0, 0, 1, 1, 1231005, 0, "王子はんと一緒なら\nどんなとこを冒険しても\n守ってくれますやろ？\n頼りにしてますえ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1231006, 0, "ついに見つけましたえ！\nあれが伝説の遺跡…\nうちドキドキしてきましたわ\n王子はん、行ってみまひょ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1231007, 0, "緑のざわめきに鳥さんの声…\nなんや冒険らしい雰囲気に\nなってきましたわぁ♪\n王子はん、準備はよろしおすか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1231008, 0, "冒険を重ねて、うちも\nけっこう成長してますんよ？\nうちが王子はんを守りますさかい\n王子はんはうちのこと守ってな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1231009, 0, "おうちに着くまでが冒険どす\n行きはよいよい帰りは怖い\n言いますやろ。冒険も旅行も\n怪我せぇへんのが大事なんよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1231010, 0, "うちのポケットの中には\n愛と勇気とお茶菓子が\nぎょうさん入っとりますんよ～\n王子はんにもお裾分けや♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123101, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1231011, 0, "お誕生日おめでとさん♪\nお祝いにとっておきの紅茶と\nお菓子を用意してますんよ\n森のお茶会にご招待や～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1231012, 0, "今日は王子はんの誕生日を\nお祝いする大冒険ツアーや♪\nうちがガイド役務めますさかい\n楽しんでくれるとうれしいわぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123201, 0.0, 1, 0, 1, 0, 7.7, 0, 0, 1, 6, 1232001, 0, "『いいかげん荷物が多いのによ\nすまねぇな、俺まで担がせて』\nぷうきちは荷物じゃないよ？\nぷうきちはぷうきちだもん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123201, 0.0, 6, 0, 2, 0, 3.9, 0, 0, 1, 2, 1232002, 0, "見て見てお兄ちゃん！\n旅のしおりを作ったの♪\nえ？　絵しか描いてないよって？\nいいの～こういうのは気分なの～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123201, 0.0, 2, 0, 3, 0, 4.8, 0, 0, 1, 1, 1232003, 0, "地図をグルグル回すのはね\n方向音痴の証拠なんだって\nってことは、私もお兄ちゃんも\n方向音痴で…おそろいだね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123201, 0.0, 6, 0, 4, 0, 8.7, 0, 0, 1, 2, 1232004, 0, "じゃ～ん、旅のお洋服だよ♪\n『おお…完璧装備じゃねぇか！\n何が起きても大丈夫だな』\n…でもお兄ちゃんは側にいてね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123201, 0.0, 2, 0, 5, 0, 2.4, 0, 0, 1, 1, 1232005, 0, "私の冒険に終わりはないの\nお兄ちゃんとぷうきちと\nどこまでもどこまでもずーっと\n一緒に進むんだもん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1232006, 0, "近道より、遠回りがしたいな～\nだってせっかくのお兄ちゃんと\n一緒の冒険なんだもん♪\nね、あっちにも行ってみようよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1232007, 0, "海のむこうには何があるのかな？\n空のむこうは？　…お兄ちゃんも\n知らないんだ。…じゃあじゃあ、\n想像で言い合いっこしよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1232008, 0, "『道なんてねぇぜ？　道は俺たち\nの後ろにできるんだよ。キリッ』\nい～眺めだね～ぷうきちが思わず\nキリッとしちゃうのもわかるね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1232009, 0, "冒険って、不安と期待でドキドキ\nするでしょ？　…その感じがね\n私が時々お兄ちゃんに感じる\nあの気持ちと…すごく似てるの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1232010, 0, "ふふ、お兄ちゃんも泥だらけだ～\n冒険をがんばった証拠だよね\nお兄ちゃん…カッコいいよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123201, 0.0, 5, 0, 6, 0, 6.2, 0, 0, 2, 2, 1232011, 0, "ハッピーバースデーお兄ちゃん！\nお兄ちゃんはね、私のお守りなの\nだからこれからもそばにいて\n私の冒険を見守ってね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1232012, 0, "『誕生日おめでとう坊ちゃん！』\nあー私が先に言いたかったのに～\n『ああ、す、すまん…！　\n旅先でつい浮かれちまった…』");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123301, 0.0, 1, 0, 1, 0, 6.7, 0, 0, 1, 6, 1233001, 0, "テキだったコトもあっけどー\nそーゆーめんどいの忘れてー\nとりま、ユルめなカンジでよろー");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123301, 0.0, 2, 0, 2, 0, 7.5, 0, 0, 1, 1, 1233002, 0, "アタマのツノだけはー\nこのカッコでもスライムの\nまんまなんだよねー\nンフフ～さわってみる～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123301, 0.0, 2, 0, 3, 0, 9.0, 0, 0, 1, 1, 1233003, 0, "こっちの世界て楽し～ねー\n来てよかったわー\n甘いのおいし～し～\nショタもたくさんいるし～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123301, 0.0, 2, 0, 4, 0, 5.6, 0, 0, 1, 5, 1233004, 0, "騎士きゅんのほっぺ～\nつんつんしてい～い？\nだってちょ～ぷにぷにで～\nかんわゆ～いんだも～ん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123301, 0.0, 2, 0, 5, 0, 8.2, 0, 0, 1, 1, 1233005, 0, "騎士きゅんは～\nみんなに優しーよね～\nけどアタシには～\nもーっと優しくしてね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1233006, 0, "アタシのスライム、いーっしょ？\nべっとべとのぬっるぬるで～\nショタつかまえて放さんつかー\nねー？　カリザきゅーん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1233007, 0, "ン～♪　やっぱ～\nショタの鳴き声ってサイコ～♪\n騎士きゅんも鳴いてみる？\nンフフ♪　なーんちて～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1233008, 0, "騎士きゅん変わってるよねー\nニンゲンもスライムも\n困ってたら助けちゃうなんてさ～\nらしーっちゃらしーけどー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1233009, 0, "騎士きゅんのおかげで～\nカンゼンに力戻ったし～\nこっからアタシが\n騎士きゅん守ってあげっかんね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1233010, 0, "カリザきゅんは\nイヂめたいかわゆさだけどー\n騎士きゅんはなんつかー\nちゅっちゅしたいかわゆさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123301, 0.0, 1, 0, 6, 0, 5.3, 0, 0, 2, 2, 1233011, 0, "騎士きゅんお・た・お・め～！\n今日はアゲてアゲて\nオールで祝いまくっから～\nカクゴしとき～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1233012, 0, "ショタコン的には～\n好きぴがトシとるのフクザツ～\nけどまーいっか～\nめちゃめでてーし～おめおめ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123501, 0.0, 5, 0, 1, 0, 4.3, 0, 0, 1, 1, 1235001, 0, "ハロウィンだから、街もすごい\n賑わいになってるね～\nあたしは平常運転\nぐうたらハロウィンを満喫するよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123501, 0.0, 1, 0, 2, 0, 2.1, 0, 0, 1, 6, 1235002, 0, "ん？　この格好？\nキョンシーの仮装だよ～\n…で、キョンシーってなに？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123501, 0.0, 2, 0, 3, 0, 4.0, 0, 0, 1, 1, 1235003, 0, "あんたもなにか\nお化けの仮装とかしなよ～\nミイラ男とか、カエル人間とか\nインパクトのあるやつね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123501, 0.0, 2, 0, 4, 0, 3.6, 0, 0, 1, 1, 1235004, 0, "ハロウィンのお祭り\nちゃんと楽しんでる？\nあたしは…まぁ、うん\nあんたがいれば、なんでもいいや");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123501, 0.0, 2, 0, 5, 0, 6.5, 0, 0, 1, 6, 1235005, 0, "えっ、あたしとハロウィンに\n参加したいって？\n…分かったよ、行くよ～\nでも、最後までつきあってよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1235006, 0, "トリックオアドングリ～♪\nあんパンかゲームでもいいけど\nあたしが欲しい物くれないと\nイタズラしちゃうよ～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1235007, 0, "ハロウィンってお菓子が\nもらえなかったら、好きなだけ\nぐうたらしていいお祭りだよね？\nえっ？　違うの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1235008, 0, "これってキョンシーの仮装\nなんだけどさ、袖が\nお気に入りなんだよね～\nだら～っとした感じがさ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1235009, 0, "ところで、あんたはあたしの\n仮装をどう思ってるの？\n…お腹が寒そうだって？\nそういうことじゃないよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1235010, 0, "ゆっくり、のんびり\nハロウィンを見て回ろ～？\n焦ったって、一緒にいられる\n時間が短くなるだけだもん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123501, 0.0, 5, 0, 6, 0, 6.0, 0, 0, 2, 2, 1235011, 0, "誕生おめでと～\nやだな、忘れるわけないじゃん\n…プレゼントは、さっきもらった\nお菓子でもいいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1235012, 0, "誕生日、めでたいね～\nプレゼントはあたし手作りの\nカボチャあんパンだよ\nおかわりもあるからね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123601, 0.0, 3, 0, 1, 0, 7.0, 0, 0, 1, 1, 1236001, 0, "パレードは賑やかで楽しいね\nでも羽目を外しちゃう人は\n私が取り締まっちゃうよ！\nハロウィンの平和は私が守る！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123601, 0.0, 1, 0, 2, 0, 2.8, 0, 0, 1, 2, 1236002, 0, "あなたも一緒に仮装しようよ！\n私の隣を歩くならお似合いなのは\n…騎士様？　王子様？\n逆に囚人服とかもありかもね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123601, 0.0, 2, 0, 3, 0, 5.4, 0, 0, 1, 6, 1236003, 0, "ちょっと目を離すと、あなたは\nいつもどこかに行っちゃうね…\n心配だからフラフラできないよう\n手錠かけちゃおうかな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123601, 0.0, 2, 0, 4, 0, 6.3, 0, 0, 1, 1, 1236004, 0, "ハロウィンだしお化けが出てきて\n私のこと驚かせてくれないかな？\nだってそれなら、怖がるふりして\nあなたに抱きつけちゃうもんね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123601, 0.0, 2, 0, 5, 0, 5.3, 0, 0, 1, 6, 1236005, 0, "ハッピーハロウィンって言うけど\nなにがハッピーなんだろうね？\n…私はあなたと一緒に過ごせて\n超超ハッピーなんだけど♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1236006, 0, "かわいい飾りや花火に目もくれず\n私を見つめてるのはなんでかな？\nふふ、もしかして、あなたの瞳\n逮捕しちゃった？　なーんてね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1236007, 0, "ん？　手錠を貸してほしいって？\n別にいいけどイタズラに使っちゃ\nダメだよ…って言ってるそばから\n私に手錠、かけるなぁ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1236008, 0, "倒すだけじゃなく\n相手を無力化して捕縛する剣術も\n会得したい…ということで\n稽古のお相手、よろしく頼むよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1236009, 0, "いつもは厳かな雰囲気の王城も\nハロウィンの時はムードがあるね\nはは、こんなところで二人っきり\nなんて、緊張しちゃってる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1236010, 0, "イタズラとからかいの違いか～…\nそうだなぁ多分、好きな人にしか\nやらないのがからかいかな…って\n別に今の告白とかじゃないよ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123601, 0.0, 5, 0, 6, 0, 9.1, 0, 0, 2, 2, 1236011, 0, "今日はあなたの誕生日だね\nカボチャの料理を用意したけど\nお口に合うかな…合えばいいな\n一応これ、手作りなんだ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1236012, 0, "ハロウィン姿の私をプレゼント♪\nこの格好見て目が幸せにな～れ\nってあれれ、ちょっと残念そうだ\nふふ、なにを想像してたのかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123701, 0.0, 7, 0, 1, 0, 7.1, 0, 0, 1, 2, 1237001, 0, "じゃーん！　どうどう？\n今日のナナカちゃんは\n自称ハロウィン魔法少女なのだ！\nシャランラッ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123701, 0.0, 6, 0, 2, 0, 4.5, 0, 0, 1, 1, 1237002, 0, "ふっふっふ、ナナカちゃんが\n魔法をかけちゃうぞ♪\n何の魔法かって？　それじゃあ\n恋の魔法～、な、なんちゃって…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123701, 0.0, 4, 0, 3, 0, 3.6, 0, 0, 1, 6, 1237003, 0, "パンプキィ、ハロウィッチ、\nうーん、どうしよ～…\nナナカちゃんの魔法少女ネーム\n決めらんないよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123701, 0.0, 4, 0, 4, 0, 5.1, 0, 0, 1, 3, 1237004, 0, "万聖節の夜に舞い降りる\n南瓜の使者…！\nう～ん、決めゼリフどうしよう\nアンナたそに考えてもらおっか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123701, 0.0, 2, 0, 5, 0, 6.3, 0, 0, 1, 1, 1237005, 0, "ハロウィン魔法少女！\nって５人目っていうより\nゲストキャラな感じしない？\nいいよね、ゲスト枠戦士！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1237006, 0, "ハロウィンナナカちゃん参上！\nどうかなどうかな？\nえへへ、せっかくだから\nかわいく撮ってね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1237007, 0, "見渡す限り、コスプレの人だらけ\nここが約束の地か…って\nぼーっとしてたらもったいないね\n楽しまなくっちゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1237008, 0, "コスにあわせた決めポーズを\n考えるのって難しいけど\n一度始めたらハマっちゃうな～\nよかったら一緒に考えてよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1237009, 0, "さっき君のこと、相方かって\n聞かれたから、ついつい\nそうですって答えちゃったけど…\nいい、よね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1237010, 0, "はぁ、コスプレって楽しいなあ…\nって、普段からやればいいって？\nそれはさすがに恥ずかしいけど…\n君と二人きりだったら、いいよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123701, 0.0, 1, 0, 6, 0, 3.3, 0, 0, 2, 2, 1237011, 0, "お誕生日おっめでと～♪\n私ってば、推しのプロフィールは\n暗記する系のオタクなんだよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1237012, 0, "ハッピ～バースデ～♪\nプレゼントはナナカちゃんの\nコスプレ姿！　…なんちゃって～\n何か言ってよ、ハズイじゃんか～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123801, 0.0, 2, 0, 1, 0, 5.1, 0, 0, 1, 6, 1238001, 0, "おおいワタシを一人にするなよ？\n寂しいとむしずが走るんだよ\n命懸けでかわいがっておくれ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123801, 0.0, 2, 0, 2, 0, 0.0, 0, 0, 1, 2, 1238002, 0, "さぁ坊や、互いに狩り合おう！\n坊やがワタシを食らうか\nワタシが坊やを食らうか\nどちらにしても…胸が躍るよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123801, 0.0, 2, 0, 3, 0, 5.5, 0, 0, 1, 1, 1238003, 0, "オマエを見ていると\n狩猟本能と庇護欲の\n両方がかき立てられる…\nまったく、稀有な生き物だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123801, 0.0, 2, 0, 4, 0, 5.4, 0, 0, 1, 1, 1238004, 0, "どうだ、この毛並み…\n思う存分、モフりたいだろう？\nしかし残念ながらまだダメだ\n誓約を果たしてから…な☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123801, 0.0, 1, 0, 5, 0, 5.7, 0, 0, 1, 2, 1238005, 0, "どうだ、愛らしいだろう？\n坊や、ワタシを飼ってみるか？\n飼い殺しにしてもかまわんぞ\nこう見えて主従には堅いのだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1238006, 0, "ウサギにしては強そうだと？\nあっはは☆　かまわんさ！\nオマエの中のウサギ像を\nワタシ色に塗り替えてやろう…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1238007, 0, "ずいぶんと熱心に見つめるのだな\nいいだろう、教えてやる\nオマエが今感じているのはな…\nギャップ、というヤツだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1238008, 0, "こうして下から見上げるのも\n悪くない光景だな\nふふ…しばらくそこに立っていろ\nワタシが見飽きるまで、な♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1238009, 0, "ん、尻尾が動いているだと？\nさすがにそれはないさ。鎧だぞ？\n…ま、オマエと二人きりだからな\n尻尾を振りたい気分ではあるが☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1238010, 0, "耳は繊細な作りのようでね\nどうやらこの鎧の弱点らしい\n…なに、教えていいのかだと？\nふふ、坊やにだけは特別さ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123801, 0.0, 2, 0, 6, 0, 9.1, 0, 0, 2, 1, 1238011, 0, "今日は坊やの誕生日らしいな\n生き物は皆一人で生まれ\n一人で死んでいくと言うが…\n安心しろ。我々は二人で一匹さ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1238012, 0, "オマエと出会わなければ\nワタシは退屈の荒野に\n野たれ死んでいたかもしれん\n坊やの誕生に感謝しよう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123901, 0.0, 2, 0, 1, 0, 5.2, 0, 0, 1, 1, 1239001, 0, "これが自分の新しい姿ッス！\nただの鎧じゃないッスよ？\nなんと、お母さんの手作りッス！\nいかしてるッスよね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123901, 0.0, 2, 0, 2, 0, 4.5, 0, 0, 1, 1, 1239002, 0, "自分のイチ押しは\nなんといってもこのマント！\nかっこいいし、くるまったら\nお昼寝もできるすぐれモノッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123901, 0.0, 1, 0, 3, 0, 3.2, 0, 0, 1, 3, 1239003, 0, "鍛え抜かれた自分の腹筋！\n…には、ほど遠いッスけど…\n魂のほうはもうすごいッスよ！\nモリモリのバッキバキッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123901, 0.0, 1, 0, 4, 0, 12.1, 0, 0, 1, 6, 1239004, 0, "耳？　いやー…耳を見せるのは\nまだ恥ずかしいッス…\nずっと見せないようにしてたから\nなんか…すーすーしちゃうんス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123901, 0.0, 2, 0, 5, 0, 7.7, 0, 0, 1, 1, 1239005, 0, "そうだ！　二人でお揃いの\n鎧を着るのはどうッスか？\nもちろん色違いで！\nうんうん、相棒っぽいッス～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1239006, 0, "トラか？　王宮騎士か？\n答えは一つ！　どっちもッス！\nそれが自分─\nマツリの生きざまッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1239007, 0, "悪事は絶対見逃さない！\nこの空に輝く太陽と…\nブレイブタイガーが\nお見通しッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1239008, 0, "マントを、こう…バサッ！\nこの風をまとった感じ\nまさしくヒーロー！\n悪に立ち向かう者の姿ッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1239009, 0, "あんたのおかげで強くなれた…\nだから、見てほしいッス！\nここまでこられた自分の姿を！\n他の誰でもない、あんたに！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1239010, 0, "応援ありがとうッス！\nおかげで勇気と力が\nモリモリ湧いてくるッスよ！\n今の自分は、最強無敵ッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123901, 0.0, 2, 0, 6, 0, 4.1, 0, 0, 2, 1, 1239011, 0, "今日はいい日ッスね～\nなにせ、あんたの誕生日！\nいやーめでたいッス！\nほんっとーに、おめでとうッス！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 123931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1239012, 0, "お誕生日おめでとうッス！\n最高の一日になるように\nこのブレイブタイガーがサポート\nするッス！　遠慮はなしッスよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124001, 0.0, 2, 0, 1, 0, 3.0, 0, 0, 1, 5, 1240001, 0, "お兄ちゃん、もっと見て…？\nアカリサンタのお洋服！\nエンジェルの羽もついてて\nとってもかわいいでしょ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124001, 0.0, 2, 0, 2, 0, 5.0, 0, 0, 1, 1, 1240002, 0, "サンタのお洋服を着た\n世界一かわいいエンジェル…\nえへへ、アカリってば\nかわいさテンコ盛りですね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124001, 0.0, 1, 0, 3, 0, 4.6, 0, 0, 1, 2, 1240003, 0, "クリスマスパーティーなら\nアカリにお任せあれです♪\nアカリの演奏で、みーんな\nハッピーにしちゃいますよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124001, 0.0, 5, 0, 4, 0, 4.1, 0, 0, 1, 6, 1240004, 0, "賑やかなクリスマスも\nとっても楽しいけど…\n今夜はアカリ、お兄ちゃんと\n二人きりで過ごしたいなぁ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124001, 0.0, 2, 0, 5, 0, 7.4, 0, 0, 1, 1, 1240005, 0, "お兄ちゃん、寒くないですか？\nこうやって手をすりすりして…\nふふ、アカリサンタが温もりを\nお届けしちゃいますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1240006, 0, "アカリの素敵なスウィングで\nハッピーなクリスマスを\nもーっとハッピーに\nしちゃいますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1240007, 0, "ほらほら、お兄ちゃん！\nアカリがクリスマスナンバーを\n演奏してあげますから…\nアカリサンタとダンスしましょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1240008, 0, "キレイなイルミネーション…\nでも、アカリだって負けません！\nお兄ちゃんの視線は\nアカリが独り占めしちゃいます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1240009, 0, "お兄ちゃんがそばにいると\nアカリのカラダ…\nなんだかすごく火照っちゃって…\n冬の寒さもへっちゃらです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1240010, 0, "アカリは世界一かわいい\nサンタさんだけど…\nお兄ちゃんは、世界一かっこいい\nアカリだけのサンタさんです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124001, 0.0, 5, 0, 6, 0, 5.4, 0, 0, 2, 2, 1240011, 0, "お兄ちゃんのお誕生日\nアカリ、全力でお祝いします♪\n今ならお兄ちゃんのしたいこと\nなんでもしていいですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1240012, 0, "ハッピーバースデーお兄ちゃん！\nサンタでエンジェルな\nアカリが、いーっぱい\nお祝いしてあげますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124101, 0.0, 6, 0, 1, 0, 7.2, 0, 0, 1, 4, 1241001, 0, "あ、あんまり見ないでよっ\n急に変身したから心の準備が…\nへ？　かわいいから安心して？\nそそそそういう問題じゃな～い！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124101, 0.0, 1, 0, 2, 0, 6.2, 0, 0, 1, 6, 1241002, 0, "サンタコスのエンジェルとか…\n属性盛り過ぎちゃってない？\nここまできたら、もうどこを\n恥ずかしがればいいのか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124101, 0.0, 4, 0, 3, 0, 7.5, 0, 0, 1, 1, 1241003, 0, "ね、ねえ…今の私、大丈夫？\nちゃんとエンジェルに見える？\nただただクリスマスだからって\n浮かれてる人に見えない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124101, 0.0, 6, 0, 4, 0, 6.1, 0, 0, 1, 2, 1241004, 0, "こういう形でもアンタと一緒に\nクリスマスを過ごせるなんて…\nハッ！？　なんでもないわよ\nなんでもっ。あはははは…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124101, 0.0, 3, 0, 5, 0, 4.8, 0, 0, 1, 1, 1241005, 0, "人見知りだからって\n恥ずかしがってるのはダメよね\n…でもアンタは近くにいること！\nお守りみたいなものなんだから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1241006, 0, "ハ…ハッピークリスマス\nサンタなエンジェルからの…\nううん、私からのプレゼントよ\n…いいから受け取りなさいっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1241007, 0, "アンタも隠れてっ\nもし、誰かにこんなところを\n見られたら私、私ぃ～…\nぷしゅぅ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1241008, 0, "この子の名前？　まだないけど…\n好きに呼べばいいんじゃない？\n…コヨリ？　私に似てるから？\nえ…えええええ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1241009, 0, "深呼吸して…すーはぁ～…\nよしっ。い、今、渡すからっ\nもうちょっとだけ待ってて\n…ううう、がんばれ私！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1241010, 0, "エンジェルだって…\n「みんな」じゃなくて\n「誰か」を幸せにしたいって\n思うときがあってもいいでしょ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124101, 0.0, 3, 0, 6, 0, 11.4, 0, 0, 2, 5, 1241011, 0, "ほ、欲しいものとか…ある？\nアンタ、今日アレでしょ？\nいわゆる…誕生日？　でしょ？\nほら、いいから欲しいもの言う！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1241012, 0, "お、お誕生日よね、今日は\nめ…めでたいわね～。ほんとにっ\nで、で…えっと、でかしたわ！\nと…と？　と～…と、とうッ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124201, 0.0, 2, 0, 1, 0, 9.8, 0, 0, 1, 6, 1242001, 0, "ほら空には白いハトが飛んでるよ\nまるで羽根が雪のように舞って\n世界が輝いているようだ…\nふふ…少年、メリークリスマス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124201, 0.0, 2, 0, 2, 0, 5.5, 0, 0, 1, 1, 1242002, 0, "いつもより体が軽いのは、鎧を\n脱いでいるからだけじゃない…\n子どもみたいで恥ずかしいけど\n少し聖夜に浮かれているみたいだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124201, 0.0, 6, 0, 3, 0, 6.5, 0, 0, 1, 3, 1242003, 0, "聖夜の平和を守る慈愛の戦士\n聖女仮面、見参！\n…一応考えてみたけど\n決めポーズはいらないかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124201, 0.0, 2, 0, 4, 0, 6.7, 0, 0, 1, 4, 1242004, 0, "うう、突き刺すような寒さだね\n君のほっぺも真っ赤じゃないか\nほら、暖めてあげるから\nこっちにおいで");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124201, 0.0, 2, 0, 5, 0, 6.3, 0, 0, 1, 1, 1242005, 0, "見慣れた街もクリスマスの装いで\nキラキラと輝いているね\n…いやそう見えるのは多分\n君が隣にいるからかもな…ふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1242006, 0, "静かな教会に白いハト\nなんとも神秘的な光景だね\n美しい鳴き声の響きがまるで\nクリスマスキャロルのようだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1242007, 0, "素顔でいる事も少し慣れてきた\nこのままクリスマスの魔法に\nかかったままでもいいかな…\nああ、いや、冗談だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1242008, 0, "素顔は皆に晒しても、\n私がジュンだということは秘密だ\nもし私を呼ぶときは別の名前で…\nいや少年。聖女さまはやめてくれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1242009, 0, "少年、そうまじまじ見つめるのは\nやめてほしい…恥ずかしい\nほら顔が、トナカイの鼻よりも\n真っ赤になってしまうよ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1242010, 0, "クリスマスは繁忙期だけど\n束の間の休息は君と過ごしたい\n…ふふ、クリスマスの魔法で\n心の鎧まで脱げてしまったのかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124201, 0.0, 2, 0, 6, 0, 4.5, 0, 0, 2, 1, 1242011, 0, "少年の誕生日に\nブッシュドノエルを焼いたんだ\n鎧を脱いだ分、作りやすかったよ\nきっと美味しいから食べてくれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1242012, 0, "ささやかだけど誕生日の贈り物…\n喜んでくれてるみたいでうれしいな\n君の反応をよーく見たくて\n兜を脱いだかいがあったよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124501, 0.0, 2, 0, 1, 0, 5.4, 0, 0, 1, 6, 1245001, 0, "普段は着物とか着ないんだけど\nおかしかったりしないかな～？\n素直な感想を聞かせてね\n君を焼き払ったりしないから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124501, 0.0, 2, 0, 2, 0, 4.3, 0, 0, 1, 1, 1245002, 0, "君は、なにかお正月らしいものを\n食べたりした？\nお雑煮とかでよければ\n私が作ってあげるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124501, 0.0, 1, 0, 3, 0, 6.0, 0, 0, 1, 2, 1245003, 0, "新年は私たちのファミリーも\nゆっくり過ごすことが多いかな？\nもちろん、誰かがちょっかい\nかけてきたら別だけどね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124501, 0.0, 1, 0, 4, 0, 6.9, 0, 0, 1, 6, 1245004, 0, "ん～？　私がこんな格好で\n誰かをもてなすのが珍しいって？\n心外だな～、そんなことないって\nちゃんと証明してあげるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124501, 0.0, 5, 0, 5, 0, 6.0, 0, 0, 1, 2, 1245005, 0, "君が満足するまで、私のそばで\nお正月休みを楽しんでね☆\n他のことなんて考えず…\nず～っと…永遠に…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1245006, 0, "あけましておめでと～☆\n君が新年をのんびりまったり\n過ごせるように、精一杯\nおもてなししちゃうよ～☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1245007, 0, "君は、どんなお正月を\n過ごす予定なのかな～？\nせっかくなら、この旅館で\nくつろいでいかない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1245008, 0, "いつもより、私が穏やかな\n雰囲気に感じる…って？\nそれはね～、お客様向けに\n笑顔を作ってるからだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1245009, 0, "う～ん、まだまだ君の\n表情が固い気がするな～？\n今回は親睦を深めるためにも\n精一杯おもてなしするね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1245010, 0, "せっかく温泉があるんだし\n二人で一緒にあったまっていく？\n…なんて、抜け駆けしたら\nいろんな子から恨まれそうだね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124501, 0.0, 2, 0, 6, 0, 7.6, 0, 0, 2, 1, 1245011, 0, "今日は君の誕生日だね～☆\nプレゼントとして、精一杯\nサービスさせてもらおうかな？\n忘れられない思い出になるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1245012, 0, "今日、誕生日なんだよね？\nそれじゃあ私も、特別な\nサービスを提供しちゃおうかな☆\n…どんなこと、されると思う？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124601, 0.0, 2, 0, 1, 0, 12.5, 0, 0, 1, 1, 1246001, 0, "一緒にお餅つきをしましょうか～\nあなたがつき手で先生が返しね♪\n息を合わせてぺったんぺったん…\nふふふ、とっても上手よ〜♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124601, 0.0, 2, 0, 2, 0, 0.0, 0, 0, 1, 2, 1246002, 0, "子どもたちに教えてもらった\n年越しの瞬間にジャンプするの\nとっても楽しかったわね～\n空中で新年を迎えちゃったわ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124601, 0.0, 1, 0, 3, 0, 8.1, 0, 0, 1, 2, 1246003, 0, "は～い、あけましておめでとう！\n上手に新年の挨拶できましたね～\n今年も子どもたちとあなたの成長\nとっても楽しみにしてますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124601, 0.0, 2, 0, 4, 0, 6.7, 0, 0, 1, 5, 1246004, 0, "うふふ、あなたと過ごす\n静かなお正月も素敵よね\nこたつでミカンでも食べて\nお昼寝しちゃいましょうか〜♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124601, 0.0, 2, 0, 5, 0, 5.0, 0, 0, 1, 1, 1246005, 0, "出会ってからあなたはどんどん\nたくましくなっていくわね～\n今年もた～くさん成長して\n先生を驚かせてね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1246006, 0, "後で街に行ってみましょうか～\nきっと初売りで大賑わいよ～♪\nでもお外は寒いからお雑煮食べて\n体の芯を温めましょうね～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1246007, 0, "うん、おいしそうな香りね～♪\nあとは最後の仕上げの隠し味\n…おいしくなりますように…\nはい、しっかり愛情込めました♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1246008, 0, "保育園も飲食店も素敵なお仕事ね\nどちらも、人の笑顔で溢れていて\nこっちも自然と幸せになるわ～\n笑う門には福来る…その通りね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1246009, 0, "いい匂いにつられて\n私もお腹がすいてきちゃった…\n二人分のお雑煮残しておくから\nお仕事終わったら食べましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1246010, 0, "新年に故郷を思いだすのはきっと\n子どもの頃が楽しかったからよね\nこの先…あなたが思い出す新年が\nこのお雑煮の味ならうれしいわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124601, 0.0, 2, 0, 6, 0, 8.2, 0, 0, 2, 1, 1246011, 0, "お誕生日のお祝いに今日は一日\n先生のお膝、貸してあげますね♪\nふふふ、よしよしいい子〜♪\n着物がすべすべで気持ちいい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1246012, 0, "お誕生日おめでとう♪\n先生、ちょっと趣向を凝らして\n誕生日雑煮を作ってみたの！\nあなたの好物、てんこ盛りよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124701, 0.0, 5, 0, 1, 0, 4.7, 0, 0, 1, 2, 1247001, 0, "明けましておめでとう\nさぁ、横になってちょうだい\n初実験体は絶対君にしようって\n決めてたんだから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124701, 0.0, 2, 0, 2, 0, 2.7, 0, 0, 1, 6, 1247002, 0, "どんな初夢を見たかって？\nそれはもちろん、君に\nあんな実験やこんな治療を試す\n夢に決まってるじゃない。ふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124701, 0.0, 2, 0, 3, 0, 5.1, 0, 0, 1, 1, 1247003, 0, "はい、お屠蘇をどうぞ　\nもちろんただのジュースよ？\n…ほんのちょっぴり\n新しいお薬は入っているけど♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124701, 0.0, 5, 0, 4, 0, 10.3, 0, 0, 1, 6, 1247004, 0, "着てみたはいいものの、この格好\nやっぱり診察がしにくいわね\n…え？　でも、すごくきれい？　\nお、大人をからかわないの…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124701, 0.0, 1, 0, 5, 0, 5.8, 0, 0, 1, 4, 1247005, 0, "せっかく君と一緒のお正月なのに\n全然ゆっくりできなくて残念\n残業・休日診療いたしません！\nなんて言ってみたいものだわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1247006, 0, "ほんのり赤くなってる？\nいやだ、見ないで恥ずかしいわ\n男の人に裸足を見せたのなんて\nこれが初めてだもの…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1247007, 0, "こうして同じ足湯に浸かるだけで\n元気がもらえるだなんて…\n君の効能ってすごいのね\n分析したいわ。触れてもいい…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1247008, 0, "足湯って体にいいのよ\n心臓に負担もかからないし\n…にしては動悸がする？\nあら…それはイケない病かもね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1247009, 0, "みんな初日の出は必死で見るのに\n夜は空を見ようともしないのね\nそれでもほら、ちゃんと昇ってる\nお正月の月っていじらしいわね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1247010, 0, "ねぇ、月が見えないわ\nんー、そこだと景色が見えない\nだからそっちじゃなくて…もうっ\nそばに来なさいって言ってるのよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124701, 0.0, 1, 0, 6, 0, 2.2, 0, 0, 2, 2, 1247011, 0, "お誕生日おめでとう\n君の存在は私にとって\nまさに百薬の長\n今年も側に常備させてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1247012, 0, "盆と正月がいっぺんにきたって\nこのおめでたさには敵わないわ\nさ、新薬で乾杯したら…\nバースデー治験を始めるわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124801, 0.0, 1, 0, 1, 0, 2.8, 0, 0, 1, 7, 1248001, 0, "アイドルの新衣装おひろめ！\n…ってわけじゃないけど\n新しい私の姿を見てもらえるのは\nうれしいな。それで…感想は？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124801, 0.0, 2, 0, 2, 0, 7.4, 0, 0, 1, 1, 1248002, 0, "みんなを楽園から解放する力…\nそれが形になったのが\nこの姿なんだよね\n開放的なデザインも納得だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124801, 0.0, 6, 0, 3, 0, 11.0, 0, 0, 1, 4, 1248003, 0, "楽園の影響を受けた人たち…\nみんな笑顔だったよね。なのに…\n見てると悲しい気持ちになるの\n…キミも、私と同じみたいだね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124801, 0.0, 7, 0, 4, 0, 7.0, 0, 0, 1, 1, 1248004, 0, "キミには守ってもらってばっかり\nだから…今度は私がキミを守るよ\nでも、応援はよろしくね♪\nもちろん最前列じゃないとダメ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124801, 0.0, 6, 0, 5, 0, 5.3, 0, 0, 1, 2, 1248005, 0, "キミもおそろいの格好になる？\nなーんて…え、なってもいい？\n気持ちはうれしいけど…ダメ！\nセクシーすぎるもん、私がＮＧ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1248006, 0, "「これまで」だって素敵な思い出\n…だから、もっと増やしたい！\nさあ、一緒に行こう！\n「これから」を迎えに！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1248007, 0, "アイドルは強くてかわいい！\nそれが当たり前！　つまり？\n未来のトップアイドルなら最高に\n強くてかわいいのも当たり前っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1248008, 0, "この輝きは未来の輝き！\n私とキミと、みんなを待ってる\nまだ見たことのない世界の光！\nね、こわくなんかないでしょ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1248009, 0, "キミがいつも通りでいてくれる…\nそれだけで私は前を向けるんだ\nたとえどんなに暗い道でも─\n堂々と胸を張って！　笑顔で！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1248010, 0, "前から好きだった自分の名前…\nもっともっと大好きになったよ！\n私はノゾミ─希望の名前！\nさあ、キミも呼んで！　せーの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124801, 0.0, 1, 0, 6, 0, 4.4, 0, 0, 2, 2, 1248011, 0, "ハッピーバースデー！\n今日はキミが主役の日だよ！\nいつも応援してもらってる分\n思いっきり目立たせてあげるね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1248012, 0, "キミが生まれてきてくれたから\n私たちは出会えたんだよ\nこの奇跡に、私は感謝してるんだ\n心を込めて…誕生日おめでとう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124901, 0.0, 2, 0, 1, 0, 5.4, 0, 0, 1, 1, 1249001, 0, "たとえ未来が茨の道でも\n道があるなら歩けるだろ\nなにも迷うことはない\n突き進もうぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124901, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1249002, 0, "今がダサくて惨めだろうが\n立ち止まっちまうよりはマシだ\nそういう泥くさい生き方だって\nオレは嫌いじゃないぜ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124901, 0.0, 1, 0, 3, 0, 4.3, 0, 0, 1, 2, 1249003, 0, "未来はいつだって\nひとっ走りで行ける距離にある\n自分の気持ちに\n正直でいさえすればな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124901, 0.0, 1, 0, 4, 0, 4.8, 0, 0, 1, 6, 1249004, 0, "思えばずいぶん遠くまで\n来たもんだよな…\nキミといると、時間の流れが\nやたらと早く感じるぜ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124901, 0.0, 1, 0, 5, 0, 8.1, 0, 0, 1, 2, 1249005, 0, "オレにも大切なもんがたくさん\nできた。イノリとボスと\nファミリーの連中…\nもちろんキミもその一人だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1249006, 0, "たぎる！　たぎるぜ！\nどんな過去も置き去りにする力！\nこの拳にみなぎってくる！\nさあ、ケンカだケンカぁ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1249007, 0, "ぐずぐず立ち止まってんなよ\nたった一歩でも踏み出せば…\nその先に未来の景色が見える\n踏み込んでこいよ、ここまで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1249008, 0, "オレ様の未来とテメェの未来\nどっちが強いか、未来バトルと\nいこうじゃねえか！\n負けたほうが飯おごりだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1249009, 0, "キミ、遅れるなよ！\n今のオレ様の拳は\nランドソル最速だぜ！\n未来に向かって一直線だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1249010, 0, "どんな苦難が待っていようが\nオレはもう立ち止まらない\nダチも仲間も、自分自身も！\nまとめて未来へ連れてくぜ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124901, 0.0, 2, 0, 6, 0, 2.1, 0, 0, 2, 1, 1249011, 0, "誕生日、おめでとな！\nへへっ、ケーキ食ったら\nアジトまで遊びに来いよ\nファミリー総出で祝ってやるぜ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 124931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1249012, 0, "ハッピーバースデー、ってな！\n今日は一日、遊び倒そうぜ\n夜が明けるまで\n付き合ってやるからさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125001, 0.0, 1, 0, 1, 0, 4.9, 0, 0, 1, 2, 1250001, 0, "はは、なんだか分かんないけど\nすっごい力を感じるぞ！\nこいつでオマエを守ってやる！\n明日も明後日もその先もな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125001, 0.0, 2, 0, 2, 0, 3.5, 0, 0, 1, 1, 1250002, 0, "たまには思い出を\n懐かしむのもいいと思う…\nでもその後はちゃんと前を向いて\n未来へ進まなきゃ駄目だよな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125001, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1250003, 0, "進むべき道に迷った時は\nアタシを見ろ！\nアタシがいればオマエの未来は\nピッカピカに明るいぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125001, 0.0, 1, 0, 4, 0, 5.5, 0, 0, 1, 6, 1250004, 0, "思い出ってのは未来に進むのを\n邪魔する時もあるんだよな…でも\nオマエとは進む力になるような\n思い出を作っていきたいぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125001, 0.0, 2, 0, 5, 0, 1.6, 0, 0, 1, 1, 1250005, 0, "アタシの望む未来…絶対に\n楽しいに決まってるよな！\nもちろんオマエもそこにいろよ\n約束だからな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1250006, 0, "はーっはっは！　大悪党コンビ\nここにふっかーつ！\n『この感じ、懐かしいね\n柄にもなく心が踊るよ』");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1250007, 0, "『ノウェム、連携が乱れてるよ\nもしかして腕落ちた？』\nああ？　それはこっちの台詞だ！\n久々にオマエの本気見せてみろ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1250008, 0, "アタシは一人でも最強だけど\nオクトーとなら超最強だ！\nやっぱりアタシたちは\n二人でひとつってことだ、相棒！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1250009, 0, "『ノウェムの隣って妙に落ち\n着くんだよね。言わないけどさ』\nどうしたオクトー？　何か用か？\n『べっつに～』");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1250010, 0, "アタシたち３人って\n昔は敵だったよな…でも、今は\nこうやって一緒に戦ってる！\n未来って面白いな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125001, 0.0, 2, 0, 6, 0, 1.8, 0, 0, 2, 1, 1250011, 0, "誕生日おめでとう！\n今日は一日アタシが\n一緒にいてやるよ！\nうれしいだろ？　うれしいよな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1250012, 0, "オマエに誕生日プレゼントを\n用意したぞ！　何かはまだ内緒だ\n悪党ってのは色々秘密がある\nものだからな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125101, 0.0, 2, 0, 1, 0, 9.0, 0, 0, 1, 1, 1251001, 0, "今日もお疲れさまにゃ！\n休憩に温かいコーヒー飲むにゃ？\nお代は従業員割引で200ルピ！\n福利厚生バッチリ完備にゃ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125101, 0.0, 2, 0, 2, 0, 4.4, 0, 0, 1, 1, 1251002, 0, "コーヒーとたい焼きのあんこが\n織りなすハーモニー…！\nにゃはは♪　相性バツグンにゃ！\nこのセットは爆売れ必至だにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125101, 0.0, 1, 0, 3, 0, 11.9, 0, 0, 1, 6, 1251003, 0, "猫従業員のシフト組んでるにゃ…\nこの子は連勤だから休ませて…\n…ねえキミ、ヘルプ頼めるにゃ？\n猫みたいにかわいく接客してにゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125101, 0.0, 2, 0, 4, 0, 3.5, 0, 0, 1, 6, 1251004, 0, "ん？　目が冴えて眠れない？\nにゃはは、コーヒー飲みすぎにゃ\nじゃあ眠くなるまでお姉さんが\n頭ポンポンしてあげるにゃ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125101, 0.0, 5, 0, 5, 0, 3.6, 0, 0, 1, 2, 1251005, 0, "猫とコーヒーに癒されるにゃ～\nでもあたしにとって最大の癒しは\nいっつもそばで微笑んでくれる\nキミの存在なんだにゃ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1251006, 0, "はい、猫専用マタタビミルク♪\nキミにはキミ専用特製コーヒー♪\nなにが特製かって？　にゃはは！\n愛情をいっぱい入れてるのにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1251007, 0, "気持ちのいい天気だにゃ～♪\nこんな日はテラスで休憩にゃ！\n太陽の光とコーヒーの香りで\nいっぱい癒されてほしいのにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1251008, 0, "キミが猫だったらにゃ～\nそしたらずっと一緒にいて\nなでなでしたり、すりすりしたり\nずっと愛でてられるのににゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1251009, 0, "キミが優しくしてくれるから\nすっかり懐いちゃったにゃ～♪\nにゃ！？　違…猫の話にゃ！\nあたしのことじゃないにゃ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1251010, 0, "オシャレな外観のカフェで\nキミと働けてすごく楽しいにゃ♪\nこのままずっと二人で一緒に…\nにゃはは…なんてにゃ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125101, 0.0, 2, 0, 6, 0, 8.6, 0, 0, 2, 1, 1251011, 0, "今日はカフェを貸し切りにして\nキミの誕生日パーティーにゃ♪\nじゃあ猫と一緒に歌うのにゃ…\nハッピバ～スデ～にゃ～にゃ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1251012, 0, "誕生日おめでとにゃ～！\nお祝いにキミの顔のラテアートを\n作ったんだけど、上手くできて\n飲むのがもったいないのにゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125201, 0.0, 6, 0, 1, 0, 5.1, 0, 0, 1, 2, 1252001, 0, "…にゃんだおまえ\nおやつがほしいにゃ～？\nって、き、貴公っ！\nいつから聞いていたのだ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125201, 0.0, 2, 0, 2, 0, 4.4, 0, 0, 1, 1, 1252002, 0, "コーヒーだけではなく\n一緒にお菓子はどうだ？\nこの間仕入れたチョコレートが\nおすすめだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125201, 0.0, 1, 0, 3, 0, 5.1, 0, 0, 1, 7, 1252003, 0, "お菓子好きなお客さんのために\n新作の仕入れは欠かせないな\nコーヒーもそうだが、お菓子も\n奥が深くて面白いのだぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125201, 0.0, 2, 0, 4, 0, 8.0, 0, 0, 1, 1, 1252004, 0, "いらっしゃい…って、貴公か\nその顔、もしやなにか相談事か？\nよし、コーヒーでも淹れてやるから\n私に話してみないか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125201, 0.0, 6, 0, 5, 0, 1.5, 0, 0, 1, 2, 1252005, 0, "もぐ…なんだ？\nし、新作の味見をしていたのだ！\n貴公も気になるのなら\n一緒に味見してみるか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1252006, 0, "ほら、コーヒーを淹れたから\n貴公も少し休憩するといい\n疲れが溜まっているようなら\nパンケーキもつけてやろう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1252007, 0, "店に立つ姿もなかなか\n様になってきたと思わないか？\nふふっ、貴公の給仕服も\n良く似合っているぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1252008, 0, "ふふっ♪\n猫たちが気持ちよさそうに\n日向ぼっこしているぞ\n…正直、うらやましいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1252009, 0, "ぬあっ！？\nき、貴公、そのコーヒーは\n私が飲んでいたものでは…\nいや、なんでもないっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1252010, 0, "貴公のためにコーヒーを淹れたぞ\n…私にも淹れてくれるのかっ？\nふふっ、では一緒にテラスで\n語らおうではないか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125201, 0.0, 1, 0, 6, 0, 6.0, 0, 0, 2, 2, 1252011, 0, "今日は貴公の誕生日だからな\n好きなお菓子を選ぶといい！\n…私が淹れたコーヒーがいい？\n相変わらず欲がないな、貴公は");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1252012, 0, "誕生日おめでとう！\n貴公のために特別なパンケーキを\n用意したから一緒に食べよう♪\n味見をしたが絶品だったぞ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125301, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1253001, 0, "この服はいかがでしょうか？\n大自然の中でもしっかりと\nお勤めを果たすため、いろいろ\nこだわった逸品でございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125301, 0.0, 2, 0, 2, 0, 5.5, 0, 0, 1, 1, 1253002, 0, "わんわんの主さまも\n最近は芸が達者になったのですよ\nお手、お座り、お回り…\nふふっ、よくできました");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125301, 0.0, 2, 0, 3, 0, 7.4, 0, 0, 1, 1, 1253003, 0, "自然は分け隔てなく\nそこに生きる者全てに恵みを\nもたらしてくれます\n大切にしたいですね、主さま");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125301, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1253004, 0, "主さま、わたくしと\n森でお散歩をしませんか？\nたまには街の喧騒を離れ、二人で\n静かに過ごすのもよろしいかと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125301, 0.0, 2, 0, 5, 0, 3.2, 0, 0, 1, 1, 1253005, 0, "森を守るお仕事は大変です\nしかし主さまたちと一緒なら\nやり遂げられると信じております\n今までの冒険と同じように");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1253006, 0, "森の中で、主さまと\n穏やかなひとときを過ごす…\nわたくしにとって\nこれに勝る幸せはございません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1253007, 0, "わんわんのほうの主さまも\n森の中でのんびりできて\n大満足のようでございます\nお腹をなでなでしてあげましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1253008, 0, "森の中はとても静かで\nわたくしどもしかいないような\n気分になってまいりますね\n和やかな心地がいたします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1253009, 0, "ずっとずっと、こうして主さまと\n過ごしていたいです…\nいずれお勤めに戻らなくては\nなりませんが、それでも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1253010, 0, "主さま、もう少し傍へ\n寄ってもよろしいでしょうか…？\n寒気がするので、主さまたちの\n温もりをより感じたいのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125301, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1253011, 0, "本日はお誕生日\nおめでとうございます、主さま\n人も自然も、このように時を経て\n成長していくのですね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1253012, 0, "主さま、誕生日を迎えられたこと\n心からお祝い申し上げます\n精霊や自然も、きっと主さまの\n成長を祝福してくれますよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125401, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1254001, 0, "私もレンジャーの仲間入りです\nその…この制服、どうですか？\n少しブカブカですけど…\nいつかピッタリになるといいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125401, 0.0, 2, 0, 2, 0, 4.8, 0, 0, 1, 1, 1254002, 0, "レンジャーのみなさんは\n弓矢の扱いが上手な人ばかりです\n私も技術を磨いて、みなさんや\nあなたのお役に立ちたいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125401, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1254003, 0, "今日はニャットちゃんを洗って…\n…え？　お世話してえらい？\nふふ、私はニャットちゃんの\nママ代わりですからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125401, 0.0, 2, 0, 4, 0, 8.1, 0, 0, 1, 3, 1254004, 0, "魔物は怖い生き物。でも…\nレンジャーになってから\n色んなことを考えています\nこれが学ぶということなんですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125401, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1254005, 0, "見てください\nちょっと筋肉がついてきたんです\nこのままムキムキになれたら…\n私があなたのこと、守れますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1254006, 0, "レンジャーの権限において\nターゲットを拘束します\nニャットちゃん、\n私のフォローをお願い！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1254007, 0, "私の世界と\nニャットちゃんの世界は違う\nでも同じ場所で生きているから…\nこの『居場所』を一緒に守ろう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1254008, 0, "人と魔物…\n共生の道は険しいけれど\nできないわけじゃない\n私たちが、その証明になろう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1254009, 0, "ここはニャットちゃんと私で\n対処します\n私たちが成長した姿、\nあなたも見守っててください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1254010, 0, "弱肉強食は真実だと思うけれど、\nそれが全てなわけじゃない…\n私も誰かを助けて助けられながら\nあなたの隣を歩いていきたいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125401, 0.0, 2, 0, 6, 0, 2.9, 0, 0, 2, 6, 1254011, 0, "少し、時間ありますか？\nレンジャーの仲間たちと一緒に\nケーキを用意したんです\nその…あなたの誕生日なので…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1254012, 0, "お誕生日おめでとうございます\nそれと、ニャットちゃんからも…\n『いつもありがとにゃん♪』\n…な、なんて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125501, 0.0, 2, 0, 1, 0, 8.0, 0, 0, 1, 1, 1255001, 0, "剣に拳、そして魔法…\n気付けばいろんな戦い方が\nできるようになっちゃった\nどんどん私を頼っていいからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125501, 0.0, 2, 0, 2, 0, 4.8, 0, 0, 1, 1, 1255002, 0, "魔法使いって結構\nいろんな依頼で重宝されるんだ！\nおかげで騎空艇のための貯金が\nいいペースで貯まってるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125501, 0.0, 1, 0, 3, 0, 5.0, 0, 0, 1, 6, 1255003, 0, "陰キャとか陽キャとか\nよく分からないけど…\nどっちのビカラちゃんも大好き！\nあなたもそうだよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125501, 0.0, 1, 0, 4, 0, 7.3, 0, 0, 1, 2, 1255004, 0, "あなたやビカラちゃんたちと\n空を思いっきり冒険できたら\nきっと楽しいだろうなぁ\n…よし、騎空団活動頑張ろっと！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125501, 0.0, 5, 0, 5, 0, 7.1, 0, 0, 1, 1, 1255005, 0, "ジョブチェンジしたら衣装も\n変わるけど…あなたはどの服が\n好きかな？　…全部好き？\nな、なんだか少し照れるかも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1255006, 0, "あなたがそばにいてくれたら\n誰が相手でも負ける気がしない！\n見てて！　この新たな力で\n私たちの道を切り開いてみせる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1255007, 0, "細かいコントロールは苦手で…\nやりすぎちゃったらゴメンね！\n思いっきり魔力を集中させて…！\nいくよ！　エーテルブラスト！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1255008, 0, "え、かわいいポーズだねって…？\n実は私もお気に入りなんだ！\n魔法詠唱に必要な動作でね\nこうしたら集中できるの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1255009, 0, "あなたとの冒険はいつだって\nワクワクがいっぱい！\nさあ、次はどこへ行こっか！\nその道は絶対空に続いてるから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1255010, 0, "魔法は便利で強力な力だけど…\n私の一番の武器は団員との絆！\nこれからもあなたのこと\n頼りにさせてもらうからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125501, 0.0, 5, 0, 6, 0, 10.1, 0, 0, 2, 1, 1255011, 0, "実は、ケーキを用意したんだ！\n魔法を使うのはズルい気がして\n手作りに挑戦したんだけど…\n気に入ってくれるといいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1255012, 0, "団員の誕生日だもん\n思いっきり祝わなくっちゃ！\n見てて、エーテルブラストを\n花火代わりに打ち上げるから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125601, 0.0, 2, 0, 1, 0, 3.6, 0, 0, 1, 1, 1256001, 0, "遠路はるばるランドソルに来て\n正解だったよ！\nまさにラッキー！　クッキー！\nビッキィ！　だね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125601, 0.0, 1, 0, 2, 0, 3.5, 0, 0, 1, 2, 1256002, 0, "ランドソルの街並はどこも\n見事だね！　しっかりと目に\n焼き付けてパレードの山車の\n参考にさせてもらうよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125601, 0.0, 2, 0, 3, 0, 4.1, 0, 0, 1, 6, 1256003, 0, "この街で一番活気があるのは\nどこかな？　ネズ耳を売るなら\nそういうところが\nうってつけだからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125601, 0.0, 1, 0, 4, 0, 4.5, 0, 0, 1, 2, 1256004, 0, "やぁ、いらっしゃい！　ネズ耳を\n売るのを手伝ってくれるかい？\nきみと一緒なら\nたくさん売れる気がするんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125601, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1256005, 0, "きみの運勢は大吉だよ！\nなんたって鼠神宮の巫女が\nそばにいるんだからね！\nこれって最高のご利益だろう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1256006, 0, "みんな集まれー！\nぼくと一緒に楽しく踊ろうよ！\nイヤなことも辛いことも全部\nぼくが忘れさせてあげるからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1256007, 0, "ぼくが笑顔でいれば\nつられてみんなも笑顔になる\nだからぼくはいつも\n笑っていたいんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1256008, 0, "どんな人でも手を繋げば\nもう友達さ！　街中の人と\n仲良くなってみせるよ！\nさぁ、ランドソルでぼくと握手！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1256009, 0, "きみが笑顔でいると、不思議と\nぼくまで幸せな気持ちになるんだ\nだからきみのこと\nずっと笑わせてあげるからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1256010, 0, "もし元気が出ない時があれば\n遠慮なくぼくを呼んでほしいな！\nどれだけ離れていたとしても\n駆けつけて笑顔にしてみせるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125601, 0.0, 2, 0, 6, 0, 3.2, 0, 0, 2, 1, 1256011, 0, "きみ、今日誕生日なんだって？\nそういうことなら早速お祝いだ！\nハッピー！　バースデー！\nトゥーユー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1256012, 0, "誕生日おめでとう！　今日一日\nきみのお祝いをしてあげるよ！\nビッキィを独り占めできるなんて\nきみは幸せ者だね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125701, 0.0, 2, 0, 1, 0, 4.6, 0, 0, 1, 1, 1257001, 0, "錬金術は、戦闘のサポートに\n長けた学問ですね\n私の性格に合っていますし\n冒険の手助けは任せてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125701, 0.0, 2, 0, 2, 0, 6.6, 0, 0, 1, 1, 1257002, 0, "実際に冒険に出ることで\nサポート業務に何が必要かを\nしっかり学ばせていただきます\nさあ、冒険にでかけましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125701, 0.0, 2, 0, 3, 0, 8.1, 0, 0, 1, 1, 1257003, 0, "現場でのサポートだけでなく…\nもちろん、ギルド管理協会として\n事前のサポートも行いますよ\n冒険のお手伝いは任せてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125701, 0.0, 1, 0, 4, 0, 4.6, 0, 0, 1, 2, 1257004, 0, "あなたと一緒なら\nどんな危険な場所でも\n不安はありません\n背中はお任せしますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125701, 0.0, 2, 0, 5, 0, 8.2, 0, 0, 1, 6, 1257005, 0, "じー…はっ、すみません！\n次はいつ冒険に行かれるのか\n気になってしまい、つい…\n私はいつでも大丈夫です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1257006, 0, "さあ、調合の時間です！\n素敵なお薬でみなさんを\nサポートいたしますね\n私にお任せください♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1257007, 0, "ふむふむ…この本によると\nそんな効果まで…？\n…あっ、独り言聞こえてました？\nお、お恥ずかしいです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1257008, 0, "このお薬の材料ですか？\nそれはですね…\nふふ、内緒です♪\n聞かないほうがいいと思いますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1257009, 0, "あなたの冒険のスタイルに合う\nお薬を作っているところなんです\nどんなものが役立ちそうか\nあなたのことなら研究済みです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1257010, 0, "私の錬金術が、あなたの冒険に\n少しでも役に立つように…\nそう思って心を込めて作成中です\nこれが私なりの戦い方ですから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125701, 0.0, 1, 0, 6, 0, 5.9, 0, 0, 2, 2, 1257011, 0, "お誕生日おめでとうございます！\nお祝いに宝の地図をどうぞ\n私の錬金術による\n冒険のサポート付きですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1257012, 0, "今日はあなたのお誕生日ですね\nプレゼントは少々お待ちください\n今作っているところですので…\nえ？　危なくなんてないですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125801, 0.0, 2, 0, 1, 0, 5.1, 0, 0, 1, 1, 1258001, 0, "まだまだ冒険者としては\n知らぬことばかりの身\nこれから近くで、冒険のいろはを\n学ばせてくださいませ。兄様");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125801, 0.0, 5, 0, 2, 0, 5.6, 0, 0, 1, 6, 1258002, 0, "この姿に変身すること自体は\n造作もないことなのですが\n姿が変わる最中を見られるのは\n…す、少し恥ずかしいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125801, 0.0, 6, 0, 3, 0, 7.2, 0, 0, 1, 2, 1258003, 0, "兄様はこの姿のわたくしにも\n普段通り接してくださいますね\n…これはこれでかわいい？\nそ、そのようなご冗談は…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125801, 0.0, 2, 0, 4, 0, 5.1, 0, 0, 1, 5, 1258004, 0, "『兄様』と呼ぶのが、まだ少し\n恥ずかしいようで。呼ぶたびに…\nドキドキ、してしまいます…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125801, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1258005, 0, "兄様はいつも優しくて、強くて、\n勇敢で、たくましくて…\nそれにその…すごく\nかっこいいおかた…です♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1258006, 0, "わたくしの力は忌むべきもの\nそれでも頼らねばならぬのは\nわたくしが未熟者ゆえ…\nだから必ず、強くなります…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1258007, 0, "他者に操られていたとはいえ\n罪を重ねたのは事実…\nそれを止めてくださった兄様には\n感謝してもしきれません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1258008, 0, "一人前の冒険者になれば\n世のため人のために働けます！\nそうやって一つずつ、世界への\n償いをしていきたいのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1258009, 0, "わたくしの過ちを知っても\n受け入れてくださったこと\nとてもうれしく思っております…\nわたくしは果報者ですね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1258010, 0, "無知なわたくしに、もっと\nたくさんのことを教えてください\n冒険のこと、戦いのこと、\nそれに…兄様のこと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125801, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1258011, 0, "お誕生日、おめでとうございます\n兄様のためにご馳走をと\n腕を振るいましたのでどうぞ！\n…上手くできたところだけ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 125831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1258012, 0, "ご生誕のお祝いとしては少々\n足りませんが、どうかお部屋に\n花を生けさせてくださいませ！\nうーん…兄様に似合う花は…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126001, 0.0, 5, 0, 1, 0, 6.1, 0, 0, 1, 2, 1260001, 0, "あまりにお兄さんが普通なので\nこの姿なこと忘れちゃいます…♪\nこれでも本当は、ちょっと\n恥ずかしかったりするんですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126001, 0.0, 2, 0, 2, 0, 0.0, 0, 0, 1, 2, 1260002, 0, "まだまだ未熟なわたしですけど、\nお兄さんが側にいると安心です♪\n魔物との戦闘の時はもちろん…\nそれ以外も…なんて…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126001, 0.0, 4, 0, 3, 0, 6.3, 0, 0, 1, 1, 1260003, 0, "聖女って呼ばれるの苦手なので、\nこの姿のままいればいいかもです\n代わりに、悪魔…とか呼ばれて\nしまいそうですけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126001, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1260004, 0, "少し落ち込んじゃった時も\nお兄さんの優しい笑顔と言葉で\nわたしの心はすーって癒される…\nお兄さんはわたしの救急箱です♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126001, 0.0, 5, 0, 5, 0, 6.4, 0, 0, 1, 2, 1260005, 0, "お兄さんの前では普通の女の子で\nいられるから居心地いいです…♪\nだからお兄さん、少しの間だけ…\n二人っきりでいていいですか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1260006, 0, "お兄さんに誓わせてください\nこの力を正しく使えるように…\n堂々と前を向けるように…\nわたしは強くなります…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1260007, 0, "朽ち果てた天使の輪に漆黒の翼…\nふふ、まるで堕天使ですね…\nこの姿…好きではないですけど\n罪を忘れずにいられる気がして");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1260008, 0, "この姿の自分も受け入れられる…\nたとえ恐れられてしまっても\n誰かを守る盾となれるのなら…\nそれがわたしの贖罪です…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1260009, 0, "冒険中どんな強敵に出くわしても\nお兄さんに傷はつけさせません\nだって、わたしが守りますから…\nこの身に代えても…全力で…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1260010, 0, "どんなわたしでも受け入れてくれる\nお兄さんのことが大好きです\n…今のは妹としての言葉じゃ…\nいえ…なんでもありません♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126001, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1260011, 0, "今日はお兄さんの誕生日ですね！\nリリちゃんとプレシアちゃんと\n一生懸命ケーキ作ったんです…♪\nみんなでお祝いさせてください♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1260012, 0, "お誕生日おめでとうございます\n実は…アルバイトで貯めたお金で\nプレゼント買ったんです…\n受け取ってください…お兄さん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126101, 0.0, 1, 0, 1, 0, 4.7, 0, 0, 1, 6, 1261001, 0, "くろい、はね\nわるいこ、ぽい？\n…かも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126101, 0.0, 2, 0, 2, 0, 7.8, 0, 0, 1, 6, 1261002, 0, "ごちそうの、けはい\nくんくん…ちかい…\n…はっ\nお兄…だった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126101, 0.0, 2, 0, 3, 0, 3.4, 0, 0, 1, 1, 1261003, 0, "ぱとろーる、いこ？\nあ\nそのまえに\nごはん。ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126101, 0.0, 2, 0, 4, 0, 3.0, 0, 0, 1, 1, 1261004, 0, "お兄\nて、よぶと\nはっぴーな、きもち\n…お兄…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126101, 0.0, 5, 0, 5, 0, 8.5, 0, 0, 1, 2, 1261005, 0, "お兄\nいつも、おいしそう\nぱわーふーど？　かも\n…じゅる…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1261006, 0, "くろい、ちから。も\nわたしの、ちから\nつよくなる。よ\nみてて。お兄");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1261007, 0, "やっと。なかま、なれた\nいっしょに、たたかう。よ\n…だから。お兄\nどこにも、いかないで。ね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1261008, 0, "しんまい、ぼうけんしゃ\nできない、こと\nしらない、こと、たくさん\nおしえて、ね？　お兄");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1261009, 0, "もっと、おおきく…\nもっと、つよく…\nお兄、まもれる、くらい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1261010, 0, "ぽかぽか、もちもち\nいーにおい\nお兄の、こと\nずっと、はむって、たい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126101, 0.0, 1, 0, 6, 0, 3.3, 0, 0, 2, 2, 1261011, 0, "たんじょうび、おめでと\n肉まん…どぞ\nじー…\n……ごくり");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1261012, 0, "はっぴーばーすでー\nきのうより、おとな？\nじゅくした、お兄\nあじみ。するね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126201, 0.0, 2, 0, 1, 0, 4.5, 0, 0, 1, 1, 1262001, 0, "眷属よ、艶やかな装いじゃろう？\nほれ、思いつく限り\n気の利いた文句を並べてみよ\nわらわが聞いてやろう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126201, 0.0, 2, 0, 2, 0, 5.9, 0, 0, 1, 1, 1262002, 0, "なりゆきで手に入った装束じゃが\nわらわは気に入っておる\n夜を統べる者にふさわしい\n意匠じゃからの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126201, 0.0, 1, 0, 3, 0, 10.2, 0, 0, 1, 6, 1262003, 0, "この晴れ着を披露するのに\nうってつけの舞台はないかの？\nなに、今から魔物の討伐とな？\nまあそれでもよかろう…ゆくぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126201, 0.0, 1, 0, 4, 0, 4.9, 0, 0, 1, 2, 1262004, 0, "眷属よ、どこか大きな会場を\nおさえよ。お主とわらわの\n威風堂々たる姿を\n世界に知らしめてやるのじゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126201, 0.0, 2, 0, 5, 0, 7.1, 0, 0, 1, 1, 1262005, 0, "わらわの艶姿を隣で楽しめるのは\nお主だけの特権じゃぞ\n特別な眷属であることを\n誇りに思うがよい♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1262006, 0, "眷属よ、礼を言うぞ\nこの装束を纏うたび、わらわは\n今宵の景色を思い出すじゃろう…\n湖上の月と…お主の笑顔を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1262007, 0, "きれいな湖じゃのう…\nじゃが、油断は禁物じゃ\nお主は泳ぎが苦手であろう？\nわらわの傍を離れるでないぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1262008, 0, "眷属よ、お主は夜が好きか？\nもし一日の終わりをさみしく\n思うことがあれば、わらわが\n一緒にいてやらんこともないぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1262009, 0, "まったく、お主は大胆じゃのう…\n裳裾が濡れてしまったではないか\n風邪を引いてはならん…\n近う寄れ…く、くっつくのじゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1262010, 0, "水面に映る、今宵の月のように\nわらわはきれいなものとして…\nお主の瞳に映っておるか？\nこ、言葉にして、教えるのじゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126201, 0.0, 2, 0, 6, 0, 4.1, 0, 0, 2, 1, 1262011, 0, "ハッピーバースデー、眷属よ\n今日が最良の日となるように\nわらわが全てを尽くして\nお主を祝福してやろう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1262012, 0, "今日はお主の誕生日じゃ\n遠慮せず欲しいものを述べてみよ\nいつも隣で支えてくれるお主に\n感謝の気持ちを示したいのじゃ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126301, 0.0, 2, 0, 1, 0, 9.3, 0, 0, 1, 1, 1263001, 0, "人の心を惑わすほどの美を持ち、\nその美を磨くことを怠らない…\nそんなボクがどこまで行きつくか\nちゃんと期待しててよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126301, 0.0, 2, 0, 2, 0, 6.2, 0, 0, 1, 6, 1263002, 0, "吸血鬼の力の影響かな…\n時々、無性に飲みたくなるんだ…\nあの真っ赤なトマトジュースを！\n差し入れ、いつでも歓迎するよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126301, 0.0, 2, 0, 3, 0, 4.9, 0, 0, 1, 5, 1263003, 0, "魔性の雰囲気をたたえた\nこの麗しきドレス…！\n人の身とは思えぬ美しさの\nボクにピッタリだよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126301, 0.0, 2, 0, 4, 0, 5.4, 0, 0, 1, 6, 1263004, 0, "このドレス着てると、凄い力が\n勝手に出ちゃって不便なんだよね\nまあ、全然オッケーだけど！\n美は全てに優先されるからね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126301, 0.0, 5, 0, 5, 0, 6.2, 0, 0, 1, 1, 1263005, 0, "遠慮せずにもっと近付きなよ？\n体温すら感じるくらいにさ\nそれくらいの距離じゃないと\n見えない美があるからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1263006, 0, "ボクほどの美しさになれば\n魔物すらかしずくのさ♪\nま、それも当然だよね\nボクは美を統べる者なんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1263007, 0, "ボクという存在を後世に残すため\n絵画を描いてもらおうと思ってね\n有名な画家に依頼したから\nサイフの中身は空っぽさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1263008, 0, "ああ、ボクの周りのこの枠かい？\n美を統べる者としての圧倒的な\nかわいい力で具現化したものさ！\nどうだい、すごいでしょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1263009, 0, "吸血鬼っていろいろな姿に\n変身できるみたいだけど…\nボク以上に美しいものには\nさすがになれないだろうね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1263010, 0, "ボクという美を見ること\nそれ以上の幸福なんて、\n存在するわけないだろう？\n思う存分、目に焼きつけて？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126301, 0.0, 1, 0, 6, 0, 3.9, 0, 0, 2, 2, 1263011, 0, "誕生日だって？　めでたいね♪\nそれはそうと、話があるんだ\n実は欲しい化粧品があってね\nボクにプレゼントする気はない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1263012, 0, "ハッピーバースデー！\n今日という日の記念に、\nボクを撮影する権利をあげるよ！\nさぁ、思う存分、ボクを撮って！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126401, 0.0, 2, 0, 1, 0, 2.8, 0, 0, 1, 1, 1264001, 0, "こう見えてもわたし、\nシュヴァリエなんですっ！\n『王家の装備』を\nお守りしてるんですよっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126401, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1264002, 0, "ライラエルさまは\nとってもとっても優しいんですっ\nいつも笑顔でわたしの話を\n聞いてくださるんですよ～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126401, 0.0, 2, 0, 3, 0, 4.0, 0, 0, 1, 1, 1264003, 0, "ほんとはハグとか握手とか\nいっぱいしたいんですけどねっ？\nわたしのパワーってちょっと\nすごすぎちゃって！　我慢です～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126401, 0.0, 2, 0, 4, 0, 5.3, 0, 0, 1, 1, 1264004, 0, "わたしを充填できる人が\nもう一人いるなんて、ぜーんぜん\n知りませんでしたっ！\nナイトさんってすごいんですね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126401, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1264005, 0, "ナイトさんのこと、\nもっともっと教えてくださいっ！\nわたし、あなたに\n興味津々みたいなんですっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1264006, 0, "戦うことはあんまり楽しくない\nですけど…悪い魔物さんを\n放置なんて絶対できませんっ！\nクローチェ、戦闘開始しますっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1264007, 0, "魔物さんの好き放題には\nさせませんよっ！\nこの国とみなさんを守るのも\nわたしの役目ですからっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1264008, 0, "どこに逃げてもロックオン！\nわたしの両手は甘くないです！\nひっさつ！\nダブルロケットぱーんちっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1264009, 0, "わたしからハグとかすると\n危ないので…なのでっ！\nナイトさんからしてもらえたり\nしないかな～って！　えへへっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1264010, 0, "充填してくれたときにもらった\nナイトさんのあったかさ…\nわたしのタンクにまだ残ってます\nだから…わたしは無敵ですっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126401, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1264011, 0, "今日はナイトさんのお誕生日っ♪\nいっぱいいっぱいおめでとうって\n言わせてくださいねっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1264012, 0, "お誕生日おめでとうございます！\n今日はどんなお願いだって\nフルパワーで叶えちゃいますっ！\nさあさあ、なんなりとっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126501, 0.0, 2, 0, 1, 0, 4.0, 0, 0, 1, 1, 1265001, 0, "『ジオ・テオゴニア』を\nわたしは愛しています\nあなたにも気に入って\nいただけると…うれしく思います");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126501, 0.0, 1, 0, 2, 0, 6.7, 0, 0, 1, 4, 1265002, 0, "頼りになる家臣たちがいなければ\nわたしは国主たり得ません\n…みなの想いに応えるためにも\n迷いがあってはいけませんね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126501, 0.0, 4, 0, 3, 0, 7.3, 0, 0, 1, 6, 1265003, 0, "『王家の装備』の力は強大で…\n反動も覚悟しなければなりません\n…傲慢なわたしを見ても\nどうか怖がらないでください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126501, 0.0, 6, 0, 4, 0, 4.3, 0, 0, 1, 1, 1265004, 0, "かしこまった服装は\nあまり好みではないのです\nどうにも窮屈に感じてしまって…\nそれほど奇抜ではないですよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126501, 0.0, 2, 0, 5, 0, 4.0, 0, 0, 1, 6, 1265005, 0, "これでも、しっかり者だと\n言われているのですよ？\n…あなたには気を抜いたところを\nよく見られてしまっていますが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1265006, 0, "あなた方は希望の光…\nその光を守るため\nあらゆる困難を退けましょう\n…どうか安心してくださいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1265007, 0, "天使のように見えた…ですか\nそれは羽のせいでしょうか\nあるいは…別の理由が？\nふふ、教えてくださいませんか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1265008, 0, "偽ることも後ろ暗いこともなく\nあなたと向き合うことができる…\nああ、なんと晴れやかな…\nなんと清々しい心持ちでしょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1265009, 0, "あなたはためらうことなく\n手を差し伸べてくださいましたね\n…ですから、わたしも\n大丈夫。もう迷いはありません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1265010, 0, "高き天から舞い降りて\n大地の上に立つというのも\n悪くはないかもしれません\n…そう、あなたの隣であれば");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126501, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1265011, 0, "お誕生日おめでとうございます\nあなたの未来が\n幸多きものになりますように…\n心から祈っています");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1265012, 0, "今日という日の奇跡のおかげで\nあなたと出会うことができました\nだから…どうか言わせてください\n万感の想いを込めた感謝と祝福を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126601, 0.0, 6, 0, 1, 0, 7.1, 0, 0, 1, 1, 1266001, 0, "暑さ対策は万全にすべきです\nこの麦わら帽子を被りなさい\n…私の帽子とお揃いなことに、\n特に深い意味はありませんよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126601, 0.0, 1, 0, 2, 0, 7.2, 0, 0, 1, 2, 1266002, 0, "秘密とは人にとって大事なもの…\n貴方も秘密を作るといいでしょう\nただし、私にだけはその内容を\n共有すること。いいですね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126601, 0.0, 7, 0, 3, 0, 7.9, 0, 0, 1, 1, 1266003, 0, "七冠とは利己的な存在\n当然、私も例外ではありません\nというわけで、貴方には私の夏に\n徹底的に付き合ってもらいますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126601, 0.0, 2, 0, 4, 0, 12.2, 0, 0, 1, 6, 1266004, 0, "ふむ…大人らしい水着で、\n私によく似合っている、と…\n褒め方を教えた覚えはないですが\nしかし…悪くありませんね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126601, 0.0, 2, 0, 5, 0, 7.6, 0, 0, 1, 6, 1266005, 0, "おや、日差しが眩しいのですか？\nでは私のサングラスを使いなさい\n…ふふっ。いえ、失礼\nお似合いですよ、かわいらしくて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1266006, 0, "私が…いえ、私たちが\n存分にもてなしてあげますよ\n貴方の望む夏を\n実現してあげましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1266007, 0, "…考えてみれば、こうして\nゆっくり過ごす時間など\nいつ以来でしょうか\n…想い出に残る夜にしましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1266008, 0, "今日くらいはすべて忘れて\n羽を伸ばすことにします\n貴方もそうしなさい？\n我々は日々働きすぎなのですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1266009, 0, "このナイトプールは貸切です\nつまり、ここでの出来事は\nなにもかも我々だけのもの…\n今夜のことは秘密ですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1266010, 0, "呆れるほどのお人よし…\n貴方を一言で表せばこうも単純\nなのに、私は貴方になれない…\nこの秘密をいつか暴いてみせます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126601, 0.0, 2, 0, 6, 0, 10.2, 0, 0, 2, 6, 1266011, 0, "虫取り網をプレゼントした程度で\nそこまで喜ぶとは…まあ、\n気に入ったのならいいのですが\n…ハッピーバースデー");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1266012, 0, "夏らしく貴方の誕生日を\n祝わせてもらいましょう。分身に\n腕利きの花火職人を模倣させます\n思う存分、花火を楽しみなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126701, 0.0, 2, 0, 1, 0, 1.8, 0, 0, 1, 1, 1267001, 0, "夏といえば…いえ春夏秋冬\nいつでもマイシーズンですわ！\n私に似合わない季節など\nありませんもの。そうでしょう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126701, 0.0, 1, 0, 2, 0, 6.5, 0, 0, 1, 2, 1267002, 0, "きらめく海に輝く太陽…\nそして魅力的な私！\n夏を楽しむのに、これ以上\n必要なものがありまして？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126701, 0.0, 2, 0, 3, 0, 2.6, 0, 0, 1, 1, 1267003, 0, "あなたさま、夏ですわ！\nウィスタリア家の威信を\nかけたバカンスの始まりを\nここに宣言しますわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126701, 0.0, 2, 0, 4, 0, 3.0, 0, 0, 1, 1, 1267004, 0, "夏のグルメ、夏のレジャー…\n全て楽しみ切るなら\nいくら時間があっても\n足りませんわね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126701, 0.0, 2, 0, 5, 0, 6.8, 0, 0, 1, 1, 1267005, 0, "叶うならあなたさまと\nこの時間を永遠に…なんて\n…ふふ、何でもありませんわ\n先へ進んでこそ私たちですもの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1267006, 0, "いつも慌ただしいからこそ\n今だけはあなたさまと…\nこの潮騒と夕焼けのひと時に\n身を委ねていたいのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1267007, 0, "ほら、あなたさまも\nもっと近くに来てくださいな\nふふっ…捕まえましたわ♪\nさ、こちらで横になって…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1267008, 0, "ずっと走り続けていれば\nいつか疲れ切ってしまいますわ\nさざ波の音に耳を傾け\nしばしの休憩といたしましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1267009, 0, "夏の美しさに目を奪われるのも\n結構ですが、ちゃんと私も\n見てくださいませんと\n拗ねてしまいますわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1267010, 0, "太陽はまばゆく燃える\nばかりではありませんわ\n夕焼けのように甘く焼き付く…\nそんな一面もありますのよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126701, 0.0, 6, 0, 6, 0, 9.0, 0, 0, 2, 2, 1267011, 0, "ハッピーバースデーですわね！\nささやかなお祝いですが、記念に\n打ち上げ花火を用意しましたの\n一万発では少なすぎるかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1267012, 0, "あなたさまの誕生日をこうして\n一緒にお祝いできるなんて…\nこの幸せな時間、私にとっても\n最高の思い出になりますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126801, 0.0, 1, 0, 1, 0, 6.1, 0, 0, 1, 4, 1268001, 0, "ふう、暑いわね…\n熱中症には気を付けないと\n喉がかわいたらすぐに言ってね？\n麦しゅわ以外も用意してるわよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126801, 0.0, 4, 0, 2, 0, 5.3, 0, 0, 1, 6, 1268002, 0, "夏の海辺の仕事なら水着が効率的\nってみんなが言ってたけど\nもしかして…うちのギルドだけの\n常識だったんじゃ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126801, 0.0, 6, 0, 3, 0, 6.7, 0, 0, 1, 4, 1268003, 0, "聖騎士っぽい水着、って\n注文したんだけど…\nどの辺がそれっぽいのかしら？\n…もしかして、色？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126801, 0.0, 1, 0, 4, 0, 5.4, 0, 0, 1, 4, 1268004, 0, "キミに見られるなら、\nもう少しおとなしいデザインの\n水着がよかったかも…\nう、ううん…なんでもないわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126801, 0.0, 2, 0, 5, 0, 5.4, 0, 0, 1, 1, 1268005, 0, "この水着、どう…かな？\n少しは夏っぽい格好してたほうが\nキミも涼しく感じるかな、なんて\n思ったんだけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1268006, 0, "せっかくの海なんだもの\nタマキさんとミフユさんに\n負けないくらい\n楽しんじゃいましょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1268007, 0, "暑いときはすっきりした\nセゾン系もいいけど…\n私はボディがしっかりしてる\nアベイ系が好きだわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1268008, 0, "普段忙しければ忙しいほど\n遊べるときにしっかりと遊んで\n日常とメリハリをつけるの\n社会人からのアドバイスよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1268009, 0, "青い海！　白い泡！\nそして、キミ…！\n必要なものが全部あるんだもの\nふふっ、ここはまるで天国ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1268010, 0, "せっかくのバカンスが\nキミと二人きりじゃないのは\nちょっと残念だけど…\nそれはまた今度、ね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126801, 0.0, 1, 0, 6, 0, 2.3, 0, 0, 2, 2, 1268011, 0, "お誕生日おめでとう♪\n急いで来ちゃったから\nこんな格好でごめんね？\n少しでも早くお祝いしたかったの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1268012, 0, "ハッピーバースデー、キーミッ♪\nさあ、めでたい日なんだから\n盛大にお祝いしなくちゃ\nはい、カンパーイ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126901, 0.0, 5, 0, 1, 0, 11.3, 0, 0, 1, 1, 1269001, 0, "水着って、そわそわするわね…\n薄手だし…布も少ないし…\nま、まるで…ほとんど\n着てない、みたい…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126901, 0.0, 1, 0, 2, 0, 16.8, 0, 0, 1, 4, 1269002, 0, "夏の日差し、苦手で…\n暑いとすぐ疲れちゃうから\n毎年おうちにこもってるの…\nいつもこもり気味だけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126901, 0.0, 2, 0, 3, 0, 12.3, 0, 0, 1, 6, 1269003, 0, "暑い日は体調を崩しやすいけど…\nあなたは、大丈夫…？\n日差しの下でも、熱帯夜でも…\nお世話するから、言ってね…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126901, 0.0, 2, 0, 4, 0, 15.4, 0, 0, 1, 1, 1269004, 0, "海を見てると、すごく落ち着く…\nでもきっと、これは海の力だけ\nじゃなくて…あなたが隣にいる\nから…よね。うふふ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126901, 0.0, 5, 0, 5, 0, 0.0, 0, 0, 1, 5, 1269005, 0, "み、水着…恥ずかしいけれど、\nあなたが褒めてくれるなら\n毎日でも、着…あ、でも…\nさすがに毎日は…うぅ～…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1269006, 0, "潮騒は最高の子守歌って\nよく言うわよね…\n…あなたに安眠してもらうための\n歌なら、負けない…わ…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1269007, 0, "海の遊び方はいろいろあっても…\nあなたとこうして夕焼けを見て\n過ごしているだけで…\nもう胸が…いっぱい…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1269008, 0, "海って素敵だけど…\n簡単には来れないのが残念ね…\nあっ…こ、今度はわたしの\nワイバーンで来る…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1269009, 0, "波が寄せては消えていく音…\nとてもきれいだけど、\n引き込まれそうで、怖い…\nだから…手をつないでいて、ね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1269010, 0, "浜辺に残した足跡が消えても…\nあなたとわたしの歩む道は\nこれからも消えることなく\nずぅっと続きますように…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126901, 0.0, 6, 0, 6, 0, 13.2, 0, 0, 2, 2, 1269011, 0, "お誕生日なのね…\nあなたをお祝いするためなら、\nわたし、どこにでも行くわ…♪\nでも…水着だと、変かしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 126931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1269012, 0, "ハッピーバースデー…♪\n花火…音が大きくて怖いけど、\nあなたをお祝いするために\n一回上げてみたい…かも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127001, 0.0, 2, 0, 1, 0, 5.2, 0, 0, 1, 1, 1270001, 0, "海…それは身も心も\n解放されてしまう場所…！\nぐふふ…クウカの全てが\n見られてしまうのですね…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127001, 0.0, 5, 0, 2, 0, 8.0, 0, 0, 1, 2, 1270002, 0, "はうぅ…視線を感じます…\n水着は肌の露出が多くて\nちょっと恥ずかしいです…\n体が熱くなって…それがイイ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127001, 0.0, 2, 0, 3, 0, 9.2, 0, 0, 1, 1, 1270003, 0, "夏の砂浜はとっても熱々です…！\nここに寝転がれば…ぐふふ…♪\n見ていてください、ドＳさん！\nクウカが熱砂の上で悶える姿を！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127001, 0.0, 5, 0, 4, 0, 7.8, 0, 0, 1, 3, 1270004, 0, "水着のクウカを前に、ドＳさんは\n「まだ布の面積が多いな…\n俺が減らしてやるぜ」\nと言い放ち…じゅるり…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127001, 0.0, 1, 0, 5, 0, 6.2, 0, 0, 1, 6, 1270005, 0, "海に二人で来るのって\nなんだか…照れくさいです、ね\n何をしたらいいんでしょう？\nドＳさん…教えてくれますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1270006, 0, "イイ…イイですよ…もっと…！\nえっ、なにをしているのかって？\nご覧の通り、海を楽しんでいます\nドＳさんも一緒に、どうですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1270007, 0, "海の生き物はクウカの\n妄想によく出てきます…！\nヌルヌル、ビリビリ、ペロペロと\nバリエーション豊富ですから…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1270008, 0, "ドＳさん、そんなに\n熱い眼差しで見つめられたら\nクウカ…痺れちゃいます…♪\n体がビリビリして…止まりません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1270009, 0, "夜の海って…不思議な雰囲気です\nお互いの顔がよく見えなくて…\nあっ…！　す、すみません…\nくっつき過ぎてしまいました、ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1270010, 0, "こうして二人で遊んでいると\nあっという間に時間が過ぎて\nしまいますね…\n楽しくて…少しだけ、切ないです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127001, 0.0, 2, 0, 6, 0, 4.3, 0, 0, 2, 1, 1270011, 0, "ドＳさんのお誕生日を\n海でお祝いするなんて…！\nあっ、先ほど砂浜で拾った\nスゴい形の貝殻をどうぞ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1270012, 0, "おめでとうございます！\nドＳさんが生まれた日…\nクウカと一緒に痺れるような\n体験をしましょう～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127101, 0.0, 1, 0, 1, 0, 2.9, 0, 0, 1, 1, 1271001, 0, "毎日暑いですよね\nそんなときはやはり怪談です\nとっておきの怖い話で\n芯まで涼しくしてあげますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127101, 0.0, 2, 0, 2, 0, 7.1, 0, 0, 1, 1, 1271002, 0, "この水着、どうでしょうか？\n普段は黒ばかり着てますが\nこれは幽霊っぽくて\n涼しげで私は好きです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127101, 0.0, 2, 0, 3, 0, 6.8, 0, 0, 1, 6, 1271003, 0, "お水や帽子、日焼け止めなど\n日差し対策は万全です\nさあ、安全で健やかに\n夏を満喫しましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127101, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 1, 1271004, 0, "海や川、湖などに行くのなら\nお付き合いしますよ\n水場は霊が集まりやすいので\n私がお守りします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127101, 0.0, 2, 0, 5, 0, 8.4, 0, 0, 1, 2, 1271005, 0, "すみません、夏の海はあまり\n縁がないので楽しみ方が\n分からなくて…よければ教えて\nいただけますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127131, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 4, 1271006, 0, "今の私は海に溶けゆく白き\n泡沫のよう…ふふ、夏のせい\nでしょうか、がらにもなく詩的に\nなってしまいました");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1271007, 0, "海はときに恐ろしい一面を\n見せます。でもどうか恐れないで\n静かに身を任せれば必ず\n受け入れてくれるはずだから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1271008, 0, "昏く、涼やかな静寂の世界\n海がこんなにも落ち着くのは\n霊の世界に似ているからなのかも\nしれません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1271009, 0, "もし暑いようでしたら\nもっと私のそばにどうぞ…\n体温が低いので\nきっと涼しいと思います…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1271010, 0, "青く昏い海の底でこのまま\nあなたと二人永遠に…\nここにいるとそんな夢想ばかり\nしてしまいます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127101, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1271011, 0, "お誕生日おめでとうございます\n占いによると海が縁起がいいと\nでましたので、そちらでお祝い\nさせてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1271012, 0, "これ、誕生日プレゼントです…\n流木と貝殻で作った特製のお守り\nしっかりご祈祷したので、\nあらゆる魔を防ぐはずです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127201, 0.0, 5, 0, 1, 0, 9.5, 0, 0, 1, 2, 1272001, 0, "制服ってけっこういいわね\nなんとなく気が引き締まるし\nそれに意外と…かわいいし？\nや…別に頷かなくていいから…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127201, 0.0, 4, 0, 2, 0, 8.6, 0, 0, 1, 6, 1272002, 0, "今日の３時間目体育か～…ねえ、\nかったるいし屋上でサボらない？\n…はいはい、やっぱダメよね～\n分かりました～行くってば～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127201, 0.0, 3, 0, 3, 0, 9.5, 0, 0, 1, 6, 1272003, 0, "なんで青い春っていうのかしらね\n燃えるような赤い春でも、希望に\n満ちた黄色い春でもよくない？\n…めんどくさいって顔すんな～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127201, 0.0, 5, 0, 4, 0, 5.6, 0, 0, 1, 2, 1272004, 0, "ねー新しくクレープ屋ができたん\nだって。今日帰りに二人で…\n…って、べ…別に放課後デートの\nお誘いとかじゃないんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127201, 0.0, 2, 0, 5, 0, 7.7, 0, 0, 1, 6, 1272005, 0, "今日、夕方から雨らしいわよ。\n…へ～、傘持ってきてないんだ？\nったくしょうがないわね～\n入れてあげるから感謝しなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1272006, 0, "あいつ遅いな…待ち合わせ場所、\nここで合ってるわよね…あれ\nやっぱり裏門だったかも…？\nもう…不安になるから早く来い！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1272007, 0, "マジックアワーって名付けた人、\nきっとロマンチストな魔法使いね\n幻想的な空の下で、\nあいつとのんびり下校したいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1272008, 0, "放課後暇だし部活とかしようかな\n吹奏楽とか美術とか…あたし、\n芸術的センスあると思うのよね～\n今度あいつと見学いこうかしら♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1272009, 0, "なーんか最近、青春のせいで\n感情駄々洩れ気味なのよね…\nうっかりしてるとあいつに告は…\n…って、うっかりすんなあたし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1272010, 0, "まっすぐ寮に帰らないで\nどこかに寄り道しちゃいたいな…\n目的地なんてないけど、このまま\nあんたと一緒に歩きたいだけよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127201, 0.0, 6, 0, 6, 0, 4.5, 0, 0, 2, 2, 1272011, 0, "へーあんた今日誕生日なんだー\n知らなか…って教室行くの禁止！\nみんなでサプライズパーティーの\n準備してんだからね！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1272012, 0, "誕生日おめでと～！\n空の方見てて。今から魔法で、\nあんたの歳の数のバースデー花火\nぶっぱなすわよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127301, 0.0, 7, 0, 1, 0, 7.9, 0, 0, 1, 1, 1273001, 0, "ヒデサイ、どーうちの制服？\nアオハルって感じっしょ？\nどういう意味って…それは\nうちと一緒に見つけるんじゃん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127301, 0.0, 1, 0, 2, 0, 7.9, 0, 0, 1, 6, 1273002, 0, "ねねね、ヒデサイは何派？\nセーラー、ブレザー、ボレロ、\nどれが好き？　教えて！\n…あ、もしかして全部好きの人？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127301, 0.0, 1, 0, 3, 0, 4.9, 0, 0, 1, 4, 1273003, 0, "魔法まじムズい\n九九といい勝負してるよ\nあ、もしかして両方できたら\nブンブリョードーってやつ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127301, 0.0, 7, 0, 4, 0, 6.1, 0, 0, 1, 2, 1273004, 0, "ヒデサイ、暇な感じ？\n制服デートしよーよ\n楽しそーっしょ？\nじゃあ放課後よろ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127301, 0.0, 5, 0, 5, 0, 6.9, 0, 0, 1, 2, 1273005, 0, "ヒデサイといるとさ\nバイブス上がるんだよね\n楽しいし、ウキウキするし…\nうちに何か魔法かけたっしょ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1273006, 0, "ねねね、ヒデサイ\nみんなに見つからないように\n隠れるのドキドキして楽しーね！　\nあっ、そっか今は、しー…だね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1273007, 0, "二人でこーしてるとさ\nなんか少女漫画っぽくね？\nキラキラでアオハル系のやつ\nふふ、もうちょっと浸ってこーよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1273008, 0, "てゆーか、いい時間だし\nちょっちお腹減ったね…\nあ、そだ、お菓子あるし食べる？\nふふ、センセーには内緒だよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1273009, 0, "この屋上でも色々あったね\nヒデサイとの思い出いっぱいだよ\n…せっかくだし、今日も\n二人だけの思い出作っちゃう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1273010, 0, "割と風あるし\nちょっち冷えてきたね\nヒデサイも寒いっしょ？\n…ね、そっち寄ってもいい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127301, 0.0, 7, 0, 6, 0, 7.0, 0, 0, 2, 2, 1273011, 0, "ヒデサイ、おたおめ～！\nこないだ、パないお店\n見つけたから、一緒いこーよ\nもち、今日はうちがおごるし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1273012, 0, "ヒデサイ、誕生日だよね\n今日はうちがハッピーな日に\nするから、覚悟してて！\nたくさんお祝いしちゃうよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127501, 0.0, 2, 0, 1, 0, 7.7, 0, 0, 1, 1, 1275001, 0, "この格好、地元の子どもたちが\nきれいだって褒めてくれたわ♪\nあんたの感想も聞かせて？\n…ふふ、ありがと♪　うれしいわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127501, 0.0, 6, 0, 2, 0, 9.9, 0, 0, 1, 1, 1275002, 0, "その土地の匂いってあるわよね\nサラサリアだとスパイスの香りに\nお菓子と果物の甘い匂いも…\nあ～、おなか空いてきちゃった…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127501, 0.0, 1, 0, 3, 0, 6.2, 0, 0, 1, 4, 1275003, 0, "砂漠を渡る長旅なんて\n正直、一人だと心細かったのよ\nあんたが居てくれてよかったわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127501, 0.0, 5, 0, 4, 0, 6.9, 0, 0, 1, 6, 1275004, 0, "大通りだけあって人が多いわね…\n迷子にならないようにしないと\nたとえば…う、腕を組む、とか？\n…ほら、照れてないで早くする！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127501, 0.0, 2, 0, 5, 0, 9.0, 0, 0, 1, 1, 1275005, 0, "いつもあんたが居てくれるから\nどこにいたって頑張れるの\n改まって言うと、変な感じね？\nでも…今言っておきたかったのよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1275006, 0, "旦那さま、あたしと一曲\n踊っていただけるかしら？\n…なんてね、ふふっ♪\nさぁ、一緒に踊りましょ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1275007, 0, "熱砂の国、灼熱の太陽…\n元気と勢いを存分に乗せた\n情熱的なステップで踊るの！\nそうそう、上手いじゃない♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1275008, 0, "音楽に乗せて体を動かすのって\n最高の気分ね！\nまだまだ体力は余裕かしら？\nなら、あたしについてきてよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1275009, 0, "だめね、あたしったら\nどうにも浮かれちゃってるみたい\nあんたと二人っきりだと思うと…\nどうしても…熱くなっちゃうのよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1275010, 0, "あんたがよそ見できないくらい\n華麗な舞を見せてあげる♪\nあんたを想う気持ちなら\n誰にだって負ける気はないもの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127501, 0.0, 2, 0, 6, 0, 5.6, 0, 0, 2, 1, 1275011, 0, "誕生日おめでとう\nあんたのためにごちそうを作って\n待ってたのよ。ほら早く♪\n熱いうちが美味しいんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1275012, 0, "いつもは子どもたちと一緒だけど\n今日はあたしに最初に祝わせて\n誕生日おめでとう。その…ずっと\nそばに居てくれて、ありがとうね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127601, 0.0, 2, 0, 1, 0, 2.8, 0, 0, 1, 1, 1276001, 0, "郷に入りては郷に従え、\nってことでこっちの衣装に\n着替えてみたよ\n似合ってるかい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127601, 0.0, 1, 0, 2, 0, 4.5, 0, 0, 1, 6, 1276002, 0, "この服、すごく快適だねえ…\n覚えときな。その土地にある\n伝統的なものってのは、そこに\n生きる人々の知恵そのものなのさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127601, 0.0, 1, 0, 3, 0, 6.0, 0, 0, 1, 4, 1276003, 0, "さすがにこの服に刀二本は\n目立ちすぎるからねえ…\n今回は別に用意してみたよ\n試し斬りがてら冒険にでも行こうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127601, 0.0, 1, 0, 4, 0, 7.5, 0, 0, 1, 2, 1276004, 0, "風さえ通れば涼しいもんだね\nなるほど、それでこんなに\n布地が…って、お前さん\n視線がまる分かりだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127601, 0.0, 2, 0, 5, 0, 7.5, 0, 0, 1, 1, 1276005, 0, "ふん、ふん…♪　今のかい？\nサラサリアの踊りなんだけどね\n振りを覚えたらお前さんのために\n踊ってやるから待ってておくれよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1276006, 0, "ふう…いい風が吹くね\nもうちょっとこっちへきなよ\nそっちだと日陰から出ちまうよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1276007, 0, "ん、どうした\n眠くなっちまったのかい？\nなら、アタシの膝枕で\n少し眠るといいさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1276008, 0, "今みたいな何もない時間を\n一緒に過ごして楽しいってのが\n相性ってやつなのかもしれないね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1276009, 0, "やっぱり旅ってのはいいねえ…\nお前さんはどこに行きたい？\nたとえどこだろうと付き合うよ\n二人一緒なら退屈しないだろう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1276010, 0, "こうしてると、まるで世界に\nアタシらしかいないみたいだね…\n大丈夫さ、もしそうなっても\nアタシが養ってやるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127601, 0.0, 2, 0, 6, 0, 5.0, 0, 0, 2, 1, 1276011, 0, "誕生日おめでとう。無事に一年を\n過ごせたかい？　次の一年も\nアタシがついててやるから\n大船に乗ったつもりでいなよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1276012, 0, "今日はお前さんの誕生日\nだったね。おめでとう\n出会った頃に比べたらずいぶん\n頼りになる男になってきたね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127701, 0.0, 3, 0, 1, 0, 5.2, 0, 0, 1, 2, 1277001, 0, "ライラエルさまは優しくて\nいつも民のことを考えてます\nあの方のメイドとして\n私も相応しくなりたいんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127701, 0.0, 3, 0, 2, 0, 7.8, 0, 0, 1, 4, 1277002, 0, "『ジオ・テオゴニア』の人たちは\nいつものんびりしすぎなんです\nそれにちょっといい加減で…\nだから私がしっかりしなきゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127701, 0.0, 3, 0, 3, 0, 4.7, 0, 0, 1, 4, 1277003, 0, "ヴルムってば、また私のことを\nチョロいって…\n私のほうがしっかりしてますし\nそんなことないですよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127701, 0.0, 8, 0, 4, 0, 6.4, 0, 0, 1, 2, 1277004, 0, "騎士さんって器用なんですね\nそれに優しくて、頼りに…\nって私、何言ってるの…！？\n今言ったことは忘れてくださいっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127701, 0.0, 2, 0, 5, 0, 9.5, 0, 0, 1, 6, 1277005, 0, "騎士さんみたいに気が利いて\nなんでもできれば、私ももっと\nみんなの力になれるのかな？\nそしていつか、恩返しも…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1277006, 0, "いいお天気…\n絶好のお洗濯日和ですね\nこれならお城のみんなに\nふかふかのシーツを届けられます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1277007, 0, "このあとはお部屋の掃除に\n料理の配膳をして、それから…\n今日も一日、ばりばりしっかり\n働かなきゃ！　よーし…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1277008, 0, "わぁっ、きれいなシャボン玉…♪\nでもどうしてこんなに…\nって、ヴルム！\nサボってないで干してってば！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1277009, 0, "高いところ、私の代わりに\n干してくれたんですか？\n…ありがとうございます\nおかげでとっても助かりました！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1277010, 0, "やっぱり優しくてすごいなぁ…\nって、そんなんじゃダメ！\nいくら騎士さんが優しいからって\n厚意に甘えないようにしないと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127701, 0.0, 5, 0, 6, 0, 9.3, 0, 0, 2, 2, 1277011, 0, "お誕生日おめでとうございます！\nライラエルさまが、お祝いに一日\nあなたに仕えてはどうかって…\nいかがでしょうか、主さま…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1277012, 0, "騎士さんには私もたくさん助けて\nいただきましたから…その…\n私にできるお祝いなら、\nなんでも言ってくださいね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127801, 0.0, 2, 0, 1, 0, 7.0, 0, 0, 1, 1, 1278001, 0, "騎士さん、こんなとこ\nぶらついてて何か用事でも？\nあたしが案内しましょうか\n…サボりの口実にもなりますし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127801, 0.0, 6, 0, 2, 0, 5.9, 0, 0, 1, 1, 1278002, 0, "この仕事は終わりっと…\n手伝ってもらって助かりました\n何かお礼しないとですね\nお茶でも一杯淹れますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127801, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1278003, 0, "男のひとってドラゴンとか\n秘密兵器とかほんと好きですね\nふ、あたしも嫌いじゃないですよ\nそーゆーの理解あるほうなんで");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127801, 0.0, 2, 0, 4, 0, 7.2, 0, 0, 1, 6, 1278004, 0, "騎士さん、このままこっちに\n居てくれたらいいんですけど\n仕事の手伝いも頼めますし\nそれに…見てて飽きないので");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127801, 0.0, 2, 0, 5, 0, 3.1, 0, 0, 1, 6, 1278005, 0, "もし何もかも嫌になった…\nそんな日には声かけてください\n適当にどっか出かけましょう\nんで、怒られる時は一緒ですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1278006, 0, "こんな天気のいい日は\nゆっくりするに限りますってー\n騎士さんも休憩してきます？\nお菓子ならほらここに");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1278007, 0, "クッキー食べたいんですか？\nま、あたしのサボりに\n目瞑ってもらってますし…どぞ\n今の話、姉貴には内緒でヨロです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1278008, 0, "はーめんど…毎日掃除する\n意味なんてあるんですかね\nキレイに保つなら週一とかで\n十分だと思うんですけど…はぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1278009, 0, "お客さまにはこんな姿\n見せらんないですからね\nふ、騎士さんは特別ですよ\n身内みたいなもんなんで");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1278010, 0, "今日も代わり映えのない\nサボるにはうってつけの日…\nって訳で、この幸せな時間を\n一緒に堪能しましょうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127801, 0.0, 4, 0, 6, 0, 5.5, 0, 0, 2, 6, 1278011, 0, "今日、誕生日なんですね\nおめでとうございます\n…なんですか、あたしだって\nお祝いくらい普通にしますって");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1278012, 0, "誕生日おめです。プレゼント、\nこのクッキーでいいですか？\n…冗談ですよ。用意してます\nいつもお世話になってますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127901, 0.0, 2, 0, 1, 0, 8.4, 0, 0, 1, 1, 1279001, 0, "クリスマス気分を盛り上げる\n挨拶を考えちゃいました！\nでは一緒に大きな声で…せーの！\nメリークリスマオイッスー☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127901, 0.0, 5, 0, 2, 0, 5.0, 0, 0, 1, 1, 1279002, 0, "ホワイトクリスマスでも\nあまり寒さは感じませんね～\nあなたの幸せそうな笑顔を見て\n心がぽかぽかしてますから☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127901, 0.0, 7, 0, 3, 0, 11.7, 0, 0, 1, 2, 1279003, 0, "す～は～、えへへ…☆\nクリスマスはあちこちのお店から\nおいしそうな匂いが漂ってきて…\nお腹が大合唱状態です☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127901, 0.0, 5, 0, 4, 0, 7.6, 0, 0, 1, 6, 1279004, 0, "少し風が出てきましたね…\nよければわたしのマントで\n一緒にくるまります…？\nあ、熱くなりすぎるかもですが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127901, 0.0, 2, 0, 5, 0, 7.3, 0, 0, 1, 5, 1279005, 0, "いつか大切な方と二人っきりで\nクリスマスを過ごしてみたいと\n夢見ていたんですが…あなたの\nおかげで叶っちゃいましたね…☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1279006, 0, "急いでプレゼントを用意したので\nリボンが絡まっちゃいました…\n見た目はちょっと不格好ですけど\nあなたへの気持ちは保証します♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1279007, 0, "箱を開けて、わたしを見た時\nどんな顔で喜んでくれるかな…？\nえへへ、プレゼントの気持ちって\nこんな感じなんですかね…☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1279008, 0, "あ、あはは～…一人で箱から\n出られなくなっちゃいました～…\nあの…抱き上げてくれますか？\nできればお姫さま抱っこで…☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1279009, 0, "プレゼントはわたし…なんて\nかなり大胆なこと言ってますね…\n言っておきますけど、こんなこと\nあなた以外には言えませんよ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1279010, 0, "こんな姿、他の人に見られたら\nびっくりされちゃいますね…♪\n今日だけは、この聖なる夜だけは\nあなただけのわたし…です…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127901, 0.0, 2, 0, 6, 0, 7.5, 0, 0, 2, 1, 1279011, 0, "あなたの誕生日のお祝いに\nクリスマスケーキより大きな\n超特大ケーキを用意しました☆\n量が多いのでわたしも一緒に…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 127931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1279012, 0, "お誕生日おめでとうございます\n喜んでもらえるとうれしいですが\n…受け取ってくれますか？\nプレゼントは…わたしです☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128001, 0.0, 2, 0, 1, 0, 3.7, 0, 0, 1, 7, 1280001, 0, "クリスマスは年に一度の稼ぎ時！\nそう思っていたわたくしですが…\n今年は一味違いますわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128001, 0.0, 1, 0, 2, 0, 6.4, 0, 0, 1, 3, 1280002, 0, "陛下のクリスマスを彩るには\n忠臣であるわたくしも\nふさわしい格好をするのが筋！\nこれは忠誠心の体現なのです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128001, 0.0, 4, 0, 3, 0, 3.9, 0, 0, 1, 6, 1280003, 0, "わたくし、サンタさんには\nお会いしたことがありませんの\nあいにくと「良い子」のままでは\n生活できなかったものですので");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128001, 0.0, 2, 0, 4, 0, 4.7, 0, 0, 1, 6, 1280004, 0, "ちょっと少年？　お仕事ですわよ\nもう少しこちらにいらっしゃいな\nまさか緊張なさっているとでも？\nあらあら、おませさんですわね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128001, 0.0, 2, 0, 5, 0, 5.8, 0, 0, 1, 1, 1280005, 0, "貴方にでしたらエスコートを\n依頼してもよろしくてよ？\n知らない仲ではありませんし…\n報酬も格安で済みますものね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1280006, 0, "わたくし、見た目を隠している\nつもりはございませんの\n隠しているのは…\n個人の資産だけですわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1280007, 0, "こう見えまして、わたくし\nモデルの経験もありますの\n商工会の広告費節約のために\nなかなか様になってますでしょ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1280008, 0, "ひゃあっ！？　む、胸に…胸に！\nこの量ですら麦しゅわが何杯も\nおかわりできますのに…！\nああ…なんてもったいない～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1280009, 0, "ちょっとちょっと秘書１号？\nなぜ少年に撮影を任せようと\nしていますの？　小休憩？\nまったく…少しだけですわよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1280010, 0, "貴方にはジュースがお似合いです\nただ…そうですわね\nいつか大人になったあかつきには\nご一緒してあげてもよろしくてよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128001, 0.0, 2, 0, 6, 0, 5.1, 0, 0, 2, 1, 1280011, 0, "わたくしの記憶によれば\n今日は貴方のお誕生日でしたわね\nおめでとうございます\nケーキの一つでもいかがかしら？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1280012, 0, "お誕生日おめでとうございます\n本日ばかりはケチケチせずに\n貴方の希望を聞いて差し上げます\nさ、言ってごらんなさいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128301, 0.0, 8, 0, 1, 0, 6.9, 0, 0, 1, 2, 1283001, 0, "どうです？　大幹部にふさわしい\nゴージャスな装いってヤツです！\nさあ、思う存分\n褒めるがいいですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128301, 0.0, 2, 0, 2, 0, 8.9, 0, 0, 1, 8, 1283002, 0, "ふっふっふ、新年早々\nあたしの晴れ着姿を見られて\nお兄さんは運がいいですね！\nさあ、お年玉をよこすですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128301, 0.0, 8, 0, 3, 0, 5.4, 0, 0, 1, 2, 1283003, 0, "トーゴクにはドラゴンの年\nってのがあるらしいですね\nまさにあたしのための年\nじゃないですか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128301, 0.0, 3, 0, 4, 0, 2.2, 0, 0, 1, 6, 1283004, 0, "なんですか？　いくらあたしの\n晴れ着姿がキレイだからって\nそんなにジロジロみてたら\n通報もんですからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128301, 0.0, 2, 0, 5, 0, 7.6, 0, 0, 1, 1, 1283005, 0, "はー、どこも人ごみだらけで\nまともに歩けないですね…\nはぐれちゃ大変ですから\n手をつないどいてやるですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1283006, 0, "ふっふっふ、あたしの勝ちです！\nさーて、何を描いてやるですかね\n覚悟するですよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1283007, 0, "あたしの顔に落書きした罰です\nその顔にたーっぷりと\n仕返ししてやるですからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1283008, 0, "やっぱりあたしが最強です！\nこの調子で勝ちまくって\nランドソルをせーふくして\nやるのもいいですね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1283009, 0, "ま～たあたしの勝ちですね！\nお兄さんザコザコすぎですよ\nもしかしてあたしに描いて\nほしくて手を抜いてやがるです？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1283010, 0, "イ、ノ、リ、さ、ま、専、用っと\nふっふっふ、これでお兄さんは\nあたしの所有物ですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128301, 0.0, 8, 0, 6, 0, 3.0, 0, 0, 2, 2, 1283011, 0, "お誕生日おめでとうですよ！\nさあ、このあたしの晴れ姿、\nプレゼント代わりに\n目に焼き付けとくがいいです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1283012, 0, "さあ、バースデープレゼントは\nあたしの直筆サインです！\nこうやってたっぷりの墨で…\nふふっ、遠慮はいらねーですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128401, 0.0, 2, 0, 1, 0, 6.0, 0, 0, 1, 1, 1284001, 0, "じゃじゃーん！\nシオリンに振袖の着付けを\n手伝ってもらったのだ～\nえへへ、かわいいでしょ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128401, 0.0, 6, 0, 2, 0, 5.1, 0, 0, 1, 1, 1284002, 0, "振袖を着ると姿勢が\nシャキッとするんだよね～\nあ、ひょっとして寝落ち対策に\nなったりするのかな…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128401, 0.0, 2, 0, 3, 0, 6.8, 0, 0, 1, 1, 1284003, 0, "仕事納めの解放感…\nそれがお正月の醍醐味だよね～\nだから、今日はキミと一緒に\nゆっくり休むのだ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128401, 0.0, 7, 0, 4, 0, 13.4, 0, 0, 1, 6, 1284004, 0, "え～っと、おみくじの結果は…\n待ち人来る…これって\nもしかしてキミのことだったり？\n…なんだかドキドキしてきたかも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128401, 0.0, 5, 0, 5, 0, 6.4, 0, 0, 1, 2, 1284005, 0, "せっかく振袖を着てるんだし\nキミと二人で初詣に行きたいな～\nまだ草履を履き慣れてないから\n手を繋いでさ…どうかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1284006, 0, "あけましておめでとう！\n新年の始まりに相応しい\nきらーんっ☆　な景色だね～\nきっといい一年になるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1284007, 0, "初日の出には、その年の幸せを\n祈る意味があるんだって\n私はこれからも、キミと一緒に\n冒険できれば、それで幸せだな～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1284008, 0, "空でも冷静でいられるキミとなら\nいいコンビになれるはずだよ～\n私はちょっと慌てることが多いし\n持ちつ持たれつってやつだねっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1284009, 0, "これだけ空高く浮かんでると\n風もちょっと冷たいね…\n新年早々に風邪を引いたら\n大変だしくっつくのはどうかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1284010, 0, "キミと一緒ならハツネちゃんは\n最強だねっ！　この景色も\nキミがいたから見られたんだし\nこれからもずっと隣にいたいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128401, 0.0, 2, 0, 6, 0, 5.5, 0, 0, 2, 1, 1284011, 0, "誕生日おめでとう！\n振袖姿でお祝いなのだ～！\n今日はお正月以上に\n盛り上げていくよ～☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1284012, 0, "誕生日おめでとう！　キミに\n特別な景色をプレゼントなのだ～　\nえへへ、めでたいでしょ？\nこれで今日もいい日になるね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128501, 0.0, 4, 0, 1, 0, 6.3, 0, 0, 1, 6, 1285001, 0, "着物はきれいだけど動きづらいね\nさっき手がかりを見つけて急いで\nかがんだら、帯が食い込んで\n死ぬかと思ったよ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128501, 0.0, 2, 0, 2, 0, 5.2, 0, 0, 1, 6, 1285002, 0, "新年は人でごった返すぶん、\nいろんな事件が起きるね…\nやれやれ。探偵には\nお正月休みなんてなさそうだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128501, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1285003, 0, "コマ遊びに福笑い…\nお正月は興味深い遊びが\nたくさんあるね！　えへへ…\n何から遊ぼうかなあ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128501, 0.0, 5, 0, 4, 0, 9.1, 0, 0, 1, 1, 1285004, 0, "君と一緒に事件を解決するのも\nいいけど、家でのんびり過ごす\nお正月もすっごく幸せ…\n…あ、いや、何でもない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128501, 0.0, 5, 0, 5, 0, 5.8, 0, 0, 1, 4, 1285005, 0, "ずっとひとりだったから、誰かと\n一緒のお正月に慣れてないんだ\n君と一緒に過ごせたら、\n勉強になる…かも？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1285006, 0, "助手くん、助手くん！\nすごいミステリースポットだよ！\n早く上まで行ってみよう？\nよーい、どんっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1285007, 0, "今年もいい一年にしようね！\n騒がしいのは苦手だけど\n君とは賑やかで楽しい\n日々を過ごしたいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1285008, 0, "ひとりで過ごすのは好きだけど\n初詣はひとりだと寂しくてね…\n助手くんがいてくれて\n本当に良かった…えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1285009, 0, "この木の板は『絵馬』だよ\nお願い事を書いて奉納すると\n願いが叶うんだって…\nあっ！？　…み、見えちゃった？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1285010, 0, "噂によるとここは\nえ、縁結びにもご利益がある\nらしいよ？　ま、まあそんなの\n迷信かな！　きっとそうだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128501, 0.0, 5, 0, 6, 0, 4.9, 0, 0, 2, 1, 1285011, 0, "君の誕生日を祝うには\nこの装いがぴったりだね\nほら…だってすごく\nおめでたい日…じゃないか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1285012, 0, "ハッピーニューバースデー♪\nふふっ！　新年もめでたいけど\n今日もおめでたい日だよ\n助手くん…おめでとう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128701, 0.0, 7, 0, 1, 0, 11.4, 0, 0, 1, 1, 1287001, 0, "アストラル…これが新しい力！\n心の奥から燃え上がる火の力なら\n次の冒険もきっと乗り越えられる\nそうだよね、騎士くんっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128701, 0.0, 2, 0, 2, 0, 11.2, 0, 0, 1, 1, 1287002, 0, "燃え広がる炎みたいに、あたしの\nエールをみんなに届けたいんだ！\nこの力なら今まで以上にそれが\nできる気がして…楽しみだなっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128701, 0.0, 6, 0, 3, 0, 9.0, 0, 0, 1, 2, 1287003, 0, "この衣装かわいいだけじゃなくて\n防火性もバツグンなんだよ！\nこれならマグマで泳げちゃうかも\n…ってさすがにそれは無理かな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128701, 0.0, 6, 0, 4, 0, 12.3, 0, 0, 1, 5, 1287004, 0, "ね…ねえ騎士くんっ！\nあの…手、繋いでもいいかな…？\nグローブがないから…その…\n…な、なんでもないっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128701, 0.0, 6, 0, 5, 0, 4.9, 0, 0, 1, 5, 1287005, 0, "騎士くん、今日は二人っきりだね\n…え、顔赤い？　ち、違うよ！？\nこれは炎に照らされてるだけで…\n照れてなんか…いる、けど…っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1287006, 0, "騎士くんのおかげであたし\nまた強くなっちゃった！\nあはははっ！　火よ舞い上がれ！\nこれがアストラルの力だーっ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1287007, 0, "もし心の火が消えちゃいそうなら\nあたしがすぐに駆けつけて、\n笑顔の炎をともしてあげるね！\nだから安心してね、騎士くんっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1287008, 0, "あたしはこの火の力で、みんなを\n暖かく包み込みたいんだっ！\n炎は熱く激しいだけじゃないって\n見せてあげちゃうよーっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1287009, 0, "騎士くんと目が合うと、心も体も\n熱くなって、焦げちゃいそう！\nお、おかしいな、自分の炎なのに\nどうして焦げそうになるんだろ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1287010, 0, "…この合言葉を心で唱えたら、\nあたしの火力に際限はないよっ！\n『騎士くん大好き』！　唱えると\nどんどん強くなれちゃうよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128701, 0.0, 4, 0, 6, 0, 9.5, 0, 0, 2, 2, 1287011, 0, "今日は騎士くんのお誕生日だから\nお祝いにケーキを用意したよ♪\n火をつけるのはあたしにお任せ…\nって、ケーキ焦げちゃったっ！！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1287012, 0, "騎士くん、お誕生日おめでとう！\n舞い上がる炎でお祝いするよ！\nあはは、楽しくなってきた！\n騎士くんも一緒に舞い上がろー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128801, 0.0, 1, 0, 1, 0, 5.9, 0, 0, 1, 6, 1288001, 0, "アストラル…これが\n私に眠っていた新たな力…\nおかげで私はもっと強くなれる\n大切な仲間を守るために！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128801, 0.0, 2, 0, 2, 0, 6.8, 0, 0, 1, 1, 1288002, 0, "キミが共に戦ってくれるなら\n誰を敵に回しても\n負ける気がしないんだ\n私の背中は任せたからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128801, 0.0, 2, 0, 3, 0, 10.0, 0, 0, 1, 6, 1288003, 0, "最近は風の力の制御も\n鍛錬に取り入れてるんだ\nおかげで少し風が読めるように\nなってきて…釣りに使えるかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128801, 0.0, 1, 0, 4, 0, 6.4, 0, 0, 1, 2, 1288004, 0, "アストラルの力に目覚めて\n新しい扉が開いた気がするよ\n私たちの限界はまだまだ\nもっと先にある…そうだよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128801, 0.0, 2, 0, 5, 0, 8.7, 0, 0, 1, 1, 1288005, 0, "何度道に迷ったとしても\nキミがそばに居れば\n嵐の中だって進んでいける…\nその強さをキミがくれたんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1288006, 0, "キミとの心の繋がりが\n新たな風を呼ぶ力となる！\n進むべき道を切り拓くための…\nこれが私の覚悟の姿だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1288007, 0, "体の内から湧き上がる熱い力…\n今の私たちならきっと無敵だ！\nどんな嵐がやって来ようと\n紡いだ私たちの絆は消えない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1288008, 0, "失えないものが私にもできた\n大切な仲間を守るためなら\nどれほど傷つこうと構いはしない\n私はただこの剣を振るうだけだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1288009, 0, "キミとの出会いが\n私の未来を大きく変える\n分岐点だったのかもしれない\n今は…もっとその先を見てみたい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1288010, 0, "願った夢を叶えるには\n叶うまで走り続ければいい\nそばにキミが居てくれるなら…\n私はどこまでだって行ける");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128801, 0.0, 5, 0, 6, 0, 6.9, 0, 0, 2, 2, 1288011, 0, "キミ、誕生日おめでとう\n今日は手料理を作ったんだ\n頑張ってはみたんだけど…うん\n大事なのは見た目より味だからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1288012, 0, "誕生日を迎えたお祝いをする…\n不思議だね、祝っている私まで\nうれしくなってしまうんだ\nキミとだから…なんだろうね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128901, 0.0, 7, 0, 1, 0, 8.6, 0, 0, 1, 1, 1289001, 0, "騎士クン、どうかな？\nこれがわたしたちの…\n新しい絆の形だよ\nえっと…に、似合ってる？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128901, 0.0, 2, 0, 2, 0, 9.4, 0, 0, 1, 7, 1289002, 0, "えっ…髪型、かかかわいい…？\nあ、ありがとうっ！\n実はわたしも気に入ってるの\nよかったぁ…またやろうっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128901, 0.0, 1, 0, 3, 0, 5.7, 0, 0, 1, 6, 1289003, 0, "すごい力を感じるのに…\n不思議と落ち着いてるんだ\nエルフの里で自然や精霊のことを\n勉強してきたおかげかな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128901, 0.0, 7, 0, 4, 0, 0.0, 0, 0, 1, 7, 1289004, 0, "あ、あんまり見ないで…？\nぴったりした服だから、その…\nいろいろ気になっちゃって…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128901, 0.0, 2, 0, 5, 0, 7.1, 0, 0, 1, 1, 1289005, 0, "この姿になって分かったよ\n騎士クンのためにできること…\nまだまだあるんだね\nすっごくうれしい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1289006, 0, "もっともっと強くなりたい…！\n二度と後悔なんてしないように\n助けてほしいと願う人を\nひとりぼっちにさせないように！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1289007, 0, "世界の力とわたしたちの絆の力\n全てを一つの光にして解き放つ！\nそれができるのは…\nわたししかいないんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1289008, 0, "世界に満ちる光の精霊たちよ…！\nわたしに…\nわたしと騎士クンに力を貸して！\n未来を照らすために！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1289009, 0, "光を通じて伝わってくるよ\n騎士クンの優しくて力強い想い\nだから…わたしも応えたいの\nあなたと同じように全身全霊で！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1289010, 0, "騎士クンを好きになる資格なんて\nもうないと思ってた\n…でも、やっぱり諦めたくない\nそれがわたしの…ワガママ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128901, 0.0, 2, 0, 6, 0, 7.6, 0, 0, 2, 6, 1289011, 0, "あの…騎士クン\n何か欲しいものとか…あるかな？\nだって、お誕生日でしょ？\nわたしもできることをしたくって");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 128931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1289012, 0, "お誕生日おめでとう！\nこれからの騎士クンの未来が\n光でいっぱいになるように…\nわたし、頑張るね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129001, 0.0, 5, 0, 1, 0, 4.5, 0, 0, 1, 6, 1290001, 0, "わたしの姿…何か、おかしい？\nあんまり見つめられると…\nうれしいけれど、なんだか少し…\n心許なくて。恥ずかしい…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129001, 0.0, 4, 0, 2, 0, 4.3, 0, 0, 1, 6, 1290002, 0, "表情…柔らかくなった？\n…そう、だね\n強いままであり続けるには…\n諦めたものが多すぎたから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129001, 0.0, 7, 0, 3, 0, 0.0, 0, 0, 1, 7, 1290003, 0, "普段は、力を抑えているの\n戦うべきときのため…\n騎士クンが必要としてくれる\nそのときのために");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129001, 0.0, 2, 0, 4, 0, 7.9, 0, 0, 1, 1, 1290004, 0, "騎士クン…手を貸してもらえる？\n…疲れたわけじゃないよ\nあなたに、触れてみたくて\nわたしの存在を…感じてほしくて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129001, 0.0, 4, 0, 5, 0, 6.3, 0, 0, 1, 2, 1290005, 0, "…あなたがいる\n手を伸ばせば届く距離に\n…想いまでも届けてしまいたいと\nそう望むことは…欲が深い、かな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1290006, 0, "わたしの力の全ては\nミネルヴァあってのもの…\nそう思われるのはひどく心外\nさあ…そこに直りなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1290007, 0, "かつても。今も。これからも\nわたしが騎士クンのために\n力を振るうことに変わりはない\nたとえ…何も得られないとしても");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1290008, 0, "…脅威も敵意も運命すらも\n騎士クンを脅かすことはできない\n忌まわしく疎ましいこの世界に…\nわたしが存在し続ける限り");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1290009, 0, "プリンセスナイト…絆の力\nわたしの体を満たしているのは\n紛れもなく騎士クンと結ばれた証\n…ならば…わたしは、無敵だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1290010, 0, "二人だけの世界は望めない\nそれならせめて…二人だけの時を\n全ての命が消え失せてもなお残る\n騎士クンとわたしが紡ぐ物語を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129001, 0.0, 2, 0, 6, 0, 10.5, 0, 0, 2, 1, 1290011, 0, "騎士クンが\n生まれてきてくれたこと…\nそれがわたしの何よりの幸せなの\nだから…ありがとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1290012, 0, "お誕生日おめでとう、騎士クン\nさあ…願い事をなんでも言って\n必ず、叶えてみせるから\nたとえ…どんなことであっても");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129301, 0.0, 7, 0, 1, 0, 7.6, 0, 0, 1, 8, 1293001, 0, "ここはまるで楽園みたい！\nまさに冬といえばサマーだわ～！\n…よし、ぼくの演技力も\nだいぶ磨きがかかってきたな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129301, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1293002, 0, "水着がシックだからとチエル君に\nこのビーチボールを持たされた\n外しのキュート系アイテムで\nバランスが取れるらしいぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129301, 0.0, 1, 0, 3, 0, 8.6, 0, 0, 1, 6, 1293003, 0, "高機動万能靴『神速くん』で\nプールを渡れるか試したかったが\nメ団に全力で止められたよ\n多少の客など轢いてもよかろうに");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129301, 0.0, 5, 0, 4, 0, 7.0, 0, 0, 1, 6, 1293004, 0, "君の水着はユニークな柄だな\nどれ、もっとよく見せたま…\nむっ、意外と良い身体…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129301, 0.0, 4, 0, 5, 0, 5.7, 0, 0, 1, 6, 1293005, 0, "おい同志、最近テレ女へ来る\n頻度が減っているんじゃないか？\n端的に言ってしまうが…寂しいぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1293006, 0, "ぼかあ筋金入りのインドア派だが\n屋外での開放的な読書というのも\nたまには悪くないものだな\n…ここが屋外か否かはさて置き");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1293007, 0, "彼方の白銀、此方の楽園、\nそしてトロピカル謎ポーション…\n気に入ったぞ、この露天書斎！\n後輩、永住権をもらってくるんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1293008, 0, "ぽーん！\nこちら喋る石のロゼッタです！\nユニ界一の文鎮として、今日も\nあるじの手帳を死守しています！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1293009, 0, "おい同志、足底腱膜を解してくれ\n足の裏は第二の心臓とも呼ばれ…\nふはっ、うひゃひゃ…ちょ待っ…\nんぎゃ！　あだだ…ぐ…ひぎぃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1293010, 0, "どうした同志、なにを呆けている\nぼくの顔に何か付いているかね\nなに？　水着が…似合ってる…？\n左様か…ふむ…ありがとう…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129301, 0.0, 2, 0, 6, 0, 5.8, 0, 0, 2, 6, 1293011, 0, "たしか都では、誕生日に物を\n贈る風習があったな\nこの謎ポーションをやろう\n飲んでみろ、飛ぶぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1293012, 0, "やあ同志、まずは座りたまえよ\n誕生祝いに学びを贈ってやろう\nなに、遠慮することはないさ\n端的に言えば、君と話したいんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129401, 0.0, 5, 0, 1, 0, 8.7, 0, 0, 1, 1, 1294001, 0, "ああ、このグラサン？\nいや屋内つっても天井透明だし、\n陽射し眩しめかなって思って\nあと…まあ…カッコよくない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129401, 0.0, 4, 0, 2, 0, 6.0, 0, 0, 1, 1, 1294002, 0, "アオイにこの水着見せたら、\nキャンプにも行けそうですねって\n言われたんだけど…え、最近の\nキャンプってそんなカンジなの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129401, 0.0, 5, 0, 3, 0, 9.0, 0, 0, 1, 1, 1294003, 0, "この水着うち似合ってンだって\nさっきチエルが普通に言ってきて\n無駄にウザって返しちゃった\nちょっと嬉しかったんだけどね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129401, 0.0, 1, 0, 4, 0, 9.4, 0, 0, 1, 6, 1294004, 0, "あんたの水着、変わってんね\n何その柄、よく見してみ？\nいや近い近い、そんな近付かな…\nふーん、意外といい身体…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129401, 0.0, 4, 0, 5, 0, 9.1, 0, 0, 1, 10, 1294005, 0, "あんたさ、最近テレ女に来る回数\n減ってるくない？　いいけどさ、\n忙しいのは知ってるし…とはいえ\nもうちょっと都合つかんの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1294006, 0, "あーも…チエルはしゃぎ過ぎ\n謎に水ぶっかけられたわ…\nえ、ソレあんたが使ったタオル？\nや、全然…借りるけど…ありがと");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1294007, 0, "ユニ先輩もああいう水着で静かに\n佇んでればちゃんと大人だよね\n端的に言ってセクシーってやつ？\nいや言わないよ、調子乗るもん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1294008, 0, "ちょー、その水筒うちンだよ、\nなに勝手に飲もうとしてんの\nあ、や、あんたが気にしないなら\nべつに…いいけど、それは…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1294009, 0, "あのさ、無意識なんだろうけど、\n人がラッシュガード脱いでるとこ\nまじまじ見んのやめてくんない\nや、なんか…謎にハズいんで…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1294010, 0, "きゃあっ！？　びっくりした…\nなに、いるなら声かけてよ\nほら、あンじゃん、心の準備とか\nあんまヘンな顔見られたくないし…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129401, 0.0, 7, 0, 6, 0, 3.3, 0, 0, 2, 2, 1294011, 0, "おたおめー。おばちゃんパイン\nあげるから。なんでって…いや\nかなりのパインフェチじゃなきゃ\n選ばんでしょ、その水着は");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1294012, 0, "あー、あのさ、暇だしメ団に道具\n借りてケーキ作ったんだけどさ\n今日あんた誕生日なんだって？\nあとでうちの部屋、食べに来なよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129501, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1295001, 0, "うちのガッコ女子校で良かった～\nかわいい水着選ぶのに露出度とか\n無用なノイズ気にしなくて済むし\nテレ女に男子いなくて本当最高！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129501, 0.0, 6, 0, 2, 0, 10.3, 0, 0, 1, 7, 1295002, 0, "ちぇる～ん☆　ちぇる　ちぇぱぱ\nちぇろりぽぴ　ちぇぱるれぱ？\nあ、やっぱり！　さっすが先輩\n分かってる～！　え…分かるの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129501, 0.0, 6, 0, 3, 0, 7.1, 0, 0, 1, 2, 1295003, 0, "というわけで、ちえるんでした！\n最後にチャンネル登録・高評価、\nよろしくお願いしまーす！\n…え？　カメラ回してなかった？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129501, 0.0, 1, 0, 4, 0, 5.9, 0, 0, 1, 11, 1295004, 0, "先輩、大変です！\nメリゾで売ってる飲食物どれを\nとっても美味しそう問題大勃発！\nただちに全額おごってください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129501, 0.0, 3, 0, 5, 0, 8.8, 0, 0, 1, 6, 1295005, 0, "先輩もソレ、かわいい水着ですね\nその柄を違和感なく着こなす男子\n人類になかなか存在しないですよ\n分かり手のチョイスだ…やるな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1295006, 0, "メリゾ最高！　いい波きてます！\n始まりましたよ、チエルの夏が！\n…あれ、冬でしたっけ？\nまあどっちでもいんですけど！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1295007, 0, "きゃっは～！　た～のしぃ～！\n流石ちえるん、世界の美少女！\nどんな波でもかわいく映えて…\nうお…ちょ…待っ…んぎゃあー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1295008, 0, "アーッ！　先輩どいてどいて！\n何そんなとこ突っ立って…\nきゃああぁ！　…痛ったぁ～\nちょっ…どこ触ってるんですか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1295009, 0, "正直『大きい』が『かわいい』を\n阻害しちゃう瞬間あるのがね…\nこんなきゃわ水着にミィコさんの\n第一声「でっっっ！」だもん、草");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1295010, 0, "あれ？　みんないなくなってる…\nえ、どします？　捜し行きます？\nいや、でも動かない方がいいか…\nじゃこのまま二人で遊びましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129501, 0.0, 1, 0, 6, 0, 6.7, 0, 0, 2, 2, 1295011, 0, "お誕生日おめでとうございます！\n誕プレは金のちぇると銀のちぇる\nどっちがいいです？\nま折角なんで両方あげときますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1295012, 0, "ハピバ・すぷらーっしゅ！\n今日は可愛い後輩とメリゾで１日\n遊びたおせる権を差しあげます！\nや～ん！　そんな喜ぶなよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129601, 0.0, 7, 0, 1, 0, 4.1, 0, 0, 1, 6, 1296001, 0, "─私の、話？\n…どうしてそんなもの\n聞くんですか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129601, 0.0, 7, 0, 2, 0, 0.0, 0, 0, 1, 7, 1296002, 0, "いつでも、\n辺りを警戒してます\nじゃないと、身、守れません…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129601, 0.0, 4, 0, 3, 0, 5.4, 0, 0, 1, 7, 1296003, 0, "アルラウネは\nめずらしい種族、です\n─たくさん、狩られましたから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129601, 0.0, 4, 0, 4, 0, 10.6, 0, 0, 1, 6, 1296004, 0, "媛も、ネアさんも…貴方も\nすごく、構ってきますよね\nちょっと─困ります");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129601, 0.0, 7, 0, 5, 0, 9.6, 0, 0, 1, 4, 1296005, 0, "あまり近くをうろうろ\nしないでください─\n反射的に攻撃してしまうので…\n忠告─です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1296006, 0, "自分の身は、自分で守ります\nそのための毒も棘もある\nアルラウネ─ですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1296007, 0, "花は好きです\n騙そうとしたり、襲ってきたり\nしないから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1296008, 0, "…………え？\nなんで見てるのか…？\n動くもの─観察する癖が\nあるだけです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1296009, 0, "媛は「貴方と関わって\n世界を知れ」と言います\nよく、わかりませんが…\n知ることばかりでは、あります");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1296010, 0, "私の力のこと、内緒ですよ\nもし誰かに言ったなら…\n貴方を倒さなくちゃ\nならない─から");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129601, 0.0, 6, 0, 6, 0, 5.7, 0, 0, 2, 1, 1296011, 0, "誕生日おめでとうございます\n…何ですか？　驚いた顔して…\nお祝いくらいは言いますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1296012, 0, "貴方の誕生日を祝おうって─\n媛が宴を用意してます\n私も案内係くらいは…はい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129701, 0.0, 1, 0, 1, 0, 3.0, 0, 0, 1, 2, 1297001, 0, "ジオ・ゲヘナは如何かえ？\n騎士殿には恩義多かれば\n懸念がございますれば\nどうぞおっしゃってくだしゃんせ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129701, 0.0, 1, 0, 2, 0, 8.0, 0, 0, 1, 2, 1297002, 0, "媛といえど、外へ出れば異邦者\n旅情を感じるものにありんす\n…玉座を狙う不逞の輩も\n今はおりませぬし喃");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129701, 0.0, 4, 0, 3, 0, 9.9, 0, 0, 1, 1, 1297003, 0, "モネは無礼をしておりませぬか？\n騎士殿が相手であれば\nあれもきっと懐きましょうぞ\n…恐らくは、でありんすが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129701, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1297004, 0, "お前さまとの日々は、さぞ愉快な\n事ばかりでございましょうな\n行く先々で人助けをする姿…\n目に浮かびまする");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129701, 0.0, 2, 0, 5, 0, 8.1, 0, 0, 1, 1, 1297005, 0, "ジオ・ゲヘナの媛を務めまするは\n幾つもの奇縁と目的あってのこと\n幼気な稚児らを護るも目的の一つであり申す");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1297006, 0, "さぁさぁ皆の者、\n百鬼夜行と参ろうぞ\nこの地は煉獄、『ジオ・ゲヘナ』\n強き者のみが生き残る穢土なれば");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1297007, 0, "この賑わい、活力溢れたる様は\nランドソルにも引けは取りませぬ\nただ些とばかり、暑苦しくは\nあるやもしれず…フフ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1297008, 0, "鉄は硬きもの、されど溶けるもの\n千変万化、生々流転…\nなれど蛇の化生を好みたるは\nこの『装備』が故でありんしょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1297009, 0, "恐れることはありんせん\nお前さまはこの地を救いし英雄\nわっちらが牙を剝く道理など\n何処にもありませぬ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1297010, 0, "見やるのみでは興も醒めよう\nなれば騎士殿、望みの席を\n御用意差し上げ申す\nわっちの隣など如何かえ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129701, 0.0, 2, 0, 6, 0, 6.9, 0, 0, 2, 1, 1297011, 0, "今日は騎士殿が生まれた日\n宴の席を設けさせてありんす\nわっちらには恩義があります故\nこれくらいはさせておくんなんし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1297012, 0, "お前さまの心意気に応える\n贈り物ができれば良かれど…\n頭を撫でてほしい？　…フフ\nほんに無欲でありんす喃");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129801, 0.0, 2, 0, 1, 0, 4.6, 0, 0, 1, 1, 1298001, 0, "なんだ、もしかしておまえも\n訓練に参加したいのか？\nはっ、いい度胸じゃねえか！\n望み通りしごいてやるよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129801, 0.0, 1, 0, 2, 0, 4.6, 0, 0, 1, 4, 1298002, 0, "厳しいだけじゃ新入りたちの\nモチベーションが続かねえ\nクッキーでも差し入れするか\nついでにおまえにも一つやるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129801, 0.0, 4, 0, 3, 0, 4.1, 0, 0, 1, 6, 1298003, 0, "新入りに魔法を教えてほしいって\n言われたが…そっちはからきしだ\n仕方ねえ…魔法学園の教科書で\n一から勉強してみるとするか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129801, 0.0, 2, 0, 4, 0, 7.4, 0, 0, 1, 1, 1298004, 0, "おまえはあたしの最初の教え子だ\nつまり、ある意味では\n新入りたちの先輩ってわけだ\nシャンとしろよ、先輩♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129801, 0.0, 5, 0, 5, 0, 8.2, 0, 0, 1, 1, 1298005, 0, "教官を続けてると\n早起きが習慣になっちまってな\n…あたしに朝起こしてほしい？\nバ、バカ！　自分で起きやがれ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1298006, 0, "おう、来たか…ゆっくりしてけよ\n今日も訓練は無事終わった\n今はオフでな。大したもてなしは\nできねえが歓迎するぜ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1298007, 0, "おまえにも一杯淹れてやるよ♪\n好きな砂糖とミルクの量は\n覚えてっからちょっと待ってろ…\nああ、ついでにケーキはどうだ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1298008, 0, "ずいぶん本が多いね、って？\n鍛え方だとかの勉強してんだよ\nあたしらしくねえが…\n教官として責任があるからな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1298009, 0, "教官不足が深刻でな…あ～疲れた\n…少し仮眠した方がいいって？\n…そうするか。おまえにだけは\n寝顔を見せられるからな…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1298010, 0, "なかなかいい部屋だろ？\n…ってよく考えりゃ、男を\n入れるのはおまえが初めて…！？\nそ、掃除するから一回外出ろ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129801, 0.0, 2, 0, 6, 0, 6.9, 0, 0, 2, 1, 1298011, 0, "誕生日おめでとさん！\n特別におまえ専用の\n訓練プログラムを用意してやった\nたっぷり楽しんでくれていいぜ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1298012, 0, "訓練でずいぶん靴がボロボロだな\n誕生日だし新しいの買ってやるよ\n遠慮なんてすんなよ？\nこれは教官命令だ、なんてな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129901, 0.0, 7, 0, 1, 0, 8.4, 0, 0, 1, 1, 1299001, 0, "「壊し屋」が教え導くなんて\n冗談みたいですが、やるからには\n本気でやりますわ。たとえそれで\n壊れたとしても。クスクス…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129901, 0.0, 2, 0, 2, 0, 7.6, 0, 0, 1, 1, 1299002, 0, "戦闘訓練から試験勉強\nお菓子作りまで、なんでも教えて\n差し上げますわ。手取り足取り\nじっくりと…クスクス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129901, 0.0, 6, 0, 3, 0, 9.4, 0, 0, 1, 1, 1299003, 0, "訓練に疲れて指一本動かなくても\n大丈夫です。私が調合した\n特製ドリンクで疲れなど…あら？\n遠慮などしなくていいのに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129901, 0.0, 5, 0, 4, 0, 6.7, 0, 0, 1, 2, 1299004, 0, "あなた様のために特別な訓練を\n考えましたわ。早速参りましょう\n誰も追ってこれない遠くの地で\n二人だけの秘密のレッスンを…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129901, 0.0, 8, 0, 5, 0, 7.0, 0, 0, 1, 3, 1299005, 0, "人を鍛えるばかりではなく\n私自身も強くあらねばいけません\nあなた様に近づく全ての邪魔者を\n完全に叩き潰すためにも…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1299006, 0, "選びなさい。一生地を這い\n生きていくのか、それとも\nかすかな光を求めて私の下で\n地獄の日々を送るのかを");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1299007, 0, "面を上げなさい\nおや、口をきいていいと\n言った覚えはありませんが？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1299008, 0, "クスクス、なかなかいい顔ですわ\nでは、ご褒美に追加の訓練を\nいたしましょう。きっとさらに\nいい顔になりますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1299009, 0, "私の下で鍛えられた身と心は\n全てあのおかたのために\n使いなさい。それこそが力ある者\nの使命なのですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1299010, 0, "見ていてください、あなた様\nいずれあなた様の下に屈強な者\nたちが集う日のため、私も精進\nしてまいりますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129901, 0.0, 5, 0, 6, 0, 3.5, 0, 0, 2, 2, 1299011, 0, "お誕生日おめでとうございます\nというわけで本日は特別講習\n「エリコの全て」をしっかりと\nお教えいたしますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 129931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1299012, 0, "あなた様の誕生日、それすなわち\n奇跡が顕現した日ということ\nなのでこの日を記念日にすべく\n色々手を打っていますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130001, 0.0, 2, 0, 1, 0, 6.2, 0, 0, 1, 1, 1300001, 0, "妹たちの婿にゃまだ頼りないねぇ\nアタシがビシバシしごいてやるよ\nなーに、遠慮はいらねぇさ\nボーヤはアタシの義弟だからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130001, 0.0, 6, 0, 2, 0, 2.8, 0, 0, 1, 1, 1300002, 0, "飯食いに行くよ。ついてきな！\nアアン？　今手持ちがないだぁ？\nンなもん奢りに決まってんだろ\n宵越しの金は持たねぇ主義でね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130001, 0.0, 7, 0, 3, 0, 7.9, 0, 0, 1, 2, 1300003, 0, "どっちの婿になるか決めたかい？\n姉のアタシが言うのもなんだが、\n一世一代の大博打だねぇ♪\nさあ…腹括りな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130001, 0.0, 3, 0, 4, 0, 5.3, 0, 0, 1, 1, 1300004, 0, "甘っちょろい男は好きじゃないが\nボーヤのことは嫌いじゃないねぇ\n…アン？　勘違いするなよ？\nもちろん妹の相手としてって話だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130001, 0.0, 7, 0, 5, 0, 5.5, 0, 0, 1, 2, 1300005, 0, "ボーヤの心を奪うのはちょいと\n骨が折れるだろうねぇ…フフッ\n惚れた腫れたに興味はねぇが\n盗賊としては血が騒いじまうよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1300006, 0, "バンディシャーク号に\n進めない道なんてないのさ\nどんな悪路も乗り越える快進撃を\n特等席で見せてやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1300007, 0, "嵐の前の静けさってやつだね\nこういう雰囲気は嫌いじゃない\n…さて、ここから見えるモンを\n根こそぎ奪ってやろうじゃないか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1300008, 0, "ボーヤにゃ特別に\nここからの景色を見せてやる\n妹たちにも内緒だよ？\nアタシのお気に入りさ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1300009, 0, "なんだい…こうやって隣に立つと\nボーヤ、存外背が高いじゃないか\n…あまり成長されると、妹に\nやるのがもったいなく感じるねぇ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1300010, 0, "なあボーヤ。世話になった礼に\nアンタが出した賽の目の数だけ\nいいコいいコをしてやるよ\nさあ、一世一代の運試しだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130001, 0.0, 1, 0, 6, 0, 4.4, 0, 0, 2, 2, 1300011, 0, "今日はボーヤの誕生日だって？\n贈り物は何がいいんだい。\n希少な宝石でも高価な調度品でも\nなんでも盗ってきてやるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1300012, 0, "義弟の年に一度の晴れ舞台！\nバンディシャーク号で\nド派手にバースデーパレードと\n洒落こもうじゃないか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130101, 0.0, 5, 0, 1, 0, 3.6, 0, 0, 1, 6, 1301001, 0, "騎士さんはいつも\n助けてくれますよね…\nど、どうしてなんでしょう\nこんなだめだめな、わたしを…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130101, 0.0, 1, 0, 2, 0, 5.3, 0, 0, 1, 2, 1301002, 0, "誰かの助けになれるって\n素敵なこと、ですよね\n…姉さんたちといる時は、\nあんまりできないんですけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130101, 0.0, 2, 0, 3, 0, 2.5, 0, 0, 1, 6, 1301003, 0, "服がほつれちゃってますよ\nちょっと貸してください\nすぐ直しちゃいますから…\nこういうの慣れてるんです♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130101, 0.0, 4, 0, 4, 0, 5.0, 0, 0, 1, 2, 1301004, 0, "お食事がまだでしたら\nよければご一緒にいかがですか？\nあ…えと、もしお嫌なら、\n全然断っていただいても…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130101, 0.0, 4, 0, 5, 0, 0.0, 0, 0, 1, 4, 1301005, 0, "いつも家族がご迷惑を\nおかけしてるので…\n何か、お詫びができたらと\n思うんですけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1301006, 0, "今日のカレーは自信作なんです\nたくさん作ったので\nお腹いっぱい食べてください\nお肉、多めにしておきますね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1301007, 0, "こうやって…誰かと一緒に\n料理するのっていいですね\n二人とも手伝ってくれないから\n実は…ちょっと憧れてたんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1301008, 0, "姉さんとフブキちゃんって\n料理の好みがまるで違うから…\n献立作りはよく悩んじゃいます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1301009, 0, "ハ、ハートのニンジンは…\n深い意味は、ないですよ？\nお腹に入れば、一緒ですしっ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1301010, 0, "騎士さんが見ててくれるだけで…\nちょっとだけ自信が持てるんです\nだから…叶うならですけど…\nまた、食べにきてもらえたら…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130101, 0.0, 2, 0, 6, 0, 1.8, 0, 0, 2, 6, 1301011, 0, "お、お誕生日っ、\nおめでとうございます…！\n大したものじゃないですけど…\nわたしからのプレゼント…です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1301012, 0, "今日は奮発して、ちょっといい\nお肉を使っちゃいました♪\n騎士さんのお誕生日ですから\nお口に合えばいいですけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130201, 0.0, 1, 0, 1, 0, 6.3, 0, 0, 1, 2, 1302001, 0, "ザコお兄さ～ん♡\n…いひひっ、呼んでみただーけ♡\nザコって呼ばれてフツーに\n振り向くザコお兄さん面白～い♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130201, 0.0, 2, 0, 2, 0, 3.4, 0, 0, 1, 6, 1302002, 0, "次はナニ作ろっかな～♪\n…あっ、ザコお兄さんをつよつよ\nにするメカとかってどーお？\n火力装甲盛り盛りメカお兄さん♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130201, 0.0, 5, 0, 3, 0, 8.2, 0, 0, 1, 1, 1302003, 0, "なんでもハッキリしてたほうが\nわかりやすくていいよねー♡\nお兄さんもわかりやすくて好き♡\nザコってハッキリしてるから～♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130201, 0.0, 5, 0, 4, 0, 8.1, 0, 0, 1, 4, 1302004, 0, "勉強は好きだけどガッコはだる～\nザコ教師に教わることねーし？\nそんなのより、お兄さんを\nいぢってるほーが楽しーもん♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130201, 0.0, 2, 0, 5, 0, 6.8, 0, 0, 1, 4, 1302005, 0, "設計図書くのって楽しいけど\n頭使うから疲れるんだよね～\nとーぶんほきゅーきぼー♡\n甘いのはやくもってきてー♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1302006, 0, "きゃはははっ♡\nザコお兄さんみ～っけ♡\n今日はどうやって\nお兄さんで遊ぼうかな～♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1302007, 0, "どおどお？\nこの『フライングウィーゼル』\nちょーカッコイイでしょ～♡\n夜は目が光るし、自爆装置も…♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1302008, 0, "油断してるとバズーカで…\nどっかーん♡\n…いひひっ、うっそー♡\nびびった？　びびったぁ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1302009, 0, "お兄さんのコト、大好きだよぉ？\nからかうのが、だけどねー♡\n楽しーし気分いーし、\nアイデアが湧いてくるんだもん♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1302010, 0, "ザコお兄さんも乗ってみる？\n狭いけど、ぼくがザコお兄さんの\nヒザに座ればいけるっしょ♡\nぼくの専用シートにしてあげる♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130201, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1302011, 0, "はっぴーばーすでー！\nいひひっ、びっくりした？\nザコお兄さんの誕生日覚えてて\nえらいでしょ♪　ほめていーよ♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1302012, 0, "お誕生日おめでと～！\n誕プレはぁ～…ザコお兄さんを\nもっとザコザコにするメカ♡\nぽちっと～♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130701, 0.0, 7, 0, 1, 0, 5.3, 0, 0, 1, 2, 1307001, 0, "のんびりとお花を観賞する\nこれって大人の趣味ですよね\nそれでは大人な私があなたに\nお花の楽しみ方を教えましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130701, 0.0, 2, 0, 2, 0, 1.8, 0, 0, 1, 1, 1307002, 0, "このお花は…なるほど！\n気になったお花をすぐに図鑑で\n調べる。これがお勉強という\nものですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130701, 0.0, 2, 0, 3, 0, 6.4, 0, 0, 1, 4, 1307003, 0, "この衣装、かわいいですが\n子どもっぽくないですか？\nアイドルみたい？　それは\nちょっと褒めすぎですよ…えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130701, 0.0, 5, 0, 4, 0, 7.9, 0, 0, 1, 1, 1307004, 0, "え、私からいい匂いがする？\nさっきまでお花の世話をして\nいたからでしょうか…って\nもう！　かがないでください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130701, 0.0, 5, 0, 5, 0, 7.9, 0, 0, 1, 2, 1307005, 0, "今日は山でお花を探します\nなのであなたが迷子にならない\nように、手を繋いであげます\nこ、これは特別なんですからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1307006, 0, "うららかな日和にきれいなお花畑\n心までポカポカしちゃいます\nいつまでもこんな幸せな時間が\n続くといいですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1307007, 0, "この花冠、うまくできてると\n思いませんか？　えへへ\nあなたに一番最初に見せたくて\nがんばりました♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1307008, 0, "きれいなお花を見ていると\n気持ちが安らい、で、すぅ…はっ\nね、寝てませんよ！　そこまで\n気が緩んでませんから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1307009, 0, "花冠、上手に作れてますね\nどうせだれか女の人にあげ…え？\nわ、私にですか？\nあ、ありがとうございます…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1307010, 0, "このお花畑は私のとっておきの\n場所なんです。私とあなたしか\n知らない秘密の場所。絶対に\nぜーったいに内緒ですからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130701, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1307011, 0, "お誕生日おめでとうございます\nこれ、プレゼントのお花です\nお誕生日に合わせたお花を\nちゃんと用意したんですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1307012, 0, "お誕生日のケーキ、作って\nみたんです。食べられるお花を\nいっぱい散らしてみました\nどうぞめしあがれ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130801, 0.0, 2, 0, 1, 0, 4.4, 0, 0, 1, 1, 1308001, 0, "メイド服でもお仕事着でもない\n服を着るのって、なんだか\n少し、恥ずかしいですね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130801, 0.0, 1, 0, 2, 0, 5.4, 0, 0, 1, 2, 1308002, 0, "えへへ、どうですか？\n花衣装を仕立て直したんです\nたくさん思い出の詰まった服♪\n大切にしますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130801, 0.0, 2, 0, 3, 0, 4.9, 0, 0, 1, 1, 1308003, 0, "救護院にもみんなで植えた\nお花がたくさんあるんですよ\n今度見に来てくださいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130801, 0.0, 5, 0, 4, 0, 6.7, 0, 0, 1, 2, 1308004, 0, "この服装、よく似合ってるって\nお嬢さまに褒めていただきました\n…あの、あなたはメイド服と\nどちらがお好きでしょうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130801, 0.0, 5, 0, 5, 0, 6.0, 0, 0, 1, 1, 1308005, 0, "あなたの側にいると、花たちも\nいつもより元気な気がします♪\n…もしかして、私の心が\n伝わっちゃってるのかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1308006, 0, "奇麗ですね…\n草花と、土と、暖かな風…\n春の匂いでいっぱいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1308007, 0, "ほら！　見てください！\nとっても奇麗に咲いてます！\nお嬢さまや、救護院のみんなにも\n見せてあげたいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1308008, 0, "花祭りやコンテストだけでなく\nあなたと過ごした日々…\nこの服には思い出が\nいっぱいなんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1308009, 0, "ええと…そんなにじっと\n見つめられると、照れちゃいます\nだからその…このお花を\n見つめてくれませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1308010, 0, "奇麗？　はい、私も奇麗な\nお花畑だと思います！　…て\nお、お花じゃなくて私ですか？\nじょ、冗談はよしてくださいぃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130801, 0.0, 1, 0, 6, 0, 7.7, 0, 0, 2, 2, 1308011, 0, "お誕生日おめでとうございます！\n今から、お花に囲まれてのお茶会\nなどいかがでしょう？　精一杯\nお祝いさせていただきます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1308012, 0, "今日は、特別な日…\nあなたのお誕生日ですから\n日頃の感謝と私の気持ちを込めた\n特別な花でお祝いさせてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130901, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1309001, 0, "ヒーローさんのおかげでついに\nグランドリームパークがオープン\nできましたっ♪　感謝の気持ちに\n全財産の宝石をどうぞどうぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130901, 0.0, 2, 0, 2, 0, 4.9, 0, 0, 1, 8, 1309002, 0, "あなたと一緒なら、辛い時でも\n自然と笑顔になれちゃいます\nヒーローさんのお隣は\nわたしの夢の国、ですねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130901, 0.0, 7, 0, 3, 0, 6.8, 0, 0, 1, 8, 1309003, 0, "ここを世界一のパークにしたい…\n世界で一番笑顔の溢れるパークに\n…なんて調子に乗っちゃいました\nすみませんですます～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130901, 0.0, 1, 0, 4, 0, 6.6, 0, 0, 1, 8, 1309004, 0, "アトラクションの待ち時間も\nヒーローさんと並んでると\nあっという間に感じちゃいます\nこれってなんででしょうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130901, 0.0, 4, 0, 5, 0, 0.0, 0, 0, 1, 4, 1309005, 0, "お、おかしいですます…\nゆったりしたアトラクションでも\nヒーローさんと一緒に乗ると\nドキドキしちゃって…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1309006, 0, "見てください♪　見渡す限りの\nお客さんの笑顔ですます♪\nわたしの夢、ヒーローさんの\nおかげで叶っちゃいました～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1309007, 0, "ヒーローさん、冒険の合間にまた\nこのパークに寄ってくださいね…\nヒーローさんを笑顔にするため\nわたしはここでお待ちしてます♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1309008, 0, "ヒーローさんが宝石を受け取って\nくれないので考えました…あの\nわたしのこと貰ってくださいっ！\nはわっ、別に変な意味ではなく！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1309009, 0, "かばばん君かわいいですよね♪\nえ…クルルちゃんもかわいい…？\nほわぁぁっ！？　やめてください\nドキドキで倒れちゃいます～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1309010, 0, "ヒーローさん聞こえますかー！？\n…歓声で聞こえてないですよね…\nではでは…ヒーローさんっ！！\nわたしヒーローさんのことが─");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130901, 0.0, 8, 0, 6, 0, 2.6, 0, 0, 2, 2, 1309011, 0, "お誕生日おめでとうございます！\n今日という偉大な日を記念して\n宝石で作ったヒーローさん像を\nエントランスに飾りますですっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 130931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1309012, 0, "今日はヒーローさん誕生日記念の\n特別なパレードを開催しますっ♪\n楽しくて、夢と笑顔でいっぱいの\nすてきな一年にしましょうねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131001, 0.0, 2, 0, 1, 0, 7.6, 0, 0, 1, 1, 1310001, 0, "うちが遊園地にかかわるなんて\n不思議な縁もあるもんやなぁ\n任されたからには、うちも精一杯\nお仕事してみせますえ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131001, 0.0, 2, 0, 2, 0, 5.8, 0, 0, 1, 1, 1310002, 0, "夢もワクワクもいっぱいで\nみんながキラキラしとる\nうち、ドリームパークも\nサーカスも好きやわぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131001, 0.0, 7, 0, 3, 0, 9.5, 0, 0, 1, 4, 1310003, 0, "この衣装、最初は恥ずかしいし\nうちに似合うかな思うてたけど…\n着てるうちに勇気が湧いてきたわ\n自信もって堂々とします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131001, 0.0, 1, 0, 4, 0, 2.8, 0, 0, 1, 2, 1310004, 0, "王子はんも楽しんでってや\nみんなの楽しそうな姿こそが\nうちにとっては一番うれしくて\n幸せなことなんよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131001, 0.0, 2, 0, 5, 0, 11.2, 0, 0, 1, 5, 1310005, 0, "ショーを見る王子はん、\n目がキラキラしてて素敵やなぁ\n…うちも同じ顔してる？\nほんならお揃いやね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1310006, 0, "ようこそ、ドリームパークへ！\n…なんやくすぐったいわぁ\nでも恥ずかしがってたらあかんね\nうちがみんなを笑顔にするんや");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1310007, 0, "マホマホサーカスは\nかわいいがいっぱいなんよ\nこの子らが上手くできたら\nいっぱい拍手して褒めたってな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1310008, 0, "ブランコの上って高いわぁ…\nでも怖くはあらへんよ\n一人じゃあらしまへんし\n王子はんも見てくれるさかい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1310009, 0, "演目はまだまだ盛りだくさんや\n王子はんの楽しむ姿を\nうちにもいっぱい見せて\nおくれやす♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1310010, 0, "王子はん、うちの演技する姿\n最後までじっくり見ていってな\nそのために今日までたくさん\n練習してきたんどす");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131001, 0.0, 1, 0, 6, 0, 5.1, 0, 0, 2, 2, 1310011, 0, "王子はん、誕生日おめでとさん♪\nうちからはお祝いとして\n特別なショーをプレゼントや\n楽しんでいっておくれやす♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1310012, 0, "今日は王子はんの誕生日やさかい\nうちがエスコートしますえ\n夢とメルヘンがいっぱいの時間を\n一緒にすごしまひょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131101, 0.0, 5, 0, 1, 0, 4.7, 0, 0, 1, 1, 1311001, 0, "おっすー、騎士きゅん\n楽しんでるー？\nせっかくだし、二人で\n忘れられない夏にしよっか♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131101, 0.0, 2, 0, 2, 0, 6.5, 0, 0, 1, 1, 1311002, 0, "水着って何のために\nあると思うー？\n泳ぐため？　違いまーす\nぬるぬるするタメだよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131101, 0.0, 2, 0, 3, 0, 5.2, 0, 0, 1, 5, 1311003, 0, "日焼け止めより\nイイもの塗ってあげるねー\nべたっとタイプとぬるっとタイプ\nどっちのスライムが好きー？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131101, 0.0, 2, 0, 4, 0, 5.9, 0, 0, 1, 1, 1311004, 0, "クソ暑くても、好きぴといたら\n全然ムテキだわ～\nきゃわゆさは暑さも\n吹きとばすんだね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131101, 0.0, 5, 0, 5, 0, 9.2, 0, 0, 1, 2, 1311005, 0, "騎士きゅんの笑顔って\n真夏の太陽なみに\nまぶしいし、マジ栄養だわー\nもっとおねえちゃんに見せて～♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1311006, 0, "騎士きゅん、こっちこっち～♪\n浮き輪もジュースもお城もあるし\nまだまだたっくさん遊ぼーねー\nとりま、ぬるぬるしてく～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1311007, 0, "なんか後ろの方で\nカリザきゅん騒いでんねー\n気になるし、後で\nランド探索しよっか～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1311008, 0, "どーする、もっかい海入る？\nスライムの浮き輪あるし\n騎士きゅんが気になるトコ\n連れてってあげるよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1311009, 0, "騎士きゅん、ちゃんと\n水分ホキューしてるー？\nネアおねえちゃんのジュース\nちゅっちゅしていいんだよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1311010, 0, "夕方のビーチで隣に好きぴって\nめっちゃエモなシチュじゃ～ん♪\nせっかくだし、もっとくっついて\nチルしよーよ？　ね～♡");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131101, 0.0, 1, 0, 6, 0, 9.6, 0, 0, 2, 2, 1311011, 0, "騎士きゅん、おたおめー！\n今日は、ネアおねえちゃんが\nアゲてくんでよろー\nとりま、プチョヘンザしとくー？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1311012, 0, "カリザきゅんランド招待券を\n騎士きゅんにプレゼントー♪\nもしかすっとバースデーパレード\nあるかもよー？　楽しみだね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131201, 0.0, 1, 0, 1, 0, 6.8, 0, 0, 1, 2, 1312001, 0, "水鉄砲大会しませんか？\n濡れたら溶ける水着で\nバトルロワイヤル★\nきっと楽しいですよー？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131201, 0.0, 2, 0, 2, 0, 10.8, 0, 0, 1, 1, 1312002, 0, "水泳に、砂のお城、ビーチバレー\n海の家でお昼食べたり、\n夜の浜辺で花火も素敵ですよねー\nやりたいことがいっぱいです★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131201, 0.0, 5, 0, 3, 0, 7.1, 0, 0, 1, 2, 1312003, 0, "そんなに見つめちゃダメですよ★\n…なーんて、冗談ですよー\n騎士さんになら見せてあげます\nわたしの、どんなところでも…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131201, 0.0, 5, 0, 4, 0, 3.0, 0, 0, 1, 2, 1312004, 0, "人気者な騎士さんを独占★\n誰かに見つかって咎められないか\nハラハラドキドキして…\nイケナイことしてる気分です…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131201, 0.0, 2, 0, 5, 0, 5.7, 0, 0, 1, 1, 1312005, 0, "忘れられない夏の思い出、\nたっくさん作りましょうね♪\n騎士さんのこと、もーっと好きに\nなりたいので…くすっ★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1312006, 0, "寒かったら言ってくださいね\nわたしのぺんぽこりんケープ\n貸してあげます★\nこれもイイコトですよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1312007, 0, "キレイですねー、海\nぷかぷか浮いて、海の果てまで\n流されてみたいかも…ふふっ★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1312008, 0, "ばしゃばしゃばしゃーっ\nふふ、冷たくって気持ちいいー♪\nたまに子供っぽいことするのも\nちょっとアオハルですよね★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1312009, 0, "はい、あーん♪\n海で食べさせあいっこって、\nちょっとコイビトっぽいですね？\n「キュン★」ってしちゃいそー");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1312010, 0, "このままテレポーテーションで\n無人島に駆け落ちしちゃいますか\n…ふふ、冗談です、じょーだん★\n言ってみたかっただけですよー");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131201, 0.0, 1, 0, 6, 0, 10.9, 0, 0, 2, 2, 1312011, 0, "騎士さん、ハッピーバースデー♪\nお祝いに花火とかどうでしょう？\n打ち上げて、どっかーん！　って\nえっ、危ない？　…はーい★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1312012, 0, "お誕生日おめでとうございます♪\n素敵な誕生日だったなって\n思ってもらえるように、たくさん\nイイコトしてあげますね★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131601, 0.0, 5, 0, 1, 0, 0.0, 0, 0, 1, 5, 1316001, 0, "このように肌を見せるのは\nやはりどうしても抵抗が…\nですがこれも、修行の一環と\n思えば…うぅん…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131601, 0.0, 2, 0, 2, 0, 7.1, 0, 0, 1, 6, 1316002, 0, "恥ずかしながら…\nわたくしは海に関しましても\n知らないことばかりなのです\n兄様、どうかひと夏のご教示を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131601, 0.0, 1, 0, 3, 0, 8.0, 0, 0, 1, 6, 1316003, 0, "不思議と着心地がしっくり\nくるのです…もしかすると\n自分で選んだものよりも\nさすがの慧眼ですね、ミソラ様…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131601, 0.0, 7, 0, 4, 0, 5.4, 0, 0, 1, 5, 1316004, 0, "あまり見つめられますと、その\n気恥ずかしくて…ですが\nうれしくも感じてしまいます\nこれはなぜ、なのでしょう…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131601, 0.0, 5, 0, 5, 0, 6.7, 0, 0, 1, 2, 1316005, 0, "未熟なわたくしとは違い\n兄様の水着姿はとても精悍です\n…はっ…！　し、失礼しました\nまじまじと見つめるなど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1316006, 0, "ああっ、すっかり砂まみれに…！\nこのようなあられもない姿を\n兄様にお見せするだなんて…\n砂に埋もれて隠れたいです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1316007, 0, "右往左往…翻弄されております\nスイカ割りという夏の遊びに…！\n本当にお子様からお年寄りまで\n嗜まれているのですか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1316008, 0, "どうか今一度、挑戦の機会を！\nなんとしても兄様に…！\nわたくし自ら割ったスイカを\n召し上がっていただきたいのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1316009, 0, "砂でしたら自分ではらえます\n兄様のお手を煩わせるわけには…\nダ、ダメです、兄様そんなところ\n足の裏はお許しください…っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1316010, 0, "スイカを狙う腕が震えていた…\nなんて、兄様はわたくしの迷いも\nすべてお見通しですね。…もしや\nわたくしの胸に秘めた想いも…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131601, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1316011, 0, "本日は兄様のお誕生日…！\n日頃の感謝を込めて精一杯\nおもてなしをいたします\nなんなりと仰ってくださいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1316012, 0, "兄様のお誕生日という晴れの日…\nわたくしの成長した姿をご覧\nいただけるよう研鑽いたしました\nその、喜んでくださいますか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131701, 0.0, 2, 0, 1, 0, 4.9, 0, 0, 1, 4, 1317001, 0, "あたしが水着を着る羽目に\nなるとは思わなかったわ…\nこうなったら遠慮なく\n海を楽しませてもらうわよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131701, 0.0, 6, 0, 2, 0, 10.1, 0, 0, 1, 1, 1317002, 0, "この背中の羽、目立ってるわよね\nどうしてかしら、水着着てると\n常にこうなっちゃうのよね\n…気合が入ってるから…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131701, 0.0, 1, 0, 3, 0, 5.6, 0, 0, 1, 3, 1317003, 0, "ちょっと待ちなさい\n海に入る前に、準備運動はした？\nあたしが近くにいるからには\nしっかりあんたを監督するからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131701, 0.0, 6, 0, 4, 0, 8.0, 0, 0, 1, 2, 1317004, 0, "こうして二人でいると\n昔を思い出してあんたの肩に\n乗りたくなるのよね…\n…きゃっ、肩車じゃないっての！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131701, 0.0, 2, 0, 5, 0, 3.6, 0, 0, 1, 1, 1317005, 0, "あたしが楽しんでるかって？\nあんたを見てるだけで十分よ\n気なんて遣わなくていいから\n楽しんでる顔をもっと見せなさい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1317006, 0, "はい、お疲れさま\nこれは夢かって？　違うわよ♪\nあたしとあんたの二人だけの時間\n楽しみましょ、最高の夏を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1317007, 0, "そういえば二人きりで\n遊ぶなんて、久しぶりよね\n…覚えてなくてもいいわ\nあたしがちゃんと、覚えてるから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1317008, 0, "綺麗ね…あんたもそう思わない？\n…あたしばかり見てどうしたのよ\nああ、あたしに見とれちゃった？\nちょっと、なんとか言いなさいよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1317009, 0, "貴重なひと夏の海\nあたしと一緒でよかったわけ？\n…ふぅん。そ\nなら、忘れられない夏にしましょ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1317010, 0, "ねぇ…お願いがあるんだけど…\n今日だけでいいから\n…フィオって呼んでくれない？\n………………うん、ありがと♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131701, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1317011, 0, "はい、おめでと\n誕生日を海でお祝いなんて\nなんだか贅沢ね？\n遠慮なんてしなくていいわよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1317012, 0, "あんたの誕生日ね、おめでとう\nあたし自身にとっても特別な日よ\nこれからも一緒に\n歩んでいけるといいわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131801, 0.0, 2, 0, 1, 0, 7.2, 0, 0, 1, 6, 1318001, 0, "ふぅ、こうも暑いとかき氷なんか\n食べたくなっちゃうね～\n…え～、買ってきてくれるんだ？\n気が利くね～☆　ありがと♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131801, 0.0, 2, 0, 2, 0, 8.4, 0, 0, 1, 1, 1318002, 0, "せっかくのバカンスだし\nたまには羽を伸ばさないとね～\n文字通りの意味かって？　ふふ♪\nそうだね、翼も広げちゃおう☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131801, 0.0, 6, 0, 3, 0, 0.0, 0, 0, 1, 6, 1318003, 0, "気にせず日焼けできるのって\n若者の特権って感じで\nちょっとうらやましいな～\n私も思い切って焼いてみよっか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131801, 0.0, 2, 0, 4, 0, 7.9, 0, 0, 1, 5, 1318004, 0, "オ・ト・ナの夏休み…\n君も一緒に過ごしちゃう？\nふふ…じゃ、何からしよっか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131801, 0.0, 1, 0, 5, 0, 3.4, 0, 0, 1, 4, 1318005, 0, "くらくらしちゃうくらい夏だね～\n暑さのせいってことで\nパーっとなにもかも全部\n吹っ飛ばしちゃおっか…なんてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1318006, 0, "夏の日差しって強烈だけど\nそれがたまらなく好きなんだ～☆\n白い砂浜も青い海も…\n全身で夏を感じられてさ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1318007, 0, "日焼け止めって、こまめに\n塗り直さなきゃいけないのが\n面倒だよね～…\n君、手伝ってくれないかな～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1318008, 0, "そんな日の当たるとこだと\nのぼせちゃうよ？\nちょっと日陰で涼もっか♪\nほら、遠慮しないでおいで～☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1318009, 0, "この空と海の青さは印刷じゃ\n出せないね～…しょうがないから\n記憶にしっかり刻んでおこっか\n今日の思い出と一緒に♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1318010, 0, "お姉さんとひと夏の思い出、\n作っちゃおっか♪\nふふ、どんな想像したのかな\nちょっと聞かせてよ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131801, 0.0, 2, 0, 6, 0, 5.6, 0, 0, 2, 1, 1318011, 0, "誕生日おめでと～☆\nお祝いにBBQでもしよっか♪\nカヤちゃんとイノリちゃんも\n呼んでパーッとやっちゃおう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1318012, 0, "ハッピーバースデー☆\nお祝いに海の見えるレストランを\n予約しておいたんだ～\n今夜、連れてってあげるね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131901, 0.0, 2, 0, 1, 0, 4.0, 0, 0, 1, 1, 1319001, 0, "みんなで遊びに行くのって、\nすごーく贅沢ね！\n『さまぁばけぇしょん』を\nもっともっと楽しまなくちゃ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131901, 0.0, 2, 0, 2, 0, 4.4, 0, 0, 1, 6, 1319002, 0, "この格好じゃないと参加\nできない行事があるのかしら？\n一回きりかもしれないから\nみんなで行ってみましょう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131901, 0.0, 2, 0, 3, 0, 4.7, 0, 0, 1, 1, 1319003, 0, "ランドソルの空気にも\nだいぶ馴染めてきてるわ\nみんなが優しくして\nくれるおかげね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131901, 0.0, 2, 0, 4, 0, 7.3, 0, 0, 1, 1, 1319004, 0, "夏の過ごし方、いろんな遊び、\nたくさんのことを教えてくれて…\nこの世界のみんなには、\nいくら感謝しても足りないわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131901, 0.0, 2, 0, 5, 0, 9.4, 0, 0, 1, 1, 1319005, 0, "私、来たのがこの世界でよかった\nかけがえのないお友だち、\n素敵な思い出、他にもたくさん\n宝物ができたんだもの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1319006, 0, "森の香りと息づかいを感じる…\nなんだか、私の体の中を\n風が通り抜けていくみたいで\nすごーく安心するわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1319007, 0, "きっとこの景色は、ずーっと\n今日みたいにきれいなままね\n何十年、ううん、何百年経っても\n同じように、色あせないで…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1319008, 0, "もしかして、精霊たちが\n挨拶してくれているのかしら？\n少しだけ、みんなの場所に\nお邪魔させてもらうわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1319009, 0, "いろんな景色を見せてもらって、\n私の世界が広がっていくみたい\n次は何が起きるんだろうって\n今からすごーく楽しみね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1319010, 0, "このきれいな景色のことや、\nみんなで一緒に過ごした\nかけがえのない時間も…\nちゃんと、覚えておいてね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131901, 0.0, 2, 0, 6, 0, 2.2, 0, 0, 2, 1, 1319011, 0, "お誕生日おめでとう！\n今日はみんなで一緒に楽しく\n笑いながら過ごしましょう\nしてほしいことは何でも言ってね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 131931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1319012, 0, "たまたま出会えた私たちが、\nこうして誕生日のお祝いまで…\nびっくり仰天しちゃうわね！\nこれからもよろしくお願いします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132001, 0.0, 2, 0, 1, 0, 6.1, 0, 0, 1, 1, 1320001, 0, "ボクの名前はエキドナ\n『強欲の魔女』と、\nそう名乗った方がいいかな？\nああ、ここでは誰も知らないか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132001, 0.0, 4, 0, 2, 0, 3.6, 0, 0, 1, 1, 1320002, 0, "やれやれ\n周囲の視線を感じるね\nボクが可憐な乙女だからとはいえ\nじろじろ見るのはいただけないな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132001, 0.0, 1, 0, 3, 0, 3.3, 0, 0, 1, 2, 1320003, 0, "未知の世界とはなんて心躍るんだ\n君に案内を頼んでもいいかな\nボクの好奇心を満たすものを\n見せてくれると嬉しいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132001, 0.0, 4, 0, 4, 0, 10.1, 0, 0, 1, 6, 1320004, 0, "海辺での遊戯を教えてほしい\n目隠しをして視覚を遮断し、\nこの棒で丸い果物をたたき割る？\n…もっと軽い棒はないのかい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132001, 0.0, 2, 0, 5, 0, 8.6, 0, 0, 1, 6, 1320005, 0, "どうしたんだい？　ボクになにか\n聞きたいことでもあるのかな\n…この衣装がよく似合ってる？\n君は正直者だね。好ましく思うよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1320006, 0, "ようこそ、ボクの茶会へ\nまずは歓迎の意を表して\nとびきり美味しいお茶を淹れよう\n溢さずきちんと飲み干すんだよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1320007, 0, "これが海…眺めているだけなんて\nもったいないね\n海の底まで観察しに行くとしよう\n君も一緒に行くかい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1320008, 0, "この衣装の感想を聞きたいな\nこう見えてボクも乙女だからね\n褒め言葉をもらえるかどうかで\n一喜一憂したりもするんだよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1320009, 0, "異なる世界で\nくつろぐのも悪くはないね\n君もボクのいた世界に来るといい\nそんな未来も実に興味深そうだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1320010, 0, "君はとんでもないお人よしだね\n魔女を前にして、大したものだよ\n改めて、君なら受けてくれるかな\n──ボクとの、契約を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132001, 0.0, 1, 0, 6, 0, 6.5, 0, 0, 2, 2, 1320011, 0, "今日は君の誕生日じゃないかな？\nもちろん知ってるさ。視たからね\nボクからは喝采と言葉を贈るよ\n誕生日おめでとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1320012, 0, "めでたい日にボクからの贈り物だ\nこのお茶を飲むといい\nなに、悪い物は入っていないよ\n悪い物は……ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132101, 0.0, 2, 0, 1, 0, 4.4, 0, 0, 1, 1, 1321001, 0, "メイドの給仕というものは\n時と場所を選びません\nたとえ陽の下、海の中…です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132101, 0.0, 1, 0, 2, 0, 1.7, 0, 0, 1, 6, 1321002, 0, "夏は暑いですけど\nレムは嫌いではありません\nスバルくんの名前にも\n夏が入っていますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132101, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1321003, 0, "せっかくの機会ですから\n鉄球も夏の装いにしてみました\n威力はそのままですが\n清涼感が上がっていますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132101, 0.0, 5, 0, 4, 0, 6.6, 0, 0, 1, 4, 1321004, 0, "涼しげな装いの姉様は素敵ですが\nジロジロ見るのは失礼ですよ\nレムのことも見てた、ですか…？\nからかってはいけませんよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132101, 0.0, 2, 0, 5, 0, 6.7, 0, 0, 1, 1, 1321005, 0, "海に入る前の準備運動は\nしっかりとされましたか？\nよければお手伝いしますよ\n大丈夫です、加減はしますので");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1321006, 0, "見てください、姉様！\n空も海もこんなに青くて綺麗で…\n…レムの髪の毛みたい、ですか？\n姉様…ありがとうございます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1321007, 0, "風が強くなってきましたね…\n少し心配ですが、こうして姉様と\n手を繋いでいると、それだけで\nレムは安心できるんです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1321008, 0, "これだけの海を凍らせれば\nかき氷がたくさん作れそうですね\n塩辛いのが難点ですが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1321009, 0, "あっ！　スバルくんたちが\n手を振ってますよ…あれ？\nなんだか高度がだんだんと低く…\nほっ…持ち直しました…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1321010, 0, "この景色と穏やかな時間が\nいつまでも続くように…\nレムも誠心誠意\n努めたいと思います");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132101, 0.0, 1, 0, 6, 0, 2.5, 0, 0, 2, 2, 1321011, 0, "お誕生日おめでとうございます\n日頃の感謝を込めて\nレムが腕によりをかけた\nおいしい海鮮料理をご用意します");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1321012, 0, "親しい人のお祝いができる喜びは\n住んでいた世界が違っていても\n変わりありませんね\nお誕生日おめでとうございます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132201, 0.0, 2, 0, 1, 0, 2.9, 0, 0, 1, 7, 1322001, 0, "新米錬金術師ノゾミ！\n…なんてね。アイドルと違って\n分からないことばかりだけど\n新鮮でワクワクしちゃうね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132201, 0.0, 2, 0, 2, 0, 4.2, 0, 0, 1, 1, 1322002, 0, "この衣装、着てるだけで\n成功率が上がりそうでしょ？\nうん、気合入ってきた！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132201, 0.0, 6, 0, 3, 0, 10.2, 0, 0, 1, 1, 1322003, 0, "錬金術師ってちょっと魔法少女\nみたいじゃない？　呪文もあるし\n錬れば錬るほど、ヒッヒ…\n…うーん、ちょっと違うかも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132201, 0.0, 1, 0, 4, 0, 6.0, 0, 0, 1, 2, 1322004, 0, "コーヒーとお茶菓子のいい匂い\nがキミから……クンクン♪\n仕事が終わったら一緒に\nカフェに行こう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132201, 0.0, 7, 0, 5, 0, 6.2, 0, 0, 1, 1, 1322005, 0, "まだアイドルの時みたいな\n自信はないけど、キミの力に\nなれるようにがんばるよ！\n目指せ、凄腕錬金術師！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1322006, 0, "アイドルでも錬金術師でも\n私の望みは変わらない\nキミを笑顔にしたいんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1322007, 0, "研究と研鑽が必要なのは\nアイドルと一緒だね\n錬金術、奥が深いなぁ\nハマっちゃいそうだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1322008, 0, "お客さんの注文の品、一つ一つ\n心を込めて作ってるんだ。キミの\n注文も受けてみたいな\n何か欲しいものはある？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1322009, 0, "新作の香水を作ってるんだけど\nキミ、隣で見ててくれない？\n傑作が生まれる予感がする…って\nちょ、それは近すぎだよ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1322010, 0, "つ、ついに完成しちゃった…\n彼の匂いの香水…さっそく\n嗅いでみ…ひゃあ！　キ、キミ\nいつからそこに！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132201, 0.0, 5, 0, 6, 0, 5.3, 0, 0, 2, 2, 1322011, 0, "お誕生日おめでとう！　これ、\nお手製のポーション詰め合わせ\nいつも元気でいてほしいからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1322012, 0, "お誕生日のお祝いに、香水を\n作ってみたんだけど…え？\nいい匂いって、私が？　キミの\n方が断然いい匂いだよっ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132301, 0.0, 10, 0, 1, 0, 0.0, 0, 0, 1, 10, 1323001, 0, "私の錬金術、試してみる？\n大丈夫、私は絶対失敗しないわ！\n…ただまあ、成功しすぎて\n違う効果が出ることはあるけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132301, 0.0, 2, 0, 2, 0, 5.6, 0, 0, 1, 9, 1323002, 0, "もっと一緒にいたいから、あなた\nそっくりの生き物を錬成したり…\nなんてっ、冗談よ。だって本物が\n目の前にいるんだもの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132301, 0.0, 10, 0, 3, 0, 3.9, 0, 0, 1, 1, 1323003, 0, "イロハちゃんとソノさんを\n元通りにする秘薬…\n私がきっと完成させてみせるから\nもう少しだけ待っててね…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132301, 0.0, 13, 0, 4, 0, 9.8, 0, 0, 1, 2, 1323004, 0, "あ、今日も会いに来てくれたっ♪\nこれだけ毎日一緒にいるのって\nもう夫婦ってことでいいわよね…\nきゃー、なんちゃってーっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132301, 0.0, 2, 0, 5, 0, 5.2, 0, 0, 1, 4, 1323005, 0, "ダーリンは魅力的だから、\n恋のライバルもたくさんね…\nいつか錬金術みたいに\nこの恋も大成功させてみせるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1323006, 0, "…今回の錬金術、いつもより少し\n成功しすぎたみたいね…！\nって、ゴーレムの私とあなた！\nもうちょっと離れなさーいっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1323007, 0, "私とあなた…俯瞰で見ても\n結構お似合いじゃないかしら…？\n二人で街を歩けばきっとカップル\nだって思われちゃうわよね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1323008, 0, "運命の王子さまに出会ってから\n錬金術の腕が上がってるのよね…\nこれが愛のパワーってやつかな？\n…私、まだまだ成長できそう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1323009, 0, "錬金術は自分のために使わない…\nこれは自分のためじゃないわ…！\nみんなを笑顔にするための\n錬金術の修行よ！　ほ、本当に！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1323010, 0, "ふふ…頭の天辺から爪先まで\n完璧にあの人そのものだわ…！\nま、これくらいの再現は楽勝よ\n毎晩夢であなたと逢ってるから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132301, 0.0, 2, 0, 6, 0, 8.3, 0, 0, 2, 9, 1323011, 0, "禁忌の錬金術で私そっくりの\n錬成生物を作ったらかわいがって\nくれるかしら？　…じゃなくて！\nお誕生日おめでとう、ダーリン♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1323012, 0, "誕生日おめでとう…頬を染め呟く\n私を、あなたは優しく抱き寄せ…\nよし、お祝いシミュレーションは\n完璧だわ…ふふふ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132401, 0.0, 10, 0, 1, 0, 2.9, 0, 0, 1, 1, 1324001, 0, "子どもは風の子、元気の子、\n外で遊ぶのが一番、でしょ？\nだから、お勉強とかお手伝いより\n好きに遊ばせるのがいいと思う！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132401, 0.0, 2, 0, 2, 0, 4.5, 0, 0, 1, 1, 1324002, 0, "イロハが肩もんであげると\nソノちゃんすっごい喜ぶの\n途中で面白い声上げるから\nイロハも肩もみ楽しいんだ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132401, 0.0, 4, 0, 3, 0, 3.0, 0, 0, 1, 2, 1324003, 0, "今日は何して遊ぼうかな～\nルイちゃんのコレクションが\nまた増えてないかチェック…は、\n昨日もうやったしなぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132401, 0.0, 8, 0, 4, 0, 6.5, 0, 0, 1, 7, 1324004, 0, "おじちゃんも一緒に稽古する？\nじゃあ、どっちが早く世界一に\nなれるか勝負しよっか！\nえへへ、燃えてきた～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132401, 0.0, 2, 0, 5, 0, 4.7, 0, 0, 1, 1, 1324005, 0, "今日はイロハがおじちゃんと\n遊んであげるよ。何しよっか？\n虫捕り？　釣り？　それとも\n稽古？　どれもオススメだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1324006, 0, "森はイロハのフィールドだよ！\nおっきな岩とか高い木とか\nぜーんぶ使って飛び回るの、\nアスレチックみたいだよねっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1324007, 0, "虫捕りはただの遊びじゃないよ\n虫の動き、考え、呼吸…\n全部を読んで網を振る真剣勝負\n…とそれっぽいこと言ってみた♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1324008, 0, "相手がどんなに強くても、\nイロハは全力で戦うだけだよ\nそれがイスルギ流の名を\n背負うってことだから…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1324009, 0, "ソノちゃんの体、虫捕りだと\nおっきいお胸が邪魔なんだよね…\nおじちゃんが支えてくれたら\n楽になるかな？　え、だめ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1324010, 0, "イロハは今が育ち盛り！\nよそ見してたらあっという間に\n成長しちゃうかもだよ\nだからずーっと見ててね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132401, 0.0, 6, 0, 6, 0, 5.9, 0, 0, 2, 2, 1324011, 0, "おじちゃんの誕生日のお祝いに\nお小遣いでケーキ買ったよ！\n遠慮なく食べてね！　あ、でも\nローソクはイロハが消したーい♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1324012, 0, "えへへ、誕生日おめでとう！\nプレゼントにクワガタあげるね！\nミヤマ、ヒラタ、どれにする？\n一押しはアルケスツヤクワガタ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132501, 0.0, 2, 0, 1, 0, 14.7, 0, 0, 1, 4, 1325001, 0, "今日もお疲れ～…って、こら\n頭を撫でようとしてくんなって…\nつい？　や、私の方が年上だから\n26歳の大人の女なんでよろしく");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132501, 0.0, 4, 0, 2, 0, 10.4, 0, 0, 1, 6, 1325002, 0, "いやー、すごいよこの身体\n朝めっちゃ早く目がさめるし\n寝起きも全然つらくないからね\nその分夜はすぐ眠くなるけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132501, 0.0, 2, 0, 3, 0, 5.1, 0, 0, 1, 1, 1325003, 0, "お店やってると手が欲しい時が\nあるんだよね～、ルイちゃんも\n君なら雇っていいって言ってるし\n正ギルドメンバーとして、どう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132501, 0.0, 7, 0, 4, 0, 11.8, 0, 0, 1, 2, 1325004, 0, "ハイ、クイズです\n私はイロハ？　それともソノ？\n正解者にはエナドリを～\nて、今のでバレバレかあ、残念");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132501, 0.0, 2, 0, 5, 0, 7.0, 0, 0, 1, 6, 1325005, 0, "例えば大人の私をデートに\n連れてくならどこ？\n…ほう、家でまったり\n君、分かってるね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1325006, 0, "お、ちゃんと働いてるみたいだね\n感心感心\nでもあんま根詰めないように\n省エネが一番だよ～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1325007, 0, "ん、もうこんな時間か\nちょっち休憩しよっかな\n君も付き合ってよ\nコーヒー淹れたげるからさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1325008, 0, "さて、と…そろそろ定時だし\n私は上がっちゃうけど\nこのあとなんか食べにいく？\nお姉さんが奢ったげよっか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1325009, 0, "ん～、なに？　どしたん？\nもしかして見惚れてた？\nこのエロガキ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1325010, 0, "ふふっ…いや、なんかいつもより\n作業が捗るなーって思ってさ\n君が近くにいてくれるから…\nかもね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132501, 0.0, 2, 0, 6, 0, 3.4, 0, 0, 2, 1, 1325011, 0, "君、今日誕生日でしょ？\nよーし、私が奢るからさ\nミルクで乾杯しよ～よ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1325012, 0, "おー、君今日が誕生日なんだ\nそっか、これで年の差１つ\n縮まったのかぁ…あ、いや\nええと、おめでと");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132801, 0.0, 2, 0, 1, 0, 9.2, 0, 0, 1, 1, 1328001, 0, "クリスマスは素敵な行事ですね\nジオ・テオゴニアに持ち帰れば\n民たちがさぞ喜ぶことでしょう\n甘いケーキなどは特に…ふふっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132801, 0.0, 2, 0, 2, 0, 5.6, 0, 0, 1, 1, 1328002, 0, "ランドソルの冬は寒いですが\n色々なものが煌いていて…\nはあぁ…ほら、見てください\n白い吐息が綿菓子のよう…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132801, 0.0, 5, 0, 3, 0, 7.4, 0, 0, 1, 2, 1328003, 0, "時には黒い装いも良いですね\n普段よりも悪戯心が湧くようです\n少しだけ気恥ずかしいですが…\n騎士…その、似合っていますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132801, 0.0, 5, 0, 4, 0, 7.5, 0, 0, 1, 6, 1328004, 0, "悪魔というのは大変ですね\n人をドキドキさせることを\n生業とするだなんて…\n騎士も…ドキドキしたいですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132801, 0.0, 6, 0, 5, 0, 9.3, 0, 0, 1, 4, 1328005, 0, "雪は冷たい…のですよね？\n一度足をつけてみたいのですが\nなかなか勇気が出なくて…\n騎士、支えていてくれますか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1328006, 0, "あなたと二人、特別な一夜を\n過ごせることをうれしく思います\nクリスマスの楽しみ方…\nたくさん教えてくださいね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1328007, 0, "夜も更けてきましたね…\n聖夜の星に祈りましょう\nあなたの道行きに、これからも\n祝福があらんことを…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1328008, 0, "ブドウ朱はおいひいのですね…\nろれつ？　らいじょうぶです…\nほら、いつおろーりれひょう…？\n騎士…きいていまふか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1328009, 0, "少し体が、火照ってきました…\nブドウ朱のせいでしょうか\nそれともあなたが…\n側にいるから？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1328010, 0, "いつもと違う衣装を着るのは\n少し恥ずかしいですね\n天使のわたしと、悪魔のわたし…\nその…あなたは、どちらが…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132801, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1328011, 0, "クリスマスの悪魔として\nあなたの生誕をお祝いします\nとびっきり素敵な夢を\n見せて差し上げたいですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1328012, 0, "今日はあなたが生まれた特別な日\n甘いケーキとキャンドルで\n奇跡の一夜を共に過ごしましょう\nあなたに祝福がありますように…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132901, 0.0, 2, 0, 1, 0, 8.4, 0, 0, 1, 1, 1329001, 0, "クリスマスまでの夜、\n人々の夢に現れる一つの影！\nその正体は…クリスマスの悪魔に\nなった、イオちゃんよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132901, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1329002, 0, "この衣装、意外と寒くないのよ\nきっとクリスマスの悪魔の力が\n守ってくれてるおかげね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132901, 0.0, 2, 0, 3, 0, 4.3, 0, 0, 1, 6, 1329003, 0, "少し前はよく王宮騎士団の人に\n呼び止められてたけど、\n最近は会釈してくれるの\nみんな礼儀正しいのね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132901, 0.0, 1, 0, 4, 0, 9.9, 0, 0, 1, 6, 1329004, 0, "私ってなぜか人から力を\nもらうことが多いのよね～\n闇の力とか、悪魔の力とか、\nそれから…君の強化とか♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132901, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1329005, 0, "いいことしたら褒めてあげる♪\nいい子いい子、よく頑張りました\nいつも君のこと、見守ってるね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1329006, 0, "眠れない夜は私を呼んで♪\nクリスマスの悪魔として、\n君にとびきりいい夢を\n見せてあ・げ・る♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1329007, 0, "雲の上で寝るのって\n子どもの頃からの憧れだったの♪\n夢の中で夢が叶っちゃった、ふふ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1329008, 0, "この夢の果てには\n何があるんでしょうね？\nふふ、確かめに行くときは\n君と一緒に行きたいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1329009, 0, "君がそばにいてくれるだけで\n私の心はいつもぽかぽか♪\n君もそうだったらうれしいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1329010, 0, "本当にクリスマスプレゼントが\n私だったら、君はどうする？\n夢の中なら君のために\nどんなことだってしてあげるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132901, 0.0, 2, 0, 6, 0, 5.0, 0, 0, 2, 1, 1329011, 0, "お誕生日おめでとう！\nお祝いのパーティーは盛大にね♪\nケーキはもちろん、\nショートケーキにしましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 132931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1329012, 0, "ハッピーバースデー！\n誕生日プレゼントとして、\n私がいい夢を見せてあげる♪\nきっと寝覚めもばっちりよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133001, 0.0, 1, 0, 1, 0, 6.1, 0, 0, 1, 6, 1330001, 0, "暇なときは…絵を描いたりとか？\nと言っても、この世界は\nカラフルな絵の具がないので\nもっぱら水墨画ですが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133001, 0.0, 1, 0, 2, 0, 13.8, 0, 0, 1, 7, 1330002, 0, "このサークレット何に見えます？\n実はこれ、一流の幽霊のみが\n装着を許された、伝説の三角の布\nリスペクトなんです…冗談ですが");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133001, 0.0, 2, 0, 3, 0, 7.8, 0, 0, 1, 1, 1330003, 0, "また温泉でも一緒に行きますか\n今度は男湯には侵入しませんよ\nはじめから混浴でどうです？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133001, 0.0, 7, 0, 4, 0, 6.4, 0, 0, 1, 1, 1330004, 0, "騎士氏といるとなんだか\n落ち着いてしまいますね\nこのまま健やかに\n成仏してしまいそうです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133001, 0.0, 6, 0, 5, 0, 7.5, 0, 0, 1, 2, 1330005, 0, "もしいつか騎士氏が死んだら\nレイスの嗜みを教えますよ\nまずは、そうですね\n二人で空中散歩とかどうですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1330006, 0, "深海のように静寂に包まれた\nこの世界も悪くないでしょう？\n息遣いさえ聞こえる静寂…\n私の方は息してませんけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1330007, 0, "この槍はヴァイオレット姫を\n守るためにありますが…\n貴方のこともそれなりに\n守るのでご心配なく");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1330008, 0, "貴方にはレイスのプライドを\nぼっきぼきに折られましたよ…\n恨み晴らさでおくべきか…いつか\n騎士氏を絶叫させますので");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1330009, 0, "どれだけ動いても心拍数は一定…\nというか動いてないのですが\n貴方といると、なぜか心拍数が…\nいえ、何でもありません冗談です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1330010, 0, "フフ、貴方と共に戦えること\nうれしく思いますよ、騎士氏\n無事に勝てたらハグでもしますか\nその時は、透けたりしませんから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133001, 0.0, 7, 0, 6, 0, 0.0, 0, 0, 2, 7, 1330011, 0, "ハッピーバースデー騎士氏\n幽霊やゾンビ、吸血鬼も呼んで\nじめじめした真っ暗闇の世界で\n盛大にお祝いいたししましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1330012, 0, "誕生祝いのケーキを用意しました\nフフ、下からロウソクの灯りに\n照らされた私の顔、怖いですよね\n…え、かわいい？　…そうですか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133101, 0.0, 2, 0, 1, 0, 5.3, 0, 0, 1, 1, 1331001, 0, "ジオ・ニヴルヘルは他の世界より\n荒涼としておりますでしょう\nそれでも、わたくしにとっては\n愛しく、守るべき世界なのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133101, 0.0, 3, 0, 2, 0, 6.0, 0, 0, 1, 1, 1331002, 0, "臆病で不甲斐ないわたくしを\n支えてくださる方々がいる\n皆様の期待と献身に、わたくしは\n勇気をもって報いなくては…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133101, 0.0, 2, 0, 3, 0, 9.2, 0, 0, 1, 1, 1331003, 0, "胸元にハートをかたどる服装は\n騎士様の世界でも\nよく見られるもの…なのですわね\nわたくし、安心いたしました");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133101, 0.0, 5, 0, 4, 0, 8.4, 0, 0, 1, 2, 1331004, 0, "姫としてではなく、\nゾンビとしてでもなく、\nただわたくしを見てくれる\nそんな殿方は…騎士様だけですわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133101, 0.0, 4, 0, 5, 0, 0.0, 0, 0, 1, 4, 1331005, 0, "強く在らねばならないと\n常日頃から心がけておりますが…\n挫けそうな時は、またわたくしに\n勇気をくださいますか？　騎士様");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1331006, 0, "今回の茶葉はわたくしの\nお気に入りを選びました\nジオ・ニヴルヘルでは貴重な\nやさしい味のする一杯ですわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1331007, 0, "騎士様の冒険譚は\nどれも胸が躍るようです\nどうか可能な限り聞かせて下さい\n屍人は時間だけはありますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1331008, 0, "紅茶を一緒に楽しむたびに\n少しずつ心の距離が縮まっていく\nわたくしはそう信じておりますわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1331009, 0, "わたくしに憑いている\n動物霊たちも、騎士様のことが\n気に入ったようですね…♪\nこれも人徳のなせる業でしょうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1331010, 0, "いつもはよくグレイスやルルィと\nお茶を楽しんでいたのです\n…同年代の男性をお招きするのは\nこれが初めてですけれど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133101, 0.0, 1, 0, 6, 0, 8.5, 0, 0, 2, 2, 1331011, 0, "お誕生日おめでとうございます\nささやかですが、祝宴の席を\n設けさせていただきました\nぜひ、一緒に祝わせてください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1331012, 0, "今日は騎士様が生まれた日…\n新たな生命が芽吹いた、\n特別に尊い日に思えますね\nどうかその未来に、祝福を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133201, 0.0, 2, 0, 1, 0, 8.0, 0, 0, 1, 1, 1332001, 0, "今年の抱負？　愚問だな\n私の心は常に「軍人たれ」だ\nまあ、軍人でも正月くらいは\nのんびりするけどな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133201, 0.0, 1, 0, 2, 0, 4.1, 0, 0, 1, 6, 1332002, 0, "着物というのも存外悪くないな\nニノンの気持ちが少しは分かるぞ\nどうだ、このままオーエドにでも\n繰り出そうか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133201, 0.0, 1, 0, 3, 0, 4.8, 0, 0, 1, 2, 1332003, 0, "貴公、初詣に行ったらお守りも\n買うのだぞ。ご利益のほどは\n分からないが、一つ持つだけで\n随分と心強いものだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133201, 0.0, 5, 0, 4, 0, 12.0, 0, 0, 1, 6, 1332004, 0, "こう寒いと温泉にでも行きたく\nなるな。なに、いい宿を知って\nいるだと？　そ、それは私と\n二人で…いや、何でもない…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133201, 0.0, 5, 0, 5, 0, 10.4, 0, 0, 1, 1, 1332005, 0, "正月を楽しく過ごすには沢山の\n準備が必要だが、一番大事なのは\nその正月を誰と過ごすかだと思う\nだからその、わ、分かるだろ…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1332006, 0, "日の出を拝んで参拝して屋台巡り\n正月とは実に楽しいものだな！\nこうなれば徹底的に正月を味わい\n尽くすぞ！　さあ、私に続け！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1332007, 0, "私はただ食べ歩きして浮かれてる\nわけではないぞ。自ら平和を享受\nすることで街と人々を守る決意を\n新たにしているのだ。本当だぞ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1332008, 0, "神殿、参拝客で溢れていたな\n神頼みはもちろん結構だが\n何かあればまずは私を頼れ\n必ず貴公の力となろう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1332009, 0, "何？　口元が汚れてる？\nこ、こら、これくらい自分で\n拭けるって、は、恥ずかしいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1332010, 0, "貴公と過ごす正月が、こんなにも\n楽しくて愛おしいとはな。来年も\nいや、願わくはこれから先ずっと\nこんな正月を送りたいものだな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133201, 0.0, 6, 0, 6, 0, 6.2, 0, 0, 2, 2, 1332011, 0, "誕生日おめでとう！　お祝いに\nとっておきの甘味処にご招待\nしよう。決して私が食べたい\nからではないぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1332012, 0, "貴公、年を経るごとに顔つきが\nよくなっていくな。いつか同じ\n部隊で背中を預けて戦えたら…\nなんて想像をしてしまうぞ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133501, 0.0, 2, 0, 1, 0, 5.1, 0, 0, 1, 1, 1335001, 0, "こうして和服を着ていると、\nなんだか背筋が伸びる気分だわ\n一年の計は元旦にあり、だもの\n正月が明けてもきちんとしたいわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133501, 0.0, 9, 0, 2, 0, 7.6, 0, 0, 1, 2, 1335002, 0, "お祝い事は大切にしないとね\n特に子どもたちにとっては、\n特別な時間だと思うの\n大人が考えているよりずっとね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133501, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1335003, 0, "お正月の遊びなら任せて♪\n子どもの頃は貧しいなりに\nいろいろ工夫して\n遊んでたんだから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133501, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1335004, 0, "あんたはこれから\nどんな一年を過ごしたい？\nやりたいことがあるなら\nあたしも手伝ってあげるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133501, 0.0, 2, 0, 5, 0, 6.1, 0, 0, 1, 1, 1335005, 0, "新年の目標、決めておきましょ\nあたしは、そうね…\nあんたと楽しく過ごせたら、\nそれが一番かしら。ふふっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1335006, 0, "は～…\n普段はなかなかできないけど、\n横になって休むのって\nやっぱりいいものね～…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1335007, 0, "ここから見える庭がとっても\n素敵なのよ。ふふっ♪　ちょっと\nお行儀悪いけど、今だけだから\n満喫しましょ。ほら、あんたも♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1335008, 0, "この香り…温泉の匂いかしら\nあまりなじみがない分、\n今は旅先にいるんだって\n実感できるのよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1335009, 0, "二人きり、水入らずかぁ…\nほら、いつも一緒にいても\nこういう時間はあまりないでしょ\nだから…もう少し味わわせて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1335010, 0, "こうやってごろごろする時間が\n本当に幸せなの。あんたを\nひとり占めした、最高に贅沢な\nお正月よ…なんてね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133501, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1335011, 0, "お誕生日おめでとう！\nうん、いい顔してるわね\nこれからの一年も\nきっといいことがあるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1335012, 0, "誕生日を一緒に過ごす\n特別な相手に、あたしを\n選んでくれたってことかしら\nふふっ、ありがとうね…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133601, 0.0, 8, 0, 1, 0, 4.5, 0, 0, 1, 2, 1336001, 0, "アタシの魔眼で睨まれたら、\nみーんな石になっちゃうの！\nどう？　怖いでしょ！\nわかったならひれ伏しなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133601, 0.0, 4, 0, 2, 0, 5.2, 0, 0, 1, 1, 1336002, 0, "アタシだって立派な\nジオ・ゲヘナの貴族…レディよ！\nそりゃ、お姉ちゃんたちに\n比べたらまだ半人前だけど…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133601, 0.0, 7, 0, 3, 0, 6.2, 0, 0, 1, 8, 1336003, 0, "メドゥシアナはアタシの眷属！\n良き相棒でもあり、\n友達でもあるわ\nすっごく頼もしいんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133601, 0.0, 5, 0, 4, 0, 4.9, 0, 0, 1, 4, 1336004, 0, "お姉ちゃんたち…どこに\n行っちゃったんだろう…\nあ！　い、今のは心細いとか\nそういうのじゃないから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133601, 0.0, 1, 0, 5, 0, 5.7, 0, 0, 1, 6, 1336005, 0, "アンタはまぁまぁ優しいし？\n言うこと聞いてくれるし？\nま、そばにいてもいいわよ\n光栄に思いなさいよね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1336006, 0, "どんな世界に行っても\nアタシたちは無敵なんだから！\nもっと高く、もっと速く─\n行くわよ、メドゥシアナ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1336007, 0, "このアタシのお姉ちゃん捜しを\n手伝わせてあげるんだから\n光栄に思いなさい！\nお～も～い～な～さ～い～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1336008, 0, "お姉ちゃん…\n絶対見つけてみせるわ…！\nそれまでに、今より\nもっと立派なアタシになる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1336009, 0, "アンタなら…メドゥシアナに\n乗せてあげてもいいわよ？\nた、たまにだけどね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1336010, 0, "ねぇ、これからも\nアタシを楽しませてよね？\n約束破ったら…\n石にしちゃうんだからっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133601, 0.0, 2, 0, 6, 0, 2.5, 0, 0, 2, 6, 1336011, 0, "誕生日？　しょうがないわねー\nメドゥシアナに乗せてあげるから\n世界一周でもしに行くわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1336012, 0, "アンタ、誕生日なんでしょ？\n蛇の抜け殻あげるわ！\nどう？　うれしいでしょ！\n…うれしいわよね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133701, 0.0, 2, 0, 1, 0, 3.4, 0, 0, 1, 1, 1337001, 0, "故きを温ね新しきを知る…\n迷宮女王、再起動！\nって感じかな？\nさあ、力を貸すよ少年！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133701, 0.0, 6, 0, 2, 0, 8.1, 0, 0, 1, 4, 1337002, 0, "手早くどこででも食べられて、\nおまけに栄養面もばっちりな\n非常食を考えてるんだけど…\nクレープでの実現は厳しいか～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133701, 0.0, 1, 0, 3, 0, 8.8, 0, 0, 1, 4, 1337003, 0, "世界が変わる…っていうか\n世界観を変えちゃってないかな？\nこの格好…ま、アタシの他にも\n例外はたくさんいるし今更か！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133701, 0.0, 2, 0, 4, 0, 7.9, 0, 0, 1, 1, 1337004, 0, "どんどん可能性を広げていく\nキミを見てると…アタシのした\n選択は間違いじゃなかったって\n思えるよ。ありがとう、少年");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133701, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1337005, 0, "少年、どんなに難しい局面でも\nアタシがついてるから安心して！\n立ち塞がる敵にお見舞いする、\n百発百中の援護射撃をご覧あれ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1337006, 0, "誰から相手してほしい？\n今日はすごく気分がいいんだ\nとっておきの大技も必殺技も\n大盤振る舞いで見せちゃおう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1337007, 0, "少年、みんなと一緒に\n心置きなく前に出ちゃって！\nアタシが援護するから\n背中は任せてくれていいよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1337008, 0, "どんな状況でも打開する道を\n最短で探査し駆け抜ける…\n迷宮の名を冠する女王は勿論、\n攻略にも詳しいってわけさ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1337009, 0, "後衛で背後を守る\nってのも悪くないもんだね！\n少年の成長が実感できるよ\nもうすっかり頼れる背中だ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1337010, 0, "昔、アタシの進む道を\n少年が導いてくれたように…\n今度はアタシが切り拓こう\nキミの進む先を、この光で！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133701, 0.0, 6, 0, 6, 0, 9.5, 0, 0, 2, 2, 1337011, 0, "お誕生日おめでとう少年！\n今日は好きなのを作ってあげよう\nまたクレープかって？　ご名答！\nあー…たまには他のがいいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1337012, 0, "ハッピーバースデー！\n今日はいつもと趣向を変えて\nとっておきの手料理をご馳走だ！\n料理の腕には自信あるんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133801, 0.0, 2, 0, 1, 0, 5.9, 0, 0, 1, 1, 1338001, 0, "最適化のためのアバター変更に\n特に感慨はなかったのですが…\n今となっては多少の懐かしさを\n感じてしまいますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133801, 0.0, 4, 0, 2, 0, 7.7, 0, 0, 1, 1, 1338002, 0, "この状態のグレゴールは今とは\n比較にならないほど強力ですが\nそれ以上に…比較できないほど\n燃費が悪いのが問題ですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133801, 0.0, 2, 0, 3, 0, 6.7, 0, 0, 1, 6, 1338003, 0, "見た目は確かに過去の私ですが\n中身は今の進化した私です\n当然、あの頃よりも更に\n強いに決まっているでしょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133801, 0.0, 2, 0, 4, 0, 2.5, 0, 0, 1, 6, 1338004, 0, "ひらひらしてキレイ？\nふふっ、そうですか\n貴方の好みは\nこういう服装なのですね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133801, 0.0, 2, 0, 5, 0, 3.0, 0, 0, 1, 6, 1338005, 0, "このアバターについて知りたい？\nいい傾向ですね\n正しい好奇心は学習の質と量を\n高めますから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1338006, 0, "あら、そう…ふふっ\nグレゴールもそう思うのですね\nよかったですね、貴方の事は\n気に入ってるみたいですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1338007, 0, "この場所は完全に私の支配領域\nいうなれば妖精の女王の統べる\n常春の国ティル・ナ・ノーグ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1338008, 0, "七冠と呼ばれ始めた頃のことなど\nすっかり忘れていましたが…\nたまに振り返るのも良いでしょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1338009, 0, "この姿は普段より偉そう…？\nふふっ、そう思うのであれば\n足元に跪いてもよいのですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1338010, 0, "この姿の時のグレゴールは\nいつもより甘えたがりなのです\nおや、貴方も甘えたいのですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133801, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1338011, 0, "誕生日おめでとうございます\n…貴方にとっては私と出会った日\nこそが本当の誕生日と言えますね\nそちらもしっかりと祝うのですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1338012, 0, "誕生日の祝祭を始めましょう\n貴方が生まれ、私のものとなる\nこの必然を祝うために");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133901, 0.0, 2, 0, 1, 0, 4.5, 0, 0, 1, 1, 1339001, 0, "再びこの姿になれる日が来るとは\n夢にも思わなかったな！\nどれ、泡沫の夢と消える前に\n存分に愉しませてもらおうか☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133901, 0.0, 1, 0, 2, 0, 6.7, 0, 0, 1, 6, 1339002, 0, "罵倒の言葉も数あれど、\n最も多くワタシに向けられた\nフレーズは『悪魔』だったな\nフフ、お似合いだろう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133901, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1339003, 0, "悪魔は邪悪な存在だが\n契約は決して違えぬものらしい\n思わず親近感を覚えてしまうな☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133901, 0.0, 1, 0, 4, 0, 5.2, 0, 0, 1, 2, 1339004, 0, "おいおい、そう警戒してくれるな\n取って食いやしないさ。それとも\n坊やは食われるのがお好みか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133901, 0.0, 1, 0, 5, 0, 5.8, 0, 0, 1, 6, 1339005, 0, "ワタシの昔話が聞きたいのか？\n興味があるなら聞かせてやろう\n寝物語には悪くない♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1339006, 0, "正気で戦場は成り立たない！\nさあ、ワタシと狂気の舞踏会と\n洒落込もうじゃないか、坊や☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1339007, 0, "戦いは派手なほどいい☆\n多少の過負荷で悲鳴を上げる\nアラートなぞ捨て置け！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1339008, 0, "退屈予防にいくつか火種を\nバラ撒いておくか？\n祭りになればそれはそれで\n愉しめるだろうよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1339009, 0, "悪魔の誘惑というやつは\n甘く逆らいがたいものらしいぞ\nフフ、誘惑されてみるか、坊や？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1339010, 0, "使い道を過てば己の破滅さえも\n招きかねんほどの絶大な力だ！\n坊やのついでに世界までも\n守ってやれるだろうよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133901, 0.0, 2, 0, 6, 0, 2.6, 0, 0, 2, 1, 1339011, 0, "今日はオマエの誕生日だったか\nどれ、ケーキでも作ってやろう☆\n味は期待してくれて構わんぞ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 133931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1339012, 0, "ハッピーバースデー！\n誕生日パーティーは任せておけ\n決して退屈しない一日を\nオマエにプレゼントしてやろう☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134001, 0.0, 2, 0, 1, 0, 6.2, 0, 0, 1, 1, 1340001, 0, "おはよ♪\n今日はどこに行きたい？\nどこへだって案内するわよ\nあたしはガイド妖精なんだから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134001, 0.0, 1, 0, 2, 0, 3.1, 0, 0, 1, 6, 1340002, 0, "ちっこい体って便利なのよね\nあんたの肩の上に乗って\n楽できるし、頭の上に乗って\n広い世界を見下ろせるし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134001, 0.0, 3, 0, 3, 0, 1.1, 0, 0, 1, 4, 1340003, 0, "ひゃっ！？　こらーっ！\n羽をつまむんじゃないわよ！\n妖精サイズでも女の子なのよ？\nもっと丁寧に扱いなさい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134001, 0.0, 2, 0, 4, 0, 9.8, 0, 0, 1, 1, 1340004, 0, "この姿だといろいろ思い出すわね\n今よりもお調子者なあんたに\n振り回されたり、振り回したり\n毎日が大騒ぎだったんだから♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134001, 0.0, 1, 0, 5, 0, 11.0, 0, 0, 1, 6, 1340005, 0, "行きたい場所があるの？\nどこ？　案内なら任せなさい♪\n…あたしの、好きなところに？\nなら…迷うことはないわね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1340006, 0, "ようこそ、アストルムへ♪\nあたしは妖精のフィオ！\nあんたを導く、一番の相棒よ♪\nこれからも…よろしくね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1340007, 0, "あたしのあるべき姿はこれよね♪\nさぁ、一緒に冒険に出発よ！\nあたしがガイドしてあげるから\nなんでも聞きなさいっ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1340008, 0, "このメンバーで冒険してると\nなんだか…ううん、なんでもない\n行きましょ、願いを叶える旅に！\nどこまでもついていくわよっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1340009, 0, "この姿でいるとさ、あんたとの\n距離が近くなる気がするのよね\n一人の─\nあんただけの、妖精でいられる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1340010, 0, "妖精のおまじないをしてあげる\nちょっと、目を閉じなさい\n…～～～フィオ、ぱーんちっ！\nダマされたわね？　…ふぅ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134001, 0.0, 5, 0, 6, 0, 2.6, 0, 0, 2, 2, 1340011, 0, "誕生日おめでとう♪\n長い付き合いだもの\n二人っきりのお祝いも\nいいものよね…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1340012, 0, "ハッピーバースデー！\n優秀なガイド妖精は\n記念日を忘れたりしないのよ？\n妖精の祝福を受け取りなさいっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134301, 0.0, 1, 0, 1, 0, 10.1, 0, 0, 1, 6, 1343001, 0, "唱喚士の素質がある、なんて…\nまだ…信じられない、わ…\nひとを傷つけてきた、わたしが…\nひとを守るため、歌うなんて…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134301, 0.0, 4, 0, 2, 0, 11.2, 0, 0, 1, 6, 1343002, 0, "エルフの里に伝わる、精霊の舞を\n教えてもらったん、だけど…\nころびすぎて、コッコロちゃんに\n「諦めも肝心」って言われたわ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134301, 0.0, 6, 0, 3, 0, 11.8, 0, 0, 1, 1, 1343003, 0, "精霊さんばかりかまってると…\nワイバーンがやきもち妬いちゃう\nみたいで…な、なんでかしら…？\nわたしが造ったのに…はぅ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134301, 0.0, 1, 0, 4, 0, 14.7, 0, 0, 1, 6, 1343004, 0, "どうしてこの服って\nこんなに薄着なのかしら…？\nマナを感じやすかったり…？\nなら仕方ない…の、かしら…ね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134301, 0.0, 5, 0, 5, 0, 18.0, 0, 0, 1, 2, 1343005, 0, "同じお世話でも、あなたと\n精霊さんとでは、少し…違うわ…\nあなたをお世話、してる時は…\nド、ドキドキ…しちゃって…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1343006, 0, "ひとの声が、ずっとこわかった…\nでも今は…だれかの声に合わせて\n歌うことが…ふふっ…\nこんなにも、たのしい…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1343007, 0, "もっと色々な歌、歌いたいわ…\nカルミナみたいな\n明るくて、かわいい歌も…\n自信はないけど、そのうち…はい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1343008, 0, "チカちゃんの歌は…\nひとを包み込む、優しい、歌…\n本当に、すてき…ふふっ\n弟子になれて、うれしい…わ…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1343009, 0, "あなたとも、一緒に…\n歌ってみたい、わ…\nあなたとの、ハーモニー…\nたくさん、奏でていきたいから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1343010, 0, "ひとりでいるのも好きだけれど…\nみんなでいるのも好きになれたわ\nあ…もちろん、あなたと\nふたりでいるのも…好き、よ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134301, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1343011, 0, "お誕生日ケーキ…作ってみたの\n何度も失敗しちゃったけど\nやっとうまくいったから…その…\n…はい、あ～ん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1343012, 0, "チカちゃんと、ね…\nたくさん、歌の練習、したの…\nあなたの誕生日に、とっておきの\n精霊の加護を贈るために…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134401, 0.0, 1, 0, 1, 0, 8.8, 0, 0, 1, 8, 1344001, 0, "いつも私を支えてくれて、\n時には導いてくれるあなたへ\n感謝を込めて歌います♪\n子守唄がいいですか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134401, 0.0, 8, 0, 2, 0, 5.8, 0, 0, 1, 1, 1344002, 0, "歌は時々、言葉よりもたくさんの\n想いを伝えてくれます。うまく\n言えないことも、歌でなら\nあなたに伝えられるかも…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134401, 0.0, 6, 0, 3, 0, 11.1, 0, 0, 1, 1, 1344003, 0, "私ももっと修行して、みんなに\n頼られる、唱喚士になりたいです\nあなたには一番近くで見守って\nいてほしい…わがままですか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134401, 0.0, 9, 0, 4, 0, 6.2, 0, 0, 1, 4, 1344004, 0, "この衣装、布が少ないから\n心もとなくて…でもあなたが\nきれいだって言ってくれたから\n胸を張って着られます…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134401, 0.0, 2, 0, 5, 0, 6.4, 0, 0, 1, 1, 1344005, 0, "肌にも模様を乗せることで精霊と\nより心を通わせられるんだとか\nふふっ…精霊とも、あなたとも\n距離が近づいた気がします…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1344006, 0, "教え、導く…言葉にするのは\n簡単ですが、実際は難しいですね\nそれでも、私の精一杯を\nランファさんに届けたいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1344007, 0, "心を込めて歌います\n私たちの旋律と歌が\n精霊たちにも、あなたにも\n届きますように…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1344008, 0, "ランファさんの師匠として\nなによりうれしいのは、一緒に\n歌えるようになったことですね♪\nあなたも一緒に歌いませんか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1344009, 0, "あなたに見つめられるとだんだん\n頬が熱くなってきて…\nどんな舞台の上に立つよりも\n緊張してしまうんです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1344010, 0, "あわわ…思いっきり歌うと\nあなたへの気持ちが隠せなく…っ\nき、聞いたらだめです…！\nだって恥ずかしいですから…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134401, 0.0, 9, 0, 6, 0, 5.9, 0, 0, 2, 2, 1344011, 0, "ハッピーバースデー！　ケーキを\n里の皆さんと一緒に用意しました\nろうそくを消す前には、お祝いの\n歌を歌いましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1344012, 0, "お誕生日おめでとうございます\nあなたの生まれた日、全力で\nお祝いしますね。歌にケーキ\n膝枕で子守唄もしちゃいます…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134501, 0.0, 2, 0, 1, 0, 4.1, 0, 0, 1, 1, 1345001, 0, "クローチェ、空戦特化仕様の\nエアリアルモードですっ♪\nあなたのピンチにびゅーんと\n駆けつけますよっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134501, 0.0, 1, 0, 2, 0, 3.3, 0, 0, 1, 2, 1345002, 0, "このボディ、すっごく軽く\nなってるんですよっ♪\n試してみますっ？　抱き上げて\nみてくださいっ、ほらっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134501, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1345003, 0, "アームを換えたら、ハグと握手が\nできるようになったんですよっ♪\n前よりも上手にナイトさんに\n触れられて、わたし、幸せですっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134501, 0.0, 2, 0, 4, 0, 6.9, 0, 0, 1, 1, 1345004, 0, "ナイトさん、充填お願いしますっ\n飛んでる途中でエネルギー切れに\nなったら大変ですからねっ！\nこまめにいっぱい補給ですっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134501, 0.0, 2, 0, 5, 0, 6.1, 0, 0, 1, 1, 1345005, 0, "一緒にお出かけしましょうっ！\nナイトさんと一緒に見たい景色が\nいーっぱいあるんですっ♪\nずっと一緒にいてくださいねっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1345006, 0, "エアリアル、高機動モードっ！\n悪い魔物さんはみんな\n空の上から狙い撃ちですっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1345007, 0, "どんなに逃げてもロックオンっ！\nわたしのビームキャノンが\n逃がしませんよ～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1345008, 0, "どれだけ大きくて強い敵が\n相手でも、諦めませんよっ\n守りたいものがあるんですからっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1345009, 0, "空から街を見下ろしていると\nナイトさんの顔が思い浮かんで…\n絶対負けられないってパワーが\nぐんぐん湧いてくるんですっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1345010, 0, "帰ったらナイトさんに\nハグしてもらって、充填もっ♪\nわたしにとってはとびきりの\nご褒美なんですよっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134501, 0.0, 1, 0, 6, 0, 3.9, 0, 0, 2, 2, 1345011, 0, "ハッピーバースデーっ！\nえへへ、お誕生日って\n歌を歌うんですよねっ？\n精いっぱい歌わせてもらいますっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1345012, 0, "お誕生日おめでとうございます！\nプレゼントはわたしですっ！\nナイトさんが喜んでくれたら、\nとってもとっても幸せですっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134601, 0.0, 2, 0, 1, 0, 4.3, 0, 0, 1, 4, 1346001, 0, "私がパイロットになったのは\n成り行きみたいなものですけど…\nそれでも全力でがんばります\n信じてくれるみなさんのために！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134601, 0.0, 4, 0, 2, 0, 0.0, 0, 0, 1, 4, 1346002, 0, "どれだけ強い機体でも、\nパイロットの腕が悪かったら\n宝の持ち腐れなんですよね…\nパイロット、責任重大ですっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134601, 0.0, 2, 0, 3, 0, 0.0, 0, 0, 1, 2, 1346003, 0, "クローチェさんってすごいです\nいつも明るいムードメーカーで\n誰とでもすぐ仲良くなれて…\nい、いつか私もあんなふうに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134601, 0.0, 7, 0, 4, 0, 2.9, 0, 0, 1, 6, 1346004, 0, "もしも団長さんがいなかったら…\nいやいや、そんなこと怖すぎて\n想像できませんよ\n世界が滅ぶみたいなものです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134601, 0.0, 6, 0, 5, 0, 7.3, 0, 0, 1, 2, 1346005, 0, "あなたに伝えたいことは、\nまだまだたくさんありますけど…\n戦いが終わったらにしましょう\n…あれ、なんかフラグみたい？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1346006, 0, "一人乗りのコクピットですけど、\n今の私はぼっちじゃありません\n応援してくれる人たちが\nたくさんいますからねっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1346007, 0, "クローチェさんの熱い気持ちが\n文字通りダイレクトに\n伝わってきます…\nこれが人機一体の友情…！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1346008, 0, "コクピットに乗って戦ってると\nなんだか叫びたくなるんですよね\nえっ？　わりといつも叫んでる？\nそそそうでしょうか…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1346009, 0, "あの人が待っていてくれるから\n絶対に生きて帰ろうって気持ちが\n湧いてくるんです\nこうなった私は無敵ですよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1346010, 0, "どんなに強い敵にも、\n挫くことはできないんです\n大切な人を守りたいと思う、\n私たちのこの願いは…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134601, 0.0, 5, 0, 6, 0, 7.3, 0, 0, 2, 2, 1346011, 0, "お誕生日おめでとうございます！\nここはクローチェさんに頼んで\nギガンティックな祝砲でも\n撃っちゃいますか！　やり過ぎ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1346012, 0, "大切な人のお誕生日を\nいっしょに祝うことができる\nそんな平和な日常を守るのも、\n大事なことなんですっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134701, 0.0, 1, 0, 1, 0, 3.5, 0, 0, 1, 6, 1347001, 0, "日々私のもとへ来るとは…\n人の子よ、良い心がけです♪\n特別にほーちゃん、と\n呼んでもいいですよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134701, 0.0, 8, 0, 2, 0, 5.4, 0, 0, 1, 1, 1347002, 0, "今日もお役目ご苦労でした\n温泉でもつかりにいきますか？\nと、鶏のだしは出ませんよ？\n出ませんからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134701, 0.0, 6, 0, 3, 0, 8.8, 0, 0, 1, 2, 1347003, 0, "絢爛にして神聖なる私\nそう、とってもえらいんです\nえっへん！\n…ほ、本当ですからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134701, 0.0, 8, 0, 4, 0, 8.3, 0, 0, 1, 2, 1347004, 0, "私の加護を受ければ、\n無病息災、食欲増進、\n恋愛成就間違いなし！\n…た、たぶん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134701, 0.0, 4, 0, 5, 0, 9.7, 0, 0, 1, 1, 1347005, 0, "あなたは見どころがありますね\nと、特別に、私のそばに\n来ることをゆるしましょう！\nほ、ほら、もっと近くに～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1347006, 0, "我が名はホウオウ\n輪廻転生を繰り返すこと幾星霜\n浄化を司る神鳥とはこの私！\n…あっ、信じてませんね！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1347007, 0, "ジオ・ゲヘナに渦巻く闇の力、\n私の浄化の炎で払ってあげます！\n…あちっ、あ゛ぢぢっ！\nや、やりすぎましたぁ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1347008, 0, "鳳凰の羽は、我が力の象徴\nこの美しい羽毛をご覧なさい！\n神々しく、美しく輝いて…\nって、抜かないでくださいぃ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1347009, 0, "だって私は鳳凰ですからっ！\nこのジオ・ゲヘナの空の覇者！\nいつか騎士さんにもあの景色を…\nぱたぱたぱた～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1347010, 0, "我が炎は、悪しき心のみを焼く…\nふふふっ。すごいでしょう？\nさぁ褒めなさいっ♪　騎士さんに\nたたえられるのは格別ですからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134701, 0.0, 7, 0, 6, 0, 9.2, 0, 0, 2, 2, 1347011, 0, "誕生日、おめでとうございます！\n私をおんぶする権利、もしくは\nだっこする権利をあげましょう！\n…え？いらない？ぞん゛な゛ぁ゛");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1347012, 0, "誕生日のごちそうといえばチキン\nだそうですね\n…わ、私はちがいますからね？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134801, 0.0, 5, 0, 1, 0, 8.5, 0, 0, 1, 6, 1348001, 0, "この服、どうですか？\nちょっとワイルドすぎますか？\nえっ？　何着てもかわいいって…\nバ、バカぁ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134801, 0.0, 5, 0, 2, 0, 8.4, 0, 0, 1, 1, 1348002, 0, "ファッションで大事なのはTPO\n時と場合で服も変えないと！\nでも騎士さんはいつも変わらない\nのが…な、なんでもないですっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134801, 0.0, 7, 0, 3, 0, 5.4, 0, 0, 1, 1, 1348003, 0, "いろんな世界の人たちに\nおしゃれすることのすばらしさ\nを知ってもらいたいです！\n仕立て屋も大繁盛！　なーんて♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134801, 0.0, 5, 0, 4, 0, 3.4, 0, 0, 1, 1, 1348004, 0, "おしゃれの醍醐味は個性を伸ばす\nことです！　騎士さんの個性は…\n優しい所…う、嘘ですよーだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134801, 0.0, 3, 0, 5, 0, 9.1, 0, 0, 1, 4, 1348005, 0, "レイ様が好きな気持ちと、今\n私が抱えてる、この気持ち\n同じ大きさで綱引きしてて…もう\n全部騎士さんのせいなんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1348006, 0, "こ、このエロガエルーっ！\n私の服食べるなーーーっ！！\nちょっと、騎士さん！\n見てないで助けてくださーい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1348007, 0, "昔似たような目に遭ったような…\n私ってば、こういう星の下に\n生まれついてたりします？\n違うって言ってください～～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1348008, 0, "ぐぬぬ…こんな魔物ごときにっ！\n私のデザインが…！　次は絶対\n破けない生地で作りますから！\n…え？　フラグじゃないですー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1348009, 0, "ううっ、こんな姿…\n誰かに見られたら泣いちゃいます\n騎士さんも、忘れてくださいね？\nお願いですから～～っ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1348010, 0, "たっ、大変だったけど素材は手に\n入ったし、これで服が作れます！\nあとは…その、騎士さん…着替え\n持ってきてくれますか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134801, 0.0, 5, 0, 6, 0, 5.4, 0, 0, 2, 2, 1348011, 0, "誕生日おめでとうございます！\nプレゼントに服を作ってあげます\n…ぺ、ペアルックとかじゃない\nですからね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1348012, 0, "今日が騎士さんの生まれた日…\nつまり私の悩みの種が生まれた日\n…なーんて、冗談ですよ！\n今日はたくさんお祝いしましょ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134901, 0.0, 3, 0, 1, 0, 7.1, 0, 0, 1, 2, 1349001, 0, "ご覧あそばせ、今日もあたくしは\nお美しく輝いていますわ～♪\nそうは見えないというなら、\nきっと目が曇っておりますわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134901, 0.0, 2, 0, 2, 0, 4.5, 0, 0, 1, 1, 1349002, 0, "さて、今日も日課を始めましょう\nヲホホッ、無様ですわねネフィ！\n…これは、ネフィに勝った後\n土下座を命じるイメトレですわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134901, 0.0, 7, 0, 3, 0, 7.8, 0, 0, 1, 4, 1349003, 0, "この辺りも全部、本来ならば\nあたくし様が統治しているはずの\n土地でしたのよっ\nう、嘘なんかじゃねぇですわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134901, 0.0, 2, 0, 4, 0, 6.2, 0, 0, 1, 1, 1349004, 0, "馬ボーンときたら、相変わらず\n締まらないお顔をしてますわねぇ\nあたくし様の華麗なオーラを\n分けてあげてもよろしくてよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134901, 0.0, 1, 0, 5, 0, 7.1, 0, 0, 1, 2, 1349005, 0, "いずれあたくしはネフィを倒し、\n本来の地位を取り戻してみせます\n馬ボーンには、その美しき物語を\n語り継ぐお役目を命じますわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1349006, 0, "ヲォ～ッホッホッホ！\nこれぞ全てを見下すポーズ！\n支配者たるあたくし様だけに\n許されたお姿をご覧あそばせ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1349007, 0, "剣闘士用に押しつけられた\nナマクラ刀？　関係ねぇですわ！\n真の最強は、どんな武器でも！\n敵をボコボコにできますのよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1349008, 0, "ヲッホッホ、ヲ～ッホッホッホ、\nヲッホッホ！　思わず五七五で\n高笑いしてしまうくらい、\n感動でお胸が膨らんでますわ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1349009, 0, "あぁっ、最高の気分ですわ～！\nもうずっと馬ボーンと一緒に\nいたいくらいですわ～♪\n…変な意味じゃありませんわよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1349010, 0, "この力をあたくし様のモノに…\nいえ、馬ボーンを永遠に\nあたくし様の下僕にするしか！\n夢が広がりまくりですわ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134901, 0.0, 9, 0, 6, 0, 14.9, 0, 0, 2, 6, 1349011, 0, "今日は馬ボーンのお誕生日～？\nまぁどうしても祝ってほしいなら\n考えてあげなくもないですけど…\n…別にいい？　なんでですの！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 134931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1349012, 0, "馬ボーンのお誕生日プレゼントに\n素晴らしいものを用意しましたわ\nそう、あたくし様に仕える地位！\n涙を流してお喜びなさいな～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135001, 0.0, 1, 0, 1, 0, 3.0, 0, 0, 1, 6, 1350001, 0, "騎士クン、暑くない…？\n涼みたくなったら\nわたしにいつでも言って\n防暑魔法の準備は、万全");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135001, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1350002, 0, "海の匂いって…有機的だね\nわたしの作る仮想領域とは違う\n複雑に濁っていて、独特で…\nだけど、命の営みを感じる匂い…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135001, 0.0, 2, 0, 3, 0, 6.1, 0, 0, 1, 6, 1350003, 0, "砂浜に刻んだ足跡も\nいつか波にさらわれてしまう…\nでも…わたしはずっと覚えてるよ\n二人で過ごした、この夏のこと…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135001, 0.0, 4, 0, 4, 0, 0.0, 0, 0, 1, 4, 1350004, 0, "わたしからの気持ちだけ\n騎士クンに受け取ってほしい…\n他の子からの好意は、忘れて、ね\n今、この時だけ、でも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135001, 0.0, 2, 0, 5, 0, 3.5, 0, 0, 1, 6, 1350005, 0, "巡り巡って…夏…\n騎士クンとこうやって\n過ごせるなんて、\n思ってもみなかった…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1350006, 0, "月が満ちて季節は巡り、命は続く\nやっと分かってきた気がする\n生きるということ…その意味を\n騎士クンが、教えてくれたんだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1350007, 0, "月の光と波の音…\n他には何もない…\n騎士クンとこの世界で\n今だけは、二人きり…だね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1350008, 0, "騎士クンとのつながりが\nわたしを温めてくれる…\nだから…平気\nもう…寒くなんて、ない");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1350009, 0, "わたしに残っているものなんて\nほとんどないけれど\nこの身も、心も…力も\n全ては騎士クンのために…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1350010, 0, "暗い…光の届かない海底で、\n泡になったとしても…\nわたしの想いは…消えない\n騎士クンへの、この想いは…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135001, 0.0, 1, 0, 6, 0, 2.6, 0, 0, 2, 2, 1350011, 0, "お誕生日おめでとう\n騎士クンが生を受けた日を\nお祝いできること…\nそれが…とってもうれしい…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1350012, 0, "騎士クンへの誕生日プレゼント…\nわたしにあげられるものなら\nなんでもあげる…夏の空も、海も\nだから、欲しいもの…教えて？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135101, 0.0, 6, 0, 1, 0, 11.7, 0, 0, 1, 1, 1351001, 0, "前はちょっと恥ずかしかったけど\n今はこの水着、気に入ってるの\n私も成長したのかしら。あ、その\n精神的に、っていう意味よ…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135101, 0.0, 2, 0, 2, 0, 7.6, 0, 0, 1, 1, 1351002, 0, "波の音って落ち着くわね\nずっと聴いていたい…\nもう少しだけ二人で\nゆっくりしましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135101, 0.0, 1, 0, 3, 0, 9.0, 0, 0, 1, 2, 1351003, 0, "ねえ、アイスクリーム食べに\nいきましょう♪　私はやっぱり\nチョコミントで決まりね\nあなたはどのフレーバーにする？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135101, 0.0, 6, 0, 4, 0, 6.1, 0, 0, 1, 1, 1351004, 0, "涼みたい時はいつでも言ってね\n私がヒンヤリさせてあげる…\nあ、羽を触らせるとかじゃなくて\n氷魔法の話よ？　念のため…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135101, 0.0, 7, 0, 5, 0, 9.1, 0, 0, 1, 1, 1351005, 0, "こうして、何も考えずに\n二人で海を眺めているのも\n私はキライじゃないわ\n…ううん、むしろ好きかも…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1351006, 0, "さあ、私の手を取って！\n一緒に波に乗って\n景色を楽しみましょう\n私たちの夏は、これからよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1351007, 0, "この水着、実は気に入っているの\nシンプルで動きやすいし\nひまわりのワンポイントが\nかわいいし…あなたもそう思う？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1351008, 0, "このフロート、何だか\n兄さんに似ている気がして…\nつい買っちゃったの\n海で頼りになりそうだしね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1351009, 0, "ふふ、こうして滑っていると\n何だか飛んでいるみたい♪\nあなたが一緒ならどんな荒波でも\n乗り越えられる気がするわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1351010, 0, "あなたと同じ波の上にいると\n不思議な一体感を感じるわ\n楽しくてドキドキするような\nうれしい感覚…私は好きよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135101, 0.0, 7, 0, 6, 0, 2.9, 0, 0, 2, 2, 1351011, 0, "誕生日おめでとう！\nあなたのバースデイパーティーは\n私が精一杯プロデュースするわ\n楽しみにしててね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1351012, 0, "ハッピーバースデイ♪\nあなたにプレゼントしたい\n景色があるの。さあ手を取って\n特別な一日を過ごしましょう♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135201, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1352001, 0, "ばーべきゅー\nうみのいえ\nやたい\nうみ、いいとこ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135201, 0.0, 7, 0, 2, 0, 0.0, 0, 0, 1, 7, 1352002, 0, "ぶぅ…\nすなはま、あつい…\nやきぶた\nちょくぜん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135201, 0.0, 2, 0, 3, 0, 3.4, 0, 0, 1, 1, 1352003, 0, "うみでも、ぱとろる\nあ\nかいがら、はっけん\nきれい");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135201, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1352004, 0, "お兄。あそぼ\nまだ、あかるい。よ\nうみ、たのしい。よ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135201, 0.0, 2, 0, 5, 0, 5.3, 0, 0, 1, 6, 1352005, 0, "お兄の\nみずぎ\nぱいなぽー\nおいしそ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1352006, 0, "お兄も。ぷかぷか、する？\nきもちいい、たのしい\nながれ、ながされ\nぼうけん、はじまり");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1352007, 0, "（じー…）\nおさかな、たくさん…\nでも。まだこども\nまだがまん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1352008, 0, "ついんて\nかいすい。ぽたぽた\nお兄、ひだりついんて\nしぼって？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1352009, 0, "こおりまほー\nひえひえ、ぷれしあ\nお兄を、ぎゅ。ってして\nひやす。ぎゅ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1352010, 0, "すいぶんほきゅー、だいじ\nお兄、ドリンク。あげる\nおいしかった。よ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135201, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1352011, 0, "たんじょび、おめでと\nおさかな\nつってきた\nいっしょに、たべよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1352012, 0, "お兄。はぴば\nすぐ、おとな。なるから\nまってて。ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135301, 0.0, 2, 0, 1, 0, 6.1, 0, 0, 1, 1, 1353001, 0, "お兄さん、海に入る前に\n日焼け止めクリーム\n塗ってあげますね♪\nわたしも…あとでお願いします♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135301, 0.0, 1, 0, 2, 0, 7.1, 0, 0, 1, 2, 1353002, 0, "『ひと夏の経験』って言いますよね\nわたしもちょっと\n冒険しちゃおうかな…\nお兄さん、付き合ってくれます？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135301, 0.0, 1, 0, 3, 0, 5.2, 0, 0, 1, 2, 1353003, 0, "海で食べる焼きそばってすっごく\nおいしいですよね♪　…あ、\nお口にソースついてますよ～？\nふふ、はい。きれいになりました♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135301, 0.0, 2, 0, 4, 0, 6.2, 0, 0, 1, 5, 1353004, 0, "お兄さんに見つめられると\n恥ずかしくって赤くなっちゃう…\nだってお兄さんの視線\n夏の日差しよりも眩しいから…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135301, 0.0, 1, 0, 5, 0, 10.1, 0, 0, 1, 8, 1353005, 0, "海を見てると落ち着くんですよね\n嫌なことや悲しいこと…全部波が\nさらってくれるような気がして…\nなーんて…ふふ、冗談ですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1353006, 0, "うふふっ、それ発射～っ♪\nなんて…ちょっと子どもっぽい？\nこんなにはしゃいじゃったことも\n内緒ですよ、お兄さん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1353007, 0, "ほら、冷たくて気持ちいでしょ♪\nきゃっ、水かけないでください♪\nお兄さん手が大きいから\n勢い激しすぎますよ～っ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1353008, 0, "夏の日差しは強くて眩しいから\nその分影は暗く沈んで…なんだか\nいけないものに感じませんか…？\n…なんて、気のせいですよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1353009, 0, "お兄さんと二人っきり…\n緊張で汗かいちゃっても\n海の中ならバレなくていいです…\nふふ、なんでもないですよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1353010, 0, "この夏がずっと続けば…なんて\nわがままだってわかってます…\nだからせめて、今だけでも…\nねぇ、お兄さん…一緒に…っ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135301, 0.0, 2, 0, 6, 0, 2.7, 0, 0, 2, 1, 1353011, 0, "お誕生日おめでとうございます♪\n今日は忘れられないお誕生日に\nなりそうですね…\nあの夏の夕日みたいに…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1353012, 0, "お兄さんへの誕生日プレゼントは\nかわいい妹と海でバカンスです♪\n今日はお兄さんを独り占め\nしちゃいますから…えへへっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135401, 0.0, 7, 0, 1, 0, 0.0, 0, 0, 1, 7, 1354001, 0, "遊びとか─バカンスとか\nそんなの、時間の無駄では…\n私には─そう思えます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135401, 0.0, 6, 0, 2, 0, 9.0, 0, 0, 1, 7, 1354002, 0, "青い空も、青い海も─\nジオ・ゲヘナには、ないです\nだから─忘れないよう、\n見ておこうかと");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135401, 0.0, 4, 0, 3, 0, 7.0, 0, 0, 1, 6, 1354003, 0, "夏だから、休む─\n奇妙な文化ですね\n季節なんて関係なく─\n休むなら、休めばいいのに");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135401, 0.0, 7, 0, 4, 0, 6.1, 0, 0, 1, 6, 1354004, 0, "貴方も、夏の正装なんですね\n─いえ、別に…\n珍しい格好だと思っただけです\n他には何も─");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135401, 0.0, 1, 0, 5, 0, 7.6, 0, 0, 1, 4, 1354005, 0, "貴方の考えてること、\nやっぱりよくわかりません\nでも─前よりは、ちょっと…\nほんの少しだけ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1354006, 0, "ハンモック─悪くないですね\n風に合わせて、静かに揺れて…\nスイカの、栽培みたいで\nこれもバカンスの楽しみ方─？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1354007, 0, "─心配しなくても、\n海に落ちたりしませんよ\n貴方みたいに、無邪気に\nはしゃいだりしませんし…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1354008, 0, "媛からの命令…どういう狙いか\nわからないことが多くて─\nいつかはわかるように\nなるんでしょうか─");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1354009, 0, "─必要以上に休むのは、\n性に合いませんが─\nまあ、たまには\n背も伸びるかもですし─");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1354010, 0, "─二人だと、狭いかな…\n…いえ、何でもないです\nただのおかしな思いつきなので\n気にしないでください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135401, 0.0, 1, 0, 6, 0, 15.2, 0, 0, 2, 6, 1354011, 0, "誕生日、おめでとうございます\n─贈り物？　ありませんよ\n─一緒に過ごせるだけでいい？\nまあ、そんなのでよければ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1354012, 0, "今日は貴方の誕生日、ですか\nそれなら、ここの隣─\n少しだけ譲ってあげてもいいです\n─少しだけ、ですよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135501, 0.0, 1, 0, 1, 0, 3.1, 0, 0, 1, 2, 1355001, 0, "ようこそ、あたしたちの街へ！\nこっちの世界で知りたい事は\nあたしに何でも聞いてね！\nおいしい名物料理とか興味ない？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135501, 0.0, 9, 0, 2, 0, 8.9, 0, 0, 1, 1, 1355002, 0, "乾いた風が気持ちいいでしょ？\nこの街を散歩するには\n涼しさと動きやすさが重要っ♪\nあれ、今見とれちゃってた～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135501, 0.0, 1, 0, 3, 0, 3.5, 0, 0, 1, 6, 1355003, 0, "騎士くん…その格好、暑くない？\nこっちで過ごしやすそうな服、\nあたしが見繕ってあげよっか？\nサラサリアの民族衣装とかさ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135501, 0.0, 8, 0, 4, 0, 6.1, 0, 0, 1, 1, 1355004, 0, "情熱の風が吹くこの国も…やっと\n新しい運命が動き出したんだね\nう～、体も心も熱くなってきた！\n騎士くんもどう？　燃えてる！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135501, 0.0, 2, 0, 5, 0, 4.9, 0, 0, 1, 1, 1355005, 0, "見知らぬ未来の道も\n一緒に歩けるなら楽しみだね♪\nこの足でどこまでも、行けるよ\nドキドキも心配も全部一緒に！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1355006, 0, "みんなの笑顔を守るためなら\n何があっても立ち上がる！\nたとえ誰が相手だって\nあたしは絶対負けないんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1355007, 0, "こーんな大舞台での試合だし、\n力いっぱいぶつかり合おうか！\n怪我させるかもなんて心配は\nいらないよ？　さあおいで！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1355008, 0, "あはは…滾ってきたよ！\nヒヨリちゃんやレイちゃん\nそして騎士くんと手に入れた未来\n心を燃やして生きてくよ～っ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1355009, 0, "騎士くんが応援してくれてる…\nこ～りゃみっともない所は\n見せらんないね！\nよーし、準備も覚悟も万端だっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1355010, 0, "どんなに離れてたって\n騎士くんと絆でつながってる…\nなら、あたしは絶対に負けない！\nそれを証明してみせる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135501, 0.0, 4, 0, 6, 0, 6.2, 0, 0, 2, 8, 1355011, 0, "騎士くん、誕生日おめでと～\nこれでやっと一緒に麦しゅわを…\nってまだダメなんだっけ？\nあ～早く来い…もっと先の未来！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1355012, 0, "今日の武闘会、頑張っちゃうよ！\n騎士くんも応援よろしくね～！\nキミへの誕生日プレゼントに\n優勝賞品ゲットしてくるからさ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135601, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1356001, 0, "よく来たな、私たちの街へ\nゆっくり休んでいってくれ\n私たちに未来をくれた恩人だ\n私も皆も心から歓迎するよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135601, 0.0, 4, 0, 2, 0, 12.3, 0, 0, 1, 6, 1356002, 0, "トーゴクは観光に訪れる者も多い\n名所案内？　私の弟子たちに\n頼んでみよう。彼らなら詳しいし\n私は、ほら…そういうのは苦手だ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135601, 0.0, 2, 0, 3, 0, 5.2, 0, 0, 1, 1, 1356003, 0, "街の人々を導くことが私の役目…\n今は、悪くないと思っている\n大変ではあるがやりがいも多い\nキミと出会えたこともその一つだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135601, 0.0, 5, 0, 4, 0, 8.9, 0, 0, 1, 4, 1356004, 0, "どうした、そんなにまじまじと…\n私は何か変な顔をしていたのか？\n…いや、考え事はしていないが\nぼうっとしているとよく聞かれる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135601, 0.0, 2, 0, 5, 0, 0.0, 0, 0, 1, 2, 1356005, 0, "…あぁ、すまない。少しばかり\n感じ入っていたよ。この未来は\n戦いの末に皆で勝ち取ったもの\nなんだ…とな。大切にしなくては");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1356006, 0, "あぁ…いい夜だ\nたとえここが複製されただけの\n世界でも、月の美しさは同じ…\nキミもそう思うだろう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1356007, 0, "世界のため、人々のため、\n私は強くあらねばならなかった\nそうして手に入れた強さが、\n今は私の誇りになっているんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1356008, 0, "真剣勝負の中でこそ、\n交わすことができる言葉がある…\nなに、声に出す必要はない\n刃は時に雄弁に、心を語るものだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1356009, 0, "剣をぶつけ合い火花を散らす…\nその輝きは一瞬のもの。だが\n互いの心に宿した火は、決して\n消えはしない。希望と同じだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1356010, 0, "今こそこの剣に誓おう\nたとえ隔てられた世界に在っても\n私の心はキミと共にあると、\nかけがえのない仲間として…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135601, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1356011, 0, "今日はキミの誕生日か。方々で\n祝われただろうが、私からも\nおめでとう…そしてありがとう\nキミがいてくれて、よかった…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1356012, 0, "誕生日か…今まで私はその\n価値を知ることもなかった\nだが今は、心からうれしく思うよ\nキミが生まれてくれて、よかった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135701, 0.0, 5, 0, 1, 0, 12.5, 0, 0, 1, 8, 1357001, 0, "この服かわいいと思うけど\nちょっとセクシーすぎないかな…\n騎士くんはこういうの…好き？\nなら…いいけど…っ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135701, 0.0, 8, 0, 2, 0, 12.7, 0, 0, 1, 1, 1357002, 0, "ふー、暑いね騎士くん…そうだ！\nオアシスで水浴びしようよっ！\n冷たい水に浸かれば火照った体も\n…って二人っきりは恥ずかしい…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135701, 0.0, 7, 0, 3, 0, 10.0, 0, 0, 1, 2, 1357003, 0, "たん、たたたん、キメっ！\nえへへ、街の人に教えてもらった\nサラサリアのダンスだよ♪\n騎士くんも一緒に踊ろ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135701, 0.0, 8, 0, 4, 0, 13.7, 0, 0, 1, 6, 1357004, 0, "こんなに熱くなって赤くなって\n汗もいっぱいかいちゃうのは\nサラサリアの太陽のせいかな…？\nそれとも…な、なんでもないっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135701, 0.0, 2, 0, 5, 0, 6.5, 0, 0, 1, 3, 1357005, 0, "あ、あたしだって、いざとなれば\n積極的になるんだからね？\n…猫には狩猟本能があるってこと\n覚えておいてよ？　…えへへへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135731, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1357006, 0, "騎士くんと夜の空中散歩…♪\n幸せだな…この時間がずっと\n続けばってくらい…騎士くんも\n同じ気持ちだとうれしいなぁ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135731, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1357007, 0, "風が気持ちよくて景色もきれいで\n隣に騎士くんがいてくれて…\nうう…憧れが詰まりすぎてて\n心臓が飛び出しちゃいそう！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135731, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1357008, 0, "いつもより笑ったからかな…\nほっぺたが少し痛いや\nもしかして騎士くんも？\nえへへ、お揃いだねっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135731, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1357009, 0, "夜はちょっと冷えるね…\nいつもより肌がでてるからかな…\nえ！？　温めてくれる…っ！？\nそ…その一言で熱くなっちゃった");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135731, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1357010, 0, "言葉だけじゃ伝えきれないね、\n自分の気持ちって…だから\n手を繋いだりして、心の距離を\n少しでも近づけようとするんだね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135701, 0.0, 5, 0, 6, 0, 10.8, 0, 0, 2, 2, 1357011, 0, "お誕生日おめでとう、騎士くん！\n広場で盛大にお祝いしようねっ♪\n食べ物にダンスにそれから…\nまた、一緒に絨毯くんに乗ろ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135731, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1357012, 0, "ハッピーバースデー♪　行きたい\nところがあったら言って！\n絨毯くんとあたしが、どこへでも\n連れていってあげるっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135801, 0.0, 4, 0, 1, 0, 2.6, 0, 0, 1, 1, 1358001, 0, "あ、えっと、その…す、すまね\nひ、人と話すの慣れてねぇで…\nこういうとき、何話せばいいか\nよくわがんねぇんだ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135801, 0.0, 6, 0, 2, 0, 8.1, 0, 0, 1, 1, 1358002, 0, "あての周りにいる子たち…\nみんな、すげぇ子ばっかなんだよ\nあ、あてもみんなみてぇに、って\nいつも思ってっけど、難しいな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135801, 0.0, 5, 0, 3, 0, 8.7, 0, 0, 1, 2, 1358003, 0, "『久遠氷河』はあてらの土地…\n静かで涼しくて、いいとこだよ\nお、おめさも気に入ってくれたら\nうれしいな…なんて、えへへ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135801, 0.0, 1, 0, 4, 0, 9.7, 0, 0, 1, 6, 1358004, 0, "お、おめさは、あての傍にいて…\nさ、寒かったりはしねぇか？\n…だ、大丈夫ならいいけど…\nその…無理はしねぇでくれな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135801, 0.0, 7, 0, 5, 0, 12.2, 0, 0, 1, 2, 1358005, 0, "男の人と話すの、緊張するけど…\n騎士さならちょっと安心するの、\nなんでだろ…って、き、騎士さ？\nい…今の聞いてた？　ひえぇ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135831, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1358006, 0, "あのね、あて…三味線の弾き方は\nお母から教わったんだ…\nこの先どれだけ経ったって、\n絶対に忘れねぇ、宝物だよ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135831, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1358007, 0, "仁太坊、ハルちゃ、竹山さ…\nみんなも手伝ってくんろ\nあてが奏でる、精いっぱいの三味\n騎士さにも、聴いてほしいから…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135831, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1358008, 0, "雪の積もる音、氷の軋む音…\nここも賑やかな場所なんだな\n今まで気づかなかった音も、\n前より良く聴こえてくるよ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135831, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1358009, 0, "あては雪女、身も心も久遠の氷と\n共にある…そう思ってた\nでも…やっぱ求めちまうみてぇだ\nぽかぽかする、騎士さの温もり…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135831, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1358010, 0, "姫ちゃんやサツキちゃん…\nみんなに勇気を教えてもらった\nだからあても、いつか伝えるんだ\n氷に閉じ込めてた、この心を…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135801, 0.0, 5, 0, 6, 0, 11.8, 0, 0, 2, 2, 1358011, 0, "お、おめさの誕生日、かぁ…\nそんなめでてぇ日に、あてと\n一緒にいてくれるの…うれしいな\nあ、あてにもお祝いさせてくんろ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135831, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1358012, 0, "今日は騎士さの誕生日、だな\nあてからも…お祝いの曲、贈るよ\n雪女の一族に伝わる、大事な曲…\n…特別な相手にだけ聴かせるんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135901, 0.0, 1, 0, 1, 0, 4.4, 0, 0, 1, 6, 1359001, 0, "んだよ、妙にガンくれるな\n俺ばっかり見てっと\n足元がお留守になっちまうぜ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135901, 0.0, 1, 0, 2, 0, 6.4, 0, 0, 1, 3, 1359002, 0, "露出しすぎだぁ？\nゴテゴテ鎧う方が動きづれぇだろ\n「鬼に衣」って言うしな、昔から");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135901, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1359003, 0, "俺ら鬼のあいだじゃァ\nケジメは何より大事でな\n図体ばかりデカくても\nスジを通せねぇ奴ぁ三下だぜ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135901, 0.0, 2, 0, 4, 0, 3.8, 0, 0, 1, 1, 1359004, 0, "矢の傷痕が心配だって？\n騎士公に心配されるほど\nヤワな鍛え方してねェよ\n試してみっか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135901, 0.0, 2, 0, 5, 0, 8.1, 0, 0, 1, 1, 1359005, 0, "背負い込んでるモンが\n重いヤツはやっぱり強ぇよ\nけど騎士公、潰れちまいそうなら\n俺に半分寄越しな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1359006, 0, "おう、来たな騎士公\nここがジオ・ニヴルヘルは\n夜叉峠の鬼の村だ\n案内してやっから付いてきな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1359007, 0, "鬼族の若い衆はちっとばかし\n荒っぽいかもしれねェが、\n気のいいヤツらでな\n良くしてやってくれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1359008, 0, "内輪揉めは拳固一発落としゃ済む\nが、外の脅威はそうはいかねェ\n何が来ようと、\n俺が通しゃしねェがな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1359009, 0, "長旅で疲れたろ\nちっと腰を落ち着けながら\n騎士公の土産話を聞かせてくれよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1359010, 0, "面が気になるって？\n気に入っちゃいるが、\n魔法の道具とかじゃねェよ\nほら、お前さんも被ってみっか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135901, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1359011, 0, "誕生日おめでとさん\nひとまず一杯グイっといっとけ\nハレの日なんだ、遠慮するなよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 135931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1359012, 0, "よお、騎士公。ちっとツラ貸せ\n村で一席宴があるんだ\nお前さん、誕生日だろ？\n俺たちが祝ってやるよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136001, 0.0, 4, 0, 1, 0, 0.0, 0, 0, 1, 4, 1360001, 0, "生きている…\nそれだけでも幸運なのはわかって\nいるのです。それでも、求めて\nしまうわたしがいるのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136001, 0.0, 1, 0, 2, 0, 8.0, 0, 0, 1, 4, 1360002, 0, "記憶を失う前でしたら、きっと\nこの世界をご案内できたと思うの\nですが…あ、おいしい虫が獲れる\n場所なら、ご案内できます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136001, 0.0, 2, 0, 3, 0, 7.4, 0, 0, 1, 1, 1360003, 0, "あなたと出会えたこと…\nそれは間違いなく、わたしに\nとっての幸せ。そしてできること\nなら、ずっとあなたのそばに…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136001, 0.0, 1, 0, 4, 0, 10.5, 0, 0, 1, 2, 1360004, 0, "なーちゃんと騎士さまは、\nとても大切な友達。友達の\nためなら、わたしはなんでもする\nと思います。えぇ、なんでも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136001, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1360005, 0, "記憶については今はこのままで…\n過去の行いのことを思うと、\n記憶を取り戻したとき、わたしが\nどうなるか分かりませんので…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1360006, 0, "あぁ…ここは冷たい…\nそれに、何も思い出せません…\n誰か…いらっしゃいませんか\nわたしを救ってくれる方は…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1360007, 0, "寒い…一人でいるというのは、\nこんなにも寒いものなのですね\nぬくもりが欲しい…耐えがたい…\nあぁ…騎士さま…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1360008, 0, "お腹が空いて、空いて…\nほんとうに、苦しいです…\n…でも、本当に飢えているのは\n体より、心の方なのかも…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1360009, 0, "あたたかい光の先には、なにが\n待っているのでしょう…安全な\n場所から動くのは怖い…それでも\nこの光に惹かれてしまうのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1360010, 0, "騎士さまとお会いできなければ\nわたしの心は闇に囚われたまま…\n命がけでお守りし、お仕えします\n騎士さま…わたしの光");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136001, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1360011, 0, "お誕生日、おめでとうございます\nあなたが生まれた日にご一緒\nできたことを、うれしく思います");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1360012, 0, "誕生日…それはとても素敵な日\n本人にとっても、周りの人に\nとっても、きっと。わたしも、\nその輪の中に入れたら…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136101, 0.0, 2, 0, 1, 0, 4.8, 0, 0, 1, 1, 1361001, 0, "私はサクラ！　今回も訳あって、\nランドソルでソロ活動中！\nこの素敵な街で、私の毎日、\nまた何かが変わるかもしれんね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136101, 0.0, 7, 0, 2, 0, 4.9, 0, 0, 1, 6, 1361002, 0, "ゾンビは…アストルムでは\n珍しくなかと？　モ、モンスター\nやなかとよ！　倒しても経験値に\nならんばいっ。討伐せんで～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136101, 0.0, 4, 0, 3, 0, 8.7, 0, 0, 1, 10, 1361003, 0, "はっ。ズ、ズレとる…\nどやんすどやんす…\nこ…これで…よしっと、ふう\nまた取れるとこやった、首が");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136101, 0.0, 2, 0, 4, 0, 4.8, 0, 0, 1, 1, 1361004, 0, "騎士さんもサガば好きになって、\nそれからフランシュシュの…\n私のファンになってくれたら\nうれしかね、えへへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136101, 0.0, 6, 0, 5, 0, 0.0, 0, 0, 1, 6, 1361005, 0, "騎士さん、「持っとる」って\n言われません？　私の知っとる\n人にどっか似とるっていうか…\n性格は違うとに…不思議やね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1361006, 0, "おはようございまーす！\nフランシュシュ１号のライブ\nですっ！　伝説が、ランドソルに\nやってきました！　聴いとって！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1361007, 0, "サガの街はよかとこやけど、\nランドソルも治安のよか！\nここで安心安全に\n生きていくとも素敵かもっ…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1361008, 0, "騎士さんのおかげで、\nライブは大成功の予感っ\n天気も最高やし、今すぐにも\n素敵なことが起こりそうっ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1361009, 0, "騎士さんって、いつも\n女の子に囲まれとらん…？\nもしかしてアイドル向きかもっ\nん～、あの人に会わせたか～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1361010, 0, "だめですよ、騎士さんっ\nみんなのハートに\n火ばつけたっちゃけん、\n責任ば取らんとっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136101, 0.0, 2, 0, 6, 0, 6.8, 0, 0, 2, 1, 1361011, 0, "お誕生日おめでとうございます！\nサガでももちろん、こやんか日は\nメンバー皆でパーティーやけんっ\nえへへ…ワクワクしてきたと♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1361012, 0, "私、今日は騎士さんのために、\n心ば込めて歌うけんっ！\nハッピーバースデー、サガ！\nえへへ、縁起のよか歌ばいっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136201, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1362001, 0, "ごっはん～、ごっはん～♪\n今日の訓練はいつもより\nがんばったから、ごほーびに\nごはんいっぱいもらっちゃおっ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136201, 0.0, 2, 0, 2, 0, 6.2, 0, 0, 1, 1, 1362002, 0, "ナーナね、かけっこは得意なの！\n「お逃げ足だけは一人前ですわ」\nって、前にカノンちゃんにも\nほめてもらったんだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136201, 0.0, 1, 0, 3, 0, 6.3, 0, 0, 1, 7, 1362003, 0, "ジオ・ゲヘナはきびしーとこだし\n怖い魔物がたくさんいるけど…\nお友だちもたくさんいるから、\n毎日楽しいんだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136201, 0.0, 2, 0, 4, 0, 9.4, 0, 0, 1, 1, 1362004, 0, "甘い物はね、おいしいだけ\nじゃなくて、みんなで食べると\nいっぱいうれしくなるんだよ\nだから騎士ちゃんも、どーぞっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136201, 0.0, 2, 0, 5, 0, 5.5, 0, 0, 1, 4, 1362005, 0, "ナーナはまだまだダメダメで\nよわっぴぃウサギさんだけど…\n騎士ちゃんが応援してくれるから\nいっぱいがんばれちゃうんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1362006, 0, "ぴえぇ～…びしょびしょだよぉ…\nあっ、でもこれなら、思いっきり\n水遊びしても怒られないかも？\nえへへ、ナーナあったまいい～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1362007, 0, "んむ、おくちにお水が…あれ？\nこのお水…あまあまだぁ…\nえっ、これ全部あまあまなの？\nほわわわぁ～…ご、ごくり…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1362008, 0, "クルルちゃんはナーナのいとこで\n大切なお友だちなんだよっ♪\n住んでる世界は離れてても、\nココロはず～っと一緒なんだっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1362009, 0, "ナーナだけびっしょりなの、\nちょっと恥ずかしいかもぉ…\n騎士ちゃんも、どぼーんって\nしちゃお！　絶対たのしーよっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1362010, 0, "遊園地でいーっぱい遊んで\n甘いお池でパシャパシャして…\n毎日こんなだったらいーのになっ\nもちろん騎士ちゃんも一緒に♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136201, 0.0, 9, 0, 6, 0, 8.9, 0, 0, 2, 1, 1362011, 0, "騎士ちゃん、お誕生日なの？\nじゃあ今日のナーナのおやつ、\n騎士ちゃんにも半ぶ…\nぜ、ぜんぶあげちゃうっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1362012, 0, "騎士ちゃんのお誕生日、\nナーナにもお祝いさせてねっ\n騎士ちゃんが笑顔になったら\nナーナだってうれしいもん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136301, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1363001, 0, "メリークリスマス、騎士さん♪\n聖夜騎姫★チャーミングスカイが\nクリスマスの街をロマンチックに\nイイカンジに彩ってみせますよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136301, 0.0, 2, 0, 2, 0, 9.6, 0, 0, 1, 1, 1363002, 0, "クリスマスらしく白でふわふわな\n衣装に着替えてみました★\n赤色はサンタさんとユイさんに\n譲ってあげようかな、みたいな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136301, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1363003, 0, "わたし、クリスマスは騎士さんの\n隣で過ごすって決めてたんです♪\n倍率高めの場所なのでワンチャン\n修羅場と化して楽しそうですし★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136301, 0.0, 2, 0, 4, 0, 8.8, 0, 0, 1, 1, 1363004, 0, "いいですか、騎士さん\nクリスマスを一緒に過ごすって\n乙女にとって特別なコトなんです\nそれぐらい好きってコトですよ★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136301, 0.0, 2, 0, 5, 0, 8.2, 0, 0, 1, 1, 1363005, 0, "イイコの靴下を枕元に吊るしたら\n悪い子なわたしでもプレゼント\n貰えますかね～？　それじゃあ\n騎士さん、靴下脱いでください★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1363006, 0, "あはっ★　クリスマスだからって\nダイタン過ぎましたかねー？\nでも、ふたりっきりの聖夜ですし\nイイコトしないとですよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1363007, 0, "この状況、誰かに見られると\n勘違いされるかもですね♪\nもし修羅場になったら\nわたしを選んでくれますか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1363008, 0, "騎士さん、この服何点ですか？\n100点以下だったら\nいっそ脱いじゃおっかなーって\n…何点か、教えてください★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1363009, 0, "最近いっぱいイイコトしてたぶん\n反動が来ちゃったかもです★\n騎士さんも悪い子になるまで\nぜーったい逃がしませんよ♥");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1363010, 0, "メリークリスマス、騎士さん♪\nプレゼント、何がほしいですか？\nわたしのファーストキスなら\n今すぐプレゼントできますよ…★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136301, 0.0, 1, 0, 6, 0, 6.0, 0, 0, 2, 2, 1363011, 0, "騎士さん、ハッピーバースデー★\nパーティーの用意は万端ですよ\nランファさんたちも待ってるので\n早速、テレポーテーションです★");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1363012, 0, "誕生日、おめでとうございます♪\n今日が特別な思い出になるように\nわたしがシゲキ的でキケンな\nひと時をプレゼントしますね♥");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136401, 0.0, 2, 0, 1, 0, 7.9, 0, 0, 1, 1, 1364001, 0, "騎士クンに見てもらいたくて\n精いっぱいおめかししてみたの\n似合ってる？　ふふ、うれしい！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136401, 0.0, 1, 0, 2, 0, 6.4, 0, 0, 1, 6, 1364002, 0, "街の飾りつけが変わってくると\nみんなそわそわしちゃうよね\nふふっ、わたしも同じだよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136401, 0.0, 2, 0, 3, 0, 3.0, 0, 0, 1, 5, 1364003, 0, "騎士クンと過ごすクリスマス…\nドキドキで体が熱くなりそう\nおかげで寒さも平気だよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136401, 0.0, 7, 0, 4, 0, 6.3, 0, 0, 1, 1, 1364004, 0, "騎士クン、最近気になることや、\n欲しいものはない？\n…ふ、深い意味はないよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136401, 0.0, 6, 0, 5, 0, 10.1, 0, 0, 1, 1, 1364005, 0, "わたし本当は、欲張りなの\n騎士クンとやりたいこと、\n少しずつ叶えていきたいんだ\n…いいかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1364006, 0, "雪のクリスマスって、いいよね\nふわふわの雪に背中を押されて\nいつもより大胆になれちゃうの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1364007, 0, "わたし、いつもと雰囲気違う？\nえへへっ、うれしいな\n今日は騎士クンに見せたくて\nおめかししたの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1364008, 0, "騎士クン、いつもより頼もしくて\nカッコよく見えちゃう…\nこれもクリスマスの…ううん\nデートの魔法、かな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1364009, 0, "冬の夜って、好き\nこんなにくっついてても、\nちっとも不自然じゃないし…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1364010, 0, "この時間が終わっても\n明日も明後日もその先も、\n騎士クンと一緒にいたいの…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136401, 0.0, 5, 0, 6, 0, 3.8, 0, 0, 2, 2, 1364011, 0, "騎士クン、お誕生日おめでとう♪\nパーティーなら任せて\nまごころこめて用意するから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1364012, 0, "お誕生日おめでとう♪\nほら、星も街の明かりも、\nお祝いしてくれてる…\nって、ロマンチックすぎかな？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136501, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1365001, 0, "あけましておめでとう！　弟くん\nはい、お年玉♪　足りなかったら\n追加してあげるから、遠慮なく\nお姉ちゃんに甘えてねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136501, 0.0, 1, 0, 2, 0, 2.9, 0, 0, 1, 2, 1365002, 0, "お姉ちゃん特製おみくじ～♪\nなんと大吉より上があるんだよ！\nそれは…『弟吉』！　引いたら\n最高の一年になっちゃうよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136501, 0.0, 2, 0, 3, 0, 9.2, 0, 0, 1, 1, 1365003, 0, "お正月の空気って、ワクワクして\nきちゃうよね♪　初詣、お年玉、\nおせちにお正月遊び…特別な\nイベントが多いからかもね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136501, 0.0, 2, 0, 4, 0, 2.8, 0, 0, 1, 6, 1365004, 0, "弟くん、寒くない？　いつでも\nどこでも、お姉ちゃんが温めて\nあげる！　体だけじゃなくて、\n弟くんの心もねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136501, 0.0, 5, 0, 5, 0, 2.7, 0, 0, 1, 2, 1365005, 0, "着物のお姉ちゃんもいいでしょ？\n弟くんなら、もっと近くで見て\nいいんだぞ？　ほらほら～♪\n襟のところとか色っぽいでしょ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1365006, 0, "弟くんが元気に過ごせますように\n私と弟くんがもっと一緒にいられ\nますように…本年も、どうか\nよろしくお願いします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1365007, 0, "お正月はゆっくりしようね。\n年末の忙しさを忘れちゃうくらい\n食べて休んで…そして、\n甘やかしてあげるっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1365008, 0, "今年の抱負は弟くんを去年以上に\n甘やかすこと！　…実はほぼ毎年\n同じ抱負なんだけど、これが私の\n本音なんだもん仕方ないよね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1365009, 0, "まるで神前式をしてるみたい…♪\nそれも、相手は弟くん！\nたまには呼び方を変えるのもアリ\nかも？　たとえば…あ・な・た♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1365010, 0, "着物姿の弟くんもかっこいい…♪\nって、いけないいけない…初詣で\n来てるのに、弟くんを見ちゃう…\nはぁぁ…やっぱりかっこいい…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136501, 0.0, 1, 0, 6, 0, 7.0, 0, 0, 2, 2, 1365011, 0, "ハッピーバースデー♪　おせちを\n用意したから一緒に食べようね！\n縁起がいいものもいっぱいだよ\n数の子とかレンコンとか！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 136531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1365012, 0, "お誕生日おめでとう！\nプレゼントの前にお参りに行こ？\nこの一年と次の一年とその次の\n一年の、お礼とお願いをしにねっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137101, 0.0, 2, 0, 1, 0, 3.1, 0, 0, 1, 3, 1371001, 0, "忍者とは忍ぶ者…\nなので、海では忍び装束よりも\n水着のほうが忍べマース！\nニンニンデース♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137101, 0.0, 6, 0, 2, 0, 6.4, 0, 0, 1, 1, 1371002, 0, "新しい印結びを見てクダサイ！\n指をこうして…ショーグン？\nなんで目を逸らすデス？\n目のやり場に困る…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137101, 0.0, 2, 0, 3, 0, 8.4, 0, 0, 1, 1, 1371003, 0, "ついにショーグンに見せる時が\n来たようデスね…\n海でしか使えない、とっておきの\n忍法・スイカ割りを！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137101, 0.0, 1, 0, 4, 0, 10.5, 0, 0, 1, 2, 1371004, 0, "たとえ火の中、海の中、どこでも\nショーグンのお供をいたしマス！\nチューシン、ニノンここにあり！\n…デス♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137101, 0.0, 7, 0, 5, 0, 5.8, 0, 0, 1, 1, 1371005, 0, "この夏、この海で！\nまた新しい忍法を編み出しマス！\nショーグンと天下統一のため、\n一肌脱ぎマスよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1371006, 0, "すごいすごい、すごいデース！\n水がキラキラしてキレイで…\nショーグンのくれたこの景色…\n最高のゴホウビデス♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1371007, 0, "新たなる忍法を思いつきまシタ！\n水手裏剣の術！　シュババババッ\nこれでショーグンの夏を涼しく\n彩りマース♪　いかがデスか？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1371008, 0, "フーコーメービな隠れ里で\nショーグンと二人、忍術に興じる\nなんて素敵な夏の過ごし方…♪\n時を止める忍法が欲しいデス");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1371009, 0, "涼しくて気持ちいいデース♪\nショーグンも一緒に水浴び\nしまセンか！　水も滴るいい\nショーグン…見惚れそうデス…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1371010, 0, "水遊びの後は冷えないように\nお気をつけデスよ？　風邪は\nマンビョーの元デス。そうだ、\nこの後サウナで温まりまショウ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137101, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1371011, 0, "めでてえめでてえめでてえな♪\nショーグンがいい一年だったと\n思えるように、チューシンとして\nもっともーっと頑張るデスよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1371012, 0, "お誕生日おめでとうゴザル！\n今日のために新しく編み出した\n忍法をお披露目しちゃいマス！\nそれそれー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137401, 0.0, 1, 0, 1, 0, 4.9, 0, 0, 1, 2, 1374001, 0, "真実の信奉者にしてシスター\nここに推参！　…なーんてね\nこの装い、心まで清らかになるよ\nふふ、似合ってるかな、助手くん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137401, 0.0, 6, 0, 2, 0, 4.4, 0, 0, 1, 1, 1374002, 0, "このベール、耳が出せるのが\n機能的だね♪　え？　時々動く\nのがかわいいって？　いきなり\n何を言い出すんだいっ！？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137401, 0.0, 5, 0, 3, 0, 10.6, 0, 0, 1, 4, 1374003, 0, "シスターたるもの恋をしたり\n誰かと結ばれたりは禁忌なのかな\nそれはちょっと…すごく困るね…\nあ、ああいや、こっちの話さ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137401, 0.0, 2, 0, 4, 0, 4.8, 0, 0, 1, 1, 1374004, 0, "悩みがあるなら、私の耳が\nとことん聞き届けるよ\n解決は二人で一緒にね。君の\n悩みは私の悩み。そうだろう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137401, 0.0, 4, 0, 5, 0, 3.2, 0, 0, 1, 6, 1374005, 0, "ずっしりと重いね、この本は\n抱いていると、真実に責任を持つ\n者としての実感が湧いてくるよ\n読破するまで何日かかるかな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1374006, 0, "厳かな光の中で、誰かを想い祈る\nのは、自分自身の真実と向き合う\nことと同じく尊い時間だ。隣に\n君がいてくれるなら、なおさらね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1374007, 0, "なんてきれいな聖堂だろうね\nいつかこんな場所で君と永遠を\n誓い合うことができたなら…\nあ、いや、その…今のナシでっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1374008, 0, "こんな時よく思い出す\nいつも私を見守ってくれる\n大切な友達を。君も、ときどき\n思い出してくれたらうれしいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1374009, 0, "現実がいつも残酷だとしても\n誰かの心を映す真実が\n優しいものであることを祈るよ\nこの鏡に誓って、ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1374010, 0, "きっと私は、とても幸せな人間だ\n助手くん、君というパートナーが\nいつも背中を、心を支えてくれる\nこれ以上の祝福はないよ、ふふっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137401, 0.0, 2, 0, 6, 0, 7.9, 0, 0, 2, 1, 1374011, 0, "祝福の日、来たれり。そう、君が\nこの世に生を受けた特別な…\nふふっ、堅苦しかったかな♪\nハッピーバースデー、助手くんっ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1374012, 0, "祈らせてほしい。この世界に君が\n生まれてきてくれたこと…\nそれがみんなに、そして私に\nとって、どれほどの幸いだったか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137601, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1376001, 0, "新たな姿、いかがでしょうか？\n魔力による変身、時間の限られた\nものですが…主さまに尽くす心も\nよりパワーアップしておりますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137601, 0.0, 2, 0, 2, 0, 6.2, 0, 0, 1, 1, 1376002, 0, "大人の姿を手に入れはしましたが\nそれで満足してはいけませんね\n見た目だけでなく内面も成長し、\n誇れる自分になれるよう励みます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137601, 0.0, 2, 0, 3, 0, 9.9, 0, 0, 1, 1, 1376003, 0, "ただ強くなりたいと願うだけでは\n本当の力は得られません…\n確かな願いとともに力を望むこと\nわたくしはそう学びました");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137601, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1376004, 0, "体が大きくなると、周りの景色も\nまるで違って見えるのですね\n素晴らしいのは、主さまのお顔が\nとても近くなったことです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137601, 0.0, 7, 0, 5, 0, 9.9, 0, 0, 1, 2, 1376005, 0, "わたくしはこれまでと変わりなく\n主さまに寄り添い支えていきます\nそれは使命や責務ではなく…\nわたくし自身の望みですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1376006, 0, "『嚮導幼君』の権能は、\n理の破壊と再構成を行う禁断の力\nしかし世界を救うためならば\nこの上なく頼もしい力となります");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1376007, 0, "世界を巡る精霊たちの声が、\n以前よりもはっきりと聞こえます\n景色も音も、鮮やかになって…\nその美しさに感じ入るほどです…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1376008, 0, "七冠であるお父さまに比べれば\nわたくしは未熟な存在でしょう\nそれでも守れるものがあるならば\n戦うことに迷いなどありません…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1376009, 0, "この力はお父さまから継いだもの\nしかし力を目覚めさせたのは、\nわたくしが抱いた何よりの願い…\n主さまを愛する心に他なりません");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1376010, 0, "主さまがわたくしを信じ、\n運命さえも委ねてくださる…\nそれが何より心強いのです\nその想いに、必ず応えてみせます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137601, 0.0, 7, 0, 6, 0, 0.0, 0, 0, 2, 7, 1376011, 0, "本日は主さまの誕生日ですね\nぜひお祝いさせてくださいまし\nお料理、添い寝、ハグになでなで\n何でも用意しておりますので♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1376012, 0, "お誕生日、おめでとうございます\nこれからも共に経験を積み重ね\nより大きく、さらに大きく\n成長をして参りましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137901, 0.0, 2, 0, 1, 0, 3.5, 0, 0, 1, 1, 1379001, 0, "晴れ着姿の妹はどうですか～？\n気合い入れて作ったんですよ☆\n笑う門には福着たる、です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137901, 0.0, 1, 0, 2, 0, 5.0, 0, 0, 1, 2, 1379002, 0, "お正月と言えば、やっぱり\nおみくじは欠かせませんよね！\n今年の運勢は…どんな結果でも\nお兄ちゃんと一緒なら大吉です！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137901, 0.0, 2, 0, 3, 0, 2.5, 0, 0, 1, 1, 1379003, 0, "今年はどんな年でしょうね？\n私は、お兄ちゃんとたくさん\n一緒にいられる年になって\nくれると思ってます☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137901, 0.0, 2, 0, 4, 0, 6.8, 0, 0, 1, 1, 1379004, 0, "羽子板、投げゴマ、福笑い…\n正月遊びはいっぱいありますよ！\nコンプリート目指して、\n私と遊びましょう、お兄ちゃん♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137901, 0.0, 4, 0, 5, 0, 6.4, 0, 0, 1, 6, 1379005, 0, "毎年書き初めをすると、なんでか\nみんな首を傾げるんですよね～\n私、何か変なことでも\n書いてるんでしょうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137931, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1379006, 0, "絵馬に書いた願い事、ですか？\nそれは、ええっと…秘密ですっ！\nきっと叶うって信じてるから、\nそしたら教えてあげますねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137931, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1379007, 0, "きれいな雪景色ですねっ！\nお参りにも気合が入りますっ\nお願いは一つだけ、お兄ちゃんと\n一緒に…あっ内緒だったのに！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137931, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1379008, 0, "こうしてお正月から一緒に\n過ごせるなんて、今年の運勢は\n大吉決定です♪　お兄ちゃんも\n妹が充実した一年になりますね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137931, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1379009, 0, "楽しいですね、初詣デート♪\nお参りして、おいしいもの食べて\nお正月遊びをしてこたつに入って\nずーっと一緒ですよ、お兄ちゃん");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137931, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1379010, 0, "やっぱり冷えますね。絵馬を\n掛けたら、手を温めてあげます♪\n今年の私は頼りになる妹ですから\nどーんと頼ってくださいね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137901, 0.0, 1, 0, 6, 0, 6.1, 0, 0, 2, 2, 1379011, 0, "お誕生日おめでとうございます☆\n今年もいい年になりますように…\nいえっ、私がいい年にします！\nなんてったって頼れる妹ですし！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 137931, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1379012, 0, "ハッピーバースデーお兄ちゃん！\nパーティーの準備はばっちりです\nケーキに餅つき、蠟燭に初詣♪\nいいとこどりして楽しみますよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138001, 0.0, 2, 0, 1, 0, 9.6, 0, 0, 1, 1, 1380001, 0, "食と文化の祭典、ランドソル万博\nジオ・二ヴルヘルも国を挙げ\n準備してまいりました。どうか\n皆様にご満足いただけますように");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138001, 0.0, 2, 0, 2, 0, 6.7, 0, 0, 1, 6, 1380002, 0, "姫として万博に参加するのは\n少しだけ緊張しますが…\n会場には騎士様がいてくださる…\n…ええ、きっと大丈夫ですわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138001, 0.0, 1, 0, 3, 0, 8.3, 0, 0, 1, 4, 1380003, 0, "この装い、身のこなしに気を付け\nないと…見えてしまいそうで…\n…いえ、これはいわば正装です\n恥ずかしがってはなりませんね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138001, 0.0, 2, 0, 4, 0, 11.2, 0, 0, 1, 1, 1380004, 0, "騎士様のおかげで民と国の矜持を\n守ることができました。あなたの\n優しさに頼ってばかりですが…\nいつかお返しできたら…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138001, 0.0, 5, 0, 5, 0, 5.1, 0, 0, 1, 1, 1380005, 0, "騎士様は、手作りのお菓子は\nお好きでしょうか？　…実は\nわたくし、お菓子作りにも興味が\nあって…あの、いかがですか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138031, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1380006, 0, "大いなる海神よ、イサナミよ\n今わたくしの祈りに応え、\n万物に慈悲をお与えください\nそしてこの手に、民を照らす力を");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138031, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1380007, 0, "海は太古より万物を育むと同時に\n数多の命を飲み込んできました\n生と死のあわい…その在り方は\n我が国に近いのかもしれませんね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138031, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1380008, 0, "あなたたちも、わたくしに\n力を貸してくれるのですか？\n…ふふ、ありがとうございます\nでは、共に参りましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138031, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1380009, 0, "騎士様も慈愛の心をお持ちの方\nその魂が曇ることないよう、\nわたくしがお守りいたします\nどんなときも、ずっと");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138031, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1380010, 0, "騎士様が水底に堕ちたときは\n必ずお助けすると誓います\nこれは姫としての責務ではなく\nわたくし個人の…約束、ですわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138001, 0.0, 1, 0, 6, 0, 7.2, 0, 0, 2, 2, 1380011, 0, "おめでとうございます、騎士様\nお誕生日祝いの料理をどうぞ\n数か月前から練習を重ねたので…\nほんの少しだけ、自信作です…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138031, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1380012, 0, "騎士様、お誕生日おめでとう\nございます。これからもこの日を\n二人でお祝いできれば…などと、\n少々贅沢すぎるでしょうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138101, 0.0, 1, 0, 1, 0, 8.1, 0, 0, 1, 2, 1381001, 0, "次は『ジオ・テオゴニア万博』を\n開催したいですね。国民もきっと\n歓迎してくれます。わたしも腕に\nよりをかけてお菓子を作りますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138101, 0.0, 7, 0, 2, 0, 7.2, 0, 0, 1, 2, 1381002, 0, "争いは好みませんが、国を負った\n勝負となれば話は違います…\nええ、つい熱くなってしまうのは\n副賞のためでは、決して…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138101, 0.0, 2, 0, 3, 0, 8.1, 0, 0, 1, 1, 1381003, 0, "各世界の姫やシュヴァリエとの\n交流は、危機の中ではあっても\n胸躍る時間でした。他国の\n民たちとの交流も、同じように");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138101, 0.0, 2, 0, 4, 0, 9.2, 0, 0, 1, 1, 1381004, 0, "こうして、プリンセスとして立つ\nわたしの隣にあなたがいてくれて\nとても心強く感じます。ずっと\n一緒に…と、望んでしまいますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138101, 0.0, 5, 0, 5, 0, 0.0, 0, 0, 1, 5, 1381005, 0, "あなたにお菓子を食べてもらう\n時間は特別で、少し気恥ずかしく\n胸がドキドキしてしまうのです\n誰にも内緒ですよ…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1381006, 0, "ジオ・テオゴニア─\nわたしの愛するこの世界が、\nわたしに力をくれるのを感じます\nどんな敵からも護ってみせますよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1381007, 0, "少し羽が硬くなりましたが…\nこれならクローチェのように\n素早く飛ぶことができますね\nさぁ、掴まってください騎士");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1381008, 0, "平和を脅かす者は誰であろうと\n許しません。ジオ・テオゴニアの\nプリンセスとして、皆のために\nこの力を振るいましょう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1381009, 0, "ただ傲慢なだけなら、裸のプリン\nセスになっていたはず…ですが、\n助言をくれる存在がいるから、\nわたしはわたしでいられるのです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1381010, 0, "時折、自分がひどく傲慢な者だと\n気づかされます。あなたを帰さな\nい方法を考えてしまって…大丈夫\n実行はしませんよ…今は、まだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138101, 0.0, 1, 0, 6, 0, 2.6, 0, 0, 2, 2, 1381011, 0, "お誕生日おめでとうございます\nジオ・テオゴニアで採れた\nフルーツをたっぷり使った\nケーキを、どうぞ召し上がれ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 138131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1381012, 0, "今日は騎士の誕生日という特別な\n日。一緒に空中散歩はどうですか\nジオ・テオゴニアの美しい風景を\nあなただけにプレゼントしますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180101, 0.0, 2, 0, 1, 0, 4.2, 0, 0, 1, 1, 1801001, 0, "チェンジ、プリンセスフォーム！\n…くぅぅー！　やっぱり変身って\nすっごくワクワクするよね！\n騎士くん、一緒にポーズとって！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180101, 0.0, 4, 0, 2, 0, 0.0, 0, 0, 1, 4, 1801002, 0, "ううぅ…変身した後は\nお腹が減るって言ってたけど、\nほんとなんだね…\nご飯まだかなぁ…お腹空いたな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180101, 0.0, 2, 0, 3, 0, 7.8, 0, 0, 1, 1, 1801003, 0, "この力で、世界中の人たちを\n助けにいくよ！　騎士くんも\n何かあったら言ってね？\n困った時はお互いさまさま♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180101, 0.0, 2, 0, 4, 0, 11.0, 0, 0, 1, 6, 1801004, 0, "えっと…騎士くん\n聞いていいかな？　このカッコ…\n…え？　似合ってる…？\nえへへっ、ほめられちゃった♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180101, 0.0, 2, 0, 5, 0, 10.1, 0, 0, 1, 1, 1801005, 0, "騎士くん、ちょっとほっぺ触るね\nどうどう？　肉球、\nいつもよりあったかいでしょ？\nこれぞ、プリンセス肉球だー♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180131, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1801006, 0, "うにゃにゃにゃにゃー！\nパワーが溢れて止まらない！\nこれがあたしと騎士くんの！\nプリンセスフォームの力だぁー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180131, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1801007, 0, "すごい力だよね…だけど、\nあたし、使いこなしてみせるよ！\n大切な人たちに、\nもう傷ついて欲しくないもん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180131, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1801008, 0, "あたしの姿が変わっても、\n騎士くんはそのままなんだ…あ！\nこれと同じ服作る？　そしたら\n一緒にプリンセスフォームだよ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180131, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1801009, 0, "変身するとね？\n胸の奥が燃えてるみたいに\n熱くなるんだ…\n騎士くん、あたしに何かした？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180131, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1801010, 0, "この力は、騎士くんと一緒に\nいないと使えないんだよね？\nじゃあ、ずーっといちゃおっかな\n…じょ、冗談！　冗談だよー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180101, 0.0, 1, 0, 6, 0, 5.9, 0, 0, 2, 2, 1801011, 0, "お誕生日おめでとう、騎士くん！\nお祝いになんでも食べて！\n串焼き…リンゴ飴…うーん、\nそうだ！　全部食べちゃおー！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180131, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1801012, 0, "ハッピーバースデー、騎士くん！\nあたしにしてほしいこと、ない？\n今日は騎士くんのためだけに\n超頑張っちゃうよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180201, 0.0, 7, 0, 1, 0, 3.5, 0, 0, 1, 1, 1802001, 0, "チェンジ・プリンセスフォーム…\nあれ！？　変身しちゃってる！？\n騎士クン！　今のはちょっと、\n言ってみたかっただけで…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180201, 0.0, 2, 0, 2, 0, 9.5, 0, 0, 1, 1, 1802002, 0, "変身したらお腹が減るけど\n何かを頑張った後のごはんって\n美味しいから、楽しみだよね\n今日は何を食べようかな…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180201, 0.0, 7, 0, 3, 0, 13.3, 0, 0, 1, 6, 1802003, 0, "この力を使ってる時、騎士クンの\n格好は変わらないんだね…\nその…わたしがプリンセスなら\n王子様みたいに…なんでもない！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180201, 0.0, 5, 0, 4, 0, 5.3, 0, 0, 1, 1, 1802004, 0, "騎士クンのためなら、わたしは\nどんなことが起きても平気だよ\n騎士クンと一緒にいられて、\n騎士クンが笑顔でいてくれたら…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180201, 0.0, 7, 0, 5, 0, 3.3, 0, 0, 1, 6, 1802005, 0, "騎士クンと二人でパワーアップ…\nううぅ～…すごくうれしくて、\n胸の奥の気持ちが溢れ出そう\nこれも力の影響なのかな…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1802006, 0, "たとえこの先に、何があっても…\n騎士クンを、みんなを守りたいの\nわたしの、全てをかけて！\nこの思いは…誰にも負けない！　");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1802007, 0, "これだけの大きな魔力、\n本当に使いこなせるのかな…\n…ううん、わたしはもう迷わない\nこの瞬間に、全力を出し切る！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1802008, 0, "戦うのは、わたしにまかせて！\n今の幸せも、未来の幸せも\n壊させたりしないよ…！\n今度こそ！　必ず！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1802009, 0, "騎士クンのあったかい力\n前よりもすごく強くなってる…\nわたしの気持ちも、同じだよ\n騎士クンの気持ちは…どう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1802010, 0, "戦いや普段の時だけじゃなくて\nもっと色んな時間を騎士クンと\nずっっと一緒に過ごしたい…\n…まずは、食事に誘わなくちゃ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180201, 0.0, 5, 0, 6, 0, 6.5, 0, 0, 2, 2, 1802011, 0, "お誕生日おめでとう！\n今日はずっと騎士クンを\nお祝いしてあげたいの\nだから、そばにいてもいい…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1802012, 0, "騎士クン、お誕生日おめでとう！\n…お祝いの気持ちだけじゃなくて\nもう一つ、伝えたいことがあるの\n…やっぱり言えないよぉ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180301, 0.0, 2, 0, 1, 0, 4.5, 0, 0, 1, 6, 1803001, 0, "プリンセスフォーム…\nすごい力だね。ありがとう\nキミのおかげで私はまた\n一つ強くなれたよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180301, 0.0, 6, 0, 2, 0, 3.8, 0, 0, 1, 3, 1803002, 0, "チェンジ！　プリンセスフォー…\nキミ！？　いつからそこに！？\n今のはその、かけ声の練習で…\n…え？　もっと凛々しく？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180301, 0.0, 1, 0, 3, 0, 4.6, 0, 0, 1, 2, 1803003, 0, "変身できるからといって\n日々の鍛錬は欠かさないよ\nそうだ。キミも一緒にどう？\n特別メニューを組んであるんだ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180301, 0.0, 6, 0, 4, 0, 5.6, 0, 0, 1, 4, 1803004, 0, "やっぱり私が着るには、\nヒラヒラしたものが多すぎる…\n似合ってる？　そうかな…\n…ねえキミ。全部聞いてたの？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180301, 0.0, 6, 0, 5, 0, 9.2, 0, 0, 1, 2, 1803005, 0, "この姿でいると、まるでキミに\n抱き締められているようだ\nふふっ、冗談だよ…キミ？\nじ、実際にはしなくていいから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180331, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1803006, 0, "見ていてくれ！\nこれこそ私の思いが…\n私たちの力が\n一つになった証だ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180331, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1803007, 0, "何度強敵が立ちはだかろうとも\n私は決して挫けない！\n世界の滅びを食い止めてでも\nキミのそばにいてみせる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180331, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1803008, 0, "限界なんてものはない…\nキミとの絆が私に教えてくれたよ\nだから私は剣を振るい続ける！\nあらゆる困難を断ち切るために！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180331, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1803009, 0, "キミへの思いが強さになるなら\nもっと早く素直に\nなっておけばよかったよ…\nどんな思いか？　そ、それは…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180331, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1803010, 0, "私とキミ…お互いへの気持ちを\nより強くするにはどうしたら…\n剣を交える以外の方法を\n見つけないといけないな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180301, 0.0, 5, 0, 6, 0, 7.6, 0, 0, 2, 1, 1803011, 0, "今日はキミの誕生日だ\nプレゼントを贈らせてほしいから\n一緒に買い物へいかない？\n…変身を解いてからね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180331, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1803012, 0, "誕生日おめでとう！\nキミを祝えて私もうれしいよ\n次の誕生日もまた\nキミの隣で祝えるといいな");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180401, 0.0, 1, 0, 1, 0, 4.5, 0, 0, 1, 2, 1804001, 0, "オイッスー☆\n一緒にお散歩、行きませんか？\n王女様なんて呼んじゃだめですよ\n今のわたしはペコリーヌです☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180401, 0.0, 2, 0, 2, 0, 10.9, 0, 0, 1, 6, 1804002, 0, "いつもと違うこの姿、\nあなたはどう思いますか？\n強そう？　う～ん、そっちですか\nかわいい？　えっへへ、それです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180401, 0.0, 2, 0, 3, 0, 7.1, 0, 0, 1, 1, 1804003, 0, "王宮から抜けだして会っていると\nなんだかドキドキしますね♪\nお付きの護衛もいませんから\nあなたが守ってくださいね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180401, 0.0, 1, 0, 4, 0, 4.2, 0, 0, 1, 4, 1804004, 0, "わたしがごはんのことばかり\n考えてると思っていませんか？\n半分は正解ですけど、もう半分は\n…あなたのことですよ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180401, 0.0, 6, 0, 5, 0, 7.7, 0, 0, 1, 2, 1804005, 0, "やっぱりあなたと囲む食卓が\n一番ですね☆　いっそのこと\n王宮で一緒に住みませんか？\nやっぱり、まずいですかね～？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180431, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1804006, 0, "超全力全開☆\n私は守られるだけの王女様じゃ\nありませんよ？　この国と…\nあなたのために、戦います！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180431, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1804007, 0, "この姿、とっても強いんですけど\nその分お腹が減るんですよ～…\nでも、それってつまり…\nもっとごはんが食べられますね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180431, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1804008, 0, "力が溢れ出て、止まりません！\nこの力で、わたしの大切なものを\nぜ～んぶ守ってみせますね\n今度は何もなくしたりしません！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180431, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1804009, 0, "あなたの力をこれまでより\n強く感じます…温かくて、\n胸がキュンキュンしちゃいます…\nえへへ、やばいですね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180431, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1804010, 0, "お腹が空きましたね！\nさぁ、わたしと一緒にごはんを\n食べに行きましょう♪\n今日も明日も仲良くごはんです☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180401, 0.0, 5, 0, 6, 0, 8.1, 0, 0, 2, 1, 1804011, 0, "あなたの誕生日となれば、\n国を挙げてお祝いしなきゃですね\nそれとも二人きりがいいですか？\n…えへへ、照れちゃいますね☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180431, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1804012, 0, "今日はあなたの誕生日ですね\nおめでとうございます！　\nそれと…ありがとうございます\nこうしてわたしと出会ってくれて");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180501, 0.0, 2, 0, 1, 0, 6.1, 0, 0, 1, 1, 1805001, 0, "姿形が変わっても\nわたくしはわたくしのまま…\nですが、これまで以上に\n主さまにお仕えいたします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180501, 0.0, 2, 0, 2, 0, 4.5, 0, 0, 1, 6, 1805002, 0, "主さまが変身するところも\n見とうございますね\nわたくしに続いて\n掛け声をどうぞ。せーの…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180501, 0.0, 6, 0, 3, 0, 5.3, 0, 0, 1, 5, 1805003, 0, "プリンセス耳かきに\nプリンセスふーふー…\nあ、いえ。この姿でのサポートは\n一味違ったものにしてみようかと");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180501, 0.0, 1, 0, 4, 0, 5.5, 0, 0, 1, 2, 1805004, 0, "この姿はまるでおとぎ話の\n妖精のようでございます\nいつも主さまの隣で\n舞っていたいものです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180501, 0.0, 2, 0, 5, 0, 2.7, 0, 0, 1, 6, 1805005, 0, "『プリンセス・フォーム』…\nいつもより力が湧いてくるのは\n心の奥底で、主さまとの絆を\n強く感じるからでしょうか");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180531, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1805006, 0, "わたくしの願いは、ただ一つ…\n末永く主さまの隣にいることです\nどんな困難もこの力で乗り越えて\n必ずや叶えてみせます！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180531, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1805007, 0, "わたくしたちの旅がどんな結末を\n迎えようとも、主さまのために\n戦います。迷いはありません\nこの力が全ての証明でございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180531, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1805008, 0, "もっと強く、もっと高く…\n絆が生んだこの羽で\n主さまと未来に羽ばたきます\nいつまでも…どこまでも一緒に…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180531, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1805009, 0, "主さまが傷つくところを\nこれ以上見たくはありません\nいつも笑っていていただくために\nわたくしはこの力でお守りします");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180531, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1805010, 0, "以前にも増して主さまを近くに\n感じています\n主さまも、わたくしを感じて\nおいででしょうか…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180501, 0.0, 2, 0, 6, 0, 0.0, 0, 0, 2, 2, 1805011, 0, "わたくしは今日という日を心から\nお待ちしておりました。主さまの\nお誕生日を、全身全霊をかけて\nお祝いいたしますね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180531, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1805012, 0, "お誕生日おめでとうございます\nなにか特別にしてほしいことは\nございますか？　遠慮せずに\nなんでもお申し付けくださいまし");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180601, 0.0, 1, 0, 1, 0, 4.0, 0, 0, 1, 2, 1806001, 0, "あたしもなれたのね\nプリンセスフォームに…！\nねえ、見てなさいよ？\nここからのあたし、すごいから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180601, 0.0, 2, 0, 2, 0, 2.4, 0, 0, 1, 1, 1806002, 0, "あたしの魔力にびっくりした？\nしょうがないわね～\n仕方がないから\n自慢させてあげるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180601, 0.0, 1, 0, 3, 0, 3.6, 0, 0, 1, 4, 1806003, 0, "プリンセスフォームって\n空を飛べるのよね？\nあんたにも付き合ってもらうわよ\n秘密の特訓！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180601, 0.0, 1, 0, 4, 0, 6.7, 0, 0, 1, 5, 1806004, 0, "あんたみたいな底抜けのお人よし\nあたしが守ってあげなきゃ\n誰が守ってくれるってのよ\nありがたく思いなさいよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180601, 0.0, 2, 0, 5, 0, 6.9, 0, 0, 1, 1, 1806005, 0, "この力で自分の居場所を…ううん\nみんなの居場所を守りたいわ\n…こんな風に思えるように\nなったのは、あんたのおかげかも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180631, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1806006, 0, "チェンジ・プリンセスフォーム！\nこの力で、あんたと一緒に\nどんな限界だって\n超えてみせるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180631, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1806007, 0, "あたしはもう迷わないし\nあきらめない！\nあんたがくれたこの力で\nどこまでもあがき続けてやるわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180631, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1806008, 0, "ふふ～ん♪\n優秀なあたしの完っ璧な大魔法で\nどんなやつだって\nぶっ飛ばしちゃうんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180631, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1806009, 0, "プリンセスフォームって\n心の繋がり…なのよね？\nつまり、あんたとあたしの心が\n深く…って、こっち見んな！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180631, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1806010, 0, "未来は、変えていけるのよね…？\nあんたがいてくれたらもうあたし\n怖いものなんてないわ！\nお願い、力を貸して！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180601, 0.0, 3, 0, 6, 0, 6.2, 0, 0, 2, 1, 1806011, 0, "はいはい、誕生日おめでとう\nせっかくだしみんなで\nパーティーしましょうよ\n料理は魔物料理以外でね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180631, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1806012, 0, "今日ってあんたの誕生日よね\nなんかしてほしいこととかある？\nあんたのこと、思いっきり\n甘やかしてあげるわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 2, 180701, 0.0, 1, 0, 1, 2, 3.4, 2, 118301, 1, 2, 1807001, 2, "今度一緒におでかけしようよ！\n空を飛んでたら、とっても素敵な\n場所を見つけたから、\nシオリンと一緒に行きたいんだ☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 2, 180701, 0.0, 2, 0, 2, 2, 3.8, 2, 118301, 1, 1, 1807002, 2, "シオリンがおすすめしてた本を\n読んでたんだ☆\nわくわくドキドキな冒険の連続で\nすっごく面白いね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (7.0, 1, 180701, 7.0, 1, 0, 3, 1, 2.9, 2, 118301, 1, 6, 1807003, 2, "シオリンはしっかりもの\nだけど…もしもの時は\n私が守ってあげなきゃ！\nだって、お姉ちゃんだもん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 2, 180701, 0.0, 6, 0, 4, 2, 5.8, 2, 118401, 1, 1, 1807004, 2, "お姉ちゃん、今日は一緒に\n少し遠くまで冒険しに行こう？\n体調は大丈夫。ふふっ…\nお姉ちゃん、心配性なんだから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (6.2, 6, 180701, 6.2, 1, 0, 5, 6, 0.0, 2, 118401, 1, 1, 1807005, 2, "あれ？　お姉ちゃん、その本…\n読んでくれてたんだね\nふふっ、うれしいなぁ\n読み終わったら感想聞かせてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (8.4, 5, 180701, 8.4, 1, 0, 6, 5, 0.0, 1, 118401, 1, 1, 1807006, 1, "お姉ちゃんに心配されるのって\n気恥ずかしいけど…実は少し\nうれしくて。でも迷惑かけるのは\nいやだから、もっと強くなるね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 1, 0, 0.0, 0, 118301, 1, 1, 1807007, 0, "シオリンと一緒なら\n私たちは無敵なのだ～☆\nそうだよね、シオリン♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 2, 0, 0.0, 0, 118301, 1, 1, 1807008, 0, "合体必殺攻撃なのだ～☆\n二人でカッコよく、\nキメちゃおうね、シオリン♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 3, 0, 0.0, 0, 118301, 1, 1, 1807009, 0, "シオリンとの絆とキミの優しさ！\nこの二つがあれば、私は\nどんな相手にだって\n絶対に負けないのだ～☆");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 4, 0, 0.0, 0, 118401, 1, 1, 1807010, 0, "お姉ちゃんがいてくれるなら\nきっと、どんなことがあっても\n怖くないよ…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 5, 0, 0.0, 0, 118401, 1, 1, 1807011, 0, "感じるよ、お姉ちゃんとの\nあったかくて力強い、絆の力…！\n私たちの全力、\n見ていてください…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 6, 0, 0.0, 0, 118401, 1, 1, 1807012, 0, "お姉ちゃんとの姉妹の絆と\nあなたからもらった力…！\n二つをこの矢に込めて…\n放ちます…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 2, 180701, 0.0, 2, 0, 7, 2, 6.4, 2, 118301, 2, 1, 1807013, 2, "お誕生日、おめでとう☆\nプレゼントはシオリンと一緒に\n選んだんだよ！　よろこんで\nもらえたらうれしいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 7, 0, 0.0, 0, 118301, 2, 1, 1807014, 0, "今日は何の日か知ってる～？\nなんとなんと…！　キミの\nお誕生日なんだよ！　おめでと☆\nめいっぱいお祝いしちゃうよ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (7.9, 2, 180701, 7.9, 1, 0, 8, 2, 0.0, 5, 118401, 2, 1, 1807015, 5, "ハッピーバースデー…！\nお祝いの品を用意したのですが\n…受け取っていただけますか？\n私の手作りの詩集、です");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180731, 0.0, 1, 0, 8, 0, 0.0, 0, 118401, 2, 1, 1807016, 0, "今日は特別な日ですね\nだってあなたのお誕生日ですから\nこうして一緒にお祝いできて、\n私もうれしいです");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 5.1, 1, 0, 1, 1, 0.0, 3, 120602, 1, 1, 1808001, 0, "さっきから私たちをじっと見て\nどうかしましたか？　なんだか\n目つきがあやしい…やっぱり\nへんたいふしんしゃさんです！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 10.1, 1, 0, 2, 2, 0.0, 6, 120602, 1, 1, 1808002, 0, "冒険に出かける前の予習復習は\nとても大事です。どんな場所か\n知っておけば、対処は簡単なので\n…怖いわけじゃ、ありません！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 7.9, 1, 0, 3, 1, 0.0, 2, 120602, 1, 1, 1808003, 0, "あなたは一人だとなにをするか\n分からなくて心配ですから、\n私たちから離れちゃダメですよ\nほら、手をつないでください");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 0.0, 1, 0, 4, 0, 5.4, 0, 120402, 1, 2, 1808004, 0, "えへへ、おにいちゃん♪\n今日も一緒に遊ぼう～\nミソギちゃんとキョウカちゃんも\nさそって、おままごとしたいな♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 0.0, 2, 0, 5, 0, 0.0, 0, 120402, 1, 2, 1808005, 0, "えへへ、みんな一緒だね～♪\nなんだかうれしくって、\nこころがうさぎさんみたく\nぴょんってしちゃうね～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 0.0, 2, 0, 6, 0, 5.5, 0, 120402, 1, 1, 1808006, 0, "冒険のおべんとうに、いっぱい\nにんじんさんを入れてきたんだ♪\nおにいちゃんにもあげるね～\nにんじんさんパワーでがんばろ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (3.7, 2, 180801, 0.0, 1, 0, 7, 0, 0.0, 0, 120501, 1, 1, 1808007, 1, "にひひ♪　新しいイタズラ\n思いついちゃった！\nミミもキョウカもにいちゃんも\nみんなまとめて驚かせちゃお♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 2, 180801, 0.0, 1, 0, 8, 0, 0.0, 0, 120501, 1, 1, 1808008, 2, "わあ～い！　にいちゃんまた\nイタズラにひっかかったね♪\nどうだった？　今どんな気分？\nやっぱり楽しかった？　だよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (3.7, 1, 180801, 0.0, 1, 0, 9, 0, 0.0, 0, 120501, 1, 1, 1808009, 2, "ミソギたちがみんな揃ったら\nいつでもどんな場所でも\nサイッコーに楽しい冒険が\nできちゃうと思うんだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 1, 0, 0.0, 0, 120602, 1, 1, 1808010, 0, "私たち【リトルリリカル】の\n３人が力を合わせれば…！\n相手がどんなに強くても\n絶対に負けたりしません！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 2, 0, 0.0, 0, 120602, 1, 1, 1808011, 0, "いこうミミ、ミソギ！\n私たちの絆の力を見せつけよう！\n【リトルリリカル】に\nできないことはないんだから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 3, 0, 0.0, 0, 120602, 1, 1, 1808012, 0, "あなたの力で強くつながった\n私たちの絆の三角形…！\nその底力を、見ていてください！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 4, 0, 0.0, 0, 120402, 1, 1, 1808013, 0, "一人じゃ、怖くて泣いて\n逃げちゃうような時でも\nさんにん一緒なら、ミミ、\nいっぱいがんばれるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 5, 0, 0.0, 0, 120402, 1, 1, 1808014, 0, "ミソギちゃんとキョウカちゃんと\nそれにおにいちゃん♪\nみんなの優しい力を感じるよ\nだから、今はなにも怖くないんだ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 6, 0, 0.0, 0, 120402, 1, 1, 1808015, 0, "おにいちゃん、ミミのがんばる姿\nちゃんと見ててね～♪\nみんながいれば\nミミだってやればできるんだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 7, 0, 0.0, 0, 120501, 1, 1, 1808016, 0, "すごい、これが３人の絆の力…！\n今のミソギたちなら\nどんなことでもできちゃいそう！\nなんだかワクワクしてきた！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 8, 0, 0.0, 0, 120501, 1, 1, 1808017, 0, "ミミもキョウカも、怖かったら\nミソギの後に続いていいからね！\n…まあ、そんな心配いらないか！\n今なら怖いものなしだもんね！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 9, 0, 0.0, 0, 120501, 1, 1, 1808018, 0, "まばたき禁止だよ、にいちゃん！\n今からイタズラじゃなくて、\nミソギたち３人の絆の力で\nにいちゃんを驚かせちゃうから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 5.6, 1, 0, 10, 1, 0.0, 2, 120602, 2, 1, 1808019, 0, "プレゼントが欲しそうな顔を\nしていますね？　まったく…\nちゃんと用意してあげましたよ！\nお誕生日、おめでとうございます");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 10, 0, 0.0, 0, 120602, 2, 1, 1808020, 0, "あの…な、なにも言わずにこれを\n受け取ってください…！　私の\n手作りカップケーキです。その…\nハッピーバースデー、です…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180801, 0.0, 2, 0, 11, 0, 6.5, 0, 120402, 2, 1, 1808021, 0, "今日はおにいちゃんのお誕生日\nだよね。ちゃんと覚えてるよ～♪\nだってミミ、おにいちゃんのこと\n大好きだから♪　えへへ～");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 11, 0, 0.0, 0, 120402, 2, 1, 1808022, 0, "ミミ、今日はいっぱい踊るね\nおにいちゃんのお誕生日を\nいっぱいおいわいしたいから♪\nるんたったるんたった～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (4.8, 2, 180801, 0.0, 1, 0, 12, 0, 0.0, 0, 120501, 2, 1, 1808023, 1, "クラッカー、パンパンパーン！\nえへへ、びっくりした？\nにいちゃんの誕生日祝いの\nサプライズだよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180831, 0.0, 1, 0, 12, 0, 0.0, 0, 120501, 2, 1, 1808024, 0, "ハッピーバースデーにいちゃん！\nプレゼントに特大のイタズラを\n準備しといたよ！　ちゃんと\n引っかかってよね！　にひひ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 0.0, 1, 0, 1, 0, 0.0, 0, 121701, 1, 1, 1809001, 0, "意気込みは十分ですわよ！\nサレンさんに情けないところは\n見せられませんもの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 0.0, 2, 0, 2, 0, 2.3, 0, 121701, 1, 1, 1809002, 0, "共に競い高め合う…\nそれこそが高貴な\n友情というものですわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 0.0, 1, 0, 3, 0, 0.0, 0, 121701, 1, 1, 1809003, 0, "いくらサレンさんが相手でも\nあなたさまに関することでしたら\n一歩も引く気はありませんわ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 3.8, 1, 0, 4, 2, 0.0, 1, 121801, 1, 1, 1809004, 0, "アキノさんがいてくれると\nとっても心強いわ\n二人ならどんな相手にも\n勝てちゃいそうよ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 4.7, 1, 0, 5, 1, 0.0, 2, 121801, 1, 1, 1809005, 0, "何かにつけて張り合ってくるのも\n最初は戸惑ったけれど…\n今ではアキノさんなりの親愛の\n表し方なのかなって思うわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 4.5, 1, 0, 6, 6, 0.0, 5, 121801, 1, 1, 1809006, 0, "あたしだってアキノさんに\n張り合うことだってあるわよ？\nべ、別にあんたがどうとかは\n関係ないけど…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 1, 0, 0.0, 0, 121701, 1, 1, 1809007, 0, "サレンさんに恥ずかしい\nところは見せられませんわ！\n私が持てる全力で\nお相手いたします！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 2, 0, 0.0, 0, 121701, 1, 1, 1809008, 0, "サレンさん、私の見せ場も\n残しておいてくださいね？\nかっこいいところを\nお見せしたいですから");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 3, 0, 0.0, 0, 121701, 1, 1, 1809009, 0, "私たちが揃えばできないことなど\nありませんわ！\n邪魔をするというなら\n覚悟してくださいまし♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 4, 0, 0.0, 0, 121801, 1, 1, 1809010, 0, "アキノさんに\n好敵手って呼んでもらえる以上\n後れを取るわけにはいかないわ！\nあたしの全力、見せてあげる！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 5, 0, 0.0, 0, 121801, 1, 1, 1809011, 0, "まったく、アキノさんの勢いに\nついていくのは大変だわ…\nまぁ、その分あたしも\n強くなれるんだけど");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 6, 0, 0.0, 0, 121801, 1, 1, 1809012, 0, "あたしたちが揃えば\n向かうところ敵なしね♪\nおとなしくしてないと\nけがするわよ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 0.0, 1, 0, 7, 0, 6.8, 0, 121701, 2, 2, 1809013, 0, "お誕生日おめでとうございます！\n私たち二人でたっぷりと\nお祝いさせていただきますわ！\nささ、こちらへどうぞ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 7, 0, 0.0, 0, 121701, 2, 1, 1809014, 0, "おめでとうございます！\nあなたさまのお誕生日を\nサレンさんと二人で祝えるなんて\n光栄の極みですわ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180901, 5.6, 1, 0, 8, 1, 0.0, 2, 121801, 2, 1, 1809015, 0, "誕生日おめでとう♪\n今日はアキノさんと二人で\nお祝いしてあげるわ\nほら、いきましょう？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 180931, 0.0, 1, 0, 8, 0, 0.0, 0, 121801, 2, 1, 1809016, 0, "誕生日おめでとう\nあんたのおかげで\nもっと仲良くなれたから\n二人でお祝いさせてもらうわね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 0.0, 6, 0, 1, 0, 8.9, 0, 124301, 1, 1, 1810001, 0, "え？　私とグレアは\nいつも一緒にいるって？\nもちろん別行動だってするよ！\n最近だって…ええと、ええと…？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 0.0, 2, 0, 2, 0, 4.3, 0, 124301, 1, 1, 1810002, 0, "グレアの体って\n他の人より温かいんだよね！\nだから寒い時は本を読む前に\n手を握ってもらうんだ～♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 0.0, 2, 0, 3, 5, 7.3, 5, 124301, 1, 1, 1810003, 0, "グレアって褒められるとすぐ\n顔が真っ赤になっちゃうんだ\nもっと褒めると涙目になって…\nそんな顔もかわいいんだよね♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 5.9, 1, 0, 4, 1, 0.0, 2, 124401, 1, 1, 1810004, 0, "時々、アンと一緒に\n『もしも』の話をするんだ…\nあなたも同じ学院に通ってたら\nきっと楽しいだろうなって");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 5.3, 1, 0, 5, 4, 0.0, 6, 124401, 1, 1, 1810005, 0, "アンったら…さっきからずっと\n魔導書を読んでるの…\n…え！？　寂しいなんてこと…！\n…ちょっとだけ、あるかも");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 4.5, 1, 0, 6, 1, 0.0, 2, 124401, 1, 1, 1810006, 0, "今日はピアノを弾きに\n行こうと思ってるんだ\n…その、アンと一緒に\n連弾する約束をしてて…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 1, 0, 0.0, 0, 124301, 1, 1, 1810007, 0, "感じるよ…\nグレアの温かくて優しい魔力を\nお願い、私に力を貸して…\n─英霊の槍よ、敵を貫け！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 2, 0, 0.0, 0, 124301, 1, 1, 1810008, 0, "一人では乗り越えられないことも\nグレアと一緒なら大丈夫！\nそう信じられる絆が\n私たちにはあるから！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 3, 0, 0.0, 0, 124301, 1, 1, 1810009, 0, "グレア、私の陽だまり…\nいつも私のそばにいてくれて\nありがとう。グレアがいるから\n私の魔法は光り輝くんだ！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 4, 0, 0.0, 0, 124401, 1, 1, 1810010, 0, "感じるよ…\nアンの純粋で真っすぐな魔力を…\n最後まで、そばを離れないから…\n─竜の炎よ、敵を燃やしつくして！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 5, 0, 0.0, 0, 124401, 1, 1, 1810011, 0, "私を導いてくれる光…アン\nどんな敵でも、私は負けない…\nアン、あなたと一緒だから\nそんな気持ちになれるの！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 6, 0, 0.0, 0, 124401, 1, 1, 1810012, 0, "昔の私は自分の力が怖かった…\nでも今は、どんな私を見ても\nアンが笑ってくれるから…だから\nもっと強くなりたいって願えた！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 0.0, 2, 0, 7, 0, 4.6, 0, 124301, 2, 1, 1810013, 0, "誕生日おめでとう！\n私たちも友達として\nお祝いさせて！\nプレゼントもグレアと選んだの♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 7, 0, 0.0, 0, 124301, 2, 1, 1810014, 0, "今日のためにグレアと\nピアノの練習をしたんだ！\n曲はもちろん…\n『ハッピーバースデー』だよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181001, 0.0, 1, 0, 8, 2, 0.0, 2, 124401, 2, 1, 1810015, 0, "アンと一緒に\nプレゼントを用意したんだ…\n今日は誕生日、おめでとう");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181031, 0.0, 1, 0, 8, 0, 0.0, 0, 124401, 2, 1, 1810016, 0, "今日はおめでとう\nあなたの誕生日をアンと\nお祝いできてうれしい…\n…その、友達って感じがして…");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 0.0, 5, 0, 1, 0, 5.1, 0, 128101, 1, 2, 1811001, 0, "私とリノちゃんがお揃いなんだし\n弟くんも着替えるべきだよねっ！\n…恰好よくタキシードとかどう？\nお姉ちゃん、隣を歩きたいな…♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 0.0, 5, 0, 2, 0, 3.1, 0, 128101, 1, 2, 1811002, 0, "姉妹の力でパワーアップだよっ♪\nリノちゃんとお姉ちゃんの\n愛の力で、大好きな弟くんを\n守り抜いてみせるからね…！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 0.0, 2, 0, 3, 0, 6.9, 0, 128101, 1, 5, 1811003, 0, "弟くんお兄ちゃんっ♪\n今ね、姉妹の絆を深めるために\nリノちゃんになりきってるの♪\n似てるでしょ？　似てるよねっ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 6.3, 1, 0, 4, 6, 0.0, 4, 128201, 1, 1, 1811004, 0, "お、弟くんっ、お姉ちゃんだぞ♪\n…いま、もっと絆を深めるために\nお姉ちゃんになりきってるんです\nわっ、笑わないでくださいよ～！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 12.6, 1, 0, 5, 5, 0.0, 6, 128201, 1, 1, 1811005, 0, "この姿は私とお姉ちゃんの\n繋がりの証、なんですよね\n心強くて…力が湧いてきます\nこれがお姉ちゃん力…え、違う？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 5.5, 1, 0, 6, 2, 0.0, 5, 128201, 1, 1, 1811006, 0, "お兄ちゃんっ、ちょっと私の手を\n引いて、歩いてもらえませんか？\n…こんなにきれいな衣装ですから\n特別感を味わいたくて…えへへ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 1, 0, 0.0, 0, 128101, 1, 1, 1811007, 0, "姉と妹のダブルアタックだぞ♪\nえへへ、覚悟してね弟くん！\nパワーアップした私たちからは\nもう逃げられないんだからっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 2, 0, 0.0, 0, 128101, 1, 1, 1811008, 0, "弟くんと、リノちゃんと、私\nこの繋がりは特別なものなんだ\nだから必ず、守ってみせるからね\nあなたたちの、お姉ちゃんとして");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 3, 0, 0.0, 0, 128101, 1, 1, 1811009, 0, "ねぇ…あとでお姉ちゃんと\n一緒にお出かけしない？\nこの衣装でデートしよっ♪\n…リノちゃんには内緒だよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 4, 0, 0.0, 0, 128201, 1, 1, 1811010, 0, "お兄ちゃん、あとで私とお出かけ\n…デート、しませんか？\nこの衣装、見せびらかしたくて…\nお姉ちゃんには秘密ですよっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 5, 0, 0.0, 0, 128201, 1, 1, 1811011, 0, "こうやってお揃いの衣装を\n着るの、仲良し姉妹って\n感じがしていいですよね！\nお兄ちゃんも揃えちゃいますっ？");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 6, 0, 0.0, 0, 128201, 1, 1, 1811012, 0, "お兄ちゃん、さぁどうぞ\n私とお姉ちゃんの間に♪\nえへへ、どうですか？\nこれが姉妹ホールドですっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 0.0, 2, 0, 7, 0, 8.1, 0, 128101, 2, 5, 1811013, 0, "弟くんの生まれた日、それは\nお姉ちゃんとリノちゃんにとって\n運命の日、なんだよ…！\n盛大にお祝いしちゃおうねっ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 7, 0, 0.0, 0, 128101, 2, 1, 1811014, 0, "お誕生日おめでとう♪\nリノちゃんと一緒に選んだ\nプレゼントだよっ♪　…それと\nお姉ちゃん一人からのもあるよ♪");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181101, 4.1, 1, 0, 8, 1, 0.0, 2, 128201, 2, 1, 1811015, 0, "お兄ちゃんがまた一つ\nお兄ちゃんになっちゃいましたね\n妹として誇らしいです！\n恰好いいですよ、お兄ちゃん！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181131, 0.0, 1, 0, 8, 0, 0.0, 0, 128201, 2, 1, 1811016, 0, "お誕生日おめでとうございます！\n私とお姉ちゃんでプレゼントを\n用意したんです、けど…実は\n私一人からのもあったり…えへへ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181201, 0.0, 2, 0, 1, 0, 0.0, 0, 0, 1, 2, 1812001, 0, "迎えに来てくれてありがとう\nもう二度と会えないって\n私自身覚悟してたから…\nこんな奇跡があるなんてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181201, 0.0, 2, 0, 2, 0, 5.4, 0, 0, 1, 6, 1812002, 0, "正直、戻ってきた実感は\nまだあまりないのだけど…\nあなたの顔を見ると、\nやっぱりほっとするわね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181201, 0.0, 2, 0, 3, 0, 6.3, 0, 0, 1, 10, 1812003, 0, "今一番みんなとしたいのは\nやっぱり食べ歩きの旅かしら\n…そのためにも、頑張って\n世界を救わなきゃね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181201, 0.0, 2, 0, 4, 0, 0.0, 0, 0, 1, 2, 1812004, 0, "みんなが捜してくれたって聞いて\nすごくうれしかったの\n今度は私がこの力で\nみんなを守る番ね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181201, 0.0, 1, 0, 5, 0, 7.6, 0, 0, 1, 2, 1812005, 0, "あなたといる時間は\n決して当たり前じゃないって\n改めて気づけたわ\nだからこそ、大事にしたいの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181231, 0.0, 1, 0, 1, 0, 0.0, 0, 0, 1, 1, 1812006, 0, "待っててくれて、信じてくれて\n本当にありがとう\nだから何度だって言うわ\n─ただいま！");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181231, 0.0, 1, 0, 2, 0, 0.0, 0, 0, 1, 1, 1812007, 0, "今の私には、こんなに綺麗で\n大きな翼があるんだもの\n今度は私が抱き留めてあげる");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181231, 0.0, 1, 0, 3, 0, 0.0, 0, 0, 1, 1, 1812008, 0, "…私、涙もろくなったみたい\nあなたの顔を見られただけで\n胸がいっぱいになってしまうの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181231, 0.0, 1, 0, 4, 0, 0.0, 0, 0, 1, 1, 1812009, 0, "もう一度この手を掴んでくれる？\nそうしたら、二度と離さないわ\nあなたは私の…大切な人だもの");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181231, 0.0, 1, 0, 5, 0, 0.0, 0, 0, 1, 1, 1812010, 0, "これがプリンセスフォーム…\nあなたとの絆の証なのね\n今まで以上に一緒に戦ってる\n気がして、勇気がもらえるわ");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181201, 0.0, 2, 0, 6, 0, 5.4, 0, 0, 2, 10, 1812011, 0, "お誕生日おめでとう！\nまたお祝いできてうれしいわ\nとびきりのパーティーを開くから\n期待していてね");
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0.0, 0, 181231, 0.0, 1, 0, 6, 0, 0.0, 0, 0, 2, 1, 1812012, 0, "あなたたちがしてくれたように、\n私もケーキを作ってみたいの\n愛情いっぱいの特製ケーキ…\n完成したら食べてくれる？");
