INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1001001, 100101, 0, 4.0, 2, "みんなが笑顔でいてくれたら\nあたしも笑顔でいられるんだ！\nだからほら、スマイルスマイル！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1001002, 100101, 0, 3.0, 2, "困った時はお互いさまさま！\n何かあったら相談してね！\nできること、あるはずだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1001003, 100101, 0, 7.5, 2, "いつでも元気なのが一番っ！\n疲れた時はちゃんと休んでね？\nあたしは…まだ元気だもんっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1001004, 100101, 0, 6.0, 2, "実はね…？　一緒にいると\nすっごく楽しいんだ！\nえへへ…ありがとね♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1001005, 100101, 0, 6.0, 6, "なんだろ…時々\n胸の真ん中が熱くなるんだ\nもしかして風邪引いちゃった！？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1001006, 100131, 0, 0.0, 1, "強くなるんだ…！\nあたしはみんなと…\nずっと笑顔でいたい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1001007, 100131, 0, 0.0, 1, "傷だらけになっても大丈夫！\n支えてくれる人がいるから…\n何度でも立ち上がってみせる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1001008, 100131, 0, 0.0, 1, "騎士くんが近くにいてくれたら\n元気100万倍って感じ！\n…ちょっと言い過ぎかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1001009, 100131, 0, 0.0, 1, "聞こえるよ…騎士くんの声\nどんなに離れてても、\nあたしには分かる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1001010, 100131, 0, 0.0, 1, "たとえどんな人が相手でも、\n今は負ける気がしない…！\n支えてくれるあの人がいるから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1001011, 100101, 0, 11.3, 2, "ハッピーバースデー、騎士くん！\nプレゼントは何がいい？\nごはん？　装備？　それとも…\nそうだ！　修行に付き合うね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1001012, 100131, 0, 0.0, 1, "お誕生日おめでとー！\nはいこれ！　猫のお守り！\nあたしがそばにいない時は、\nきっと代わりに守ってくれるよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1001013, 100161, 0, 0.0, 1, "うにゃにゃにゃにゃー！\n騎士くんが一緒なら\nあたしは無敵だもんね！\nいっくぞぉー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1001014, 100161, 0, 0.0, 1, "どんなピンチだって、今日まで\n騎士くんと乗り越えてきた！\nこれからも二人でいれば\nあたしは負けないよー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1001015, 100161, 0, 0.0, 1, "守りたい笑顔があるから\nあたしは戦える！\nだから騎士くん\nあたしのそばにいて…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1001016, 100161, 0, 0.0, 1, "もっとドキドキを感じたい！\nよくわかんないけど、\nそうすれば騎士くんとなんだって\nできそうな気がするから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1001017, 100161, 0, 0.0, 1, "あたし、こんなに強かったっけ？\nきっと騎士くんのおかげだね！\nだからずっとそばにいてくれる？\nだ、だって、強くなりたくて…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1002001, 100201, 0, 6.0, 5, "あ、あの…\nわたしが話し相手で、\n本当にいいの？\n…そっか。ありがとう…！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1002002, 100201, 0, 4.0, 5, "戦うなら、\n治療と援護はまかせて！\n攻撃はその、一緒に\n頑張って欲しい…かな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1002003, 100201, 0, 4.0, 8, "お願い…\nそんなにじっと見ないで…\nは、恥ずかしいよ…あうぅ…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1002004, 100201, 0, 4.0, 2, "お腹空いてない？　ご飯が\n食べたくなったら言ってね\n軽い物ならすぐ作れるから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1002005, 100201, 0, 3.0, 5, "何か手伝ったほうがいい？\nどこかへ行くなら、わたしも\n連れて行って欲しいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1002006, 100231, 0, 0.0, 1, "わたしに出来ることがあったら、\n何でも言ってね\nちょっとした怪我なら、\nすぐに治せるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1002007, 100231, 0, 0.0, 1, "騎士クンのためなら\nわたし…もっともっと\nがんばろうって思える！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1002008, 100231, 0, 0.0, 1, "癒しの輝きを…！\n見てるだけで癒されるって、\nそんな…なんか、\n恥ずかしくなってきちゃった…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1002009, 100231, 0, 0.0, 1, "どんな魔法を勉強しよう…\n恋が成就する魔法…\nそ、そんなのあるわけないよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1002010, 100231, 0, 0.0, 1, "新しく覚えた魔法はね、\n防炎、防寒、耐熱…あとは、\nのどに魚の骨が刺さらない\n魔法…内緒だよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1002011, 100201, 0, 7.9, 5, "騎士クン。お誕生日おめでとう\nあの…これ、お祝いのケーキ！\nその…良かったら食べてみて！\nえっと…これからも、よろしくね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1002012, 100231, 0, 0.0, 1, "誕生日おめでとう、騎士クン\n今日はケーキの他にも\nいろんな料理を用意したから\nみんなでお祝いしようね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1002013, 100261, 0, 0.0, 1, "騎士クンが、大切な人が\n傷ついた時は癒してあげたい…\nそれが今の、わたしの願い", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1002014, 100261, 0, 0.0, 1, "騎士クン、痛いところはない？\n傷はちゃんと治ってる？\n…よかった。もう大丈夫だね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1002015, 100261, 0, 0.0, 1, "お願い騎士クン。これからも\nわたしに、勇気をください…\nもっと強く、優しくなるために！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1002016, 100261, 0, 0.0, 1, "思いの強さなら、わたしだって…\nでも、気持ちだけじゃダメだよね\nちゃんと行動で示さないと！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1002017, 100261, 0, 0.0, 1, "騎士クンを好きな気持ちが\nいつもわたしに勇気をくれる…\n騎士クン！？　今の聞いて…！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1003001, 100301, 0, 4.0, 2, "目的を果たすためなら、\nどんな努力でもする。キミにも\nそういう気持ちでいてほしいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1003002, 100301, 0, 4.5, 6, "次の修行の予定について\n相談したいんだけど、いい？\n場所はどこにしようか…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1003003, 100301, 0, 4.5, 2, "強くなりたい…\nその気持ちさえ失わなければ\n必ずキミも強くなれるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1003004, 100301, 0, 2.0, 2, "何故だろう…\nキミと剣を交えていると\n踊っているみたいだ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1003005, 100301, 0, 4.5, 2, "今度一緒に釣りへ行かない？\n一人でも退屈はしないけど\nキミと行きたいんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1003006, 100331, 0, 0.0, 1, "さあ構えて！\n私から一本取るんだろう？\nのんびりしている暇はないよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1003007, 100331, 0, 0.0, 1, "決して諦めないその目…\nやっぱりキミはすごいな\n私の見込みは正しかった", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1003008, 100331, 0, 0.0, 1, "キミはどんどん強くなる\nこんなものが限界じゃないはず\n…それを証明してみせて", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1003009, 100331, 0, 0.0, 1, "（不思議な気分…\nずっと続けていたいけど、\nそういうわけにもいかないか）", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1003010, 100331, 0, 0.0, 1, "何でもいうことを聞くとは\nいったけど…破廉恥なのは\nなしだからね…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1003011, 100301, 0, 2.6, 5, "誕生日おめでとう\n実は何を渡せば喜んでくれるか\n分からなくて…良ければ\n一緒に選びにいかない？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1003012, 100331, 0, 0.0, 1, "キミの誕生日か。おめでとう\nせっかくだし今日の修業は\nなしにしよう。…あれ？\nどうして不満そうなの？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1003013, 100361, 0, 0.0, 1, "キミの思いが、私を\n強くしてくれているんだね…\n私もその思いに応えよう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1003014, 100361, 0, 0.0, 1, "驚くほど体が軽い…\nこれなら剣技も、いつも以上に\n冴え渡る！　いくよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1003015, 100361, 0, 0.0, 1, "この力は、私とキミの絆の力…\nこれからも二人一緒に、\nどこまでも強くなりたい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1003016, 100361, 0, 0.0, 1, "私にとってキミは、\nかけがえのない存在だよ\n今までも、この先も…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1003017, 100361, 0, 0.0, 1, "私が自分を信じられるのは\nキミが私を信じてくれるから…\nお願い…ずっと私を信じていて", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1004001, 100401, 0, 2.5, 2, "あっ、にいちゃんだー！\nねえねえ、\nミソギとなにして遊ぶ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1004002, 100401, 0, 4.0, 4, "う～イタズラ用の\n激辛キャンディー、\n自分で食べちゃったよぉ…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1004003, 100401, 0, 6.0, 6, "よーし、イタズラの設置完了！\nにいちゃんをひっかけて…\nあっ…もしかして、\n見ちゃった？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1004004, 100401, 0, 4.5, 2, "ミソギがイタズラを用意する…\nにいちゃんが引っかかる…\nうんっ、最高のコンビだね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1004005, 100401, 0, 4.0, 2, "にいちゃん、もっともっと\n遊ぼうね。約束だよ！\nほら、指切りげんまん♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1004006, 100431, 0, 0.0, 1, "ミソギ探検隊、全員集合！\nミソギとにいちゃんの二人だけ\nなんだけどね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1004007, 100431, 0, 0.0, 1, "これからも、いっぱい\nにいちゃんで遊べるよね！\n…あ、にいちゃんと遊べる、\nって言いたかったんだよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1004008, 100431, 0, 0.0, 1, "にいちゃーん！\n早く早くっ探検に行こっ！\nにいちゃんのこと\n待ってたんだよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1004009, 100431, 0, 0.0, 1, "大好きなにいちゃんのために、\nたっくさんイタズラ\n用意しなくっちゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1004010, 100431, 0, 0.0, 1, "ミソギねぇ、本当の\nにいちゃんが出来たみたいで、\nすっごくうれしいんだー♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1004011, 100401, 0, 7.6, 2, "誕生日おめでとう、にいちゃん！\nはい、バースデープレゼント！\nそれから、バースデーケーキ！\n早く開けて！　食べて！　わくわく", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1004012, 100431, 0, 0.0, 1, "誕生日おめでと！　ミソギ、\nにいちゃん大好き！　本当だよ！\nんーイタズラばっかりしてるから\n信じてもらえないや…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1004013, 100461, 0, 0.0, 1, "にいちゃん！\nパワーアップしたミソギの\nスペシャルなイタズラ\nぜったい見逃さないでね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1004014, 100461, 0, 0.0, 1, "にいちゃんとミソギなら\nどんな魔物もあっという間に\nやっつけられちゃうよね！\nえへへ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1004015, 100461, 0, 0.0, 1, "にいちゃんと一緒に居ると\nどんどん力が湧いてくる！\nこの力でにいちゃんに\nすっごいイタズラしちゃお♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1004016, 100461, 0, 0.0, 1, "どんどん新しいイタズラが\n浮かんでくるよ！\nあ～早く全部にいちゃんで\n試したいな～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1004017, 100461, 0, 0.0, 1, "にいちゃんも\nミソギのイタズラからは\n逃げられないよ！\n覚悟してね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1005001, 100501, 0, 4.6, 2, "あんたが困ったときは、是非とも\n自分を呼んでほしいッス！\n見習いでも、\n心はヒーローッスから！　", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1005002, 100501, 0, 3.2, 2, "一緒に悪の組織を退治しに\n行くッス！　半人前でも\n二人で行けば一人前ッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1005003, 100501, 0, 8.8, 4, "お父さんやお母さんが、やたらと\n可愛い恰好させようとするッス\nリボンとかレースとか…でも\n自分は飾りの少ない服が好きッス", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1005004, 100501, 0, 4.6, 5, "たまには二人で\n静かなところに行きたいッス\nヒーローにも、休息は\n必要ッスから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1005005, 100501, 0, 9.8, 5, "あんたと一緒にもっともっと\n遊びたいッスけど…これ以上\n仲良くなってもし秘密が…\nい、いやその何でもないッス！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1005006, 100531, 0, 0.0, 1, "とぉーうっ！\n悪に立ち向かう勇気がある限り\n正義は必ず勝つッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1005007, 100531, 0, 0.0, 1, "うおおおっ！　燃えてきたー！\n真のスーパーヒーロー目指して\n突撃ッスー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1005008, 100531, 0, 0.0, 1, "自分、世界平和のためなら\nどんな敵にも負けないッス！\nヒーローに二言は無いッスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1005009, 100531, 0, 0.0, 1, "あんたがくれた勇気で\n最強のヒーローに\nバッチリ変身ッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1005010, 100531, 0, 0.0, 1, "あんたと一緒なら勇気100倍、\nいや100万倍ッス！\nこれからも一緒に頑張るッスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1005011, 100501, 0, 5.3, 2, "えっ、今日誕生日なんスか！？\nじゃあ、お祝いにとっておきの\nトラタイガーグッズあげるッス！\nお近づきの印ッスよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1005012, 100531, 0, 0.0, 1, "ハッピーバースデー♪\nいつもお世話になってるアンタに\nトラタイガーのカップケーキッス！\n自分、手作りに挑戦したッスよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1005013, 100561, 0, 0.0, 1, "自分たちは正義のヒーローッス！\n種族なんて関係ない…\nみんなを守るために戦う！\n騎士団は、変わったんスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1005014, 100561, 0, 0.0, 1, "あんたも困ったことがあったら\n遠慮なく自分たちに相談するッス\nいつだって、力になるッスよ\nそれが、ヒーローッスから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1005015, 100561, 0, 0.0, 1, "みんなで力を合わせて\n大切なものを守る…\n一人だけじゃ難しいことも\n仲間がいたらなんとかなるッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1005016, 100561, 0, 0.0, 1, "なんの遠慮もなく、自分が\nこうしてお仕事できるのは\nあんたのおかげッス\nこれからも、ずっと一緒ッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1005017, 100561, 0, 0.0, 1, "自分の活躍、見ててほしいッス\nいろんな人のお世話になったッス\nけど、一番辛いとき、あんたは\nそばにいてくれた人ッスから…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1006001, 100601, 0, 3.5, 2, "お兄ちゃん、\nまたあとでサックスの練習、\n付き合ってくれますか？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1006002, 100601, 0, 5.0, 5, "はぁ、はぁ…\nもう練習できません…\nお兄ちゃん激しすぎます…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1006003, 100601, 0, 3.0, 4, "え？　もっとするの…？\n今日はもうヘトヘト…\n私、限界です、お兄ちゃん…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1006004, 100601, 0, 5.5, 2, "お兄ちゃんったら\nいつも激しすぎて、私…\nドキドキが止まらないんです…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1006005, 100601, 0, 4.5, 4, "あのね、お兄ちゃんは\n私とお姉ちゃんとどっちが…\nううん、何でもないです…", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1006006, 100631, 0, 0.0, 1, "ふふ、お兄ちゃん見てください！\nジャズのリズムに乗って\nアカリ流ポールダンスです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1006007, 100631, 0, 0.0, 1, "お兄ちゃん、そんなに\nアカリのが見たいんですか…\nそ、それじゃ…恥ずかしいけど\n行きます！　アカリ大車輪―！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1006008, 100631, 0, 0.0, 1, "はあ、はあ…もう、らめぇ…\nお兄ちゃんが上手だって\n褒めてくれるから、つい\n調子に乗って回りすぎちゃった♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1006009, 100631, 0, 0.0, 1, "め、目が回っちゃいました～\nお兄ちゃん抱っこしてください♪\nアカリをこんなにした責任…\nとってくださいね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1006010, 100631, 0, 0.0, 1, "こんなアカリの姿、誰にでも\n見せるわけじゃないですよ？\nお兄ちゃんは特別だから、\nアカリの全てを見てほしいの♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1006011, 100601, 0, 5.9, 5, "あ！　お兄ちゃんったら、\nまた大きくなっちゃいましたね\nなんだかアカリ、\nドキドキしてきちゃいました～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1006012, 100631, 0, 0.0, 1, "お兄ちゃん、お誕生日ですよね\nおめでとうございます！\nアカリと一緒に、\n大人の階段、上りましょうね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1006013, 100661, 0, 0.0, 1, "感じます、お兄ちゃんの力…\nあぁ、ドキドキが止まりません！\nいまのアカリなら、きっと\nなんでもできちゃいます♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1006014, 100661, 0, 0.0, 1, "お兄ちゃんのおかげで、アカリ、\n魔法だってうまくなったんです！\nかわいくて強い、最高の\nアカリになれちゃいました♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1006015, 100661, 0, 0.0, 1, "見ててくださいね、お兄ちゃん！\n一緒に特訓してきた成果を\n見せちゃいますから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1006016, 100661, 0, 0.0, 1, "お兄ちゃん、今日のアカリも\nかわいいですか？\nもう、恥ずかしがらないで、\nかわいいって褒めてほしいです～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1006017, 100661, 0, 0.0, 1, "お兄ちゃんとの特訓はいつも\n『ハジメテ』だらけで楽しいです！\nこれからも二人で『ハジメテ』を\nいっぱい体験しましょうね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1007001, 100701, 0, 4.5, 2, "１にプリン、２にプリン、\n３、４もプリンで、\n５も、もちろんプリンなの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1007002, 100701, 0, 6.0, 2, "お空を飛ぶのが好きなの～\nイタズラするのも好きなの～\nでもプリンは\nもーっと好きなの～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1007003, 100701, 0, 5.0, 6, "魔物に追いかけられて、\n死ぬかと思ったの～\n…って、よく考えたら、\nもう死んでたの！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1007004, 100701, 0, 7.0, 6, "ミヤコは幽霊なの～\nだから、オマエが死んでも\nずっと一緒なの～♪\nん？　うれしくないの？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1007005, 100701, 0, 3.0, 5, "今日は一緒にお出かけするのー\nだから今日はずっと、\nオマエに憑いてるの～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1007006, 100731, 0, 0.0, 1, "もっともっともっともっと\nたくさんプリンを買って来るの！\nそうじゃないとミヤコ、\n成仏しちゃうかもしれないの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1007007, 100731, 0, 0.0, 1, "ミヤコのプリンは、\n絶対にあげないの～！\nもし一口でも盗ったら、\n夢枕に立ち続けてやるの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1007008, 100731, 0, 0.0, 1, "幽霊は人を怖がらせるものなの～\nオマエも、もっと怖がれなのー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1007009, 100731, 0, 0.0, 1, "ムー！　オマエはミヤコとプリン\nどっちが大事なの！？\n…どっちも大事？\nそれならいいの～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1007010, 100731, 0, 0.0, 1, "ミヤコばかり幽霊でずるいの！\nオマエも死んで幽霊になるの！\nそうすれば、二人でずっと\nいっしょにいられるの～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1007011, 100701, 0, 5.8, 2, "ミヤコは死んでるけど、誕生日の\nお祝いは大事にしてるの！\nだから早くバースデープリン\nパーティーを始めるの～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1007012, 100731, 0, 0.0, 1, "おめでとうなの！　誕生日は、\n仲良しとごちそうを食べるの\nだから今日は、ミヤコと一緒に\nプリンケーキを食べるの～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1007013, 100761, 0, 0.0, 1, "ふっふっふ……\nおもしろいことするヤツらなの\nだからお返しに、もっとも～っと\nおもしろいことをしてやるの…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1007014, 100761, 0, 0.0, 1, "冥土のおみやげに\nオマエらをプリンにしてやるの\nプリンはおみやげに\nもってこいなの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1007015, 100761, 0, 0.0, 1, "オマエの恨みはミヤコが晴らすの\nだからスプーン二本なの\n二人分の恨み、\nミヤコが思い知らせてやるの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1007016, 100761, 0, 0.0, 1, "ミヤコのものはミヤコのものなの\nミヤコがほしいと思ったから\nオマエはもうミヤコのものなの！\nじょーしきなの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1007017, 100761, 0, 0.0, 1, "オマエが幽霊でも\n幽霊じゃなくても、関係ないの\nオマエがオマエなら\nミヤコはそれでいいの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1008001, 100801, 0, 7.0, 2, "あ、良いところに来てくれた！\nより可愛く見える角度を、\nさっき発見したんだよ！\nキミも、見て見て！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1008002, 100801, 0, 6.5, 2, "このボクの美しさ…\nいつか、「歩く観光名所」って\n呼ばれちゃったりするのかな？\nガイドブックを作らないと！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1008003, 100801, 0, 7.0, 1, "あっ、キミ！　いいところに！\nボクの美しさがとどまることを\n知らないんだ！\n止めて！　いや、止めないで！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1008004, 100801, 0, 4.5, 2, "こんなに可愛いボクと\n一緒にいられること、喜んでね？\nキミは、世界一の\n幸せ者なんだから♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1008005, 100801, 0, 4.0, 2, "ボクは、キミといたいから\nここにいるんだよ\n引き立て役ってわけじゃ\nないから、安心してね！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1008006, 100831, 0, 0.0, 1, "どんな宝石や宝物も…\nボクの美しさと輝きには\n勝てないんだなあ…\n罪作りな、ボク♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1008007, 100831, 0, 0.0, 1, "ああ、本当にボクは美しい♪\nボクの美しさを表現するには…\nいや、言葉じゃ表現できないよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1008008, 100831, 0, 0.0, 1, "ボクほど美しくない、なんて\n恥ずかしがることじゃないよね\nだって改めて言うまでもなく\n当たり前じゃない、そんなの♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1008009, 100831, 0, 0.0, 1, "いつか、どんな宝石よりも\n価値のあるこのボクを…\nキミにプレゼントするって\n言ったら、キミは喜ぶかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1008010, 100831, 0, 0.0, 1, "このボクがここにいるという\nプレシャス…キミと\n二人で作っていくときめき…\n世界は希望に満ちているんだね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1008011, 100801, 0, 9.9, 2, "誕生日おめでとう♪\n今日のキミは輝いて見えるね\n綺麗で美麗で華麗で壮麗だ\nきっと世界で二番目に美しいよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1008012, 100831, 0, 0.0, 1, "ハッピーバースデー♪\n今日はボクの美しさを、\n独り占めにしてもいいよ♪\n全部受け止めきれるかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1008013, 100861, 0, 0.0, 1, "ボクの美しさはもはや神秘の領域\n奇跡や魔法に等しいよ\nボクと同じ時代を生きてることを\n誉れに思ってよね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1008014, 100861, 0, 0.0, 1, "笑顔も泣き顔も、怒った顔だって\n究極にかわいいボク♪\nねえ、キミはどのボクが\n一番好きなのかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1008015, 100861, 0, 0.0, 1, "もし美しさが罪になるのなら\nボクは世界一の大罪人に\nなっちゃうね！　ああ、自分の\n美しさが恐ろしいよ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1008016, 100861, 0, 0.0, 1, "ここまでボクについてこられる\nなんて、キミは想像以上だよ\nそんなにボクの隣がいいのかい？\nならこれからもいるといいよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1008017, 100861, 0, 0.0, 1, "常に更新し続ける美の最高到達点\nそれを見続ける権利を\nキミにあげるよ！　ふふ、これで\nもうボクから離れられないね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1009001, 100901, 0, 3.5, 2, "貴様も早く前世の記憶を\n取り戻すのだ！　我が宿敵にして\n盟友、閃光のシグルドよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1009002, 100901, 0, 2.5, 2, "我が真名はちゃんと覚えたか？\nアンネローゼ・フォン・\nシュテッヒパルムだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1009003, 100901, 0, 5.0, 3, "くっ…！　魔力を封じた\n「瞳」が疼く…！\n目薬？　いらんわっ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1009004, 100901, 0, 5.5, 2, "シグルドよ、現世においても\n我ら盟友の絆は不滅だ！\nはーっはっはっはっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1009005, 100901, 0, 5.0, 5, "我ら二人で手を取り合い\n『機関』の野望を打ち砕こう！\nそしてその暁には、前世と同じく\nめ、夫婦の誓いを…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1009006, 100931, 0, 0.0, 1, "その目に、魂に刻めっ…！\nこれぞ我が禁断の\n最上究極殲滅魔法だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1009007, 100931, 0, 0.0, 1, "貴様も技を持っているなら\nかっこよく放つべきだ！\n例えばだな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1009008, 100931, 0, 0.0, 1, "魔力を込め続けた\n私の瞳が輝きを放つ時\n敵の命運は尽きる…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1009009, 100931, 0, 0.0, 1, "防御を捨て、攻撃に特化した\n『撃滅形態』をとくと見よ！\n…ど、どこを見ているのだ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1009010, 100931, 0, 0.0, 1, "シグルドよ…貴様のおかげで\nいつも私は自分を貫けている\nだから…ありがとう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1009011, 100901, 0, 6.8, 2, "今日は貴様が転生した日か\n記念に私からこれを授けよう！\n見よ！　暗黒騎士の紋章を！\n…どうだ。かっこいいだろう？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1009012, 100931, 0, 0.0, 1, "シグルドよ、誕生日おめでとう！\n私からは日記をプレゼントだ！\n我らの活動を書き残すがいい！\n…字は書ける、よな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1009013, 100961, 0, 0.0, 1, "慄くがいい！\n何人たりとも\n我が魔瞳の魔力には\n抗えはしない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1009014, 100961, 0, 0.0, 1, "さあ、始めようか！\n破壊と再生…\nあらたな神話を\n目に焼き付けるがいい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1009015, 100961, 0, 0.0, 1, "世界が終末を迎えようとも…\n我らの絆は滅びはしない！\nそうだろう？　シグルドよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1009016, 100961, 0, 0.0, 1, "シグルド、失われし我が半身よ…\nって、聞こえてたのか？\nいや、その…\nな、なんでもないってばぁ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1009017, 100961, 0, 0.0, 1, "我が封印は完全に解かれた！\n魂の盟友と共にある限り、\n疾風の冥姫は止められん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1010001, 101001, 0, 5.5, 2, "王子はん、王子はん\nおぶういかがどすかぁ？\nおぶういうんはお茶どす！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1010002, 101001, 0, 6.0, 2, "女の子は夢の国で暮らすことに、\n夢を見るんどすなぁ\n運命の王子はんと夢の国～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1010003, 101001, 0, 5.5, 2, "古今東西の絵本ではなぁ、\nお姫はんを守るのは、\n王子はんの役目なんよ？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1010004, 101001, 0, 4.5, 2, "怪我したときは、\nうちを頼ってほしいわぁ\n特別な呪文があるんどす\n痛いの痛いの飛んでけ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1010005, 101001, 0, 5.5, 2, "朝、目を覚ましたら、\nまずは小鳥はんたちと、\nおはよーって挨拶するんどす～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1010006, 101031, 0, 0.0, 1, "『くるりんぱっ☆　』あららぁ、\nここ洞窟やってんなあ\nでもうちの魔法で明るうなったし\nそれにとってもきれいですやろ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1010007, 101031, 0, 0.0, 1, "え、手ぇ引いてくれはるん？　\nおおきに、嬉しおす…\nほなうちは王子はんの足元明るう\n照らして、一緒に歩きまひょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1010008, 101031, 0, 0.0, 1, "王子はんの道筋はうちが\n照らします～\nせやから、王子はんは安心して\n一緒にいておくれやす～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1010009, 101031, 0, 0.0, 1, "王子はんとならどこにいても\nお伽噺の世界になるわ～\nさあ王子はん、マホ姫を連れて\n探検に参りまひょ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1010010, 101031, 0, 0.0, 1, "ずーっと一緒に歩きまひょ\nたまぁに足踏み外したかて\n二人でいてたら心配あらへんよ\nせやろ？　マホ姫の王子はん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1010011, 101001, 0, 4.0, 5, "誕生日、おめでとうさん♪\n王子はんの誕生日を\n一緒にお祝いできて幸せやわぁ♪\nこれからもほっこりしようなぁ～", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1010012, 101031, 0, 0.0, 1, "今日が誕生日なんどすか？\nそれは、ほんにめでたいなあ～\n今日をマホマホ王国の\n記念日にしたいぐらいやわあ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1010013, 101061, 0, 0.0, 1, "さ～て、みんなを\nマホマホ王国にご招待や～\nみらくるまほりんくるりんぱ♪\nも一つおまけにくるりんぱ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1010014, 101061, 0, 0.0, 1, "マホマホ王国のパレード開始や～\n王子はんも、\nマホマホ王国の王子として、\nみんなと一緒に楽しんでな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1010015, 101061, 0, 0.0, 1, "これがうちと王子はんの力…\nこないなことができるなんて、\nやっぱり王子はんとうちは\n運命の相手なんやね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1010016, 101061, 0, 0.0, 1, "王子はんがそばにおってくれたら\nうち、なんでもできる気がする…\n王子はん、\nこれからもずっとそばにいてな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1010017, 101061, 0, 0.0, 1, "王子と姫がそろうとき、\nマホマホ王国への扉が開かれる…\nさあ、王子はん\n二人でマホマホ王国に凱旋や～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1011001, 101101, 0, 4.0, 6, "お兄ちゃんと私は、\n血は繋がってないけど\nきょうだいなの！\nえ？　よくわかんない？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1011002, 101101, 0, 3.0, 6, "私の弓は百発八中！\nん？　これじゃあ、ほとんど\n当たってないことに…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1011003, 101101, 0, 4.0, 1, "今度お兄ちゃんと、\nスイーツ巡りしたいなぁ\nつきあってくれますか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1011004, 101101, 0, 4.0, 2, "お兄ちゃんが一緒なら、\nなんだってできる気がします！\nよーし、どんどん行きましょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1011005, 101101, 0, 6.0, 2, "私たちの絆、どんどん深まってる\nお兄ちゃんも…\nそう思ってくれてたらいいなぁ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1011006, 101131, 0, 0.0, 1, "なんか、最近は\n「ケーキがいい」んですって！\n早速、食べに行きましょうよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1011007, 101131, 0, 0.0, 1, "弓も上達しなくっちゃ…\nお兄ちゃんの前で\nカッコ悪いところ\n見せられないもん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1011008, 101131, 0, 0.0, 1, "「習うより離れろ」って、\nお兄ちゃんに勉強\n教わってちゃダメってこと？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1011009, 101131, 0, 0.0, 1, "えへへ、この調子だと…\nお兄ちゃんのハートを射抜ける\n日も、近いかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1011010, 101131, 0, 0.0, 1, "お兄ちゃんを傷つける相手は\n私がこの弓で、ぜーんぶ\nやっつけちゃうんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1011011, 101101, 0, 7.2, 2, "お誕生日おめでとうございます！\nお兄ちゃん、やっぱり誕生日は\n家族と過ごすべきですよ\n私とパーティーしましょう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1011012, 101131, 0, 0.0, 1, "ハッピーバースデー！\nお兄ちゃんが大きくなっても、\n私が歳を取っても\n二人は兄妹のままですからね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1011013, 101161, 0, 0.0, 1, "妹さえいれば兄に金棒ですよ！\n私の活躍しっかり見てて下さいね\nお兄ちゃん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1011014, 101161, 0, 0.0, 1, "お兄ちゃんといっしょですから\n今の私は無敵ですよ！\nイヤでも鉄砲でもどんとこいです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1011015, 101161, 0, 0.0, 1, "例えお兄ちゃんになにがあっても\n私はお兄ちゃんの妹ですからね\n絶対に見つけて、再会して…\nそして、ずっと一緒にいるんです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1011016, 101161, 0, 0.0, 1, "任せてください、お兄ちゃん\n私がいる限り、お兄ちゃんには\n指一本触れさせませんからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1011017, 101161, 0, 0.0, 1, "お兄ちゃんのおかげで、\nとってもいい調子なんです\nこのまま恋も受験も\nバッチリいただいちゃいますよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1012001, 101201, 0, 4.0, 1, "何かあったら、\n私にどーんと頼っていいんだよ！\nハツネちゃんが、\n全力で助けるのだー！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1012002, 101201, 0, 3.0, 1, "ぐっすり眠って元気いっぱい！\nほらほらぼーっとしてると\n置いてっちゃうぞー！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1012003, 101201, 0, 5.0, 5, "ん～、ちょっと眠たいけど…\nじょぶじょぶ大丈夫！\nきら～ん☆", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1012004, 101201, 0, 9.0, 6, "スヤスヤ…\nシオリン…柔らか～い…\nんん？　あれ？　キミだったの？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1012005, 101201, 0, 6.0, 2, "いぇ～い、ピースピース！\n魔物なんか、\n超能力で一発だよ！\n私は強いのだぁ～！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1012006, 101231, 0, 0.0, 1, "ねえねえ、キミも一緒に\n寝てみない？　太陽さんを\nいーっぱいあびた草原のお布団！\nとっても気持ちいいよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1012007, 101231, 0, 0.0, 1, "あのね、超能力をたくさん\n使っちゃっても、こうやって\n大地のパワーでチャージ完了！\nバッチリだよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1012008, 101231, 0, 0.0, 1, "えへへ\nキミの側にいると、ポカポカ\nしてきて、なんだか…眠く\n…ふあぁ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1012009, 101231, 0, 0.0, 1, "スヤスヤ…\nキミがいると、気持ちいい…\nいい夢見られそう…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1012010, 101231, 0, 0.0, 1, "なんだか、いい夢\n見てた気がする\nキミと一緒に…\nえへへ、予知夢だったらいいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1012011, 101201, 0, 7.2, 2, "お誕生日のお祝いに、\nハツネ膝枕をプレゼント！\n寝心地いいよ～☆\nん～なんだか私も眠く…すやぁ～", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1012012, 101231, 0, 0.0, 1, "お誕生日だね。おめでとうー！\nお祝いに超能力で…\nあっで、でも眠くなっちゃうし～\nじゃあ……………………カプッ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1012013, 101261, 0, 0.0, 1, "いっくよー☆　きらーん☆\nきっらきらのお星さま、\nみ～んなにプレゼントだよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1012014, 101261, 0, 0.0, 1, "今日のハツネちゃんは\n絶好調なのだ！\nキミと一緒なら、\n何でもできちゃう気がするよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1012015, 101261, 0, 0.0, 1, "それー！　ハツネちゃんの\nスターライト☆ショーだよ～！\nキミには一番の特等席で\n見せてあげちゃうね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1012016, 101261, 0, 0.0, 1, "それ！　きらーん☆\nキレイだからって、\n星ばかり見てちゃダメだからね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1012017, 101261, 0, 0.0, 1, "見て見て☆\nハツネちゃんのとっておき\nキミだけに見せちゃうぞ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1013001, 101301, 0, 7.3, 2, "日課の定期巡回！\nレアな魔導書なんかあった日には\nwktkが止まりませんぞー！\nさあ、いざ君もこっちの世界へ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1013002, 101301, 0, 1.5, 5, "ヒャッハー！\n推しキャラの抱き枕に顔を埋めて、\n魔力を充電せねばー！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1013003, 101301, 0, 7.4, 2, "私はたくさんの人を守る魔法少女\n…老若男女、全ての命を平等に\n…もちろん、大きなお友達もね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1013004, 101301, 0, 5.2, 2, "君にはお世話になってるし、\nたまにはナナカちゃんが\nなにか奢ってあげよー！\nよーし、支払いはまかせろー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1013005, 101301, 0, 4.5, 2, "私は魔法少女としてみんなを\n笑顔にしたいのだ！\nもちろん、君のこともね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1013006, 101331, 0, 0.0, 1, "絶大な力の前に絶望するナナカ\nそんな中、新たな魔導書の力が\nナナカに奇跡をもたらす！\n次回、ナナカ死す！　お楽しみに", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1013007, 101331, 0, 0.0, 1, "新しい魔法を使う時って\nすごくドキドキするんだー！\n思った通りに成功すると、\nもうテンションマックス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1013008, 101331, 0, 0.0, 1, "この魔法は、私自身の命を糧に\n放つ最後の必殺技…えへへ\n君との毎日、楽しかったな\n…っていう展開よくない！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1013009, 101331, 0, 0.0, 1, "君と私のバディタッグで挑めば、\nどんな敵だって怖くないよ！\n私たちは同志であり相棒！\n二人で一人の魔法少女なのだー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1013010, 101331, 0, 0.0, 1, "私の魔法少女物語は、\n君っていう相棒がいてこそ！\n私たち二人で、みんなを守れる\n魔法少女になろうね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1013011, 101301, 0, 2.8, 1, "ハッピーバースデイッ！！\nキミへのプレゼントはこれだっ！\n私のおすすめ作品全185巻！\n…持って帰れるかな？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1013012, 101331, 0, 0.0, 1, "お誕生日おめでとーっ！\n眼鏡をプレゼントだー！\nつけてみて！　…うん！\nパーペキ！　似合ってるよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1013013, 101361, 0, 0.0, 1, "ふおおー！　力がみなぎる！\nこれが君と私の最強奥義…\n二人で一人の魔法少女\nサイツヨの合体攻撃だーーッ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1013014, 101361, 0, 0.0, 1, "これは…、君の力が私の中に\n流れ込んでる、ってコト？\nえへへ、今の私は真のサイツヨ！\nなんでもできる気がするよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1013015, 101361, 0, 0.0, 1, "私が！　私たちが！\nサイカワサイツヨ\n魔法少女なのだ！\nいくぞ相棒！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1013016, 101361, 0, 0.0, 1, "今ここに！　ナナカちゃんの\n真の力が解放されたのだ！\nなーんちゃってね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1013017, 101361, 0, 0.0, 1, "ふっふっふ、君と私の絆の力が\n最強の魔法なのだ～っ！\nって、真顔でうなずかれると\nちょっと恥ずかしいんだけどぉ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1014001, 101401, 0, 0.0, 1, "私の好きな推理小説の\n新作が出るんだ\nよければ君もどうだ？\n全巻貸すよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1014002, 101401, 0, 5.4, 2, "たまには、探偵と助手として\nではなく、オフの状態で\n出かけないかい？\n頭にも休憩が必要だからね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1014003, 101401, 0, 6.2, 1, "昼夜逆転になりがちな仕事だし\nとにかく歩き回るし根気がいるし\n…の割に、君はよくやって\nくれているね。助かっているよ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1014004, 101401, 0, 4.8, 2, "助手くんとなら\nどんな難しそうな事件も\n一発解決間違いナシだねっ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1014005, 101401, 0, 6.5, 5, "今までは一人で\n探偵をしていたけれど、\nあの頃に戻りたいとは思わない…\n君と出会ってしまったからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1014006, 101431, 0, 0.0, 1, "そぉーれ～っ！\nふふっ【自警団（カォン）】が誇る\n名探偵の力を\n見くびってもらっては困るよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1014007, 101431, 0, 0.0, 1, "はやての速度で事件を暴き\n犯人確保は雷撃のごとし！\n名探偵・カスミに\nお任せあれ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1014008, 101431, 0, 0.0, 1, "荒事は好みじゃないけど…\n事件解決のためなら\nおとなしくしているつもりは\nないんだよっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1014009, 101431, 0, 0.0, 1, "探偵と助手という関係の\n私たちだけど、今はそれを\n抜きにしても君のことを大事な\nパートナーだと思っているよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1014010, 101431, 0, 0.0, 1, "的確にフォローしてくれる\n君がいるから、私は存分に\n力を振るえるんだよ\nまさに名探偵名助手、だねっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1014011, 101401, 0, 6.7, 5, "今日は助手くんの誕生日\nなんだって？　おめでとう！\nなら、捜査は早めに切り上げて\nお祝いをしようじゃないか！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1014012, 101431, 0, 0.0, 1, "誕生日の余興に、今日は\n助手くんが探偵役をしてみよう\n…ねぇ先生、今日はどんな謎に\n挑まれるんですか？　ふふっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1014013, 101461, 0, 0.0, 1, "この手にあるのは真実の輝き\nずっと、私が求めていたものさ\n今度はちゃんと笑顔を贈るよ\nキーリ…大切な、もう一人の私", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1014014, 101461, 0, 0.0, 1, "迷い惑う人々に救いの光を与える\n知的で格好良い名探偵、か\n…そう在り続けたいものだね\n『私自身』への、誓いとして", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1014015, 101461, 0, 0.0, 1, "名探偵としての私\n儚く弱々しい私\nどちらも紛れもなく私だよ\n全部見せられるのは、君だけさ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1014016, 101461, 0, 0.0, 1, "『大好きです、先輩♪』\nわぁ違う！　今のは私じゃなくて\nキーリが言ったんだよ！\nもうっ、正直すぎるよキーリは！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1014017, 101461, 0, 0.0, 1, "…ねぇ、助手くん。私たちは\n探偵と助手としてなかなかの\n名コンビになったと思うんだ\nだから、ね…次は仕事以外でも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1015001, 101501, 0, 3.0, 2, "あらあら、先生に\n会いに来てくれたの？\nふふふ、嬉しいわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1015002, 101501, 0, 3.0, 2, "今日はどんなご本を\n読もうかしら…\nあなたは、どれが好き？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1015003, 101501, 0, 1.0, 5, "うふふ♪\n先生のことは、\nお母さんだと思っていいからね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1015004, 101501, 0, 0.0, 2, "いいこいいこ♪\n先生がずっと\nそばにいるからね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1015005, 101501, 0, 4.0, 5, "先生はいつでも\nあなたの味方\nたくさん、甘えていいのよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1015006, 101531, 0, 0.0, 1, "もしもなにか辛いことがあったら\n遠慮しないで先生に言ってね？\nなにがあっても、私があなたの\n力になってあげるから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1015007, 101531, 0, 0.0, 1, "いつもお疲れ様♪\nあなたが頑張っているところ、\n先生はちゃーんと見ているからね\nいい子いい子", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1015008, 101531, 0, 0.0, 1, "あらあら、怪我しちゃったの？\nこっちに来て見せてごらん？\n先生が治してあげるわね～\nほーら、痛いの痛いの飛んでけ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1015009, 101531, 0, 0.0, 1, "今日の私は、あなただけの\nミサト先生ですよ～\n子どもたちには秘密よ？\nやきもちやかれちゃうもの♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1015010, 101531, 0, 0.0, 1, "私と一緒にいて安心するって\n思ってくれたらすごく嬉しいわ\n私も、あなたといると胸が\nとってもあったかくなるの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1015011, 101501, 0, 10.4, 2, "今日は、一年に一度の特別な日\nあなたの誕生日をお祝いできて\n先生とっても嬉しいわ\n本当におめでとう♪", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1015012, 101531, 0, 0.0, 1, "お誕生日おめでとう！\n今日はあなたが主役だから、\nなんでも言うこと聞いちゃうわ～\nいっぱい甘えていいのよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1015013, 101561, 0, 0.0, 1, "私があなたを癒してあげる…♪\n心も体も、全部先生に任せてね\n今だけは何も考えずに\n私のぬくもりを感じてていいのよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1015014, 101561, 0, 0.0, 1, "体の奥から、温かい力が\nあふれてくるの\n私もまだ成長できるのね\nあなたと一緒に…うふふ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1015015, 101561, 0, 0.0, 1, "そろそろ子ども扱いは\nやめた方がいいのかしら…\nえ？　まだいいの？\nあらあら、甘えん坊さん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1015016, 101561, 0, 0.0, 1, "先生にしてほしいことはある？\n…あらあら、そんなことを？\nうふふ。いいわよ、いらっしゃい\nあなたのお願いだものね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1015017, 101561, 0, 0.0, 1, "先生の役目はあなたを見守ること\nあなたさえよければ、\nずっと私をそばにいさせてね\n成長するあなたを、見ていたいの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1016001, 101601, 0, 2.0, 1, "ヒデサイ、ちょす～！\nまた計算手伝ってほしいんだけど\nお願いしてもいいかなー？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1016002, 101601, 0, 5.0, 6, "ねーねー、ヒデサイってさ\n九九どの段まで覚えてるのー？\nうっそー？　マジ？　超天才ー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1016003, 101601, 0, 4.5, 1, "今日はねー、このあとイベントで\nモデルの仕事やるんだー\n良かったら見に来てよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1016004, 101601, 0, 7.0, 2, "ヒデサイと話してると、なんか\nほっこりする～…あったか～い\nえ？　ウソじゃないよ、マジマジ！\nガチバナだって～！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1016005, 101601, 0, 4.0, 1, "ヒデサイ～、ちょす～！\n一緒にごはんしよ！\n最近みつけた美味しいお店、\nうちが教えてあげる～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1016006, 101631, 0, 0.0, 1, "うちの愛の矢で\nファンのみんなを\nメロメロにしちゃうよ～\nもちろんヒデサイもね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1016007, 101631, 0, 0.0, 1, "今度の撮影で使う\n新しいポーズ考えてたんだ～\nこんなのどう？\nツヨカワイイ感じでよくない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1016008, 101631, 0, 0.0, 1, "うわぁ、海ちょ～キレー！\n海なんて何回も見たことあるけど\nこれまでで一番キレイかも！\n一緒に見てる人のおかげかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1016009, 101631, 0, 0.0, 1, "モデル仲間に、ヒデサイのこと\nカレシだと思われちゃったんだ\nマジうける～！　けど、うちも\n少しは…んん…何でもない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1016010, 101631, 0, 0.0, 1, "うちのこと、モデルだからって\n特別扱いしなくていいよ！\nうちから見ればヒデサイのほうが\nよっぽどすごいんだからさ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1016011, 101601, 0, 4.0, 1, "ハッピーバースデー！\nうちがお祝いしてあげる☆\nん～誕生日に一緒なんて\nカップルみたいかも？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1016012, 101631, 0, 0.0, 1, "今日はヒデサイの誕生日だよね！\n学院のみんなでパーティーしよ？\nあ、でも、二人きりのパーティー\nってのも楽しいかも♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1016013, 101661, 0, 0.0, 1, "うちが楽しいって思うこと\nみんなにも楽しいって\n思ってもらいたいんだよね！\nもち、ヒデサイにもね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1016014, 101661, 0, 0.0, 1, "ランウェイの上から、絶対に\nヒデサイを見つけちゃうし！\nだからヒデサイも、うちのこと\n見ててね？　絶対約束だかんね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1016015, 101661, 0, 0.0, 1, "うちのこと、これからもずーっと\n夢中にさせちゃうよ！\nヒデサイの視線は、うちから\n絶対に外させないんでよろ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1016016, 101661, 0, 0.0, 1, "ヒデサイが応援して\nくれてるってだけで\nうちすっごく楽しいんだよ？\n人生楽しんだもん勝ちっしょー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1016017, 101661, 0, 0.0, 1, "ばっきゅ～ん♪\nどうどう？　うちのランウェイ！\nもにカワっしょ？　あ、ヒデサイ\n顔赤くなっててわろ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1017001, 101701, 0, 1.5, 2, "ハイターイ！\n元気にあいさつすると、\n気分がいいさ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1017002, 101701, 0, 3.0, 2, "焦らずのんびりやっていくさ～\n急いでも、いいことなんて\nなんにもないさ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1017003, 101701, 0, 4.0, 2, "ゴーヤーにソーキ…\nサーターアンダギー！\n故郷の食べ物が恋しいさ～", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1017004, 101701, 0, 0.0, 2, "キミと一緒なら、\nどんな冒険もなんくるないさ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1017005, 101701, 0, 4.0, 2, "いつかキミに、\n故郷を見せてあげたいさー\n青い海に白い砂浜！\n絶対に感動するさー！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1017006, 101731, 0, 0.0, 1, "ハイターイ！\n今日も楽しくなりそうさー\nワクワクして、\n踊らずにはいられないさ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1017007, 101731, 0, 0.0, 1, "友達いっぱいで、\n毎日楽しいさー\n楽しすぎて、\nついつい踊り出しちゃうよ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1017008, 101731, 0, 0.0, 1, "一緒に海に行くさ！\n水の中は、\n最高に気持ちいいさ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1017009, 101731, 0, 0.0, 1, "水浴びは気持ちいいさー！\nキミも一緒に入ろうよ～！\n転ばないように\n手つないであげるよー ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1017010, 101731, 0, 0.0, 1, "みんなで踊ると楽しいさ～！\nやさやさ、\nキミも一緒に踊るさ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1017011, 101701, 0, 4.2, 2, "おお～！\n今日はキミの誕生日なの～？\nそうと聞いたら\n全力でお祝いするさ～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1017012, 101731, 0, 0.0, 1, "誕生日おめでとうさ～！\nお祝いに私が、\n24時間連続で祝いの踊りを\n踊り続けてあげるさ～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1017013, 101761, 0, 0.0, 1, "キミ、見て見て～\nお月さまと夜のダンスさ～！\nキラキラして、綺麗でしょー？\n…あは～、見つめすぎだよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1017014, 101761, 0, 0.0, 1, "なんくるないさー！\nはい、言ってみて～？\n…うんうん、いいね～♪\nいつでも私の故郷に住めるよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1017015, 101761, 0, 0.0, 1, "キミと一緒にいると、いつだって\n胸の奥のリズムが高鳴るね～♪\nどうしてかな～？\nねぇ、どうしてだと思う～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1017016, 101761, 0, 0.0, 1, "海辺で踊ってると\n私の故郷を思い出すよ～\nいつかキミを連れて\nあの海辺を歩いてみたいさ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1017017, 101761, 0, 0.0, 1, "ちょっと手を出して～？\n…よしっ、つかまえたよ～♪\nもう逃げられないからね～？\n夜が明けるまで、踊ろうね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1018001, 101801, 0, 9.0, 2, "私、こう見えても新米教師なの\nもし何か国語でわからないことが\nあったら質問してね\n先生が教えてあ・げ・る♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1018002, 101801, 0, 7.5, 5, "私、担当は国語なんだけど\n少女漫画とか恋愛小説のほうが\nもっと得意よ\nそういう科目があればいいのに～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1018003, 101801, 0, 5.5, 2, "魔族の女子校時代…\n楽しい思い出がいっぱいあるわ\n君もこの学院で\nすてきな時間を過ごしてね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1018004, 101801, 0, 10.0, 6, "宿題、手こずってるの？\n先生がじっくり教えてあげる♪\nまず…この文章の意味は…\n…え？　くっつきすぎ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1018005, 101801, 0, 8.0, 5, "二人で歩いてるとよくカップルに\n間違われちゃうわね\n恋人同士に見えるのかな？\n私はイヤじゃないけど…　", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1018006, 101831, 0, 0.0, 1, "こ、ころんじゃったぁ～\n教師なんだし、もっとしっかり\nしなくちゃって思うんだけど\nまだまだなのよねぇ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1018007, 101831, 0, 0.0, 1, "私、天然キャラじゃないのよ？\nたま～にうっかりすることが\nあるだけで…ほんとよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1018008, 101831, 0, 0.0, 1, "魔物さんに襲われたんだけど、\n私の顔を見たら、\nまっ赤になって倒れちゃって…\nどうしたのかしら？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1018009, 101831, 0, 0.0, 1, "あのね、もしまた転んじゃったら\n助けてくれる？\nえへへ、私すっかり君の事頼りに\nしちゃってる。先生なのにね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1018010, 101831, 0, 0.0, 1, "初めての彼氏はどんな人かな\n転んでもバカにせず助けてくれる\n君みたいに優しい人がいいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1018011, 101801, 0, 6.2, 2, "お誕生日おめでとう～♪\n昨日より一つ大人になったのね\nお祝いに先生が、\nナデナデしてあげるわ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1018012, 101831, 0, 0.0, 1, "今日は君の誕生日ね～！\n年下の男の子が、結婚できる\n年齢になってプロポーズ…なんて\n小説みたいであこがれちゃうな♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1018013, 101861, 0, 0.0, 1, "君にだけ\n特別な授業をしてあげる\nじっくり教える予定だから\nサボっちゃダメよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1018014, 101861, 0, 0.0, 1, "いつも優しくて頼れる君の姿から\n色んなことを学んでるのよ\n例えばどんなことかって？\nふふ、いいわよ。教えてあげる", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1018015, 101861, 0, 0.0, 1, "たまには愛の鞭も必要かしら！\nあれ？　どうしてうれしそうな\n顔をするの？\nしっかり反省して欲しいのに～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1018016, 101861, 0, 0.0, 1, "君のおかげで私も前に進めたわ\n特大の花丸よりもすごいご褒美を\nあげなきゃ！\nどんなことかは、まだ内緒よ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1018017, 101861, 0, 0.0, 1, "どうしていつも彼氏じゃないって\n否定しないの？　先生のこと\nあんまりからかっちゃだめよ\n勘違いしちゃうじゃない", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1020001, 102001, 0, 8.0, 2, "あか、しろ、きいろ～\nきれいに咲いたお花さん～♪\n虫さんブーン\nちょうちょさんヒラヒラ～♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1020002, 102001, 0, 3.5, 2, "うさぎさん～♪\nネコさん♪　イヌさん♪\nみんな、みんな、おともだち～♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1020003, 102001, 0, 4.0, 2, "おにいちゃん、\nミミと遊んでくれるの？\nやったぁ！　うれしいな～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1020004, 102001, 0, 4.0, 2, "ミミね、おにいちゃんのこと、\nだいすき～！\nうさぎさんも、お花さんも、\nみんなすき～！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1020005, 102001, 0, 2.5, 5, "おにいちゃんすき～！\nギュー！\nおにいちゃんも、ギューして～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1020006, 102031, 0, 0.0, 1, "わぁ、プチグリフォンさん、\nはやーい！\nおにいちゃん、はやくはやく～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1020007, 102031, 0, 0.0, 1, "ミミね、お花のかんむりが\n作りたいな♪　できたら\nおにいちゃんにつけてあげるね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1020008, 102031, 0, 0.0, 1, "パパとママとおともだちと\n動物さんとお花さんと\nあと、おにいちゃん！\nぜんぶミミのだいじなものだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1020009, 102031, 0, 0.0, 1, "ここ、ないしょのお花畑なんだ！\nでもね、おにいちゃんには\nとくべつにおしえてあげる\nだってだいすきなんだもん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1020010, 102031, 0, 0.0, 1, "ミミもプチグリフォンさんも\nおにいちゃんのことだ～いすき♪\n一緒にいられてうれしいな～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1020011, 102001, 0, 2.7, 5, "お誕生日おめでとう～♪\nミミも早く大きくなって、\nおにいちゃんの、\nおねえちゃんになりたいな～♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1020012, 102031, 0, 0.0, 1, "ミミ、お祝いのお歌をうたうね\nるんたったるんたった～♪\nお誕生日おめでと～♪\n大好き、大好き♪　おにいちゃん♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1020013, 102061, 0, 0.0, 1, "えへへ、ミミ、とんでるよ♪\n行こう、プチグリフォンさん\nお空のむこうまで\nご～ご～ご～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1020014, 102061, 0, 0.0, 1, "ミミとプチグリフォンさんは\nさいこうのおともだち♪\nずっとず～っと\nいっしょだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1020015, 102061, 0, 0.0, 1, "おにいちゃんどう？\nミミ、カッコいいかな\nおねえさんできてるかな？\nほんとに？　やったやった～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1020016, 102061, 0, 0.0, 1, "ミミがおとなになったら\nおにいちゃんといっしょに\n遠くまでいきたいな～\nはやくおっきくな～れ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1020017, 102061, 0, 0.0, 1, "これまでおにいちゃんに\n助けてもらってばかりだったけど\n今度はミミがおにいちゃんを\n守ってあげるね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1021001, 102101, 0, 0.0, 2, "お兄ちゃんのそばにいると\n何だかすごく落ち着きますぅ\nえへ、つい甘えちゃいました…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1021002, 102101, 0, 7.0, 1, "あ、あの…\nお兄ちゃんは、こんな私にも\nすごく優しいですよね\nどうしてなんですか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1021003, 102101, 0, 7.0, 5, "あえいうえおあお…\nも、もっとみんなと、\nお話しできるように、\n練習です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1021004, 102101, 0, 4.5, 2, "あ、あの、\nおままごとしませんか？\n私、お母さん役で、\nお兄ちゃんは、お父さん役です♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1021005, 102101, 0, 0.0, 2, "えへ、\n今日も遊びに来てくれた…！\nうれしいですぅ…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1021006, 102131, 0, 0.0, 1, "あ、あのぅ…\nご、ごめんなさい！\nやっぱりなんでもないですぅ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1021007, 102131, 0, 0.0, 1, "はぅ！？\nな、なにか私の顔についてます？\nあ、あんまりじっと見ないでぇ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1021008, 102131, 0, 0.0, 1, "もう少しこの森で\nお稽古していきたいな…\nうまくなるまで\n人に見せるの恥ずかしいもん… ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1021009, 102131, 0, 0.0, 1, "うぅ…あまり見つめないでぇ\nあ、えっと…でも、\n見られたくないわけじゃ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1021010, 102131, 0, 0.0, 1, "えっと…あのぅ…\n私、お芝居が好きで…\nお兄ちゃんに見てほしい…な", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1021011, 102101, 0, 5.6, 2, "おお、お誕生日…\nおめでとうございますぅ…！\nわわ、私からのプレゼント…\nもらって…くれますか…？", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1021012, 102131, 0, 0.0, 1, "お兄ちゃん、お誕生日…\nお、おめでとうございますっ！\nきょ、今日はその…\nい、一緒にお祝いしたいですぅ…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1021013, 102161, 0, 0.0, 1, "『小さな勇気、小さな一歩！\nみんなの希望の光…\n魔法少女べルルちゃん参上☆』", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1021014, 102161, 0, 0.0, 1, "えへへ…\nお兄ちゃんのおかげで\nお芝居うまくなりました…！\nまた練習に付き合ってくださいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1021015, 102161, 0, 0.0, 1, "ひゃぁ！？　お、お兄ちゃん…！\n急に触っちゃ嫌ぁ…！\n…で、でも…嫌いって言ってる\nわけじゃないですからね…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1021016, 102161, 0, 0.0, 1, "ステージの上ってこんなに\nキラキラしてるんですね…！\n私、もっとお芝居上手になって\nいろんな人を笑顔にしたいですぅ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1021017, 102161, 0, 0.0, 1, "私の夢はまだ始まったばかり…\nすごく怖いし、自信はないけど\nお兄ちゃんが応援してくれる…\nだから私、がんばりますぅ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1022001, 102201, 0, 5.5, 1, "ね、ねえ。ちょっと、\nアンタに話したいことが…\nや、やっぱりなんでもない！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1022002, 102201, 0, 9.0, 1, "今日は新作ゲームの\n発売日なんだけど…\nきょ、興味があれば、\n一緒に…や、やっぱりいい！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1022003, 102201, 0, 4.5, 1, "…アンタが相手だと、\n少しだけまともに話せるけど、\nもっとうまく喋れるように\nなりたいな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1022004, 102201, 0, 4.5, 5, "アンタと一緒にいるのが、\n普通になってきたかな\n特別じゃないって、\nけっこう特別なことかも…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1022005, 102201, 0, 5.5, 6, "今度、一緒に買い物に行かない？\nうん、練習は完璧\n…って、アンタもしかして、\n今の聞いてた！？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1022006, 102231, 0, 0.0, 1, "こ、この花は、べ、別に\nアンタに見せようと思って\n摘んだわけじゃないわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1022007, 102231, 0, 0.0, 1, "きれいな花畑よね…\nわ、私みたいなのには\n似合わないかもだけど\n…な、何でもないわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1022008, 102231, 0, 0.0, 1, "私だって、こういう場所は\n嫌いじゃないわよ\nい…一応、女の子、だし", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1022009, 102231, 0, 0.0, 1, "せっかく花畑に来たんだし…\n好き、嫌い、好き、嫌い…す、\nわああ！　な、なんでもないわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1022010, 102231, 0, 0.0, 1, "ねぇ、こういうところに\n連れてきてくれるってことは、\n私のこと、少しは女の子扱い\nしてくれてるってこと…かな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1022011, 102201, 0, 5.5, 4, "お、お誕生日お、おめ、おめで…\nや、やっぱり無理！\nおめでとうなんて言えないー！\nプレゼントなんて渡せないよー！", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1022012, 102231, 0, 0.0, 1, "た、誕生日、おめでとう！\nプレゼントは、アンタが私に\nゲームで勝ったらあげるわよ\n…アンタ、このゲーム得意よね？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1022013, 102261, 0, 0.0, 1, "進化した私の力…\n甘く見ないでほしいわね！\nどんな相手もみんなまとめて\nやっつけちゃうんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1022014, 102261, 0, 0.0, 1, "アンタと私のコンビなら\nなんだって乗り越えていける\n気がするの…不思議とね！\n限界だって振り切ってみせるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1022015, 102261, 0, 0.0, 1, "どんどん流れ込んでくる…\nアンタの想い、そして力が…\nこれなら誰にだって負けないわ！\n私の本気、見せてあげる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1022016, 102261, 0, 0.0, 1, "アンタが見ててくれるから\n私も安心して戦えるっていうか…\nだから…その…こ、これからも\n私のそばにいなさいよねっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1022017, 102261, 0, 0.0, 1, "アンタが一緒にいてくれて\nその…私もちょっとずつ\n変われたっていうか…\n…な、なんでもない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1023001, 102301, 0, 4.2, 5, "ぷうきちは私の大切な\nお友達なんだよ\nお兄ちゃんも、ぷうきちと\n仲良くしてね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1023002, 102301, 0, 4.1, 1, "私が先に救護院に来たんだから\nお姉ちゃんだよ！\nお兄ちゃんは弟ね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1023003, 102301, 0, 3.7, 2, "私とぷうきちと、お兄ちゃん！\n三人そろえば、\n怖いものなしだね！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1023004, 102301, 0, 2.6, 1, "『俺たちともっと遊べ』って\nぷうきちが怒ってたよ", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1023005, 102301, 0, 2.9, 2, "お兄ちゃんはぷうきち好き？\n私は大好き！\nお兄ちゃんと同じくらい好き！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1023006, 102331, 0, 0.0, 1, "お、お兄ちゃん！\nあんまりうろうろすると\nぷうきちが\n当たっちゃうよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1023007, 102331, 0, 0.0, 1, "えいっ！　やあっ！　魔物めぇ！\nこ、こっち来るなったらぁ！\nあっ、そっちもダメ！\nお兄ちゃんの方に行くなー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1023008, 102331, 0, 0.0, 1, "見ててお兄ちゃん、\nぷうきちフルスイングの練習の\n成果、見せてあげる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1023009, 102331, 0, 0.0, 1, "お兄ちゃん！\n大丈夫？　けがはない？\nもう平気だよ、安心して\n私とぷうきちが来たからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1023010, 102331, 0, 0.0, 1, "お兄ちゃんに悪さする魔物めー！\n許さないんだから！\nやっちゃおう、ぷうきち！\n『合点だぜアヤネ！』", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1023011, 102301, 0, 1.9, 1, "ハッピーバースデー♪\nぷうきちも、\nお兄ちゃんのお祝いしたいって！\n３人でパーティーしよっ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1023012, 102331, 0, 0.0, 1, "お兄ちゃん、お誕生日おめでと♪\nぷうきちからも\nプレゼントがあるみたいだよ！\nいっぱいもらえて、いいな～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1023013, 102361, 0, 0.0, 1, "見て見てお兄ちゃん、\nぷうきちがいっぱい！\nえへへ、み～んな私の、\n新しい家族だよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1023014, 102361, 0, 0.0, 1, "新しい家族だ～♪\nよ～し、私がお姉ちゃんとして、\nみんなの場所を\n作ってあげるからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1023015, 102361, 0, 0.0, 1, "こうやってお兄ちゃんと一緒に\n居ると、本当の兄妹みたいだね\nえへへ♪　今日は妹として、\n思いっきり甘えちゃおうかな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1023016, 102361, 0, 0.0, 1, "この子たちは私とお兄ちゃん\nにとって家族…それって…\n『子どもみたいなもんだな』\nち、ちち、違うからぁ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1023017, 102361, 0, 0.0, 1, "『お、俺の家族がこんなに！？』\n大家族のお兄ちゃんとして、\n頑張らないとね、ぷうきち♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1025001, 102501, 0, 6.0, 6, "いつも失敗してすみません…\nは、腹を切ってお詫びをっ！\n…え、そっちの方が迷惑？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1025002, 102501, 0, 2.5, 6, "身の回りのお世話は私が─\nわぁ！\nあ、頭ぶつけちゃいました…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1025003, 102501, 0, 4.0, 4, "ごはん作って、お洗濯して、\nお部屋の掃除も…\nや、やることがいっぱいで\n目が回っちゃいます～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1025004, 102501, 0, 2.5, 2, "精一杯、ご奉仕します！\nなんでも言ってくださいね？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1025005, 102501, 0, 7.0, 5, "あの…お掃除頑張るので、\n終わったら、その…\n褒めてもらえませんか…？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1025006, 102531, 0, 0.0, 1, "このゴーレムさん、\n道端に捨てられていたんですよ\n可哀想に…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1025007, 102531, 0, 0.0, 1, "ゴーレムさんを綺麗にするの\n手伝ってくれるんですか？\nえへへ、ありがとうございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1025008, 102531, 0, 0.0, 1, "怪我とかしていません？\n回復魔法なら得意なので♪\nそれ以外の魔法は…それなりに", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1025009, 102531, 0, 0.0, 1, "あっ！　またやっちゃいました～\nあなたの前では失敗しないように\n気を付けていたのに～っ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1025010, 102531, 0, 0.0, 1, "お帰りなさいませ\nご主人さま\nな、な～んて、えへへ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1025011, 102501, 0, 5.7, 1, "お誕生日、おめでとうございます\nケーキをご用意しました！\nこ、転ばないように、\nそーーーっと持っていきますね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1025012, 102531, 0, 0.0, 1, "お誕生日おめでとうございます♪\n今日だけは普通の女の子として\nあなたのお祝いをしちゃ、\nダメ…ですか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1025013, 102561, 0, 0.0, 1, "ひゃわあんっ！？\nえ、えへへ…転んじゃいました\nでも私、くじけませんから！\n一人前のメイドになる日までっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1025014, 102561, 0, 0.0, 1, "あ…あなたもお片付け\n手伝ってくれるんですか？\nありがとうございます♪\nこのご恩は必ず、お返ししますね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1025015, 102561, 0, 0.0, 1, "なんだか最近、ドジが\nパワーアップしてる気がします～\nいえ、前向きに考えましょう！\nこれもまた成長なんですよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1025016, 102561, 0, 0.0, 1, "こ、こっち見ないでください～！\nはい？　どうしてって…？\n失敗したところを見られたくない\n乙女心…ううん、メイド心です！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1025017, 102561, 0, 0.0, 1, "また大失敗しちゃいましたけど\nあなたがすぐに手を差し伸べて\nくれて…なんだか私、それが\nすごくうれしかったり…えへへ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1026001, 102601, 0, 5.4, 1, "できればずーっと\nゴロゴロしてたい…\nねえ、あんたもそう思わない？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1026002, 102601, 0, 6.8, 5, "ねーねー、あたしと一緒に\nのんびりゲームでもしようよ～\nたまには休むのも大事だよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1026003, 102601, 0, 6.5, 5, "あたし、お腹ぺこぺこだよ\nあんた何か持ってない？\n美味しいあんパンでもあれば\n最高なんだけどね～…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1026004, 102601, 0, 3.8, 2, "一人でだらだらしてることが\n多かったからさ、\nあんたっていう遊び相手が\nできて結構嬉しいんだー", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1026005, 102601, 0, 9.5, 2, "…あたしみたいなタイプってさ、\n自分の領域に人が入ってくるの\n少し苦手だったりするんだけど、\nあんたは不思議と嫌じゃないや", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1026006, 102631, 0, 0.0, 1, "大好きなドングリの中で\nごろごろできるなんて…\nなんだい？\nここは天国かい？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1026007, 102631, 0, 0.0, 1, "ああ、まさにこの空間は、\nあたしのために\nあるような場所だね…\n戻ってこれなくなりそうだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1026008, 102631, 0, 0.0, 1, "ふかふかのベッドもいいけど\n自然に包まれてだらけるのも\nまた乙なんだよねぇ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1026009, 102631, 0, 0.0, 1, "ほらほら、あんたもこっちに来て\n一緒にこのパラダイスを\n堪能しようじゃないか…\nあたしの隣、あいてるよ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1026010, 102631, 0, 0.0, 1, "こんな素敵な場所に\n連れてきてくれてありがとね\nこのためならいくらでも\n外にでるし、また連れてきておくれ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1026011, 102601, 0, 6.2, 2, "誕生日おめでと～\nプレゼントは買い忘れてないよ～\n通販の時間指定を使ってるから、\nもうすぐ届くはずだよ～", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1026012, 102631, 0, 0.0, 1, "誕生日おめでと～\n特別な日だからこそ、\nぐうたら過ごすに限るよね\nあたしも一緒に付き合うよ～", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1026013, 102661, 0, 0.0, 1, "飲み物よし、食べ物よし！\nあたしはもう一歩も動かないよ！\nほら、あんたもこっちきな\n一緒にゲームするよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1026014, 102661, 0, 0.0, 1, "ぐうたらバンザ～～イ！\nはぁ…幸せだねぇ\nずっとこうしていられたら\nいいのになぁ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1026015, 102661, 0, 0.0, 1, "靴下を脱ぎ散らかしちゃダメ？\nいいじゃん、もうオフなんだし\n文句があるならあんたが\n片付けといて～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1026016, 102661, 0, 0.0, 1, "休憩って大切だよね？\n大切なんだからたくさん\nとらなきゃだよね？\nほら、あんたも早く休む休む！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1026017, 102661, 0, 0.0, 1, "あんたがいると心の底から\nくつろげるねぇ…どうせだったら\nあんたも牧場に住んじゃえば？\nなんてね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1027001, 102701, 0, 5.0, 2, "あなた様のことを思って\nまごころをこめて\n媚薬づくりをしていますわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1027002, 102701, 0, 6.0, 2, "私の本業は花屋ですわ\nブーケなども作っています\n最近は毒草アレンジが人気です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1027003, 102701, 0, 9.0, 6, "花は嫌いではありません…\n生活の糧ですし薬の材料にも\nなりますから。…美しさ？\nそれは考えたこともありません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1027004, 102701, 0, 5.5, 5, "こうして研究をしていると\n心が弾みます…\n薬が完成すればあなた様を\n自由にできると思うと…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1027005, 102701, 0, 3.5, 5, "花占いでもしましょうか\nあなた様は私のことが、好き…\n大好き…好き…大好き…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1027006, 102731, 0, 0.0, 1, "どのような女性が好みですか？\n明るい私？　クールな私？\nもの静かな私？　…もちろん、\n私以外の方ではありませんよね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1027007, 102731, 0, 0.0, 1, "森の魔物…人の心を読み取り\n好みの幻を見せると聞きますわ\nあなた様がその魔物に遭ったら\nどのような幻を見るのでしょうね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1027008, 102731, 0, 0.0, 1, "私だけを見てください\n今目の前にいる私だけを\nたとえ別の私でも、\n目移りするのは許しませんよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1027009, 102731, 0, 0.0, 1, "もしもあなた様の偽物が現れたら\nいっそ縛り上げて観賞用に…\nいえ、偽物は所詮偽物ですわ\n私に必要なのはあなた様だけ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1027010, 102731, 0, 0.0, 1, "どんな私をお望みですか？\nあなた様が望むなら、\nその通りに私は変わります\n愛していますから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1027011, 102701, 0, 7.2, 5, "お誕生日を祝して、バースデー\nケーキをつくりましたわ\n隠し味は、睡眠薬としびれ薬と\n惚れ薬と媚薬と…私の愛情です", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1027012, 102731, 0, 0.0, 1, "お誕生日おめでとうございます\nプレゼントを用意しましたわ\n婚姻届と、結婚指輪と、\n二人の新居です", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1027013, 102761, 0, 0.0, 1, "クスクスクス…♪\nこのブーケのように\n私たちの未来は\nバラ色間違いなしですわ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1027014, 102761, 0, 0.0, 1, "私を狂わせるのも\n私を満たすのも、あなた様\nそう…私の全ては\nあなた様次第なのですわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1027015, 102761, 0, 0.0, 1, "優しいあなた様と私を\n無限に祝福する優しい世界…\n必ずや実現してみせますわ…！\nクスクスクスクス…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1027016, 102761, 0, 0.0, 1, "あなた様を私だけの物にしたい\nそれも本心のひとつですわ\nですが…もうその想いだけに\n囚われる私ではありません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1027017, 102761, 0, 0.0, 1, "バージンロード…\n指輪交換…\nそして誓いの…あぁっ！\n恥ずかしいですわっ…！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1028001, 102801, 0, 5.0, 2, "クレヨンと絵本と積み木が\nいっぺんに買えて\nしかも安い店を見つけたの！\n選ぶのを手伝ってくれない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1028002, 102801, 0, 4.5, 2, "料理をする時は、\n食材の隅から隅まで\n使い切らないとね\nもったいないじゃない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1028003, 102801, 0, 7.0, 2, "お金も大事だけど、あたしはただ\nみんなの笑顔が見たいの\nもちろん、その中には\nあんたも入ってるんだからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1028004, 102801, 0, 3.5, 5, "あんたと今度、\nどこかに遊びに行きたいわね\nあ、もちろん、\nお金のかからないところによ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1028005, 102801, 0, 2.0, 2, "お茶会を開こうと思うの\nもちろん、\nあんたも招待してあげるから\n楽しみにしてなさい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1028006, 102831, 0, 0.0, 1, "限定アイテムの列はこちらが\n最後尾です…あら、奇遇ね\nあんたも寄ってかない？\nあたしたちが主催するバザー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1028007, 102831, 0, 0.0, 1, "あたしがお客さん呼び込むから、\nあんたはこの看板持って\n誘導してくれる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1028008, 102831, 0, 0.0, 1, "予想以上にお客さんが来てくれて\nビックリだわ！　せっかくだし\nあんたも寄ってきなさいよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1028009, 102831, 0, 0.0, 1, "整列手伝ってくれてありがと♪\nあんたと一緒だといつもより\n何倍も仕事が楽しいわ♪\nまたお願いね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1028010, 102831, 0, 0.0, 1, "バザーは大盛況よ♪\nこのあとパーッとご飯でも\n行きましょ！　あんたがいれば\n疲れなんか吹っ飛んじゃう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1028011, 102801, 0, 5.1, 2, "ハッピーバースデー♪\n救護院のみんながプレゼントを\n選んだわ。もちろんあたしもね\nありがたく受け取りなさい！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1028012, 102831, 0, 0.0, 1, "誕生日だったわね？\nお祝いに遊びに行くわよ\nこんな日くらいはしっかり休んで\nたまには楽しみましょ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1028013, 102861, 0, 0.0, 1, "みんなが楽しそうに笑ってるのが\n何よりもうれしいの\nそんな場所であんたと過ごせるの\n本当に幸せよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1028014, 102861, 0, 0.0, 1, "帰れる場所があるって、本当に\n素敵よね。あんたも疲れたら\nいつでもここに来なさい\n待ってるから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1028015, 102861, 0, 0.0, 1, "見慣れた何気ない光景だけど\nこういう当たり前の日々を\n大切にしていきたいわよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1028016, 102861, 0, 0.0, 1, "あんたもだいぶここに\nなじんでるわよね\nこのままずっと\n居てくれてもいいのよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1028017, 102861, 0, 0.0, 1, "あんたの前でくらい、肩の力を\n抜いてもいいわよね\nママ・サレンはちょっとお休み\n…なんてね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1029001, 102901, 0, 3.6, 2, "私、今のポジションには\n満足してないんだ！\nトップアイドルめざして\n歌も踊りも磨かないとね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1029002, 102901, 0, 6.0, 5, "…私の写真集みてるの？　この\n派手な水着は撮影用だってば！\nプライベートで着るやつは\nもっと可愛いもん。…見たい？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1029003, 102901, 0, 3.6, 2, "トップアイドルへの道は\nまだまだ遠いなあ…\nダンスと歌のレッスン、\nもうちょっと頑張ろうっと！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1029004, 102901, 0, 3.4, 2, "歌も踊りも\nもっともっと上手になりたいな！\nキミには、一番輝いてる私を\n見てほしいから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1029005, 102901, 0, 2.3, 5, "アイドルのお勉強は進んでる？\nでも…私以外のアイドルの\nことはあまり勉強しなくても\nいいからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1029006, 102931, 0, 0.0, 1, "トップアイドルになれたら\n世界はどんな風に見えるんだろ？\nもしそうなった時も\nキミは隣にいてくれるかな…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1029007, 102931, 0, 0.0, 1, "ステージに立つからには\nチャレンジする気持ちを\n忘れたらダメだよね！\nこれからも前向きにいくよー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1029008, 102931, 0, 0.0, 1, "私、がんばってトップアイドルの\n高みを目指すから！\n応援、よろしくね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1029009, 102931, 0, 0.0, 1, "キミが望むなら\nアイドルやめてもいいかなって\n思ったこともあったんだよ？\n…ちょっとだけね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1029010, 102931, 0, 0.0, 1, "この歌、片思いの女の子の心が\nよく出てるねって？\n…ふふふ、キミって鈍感♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1029011, 102901, 0, 1.2, 1, "おめでとう♪　キミの誕生日を\nお祝いできるように、\nずっと前からスケジュールを\n調整してたんだ！　うれしい？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1029012, 102931, 0, 0.0, 1, "ハッピーバースデー、トゥユー♪\nハッピーバースデー、トゥユー♪\nどう？　キミだけのための\nプライベートライブだよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1029013, 102961, 0, 0.0, 1, "今の私にできる最高の歌を\nキミに贈るよ。キミを想って\nキミのために歌うから…\nどうか最後まで聴いてね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1029014, 102961, 0, 0.0, 1, "この歌には私の色んな想いを\n込めたんだ。え、どんな\n想いかって？　ふふ、それは秘密\nキミ自身で見つけてね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1029015, 102961, 0, 0.0, 1, "みんなの応援がトップアイドルを\n目指す私の力になるんだ\nだからこれまで以上にいーっぱい\n応援してくれなきゃダメだよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1029016, 102961, 0, 0.0, 1, "私がトップアイドルになっても\nキミには今まで通りで\nいてほしいな。そういうのが\n一番うれしいんだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1029017, 102961, 0, 0.0, 1, "アイドルは恋愛禁止だけど\nいつかちゃんとキミに…ううん\nまずはトップアイドルになるんだ\n勇気を出すのはその後だよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1030001, 103001, 0, 5.5, 2, "デケデケデン！\nこのニノン、ショーグンのために\n忠義を尽くすデース！\n見ててクダサイ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1030002, 103001, 0, 4.0, 2, "一大事デス！\nアクダイカンが現れたデス！\n一緒に成敗するでゴザルデス！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1030003, 103001, 0, 4.0, 1, "あ、ショーグン！\nそこでなにを…ハッ！？\nもしや忍法の修行デスか！？\nニノンもやるデス！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1030004, 103001, 0, 4.5, 1, "ショーグンのためなら、\nたとえ火の中、水の中デス！\nなぜなら…ワタシの命は、\nショーグンのものだからデス！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1030005, 103001, 0, 5.5, 5, "ヤマトナデシコ…\nニノンの国にも伝わる言葉デス\nワタシがそうなったら、\nショーグンもメロメロデスよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1030006, 103031, 0, 0.0, 1, "いつか…\nショーグンと共に\n天下統一するデス！\nそれがニノンの夢なのデス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1030007, 103031, 0, 0.0, 1, "ニノンに切れぬものはないデス！\nショーグンはヤカタブネに\n乗ったつもりで\n安心していてクダサーイ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1030008, 103031, 0, 0.0, 1, "ニノンが、ショーグンのゾーリ\nふところで\nあっためておいたデス！\n一緒にでかけマショ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1030009, 103031, 0, 0.0, 1, "ショーグン、手をつなぐデース！\nえへへ…\nこれでニノンとショーグンは、\n固い絆で結ばれたデス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1030010, 103031, 0, 0.0, 1, "ショーグン！　ご飯にするデス？\nお風呂にするデス？\nそれとも…ワ・タ・シ…？\nは、恥ずかしいデス…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1030011, 103001, 0, 6.2, 2, "誕生日おめでとうデス！\nお祝いの紅白マンジェウを\n用意したデス！　二人羽織で\n食べさせてあげるデス！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1030012, 103031, 0, 0.0, 1, "ショーグンのお祝いするデス！\n誕生日は、おジャシキ遊びで\nシュラシュシュシュデース！\nどんちゃん騒ぎデース！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1030013, 103061, 0, 0.0, 1, "チューシン・ニノン、呼ばれて\n飛び出てシュバババッー！\n命燃やしてショーグンを\nお守りいたしマス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1030014, 103061, 0, 0.0, 1, "シンリャクすること火の如くデス\nささ、ショーグン、ご命令を！\n最高のイクサバタラキを\nご披露いたソウロウデス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1030015, 103061, 0, 0.0, 1, "ショーグン、見てクダサイ！\nメラメラ燃える究極忍法デス！\nこれでゾーリもホカホカに…\nアアー！　も、燃えてるデース！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1030016, 103061, 0, 0.0, 1, "シュババババッ！\nこの印さばき、どうデスか？\nえ、ゆ、指がきれい？　うぅ…\n不意打ちはズルいデ～ス…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1030017, 103061, 0, 0.0, 1, "ショーグンのおそばにいるだけで\nワタシは笑顔になりマス！\nそれこそがワタシの一番の\nゴホウビデース！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1031001, 103101, 0, 6.5, 2, "今日の運勢は最悪です\nえっと、気にしないでください\nちなみに…私の占いは、\nよく当たります", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1031002, 103101, 0, 4.5, 4, "あなたの後ろの人、\n誰ですか…？\nえ？　見えない？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1031003, 103101, 0, 7.0, 2, "タロットカード、星占い、\n四柱推命…いろいろ\nありますが、私のおススメは、\n父を使った水晶占いです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1031004, 103101, 0, 4.0, 5, "父はあなたを\n誤解しているようです\nとてもいい人なのに…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1031005, 103101, 0, 5.0, 5, "今日のあなたには、\n女難の相がでていますね\nあまり私以外の女性とは、\n話をしない方が…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1031006, 103131, 0, 0.0, 1, "舞がきれい…ですか\nうれしい\n修練した甲斐がありました", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1031007, 103131, 0, 0.0, 1, "これは祓魔の舞です\nまだ練習中なので\nあまり見ないでください\n…恥ずかしいですから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1031008, 103131, 0, 0.0, 1, "父がこの舞を見るたびに\n成仏しそうだと褒めてくれます\n父に成仏されては\n困るのですが…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1031009, 103131, 0, 0.0, 1, "あなたに褒めてもらえると\nいつもより舞がうまくできます\nプラスの気は\n霊を祓うのに最適なんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1031010, 103131, 0, 0.0, 1, "生きている人で舞を見せるのは\nあなたが初めてです\n少し恥ずかしいですけど\n…あなたに見てほしいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1031011, 103101, 0, 10.3, 5, "あなたが歳を取って、\nおじいさんになったら、一緒に\n私もおばあさんになりますね\nもちろん、幽霊になったら…", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1031012, 103131, 0, 0.0, 1, "お誕生日おめでとうございます\n今日は、とてもいい運勢ですよ\n金運とか健康運とか…\nその…れ、恋愛運とか…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1031013, 103161, 0, 0.0, 1, "あなたがそばにいれば\n何も恐れるものはありません\n降りかかる不安や恐怖は\n私が全て刈りとります", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1031014, 103161, 0, 0.0, 1, "たとえ誰からも恐れられようとも\nあなたを守るためなら\n私はためらうことなく\nこの刃を振るいます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1031015, 103161, 0, 0.0, 1, "キレイな鬼火…\n妖しくもはかなげで\nそして私たちの行く末を照らす\n希望の灯火なんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1031016, 103161, 0, 0.0, 1, "よければ力をお貸しください\nあなたとは共に助け合い\n守り合う仲でいたいですから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1031017, 103161, 0, 0.0, 1, "あなたのそばにいると\n胸が高鳴ってしまいます…\nこれは魂の共鳴？\nあるいはもっと別の…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1032001, 103201, 0, 4.0, 1, "困ったことがあれば、\n何でも申してくださいな！\n私がなんとかして\n差し上げますわ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1032002, 103201, 0, 2.0, 1, "ごきげんよう\n私が一人前の商人になるため、\nご指導ご鞭撻のほど\nよろしくお願いしますわ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1032003, 103201, 0, 6.5, 5, "私は確かに方向音痴ですが、\nそこまで酷くありませんわ\n…ところで、私の家は\nどちらかしら？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1032004, 103201, 0, 6.0, 2, "あなたさまが私の騎士になる日を\n私はいつまでも待っていますわ\nその時には、二人で\n色々な場所に行きますわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1032005, 103201, 0, 3.5, 2, "お金で買えないものも\nあるのですわね…\n例えば、あなたさまの\n心…ですわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1032006, 103231, 0, 0.0, 1, "『一人前の商人とは\n一人前の武人でもある！』\nウィスタリア家の教えを\n今こそ守りますわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1032007, 103231, 0, 0.0, 1, "この程度の相手、\n私の敵ではありません\n手早く片付けて、\n勝利の祝宴と参りますわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1032008, 103231, 0, 0.0, 1, "私の商売を邪魔するやからは\nなんぴとたりとも許しません！\n成敗してさしあげます！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1032009, 103231, 0, 0.0, 1, "あなたさまが\nそばにいてくださることが、\nどれほど私を強くしているか、\nお気づきかしら？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1032010, 103231, 0, 0.0, 1, "あなたさまと一緒なら、\n私は迷わずに、\nどこまでもまっすぐ進みますわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1032011, 103201, 0, 5.2, 2, "今日はあなたさまの誕生日？\nまあ、おめでとうございます！\nすぐにパーティーを\n開きましょう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1032012, 103231, 0, 0.0, 1, "誕生日、おめでとうございます\nあなたさまの誕生日の\n価値の前では、どんな宝石も\nかすんで見えてしまいますわね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1032013, 103261, 0, 0.0, 1, "おーほっほっほ！\n情熱の炎が燃え滾りますわ！\n今の私は完全無欠！　遅れずに\nついてきてくださいませ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1032014, 103261, 0, 0.0, 1, "やはり私には赤が似合いますわ\nこの全てを焼き尽くす炎でさえ\n私にとっては艶やかに彩る装飾の\n一つでしかありませんわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1032015, 103261, 0, 0.0, 1, "あなたさまがいてくれれば\n私は不死鳥の如く蘇りますわ！\nさあ、一気呵成に\n決めてしまいましょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1032016, 103261, 0, 0.0, 1, "あなたさまと一緒にいると\n知らない世界を知ることが\nできますわ。さあ、次は何を\n見せてくださるの？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1032017, 103261, 0, 0.0, 1, "私が背中を任せられるのは\nあなたさまだけですわ\nそして肩を並べ共に歩むのも\n私はあなたさまがいいですわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1033001, 103301, 0, 3.0, 2, "お笑いの道は奥が深けぇべ！\nオラと一緒に、\n笑いの道、極めるべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1033002, 103301, 0, 4.0, 4, "オラが一番おっかねぇのは、\n敵に負けることじゃねえ\nネタがすべることだ～！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1033003, 103301, 0, 3.0, 1, "あんちゃん、この牛乳ウマいべ？\nお乳を出したのは、ウマじゃなくて\n牛だけどな！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1033004, 103301, 0, 5.0, 2, "あんちゃん、オラと一緒に、\nお笑い界の頂点めざすべ！\nめざせ、お笑いチャンピオン！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1033005, 103301, 0, 4.0, 2, "あんちゃん、オラと一緒に\nネタ、作るべ！\nもう二時間だけ「寝た」後に…\nなんちゃって！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1033006, 103331, 0, 0.0, 1, "ほーい、集まってけれ～！\nエリザベスパーク印の\n牛乳、ヨーグルト、チーズ！\n直売だべよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1033007, 103331, 0, 0.0, 1, "街の衆、味見してけろ！\n出来立てだぁ、ほんとに旨ぇぞ～\nオラ嘘はつかねえよ、\n冗談は言うけどな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1033008, 103331, 0, 0.0, 1, "街のみんながこんなに\n応援してくれてんだ！\n絶対に爆笑をかっさらうべよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1033009, 103331, 0, 0.0, 1, "オラのギャグ、おもしろいべか？\nなんつってもあんちゃんは、\nオラの初めてのファンだ\nきっとオラたち波長が合うだよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1033010, 103331, 0, 0.0, 1, "な、なあ…あんちゃんのこと\n相方って呼んでもいいべか？\nそれがダメなら婿さんに…\nあ、逆だ！　こっぱずかしー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1033011, 103301, 0, 4.7, 2, "ひょうたん、常備してっか？\nなにキョトンとしてんだべ？\n今日誕生日のあんちゃんのための\nギャグだべ！　おめでとう～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1033012, 103331, 0, 0.0, 1, "ハッピーバースデーだべ！\n今日はあんちゃんのためだけに\n渾身ネタを、飛ばーすでー！\nいっぱい笑ってほしいべよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1033013, 103361, 0, 0.0, 1, "オラの手料理をあんちゃんに\n食べてもらうの、なんかドキドキ\nするべな…味には自信あっから\n楽しみにしててけろ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1033014, 103361, 0, 0.0, 1, "あんちゃんと二人でキャンプ\nなんてのもいいかもしんねぇな\n朝までみっちりネタ合わせ…\n最高の時間になりそうだべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1033015, 103361, 0, 0.0, 1, "牧場がこんなににぎやかに\nなるなんて…それもこれも\nあんちゃんが励ましてくれた\nおかげだべ。あんがとな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1033016, 103361, 0, 0.0, 1, "みんなの笑顔が行商人としても\n牧場主としても一番の幸せだべ\nこの景色をずっと、あんちゃんと\n一緒に見ていきてぇなぁ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1033017, 103361, 0, 0.0, 1, "オラの相方はエリザベスだけって\n思ってきたけど、あんちゃんも\n加えてトリオってのもいいなぁ…\nまじめに考えてくれねぇべか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1034001, 103401, 0, 5.5, 2, "何か困ったことがあったら\n相談してね\nいつでも力になるから\nお姉さんとの約束よ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1034002, 103401, 0, 6.5, 2, "キミ、いま暇だったりしない？\nだったら私と一緒に、\nどこか飲みにいきましょ？\nもちろんキミはジュースで、ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1034003, 103401, 0, 5.0, 5, "仕事持って帰ってきたけど\n終わりそうにないよ～！\nうぅ…キミ、\n少し手伝ってくれない？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1034004, 103401, 0, 7.5, 2, "えへへ～…\nキミって暖かいんらね～\nちょっと飲みすぎらから、\nキミで暖を取らせて～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1034005, 103401, 0, 4.0, 2, "キミ、ご飯はちゃんと食べてる？\nあ、良かったらお姉さんが、\n作ってあげようか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1034006, 103431, 0, 0.0, 1, "はぁ…このベリー、いい香り♪\n飲み物にしたらさぞ美味しい…\nいけないいけない！\n仕事中なのに！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1034007, 103431, 0, 0.0, 1, "だめよユカリ…！\nこのままじゃいつもみたいに\nダメなところを見せちゃうわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1034008, 103431, 0, 0.0, 1, "な～んか、ちょっとだけ\nいい気分になってきたかも…\n気のせい…かなぁ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1034009, 103431, 0, 0.0, 1, "キミが大人になった時のために\n飲み物、一本作っておこうか？\nその日がきたら、二人で一緒に\nお祝いしながら飲みましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1034010, 103431, 0, 0.0, 1, "ねぇ…今度、一緒に\nデートしない？\nそれとも、私みたいなのが\n相手じゃ、いやかな…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1034011, 103401, 0, 2.0, 1, "誕生日おめでとう！\n今日はいっぱい\nお祝いしてあげるね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1034012, 103431, 0, 0.0, 1, "キミの誕生日というわけで\nまずは…かんぱーい！\nねえ、今日はずっと\n一緒にいてもいいかな…？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1034013, 103461, 0, 0.0, 1, "んふふ～気持ちいい～♪\nほら、キミも飲も飲も！\nもちろんキミはジュースだからね！\nはい、かんぱーいっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1034014, 103461, 0, 0.0, 1, "あぁ～染み渡るわぁ～\n仕事の後はこれに限るわよね～！\nそうだキミ、肩揉んでくれない？\nんっ、あ～っ、上手ね、キミ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1034015, 103461, 0, 0.0, 1, "今日はお姉さんのおごりよぉ\nたくさん食べていいわよぉ～\n本当は手料理とかご馳走して\nあげたいんだけど…今度ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1034016, 103461, 0, 0.0, 1, "私がこんな風にリラックス\nできるのはキミの前だけよ～\n迷惑かけちゃうかもしれないけど\nキミは許してくれるわよね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1034017, 103461, 0, 0.0, 1, "よぉ～っし言っちゃおうかな～！\n私ねぇ～、キミのことが～…！\nふふ、や～っぱ秘密～！\n…あ、危なかったわ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1036001, 103601, 0, 5.9, 5, "今は、勉強の時間です\n話しかけないでくださ…\nもう、そんな顔しないでください\n遊んであげますから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1036002, 103601, 0, 4.8, 4, "ピーマンとにんじんを食べないと\n大きくなれない…？\nううっ…大きくはなりたい\nですけれどぉ～っ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1036003, 103601, 0, 0.0, 2, "聞いてください\n身長が伸びていました！\n一ミリも！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1036004, 103601, 0, 5.2, 5, "今は子供ですけど、\nすぐに大きくなりますから\nその時には…その…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1036005, 103601, 0, 0.0, 2, "ふふっ。とても頑張りましたね\n今日はほめてあげます\n頭をナデナデしてあげましょう", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1036006, 103631, 0, 0.0, 1, "私を守ってあげる？　\n余計なお世話です！\nで、ですが…\nありがとうございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1036007, 103631, 0, 0.0, 1, "こ、腰が抜けちゃいました…\nは、恥ずかしい…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1036008, 103631, 0, 0.0, 1, "うぅ…どうして\nこの人がいる時に限って、\nかっこ悪くなっちゃうのかな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1036009, 103631, 0, 0.0, 1, "ま、魔物なんてこわくないです！\nで、でもその…守ってくれて\n助かりました…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1036010, 103631, 0, 0.0, 1, "街の外は魔物が出て危ないです\nですから、私が守ってあげますよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1036011, 103601, 0, 3.0, 2, "今日、お誕生日なんですよね\nおこづかいを貯めて、\nカップケーキを買ってきたので、\n召し上がってください", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1036012, 103631, 0, 0.0, 1, "お誕生日おめでとうございます\nどんどん大人になっちゃう…\n私も早く追いつかないと…\nな、なんでもないです！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1036013, 103661, 0, 0.0, 1, "私、もう子供じゃないんですから\nひとりでも戦えますっ！\n今度は私があなたを守れるように\nなるんですからっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1036014, 103661, 0, 0.0, 1, "あなたの力を感じます…\n不思議です。今ならなんだって\n出来る気がする…！\nそばで見ていてくださいね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1036015, 103661, 0, 0.0, 1, "たくさん勉強してきた\n成果を見せますから！\n失敗しちゃっても、\n笑わないでくださいねっ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1036016, 103661, 0, 0.0, 1, "私の魔法…どうですか？\n別に、褒めて欲しいとかじゃ…\nあ…頭ナデナデしないでください！\nもうっ…！　もう…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1036017, 103661, 0, 0.0, 1, "私、あなたの隣でちゃんと\n戦えてますよね？\nこれからはもっと、あなたと\n一緒にいられますよね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1037001, 103701, 0, 8.2, 1, "おや、何をしてるのかな？\n不審な行動をしたら、\nあなたでも逮捕するよ？\nなんて、冗談だけど♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1037002, 103701, 0, 6.6, 2, "稽古に付き合ってほしいな\n大丈夫大丈夫！\nきちんと手加減するから！\nふふ、多分だけどね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1037003, 103701, 0, 5.6, 2, "さて、今日も町の見回りに\n行かないと。あなたも来る？\n何があっても、私が守って\nあげるから安心していいよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1037004, 103701, 0, 6.4, 4, "うーん、自分では器用な方だと\n思ってたんだけどなぁ…\nあなたの前だと上手くいかない\n…どうしちゃったのかなぁ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1037005, 103701, 0, 9.1, 5, "からかうつもりなのに、あなたには\nいつも逆にやられちゃう\nまったく、調子が狂って困る\nほんと…困るよ…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1037006, 103731, 0, 0.0, 1, "相手の動きを見て、正確に急所へ\n必殺の一撃を叩きこむ！\nそれが剣術の基本だよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1037007, 103731, 0, 0.0, 1, "戦いの後ってすごく疲れるし、\nお腹が空くんだ…あなたも\n一緒にご飯なんてどうかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1037008, 103731, 0, 0.0, 1, "無駄な動きを削っていけば、\n剣技は磨き上げられる\n単純だけど難しいんだよね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1037009, 103731, 0, 0.0, 1, "あなたを守るためにも、もっと\n強くなりたい。最近は\nそう思うようになったよ\n…剣をやっていてよかった", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1037010, 103731, 0, 0.0, 1, "一緒にいるだけで力が\n湧いてくる。あなたの存在が\n私を強くしてくれるんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1037011, 103701, 0, 9.3, 2, "ふ～ん、今日が誕生日なんだ？\nじゃあ、大人になったあなたに\nご飯でもおごってもらおうかな？\nな～んて、冗談だよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1037012, 103731, 0, 0.0, 1, "誕生日おめでとう！　ん、急に\n押しかけて迷惑だった？　今日は\nあなたの部屋を片付けようと思って\nまるでお嫁さんみたいじゃない？　", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1037013, 103761, 0, 0.0, 1, "心が鈍れば、剣も鈍る\n逆もまた然りだ。けれど今は…\n一点の曇りもなく澄み渡ってる\n…ふふ、あなたへの気持ちもね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1037014, 103761, 0, 0.0, 1, "今の私なら、どんな相手にだって\n負ける気がしないよ…ふふ、\nあなたがそばにいるからだろうね\nこれからもずっと頼りにさせて", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1037015, 103761, 0, 0.0, 1, "誰のために剣を振るうのか\n力よりも技よりも\nその想いこそが大切なんだね\n…あなたが教えてくれたことだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1037016, 103761, 0, 0.0, 1, "どんなに苦しくて大変なときでも\nどうか忘れないでいて\nあなたにはミクマの剣が…\nこの私が、そばについてることを", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1037017, 103761, 0, 0.0, 1, "私、あなたに伝えたいことがある\n今はまだ…うまく言葉に\nできないけど、そのときが来たら\n必ずこの想いを伝えるから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1038001, 103801, 0, 2.5, 5, "おすすめの本があるんです\nお互いに感想をいいあえたら\n楽しいだろうなって…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1038002, 103801, 0, 2.0, 1, "ケホッ、ケホッ…\nあの、背中を\nさすってもらえませんか？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1038003, 103801, 0, 7.0, 5, "もう少し身体がよくなったら、\n一緒に、どこか遠くへ…\n…なんて。えへへ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1038004, 103801, 0, 4.5, 5, "体力がないから\nすぐに疲れちゃって…\n私、迷惑かけてませんか？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1038005, 103801, 0, 5.5, 4, "頼ってばかりではいけませんね\n私も自分のことは自分で…\nごほごほっ…！\nすみません…お水をください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1038006, 103831, 0, 0.0, 1, "この子、ニャットちゃんって\nいいます\nとっても聞き分けの\nいい子なんですよ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1038007, 103831, 0, 0.0, 1, "体が大きいけどニャットちゃん\nとっても甘えん坊なんですよ\nニャットちゃん、\nいい子でちゅね～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1038008, 103831, 0, 0.0, 1, "子どもだったニャットちゃんも\nすっかり立派になりました\n私の、大事なお友達です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1038009, 103831, 0, 0.0, 1, "疲れていませんか？\nよかったら…\n私と一緒に休憩しません？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1038010, 103831, 0, 0.0, 1, "ニャットちゃんに抱きつくと、\nモフモフして気持ちいいです\nあなたの傍も…気持ちいいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1038011, 103801, 0, 8.2, 5, "お誕生日おめでとうございます\nあの…お姉ちゃんからも、\nお祝いされましたか？\nす、少し気になっただけで…", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1038012, 103831, 0, 0.0, 1, "この本、お誕生日プレゼントです\n恋愛小説なんですけど、たまたま\nヒロインの名前がシオリで、\nしゅ、主人公の名前が…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1038013, 103861, 0, 0.0, 1, "ふふ、心強いですね\nあなたと力を合わせれば\nなんだって射抜ける気がします\n…さあ、いきますよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1038014, 103861, 0, 0.0, 1, "ずっと諦めていたことにも\n向き合っていきます\n生きているかぎり、可能性は\n私の中にあるはずですから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1038015, 103861, 0, 0.0, 1, "すぅ…はぁ…すぅ…はぁ…\n呼吸を合わすって不思議ですね\nあなたと一つになったみたいで、\n力が湧いてきます！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1038016, 103861, 0, 0.0, 1, "命中したらデートしてください！\nなんて台詞、あこがれちゃいます\n…あ、恋愛小説の話ですよ！\nえ、いいんですか？　うれしい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1038017, 103861, 0, 0.0, 1, "あなたと一緒だと、どんなことも\nドキドキで彩られて、楽しくて…\nそんな毎日を過ごしたいです\nこれからも、ずっとずっと…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1040001, 104001, 0, 4.5, 2, "友達を作るためのチーム、\nそれがＢＢ団！\nえへへ…これからも、\nよろしくお願いします！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1040002, 104001, 0, 6.5, 5, "友達作りの道は、\n誰でも一歩目から\n踏み出してるんですよね\n私も…踏み出したいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1040003, 104001, 0, 0.0, 2, "今日も、お花に水あげて、\n動物さんにご飯をあげて～♪\nえへへ、ぼっちでも、\n楽しみはそれなりにあります", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1040004, 104001, 0, 9.0, 7, "私、たまに思っちゃうんです\nあなたがいてくれるなら、\n友達ができなくても…\nはっ、な、なんでもないです…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1040005, 104001, 0, 3.5, 6, "あの人は、私のことを…はっ、\nいつからそこに！？　ええと、\nこれは、花占いです。ど、どんな\n占いかって…内緒ですっ！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1040006, 104031, 0, 0.0, 1, "いまは微力な私だけど、きっと\nみなさんのお役に立ちます\nそうすれば…いつか友達、\n増えるかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1040007, 104031, 0, 0.0, 1, "ぼっちだとしても…\n私にもきっとできることが\nあるはずだって思ったんです\n精一杯がんばりますね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1040008, 104031, 0, 0.0, 1, "守りたいものを守る…\nそのためにがんばります\nＢＢ団は私に、色々なことを\n教えてくれました！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1040009, 104031, 0, 0.0, 1, "ＢＢ団の活動のたびに\nあなたに会えるのが楽しみで…\n毎日、早く明日がこないかなって\n思っちゃいます！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1040010, 104031, 0, 0.0, 1, "えへへ…私にこの先、\nどれだけ友達が増えても、\n一番はあなただから…\n安心してくださいねっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1040011, 104001, 0, 9.2, 2, "お、おたたん、おたんこなすー！\nすみません、ハッピーなお祝いの\n言葉を口にしたことがなくて…\n誕生日おめでとう五郎丸ー！", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1040012, 104031, 0, 0.0, 1, "お誕生日おめでとうございます！\n森にいる小鳥さんたちと\n合唱団を結成して、\nお祝いの歌を作ってきました！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1040013, 104061, 0, 0.0, 1, "ＢＢ団の絆の強さ、\nこの一撃で証明します！\n見ていてくださいね、団長さん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1040014, 104061, 0, 0.0, 1, "困っている友達を助けるのは\n当然！　ですよね、団長さん！\nあなたがピンチの時には\n誰より早く駆け付けますからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1040015, 104061, 0, 0.0, 1, "団長さんと一緒なら、\nどんな困難でも乗り越えられる\n気がします。だって私たち、\n友達ですからっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1040016, 104061, 0, 0.0, 1, "もしこの先、私に友達がたくさん\nできたとしても、団長さんは私の\n特別な人です。だってあなたは\n私の最初の友達なんですから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1040017, 104061, 0, 0.0, 1, "時々、団長さんと一緒にいると\nなんだかドキドキしちゃいます\nこの気持ちは…友情が深まった\n証なのでしょうか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1042001, 104201, 0, 4.5, 2, "私の唱喚術は、人々を\n守るためのもの…この力を、\nもっと役立てたいんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1042002, 104201, 0, 3.5, 2, "あの…良かったらまた、\nアイドルの練習に\n付き合ってもらえませんか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1042003, 104201, 0, 3.5, 6, "～～…♪\nい、いつからそこに！？\n…今の、聞いてました？", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1042004, 104201, 0, 4.0, 5, "あなたの前だと、\n緊張せずに歌えるんです…\nどうしてでしょう…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1042005, 104201, 0, 3.5, 5, "歌にはいろんな可能性がある…\nそれを教えてくれたのは仲間と\n…あなたです", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1042006, 104231, 0, 0.0, 1, "…もう少しだけ、近くに…\nそばにいるだけで、不思議と\nメロディが浮かんでくるんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1042007, 104231, 0, 0.0, 1, "目が覚めました？　向こうの街で\nお祭りが始まりましたよ\n後で一緒に行ってみませんか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1042008, 104231, 0, 0.0, 1, "…書きとめられないくらい\n心の中から言葉が溢れてくる…\n私、こんなに歌いたかったんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1042009, 104231, 0, 0.0, 1, "いつか…聞いてくださいね\nあなたへの思いを綴った歌を…\n恥ずかしがらずに、歌いますから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1042010, 104231, 0, 0.0, 1, "波の音が心地いいですね\nずっと一緒に聞いていたいくらい\n…私、今なにか言いました？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1042011, 104201, 0, 6.6, 5, "お誕生日おめでとうございます\n歌以外にいいプレゼントが\n思いつかなくて…\nよかったら聞いてください", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1042012, 104231, 0, 0.0, 1, "お誕生日おめでとうございます\n短いものですが、お祝いに\n曲を書いてみました\nここで歌ってもいいですか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1042013, 104261, 0, 0.0, 1, "あなたと共に歩んだ日々が\n作り上げたこの歌を\nあなたに聞いてもらえて…\n私、とても幸せです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1042014, 104261, 0, 0.0, 1, "自分で言うのもなんですが\nこの歌は特に自信作なんです\nあなたの心に響かせてみせます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1042015, 104261, 0, 0.0, 1, "歌っていると心が軽くなって\nつい体が動いてしまうんです\nよければあなたもどうですか？\nさあ、お手をどうぞ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1042016, 104261, 0, 0.0, 1, "いつかあなたにこの思いが\n届くと信じて歌詞を綴り…\nそして歌い続けることを\nここに誓います", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1042017, 104261, 0, 0.0, 1, "あなたが望むなら唱喚士でも\nアイドルでもない\nあなただけの歌い手として\nいつでも曲を捧げます…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1043001, 104301, 0, 3.3, 2, "さて、パトロールにでも行くか\n一緒に行きたいなら、\nついてきてもいいぜ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1043002, 104301, 0, 3.5, 2, "おい、飯はちゃんと\n食ってんだろうな？\n食ってないなら、\nあたしが用意してやるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1043003, 104301, 0, 6.2, 5, "あたしみたいな奴、\n普通の女の子扱いするのなんて、\nおまえぐらいだぜ\nわ、わりとうれしいな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1043004, 104301, 0, 4.6, 2, "あたしの親友の恩人なら、\nあたしの恩人みたいなもんだ\nお互い仲良くしていこうぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1043005, 104301, 0, 5.1, 5, "お前って、けっこう男気もあるし\n肝っ玉もすわってるよな…\n一緒にいて、あたしは楽しいよ\nお前はどうだ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1043006, 104331, 0, 0.0, 1, "あたしから離れんなよな！\nどんなやつが来ようが、\nぶった切ってやる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1043007, 104331, 0, 0.0, 1, "弱い奴らばっかで、\n暴れ足りねぇ\n歯ごたえのある奴は、\nいねぇのか！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1043008, 104331, 0, 0.0, 1, "お前と一緒なら、\nどんな敵も楽勝だぜ！\nお前はあたしが守るからよ、\nだからあたしのことも、頼むぜ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1043009, 104331, 0, 0.0, 1, "よっしゃー！\n敵が全部片付いたら、\n二人でパーッと飯だ飯！\nお前の好物たくさん作ってやる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1043010, 104331, 0, 0.0, 1, "力がみなぎってくるぜぇ！\nあたしから離れんなよ！\n何があっても絶対に\nお前を守ってやんよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1043011, 104301, 0, 3.2, 2, "へぇ、今日が誕生日だったのか\nおめでとさん！\nんじゃ、せっかくだし\nなんかおごってやるよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1043012, 104331, 0, 0.0, 1, "今日ってお前の誕生日だよな？\nぐ、偶然、たまたま作った\nケーキがあるんだけど…\n食わねぇか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1043013, 104361, 0, 0.0, 1, "あたしの本能が目覚めちまったぜ\n…こっから先は手加減なしだ\nこの研ぎ澄まされた爪と牙で\n刻みつけてやる…あたしの力を！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1043014, 104361, 0, 0.0, 1, "なぁ…その、大丈夫だよな？\n全力のあたしを見て…\n引いたりしてないよな？\nっ！　そっか、よかった…へへ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1043015, 104361, 0, 0.0, 1, "力がありあまってるからか\n体がうずきやがる…えっ？\nおまえが相手をしてくれる？\nいいね、じゃあぶつかりあうか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1043016, 104361, 0, 0.0, 1, "へへっ、すげぇ力だろ？\nおまえのために強くなったんだぜ\nだから…その、なんだ\n…あたしから目をそらすなよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1043017, 104361, 0, 0.0, 1, "あたしがおまえを守る\nおまえがあたしを守る\nそういう関係が続けばいいよな\nずっと…ず～っと、なっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1044001, 104401, 0, 5.0, 1, "お主…わらわと何処かで\n会ったことはないか？\nお主を見ていると、何故か\nなつかしさを覚えるのじゃが…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1044002, 104401, 0, 6.4, 1, "むう…どういうわけか\n無性にトマトジュースが\n飲みたい気分じゃの…\nお主、どこぞで求めてまいれ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1044003, 104401, 0, 6.9, 2, "昼間は思うように動けぬ…\n夜になるまで、じっとしておこう\nそれまでお主は、わらわの\n話し相手になるのじゃぞ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1044004, 104401, 0, 4.2, 4, "お主はちっとも、わらわを\n恐れないのじゃな？\nな？　可愛らしいじゃと？\n眷属の分際で何を申すー！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1044005, 104401, 0, 9.0, 5, "お主は、特別な眷属じゃ\n今より「昼を総べる者」を\n名乗るが良いぞ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1044006, 104431, 0, 0.0, 1, "わらわの真の姿は美しかろう？\nなに？　見慣れてないから\nいつもの方が良い？　なんと\n張り合いのないことを…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1044007, 104431, 0, 0.0, 1, "わらわの野望を聞きたいのか？\nもちろん世界征服じゃ！\n女と生まれたからには\n一度は目指したいものよの♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1044008, 104431, 0, 0.0, 1, "わらわは伝説の吸血鬼\nお主の命を奪うなど\nたやすいことじゃが…まあ、\n眷属ゆえ生かしておいてやろうぞ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1044009, 104431, 0, 0.0, 1, "世界を征服した暁には\nその半分をお主に分け与えよう\nど、どういう意味かわかるな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1044010, 104431, 0, 0.0, 1, "昼間にお主と会うために、\n最近は昼夜逆転してしまった…\n昼ふかしは、美容によくないの\nじゃが…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1044011, 104401, 0, 12.9, 6, "今日は誕生日か？　めでたいな！\nいくつになったのじゃ？\nそうか。わらわがその歳の頃は…\nはて？　何をしていたかのう？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1044012, 104431, 0, 0.0, 1, "眷属の誕生日を祝して、\nわらわの膝枕を贈呈しようぞ\nどうじゃ、心地よいじゃろう？\n腕枕でも胸枕でも良いのじゃぞ？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1044013, 104461, 0, 0.0, 1, "そうじゃ、この威厳ある姿こそ\nわらわの本当の姿\nお主だけは特別じゃ\n存分に見惚れるがよいぞ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1044014, 104461, 0, 0.0, 1, "わらわこそが夜を統べる者\nさあ、夜の散歩へゆくぞ\nついてくるがよい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1044015, 104461, 0, 0.0, 1, "はーっはっはっは！　見よ！\n月までもがわらわたちを\n祝福しておるようじゃ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1044016, 104461, 0, 0.0, 1, "くっくっく……\nこれぞわらわの真の力じゃ\n眷属として誇りに思うがよい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1044017, 104461, 0, 0.0, 1, "なんじゃ？\nそのように見つめられると\nうっかり魅了して\nしまいそうじゃのう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1045001, 104501, 0, 4.0, 2, "ぐふふふ…\nえ？　な、何でもないですよ？\nあなたを見て、妄想なんて…\nはぁはぁ…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1045002, 104501, 0, 5.0, 6, "「ドＳさんは、クウカを\n追いつめるとムリヤリ…」\nでゅふふふふ…あっ！\nいいいつの間に居たのですか！？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1045003, 104501, 0, 3.3, 6, "この間読んだ本が、\nとっても素敵で…え？\n内容ですか？　そ、それは…\n聞かないほうがいいですよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1045004, 104501, 0, 4.0, 5, "夢の中にまでドＳさんが\n出てきてくれました…\n寝ても覚めても、クウカは\n支配され続けてしまうのですね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1045005, 104501, 0, 3.9, 5, "ドＳさんと二人っきり…\nこ、これは…！？\nクウカはどんな辱めを\n受けてしまうのでしょう…！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1045006, 104531, 0, 0.0, 1, "こんな汚れたクウカを見る\nドSさんの冷ややかな視線\n…ふひっ、ふひひ！\nああ、また昂ぶってしまいます！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1045007, 104531, 0, 0.0, 1, "こんな醜態をさらしながらも\nこの後さらにドSさんに\nめちゃくちゃにされることを\nクウカは望んでいますぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1045008, 104531, 0, 0.0, 1, "「魔物どもにこんなにされて\n嬉しいんだろええおい！」なんて\n言葉責めするつもりですね！？\nさすがはドＳさん、上級者…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1045009, 104531, 0, 0.0, 1, "もうお嫁にいけません…\nだから、一生ドSさんの\n支配下に置いてください！\nもっと体に教え込んでください！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1045010, 104531, 0, 0.0, 1, "クウカはもうドＳさんなしでは\n生きられない体に…\nこ、この責任はちゃんと\n取ってくれるのですよね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1045011, 104501, 0, 8.5, 2, "誕生日おめでとうございます\nケーキに深く刺さるロウソクが、\nとろっとろに溶けていますよ\nフゥー…って消してください", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1045012, 104531, 0, 0.0, 1, "ドＳさんの誕生日ですね…\nクウカの体に巻いたリボンを\n乱暴にほどいて、包装紙を破る\n子供のように服も…ぐふふふふ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1045013, 104561, 0, 0.0, 1, "気持ちいいです～っ♪\nクウカの体を昂らせるこの力…\n全て、ドＳさんに捧げます！\nどうぞお好きなように…ぐふふ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1045014, 104561, 0, 0.0, 1, "はっ！？　ドＳさんの目が\nクウカに告げています…！\n「どこが成長したか見せてみろ」\n分かりました…み、見てください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1045015, 104561, 0, 0.0, 1, "ドＳさんの激しい攻めを\n受け止めるためなら、クウカは\nどこまでも強くなれるんですっ！\nねっ、ドＳさん♪　…じゅるり♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1045016, 104561, 0, 0.0, 1, "ドＳさんとクウカは以心伝心…\n言葉がなくとも通じ合っています\n今は…クウカを弄びたいのですね\n…違うのですか！？　そんな～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1045017, 104561, 0, 0.0, 1, "ドＳさん…いえ、あなたといると\nクウカはいつも心地良くて…\n胸の奥が、温かくなるのです\nだからいつまでも…ふふっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1046001, 104601, 0, 3.0, 2, "野良猫たちに、\nたい焼きを買ってあげてるにゃ！\nみんな喜んでくれるんだにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1046002, 104601, 0, 2.3, 2, "早く準備して出かけるにゃ！\n「時はルピなり」だにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1046003, 104601, 0, 5.8, 2, "お客さ～ん♪\nここだけのオイシイ話が\nあるんだにゃ～\n一口乗ってみないかにゃ～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1046004, 104601, 0, 4.9, 2, "あたしは気まぐれだから、\nちゃーんとつかまえて\nほしいにゃ？\nでないと逃げちゃうにゃ～！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1046005, 104601, 0, 6.0, 2, "ふふふ\nキミが怪我したら…\nぺろぺろ舐めてあげにゃくも…\nないかにゃ～？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1046006, 104631, 0, 0.0, 1, "これでも、困ってる人がいたら\n助けずにはいられないのにゃ！\n今夜は正義のため、猫と一緒に\n飛び回るのにゃー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1046007, 104631, 0, 0.0, 1, "今度、ギルドにも\n遊びに来るといいにゃ\nにゃふふ、これで人手が増え…\n…にゃ、にゃんでもないにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1046008, 104631, 0, 0.0, 1, "あたしは気まぐれだけど、\nやる時はやる猫なのにゃ\n今日はやるかにゃ？\nだったら…全力にゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1046009, 104631, 0, 0.0, 1, "えへへ。キミといられるなら…\n家猫になっちゃっても\nいいかにゃ～\nにゃんてにゃ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1046010, 104631, 0, 0.0, 1, "あとで、キミと二人で\n食べようと思って\nたい焼きを取ってあるのにゃ\n半分こして食べようにゃ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1046011, 104601, 0, 4.5, 2, "今日はキミの誕生日みたいにゃ！\nだから今日だけ特別に\nたい焼き食べ放題に\nしてあげるにゃ～", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1046012, 104631, 0, 0.0, 1, "お誕生日、おめでとうにゃ！\n気まぐれなあたしでも、\n今日ぐらいは…キミだけの\n猫になってあげてもいいにゃ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1046013, 104661, 0, 0.0, 1, "キミとあたしがそろえば、どこに\nお宝が隠されてようと楽勝にゃ！\nファントムキャッツに\n不可能はないのにゃ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1046014, 104661, 0, 0.0, 1, "す、すっごいお宝にゃ…！\nこんなにいっぱいあるなら、\nちょっとくらいネコババしても…\nな、なんでもないですにゃ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1046015, 104661, 0, 0.0, 1, "このお宝があれば、\n苦しんでる人たちを\nいっぱい助けられるにゃ～♪\nさっそく一緒に配りに行くにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1046016, 104661, 0, 0.0, 1, "キミが支えてくれるから、\nあたしはファントムキャッツで\nいられるにゃ…いつも\n一緒に居てくれてありがとにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1046017, 104661, 0, 0.0, 1, "い、いつもは猫とあたしでお宝を\n頂戴してるけど、誰かと一緒も\n悪くにゃいというか…その、\nまた一緒に来てくれるかにゃ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1047001, 104701, 0, 0.0, 1, "こう見えて私は女なんだ\nわけあって鎧は外せないが\nよろしく頼むよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1047002, 104701, 0, 0.0, 1, "鎧に太陽光を浴びせると\n表面が恐ろしく熱くなるが\n中の私は意外と平気なんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1047003, 104701, 0, 0.0, 1, "体を動かすのは好きだが\n水泳は苦手なんだ\n鎧が重くてね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1047004, 104701, 0, 0.0, 1, "顔を見せられない分\n今なにをどう思っているか\n伝えるよう努力するよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1047005, 104701, 0, 0.0, 1, "私の表情は見えないかも\nしれないが、君の表情は\nしっかり見えているよ、いつもね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1047006, 104731, 0, 0.0, 1, "この剣を振らずに済む\n世の中にするためにも、\n私は剣を振らねばならないんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1047007, 104731, 0, 0.0, 1, "どんな強敵が相手であろうと\n私は恐れずに立ち向かうよ\n子供たちの笑顔のために", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1047008, 104731, 0, 0.0, 1, "たとえ炎に包まれた戦場でも\n助けを呼ぶ声がある限り\n私はその地へと赴こう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1047009, 104731, 0, 0.0, 1, "私が戦っている時\n君の視線を背中に感じると\n心が安らぐんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1047010, 104731, 0, 0.0, 1, "私は団長として人々の安息は\n守らねばならないが、君だけは\n優先して守ってしまいそうだ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1047011, 104701, 0, 0.0, 1, "今日は君の誕生日なんだってね\n一つ大人になったんだから、今年は\nあまり無茶をしないでくれよ？\n君のことが心配なんだ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1047012, 104731, 0, 0.0, 1, "誕生日おめでとう！　何か\nお願い事はないかい？　そ、それは\nダメだ少年！　兜を脱ぐなんて…\n恥ずかしくて爆発してしまうよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1047013, 104761, 0, 0.0, 1, "君がそばにいるから…\n限界のその先に手が届く\nこの力は私だけのものじゃない\n君と私…二人の力だ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1047014, 104761, 0, 0.0, 1, "みんなが何事もなく\n平和な日常を送れたらうれしい\nそのために私は剣を振るい\n戦っているんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1047015, 104761, 0, 0.0, 1, "戦いで臆病になって退くな\n私たちの後ろには\n守るべき者がいると思え\n安心しろ、私がついている", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1047016, 104761, 0, 0.0, 1, "君はまた何かに巻き込まれたのか\n…どうして分かるのかって？\nふふっ、顔を見ればすぐに分かる\n私で良ければ力になろう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1047017, 104761, 0, 0.0, 1, "君は私を特別扱いしない\nだから、一緒にいると\nとても居心地が良いんだ\nこれからも変わらずに頼むよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1048001, 104801, 0, 3.9, 2, "素材を稼ぐか、ルピを稼ぐか…\nどっちの戦略も練ってあるから\n必要なら相談してね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1048002, 104801, 0, 2.9, 2, "今回用意した計画もバッチリよ\nもし街の外に行くのなら\n早めに出かけましょう", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1048003, 104801, 0, 4.5, 1, "効率的な動きに大事なのは、\n観察する力よ\n普段からちゃんと鍛えてる？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1048004, 104801, 0, 4.1, 5, "かけ持ちしすぎちゃったから\nバイトを手伝ってくれない？\nほら、キミなら頼りになるし", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1048005, 104801, 0, 5.8, 2, "キミのスケジュール管理、\n私がしてあげようか？\n24時間つきっきりで\nもちろん管理費はいただくけど", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1048006, 104831, 0, 0.0, 1, "よければキミのお財布、\n私が管理してあげよっか？\nえっ？　盗らないわよっ！\n見損なわないでっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1048007, 104831, 0, 0.0, 1, "この町に泥棒が現れたの！？\nまじめに働いてる人への\n冒涜じゃない！\nゆ、許せないわっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1048008, 104831, 0, 0.0, 1, "今日は特売セールの日！\nもちろんキミも、一緒にきてね！\n油断したら命に関わるわよ！\nそれじゃあ…とっつげき～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1048009, 104831, 0, 0.0, 1, "あの…キミって、\n好きなタイプっているのかしら？\nえ？　ちょ、ちょっと気になった\nだけよ。それだけだってばっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1048010, 104831, 0, 0.0, 1, "キミを傷つけるやつがいたら…\nこうしてやっつけてあげるわ！\n私の大切な弟子だもの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1048011, 104801, 0, 0.0, 2, "ケーキの準備よし！\nプレゼントもよし！\nさあ、効率的な\n誕生日パーティーを始めるわよ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1048012, 104831, 0, 0.0, 1, "誕生日おめでとう\nお祝いの言葉なんて\n非効率的かもしれないけど…\nキミにはちゃんと言いたかったの", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1048013, 104861, 0, 0.0, 1, "キミの力と私の力が合わされば、\n相手が誰であろうと\n負けるはずがないわ！\n最大効率で一気に片付けるわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1048014, 104861, 0, 0.0, 1, "攻撃に大事なのは、１に効率、\n２に効率！　３も４も５も効率！\n究極の効率を体現したこの技で、\n纏めてぶっ飛ばしてやるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1048015, 104861, 0, 0.0, 1, "こんな討伐依頼は\nさっさと終わらせて、\nバーゲンセールに行くわよ！\n…もちろん、キミと二人でね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1048016, 104861, 0, 0.0, 1, "敵に包囲されたか…\n前の敵は私がやるから、\nキミは後ろから援護をお願い！\n…私の背中、預けたからね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1048017, 104861, 0, 0.0, 1, "この力…キミの優しい心が、\n私に力を与えてくれてるのね…\nありがとう…私、キミのためなら\nどこまでも強くなってみせるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1049001, 104901, 0, 4.8, 2, "なんでもかんでもどんなことでも\nお姉ちゃんに言ってね！\nもっともっと頼って甘えて～\n遠慮しないでいいんだぞっ☆", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1049002, 104901, 0, 3.2, 2, "いつでもお姉ちゃんに\n甘えてくれていいんだぞっ☆\nナデナデだって、ギューだって、\nいっぱいしてあげちゃうからね♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1049003, 104901, 0, 4.3, 2, "お姉ちゃん、うれしくって、\nついチョップが出ちゃいそう～\nこれはお姉ちゃんの愛情だから、\nしっかり受け止めてね！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1049004, 104901, 0, 2.8, 2, "色んなこと、全部やってあげる♪\nだって、弟くんのお世話をするのは\nお姉ちゃんの役目だもん♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1049005, 104901, 0, 2.1, 2, "なになに？　用事？\nいつでも、どこでも、\nどんなことでも、\nお姉ちゃん、大歓迎だよ～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1049006, 104931, 0, 0.0, 1, "可愛い弟くんを困らせる人は、\nお姉ちゃんが\nぜーんぶ成敗してあげる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1049007, 104931, 0, 0.0, 1, "どんなことがあっても、\n弟くんはお姉ちゃんが\n絶対守るからね！\n弟くんには指一本触れさせない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1049008, 104931, 0, 0.0, 1, "お姉ちゃんは、弟くんを\n守るために強くなったんだもん\nお姉ちゃんは、弟くんの盾\n弟くんの剣。弟くんの鎧だよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1049009, 104931, 0, 0.0, 1, "弟くんとのひとときを邪魔する\n野暮な奴は許さないよ！\nチョップくらいじゃ\n済まさないんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1049010, 104931, 0, 0.0, 1, "弟くんをいじめるなんて、\n神さまが認めても、\n天が見逃しても、\nお姉ちゃんが許さない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1049011, 104901, 0, 7.9, 2, "キミが一つ歳をとっても、\nお姉ちゃんも歳をとってるから、\n私はずっと弟くんのお姉ちゃんの\nまんまだよ♪　安心した？", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1049012, 104931, 0, 0.0, 1, "お誕生日おめでとう！\n弟くんがどれだけ大人になったか\nお姉ちゃんに、しっかり見せて！\nじーーーーーーーーーーーーっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1049013, 104961, 0, 0.0, 1, "弟くんっ、お姉ちゃんだよっ☆\nえへへ、ビックリした？\nじゃあ今度は驚かないように\n耳元で優しく…お姉ちゃんだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1049014, 104961, 0, 0.0, 1, "お姉ちゃん、強くなっちゃった！\nこの力で弟くんを守っちゃうね♪\nそのためには～、もっとも～っと\n一緒にいなくちゃね？　ねっ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1049015, 104961, 0, 0.0, 1, "ねぇ弟くん、お姉ちゃんの悩みを\n聞いてくれるかなぁ？　あのね、\nもっと弟くんに甘えてほしいんだ\n…どうしたらいいと思う？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1049016, 104961, 0, 0.0, 1, "ぎゅう～っ♪\nお姉ちゃんハグ、温かいでしょ？\nあっ…ちょっと離れてもいい…？\n…ドキドキ、伝わっちゃったかな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1049017, 104961, 0, 0.0, 1, "ぜ～んぶお姉ちゃんに任せてね♪\nん？　全部ってどれくらいって？\n弟くんが望むことすべて、だよっ\n…私の幸せは、キミの幸せだもん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1050001, 105001, 0, 2.5, 2, "ちょっとこの雑誌見てー！\n右の服と左の服、\nどっちがアタシに似合うと思う？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1050002, 105001, 0, 6.9, 6, "ね、ちょっと聞いていい？\nアンタって３桁の掛け算とかも\n出来たりするわけ？\nま、まさか割り算とかも…！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1050003, 105001, 0, 3.5, 5, "大人のレディには教養も大事？\nきょ、教養ってあれよね、\n成績とは関係ないのよね！？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1050004, 105001, 0, 5.5, 2, "同年代の子と話しても、みんな\n子どもっぽくって退屈なのよね\nだからアンタと遊んであげるわよ\n感謝しなさいよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1050005, 105001, 0, 3.4, 5, "まずカフェに行って、次に…\nアンタはこうゆうの、\n詳しくなさそうだから\nアタシがエスコートしないとね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1050006, 105031, 0, 0.0, 1, "大人なレディだもん、\n魔物退治くらい簡単よ！\nまぁ、どうしても気になるなら、\n付いてきてもいいけど？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1050007, 105031, 0, 0.0, 1, "この前、魔物を蹴散らして\nがっつりお宝ゲットしたのよ！\n「子どもに大金は必要ない」って\n親にとりあげられたけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1050008, 105031, 0, 0.0, 1, "どきなさーい魔物ども♪\nレディ・ミサキのお通りよ！\nアンタはちゃんと、\nエスコートしなさいよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1050009, 105031, 0, 0.0, 1, "なんか最近、アンタと出かける\n機会、多いわよね\nアタシはけっこう楽しんでるわよ\nアンタはどう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1050010, 105031, 0, 0.0, 1, "二人で魔物退治して、\nそのお金で豪華にディナー！\nそれってすっごく大人よねぇ♪\nで、デートじゃないわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1050011, 105001, 0, 9.8, 6, "誕生日、おめでと！\nプレゼントは…ア・タ・シ・よ♪\n…あれ？　引いてる？\n雑誌に書いてあったのに…？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1050012, 105031, 0, 0.0, 1, "ハッピーバースデー！\nアタシが、大人なバースデーを\nプロデュースしてあげるわ\n楽しみにしてなさい♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1050013, 105061, 0, 0.0, 1, "うふ♪\n今日のアタシは一味違うわよ\nアタシがよそ見しないように\nしっかりエスコートしてよね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1050014, 105061, 0, 0.0, 1, "ねぇねぇ、アタシキレイでしょ♪\n…むぅ。かわいいじゃなくて、\nキレイって言いなさいよ\n言うまで帰さないんだからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1050015, 105061, 0, 0.0, 1, "え？　いつもより大人っぽい？\nほんと！？　…じゃなくて…\n当然のことよ。だってアタシは\n正真正銘のレディなんだから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1050016, 105061, 0, 0.0, 1, "大人のレディなら微笑むだけで\nみんなを虜にしちゃうのよ♪\nねぇ、アンタはどう？\nアタシにドキドキする！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1050017, 105061, 0, 0.0, 1, "アタシ、これからもっと\nキレイになると思うわ\nだからその日まで、アタシから\n目を離さない方がいいわよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1051001, 105101, 0, 3.0, 2, "ねえ君、今時間ある？\n怖がらなくても大丈夫\n危ないことはしないわ\n私を信じて…ね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1051002, 105101, 0, 2.3, 5, "良かったら一緒に実験しない？\n新しい発見があった時の\n興奮は計り知れないわよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1051003, 105101, 0, 2.5, 2, "科学の発展はみんなのため\nこう見えても、病気や怪我を\n治すのが目標なの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1051004, 105101, 0, 5.4, 2, "君の体を調べている内に、\n君自身に興味が出て来たわ\nうふふっ…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1051005, 105101, 0, 1.7, 5, "いつも実験に協力してくれて\nありがとう。お礼に今度、\nとっておきの秘薬をあげるわ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1051006, 105131, 0, 0.0, 1, "あーら、私におしおきされたいの？\nそう…目覚めちゃったのね\nたっぷりと可愛がってあげる♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1051007, 105131, 0, 0.0, 1, "ねぇ、どうかしら…？\n私を見て、何か感じない？\n恐怖？　…おかしいわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1051008, 105131, 0, 0.0, 1, "痛みが快楽に変わる瞬間は、\n必ず訪れるものよ…\n君も体験してみない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1051009, 105131, 0, 0.0, 1, "君を虜にするための香水を\nつけてきたはずなのに、\nどうして魔物ばっかり…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1051010, 105131, 0, 0.0, 1, "君といると、不思議だけど…\n鼓動がぐっと早まるの\n…確かめてみる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1051011, 105101, 0, 12.8, 2, "君、今日が誕生日でしょう？\nはいこれ。私からのプレゼント\n特別に調合した薬なの。効能？\n…飲んでからのお楽しみ♪", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1051012, 105131, 0, 0.0, 1, "ハッピーバースデー！\n私からの贈り物は…これよ\n元気が出る特製ドリンク♪\n一気に飲んじゃだめよ…？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1051013, 105161, 0, 0.0, 1, "探求心は人間の限界を超えていく\n全ての不可能はやがて可能になる\n…君と実験を続けていけば\nそれを証明できると信じているわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1051014, 105161, 0, 0.0, 1, "新しい薬の効果は絶好調ね\nこれも君がずーっと実験台を\n務めてくれたおかげよ♪\n…あら、何か言いたそうな顔ね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1051015, 105161, 0, 0.0, 1, "新しい研究のアイデアが\n次から次へと溢れてくるのよ\nどんな実験をするつもりかって？\nふふっ、すぐに体験させてあげる", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1051016, 105161, 0, 0.0, 1, "研究に行き詰ったときでも\n君の顔を思い出すと、もう少し\nがんばろうって気になるのよ\n私にとっては、栄養剤ね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1051017, 105161, 0, 0.0, 1, "どれだけ研究を重ねても、結婚の\n正解だけは分からないわね\nでも、君と検証していけば\n答えも出るかしら…ふふっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1052001, 105201, 0, 5.8, 5, "私、力持ちだからそれを活かして\nお仕事もいっぱい頑張ってるの♪\nだけど、これって女子的には\nちょっとどうなのかしら～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1052002, 105201, 0, 7.0, 9, "どう？　すてきな鎧でしょ？\nデザインもおしゃれだし\nボディにジャストフィットなの！\n私のお気に入りよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1052003, 105201, 0, 0.0, 5, "も～、あなたったらさっきから\nモフモフしすぎじゃない？\nせっかくブラッシングして\n毛並みを整えたばかりなのに～", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1052004, 105201, 0, 4.8, 1, "少しでも女の子らしく\n見えるように、私、がんばるわ！\nこのままあなたのそばに\n居られるように…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1052005, 105201, 0, 0.0, 5, "はぁ～…憧れちゃう…\n普通の人間の女の子みたいに\nおしゃれしたり食べ歩きしたり\nあなたとデートしたいな…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1052006, 105231, 0, 0.0, 1, "こんな見た目だからって\n最初からあきらめちゃダメよね！\nあなたに嫌われたくないし\n身だしなみには気を付けるわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1052007, 105231, 0, 0.0, 1, "なあに？　またモフモフするの？\nまあいいけどね、あなただから\nい～こい～こしてあげるわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1052008, 105231, 0, 0.0, 1, "魔物に間違われることも多いけど\n私、運命なんかに負けないわ！\nあなたと会ってから、ますます\nそう思えるようになったの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1052009, 105231, 0, 0.0, 1, "あなたは私のこの姿を見ても\n女の子として接してくれた！\n人の内側をきちんと見てくれる\nそんなあなたが大好き！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1052010, 105231, 0, 0.0, 1, "こんなに綺麗な空の下を\n大好きな人と一緒に歩けること\n普通の女の子になったみたいで\nすっごく嬉しいの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1052011, 105201, 0, 7.2, 9, "お誕生日おめでとう～！\nあなたのためにお祝いのプランを\nいっぱい考えたの♪\nやだっ、私ったらはしゃぎすぎ？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1052012, 105231, 0, 0.0, 1, "ハッピーバースデートゥーユー♪\n人間の男の子の誕生日をこうして\nお祝いできるなんてね…\n私がプレゼントをもらった気分♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1052013, 105261, 0, 0.0, 1, "いつかこの素敵なドレスを着て\nあなたと踊れたらいいな…\n踊りの練習だけでも\n始めておこうかしら♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1052014, 105261, 0, 0.0, 1, "舞台の幕が上がったら\n私はお姫様…まさかこんな形で\nお姫様になれるなんて\n思わなかったけど、うれしいわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1052015, 105261, 0, 0.0, 1, "私がお姫様になったら\n何をしようかしら…\n王子様と二人きりの舞踏会とか\nしてみたいわね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1052016, 105261, 0, 0.0, 1, "私、あなたと出会ってから毎日が\nどんどん楽しくなっていくの！\n次はいつ会えるんだろうって\n考えてるだけで幸せよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1052017, 105261, 0, 0.0, 1, "あなたが王子様だったら、\n誰のどんなピンチにでも\n駆けつけてくれるんでしょうね！\n優しい王子様に憧れちゃう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1053001, 105301, 0, 4.1, 2, "貴公と私、共に\nヴァイスフリューゲルの\n興隆のために\n力を尽くそうではないか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1053002, 105301, 0, 2.7, 5, "こ、子供ではない！\nた、確かに少々背は低いが…\nまだ成長期に違いない！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1053003, 105301, 0, 4.5, 2, "ん？　どうした？\n私と鍛錬がしたいのか？\n殊勝な心がけだな、貴公！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1053004, 105301, 0, 4.5, 2, "軍人は、受けた恩は返すものだ\nだから…私にできることが\nあれば、なんでも言ってくれ\n頼りにしてくれよな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1053005, 105301, 0, 3.0, 5, "と、ところでだな…\n今度、貴公と二人で、\n我が本国を旅行するとか…\nど、どうだろうか？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1053006, 105331, 0, 0.0, 1, "何度言えばわかるのだ！\nだから、私は子供ではないっ！\nば、バカにするな～！\nこ、こら、頭をなでるな～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1053007, 105331, 0, 0.0, 1, "天空より舞い降りし\nヴァイスフリューゲルの剣技！\nその華麗なる姿を、\n見せてやる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1053008, 105331, 0, 0.0, 1, "貴公！\n気がたるんではいないか？\n今すぐ準備をして平原に来い！\n私と共に鍛錬だ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1053009, 105331, 0, 0.0, 1, "私は子供ではない、が…\n貴公から見たら、やはり子供に\n見えてしまうのだろうか？\nそうでないと、いいのだが…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1053010, 105331, 0, 0.0, 1, "貴公に私の命は預けた\nこの身が尽きるまで…\n軍人の誇りを持って、\n二人で戦いぬくぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1053011, 105301, 0, 8.1, 2, "今日は貴公の誕生日らしいな\n大切な一日を共に祝えて、\n私も嬉しく思うぞ！\n心から祝おうじゃないか！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1053012, 105331, 0, 0.0, 1, "誕生日おめでとう\nプレゼント代わりに…\n今日は私が共に過ごしてやろう\nじ、時間を無駄にするでないぞ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1053013, 105361, 0, 0.0, 1, "待たせたな！\nこれが私の新しい奥義だ！\n友よ、見届けてくれ！\n我らの勝利の瞬間を！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1053014, 105361, 0, 0.0, 1, "驚くのはまだ早いぞ！\nなんといっても私は成長期！\n修行すれば更に大きく…\n背の話ではなく奥義の話だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1053015, 105361, 0, 0.0, 1, "よし！　また成功だ！\n貴公、見てたか！？\n…『努力の天才』？\nて、照れるではないか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1053016, 105361, 0, 0.0, 1, "私の勝利が貴公の勝利となる！\nだからこそ負けられん！\n我が名誉よりも、友への想いで\n私はこの勝負に勝つのだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1053017, 105361, 0, 0.0, 1, "どれほどこの身が傷つこうとも\n敵は誰一人通さんっ！\n我が友を…\n貴公を守るのは、この私だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1054001, 105401, 0, 1.9, 5, "いいですか騎士さん\nレイ様に変なことしたら\nただじゃおきませんよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1054002, 105401, 0, 2.8, 2, "騎士さんがレイ様に\n変なことしないように\n私がしっかり見張ります！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1054003, 105401, 0, 2.0, 1, "ああ、また服が…\nちょっとこっちに来てください\nすぐに直してあげますから", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1054004, 105401, 0, 4.8, 6, "あの…騎士さんって\nやっぱりレイ様のこと…\nな、なんでもないです！\n忘れてください！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1054005, 105401, 0, 6.5, 6, "どうして騎士さんは\nひどいことした私にここまで\n優しくしてくれるんですか？\nまさか…また縛られたいとか！？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1054006, 105431, 0, 0.0, 1, "何匹来ても平気です！\n騎士さんがそばにいてくれるから！\n…変な意味じゃありませんよ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1054007, 105431, 0, 0.0, 1, "そこはさすがに近すぎます\nもう少し下がってください\nさもないと、日頃の鬱憤\nぶつけちゃいますよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1054008, 105431, 0, 0.0, 1, "素材が余ったら騎士さんにも\n何か作ってあげましょうか\nそうですね…何がいいですか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1054009, 105431, 0, 0.0, 1, "最近も…相変わらずレイ様と\n仲いいんですか…？\nき、聞いてみただけですっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1054010, 105431, 0, 0.0, 1, "騎士さんは私に何か\n手伝ってほしいことあります？\n報酬は特別になしでいいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1054011, 105401, 0, 5.3, 5, "た、誕生日なんですよね？\n一応おめでとうございます！\n…どこで知ったかなんて\nどうでもいいじゃないですか！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1054012, 105431, 0, 0.0, 1, "あの、おめでとうございます…\n騎士さんの誕生日ですよ！\nこれ…ハンカチです…え？\nプレゼントですっ！　バカっ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1054013, 105461, 0, 0.0, 1, "騎士さんとの絆が強くなるほどに\n私の力も強くなります！\n…って、いいですけど\nちょっと近くないですか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1054014, 105461, 0, 0.0, 1, "一人一人が輝く服を作るのが\n私の夢なんですっ！\nこの力があれば、\nそれを叶えられますよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1054015, 105461, 0, 0.0, 1, "見ていてください！\nアイドルとしてだけじゃなく\n仕立て屋としても\nみんなを笑顔にしてみせますっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1054016, 105461, 0, 0.0, 1, "いつもお世話になってますから\n今度は私がお手伝いしますね？\nべ、別に騎士さんと一緒に\n居たいわけじゃないです…からっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1054017, 105461, 0, 0.0, 1, "もしかして、私の運命の赤い糸は\nレイ様じゃなくて騎士さんと\nつながってたり…して…？\nやだ、私なにを考えてるんだか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1055001, 105501, 0, 2.5, 5, "ジー…っは！\nせ、先輩！？　えっとあのその…\nな、なんでもないですぅ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1055002, 105501, 0, 4.7, 5, "はわわ、しぇんぴゃい！？\nほほ、ほんじちゅはお日柄もよく\nか、噛んじゃった…恥ずかしいよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1055003, 105501, 0, 3.6, 4, "あ、せ、先輩！？\nつ、つけてたわけじゃ…\nこ、これはその…観察で…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1055004, 105501, 0, 5.7, 5, "あ、あ、あの…せせ、先輩！\nよかったりゃ今度、一緒に…\nご飯…や、やっぱむり～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1055005, 105501, 0, 5.4, 5, "せ、先輩…い、いつかちゃんと\nお礼するので…\nま、待っててください！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1055006, 105531, 0, 0.0, 1, "魔物退治のお手伝いを\n私なんかにさせていただいて\nありがとうございます！\nが、頑張りますから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1055007, 105531, 0, 0.0, 1, "戦うのは怖いけど、\n今は先輩がいるから\nとっても心強いです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1055008, 105531, 0, 0.0, 1, "せ、先輩は下がっててください！\nわた、私が…魔物を…\nうぅ、やっぱり手伝ってくださーい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1055009, 105531, 0, 0.0, 1, "いつも助けられてばっかりだから\n…今度は私が先輩を助けます！\nお、恩返しがしたいんです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1055010, 105531, 0, 0.0, 1, "せ、先輩と私のコンビネーション\n…バ、バッチリですね！\nえへへへ、共同作業だぁ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1055011, 105501, 0, 8.8, 5, "あっあの、先輩っ！\nお誕生日おめでとうございます！\nお祝いさせてくだひゃい！\nちゃ、ちゃんと言えましたっ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1055012, 105531, 0, 0.0, 1, "お誕生日おめでとうございます！\nプ、プレ、プレゼントどうぞっ！\n先輩に似合う服を見つけました\nそれから次は、先輩が好きな…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1055013, 105561, 0, 0.0, 1, "せ、先輩に助けてもらって\nばっかりじゃありません…！\nこれからは、私だって…\n先輩と一緒に戦います！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1055014, 105561, 0, 0.0, 1, "ど、どうですか…？\n前より、ちょっとくらいは\n頼りがいのある感じに\nなってますか？　私…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1055015, 105561, 0, 0.0, 1, "キ、キリっとした顔で、\n先輩に成長したところを\nちゃんと見せなきゃ…！\n眉毛をあげればいいのかな…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1055016, 105561, 0, 0.0, 1, "先輩と一緒にいろんなことを\n経験して、自信がついてきました\nだ、だからこれからも…\nよろしくお願いしましゅ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1055017, 105561, 0, 0.0, 1, "怖いことや危ないことがあっても\nずっと先輩のそばにいます\nきっとそれだけが、私に\nできることですから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1056001, 105601, 0, 5.2, 2, "お節介がすぎるのか、よく\n面倒ごとに巻き込まれるんだ\nお前さんも困ったことがあれば\n遠慮なく声かけておくれ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1056002, 105601, 0, 4.4, 1, "ひと仕事終えたし\n釣りにでも行くとするか…\nお前さんもどうだい？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1056003, 105601, 0, 8.3, 5, "お前さん、いい所に来たね！\n悪さしてたヤツらをちょいと\n懲らしめたら謝礼がもらえてね\nちょいと一杯付き合っとくれよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1056004, 105601, 0, 2.7, 1, "抱き締めるのはよせって？\n分かっちゃいるんだけどね\nお前さんがいい男なのが\n悪いんだよ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1056005, 105601, 0, 5.0, 2, "かわいい見た目してるのに\n意外と肝が据わってるね\nまた厄介事があった時は\n頼らせておくれよ、お前さん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1056006, 105631, 0, 0.0, 1, "お前さんのためなら\nコイツを抜くのも悪くない…\n全力でいくとしようか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1056007, 105631, 0, 0.0, 1, "アタシにもう一本使わせたこと\nたっぷり後悔させてやるよ\nその身でとくと味わいな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1056008, 105631, 0, 0.0, 1, "何人相手にすりゃいいんだ？\nこっちは早いとこ終わらせて\nのんびりしたいってのに…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1056009, 105631, 0, 0.0, 1, "手伝ってくれるのかい？\nお前さんがいると心強いね！\nとっとと片付けちまおう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1056010, 105631, 0, 0.0, 1, "誰かに背中を預けるなんて\nお前さんくらいなもんさ\nこれからも、頼りにしてるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1056011, 105601, 0, 3.3, 2, "お前さん、今日が誕生日なのか？\nそうか…めでたい日にはパーッと\n祝うに限る。そうと決まれば\n今日の飯はアタシのおごりだ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1056012, 105631, 0, 0.0, 1, "誕生日おめでとさん！\nいい釣竿が手に入ったんだ\nお前さんにあげるよ。今度二人で\n一緒にのんびり釣りに行こう", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1056013, 105661, 0, 0.0, 1, "惚れたからにゃ命懸けさ\nさぁ、アタシの恋路を\n邪魔するのはどいつだい！\n斬り刻んでやるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1056014, 105661, 0, 0.0, 1, "お前さんがアタシを女にしたのさ\n惚れるってのはね、心に彫りモン\n入れるようなもんなんだ\n取り返しはつかないよ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1056015, 105661, 0, 0.0, 1, "万一の時はお前さんが\n介錯してくれるんだろう？\nならどっちに転んでも\nアタシは果報者さ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1056016, 105661, 0, 0.0, 1, "刀と鞘、竿と針、椿と一輪挿し\nそんな関係でいられたら…なんて\nも、もちろん\nお前さん次第だけどさ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1056017, 105661, 0, 0.0, 1, "確かにアタシは男勝りの剣客さ\nだけど、思っちゃだめかい？\nお前さんの前でだけは\nかわいい女でいたいって", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1057001, 105701, 0, 3.2, 2, "同じ騎空士として\n一緒に頑張ろうね！\n何か困ったことがあったら\nいつでも相談して！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1057002, 105701, 0, 3.0, 2, "良かったら一緒に修行しない？\n一人よりも二人でやったほうが\nもっと強くなれると思わない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1057003, 105701, 0, 0.0, 1, "私たちの騎空艇ができたら\nまずはアストライア大陸を\n一周したいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1057004, 105701, 0, 3.7, 5, "あなたが一緒なら、\n何があっても怖くないよ！\n…本当だよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1057005, 105701, 0, 4.0, 6, "ねぇ、副団長！　…おーい！\nやっぱり名前で呼んだほうが\nいいかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1057006, 105731, 0, 0.0, 1, "今までの修行の成果\n見せてあげる！\nいくよっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1057007, 105731, 0, 0.0, 1, "大切なものを\n守るためなら、\n私は戦うよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1057008, 105731, 0, 0.0, 1, "冒険のために、\n毎日特訓してるんだ…！\n絶対負けないんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1057009, 105731, 0, 0.0, 1, "団長として、仲間には\n指一本触れさせない！\n私が相手になります！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1057010, 105731, 0, 0.0, 1, "あなたと一緒にいると、\nどんな困難だって\n乗り越えられる気がする！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1057011, 105701, 0, 4.0, 1, "今日はあなたの誕生日だよね、\nおめでとう！\nいっぱいお祝いしないとね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1057012, 105731, 0, 0.0, 1, "誕生日おめでとう！　お祝いに\nパーティーしようよ！　それと何か\n欲しいものがあったらプレゼント\nしてあげるから言ってね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1057013, 105761, 0, 0.0, 1, "あなたとこの蒼い空を守る…！\n私たちならきっとできるよ！\nさあ立って！　一緒に戦おう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1057014, 105761, 0, 0.0, 1, "新しい力と、新しい姿…！\nあなたがいてくれたから\n私、また強くなれたよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1057015, 105761, 0, 0.0, 1, "空に剣の光が輝き続ける限り\n私は負けない！　だって私には\nあなたとの冒険が待ってるから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1057016, 105761, 0, 0.0, 1, "一人じゃないって\nこんなにも安心できるんだ…\nあなたが不安で寂しい時は\n私が支えてあげるね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1057017, 105761, 0, 0.0, 1, "私ね、これからもずっと\nずーっと二人で冒険して…\nいつか父さんに会いたいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1058001, 105801, 0, 3.0, 2, "えへへ、ペコリーヌってあだ名、\nわたし結構気に入ってるんですよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1058002, 105801, 0, 3.2, 2, "もしかして魔物料理、\n苦手でしたか？\nわたしは好きなんですけど", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1058003, 105801, 0, 2.5, 7, "ぐるるる～…\nあ、お腹が鳴っちゃいました\nわ、笑っちゃダメですよ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1058004, 105801, 0, 2.1, 1, "ごはん食べにいきましょう！\n一緒に食卓を囲むと仲良くなれ\nますよね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1058005, 105801, 0, 2.2, 5, "食べ物くれるんですか？\nわあ、恋してもいいですかね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1058006, 105831, 0, 0.0, 1, "お腹空きませんか？\n良かったらわたしと一緒に、\nご飯でも食べに行きません？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1058007, 105831, 0, 0.0, 1, "食べものは大事にしましょう\nまあ、わたしがいれば食べ残し\nなんてありえませんけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1058008, 105831, 0, 0.0, 1, "好きな料理ですか？\nそうですね～\n「食べ放題」とか\n「飲み放題」とか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1058009, 105831, 0, 0.0, 1, "男の人とふたりで食事って\nちょっとドキドキしますね！\nやばいですね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1058010, 105831, 0, 0.0, 1, "好みの男性のタイプ？\n「食べものを粗末にしない人」\nです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1058011, 105801, 0, 4.5, 1, "今日、誕生日なんですよね！\nおめでとうございます！\n特別な日の特製特大ケーキ、\n一緒に食べませんか？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1058012, 105831, 0, 0.0, 1, "おめでとうございます☆\n来年のあなたのお誕生日も\n必ずお祝いしますね♪\n絶対絶対ぜーったいです！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1058013, 105861, 0, 0.0, 1, "あなたが側にいるだけで\n体の奥から\n勇気が溢れちゃいます！\nやばいですね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1058014, 105861, 0, 0.0, 1, "ごはんの力！\nそしてあなたとの絆！\n今のわたしは止められませんよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1058015, 105861, 0, 0.0, 1, "あなたのためなら\nわたしはどこまでも\n強くなれます！\n一緒に頑張りましょう☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1058016, 105861, 0, 0.0, 1, "あなたと食卓を囲むと\nごはんがとっても美味しいんです\nわたしたちって\n相性ばっちりですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1058017, 105861, 0, 0.0, 1, "わたし、もっともっと強くなって\nあなたを守り続けてみせます！\nずっとあなたといたいですから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1059001, 105901, 0, 2.0, 2, "ガイド役のわたくしが、\nしっかりサポートさせて\nいただきます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1059002, 105901, 0, 3.7, 2, "主さま、\nお腹はすいていませんか？\n何事もお腹を満たしてからで\nございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1059003, 105901, 0, 2.3, 2, "お疲れのようですね\n体調管理もわたくしの役目です\n何かあれば、遠慮せず\nおっしゃってください", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1059004, 105901, 0, 0.0, 5, "主さまのような優しい方に\nお仕えできて、わたくしは\n幸せ者でございます", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1059005, 105901, 0, 0.0, 2, "主さまの奥さんになったつもりで\n尽くさせていただきますね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1059006, 105931, 0, 0.0, 1, "主さまと出会うべき日に備え、\n幼少期から精霊と\n語り合ってきました\n存分にお使いくださいませ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1059007, 105931, 0, 0.0, 1, "精霊たちも、主さまが見ていると\n調子が良いみたいです♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1059008, 105931, 0, 0.0, 1, "わたくしは自然と共に、\n主さまのお力となります", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1059009, 105931, 0, 0.0, 1, "主さまのお世話をすることを\n幼い頃から、楽しみにして\nおりました", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1059010, 105931, 0, 0.0, 1, "何かあれば、わたくしを\nお呼びください\n全身全霊で主さまの要求に\nお応えいたします", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1059011, 105901, 0, 11.3, 2, "お誕生日おめでとうございます♪\nお誕生日おめでとうございます♪\n何度口にしても言い足りません…\n主さま、おめでとうございます♪", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1059012, 105931, 0, 0.0, 1, "主さまの生誕された、この日は、\nわたくしには一年で最も尊い日\n心から祝福させていただきます\nお誕生日おめでとうございます♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1059013, 105961, 0, 0.0, 1, "主さまとの心のつながりが、\nこの力を生み出したのですね\n主さま…これからもずっと、\nおそばに居させてくださいまし♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1059014, 105961, 0, 0.0, 1, "わたくしの想いに、\n精霊たちが応えてくれている…\nこの力があれば、\nもっと主さまにご奉仕できます♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1059015, 105961, 0, 0.0, 1, "なんて優しい風…\nふふっ…まるで精霊たちが、\nわたくしと主さまを\n祝福してくれているようです♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1059016, 105961, 0, 0.0, 1, "花びらが花嫁のブーケのように…\nわたくしと主さまも…はっ！\nわ、わたくしとしたことが、\nなんて大それたことを…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1059017, 105961, 0, 0.0, 1, "この力があれば、\nもっと主さまのお世話ができます\nおはようからおやすみまで、\n全てわたくしにお任せください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1060001, 106001, 0, 3.5, 2, "あんた、あたしを\n助けてくれたんだって？\nまぁ、一応だけど\nお礼をいっておくわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1060002, 106001, 0, 0.0, 5, "あんた、あんまり気安く\n話しかけないでよね！\n友だちでも\n何でもないんだから！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1060003, 106001, 0, 6.5, 5, "気配を消して、周りをよく見て、\nあいつに気付かれないように…\nきゃっ！　なんでいっつも\n尾行がバレちゃうのよ！？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1060004, 106001, 0, 6.0, 2, "あんた、記憶喪失だし\nたいへんだろうから、\nあたしが面倒みてあげるわ\n感謝しなさい♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1060005, 106001, 0, 5.0, 2, "本当は、あんたと仲良くしてちゃ\nダメなんだけどね…\nまぁ、楽しいからいっか♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1060006, 106031, 0, 0.0, 1, "み、見たわね、秘密の魔法特訓\nこうなったらあんたにも、\n付き合ってもらうわよ！　", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1060007, 106031, 0, 0.0, 1, "あたしの魔法、\nけっこうすごいでしょ？\nふふ～ん♪\nもっと褒めて褒めて♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1060008, 106031, 0, 0.0, 1, "完璧完璧！　ちゃーんと\n上達してるじゃない♪\nやっぱり、あたしって\nやればできる子ね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1060009, 106031, 0, 0.0, 1, "ふふん、華麗なもんでしょ\nあんたにだけ教えてやるけど\n実は最近まで初心者だったのよ\nほら、もっと驚きなさい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1060010, 106031, 0, 0.0, 1, "回復魔法もそこそこ使えるのよ\n万一のことがあったら、まあ\n助けてあげなくもないわ\n万一なんて無い方がいいけどね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1060011, 106001, 0, 8.9, 5, "へ～、あんた、誕生日なんだ～\nあ、たまたまケーキ持ってるから\n一緒に食べましょ？\n…たまたまよ！　本当だからね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1060012, 106031, 0, 0.0, 1, "はい、バースデープレゼント\n感謝しなさいよ！\nこんなものあげてるって、\n本当はバレたらマズいんだから…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1060013, 106061, 0, 0.0, 1, "あたしの魔力とあんたの力が\n一つになっていく…！\nさあ、かかってきなさい！\n今日のあたしは一味違うわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1060014, 106061, 0, 0.0, 1, "これがあんたとあたしの\n真の力なのね…！\nあんたと一緒なら\n負ける気がしないわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1060015, 106061, 0, 0.0, 1, "ふっふっふっ…どうよこの力！\nもっと褒めても…うひゃあ！\nきゅ、急に頭なでんな！\nぶ、ぶっ殺すぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1060016, 106061, 0, 0.0, 1, "あいつとの心のつながりが力に…\nこういうのも悪くないわね\nなっ！　あんた聞いてたの！？\nい、今すぐ忘れろばかぁぁ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1060017, 106061, 0, 0.0, 1, "この新しい力であんたを\n守ってあげるから…その…\nあ、あたしのそばから\n離れるんじゃないわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1061001, 106101, 0, 2.2, 2, "ん？　ああ、オマエか！\nアタシになんか用か？\n困ったことがあったらちゃんと\n言うんだぞ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1061002, 106101, 0, 5.8, 2, "オマエが記憶喪失か…\n早くよくなるといいな！\n効きそうな薬、探してやるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1061003, 106101, 0, 0.0, 2, "オマエのプリンセスナイトの力\nアタシにも使ってくれよな！\n敵だった時にさ、見てて\nいっつもわくわくしたんだよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1061004, 106101, 0, 2.9, 2, "ちょっと前までは色々と\n不安だったけど、今はもう\n大丈夫だぞ！　オマエやみんなと\n助け合えるからな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1061005, 106101, 0, 5.1, 3, "敵だったオマエとアタシが\n今は味方同士になれたんだ…\nだからきっと、オクトーだって…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1061006, 106131, 0, 0.0, 1, "オマエら全員道を開けろ！\n邪魔するヤツは、みんな\nアタシがぶった斬るぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1061007, 106131, 0, 0.0, 1, "新しい天楼覇断剣か…どんな\nヤツが相手でも、コイツがあれば\nアタシは無敵だ！　いくぞぉぉぉ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1061008, 106131, 0, 0.0, 1, "もう逃げも隠れもしないぞっ！\nどんなに傷ついても最後まで\n戦ってやる！　そう決めたんだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1061009, 106131, 0, 0.0, 1, "はっはっはーっ！\nどけどけえっ！！\nノウェム様のお通りだぁ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1061010, 106131, 0, 0.0, 1, "やっとオマエと味方になれたんだ\n早いとこオクトーの目を覚まして\nアイツとも一緒に戦いたいな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1061011, 106101, 0, 2.1, 5, "誕生日おめでとう！\nあんまり人に物あげたこと\nなくてさ…何が欲しいんだ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1061012, 106131, 0, 0.0, 1, "ハッピーバースデー！\nよーし！　ケーキ買いに行くぞ！\n今日はアタシのおごりだ！\nたぶん明日は雪が降るぞ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1061013, 106161, 0, 0.0, 1, "王様だろうが神様だろうが\n誰かを苦しめる悪いヤツは\nアタシと天楼覇断剣が\nキレイにバッサリぶった斬るぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1061014, 106161, 0, 0.0, 1, "いつだってどんな世界にいたって\nアタシたちでみんなを救うぞ！\n立ち止まるな！　前を向け！\nもう二度と負けないために！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1061015, 106161, 0, 0.0, 1, "どうだ！　カッコいいだろ！\nもっと近くでよく見ていいぞっ！\nおっと危ない…！　間違って\n真っ二つにするところだった…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1061016, 106161, 0, 0.0, 1, "助け合う仲間がいるって\nやっぱりいいな！　オマエと\nアタシが一緒に立ち向かえば\n何だって誰だって敵じゃないぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1061017, 106161, 0, 0.0, 1, "オマエともいろんなことを\n乗り越えてきたな！\n天楼覇断剣と同じくらい\n頼もしくなったぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1063001, 106301, 0, 5.8, 1, "ランドソルってすごいなぁ…\n色々あって、大きいし…\n私の里とは全然違うや", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1063002, 106301, 0, 5.3, 1, "私の里もいいところなんだよ\n…なーんもないけど、\nでも、気に入ってくれると思うな！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1063003, 106301, 0, 6.1, 2, "あなたとロザリアって、\nちょっと似てるかも？\n例えば…\n一緒にいて安心するところ、とか", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1063004, 106301, 0, 5.7, 2, "あなたといると、\nドキドキしたり、苦しくなったり…\nでも、なんか…嬉しいんだ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1063005, 106301, 0, 4.1, 2, "私、あなたと一緒にいると…\n安心するし、ドキドキするんだ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1063006, 106331, 0, 0.0, 1, "いつか必ず、森番になるんだ\nもちろん、ロザリアと一緒にね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1063007, 106331, 0, 0.0, 1, "ランドソルは広いけど…\n風の匂いは、里と同じかも", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1063008, 106331, 0, 0.0, 1, "私の里にはね、\n妖精さんがいっぱいいるんだよ！\nあなたにも見せてあげたいなぁ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1063009, 106331, 0, 0.0, 1, "ドキドキするのに、落ち着くし…\n安心するのに、たまに苦しくて…\nあなたの側にいると、\n不思議な気持ちになっちゃうな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1063010, 106331, 0, 0.0, 1, "森にいた頃よりも、\n今の方が楽しく思えちゃうのは…\nきっと、あなたのおかげかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1063011, 106301, 0, 4.9, 2, "ちょっと自信ないけど…\nこれ！　作ったんだ！\n今日は特別な日だから…\nあーんってしてあげよっか…？", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1063012, 106331, 0, 0.0, 1, "このままどんどん成長したら\nあなたはどうなっちゃうんだろ？\n…なんか、ずっと楽しみだね\nえへへ…お誕生日おめでと！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1063013, 106361, 0, 0.0, 1, "力と、想い…大切なこと\n少しだけ、分かった気がするよ\n私、ちょっとは成長できたかな？\nえへへ…あなたのおかげだね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1063014, 106361, 0, 0.0, 1, "ランドソルに来てよかったなぁ\nいろんな思い出が出来て、\nいろんな人と話せて…\n何より、あなたと出会えたから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1063015, 106361, 0, 0.0, 1, "森番の試練…大変だったな\nだけどおかげで、一歩ずつでも\n前に進めてる気がするんだ\n大人になるってこういう事かな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1063016, 106361, 0, 0.0, 1, "ロザリアみたいに、じゃない。\n私は私らしく、頑張るんだ\nこの想いも、いつか私らしく…\n…ひゃあ！？　き、聞いてた！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1063017, 106361, 0, 0.0, 1, "最初に会った時から、\nあなたはずっと優しいね\nそういうところが…す、す…\nす、素敵だと思うな！　うん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1064001, 106401, 0, 7.2, 2, "自分のなくした記憶もだけど…\nあなたが忘れた過去も気になるわ\n二人で一緒に\n見つけられたらいいわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1064002, 106401, 0, 6.6, 2, "アイスクリームって、どうして\nこうもおいしく感じるのかしら…\n『前の私』もひょっとしたら\nアイスが好きだったりしてね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1064003, 106401, 0, 4.7, 1, "そんなに真面目っぽい性格に、\n見えるかしら？\n冗談とか言うのは、確かに苦手ね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1064004, 106401, 0, 4.1, 5, "…ふとした弾みで、思い出すの\nコッコロさんに甘えてたこととか\nキャルのしっぽの味とか…\nもう…顔から火が出そうよ…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1064005, 106401, 0, 6.7, 1, "【美食殿】での生活は\n毎日が充実していて、楽しいわ\nこの時間が、少しでも長く続く\nように…精一杯努力するつもりよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1064006, 106431, 0, 0.0, 1, "必ず、みんなを守ってみせる！\nだって、みんなの隣が\n『今の私』の居場所だから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1064007, 106431, 0, 0.0, 1, "氷を滑る、この動き─\n身体が覚えてる？\nううん、今はそんなこと\n考えてる場合じゃない…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1064008, 106431, 0, 0.0, 1, "っくしゅっ…！\n氷の魔法は使えても、そこまで\n寒さに強いわけじゃないみたい\n後で、温かいスープが欲しいわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1064009, 106431, 0, 0.0, 1, "私を迎えてくれた\n【美食殿】のみんな、\nそれにあなたも…\n絶対に、守ってみせるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1064010, 106431, 0, 0.0, 1, "…不思議ね\nあなたから力をもらうと、\n胸が熱くなって…\nどこまでも跳べそうな気がするわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1064011, 106401, 0, 6.5, 4, "お誕生日、おめでとう\nケーキ、一緒に食べましょう\nでも…魔物とか昆虫とか\nケーキに入ってないわよね？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1064012, 106431, 0, 0.0, 1, "私、自分の誕生日が分からなくて\n…だから、お祝いとかできないの\nその分、あなたの誕生日を\nお祝いさせて貰っていいかしら？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1064013, 106461, 0, 0.0, 1, "願いと引き換えに\n手に入れたこの『力』…\n私の愛する人たち…\n家族やあなたは私が守るわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1064014, 106461, 0, 0.0, 1, "私は【美食殿】のシェフィ…\n今なら胸を張ってそう言える\nだからあなたのそばにいたいって\n気持ちも、もう隠したりしない", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1064015, 106461, 0, 0.0, 1, "記憶がなかった頃\nあなたは大丈夫だよって笑って\n迷子のような私を支えてくれた…\n今度は私があなたを助ける番よ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1064016, 106461, 0, 0.0, 1, "みんなと過ごす時間は、\n私を幸せな気持ちにしてくれた\nでも、あなたと二人で過ごす\nひと時も…キライじゃなかったわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1064017, 106461, 0, 0.0, 1, "過去を知った時は辛かった…\nでも、知らないままだったら\nなにも気づけなかったと思うの\nきっと、あなたへの想いにも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1065001, 106501, 0, 7.7, 2, "キミと一緒にいれば\nなんか面白いことが起きるだろ？\nだから期待させてもらってるよ\nさ、どっか遊びにいこうぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1065002, 106501, 0, 0.0, 3, "オレはこの世界に\nなにが起きたのか知りたい…\nそのためにもオレは\nもっと強くなる必要があるんだよ", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1065003, 106501, 0, 8.9, 1, "なあ、仕事の依頼はないのか？\nだからケンカだって。ケンカの\n仕事。オレがたぎるようなやつを\n持ってきてくれよな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1065004, 106501, 0, 7.6, 2, "そう心配すんなって\nそこらのヤツにやられるほど\nオレは弱くはないさ\nなんなら、試してみるか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1065005, 106501, 0, 5.8, 1, "ドラゴン族以外のやつと\nここまで仲良くなるなんて\n思わなかったぜ\nキミ、一体何者なんだ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1065006, 106531, 0, 0.0, 1, "喧嘩屋の本領、見せてやるよ！\nダチが一緒じゃ負けられねぇ！\n誰だろうとぶっ倒して、オレ様が\n最強だって証明してやるぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1065007, 106531, 0, 0.0, 1, "すげえぜ、この力！\n体の奥から\nどんどん溢れてきやがる！\nオレたちに壊せないものはねえ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1065008, 106531, 0, 0.0, 1, "これがドラゴン族の力…！\nオレ様が使いこなしてやるよ！\nダチが見てるんだ。情けねえ\nカッコは見せらんねぇよな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1065009, 106531, 0, 0.0, 1, "ダチと一緒に戦うのが\nこんなに楽しいなんてな…！\n知らないこの世界で知った\n最高にたぎる感情だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1065010, 106531, 0, 0.0, 1, "いいぜいいぜ、たぎってきた！\nキミが一緒なら、これっぽっちも\n負ける気がしねえ！\n派手に暴れてやろうぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1065011, 106501, 0, 4.7, 2, "よっ！　おめでとう！\n今日はキミの誕生日だろ？\nパーッとなんか食べに行こうぜ\n今日はオレがおごってやるよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1065012, 106531, 0, 0.0, 1, "誕生日おめでとう！\nダチにとって良い日ってのは\nこっちまでうれしくなるんだな\nこんな気持ち、初めてだ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1065013, 106561, 0, 0.0, 1, "くはははっ！　すげぇ…！\nコイツが本当の覚醒か！\n最高に気持ちいいぜ！\n力がたぎって止まらねぇ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1065014, 106561, 0, 0.0, 1, "制御しようなんて考えるな\nどうせ抑えられないんだ…\n…だったらもう、ただ欲して\n焼き尽くせばいいんだよな…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1065015, 106561, 0, 0.0, 1, "ああ、ちくしょう…なんでだ…！\n喉が渇いて仕方ねぇ…！\n早く全部ぶっ壊さないと\nオレ様がおかしくなっちまう…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1065016, 106561, 0, 0.0, 1, "頼むから止めないでくれよ？\nもしそんな真似しやがったら\nオレ様はキミを…！\n…違う…オレは…アタシは…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1065017, 106561, 0, 0.0, 1, "近づくんじゃねぇ！　今はダメだ\n炎でキミまで傷つけちまう…！\nやめろって！　おい！　ちょっ！\n分かったから抱き締めんなって！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1066001, 106601, 0, 4.2, 3, "ぴゃっ！　って、お兄さんですか\n何じろじろ見てるです、えっち！\n煩悩まみれ！　不審者だったら\n叩き潰してるところですよ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1066002, 106601, 0, 5.4, 4, "あたしがサボってここにいるのは\nボスには内緒にするですよ？\nもしあのババアに見つかったら\n何されるか分からないです…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1066003, 106601, 0, 6.9, 1, "は～…ほんとうに\nあたしたち以外にも\nドラゴン族っているんですかね？\n…そこらへんに歩いてないです？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1066004, 106601, 0, 5.0, 6, "今日の仕事はここまでです！\n縄張りを見回ってたですよ！\nべ、別にサボってブラブラしてた\nわけじゃないですからね？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1066005, 106601, 0, 6.5, 3, "あ～あ、どこかにぽろっと\n大金が落ちてないですかね？\n早く自由の身になって\nボスを見返してやりたいですよ…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1066006, 106631, 0, 0.0, 1, "どうですかお兄さん！\nこれがあたしの…\nドラゴン族の力ですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1066007, 106631, 0, 0.0, 1, "ふっふっふ…心技体ともに\n覚醒した今のあたしには\n死角はないです…ってぴゃあ！\nあ、足場が崩れたですよぉ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1066008, 106631, 0, 0.0, 1, "力がわきあがってくるです！\n知性派のあたしですけど\nたまには本能のおもむくまま\nってのも、悪くないです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1066009, 106631, 0, 0.0, 1, "この力があれば…\nお兄さんがどれだけ\nへっぽこナイトでも\n助けてあげられるですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1066010, 106631, 0, 0.0, 1, "もう守ってもらうばかりの\nあたしじゃないですよ！\nこの力でなりあがってやるです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1066011, 106601, 0, 6.3, 2, "え？　なんです？　今日は\nお兄さんの誕生日ですか？\nじゃあ特別に、あたしに\n何かおごる権利をやるですよ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1066012, 106631, 0, 0.0, 1, "お誕生日おめでとうです！\nこのあたしに祝われるなんて\nそうそうない名誉ですからね！\n感謝して何かおごりやがれです！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1066013, 106661, 0, 0.0, 1, "力が溢れてくるです！\nこれが完全な覚醒…\nこうなったあたしは、\n誰にも止められねーですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1066014, 106661, 0, 0.0, 1, "どうですか！　これこそが\nあたしの真のパワーですよ！\nこの姿、その目にしっかりと\n焼き付けるがいいです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1066015, 106661, 0, 0.0, 1, "あたし一人だけだったら、きっと\n手に入れられなかった力です…\nお兄さんだけには、特別に\n手を貸してやるですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1066016, 106661, 0, 0.0, 1, "あたしが覚醒するには\nお兄さんが必要ですからね\nふふーん。頼まれたって\n離れてやんねーですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1066017, 106661, 0, 0.0, 1, "そんな顔してどうしたです？\n覚醒してもあたしはあたしです\nどっかいったりしねーですから\n安心しやがれです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1067001, 106701, 0, 9.3, 2, "揉め事に巻き込まれたら\n私の名前、出しちゃっていいよ\n…もっと悪いことが起きる？\nだって、そのほうが楽しいし♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1067002, 106701, 0, 6.6, 1, "ギャングの心得その一はね…\nファミリーに入れる気はないよ～\n知ってるだけでいいから♪\n…何かあった時のために、ね？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1067003, 106701, 0, 7.0, 2, "カヤちゃんとイノリちゃんと\nこれからも仲良くしてあげてね\n…私？　…君とはずっ～と前から\n仲良しだし、大丈夫♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1067004, 106701, 0, 6.5, 2, "君の頑張りが世界を\nより楽しく変えてくれるなら\nなんだって協力するよ\n遠慮せずに頼ってきてね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1067005, 106701, 0, 7.8, 5, "君がどんな色に変わっていくのか\n私はそれを最後まで見ていたいの\n…本当は私の選んだ色を\n使ってほしいけど、まだ早いかな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1067006, 106731, 0, 0.0, 1, "ふふっ…あははっ♪\n戦うのって、本っ当に面白い…\n命を、魂を懸けた勝負…\nとってもワクワクするよね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1067007, 106731, 0, 0.0, 1, "私の目に狙われてる意味も\n理解できない相手なんて\n敵としての存在価値もないし…\nすぐに消してあげないと♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1067008, 106731, 0, 0.0, 1, "私たちの生きていく未来は\n誰かに決められるものじゃない…\nものすご～く大変だったとしても\nみんなで一緒に奪い返そうね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1067009, 106731, 0, 0.0, 1, "ねえ…君の目に、今の私は\nどんな風に見えてるのかな？\n怖い？　かっこいい？\n私はね…心の底から楽しいよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1067010, 106731, 0, 0.0, 1, "ねえ、君を苦しませるものを\n世界ごと焼き尽くしちゃおうか…\n実はそれだけのリスクがあるって\nじっくりと教えてあげたいし…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1067011, 106701, 0, 0.0, 2, "誕生日おめでとう～☆\nプレゼントは、一日私を\n好きにできちゃう券で～す\nいつ使いたい？　今でもいいよ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1067012, 106731, 0, 0.0, 1, "ハッピーバースデー☆\n君もドラゴン族になれる\nアイテムを作ってきました～☆\nドラゴン族…なってみたくない？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1068001, 106801, 0, 8.1, 1, "やあ少年。元気にしてる？\nたまには店に寄っていってよ\n美味しいクレープ作るからさ\n味は普通？　…そうかな？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1068002, 106801, 0, 8.5, 6, "おや、今日は人助けはお休み？\n一人きりでのんびりしたいなら\n迷宮を作ってあげようか\nあれ？　なんで逃げるの？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1068003, 106801, 0, 8.5, 6, "実はちょっと困ってるんだよね…\n予約してた新作ゲームが\nこっちの世界だと遊べない！\nこれは由々しき問題だよ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1068004, 106801, 0, 7.7, 2, "少年と一緒にいると\n世界も自分も、いい方向に\n向かう予感がするんだ\nキミはずっと、そのままでいてね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1068005, 106801, 0, 8.4, 2, "…記憶がなくても、キミの本質は\n変わらない。それを知った時は\n本当にうれしかったよ\nこれからもよろしくね、少年", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1068006, 106831, 0, 0.0, 1, "迷宮女王とプリンセスナイトが\n力を合わせれば、どんな敵が\n相手でも勝てるさ\nさあ少年、一緒に迎え撃とう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1068007, 106831, 0, 0.0, 1, "ああ…やっとキミのそばで戦える\nうれしいよ、少年。七冠として、\n一人の人間としてキミを\n守り抜くと、今ここに誓うよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1068008, 106831, 0, 0.0, 1, "おいで、憐れな挑戦者たち\nアタシの迷宮から、果たして\n生きて帰れるかな…？\nその足掻き、見せてもらうよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1068009, 106831, 0, 0.0, 1, "近頃少年を近くに感じていると\n力が溢れて止まらないんだ\nこれってもしかして…恋？\n…いや、愛だ！　だよね、少年！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1068010, 106831, 0, 0.0, 1, "アタシは人間が大好きだ\nでもそれ以上に、少年が好きだよ\n…どうかな、ドキッとした？\n真剣だよ？　それがどうかした？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1068011, 106801, 0, 0.0, 2, "少年、誕生日おめでとう～！\n今日くらいはクレープじゃなくて\nケーキをプレゼントしちゃおう！\nでもクレープもあげちゃう！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1068012, 106831, 0, 0.0, 1, "少年、誕生日おめでとう\nキミがこの世に生を受けた日は\n盛大に祝うべきだ。今から一緒に\nプレゼントを選びに行こう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1069001, 106901, 0, 7.0, 1, "あなた、私と一緒に戦うなんて\nおかしな未来を選んだのね\n後悔しても、もう遅いわよ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1069002, 106901, 0, 0.0, 1, "剣の練習に付き合ってあげるわ\n一刻も早く成長して\n私にそれを突き立てなさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1069003, 106901, 0, 5.9, 4, "私に勉強を教わりたい？\n何を言うかと思えば…そもそも\n私の言葉を正しく理解できて？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1069004, 106901, 0, 3.1, 3, "食事を持ってきた…？\nなによこれ…魔物じゃないの\n見た目のなってない料理は嫌いよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1069005, 106901, 0, 4.5, 6, "今日もくだらない一日を\n過ごしてきたみたいね…\nどうして満足気なのかしら？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1069006, 106931, 0, 0.0, 1, "私を否定する世界はいらないの\n全て作り変えるわ、全てね\nだから何もかも終わらせるの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1069007, 106931, 0, 0.0, 1, "私に全てを奪われたあなたには\n私の全てを奪う資格があるわ\n互いの全てをかけて戦いましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1069008, 106931, 0, 0.0, 1, "仲間に頼らないと何もできない…\nそんなあなたには負けられないの\n私の強さを証明させてもらうわよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1069009, 106931, 0, 0.0, 1, "守る価値のない未来のために\nどうして必死になれるのかしら\n本当にあなたは…理解できないわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1069010, 106931, 0, 0.0, 1, "私を倒しても、その先には\n今以上の苦しみがあるのに…\n理解してもなおあなたは進むのね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1069011, 106901, 0, 3.9, 1, "誕生日？　どうでもいいじゃない\nいつ世界に生まれたかじゃなくて\n何を成すかが大事なのだから", 3, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1069012, 106931, 0, 0.0, 1, "あなた、今日が誕生日らしいけど\nいつまで祝えるかしらね…？\n来年まで生き延びてたら\nその時は祝福してあげるわ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1070001, 107001, 0, 5.8, 2, "敵の敵は味方…\n今回は貴方に協力しましょう\nよろしく頼みます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1070002, 107001, 0, 5.6, 1, "プリンセスナイトとしてではなく\n貴方だけが持つ可能性\nそれに賭けさせてもらいます", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1070003, 107001, 0, 2.0, 2, "今の私？　ええ、私本人ですよ\n今の貴方に自分を偽っても\n意味がありませんから", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1070004, 107001, 0, 5.3, 6, "貴方といると、犬を\nもう一匹飼い始めた気分です\n飼ったこと？　ありませんよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1070005, 107001, 0, 5.3, 2, "貴方の好みのタイプを\n教えなさい。再現してあげます\n妹系？　姉系？　幼馴染？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1070006, 107031, 0, 0.0, 1, "自分の目に映った私が\n本当の私だと思いますか…？\n私は変貌大妃\n千変万化の女ですよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1070007, 107031, 0, 0.0, 1, "私の『冠』が偽物ではないと\nその身に刻み込んであげます\nもっとも全ての敵は私に模倣され\n消えて無くなりますが", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1070008, 107031, 0, 0.0, 1, "鏡よ鏡…世界で最も\n賢く強いのは誰ですか？\n…ええその通り。当然私です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1070009, 107031, 0, 0.0, 1, "私に仕えたくなりましたか？\n私の中にデータさえあれば\n本人は不要なのです\n…彼以外は", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1070010, 107031, 0, 0.0, 1, "こそこそ隠れるのは終わりです\nこれから先は打って出ます\n貴方とその仲間たち…\n戦力として当てにしていますよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1070011, 107001, 0, 5.8, 6, "今日が誕生日ですか\nおめでとうございます\n…これ以上何か必要ですか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1070012, 107031, 0, 0.0, 1, "誕生日おめでとうございます\n贈り物は何にしましょう…\n計算問題集ではどうですか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1071001, 107101, 0, 5.8, 2, "ややこしい話し合いは面倒だ！\n物事はシンプルに解決しよう\nそう、生きるか死ぬかの\n本気の殺り合いでな☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1071002, 107101, 0, 2.5, 1, "息苦しい世の中だ\n自分の欲求に従って\n好き勝手に生きた者の勝ちだとは\n思わんか？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1071003, 107101, 0, 3.1, 4, "強すぎるというのも考えものだ\n歯ごたえのない連中ばかりで\n命の遣り取りをする愉しい戦いに\nそうそう出会えないからな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1071004, 107101, 0, 8.5, 2, "ワタシの前に散っていった\n男たちは、この胸を触ったオマエを\n心底憎く思うだろうなあ\n安心しろ、全員墓の中だ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1071005, 107101, 0, 6.8, 5, "オマエが幸せな結婚生活を\n送れるように、ワタシだって\n努力に手を抜かない\n意外と可愛いところもあるだろ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1071006, 107131, 0, 0.0, 1, "イかれてなどいないさ\n人間の命は儚く脆い…\nその刹那の時もワタシは\n楽しみ切ってやるのさ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1071007, 107131, 0, 0.0, 1, "戦場を満たす血だまりの上で\n一緒に陽気なステップを\n踏もうじゃないか\nさあ、お手をどうぞ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1071008, 107131, 0, 0.0, 1, "誓約で仕方なく…ではないぞ？\n少なくともワタシは納得している\nこのワタシに触れた男だ\n結婚相手として認めているよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1071009, 107131, 0, 0.0, 1, "この胸の高ぶるような\n愉しい出来事はないか？\nないならばワタシが\n火種をつけてやろう☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1071010, 107131, 0, 0.0, 1, "ワタシの幼女時代の写真が\nもっと見たいだと？　もしや\n第二次性徴期前の幼子にしか\n興奮しない性癖だったのか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1071011, 107101, 0, 9.1, 2, "オマエ、今日が誕生日らしいな？\nほら、一つ歳をとった哀れな子羊に\nプレゼントだ\n今年もワタシを楽しませてくれよ？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1071012, 107131, 0, 0.0, 1, "誕生日おめでとう♪\nオマエの記念すべき日に\n立ち会えてうれしいよ！\n記念の誓約は…まだ早いかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1071013, 107161, 0, 0.0, 1, "気をつけろ坊や\nこの「結婚」という伏魔殿…\nあらゆる所に落とし穴がある\nどんな牙城よりも手ごわいぞ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1071014, 107161, 0, 0.0, 1, "一人で千万の敵に挑むよりも\n二人で普通に生きることの方が\n難しいとはな…\nふふ、結婚…実に面白い！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1071015, 107161, 0, 0.0, 1, "このワタシが共同作業とはな…\nしかし、共に乗り越えた先でしか\n見られない景色があるらしい\n坊や…見に行くぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1071016, 107161, 0, 0.0, 1, "ワタシと共に生きるのは\n想像以上にハードだろう？\nだがまだこんなものじゃあないぞ\n坊や─死ぬなよっ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1071017, 107161, 0, 0.0, 1, "上り坂下り坂まさかの連続とは…\nふふっ、結婚もとんだ戦場だな\n何があっても手を離すなよ？\n病める時も健やかなる時もな☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1075001, 107501, 0, 2.2, 2, "水着買っちゃいました～！\nどうですどうです？\n似合ってますか～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1075002, 107501, 0, 0.0, 1, "この水着、結構動きやすいので\n素潜りとかして\n海の幸をゲットできそうです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1075003, 107501, 0, 0.0, 2, "マリンスポーツしましょう！\nビーチボールとか、\nビーチフラッグとか！\n負けませんよ～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1075004, 107501, 0, 4.3, 2, "私の水着姿で、\nあなたを誘惑しちゃいます♪\nなんちゃってー！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1075005, 107501, 0, 5.0, 2, "せっかくの水着ですし、\nたくさん泳いで、\nたくさんはしゃいで…\n思い出いっぱい作りましょうね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1075006, 107531, 0, 0.0, 1, "わぁっ、やりましたね～！\n塩水しょっぱいぃ～\nこうなったらわたしも本気です！\n覚悟してくださいよぉ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1075007, 107531, 0, 0.0, 1, "青い海、白い砂浜、輝く太陽！\nそしておいしいお魚！\nここは天国ですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1075008, 107531, 0, 0.0, 1, "水が気持ちいいですね～\nこんなに充実した夏、\n生まれて初めてです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1075009, 107531, 0, 0.0, 1, "えへへ♪\nあなたと海で遊ぶなんて、\nとっても新鮮な感じです！\n青春ですね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1075010, 107531, 0, 0.0, 1, "はしゃぎすぎて水着が\n流されちゃったりしたら、\nあなたに抱きついて\n隠しますね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1075011, 107501, 0, 2.6, 5, "お誕生日おめでとうございます！\nお魚、貝、タコ、エビ、カニ…\n海の幸満載のバースデー\nパーティーにしましょうね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1075012, 107531, 0, 0.0, 1, "水着で過ごす、あなたの誕生日！\n開放的でわくわくしちゃいます！\nさぁ、今日は二人で\nおもいっきりはしゃぎましょう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1075013, 107561, 0, 0.0, 1, "夏の海に二人きり…\nなんだかドキドキしちゃいます！\nせっかくなので、雰囲気を出して\nダーリンって呼んでいいですか☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1075014, 107561, 0, 0.0, 1, "不思議ですね…\n毎日見ているはずなのに\nあなたと眺める夕日は、なんだか\nロマンチックな感じがします♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1075015, 107561, 0, 0.0, 1, "周りはカップルだらけ…\n郷に入っては郷に従え！\nということで、その…\n二人で手を繋いでみませんか♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1075016, 107561, 0, 0.0, 1, "海、楽しかったですね！\nなので、今日が終わることが\nすごくさみしいです。あの…\nまたあなたを誘っていいですか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1075017, 107561, 0, 0.0, 1, "あなたと過ごした夏は\nわたしにとってキラキラした\n青春で、大切な宝物です！\n…ずっと、忘れませんから☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1076001, 107601, 0, 4.8, 5, "救護院の皆さまに、水着を\n選んでいただきました…\n似合っていますでしょうか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1076002, 107601, 0, 0.0, 1, "主さま、よろしければ\n一緒に泳ぎの\n練習をしましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1076003, 107601, 0, 0.0, 2, "主さま。わたくし、\n砂のお城というものを\n作ってみたいのですが、\nお手伝いいただけますでしょうか", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1076004, 107601, 0, 0.0, 5, "このような\n過度な肌の露出は、\nやはり恥ずかしいです…\n主さまにしかお見せできません", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1076005, 107601, 0, 4.2, 2, "日焼け止めを塗っていただいても\nよいでしょうか？\n主さまにでしたら、\n肌を許すことができます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1076006, 107631, 0, 0.0, 1, "主さま。どんな場所でも、\nわたくしがついております\nこの手を離さないでくださいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1076007, 107631, 0, 0.0, 1, "見てください、主さま\nおさかながこんなに近くに…\nとても神秘的でございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1076008, 107631, 0, 0.0, 1, "このまま流れに身を任せ、\n二人でどこまでも進む\n…それも悪くありませんね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1076009, 107631, 0, 0.0, 1, "これからもずっと、\n主さまと同じ景色を見て、\n主さまと同じ感動を抱き、\nともに歩んでいきたいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1076010, 107631, 0, 0.0, 1, "この広い海の中で、\n主さまと二人っきり…\nいまだけは、主さまを\n独り占めです♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1076011, 107601, 0, 0.0, 2, "海で、主さまのお誕生日を\nお祝いするなどと、故郷にいた\n時には考えもしませんでした\nわたくし、幸せでございます", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1076012, 107631, 0, 0.0, 1, "空も海も輝いて…この世の\n全てが、主さまのお誕生日を\n祝福しているようです\nわたくしも心よりお祝いします♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1076013, 107661, 0, 0.0, 1, "山奥の里で育ったわたくしですが\nもう井の中の蛙ではございません\n主さまのおかげで、このように\n大海を知ることができました♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1076014, 107661, 0, 0.0, 1, "主さま、ご覧くださいまし…\nわたくしたちの冒険の行く先を\n星々の光ように、お魚さんたちが\n照らしておりますよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1076015, 107661, 0, 0.0, 1, "主さまと過ごす夏は\nなんと刺激的なのでしょう…\nこれからも、どうか夏のガイドを\n務めさせてくださいまし！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1076016, 107661, 0, 0.0, 1, "主さまのお手手を引いて\nこれからもまだ見ぬ神秘の地へと\nガイドを続けてまいります\nどうぞお付き合いくださいまし♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1076017, 107661, 0, 0.0, 1, "『星々の海』…この素晴らしい\n光景を、主さまとともに\n見て、感じて、味わうことができ\nたいへんうれしく思います♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1077001, 107701, 0, 2.8, 5, "働くのって楽しいですね！\nあなたと一緒だと、もっと\n楽しく感じちゃいます♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1077002, 107701, 0, 2.9, 2, "休憩時間に海へ\n行きたいんですか？\nもちろん、いいですよ～\n一緒に泳ぎましょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1077003, 107701, 0, 6.2, 2, "私ドジだから、砂のお城って\n完成させたことないんです～\n…一緒に作ってくれるんですか？\nありがとうございます♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1077004, 107701, 0, 4.2, 2, "水着姿を褒めて下さるのは\n嬉しいですが…あ、あまり\n見つめられると恥ずかしいです～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1077005, 107701, 0, 4.7, 5, "御用はありますか？\nもしもなくても…\nお、おそばにいても\nいいですか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1077006, 107731, 0, 0.0, 1, "まさか足元に花火があるなんて～！\n子供たちは危険ですから、\n離れてください！\nひゃう！　あつい～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1077007, 107731, 0, 0.0, 1, "だ、大惨事です～！\nきれいとか言ってる場合じゃ\nありませんから～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1077008, 107731, 0, 0.0, 1, "みんなで花火をするのって\nとっても楽しいですね\n今度は両手持ちで\n行きますよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1077009, 107731, 0, 0.0, 1, "二人で花火をしてると\n恋人同士に見えるんでしょうか？\nなんて…えへへ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1077010, 107731, 0, 0.0, 1, "あ、あなたにも、お仕えさせて\nもらえませんか？\nや、やっぱり、今の言葉は\n忘れてください！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1077011, 107701, 0, 8.6, 6, "お誕生日おめでとうございます♪\nクリームとおっきな苺を乗せた\nかき氷ケーキを召し上がれっ\nあわわ、溶けかけてる～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1077012, 107731, 0, 0.0, 1, "お誕生日おめでとうございます♪\n今日は特別に、あなただけに\nお仕えさせてください\nえへへ、お嬢さまには内緒ですよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1078001, 107801, 0, 5.8, 5, "水着が似合ってるって？\nあんたに褒められてもねぇ～\nで、でも…ありがと", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1078002, 107801, 0, 2.0, 2, "この日を待ってたわ！\n思いっきり遊ぶから、\n覚悟しなさいよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1078003, 107801, 0, 4.3, 2, "この浮輪、かわいいでしょ\n乗り心地もいいんだから\nどうしてもって言うなら、\n乗せてあげるけど？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1078004, 107801, 0, 5.6, 5, "あんたといると退屈しないわ\n…どういうことかって？\nそれは、えっと…\n一緒にいると楽しいってこと！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1078005, 107801, 0, 3.7, 2, "たまには二人っきりで\n出かけてあげてもいいわよ\n言っておくけど、\nあんたに拒否権はないから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1078006, 107831, 0, 0.0, 1, "ね、ねえ、浮輪に穴あいてない？\n空いてるわよね！？　っていうか\n魔物もいるわよね！？\nぎゃー！　こっち来んなぁ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1078007, 107831, 0, 0.0, 1, "何よ、物欲しそうな顔して\nこのジュースが飲みたいの？\nしょうがないわね、\n一口だけよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1078008, 107831, 0, 0.0, 1, "汗をかいた後の飲み物って\n最高よね\nはぁー、おいし♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1078009, 107831, 0, 0.0, 1, "疲れてるみたいね\n一緒に浮輪に乗って、\nゆっくりする？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1078010, 107831, 0, 0.0, 1, "なんか、幸せーって感じ\nこれもあんたのおかげ、かな？\n…その、あ、ありが…\nな、なんでもないわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1078011, 107801, 0, 5.9, 1, "誕生日、おめでと！\nこんな日くらい、パーッと\n羽目を外して楽しみましょ☆\nさ～海、海！　遊ぶわよー！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1078012, 107831, 0, 0.0, 1, "海の上で誕生日パーティーって\nいうのも、オシャレでしょ？\nさ、アンタの誕生日に乾杯よ！\nハッピーバースデー♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1078013, 107861, 0, 0.0, 1, "こんな楽園みたいな所に\n来れるなんて夢みたいだわ！\nあーもう最っ高！\nさあ、楽しみつくすわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1078014, 107861, 0, 0.0, 1, "せっかくの穏やかな時間なのよ？\nあくせくしないで波に身を任せて\nのんびり過ごすの。こういうのも\nたまにはいいでしょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1078015, 107861, 0, 0.0, 1, "キレイな海もまばゆい太陽も\n別に初めてじゃないのに\n何倍も心地よく感じるわ\nこれもあんたのおかげかしら？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1078016, 107861, 0, 0.0, 1, "なに、あんたもこの浮き輪\n使いたいの？　仕方ないわね\nこっち来なさ…ちょ、近い！\n顔が近いってもう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1078017, 107861, 0, 0.0, 1, "このままあんたとどこかに\n流れていっちゃうのも悪くな…\nや、やっぱ今のなし！\n忘れろばかぁ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1079001, 107901, 0, 0.0, 1, "どうしたにゃ？\nあたしの水着姿に\nドキドキしたかにゃ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1079002, 107901, 0, 4.3, 2, "何してるのって、\n見てのとおり屋台の準備にゃ！\nいっぱい稼ぐにゃ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1079003, 107901, 0, 6.2, 2, "手伝ってくれるのかにゃ？\nありがとにゃ！\nお礼はあたしで…\nなんて冗談にゃ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1079004, 107901, 0, 3.4, 6, "水は嫌いだから\n砂浜で遊ぶにゃ～！\nにゃ！？　波が来るにゃ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1079005, 107901, 0, 4.1, 4, "海には美味しいものが\nいっぱいあるから嬉しいにゃ！\nうぅ…でも、水は嫌いにゃ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1079006, 107931, 0, 0.0, 1, "焼きそば、かき氷、たい焼き…\n海の家には美味しいものが\nたくさんあるにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1079007, 107931, 0, 0.0, 1, "よってらっしゃい\n見てらっしゃいにゃ！\nここにしかないスイーツが\n看板メニューにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1079008, 107931, 0, 0.0, 1, "このスイーツを海で食べなきゃ\n夏は始まらないにゃ！\n誰も知らない\n未知の味を約束するにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1079009, 107931, 0, 0.0, 1, "一口食べるにゃ？\nほら、あーんするにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1079010, 107931, 0, 0.0, 1, "今年の夏もキミにはいつも\n助けてもらって感謝してるにゃ\nこれからもよろしくにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1079011, 107901, 0, 2.3, 1, "今日はキミの誕生日にゃ！\nキャットコインを献上してくれたら\nデートくらいは\n考えてやらなくもないにゃ♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1079012, 107931, 0, 0.0, 1, "ハッピーバースデー！\n今日は特別にキミのお願い事を\n聞いてあげるにゃ！　たとえば、\n無人島で二人っきり…なんてにゃ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1080001, 108001, 0, 9.2, 2, "有能な弟子がいてくれて助かるわ\nえ？　お給料がほしい？\nそうねぇ…あぁ、あのさ\nこの水着姿がお代じゃダメ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1080002, 108001, 0, 3.2, 2, "忘れ物はない？\n装備は万全？\nさぁ、冒険に出発するわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1080003, 108001, 0, 2.3, 2, "どうして水着なのかって？\n夏の海で過ごすのに\nとっても効率的だからよ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1080004, 108001, 0, 3.2, 5, "せっかくこんな格好を\nしてるんだもの…\nキミにも見てもらわないと\nもったいないわよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1080005, 108001, 0, 3.6, 2, "海のバイトって割がいいから\n助かっちゃうわ！\nキミと一緒にいられるのも\nポイント高いわねっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1080006, 108031, 0, 0.0, 1, "キミの前だと\nなんだか落ち着くわ…\nあ、でもジロジロ見るのは\nダメよ？　恥ずかしいもの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1080007, 108031, 0, 0.0, 1, "海ってのんびりできていいわね\n戦いかたを忘れちゃいそう…\nふふ、なーんてね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1080008, 108031, 0, 0.0, 1, "こういう海でのすごし方って\n経験がなかったけど…\n楽しいものね\nキミといるからかしら？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1080009, 108031, 0, 0.0, 1, "バイトばっかりな私だけど\n最近はキミとの時間も\n大事にしたいなって\n思ってるんだからね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1080010, 108031, 0, 0.0, 1, "師匠と弟子の関係だったのに\n気がついたらキミに\n頼りっぱなしになってたわね…\nふふ、恥ずかしいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1080011, 108001, 0, 4.6, 2, "今日がキミの誕生日だったのね\nだったらお祝いをしないと！\nさあ、海でパーティーの食材を\nゲットしてくるわよ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1080012, 108031, 0, 0.0, 1, "誕生日おめでとう\n今日は二人で海を見ながら\n散歩したり…たまには非効率的に\n過ごしてみない？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1081001, 108101, 0, 3.9, 5, "は、ハッピーハロウィン…\nうう、恥ずかしいな…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1081002, 108101, 0, 6.7, 1, "トリックオアトリート…\nお菓子をくれないと呪いを…\nではなくて、いたずらをします", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1081003, 108101, 0, 4.8, 4, "ハロウィンの時期は\nあなたの周りも賑やかなので…\n肩が重く感じませんか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1081004, 108101, 0, 6.3, 2, "この格好、ハロウィンが好きな人\nみたいで恥ずかしいですけど…\n気に入ってもらえたら\n嬉しいです", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1081005, 108101, 0, 6.9, 5, "霊のみなさん、楽しそうです…\nハロウィンですからね\nわ、私も…楽しいです\nあ、あなたと、いられるので…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1081006, 108131, 0, 0.0, 1, "ハロウィンのダンスパーティーが\n始まりますよ\nあなたも来てください…\n素敵な時間が待っていますから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1081007, 108131, 0, 0.0, 1, "ハロウィンパーティーに、大勢の\n霊のみなさんが来てくれました…\n霊感の高まる時期ですから、\nあなたにも見えるでしょうか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1081008, 108131, 0, 0.0, 1, "お父さん\n素敵なハロウィンなんだから\n女の子へのセクハラで\n台無しにしちゃダメだよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1081009, 108131, 0, 0.0, 1, "恥ずかしいですけど…\n一緒に踊ってもらえますか？\n霊のみなさんと\n踊り明かしたいんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1081010, 108131, 0, 0.0, 1, "こんな幸せなハロウィンは\n初めてなので…\nつい、はしゃいでしまいます\n…あなたのおかげです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1081011, 108101, 0, 5.3, 6, "父もあなたのお誕生日を\n祝福しているみたいですね\nあ…お父さん！　\nこの人を噛んじゃダメ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1081012, 108131, 0, 0.0, 1, "ハッピーハロウィン＆\nハッピーバースデー♪\n今日はお菓子ではなく、\nケーキをお渡ししますね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1082001, 108201, 0, 6.3, 1, "いまのミヤコは、血も涙もない\n獰猛な狼女なの！\n食べられたくなければ、\nプリンをよこすの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1082002, 108201, 0, 8.4, 2, "カボチャプリン？\nそんなの邪道なの、認めないの！\n…ぱくっ…お、美味しいの～♪\nさっきの言葉は取り消すの～", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1082003, 108201, 0, 7.2, 6, "子どもたちの仮装にまじって、\nお菓子をもらいに来てる\n幽霊がたくさんいるの～\nほら、オマエの後ろにも！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1082004, 108201, 0, 2.4, 1, "トリックオアプリンなの～\nプリンをくれなきゃ、\n死ぬまで取り憑くの～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1082005, 108201, 0, 5.1, 1, "ミヤコのハロウィンパーティーに\nオマエも招待してやるの！\n参加費はプリン10個で\n手を打ってやるの～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1082006, 108231, 0, 0.0, 1, "幽霊大集合なの～\nみんなでハロウィンを\n盛り上げるの！\nパーティーのはじまりなの～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1082007, 108231, 0, 0.0, 1, "生きてても死んでても、\nハロウィンはみんなが楽しい\n最高のイベントなの！\nミヤコ、ハロウィン大好きなの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1082008, 108231, 0, 0.0, 1, "トリックオアプリーン！\nプリンがあるところに\nミヤコありなの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1082009, 108231, 0, 0.0, 1, "オマエと一緒にハロウィンが\nできて、とっても嬉しいの！\nプリンが食べられてオマエもいて、\nミヤコの幽霊人生はバラ色なの～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1082010, 108231, 0, 0.0, 1, "今夜のミヤコは、きっと\n生きていた時より輝いてるの！\nオマエとのハロウィン、\nミヤコの大切な思い出にするの～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1082011, 108201, 0, 5.2, 2, "ひゅ～どろろろ～\nお誕生日～\nおめでとうなの～～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1082012, 108231, 0, 0.0, 1, "オマエの誕生日パーティーに、\nミヤコの友達の幽霊さんを\nたくさんつれてきたの～！\nどろろろ～おめでとうなの～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1083001, 108301, 0, 8.5, 5, "うふん♪　この仮装、素敵よね\n大人の色気が最高潮って感じ！\nメロメロにしちゃうわよ～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1083002, 108301, 0, 9.8, 2, "はぁ…困ったわ…\nこんなセクシーな仮装してたら、\nモテモテになっちゃうじゃない…\nアタシって罪な女…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1083003, 108301, 0, 5.6, 3, "えー！　お菓子くれるの！？\nやったやったー！\n…って、喜んでないわよ！\n子ども扱いしないでよね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1083004, 108301, 0, 6.0, 5, "アタシと一緒にハロウィンを\n過ごせるなんて光栄なことよ？\nほら、お菓子をもらいに行くから\nエスコートしなさい！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1083005, 108301, 0, 6.0, 5, "いまのアタシなら、\n箒で空も飛べちゃうかも～♪\nそのときは、アンタもちゃんと\n乗せてあげるわね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1083006, 108331, 0, 0.0, 1, "トリックオアトリートー♪\n今日のパーティーの主役は、\nセクシー魔女のミサキちゃんよ！\nさぁ、お菓子をよこしなさーい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1083007, 108331, 0, 0.0, 1, "おほほほほ♪　どう！？\nこのエレガントで優雅な仮装！\nまさに魔性の女って感じよね～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1083008, 108331, 0, 0.0, 1, "アタシの溢れ出る大人の魅力に、\nみんな見とれちゃってるのね～\n仕方ないわ…だってアタシ、\nいま最高に輝いてるし！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1083009, 108331, 0, 0.0, 1, "今日はアンタをパーティーに\n招待してあげるわ！\n会場まで、ちゃーんと\nエスコートしてよね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1083010, 108331, 0, 0.0, 1, "今年のハロウィンは最高ね！\nパーティーにお菓子に仮装！\nそれになにより…\nアンタがいるんだもん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1083011, 108301, 0, 11.2, 2, "ハッピーバースデー＆\nハッピーハロウィン♪\nというわけで、アンタへの\nプレゼントは…イタズラよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1083012, 108331, 0, 0.0, 1, "お誕生日おめでと！\nプレゼントは何がいい？\n…え？　\nこのアイちゃんが欲しいの！？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1084001, 108401, 0, 1.8, 1, "メリークリスマス！\nどうかあなたの周りが\n幸せな歌で包まれますように…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1084002, 108401, 0, 2.9, 2, "あなたと一緒に過ごす\nクリスマス…\nとても楽しみです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1084003, 108401, 0, 5.3, 5, "唱喚士として、もっともっと\n頼られる存在になりたい…\nどうか、そばで見ていてください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1084004, 108401, 0, 5.7, 2, "この服、結構目立つので\n恥ずかしいですけど…\nあなたに見てもらえるのなら\n好きになれそうです", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1084005, 108401, 0, 5.7, 5, "あなたがそばにいてくれるから\n私は歌に集中できるんです…\nそんな気持ちも、いつか歌にして\nあなたに届けたいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1084006, 108431, 0, 0.0, 1, "メリークリスマス！\nありったけの気持ちを込めて\n歌いますから…聴いてくださいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1084007, 108431, 0, 0.0, 1, "クリスマスを\n幸せな歌で彩ることができたら…\n唱喚士としてこの上ない幸せです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1084008, 108431, 0, 0.0, 1, "平和を守るため…そして\n人々にささやかな幸せを\n届けるために、私は歌い続けます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1084009, 108431, 0, 0.0, 1, "私の歌声の可能性…\n今は信じています\nだって、あなたが\n信じてくれているんですから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1084010, 108431, 0, 0.0, 1, "どんな些細な感情でも\nあなたと分かち合いたいんです\nもう、あなたがいてくれないと\nダメみたいですね、私…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1084011, 108401, 0, 4.1, 5, "今日は誕生日ですよね、\nおめでとうございます\nプレゼントの他に、私の\n歌も贈らせてください", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1084012, 108431, 0, 0.0, 1, "誕生日、おめでとうございます\n子どもたちや精霊も一緒に\nお祝いしてくれるそうです\n素敵な一日にしましょうね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1085001, 108501, 0, 7.6, 2, "【サレンディア救護院】のみんな\nだけじゃなく、お兄ちゃんとも\nクリスマスを過ごせるなんて…\n今年は最高のクリスマスです…♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1085002, 108501, 0, 4.8, 2, "この服なら、サンタさんの役\nだってできちゃうかも…♪\nえへへ…子供たちに\nプレゼントを配っちゃいますよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1085003, 108501, 0, 0.0, 2, "サンタさんをイメージした\n新しいお洋服です…♪\nスズメお姉ちゃんに\n作ってもらったんです…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1085004, 108501, 0, 4.1, 2, "お兄ちゃんの手…あったかい…\nお兄ちゃん…来年も…再来年も…\nずーっと一緒に居てくださいね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1085005, 108501, 0, 6.4, 5, "あの…お兄ちゃん…\nこの服似合って…ひゃぅぅ…！\nきゅ、急に頭を撫でないでぇぇ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1085006, 108531, 0, 0.0, 1, "うう…ツリーのてっぺんに\n手が届かないよぉ…\nお行儀悪いけど、\n椅子を使って…ひゃわぁ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1085007, 108531, 0, 0.0, 1, "ツリーの飾り付けが終わったら、\nお買い物と、お料理の準備と…\nうう…アヤネちゃん、雪だるま\n作ってないで手伝ってよぉ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1085008, 108531, 0, 0.0, 1, "あ…このお人形かわいい…♪\n作り方を教えてもらって、\nお兄ちゃん人形を\n作ってみようかなぁ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1085009, 108531, 0, 0.0, 1, "お、お兄ちゃん…\nクリスマスは恋人たちの夜って\nいいますよね…だ、だからその…\nあうぅぅ…何でもないですぅ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1085010, 108531, 0, 0.0, 1, "…お兄ちゃんは、私の両親みたい\nに居なくなったり…ひゃわっ！\nい、いきなり抱きしめるなんて…\nでも…あったかいです…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1085011, 108501, 0, 7.6, 2, "お兄ちゃん、お誕生日…\nお、おめでとうございますぅ…！\n私なりに一生懸命お祝いするから\nよ、喜んでくれると嬉しいです…♪", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1085012, 108531, 0, 0.0, 1, "お誕生日おめでとうございますぅ\nいい子にしてたお兄ちゃんに\nプ、プレゼントを用意しました…\n靴下の中を見てみてください…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1086001, 108601, 0, 4.0, 2, "メリークリスマース♪\nお兄ちゃん見て見て！\nぷうきちも私みたいに、\nクリスマス衣装なんだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1086002, 108601, 0, 2.7, 5, "あったか～い♪\nクリスマスのお洋服で、\nぷうきちがぬっくぬくだよ～♪\n『俺のハートが燃えてるぜ』", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1086003, 108601, 0, 9.6, 5, "サンタさんからのプレゼント…\nデッカい七面鳥に甘ぁいケーキ…\nやっぱり、\nクリスマスは最高だよぉ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1086004, 108601, 0, 3.1, 3, "クリスマスのごちそう楽しみ～♪\n…なんか、お兄ちゃんから食べて\nばかりと思われている気がする！\n私だってお料理できるんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1086005, 108601, 0, 5.8, 5, "今年のクリスマスは、\nぷうきちだけじゃなくて、\nお兄ちゃんも居る…えへへ♪\nなんだかとっても幸せかも♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1086006, 108631, 0, 0.0, 1, "クリスマスのお洋服で、\nぷうきちもさらに\nかわいくなったよねぇ♪\n『ぷりてぃーだろ？』", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1086007, 108631, 0, 0.0, 1, "ふっふっふっ…見よこの王冠！\nクリスマスの私は王様なのだぁ♪\nだからお兄ちゃんは、\n私を思いっきり甘やかすように♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1086008, 108631, 0, 0.0, 1, "ぷうきち大家族完成～♪\nこっちは、ぷうすけとぷうきち…\nあっちは、ぷうままとぷう兄…\nじゃなくて、お兄ちゃん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1086009, 108631, 0, 0.0, 1, "雪だるまで手がつめたいからぁ…\nお兄ちゃんにたーっち♪\nえへへ…やっぱり\nお兄ちゃんはあったかいね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1086010, 108631, 0, 0.0, 1, "あったかくなったら、\n雪だるまはなくなっちゃうけど…\n私たちはずーっと一緒だよね、\nお兄ちゃん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1086011, 108601, 0, 5.8, 6, "お誕生日おめでとう！\nバースデーケーキは、\nぷうきちの形で作ってみたよ！\nダメっ、そっちは本物だよー！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1086012, 108631, 0, 0.0, 1, "お兄ちゃん、誕生日おめでとー！\n大切な日を家族みたいに\n一緒に過ごせてうれしいな！\nぷうきちも喜んでるよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1087001, 108701, 0, 3.6, 1, "年末年始はみんな忙しそう！\n騎士くん。あたしの手なら\nいくらでも貸しちゃうよ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1087002, 108701, 0, 4.5, 2, "動きやすい着物だから\n力仕事もオッケーだよ！\nねぇねぇ、なにすればいい？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1087003, 108701, 0, 6.1, 2, "お正月の空気って好きだなー\n新しい年を迎えるのって\n気分がピーンってなるよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1087004, 108701, 0, 5.4, 5, "騎士くんだー！\nおーい、ここだよー！\n騎士くんなら、どこにいても\nすぐに分かるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1087005, 108701, 0, 7.8, 5, "初詣のおみくじ？　大吉だよ！\nいいことばっかり書いてたなー\n恋愛運！？　…内緒っ！　", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1087006, 108731, 0, 0.0, 1, "次はあっちの屋台に行こうよ！\n持ちきれない分は早く食べちゃお！\n騎士くんもほら、食べて食べて！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1087007, 108731, 0, 0.0, 1, "ユイちゃんのおせちに\n屋台の食べ物…！\n最高のお正月だよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1087008, 108731, 0, 0.0, 1, "大晦日にたくさん頑張ったし\n今日くらいは、もうちょっと\n食べちゃってもいいよね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1087009, 108731, 0, 0.0, 1, "あっ…そのお菓子\nあたしが口付けちゃった…\n気にしない？　そ、そっか！\nならオッケー！　うん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1087010, 108731, 0, 0.0, 1, "騎士くん…手、つないでもいい？\nあっ、これ食べちゃわないとだね！\nはい騎士くん！　あーんっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1087011, 108701, 0, 4.4, 1, "騎士くん、誕生日おめでとー！\n騎士くんにとって\n新しい一年のはじまりだね！\n一緒にお祝いしよ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1087012, 108731, 0, 0.0, 1, "ハッピーバースデー！\n騎士くんがこれからの一年間も\n幸せでありますように！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1087013, 108761, 0, 0.0, 1, "あはっ、すっごくきもちいい！\n騎士くんもおいでよ！\n一緒に風になろ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1087014, 108761, 0, 0.0, 1, "おっとっとぉ…\nうん、だいぶ慣れてきた♪\nよーし、もっとスピードだすぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1087015, 108761, 0, 0.0, 1, "やっほー騎士くーん！\nニューイヤートリック！\nなんちゃって♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1087016, 108761, 0, 0.0, 1, "騎士くんが見ててくれるなら…\nどんな大技でも\nきめられちゃうかも♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1087017, 108761, 0, 0.0, 1, "大丈夫だよ？\nだって何かあっても、騎士くんが\n抱っこして助けてくれるもんね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1088001, 108801, 0, 4.7, 1, "あけましておめでとう！\n騎士クン。今年もよろしくね\nおみくじはもう引いた？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1088002, 108801, 0, 5.1, 1, "お正月でも気を抜かずに\n魔法の修行をしないと…\n今年はもっと上達したいな", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1088003, 108801, 0, 3.1, 2, "騎士クン、寒くない？\n防寒の魔法が必要なら\nいつでも言ってね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1088004, 108801, 0, 7.4, 8, "いいな…恋人同士でお参り\nわたしもいつか…\nな、なんでもないよ！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1088005, 108801, 0, 7.8, 5, "今年の恋愛運は…\n「相手の胃袋を掴むべし」…\n…騎士クン、お腹空いてない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1088006, 108831, 0, 0.0, 1, "騎士クン、去年はありがとう！\n今年もよろしくね！\n来年も…その先も！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1088007, 108831, 0, 0.0, 1, "…こっちに来て欲しいな\nか、風が強くて寒いから！\n…そばにいてくれる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1088008, 108831, 0, 0.0, 1, "初日の出、キレイだね…\n…騎士クン？　どうしたの？\n太陽、まぶしくない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1088009, 108831, 0, 0.0, 1, "あのね？　今日みたいに\n毎年騎士クンと…\n…太陽が見たいの！\n…なに言ってるのわたし～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1088010, 108831, 0, 0.0, 1, "騎士クン。わたしね…\n騎士クンといるどんな時間も\n幸せに思ってるから…！\n…今、言えた…！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1088011, 108801, 0, 11.3, 2, "あ、あけましておめでとう！\nああっ、間違えちゃった…\nえっと…その…\nお誕生日おめでとう、騎士クン！", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1088012, 108831, 0, 0.0, 1, "騎士クン、お誕生日おめでとう！\nこれからも騎士クンが\n幸せな日々を過ごせますように…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1088013, 108861, 0, 0.0, 1, "騎士クン…？　あ、あの…\nおみくじ、結んじゃわないと…\nわ、わたし…わたし…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1088014, 108861, 0, 0.0, 1, "はぁ…あったかいな…あっ！\nおみくじが飛んでいっちゃう！\nで、でも、もう少しだけ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1088015, 108861, 0, 0.0, 1, "おみくじに書いてあることも\nお参りして願ったことも、\n叶えるのはわたし自身…だよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1088016, 108861, 0, 0.0, 1, "騎士クン…もう大丈夫だよ？\n…手がやわらかくて気持ちいい？\nじゃあ…あと少し、このままで…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1088017, 108861, 0, 0.0, 1, "わたし、騎士クンと離れたくない\n…今だけじゃ、ないよ…？\n今年も、来年も…ずっと…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1089001, 108901, 0, 0.0, 1, "着物を着ると、いつもより\n歩き方や所作に注意して\n動くようになるんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1089002, 108901, 0, 7.1, 2, "正月といえど、羽目を外して\n暴れる輩は、この刀で成敗！\n…冗談だよ。本気にした？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1089003, 108901, 0, 2.9, 2, "キミは正月をどう過ごすの？\n参考にしたいから、良ければ\n教えてくれない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1089004, 108901, 0, 6.8, 5, "いつもと雰囲気が違う？\nまぁ、着物を着ているし、\nキミの隣にいるから…かな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1089005, 108901, 0, 5.2, 2, "おみくじを引いたら\n恋愛運が良かったんだけど…\n…さっそく運が向いたみたいだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1089006, 108931, 0, 0.0, 1, "新年早々なんて邪な…！\n人の幸せを邪魔する者は\n私が全力で斬る！　いくぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1089007, 108931, 0, 0.0, 1, "やっぱり今年も、私にはキミが\n必要だ。…お願い。これからも、\nキミの力を貸して欲しい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1089008, 108931, 0, 0.0, 1, "冷気を帯びたこの剣技…\nキミのおかげで、更に磨きが\nかかったよ。ありがとう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1089009, 108931, 0, 0.0, 1, "…私は、自分の進む道は\n自らの手で切り開きたいんだ\nそしてその道は、大切な仲間と\nキミと一緒に歩んでいきたい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1089010, 108931, 0, 0.0, 1, "…煩悩は去年に置いてきたはず\n気にしないで！　なんでもない！\nなんでもない、はずだから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1089011, 108901, 0, 2.1, 2, "誕生日おめでとう！\nキミと一緒に過ごす日々は\nこれからも私にとって\n大切な宝物だよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1089012, 108931, 0, 0.0, 1, "今日はキミがこの世に生まれた\n特別な日だ。特別なお祝いを\nあげよう。なんでもいいよ\n…キスのフリ、以外なら…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1089013, 108961, 0, 0.0, 1, "私が私らしく生きるには\n乗り越えなければならない壁が\nいくつもある。でもキミが隣に\nいてくれるなら、何も怖くないよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1089014, 108961, 0, 0.0, 1, "この先の人生で新年を祝うことは\n何回だってあるだろうけど…\nあの瞬間、目に焼き付いた光景は\n決して色あせないだろうね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1089015, 108961, 0, 0.0, 1, "家の束縛から逃れるために、\nキミと一緒にいるわけじゃない\n大切な人だから傍にいたいと\n心から願っている。それだけだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1089016, 108961, 0, 0.0, 1, "新たな一歩を踏み出せば、\nきっと何かを変えられる\nそして必ず掴めるはずだよ\nキミと生きる、輝く未来を…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1089017, 108961, 0, 0.0, 1, "愛の力は偉大だ…なんて\n大真面目に言うことじゃないと、\n以前の私なら考えていただろうね\nでも、確かにその通りだったんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1090001, 109001, 0, 12.7, 2, "あなた様。手作りのチョコです\n受け取ってくださいますか…？\n愛情がいっぱい入っていますわ\nさぁ、お口を開けてください…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1090002, 109001, 0, 10.8, 2, "いい香りでしょう…？\nハーブを混ぜてあるんです\nどんな効能か？\nクスクス…なんでしょうね…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1090003, 109001, 0, 3.7, 2, "周りのビンが気になりますか？\nご安心を、すべて調味料です\n料理するときは手になじむものを\n使いたいという、乙女心ですわ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1090004, 109001, 0, 3.2, 5, "不思議なものですね\nチョコを作っているとき、\nあなた様の喜ぶ顔が見たくて\n一生懸命になってしまいました", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1090005, 109001, 0, 11.2, 2, "こうして料理をしていると、\n本当の伴侶のようですわね\nもちろん、いずれは本当に\nなるのですけれど…クスクス", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1090006, 109031, 0, 0.0, 1, "バレンタインデーとは\n愛する方にチョコレートを\nお贈りする日…私の甘い愛で\nお腹を満たして差し上げましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1090007, 109031, 0, 0.0, 1, "運命で結ばれたあなた様への想い\nチョコだけでは表現しきれません\n三日三晩かけて想いを綴った\nお手紙も一緒にお付けしますわ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1090008, 109031, 0, 0.0, 1, "エプロン、似合っていますか？\nクスクス…うれしいですわ\nでしたら毎日お見せしますね\nどんな時間、どんな場所でも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1090009, 109031, 0, 0.0, 1, "バレンタインデーでは、チョコと\n一緒に告白する方もいるみたい\nですが…特別な日じゃなくても\n私は毎日愛をお伝えしますわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1090010, 109031, 0, 0.0, 1, "調合した薬を混ぜるのもいいです\nけれど、やっぱりチョコレートを\n甘くするのは、込めた想いですわ\n愛しています…あなた様", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1090011, 109001, 0, 11.2, 2, "あなた様が生まれた日…つまり\n私たちの運命が始まった日ですわ\n祝杯をあげましょう\nさぁ、これをお飲みになって…", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1090012, 109031, 0, 0.0, 1, "誕生日おめでとうございます\nふたりきりでお祝いする準備は\nできていますわ\nそのあとは…クスクス", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1091001, 109101, 0, 6.7, 2, "弟くん、バレンタインは\n知ってる？　大好きな人に\nチョコレートをあげる日なんだよ\nお姉ちゃんの愛、受け止めてね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1091002, 109101, 0, 7.5, 5, "お姉ちゃんのよしよしみたいに\n甘いチョコか、お姉ちゃんの\nナデナデみたいに大人なチョコ\n弟くんはどっちがいいかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1091003, 109101, 0, 2.0, 1, "虫歯になっちゃいそう？\n大丈夫だよっ、お姉ちゃんが\n弟くんの歯を綺麗に磨いてあげる\nあーんしてみて♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1091004, 109101, 0, 5.5, 2, "ナデナデしてあげようか？\nそれとも肩もみ？　膝枕？\nチョコとセットで、お姉ちゃんの\nあま～いお世話はいかがっ？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1091005, 109101, 0, 7.1, 2, "ほら、口元にチョコが付いてるよ\nお姉ちゃんがとって…あむ\nうん、とっても甘い♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1091006, 109131, 0, 0.0, 1, "バレンタインは甘いチョコと\n一緒に愛を伝える日♪\nお姉ちゃんの愛はチョコよりも\n甘いから、覚悟しててね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1091007, 109131, 0, 0.0, 1, "チョコはいくつもらえたのかな？\nお姉ちゃんに教えてごらん？\n弟くんのすべてを知りたい\n純粋な姉心だよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1091008, 109131, 0, 0.0, 1, "そわそわしちゃってどうしたの？\n弟くんは何を待ってるのかな？\nふふっ、焦らされる弟くんも\nかわいいなぁ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1091009, 109131, 0, 0.0, 1, "えっ？　この衣装、似合ってる？\n弟くんったら、女の子の褒め方が\n上手になったね♪\nお姉ちゃん、ドキッとしちゃった", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1091010, 109131, 0, 0.0, 1, "弟くん、大好きだよ♪\nチョコはまだまだあるから\nあげるたびに言ってあげるね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1091011, 109101, 0, 2.8, 2, "今日は弟くんの誕生日！\n大きなチョコケーキを作ったから\n一緒に食べようね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1091012, 109131, 0, 0.0, 1, "お誕生日だね！　お姉ちゃんは\n今日という記念日のために、\nプレゼントを考え続けてたよ！\nはい、お姉ちゃんをあげるね☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1092001, 109201, 0, 5.0, 2, "せっかく別の世界に来たんだし、\nあなたとも仲良くなりたいな！\nよろしくねっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1092002, 109201, 0, 4.0, 1, "確かに、マナリアの第一王女では\nあるけど…あまり気にしないで、\n気軽に話してくれるとうれしいな！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1092003, 109201, 0, 3.7, 2, "普段はグレアと二人でいることが\n多いかな！　グレアの隣は\nとっても落ち着くし、安心するの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1092004, 109201, 0, 3.0, 1, "あなたのお話、すっごく面白い！\nもっとも～っと聞かせてほしいな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1092005, 109201, 0, 3.2, 2, "良かったら、あとで一緒に\nお茶しない？　紅茶を淹れるの\n実は、ちょっと得意なんだ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1092006, 109231, 0, 0.0, 1, "英霊よ、我が声に応えよ！\nみんなを護るために\nあなたの力を貸して！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1092007, 109231, 0, 0.0, 1, "グレアには絶対に\n悲しい顔をさせたりしない！\n私、頑張るよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1092008, 109231, 0, 0.0, 1, "大切な人たちとの絆が、\n私に無限の力を\n与えてくれるんだ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1092009, 109231, 0, 0.0, 1, "私の魔法は、みんなを…\nあなたを守るためにあるんだよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1092010, 109231, 0, 0.0, 1, "あなたのことは絶対に\n傷つけさせないから！\nここは私に任せてね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1092011, 109201, 0, 1.8, 2, "お誕生日おめでとうっ！\n今日は思いっきり幸せな\n一日になるといいね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1092012, 109231, 0, 0.0, 1, "ハッピーバースデー！\n一緒に楽しい一日にしようねっ！\nまず何からしよっか！？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1093001, 109301, 0, 3.1, 2, "お友だちが増えるのは\nうれしいですぅ！\n一緒にせいしゅんしたいですぅ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1093002, 109301, 0, 3.0, 1, "追試と赤点の毎日ですけど、\nルゥはマナリア魔法学院が\nだぁーい好きですぅ♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1093003, 109301, 0, 2.7, 2, "せいしゅん、してるですか？\nルゥは毎日してるですぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1093004, 109301, 0, 1.2, 2, "ルゥと一緒に、もっともーっと\n遊んでほしいですぅ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1093005, 109301, 0, 3.4, 1, "オメメちゃんのことが\n気になるですか？\nつんつんってしてみます？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1093006, 109331, 0, 0.0, 1, "ルゥの華麗なオメメちゃんさばき\n見せてあげるですよ！\nそれそれですぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1093007, 109331, 0, 0.0, 1, "ルゥとオメメちゃんの\nこんびねーしょんで\nやっつけちゃうですよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1093008, 109331, 0, 0.0, 1, "別の世界での思い出も、\nせいしゅんの１ページです！\nルゥ、とっても楽しいですぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1093009, 109331, 0, 0.0, 1, "え、オメメちゃんの正体ですか？\nさぁ…？　でも、ルゥとずーっと\n一緒のお友だちですぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1093010, 109331, 0, 0.0, 1, "追試の女帝、落第の女王…\n数々のいみょうを持つルゥの実力\n見せてやるですぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1093011, 109301, 0, 2.8, 2, "今日はあなたの誕生日です？\nじゃあ、ルゥもお祝いするです！\nおめでとうですぅ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1093012, 109331, 0, 0.0, 1, "ルゥ、暗記は苦手ですが…\nお友だちの誕生日は\nばっちり覚えてるですぅ！\nお誕生日おめでとうですよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1094001, 109401, 0, 4.4, 4, "私のこの姿や、竜の力…\nあなたは、\n怖がらないでいてくれる…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1094002, 109401, 0, 3.6, 2, "私はみんなみたいに\nはしゃげないけど…\nこれでも楽しんでるつもり", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1094003, 109401, 0, 5.3, 2, "アンは、ずっと一人だった私の\nそばにいてくれて…\n私の世界を照らしてくれたんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1094004, 109401, 0, 3.7, 2, "違う世界に来てしまうなんて…\nでも、あなたたちみたいな\n優しい人に出会えて、よかった", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1094005, 109401, 0, 0.0, 2, "あなたには本当に感謝してる…\nもし私に手伝えることがあれば\n何でもいってね、頑張るから", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1094006, 109431, 0, 0.0, 1, "皆を護るためなら、\nいくらでも力が出せるよ…！\n竜の炎を見せてあげる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1094007, 109431, 0, 0.0, 1, "皆のことは…\n絶対に傷つけさせない\n私の炎は、そのためにあるの…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1094008, 109431, 0, 0.0, 1, "私に足りなかったのは、\n自分に負けない強さ…\nでも今は、もう大丈夫！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1094009, 109431, 0, 0.0, 1, "こんな力、いらないって\n思ったこともあるけれど…\n今は少し誇れる気がするんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1094010, 109431, 0, 0.0, 1, "私、結構丈夫なんだ\nだから…任せて！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1094011, 109401, 0, 4.3, 2, "今日はあなたの誕生日なんだね\nその…おめでとう\nなにか欲しいものとかある？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1094012, 109431, 0, 0.0, 1, "ハッピーバースデー\nあなたのお祝いの日なのに、\n私までうれしいんだ。不思議だね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1095001, 109501, 0, 2.6, 2, "これが噂の着物ですね…\n見てよし、\n脱がせてよし、\n回してよしの魔性の服…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1095002, 109501, 0, 2.9, 6, "あ～れ～～～～～！\nドＳさん、見ていたのですか！？\nクウカの回る姿を楽しんでいたの\nですね…やはり人格破綻者…！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1095003, 109501, 0, 4.8, 5, "噂によると、着物では\n履かないものなのだとか…\nドッ、ドＳさん…確かめさせろ\nだなんていけませんよぉ…ぐふふ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1095004, 109501, 0, 7.4, 5, "あっ、帯が緩んでしまいました…\nドＳさん、締めなおしてくれるの\nですか？　…じゅるり。いい…\nいいですっ！　もっときつく～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1095005, 109501, 0, 5.6, 5, "ドＳさん…いえ、今はドS様と\n呼んだ方がいいでしょうか…\nクウカはドＳ様にお仕えする\n従順な…じゅるり", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1095006, 109531, 0, 0.0, 1, "この着物、露出が多くて\n少し恥ずかしいです…でも、\nドＳさんに見られていると思うと\nなんだか気持ちが…じゅるり", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1095007, 109531, 0, 0.0, 1, "あ～～～れ～～～～～！\nこの回転がクセになりそうです\nもっと、もっと回ります～！\nぐふ、ぐふふふ～～～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1095008, 109531, 0, 0.0, 1, "この衣装で踊ると、いつもより\nきれいに踊れている気がします…\n蝶のように舞うクウカを、\n蜘蛛のようにドＳさんが…ぐふふ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1095009, 109531, 0, 0.0, 1, "「クウカの着物を剥ぎ取り、\nドＳさんは野獣のように…」\nぐふふ…はっ！　ドSさんに\n見られている気配がします…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1095010, 109531, 0, 0.0, 1, "この帯が気になるんですか？\nつ、つまりクウカを回したいと…！\nそして着物のはだけたクウカを\nそのまま美味しく…ぐふふ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1095011, 109501, 0, 5.7, 5, "今日は大切な日ですね…\nドＳさんが生まれた日…じゅるり\nつまりクウカの体でお祝いしろと\nいうのですね！？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1095012, 109531, 0, 0.0, 1, "お誕生日おめでとうございます\nところでドＳさん、和服の帯って\nラッピングしてるみたいですよね\nプレゼントは私、なんて…ぐふふ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1096001, 109601, 0, 7.9, 2, "オシノビでお出かけするなら\nいいところを知ってマス\n今度ワタシが案内するデス！\nきっと楽しいデスよー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1096002, 109601, 0, 4.9, 6, "もみじも好きデスけど\nもみじマンジュウも好きデスよ\n考えてたらお腹が空きまシタ…\nハナヨリダンゴデス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1096003, 109601, 0, 8.6, 2, "ヒトツ投げては天下統一のため…\nフタツ投げても天下統一のため…\nショーグンも手裏剣投げの特訓を\nしまセンか？　投げ放題デス！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1096004, 109601, 0, 11.0, 2, "このキツネのお面はオーエド町の\n縁日で買いまシタ。歩くと鈴が\n鳴って、とてもかわいいデス\nコンコンデース！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1096005, 109601, 0, 4.0, 1, "忍法・氷結地獄を思いつきまシタ\n冷たいカキ氷を\n敵に無理やり食べさせて\n頭をキーーーンとさせる技デス！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1096006, 109631, 0, 0.0, 1, "ショーグンに仇名す敵に\n天誅デス！　天下統一するまで\n立ち止まりまセン！\n必殺忍法で勝ち進みマス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1096007, 109631, 0, 0.0, 1, "フッフッフ…とっておきの\n新忍法の見せどころデース！\nムム、まだ使っちゃダメデスか？\nショーグンのイケズデス～…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1096008, 109631, 0, 0.0, 1, "ショーグンは天下泰平の世を\n築いてくれるでショウ！\nワタシはそんな城下町を\nずっとお守りいたソウロウ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1096009, 109631, 0, 0.0, 1, "ワタシのカチュヤク、しっかり\n見ていてクダサイ！　いつか\nチューシンと呼ばれるその日まで\nシロクジチュウおそばにいマス", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1096010, 109631, 0, 0.0, 1, "ショーグンのためならどんな\n敵とだって戦うデス！\nチューセーシンこそ天下統一の\n切り札だと信じていマス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1096011, 109601, 0, 7.4, 1, "誕生日おめでとうデス！\nプレゼントはショーグン専用の\nマイ手裏剣デース！\nいっぱい投げ込んでクダサーイ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1096012, 109631, 0, 0.0, 1, "今日はおめでとうゴザルデス！\n乾杯の音頭はワタシがとりマス！\nお手をハイシャクデース！\n朝までワッショイデース！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1097001, 109701, 0, 4.9, 2, "家事全般は得意ですので、\nなにかお役に\n立てることがありましたら、\nこのレムにお申し付けください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1097002, 109701, 0, 5.8, 2, "姉様がメイドの仕事を\nしないで休憩している…ですか？\nさすが姉様！\nお休みする姿もすてきです！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1097003, 109701, 0, 4.0, 2, "鬼といっても、\n人間と大きく変わりませんよ\n魔獣を薙ぎ払ったり、\n鉄球を振り回せるぐらいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1097004, 109701, 0, 0.0, 1, "身の回りの世話は\nこのレムにお任せください\n懸命にご奉仕\nさせていただきます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1097005, 109701, 0, 5.1, 2, "こちらの世界では、\n角があっても\n珍しくはないのですね\nなんだか、少しうれしいです", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1097006, 109731, 0, 0.0, 1, "この鉄球ですか？\nこれは携帯用で、\n短くなっているから、\n普通の鉄球より軽いんですよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1097007, 109731, 0, 0.0, 1, "巨木であろうと、\n鬼であるレムを\n阻むことは不可能です\n全て粉砕させていただきます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1097008, 109731, 0, 0.0, 1, "この程度の巨木ならば、\nレムの力でどけられると思います\nあ、あの…上手くいったら\n褒めてくれてもいいんですよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1097009, 109731, 0, 0.0, 1, "女性にこの倒木を\n破壊するのは無理、ですか？\nご託は結構ですので、\nそこで見ていてください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1097010, 109731, 0, 0.0, 1, "強いって…\n女の子にその言葉は\nどうかと思いますよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1097011, 109701, 0, 5.4, 1, "お誕生日おめでとうございます\nお祝いにケーキを焼きますね\nより豪華にするため、まずは\n飛竜の卵を調達してこなくては", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1097012, 109731, 0, 0.0, 1, "お誕生日おめでとうございます！\n今日のために色々と\n用意させていただきましたので、\n精いっぱいお祝いいたしますね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1097013, 109761, 0, 0.0, 1, "お掃除はレムの得意分野です\nそれが食材調達も兼ねるのなら\nまさに好都合というものです\n綺麗さっぱりお片付けします！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1097014, 109761, 0, 0.0, 1, "魔物であろうと魔獣であろうと\nこちらの世界の皆様に\n害をなすというのであれば…\nレムがお相手いたします！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1097015, 109761, 0, 0.0, 1, "皆様に喜んでいただけるよう\n心を込めて調理しますね\n材料が魔物だろうと野菜だろうと\nご心配には及びません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1097016, 109761, 0, 0.0, 1, "少し離れていてください\n大丈夫です。レムが皆様を\nお守りします\n簡単にやられたりはしませんよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1097017, 109761, 0, 0.0, 1, "お任せください\n忠実なるメイドとして冷静に\n姉様のように堂々と\n完璧にお掃除を遂行します！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1098001, 109801, 0, 5.0, 3, "ここが異世界。正直、\n信じられるものでもないけど…\nバルスの軽口よりは\nいくらかましかもしれないわね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1098002, 109801, 0, 2.4, 3, "面倒なことになったものね\nまぁ、危険人物はいないようだし\n楽できる分には問題ないわ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1098003, 109801, 0, 1.9, 2, "仕事ならしているわよ\n紅茶を優雅に飲むラムの姿が、\n周りの目を楽しませているわ", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1098004, 109801, 0, 5.4, 4, "これは風の系統魔法よ\nその気になれば、\n何でも細切れにできるわ\n疲れるから、やらないけど", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1098005, 109801, 0, 5.9, 2, "蒸かし芋を食らうがいいわ\nおいしかったでしょう\n出来たて…いえ、\nふかしたてよ", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1098006, 109831, 0, 0.0, 1, "千里眼をもつラムからは\n逃げられないわよ\n死にたくなければ、せいぜい\n知恵と勇気を振り絞ることね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1098007, 109831, 0, 0.0, 1, "覚えておきなさい\n角をなくそうと、\n天才であるラムには\n遠くおよばないということを", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1098008, 109831, 0, 0.0, 1, "戦闘では本当に役立たずね\n失望を隠すのが大変だわ\n…せいぜいラムの側から\n離れないことね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1098009, 109831, 0, 0.0, 1, "一気に決めさせてもらうわ\nなぜって？\nラムが…いえ、\n早く帰りたいからよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1098010, 109831, 0, 0.0, 1, "風の魔法は便利よ。敵を\n切り裂くだけでなく、面倒な男を\nねじ切ることだってできるわ\nどこを？　ご想像にお任せするわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1098011, 109801, 0, 6.5, 2, "誕生日をラムに祝ってもらえる\nなんて光栄に思うことね\n誕生日プレゼント？　もちろん\n出来たての蒸かし芋よ！", 3, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1098012, 109831, 0, 0.0, 1, "何が欲しいの？\n言ってごらんなさい\nなんでって、誕生日なんでしょ？\nご褒美くらいくれてやるわ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1098013, 109861, 0, 0.0, 1, "ラムが味方にいることを\n幸運に思いなさい\n何を隠そう、\nラムの得意料理は蒸かし芋よ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1098014, 109861, 0, 0.0, 1, "ラムの助けになりたいだなんて\n思い上がりも甚だしいけど\n殊勝な態度は認めてあげるわ\nさっさとラムを強化しなさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1098015, 109861, 0, 0.0, 1, "ラムがあなたに恩を感じるなんて\nこれっぽっちもないのだけど\n偶然、蒸かしたての芋があるわ\n熱いうちに貪りなさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1098016, 109861, 0, 0.0, 1, "あなたが信用に値するかは\nラムがこの目で判断するわ\n僕はラムを信用してる…？\nそう、ならせいぜい尽くしなさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1098017, 109861, 0, 0.0, 1, "レムやエミリア様に\nいやらしい目を向けないことね\n……ラムに？\n浮気者は死になさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1099001, 109901, 0, 7.9, 1, "不思議なものね\nマナもあって精霊もいるのに\n見たこともないものがいっぱい\nここが異世界なんだ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1099002, 109901, 0, 6.3, 2, "軽くお散歩でもする？\n精霊の子たちとお話ししにいくの\nそれがこの子たちとの\n契約条件の一つだから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1099003, 109901, 0, 5.0, 6, "さっき街に行ったら\nエルフの人をみかけたわ\nもしかしてこの世界にも\nハーフエルフっているのかな…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1099004, 109901, 0, 5.2, 6, "コッコロちゃんって賢くて\nすごーくかわいいわよね\n…本当に私の妹だったら\nよかったのにな", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1099005, 109901, 0, 5.4, 6, "こっちの世界に来てから\nパックってあまり出てこないの\nマナを使いすぎないように\n気を使ってるのかな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1099006, 109931, 0, 0.0, 1, "あなたも少し座らない？\nランドソルの花もすごーくきれい\n微精霊たちもよろこんでるわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1099007, 109931, 0, 0.0, 1, "少しお話しましょう？\nこの世界のことや\n私の世界のこと…\n話したいことがたくさんあるの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1099008, 109931, 0, 0.0, 1, "そっか、コッコロちゃんも\n精霊たちとお話しできるのね\nふふ、やっぱり他人とは\n思えないな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1099009, 109931, 0, 0.0, 1, "あら、花びらが髪についてる\nとってあげるから\n頭をこっちに向けて？\nそうそう…はい、とれたよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1099010, 109931, 0, 0.0, 1, "ねえ…またこのお花畑を\n一緒に見に来てくれる？\n…精霊使いにとって約束は\n何よりも大切なんだからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1099011, 109901, 0, 6.0, 2, "こっちの世界だと\nはっぴーばーすでー\nでいいのかな？\nお誕生日おめでとう", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1099012, 109931, 0, 0.0, 1, "お誕生日なんだね、おめでとう\nこうやってお祝いできて\nすごーくうれしいな", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1099013, 109961, 0, 0.0, 1, "最近、ランドソルの人たちに\n名前で呼ばれるようになったの\n受け入れてもらえた気がして、\nすごーく嬉しいわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1099014, 109961, 0, 0.0, 1, "散歩や旅行、もちろん冒険も、\nいろんなことを通じて、みんなと\n絆を深めていきたいわ\n魔物料理を食べるのもいいわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1099015, 109961, 0, 0.0, 1, "雪が降ってるのに、\nなんだかすごーくあったかいの\n体を動かしたからかもだけど\nそれだけじゃないような…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1099016, 109961, 0, 0.0, 1, "思えば、この世界では素敵な\n思い出ばかりもらっちゃってる\n私も何かお返ししたいんだけど…\nえ？　もうもらってる…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1099017, 109961, 0, 0.0, 1, "私、お友だちって呼べる人は\nあんまり多くないの\nだから、ここで出会ったみんなは\nすごーく大事な宝物なの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1100001, 110001, 0, 4.9, 2, "ちょす～♪\n夏の海ってキラキラしてて\nマジヤバいよね！\nめいっぱい楽しんじゃお～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1100002, 110001, 0, 6.9, 6, "ヒデサイ、知ってた？\n海の水ってしょっぱいんだよ！\nうち物知りっしょ～♪\nでぃすいずあんだすたんど？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1100003, 110001, 0, 3.4, 1, "撮影が終わったら\n一緒に海で遊ぼうよ！\nすぐ終わるから\nちょいまっててね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1100004, 110001, 0, 2.5, 5, "この水着、イケてるっしょ！\nファンのみんなに\n喜んでもらえるといいな～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1100005, 110001, 0, 7.7, 1, "んー！　海って最高ー！\nヒデサイといっぱい遊べるし\nマジで来てよかったよー♪\n今は夏の宿題の話はナシでよろ～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1100006, 110031, 0, 0.0, 1, "見て見て、うちのモデル姿！\nどう？　いい感じっしょ♪\nこんなに近くで見ていいのは\nヒデサイだけだかんね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1100007, 110031, 0, 0.0, 1, "夏の海って楽しいことが\nマジでいっぱいあるけど…\nヒデサイといるだけで、めっちゃ\nバイブス上がるんだよね～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1100008, 110031, 0, 0.0, 1, "セクシーってよく分かんないけど\nいまのうち、セクシーかな？\nヒデサイがドキドキ\nしてたら、うれしいな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1100009, 110031, 0, 0.0, 1, "夏って気分アガるよね♪\nヒデサイも応援してくれるし、\nもう怖いもんなしって感じだよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1100010, 110031, 0, 0.0, 1, "日ざしも砂浜もあっついねー！\n今日はめっちゃ\nフィーリングがいいんだ～♪\n超かわいい写真が撮れそう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1100011, 110001, 0, 3.3, 2, "ちょすー！\nヒデサイ、誕生日なんだよね？\nおめでとー！\n今日はパーティーだね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1100012, 110031, 0, 0.0, 1, "ちょす！　ヒデサイ、ハピバー☆\nお祝いしなきゃだよね！\nうちにしてほしいことがあったら\nなんでも言っていいよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1101001, 110101, 0, 7.4, 2, "海に入る前には\n準備運動をしなくちゃ！\n手伝ってあげるから力を抜いて？\nそう、とっても上手よ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1101002, 110101, 0, 7.1, 1, "海って楽しいところだけど\n危ないこともあるんだから\n気を付けて遊びましょうね？\n先生がついていてあげる", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1101003, 110101, 0, 4.8, 4, "なんだかやたら男の人が\n話しかけてくるの。もしかして\n水着がずれちゃってるのかな\n君、ちょっと見てくれる？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1101004, 110101, 0, 7.0, 4, "ああ～ん、\n砂が水着の中に入ってきちゃって\nくすぐったいわ～\nお願い、なんとかして～", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1101005, 110101, 0, 8.0, 6, "久しぶりに水着を着るから\n今日のために新調したのよ\n似合ってるかな？　ってどうして\n顔をそむけちゃうのよ～", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1101006, 110131, 0, 0.0, 1, "くしゅんっ…\nちょっと冷えてきちゃった\n君にくっついて\n暖まってもいいかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1101007, 110131, 0, 0.0, 1, "君があまりにも頼もしいから\n私たちが先生と生徒って事を\n忘れちゃいそうなの…\nそれでも…ううん、なんでもない", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1101008, 110131, 0, 0.0, 1, "こうして男の子と海にくるなんて\n初めてのことだから\nなんだか照れちゃうわ\nねえ、君はいまどんな気持ち？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1101009, 110131, 0, 0.0, 1, "夏休みの一番の思い出は\nこのきれいな夜の海かな\n君と一緒に見たんだから\n一生忘れないわ、きっと…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1101010, 110131, 0, 0.0, 1, "君、もしかして少し背が伸びた？\n気のせいじゃないと思うよ\n君の成長を感じると、なんだか\nうれしくて胸がドキドキするから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1101011, 110101, 0, 9.2, 2, "今日は君のお誕生日ね\nふふ、もちろん忘れてないわ\n一つ大人になったお祝いに\n海の家でパーティーしましょ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1101012, 110131, 0, 0.0, 1, "お誕生日おめでとう～\n浜辺で見る君の顔が\nすっかり大人にみえるわ\nふふ、夏のせいかしらね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1103001, 110301, 0, 4.9, 1, "あははっ\n日が暮れるまで浜辺でゆっくり…\nこんな日があってもいいわよね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1103002, 110301, 0, 6.0, 6, "貝殻を集めてアクセサリー作り\nなんてのも悪くないわね\n思い出作りよ？　売ろうなんて\n考えてないわよ？　たぶんね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1103003, 110301, 0, 4.1, 2, "海の砂ってサラサラしてて気持ち\nいいわよね。持ち帰って救護院に\n砂場を作ったりできないかしら？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1103004, 110301, 0, 4.9, 5, "子どもたちと賑やかに\n過ごすのはもちろん楽しいけど\nあんたと二人きりで過ごすのも\nなかなかいいわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1103005, 110301, 0, 3.1, 2, "あたしの水着をこんな間近で\n見られるなんて、あんた本当に\n得してるわよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1103006, 110331, 0, 0.0, 1, "エレガントじゃないかもだけど\n今だけは許してね\nあたし今、とびっきり\n舞い上がっちゃってる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1103007, 110331, 0, 0.0, 1, "夏の日差しって、開放的な\n気分にさせるわよね！\nあたしももう少し\n冒険してみようかしら♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1103008, 110331, 0, 0.0, 1, "海に来るのなんて久しぶりね～\nせっかくのバカンスなんだから\n楽しまなきゃ損よ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1103009, 110331, 0, 0.0, 1, "本当にきれいな海よね\nもし誰もいない浜辺であんたと\n二人っきりだったら…って！\nなんであたしそんなことを！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1103010, 110331, 0, 0.0, 1, "捕まえてみなさ～い♪\nな、なんてことあたしが\nできるわけないじゃない！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1103011, 110301, 0, 1.9, 1, "誕生日おめでとう♪\n救護院のみんなもあんたの\n誕生日を祝いたがってるから\n楽しみにしてなさいね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1103012, 110331, 0, 0.0, 1, "誕生日おめでとう！\n二人きりでバースデーパーティー\nしましょうか♪　なんてね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1104001, 110401, 0, 2.9, 6, "へへっ、海だー！\nな、なんだよ、あたしが\nはしゃいじゃおかしいかよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1104002, 110401, 0, 2.6, 2, "ビーチで焼きそばもいいよな\nよし！　材料持っていって\n昼飯は作ってやるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1104003, 110401, 0, 3.2, 1, "暑い日に泳ぐのって最高だよな！\nそうだ、浮き輪引っ張って\n泳いでやろうか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1104004, 110401, 0, 3.3, 5, "おまえと一緒だと、なんか楽…\nなっ、なんでもねぇ！\n…海だからって、開放的に\nなりすぎるのもよくないな、うん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1104005, 110401, 0, 8.0, 6, "ちょっと休憩しようぜ\nお、なんかおまえの手、\nひんやりして冷たいな！\nって…わりぃ、触りすぎたな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1104006, 110431, 0, 0.0, 1, "うぉわっ！？　み、水着がっ！？\nだ、誰も見てねぇ、よな…って\nなんでそこにいやがんだよ！？\n見てんじゃねぇ！　…ったく", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1104007, 110431, 0, 0.0, 1, "ん～、風が気持ちいいな！\nへへっ、おまえと二人で海なんて、\nくすぐったい感じだぜ\nさぁて、まずは何をする？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1104008, 110431, 0, 0.0, 1, "なんか、おまえには恥ずかしいとこ\nばかり見られてる気がするな…\nイヤってわけじゃねぇけどよ\n…って何言ってんだあたしは！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1104009, 110431, 0, 0.0, 1, "不思議なんだけどさ\nおまえといると、時間が\nあっという間に過ぎちまう…\nおまえもか？　…そ、そっか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1104010, 110431, 0, 0.0, 1, "か、かわいい？\nあたしの水着姿が？\nう、うれしくねぇっての！\n尻尾？　振ってねぇ！　見んな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1104011, 110401, 0, 8.7, 2, "誕生日なんだってな。そうだな…\n砂浜特訓メニューのプレゼント、\nってのは冗談で、\nあとでカキ氷でもおごってやるよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1104012, 110431, 0, 0.0, 1, "今日、誕生日なんだろ？\n食材の買い出し、付き合えよ\nあとでなんか作ってやるからさ\nおまえの好きなもの…教えろよな", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1105001, 110501, 0, 1.9, 1, "元気してるさー？\n私はいつでも元気だよー！\nさーさ、一緒に何するさー？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1105002, 110501, 0, 3.2, 1, "なんくるないさー！\n海を見ると叫びたくならないー？\nほらキミも、なんくるないさー！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1105003, 110501, 0, 5.7, 2, "海は楽しいことでいっぱいよー！\n走って、泳いで、歌って、踊って\n思う存分楽しむさー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1105004, 110501, 0, 8.2, 1, "ん～？　どこを見てるのー？\nこの水着が気になるさー？\nじゃあ触ってみるー？　\nほらほら、遠慮はいらないさ～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1105005, 110501, 0, 3.1, 5, "波の音は心地いいさ～\nキミと一緒に聞いてるとなんだか\n胸がドキドキするね～\nキミも？　聞いてみていい～？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1105006, 110531, 0, 0.0, 1, "夏の楽しみ方は色々あるけど～\nこうして演奏するのは格別さ～\n聞こえる波のリズムに乗って\n心のままに音楽を楽しむさー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1105007, 110531, 0, 0.0, 1, "あは～♪　楽しいねぇ～！\n夏は体がウズウズするさ～！\nほら、キミも一緒に歌うさ～\n恥ずかしがってちゃダメだよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1105008, 110531, 0, 0.0, 1, "思う存分歌ったあとは\n浜辺を走って、海で泳ぐよー！\n夏は待っててくれないからね～\n今日は私と限界を超えるさ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1105009, 110531, 0, 0.0, 1, "今度、キミをテーマに\n一曲作ってみたいさ～\nきっと楽しい曲になるさ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1105010, 110531, 0, 0.0, 1, "キミのことを考えて演奏すると\n音がいつもと違う感じになるさー\nなんて言えばいいのかな～…\nとにかく聴いてほしいさー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1105011, 110501, 0, 2.8, 1, "ばっちり準備万端さー！\n何って、キミの誕生日だよー！\nさぁ手と手を繋いで、\nレッツダンスさー！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1105012, 110531, 0, 0.0, 1, "誕生日、めでたいさ～！\n体が勝手に踊りだすさ～、あっ！\nぶつかってごめんねぇ、でも\nやわいとこだから平気だよねー？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1106001, 110601, 0, 4.2, 2, "夏の海は不思議の国どすえ\nうちと一緒に\n楽しく過ごしまひょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1106002, 110601, 0, 5.5, 1, "ふう、日差しが暑くて\nかなわんわぁ～\n王子はん、あっちの日陰で\n涼みまへんか♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1106003, 110601, 0, 4.1, 2, "冷たいスイカ、切ってきましたぇ\nぬるならへんうちに一緒に\n食べまひょ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1106004, 110601, 0, 7.9, 5, "夏と言えば、花火どす\n夜空に咲く大輪の花…\nはぁ、王子はんと二人で\n見に行きたいわぁ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1106005, 110601, 0, 5.0, 2, "この水着、\nうちに似合うてますやろか？\n王子はんに見せたくて\n一生懸命選んだんどす♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1106006, 110631, 0, 0.0, 1, "いやぁ、気持ちええわぁ～♪\nこうして海辺で寝転がってると\n夏を全身で感じられそうやわぁ\n王子はんも一緒に、どうやろか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1106007, 110631, 0, 0.0, 1, "ぬいぐるみのみんなも\nお日さま浴びて気持ちよさそうや\n王子はんもこっち来て触ります？\nふかふか、もふもふですよ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1106008, 110631, 0, 0.0, 1, "うちの水着、\nヒラヒラしててかいらしやろ？\nじっくり見てもええんよ？\nもっと近くで、ほら、な♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1106009, 110631, 0, 0.0, 1, "夏の海辺はロマンチックやねぇ\nこんな場所に王子はんと二人きり\nゆったり暮らしたいどすなぁ\n…お城はどこに建てまひょか♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1106010, 110631, 0, 0.0, 1, "王子はん、一緒に踊りまへんか？\nマホ姫と王子はん、二人だけの\n海辺の舞踏会や♪\nさぁ、一緒に…くるりんぱ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1106011, 110601, 0, 6.2, 2, "お誕生日おめでとうなぁ～\nまだまだ暑いさかい\n今日は、和菓子と冷やし抹茶で\nお祝いしまひょ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1106012, 110631, 0, 0.0, 1, "今日は王子はんの誕生日やねぇ\nほんま、めでたい日やわぁ\n魔法でお祝いの花火\nあげるからしっかり見といてな", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1107001, 110701, 0, 3.0, 4, "制服って、スカートなんですね…\nうう…足元がすーすーして\n落ち着かないです…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1107002, 110701, 0, 3.7, 2, "私が学院に行くのが心配ですか？\nご安心ください！\nいざとなったら、\n保健室登校しますから！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1107003, 110701, 0, 2.7, 2, "学院にも植物が…！\nぼっちのままでも、\nお花さんたちに\n話しかければ寂しくないです！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1107004, 110701, 0, 6.3, 2, "ＢＢ団の特訓が無かったら、\n学院に近づくだけで\n失神していたかも…えへへ♪\nあなたには、とっても感謝です♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1107005, 110701, 0, 7.2, 5, "あ、あの…\n私の制服姿はどうでしょうか？\nえ…か、かわいいって…\nあわ、あわわわわ…！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1107006, 110731, 0, 0.0, 1, "とにかく目立たない！\nそして、常に隅っこに！\nこうすれば、ぼっちの私でも\n平和な学院生活が…ひぃ！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1107007, 110731, 0, 0.0, 1, "あ、あの人と一緒の学院…\nなんだかドキドキして、\n授業に集中できな…はうぅ！？\nちゃんと授業も頑張ります～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1107008, 110731, 0, 0.0, 1, "つ、次の授業、団体行動がある…\n保健室に逃げ込むしか…ひぃぃ！\nう、嘘です嘘です！\nそんなこと考えてませんから～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1107009, 110731, 0, 0.0, 1, "きょ、今日は友達運が最高！？\n誰かに話しかけられるかもって…\n早速話しかけられる練習を\n始めないと…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1107010, 110731, 0, 0.0, 1, "きょ、今日こそ、\nあの人と下校を…はわぁ！\nご、ごめんなさい！\n私には大それた願いでした！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1107011, 110701, 0, 6.8, 2, "お、おお、お誕生日\nおめでとうございます！\nああ…他の誰かの誕生日を\n祝えるだけでも幸せです…", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1107012, 110731, 0, 0.0, 1, "お誕生日おめでとうございます！\nこんな日に\nあなたと一緒に居られるなんて…\n友達ができるよりうれしいです♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1108001, 110801, 0, 6.1, 2, "いいけど、\nなんかガッコーとかだと\nうち謎に恐がられてンだよね\nあんたは違うみたいだけどさ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1108002, 110801, 0, 5.1, 1, "ちょっと小耳に挟んだんだけど、\nあんたお金食べようとしたこと\nあんだって？　ウケる\nアレあんま美味しくないよね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1108003, 110801, 0, 3.0, 4, "いらっしゃいませぇ～！\n…や、なんかバイト先で愛想よく\nしろとか言われて。はーマジだる", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1108004, 110801, 0, 3.4, 2, "なんてーか、あんたのバイブス\nわりと悪くないんじゃない\n知んないけど", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1108005, 110801, 0, 3.1, 5, "うわ、きも…\n何カッコつけてんの\n不覚にも一瞬キュンって\n来ちゃったじゃん…うち、きもっ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1108006, 110831, 0, 0.0, 1, "夜っていいよね。なんてゆか…\n色々見えすぎないカンジとか\n言えないこと言えちゃうノリとか\nあと…なんかカッコいいじゃん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1108007, 110831, 0, 0.0, 1, "何。あんたまた道に迷ってんの\nしゃーないなー、送ってくよ\nいや、礼とかいいって、べつに\nなんかそんな気分なんだよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1108008, 110831, 0, 0.0, 1, "夜に佇むミステリアスな少女…\nどーよコレ。カンジ出てない？\nただの夜勤休憩中なんだけどさ\nいいの、こういうのは気分だから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1108009, 110831, 0, 0.0, 1, "あのさ。あんたのことだから\nべつに意味とか無いンだろうけど\n無言で人のカオ見んのやめて\nなんか謎にモジついて来るから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1108010, 110831, 0, 0.0, 1, "あんたが？　送ってくれんの？\n何ソレ、誰が誰に言ってんのよ\n夜道は危ないって？　ウケる\nちょっとカッコいいじゃん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1108011, 110801, 0, 9.3, 5, "あのさ。弟たちに作ったケーキ、\n余ってるから食べてくんない\nべつに無理にとは言わないけど\nあー…あと、おたおめ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1108012, 110831, 0, 0.0, 1, "いいけど、あんた、\n今日が誕生日なんだって？\nおめでと。よかったね\nキラーペンシルあげるよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1109001, 110901, 0, 2.8, 1, "なんか楽しいことないです？\n楽しいだけで生きていたい！\n楽しい為なら苦労もできる！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1109002, 110901, 0, 8.1, 6, "せんぱーい！\n違うな…せ～んぱいっ！\nよし、こっちの方がかわいい\nあ、呼んだだけで用はないです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1109003, 110901, 0, 6.7, 1, "え、先輩こんな簡単なことすら\nできないんですか？\nすごいな…すご…\nえっ、大丈夫ですか？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1109004, 110901, 0, 4.9, 4, "今更なんですけど先輩って\n見た目パッとしないですよね\nうーん…もうちょっと\n内面が滲み出るといいですね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1109005, 110901, 0, 11.6, 3, "ちょっと聞きたいんですけど\nえっと、もしかしてアレです？\n先輩、私に気があったりします？\n違います？　違う？　違うのかよ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1109006, 110931, 0, 0.0, 1, "かわわなパフェと無敵の美少女！\nうーん映えるなー絵になるなー\nあ、先輩、悪いんですけど少し\n見切れてもらってていいです？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1109007, 110931, 0, 0.0, 1, "この後カラオケでも行きます？\nチエルが歌うんで、先輩には\nちーえるフゥ！　ちーえるフゥ！\nって跳ね散らかす係をあげますね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1109008, 110931, 0, 0.0, 1, "ちぇる～ん♪　ちぇる　ちぇぱら\nちぇらるれろ　ちぇるぽぱっぴ？\nなーんて、嘘ですよー。えへっ☆\nいやあアオハルって感じですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1109009, 110931, 0, 0.0, 1, "ハイ、あーん？　…うわキモ\nなに本当に口開けてるんです、\n冗談に決まってるじゃないですか\n…もう、一口だけですよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1109010, 110931, 0, 0.0, 1, "先輩って、こんな顔のいい後輩と\n二人きりで何も思わないんです？\nいえ、それが先輩のいいとこです\nけど…え、まじ全然ないです？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1109011, 110901, 0, 7.5, 1, "お誕生日おめでとうございます！\n可愛い後輩から可愛い笑顔を\nプレゼント！　ちぇる～ん☆\n良かったですね、一生ものですよ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1109012, 110931, 0, 0.0, 1, "先輩、ハピバ！\nプレゼントは何がいいです？\nちなみにチエルは可愛いアクセ、\n９月１５日生まれちゃんでーす！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1110001, 111001, 0, 10.2, 2, "気付き、学び、悟り\n先人の遺した書にも\n日々の生活にもそれはある\n共に高め合おうじゃないか、同志", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1110002, 111001, 0, 7.0, 4, "この制服…袖や丈の余剰が\n気にならないと言えば嘘になる\n入学当時の成長見込みから、\n多少の下方修正が必要なようだ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1110003, 111001, 0, 6.7, 1, "ぼくは本質的に文系なのでね、\n余白や行間を愛すのだよ\nゼロかイチかの理系思考に\nあまり浪漫は感じないのさ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1110004, 111001, 0, 5.4, 2, "ふむ…そろそろ睡眠を\n取ろうと思っていたのだが、\n君が話相手になってくれるなら\n起きているのもやぶさかではない", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1110005, 111001, 0, 7.8, 6, "学びは最高の快楽だよ\nそれは脳を耕し、心を潤す\n差しあたっては…そうだな、\n君のことをもっと知りたい", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1110006, 111031, 0, 0.0, 1, "行儀が悪い？　なに、寝転がって\n読もうが背筋を正して読もうが、\n頭に入れてしまえば同じ知識さ\n君も隣りで横になりたまえよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1110007, 111031, 0, 0.0, 1, "読書は孤独なスポーツだからな\n存外と体力を消耗するのだよ\nそこで君に腓腹筋のマッサージを\n依頼する。ほれほれ、揉みたまえ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1110008, 111031, 0, 0.0, 1, "あまりここから出たくはないが…\n信頼する君が請うなら仕方ない\nそこに学びがあると云うならば、\nぼくは書を閉じ、扉を開けよう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1110009, 111031, 0, 0.0, 1, "ぼくにとって失いがたい学びは、\n君たちとの青春だ。世界の真理\nより大切なのさ。ことに生まれて\n初めて友達になってくれた君がね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1110011, 111001, 0, 0.0, 2, "生命とは実に尊い\n存在自体が億分の一の奇跡だ\nつまり端的に換言すれば、\n誕生日おめでとう", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1110012, 111031, 0, 0.0, 1, "ハッピーバースデー\n今日は君がこの世に\n生まれ落ちた記念日らしいな\nぼくの情報は正しいんだ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1111001, 111101, 0, 4.9, 6, "ハロウィンですね…\nみんな仮装して、楽しそうです\n…怖いわけじゃありませんよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1111002, 111101, 0, 6.0, 6, "カボチャがいっぱいです…\nみんな顔があって、うぅ…\nな、なんで被ってるんですか！\nビックリさせないでください！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1111003, 111101, 0, 8.7, 5, "ど、どうでしょうか、この衣装…\n私は子供っぽいと思うんですけど\n…似合ってる？　そ、そうですか\nだったら…よかったです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1111004, 111101, 0, 11.5, 6, "あなたは仮装しないんですか？\nどんな衣装が似合うか、ですか？\nえっと…う～ん…吸血鬼さん？\nあ、血は吸わないでくださいね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1111005, 111101, 0, 6.0, 5, "さっきからどうして\n頭を撫でてるんですか！\nかわいいから、って…\nもう！　もう～～～っ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1111006, 111131, 0, 0.0, 1, "お菓子、ありますか？\nない？　だったらイタズラですね\nか、噛みついちゃいますから！\nえ…えーいっ！　かぷっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1111007, 111131, 0, 0.0, 1, "私は子供じゃないので言いません\n絶対に言いませんから！　え？\nお菓子、おいしいんですか？\n…トリック・オア・トリート…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1111008, 111131, 0, 0.0, 1, "私、カップケーキ作ったんです\n食べますか？　え？\nイタズラの方がいい？\nへんた～い！　もうあげません！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1111009, 111131, 0, 0.0, 1, "ハロウィン、\n一緒に…回りませんか？\n怖いんじゃありません！　もう、\nあなたはしょうがない人ですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1111010, 111131, 0, 0.0, 1, "え？　お菓子、くれるんですか？\n……甘くて、おいしいです\nあ、手についちゃった…ぺろっ\nなっ、なに見てるんですかぁっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1111011, 111101, 0, 7.1, 6, "あなたのお誕生日…これ、どうぞ\n私が作ったカップケーキです\nあっ、そんなに急いで食べたら\nポロポロこぼしちゃいますよ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1111012, 111131, 0, 0.0, 1, "お誕生日おめでとうございます\nこれ、パンプキンケーキです\nカボチャは少し苦手ですけど、\n頑張って味見もしたので…！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1112001, 111201, 0, 5.5, 6, "ハロウィンって\nいくらいたずらしても\n怒られないから、最高だよね！\nえ？　違うの？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1112002, 111201, 0, 1.6, 1, "ハッピーハロウィン！\nにいちゃんにいたずらできて\nお菓子ももらえるなんて、ミソギ\n本当にハッピーだよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1112003, 111201, 0, 4.8, 2, "トリックオアトリート！\nお菓子をくれなきゃいたずら\nするぞ！　ミソギはお菓子\nもらってもするけどね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1112004, 111201, 0, 3.1, 1, "にいちゃんはハロウィンに\nいたずらしないの？　しないなら\nミソギがにいちゃんのかわりに\nいたずらしておくね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1112005, 111201, 0, 5.1, 1, "にいちゃん見て見て！\nミソギ、こんなにたくさん\nお菓子もらっちゃった！\n一緒に食べよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1112006, 111231, 0, 0.0, 1, "ハッピーハロウィン！\nお菓子のお礼にとびっきりの\nいたずらをプレゼントだよ！\nちゃんと受け取ってね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1112007, 111231, 0, 0.0, 1, "あはははは！\nみんな驚いてる驚いてる！\nいたずらって本当に楽しいね！\nえ？　少し手加減しなきゃだめ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1112008, 111231, 0, 0.0, 1, "走り回ってたら\nお腹空いてきちゃった…\nもらったお菓子、少しだけ\nつまみ食いしちゃおうかな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1112009, 111231, 0, 0.0, 1, "にいちゃんはいっつも\nミソギと一緒にいてくれるよね！\nにいちゃんがいるからミソギは\n安心していたずらできるんだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1112010, 111231, 0, 0.0, 1, "いろんな人にいたずらするのも\n楽しいけど\nにいちゃんにいたずらするのが\n一番楽しいなー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1112011, 111201, 0, 2.7, 1, "誕生日おめでとう、にいちゃん！\nミソギからのお祝いはー、んー\nやっぱりいたずらかな！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1112012, 111231, 0, 0.0, 1, "ハッピーバースデーにいちゃん！\nいっつもミソギと遊んでくれて\nありがとう！\nこれからも一緒に遊ぼうね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1113001, 111301, 0, 4.2, 1, "お菓子をくれなきゃ\nいたずらしちゃうよ～！\nえっと…どんないたずらをすれば\nいいのかな～？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1113002, 111301, 0, 1.4, 1, "おにいちゃん♪\nハロウィンのパレードで\nぴょんぴょんってしよ～！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1113003, 111301, 0, 5.2, 2, "ハロウィンって楽しいね～\nミミも、みんなが楽しそうだと\nもっとも～っと\n楽しくなってきちゃうんだ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1113004, 111301, 0, 3.1, 5, "トリックオアトリート～！\nえへへ、おにいちゃん\nお菓子ちょうだい♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1113005, 111301, 0, 6.0, 2, "こちょこちょこちょ…えへへ～\nハロウィンのいたずらだよ～♪\nトリックオアトリート！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1113006, 111331, 0, 0.0, 1, "にこにこ笑顔でおさんぽすれば\nハロウィンの魔法がうまれるの～\nえへへ、ハッピーハロウィン♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1113007, 111331, 0, 0.0, 1, "ふぁぁ…ハロウィンって\nとっても楽しいね～！\nねえねえ、おにいちゃんも\nいっしょにパレードしよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1113008, 111331, 0, 0.0, 1, "ハッピーハロウィン♪\nおにいちゃん\nミミのハロウィンのおようふく\nにあってるかな～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1113009, 111331, 0, 0.0, 1, "おにいちゃんと\nいっしょのハロウィン\nミミず～っとおぼえてるからね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1113010, 111331, 0, 0.0, 1, "毎日がハロウィンならいいのに…\nだってだって\nそしたらミミ、おにいちゃんと\nず～っといっしょに遊べるもん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1113011, 111301, 0, 4.1, 2, "ハッピーハロ…じゃなくて\nハッピーバースデイ！\nおにいちゃん！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1113012, 111331, 0, 0.0, 1, "おにいちゃん\nお誕生日おめでとう～！\nミミね、おにいちゃんのこと\nだーいすきだよ～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1114001, 111401, 0, 2.7, 4, "ルナ、おともだちが欲しいんだ\nさみしいのは嫌いだもん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1114002, 111401, 0, 2.9, 2, "ルナ、優しい人好きだなー\nおにいさんは優しいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1114003, 111401, 0, 2.9, 4, "パパとママはここにいるよ\nでも、喋ってくれないんだ\nさみしいなぁ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1114004, 111401, 0, 3.0, 6, "おにいさんの手ってあったかい\nルナの手、冷たい？\nもっとぎゅってしたら、\nあったかくなるかな？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1114005, 111401, 0, 6.7, 5, "ルナ、もっとおっきくなるよ\nお料理もできるようになるよ\nえへへ…ルナ、いいこ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1114006, 111431, 0, 0.0, 1, "ルナね、サンドイッチ好きなんだ\nおにいさんは好き？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1114007, 111431, 0, 0.0, 1, "ルナ、お外は好きじゃないんだ\nみんな、一人じゃなくて…\nルナだけ一人だから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1114008, 111431, 0, 0.0, 1, "おにいさんは何が好き？\nルナはお人形さんと、\nサンドイッチと…\nあと、おにいさん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1114009, 111431, 0, 0.0, 1, "パパとママみたいになれたら、\nきっと楽しいと思うんだ\nだから待っててね、おにいさん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1114010, 111431, 0, 0.0, 1, "おにいさんはルナのこと好き？\nルナはおにいさんのこと好き！\nだから嫌いにならないでね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1114011, 111401, 0, 1.7, 2, "お誕生日だね！\nおにいさんが一番！\nふふっ、うれしいね！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1114012, 111431, 0, 0.0, 1, "お誕生日なの？　すごいすごい！\nルナ、お誕生日大好き！\nだからいっぱいお祝いしようね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1115001, 111501, 0, 6.1, 1, "メリークリスマス☆\nクリスティーナさんと一緒に\n愉しい聖夜を過ごそうじゃないか\n問答無用だ、こっちへ来い♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1115002, 111501, 0, 6.4, 4, "この飲み物が気になるのか？\n飲ませてやってもいいが、\n後悔はするなよ？\nなんだ、やめるのか…残念だ☆", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1115003, 111501, 0, 8.4, 1, "さぁ坊や、ワタシを愉しませろ！\n特別な日だ、くだらんことを\nしたら命はないと思え！\nまぁ、優しめに採点してやるさ♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1115004, 111501, 0, 6.1, 2, "おや、震えているじゃないか\n寒いだと？　この軟弱者め\nだったらこのコートに入るか？\nふっ、遠慮しなくていいんだぞ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1115005, 111501, 0, 3.1, 2, "クリスマスをオマエと一緒に\n過ごすとはな…\nまぁ悪い気分じゃないさ\n坊やは見ていて愉しいからな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1115006, 111531, 0, 0.0, 1, "どうだい、坊や\n聖夜に相応しい絶景だろう？\n感謝するがいいよ。ここに連れて\n来たのはオマエが初めてだ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1115007, 111531, 0, 0.0, 1, "こちらを見つめてどうした？\nいいや言わんでも分かるさ\n景色よりワタシの方が綺麗だと\n言いたいんだろう？　あっはは☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1115008, 111531, 0, 0.0, 1, "体が冷えてきたな…\nよし坊や、剣を抜け！\nこういう時は、剣と剣をぶつけて\n温まろうじゃないか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1115009, 111531, 0, 0.0, 1, "クリスマスを誰かと過ごすのも\n悪くはないな…\nそれがワタシを愉しませてくれる\n男なら、なおさらだ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1115010, 111531, 0, 0.0, 1, "愉快なクリスマスだったな\nまた次も、よろしく頼むぞ？\nふふっ、忘れるなよ？\nワタシと交わす誓いは絶対だ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1115011, 111501, 0, 6.5, 1, "ふぅん。今日が誕生日なのか\nなんだそのもの欲しそうな顔は？\nあはは、プレゼントが欲しいなら\n贈るに相応しいか示して見せろ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1115012, 111531, 0, 0.0, 1, "オマエが生まれた日を祝し\nクリスティーナさんがプレゼント\nを用意してあげたよ♪\nい～い反応を期待しているぞ☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1116001, 111601, 0, 1.7, 1, "メリークリスマス！\n私のライブがキミにとって\n最高のプレゼントに\nなったらうれしいな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1116002, 111601, 0, 4.6, 6, "チキンにケーキ…クリスマスの\nごちそうって大好き！\n今日だけは\n食べ過ぎちゃってもいいよね？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1116003, 111601, 0, 3.0, 2, "いつものクリスマスより\n楽しく感じるのは\nキミと一緒だからかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1116004, 111601, 0, 4.1, 5, "はぁ～\n今日はすごく寒いね\n…ねえ、もう少しだけ\nそっちに行ってもいいかな？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1116005, 111601, 0, 3.7, 2, "どうかな？\nこの服いい感じでしょ♪\nキミには一番かわいい私を\n見てほしいんだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1116006, 111631, 0, 0.0, 1, "みんなが笑顔になるクリスマスが\n大好きだよ！\nこの思いは最高の\nパフォーマンスでお返しするね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1116007, 111631, 0, 0.0, 1, "私のライブでクリスマスを\n盛り上げちゃうよー♪\nサンタもアイドルも夢を届ける\nお仕事だもんね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1116008, 111631, 0, 0.0, 1, "メリークリスマス！\nイルミネーションよりも\n輝いてみせるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1116009, 111631, 0, 0.0, 1, "クリスマスソングの別バージョン\nキミをイメージして作ったんだ\nちょっと恥ずかしいけど\n聴いてほしいな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1116010, 111631, 0, 0.0, 1, "これまでの人生で一番の\nクリスマスだよ！\nキミと過ごした最高の一日を\n絶対に忘れないからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1116011, 111601, 0, 1.6, 1, "ハッピーバースデー！\nはい、プレゼント\n私のサイン入りだよ\n大事にしてね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1116012, 111631, 0, 0.0, 1, "誕生日おめでとう！\nバースデーソングでお祝いするよ！\nキミだけのためのライブ、\n楽しんでってね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1117001, 111701, 0, 3.1, 1, "『聖なる夜』というのは\n気に入らんが、\nせっかくの夜の宴じゃ\n楽しませてもらおうかのう♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1117002, 111701, 0, 4.1, 5, "わらわの美貌を\n引き立てる良い衣装じゃな\n…これで、あやつを\n誘惑するのもよいかもしれぬ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1117003, 111701, 0, 3.7, 2, "今のわらわは\nサンタクロースじゃからな\n聖夜を盛り上げるために、\n協力してやろうではないか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1117004, 111701, 0, 5.2, 4, "聖夜の宴ならば、\nチキンとケーキを食べねばな♪\n…な、なんじゃその目は？\nべ、別に浮かれてなどおらぬわ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1117005, 111701, 0, 5.0, 5, "この格好でプレゼントを\n用意せぬのは無粋というもの…\nし、仕方ないから、あやつにも\nプレゼントを用意してやるか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1117006, 111731, 0, 0.0, 1, "ク、クリスマスはサンタが\nプレゼントを贈る日で、\n今のわらわはサンタだから…\nと、とにかく受け取れ馬鹿者～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1117007, 111731, 0, 0.0, 1, "アカリに借りた本によると…\nイリヤサンタが、キミに\nプレゼントをあげちゃうぞ～☆\n…できるかこんなの～～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1117008, 111731, 0, 0.0, 1, "夜を統べる者たる、このわらわが\nプレゼントをくれてやろう！\nありがたく受け取るがよい！\nうむ、これなら完璧じゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1117009, 111731, 0, 0.0, 1, "このプレゼント、あやつは\n喜んでくれるじゃろうか…\nうう…なんだか\nドキドキしてきたのじゃ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1117010, 111731, 0, 0.0, 1, "…じ、時間じゃな！\nプレゼントの準備…よし！\nこ、ここ、心の準備…よ、よし！\nあとはあやつに渡すだけじゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1117011, 111701, 0, 4.8, 2, "たしか、今日はお主の\n誕生日であったな\nならばこのわらわが、\n祝ってやろうではないか", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1117012, 111731, 0, 0.0, 1, "今日はあやつの誕生日…！\nプレゼントを渡しつつお礼を…\nえ…お主、いつからそこに…\nい、今すぐ忘れろ馬鹿者ぉ～！！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1118001, 111801, 0, 5.9, 1, "あけましておめでとうございます\n今年もたくさんおいしいものを\nいっぱい食べましょう！\n新年最初の約束です☆", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1118002, 111801, 0, 3.6, 2, "あなたはもう今年の抱負を\n決めていますか？\nわたしは…みんなが幸せに\n暮らせるような世界を、です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1118003, 111801, 0, 3.0, 2, "わたしの着物、似合ってますか？\n髪もいつもと違うんです\nもっと近くで見てください\n特別ですよ☆", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1118004, 111801, 0, 3.7, 2, "あっ！　あっちで餅つきを\nやってるみたいですね！\n行きましょう！　自分でついた\nお餅は格別ですよ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1118005, 111801, 0, 5.4, 7, "広場で凧揚げしてますよ！\n凧揚げ…たこあげ…タコ揚げ…\nなんだかお腹が空いて\nきちゃいましたね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1118006, 111831, 0, 0.0, 1, "おせち、心を込めて作りました♪\n見た目はちょっとアレかも\nしれませんが、味は保証します！\nはい、どうぞ召し上がれ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1118007, 111831, 0, 0.0, 1, "おせちには魔除けの意味も\nあるみたいなんですけど\n素材に魔物を使うのは…\n魔を倒してるからありですよね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1118008, 111831, 0, 0.0, 1, "今年もこうやってみんなで\nおいしいものをたくさん\n食べて行きたいですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1118009, 111831, 0, 0.0, 1, "こうして料理を食べるのは\nいつものことですけど、お正月を\n一緒に過ごしてるって思うと\nなんだか照れちゃいますね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1118010, 111831, 0, 0.0, 1, "毎年こうやってあなたに\n手作りおせちを食べさせたいって\n思うのは…欲張りでしょうか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1118011, 111801, 0, 7.1, 1, "誕生日おめでとうございます！\n縁起のいい食べ物をたくさん\n揃えたので一緒に食べましょう！\n魔物は…ヒミツです☆", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1118012, 111831, 0, 0.0, 1, "お誕生日おめでとうございます\n今年もこうしてあなたと過ごせて\n本当に幸せです☆\n来年もお祝いさせてくださいね？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1119001, 111901, 0, 5.2, 2, "主さま、新年あけまして\nおめでとうございます\n今年も主さまと一緒に\n歩めることを幸せに思います", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1119002, 111901, 0, 3.9, 1, "まだまだ寒い日が続きますね\n主さま、お手手を繋ぎ\n暖めあって歩くといたしましょう\nぎゅっと握ってくださいまし", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1119003, 111901, 0, 8.0, 5, "たまの里帰りもいいものですが\n主さまと離れるのは心細いです\n次の里帰りは一緒に、というのは\n欲張りでしょうか…？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1119004, 111901, 0, 4.9, 1, "甘酒には疲労回復の効果が\nあるとのことです。お疲れの際は\nわたくしがいつでもフーフーして\n飲ませて差し上げます", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1119005, 111901, 0, 6.7, 5, "主さまにお仕えするという至高の\n初夢を見ることができました\n主さまの初夢にも、わたくしが\n出ていたならいいのですが…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1119006, 111931, 0, 0.0, 1, "一つ一つの動きに、主さまへの\n想いを込めております\n拙い舞ではございますが\n見守っていてくださいまし", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1119007, 111931, 0, 0.0, 1, "わたくしの想いに反応して\n精霊たちも楽しそうに\n舞っておりますね。主さまも\nお楽しみいただけておりますか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1119008, 111931, 0, 0.0, 1, "風情のある舞台で主さまと\n二人きりで過ごせる…\nわたくしにとって最高の\nお年玉でございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1119009, 111931, 0, 0.0, 1, "今年の抱負でございますか？\nそれはもちろんこれまで通り…\nいえ、これまで以上に主さまを\nお導きすることでございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1119010, 111931, 0, 0.0, 1, "主さまを想って舞っていると\n心も体も暖かくなります\nこの熱を主さまと分け合うことが\nできればいいのに…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1119011, 111901, 0, 5.9, 5, "お誕生日おめでとうございます\nもし…もし主さまがよろしければ\n二人きりで過ごすことを\nお許しいただきたく…", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1119012, 111931, 0, 0.0, 1, "今日は主さまにとって\nおめでたい一日。祝いの舞を\n奉納させてくださいまし\n心からの祝福をお贈りいたします", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1120001, 112001, 0, 4.0, 2, "なに？　あたしと羽根つきで\n勝負したいの？\n手加減しないからね！\n墨まみれにしてあげるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1120002, 112001, 0, 4.6, 1, "あけましておめでとう\nまぁ今年もよろしくね\nまた騒がしい一年に\nなりそうだけど…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1120003, 112001, 0, 7.8, 6, "みんな年の始めから元気ね\nあたしは家で静かに過ごしたい…\nい、いや、別にあんたと居るのが\n嫌ってワケじゃないから！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1120004, 112001, 0, 5.5, 2, "着物が似合ってる？\nいちいち言わなくていいわよ！\nけど、見る目あるじゃない\nふふん。いい気分だわ♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1120005, 112001, 0, 5.9, 5, "おみくじ引いたんだけど、\n待ち人はすぐそばに、だって\nまさかあんたのこと…？\nあ、あっちいきなさい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1120006, 112031, 0, 0.0, 1, "せっかくだからいろいろ願い事\nしたいわよね。あんたは何を\n願うの？　あたしのこと？\nふ、ふーん。そ、そう…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1120007, 112031, 0, 0.0, 1, "夜の参道ってのも悪くないわね\n一緒なら怖くないし…って！\n暗いの平気だから！\n勘違いしないでよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1120008, 112031, 0, 0.0, 1, "ほら、行くわよ\n疲れたっていうなら\n手繋いでやっても…て、本当に\n繋ぐ気？　べ、別にいいけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1120009, 112031, 0, 0.0, 1, "神殿にお参りするだけなのに\n特別な気がするのは\n着物のおかげ？　それとも…\nあんたと一緒だから？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1120010, 112031, 0, 0.0, 1, "すごく静かね…まさか道間違えて\nないわよね？　まぁ、のんびり\n行くのも悪くないかもね。二人で\nゆっくり歩きましょ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1120011, 112001, 0, 5.7, 4, "ハッピーバースデー\nケーキ買ってきたから\n一緒に食べましょう\n変なモノは入ってないわよ？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1120012, 112031, 0, 0.0, 1, "お誕生日おめでとう\nい、一応プレゼントも用意して\nあるのよ？　\nありがたく受け取りなさい！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1121001, 112101, 0, 5.9, 1, "あなたもお参りに来たんですね\n私は見ての通り、巫女さんです！\n年末から新年にかけて、神殿の\nお仕事をお手伝いしてるんです", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1121002, 112101, 0, 2.7, 1, "この服、かわいいですよね♪\n巫女さんの服を着る機会なんて\n普段はありませんから\n新鮮で楽しいんです", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1121003, 112101, 0, 5.2, 2, "神殿には色んなお守りがあるので\nぜひ見ていってくださいね\n私は家内安全のお守りを\n買っておきました～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1121004, 112101, 0, 4.5, 5, "はぁ～…寒くて手が冷えますね\n…はわっ！？　あ、あのぉ…\nい、いきなり手をギュって\n握られると、恥ずかしいです…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1121005, 112101, 0, 3.4, 6, "もしよければ、甘酒を飲みます？\n…え、私と分け合いっこ！？\nあ、ああ、あの、仕事中なので！\nわわ、私は、だ、大丈夫れしゅ！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1121006, 112131, 0, 0.0, 1, "おはようございますっ！\n今日は参拝に来たんですか？\n私は神殿のお掃除中です\nほら、きれいになりましたよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1121007, 112131, 0, 0.0, 1, "もうすぐ休憩時間なので\n私もご一緒していいですか？\nこの神殿の見どころを\n色々教えて頂いたんです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1121008, 112131, 0, 0.0, 1, "それじゃあ、張り切って続きを…\nえ、危ない？　何がですか？\n…はわっ！　崩れちゃう！？\nわ、ちょ、まっ、きゃー！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1121009, 112131, 0, 0.0, 1, "恋愛運に書かれていたんですけど\n今年は「身近なところに縁あり」\nらしくて…あっ！？　い、いえ！\nべ、別に深い意味はないですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1121010, 112131, 0, 0.0, 1, "あなたの恋愛運はどうでした？\n…え？　失敗から始まる縁？\nそれってもしかして、私と…\nはっ！　な、なんでもないです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1121011, 112101, 0, 5.1, 6, "お誕生日おめでとうございます！\n私もあなたにプレゼントを…\nって、あれ…？　ない！？\nわ、忘れちゃいましたぁ…！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1121012, 112131, 0, 0.0, 1, "今日はあなたのお誕生日ですよね\nおめでとうございます！\nこれから良い一年を過ごせる\nおまじないをかけちゃいますね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1122001, 112201, 0, 6.4, 4, "へ、変身したら、\n髪型まで変わってる！？\n一体どういう理論で…はあ\nもう、考えないようにしよう…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1122002, 112201, 0, 5.9, 5, "魔法探偵ミスティ★カスミか…\nちょっとだけ楽しいかも♪\n…って、助手くん！？　\nい、今のは忘れてくれたまえ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1122003, 112201, 0, 5.1, 1, "うぅ、周りの視線が気になる…\nん…助手くん、前に立ってくれる\nのかい？　ありがたいよ\nふふ。お姫様気分だね", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1122004, 112201, 0, 3.0, 4, "ミスティ、セオリー、\nトゥルーリィ！\n…はぁ。だんだんと\n慣れてきているのが複雑だね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1122005, 112201, 0, 6.3, 1, "歩いているだけで子供に囲まれる\nなんて…魔法少女って人気なんだ\n助手くん、君も魔法少女のこと\n好きだったりするのかな？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1122006, 112231, 0, 0.0, 1, "ミスティセオリートゥルーリィ！\nマジカルリボン、スパイラル！\n魔法探偵ミスティ★カスミ\n魔法の力で捜査開始！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1122007, 112231, 0, 0.0, 1, "…や、やっぱり恥ずかしいね\nあまりジロジロ見てはダメだよ？\nこ、こら！　近い、近いったら！\nもう、これは現行犯で確保だね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1122008, 112231, 0, 0.0, 1, "体の奥からすごい力を感じるよ\nけれど、この格好で動き回ると\nその、スカートとかヒラヒラして\n落ち着かないんだよね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1122009, 112231, 0, 0.0, 1, "助手くん、どうかな？\n魔法少女らしく見えるかね？\nこんなはじけた姿、君の前でしか\n見せられないよ。まったく…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1122010, 112231, 0, 0.0, 1, "魔法少女と、パートナー\n探偵と、助手\n私たちはどうやら、離れられない\n運命のようだね。…ふふっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1122011, 112201, 0, 5.9, 2, "今日は助手くんの誕生日だよね\n魔法少女の役目を\n早く終わらせてくるから、二人で\n一緒にお祝いしようじゃないか♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1122012, 112231, 0, 0.0, 1, "…魔法の力で捜査開始！\nた、誕生日のお祝いとして、\n君のためだけに変身して\nみたんだけど…ど、どうかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1123001, 112301, 0, 0.0, 2, "私、魔法少女になったんです\n私もお姉ちゃんみたいに\nたくさん人助けできたらいいな\nなんて…えへへ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1123002, 112301, 0, 5.4, 2, "魔法少女になってから\nあまり体力のことで\n悩まなくなったんです\nなんだか出来すぎた物語みたい…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1123003, 112301, 0, 2.8, 5, "魔法少女って憧れだったんです\n見た目は、その…\nちょっと恥ずかしいですけど", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1123004, 112301, 0, 5.0, 4, "早く一人前の魔法少女に\nならなきゃって思うんですけど…\n読書の時間がなくなっちゃう…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1123005, 112301, 0, 5.0, 2, "ランドソルの街を歩いてたら\n子供たちに囲まれちゃいました\nあの子たちを守るためにも\nもっともっと頑張らなくちゃ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1123006, 112331, 0, 0.0, 1, "ピュアなハートにロックオン\nマジカルアロー、クリティカル！\n魔法狩人ピュアリー★シオリ\nあなたの心に愛の矢を！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1123007, 112331, 0, 0.0, 1, "ランドソルの平和は私が守ります\n体からあふれる愛と希望の力…\nこの一射に愛をこめて！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1123008, 112331, 0, 0.0, 1, "ここは私に任せてください！\nアンチビーストを倒して\nみなさんに笑顔を取り戻します！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1123009, 112331, 0, 0.0, 1, "魔法少女になったおかげで、少し\n自信がついたかもしれません\nこの街を守る覚悟ができました\nあなたのことも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1123010, 112331, 0, 0.0, 1, "アンチビーストが街を襲うかぎり\n魔法少女は戦い続けます\nそばにいてくださいね…あなたの\n存在が、私の力になりますから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1123011, 112301, 0, 0.0, 2, "今日、お誕生日ですよね\nふふ、ちゃんと覚えてますよ\n魔法で盛大にお祝いしなきゃ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1123012, 112331, 0, 0.0, 1, "お誕生日おめでとうございます\n今後もお祝いさせてくださいね\nこれからも、ずっと…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1124001, 112401, 0, 3.8, 2, "千里の道も一歩から、ですよね\n素敵なアイドルを目指して\nレッスン頑張ります！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1124002, 112401, 0, 4.6, 4, "冒険者としての実力、ですか？\n実はあんまり自信がないんです\n運動神経は普通ですし\nそこまで活躍は…うぅ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1124003, 112401, 0, 2.6, 1, "今からダンスの練習なんです！\nどこかおかしいところがないか\n見てもらってもいいですか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1124004, 112401, 0, 9.6, 2, "鼻歌を口ずさむ時、たまに…\nすごく懐かしい、大切な曲を\n歌ってる気持ちになるんです\n不思議ですよね、えへへ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1124005, 112401, 0, 3.0, 4, "やぁー！　とぉー！\nう～ん、剣を振る時って\nもっと頭から叩くように\nする方がいいんでしょうか…？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1124006, 112431, 0, 0.0, 1, "これ、夢じゃないんですよね…？\n私にこんな力があったなんて…\nまるで、魔法をかけられた\nおとぎ話の主人公みたいです…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1124007, 112431, 0, 0.0, 1, "魔法でお姫様に変身…じゃなくて\nなんだか勇者になったみたいです\nでも、自分の力で切り開くって\n意味では、ピッタリかもですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1124008, 112431, 0, 0.0, 1, "私は、普通の女の子ですけど…\n自分らしい、私だけのキラキラが\nあるんだって胸を張って言えます\nだって私は、アイドルですから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1124009, 112431, 0, 0.0, 1, "これ、伝説の剣を召喚！\nみたいにできたりしませんかね？\n念じたらもしかして…う～ん…！\n…で、できないみたいです…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1124010, 112431, 0, 0.0, 1, "そうだ！　私もリンちゃんみたいな\n必殺技を使うのはどうでしょう？\nそうですね～…勇気！　元気！\nピンキー・ライブリィ！　…とか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1124011, 112401, 0, 4.7, 2, "みなさんから、今日があなたの\nお誕生日だって聞きました\nおめでとうございます！\nきっといい一年になりますよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1124012, 112431, 0, 0.0, 1, "お誕生日おめでとうございます！\n色んなことでお世話になったので\nそのお礼も兼ねたプレゼントです\nどうぞ、受け取ってください♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1125001, 112501, 0, 5.4, 1, "アイドルに向いてるのかどうか\n自分じゃよく分からないけど…\nやると決めたからには頑張るよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1125002, 112501, 0, 4.9, 6, "冒険者としての腕なら\n結構自信があるんだけどね\nアイドルって全然違うことだし\n今後どうなるか想像できないな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1125003, 112501, 0, 2.8, 2, "アイドルは体力も必要…\nなら、剣の素振りでもすれば\n冒険者としての修行にもなるよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1125004, 112501, 0, 11.0, 3, "ラズール・レオ！　…違うかな？\nもっとこう、相手を貫くように…\nもう一回！　ラズール・レオ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1125005, 112501, 0, 6.7, 2, "ちょっと疲れたし休もうかな…\nキミもチョコレート食べる？\n甘いものを食べると\n元気が出てくるよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1125006, 112531, 0, 0.0, 1, "誰が相手だろうと退かないよ\n私は立ち止まらないって決めた…\nだから、倒して先に進むだけ！\n蒼の剣を受けよ！　はぁーっ！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1125007, 112531, 0, 0.0, 1, "私と戦ったのが失敗だったね\n見た目で油断したんだろうけど…\nこう見えても私、結構強いんだ\n自分の甘い判断を恨むんだね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1125008, 112531, 0, 0.0, 1, "…アイドルを目指してるのに\nこんなことしてて大丈夫かな？\nえ、それはそれで需要がある？\n本当かな、だといいんだけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1125009, 112531, 0, 0.0, 1, "そろそろ、何か新しい魔法を\n考えてみるのもアリかもね\n名前は…ラピス、ターコイズ…\nうーん、どういうのがいいかな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1125010, 112531, 0, 0.0, 1, "呪文を唱えてる理由が知りたい？\nなんていうんだろう、魔法の形を\nイメージしやすくなるんだよね\n…それにほら、カッコいいでしょ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1125011, 112501, 0, 7.5, 1, "今日、キミの誕生日なんだって？\nみんなから聞いたよ、おめでとう\n今からお祝いするって言ってたし\n一緒に行こうか", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1125012, 112531, 0, 0.0, 1, "今日はキミの誕生日だよね\nはいこれ、プレゼント\nあんまり大した物じゃないけど…\nお世話になったお礼ってことで", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1126001, 112601, 0, 4.8, 2, "冒険とアイドル、どっちも\n全力投球で頑張っちゃうよ！\nミオちゃんの応援よろしく～☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1126002, 112601, 0, 5.9, 1, "ほらほら、早く出かけようよ！\nワクワクの冒険が待ってるぞ～！\nあ、その後はレッスンするから\nちゃーんと手伝ってね☆", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1126003, 112601, 0, 2.6, 1, "よっ、ほっ…！\nえへへ、自主練やってるんだー\n最高のアイドルに少しでも\n近づけるように、頑張らなきゃ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1126004, 112601, 0, 9.0, 6, "大きな声で挨拶するのは\nアイドルの基本中の基本！\nせーの、おいっす！\nこらこら声が小さいぞ～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1126005, 112601, 0, 6.6, 4, "魔物料理って結構おいしいのに\nあんまり一般的じゃないよね～\n確かに最初は勇気がいるけど\n食べたらアリなのになぁ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1126006, 112631, 0, 0.0, 1, "さぁ、歌って踊れるアイドル\nミオちゃんのショーが始まるよ！\nキラキラ輝くこの魔法で、\nみんなをファンにしちゃうから☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1126007, 112631, 0, 0.0, 1, "踊りながら魔法を使ってると\nテンションが上がって楽しいの！\nこういうところでもアイドルの\n経験は活かさなくっちゃね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1126008, 112631, 0, 0.0, 1, "この魔法陣、キレイでしょ？\nたくさん描くと星も増えていって\n流星がきらめく感じになるの\n私も気に入ってるんだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1126009, 112631, 0, 0.0, 1, "そうだ！　ミオちゃんがキミの\nリクエストに答えてあげよう！\nどんな魔法陣を描いてほしい？\nパパっとやってみちゃうよ～☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1126010, 112631, 0, 0.0, 1, "笑顔はパワーのみなもと☆\n大変なことはたくさんあるけど\nいつでも笑顔を忘れない\nそれがミオちゃんなのだー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1126011, 112601, 0, 5.7, 1, "パンパカパーン、おめでとー！\n今日はキミの誕生日なんでしょ？\nほら、おめでたい日なんだし\nみんなでレッツパーリーだよっ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1126012, 112631, 0, 0.0, 1, "誕生日、おっめでと～！\nまた一つ大人になったキミへ\nミオちゃんからのプレゼント！\nちゃーんと大事にしてよね☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1127001, 112701, 0, 7.4, 4, "何の因果か労働に勤しむあたし\nま、ほどほどにがんばるよ\nあ～帰りたい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1127002, 112701, 0, 3.5, 5, "働いた後のあんパンは美味しいね\nまあ、働いてなくても\n美味しいんだけど", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1127003, 112701, 0, 3.6, 4, "ねえ、次の休みはいつ？\nげぇ～、マジで…\n働き過ぎて死んじゃうよ～", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1127004, 112701, 0, 5.9, 2, "働き出してからもあんたには\nなにかと助けられっぱなしだね\n気付いてないかもだけど\n結構頼りにしてるんだよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1127005, 112701, 0, 5.1, 5, "仕事も遊びも、気付けば\nいつもあんたと一緒だね\nそばに誰かいてくれるって\n案外いいもんだね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1127006, 112731, 0, 0.0, 1, "森の中はあたしの独壇場！\nリスの怖さを見せてあげるよ\nさあ、疲れる前に終わらすよ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1127007, 112731, 0, 0.0, 1, "ん～？　魔物退治のお仕事？\nはいはい、さくっとやっちゃうよ\nあんたは勝利のあんパンの\n用意でもしといてね～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1127008, 112731, 0, 0.0, 1, "あたし、なかなかキマってない？\nカッコよくない？\nまた人気出ちゃうよ～\n困るな～マジで～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1127009, 112731, 0, 0.0, 1, "戦いに慣れてきたとはいえ\nやっぱりあんたが隣にいると\n心強いし、がんばれるよ\n…あんがとね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1127010, 112731, 0, 0.0, 1, "色々あってあたしの日常は\n変わっちゃったけど、あんたは\n変わらずにいてくれるんだね\nそういうの、結構うれしいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1127011, 112701, 0, 6.6, 7, "え、あんた今日誕生日なの？\n忙しくて用意できてないよ…\nと、とりあえず今はこの\nキレイなドングリで！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1127012, 112731, 0, 0.0, 1, "誕生日おめでと～。ゲームと\nあんパンでお祝いするよ～\nさあ、ゴロゴロしながら思い切り\n楽しもうね～", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1128001, 112801, 0, 3.0, 3, "今のオラは牧場の守護神だべ！\nみんなには指一本ふれさせねぇ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1128002, 112801, 0, 3.3, 1, "強くなると農作業もはかどるべ\n戦いは好きじゃねぇけど\n力も使い方次第で人を\n幸せにできるべさ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1128003, 112801, 0, 6.3, 2, "この格好、子供たちに\nウケがいいべ！　ネタでとった\n笑いじゃなくても\nやっぱ笑顔はうれしいべさ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1128004, 112801, 0, 5.4, 5, "よ、鎧姿が珍しいからって\nそんなにじっと見られると\nは、恥ずかしいべ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1128005, 112801, 0, 7.4, 6, "オラたちお笑いだけじゃなくて\n戦いも息ピッタリだべ！\nもういっそ婿に来て…\nな、なんでもねぇべさ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1128006, 112831, 0, 0.0, 1, "見た目で判断すると\nいてぇ目にあうど？\n体はちっこいけど\n心に秘めた愛は無限大だべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1128007, 112831, 0, 0.0, 1, "今だけは笑い無しだべ！\n牧場の平和を乱すヤツは\nオラの突きで月まで\n吹っ飛ばすべよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1128008, 112831, 0, 0.0, 1, "日頃の労働のおかげか\n戦いでも案外動けるもんだべな\nよーし、このまま一気に\n終わらせちまうべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1128009, 112831, 0, 0.0, 1, "本当は戦いなんて怖くて\n仕方ねぇべ。でも隣に\nあんちゃんがいてくれんなら\nオラはいくらでも強くなれんべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1128010, 112831, 0, 0.0, 1, "あんま強くなると、嫁の貰い手が\nなくなっちまうな\nそ、そん時はあんちゃんに…\nや、やっぱり何でもねぇべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1128011, 112801, 0, 5.2, 2, "お誕生日おめでとうだべ！\nケーキも料理も用意してっけど\nまずは搾りたてのミルクで\n乾杯だべさ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1128012, 112831, 0, 0.0, 1, "よく来たなあんちゃん！\n今日は牧場を貸し切りで\nパーティーだべ！\nいっぱい楽しんでくんろ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1129001, 112901, 0, 8.7, 6, "この服を着てると、自分が\n物語の主人公になったみたいです\n馬子も大爆笑、ってやつですね！\n…え？　何か間違えてましたか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1129002, 112901, 0, 9.5, 4, "いいなぁ～、このお洋服…\n見れば見るほどよくできてる\nちょっと脱いで仕立ての確認を～\nってお兄ちゃん、見ちゃダメ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1129003, 112901, 0, 7.7, 6, "おっとと、危ない危ない\nいつもみたいにぴょんぴょん\n動き回るとスカートがふわ～って\n…誰にも見られてませんよね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1129004, 112901, 0, 5.6, 5, "いつもと違うお洋服…\nお兄ちゃん、褒めてくれるかな？\nかわいいね、なんて言われたら\n私…ドキドキしちゃいますよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1129005, 112901, 0, 4.7, 2, "ちょっと動かないでくださいね～\n何してるのって？　えへへ、\nお兄ちゃんのサイズを測って\nおそろいの衣装を作るんですよ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1129006, 112931, 0, 0.0, 1, "私、物語のヒロインみたいです♪\nもちろんヒーローはお兄ちゃん！\n私がピンチの時は、\nかっこよく助けてくださいね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1129007, 112931, 0, 0.0, 1, "いつもお兄ちゃんの後ろに隠れて\nばかりだった私はもういません！\nこれからはお兄ちゃんの隣で…\n一緒に、歩いていきますからね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1129008, 112931, 0, 0.0, 1, "お兄ちゃん見てくださいよ！\nこの服、とってもかわいいです！\nえ？　そうだね、って…\nて、照れちゃいますよ…えへへ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1129009, 112931, 0, 0.0, 1, "この服のおかげなのか、\nとびきり調子がいい気がします！\nバッチリ狙いをつけますよ～\n目標はお兄ちゃんのハートです♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1129010, 112931, 0, 0.0, 1, "こ～んなかわいい格好で\nアプローチしたら、お兄ちゃんも\nドキドキしますかね…よぉしっ！\n勇気を出してっ、お兄ちゃ～ん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1129011, 112901, 0, 4.9, 2, "今日はお兄ちゃんの誕生日！\nおめでとうございます！\nえへへ、またお兄ちゃんが\n格好よくなっちゃいましたね…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1129012, 112931, 0, 0.0, 1, "ハッピーバースデーお兄ちゃん！\nお誕生日ケーキもバッチリです！\nあとは…えっと、その…うんっ！\n私が食べさせてあげちゃいます！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1130001, 113001, 0, 3.7, 5, "この服…\n本物のウサギさんみたいで、\n私にはかわいすぎだよぉ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1130002, 113001, 0, 5.0, 6, "…ウ、ウサギさんだぴょん♪\nなんちゃって…え？\nせ、先輩、いつからそこに…\n今のは忘れてくださいぃぃ…！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1130003, 113001, 0, 9.3, 5, "ウ、ウサギさんは幸運の象徴\nですので、私が先輩のそばに\nずっと居れば…その…や、\nやっぱりなんでもないですぅ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1130004, 113001, 0, 4.1, 2, "なんだかいつもより体が\n軽いような…これも、\nアリスちゃんの力が宿った、\nこの服のおかげかな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1130005, 113001, 0, 11.7, 5, "アリスちゃんの力で強くなった\n今の私なら、先輩を誘えるかも！\nせ、先輩！　一緒にご飯でも…\nや、やっぱりまだ無理ぃぃ！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1130006, 113031, 0, 0.0, 1, "ま、魔物と戦うのは怖いけど、\n私だって、\nアリスちゃんの力を受けた一人！\nやるときはやるんですぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1130007, 113031, 0, 0.0, 1, "体が軽い…まるで、本物の\nウサギさんになったみたい…\nえへへ、今の私なら、\n先輩に褒めてもらえるかも♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1130008, 113031, 0, 0.0, 1, "せ、先輩には\n指一本触れさせません！\n私が守りまひゅ…！？\nうう…またかんじゃったよぉ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1130009, 113031, 0, 0.0, 1, "せ、先輩たちに\n頼ってばっかりじゃダメ！\n私だって頑張らないと！\nファイトよアユミぃぃぃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1130010, 113031, 0, 0.0, 1, "時間を操作する力…\nこの力で、ずっと先輩を後ろから\n見て…じゃ、じゃなくてぇ！\nみんなの役に立ってみせますぅ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1130011, 113001, 0, 8.0, 4, "せ、先輩！！\nえっと…その、お、お誕生日、\nおめでとうごじゃいまひゅ！\nうう、またかんじゃったよぉ…", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1130012, 113031, 0, 0.0, 1, "お誕生日おめでとうございます！\nこれ、プレゼントですぅ！\n…つ、ついに、\nかまないでちゃんと言えました♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1131001, 113101, 0, 9.2, 2, "風が通り抜けて風鈴が鳴ると\nああ、今年も夏が来たなぁ…\nって感じたりするじゃないか\nあの空気が、好きなんだよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1131002, 113101, 0, 5.6, 5, "こういうヒラヒラした格好は\nあんまりしないんだよねぇ…\nいつもと勝手が違うから\nちょっと気恥ずかしいね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1131003, 113101, 0, 5.9, 2, "お前さん、スイカを食べるとき\n何かをかけたりする派かい？\nアタシはそのまま食べるのが\n好きなんだよね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1131004, 113101, 0, 3.7, 2, "お前さん、夏の支度は万全かい？\nアタシはこの通りさ\n一緒にバカンスってやつを\n楽しむとしようか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1131005, 113101, 0, 4.2, 1, "こう暑いと、かき氷が食べたいね\nお前さんも一緒にどうだい？\n色んな種類があるみたいだし\n別々の味を買おうじゃないか", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1131006, 113131, 0, 0.0, 1, "潮風を感じたり波音を聞いたり…\n普段は忙しくて、こんな時間\n取れたりしないからね\n夏を満喫しようじゃないか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1131007, 113131, 0, 0.0, 1, "もし急ぎの用事がないんなら\nここでゆっくりしていきなよ\n何をするわけでもないけど\nたまにはこういうのも、ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1131008, 113131, 0, 0.0, 1, "おや、お前さん。散歩かい？\nアタシはのんびりしてたとこだよ\nきれいな海を眺めながら\nゆっくりするのは一番の贅沢さ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1131009, 113131, 0, 0.0, 1, "お前さんと一緒にいるとさ\nなんていうか安心できるんだよね\n自然体の自分になれるというか\n肩肘を張らなくていいんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1131010, 113131, 0, 0.0, 1, "これからもお前さんと\n一緒に居られたらうれしいよ\n…ふふ、なんだいその顔は\n相変わらず読めないね、まったく", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1131011, 113101, 0, 7.7, 2, "誕生日おめでとう\nあんまり大したものじゃないけど\nお祝いの品を用意しといたよ\nほら、受け取っておくれ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1131012, 113131, 0, 0.0, 1, "今日はお前さんの誕生日だよね\nお祝いとして、アタシの手料理を\nたくさん用意しておいたんだよ\nさ、向こうで一緒に食べようか", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1132001, 113201, 0, 5.4, 3, "我が真名を勝手に晒しているとは\nなんという装束なのだ…\nこ、こら、シグルドよ！\nあまり胸元を見るんじゃない！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1132002, 113201, 0, 0.0, 2, "はーっはっはっは！\n我ら盟友の絆、この海でも\n変わることはないぞ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1132003, 113201, 0, 2.4, 1, "マフラーをしている理由？\nふっ、カッコいいからに\n決まっているではないか！\n他の理由などあるわけがない！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1132004, 113201, 0, 13.1, 3, "な、なぁ、シグルドよ\n貴様は私の水着を見てどう思う？\n…似合ってる？　いや、もっとこう\nかわいいとか…な、なんでもない", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1132005, 113201, 0, 2.3, 1, "日焼け止めはちゃんと塗ったか？\nもしまだ塗っていないなら\n私が貴様に塗ってやろう！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1132006, 113231, 0, 0.0, 1, "シグルドよ、何をしているのだ？\n今こそ日光の力を集める機会\n我らの力を高めるためにも\nバカンスを満喫するのだ～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1132007, 113231, 0, 0.0, 1, "ふふふ。海に来たことで私は\n新たな力を得ることができた！\nこの強さを目に焼き付けるがいい\n行くぞ！　アターック！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1132008, 113231, 0, 0.0, 1, "暑さが辛くないのか、だと？\nふっ。私を誰だと思っている…\n我が闇の力をもってすれば\n夏の日差しなど敵ではない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1132009, 113231, 0, 0.0, 1, "最初は、この水着も\n恥ずかしいと思っていたのだが…\n貴様との絆の証だからな\n別の水着など考えられんな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1132010, 113231, 0, 0.0, 1, "シグルド、貴様と過ごす夏は\n最高に愉快だ！\n来年もまた来よう！\nその次もまた一緒だ、いいな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1132011, 113201, 0, 5.8, 1, "誕生日、おめでとう！\n今日は私から闇の祝福を与えよう\nこの力があれば貴様には様々な\n幸運が訪れる。感謝するがよい！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1132012, 113231, 0, 0.0, 1, "今日は貴様の誕生日か…\nよし、これをプレゼントしよう！\n私が考えた貴様の名乗り口上だ\nさぁ、今から練習をしてみるぞ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1133001, 113301, 0, 6.4, 2, "どうどう？　この水着！\nいつもの魔法少女とは違う\n新たなナナカちゃんの魅力、\n君に教えてあげようじゃないか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1133002, 113301, 0, 3.1, 2, "こういうダンサー風の\n魔法少女もいいでしょ？\nぐふふ、妄想が捗りますな～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1133003, 113301, 0, 6.0, 1, "ミステリアスな水着美少女\n彼女の正体は、敵か味方か…？\nみたいな登場しそうじゃない！？\n物語中盤で仲間になる系だよ！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1133004, 113301, 0, 6.2, 6, "この格好、君もちょっとくらい\nドキッとしちゃうんじゃな～い？\n…ここまでアピールしてるのに\n顔が普通なの、逆にすごいわ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1133005, 113301, 0, 8.0, 5, "も～、ちょっとはナナカちゃんを\n意識したっていいのにな～\nえ？　どうしてなのかって…\nさぁ、どうしてだろうね～♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1133006, 113331, 0, 0.0, 1, "え！？　そのビンなにって…\nここ、これは君との夏の思い出を\n海に流してみようかなって…\nなにを書いたのかは絶対に秘密！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1133007, 113331, 0, 0.0, 1, "もし、無人島に流されちゃっても\n君と一緒だったら\n助けなんか来なくてもいいな…\nって、ナシナシ！　今のナシ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1133008, 113331, 0, 0.0, 1, "一度しか言わないよ？\n…ナナカちゃんの水着姿を\nじっくり見ていいのは\n君だけだからね…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1133009, 113331, 0, 0.0, 1, "ちょ、ちょっと～、そんな真顔に\nなられるとさ…えと、その…\nドキっとしちゃうよ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1133010, 113331, 0, 0.0, 1, "ギャルゲーなら照れた反応も\n王道のリアクションだけど…\n君が相手だと、なんでだろ…\n上手にできないね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1133011, 113301, 0, 6.3, 1, "おたおめ～♪\nというわけで、君にはこの本を\nプレゼントしちゃいまーす！\n私のオススメ、選んでおいたぞ☆", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1133012, 113331, 0, 0.0, 1, "誕生日おめでとーっ！\nさぁ、ナナカちゃんお手製の\n変身グッズをプレゼントだよん☆\nこれで君も魔法少女になるのだ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1134001, 113401, 0, 2.2, 1, "ファイトファイト～♪\nキミが元気いっぱい\nがんばれるように\n私がいっぱい応援するよ！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1134002, 113401, 0, 2.4, 1, "今日もお疲れさま！\nいっぱいがんばった\n後は、ゆっくり休憩して\n疲れを取らなきゃだよね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1134003, 113401, 0, 6.5, 6, "動きやすいしかわいいし\nこの水着、と～っても\nいい感じだね！\nえ？　キミも着てみたいの？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1134004, 113401, 0, 5.1, 2, "キミのこと、他の誰よりも\nおっきな声で応援するね！\nどんな時でも、ちゃんとキミに\n聞こえるように！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1134005, 113401, 0, 6.2, 5, "全力でがんばってるキミを\n見てたら、なんだかドキドキ\nしちゃった…張り切って\n応援しすぎたからかな…？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1134006, 113431, 0, 0.0, 1, "きらーん☆\nこれがハツネちゃんの本気だよ！\nキミが元気にがんばれるように\n私もいっぱい応援するからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1134007, 113431, 0, 0.0, 1, "潮風を感じながら\n体を動かすのって、\nすっごく気持ちいいね！\nキミも一緒に、やってみようよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1134008, 113431, 0, 0.0, 1, "超能力を使っちゃったから、\nちょっと眠くなってきちゃった…\nけど…がんばるキミのために…！\n私もがんばるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1134009, 113431, 0, 0.0, 1, "諦めないでがんばるキミって\nすっごくかっこいいかも…\nあぁ～っ、ごめんなんでもない！\nひとりごとだから気にしないで！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1134010, 113431, 0, 0.0, 1, "キミががんばり続ける限り\n私もずっと応援してるよ！\nいつでもそばにいるからね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1134011, 113401, 0, 7.5, 7, "今日ってキミのお誕生日だよね！\nえへへ、忘れるわけないよ～！\nパーティの準備もばっちりだし、\nいっぱい楽しく過ごそうね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1134012, 113431, 0, 0.0, 1, "ハッピーバースデー☆\nお祝いに、今日は朝から晩まで、\nキミをずっと応援するのだ～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1135001, 113501, 0, 3.8, 6, "よしよし。今日も頑張ったわね♪\nあら、顔が真っ赤ね？\nいつもより恥ずかしい？\nん～？　どうしてかしら…？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1135002, 113501, 0, 9.9, 2, "水分補給はちゃんとしてる？\n海はとっても楽しいけれど\n体調には気をつけないとね\nはい、先生特製のドリンクよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1135003, 113501, 0, 8.9, 6, "さ、次はお洗濯しなきゃ…\nえ？　手伝ってくれるの？\nありがとう、助かるわ♪\nあっ！　そっちの洗濯物は私の…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1135004, 113501, 0, 11.6, 2, "あなたの体、引き締まってるわね\n男の子って感じで格好いいわ♪\nもう子ども扱いできないかも…\nなんて、ね？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1135005, 113501, 0, 8.3, 2, "二人で波打ち際を歩いてみない？\n…ロマンチックな雰囲気を\n味わってみたくて…\nふふ、子どもっぽかったかしら？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1135006, 113531, 0, 0.0, 1, "みんなー、ファイトよーっ！\nふふ、先生の応援どうかしら？\n元気になったのなら、うれしいわ\nさぁ、もうひと頑張りしましょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1135007, 113531, 0, 0.0, 1, "夏の海で体を動かすのって\nとても気持ちがいいのね\n応援だけじゃなくて、先生も\n一緒に参加しちゃおうかしら？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1135008, 113531, 0, 0.0, 1, "水着なんて森では着ないから\nちょっとだけ恥ずかしいわ\nやっぱり肌を出し過ぎてる気が…\n…もう、あまり見つめちゃダメよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1135009, 113531, 0, 0.0, 1, "ね、海の楽しみ方…\n先生に教えてもらえないかしら？\nできれば、あなたと一緒に\n夏を満喫したいなって…ふふ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1135010, 113531, 0, 0.0, 1, "うふふ、青春っていいわね～\nあら、先生も青春しようって？\nそうね、あなたとなら…\n甘酸っぱい青春、できそうね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1135011, 113501, 0, 8.7, 2, "なんだかソワソワしてるわね？\nそれもそうよね、今日はあなたの\nお誕生日だもの。先生と一緒に\nいっぱいお祝いしちゃいましょ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1135012, 113531, 0, 0.0, 1, "今日はあなたのお誕生日ね！\nずっと頑張ってきたいい子には\n先生から特別なお祝いよ♪\nほら、いい子いい子…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1136001, 113601, 0, 1.9, 5, "どうしたんだい？\n…そんなにじっくりと見られると\n恥ずかしいじゃないか…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1136002, 113601, 0, 5.7, 2, "直接日差しを浴びるのは\n鎧越しとは違った暑さだね…\nこれはこれでいいものだよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1136003, 113601, 0, 3.8, 2, "思い切り身体を動かせるのも\nいいものだね…\nこれなら存分に泳げるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1136004, 113601, 0, 3.0, 2, "いつもより無防備な格好だが…\n君がそばにいてくれるおかげで\n心強いよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1136005, 113601, 0, 1.7, 2, "こうしていると\nいつもより君を近くに\n感じられる気がするよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1136006, 113631, 0, 0.0, 1, "どうしたんだい少年\nそんなにじっと見て…\nみ、見ないでくれとは言わないが\nいつもとは違った恥ずかしさだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1136007, 113631, 0, 0.0, 1, "日差しが強くなってきたね\n熱中症になると危ないし…\nき、君さえよければだが、\n一緒にパラソルに入らないかい？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1136008, 113631, 0, 0.0, 1, "君の前でならありのままの\n自分でいられる気がするよ\n…どうしてだろうね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1136009, 113631, 0, 0.0, 1, "もう少しゆっくりしていこうか\nたまにはこういうのもいいだろう\nここには君と私しか\nいないのだから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1136010, 113631, 0, 0.0, 1, "夏は人を大胆にする、なんて\n信じてはいなかったのだけれど…\n今なら分かる気がするよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1136011, 113601, 0, 7.1, 2, "今日は君の誕生日だったね\nこの格好では大した祝福も\nできないが…祝えてうれしいよ\n誕生日おめでとう", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1136012, 113631, 0, 0.0, 1, "誕生日おめでとう、少年\n…そんなにじっと顔を見られると\nどうにも照れてしまうが…\n君が望むなら、受け入れるさ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1137001, 113701, 0, 10.6, 5, "お兄ちゃん、見てください！\nエンジェルの服、どうですか？\nアカリ…かわいいですか？\nやんっ、見つめすぎですよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1137002, 113701, 0, 6.5, 5, "このたくさんのハートの飾り\nアカリの想いが詰まってるんです\nどんな想いか分かりますか？\nねぇ、お兄ちゃん…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1137003, 113701, 0, 4.5, 1, "アカリ、エンジェルみたいに\nみんなを幸せにしたいです！\nお兄ちゃん、特訓してください！\nビシバシ、しごいてくださいね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1137004, 113701, 0, 7.2, 1, "エンジェルの力で、サックスが\n上手になったりは…しませんよね\nううん、自分で努力してこそです\nアカリ…もっと頑張ります！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1137005, 113701, 0, 6.1, 5, "お兄ちゃん、アカリなんだか\nドキドキしてます…\nお兄ちゃんの愛の力を\n感じてるから、ですかね♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1137006, 113731, 0, 0.0, 1, "お兄ちゃん、アカリの愛の力\n受け止めてくださいね！\nアカリしか見えなくなるくらい\nメロメロにしちゃいますからっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1137007, 113731, 0, 0.0, 1, "フワフワな羽が気になりますか？\nどうぞ…触ってもいいですよ\nでも、優しくしてくださいね…？\n敏感なんですから…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1137008, 113731, 0, 0.0, 1, "この格好、お姉ちゃんとお揃いで\nとってもかわいいですよね？\nアカリ、お気に入りなんです\nお姉ちゃんは照れてますけど…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1137009, 113731, 0, 0.0, 1, "お兄ちゃん、元気ないんですか？\nだったらアカリに任せてください\n頭をナデナデしてあげますね…\nよしよし、元気になぁれ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1137010, 113731, 0, 0.0, 1, "えへへ、アカリの大切な想い\nお兄ちゃんに届きましたか？\nそれとも、もう一回…しますか？\nもっとすごいのあげちゃいます♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1137011, 113701, 0, 4.2, 5, "お兄ちゃん、お誕生日\nおめでとうございますっ！\nなんだかアカリもうれしくて\nウズウズしてきちゃいます♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1137012, 113731, 0, 0.0, 1, "お兄ちゃん、お誕生日の\nお祝いしちゃいますよ！\nプレゼントはア・カ・リ♪\nな～んて、ドキドキしましたか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1138001, 113801, 0, 5.5, 1, "さて、今日もエンジェルとして\n困っている人を助けに行かないと\nもちろん、アンタにも\n付き合ってもらうわよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1138002, 113801, 0, 2.5, 2, "エンジェルらしさ…\nあなたの願いを\nかなえて差し上げます☆\nこ、こんな感じかな…？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1138003, 113801, 0, 6.4, 6, "うう…やっぱりこういう格好は、\n私には似合わな…え？\nか、かわいいって…ば、ばかぁ！\nそういう恥ずかしいこと言うな！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1138004, 113801, 0, 8.2, 6, "困ってる人に話しかける…\n人見知りの私には難しいかも\nま、まあ、アンタも\n手伝ってくれるし、頑張るけどね", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1138005, 113801, 0, 4.6, 1, "エンジェルとして困ってる人を\n救うかぁ…これがきっかけで、\nちゃんと人と\n話せるようになれるといいな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1138006, 113831, 0, 0.0, 1, "こ、こういうかわいいのって\nアカリの得意分野なのに…\nでも、今なら私だって…\nし、しっかり見てなさいよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1138007, 113831, 0, 0.0, 1, "手の位置とかこのへんで\n姿勢ももう少しこう…\nな、何よ、やると決めたからには\n完璧を目指すわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1138008, 113831, 0, 0.0, 1, "何か子供たちがこのポーズの\nまねしてくれてるのよね\nちょっと気恥ずかしいけど\n悪くない気分ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1138009, 113831, 0, 0.0, 1, "みんなの役に立つのって\n想像以上に大変なことだわ\nアンタはそれをずっとしてたのね\n…ちょっと見直したわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1138010, 113831, 0, 0.0, 1, "ア、アンタにはいつも助けられて\nばかりだから、アンタだけに\n特別なポーズ、見せてあげるわ\nこ、光栄に思いなさい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1138011, 113801, 0, 8.0, 6, "ア、アンタの誕生日とかどうでも\nいいんだけど、一応お祝いは\nしてあげるわ\nエ、エンジェルとしてね！？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1138012, 113831, 0, 0.0, 1, "た、誕生日おめでとう。今日は\nたまたまエンジェルの仕事が\n休みだから、私が祝ってあげるわ\nか、感謝しなさいよね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1139001, 113901, 0, 5.7, 3, "騎士さん、ハロウィンですね…\n少し付き合ってほしいんですけど\nかっ、勘違いしないでください！\n仕事の手伝いですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1139002, 113901, 0, 6.7, 1, "なんですか、ジロジロ見て…\nもしかして騎士さんも仮装\nしたいんですか？　なら…\n仕方ないので作ってあげますよ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1139003, 113901, 0, 8.4, 2, "それっ！　えーいっ！\n何してるのって、糸で縛る練習に\n決まってるじゃないですか\n標的はもちろん…分かりますね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1139004, 113901, 0, 9.1, 5, "ハロウィンと言えば仮装ですよね\nどうですか、私の衣装は？\n力作なのでどうぞ褒めてください\nかわいい？　…で、でしょうっ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1139005, 113901, 0, 3.4, 1, "よし、お菓子の準備はオーケー♪\n騎士さんのことだから、お菓子を\n欲しがるに違いありませんからね\n手作りだけど…それは秘密です！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1139006, 113931, 0, 0.0, 1, "えっと、騎士さん\nどうせ暇ですよね？\nなら私と一緒にハロウィンを…\nもうっ、いいから行きますよっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1139007, 113931, 0, 0.0, 1, "ふふ、きれいでしょこの飾り付け♪\nん？　どこを見てるんです？\n…わ、私じゃなくて飾り付けを\n見てください！　もうっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1139008, 113931, 0, 0.0, 1, "騎士さん、覚悟してください！\n今の私はこわ～い吸血鬼です！\nガブッと噛みついて、えっと…\nやっぱいいです！　今のナシで！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1139009, 113931, 0, 0.0, 1, "色んな仮装を見てると\n自分でも作りたくなってきますね\n騎士さんはどんなのが好きです？\n…参考に聞いてるだけですからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1139010, 113931, 0, 0.0, 1, "騎士さんと二人でハロウィン…\n周りからどう見えるんだろう…？\nまぁ、どうだっていいですけど！\n私は別に、気にしませんよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1139011, 113901, 0, 9.1, 5, "…ついさっき思い出しましたけど\n今日は騎士さんの誕生日でしたね\nはいこれ、誕生日プレゼントです\nあっ…これは今買ったんですよ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1139012, 113931, 0, 0.0, 1, "えっと、騎士さん\n誕生日おめでとうございます！\n吸血鬼からの誕生日プレゼント\n存分にありがたがってください！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1140001, 114001, 0, 6.2, 2, "ト、トリックオアトリート…\n実はこういうことには不慣れでね\nよければ、キミが私の\n師匠になってくれるかな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1140002, 114001, 0, 8.8, 2, "子供から大人までみんな\nハロウィンを楽しんでいるね…\n私はちゃんと馴染めているかな？\nばっちり？　ならよかった", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1140003, 114001, 0, 9.7, 4, "ハロウィンのお菓子のやりとり…\nええと、ハッピーハロウィン…\nお菓子をどうぞ…って\nうまくできてる？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1140004, 114001, 0, 5.6, 2, "賑やかなハロウィンの街並み…\n眺めているだけで\n楽しいものだね\nさあ、私たちも出かけよう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1140005, 114001, 0, 5.3, 2, "キミと一緒に\nハロウィンを過ごすのは\n気恥ずかしいんだけど…\nうれしいよ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1140006, 114031, 0, 0.0, 1, "トリックオアトリート\nお菓子をくれないと…\nとり憑いてしまうよ？\nふふ、なんてね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1140007, 114031, 0, 0.0, 1, "さあ、手を出して？\n幽霊たちと一緒に\nハロウィンのダンスを踊ろうよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1140008, 114031, 0, 0.0, 1, "ツムギが作ってくれたこの衣装\nとても素敵だね…ふふ、\n幽霊たちも、似合ってるって\n言ってくれているのかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1140009, 114031, 0, 0.0, 1, "キミ、休んでいる暇はないよ？\nハロウィンを\n遊び尽くさなくちゃね\nほら、一緒に行こう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1140010, 114031, 0, 0.0, 1, "ハロウィンの祭りで賑わう\nこんな時だからこそ、かな？\nキミと過ごせる時間を\nとても幸せに感じるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1140011, 114001, 0, 5.6, 2, "誕生日おめでとう。キミの成長が\n感じられて私もうれしいよ\n来年のキミが今から楽しみだ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1140012, 114031, 0, 0.0, 1, "キミの誕生日は私にとっても\nかけがえのない大切な日だよ\nキミと巡り会えた運命に\n感謝しなくちゃね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1141001, 114101, 0, 2.6, 2, "ホワイトタイガー出動ッス！\nさあ、今日も平和を守るため\n一緒にパトロールにいくッスよ！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1141002, 114101, 0, 3.2, 2, "今日も張り切って\n任務に出発ッス！\n遅れないように\nついてくるッスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1141003, 114101, 0, 4.2, 1, "極秘任務に備えて\n秘密の特訓をするッス！\n付き合ってもらっていいッスか？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1141004, 114101, 0, 2.3, 2, "…今、ヒマッスよね？\nだったら二人で\nキメポーズの研究をするッスよ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1141005, 114101, 0, 3.5, 1, "ヒーローにも休息は必要ッス！\nあんたさえよければ\n今日は二人でごろごろする\nっていうのもいいッスよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1141006, 114131, 0, 0.0, 1, "どうッスか、この勇姿！\nどんな悪も\nホワイトタイガーからは\n逃げられないッスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1141007, 114131, 0, 0.0, 1, "あんたと一緒につくった\nこのカッコイイキメポーズで、\n今日も平和を守るッスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1141008, 114131, 0, 0.0, 1, "いつもと姿は変わっても\n正義の心は変わらないッス！\n一緒にがんばってもらうッスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1141009, 114131, 0, 0.0, 1, "あんたも自分と一緒におそろいの\nカッコイイポーズをキメるッス！\nせーの…\nトリックオアジャスティスッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1141010, 114131, 0, 0.0, 1, "あんたが背中を守ってくれるから\n自分は正義に突き進めるッス！\nこれからもよろしく頼むッスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1141011, 114101, 0, 7.8, 2, "今日はあんたの誕生日ッスね！\nお祝いに今日のヒーローごっこの\nトラタイガー役を譲って…\nえ？　自分がやっていいッスか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1141012, 114131, 0, 0.0, 1, "お誕生日おめでとうッス！\n今日のトラタイガーごっこは、\n自分とあんたで\nダブルタイガーッスよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1142001, 114201, 0, 6.8, 2, "魔法少女と提督\nそれらが一つになった私こそ\n愛と希望の戦士\n魔法提督ラブリー★モニカだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1142002, 114201, 0, 4.2, 1, "この格好にもすっかり\n慣れてしまったな\n子どもっぽいかと思っていたが\nこういうのも案外…悪くない", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1142003, 114201, 0, 5.6, 1, "ずっと剣を扱ってきたからか\n魔法にはまだ慣れないな…\nしかし剣先から光線が出るのは\nなぜかワクワクするな…！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1142004, 114201, 0, 3.5, 5, "輝け、愛の力！\nちょっとかわいすぎるか？\n…今からでも全砲門、発射！\nとかにした方がいいだろうか…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1142005, 114201, 0, 0.0, 1, "困ったことがあれば\nなんでも私に言ってくれ\n魔法提督は困っている者を\n決して見捨てないのだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1142006, 114231, 0, 0.0, 1, "ラブリーキュアリーブレイブリー\nマジカルレベルインフィニット！\n魔法提督ラブリー★モニカ\n空に代わって指揮を執る！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1142007, 114231, 0, 0.0, 1, "戦いの指揮は私に任せろ！\n愛と希望に満ちた戦略で\n敵を討ち果たして見せよう\n最後の決めポーズまで完璧だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1142008, 114231, 0, 0.0, 1, "魔法少女は子どもたちとの\n触れ合いも大切にせねばな！\n今日も一緒に提督ごっこだ！\nなんだ、貴公も一緒にやるか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1142009, 114231, 0, 0.0, 1, "魔法提督の指揮を見せてやろう！\n貴公、今すぐ街中のお菓子を\n買い占めてくるのだ！\n…すまん、冗談だ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1142010, 114231, 0, 0.0, 1, "人々の平和を守るという使命\n不安がないかと言えばウソになる\nだが、貴公がいてくれるなら\n私はどんな苦難にも立ち向かえる", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1142011, 114201, 0, 8.5, 6, "貴公、今日は誕生日らしいな\nみなまで言うな、お祝いに\nお菓子を用意してある。決して\n私が食べたいからではないぞ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1142012, 114231, 0, 0.0, 1, "誕生日おめでとう。貴公のほしい\nものを言うがいい…え、私と\n一緒に過ごしたい？　そ、そうか\n欲がないな、貴公は", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1143001, 114301, 0, 3.7, 2, "このキラキラした衣装、\nキュートな武器…\nえへへ、やっぱり\n魔法少女っていいなぁ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1143002, 114301, 0, 3.7, 1, "まさか、私が\n魔法少女になれるなんて！\n…おっと、このことは\nみんなに内緒にしないとね♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1143003, 114301, 0, 4.6, 1, "魔法少女はみんなに\n夢を与える存在…\nその名に恥じぬように、\n私も精進しないとね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1143004, 114301, 0, 5.8, 2, "シャイニー、\nフェザリー、グローリー！\nマジカルソード、シャープネス！\nよし、今のポーズは完璧だったね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1143005, 114301, 0, 10.1, 5, "変身したら髪型まで変わるとはね\nさすがに長いから結んで…え？　\n似合ってるって…じゃ、じゃあ、\nこのままにしとこうかな…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1143006, 114331, 0, 0.0, 1, "シャイニーフェザリーグローリー\nマジカルソード、シャープネス！\n魔法剣士シャイニー★トモ！\n煌めく剣技で、悪を断つ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1143007, 114331, 0, 0.0, 1, "魔法少女になったとしても、\nミクマ流剣術に死角はない\n…なんなら、\nその身でためしてみる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1143008, 114331, 0, 0.0, 1, "半人前の私にどこまでできるか\n分からないけど、諦めることは\n絶対にしない。それが、\n私なりの魔法少女の在り方だよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1143009, 114331, 0, 0.0, 1, "私は魔法少女であると同時に、\nミクマ流の剣士…悪いけど、\n真剣勝負というならば、\n手加減はできないからね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1143010, 114331, 0, 0.0, 1, "変身できるのは、あなたのおかげ\nでもあるんだよね…だったら、\nこれからはず～っと一緒に居て\nもらわないと…な～んてね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1143011, 114301, 0, 6.4, 2, "今日はあなたの誕生日だよね\nだったら、私がケーキを焼いて\nあげよっか？　こう見えて、\n料理には自信があるんだよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1143012, 114331, 0, 0.0, 1, "誕生日おめでとう！\n魔法少女の役目が\n終わったあとで、\n二人でお祝いしようね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1144001, 114401, 0, 4.1, 1, "おーっほっほっほ！\n聖夜を彩るパーティーも\nこのアキノ・ウィスタリアに\nお任せあれ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1144002, 114401, 0, 3.1, 1, "買い出しなら私の出番ですわね\n料理にツリーにプレゼントに…\n価格交渉ならお手のものですわ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1144003, 114401, 0, 2.8, 2, "クリスマスには\nプレゼントが付き物！\nもちろん準備は万端ですわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1144004, 114401, 0, 5.0, 5, "この衣装、新しく\n仕立てたのですがいかがですか？　\nえ、似合ってる…ほめて\nいただけてよかったですわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1144005, 114401, 0, 4.3, 2, "あなたさまとクリスマスを共に\n過ごせてうれしいですわ！\nさあ、さっそくパーティーの\n準備をいたしましょう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1144006, 114431, 0, 0.0, 1, "さあ、楽しい聖夜の晩餐会を\n始めるといたしましょう！\nほら、あなたさまも\n席にお座りくださいな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1144007, 114431, 0, 0.0, 1, "おーっほっほっほ！　この私に\nかかれば、料理など造作も\nありませんわ！　さあ、遠慮せず\n召し上がってくださいませ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1144008, 114431, 0, 0.0, 1, "料理とは面白いものですわね\nあなたさまには、別の手料理も\n召し上がっていただきたいですし\n今後も続けていきたいですわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1144009, 114431, 0, 0.0, 1, "せっかくのお料理ですもの\n温かいうちにいただきましょう？\nふふ、安心なさってくださいまし\nお代わりはたーんとありますわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1144010, 114431, 0, 0.0, 1, "みなさまと過ごすクリスマスも\nよいものですけれど…\nあなたさまと二人でいる\nこの時間も…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1144011, 114401, 0, 5.8, 1, "今日はあなたさまの\n誕生日ですもの\nささやかに、とは言いません\n盛大にお祝いいたしましょう！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1144012, 114431, 0, 0.0, 1, "誕生日のお祝いに手料理を\nご用意してみましたわ！\n是非とも\n召し上がってくださいませ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1145001, 114501, 0, 4.0, 1, "お金をかけなくても素敵な\nクリスマスが過ごせるってこと\n見せてあげるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1145002, 114501, 0, 6.8, 2, "子どもってクリスマスが好きよね\nまあ、あたしも人のこと\n言えないけど…何だか\n楽しい気分になれるのよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1145003, 114501, 0, 4.3, 2, "子どもたちが笑顔で\nクリスマスを\n過ごせること\nそれが一番の幸せよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1145004, 114501, 0, 3.4, 2, "せっかくのクリスマスなのに\n暇そうね。仕方ない、少しだけ\n付き合ってあげるわ。何か\n温かい物でも飲みに行きましょ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1145005, 114501, 0, 3.3, 5, "え、ドレスが似合ってる？\nそ、そう…ありがと…\nあんたに褒めてもらうのって…\nうれしいわ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1145006, 114531, 0, 0.0, 1, "さあ、熱く楽しいクリスマスを\n始めるわよ！\n寒がってる暇なんて\nないんだからね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1145007, 114531, 0, 0.0, 1, "少しエレガントじゃないかも\nしれないけど、許してね？\nあたし、目いっぱいクリスマスを\n楽しんじゃうんだから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1145008, 114531, 0, 0.0, 1, "あんたと一緒のクリスマス…\n結構いい感じね♪\n気の許せる相手と\n過ごせるのって…幸せよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1145009, 114531, 0, 0.0, 1, "外にいるのに何だか暑いって\n思うのは、もしかして…\nあんたが隣にいるから\nなのかしら…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1145010, 114531, 0, 0.0, 1, "高級レストランで豪華なディナー\nなんてのは柄じゃないし\nあんたがいてくれれば、それが…\nその…さ、最高のクリスマスよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1145011, 114501, 0, 5.4, 5, "ハッピーバースデー！\n子どもたちからあんたに\nプレゼントがあるみたい\nあたしも…一応用意してあるわ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1145012, 114531, 0, 0.0, 1, "ええと…誕生日おめでとう\nこれから一緒にプレゼントを\n見に行かない？\nデ、デートじゃないわよ、もう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1146001, 114601, 0, 2.1, 1, "お姉さんに任せなさい！\nキミのクリスマスを\n最高のものにしてあげるわよ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1146002, 114601, 0, 2.8, 6, "キミと二人のクリスマスかぁ…\nき、緊張なんかしてないわよ？\n私がリードしてあげるんだから！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1146003, 114601, 0, 3.8, 5, "ちょっと派手じゃないかしら\nこの格好…ま、まあ、\nキミが気に入ってくれてるなら\nいいんだけどね？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1146004, 114601, 0, 5.7, 1, "一人のクリスマスに\n慣れちゃってたから\nキミと過ごすクリスマス…\nちょっとドキドキしちゃうかも", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1146005, 114601, 0, 0.0, 2, "メリークリスマース！\nえへへ、大丈夫よ～\n酔ってなんかないから～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1146006, 114631, 0, 0.0, 1, "さあ、最っ高の\nクリスマスが始まるわよ♪\nキミも早くおいで～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1146007, 114631, 0, 0.0, 1, "キミも飲み物どうかしら？\n一緒に乾杯しましょ♪\nはーい、メリークリスマース！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1146008, 114631, 0, 0.0, 1, "おかわりはいかが？\n遠慮しなくてもいいわよ\nクリスマスパーティーなんだもの\n思いっきり楽しまなくちゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1146009, 114631, 0, 0.0, 1, "どう？　楽しんでるかしら？\n…もしよかったらなんだけど\nこの後二人で…\nや、やっぱりなんでもないわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1146010, 114631, 0, 0.0, 1, "キミと過ごすクリスマス…\nとっても素敵ね\nこんな幸せな時間が\nずっと続けばいいのに…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1146011, 114601, 0, 4.8, 2, "今日はキミの誕生日だもの\nお祝いしないともったいないわ！\nそれじゃあ、かんぱ～い☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1146012, 114631, 0, 0.0, 1, "お誕生日、おめでとう！\n後で二人きりでパーティー\nしちゃおっか♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1147001, 114701, 0, 5.9, 1, "はーっはっはっは！\n新年の初笑いは悪党らしく\n盛大にだ！　オマエも一緒に\nやらないか？　気持ちいいぞ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1147002, 114701, 0, 5.3, 3, "悪党に休みなしだ！\n正月だろうとアタシは戦うぞ！\nさあ、文句があるヤツは\n全員かかってこい！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1147003, 114701, 0, 7.8, 8, "一年の計はなんとかっていうだろ\nだからアタシは、悪党らしく\n正月から悪いことをするぞ！\nま、年中悪さしてるけどな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1147004, 114701, 0, 8.3, 6, "出店がいっぱいだな！　これじゃ\nお年玉がいくらあっても\n足りないぞ！　なあ、オマエのは\nまだ…な、なんでもない！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1147005, 114701, 0, 3.9, 2, "まさかオマエと新年を\n過ごすことになるなんて…\nでも悪い気はしないな！\nなんか美味いもんでも食うぞ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1147006, 114731, 0, 0.0, 1, "アタシが天楼覇断剣で\n全部真っ二つにしてやる！\n空だろうが海だろうが\n知ったことか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1147007, 114731, 0, 0.0, 1, "飾りをつけた天楼覇断剣も\n意外と悪くないぞ！\nぶった切れればなんだって\nいいんだけどな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1147008, 114731, 0, 0.0, 1, "気持ちのいい景色だな！\nどこかでアイツも…\nオクトーも、こんな風に\n日の出を見てるのかな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1147009, 114731, 0, 0.0, 1, "今年もオマエにはいい事あるぞ！\nなんてったってアタシと\nこの天楼覇断剣が一緒だからな！\nどんな未来も切り拓いてやる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1147010, 114731, 0, 0.0, 1, "プリンセスナイトの力って\n初日の出に似てると思わないか？\n温かくて、柔らかくて…それに\nなんか力がみなぎってくるだろ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1147011, 114701, 0, 5.4, 2, "誕生日おめでとう！\nオマエのお祝いができて\nアタシもうれしいぞ！\nもっともっと祝わせろ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1147012, 114731, 0, 0.0, 1, "ハッピーバースデー！\n今日はオマエの言う事を\nなんでも聞いてやるぞ！\nアタシに遠慮はするなよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1150001, 115001, 0, 6.1, 2, "この私を待たせるとは\n良い度胸をしていますね\n…冗談ですよ\n待ち合わせ時刻、丁度です", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1150002, 115001, 0, 4.0, 1, "正月の街は騒々しいですね…\nですが、これはこれで\n観測するデータには\n事欠かないとも言えますか", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1150003, 115001, 0, 8.7, 2, "せっかくの正月です\n貴方も人の事ばかりにかまけず\n自分のための時間を持ちなさい\nお姉さんからのアドバイスですよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1150004, 115001, 0, 5.1, 6, "これを飲みなさい\nそこで売っていた甘酒です\n貴方に体調を崩されては\n私としても困りますから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1150005, 115001, 0, 9.8, 2, "年下に見えるお姉さんは\n好きですか？\nええ、貴方の好みを聞いていますが\n…そう。それなら良いのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1150006, 115031, 0, 0.0, 1, "あけましておめでとうございます\n今年も私の様々な姿を\n貴方に見せてあげますよ…\nそのままがいい？　…そうですか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1150007, 115031, 0, 0.0, 1, "雪…こうして触れてみると\n儚いものなのですね\nあっという間に形が変わる…\n少し、親近感を覚えます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1150008, 115031, 0, 0.0, 1, "舞い散る雪を眺めながら\n温かい甘酒を飲むというのも\nなかなか乙なものですね\n貴方も付き合いなさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1150009, 115031, 0, 0.0, 1, "外出など普段はしないのですが…\n気が向いたので初詣に行きますよ\n…ええ、他意など一切ない\n単なる気まぐれです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1150010, 115031, 0, 0.0, 1, "女性の晴れ姿を見た際には、まず\n言うべき事があると思いますが？\n…ふふ、よくできました\nもっと褒めてもいいのですよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1150011, 115001, 0, 7.7, 2, "今日は貴方の誕生日ですね\n私の独断で贈り物を用意しました\nありがたく受け取りなさい", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1150012, 115031, 0, 0.0, 1, "誕生日おめでとうございます\nまた一つ成長した貴方を\n見せてくれることを\n期待していますよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1155001, 115501, 0, 8.2, 2, "どんな困難が行先を阻もうとも\nわたくしはこの手で明日を\n掴んでみせます。わたくしの\n決意を見ていてくださいまし", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1155002, 115501, 0, 0.0, 2, "主さまのおそばにいられる幸せは\n当たり前のものではなく\nとても特別なことなのだと\n感じておりますよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1155003, 115501, 0, 4.7, 2, "主さま、なにかお困りのことは\nございませんか？　これからも\n変わらず、わたくしになんなりと\nお申し付けくださいまし♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1155004, 115501, 0, 0.0, 2, "この身に新たな力を宿せたのは\n全て主さまのおかげでございます\n恩返しできるよう、一生をかけて\nガイドさせていただきます", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1155005, 115501, 0, 8.8, 5, "主さまをお慕いする気持ちには\nこの真っ白な装束と同じく\n一切の曇りもございません\nわたくしの全ては、主さまに…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1155006, 115531, 0, 0.0, 1, "わたくしは独りではございません\nかけがえのない仲間のため…\n絆を紡いだ主さまのために\nこの身の全てを捧げます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1155007, 115531, 0, 0.0, 1, "精霊たちが、わたくしたちの絆を\n祝福してくれているようです\n笑顔あふれる素晴らしい仲間に\n心からの加護を！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1155008, 115531, 0, 0.0, 1, "猛き風の精霊よ…\n未来に向かって歩まんとする\nわたくしたちの背中に\n祝福の追い風を！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1155009, 115531, 0, 0.0, 1, "絆を繋ぎとめてくださった\n主さまの優しいお手手…\nその温もりは、この胸に灯る\n希望の明かりでございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1155010, 115531, 0, 0.0, 1, "主さまの優しくて\n耳をくすぐるそのお声で\nどうかこれからもわたくしの名を\n呼んでくださいまし♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1155011, 115501, 0, 0.0, 2, "今日は主さまがお生まれになった\nとてもおめでたい日ですね\n精霊たちとともに、心を込めて\nお祝いをさせてくださいまし♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1155012, 115531, 0, 0.0, 1, "お誕生日おめでとうございます\nまた一つ大人になられましたね\n未来に向かって歩むその隣で\nこれからもわたくしはずっと…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1156001, 115601, 0, 9.0, 7, "精霊の意思を感じ取る…\nやっぱり難しいなぁ…\n騎士クンもやってみる？\n…あれ？　寝ちゃった！？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1156002, 115601, 0, 8.4, 7, "精霊って、色んな子がいるんだね\n優しい子だけじゃなくて、\nいたずら好きな子も…ふにゅ！\nうぅ～…ほっぺ突っつかないで～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1156003, 115601, 0, 9.6, 8, "この服、刺激が強くないかな…？\nだってほら、こんなに布が\n少なくて…はっ！\nあんまり近くで見ないで～！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1156004, 115601, 0, 13.3, 6, "精霊を使役してる時って\n感覚が普段より冴えるの\n今も風や光の変化を感じるよ…\n騎士クンは…お腹減ってきた？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1156005, 115601, 0, 7.4, 7, "…うん、騎士クンとの距離が\n中々縮まらな…え、騎士クン！？\nこ、これはその、精霊と話を\nしてて…い、今すぐ忘れて～！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1156006, 115631, 0, 0.0, 1, "騎士クン…この子を一緒に\n育ててくれて、ありがとう\nわたしね、とってもうれしかった", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1156007, 115631, 0, 0.0, 1, "大丈夫…伝わってるよ\nあなたの思いも、あなたの力も…\nずっと元気でいてね。約束だよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1156008, 115631, 0, 0.0, 1, "騎士クンのためなら、\nいつでも呼んで…だって\nこの子もわたしも、\n同じ気持ちだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1156009, 115631, 0, 0.0, 1, "お父さん…ちがっ！　違うの！\nこの子が今、騎士クンのこと\nそう呼んでて…もぉ～！\nさっきのは忘れて～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1156010, 115631, 0, 0.0, 1, "人と精霊の気持ちが通じ合うなら\nこの気持ちも、騎士クンに\n通じたらいいのにな…あれ…？\n今、何も言ってない…よね…！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1156011, 115601, 0, 6.4, 2, "お誕生日おめでとう、騎士クン！\n精霊もおめでとうだって\nふふっ、やっぱり騎士クンは\nみんなに慕われてるんだね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1156012, 115631, 0, 0.0, 1, "お誕生日おめでとう！\nあ、あのね？　精霊から\n祝いの舞を教わったの\n踊ってみても…いい？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1157001, 115701, 0, 8.3, 6, "助手くん、準備はできたかい？\n今度の事件現場は海だよ！\n二人で一緒にバカンス\n…じゃなかった、調査に出発だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1157002, 115701, 0, 9.1, 6, "海に持っていくものといえば\n手帳にルーペ、望遠鏡…\nあとは浮き輪にサーフボード…\nあれ…なにをしに行くんだっけ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1157003, 115701, 0, 5.2, 2, "海か…みんなに聞かれたら\nついていくって言いだしそうだ…\nだから、内緒にしておこうね？\n私と君だけの秘密、だよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1157004, 115701, 0, 6.4, 5, "この髪型、動きやすくていいね\n…助手くんも気に入ってくれた？\nじゃ、じゃあしばらくはこれで…\n深い意味はないんだよっ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1157005, 115701, 0, 6.9, 5, "事件、すぐに解決できるといいね\nえっ？　なんでって…それは…\n…助手くんと遊びたいからだよ！\nそれくらい見抜きたまえ、もう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1157006, 115731, 0, 0.0, 1, "助手くんっ、見たまえ、海だよ！\nなんだい？　その驚いた眼差しは\n私だってはしゃぎたいときもある\n…助手くんと海に来たときとかね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1157007, 115731, 0, 0.0, 1, "波打ち際の名探偵…\nう～ん、いい響きだ♪\nさぁ助手くん、青き水平線に\n隠されし真実、見つけに行こう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1157008, 115731, 0, 0.0, 1, "助手くん、君の観察力を試そう\n私は今、普段と違う格好だろう？\nなにか気づいたこととか…えっ？\n水着、似合ってる？　えへへ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1157009, 115731, 0, 0.0, 1, "世界にはまだまだ謎があふれてる\nこうして色々なところを巡って\n真実を見つけるのも…いいかもね\nもちろん助手くんと一緒に、だよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1157010, 115731, 0, 0.0, 1, "いつも私は君のことを助手くんと\n呼んでいるだろう？　でも今は、\n遊びに来ているわけだから…\nその…やっぱりなんでもないっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1157011, 115701, 0, 3.7, 2, "さて助手くん、\n呼ばれた理由は分かるかな？\nふふ、私にはお見通しなのさ…！\n誕生日、おめでとうっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1157012, 115731, 0, 0.0, 1, "助手くん、誕生日だよね\nこれを受け取って欲しい\nこのプレゼントに込められた意味\n…解き明かしてみたまえ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1158001, 115801, 0, 4.6, 1, "まさか私が貴族様の舞踏会に\n出られるなんて…きちんと\nレッスンがんばらなきゃ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1158002, 115801, 0, 4.2, 2, "人間の姿になれるだけでも\nすっごく幸せなのに、こんなに\nすてきなドレスを着られるなんて\n本当に夢みたいだわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1158003, 115801, 0, 4.4, 1, "淑女ってどういう人のことを\nいうのかしら…森で木を切る\n淑女がいてもいいわよね？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1158004, 115801, 0, 6.6, 2, "牧場での暮らしは本当に幸せで\nこれ以上を望む必要なんて\nないけど、やっぱり華やかな\n世界も憧れちゃうわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1158005, 115801, 0, 2.7, 2, "ドレス姿が似合ってる？\nふふ、ありがとう。あなたに\nそう言ってもらえれば\n安心だわ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1158006, 115831, 0, 0.0, 1, "アン・ドゥ・トロワ♪\nまさか私がきれいなドレスで\n踊れるなんて…キラキラした\n夢の中にいるみたいだわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1158007, 115831, 0, 0.0, 1, "こんなに素敵な場所で\nあなたと踊れるなんて…本当に\nお話の中のお姫様になったみたい\nあなたも王子様みたいで素敵よ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1158008, 115831, 0, 0.0, 1, "素敵なドレスを着て優雅に踊る…\nすっごく楽しいけど、あなたや\n牧場のみんなと居る時間も\n負けないくらい素敵な時間よね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1158009, 115831, 0, 0.0, 1, "あなたがいてくれたから\n私は明るく前向きでいられたの\nこれからは私が誰かのそんな人に\nなれたらいいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1158010, 115831, 0, 0.0, 1, "魔法はいつか終わっちゃう…\nでも魔法みたいに素敵な時間は\n自分の力で作っていけるのよね\n私、これからもがんばるわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1158011, 115801, 0, 2.3, 1, "お誕生日おめでとう！\nあなたのために森で花を摘んで\nきたんだけど、受け取って\nもらえるかしら？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1158012, 115831, 0, 0.0, 1, "あなたの特別な日をこうして\n一緒に迎えられるのはとっても\nうれしいわ♪　また来年も一緒に\n過ごしたいな…なんて", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1159001, 115901, 0, 0.0, 5, "いつもズボンだから、\nスカートってのは落ち着かねえな\nま、まあ、こういう系の服も\nきらいじゃねえけどさ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1159002, 115901, 0, 0.0, 5, "パトロールに行きてえけど、\nこの服で行ったら目立つよなぁ\nえ、似合ってるから大丈夫って…\nそ、そういう問題じゃねえよ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1159003, 115901, 0, 0.0, 2, "この服、結構動きやすいんだな\nへへっ、これなら、\n思いっきり暴れられそうだぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1159004, 115901, 0, 0.0, 2, "お嬢さまみたいな格好してる\nわけだし、しゃれた料理でも\n作ってみるか…もちろん、\nおまえの分も作ってやるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1159005, 115901, 0, 0.0, 1, "どんな格好だろうと、\nあたしの剣の腕は変わらねえぜ\nなんなら、\n今から稽古つけてやろうか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1159006, 115931, 0, 0.0, 1, "こんな格好してるやつが、\n戦えるわけないって？\n…だったら、\nその身で確かめてみるんだな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1159007, 115931, 0, 0.0, 1, "優雅に舞い…そして、\n狼のように敵をかみ砕く！\nあたしなりの華麗なる\n剣術ってやつを見せてやるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1159008, 115931, 0, 0.0, 1, "この服だと、お嬢さまになった\n気分だな…華麗に舞いますわ♪\nな～んて…！？　おまえいつから\nそこに…み、見るんじゃねぇ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1159009, 115931, 0, 0.0, 1, "なんだよ、急にじーと見てきて…\nそ、そういう服も似合うねって…\n急になに言ってんだよ！\nまあでも、あ、ありがとな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1159010, 115931, 0, 0.0, 1, "ここなら、思いっきり体を\n動かせそうだし、訓練してくか\nおまえも一緒にどうだ？\n二人ならもっと楽しめると思うぜ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1159011, 115901, 0, 0.0, 2, "今日はおまえの誕生日だよな\nせっかくだし、\nなんか飯でも作ってやるよ\nリクエストがあったら言ってくれ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1159012, 115931, 0, 0.0, 1, "きょ、今日って誕生日だよな？\nおまえには世話になってるし、\nその…ケ、ケーキ焼いてきた\nから、さっさと受け取れ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1160001, 116001, 0, 8.2, 2, "マホマホ王国のお茶会へようこそ\nマホ姫が王子はんのために、\n愛を込めておもてなししますわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1160002, 116001, 0, 3.5, 2, "日差しは苦手どすけど、\n春のひなたはぽかぽかしとって、\n心地ええなぁ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1160003, 116001, 0, 10.1, 2, "花の香り、小鳥のさえずり、\nそして王子はんと過ごす時間\nふふっ、ここは夢の国やね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1160004, 116001, 0, 6.5, 5, "王子はんに見てほしくて、\n衣装を新調したんよ\n王子はん、うちはきれいどすか♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1160005, 116001, 0, 4.8, 2, "礼儀作法のことならうちに任し\nなんならうち、王子はんのために\nつきっきりで教えまひょか♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1160006, 116031, 0, 0.0, 1, "王子はんに喜んでほしくて\nたくさんお菓子作ったんや\nああ、お礼なんていいんよ\n王子はんの笑顔だけで充分や♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1160007, 116031, 0, 0.0, 1, "テーブルマナーはお互いが\n仲よく過ごすための心配りなんよ\nでも一番大切なのは\n一緒に楽しい時間を過ごすことや", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1160008, 116031, 0, 0.0, 1, "王子はんと一緒におると\nうちの知らない世界が見えて\n楽しいわぁ。王子はんの世界も\nきっとワンダーランドなんやねぇ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1160009, 116031, 0, 0.0, 1, "童話の幸せな結末\nその続きはだれも分からへんけど\nうちと王子はんならずっとずっと\n幸せに暮らせそうやねぇ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1160010, 116031, 0, 0.0, 1, "今日はマホマホ王国のお茶会や\nマホ姫の特別なおもてなしで\n王子はんをたくさんたくさん\n甘やかしますわぁ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1160011, 116001, 0, 4.4, 1, "お誕生日おめでとう～\nうち王子はんのために\nケーキを用意したんよ\nおいしく召し上がってなぁ♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1160012, 116031, 0, 0.0, 1, "今日は王子はんのお誕生日やね！\nそういうわけで…じゃーん！\nお花のシャワーや！\nふふっ、きれいやろう？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1162001, 116201, 0, 9.0, 4, "テレフェスっていうか、\nまあ体育とかスポーツ的なのは\n得意な部類だよね、個人競技は\n団体競技は…協調性が課題だわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1162002, 116201, 0, 0.0, 6, "いいけどユニ先輩のフェス服、\nアレ何なの。似合ってるけど\nチエルのも無駄にデコってて、\nアレ何なの。似合ってるけど", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1162003, 116201, 0, 6.1, 6, "いんだけどさ、最近テレ女の空気\n全体的にちょっとアレじゃない？\nや、なんか校風崩れっていうの？\nルーセンティズム入ってきてね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1162004, 116201, 0, 10.1, 6, "そういやあんたもどっかの学校で\nスポーツ経験あるらしいね\nそうなんだ…へー、いいじゃん\nえ、海辺で野球？　何それ冗談？", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1162005, 116201, 0, 8.5, 1, "え、ちょ何…ガン見やめてよ\nわりかし肌出てンの思いだして、\n急にモジついて来ちゃうじゃん…\nあ？　寒そう？　いいえ、別に", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1162006, 116231, 0, 0.0, 1, "よっ、と楽勝…！\nつーかさ、障害物競走なんて、\nフツー子供のやるもんでしょ…\nマジでこの学校アタオカだわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1162007, 116231, 0, 0.0, 1, "ちょー、いつもの外野うるさい\nハズめの声援やめてくんない\nあと、うち不良ではないから\nいや毒婦でもねーわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1162008, 116231, 0, 0.0, 1, "そんな、うちの応援とかいいから\nあんたも自分の競技がんばりー？\n次なに出んの。あ？　二人三脚？\nえ、誰と。別にいんだけど、誰と", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1162009, 116231, 0, 0.0, 1, "なんか悪いね、あんたに弟の\n面倒まで見てもらっちゃって…\nそだ、このあと家族とおべんと\n食べんだけど良ければ一緒にど？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1162010, 116231, 0, 0.0, 1, "ハ？　いや、格好いいとかそんな\n真顔で言うフツー？　はっず\nや、べつに言うなってわけじゃ…\n言いたきゃそれは…言いなよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1162011, 116201, 0, 7.7, 1, "あんた今日、誕生日だって？\nなんで言わんの、そーゆーこと…\nなんも用意してなかったじゃん\nじゃあ飴ちゃん多めにあげるわ", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1162012, 116231, 0, 0.0, 1, "あんた今日、誕生日だよね\nああいや、さっきチエルに聞いて\nそだ、偶然ケーキ作ってきたから\nコレあげるわ。いや偶然だけど", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1163001, 116301, 0, 4.1, 3, "先輩、大変です！\n若者のちぇる離れが深刻です！\nちぇる語普及委員の一人として\nどうお考えなんですか！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1163002, 116301, 0, 6.8, 1, "先輩、大変です！　今ならなんと\nこの「幸せになれるちぇる」が\n１万ルピでお手元に。どうです？\nちぇるは肉眼では見えませんけど", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1163003, 116301, 0, 8.3, 4, "クロエ先輩がチエルのフェス姿\n可愛いって言ってくれないんです\n…え、先輩の前では言ってた？\nやだー！　直接言ってほしいー！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1163004, 116301, 0, 10.4, 6, "そういえば先輩もどっかの学校で\nスポーツ経験あるらしいですね\nそうなんだ…ふうん意外な一面…\nえ、海辺で野球？　何それ冗談？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1163005, 116301, 0, 5.4, 2, "ちぇる～ん☆　どうですか、この\nチエル仕様のフェス用ウェア！\nえっ？　そんなに似合ってます？\nですよね、知ってます。えへへ", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1163006, 116331, 0, 0.0, 1, "ちぇる～ん！　ちぇる　ちぇろぱ\nちぇるぴぴぱ　ちぇぴらぺぽー？\nうんうん、だよねーおっけー！\nじゃあリズムを上げて行くよー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1163007, 116331, 0, 0.0, 1, "みんなー！\nちえるん単推しなのは分かるけど\n後ろの地味な二人にも\n少しは歓声送ってあげてねー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1163008, 116331, 0, 0.0, 1, "やっぱりフェスの最後はお約束、\nヒロインたちのライブだよねー！\n会場のみんなも配信のみんなも…\nえ、これ魔石配信してないの？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1163009, 116331, 0, 0.0, 1, "ちぇるミナすこすこ侍の先輩も\nファンサで釣ってあげますよー！\nそうれ、投げキッＣＨＵ～☆\nって、何よそ見してるんですか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1163010, 116331, 0, 0.0, 1, "歌って踊れるスーパーアイドル、\nこれが世界の美少女ちえるんです\nどうです先輩、惚れ直しました？\nえ、真顔で頷かれても…困る…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1163011, 116301, 0, 7.3, 1, "お誕生日おめでとうございます！\n可愛い後輩から可愛いちぇるを\nプレゼント！　ちぇる～ん☆\n良かったですね、一生ものですよ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1163012, 116331, 0, 0.0, 1, "先輩、ハピバ～！\nじゃあ今日は特別なんでチエルが\n一緒にケーキ食べてあげますね！\nお祝いだから割り勘でいいですよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1164001, 116401, 0, 8.7, 1, "聖学祭か…気が重いな…\n学芸発表はともかく運動競技は…\nうむ、人にはそれぞれ適性がある\n荒事は荒くれ後輩たちに任せよう", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1164002, 116401, 0, 6.8, 1, "聖学祭に提出したぼくの芸術は、\nあまり評価を得なかったらしい\nぼくは印象派だからな、\n心得のない者には分からぬ領域だ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1164003, 116401, 0, 10.6, 2, "高機動万能靴『神速くん』\nこれはからくり工学の成果物を、\nぼくが魔法で再解釈したものだ\n理系不要論の物証なのだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1164004, 116401, 0, 10.7, 4, "ときに後輩、君はどこぞの学院で\nスポーツを学んできたらしいな\nそれはどういった類の…\nなに、海で野球？　難しいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1164005, 116401, 0, 10.5, 6, "君と二人で撮った写真だが、\n塔の書庫の深くに保管してある\n他人に見せるのは躊躇われてな\n何なのだろうか、この情動は…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1164006, 116431, 0, 0.0, 1, "年に一度の文武の祭典…\n皆が祭囃子に浮足立つ日こそ、\n賢者はあえて日常を貫くのだよ\nではロゼッタ、後はよしなに…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1164007, 116431, 0, 0.0, 1, "これは決してゴロ寝ではない\n体力温存と精神集中を兼ねた\n革新的なトレーニング法なのだ\n端的に換言すれば、ゴロ寝だ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1164008, 116431, 0, 0.0, 1, "ぽーん！　子守唄を再生します！\n♪ユニちゃん良い子だ眠りゃんせ\n♪夢の扉で真理に接続\n♪多次元宇宙で神を見な～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1164009, 116431, 0, 0.0, 1, "なに、ぼくの運動不足が心配？\nそうか、よくぞ言ってくれた\nでは君に下肢部のストレッチを\n依頼する。ほれほれ、解したまえ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1164010, 116431, 0, 0.0, 1, "やあ同志、君も午睡に来たのか\n遠慮せず横になりたまえよ\nロゼッタの膝枕は売約済みだが、\nぼくので良ければ貸してやろう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1164011, 116401, 0, 0.0, 2, "おい同志、ぼくのメモによれば\n今日は君の誕生日じゃあないか\n祝いにぼくの講義を授けよう\n席につきたまえ、夜は長いぞ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1164012, 116431, 0, 0.0, 1, "ちぇる～ん♪　後輩ハピバ！\nぼくは都の風習に疎いゆえ、\nチエル君に祝い方を聞いてきた\nどうだ愛くるしいだろう。にこり☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1165001, 116501, 0, 0.0, 2, "はーっはっはっは！\nビッグボス・イノリ様の登場です\nお兄さんは今日から側近として\nあたしをサポートするですよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1165002, 116501, 0, 10.4, 3, "あたしの知名度が広まるよう\nお兄さんはあたしを担いで…え？\n肩車すればいいのかって…\nそういう意味じゃねーですよ！！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1165003, 116501, 0, 0.0, 2, "あたしがランドソルで一番の\nボスになったら、カルミナを\n呼んでパーティーです！\nお兄さんも呼んであげますから！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1165004, 116501, 0, 11.0, 3, "このポーズとこのポーズ\nどっちがかっこいいですか！？\n…どっちもかわいい？\nガ、ガキ扱いするなですよ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1165005, 116501, 0, 7.9, 3, "お兄さんの仕事は\nそのお人好し力を\nあたしのために使うことです！\nよそ見は禁止ですよ！！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1165006, 116531, 0, 0.0, 1, "どうですか、このポーズ！\n…え？　台の上はあぶない？\nお兄さんは分かってないですね\n高い所に登るのがボスの鉄則です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1165007, 116531, 0, 0.0, 1, "お兄さんには見えますか！\n空に輝くあの一番星が！\nあれこそギャングスターです！\n今は昼？　心の目で見るですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1165008, 116531, 0, 0.0, 1, "あたしがボスになったら\nイノリのお菓子工場を作らせます\nお小遣いとお菓子の両取りですよ\nくぅ〜！　夢が広がるです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1165009, 116531, 0, 0.0, 1, "あたしの計画ではお兄さんは側近\nカヤぴぃは右腕にして、ふふっ\nボスは雑用係にしてやるですよ！\nぴっ！？　今寒気がしたような…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1165010, 116531, 0, 0.0, 1, "ふっ、この服どうですか？\nかっこいいって言っても…\nはぁ！？　か、かわいいって\n言うのは、やめやがれですー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1165011, 116501, 0, 2.9, 2, "今日はお兄さんの誕生日？\n一億年前から知ってましたよ！\nタコスおごるので、お兄さんは\nあたしにタコスおごるですよ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1165012, 116531, 0, 0.0, 1, "お誕生日おめでとうです！\nあたしは部下想いのボスですから\nわがままを聞いてあげなくも…\nってなんですその本気の目は！？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1166001, 116601, 0, 6.4, 1, "全く、喧嘩屋のオレに\nこんな格好までさせやがって\nまあ、やるからにはキッチリ\nやるさ。半端なのは嫌いだからな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1166002, 116601, 0, 7.5, 6, "この格好、\n案外動きやすいんだよな\nこれならケンカも…\nや、やらねえよ！　…多分", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1166003, 116601, 0, 4.9, 6, "本日はご利用いただき\nありがとうございまーす！\n…自分で言うのもなんだけど\n全然似合わねーな！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1166004, 116601, 0, 13.5, 6, "この仕事は笑顔が大事\n分かっちゃいるけど難しいぜ…\nに、にこぉ…って\n何見てんだよ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1166005, 116601, 0, 6.0, 2, "キミがついてきてくれてると\nなんだか一緒に旅してる\nみたいな気がする\n結構楽しいもんだな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1166006, 116631, 0, 0.0, 1, "よく来たな。人気スポットから\n危険地帯まで、どんなトコでも\nガイドしてやるよ\nさあ、たぎるような旅に出発だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1166007, 116631, 0, 0.0, 1, "現地でトラブルがあったら\n迷わずオレを呼びな！\nランドソルで一番荒事に強い\nガイドだからな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1166008, 116631, 0, 0.0, 1, "キミの頼みならどんな危険な\n場所だって連れてってやるよ\nドラゴン族渾身のガイドで\n存分に楽しませてやるからな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1166009, 116631, 0, 0.0, 1, "キミがいなけりゃ、この仕事も\nこんなに上手くいかなかった\nだろうな。やっぱダチってヤツは\n最高だぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1166010, 116631, 0, 0.0, 1, "なあ、いつか二人で\n旅に行こうぜ！　仕事とか\n関係なく、好きなとこ行くんだ\nキミとならきっと楽しいだろうな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1166011, 116601, 0, 2.0, 2, "今日誕生日だろ？\nほら、プレゼントだ\nこの前土産物屋に行った時に\nいいなって思ってさ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1166012, 116631, 0, 0.0, 1, "よう、誕生日おめでとう！\n今日はオレが厳選した店に\n連れてってやるよ。年に一度の\n大事な日だ、パーッと祝おうぜ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1167001, 116701, 0, 5.4, 2, "…よし！　…よし！　…よし！\nうん、発声練習終わり…！\n今日も一日、ベルトコンベアと\nにらめっこです…！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1167002, 116701, 0, 0.0, 2, "日が昇る前から\n工場の前に立ってますよ…！\n無遅刻無欠勤を目指します！\nえっへん！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1167003, 116701, 0, 5.7, 2, "ランチは工場の先輩から\n誘われるまで倉庫の隅っこで…\nあなたも一緒にどうですか…？\n静かで過ごしやすいですよ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1167004, 116701, 0, 0.0, 2, "工作の技術は\nマイフレンドくん作りで\n身に付けました…！\n工場でも通用するといいなあ…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1167005, 116701, 0, 6.8, 6, "うう…人生初めてのアルバイト…\n不安で消えてなくなりそうです…\nで、でも…あなたも一緒だから\nきっと大丈夫ですよね…！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1167006, 116731, 0, 0.0, 1, "『だいじょぶマイフレンドくん\nＶ４』のカスタマイズも…\nバイト仲間を作ってぼっち卒業も\nこのバイトで叶えてみせます…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1167007, 116731, 0, 0.0, 1, "ぼっちの私に、誰かと一緒に\n作業する日が来るなんて…！\nまさか夢…！？\n絶対そうに決まってます〜！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1167008, 116731, 0, 0.0, 1, "バイト先でもＢＢ団の使命は\n忘れませんよ…！\nぼっち卒業を目指して\nバイト仲間作りにも励みます…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1167009, 116731, 0, 0.0, 1, "工具や機械の使い方も\n慣れてきました…！\nけど、バイト先の人との会話は\n全然慣れません…ううっ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1167010, 116731, 0, 0.0, 1, "ＢＢ団でそろって\n一緒にバイトができるなんて…！\nおもちゃも、バイト仲間も\n作りましょうね…えへへ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1167011, 116701, 0, 7.9, 2, "あなたのお誕生日を祝う言葉を\nわ、私が…！？　し、幸せすぎて\nちょっと怖いですが…せーのっ\nお誕生日おめでとうございます！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1167012, 116731, 0, 0.0, 1, "今日はあなたのお誕生日をお祝い\nさせてください！　歌に、花束の\n贈呈…それと100通の手紙です\n…では心を込めて歌います！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1168001, 116801, 0, 5.4, 2, "たい焼きいかがかにゃ～！\n…あ、キミいいところに！\n手伝ってくれたら特別に\nたい焼きの賄いをあげるにゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1168002, 116801, 0, 5.8, 1, "にゃはは♪\nこのたい焼きメーカーがあれば\n売り上げ爆増だにゃ！\n貯金箱が貯まっていくにゃ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1168003, 116801, 0, 3.8, 2, "お客さんが美味しそうに\nたい焼きを食べてるにゃ！\nやっぱりたい焼きは\nみんなを幸せにするにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1168004, 116801, 0, 6.0, 1, "キミ、じっとあたしを見てるけど\nこの服装気に入ったにゃ？\nなんなら…特別価格で\nポーズしてあげるにゃ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1168005, 116801, 0, 8.7, 2, "忙しくても笑顔は忘れにゃい！\n商売人の鉄則にゃ！\nほら、キミも笑顔にゃ！\n…うん、その方が男前にゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1168006, 116831, 0, 0.0, 1, "忙しくても大丈夫にゃ♪\n猫の手に代わって\n自動たい焼きメーカーが\nあたしをサポートしてくれるにゃ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1168007, 116831, 0, 0.0, 1, "たい焼きを買ってくれたお礼に\nスマイルもサービスするにゃ！\nウェイトレスのバイトも\n出来そうな気がするにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1168008, 116831, 0, 0.0, 1, "たい焼き屋の商売が忙しくても\n義賊活動でピンチになっても\n背中を預ける人がいれば平気にゃ\nこれからもよろしくにゃ、相棒！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1168009, 116831, 0, 0.0, 1, "キミへ賄いのたい焼きにゃ！\nサイズが大きいのは\nいつも助けてもらってるお礼って\nことで、受け取るにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1168010, 116831, 0, 0.0, 1, "お客さんの笑顔が\nあたしの夢の源にゃ！\nそれでキミの笑顔は…\nあたしの元気の源にゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1168011, 116801, 0, 5.6, 1, "誕生日おめでとうにゃ！\n特別にたい焼きケーキを\n作ってきたにゃ！\n食べさせてあげようかにゃ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1168012, 116831, 0, 0.0, 1, "今日はキミの誕生日にゃ！\n特別に0ルピで今日はあたしを\n貸し出ししてあげるにゃ！\nなんならデートするかにゃ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1169001, 116901, 0, 4.6, 2, "私たちが作ったおもちゃで\n子どもたちが喜んでくれる…\nふふ、やりがいのある\n仕事って素敵よね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1169002, 116901, 0, 1.7, 1, "こういう作業服って、\n普段着とは違って\nちょっと新鮮な感じが\nしていいわね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1169003, 116901, 0, 3.9, 1, "機械や工具のメンテも\n定期的にやらないと…\nみんなが使うものほど\n丁寧に扱わないとね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1169004, 116901, 0, 4.6, 1, "キミを一人前に鍛えるのも\nバイトリーダーのお仕事だもの\nわからないことがあれば\nすぐに聞いてちょうだいね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1169005, 116901, 0, 7.3, 2, "キミさえよければ、このまま\n私の右腕としてずっと\n一緒にいてくれないかしら…\nなんてね。さ、仕事するわよ！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1169006, 116931, 0, 0.0, 1, "リーダーとして\nキミを導いてみせるわ！\nもちろん、効率全開で！\nさあ、準備はいい！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1169007, 116931, 0, 0.0, 1, "今日の製造ノルマも\n順調に消化中…いい感じね！\nさあ、このまま最大効率で\n走り抜けるわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1169008, 116931, 0, 0.0, 1, "アオイちゃんもタマキさんも\nよく働いてくれて助かるわ\nキミも一緒なら鬼に金棒よ！\nどんなノルマもどんとこーい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1169009, 116931, 0, 0.0, 1, "お仕事が終わったら\n夕飯でも一緒にどう？\n近場の安くておいしい\nお店、見つけたのよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1169010, 116931, 0, 0.0, 1, "キミといるとそれだけで\nいつも以上に能力を\n発揮できる気がするのよね…\nなんでなのかしら…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1169011, 116901, 0, 4.5, 2, "誕生日おめでとう！\nまた一歩大人になったわね\nキミの成長をそばで見てると\n私も師匠として鼻が高いわ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1169012, 116931, 0, 0.0, 1, "今日はキミの誕生日よね？\nお祝いのケーキを作ってみたの\n自分で作ると節約できるし、\n味にもけっこう自信あるのよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1170001, 117001, 0, 6.1, 5, "真夏の日差しのもと、あなた様の\nすべてを手に入れます…\n運命の伴侶として…\n永遠に…離しませんわ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1170002, 117001, 0, 5.5, 2, "私たちの間を引き裂くものは\nすべて破壊しつくしますわ…\nそう、海の藻屑と消えるまで\n細かく細かく…念入りに…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1170003, 117001, 0, 3.4, 2, "この水着、似合いますか？\n…えぇ、いいお返事…\nあなた様との最高の夏のために\n最高のものを選びましたわ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1170004, 117001, 0, 9.9, 2, "海で水着くらい当然ですわ…\nでもあなた様に褒めていただける\nのは純粋にうれしいもの\nクスクス…もっと褒めてください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1170005, 117001, 0, 15.4, 2, "クスクス…真夏の海で二人きり…\n私だけを…見てくださいね…\nよそ見をしたらどうなるかは\n…秘密です…クスクス…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1170006, 117031, 0, 0.0, 1, "夏の夜空に花を咲かせましょう\nあなた様が私以外全て\n忘れてしまうくらい…めくるめく\n夢のような花火を…クスクスクス", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1170007, 117031, 0, 0.0, 1, "私とあなた様が結ばれるべき\n運命だということを…この夏に\n証明して差し上げますわ\nどんな手段を使ってでも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1170008, 117031, 0, 0.0, 1, "この水着、似合いますか？\nあなた様を想って選びました…\n生地もこだわって…ほら、もっと\n近くで見てください…クスクス", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1170009, 117031, 0, 0.0, 1, "昼も夜もあなた様のことを\n考えてしまいます…あなた様も\nずっと私のことを考えて\nくださいね…絶対に…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1170010, 117031, 0, 0.0, 1, "寄せては返す波のように\n空に瞬く花火のように\nずっとあなた様といたいのです…\n愛していますわ、あなた様…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1170011, 117001, 0, 8.4, 5, "お誕生日おめでとうございます\nあなた様…特製の薬草茶で\n乾杯しましょう…そして薬草\n料理のフルコース…クスクス…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1170012, 117031, 0, 0.0, 1, "あなた様の誕生日…この日を\n共に迎えることができて私は\n幸せですわ…これからも毎年\n私とだけお祝いいたしましょう…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1171001, 117101, 0, 5.0, 1, "二人で一緒に海デート♪\n仲良しきょうだいだもん\nこれくらい普通だよねっ！\nあ、腕組んで歩こっ、弟くんっ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1171002, 117101, 0, 3.2, 2, "熱い日差しにキラキラ輝く海！\nそして水着のお姉ちゃん♪\n弟くん、ワクワクしてこないっ？\nねぇねぇ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1171003, 117101, 0, 6.3, 5, "この水着、過激すぎないよね？\n弟くんを誘惑しすぎないよう\n気をつけなくちゃ…だけど…\nもう少しだけ、大胆にしちゃお♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1171004, 117101, 0, 4.6, 2, "弟くんが海で危ない目にあっても\nお姉ちゃんがいるから大丈夫！\n絶対にキミのそばを離れたり\nしないんだからねっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1171005, 117101, 0, 7.5, 2, "ねぇ、もっとくっついて歩こう？\n周りの人たちに見せつけたいんだ\nお姉ちゃんの自慢の弟を♪\nえへへ、照れない照れない♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1171006, 117131, 0, 0.0, 1, "弟くん、休憩中かな\nお姉ちゃんも一緒に休んでいい？\nふふっ、あんまり見つめられたら\nお姉ちゃん、照れちゃうな…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1171007, 117131, 0, 0.0, 1, "ねっ、次はなにしてあそぼっか！\n弟くんが楽しめること\nぜ～んぶやっちゃおう♪\nほら、立って立って♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1171008, 117131, 0, 0.0, 1, "この水着ね\n弟くんが好きそうなのを\n一生懸命選んだんだよ？\nじ～っくり、堪能してねっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1171009, 117131, 0, 0.0, 1, "弟くん、暑いでしょ？\nお姉ちゃんが冷やしてあげよっか\n…ふ～っ♪\nえへへ、涼しいでしょ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1171010, 117131, 0, 0.0, 1, "弟くんと一緒に海水浴なんて\nお姉ちゃん、ドキドキしちゃうな\n…ねぇ、弟くん？　私たち…\n周りからはどう見えるのかな…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1171011, 117101, 0, 7.1, 2, "弟くんのお誕生日だから\n水着でお祝いしてあげるねっ♪\nえ？　なんで水着なのかって？\nもうっ、うれしいくせに～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1171012, 117131, 0, 0.0, 1, "弟くん、欲しいものはある？\nまた一つ大人になった弟くんには\nなんでもあげちゃうよっ♪\nお姉ちゃんでも…いいんだぞ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1172001, 117201, 0, 4.6, 7, "夏といえばアイドルの季節！\n歌って、踊って、キラキラして！\nみーんなまとめて\n笑顔にしちゃうから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1172002, 117201, 0, 4.3, 1, "アイドルって結構、\n体力勝負なとこあるんだよね\n今度、砂浜で走りこもうっと！\nキミも一緒に走る？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1172003, 117201, 0, 4.7, 1, "ねえねえ！　せっかくの夏だし、\nお化け屋敷にでも行こう！\n怖くなっちゃったら、\n私の手を握ってもいいよ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1172004, 117201, 0, 7.3, 2, "水着のために、\nダイエット頑張ったんだよ！\n誰か褒めてくれないかなー？\n…えへへ、ありがと！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1172005, 117201, 0, 6.4, 2, "夏の紫外線はすっごく強烈なの！\nキミも気をつけた方がいいよ？\n…というわけで\n私の日傘で相合い傘しよっか♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1172006, 117231, 0, 0.0, 1, "水着はアイドルを輝かせる\n特別な衣装なの！\nいつもより更にキラッキラな私に\n夢中になっちゃってもいいよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1172007, 117231, 0, 0.0, 1, "海も空もきれいだけど、\nノゾミの方がもっときれいだよ…\n的なこと言ってもいいんだよ？\nなーんてね、ふふっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1172008, 117231, 0, 0.0, 1, "今度、いつキミと海に来られるか\n分かんないし…\n今日は徹底的に遊んで\nいーっぱい思い出、作っちゃお♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1172009, 117231, 0, 0.0, 1, "私って人気アイドルなんだよ？\nそんな私の水着を見たのに、\n普段通りの反応？　ふふふ、\nキミのそういうとこ、好きだよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1172010, 117231, 0, 0.0, 1, "きゃっ！　…もー、やったな～！\n私も水かけちゃうから！　えい！\n…なんかバカップルっぽいね！\nあはは！　さ、もっと楽しもう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1172011, 117201, 0, 7.0, 5, "誕生日おめでとー！\nキミの好きそうなアイスケーキ、\n人気のお店で買ってきたよ♪\n後で、一緒に食べようね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1172012, 117231, 0, 0.0, 1, "ハッピーバースデー！\nキミへのプレゼントは…\n私を今日だけ独り占めできる券！\n…なんて、どうかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1173001, 117301, 0, 9.8, 2, "きらめく太陽に美しい海…\n夏まっさかり、ですね\nあなたと過ごす夏がどんなものに\nなるのか…楽しみです。ふふ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1173002, 117301, 0, 7.1, 2, "この衣装、ツムギが\n作ってくれたんです\n少し恥ずかしいですけど…\nでも、大好きな衣装です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1173003, 117301, 0, 6.6, 6, "ビーチバレーにバーベキュー…\n夏にやってみたいこと、\nたくさんあるんです！\n…少し浮かれ過ぎでしょうか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1173004, 117301, 0, 8.2, 4, "日焼けには気をつけないと\n…え？　日焼け止めを塗るの\n手伝おうかって…え、えっと\nでは…お、お願い…します…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1173005, 117301, 0, 6.5, 5, "波の音が好きなんです\nまるで心が洗われるようで…\n…よければ、ですけど\n一緒に、海を見に行きませんか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1173006, 117331, 0, 0.0, 1, "夏は開放的な気持ちになりますね\n今なら素直に\n自分の想いを歌に込められそう…\n私の夏の歌、聴いてもらえますか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1173007, 117331, 0, 0.0, 1, "海で歌うのがこんなにも\n楽しいなんて知りませんでした\nあなたも一緒に歌いませんか？\n夏の思い出を共に作りたいんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1173008, 117331, 0, 0.0, 1, "唱喚士として、アイドルとして…\nみんなの期待に応えるためにも\nこの夏、もっと成長したいです\nどうか見守っていてください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1173009, 117331, 0, 0.0, 1, "まるで人魚みたい、ですか…？\nなら、おとぎ話のように、\n歌であなたを魅了しちゃいます\n…なんて、ふふ。冗談です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1173010, 117331, 0, 0.0, 1, "今までの人生の中で\nこの夏が一番ドキドキして\n一番、幸せでした\n…あなたのおかげです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1173011, 117301, 0, 2.2, 1, "ハッピーバースデー！\nあなたと過ごした夏は、\n私にとって大切な思い出です\n今後ともよろしくお願いしますね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1173012, 117331, 0, 0.0, 1, "お誕生日、おめでとうございます\n夏の太陽のようなあなたに贈る\nバースデーソングを作りました\n気に入ってくれるといいのですが", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1174001, 117401, 0, 7.6, 1, "いちにーさんしー…\nえ？　何してるのかって\nライブの練習ですよ\n騎士さんも一緒にやります？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1174002, 117401, 0, 7.7, 1, "レイ様と海へ来たかったな…\nあ、別に騎士さんと一緒が\n不満ってわけじゃないですよ！？\nむしろ結構楽しんでますから！！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1174003, 117401, 0, 5.8, 1, "騎士さんもう疲れたんですか？\nもう、だらしがないですねぇ\n夏バテなら大変なんで\nこっちで一緒に涼みましょうか", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1174004, 117401, 0, 3.6, 6, "今日は仕事のことを忘れて\n遊びますよ！\n…あれ、あの子の水着かわいい\n次のデザインの参考にしよう…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1174005, 117401, 0, 5.3, 3, "さっきから何見てるんですか？\n言いたいことはハッキリと…\nあ、でも変なこと言ったら\n縛りますからね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1174006, 117431, 0, 0.0, 1, "ん～！　いい天気ですね！\n風も気持ちい～！\n今日は思いっきり遊びますよ！\n騎士さんも付き合ってください！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1174007, 117431, 0, 0.0, 1, "どうですか、この水着！\nサマーライブのために\n私が作った衣装なんですよ！\nかわいいでしょう！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1174008, 117431, 0, 0.0, 1, "どうですか？\nアイドルと一緒に過ごす夏は？\n…すごく楽しいって\nほんと騎士さんは素直ですね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1174009, 117431, 0, 0.0, 1, "騎士さん、泳げないんですか？\nそれなら浅瀬へ行きましょう\n…一人で泳いでたら\n騎士さんと来た意味ないですし…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1174010, 117431, 0, 0.0, 1, "あれ？　この浜辺よく見たら\nカップルだらけじゃないですか…\nということは私たちも…\nだとしても私は気にしませんよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1174011, 117401, 0, 5.1, 5, "レイ様があげるというので\n私からもプレゼントです\nべ、別に仕方なくじゃないです\nちゃんと心は込めてるので！", 3, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1174012, 117431, 0, 0.0, 1, "大したものじゃないですけど\n作ってみたのでどーぞ\nどうして僕にって…\nわ、私も誕生日ぐらい祝います！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1175001, 117501, 0, 4.5, 1, "さあ、診察を始めましょう\n最初の患者は…もちろん君よ\n私の助手を務めてもらうからには\n健康でいてもらわないと♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1175002, 117501, 0, 4.4, 5, "特別にあつらえた\nこの服、どう？\nふふっ、君には少しだけ\n刺激が強いかしら♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1175003, 117501, 0, 7.2, 2, "せっかくオーエドまで来たんだし\nこの機会にカンポーを使って\n新しい薬を作りたいわね\n君も実験に付き合ってね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1175004, 117501, 0, 4.8, 1, "私は闇医者だから、善意だけで\n仕事はしないわ。でも…\n人助けもたまには悪くないわね\nふふっ、誰の影響かしら♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1175005, 117501, 0, 8.4, 2, "どれほど優秀な医者でも、\n一人ではできることに限りがある\nそれは闇医者も同じよ…だから\n君のサポート、頼りにしてるわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1175006, 117531, 0, 0.0, 1, "滋養薬に強壮薬、回復薬に劇薬も\n私はどんな薬だって作れるのよ\nこれが薬か毒薬か\n君の体で試してみる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1175007, 117531, 0, 0.0, 1, "こう見えて薬で治すだけじゃなく\n手術の腕もなかなかなのよ\n痛みなんて感じさせないわ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1175008, 117531, 0, 0.0, 1, "医者として、科学者として…\n君は本当に興味深い研究対象だと\n思っているの。これからも\n実験や観察、させてもらうわね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1175009, 117531, 0, 0.0, 1, "少し疲れちゃったわ…\n肩を貸してもらえるかしら？\n君にもたれて仮眠をすると\nとても安心できるのよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1175010, 117531, 0, 0.0, 1, "いいところに来たわね\n新薬の実験に付き合ってくれる？\n私に服従する薬よ\nあら、遠慮しなくてもいいのに♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1175011, 117501, 0, 8.3, 5, "お誕生日おめでとう\nこれからも健やかでいられるよう\nお祝いに薬膳鍋を用意したわ\nほら、口をあけて…あ～ん♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1175012, 117531, 0, 0.0, 1, "ハッピーバースデー\nお祝いに特製の薬を調合したの\nひと口で全身に力がみなぎるわ\nまだ、実験段階のものだけどね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1176001, 117601, 0, 6.4, 1, "うーん、さすがボク、\nどんな服を着ててもかわいいね♪\nほらほら、もっと近くで見ないと\n美しさを堪能できないよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1176002, 117601, 0, 6.1, 1, "ふふっ！　どう？\nいつもと違うボクの美しさ！\nしっかり目に焼き付けて\nおくといいよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1176003, 117601, 0, 6.9, 1, "なんだい？　もしかして\nボクに看護してほしいのかな？\nだからって、仮病はだめだよ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1176004, 117601, 0, 5.4, 6, "ただでさえ美しいボクが\n白衣の天使だなんて…\nもしかして病気の人には\n刺激が強すぎるかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1176005, 117601, 0, 6.7, 5, "天使なボクの姿を\nもっと見たいのかい？\nキミにならいいよ\n心ゆくまでじっくりと見てて♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1176006, 117631, 0, 0.0, 1, "美しいボクはこうしているだけで\nみんなを癒しちゃうんだよね\n白衣の天使ってボクの\n天職かもしれないね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1176007, 117631, 0, 0.0, 1, "もしキミが疲れちゃった時は\nこのボクの最高のかわいさで\nたっぷりと癒してあげるよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1176008, 117631, 0, 0.0, 1, "ボクのこのかわいらしさ…\n美と医学の進歩のための\n何かのヒントになる気がするよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1176009, 117631, 0, 0.0, 1, "ちょっと待って！\n動かないでよく見せて…\nよし、キミの瞳の中のボクは\n今日もとても美しいね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1176010, 117631, 0, 0.0, 1, "いつも一番近くでボクの美しさを\n見てるキミがすごいってことは\nボクの美しさがすごいってことに\nなるんじゃないかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1176011, 117601, 0, 5.6, 5, "どう？　ボクの美しさが\n誕生日プレゼントだよ！\nいつも以上にじっくりと\n見てもいいからね♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1176012, 117631, 0, 0.0, 1, "今日のボクは特別に美しい？\nキミの誕生日だもの\n最高に美しいボクを\nプレゼントしてあげないとね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1177001, 117701, 0, 5.1, 2, "ランドソルのハロウィンって\nでーじ盛り上がるんだね～♪\n私たちもとことん\nハロウィンを楽しみまくるさ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1177002, 117701, 0, 4.2, 2, "ハロウィンの平和は\n私たちが守るさ～！\nもし幽霊が出てきても\nなんとかしてみせるさ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1177003, 117701, 0, 3.6, 1, "私も仮装してみたいさ～\nゴーヤーの仮装はどうかな～？\nきっと子どもたちの\n人気者になれるさ～♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1177004, 117701, 0, 3.0, 1, "トリックオアトリート！\n…あれ、お菓子持ってないの？\nならイタズラするしかないね～\n脇腹くすぐるから覚悟するさ～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1177005, 117701, 0, 6.5, 5, "ハロウィンセイバーズの制服、\n似合っててかわいいねって？\nあは～、ありがとね～♪\n…あれ？　なんか顔が熱いさ～…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1177006, 117731, 0, 0.0, 1, "こうやって踊れば、\n幽霊とだって友達になれるさ～♪\nほら、キミも一緒に\n自由に楽しく踊るさー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1177007, 117731, 0, 0.0, 1, "これは私の故郷の踊りさ～♪\nうれしい時とかに、\nこうして踊るんだよ～\n踊り方、教えてあげよっか～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1177008, 117731, 0, 0.0, 1, "花火って夏のイメージだけど…\n秋に見る花火も最高さ～！\nやっぱりランドソルの\nハロウィンはすごいね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1177009, 117731, 0, 0.0, 1, "花火、でーじきれいで\nロマンチックだね～♪\nキミと一緒に見れて、\n私、すっごく幸せさ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1177010, 117731, 0, 0.0, 1, "キミと過ごすハロウィンは\nとびきり特別さ～♪\n一生忘れられないくらい\n楽しい夜にしようね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1177011, 117701, 0, 0.0, 2, "誕生日おめでと～！\nカボチャのサーターアンダギーを\nいーっぱい作ってきたんだ～\n遠慮せずたくさん食べるさ～♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1177012, 117731, 0, 0.0, 1, "あは～、幽霊たちも\nおめでと～って言ってるさ～♪\n誕生日祝いに、\nみんなで一緒に踊るさ～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1178001, 117801, 0, 9.3, 4, "怪しい雰囲気が漂っていマスね…\nハっ！？　まさか、あの世から\n悪霊が蘇っているデスか！？\nなんともオソロシヤデ～ス…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1178002, 117801, 0, 6.9, 1, "妖魔や幽霊の能力を参考に\n新しい忍法を考えてみまショウ！\n変装したり…姿を消したり…\nいい忍法が開発できそうデス！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1178003, 117801, 0, 5.1, 2, "せっかくのお祭りデスから\n髪型も変えてみまシタ！\nどうデスか？　似合ってマスか？\nショーグンに褒めてほしいデス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1178004, 117801, 0, 6.0, 3, "ハロウィンセイバーズの制服は\n大きなマントがカッコイイデス！\nシュバっとひるがえして\n闇夜を駆けまショウ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1178005, 117801, 0, 5.0, 2, "あの世でも、ワタシは\nショーグンの家来でいマスよ\n永遠にお仕えするデース！\n忠誠心を燃やし続けマス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1178006, 117831, 0, 0.0, 1, "ヒャクマンゴクの\nヒャッキヤコウデース！\n夜通し踊るハロウィンの宴に\n集まるデス！　ピーヒャララー♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1178007, 117831, 0, 0.0, 1, "人間も妖魔も幽霊も\nそんなの関係ないデース！\nみんな笑顔でヒャッキヤコウを\n楽しみまショウー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1178008, 117831, 0, 0.0, 1, "夜は短し騒げよ乙女デース！\n朝まで踊り明かして\nハロウィンを満喫しまショウ！\nソイヤソイヤ、ソレソレデース！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1178009, 117831, 0, 0.0, 1, "ショーグンもハロウィンを\nお楽しみデスか？\n今夜はブレイコウで\nはしゃぎまくるデース！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1178010, 117831, 0, 0.0, 1, "ショーグンと過ごすお祭りは\n格別デス！　…ショーグンは\nどうデスか？　ワタシと過ごせて\nうれしいデスか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1178011, 117801, 0, 0.0, 2, "ショーグンのお誕生日を\nお祝いするデス！　バースデー\nソングをこの笛で演奏しマス！\nピッピヒョーロロープープーー♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1178012, 117831, 0, 0.0, 1, "お誕生日アッパレデース！\nショーグンのためにパレードを\n用意しまシタよー！\n盛大にお祝いするデース！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1179001, 117901, 0, 4.3, 1, "ハロウィンのお出かけすんの\nちょー楽しみにしてたんだ～♪\nうち見たいとこいっぱいあるし\nヒデサイも付き合ってよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1179002, 117901, 0, 2.7, 1, "カボチャ、カボチャ～♪\nヒデサイってカボチャ好き？\n今度料理作ったげよっか\nうち、腕によりをかけちゃうし！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1179003, 117901, 0, 3.9, 7, "ハロウィンセイバーズの\n制服、もにカッコカワで\nうち的にもけっこー\nお気に入りなんだ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1179004, 117901, 0, 4.9, 1, "きゃはーっ♪　みんな仮装に\n気合入りまくりじゃーん！\nヒデサイが仮装するときは\nうちがコーデしたげるし！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1179005, 117901, 0, 0.0, 2, "ヒデサイと一緒だと\nマジ楽しすぎてヤバいかも！\nテンション上がりすぎて\nやばばばーって感じ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1179006, 117931, 0, 0.0, 1, "こんなに楽しいハロウィン\nうち初めてかも！\nヒデサイも楽しんでる～？\nもっとテンション上げてこ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1179007, 117931, 0, 0.0, 1, "キャンディーにクッキーに…\nみんなに配るお菓子の\nチョイスってこんな感じでおけ？\nいっぱい配ろうね、ヒデサイ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1179008, 117931, 0, 0.0, 1, "ハロウィンのお菓子って\nもにカワすぎてヤバくない？\n食べるのもったいないし…\nうー、ヒデサイどーしよ～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1179009, 117931, 0, 0.0, 1, "ハロウィンのうちも\n超キマってるっしょ♪\nヒデサイをうちの\nトリコにしちゃうし！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1179010, 117931, 0, 0.0, 1, "はぐれたらヤバいし\nうちと手つないどこ？\nきゃははっ♪　そんな\n照れなくていいじゃん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1179011, 117901, 0, 7.6, 2, "ヒデサイ誕生日おめ～♪\nプレゼントなにがいい？\nあ、一緒に選びに行く？\nりょ！　そんじゃ行こー！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1179012, 117931, 0, 0.0, 1, "今日、ヒデサイの誕生日とか\nまじエモくない？\n最高の日じゃん！\nうちと一緒にお祝いしよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1180001, 118001, 0, 0.0, 3, "まったく、あの甘っちょろ\nお姫さまったら！\n公務をなんだと思ってるのかしら", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1180002, 118001, 0, 2.8, 2, "所詮、この世はお金です！\n金さえあれば、\nなんでもできますわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1180003, 118001, 0, 0.0, 2, "【リッチモンド商工会】は\nわたくしが一から作り上げた\n才能と努力の結晶なのですわ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1180004, 118001, 0, 4.1, 1, "貴方、あの甘ちょろ陛下の\n騎士なのですって？\nずいぶん酔狂なものですわね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1180005, 118001, 0, 4.5, 1, "貴方が望むなら、わたくしが\n雇ってさしあげましてよ？\nまずは見習いからですけれどね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1180006, 118031, 0, 0.0, 1, "金銀財宝！\n宝石に美術品！\nこうして囲まれていると…ああ！\n心が潤うようですわ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1180007, 118031, 0, 0.0, 1, "所詮、この世はお金です！\n金さえあれば、\nなんでもできますわ♪\n…そう思っておりましたけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1180008, 118031, 0, 0.0, 1, "この世には、金銀財宝にも劣らず\n輝くものがありますのね\nたとえば…そう！\nわたくしの忠義の心とかっ！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1180009, 118031, 0, 0.0, 1, "ちょっとちょっと秘書1号？\n定時だからって\n上がれませんわよ？\nまだ仕事は残っておりましてよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1180010, 118031, 0, 0.0, 1, "仕事はまだまだ山積み…\nこうなったら少年、貴方にも\n手伝わせて差し上げますわ！\nお礼の方は、応相談でしてよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1180011, 118001, 0, 0.0, 2, "貴方、誕生日ですって？\nそれはおめでとうございます\n何か食べたいものはありまして？\nご馳走くらいしてあげますわよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1180012, 118031, 0, 7.5, 1, "お誕生日、おめでとうございます\n陛下の騎士として、\n今後とも一層励んでくださいな\nわたくしも手助けいたしますゆえ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1181001, 118101, 0, 5.6, 1, "歌っている時は…落ち着くの…\nほかのうるさい音も、\n自分の声で、紛れるから…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1181002, 118101, 0, 7.8, 5, "えっ？\nまた、膝枕…してほしいの？\nま、待って…\n心の、準備がっ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1181003, 118101, 0, 11.5, 5, "ときどき…詩を書いたりもするの\n…あなたの詩も書いたけれど…\nダ、ダメ…見せられないわ\n恥ずかしいもの…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1181004, 118101, 0, 7.4, 2, "敵だったわたしに\n手を差し伸べてくれた\nあなたの気持ちに…\n少しでも、応えたいの…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1181005, 118101, 0, 12.2, 7, "話すのは苦手だけど、\nあなたと話すのは…好き…\nあっ…あなたが嫌だったら、\n無理、しないで…いいけど…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1181006, 118131, 0, 0.0, 1, "…あなたに救われてばかりの\n自分では、いたくないの…\n次は、わたしがあなたを\n助ける番…。…だからっ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1181007, 118131, 0, 0.0, 1, "人を傷つけるためではなく…\n守るために、歌います。\nもう二度と、大切な人を\n失わないように…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1181008, 118131, 0, 0.0, 1, "うるさい声に、耳を塞いできた…\nでもずっとそうしていたら、\nやさしい声も聞こえなくなるわ…\n今は…そのほうが嫌なの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1181009, 118131, 0, 0.0, 1, "あなたを見てると…甘やかして…\nぎゅっとして、あげたくなるの…\nこの気持ちは、母性…？\n…それとも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1181010, 118131, 0, 0.0, 1, "『こちら』に来る前は…\nわたしの居場所なんて\nどこにもなかった…でも、今は\n違うって。そう、思いたいの…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1181011, 118101, 0, 0.0, 2, "お誕生日、おめでとう…\nケーキ作り、失敗してしまって\n歌しかないのだけれど…\n心を込めて歌うわね…？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1181012, 118131, 0, 0.0, 1, "ハッピーバースデートゥーユー…\n…ふふ。あなたをお祝いする曲を\n歌えることがこんなにうれしい…\n来年もまた、聴いてね…？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1182001, 118201, 0, 7.6, 2, "騎士さん、お出かけしましょ♪\n場所はー…どこでもいいですよ？\n騎士さんと一緒なら…★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1182002, 118201, 0, 10.7, 2, "騎士さんは人気者ですよねー\nこんな人を好きになって\nそして想いが叶わなかったら…\nどれだけ傷つくんでしょうねー★", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1182003, 118201, 0, 2.2, 1, "過去のこと？\n責めちゃっていいですよ？\nやったことは変わりませんし\n恨まれても仕方ないです★", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1182004, 118201, 0, 7.5, 1, "んー、もっと騎士さんと\n二人きりになりたいんですよねー\n駆け落ちとかしちゃいます？\n…なーんて★", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1182005, 118201, 0, 0.0, 1, "騎士さんって、\nとっても優しいですよねー\nでもでもー、わたしには\n優しくしなくて、いいですよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1182006, 118231, 0, 0.0, 1, "どるるるる～んっ！　…あはっ★\nガトリングってサイコーですね\n狙うのヘタでも関係ないですしー\nたくさん壊せて快感♪　です♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1182007, 118231, 0, 0.0, 1, "ぺんぽこりん、ごーごー★\nえ？　中の人？\nんもー★　そんなの\nいるわけないじゃないですかぁ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1182008, 118231, 0, 0.0, 1, "ライクからラブになるには\n「キュン★」が大切ですからー\nだからもっともーっと…\n「キュン★」な展開希望です★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1182009, 118231, 0, 0.0, 1, "騎士さんがピンチの時は\n言ってくださいねー\nラブ＆テレポーテーションで\n駆けつけちゃいますから★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1182010, 118231, 0, 0.0, 1, "誰かをスキになるのって\nこんなに不思議なんですね\nまるでわたしが、違うわたしに\nなっちゃうみたいです…★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1182011, 118201, 0, 10.0, 1, "ハッピーバースデー★\nケーキのロウソクって、\n導火線みたいじゃないですか？\n消した瞬間に…どかーん♪　とかっ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1182012, 118231, 0, 0.0, 1, "騎士さんのお誕生日★　なら、\n盛大にお祝いしなくちゃですね！\nそれじゃわたし、盛大な祝砲を…\nえっ、だめです？　…はーい★", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1185001, 118501, 0, 5.3, 2, "私たちの仕事は地味かもしれない\nですけど、とても大切なんです！\nこれからも全身全霊でサポート\nさせていただきますね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1185002, 118501, 0, 4.5, 2, "冒険と同じくらい書類仕事も\n楽しいものですよ？\nどうでしょう。今度一緒に\n働いてみませんか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1185003, 118501, 0, 7.5, 1, "この書類はあの部署に提出して…\nこの申請はあの人にして…\nこの討伐依頼は…あっ！\nちょうどいいところに！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1185004, 118501, 0, 4.6, 1, "お休みの日って何をすれば\nいいのか分からないんですよね…\nもしよければ一緒に休日の計画を\n立ててくれませんか？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1185005, 118501, 0, 5.9, 1, "楽しそうに依頼達成の報告を\nしにきてくれる瞬間が\n一番好きなんです。あなたの\n報告もお待ちしてますね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1185006, 118531, 0, 0.0, 1, "何事も初動が肝心ですからね\n書類を作るときは迅速丁寧に！\n皆さんに安心して冒険して\nもらえるようにがんばります♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1185007, 118531, 0, 0.0, 1, "空中で書類を書くコツ、ですか？\nう～ん…紙が落ちるよりも早く\n筆を動かす、ですかね？\n一緒に練習してみましょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1185008, 118531, 0, 0.0, 1, "一度にたくさんの書類を\n片付けるのは楽しいんですけど\nこまめに提出していただけると\nと～っても助かります！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1185009, 118531, 0, 0.0, 1, "このペンと同じくらい武器を\n上手に扱えれば一緒に冒険\nできたんですけど事務仕事以外は\n苦手でして…ほんとですよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1185010, 118531, 0, 0.0, 1, "困ったときは相談してください\n冒険者さんの…あなたのお力に\nなれることは、私にとって\nとても幸せなことですから　", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1185011, 118501, 0, 7.1, 1, "お誕生日おめでとうございます\nあなたの成長をお祝いできて\nとてもうれしいです。これからも\nよろしくお願いしますね♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1185012, 118531, 0, 0.0, 1, "ハッピーバースデー！\nこちらギルド管理協会から…\nではなく、私からの贈り物です\n受け取ってくれますか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1186001, 118601, 0, 6.7, 2, "あんたが今見てる私は本物？\n私が見せてる幻かもよ？\n…なーんてね♪　ねえねえ、\n疑っちゃった？　きゃははっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1186002, 118601, 0, 5.7, 2, "ふ～ん？　靴が片っぽだけ\n隠されて大変だったって？\nそれは災難ね～？　ふふっ、\n誰がそんなことしたのかしら！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1186003, 118601, 0, 7.9, 4, "ねえねえ、いい事思いついたの！\nキャルのご飯に激辛ソースを…\n…やめたほうがいい？\nちぇっ、つまんないの！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1186004, 118601, 0, 6.8, 2, "あー、悪戯し疲れた！\nあんたの肩で、少し休ませて？\n…ふふっ、さ、キリキリ歩いて！\nガイドはしてあげる、多分ね♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1186005, 118601, 0, 9.2, 2, "このギルドハウスのこと、\n今はだいぶ気に入ってるわ\n色んな思い出があるし…そうね\n慣れ親しんだ我が家、ってやつ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1186006, 118631, 0, 0.0, 1, "きゃははっ！　ねぇ、見た見た？\nキャルのあの慌てっぷり！\n毎回いいリアクションするから\n悪戯のしがいがあるわ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1186007, 118631, 0, 0.0, 1, "なんにもないと退屈しちゃって\n仕方ないのよね～…あら？\nふふっ、ターゲットはっけ～ん♪\nちょっと驚かせてやろっと！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1186008, 118631, 0, 0.0, 1, "さて、次はなにをしようかしら？\n…悪戯道具でも仕入れに、\n繫華街にでも行こうっと！\n爆竹とかビックリ箱はあるかしら", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1186009, 118631, 0, 0.0, 1, "…あんたってどんな悪戯も\n平然と受け入れちゃうわよね\nそういうの良くないわよ！\nちゃんと怒らなくっちゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1186010, 118631, 0, 0.0, 1, "…私の力は、上手く利用すれば\nあんたの助けになれるわ\nだから…困った時は頼って？\nいい、わかったわね！　約束よ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1186011, 118601, 0, 0.0, 2, "へぇ…誕生日なんだ？\nならそれにふさわしい、\n特別な悪戯をしなくちゃ！\n期待してくれていいわよ♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1186012, 118631, 0, 0.0, 1, "ハッピーバースデー！\nさ、誕生日プレゼントをどーぞ\nああ、そうそう…開ける前に\n心の準備はしっかりしてよね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1190001, 119001, 0, 9.6, 2, "昼は教鞭を振るい…夜は愛と\n希望を胸に秘め、闇の力で戦う！\nしかしてその正体は！　ふふっ♪\nどの私もイオちゃんって呼んでね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1190002, 119001, 0, 7.8, 2, "『秘密の学び舎』に\n今は二人っきり…\nどういうことか分かる？\nマンツーマンで教えてあげる♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1190003, 119001, 0, 1.6, 6, "…ぐふふっ♪\nやだ！　まだ闇のドMの影響が…\nこんなところ生徒に見られたら…\nでも、それはそれで…じゅるり♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1190004, 119001, 0, 6.4, 3, "闇の力があふれないように\n私のこと、満たし続けてほしいの\n中途半端じゃダメだからね？\n本気でお願い～！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1190005, 119001, 0, 4.7, 6, "オイタする子には\nしっかりお仕置きしますからね！\nえ？　どうしてうれしそうなの？\nお仕置き…されたいってこと…？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1190006, 119031, 0, 0.0, 1, "愛と希望と闇の先生よ♪\nいつもだったら教えないことも\n今日だけは特別に…\nだって教室に二人っきりだから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1190007, 119031, 0, 0.0, 1, "先生の授業、受けたいの？\nふふ…背伸びしたい年頃だもんね\nいいわ。ちょっぴり早いかも\nしれないけど、教えてあげる♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1190008, 119031, 0, 0.0, 1, "あん、どぅ、とろわ〜♪\n先生の言うことを\nなんでも聞くようにな〜る♪\nまずは目をつぶってちょうだい？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1190009, 119031, 0, 0.0, 1, "あら、先生の愛のムチがほしくて\nオイタしちゃったの？\nふふっ…イケナイ子ね…\nじゃあ反省してもらおうかしら♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1190010, 119031, 0, 0.0, 1, "もうすっかり夜なのに\nまだ補習を続けたいなんて…\nけど、勉強熱心な生徒って好きよ\nたっくさんかわいがってあげる♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1190011, 119001, 0, 6.0, 1, "お誕生日おめでとう！\n大人の階段をひとつ登ったわね\nじゃあ授業の内容も\n君の成長に合わせなくちゃ♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1190012, 119031, 0, 0.0, 1, "ハッピーバースデー♪\n誕生日プレゼントとして\n学校じゃ習わないようなことも\n今日は特別に教えてあげる♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1191001, 119101, 0, 6.1, 2, "闇のドM魔法少女クウカです～♪\n困っている人はいませんか～？\nクウカがお手伝いしますよ～♪\nドMになれば何でも解決です～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1191002, 119101, 0, 11.1, 5, "愛と希望の妄想が捗ります～！\n「おいクウカ、お前の決めポーズ\nはこれだ」そう言ってドSさんは\nクウカを縛り上げ…ぐふふ～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1191003, 119101, 0, 7.9, 2, "変身するときの台詞、ですか？\nいえ、クウカにはありませんね…\n興奮が頂点に達すると、勝手に\n変態、いえ変身してしまうので…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1191004, 119101, 0, 7.7, 5, "ドSさんとクウカのアジト…\nこ、ここでクウカに何をしようと\nいうのですかっ！？　ぐふふ…\n何があってもご褒美です～！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1191005, 119101, 0, 7.8, 4, "はっ！？　正義の味方になって\nしまったらドSさんに攻められる\nチャンスが減るのでは…？\nクウカ、それだけは困ります～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1191006, 119131, 0, 0.0, 1, "クウカ、ドMビーーーーーーム！\nぐふふ、今日も街の平和を\n守ってしまいました～♪\nみなさん、幸せそうです～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1191007, 119131, 0, 0.0, 1, "体からあふれる愛と希望の力…！\n今なら、どんな攻めにも\n耐えられます！\nドSさん、遠慮なくどうぞ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1191008, 119131, 0, 0.0, 1, "「魔法少女め、俺がその体に\n敗北を刻んでやるよ…」\nああっ、いけませんドSさん！\nクウカ闇に染まっちゃいます～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1191009, 119131, 0, 0.0, 1, "じゅるり…この力があれば、\nすべての人々をドMに…\nはっ！　闇の力に負けそうです！\nクウカにお仕置きを、ドSさん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1191010, 119131, 0, 0.0, 1, "愛と希望の力…言葉にするのは\nちょっと気恥ずかしいですけど\nクウカの中にもあるんですよね…\nドSさん…この胸の高鳴りが…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1191011, 119101, 0, 2.5, 1, "クウカを受け取ってください！\nあっ、すみません、先走りました\nドSさんの誕生日ですので\nつい、興奮してしまい…", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1191012, 119131, 0, 0.0, 1, "ドSさんのお誕生日をお祝いする\nために…クウカ、愛と希望と、\n闇の力を溜め込んできました…！\n受け取ってくださいドMビーム！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1192001, 119201, 0, 5.8, 1, "施設の修繕に雪かきに…\n冬だからって休んでられねぇど\n今日も気張って働くべや！\nあんちゃんもよろしく頼むべ♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1192002, 119201, 0, 4.9, 2, "夏には夏の、冬には冬の\nおいしい食べ物があるべよ！\nうちで採れた旬の味を\nあんちゃんにも食べてほしいべ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1192003, 119201, 0, 4.0, 3, "オラたちのお笑いで\n冬の寒さを吹っ飛ばすべよ！\nあんちゃん、準備はいいだか？\nまずはネタ合わせから始めるべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1192004, 119201, 0, 7.3, 1, "うちの乳製品を使った\nクリスマスケーキに特製プリン…\nどれも自信作だべ！\nあんちゃんも味見してみてけろ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1192005, 119201, 0, 7.4, 2, "相方のあんちゃんがいるから\n牧場の仕事もお笑いも、\nオラは全力投球できるべさ\nいつもあんがとな、あんちゃん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1192006, 119231, 0, 0.0, 1, "やっぱここに飾んねぇとだな\nこのクリスマスリースが\nあんちゃんにも幸せを\n運んでくれますように♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1192007, 119231, 0, 0.0, 1, "クリスマスの賑やかさ…\n場の空気があったまってると\n笑いも取りやすいべな\nさ、ドッカンと笑わせにいくべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1192008, 119231, 0, 0.0, 1, "あんちゃん、この雪だるま…\n目元がなまらめんこいべ？\nオラがエリザベスに似せて作った\n会心の出来映えだべ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1192009, 119231, 0, 0.0, 1, "そんなとこ突っ立ってたら\nあんちゃん、風邪ひいちまうぞ？\nあったけぇお茶でも淹れっから\n中に入って待っててくんろ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1192010, 119231, 0, 0.0, 1, "大事な家族のみんなと、\nそんであんちゃんと…\n一緒にいれば冬でも\nポカポカあったけぇべさ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1192011, 119201, 0, 2.8, 2, "今日あんちゃんの誕生日だべ？\nこの日のためにとっておきの\n新ネタを用意してきただよ！\n楽しんでくれたらうれしいべさ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1192012, 119231, 0, 0.0, 1, "よい子にしてたあんちゃんには\nマヒルサンタから誕生日の\nプレゼントをお届けするべ♪\n誕生日おめでとう、あんちゃん", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1193001, 119301, 0, 4.4, 2, "お洋服よし！　ケーキよし！\nプレゼントよーし！　ふふふ、\n準備満タンですよ、お兄ちゃん！\n…じゃなかった、満杯ですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1193002, 119301, 0, 3.6, 2, "クリスマスケーキ？　もちろん\n作れますよ！　だてにマスターの\nお店で山ほどクレープ\n焼いてませんからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1193003, 119301, 0, 7.1, 5, "雪遊びしましょう、お兄ちゃん！\n雪だるま作って、雪合戦して、\nそれからそれから…\nデ、デートもしたいです～！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1193004, 119301, 0, 3.9, 3, "今日のために練りに練った\nクリスマスデートのプラン…\nあっ、お兄ちゃんは見ちゃだめ！\nこのメモはきぎょー秘密です！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1193005, 119301, 0, 5.0, 1, "押してダメならもっと押せ…\nお兄ちゃんを振り向かせるには、\nさらに大胆無敵なアプローチが\n必要ってことですね！？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1193006, 119331, 0, 0.0, 1, "このお洋服、お兄ちゃんに見て\nもらいたくて作ったんですよ～\nつまりクリスマスコーデの私は\nお兄ちゃん専用です！　えへへ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1193007, 119331, 0, 0.0, 1, "手袋にマフラー、セーターも…\n冬の防寒対策は、妹にまるっと\nお任せです♪　お兄ちゃんの\n健康は私が守っちゃいますよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1193008, 119331, 0, 0.0, 1, "なんでずっと見つめてるかって？\nお兄ちゃんと一緒に過ごす\nクリスマスを、いつまでも\n覚えていたいから！　ですよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1193009, 119331, 0, 0.0, 1, "お兄ちゃん、寒くないですか？\n寒いですよね？　私は寒いです！\nなので私とぎゅーってして\nあたたまっちゃいましょう…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1193010, 119331, 0, 0.0, 1, "クリスマスのお兄ちゃんは\n妹の私が独り占めです！\nお兄ちゃんも私のこと\n独り占めしちゃってください♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1193011, 119301, 0, 7.0, 2, "今日はお兄ちゃんの生まれた日…\n私にとってはどんな記念日より\n大切な日です♪　全力で\nお祝いしちゃいますよ～！", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1193012, 119331, 0, 0.0, 1, "ハッピーバースデーお兄ちゃん！\n気になる誕生日プレゼントは～…\nそう、お兄ちゃんの大好きな妹！\n私も大好きです、えへへ…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1199001, 119901, 0, 5.2, 1, "オマエのクリスマスプレゼントは\nミヤコが用意してやるの！\nさあ、ほしいものを言えなの！\nプリン以外ならくれてやるの～！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1199002, 119901, 0, 5.2, 6, "サンタは空を飛んで\nどこへでも行けるって聞いたの\n…もしかしてサンタは幽霊なの？\nミヤコと同じなの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1199003, 119901, 0, 4.4, 2, "ミヤコのほしいクリスマス\nプレゼントは～、まずプリン！\n次にプリン、それとプリンなの！\nちゃんと覚えとくの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1199004, 119901, 0, 3.6, 1, "幽霊でも寒いものは寒いの\nオマエの服の中に入れろなの♪\nむふふ、抵抗してもむだなの！\nすり抜けて入ってやるの！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1199005, 119901, 0, 0.0, 1, "クリスマスはミヤコとずっと\nくっついててもらうの～！\n嫌がっても憑いてくの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1199006, 119931, 0, 0.0, 1, "ミヤコサンタのお通りなの～！\nプリンをくれたら、お返しに\nプレゼントをあげるの！\nうぃんうぃんってやつなの～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1199007, 119931, 0, 0.0, 1, "サンタはトナカイとソリがないと\n飛べないらしいの～、でもでも！\nミヤコは一人で飛べちゃうの！\nミヤコサンタはすごいの～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1199008, 119931, 0, 0.0, 1, "プリンは全部ミヤコのだけど…\nたまにはミヤコが誰かに\nプレゼントするのも悪くないの♪\nなんだかあったかい気分になるの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1199009, 119931, 0, 0.0, 1, "ミヤコは優しいから、オマエに\n内緒でプレゼントを用意したの♪\n…あっ、言っちゃったの～！\nもう内緒じゃなくなったの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1199010, 119931, 0, 0.0, 1, "オマエと一緒にいると、毎日が\nクリスマスみたいな気分なの♪\nとーってもワクワクするの～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1199011, 119901, 0, 8.3, 3, "今日はオマエの誕生日なの！\n特別にプリンを一口だけ\n分けてあげるの～！\n…それ以上食べたら許さないの！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1199012, 119931, 0, 0.0, 1, "ハッピーバースデーなの～！\nお祝いのプリンケーキなの！\nほら、早くローソク吹き消すの！\nミヤコも食べたいの～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1200001, 120001, 0, 7.5, 1, "むむっ、闇の気配がするね！\n弟くんの平和を守ることが\n魔法少女なお姉ちゃんの使命！\nあ、街の平和ももちろん守るよ♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1200002, 120001, 0, 8.1, 5, "ふふっ、黒っぽい衣装って\nなんだか新鮮だよ～\nお姉ちゃんって清純派だからね♪\n…えへへ、ワルイコトしちゃう？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1200003, 120001, 0, 1.5, 1, "へんし～んっ！\n…あっ、弟くん、見てたの？\n変身シーンに興味津々かな？\nじゃあもう一回…見せてあげるね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1200004, 120001, 0, 4.7, 2, "つよ～い愛と希望が\n魔法少女の力の源なんだって\nだったらお姉ちゃんはいつだって\n最強無敵の魔法少女だね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1200005, 120001, 0, 5.3, 5, "あっ、なんだか闇の力が\nあふれて暴走しちゃいそう…！\n今日は弟くんをひとりじめして\nたくさん愛でないと…♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1200006, 120031, 0, 0.0, 1, "お姉ちゃんの愛の結晶！\n今、ここに合体！\nおいでっ、キング弟くんっ！\nふふっ、どかーんとやっちゃえ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1200007, 120031, 0, 0.0, 1, "じゃ～んっ♪\n魔法少女のお姉ちゃんだよっ！\nね、どうかな？　うれしい？\n変身、もう一回見せてあげよっか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1200008, 120031, 0, 0.0, 1, "弟くんっ♪\n決めポーズのリクエストはある？\nお姉ちゃんがどんなポーズでも\nバッチリ決めてあげちゃうぞ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1200009, 120031, 0, 0.0, 1, "ねっ、すっごく大きいでしょ♪\nこれはね、愛の大きさなんだよ？\nお姉ちゃんがどれだけ弟くんを\n想ってるか…伝わったかな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1200010, 120031, 0, 0.0, 1, "弟くんを守れるのなら\n愛でも、希望でも\n…闇の力でも構わないよ\n全部、お姉ちゃん力なんだから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1200011, 120001, 0, 3.5, 2, "今日はお姉ちゃんにとって\n何よりも大事な日…♪\nそう、弟くんの誕生日！\n魔法の力で、お祝いしちゃうぞ♪", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1200012, 120031, 0, 0.0, 1, "お誕生日おめでとうっ♪\n魔法少女のお姉ちゃんから\n弟くんへのプレゼント♪\n愛と希望の…魔法だよっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1207001, 120701, 0, 7.4, 4, "ど、どうかしら…？\nコッコロさんと一緒に選んだの…\n着付けも慣れてないし…\nその…変なところはない…？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1207002, 120701, 0, 3.4, 7, "せっかくの正月だものね\n私もたまには息抜きして\nあなたと新年の思い出を\n作ろうと思うわ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1207003, 120701, 0, 10.7, 2, "『記念に写真を撮ろう』って…\nイ、イヤじゃないけど\n私一人だけじゃなくて…\nあなたとも一緒に撮りたいわ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1207004, 120701, 0, 5.2, 7, "…ん。絶好のお参り日和ね\nさぁ、早く行きましょう\nあなたと過ごす貴重な\n休日を無駄にしたくないもの", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1207005, 120701, 0, 8.2, 4, "【美食殿】の一員として\n正月の名物料理は\n全て堪能してみせるわ\n魔物の肉でも…が、がんばる…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1207006, 120731, 0, 0.0, 1, "待たせてごめんなさい\n着付けに時間がかかっちゃって…\n…え？　似合ってる？\nあ、ありがとう…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1207007, 120731, 0, 0.0, 1, "おせち料理…食べすぎちゃった…\n…でも。食べないで後悔するより\n食べて後悔する日があっても\nいいわよね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1207008, 120731, 0, 0.0, 1, "『今の私』にとって\n正月を過ごすのは初めてだから\nあなたの行きたい場所があれば\n私も一緒に連れて行ってくれる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1207009, 120731, 0, 0.0, 1, "正月ってすごいわね…\nまるでお祭りみたい…\nええと…迷子にならないように\n裾…掴んでてもいいかしら…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1207010, 120731, 0, 0.0, 1, "あなたはお参りで何を願ったの？\n私は…『今の私』にとって\n大切にしたいことを…\n絶対…叶えてみせるわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1207011, 120701, 0, 10.8, 2, "誕生日、おめでとう\n正月もそうだったけれど\n一緒にお祝いできてうれしい…\nこれからもよろしくね", 7, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1207012, 120731, 0, 0.0, 1, "誕生日ケーキを作ってみたの…\n作るのはたぶん、初めてで…\nあまりキレイじゃないけど…\nでも、心は込めて…作ったから…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1208001, 120801, 0, 5.5, 2, "正月ってのはいくつになっても\n心が躍るもんだね\nこの時ばかりは一緒に浮かれ\n楽しむのが粋ってもんさ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1208002, 120801, 0, 6.0, 5, "どうだいお前さん、アタシの\n晴れ着姿は。キマってるだろ？\nえ、キレイだって？\nあ、ありがとな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1208003, 120801, 0, 7.4, 1, "羽根つき、たこあげ、福笑い\n正月の遊びってのは素朴で\n楽しいもんさ。どうだい？\nアタシと一勝負しないか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1208004, 120801, 0, 6.1, 2, "正月休みを利用して遠出する\nって人も結構いるみたいだね\nお前さんとならそういう\n過ごし方も悪くないかもね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1208005, 120801, 0, 4.8, 1, "おせちってのは作るのに\n手間がかかるんだよね\nまあ、食べたいって言うなら\n作るのもやぶさかじゃないさ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1208006, 120831, 0, 0.0, 1, "アタシの剣は見せ物じゃないけど\n今日だけは特別さ\nとっておきの舞いを\nとくとご覧あれ、ってな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1208007, 120831, 0, 0.0, 1, "冷たい空気に触れると身も心も\n研ぎ澄まされていくね\n今なら新しいアタシを\nお披露目できそうだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1208008, 120831, 0, 0.0, 1, "おや、お前さんもこの剣舞に\n興味があるのかい？\nなら手取り足取りキッチリと\n仕込んでやるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1208009, 120831, 0, 0.0, 1, "お前さんに降りかかる災いは\nアタシが全部斬り伏せる\n大船に乗ったつもりで\nいておくれよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1208010, 120831, 0, 0.0, 1, "どうしたんだいお前さん\nぼうっとしてさ？\nえ、アタシに見惚れてた？\nて、照れるじゃないかまったく…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1208011, 120801, 0, 3.5, 1, "誕生日おめでとう、お前さん\n今日はお前さんの成長を祝って\nとっておきの釣りスポットに\n連れてってやるよ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1208012, 120831, 0, 0.0, 1, "お前さん、今日誕生日だって？\nふーん…いや、この一年で\n随分と見違えたなと思ってね\nなんだかうれしくなっちまうよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1209001, 120901, 0, 8.7, 2, "見事な晴れ着姿じゃろう？\n特にこの羽織りものは、自分でも\n気に入っておる。いかにも支配者\nらしい風格があるからのう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1209002, 120901, 0, 12.0, 2, "わらわは年長者じゃからのう\nすべての者にお年玉をやる権利が\nある…ということにほかなるまい\n景気よくばらまいてやるのじゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1209003, 120901, 0, 6.4, 2, "眷属のコウモリたちにも\n正月休みをやるとするか…\nおっと、お主には年中無休で\nわらわのそばにいてもらうぞ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1209004, 120901, 0, 10.5, 2, "この姿を民草に披露するか\nお主にじっくり堪能させてやるか\n…今は後者の気分じゃな。お主、\n年明けから運に恵まれたのう♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1209005, 120901, 0, 4.4, 4, "子供の姿の晴れ着もかわい\nかった？　いくらおだててもあの\n姿には戻らぬぞ。断じて戻らぬ！\nまあ褒められて悪い気はせぬが…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1209006, 120931, 0, 0.0, 1, "正月を制覇するには\nまず形から…この華々しい\n艶姿でもって、民草の信望を\n欲しいままにするのじゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1209007, 120931, 0, 0.0, 1, "後光が差して見えるじゃと？\nふふ、忌々しい太陽もわらわの\n美貌に見惚れるあまり、\n引き立て役に回ったようじゃの♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1209008, 120931, 0, 0.0, 1, "晴れ着に合わせて髪も\n編んでみたのじゃが…ふふ\nなかなか見違えたじゃろう？\n何度でも惚れ直すがよいぞ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1209009, 120931, 0, 0.0, 1, "拝むがよい、称えるがよい！\n今のわらわはいくら褒められても\n足らぬ気分じゃ。お主からの\n賛辞であれば、なおさらのう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1209010, 120931, 0, 0.0, 1, "ほれ、もっと近う寄るのじゃ\nわらわの晴れ着姿、とくと\nその目に焼きつけるがよい\n…お主にだけ特別、じゃぞ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1209011, 120901, 0, 0.0, 2, "ハッピーバースデーなのじゃ！\nわらわほどの支配者ともなれば\n眷属の誕生日は当然把握しておる\nさあ、盛大に祝われるがよい！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1209012, 120931, 0, 0.0, 1, "今日はお主の誕生日じゃな！\nふふ、すでに準備は済んでおるぞ\n正月並みに豪勢な宴で\n祝ってやろうではないか♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1210001, 121001, 0, 4.1, 1, "これが、あなたのあふれる想い…\nとてつもない力ですが\n全力を超えて、受け止めます！\nあなたがつなげた絆のために！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1210002, 121001, 0, 5.1, 2, "わたしのそばから離れないで\nくださいね？　王女命令であり\nただのペコリーヌからの\n一生のお願いです☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1210003, 121001, 0, 7.0, 1, "あなたの想い、あなたの力…\n全部受け取って、前に進みます！\nあなたを感じているわたしは\nもう誰にも止められませんよ！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1210004, 121001, 0, 4.8, 1, "みんなに元気よく\nおいっす〜☆ って\n挨拶したいです\nそのために頑張りましょう！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1210005, 121001, 0, 4.5, 2, "あなたと一緒なら、どんな場所に\n行ってもへっちゃらです！\nこれからもずっとずっと冒険を\n続けましょうね！　絶対ですよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1210006, 121031, 0, 0.0, 1, "【美食殿】の温かい食卓が\nわたしたちの帰る場所です！\nみんなで手を合わせて\nいただきますって言いましょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1210007, 121031, 0, 0.0, 1, "あなたがつないだ絆を\n全力全開でつなぎとめて\n今日までの思い出と\n明日からの希望を守ります！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1210008, 121031, 0, 0.0, 1, "今だけはお腹空いたって泣き言は\n言いません！　\nみんなで食卓を囲むために\nわたしは剣を振るいます！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1210009, 121031, 0, 0.0, 1, "あなたの強い想いを力に変えて…\n全力を超越しちゃいます！\nあなたがつないだ絆を\nこの体と剣に宿します！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1210010, 121031, 0, 0.0, 1, "あなたにどんなことがあっても\nずっとわたしが全力全開で\n守ってみせますよ！　だって…\n恋しちゃってるんですから☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1210011, 121001, 0, 7.1, 5, "ひゅうひゅう♪　お誕生日\nおめでとうございます！\n全力全開でお祝いしちゃいます！\n今日はあなたを独り占めです！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1210012, 121031, 0, 0.0, 1, "大切な人の誕生日を祝えるって\nすごく幸せなことなんですよね\nこれからもずっと、おめでとうを\n言わせてくださいね？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1211001, 121101, 0, 6.2, 1, "あんたとのつながりを感じる…\n温かくて優しい想いを…\n…今のあたしは、\n誰にも負ける気がしないわ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1211002, 121101, 0, 6.9, 1, "気を抜くと魔力が暴走しそう…\nこの力、とんだじゃじゃ馬ね…\n…けど、使いこなしてみせるわ\nあいつのためにも…！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1211003, 121101, 0, 4.2, 2, "あたしたちの冒険は、\nまだまだこれから、でしょ？\n眠ってるヒマなんてないわよ！\n次なる美食を探しに行きましょ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1211004, 121101, 0, 4.7, 2, "…あんたの背負ってるもの、\nあたしにも背負わせなさいよ\nここまで来たら、\n運命共同体ってヤツでしょうが", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1211005, 121101, 0, 7.3, 2, "夢の中だろうがどこだろうが、\nあんたが大変な時は、\nあたしが必ず助けに行くから\nこの手は…絶対に離さないわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1211006, 121131, 0, 0.0, 1, "このつながりがある限り、\nどんな運命にだって負けないわ！\nさあ、とっとと片付けて、\nおいしいご飯を食べにいくわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1211007, 121131, 0, 0.0, 1, "【美食殿】があたしたちの\n帰る場所でしょ！\nあんたがどこにいたって、\nあたしが迎えに行ってやるから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1211008, 121131, 0, 0.0, 1, "あんたは一人じゃない\nどんな時だって、\nあたしたちは心でつながってる\nそのことを…忘れないで", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1211009, 121131, 0, 0.0, 1, "あんたと食べるご飯が、\n一番おいしいの\nだから…あたしのそばに\nずっといてもらわないと困るわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1211010, 121131, 0, 0.0, 1, "どうしようもないあたしを、\nあんたは受け入れてくれた\nだからあたしも…\nあんたの全部を受け入れるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1211011, 121101, 0, 5.9, 1, "誕生日、おめでと！\n今日はあんたのやりたいことに\n付き合ってあげるわ！\nさ、まずはなにをしましょっか♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1211012, 121131, 0, 0.0, 1, "その…バースデーケーキ、\n作ってみたの。あんたみたいに、\n上手にできなかったけど…\nと、とにかく食べなさい！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1212001, 121201, 0, 7.6, 6, "さあ少年、そこに座って！\n今回の事象についてもっと\n詳しく解析させてもらうね？\nあっ！　もう、逃げないでよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1212002, 121201, 0, 0.0, 1, "さて少年、ここで問題です！\nこの炎みたいなやつを使って\nクレープは作れるでしょうか？\n正解は…今から実験だー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1212003, 121201, 0, 2.5, 1, "力の制御？　ああ、大丈夫だよ\nもう慣れたし。少年、もしかして\nアタシが天才なの忘れてた？\n…ありがとう。心配してくれて", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1212004, 121201, 0, 0.0, 1, "えへへ…いやあ、まさか\n少年からこんな贈り物を\n貰えるなんて思ってなくてさ\nついうれしくなっちゃった", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1212005, 121201, 0, 5.5, 1, "やっぱり少年は、アタシすら\n変えてしまえるんだね…\nキミという存在の可能性\nこれからも見守らせてもらうよ", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1212006, 121231, 0, 0.0, 1, "少年を取り戻すためなら\n未知の事象も解き明かすさ\nキミを選んだ者として\n責任を取らないとね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1212007, 121231, 0, 0.0, 1, "少年。キミと繋がってる\nアタシの心も魂も、\n熱く燃えてるのが分かる？\nこの見た目だけじゃないよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1212008, 121231, 0, 0.0, 1, "実はさ、キミを待ってる人が\nこの世界には大勢いるんだよね\nその人たちのためにもアタシは\nキミを救うよ、少年！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1212009, 121231, 0, 0.0, 1, "だいたいさ、アタシから少年を\n取り上げようなんてふざけた話だ\nそんな真似、たとえ世界にだって\nさせてたまるもんか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1212010, 121231, 0, 0.0, 1, "なんでアタシ、こうなったんだろ\n少年と強く繋がり過ぎたかな？\nありえるね…愛ゆえの行動が\n二人を結びつけちゃったか～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1212011, 121201, 0, 8.7, 6, "ハッピーバースデー！\nキミへの心を込めた贈り物\n受け取ってくれるかな？\n…なんでクレープって分かった？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1212012, 121231, 0, 0.0, 1, "誕生日おめでとう、少年！\nキミの成長はアタシにとって\n何よりも喜ばしいよ。今日は\nアタシも一緒に祝っていいかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1213001, 121301, 0, 5.5, 2, "お芝居のことなら\nちょっとだけ自信があります\nいつもとは一味違う私を\n見ていてくださいね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1213002, 121301, 0, 8.1, 1, "舞台裏でも客席でも\nお兄ちゃんが見てくれてると\nちゃんとわかるんです\nなんだかぽかぽかするので…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1213003, 121301, 0, 3.8, 2, "ミサキちゃんに勇気を\nわけてもらったから\n私は舞台に立つ楽しさを\n教えてあげたいな…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1213004, 121301, 0, 7.9, 1, "いまのお稽古、どうでしたか？\nひゃわ…！？\n頭は撫でないでくださいぃ…！\nよかったのは分かったので…！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1213005, 121301, 0, 7.1, 2, "お芝居を見てくれたみんなに\n褒めてもらえるのもうれしいです\nでも…お兄ちゃんによろこんで\nもらえるのも…うれしいな…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1213006, 121331, 0, 0.0, 1, "今はまだ、つぼみのような\n未熟な私だけど…\nいつかこの花のように美しく\n舞台の上で咲いてみせます…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1213007, 121331, 0, 0.0, 1, "『言葉はいりません\nただ、なにも言わず\nこの花を受け取ってください』\nえへへ、どうですか…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1213008, 121331, 0, 0.0, 1, "お芝居をしているときの私は\n誰にでもなれますけど…\n一番なりたい役を演じるために\nいっぱい頑張りますね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1213009, 121331, 0, 0.0, 1, "私の舞台、よろこんでもらえた\nみたいでよかったですぅ…♪\n…今日は頭を撫でないんですか？\nいえ、なんでもないですよぅ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1213010, 121331, 0, 0.0, 1, "照れちゃって言えない\nお兄ちゃんへのこの気持ち…\n舞台の上から、この花に乗せて\n伝えられたらうれしいな…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1213011, 121301, 0, 8.8, 2, "観客はお兄ちゃんだけだけど…\nお誕生日祝いのお芝居、\n見てくれますか…？\nえへへ、心を込めて演じますね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1213012, 121331, 0, 0.0, 1, "お祝いの気持ちを込めて\nささやかだけどこの花を贈ります\nお兄ちゃん、お誕生日\nおめでとうございます…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1214001, 121401, 0, 2.4, 5, "どうかしら？　この衣装♪\n大人のレディなら\nどんな服でもばっちり\n着こなしちゃうんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1214002, 121401, 0, 7.6, 2, "衣装に合わせて髪型も\n変えてみたんだけど、どう？\n…似合ってる？　ふふん♪\nま、聞くまでもなかったわね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1214003, 121401, 0, 5.2, 1, "アタシのエレガントな魅力で\n物産展を盛り上げちゃうわ♪\nアンタも、見とれてないで\nしっかり働きなさいよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1214004, 121401, 0, 6.3, 3, "ねえ、衣装の後ろの方\nちょっと直してくれない？\n…ひゃぁっ！？　バカバカ！\nど、どこ触ってんのよ～！！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1214005, 121401, 0, 5.6, 4, "スタイリッシュなレディの魅力に\nメロメロなのはわかるけど…\nそ、そんなに見つめられると\nさすがに恥ずかしいわ…もうっ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1214006, 121431, 0, 0.0, 1, "お客さんを笑顔にするためには\nアタシたちも笑ってないとね！\n物産展のお仕事は大変だけど\n楽しく働きましょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1214007, 121431, 0, 0.0, 1, "歌にダンスにお芝居だって…\nどんなことも完璧にできてこそ\n大人のレディよね？\n挑戦のしがいがあるわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1214008, 121431, 0, 0.0, 1, "ねえ、このファンレター書いたの\nアンタでしょ？　アタシの魅力が\nやっと分かってきたようね！\n大事に読ませてもらうわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1214009, 121431, 0, 0.0, 1, "いらっしゃいませ～♪\n大人のフェロモンを振りまいて\nお客さんを呼び込んでみるわ！\n商売繁盛、間違いなしよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1214010, 121431, 0, 0.0, 1, "本当はね、舞台もお仕事も\n不安でいっぱいだったけど…\nアンタがいたら大丈夫になるの\nだから、その…いつもありがと！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1214011, 121401, 0, 3.9, 2, "誕生日おめでとう！\nいつもお世話になってるから\n今日はアタシが特別に\nエスコートしてあげるわ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1214012, 121431, 0, 0.0, 1, "誕生日プレゼントはア・タ・シ♪\nもー！　リアクション薄いわよ！\nこのアタシが祝ってるんだから\nもっと素直に喜びなさい！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1215001, 121501, 0, 0.0, 2, "この怪盗の衣装…\n私にはもったいないくらい\nかっこよくて、着るたびに\nちょっとドキドキしちゃいます…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1215002, 121501, 0, 6.5, 4, "せせせ、先輩…！　私の仮面\nどこかで見ませんでしたか？　\n…顔に着けてる？　…あっ\nほ、ほんとですね。あははは…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1215003, 121501, 0, 0.0, 4, "この間、怪盗活動に役立つかなと\n思って変装をしてみたんですけど\nそもそもいることに気づいて\nもらえませんでした…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1215004, 121501, 0, 4.1, 2, "先輩、私が見えてますか？\n…ふふ、それなら安心です\n先輩に見ててもらえれば\n私、勇気が出せるので…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1215005, 121501, 0, 6.1, 5, "誰にも気づかれないっていうのも\n悪いことばかりじゃないですね\n…先輩とゆっくり過ごせますし…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1215006, 121531, 0, 0.0, 1, "夜空を駆ける怪盗\nここに参上でしゅっ！\n…あわわ、大事なところで\n噛んじゃったぁ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1215007, 121531, 0, 0.0, 1, "この仮面を着けてると、ほんの\nちょっと大胆になれるんです…\nで、でもっ…空から潜入は\n大胆すぎたかもです～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1215008, 121531, 0, 0.0, 1, "ふえぇっ、高い、高いですっ！\nせせせ、先輩っ！　私に掴まって…\nいえ、掴まらせてくださーい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1215009, 121531, 0, 0.0, 1, "大丈夫、大丈夫…\n秘密道具は全部持ってきたはず…\nま、万一忘れ物があっても、\n先輩がいてくれれば大丈夫です！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1215010, 121531, 0, 0.0, 1, "怪盗活動は怖いこと\nばかりですけど…\nでもこのドキドキする気持ちは\nなんだか嫌じゃないです…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1215011, 121501, 0, 5.9, 2, "先輩…お誕生日…\nおめでとう…ござい、ます…！\nや、やった…ゆっくり喋ったら\n噛まずに言えましちゃ！　…あっ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1215012, 121531, 0, 0.0, 1, "今日は先輩のお誕生日…なので\nいろんな人にお誕生会の予告状を\n送っておきました！　えへへ…\n盛り上がるといいんですけど…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1216001, 121601, 0, 0.0, 2, "ふっふっふ…！\nあたしこそは神出鬼没の大怪盗！\n狙ったものは必ずいただくです！\n金銀財宝からボスの座まで！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1216002, 121601, 0, 7.1, 6, "下克上のためなら\nどんな手でも使うですよ！\nなーに、バレなきゃいいんです！\n…バレたらもっと大変ですし", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1216003, 121601, 0, 6.7, 3, "どうですか、このマフラー！\nいかにも大怪盗って感じで\nテンション上がるです…って、\n引っ張るのはダメですよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1216004, 121601, 0, 0.0, 2, "あたしの前でボーっとするなんて\nいい度胸ですね、お兄さん！\n今日のぶんのオヤツが\nなくなっても知らないですよ～？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1216005, 121601, 0, 7.5, 6, "べ、別になにもしてないですよ？\n気配を消して、お兄さんの話を\n盗み聞きしてたとかじゃないです\nホントホント、ただの未遂ですよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1216006, 121631, 0, 0.0, 1, "ぴぇええええ！！！\n大怪盗のかっこいい逃走劇が\nはじまるはずだったのに！\nなんで…こうなるですかぁ～！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1216007, 121631, 0, 0.0, 1, "こ…こここ、こういうときこそ\n冷静になるですよ、あたし！\n落ちついて状況を見て…\nあーうん、落ち続けてるです！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1216008, 121631, 0, 0.0, 1, "ぴぃっ！？　そ、そうだ！\nこういうときこそ覚醒です！\nドラゴン族の力、目覚めるです！\n真夜中だけど目覚めるです～！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1216009, 121631, 0, 0.0, 1, "ポーズを決めてるんじゃなくて！\nバランス！　崩して！　落下中！\nピンチなんですよ、お兄さん！\nボーっとしてる場合ですかっ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1216010, 121631, 0, 0.0, 1, "絶対、絶対受けとめるですよっ？\n前フリじゃないですからねっ？\nしっかりがっちりキャッチです！\n信じてるですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1216011, 121601, 0, 2.5, 2, "お兄さんの誕生日？\n覚えてるですよ、今日ですよね？\nほーら、どんなもんです！\nうたがったおわびをするですよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1216012, 121631, 0, 0.0, 1, "お誕生日おめでとうです！\nしょうがないからプレゼントを\n用意してあげてもいいですよ\nさあ、どーんと言ってみるです！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1219001, 121901, 0, 7.4, 2, "海賊衣装に新たな魔剣、\nと来れば次に必要なのは…\nふふ、あとは分かるな？\n…そう！　新しい必殺技だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1219002, 121901, 0, 9.6, 6, "ふふっ、新しい海賊の掟を\n思いついたぞ。記念すべき\n66個目の掟がな！\n…えっ、多すぎる？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1219003, 121901, 0, 7.1, 4, "この格好をしていれば\n冥風戦記の続編の構想が次々に\n浮かんでくる…と思ったのだが\n現実はそう甘くなかったな…うん", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1219004, 121901, 0, 7.4, 6, "新たに覚醒した深淵の力を\n制御すべく、眼帯も新調したぞ！\n…なに？　貴様も着けたい？\nわ、私のでよければ貸そうか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1219005, 121901, 0, 3.7, 2, "その格好で泳げるのか、だと？\nふっ、侮るなよ…港町の皆さんに\n泳ぎ方を教わっているからな\nいずれは泳げるようになる！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1219006, 121931, 0, 0.0, 1, "やはり船首に立つと胸が躍るな！\n…マストの上？　あ、あそこは\nちょっと高すぎるから…いや、\nあの場所は貴様に譲ってやろう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1219007, 121931, 0, 0.0, 1, "この船に主砲はまだないが\n私の最上究極殲滅魔法が、\nいわば主砲のようなものだ！\n船長にして主砲…かっこいいな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1219008, 121931, 0, 0.0, 1, "いい風が吹いているな…\nほどよくコートがはためくし\nかといって帽子が飛んでいかない\nうむ、絶妙にいい風だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1219009, 121931, 0, 0.0, 1, "ふっ、こうしてると前世で海原を\n冒険した記憶がよみがえってくる\n私と魂で結ばれた貴様も、\nいずれ思い出すはずだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1219010, 121931, 0, 0.0, 1, "航海士、操舵士…いや、やはり\n相棒というのがしっくりくるな\n大海賊の唯一無二の相棒…\nそれが貴様だ、シグルド！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1219011, 121901, 0, 7.2, 5, "シグルド、誕生日おめでとう！\nこの日のために財宝も用意した！\nわ、私の手作りなのだが…\nよければ受け取ってほしい！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1219012, 121931, 0, 0.0, 1, "今日は貴様の生誕祭だ、盟友よ！\nふふ、すでに饗宴の準備は\n整えてある…海賊流の宴で\n盛大に祝ってやろう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1220001, 122001, 0, 0.0, 2, "船で旅するのも楽しそうですね\n今度、私たちもしてみますか？\n呪われた魔道具探索ツアーとか\nランドソル霊場巡りとか…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1220002, 122001, 0, 10.5, 6, "波が穏やかでいい天気ですね\nあの空に飛んでる海鳥たちも\n気持ちよさそう…え、見えない？\nではあれは…霊鳥でしょうか", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1220003, 122001, 0, 10.7, 5, "この衣装…海賊なのか\n水兵なのかどちらなんでしょう\n…似合ってるから問題ない？\nそうではなくて…えっと…うぅ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1220004, 122001, 0, 11.2, 2, "幽霊になってでも叶えたい夢…\nそれを持てたら素敵ですね\n私にはまだ分かりませんが…\nあなたと一緒に見つけたいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1220005, 122001, 0, 3.5, 6, "潮風が気持ちいいですね\nでも少し風が強くて…\nスカートが…きゃっ！\nみ、見えましたか…？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1220006, 122031, 0, 0.0, 1, "星が降るような夜ですね…\nこのまま時が止まってしまえば\nいいのに…なんて\nそんなことを思ってしまいます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1220007, 122031, 0, 0.0, 1, "剣では救えない\n彷徨える魂たちも\n私と…あなたの力があれば\n救うことができます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1220008, 122031, 0, 0.0, 1, "あなたが迷った時は\n私にも力にならせてください\n暗夜の灯火となるように\nあなたの進む道を占いますから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1220009, 122031, 0, 0.0, 1, "あなたと一緒なら\nどこまでも遠くへ\n行けそうな気がします\nこの海の果てまででも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1220010, 122031, 0, 0.0, 1, "月明かりと星の光…\nそれにどこまでも続く\n海だけが広がっていて…\n世界に二人きりみたいですね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1220011, 122001, 0, 10.0, 5, "お誕生日おめでとうございます\n知り合いの霊から海賊に伝わる\n祝い唄を教えてもらったんです\n喜んでもらえるといいのですが", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1220012, 122031, 0, 0.0, 1, "あなたのお誕生日のお祝いに\n私からこの舞を捧げます\nこの一年の幸せを祈願して\n心を込めて舞いますね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1221001, 122101, 0, 0.0, 2, "やっぱり…自然はいいですね～\n小鳥さんや草花さんたちの\nおしゃべりも盛り上がっていて…\n心が落ち着きます…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1221002, 122101, 0, 4.4, 3, "自然はいつだって、私のことを\n受け入れてくれるんです…！\nそれはもう今か今かと\n待ち受けている勢いでっ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1221003, 122101, 0, 5.8, 2, "都会では山籠もりのことを\nキャンプって言うんですよね？\nわぁ〜、キャンプかぁ〜…\nなんだかハイセンスな響き…！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1221004, 122101, 0, 4.4, 2, "この服は、昔から山籠もりの時に\nいつも着てるんです！\n自然と向き合うための\n正装のようなものですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1221005, 122101, 0, 0.0, 2, "今度からは…ぼっち空間も\nＢＢ団の活動拠点にしましょう！\n私の大事な場所ですが…\nあなたならいつでも大歓迎です！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1221006, 122131, 0, 0.0, 1, "今夜は語り明かしましょうね？\nＢＢ団の今後について…！\n私たちの壮大なぼっち卒業計画を\n大自然や星々に聞かせましょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1221007, 122131, 0, 0.0, 1, "風のウワサで聞いたんですけど…\nBBQというものがあるそうです\n親近感がわく名前ですよね！\n多分ぼっちに優しいもののはず♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1221008, 122131, 0, 0.0, 1, "ふーふー…心も体も\nあったまります♪　なんでもない\nスープなのに、特別な味がします\nきっと二人だから…ですよね…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1221009, 122131, 0, 0.0, 1, "二人で力を合わせて\n設営をしてお料理をして…\nキャンプの共同作業は\n非ぼっちの気分が味わえます…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1221010, 122131, 0, 0.0, 1, "夜のキャンプ場は…\n本当に静か…ですね…\nまるで私とあなただけの…\nふたりぼっちの世界みたい…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1221011, 122101, 0, 2.9, 4, "お誕生日おめでとうございます！\n…あはは、やっぱり緊張しますね\n私の挨拶、大丈夫でしたか？\n声とか裏返ってませんでした？", 7, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1221012, 122131, 0, 0.0, 1, "たんおめ～♪　…すみません！\nどう挨拶しようか考えすぎて\nフランクになりすぎました…！\n改めて、おめでとうございます！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1222001, 122201, 0, 6.3, 2, "どう？　この服、素敵でしょ？\n見た目だけじゃなく機能性も\n抜群なの。キャンプ用品店で\n一目惚れして買っちゃった♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1222002, 122201, 0, 3.1, 2, "またまたキャンプ道具を\n買い足しちゃった…\n元を取れるくらい使いたいから\nキミと何度も来なくっちゃね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1222003, 122201, 0, 6.8, 1, "んー！　なんて開放感なのー！\nもうこの眺めだけで\n麦しゅわが飲めるわ！\nさあ、キャンプを始めましょ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1222004, 122201, 0, 0.0, 2, "えっへへへ〜♪\nとっことんキャンプ飯で\n飲んじゃうわよ〜♪\n最高の一杯、さいこーっ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1222005, 122201, 0, 4.0, 1, "キャンプって楽しいけど\n不便なこともあるわよね…\nなにか困ったことがあったら\nお姉さんに頼っていいからね？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1222006, 122231, 0, 0.0, 1, "すぅ〜…うん、空気がおいしい…\nもうこれだけで永遠に飲めそう…\n自然そのものが最高のおつまみ…\n朝から乾杯…しちゃおっかしら…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1222007, 122231, 0, 0.0, 1, "わ、私だって麦しゅわ以外の\n飲み物も飲むんだからっ！\nそれに、コーヒーなら\nキミと一緒に楽しめるでしょ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1222008, 122231, 0, 0.0, 1, "朝日って本当にきれい…\nランドソルじゃこんな景色は\nなかなか見られないわよね\n存分に味わいましょう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1222009, 122231, 0, 0.0, 1, "おはよう。コーヒー飲むでしょ？\nふふ、普段とは大違いね…\nだって、私がキミを起こして\n飲み物も二人分あるんだもん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1222010, 122231, 0, 0.0, 1, "キャンプ場で迎える朝って新鮮ね\n当たり前のことが\nなんだか当たり前じゃないみたい\nそれともキミと一緒だからかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1222011, 122201, 0, 7.5, 1, "ふふ、ハッピーバースデー！\nキャンプ場でケーキだなんて\n他には体験できないでしょ？\nほらほら、早速食べてみて！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1222012, 122231, 0, 0.0, 1, "お誕生日おめでと～！\nとっておきのキャンプ飯を\n振る舞ってあげるね？\n特別なキャンプにしてあげる！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1223001, 122301, 0, 4.9, 7, "けんぞくー！\nヴァンピィちゃんの\nとっておきの場所、気に入った？\nさんにんだけの秘密だからね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1223002, 122301, 0, 5.8, 2, "んー！　いい天気ー！\n絶好のお散歩日和かも！\nほらほら！\nけんぞくも早くこっちおいで♪", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1223003, 122301, 0, 3.1, 2, "けんぞく、もう疲れちゃったの？\nしょーがないなぁ…\nヴァンピィちゃんが\nひっぱってあげる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1223004, 122301, 0, 2.7, 7, "ここにも魔物がいるんだねー\nでも、大丈夫だよ！\nけんぞくはヴァンピィちゃんが\n守ってあげるから♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1223005, 122301, 0, 5.7, 7, "けんぞくにね\nヴァンピィちゃんの好きな景色を\n見せてあげたかったんだ\n今度はけんぞくも教えてね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1223006, 122331, 0, 0.0, 1, "見て、けんぞく！　ちょうちょ！\nベスちゃんったらもう夢中だね！\nけんぞくは楽しい？\nヴァンピィちゃんも楽しーよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1223007, 122331, 0, 0.0, 1, "けんぞくがまた疲れちゃったら\nヴァンピィちゃんが助けるね！\nだってヴァンピィちゃんは\nアンタのご主人様ですから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1223008, 122331, 0, 0.0, 1, "お散歩ルールその１！\nけんぞく、いいことー？\nヴァンピィちゃんのそばを\n離れたらダメだからね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1223009, 122331, 0, 0.0, 1, "ヴァンピィちゃんね\n見せたい場所、たくさんあるの！\nだからまた一緒にお散歩しよう！\n約束だよ、けんぞくぅ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1223010, 122331, 0, 0.0, 1, "ベスちゃんとけんぞくは違うけど\nおんなじぐらい大切なの！\n…つまり、ヴァンピィちゃんは\nどっちも大好きってこと♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1223011, 122301, 0, 4.2, 2, "けんぞくぅ～！\nお誕生日おめでとー♪\nケーキは作れないけど\nおいしいスープを作ってあげる♪", 7, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1223012, 122331, 0, 0.0, 1, "ヴァンピィちゃん知ってるよ～？\n今日はけんぞくのお誕生日！\nこれからもけんぞくのこと\n大切にするからね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1223013, 122361, 0, 0.0, 1, "ヴァンピィちゃんってば\nさいこーにぜっこーちょー！\n夜はまだまだこれから！\nいーっぱい遊ぼーね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1223014, 122361, 0, 0.0, 1, "今のヴァンピィちゃんなら\nあのお月様にも手がとどきそう！\nけんぞく、ベスちゃん、\n夜空のお散歩にしゅっぱーつ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1223015, 122361, 0, 0.0, 1, "夜遅くて眠くなっちゃった？\nもし寝ちゃっても\nヴァンピィちゃんがそばに\nついててあげるから安心してね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1223016, 122361, 0, 0.0, 1, "けんぞく、かぷっとしていーい？\nそうしたら、ヴァンピィちゃん\nもーっと強くなれるし…\nいつまでも、一緒にいられるよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1223017, 122361, 0, 0.0, 1, "いつもそばにいてくれてありがと\nけんぞく♪　この幸せがずっと\n続くよーに、ヴァンピィちゃんは\nずっとさいきょーでいるからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1224001, 122401, 0, 3.8, 7, "見て見て！　すっごくいい天気！\n騎士くんも一緒に\n夏を満喫しようよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1224002, 122401, 0, 9.1, 2, "騎士くん騎士くん！\nこの水着…どう…かな？\n…似合ってる？\nえへへ、うれしいな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1224003, 122401, 0, 1.7, 1, "ふう…汗かいちゃったから\n水分はしっかり取らないとね♪\n騎士くんは大丈夫？\n熱中症には気を付けようね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1224004, 122401, 0, 3.1, 2, "騎士くんと過ごす夏…\nこんなに楽しいんだね！\nもっともーっとめいっぱい\n楽しんじゃうぞー！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1224005, 122401, 0, 5.8, 1, "暑くても元気いっぱいだよ！\nだって騎士くんと一緒だもん！\nもしも元気がないなら\nあたしのをあげちゃう！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1224006, 122431, 0, 0.0, 1, "あ、騎士くん！　こっちだよ！\nえへへ、今日は一日\n思いっきり楽しもうね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1224007, 122431, 0, 0.0, 1, "見て見て騎士くん！\n夏の日差しのおかげで、\nみ～んなキラキラ輝いてるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1224008, 122431, 0, 0.0, 1, "あははっ！\n冷たくて気持ちいー♪\n騎士くんもこっちおいでよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1224009, 122431, 0, 0.0, 1, "騎士くんのそばにいるとね？\nあたしには世界が全部\nまぶしく見えるんだ…\nだからありがとね！　騎士くん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1224010, 122431, 0, 0.0, 1, "騎士くん…大好きだよ！\nこの夏も、一緒に作った思い出も\nそれに…騎士くんのことも！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1224011, 122401, 0, 3.2, 2, "お誕生日おめでとう騎士くん！\nえへへ、こんなカッコでごめんね\n早くお祝いしたくて\n急いで来ちゃった♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1224012, 122431, 0, 0.0, 1, "騎士くん、お誕生日おめでとう♪\n今日はこのプール貸し切りだから\n思いっきりお祝いしちゃうよー！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1225001, 122501, 0, 8.5, 1, "こうして広がる海を見ていると\n晴れやかな気分になるよ\n足取りまで軽くなるみたいで…\nそうだ、向こうまで競争しない？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1225002, 122501, 0, 7.8, 1, "潮風が気持ちいいね\nこんなに穏やかな時間は\nいつぶりだろう…キミもこっちで\n一緒に海を眺めようよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1225003, 122501, 0, 8.1, 2, "陽射しが強くなってきたな…\nあっちで涼みながら\nかき氷でも食べるのはどう？\n…決まりだね。行こうか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1225004, 122501, 0, 5.5, 5, "右を見ても左を見ても\nカップルばかりだけど…\nみんなの目に、私たちは…\nどう…映っているのかな…？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1225005, 122501, 0, 5.1, 2, "時間がどれだけあっても\n足りなく感じてしまうね\nキミとこんな日々をずっと…\nそう夢見るくらいは許されるかな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1225006, 122531, 0, 0.0, 1, "青い海に照り付ける太陽…\nこれぞバカンスって感じだね\n楽しむ準備はできてる？\n夏は待ってくれないよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1225007, 122531, 0, 0.0, 1, "冷たくて気持ちいいね…\nせっかくの水着なんだから\n海に入らないともったいないよ？\nほら、キミもこっちにおいで！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1225008, 122531, 0, 0.0, 1, "ここの生き物はあまり\n人を警戒しないのかな\nこんなに近くでも逃げないで…\nふふっ、小さくてかわいいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1225009, 122531, 0, 0.0, 1, "この空に羽ばたく\n海鳥のようにいつか私も…\n限りのない自由を\n掴み取ってみせる", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1225010, 122531, 0, 0.0, 1, "ずっと胸がドキドキして\nどうにも抑えきれないんだ\n…夏の雰囲気に\n私も当てられたのかな…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1225011, 122501, 0, 8.1, 2, "誕生日おめでとう\nその、ささやかなものだけど…\nプレゼントを用意してみたんだ\nぜひ受け取ってくれるとうれしい", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1225012, 122531, 0, 0.0, 1, "今日はキミの誕生日だよね\n実はリゾートでおすすめの店を\n予約してるんだけど…少しの間\n私に付き合ってくれるかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1226001, 122601, 0, 5.6, 6, "浮き輪、借りてきたよ\nこれなら騎士クンも泳げるよね？\n…あれ？　これ、二人用！？\n…一緒に使う？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1226002, 122601, 0, 7.4, 7, "暑い日は水分を欠かさないでね\n喉が渇いてなくてもこまめに…\nダ、ダメだよ騎士クン！\nわたしのはあげられないの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1226003, 122601, 0, 9.6, 6, "泳いだ後って眠くなるよね\n弟たちと海に行った時は、\nいつもあの子たちも…\n…騎士クン？　寝ちゃった？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1226004, 122601, 0, 10.8, 5, "日焼け止めを塗って欲しい？\nわ、わたしでよければ…！\n…じゃ、じゃあ、失礼します！\n騎士クンの背中…おっきいな…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1226005, 122601, 0, 7.3, 5, "波の音を聞いてるの？\n心が落ち着くよね…\n…ひょっとして、今のわたしたち\nすごくいい雰囲気…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1226006, 122631, 0, 0.0, 1, "すごい星の数…\nこれだけのきらめきに\n照らされたら\n自分の心にうそなんてつけないよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1226007, 122631, 0, 0.0, 1, "この景色を見たこと…\nわたし、忘れないよ\n静けさも、光も、自分の気持ちも\n騎士クンが隣にいてくれたことも", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1226008, 122631, 0, 0.0, 1, "な、泣いてなんかないよ…！\n―あ、ううん、ちがうよね\nわたし…泣いちゃった。ふふっ\nでも泣けたから…もう大丈夫だよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1226009, 122631, 0, 0.0, 1, "…騎士クンと出会った時にね\nわたしの中にわたしとは違う\nもう１人の自分が生まれたの\nそのわたしも、騎士クンが…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1226010, 122631, 0, 0.0, 1, "すぐに焦って、暴走しちゃって…\nこんな私で、いつもごめんね\n…どうしてそうなるのか、って？\nそ、それは…大好きだから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1226011, 122601, 0, 9.1, 1, "誕生日プレゼントに\n特製のかき氷が食べたいの？\nわたしの魔法で作れるかな…\nちょっと試してみてもいい？", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1226012, 122631, 0, 0.0, 1, "騎士クン、お誕生日おめでとう！\n実はね、花火を買ってきたの\nふ、ふたりで一緒に遊びたくて…\n火の扱いには気をつけようね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1227001, 122701, 0, 4.1, 2, "私は海に来たからって\nはしゃいだりしませんよ\n大人はこういう時、ゆっくりと\n過ごすものなんです！　えへへ♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1227002, 122701, 0, 9.1, 2, "日差しには気をつけてくださいね\nこうやって帽子をかぶるとか…\nえ？　持ってない？\n…私の、少しだけ貸してあげます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1227003, 122701, 0, 6.0, 6, "夏の海はおいしいものがたくさん\nあって、食べすぎちゃいますね…\nあ、今おなかは見ちゃダメです！\nダ、ダメなのに…へんた～いっ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1227004, 122701, 0, 4.2, 3, "夏はへんたいふしんしゃにも\n気をつけないといけません\n…私が見張ってますからね！\n絶対に離れちゃダメですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1227005, 122701, 0, 7.4, 2, "私がもし溺れちゃったりしたら…\nその…すぐに助けてくださいね？\nその代わり、あなたが危ない時は\n私が助けてあげますからね♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1227006, 122731, 0, 0.0, 1, "きゃっ！　なんで～！？\n魔法、失敗しちゃいました…\nビショビショになっちゃったけど\n水着だから平気ですっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1227007, 122731, 0, 0.0, 1, "夏の海では\n私の魔法も絶好調なんです！\nえへへ、冷たいお水…\nかけてあげましょうか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1227008, 122731, 0, 0.0, 1, "涼しくて気持ちいいですね♪\nえ？　いつもより楽しそう？\n…子どもっぽい、ですかね…？\n今日は許します、夏の海なので！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1227009, 122731, 0, 0.0, 1, "も～、水をかけないでください！\nひゃっ、つめた～いっ！\n言うことを聞かない人はこうです\n弱めのコスモブルーフラッシュ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1227010, 122731, 0, 0.0, 1, "こんなに海が楽しいなんて\n思いませんでした…あ、あなたの\nおかげかもしれません…あの…\n…大人になったら、また二人で…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1227011, 122701, 0, 7.1, 5, "お誕生日おめでとうございます\nえ？　どうして水着なのかって？\nべ、別に理由はありませんけど\n嬉しく、ありませんでしたか…？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1227012, 122731, 0, 0.0, 1, "海でお誕生日をお祝いするのって\n特別感があっていいですよね\n今回はあなたの番ですけど\n私の時もお願いしますねっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1228001, 122801, 0, 5.7, 1, "えへ、かわいい水着でしょー！\n動きやすくてお気に入りなんだ♪\nこの水着で海辺を探検したいな！\nもちろんにいちゃんも一緒だよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1228002, 122801, 0, 8.6, 2, "水鉄砲に水風船に熱湯風呂に～\n今日はずぶぬれ系のイタズラを\nにいちゃんに仕掛けちゃうよ！\n水着だからぬれても平気だよね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1228003, 122801, 0, 5.4, 2, "この水中メガネを使ったら、\n海の中でもきれいに見えるよ！\nねえにいちゃん！　海のお魚と\nにらめっこ勝負しようよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1228004, 122801, 0, 6.3, 2, "ハリセンボンのトゲトゲに～\nクラゲのチクチク、タコのスミ～\n海の生き物ってイタズラ好きな子\nいっぱいだね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1228005, 122801, 0, 3.3, 5, "やったーにいちゃんと海だー♪\n今日は最高の思い出つくろうよ！\nねっ！　にいちゃん！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1228006, 122831, 0, 0.0, 1, "ホントは派手な花火が好きだけど\n線香花火もきれいだね～\nずっとずっと見てたいから\nまだ消えちゃわないでほしいなぁ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1228007, 122831, 0, 0.0, 1, "線香花火ってきれいなだけじゃ\nなくって、音もかわいいな～\nなんだか線香花火のこと\nもっともっと好きになってくよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1228008, 122831, 0, 0.0, 1, "今日は海でにいちゃんと遊べて\nとっても楽しかったな～♪\n来年の夏も、その次の夏も\nミソギと一緒に遊ぼうね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1228009, 122831, 0, 0.0, 1, "夜の風が気持ちいいけど、\n線香花火が消えちゃうよ～！\nねえ、にいちゃん\n風よけに、ミソギの横きて～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1228010, 122831, 0, 0.0, 1, "わかってるよ、ちゃんとゴミは\n持って帰るってば！　\nきれいな海のほうが探検する時、\n気持ちいいもんね♪　", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1228011, 122801, 0, 4.0, 2, "にいちゃんの誕生日を\n今日は海でお祝いしよう！\nプレゼントにミソギがいーっぱい\n遊んであげるね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1228012, 122831, 0, 0.0, 1, "誕生ケーキはろうそくの代わりに\n花火を使っちゃおっか♪　…あ、\nケーキ用意するの忘れちゃった！\n仕方ないから花火しようよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1229001, 122901, 0, 5.2, 2, "ねえねえ、おにいちゃん！\n海でなにしてあそぼっか！\nおにいちゃんとなら、なにしても\nぜったいたのしいと思うな♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1229002, 122901, 0, 7.2, 5, "わぁ、おさかなさんいっぱい！\nヒトデさんもかわいいな～♪\nおにいちゃん、海まで\nつれてきてくれて、ありがとう♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1229003, 122901, 0, 7.3, 6, "キョウカちゃんってたまに、\nへんたいふしんしゃさんって\nおにいちゃんのこと呼ぶけど…\nそれって、なあに？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1229004, 122901, 0, 11.0, 2, "おにいちゃんの食べてるかき氷、\nメロン味？　おいしそうだね♪\n…一口くれるの？　ありがと♪\nあ～ん♪　…えへへ、おいし～♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1229005, 122901, 0, 5.8, 5, "おかあさんがね日焼けしないよう\n日傘さした方がいいよって。\nおにいちゃん、相合い傘して、\n砂浜をさんぽしよう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1229006, 122931, 0, 0.0, 1, "わぁ！　イルカさん、すごいね！\nぴょ～んって飛んでる！\nミミも海を飛んでみたいな♪\nぴょんぴょ～ん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1229007, 122931, 0, 0.0, 1, "えへへ♪　イルカさんと、\nおともだちになっちゃった！\nいっしょにいっぱい夏の思い出\nつくろうね、イルカさん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1229008, 122931, 0, 0.0, 1, "太陽サンサン、波がざぷ～んっ♪\n気持ちいいね、おにいちゃん♪\nぷかぷか浮かんでるだけでも\nミミ、す～っごく幸せ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1229009, 122931, 0, 0.0, 1, "おにいちゃんとの夏休みって\n今までとはちがう感じがする！\nドキドキワクワクな、\nとくべつな夏になりそう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1229010, 122931, 0, 0.0, 1, "お昼の海ってきれいだけど、\n夜の海ってどんな感じなんだろ？\nいつかミミが大人になったら、\n夜にあそびにこようね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1229011, 122901, 0, 8.7, 2, "お誕生日、おめでと～！\nあのね、ミミね、砂浜に\nおにいちゃんの絵をかいたの！\nよろこんでくれるとうれしいな♪", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1229012, 122931, 0, 0.0, 1, "ハッピーバースデー！\nイルカさんもね、おにいちゃんの\nおいわいに来てくれたよ♪\nみんなでいっしょにあそぼう♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1230001, 123001, 0, 4.5, 2, "あんたが今見てるのは\n夢じゃなくて、本物のあたしよ\nじっくり拝んじゃいなさい♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1230002, 123001, 0, 0.0, 1, "暇だしあんた相手しなさいよ\n何をしたいか？　そうね…\nあんたが相手ならなんでもいいわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1230003, 123001, 0, 3.5, 1, "背中のこれが羽かどうかって？\nいつの間にかこうだったのよ\nあたしもあんたとおんなじね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1230004, 123001, 0, 6.2, 2, "あーあー…ちょっと待ちなさい\n髪が変になってる…これでよし\n相変わらずおっちょこちょいね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1230005, 123001, 0, 3.7, 1, "一人でどっか行かないのかって？\nあたしはね、あんたを放って何か\nしようって気にならないのよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1230006, 123031, 0, 0.0, 1, "ほら、あたしを連れていきなさい\nまたあんたのそばにいてあげるわ\n今度こそ、最後の最後までね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1230007, 123031, 0, 0.0, 1, "飛べるようにもなったし\nあんたに会いに来てやったわ\nうれしい？　もっと喜んでいいわよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1230008, 123031, 0, 0.0, 1, "失くしたはずの羽…\n大事にするわ。これであんたの\nピンチに駆けつけてあげるから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1230009, 123031, 0, 0.0, 1, "あんたもあたしも色々あったけど\n過去よりも今、今よりも未来を\nあたしたちでより良くしましょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1230010, 123031, 0, 0.0, 1, "あたしが願う、行きたいところに\nあんたを連れてってあげられる…\n一緒に来てくれるわよね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1230011, 123001, 0, 6.7, 2, "あんた今日誕生日じゃない\nプレゼント？　…忘れてた！\nな、何か買いに行きましょ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1230012, 123031, 0, 0.0, 1, "ハッピーバースデー。おめでとう\n覚えてない？　大丈夫。あたしが\nずっと忘れないでいてあげるわよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1231001, 123101, 0, 6.0, 1, "都会を離れてこうやって\n自然に触れるのもええなぁ\n危険がいっぱいのジャングルも\n王子はんとなら大丈夫や♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1231002, 123101, 0, 8.0, 6, "この辺りは虫さんが多くて\nうち、ちょっとかなんわぁ…\nちょうちょさんとかは\n平気なんやけどなぁ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1231003, 123101, 0, 7.7, 1, "森でのサバイバルやと\n泊まるとこの準備するのも\n一苦労やね…でもみんなで\n一緒に準備するの楽しいわぁ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1231004, 123101, 0, 3.4, 1, "お疲れさんどす、王子はん\nちょうどお茶の準備が\nできましたさかいに\n少し休憩しはったらどうどす？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1231005, 123101, 0, 6.9, 2, "王子はんと一緒なら\nどんなとこを冒険しても\n守ってくれますやろ？\n頼りにしてますえ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1231006, 123131, 0, 0.0, 1, "ついに見つけましたえ！\nあれが伝説の遺跡…\nうちドキドキしてきましたわ\n王子はん、行ってみまひょ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1231007, 123131, 0, 0.0, 1, "緑のざわめきに鳥さんの声…\nなんや冒険らしい雰囲気に\nなってきましたわぁ♪\n王子はん、準備はよろしおすか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1231008, 123131, 0, 0.0, 1, "冒険を重ねて、うちも\nけっこう成長してますんよ？\nうちが王子はんを守りますさかい\n王子はんはうちのこと守ってな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1231009, 123131, 0, 0.0, 1, "おうちに着くまでが冒険どす\n行きはよいよい帰りは怖い\n言いますやろ。冒険も旅行も\n怪我せぇへんのが大事なんよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1231010, 123131, 0, 0.0, 1, "うちのポケットの中には\n愛と勇気とお茶菓子が\nぎょうさん入っとりますんよ～\n王子はんにもお裾分けや♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1231011, 123101, 0, 0.0, 2, "お誕生日おめでとさん♪\nお祝いにとっておきの紅茶と\nお菓子を用意してますんよ\n森のお茶会にご招待や～♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1231012, 123131, 0, 0.0, 1, "今日は王子はんの誕生日を\nお祝いする大冒険ツアーや♪\nうちがガイド役務めますさかい\n楽しんでくれるとうれしいわぁ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1232001, 123201, 0, 7.7, 1, "『いいかげん荷物が多いのによ\nすまねぇな、俺まで担がせて』\nぷうきちは荷物じゃないよ？\nぷうきちはぷうきちだもん♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1232002, 123201, 0, 3.9, 6, "見て見てお兄ちゃん！\n旅のしおりを作ったの♪\nえ？　絵しか描いてないよって？\nいいの～こういうのは気分なの～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1232003, 123201, 0, 4.8, 2, "地図をグルグル回すのはね\n方向音痴の証拠なんだって\nってことは、私もお兄ちゃんも\n方向音痴で…おそろいだね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1232004, 123201, 0, 8.7, 6, "じゃ～ん、旅のお洋服だよ♪\n『おお…完璧装備じゃねぇか！\n何が起きても大丈夫だな』\n…でもお兄ちゃんは側にいてね？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1232005, 123201, 0, 2.4, 2, "私の冒険に終わりはないの\nお兄ちゃんとぷうきちと\nどこまでもどこまでもずーっと\n一緒に進むんだもん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1232006, 123231, 0, 0.0, 1, "近道より、遠回りがしたいな～\nだってせっかくのお兄ちゃんと\n一緒の冒険なんだもん♪\nね、あっちにも行ってみようよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1232007, 123231, 0, 0.0, 1, "海のむこうには何があるのかな？\n空のむこうは？　…お兄ちゃんも\n知らないんだ。…じゃあじゃあ、\n想像で言い合いっこしよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1232008, 123231, 0, 0.0, 1, "『道なんてねぇぜ？　道は俺たち\nの後ろにできるんだよ。キリッ』\nい～眺めだね～ぷうきちが思わず\nキリッとしちゃうのもわかるね～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1232009, 123231, 0, 0.0, 1, "冒険って、不安と期待でドキドキ\nするでしょ？　…その感じがね\n私が時々お兄ちゃんに感じる\nあの気持ちと…すごく似てるの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1232010, 123231, 0, 0.0, 1, "ふふ、お兄ちゃんも泥だらけだ～\n冒険をがんばった証拠だよね\nお兄ちゃん…カッコいいよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1232011, 123201, 0, 6.2, 5, "ハッピーバースデーお兄ちゃん！\nお兄ちゃんはね、私のお守りなの\nだからこれからもそばにいて\n私の冒険を見守ってね♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1232012, 123231, 0, 0.0, 1, "『誕生日おめでとう坊ちゃん！』\nあー私が先に言いたかったのに～\n『ああ、す、すまん…！　\n旅先でつい浮かれちまった…』", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1233001, 123301, 0, 6.7, 1, "テキだったコトもあっけどー\nそーゆーめんどいの忘れてー\nとりま、ユルめなカンジでよろー", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1233002, 123301, 0, 7.5, 2, "アタマのツノだけはー\nこのカッコでもスライムの\nまんまなんだよねー\nンフフ～さわってみる～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1233003, 123301, 0, 9.0, 2, "こっちの世界て楽し～ねー\n来てよかったわー\n甘いのおいし～し～\nショタもたくさんいるし～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1233004, 123301, 0, 5.6, 2, "騎士きゅんのほっぺ～\nつんつんしてい～い？\nだってちょ～ぷにぷにで～\nかんわゆ～いんだも～ん♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1233005, 123301, 0, 8.2, 2, "騎士きゅんは～\nみんなに優しーよね～\nけどアタシには～\nもーっと優しくしてね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1233006, 123331, 0, 0.0, 1, "アタシのスライム、いーっしょ？\nべっとべとのぬっるぬるで～\nショタつかまえて放さんつかー\nねー？　カリザきゅーん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1233007, 123331, 0, 0.0, 1, "ン～♪　やっぱ～\nショタの鳴き声ってサイコ～♪\n騎士きゅんも鳴いてみる？\nンフフ♪　なーんちて～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1233008, 123331, 0, 0.0, 1, "騎士きゅん変わってるよねー\nニンゲンもスライムも\n困ってたら助けちゃうなんてさ～\nらしーっちゃらしーけどー♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1233009, 123331, 0, 0.0, 1, "騎士きゅんのおかげで～\nカンゼンに力戻ったし～\nこっからアタシが\n騎士きゅん守ってあげっかんね～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1233010, 123331, 0, 0.0, 1, "カリザきゅんは\nイヂめたいかわゆさだけどー\n騎士きゅんはなんつかー\nちゅっちゅしたいかわゆさ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1233011, 123301, 0, 5.3, 1, "騎士きゅんお・た・お・め～！\n今日はアゲてアゲて\nオールで祝いまくっから～\nカクゴしとき～♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1233012, 123331, 0, 0.0, 1, "ショタコン的には～\n好きぴがトシとるのフクザツ～\nけどまーいっか～\nめちゃめでてーし～おめおめ～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1233013, 123361, 0, 0.0, 1, "ンフフ・やべ～♪\nスライム溢れて止まんね～\nこのまま３人でイケるとこまで\nイっちゃおっか～♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1233014, 123361, 0, 0.0, 1, "騎士きゅんもカリザきゅんも\nスライムになっちゃったね～♪\n二人にあだ名付けんなら\nコンとムラサキかな～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1233015, 123361, 0, 0.0, 1, "じゃじゃん、クイズ★イイコト～\nアタシら３人が混ざり合ったら\n何色のスライムになるー？\n正解は…実際に確かめよっか～♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1233016, 123361, 0, 0.0, 1, "好きこそものの上手なれ、か～…\nつまり騎士きゅんとカリザきゅん\nをいっぱいちゅっちゅしたら\nもっと本気出せるってコト～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1233017, 123361, 0, 0.0, 1, "両手に好きぴとか最高か～\n二人ともたっぷりねっちり\nカワいがってあーげる♪\nもう一生放さないんでよろ～♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1235001, 123501, 0, 4.3, 5, "ハロウィンだから、街もすごい\n賑わいになってるね～\nあたしは平常運転\nぐうたらハロウィンを満喫するよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1235002, 123501, 0, 2.1, 1, "ん？　この格好？\nキョンシーの仮装だよ～\n…で、キョンシーってなに？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1235003, 123501, 0, 4.0, 2, "あんたもなにか\nお化けの仮装とかしなよ～\nミイラ男とか、カエル人間とか\nインパクトのあるやつね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1235004, 123501, 0, 3.6, 2, "ハロウィンのお祭り\nちゃんと楽しんでる？\nあたしは…まぁ、うん\nあんたがいれば、なんでもいいや", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1235005, 123501, 0, 6.5, 2, "えっ、あたしとハロウィンに\n参加したいって？\n…分かったよ、行くよ～\nでも、最後までつきあってよね？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1235006, 123531, 0, 0.0, 1, "トリックオアドングリ～♪\nあんパンかゲームでもいいけど\nあたしが欲しい物くれないと\nイタズラしちゃうよ～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1235007, 123531, 0, 0.0, 1, "ハロウィンってお菓子が\nもらえなかったら、好きなだけ\nぐうたらしていいお祭りだよね？\nえっ？　違うの？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1235008, 123531, 0, 0.0, 1, "これってキョンシーの仮装\nなんだけどさ、袖が\nお気に入りなんだよね～\nだら～っとした感じがさ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1235009, 123531, 0, 0.0, 1, "ところで、あんたはあたしの\n仮装をどう思ってるの？\n…お腹が寒そうだって？\nそういうことじゃないよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1235010, 123531, 0, 0.0, 1, "ゆっくり、のんびり\nハロウィンを見て回ろ～？\n焦ったって、一緒にいられる\n時間が短くなるだけだもん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1235011, 123501, 0, 6.0, 5, "誕生おめでと～\nやだな、忘れるわけないじゃん\n…プレゼントは、さっきもらった\nお菓子でもいいかな？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1235012, 123531, 0, 0.0, 1, "誕生日、めでたいね～\nプレゼントはあたし手作りの\nカボチャあんパンだよ\nおかわりもあるからね～", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1236001, 123601, 0, 7.0, 3, "パレードは賑やかで楽しいね\nでも羽目を外しちゃう人は\n私が取り締まっちゃうよ！\nハロウィンの平和は私が守る！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1236002, 123601, 0, 2.8, 1, "あなたも一緒に仮装しようよ！\n私の隣を歩くならお似合いなのは\n…騎士様？　王子様？\n逆に囚人服とかもありかもね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1236003, 123601, 0, 5.4, 2, "ちょっと目を離すと、あなたは\nいつもどこかに行っちゃうね…\n心配だからフラフラできないよう\n手錠かけちゃおうかな♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1236004, 123601, 0, 6.3, 2, "ハロウィンだしお化けが出てきて\n私のこと驚かせてくれないかな？\nだってそれなら、怖がるふりして\nあなたに抱きつけちゃうもんね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1236005, 123601, 0, 5.3, 2, "ハッピーハロウィンって言うけど\nなにがハッピーなんだろうね？\n…私はあなたと一緒に過ごせて\n超超ハッピーなんだけど♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1236006, 123631, 0, 0.0, 1, "かわいい飾りや花火に目もくれず\n私を見つめてるのはなんでかな？\nふふ、もしかして、あなたの瞳\n逮捕しちゃった？　なーんてね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1236007, 123631, 0, 0.0, 1, "ん？　手錠を貸してほしいって？\n別にいいけどイタズラに使っちゃ\nダメだよ…って言ってるそばから\n私に手錠、かけるなぁ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1236008, 123631, 0, 0.0, 1, "倒すだけじゃなく\n相手を無力化して捕縛する剣術も\n会得したい…ということで\n稽古のお相手、よろしく頼むよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1236009, 123631, 0, 0.0, 1, "いつもは厳かな雰囲気の王城も\nハロウィンの時はムードがあるね\nはは、こんなところで二人っきり\nなんて、緊張しちゃってる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1236010, 123631, 0, 0.0, 1, "イタズラとからかいの違いか～…\nそうだなぁ多分、好きな人にしか\nやらないのがからかいかな…って\n別に今の告白とかじゃないよ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1236011, 123601, 0, 9.1, 5, "今日はあなたの誕生日だね\nカボチャの料理を用意したけど\nお口に合うかな…合えばいいな\n一応これ、手作りなんだ…♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1236012, 123631, 0, 0.0, 1, "ハロウィン姿の私をプレゼント♪\nこの格好見て目が幸せにな～れ\nってあれれ、ちょっと残念そうだ\nふふ、なにを想像してたのかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1237001, 123701, 0, 7.1, 7, "じゃーん！　どうどう？\n今日のナナカちゃんは\n自称ハロウィン魔法少女なのだ！\nシャランラッ☆", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1237002, 123701, 0, 4.5, 6, "ふっふっふ、ナナカちゃんが\n魔法をかけちゃうぞ♪\n何の魔法かって？　それじゃあ\n恋の魔法～、な、なんちゃって…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1237003, 123701, 0, 3.6, 4, "パンプキィ、ハロウィッチ、\nうーん、どうしよ～…\nナナカちゃんの魔法少女ネーム\n決めらんないよ～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1237004, 123701, 0, 5.1, 4, "万聖節の夜に舞い降りる\n南瓜の使者…！\nう～ん、決めゼリフどうしよう\nアンナたそに考えてもらおっか？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1237005, 123701, 0, 6.3, 2, "ハロウィン魔法少女！\nって５人目っていうより\nゲストキャラな感じしない？\nいいよね、ゲスト枠戦士！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1237006, 123731, 0, 0.0, 1, "ハロウィンナナカちゃん参上！\nどうかなどうかな？\nえへへ、せっかくだから\nかわいく撮ってね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1237007, 123731, 0, 0.0, 1, "見渡す限り、コスプレの人だらけ\nここが約束の地か…って\nぼーっとしてたらもったいないね\n楽しまなくっちゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1237008, 123731, 0, 0.0, 1, "コスにあわせた決めポーズを\n考えるのって難しいけど\n一度始めたらハマっちゃうな～\nよかったら一緒に考えてよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1237009, 123731, 0, 0.0, 1, "さっき君のこと、相方かって\n聞かれたから、ついつい\nそうですって答えちゃったけど…\nいい、よね…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1237010, 123731, 0, 0.0, 1, "はぁ、コスプレって楽しいなあ…\nって、普段からやればいいって？\nそれはさすがに恥ずかしいけど…\n君と二人きりだったら、いいよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1237011, 123701, 0, 3.3, 1, "お誕生日おっめでと～♪\n私ってば、推しのプロフィールは\n暗記する系のオタクなんだよね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1237012, 123731, 0, 0.0, 1, "ハッピ～バースデ～♪\nプレゼントはナナカちゃんの\nコスプレ姿！　…なんちゃって～\n何か言ってよ、ハズイじゃんか～", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1238001, 123801, 0, 5.1, 2, "おおいワタシを一人にするなよ？\n寂しいとむしずが走るんだよ\n命懸けでかわいがっておくれ☆", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1238002, 123801, 0, 0.0, 2, "さぁ坊や、互いに狩り合おう！\n坊やがワタシを食らうか\nワタシが坊やを食らうか\nどちらにしても…胸が躍るよ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1238003, 123801, 0, 5.5, 2, "オマエを見ていると\n狩猟本能と庇護欲の\n両方がかき立てられる…\nまったく、稀有な生き物だ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1238004, 123801, 0, 5.4, 2, "どうだ、この毛並み…\n思う存分、モフりたいだろう？\nしかし残念ながらまだダメだ\n誓約を果たしてから…な☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1238005, 123801, 0, 5.7, 1, "どうだ、愛らしいだろう？\n坊や、ワタシを飼ってみるか？\n飼い殺しにしてもかまわんぞ\nこう見えて主従には堅いのだ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1238006, 123831, 0, 0.0, 1, "ウサギにしては強そうだと？\nあっはは☆　かまわんさ！\nオマエの中のウサギ像を\nワタシ色に塗り替えてやろう…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1238007, 123831, 0, 0.0, 1, "ずいぶんと熱心に見つめるのだな\nいいだろう、教えてやる\nオマエが今感じているのはな…\nギャップ、というヤツだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1238008, 123831, 0, 0.0, 1, "こうして下から見上げるのも\n悪くない光景だな\nふふ…しばらくそこに立っていろ\nワタシが見飽きるまで、な♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1238009, 123831, 0, 0.0, 1, "ん、尻尾が動いているだと？\nさすがにそれはないさ。鎧だぞ？\n…ま、オマエと二人きりだからな\n尻尾を振りたい気分ではあるが☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1238010, 123831, 0, 0.0, 1, "耳は繊細な作りのようでね\nどうやらこの鎧の弱点らしい\n…なに、教えていいのかだと？\nふふ、坊やにだけは特別さ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1238011, 123801, 0, 9.1, 2, "今日は坊やの誕生日らしいな\n生き物は皆一人で生まれ\n一人で死んでいくと言うが…\n安心しろ。我々は二人で一匹さ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1238012, 123831, 0, 0.0, 1, "オマエと出会わなければ\nワタシは退屈の荒野に\n野たれ死んでいたかもしれん\n坊やの誕生に感謝しよう♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1239001, 123901, 0, 5.2, 2, "これが自分の新しい姿ッス！\nただの鎧じゃないッスよ？\nなんと、お母さんの手作りッス！\nいかしてるッスよね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1239002, 123901, 0, 4.5, 2, "自分のイチ押しは\nなんといってもこのマント！\nかっこいいし、くるまったら\nお昼寝もできるすぐれモノッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1239003, 123901, 0, 3.2, 1, "鍛え抜かれた自分の腹筋！\n…には、ほど遠いッスけど…\n魂のほうはもうすごいッスよ！\nモリモリのバッキバキッス！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1239004, 123901, 0, 12.1, 1, "耳？　いやー…耳を見せるのは\nまだ恥ずかしいッス…\nずっと見せないようにしてたから\nなんか…すーすーしちゃうんス", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1239005, 123901, 0, 7.7, 2, "そうだ！　二人でお揃いの\n鎧を着るのはどうッスか？\nもちろん色違いで！\nうんうん、相棒っぽいッス～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1239006, 123931, 0, 0.0, 1, "トラか？　王宮騎士か？\n答えは一つ！　どっちもッス！\nそれが自分─\nマツリの生きざまッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1239007, 123931, 0, 0.0, 1, "悪事は絶対見逃さない！\nこの空に輝く太陽と…\nブレイブタイガーが\nお見通しッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1239008, 123931, 0, 0.0, 1, "マントを、こう…バサッ！\nこの風をまとった感じ\nまさしくヒーロー！\n悪に立ち向かう者の姿ッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1239009, 123931, 0, 0.0, 1, "あんたのおかげで強くなれた…\nだから、見てほしいッス！\nここまでこられた自分の姿を！\n他の誰でもない、あんたに！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1239010, 123931, 0, 0.0, 1, "応援ありがとうッス！\nおかげで勇気と力が\nモリモリ湧いてくるッスよ！\n今の自分は、最強無敵ッス！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1239011, 123901, 0, 4.1, 2, "今日はいい日ッスね～\nなにせ、あんたの誕生日！\nいやーめでたいッス！\nほんっとーに、おめでとうッス！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1239012, 123931, 0, 0.0, 1, "お誕生日おめでとうッス！\n最高の一日になるように\nこのブレイブタイガーがサポート\nするッス！　遠慮はなしッスよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1240001, 124001, 0, 3.0, 2, "お兄ちゃん、もっと見て…？\nアカリサンタのお洋服！\nエンジェルの羽もついてて\nとってもかわいいでしょ？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1240002, 124001, 0, 5.0, 2, "サンタのお洋服を着た\n世界一かわいいエンジェル…\nえへへ、アカリってば\nかわいさテンコ盛りですね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1240003, 124001, 0, 4.6, 1, "クリスマスパーティーなら\nアカリにお任せあれです♪\nアカリの演奏で、みーんな\nハッピーにしちゃいますよ～", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1240004, 124001, 0, 4.1, 5, "賑やかなクリスマスも\nとっても楽しいけど…\n今夜はアカリ、お兄ちゃんと\n二人きりで過ごしたいなぁ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1240005, 124001, 0, 7.4, 2, "お兄ちゃん、寒くないですか？\nこうやって手をすりすりして…\nふふ、アカリサンタが温もりを\nお届けしちゃいますね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1240006, 124031, 0, 0.0, 1, "アカリの素敵なスウィングで\nハッピーなクリスマスを\nもーっとハッピーに\nしちゃいますね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1240007, 124031, 0, 0.0, 1, "ほらほら、お兄ちゃん！\nアカリがクリスマスナンバーを\n演奏してあげますから…\nアカリサンタとダンスしましょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1240008, 124031, 0, 0.0, 1, "キレイなイルミネーション…\nでも、アカリだって負けません！\nお兄ちゃんの視線は\nアカリが独り占めしちゃいます♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1240009, 124031, 0, 0.0, 1, "お兄ちゃんがそばにいると\nアカリのカラダ…\nなんだかすごく火照っちゃって…\n冬の寒さもへっちゃらです♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1240010, 124031, 0, 0.0, 1, "アカリは世界一かわいい\nサンタさんだけど…\nお兄ちゃんは、世界一かっこいい\nアカリだけのサンタさんです♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1240011, 124001, 0, 5.4, 5, "お兄ちゃんのお誕生日\nアカリ、全力でお祝いします♪\n今ならお兄ちゃんのしたいこと\nなんでもしていいですよ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1240012, 124031, 0, 0.0, 1, "ハッピーバースデーお兄ちゃん！\nサンタでエンジェルな\nアカリが、いーっぱい\nお祝いしてあげますね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1241001, 124101, 0, 7.2, 6, "あ、あんまり見ないでよっ\n急に変身したから心の準備が…\nへ？　かわいいから安心して？\nそそそそういう問題じゃな～い！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1241002, 124101, 0, 6.2, 1, "サンタコスのエンジェルとか…\n属性盛り過ぎちゃってない？\nここまできたら、もうどこを\n恥ずかしがればいいのか…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1241003, 124101, 0, 7.5, 4, "ね、ねえ…今の私、大丈夫？\nちゃんとエンジェルに見える？\nただただクリスマスだからって\n浮かれてる人に見えない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1241004, 124101, 0, 6.1, 6, "こういう形でもアンタと一緒に\nクリスマスを過ごせるなんて…\nハッ！？　なんでもないわよ\nなんでもっ。あはははは…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1241005, 124101, 0, 4.8, 3, "人見知りだからって\n恥ずかしがってるのはダメよね\n…でもアンタは近くにいること！\nお守りみたいなものなんだから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1241006, 124131, 0, 0.0, 1, "ハ…ハッピークリスマス\nサンタなエンジェルからの…\nううん、私からのプレゼントよ\n…いいから受け取りなさいっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1241007, 124131, 0, 0.0, 1, "アンタも隠れてっ\nもし、誰かにこんなところを\n見られたら私、私ぃ～…\nぷしゅぅ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1241008, 124131, 0, 0.0, 1, "この子の名前？　まだないけど…\n好きに呼べばいいんじゃない？\n…コヨリ？　私に似てるから？\nえ…えええええ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1241009, 124131, 0, 0.0, 1, "深呼吸して…すーはぁ～…\nよしっ。い、今、渡すからっ\nもうちょっとだけ待ってて\n…ううう、がんばれ私！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1241010, 124131, 0, 0.0, 1, "エンジェルだって…\n「みんな」じゃなくて\n「誰か」を幸せにしたいって\n思うときがあってもいいでしょ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1241011, 124101, 0, 11.4, 3, "ほ、欲しいものとか…ある？\nアンタ、今日アレでしょ？\nいわゆる…誕生日？　でしょ？\nほら、いいから欲しいもの言う！", 5, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1241012, 124131, 0, 0.0, 1, "お、お誕生日よね、今日は\nめ…めでたいわね～。ほんとにっ\nで、で…えっと、でかしたわ！\nと…と？　と～…と、とうッ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1242001, 124201, 0, 9.8, 2, "ほら空には白いハトが飛んでるよ\nまるで羽根が雪のように舞って\n世界が輝いているようだ…\nふふ…少年、メリークリスマス", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1242002, 124201, 0, 5.5, 2, "いつもより体が軽いのは、鎧を\n脱いでいるからだけじゃない…\n子どもみたいで恥ずかしいけど\n少し聖夜に浮かれているみたいだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1242003, 124201, 0, 6.5, 6, "聖夜の平和を守る慈愛の戦士\n聖女仮面、見参！\n…一応考えてみたけど\n決めポーズはいらないかな", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1242004, 124201, 0, 6.7, 2, "うう、突き刺すような寒さだね\n君のほっぺも真っ赤じゃないか\nほら、暖めてあげるから\nこっちにおいで", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1242005, 124201, 0, 6.3, 2, "見慣れた街もクリスマスの装いで\nキラキラと輝いているね\n…いやそう見えるのは多分\n君が隣にいるからかもな…ふふ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1242006, 124231, 0, 0.0, 1, "静かな教会に白いハト\nなんとも神秘的な光景だね\n美しい鳴き声の響きがまるで\nクリスマスキャロルのようだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1242007, 124231, 0, 0.0, 1, "素顔でいる事も少し慣れてきた\nこのままクリスマスの魔法に\nかかったままでもいいかな…\nああ、いや、冗談だよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1242008, 124231, 0, 0.0, 1, "素顔は皆に晒しても、\n私がジュンだということは秘密だ\nもし私を呼ぶときは別の名前で…\nいや少年。聖女さまはやめてくれ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1242009, 124231, 0, 0.0, 1, "少年、そうまじまじ見つめるのは\nやめてほしい…恥ずかしい\nほら顔が、トナカイの鼻よりも\n真っ赤になってしまうよ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1242010, 124231, 0, 0.0, 1, "クリスマスは繁忙期だけど\n束の間の休息は君と過ごしたい\n…ふふ、クリスマスの魔法で\n心の鎧まで脱げてしまったのかな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1242011, 124201, 0, 4.5, 2, "少年の誕生日に\nブッシュドノエルを焼いたんだ\n鎧を脱いだ分、作りやすかったよ\nきっと美味しいから食べてくれ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1242012, 124231, 0, 0.0, 1, "ささやかだけど誕生日の贈り物…\n喜んでくれてるみたいでうれしいな\n君の反応をよーく見たくて\n兜を脱いだかいがあったよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1245001, 124501, 0, 5.4, 2, "普段は着物とか着ないんだけど\nおかしかったりしないかな～？\n素直な感想を聞かせてね\n君を焼き払ったりしないから♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1245002, 124501, 0, 4.3, 2, "君は、なにかお正月らしいものを\n食べたりした？\nお雑煮とかでよければ\n私が作ってあげるよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1245003, 124501, 0, 6.0, 1, "新年は私たちのファミリーも\nゆっくり過ごすことが多いかな？\nもちろん、誰かがちょっかい\nかけてきたら別だけどね☆", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1245004, 124501, 0, 6.9, 1, "ん～？　私がこんな格好で\n誰かをもてなすのが珍しいって？\n心外だな～、そんなことないって\nちゃんと証明してあげるよ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1245005, 124501, 0, 6.0, 5, "君が満足するまで、私のそばで\nお正月休みを楽しんでね☆\n他のことなんて考えず…\nず～っと…永遠に…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1245006, 124531, 0, 0.0, 1, "あけましておめでと～☆\n君が新年をのんびりまったり\n過ごせるように、精一杯\nおもてなししちゃうよ～☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1245007, 124531, 0, 0.0, 1, "君は、どんなお正月を\n過ごす予定なのかな～？\nせっかくなら、この旅館で\nくつろいでいかない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1245008, 124531, 0, 0.0, 1, "いつもより、私が穏やかな\n雰囲気に感じる…って？\nそれはね～、お客様向けに\n笑顔を作ってるからだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1245009, 124531, 0, 0.0, 1, "う～ん、まだまだ君の\n表情が固い気がするな～？\n今回は親睦を深めるためにも\n精一杯おもてなしするね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1245010, 124531, 0, 0.0, 1, "せっかく温泉があるんだし\n二人で一緒にあったまっていく？\n…なんて、抜け駆けしたら\nいろんな子から恨まれそうだね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1245011, 124501, 0, 7.6, 2, "今日は君の誕生日だね～☆\nプレゼントとして、精一杯\nサービスさせてもらおうかな？\n忘れられない思い出になるよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1245012, 124531, 0, 0.0, 1, "今日、誕生日なんだよね？\nそれじゃあ私も、特別な\nサービスを提供しちゃおうかな☆\n…どんなこと、されると思う？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1246001, 124601, 0, 12.5, 2, "一緒にお餅つきをしましょうか～\nあなたがつき手で先生が返しね♪\n息を合わせてぺったんぺったん…\nふふふ、とっても上手よ〜♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1246002, 124601, 0, 0.0, 2, "子どもたちに教えてもらった\n年越しの瞬間にジャンプするの\nとっても楽しかったわね～\n空中で新年を迎えちゃったわ～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1246003, 124601, 0, 8.1, 1, "は～い、あけましておめでとう！\n上手に新年の挨拶できましたね～\n今年も子どもたちとあなたの成長\nとっても楽しみにしてますね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1246004, 124601, 0, 6.7, 2, "うふふ、あなたと過ごす\n静かなお正月も素敵よね\nこたつでミカンでも食べて\nお昼寝しちゃいましょうか〜♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1246005, 124601, 0, 5.0, 2, "出会ってからあなたはどんどん\nたくましくなっていくわね～\n今年もた～くさん成長して\n先生を驚かせてね…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1246006, 124631, 0, 0.0, 1, "後で街に行ってみましょうか～\nきっと初売りで大賑わいよ～♪\nでもお外は寒いからお雑煮食べて\n体の芯を温めましょうね～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1246007, 124631, 0, 0.0, 1, "うん、おいしそうな香りね～♪\nあとは最後の仕上げの隠し味\n…おいしくなりますように…\nはい、しっかり愛情込めました♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1246008, 124631, 0, 0.0, 1, "保育園も飲食店も素敵なお仕事ね\nどちらも、人の笑顔で溢れていて\nこっちも自然と幸せになるわ～\n笑う門には福来る…その通りね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1246009, 124631, 0, 0.0, 1, "いい匂いにつられて\n私もお腹がすいてきちゃった…\n二人分のお雑煮残しておくから\nお仕事終わったら食べましょう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1246010, 124631, 0, 0.0, 1, "新年に故郷を思いだすのはきっと\n子どもの頃が楽しかったからよね\nこの先…あなたが思い出す新年が\nこのお雑煮の味ならうれしいわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1246011, 124601, 0, 8.2, 2, "お誕生日のお祝いに今日は一日\n先生のお膝、貸してあげますね♪\nふふふ、よしよしいい子〜♪\n着物がすべすべで気持ちいい？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1246012, 124631, 0, 0.0, 1, "お誕生日おめでとう♪\n先生、ちょっと趣向を凝らして\n誕生日雑煮を作ってみたの！\nあなたの好物、てんこ盛りよ～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1247001, 124701, 0, 4.7, 5, "明けましておめでとう\nさぁ、横になってちょうだい\n初実験体は絶対君にしようって\n決めてたんだから♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1247002, 124701, 0, 2.7, 2, "どんな初夢を見たかって？\nそれはもちろん、君に\nあんな実験やこんな治療を試す\n夢に決まってるじゃない。ふふ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1247003, 124701, 0, 5.1, 2, "はい、お屠蘇をどうぞ　\nもちろんただのジュースよ？\n…ほんのちょっぴり\n新しいお薬は入っているけど♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1247004, 124701, 0, 10.3, 5, "着てみたはいいものの、この格好\nやっぱり診察がしにくいわね\n…え？　でも、すごくきれい？　\nお、大人をからかわないの…！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1247005, 124701, 0, 5.8, 1, "せっかく君と一緒のお正月なのに\n全然ゆっくりできなくて残念\n残業・休日診療いたしません！\nなんて言ってみたいものだわ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1247006, 124731, 0, 0.0, 1, "ほんのり赤くなってる？\nいやだ、見ないで恥ずかしいわ\n男の人に裸足を見せたのなんて\nこれが初めてだもの…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1247007, 124731, 0, 0.0, 1, "こうして同じ足湯に浸かるだけで\n元気がもらえるだなんて…\n君の効能ってすごいのね\n分析したいわ。触れてもいい…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1247008, 124731, 0, 0.0, 1, "足湯って体にいいのよ\n心臓に負担もかからないし\n…にしては動悸がする？\nあら…それはイケない病かもね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1247009, 124731, 0, 0.0, 1, "みんな初日の出は必死で見るのに\n夜は空を見ようともしないのね\nそれでもほら、ちゃんと昇ってる\nお正月の月っていじらしいわね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1247010, 124731, 0, 0.0, 1, "ねぇ、月が見えないわ\nんー、そこだと景色が見えない\nだからそっちじゃなくて…もうっ\nそばに来なさいって言ってるのよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1247011, 124701, 0, 2.2, 1, "お誕生日おめでとう\n君の存在は私にとって\nまさに百薬の長\n今年も側に常備させてね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1247012, 124731, 0, 0.0, 1, "盆と正月がいっぺんにきたって\nこのおめでたさには敵わないわ\nさ、新薬で乾杯したら…\nバースデー治験を始めるわよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1248001, 124801, 0, 2.8, 1, "アイドルの新衣装おひろめ！\n…ってわけじゃないけど\n新しい私の姿を見てもらえるのは\nうれしいな。それで…感想は？", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1248002, 124801, 0, 7.4, 2, "みんなを楽園から解放する力…\nそれが形になったのが\nこの姿なんだよね\n開放的なデザインも納得だよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1248003, 124801, 0, 11.0, 6, "楽園の影響を受けた人たち…\nみんな笑顔だったよね。なのに…\n見てると悲しい気持ちになるの\n…キミも、私と同じみたいだね", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1248004, 124801, 0, 7.0, 7, "キミには守ってもらってばっかり\nだから…今度は私がキミを守るよ\nでも、応援はよろしくね♪\nもちろん最前列じゃないとダメ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1248005, 124801, 0, 5.3, 6, "キミもおそろいの格好になる？\nなーんて…え、なってもいい？\n気持ちはうれしいけど…ダメ！\nセクシーすぎるもん、私がＮＧ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1248006, 124831, 0, 0.0, 1, "「これまで」だって素敵な思い出\n…だから、もっと増やしたい！\nさあ、一緒に行こう！\n「これから」を迎えに！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1248007, 124831, 0, 0.0, 1, "アイドルは強くてかわいい！\nそれが当たり前！　つまり？\n未来のトップアイドルなら最高に\n強くてかわいいのも当たり前っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1248008, 124831, 0, 0.0, 1, "この輝きは未来の輝き！\n私とキミと、みんなを待ってる\nまだ見たことのない世界の光！\nね、こわくなんかないでしょ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1248009, 124831, 0, 0.0, 1, "キミがいつも通りでいてくれる…\nそれだけで私は前を向けるんだ\nたとえどんなに暗い道でも─\n堂々と胸を張って！　笑顔で！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1248010, 124831, 0, 0.0, 1, "前から好きだった自分の名前…\nもっともっと大好きになったよ！\n私はノゾミ─希望の名前！\nさあ、キミも呼んで！　せーの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1248011, 124801, 0, 4.4, 1, "ハッピーバースデー！\n今日はキミが主役の日だよ！\nいつも応援してもらってる分\n思いっきり目立たせてあげるね♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1248012, 124831, 0, 0.0, 1, "キミが生まれてきてくれたから\n私たちは出会えたんだよ\nこの奇跡に、私は感謝してるんだ\n心を込めて…誕生日おめでとう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1249001, 124901, 0, 5.4, 2, "たとえ未来が茨の道でも\n道があるなら歩けるだろ\nなにも迷うことはない\n突き進もうぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1249002, 124901, 0, 0.0, 1, "今がダサくて惨めだろうが\n立ち止まっちまうよりはマシだ\nそういう泥くさい生き方だって\nオレは嫌いじゃないぜ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1249003, 124901, 0, 4.3, 1, "未来はいつだって\nひとっ走りで行ける距離にある\n自分の気持ちに\n正直でいさえすればな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1249004, 124901, 0, 4.8, 1, "思えばずいぶん遠くまで\n来たもんだよな…\nキミといると、時間の流れが\nやたらと早く感じるぜ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1249005, 124901, 0, 8.1, 1, "オレにも大切なもんがたくさん\nできた。イノリとボスと\nファミリーの連中…\nもちろんキミもその一人だ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1249006, 124931, 0, 0.0, 1, "たぎる！　たぎるぜ！\nどんな過去も置き去りにする力！\nこの拳にみなぎってくる！\nさあ、ケンカだケンカぁ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1249007, 124931, 0, 0.0, 1, "ぐずぐず立ち止まってんなよ\nたった一歩でも踏み出せば…\nその先に未来の景色が見える\n踏み込んでこいよ、ここまで！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1249008, 124931, 0, 0.0, 1, "オレ様の未来とテメェの未来\nどっちが強いか、未来バトルと\nいこうじゃねえか！\n負けたほうが飯おごりだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1249009, 124931, 0, 0.0, 1, "キミ、遅れるなよ！\n今のオレ様の拳は\nランドソル最速だぜ！\n未来に向かって一直線だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1249010, 124931, 0, 0.0, 1, "どんな苦難が待っていようが\nオレはもう立ち止まらない\nダチも仲間も、自分自身も！\nまとめて未来へ連れてくぜ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1249011, 124901, 0, 2.1, 2, "誕生日、おめでとな！\nへへっ、ケーキ食ったら\nアジトまで遊びに来いよ\nファミリー総出で祝ってやるぜ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1249012, 124931, 0, 0.0, 1, "ハッピーバースデー、ってな！\n今日は一日、遊び倒そうぜ\n夜が明けるまで\n付き合ってやるからさ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1250001, 125001, 0, 4.9, 1, "はは、なんだか分かんないけど\nすっごい力を感じるぞ！\nこいつでオマエを守ってやる！\n明日も明後日もその先もな！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1250002, 125001, 0, 3.5, 2, "たまには思い出を\n懐かしむのもいいと思う…\nでもその後はちゃんと前を向いて\n未来へ進まなきゃ駄目だよな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1250003, 125001, 0, 0.0, 2, "進むべき道に迷った時は\nアタシを見ろ！\nアタシがいればオマエの未来は\nピッカピカに明るいぞ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1250004, 125001, 0, 5.5, 1, "思い出ってのは未来に進むのを\n邪魔する時もあるんだよな…でも\nオマエとは進む力になるような\n思い出を作っていきたいぞ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1250005, 125001, 0, 1.6, 2, "アタシの望む未来…絶対に\n楽しいに決まってるよな！\nもちろんオマエもそこにいろよ\n約束だからな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1250006, 125031, 0, 0.0, 1, "はーっはっは！　大悪党コンビ\nここにふっかーつ！\n『この感じ、懐かしいね\n柄にもなく心が踊るよ』", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1250007, 125031, 0, 0.0, 1, "『ノウェム、連携が乱れてるよ\nもしかして腕落ちた？』\nああ？　それはこっちの台詞だ！\n久々にオマエの本気見せてみろ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1250008, 125031, 0, 0.0, 1, "アタシは一人でも最強だけど\nオクトーとなら超最強だ！\nやっぱりアタシたちは\n二人でひとつってことだ、相棒！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1250009, 125031, 0, 0.0, 1, "『ノウェムの隣って妙に落ち\n着くんだよね。言わないけどさ』\nどうしたオクトー？　何か用か？\n『べっつに～』", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1250010, 125031, 0, 0.0, 1, "アタシたち３人って\n昔は敵だったよな…でも、今は\nこうやって一緒に戦ってる！\n未来って面白いな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1250011, 125001, 0, 1.8, 2, "誕生日おめでとう！\n今日は一日アタシが\n一緒にいてやるよ！\nうれしいだろ？　うれしいよな！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1250012, 125031, 0, 0.0, 1, "オマエに誕生日プレゼントを\n用意したぞ！　何かはまだ内緒だ\n悪党ってのは色々秘密がある\nものだからな！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1251001, 125101, 0, 9.0, 2, "今日もお疲れさまにゃ！\n休憩に温かいコーヒー飲むにゃ？\nお代は従業員割引で200ルピ！\n福利厚生バッチリ完備にゃ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1251002, 125101, 0, 4.4, 2, "コーヒーとたい焼きのあんこが\n織りなすハーモニー…！\nにゃはは♪　相性バツグンにゃ！\nこのセットは爆売れ必至だにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1251003, 125101, 0, 11.9, 1, "猫従業員のシフト組んでるにゃ…\nこの子は連勤だから休ませて…\n…ねえキミ、ヘルプ頼めるにゃ？\n猫みたいにかわいく接客してにゃ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1251004, 125101, 0, 3.5, 2, "ん？　目が冴えて眠れない？\nにゃはは、コーヒー飲みすぎにゃ\nじゃあ眠くなるまでお姉さんが\n頭ポンポンしてあげるにゃ～♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1251005, 125101, 0, 3.6, 5, "猫とコーヒーに癒されるにゃ～\nでもあたしにとって最大の癒しは\nいっつもそばで微笑んでくれる\nキミの存在なんだにゃ～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1251006, 125131, 0, 0.0, 1, "はい、猫専用マタタビミルク♪\nキミにはキミ専用特製コーヒー♪\nなにが特製かって？　にゃはは！\n愛情をいっぱい入れてるのにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1251007, 125131, 0, 0.0, 1, "気持ちのいい天気だにゃ～♪\nこんな日はテラスで休憩にゃ！\n太陽の光とコーヒーの香りで\nいっぱい癒されてほしいのにゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1251008, 125131, 0, 0.0, 1, "キミが猫だったらにゃ～\nそしたらずっと一緒にいて\nなでなでしたり、すりすりしたり\nずっと愛でてられるのににゃ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1251009, 125131, 0, 0.0, 1, "キミが優しくしてくれるから\nすっかり懐いちゃったにゃ～♪\nにゃ！？　違…猫の話にゃ！\nあたしのことじゃないにゃ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1251010, 125131, 0, 0.0, 1, "オシャレな外観のカフェで\nキミと働けてすごく楽しいにゃ♪\nこのままずっと二人で一緒に…\nにゃはは…なんてにゃ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1251011, 125101, 0, 8.6, 2, "今日はカフェを貸し切りにして\nキミの誕生日パーティーにゃ♪\nじゃあ猫と一緒に歌うのにゃ…\nハッピバ～スデ～にゃ～にゃ～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1251012, 125131, 0, 0.0, 1, "誕生日おめでとにゃ～！\nお祝いにキミの顔のラテアートを\n作ったんだけど、上手くできて\n飲むのがもったいないのにゃ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1252001, 125201, 0, 5.1, 6, "…にゃんだおまえ\nおやつがほしいにゃ～？\nって、き、貴公っ！\nいつから聞いていたのだ！？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1252002, 125201, 0, 4.4, 2, "コーヒーだけではなく\n一緒にお菓子はどうだ？\nこの間仕入れたチョコレートが\nおすすめだ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1252003, 125201, 0, 5.1, 1, "お菓子好きなお客さんのために\n新作の仕入れは欠かせないな\nコーヒーもそうだが、お菓子も\n奥が深くて面白いのだぞ", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1252004, 125201, 0, 8.0, 2, "いらっしゃい…って、貴公か\nその顔、もしやなにか相談事か？\nよし、コーヒーでも淹れてやるから\n私に話してみないか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1252005, 125201, 0, 1.5, 6, "もぐ…なんだ？\nし、新作の味見をしていたのだ！\n貴公も気になるのなら\n一緒に味見してみるか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1252006, 125231, 0, 0.0, 1, "ほら、コーヒーを淹れたから\n貴公も少し休憩するといい\n疲れが溜まっているようなら\nパンケーキもつけてやろう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1252007, 125231, 0, 0.0, 1, "店に立つ姿もなかなか\n様になってきたと思わないか？\nふふっ、貴公の給仕服も\n良く似合っているぞ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1252008, 125231, 0, 0.0, 1, "ふふっ♪\n猫たちが気持ちよさそうに\n日向ぼっこしているぞ\n…正直、うらやましいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1252009, 125231, 0, 0.0, 1, "ぬあっ！？\nき、貴公、そのコーヒーは\n私が飲んでいたものでは…\nいや、なんでもないっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1252010, 125231, 0, 0.0, 1, "貴公のためにコーヒーを淹れたぞ\n…私にも淹れてくれるのかっ？\nふふっ、では一緒にテラスで\n語らおうではないか♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1252011, 125201, 0, 6.0, 1, "今日は貴公の誕生日だからな\n好きなお菓子を選ぶといい！\n…私が淹れたコーヒーがいい？\n相変わらず欲がないな、貴公は", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1252012, 125231, 0, 0.0, 1, "誕生日おめでとう！\n貴公のために特別なパンケーキを\n用意したから一緒に食べよう♪\n味見をしたが絶品だったぞ？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1253001, 125301, 0, 0.0, 2, "この服はいかがでしょうか？\n大自然の中でもしっかりと\nお勤めを果たすため、いろいろ\nこだわった逸品でございます", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1253002, 125301, 0, 5.5, 2, "わんわんの主さまも\n最近は芸が達者になったのですよ\nお手、お座り、お回り…\nふふっ、よくできました", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1253003, 125301, 0, 7.4, 2, "自然は分け隔てなく\nそこに生きる者全てに恵みを\nもたらしてくれます\n大切にしたいですね、主さま", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1253004, 125301, 0, 0.0, 2, "主さま、わたくしと\n森でお散歩をしませんか？\nたまには街の喧騒を離れ、二人で\n静かに過ごすのもよろしいかと…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1253005, 125301, 0, 3.2, 2, "森を守るお仕事は大変です\nしかし主さまたちと一緒なら\nやり遂げられると信じております\n今までの冒険と同じように", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1253006, 125331, 0, 0.0, 1, "森の中で、主さまと\n穏やかなひとときを過ごす…\nわたくしにとって\nこれに勝る幸せはございません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1253007, 125331, 0, 0.0, 1, "わんわんのほうの主さまも\n森の中でのんびりできて\n大満足のようでございます\nお腹をなでなでしてあげましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1253008, 125331, 0, 0.0, 1, "森の中はとても静かで\nわたくしどもしかいないような\n気分になってまいりますね\n和やかな心地がいたします", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1253009, 125331, 0, 0.0, 1, "ずっとずっと、こうして主さまと\n過ごしていたいです…\nいずれお勤めに戻らなくては\nなりませんが、それでも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1253010, 125331, 0, 0.0, 1, "主さま、もう少し傍へ\n寄ってもよろしいでしょうか…？\n寒気がするので、主さまたちの\n温もりをより感じたいのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1253011, 125301, 0, 0.0, 2, "本日はお誕生日\nおめでとうございます、主さま\n人も自然も、このように時を経て\n成長していくのですね♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1253012, 125331, 0, 0.0, 1, "主さま、誕生日を迎えられたこと\n心からお祝い申し上げます\n精霊や自然も、きっと主さまの\n成長を祝福してくれますよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1254001, 125401, 0, 0.0, 2, "私もレンジャーの仲間入りです\nその…この制服、どうですか？\n少しブカブカですけど…\nいつかピッタリになるといいな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1254002, 125401, 0, 4.8, 2, "レンジャーのみなさんは\n弓矢の扱いが上手な人ばかりです\n私も技術を磨いて、みなさんや\nあなたのお役に立ちたいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1254003, 125401, 0, 0.0, 2, "今日はニャットちゃんを洗って…\n…え？　お世話してえらい？\nふふ、私はニャットちゃんの\nママ代わりですからね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1254004, 125401, 0, 8.1, 2, "魔物は怖い生き物。でも…\nレンジャーになってから\n色んなことを考えています\nこれが学ぶということなんですね", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1254005, 125401, 0, 0.0, 2, "見てください\nちょっと筋肉がついてきたんです\nこのままムキムキになれたら…\n私があなたのこと、守れますね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1254006, 125431, 0, 0.0, 1, "レンジャーの権限において\nターゲットを拘束します\nニャットちゃん、\n私のフォローをお願い！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1254007, 125431, 0, 0.0, 1, "私の世界と\nニャットちゃんの世界は違う\nでも同じ場所で生きているから…\nこの『居場所』を一緒に守ろう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1254008, 125431, 0, 0.0, 1, "人と魔物…\n共生の道は険しいけれど\nできないわけじゃない\n私たちが、その証明になろう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1254009, 125431, 0, 0.0, 1, "ここはニャットちゃんと私で\n対処します\n私たちが成長した姿、\nあなたも見守っててください！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1254010, 125431, 0, 0.0, 1, "弱肉強食は真実だと思うけれど、\nそれが全てなわけじゃない…\n私も誰かを助けて助けられながら\nあなたの隣を歩いていきたいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1254011, 125401, 0, 2.9, 2, "少し、時間ありますか？\nレンジャーの仲間たちと一緒に\nケーキを用意したんです\nその…あなたの誕生日なので…", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1254012, 125431, 0, 0.0, 1, "お誕生日おめでとうございます\nそれと、ニャットちゃんからも…\n『いつもありがとにゃん♪』\n…な、なんて", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1255001, 125501, 0, 8.0, 2, "剣に拳、そして魔法…\n気付けばいろんな戦い方が\nできるようになっちゃった\nどんどん私を頼っていいからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1255002, 125501, 0, 4.8, 2, "魔法使いって結構\nいろんな依頼で重宝されるんだ！\nおかげで騎空艇のための貯金が\nいいペースで貯まってるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1255003, 125501, 0, 5.0, 1, "陰キャとか陽キャとか\nよく分からないけど…\nどっちのビカラちゃんも大好き！\nあなたもそうだよね？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1255004, 125501, 0, 7.3, 1, "あなたやビカラちゃんたちと\n空を思いっきり冒険できたら\nきっと楽しいだろうなぁ\n…よし、騎空団活動頑張ろっと！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1255005, 125501, 0, 7.1, 5, "ジョブチェンジしたら衣装も\n変わるけど…あなたはどの服が\n好きかな？　…全部好き？\nな、なんだか少し照れるかも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1255006, 125531, 0, 0.0, 1, "あなたがそばにいてくれたら\n誰が相手でも負ける気がしない！\n見てて！　この新たな力で\n私たちの道を切り開いてみせる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1255007, 125531, 0, 0.0, 1, "細かいコントロールは苦手で…\nやりすぎちゃったらゴメンね！\n思いっきり魔力を集中させて…！\nいくよ！　エーテルブラスト！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1255008, 125531, 0, 0.0, 1, "え、かわいいポーズだねって…？\n実は私もお気に入りなんだ！\n魔法詠唱に必要な動作でね\nこうしたら集中できるの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1255009, 125531, 0, 0.0, 1, "あなたとの冒険はいつだって\nワクワクがいっぱい！\nさあ、次はどこへ行こっか！\nその道は絶対空に続いてるから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1255010, 125531, 0, 0.0, 1, "魔法は便利で強力な力だけど…\n私の一番の武器は団員との絆！\nこれからもあなたのこと\n頼りにさせてもらうからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1255011, 125501, 0, 10.1, 5, "実は、ケーキを用意したんだ！\n魔法を使うのはズルい気がして\n手作りに挑戦したんだけど…\n気に入ってくれるといいな", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1255012, 125531, 0, 0.0, 1, "団員の誕生日だもん\n思いっきり祝わなくっちゃ！\n見てて、エーテルブラストを\n花火代わりに打ち上げるから！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1256001, 125601, 0, 3.6, 2, "遠路はるばるランドソルに来て\n正解だったよ！\nまさにラッキー！　クッキー！\nビッキィ！　だね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1256002, 125601, 0, 3.5, 1, "ランドソルの街並はどこも\n見事だね！　しっかりと目に\n焼き付けてパレードの山車の\n参考にさせてもらうよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1256003, 125601, 0, 4.1, 2, "この街で一番活気があるのは\nどこかな？　ネズ耳を売るなら\nそういうところが\nうってつけだからね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1256004, 125601, 0, 4.5, 1, "やぁ、いらっしゃい！　ネズ耳を\n売るのを手伝ってくれるかい？\nきみと一緒なら\nたくさん売れる気がするんだ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1256005, 125601, 0, 0.0, 2, "きみの運勢は大吉だよ！\nなんたって鼠神宮の巫女が\nそばにいるんだからね！\nこれって最高のご利益だろう？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1256006, 125631, 0, 0.0, 1, "みんな集まれー！\nぼくと一緒に楽しく踊ろうよ！\nイヤなことも辛いことも全部\nぼくが忘れさせてあげるからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1256007, 125631, 0, 0.0, 1, "ぼくが笑顔でいれば\nつられてみんなも笑顔になる\nだからぼくはいつも\n笑っていたいんだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1256008, 125631, 0, 0.0, 1, "どんな人でも手を繋げば\nもう友達さ！　街中の人と\n仲良くなってみせるよ！\nさぁ、ランドソルでぼくと握手！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1256009, 125631, 0, 0.0, 1, "きみが笑顔でいると、不思議と\nぼくまで幸せな気持ちになるんだ\nだからきみのこと\nずっと笑わせてあげるからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1256010, 125631, 0, 0.0, 1, "もし元気が出ない時があれば\n遠慮なくぼくを呼んでほしいな！\nどれだけ離れていたとしても\n駆けつけて笑顔にしてみせるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1256011, 125601, 0, 3.2, 2, "きみ、今日誕生日なんだって？\nそういうことなら早速お祝いだ！\nハッピー！　バースデー！\nトゥーユー！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1256012, 125631, 0, 0.0, 1, "誕生日おめでとう！　今日一日\nきみのお祝いをしてあげるよ！\nビッキィを独り占めできるなんて\nきみは幸せ者だね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1257001, 125701, 0, 4.6, 2, "錬金術は、戦闘のサポートに\n長けた学問ですね\n私の性格に合っていますし\n冒険の手助けは任せてください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1257002, 125701, 0, 6.6, 2, "実際に冒険に出ることで\nサポート業務に何が必要かを\nしっかり学ばせていただきます\nさあ、冒険にでかけましょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1257003, 125701, 0, 8.1, 2, "現場でのサポートだけでなく…\nもちろん、ギルド管理協会として\n事前のサポートも行いますよ\n冒険のお手伝いは任せてください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1257004, 125701, 0, 4.6, 1, "あなたと一緒なら\nどんな危険な場所でも\n不安はありません\n背中はお任せしますね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1257005, 125701, 0, 8.2, 2, "じー…はっ、すみません！\n次はいつ冒険に行かれるのか\n気になってしまい、つい…\n私はいつでも大丈夫です！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1257006, 125731, 0, 0.0, 1, "さあ、調合の時間です！\n素敵なお薬でみなさんを\nサポートいたしますね\n私にお任せください♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1257007, 125731, 0, 0.0, 1, "ふむふむ…この本によると\nそんな効果まで…？\n…あっ、独り言聞こえてました？\nお、お恥ずかしいです…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1257008, 125731, 0, 0.0, 1, "このお薬の材料ですか？\nそれはですね…\nふふ、内緒です♪\n聞かないほうがいいと思いますよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1257009, 125731, 0, 0.0, 1, "あなたの冒険のスタイルに合う\nお薬を作っているところなんです\nどんなものが役立ちそうか\nあなたのことなら研究済みです♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1257010, 125731, 0, 0.0, 1, "私の錬金術が、あなたの冒険に\n少しでも役に立つように…\nそう思って心を込めて作成中です\nこれが私なりの戦い方ですから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1257011, 125701, 0, 5.9, 1, "お誕生日おめでとうございます！\nお祝いに宝の地図をどうぞ\n私の錬金術による\n冒険のサポート付きですよ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1257012, 125731, 0, 0.0, 1, "今日はあなたのお誕生日ですね\nプレゼントは少々お待ちください\n今作っているところですので…\nえ？　危なくなんてないですよ？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1258001, 125801, 0, 5.1, 2, "まだまだ冒険者としては\n知らぬことばかりの身\nこれから近くで、冒険のいろはを\n学ばせてくださいませ。兄様", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1258002, 125801, 0, 5.6, 5, "この姿に変身すること自体は\n造作もないことなのですが\n姿が変わる最中を見られるのは\n…す、少し恥ずかしいです", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1258003, 125801, 0, 7.2, 6, "兄様はこの姿のわたくしにも\n普段通り接してくださいますね\n…これはこれでかわいい？\nそ、そのようなご冗談は…！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1258004, 125801, 0, 5.1, 2, "『兄様』と呼ぶのが、まだ少し\n恥ずかしいようで。呼ぶたびに…\nドキドキ、してしまいます…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1258005, 125801, 0, 0.0, 2, "兄様はいつも優しくて、強くて、\n勇敢で、たくましくて…\nそれにその…すごく\nかっこいいおかた…です♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1258006, 125831, 0, 0.0, 1, "わたくしの力は忌むべきもの\nそれでも頼らねばならぬのは\nわたくしが未熟者ゆえ…\nだから必ず、強くなります…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1258007, 125831, 0, 0.0, 1, "他者に操られていたとはいえ\n罪を重ねたのは事実…\nそれを止めてくださった兄様には\n感謝してもしきれません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1258008, 125831, 0, 0.0, 1, "一人前の冒険者になれば\n世のため人のために働けます！\nそうやって一つずつ、世界への\n償いをしていきたいのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1258009, 125831, 0, 0.0, 1, "わたくしの過ちを知っても\n受け入れてくださったこと\nとてもうれしく思っております…\nわたくしは果報者ですね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1258010, 125831, 0, 0.0, 1, "無知なわたくしに、もっと\nたくさんのことを教えてください\n冒険のこと、戦いのこと、\nそれに…兄様のこと…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1258011, 125801, 0, 0.0, 2, "お誕生日、おめでとうございます\n兄様のためにご馳走をと\n腕を振るいましたのでどうぞ！\n…上手くできたところだけ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1258012, 125831, 0, 0.0, 1, "ご生誕のお祝いとしては少々\n足りませんが、どうかお部屋に\n花を生けさせてくださいませ！\nうーん…兄様に似合う花は…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1260001, 126001, 0, 6.1, 5, "あまりにお兄さんが普通なので\nこの姿なこと忘れちゃいます…♪\nこれでも本当は、ちょっと\n恥ずかしかったりするんですよ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1260002, 126001, 0, 0.0, 2, "まだまだ未熟なわたしですけど、\nお兄さんが側にいると安心です♪\n魔物との戦闘の時はもちろん…\nそれ以外も…なんて…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1260003, 126001, 0, 6.3, 4, "聖女って呼ばれるの苦手なので、\nこの姿のままいればいいかもです\n代わりに、悪魔…とか呼ばれて\nしまいそうですけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1260004, 126001, 0, 0.0, 2, "少し落ち込んじゃった時も\nお兄さんの優しい笑顔と言葉で\nわたしの心はすーって癒される…\nお兄さんはわたしの救急箱です♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1260005, 126001, 0, 6.4, 5, "お兄さんの前では普通の女の子で\nいられるから居心地いいです…♪\nだからお兄さん、少しの間だけ…\n二人っきりでいていいですか…？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1260006, 126031, 0, 0.0, 1, "お兄さんに誓わせてください\nこの力を正しく使えるように…\n堂々と前を向けるように…\nわたしは強くなります…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1260007, 126031, 0, 0.0, 1, "朽ち果てた天使の輪に漆黒の翼…\nふふ、まるで堕天使ですね…\nこの姿…好きではないですけど\n罪を忘れずにいられる気がして", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1260008, 126031, 0, 0.0, 1, "この姿の自分も受け入れられる…\nたとえ恐れられてしまっても\n誰かを守る盾となれるのなら…\nそれがわたしの贖罪です…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1260009, 126031, 0, 0.0, 1, "冒険中どんな強敵に出くわしても\nお兄さんに傷はつけさせません\nだって、わたしが守りますから…\nこの身に代えても…全力で…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1260010, 126031, 0, 0.0, 1, "どんなわたしでも受け入れてくれる\nお兄さんのことが大好きです\n…今のは妹としての言葉じゃ…\nいえ…なんでもありません♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1260011, 126001, 0, 0.0, 2, "今日はお兄さんの誕生日ですね！\nリリちゃんとプレシアちゃんと\n一生懸命ケーキ作ったんです…♪\nみんなでお祝いさせてください♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1260012, 126031, 0, 0.0, 1, "お誕生日おめでとうございます\n実は…アルバイトで貯めたお金で\nプレゼント買ったんです…\n受け取ってください…お兄さん♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1261001, 126101, 0, 4.7, 1, "くろい、はね\nわるいこ、ぽい？\n…かも", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1261002, 126101, 0, 7.8, 2, "ごちそうの、けはい\nくんくん…ちかい…\n…はっ\nお兄…だった", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1261003, 126101, 0, 3.4, 2, "ぱとろーる、いこ？\nあ\nそのまえに\nごはん。ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1261004, 126101, 0, 3.0, 2, "お兄\nて、よぶと\nはっぴーな、きもち\n…お兄…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1261005, 126101, 0, 8.5, 5, "お兄\nいつも、おいしそう\nぱわーふーど？　かも\n…じゅる…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1261006, 126131, 0, 0.0, 1, "くろい、ちから。も\nわたしの、ちから\nつよくなる。よ\nみてて。お兄", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1261007, 126131, 0, 0.0, 1, "やっと。なかま、なれた\nいっしょに、たたかう。よ\n…だから。お兄\nどこにも、いかないで。ね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1261008, 126131, 0, 0.0, 1, "しんまい、ぼうけんしゃ\nできない、こと\nしらない、こと、たくさん\nおしえて、ね？　お兄", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1261009, 126131, 0, 0.0, 1, "もっと、おおきく…\nもっと、つよく…\nお兄、まもれる、くらい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1261010, 126131, 0, 0.0, 1, "ぽかぽか、もちもち\nいーにおい\nお兄の、こと\nずっと、はむって、たい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1261011, 126101, 0, 3.3, 1, "たんじょうび、おめでと\n肉まん…どぞ\nじー…\n……ごくり", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1261012, 126131, 0, 0.0, 1, "はっぴーばーすでー\nきのうより、おとな？\nじゅくした、お兄\nあじみ。するね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1262001, 126201, 0, 4.5, 2, "眷属よ、艶やかな装いじゃろう？\nほれ、思いつく限り\n気の利いた文句を並べてみよ\nわらわが聞いてやろう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1262002, 126201, 0, 5.9, 2, "なりゆきで手に入った装束じゃが\nわらわは気に入っておる\n夜を統べる者にふさわしい\n意匠じゃからの♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1262003, 126201, 0, 10.2, 1, "この晴れ着を披露するのに\nうってつけの舞台はないかの？\nなに、今から魔物の討伐とな？\nまあそれでもよかろう…ゆくぞ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1262004, 126201, 0, 4.9, 1, "眷属よ、どこか大きな会場を\nおさえよ。お主とわらわの\n威風堂々たる姿を\n世界に知らしめてやるのじゃ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1262005, 126201, 0, 7.1, 2, "わらわの艶姿を隣で楽しめるのは\nお主だけの特権じゃぞ\n特別な眷属であることを\n誇りに思うがよい♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1262006, 126231, 0, 0.0, 1, "眷属よ、礼を言うぞ\nこの装束を纏うたび、わらわは\n今宵の景色を思い出すじゃろう…\n湖上の月と…お主の笑顔を", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1262007, 126231, 0, 0.0, 1, "きれいな湖じゃのう…\nじゃが、油断は禁物じゃ\nお主は泳ぎが苦手であろう？\nわらわの傍を離れるでないぞ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1262008, 126231, 0, 0.0, 1, "眷属よ、お主は夜が好きか？\nもし一日の終わりをさみしく\n思うことがあれば、わらわが\n一緒にいてやらんこともないぞ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1262009, 126231, 0, 0.0, 1, "まったく、お主は大胆じゃのう…\n裳裾が濡れてしまったではないか\n風邪を引いてはならん…\n近う寄れ…く、くっつくのじゃ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1262010, 126231, 0, 0.0, 1, "水面に映る、今宵の月のように\nわらわはきれいなものとして…\nお主の瞳に映っておるか？\nこ、言葉にして、教えるのじゃ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1262011, 126201, 0, 4.1, 2, "ハッピーバースデー、眷属よ\n今日が最良の日となるように\nわらわが全てを尽くして\nお主を祝福してやろう！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1262012, 126231, 0, 0.0, 1, "今日はお主の誕生日じゃ\n遠慮せず欲しいものを述べてみよ\nいつも隣で支えてくれるお主に\n感謝の気持ちを示したいのじゃ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1263001, 126301, 0, 9.3, 2, "人の心を惑わすほどの美を持ち、\nその美を磨くことを怠らない…\nそんなボクがどこまで行きつくか\nちゃんと期待しててよね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1263002, 126301, 0, 6.2, 2, "吸血鬼の力の影響かな…\n時々、無性に飲みたくなるんだ…\nあの真っ赤なトマトジュースを！\n差し入れ、いつでも歓迎するよ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1263003, 126301, 0, 4.9, 2, "魔性の雰囲気をたたえた\nこの麗しきドレス…！\n人の身とは思えぬ美しさの\nボクにピッタリだよね♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1263004, 126301, 0, 5.4, 2, "このドレス着てると、凄い力が\n勝手に出ちゃって不便なんだよね\nまあ、全然オッケーだけど！\n美は全てに優先されるからね♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1263005, 126301, 0, 6.2, 5, "遠慮せずにもっと近付きなよ？\n体温すら感じるくらいにさ\nそれくらいの距離じゃないと\n見えない美があるからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1263006, 126331, 0, 0.0, 1, "ボクほどの美しさになれば\n魔物すらかしずくのさ♪\nま、それも当然だよね\nボクは美を統べる者なんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1263007, 126331, 0, 0.0, 1, "ボクという存在を後世に残すため\n絵画を描いてもらおうと思ってね\n有名な画家に依頼したから\nサイフの中身は空っぽさ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1263008, 126331, 0, 0.0, 1, "ああ、ボクの周りのこの枠かい？\n美を統べる者としての圧倒的な\nかわいい力で具現化したものさ！\nどうだい、すごいでしょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1263009, 126331, 0, 0.0, 1, "吸血鬼っていろいろな姿に\n変身できるみたいだけど…\nボク以上に美しいものには\nさすがになれないだろうね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1263010, 126331, 0, 0.0, 1, "ボクという美を見ること\nそれ以上の幸福なんて、\n存在するわけないだろう？\n思う存分、目に焼きつけて？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1263011, 126301, 0, 3.9, 1, "誕生日だって？　めでたいね♪\nそれはそうと、話があるんだ\n実は欲しい化粧品があってね\nボクにプレゼントする気はない？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1263012, 126331, 0, 0.0, 1, "ハッピーバースデー！\n今日という日の記念に、\nボクを撮影する権利をあげるよ！\nさぁ、思う存分、ボクを撮って！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1264001, 126401, 0, 2.8, 2, "こう見えてもわたし、\nシュヴァリエなんですっ！\n『王家の装備』を\nお守りしてるんですよっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1264002, 126401, 0, 0.0, 1, "ライラエルさまは\nとってもとっても優しいんですっ\nいつも笑顔でわたしの話を\n聞いてくださるんですよ～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1264003, 126401, 0, 4.0, 2, "ほんとはハグとか握手とか\nいっぱいしたいんですけどねっ？\nわたしのパワーってちょっと\nすごすぎちゃって！　我慢です～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1264004, 126401, 0, 5.3, 2, "わたしを充填できる人が\nもう一人いるなんて、ぜーんぜん\n知りませんでしたっ！\nナイトさんってすごいんですね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1264005, 126401, 0, 0.0, 2, "ナイトさんのこと、\nもっともっと教えてくださいっ！\nわたし、あなたに\n興味津々みたいなんですっ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1264006, 126431, 0, 0.0, 1, "戦うことはあんまり楽しくない\nですけど…悪い魔物さんを\n放置なんて絶対できませんっ！\nクローチェ、戦闘開始しますっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1264007, 126431, 0, 0.0, 1, "魔物さんの好き放題には\nさせませんよっ！\nこの国とみなさんを守るのも\nわたしの役目ですからっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1264008, 126431, 0, 0.0, 1, "どこに逃げてもロックオン！\nわたしの両手は甘くないです！\nひっさつ！\nダブルロケットぱーんちっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1264009, 126431, 0, 0.0, 1, "わたしからハグとかすると\n危ないので…なのでっ！\nナイトさんからしてもらえたり\nしないかな～って！　えへへっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1264010, 126431, 0, 0.0, 1, "充填してくれたときにもらった\nナイトさんのあったかさ…\nわたしのタンクにまだ残ってます\nだから…わたしは無敵ですっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1264011, 126401, 0, 0.0, 2, "今日はナイトさんのお誕生日っ♪\nいっぱいいっぱいおめでとうって\n言わせてくださいねっ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1264012, 126431, 0, 0.0, 1, "お誕生日おめでとうございます！\n今日はどんなお願いだって\nフルパワーで叶えちゃいますっ！\nさあさあ、なんなりとっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1265001, 126501, 0, 4.0, 2, "『ジオ・テオゴニア』を\nわたしは愛しています\nあなたにも気に入って\nいただけると…うれしく思います", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1265002, 126501, 0, 6.7, 1, "頼りになる家臣たちがいなければ\nわたしは国主たり得ません\n…みなの想いに応えるためにも\n迷いがあってはいけませんね", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1265003, 126501, 0, 7.3, 4, "『王家の装備』の力は強大で…\n反動も覚悟しなければなりません\n…傲慢なわたしを見ても\nどうか怖がらないでください", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1265004, 126501, 0, 4.3, 6, "かしこまった服装は\nあまり好みではないのです\nどうにも窮屈に感じてしまって…\nそれほど奇抜ではないですよね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1265005, 126501, 0, 4.0, 2, "これでも、しっかり者だと\n言われているのですよ？\n…あなたには気を抜いたところを\nよく見られてしまっていますが", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1265006, 126531, 0, 0.0, 1, "あなた方は希望の光…\nその光を守るため\nあらゆる困難を退けましょう\n…どうか安心してくださいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1265007, 126531, 0, 0.0, 1, "天使のように見えた…ですか\nそれは羽のせいでしょうか\nあるいは…別の理由が？\nふふ、教えてくださいませんか♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1265008, 126531, 0, 0.0, 1, "偽ることも後ろ暗いこともなく\nあなたと向き合うことができる…\nああ、なんと晴れやかな…\nなんと清々しい心持ちでしょう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1265009, 126531, 0, 0.0, 1, "あなたはためらうことなく\n手を差し伸べてくださいましたね\n…ですから、わたしも\n大丈夫。もう迷いはありません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1265010, 126531, 0, 0.0, 1, "高き天から舞い降りて\n大地の上に立つというのも\n悪くはないかもしれません\n…そう、あなたの隣であれば", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1265011, 126501, 0, 0.0, 2, "お誕生日おめでとうございます\nあなたの未来が\n幸多きものになりますように…\n心から祈っています", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1265012, 126531, 0, 0.0, 1, "今日という日の奇跡のおかげで\nあなたと出会うことができました\nだから…どうか言わせてください\n万感の想いを込めた感謝と祝福を", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1266001, 126601, 0, 7.1, 6, "暑さ対策は万全にすべきです\nこの麦わら帽子を被りなさい\n…私の帽子とお揃いなことに、\n特に深い意味はありませんよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1266002, 126601, 0, 7.2, 1, "秘密とは人にとって大事なもの…\n貴方も秘密を作るといいでしょう\nただし、私にだけはその内容を\n共有すること。いいですね？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1266003, 126601, 0, 7.9, 7, "七冠とは利己的な存在\n当然、私も例外ではありません\nというわけで、貴方には私の夏に\n徹底的に付き合ってもらいますよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1266004, 126601, 0, 12.2, 2, "ふむ…大人らしい水着で、\n私によく似合っている、と…\n褒め方を教えた覚えはないですが\nしかし…悪くありませんね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1266005, 126601, 0, 7.6, 2, "おや、日差しが眩しいのですか？\nでは私のサングラスを使いなさい\n…ふふっ。いえ、失礼\nお似合いですよ、かわいらしくて", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1266006, 126631, 0, 0.0, 1, "私が…いえ、私たちが\n存分にもてなしてあげますよ\n貴方の望む夏を\n実現してあげましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1266007, 126631, 0, 0.0, 1, "…考えてみれば、こうして\nゆっくり過ごす時間など\nいつ以来でしょうか\n…想い出に残る夜にしましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1266008, 126631, 0, 0.0, 1, "今日くらいはすべて忘れて\n羽を伸ばすことにします\n貴方もそうしなさい？\n我々は日々働きすぎなのですから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1266009, 126631, 0, 0.0, 1, "このナイトプールは貸切です\nつまり、ここでの出来事は\nなにもかも我々だけのもの…\n今夜のことは秘密ですよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1266010, 126631, 0, 0.0, 1, "呆れるほどのお人よし…\n貴方を一言で表せばこうも単純\nなのに、私は貴方になれない…\nこの秘密をいつか暴いてみせます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1266011, 126601, 0, 10.2, 2, "虫取り網をプレゼントした程度で\nそこまで喜ぶとは…まあ、\n気に入ったのならいいのですが\n…ハッピーバースデー", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1266012, 126631, 0, 0.0, 1, "夏らしく貴方の誕生日を\n祝わせてもらいましょう。分身に\n腕利きの花火職人を模倣させます\n思う存分、花火を楽しみなさい", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1267001, 126701, 0, 1.8, 2, "夏といえば…いえ春夏秋冬\nいつでもマイシーズンですわ！\n私に似合わない季節など\nありませんもの。そうでしょう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1267002, 126701, 0, 6.5, 1, "きらめく海に輝く太陽…\nそして魅力的な私！\n夏を楽しむのに、これ以上\n必要なものがありまして？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1267003, 126701, 0, 2.6, 2, "あなたさま、夏ですわ！\nウィスタリア家の威信を\nかけたバカンスの始まりを\nここに宣言しますわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1267004, 126701, 0, 3.0, 2, "夏のグルメ、夏のレジャー…\n全て楽しみ切るなら\nいくら時間があっても\n足りませんわね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1267005, 126701, 0, 6.8, 2, "叶うならあなたさまと\nこの時間を永遠に…なんて\n…ふふ、何でもありませんわ\n先へ進んでこそ私たちですもの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1267006, 126731, 0, 0.0, 1, "いつも慌ただしいからこそ\n今だけはあなたさまと…\nこの潮騒と夕焼けのひと時に\n身を委ねていたいのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1267007, 126731, 0, 0.0, 1, "ほら、あなたさまも\nもっと近くに来てくださいな\nふふっ…捕まえましたわ♪\nさ、こちらで横になって…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1267008, 126731, 0, 0.0, 1, "ずっと走り続けていれば\nいつか疲れ切ってしまいますわ\nさざ波の音に耳を傾け\nしばしの休憩といたしましょう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1267009, 126731, 0, 0.0, 1, "夏の美しさに目を奪われるのも\n結構ですが、ちゃんと私も\n見てくださいませんと\n拗ねてしまいますわよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1267010, 126731, 0, 0.0, 1, "太陽はまばゆく燃える\nばかりではありませんわ\n夕焼けのように甘く焼き付く…\nそんな一面もありますのよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1267011, 126701, 0, 9.0, 6, "ハッピーバースデーですわね！\nささやかなお祝いですが、記念に\n打ち上げ花火を用意しましたの\n一万発では少なすぎるかしら？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1267012, 126731, 0, 0.0, 1, "あなたさまの誕生日をこうして\n一緒にお祝いできるなんて…\nこの幸せな時間、私にとっても\n最高の思い出になりますわ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1268001, 126801, 0, 6.1, 1, "ふう、暑いわね…\n熱中症には気を付けないと\n喉がかわいたらすぐに言ってね？\n麦しゅわ以外も用意してるわよ♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1268002, 126801, 0, 5.3, 4, "夏の海辺の仕事なら水着が効率的\nってみんなが言ってたけど\nもしかして…うちのギルドだけの\n常識だったんじゃ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1268003, 126801, 0, 6.7, 6, "聖騎士っぽい水着、って\n注文したんだけど…\nどの辺がそれっぽいのかしら？\n…もしかして、色？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1268004, 126801, 0, 5.4, 1, "キミに見られるなら、\nもう少しおとなしいデザインの\n水着がよかったかも…\nう、ううん…なんでもないわ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1268005, 126801, 0, 5.4, 2, "この水着、どう…かな？\n少しは夏っぽい格好してたほうが\nキミも涼しく感じるかな、なんて\n思ったんだけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1268006, 126831, 0, 0.0, 1, "せっかくの海なんだもの\nタマキさんとミフユさんに\n負けないくらい\n楽しんじゃいましょ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1268007, 126831, 0, 0.0, 1, "暑いときはすっきりした\nセゾン系もいいけど…\n私はボディがしっかりしてる\nアベイ系が好きだわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1268008, 126831, 0, 0.0, 1, "普段忙しければ忙しいほど\n遊べるときにしっかりと遊んで\n日常とメリハリをつけるの\n社会人からのアドバイスよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1268009, 126831, 0, 0.0, 1, "青い海！　白い泡！\nそして、キミ…！\n必要なものが全部あるんだもの\nふふっ、ここはまるで天国ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1268010, 126831, 0, 0.0, 1, "せっかくのバカンスが\nキミと二人きりじゃないのは\nちょっと残念だけど…\nそれはまた今度、ね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1268011, 126801, 0, 2.3, 1, "お誕生日おめでとう♪\n急いで来ちゃったから\nこんな格好でごめんね？\n少しでも早くお祝いしたかったの", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1268012, 126831, 0, 0.0, 1, "ハッピーバースデー、キーミッ♪\nさあ、めでたい日なんだから\n盛大にお祝いしなくちゃ\nはい、カンパーイ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1269001, 126901, 0, 11.3, 5, "水着って、そわそわするわね…\n薄手だし…布も少ないし…\nま、まるで…ほとんど\n着てない、みたい…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1269002, 126901, 0, 16.8, 1, "夏の日差し、苦手で…\n暑いとすぐ疲れちゃうから\n毎年おうちにこもってるの…\nいつもこもり気味だけど…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1269003, 126901, 0, 12.3, 2, "暑い日は体調を崩しやすいけど…\nあなたは、大丈夫…？\n日差しの下でも、熱帯夜でも…\nお世話するから、言ってね…？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1269004, 126901, 0, 15.4, 2, "海を見てると、すごく落ち着く…\nでもきっと、これは海の力だけ\nじゃなくて…あなたが隣にいる\nから…よね。うふふ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1269005, 126901, 0, 0.0, 5, "み、水着…恥ずかしいけれど、\nあなたが褒めてくれるなら\n毎日でも、着…あ、でも…\nさすがに毎日は…うぅ～…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1269006, 126931, 0, 0.0, 1, "潮騒は最高の子守歌って\nよく言うわよね…\n…あなたに安眠してもらうための\n歌なら、負けない…わ…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1269007, 126931, 0, 0.0, 1, "海の遊び方はいろいろあっても…\nあなたとこうして夕焼けを見て\n過ごしているだけで…\nもう胸が…いっぱい…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1269008, 126931, 0, 0.0, 1, "海って素敵だけど…\n簡単には来れないのが残念ね…\nあっ…こ、今度はわたしの\nワイバーンで来る…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1269009, 126931, 0, 0.0, 1, "波が寄せては消えていく音…\nとてもきれいだけど、\n引き込まれそうで、怖い…\nだから…手をつないでいて、ね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1269010, 126931, 0, 0.0, 1, "浜辺に残した足跡が消えても…\nあなたとわたしの歩む道は\nこれからも消えることなく\nずぅっと続きますように…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1269011, 126901, 0, 13.2, 6, "お誕生日なのね…\nあなたをお祝いするためなら、\nわたし、どこにでも行くわ…♪\nでも…水着だと、変かしら？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1269012, 126931, 0, 0.0, 1, "ハッピーバースデー…♪\n花火…音が大きくて怖いけど、\nあなたをお祝いするために\n一回上げてみたい…かも…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1270001, 127001, 0, 5.2, 2, "海…それは身も心も\n解放されてしまう場所…！\nぐふふ…クウカの全てが\n見られてしまうのですね…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1270002, 127001, 0, 8.0, 5, "はうぅ…視線を感じます…\n水着は肌の露出が多くて\nちょっと恥ずかしいです…\n体が熱くなって…それがイイ…！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1270003, 127001, 0, 9.2, 2, "夏の砂浜はとっても熱々です…！\nここに寝転がれば…ぐふふ…♪\n見ていてください、ドＳさん！\nクウカが熱砂の上で悶える姿を！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1270004, 127001, 0, 7.8, 5, "水着のクウカを前に、ドＳさんは\n「まだ布の面積が多いな…\n俺が減らしてやるぜ」\nと言い放ち…じゅるり…♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1270005, 127001, 0, 6.2, 1, "海に二人で来るのって\nなんだか…照れくさいです、ね\n何をしたらいいんでしょう？\nドＳさん…教えてくれますか？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1270006, 127031, 0, 0.0, 1, "イイ…イイですよ…もっと…！\nえっ、なにをしているのかって？\nご覧の通り、海を楽しんでいます\nドＳさんも一緒に、どうですか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1270007, 127031, 0, 0.0, 1, "海の生き物はクウカの\n妄想によく出てきます…！\nヌルヌル、ビリビリ、ペロペロと\nバリエーション豊富ですから…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1270008, 127031, 0, 0.0, 1, "ドＳさん、そんなに\n熱い眼差しで見つめられたら\nクウカ…痺れちゃいます…♪\n体がビリビリして…止まりません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1270009, 127031, 0, 0.0, 1, "夜の海って…不思議な雰囲気です\nお互いの顔がよく見えなくて…\nあっ…！　す、すみません…\nくっつき過ぎてしまいました、ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1270010, 127031, 0, 0.0, 1, "こうして二人で遊んでいると\nあっという間に時間が過ぎて\nしまいますね…\n楽しくて…少しだけ、切ないです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1270011, 127001, 0, 4.3, 2, "ドＳさんのお誕生日を\n海でお祝いするなんて…！\nあっ、先ほど砂浜で拾った\nスゴい形の貝殻をどうぞ…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1270012, 127031, 0, 0.0, 1, "おめでとうございます！\nドＳさんが生まれた日…\nクウカと一緒に痺れるような\n体験をしましょう～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1271001, 127101, 0, 2.9, 1, "毎日暑いですよね\nそんなときはやはり怪談です\nとっておきの怖い話で\n芯まで涼しくしてあげますね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1271002, 127101, 0, 7.1, 2, "この水着、どうでしょうか？\n普段は黒ばかり着てますが\nこれは幽霊っぽくて\n涼しげで私は好きです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1271003, 127101, 0, 6.8, 2, "お水や帽子、日焼け止めなど\n日差し対策は万全です\nさあ、安全で健やかに\n夏を満喫しましょう", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1271004, 127101, 0, 0.0, 2, "海や川、湖などに行くのなら\nお付き合いしますよ\n水場は霊が集まりやすいので\n私がお守りします", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1271005, 127101, 0, 8.4, 2, "すみません、夏の海はあまり\n縁がないので楽しみ方が\n分からなくて…よければ教えて\nいただけますか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1271006, 127131, 0, 0.0, 2, "今の私は海に溶けゆく白き\n泡沫のよう…ふふ、夏のせい\nでしょうか、がらにもなく詩的に\nなってしまいました", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1271007, 127131, 0, 0.0, 1, "海はときに恐ろしい一面を\n見せます。でもどうか恐れないで\n静かに身を任せれば必ず\n受け入れてくれるはずだから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1271008, 127131, 0, 0.0, 1, "昏く、涼やかな静寂の世界\n海がこんなにも落ち着くのは\n霊の世界に似ているからなのかも\nしれません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1271009, 127131, 0, 0.0, 1, "もし暑いようでしたら\nもっと私のそばにどうぞ…\n体温が低いので\nきっと涼しいと思います…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1271010, 127131, 0, 0.0, 1, "青く昏い海の底でこのまま\nあなたと二人永遠に…\nここにいるとそんな夢想ばかり\nしてしまいます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1271011, 127101, 0, 0.0, 2, "お誕生日おめでとうございます\n占いによると海が縁起がいいと\nでましたので、そちらでお祝い\nさせてください", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1271012, 127131, 0, 0.0, 1, "これ、誕生日プレゼントです…\n流木と貝殻で作った特製のお守り\nしっかりご祈祷したので、\nあらゆる魔を防ぐはずです", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1272001, 127201, 0, 9.5, 5, "制服ってけっこういいわね\nなんとなく気が引き締まるし\nそれに意外と…かわいいし？\nや…別に頷かなくていいから…！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1272002, 127201, 0, 8.6, 4, "今日の３時間目体育か～…ねえ、\nかったるいし屋上でサボらない？\n…はいはい、やっぱダメよね～\n分かりました～行くってば～", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1272003, 127201, 0, 9.5, 3, "なんで青い春っていうのかしらね\n燃えるような赤い春でも、希望に\n満ちた黄色い春でもよくない？\n…めんどくさいって顔すんな～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1272004, 127201, 0, 5.6, 5, "ねー新しくクレープ屋ができたん\nだって。今日帰りに二人で…\n…って、べ…別に放課後デートの\nお誘いとかじゃないんだから！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1272005, 127201, 0, 7.7, 2, "今日、夕方から雨らしいわよ。\n…へ～、傘持ってきてないんだ？\nったくしょうがないわね～\n入れてあげるから感謝しなさい！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1272006, 127231, 0, 0.0, 1, "あいつ遅いな…待ち合わせ場所、\nここで合ってるわよね…あれ\nやっぱり裏門だったかも…？\nもう…不安になるから早く来い！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1272007, 127231, 0, 0.0, 1, "マジックアワーって名付けた人、\nきっとロマンチストな魔法使いね\n幻想的な空の下で、\nあいつとのんびり下校したいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1272008, 127231, 0, 0.0, 1, "放課後暇だし部活とかしようかな\n吹奏楽とか美術とか…あたし、\n芸術的センスあると思うのよね～\n今度あいつと見学いこうかしら♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1272009, 127231, 0, 0.0, 1, "なーんか最近、青春のせいで\n感情駄々洩れ気味なのよね…\nうっかりしてるとあいつに告は…\n…って、うっかりすんなあたし！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1272010, 127231, 0, 0.0, 1, "まっすぐ寮に帰らないで\nどこかに寄り道しちゃいたいな…\n目的地なんてないけど、このまま\nあんたと一緒に歩きたいだけよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1272011, 127201, 0, 4.5, 6, "へーあんた今日誕生日なんだー\n知らなか…って教室行くの禁止！\nみんなでサプライズパーティーの\n準備してんだからね！？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1272012, 127231, 0, 0.0, 1, "誕生日おめでと～！\n空の方見てて。今から魔法で、\nあんたの歳の数のバースデー花火\nぶっぱなすわよ～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1273001, 127301, 0, 7.9, 7, "ヒデサイ、どーうちの制服？\nアオハルって感じっしょ？\nどういう意味って…それは\nうちと一緒に見つけるんじゃん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1273002, 127301, 0, 7.9, 1, "ねねね、ヒデサイは何派？\nセーラー、ブレザー、ボレロ、\nどれが好き？　教えて！\n…あ、もしかして全部好きの人？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1273003, 127301, 0, 4.9, 1, "魔法まじムズい\n九九といい勝負してるよ\nあ、もしかして両方できたら\nブンブリョードーってやつ？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1273004, 127301, 0, 6.1, 7, "ヒデサイ、暇な感じ？\n制服デートしよーよ\n楽しそーっしょ？\nじゃあ放課後よろ～♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1273005, 127301, 0, 6.9, 5, "ヒデサイといるとさ\nバイブス上がるんだよね\n楽しいし、ウキウキするし…\nうちに何か魔法かけたっしょ？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1273006, 127331, 0, 0.0, 1, "ねねね、ヒデサイ\nみんなに見つからないように\n隠れるのドキドキして楽しーね！　\nあっ、そっか今は、しー…だね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1273007, 127331, 0, 0.0, 1, "二人でこーしてるとさ\nなんか少女漫画っぽくね？\nキラキラでアオハル系のやつ\nふふ、もうちょっと浸ってこーよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1273008, 127331, 0, 0.0, 1, "てゆーか、いい時間だし\nちょっちお腹減ったね…\nあ、そだ、お菓子あるし食べる？\nふふ、センセーには内緒だよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1273009, 127331, 0, 0.0, 1, "この屋上でも色々あったね\nヒデサイとの思い出いっぱいだよ\n…せっかくだし、今日も\n二人だけの思い出作っちゃう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1273010, 127331, 0, 0.0, 1, "割と風あるし\nちょっち冷えてきたね\nヒデサイも寒いっしょ？\n…ね、そっち寄ってもいい？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1273011, 127301, 0, 7.0, 7, "ヒデサイ、おたおめ～！\nこないだ、パないお店\n見つけたから、一緒いこーよ\nもち、今日はうちがおごるし！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1273012, 127331, 0, 0.0, 1, "ヒデサイ、誕生日だよね\n今日はうちがハッピーな日に\nするから、覚悟してて！\nたくさんお祝いしちゃうよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1275001, 127501, 0, 7.7, 2, "この格好、地元の子どもたちが\nきれいだって褒めてくれたわ♪\nあんたの感想も聞かせて？\n…ふふ、ありがと♪　うれしいわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1275002, 127501, 0, 9.9, 6, "その土地の匂いってあるわよね\nサラサリアだとスパイスの香りに\nお菓子と果物の甘い匂いも…\nあ～、おなか空いてきちゃった…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1275003, 127501, 0, 6.2, 1, "砂漠を渡る長旅なんて\n正直、一人だと心細かったのよ\nあんたが居てくれてよかったわ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1275004, 127501, 0, 6.9, 5, "大通りだけあって人が多いわね…\n迷子にならないようにしないと\nたとえば…う、腕を組む、とか？\n…ほら、照れてないで早くする！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1275005, 127501, 0, 9.0, 2, "いつもあんたが居てくれるから\nどこにいたって頑張れるの\n改まって言うと、変な感じね？\nでも…今言っておきたかったのよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1275006, 127531, 0, 0.0, 1, "旦那さま、あたしと一曲\n踊っていただけるかしら？\n…なんてね、ふふっ♪\nさぁ、一緒に踊りましょ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1275007, 127531, 0, 0.0, 1, "熱砂の国、灼熱の太陽…\n元気と勢いを存分に乗せた\n情熱的なステップで踊るの！\nそうそう、上手いじゃない♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1275008, 127531, 0, 0.0, 1, "音楽に乗せて体を動かすのって\n最高の気分ね！\nまだまだ体力は余裕かしら？\nなら、あたしについてきてよね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1275009, 127531, 0, 0.0, 1, "だめね、あたしったら\nどうにも浮かれちゃってるみたい\nあんたと二人っきりだと思うと…\nどうしても…熱くなっちゃうのよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1275010, 127531, 0, 0.0, 1, "あんたがよそ見できないくらい\n華麗な舞を見せてあげる♪\nあんたを想う気持ちなら\n誰にだって負ける気はないもの！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1275011, 127501, 0, 5.6, 2, "誕生日おめでとう\nあんたのためにごちそうを作って\n待ってたのよ。ほら早く♪\n熱いうちが美味しいんだから！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1275012, 127531, 0, 0.0, 1, "いつもは子どもたちと一緒だけど\n今日はあたしに最初に祝わせて\n誕生日おめでとう。その…ずっと\nそばに居てくれて、ありがとうね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1276001, 127601, 0, 2.8, 2, "郷に入りては郷に従え、\nってことでこっちの衣装に\n着替えてみたよ\n似合ってるかい？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1276002, 127601, 0, 4.5, 1, "この服、すごく快適だねえ…\n覚えときな。その土地にある\n伝統的なものってのは、そこに\n生きる人々の知恵そのものなのさ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1276003, 127601, 0, 6.0, 1, "さすがにこの服に刀二本は\n目立ちすぎるからねえ…\n今回は別に用意してみたよ\n試し斬りがてら冒険にでも行こうか", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1276004, 127601, 0, 7.5, 1, "風さえ通れば涼しいもんだね\nなるほど、それでこんなに\n布地が…って、お前さん\n視線がまる分かりだよ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1276005, 127601, 0, 7.5, 2, "ふん、ふん…♪　今のかい？\nサラサリアの踊りなんだけどね\n振りを覚えたらお前さんのために\n踊ってやるから待ってておくれよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1276006, 127631, 0, 0.0, 1, "ふう…いい風が吹くね\nもうちょっとこっちへきなよ\nそっちだと日陰から出ちまうよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1276007, 127631, 0, 0.0, 1, "ん、どうした\n眠くなっちまったのかい？\nなら、アタシの膝枕で\n少し眠るといいさ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1276008, 127631, 0, 0.0, 1, "今みたいな何もない時間を\n一緒に過ごして楽しいってのが\n相性ってやつなのかもしれないね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1276009, 127631, 0, 0.0, 1, "やっぱり旅ってのはいいねえ…\nお前さんはどこに行きたい？\nたとえどこだろうと付き合うよ\n二人一緒なら退屈しないだろう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1276010, 127631, 0, 0.0, 1, "こうしてると、まるで世界に\nアタシらしかいないみたいだね…\n大丈夫さ、もしそうなっても\nアタシが養ってやるよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1276011, 127601, 0, 5.0, 2, "誕生日おめでとう。無事に一年を\n過ごせたかい？　次の一年も\nアタシがついててやるから\n大船に乗ったつもりでいなよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1276012, 127631, 0, 0.0, 1, "今日はお前さんの誕生日\nだったね。おめでとう\n出会った頃に比べたらずいぶん\n頼りになる男になってきたね♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1277001, 127701, 0, 5.2, 3, "ライラエルさまは優しくて\nいつも民のことを考えてます\nあの方のメイドとして\n私も相応しくなりたいんです", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1277002, 127701, 0, 7.8, 3, "『ジオ・テオゴニア』の人たちは\nいつものんびりしすぎなんです\nそれにちょっといい加減で…\nだから私がしっかりしなきゃ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1277003, 127701, 0, 4.7, 3, "ヴルムってば、また私のことを\nチョロいって…\n私のほうがしっかりしてますし\nそんなことないですよね？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1277004, 127701, 0, 6.4, 8, "騎士さんって器用なんですね\nそれに優しくて、頼りに…\nって私、何言ってるの…！？\n今言ったことは忘れてくださいっ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1277005, 127701, 0, 9.5, 2, "騎士さんみたいに気が利いて\nなんでもできれば、私ももっと\nみんなの力になれるのかな？\nそしていつか、恩返しも…！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1277006, 127731, 0, 0.0, 1, "いいお天気…\n絶好のお洗濯日和ですね\nこれならお城のみんなに\nふかふかのシーツを届けられます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1277007, 127731, 0, 0.0, 1, "このあとはお部屋の掃除に\n料理の配膳をして、それから…\n今日も一日、ばりばりしっかり\n働かなきゃ！　よーし…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1277008, 127731, 0, 0.0, 1, "わぁっ、きれいなシャボン玉…♪\nでもどうしてこんなに…\nって、ヴルム！\nサボってないで干してってば！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1277009, 127731, 0, 0.0, 1, "高いところ、私の代わりに\n干してくれたんですか？\n…ありがとうございます\nおかげでとっても助かりました！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1277010, 127731, 0, 0.0, 1, "やっぱり優しくてすごいなぁ…\nって、そんなんじゃダメ！\nいくら騎士さんが優しいからって\n厚意に甘えないようにしないと…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1277011, 127701, 0, 9.3, 5, "お誕生日おめでとうございます！\nライラエルさまが、お祝いに一日\nあなたに仕えてはどうかって…\nいかがでしょうか、主さま…？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1277012, 127731, 0, 0.0, 1, "騎士さんには私もたくさん助けて\nいただきましたから…その…\n私にできるお祝いなら、\nなんでも言ってくださいね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1278001, 127801, 0, 7.0, 2, "騎士さん、こんなとこ\nぶらついてて何か用事でも？\nあたしが案内しましょうか\n…サボりの口実にもなりますし", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1278002, 127801, 0, 5.9, 6, "この仕事は終わりっと…\n手伝ってもらって助かりました\n何かお礼しないとですね\nお茶でも一杯淹れますよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1278003, 127801, 0, 0.0, 2, "男のひとってドラゴンとか\n秘密兵器とかほんと好きですね\nふ、あたしも嫌いじゃないですよ\nそーゆーの理解あるほうなんで", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1278004, 127801, 0, 7.2, 2, "騎士さん、このままこっちに\n居てくれたらいいんですけど\n仕事の手伝いも頼めますし\nそれに…見てて飽きないので", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1278005, 127801, 0, 3.1, 2, "もし何もかも嫌になった…\nそんな日には声かけてください\n適当にどっか出かけましょう\nんで、怒られる時は一緒ですよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1278006, 127831, 0, 0.0, 1, "こんな天気のいい日は\nゆっくりするに限りますってー\n騎士さんも休憩してきます？\nお菓子ならほらここに", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1278007, 127831, 0, 0.0, 1, "クッキー食べたいんですか？\nま、あたしのサボりに\n目瞑ってもらってますし…どぞ\n今の話、姉貴には内緒でヨロです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1278008, 127831, 0, 0.0, 1, "はーめんど…毎日掃除する\n意味なんてあるんですかね\nキレイに保つなら週一とかで\n十分だと思うんですけど…はぁ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1278009, 127831, 0, 0.0, 1, "お客さまにはこんな姿\n見せらんないですからね\nふ、騎士さんは特別ですよ\n身内みたいなもんなんで", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1278010, 127831, 0, 0.0, 1, "今日も代わり映えのない\nサボるにはうってつけの日…\nって訳で、この幸せな時間を\n一緒に堪能しましょうか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1278011, 127801, 0, 5.5, 4, "今日、誕生日なんですね\nおめでとうございます\n…なんですか、あたしだって\nお祝いくらい普通にしますって", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1278012, 127831, 0, 0.0, 1, "誕生日おめです。プレゼント、\nこのクッキーでいいですか？\n…冗談ですよ。用意してます\nいつもお世話になってますから", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1279001, 127901, 0, 8.4, 2, "クリスマス気分を盛り上げる\n挨拶を考えちゃいました！\nでは一緒に大きな声で…せーの！\nメリークリスマオイッスー☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1279002, 127901, 0, 5.0, 5, "ホワイトクリスマスでも\nあまり寒さは感じませんね～\nあなたの幸せそうな笑顔を見て\n心がぽかぽかしてますから☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1279003, 127901, 0, 11.7, 7, "す～は～、えへへ…☆\nクリスマスはあちこちのお店から\nおいしそうな匂いが漂ってきて…\nお腹が大合唱状態です☆", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1279004, 127901, 0, 7.6, 5, "少し風が出てきましたね…\nよければわたしのマントで\n一緒にくるまります…？\nあ、熱くなりすぎるかもですが…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1279005, 127901, 0, 7.3, 2, "いつか大切な方と二人っきりで\nクリスマスを過ごしてみたいと\n夢見ていたんですが…あなたの\nおかげで叶っちゃいましたね…☆", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1279006, 127931, 0, 0.0, 1, "急いでプレゼントを用意したので\nリボンが絡まっちゃいました…\n見た目はちょっと不格好ですけど\nあなたへの気持ちは保証します♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1279007, 127931, 0, 0.0, 1, "箱を開けて、わたしを見た時\nどんな顔で喜んでくれるかな…？\nえへへ、プレゼントの気持ちって\nこんな感じなんですかね…☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1279008, 127931, 0, 0.0, 1, "あ、あはは～…一人で箱から\n出られなくなっちゃいました～…\nあの…抱き上げてくれますか？\nできればお姫さま抱っこで…☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1279009, 127931, 0, 0.0, 1, "プレゼントはわたし…なんて\nかなり大胆なこと言ってますね…\n言っておきますけど、こんなこと\nあなた以外には言えませんよ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1279010, 127931, 0, 0.0, 1, "こんな姿、他の人に見られたら\nびっくりされちゃいますね…♪\n今日だけは、この聖なる夜だけは\nあなただけのわたし…です…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1279011, 127901, 0, 7.5, 2, "あなたの誕生日のお祝いに\nクリスマスケーキより大きな\n超特大ケーキを用意しました☆\n量が多いのでわたしも一緒に…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1279012, 127931, 0, 0.0, 1, "お誕生日おめでとうございます\n喜んでもらえるとうれしいですが\n…受け取ってくれますか？\nプレゼントは…わたしです☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1280001, 128001, 0, 3.7, 2, "クリスマスは年に一度の稼ぎ時！\nそう思っていたわたくしですが…\n今年は一味違いますわよ！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1280002, 128001, 0, 6.4, 1, "陛下のクリスマスを彩るには\n忠臣であるわたくしも\nふさわしい格好をするのが筋！\nこれは忠誠心の体現なのです！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1280003, 128001, 0, 3.9, 4, "わたくし、サンタさんには\nお会いしたことがありませんの\nあいにくと「良い子」のままでは\n生活できなかったものですので", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1280004, 128001, 0, 4.7, 2, "ちょっと少年？　お仕事ですわよ\nもう少しこちらにいらっしゃいな\nまさか緊張なさっているとでも？\nあらあら、おませさんですわね♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1280005, 128001, 0, 5.8, 2, "貴方にでしたらエスコートを\n依頼してもよろしくてよ？\n知らない仲ではありませんし…\n報酬も格安で済みますものね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1280006, 128031, 0, 0.0, 1, "わたくし、見た目を隠している\nつもりはございませんの\n隠しているのは…\n個人の資産だけですわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1280007, 128031, 0, 0.0, 1, "こう見えまして、わたくし\nモデルの経験もありますの\n商工会の広告費節約のために\nなかなか様になってますでしょ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1280008, 128031, 0, 0.0, 1, "ひゃあっ！？　む、胸に…胸に！\nこの量ですら麦しゅわが何杯も\nおかわりできますのに…！\nああ…なんてもったいない～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1280009, 128031, 0, 0.0, 1, "ちょっとちょっと秘書１号？\nなぜ少年に撮影を任せようと\nしていますの？　小休憩？\nまったく…少しだけですわよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1280010, 128031, 0, 0.0, 1, "貴方にはジュースがお似合いです\nただ…そうですわね\nいつか大人になったあかつきには\nご一緒してあげてもよろしくてよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1280011, 128001, 0, 5.1, 2, "わたくしの記憶によれば\n今日は貴方のお誕生日でしたわね\nおめでとうございます\nケーキの一つでもいかがかしら？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1280012, 128031, 0, 0.0, 1, "お誕生日おめでとうございます\n本日ばかりはケチケチせずに\n貴方の希望を聞いて差し上げます\nさ、言ってごらんなさいな", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1283001, 128301, 0, 6.9, 8, "どうです？　大幹部にふさわしい\nゴージャスな装いってヤツです！\nさあ、思う存分\n褒めるがいいですよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1283002, 128301, 0, 8.9, 2, "ふっふっふ、新年早々\nあたしの晴れ着姿を見られて\nお兄さんは運がいいですね！\nさあ、お年玉をよこすですよ！", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1283003, 128301, 0, 5.4, 8, "トーゴクにはドラゴンの年\nってのがあるらしいですね\nまさにあたしのための年\nじゃないですか！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1283004, 128301, 0, 2.2, 3, "なんですか？　いくらあたしの\n晴れ着姿がキレイだからって\nそんなにジロジロみてたら\n通報もんですからね？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1283005, 128301, 0, 7.6, 2, "はー、どこも人ごみだらけで\nまともに歩けないですね…\nはぐれちゃ大変ですから\n手をつないどいてやるですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1283006, 128331, 0, 0.0, 1, "ふっふっふ、あたしの勝ちです！\nさーて、何を描いてやるですかね\n覚悟するですよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1283007, 128331, 0, 0.0, 1, "あたしの顔に落書きした罰です\nその顔にたーっぷりと\n仕返ししてやるですからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1283008, 128331, 0, 0.0, 1, "やっぱりあたしが最強です！\nこの調子で勝ちまくって\nランドソルをせーふくして\nやるのもいいですね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1283009, 128331, 0, 0.0, 1, "ま～たあたしの勝ちですね！\nお兄さんザコザコすぎですよ\nもしかしてあたしに描いて\nほしくて手を抜いてやがるです？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1283010, 128331, 0, 0.0, 1, "イ、ノ、リ、さ、ま、専、用っと\nふっふっふ、これでお兄さんは\nあたしの所有物ですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1283011, 128301, 0, 3.0, 8, "お誕生日おめでとうですよ！\nさあ、このあたしの晴れ姿、\nプレゼント代わりに\n目に焼き付けとくがいいです！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1283012, 128331, 0, 0.0, 1, "さあ、バースデープレゼントは\nあたしの直筆サインです！\nこうやってたっぷりの墨で…\nふふっ、遠慮はいらねーですよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1284001, 128401, 0, 6.0, 2, "じゃじゃーん！\nシオリンに振袖の着付けを\n手伝ってもらったのだ～\nえへへ、かわいいでしょ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1284002, 128401, 0, 5.1, 6, "振袖を着ると姿勢が\nシャキッとするんだよね～\nあ、ひょっとして寝落ち対策に\nなったりするのかな…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1284003, 128401, 0, 6.8, 2, "仕事納めの解放感…\nそれがお正月の醍醐味だよね～\nだから、今日はキミと一緒に\nゆっくり休むのだ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1284004, 128401, 0, 13.4, 7, "え～っと、おみくじの結果は…\n待ち人来る…これって\nもしかしてキミのことだったり？\n…なんだかドキドキしてきたかも", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1284005, 128401, 0, 6.4, 5, "せっかく振袖を着てるんだし\nキミと二人で初詣に行きたいな～\nまだ草履を履き慣れてないから\n手を繋いでさ…どうかな？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1284006, 128431, 0, 0.0, 1, "あけましておめでとう！\n新年の始まりに相応しい\nきらーんっ☆　な景色だね～\nきっといい一年になるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1284007, 128431, 0, 0.0, 1, "初日の出には、その年の幸せを\n祈る意味があるんだって\n私はこれからも、キミと一緒に\n冒険できれば、それで幸せだな～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1284008, 128431, 0, 0.0, 1, "空でも冷静でいられるキミとなら\nいいコンビになれるはずだよ～\n私はちょっと慌てることが多いし\n持ちつ持たれつってやつだねっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1284009, 128431, 0, 0.0, 1, "これだけ空高く浮かんでると\n風もちょっと冷たいね…\n新年早々に風邪を引いたら\n大変だしくっつくのはどうかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1284010, 128431, 0, 0.0, 1, "キミと一緒ならハツネちゃんは\n最強だねっ！　この景色も\nキミがいたから見られたんだし\nこれからもずっと隣にいたいな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1284011, 128401, 0, 5.5, 2, "誕生日おめでとう！\n振袖姿でお祝いなのだ～！\n今日はお正月以上に\n盛り上げていくよ～☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1284012, 128431, 0, 0.0, 1, "誕生日おめでとう！　キミに\n特別な景色をプレゼントなのだ～　\nえへへ、めでたいでしょ？\nこれで今日もいい日になるね☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1285001, 128501, 0, 6.3, 4, "着物はきれいだけど動きづらいね\nさっき手がかりを見つけて急いで\nかがんだら、帯が食い込んで\n死ぬかと思ったよ…！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1285002, 128501, 0, 5.2, 2, "新年は人でごった返すぶん、\nいろんな事件が起きるね…\nやれやれ。探偵には\nお正月休みなんてなさそうだ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1285003, 128501, 0, 0.0, 1, "コマ遊びに福笑い…\nお正月は興味深い遊びが\nたくさんあるね！　えへへ…\n何から遊ぼうかなあ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1285004, 128501, 0, 9.1, 5, "君と一緒に事件を解決するのも\nいいけど、家でのんびり過ごす\nお正月もすっごく幸せ…\n…あ、いや、何でもない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1285005, 128501, 0, 5.8, 5, "ずっとひとりだったから、誰かと\n一緒のお正月に慣れてないんだ\n君と一緒に過ごせたら、\n勉強になる…かも？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1285006, 128531, 0, 0.0, 1, "助手くん、助手くん！\nすごいミステリースポットだよ！\n早く上まで行ってみよう？\nよーい、どんっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1285007, 128531, 0, 0.0, 1, "今年もいい一年にしようね！\n騒がしいのは苦手だけど\n君とは賑やかで楽しい\n日々を過ごしたいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1285008, 128531, 0, 0.0, 1, "ひとりで過ごすのは好きだけど\n初詣はひとりだと寂しくてね…\n助手くんがいてくれて\n本当に良かった…えへへ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1285009, 128531, 0, 0.0, 1, "この木の板は『絵馬』だよ\nお願い事を書いて奉納すると\n願いが叶うんだって…\nあっ！？　…み、見えちゃった？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1285010, 128531, 0, 0.0, 1, "噂によるとここは\nえ、縁結びにもご利益がある\nらしいよ？　ま、まあそんなの\n迷信かな！　きっとそうだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1285011, 128501, 0, 4.9, 5, "君の誕生日を祝うには\nこの装いがぴったりだね\nほら…だってすごく\nおめでたい日…じゃないか？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1285012, 128531, 0, 0.0, 1, "ハッピーニューバースデー♪\nふふっ！　新年もめでたいけど\n今日もおめでたい日だよ\n助手くん…おめでとう♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1287001, 128701, 0, 11.4, 7, "アストラル…これが新しい力！\n心の奥から燃え上がる火の力なら\n次の冒険もきっと乗り越えられる\nそうだよね、騎士くんっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1287002, 128701, 0, 11.2, 2, "燃え広がる炎みたいに、あたしの\nエールをみんなに届けたいんだ！\nこの力なら今まで以上にそれが\nできる気がして…楽しみだなっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1287003, 128701, 0, 9.0, 6, "この衣装かわいいだけじゃなくて\n防火性もバツグンなんだよ！\nこれならマグマで泳げちゃうかも\n…ってさすがにそれは無理かな？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1287004, 128701, 0, 12.3, 6, "ね…ねえ騎士くんっ！\nあの…手、繋いでもいいかな…？\nグローブがないから…その…\n…な、なんでもないっ！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1287005, 128701, 0, 4.9, 6, "騎士くん、今日は二人っきりだね\n…え、顔赤い？　ち、違うよ！？\nこれは炎に照らされてるだけで…\n照れてなんか…いる、けど…っ！", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1287006, 128731, 0, 0.0, 1, "騎士くんのおかげであたし\nまた強くなっちゃった！\nあはははっ！　火よ舞い上がれ！\nこれがアストラルの力だーっ！！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1287007, 128731, 0, 0.0, 1, "もし心の火が消えちゃいそうなら\nあたしがすぐに駆けつけて、\n笑顔の炎をともしてあげるね！\nだから安心してね、騎士くんっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1287008, 128731, 0, 0.0, 1, "あたしはこの火の力で、みんなを\n暖かく包み込みたいんだっ！\n炎は熱く激しいだけじゃないって\n見せてあげちゃうよーっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1287009, 128731, 0, 0.0, 1, "騎士くんと目が合うと、心も体も\n熱くなって、焦げちゃいそう！\nお、おかしいな、自分の炎なのに\nどうして焦げそうになるんだろ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1287010, 128731, 0, 0.0, 1, "…この合言葉を心で唱えたら、\nあたしの火力に際限はないよっ！\n『騎士くん大好き』！　唱えると\nどんどん強くなれちゃうよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1287011, 128701, 0, 9.5, 4, "今日は騎士くんのお誕生日だから\nお祝いにケーキを用意したよ♪\n火をつけるのはあたしにお任せ…\nって、ケーキ焦げちゃったっ！！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1287012, 128731, 0, 0.0, 1, "騎士くん、お誕生日おめでとう！\n舞い上がる炎でお祝いするよ！\nあはは、楽しくなってきた！\n騎士くんも一緒に舞い上がろー♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1288001, 128801, 0, 5.9, 1, "アストラル…これが\n私に眠っていた新たな力…\nおかげで私はもっと強くなれる\n大切な仲間を守るために！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1288002, 128801, 0, 6.8, 2, "キミが共に戦ってくれるなら\n誰を敵に回しても\n負ける気がしないんだ\n私の背中は任せたからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1288003, 128801, 0, 10.0, 2, "最近は風の力の制御も\n鍛錬に取り入れてるんだ\nおかげで少し風が読めるように\nなってきて…釣りに使えるかな？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1288004, 128801, 0, 6.4, 1, "アストラルの力に目覚めて\n新しい扉が開いた気がするよ\n私たちの限界はまだまだ\nもっと先にある…そうだよね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1288005, 128801, 0, 8.7, 2, "何度道に迷ったとしても\nキミがそばに居れば\n嵐の中だって進んでいける…\nその強さをキミがくれたんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1288006, 128831, 0, 0.0, 1, "キミとの心の繋がりが\n新たな風を呼ぶ力となる！\n進むべき道を切り拓くための…\nこれが私の覚悟の姿だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1288007, 128831, 0, 0.0, 1, "体の内から湧き上がる熱い力…\n今の私たちならきっと無敵だ！\nどんな嵐がやって来ようと\n紡いだ私たちの絆は消えない！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1288008, 128831, 0, 0.0, 1, "失えないものが私にもできた\n大切な仲間を守るためなら\nどれほど傷つこうと構いはしない\n私はただこの剣を振るうだけだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1288009, 128831, 0, 0.0, 1, "キミとの出会いが\n私の未来を大きく変える\n分岐点だったのかもしれない\n今は…もっとその先を見てみたい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1288010, 128831, 0, 0.0, 1, "願った夢を叶えるには\n叶うまで走り続ければいい\nそばにキミが居てくれるなら…\n私はどこまでだって行ける", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1288011, 128801, 0, 6.9, 5, "キミ、誕生日おめでとう\n今日は手料理を作ったんだ\n頑張ってはみたんだけど…うん\n大事なのは見た目より味だからね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1288012, 128831, 0, 0.0, 1, "誕生日を迎えたお祝いをする…\n不思議だね、祝っている私まで\nうれしくなってしまうんだ\nキミとだから…なんだろうね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1289001, 128901, 0, 8.6, 7, "騎士クン、どうかな？\nこれがわたしたちの…\n新しい絆の形だよ\nえっと…に、似合ってる？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1289002, 128901, 0, 9.4, 2, "えっ…髪型、かかかわいい…？\nあ、ありがとうっ！\n実はわたしも気に入ってるの\nよかったぁ…またやろうっ♪", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1289003, 128901, 0, 5.7, 1, "すごい力を感じるのに…\n不思議と落ち着いてるんだ\nエルフの里で自然や精霊のことを\n勉強してきたおかげかな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1289004, 128901, 0, 0.0, 7, "あ、あんまり見ないで…？\nぴったりした服だから、その…\nいろいろ気になっちゃって…！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1289005, 128901, 0, 7.1, 2, "この姿になって分かったよ\n騎士クンのためにできること…\nまだまだあるんだね\nすっごくうれしい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1289006, 128931, 0, 0.0, 1, "もっともっと強くなりたい…！\n二度と後悔なんてしないように\n助けてほしいと願う人を\nひとりぼっちにさせないように！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1289007, 128931, 0, 0.0, 1, "世界の力とわたしたちの絆の力\n全てを一つの光にして解き放つ！\nそれができるのは…\nわたししかいないんだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1289008, 128931, 0, 0.0, 1, "世界に満ちる光の精霊たちよ…！\nわたしに…\nわたしと騎士クンに力を貸して！\n未来を照らすために！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1289009, 128931, 0, 0.0, 1, "光を通じて伝わってくるよ\n騎士クンの優しくて力強い想い\nだから…わたしも応えたいの\nあなたと同じように全身全霊で！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1289010, 128931, 0, 0.0, 1, "騎士クンを好きになる資格なんて\nもうないと思ってた\n…でも、やっぱり諦めたくない\nそれがわたしの…ワガママ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1289011, 128901, 0, 7.6, 2, "あの…騎士クン\n何か欲しいものとか…あるかな？\nだって、お誕生日でしょ？\nわたしもできることをしたくって", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1289012, 128931, 0, 0.0, 1, "お誕生日おめでとう！\nこれからの騎士クンの未来が\n光でいっぱいになるように…\nわたし、頑張るね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1290001, 129001, 0, 4.5, 5, "わたしの姿…何か、おかしい？\nあんまり見つめられると…\nうれしいけれど、なんだか少し…\n心許なくて。恥ずかしい…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1290002, 129001, 0, 4.3, 4, "表情…柔らかくなった？\n…そう、だね\n強いままであり続けるには…\n諦めたものが多すぎたから", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1290003, 129001, 0, 0.0, 7, "普段は、力を抑えているの\n戦うべきときのため…\n騎士クンが必要としてくれる\nそのときのために", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1290004, 129001, 0, 7.9, 2, "騎士クン…手を貸してもらえる？\n…疲れたわけじゃないよ\nあなたに、触れてみたくて\nわたしの存在を…感じてほしくて", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1290005, 129001, 0, 6.3, 4, "…あなたがいる\n手を伸ばせば届く距離に\n…想いまでも届けてしまいたいと\nそう望むことは…欲が深い、かな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1290006, 129031, 0, 0.0, 1, "わたしの力の全ては\nミネルヴァあってのもの…\nそう思われるのはひどく心外\nさあ…そこに直りなさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1290007, 129031, 0, 0.0, 1, "かつても。今も。これからも\nわたしが騎士クンのために\n力を振るうことに変わりはない\nたとえ…何も得られないとしても", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1290008, 129031, 0, 0.0, 1, "…脅威も敵意も運命すらも\n騎士クンを脅かすことはできない\n忌まわしく疎ましいこの世界に…\nわたしが存在し続ける限り", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1290009, 129031, 0, 0.0, 1, "プリンセスナイト…絆の力\nわたしの体を満たしているのは\n紛れもなく騎士クンと結ばれた証\n…ならば…わたしは、無敵だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1290010, 129031, 0, 0.0, 1, "二人だけの世界は望めない\nそれならせめて…二人だけの時を\n全ての命が消え失せてもなお残る\n騎士クンとわたしが紡ぐ物語を", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1290011, 129001, 0, 10.5, 2, "騎士クンが\n生まれてきてくれたこと…\nそれがわたしの何よりの幸せなの\nだから…ありがとう", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1290012, 129031, 0, 0.0, 1, "お誕生日おめでとう、騎士クン\nさあ…願い事をなんでも言って\n必ず、叶えてみせるから\nたとえ…どんなことであっても", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1293001, 129301, 0, 7.6, 7, "ここはまるで楽園みたい！\nまさに冬といえばサマーだわ～！\n…よし、ぼくの演技力も\nだいぶ磨きがかかってきたな", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1293002, 129301, 0, 0.0, 1, "水着がシックだからとチエル君に\nこのビーチボールを持たされた\n外しのキュート系アイテムで\nバランスが取れるらしいぞ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1293003, 129301, 0, 8.6, 1, "高機動万能靴『神速くん』で\nプールを渡れるか試したかったが\nメ団に全力で止められたよ\n多少の客など轢いてもよかろうに", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1293004, 129301, 0, 7.0, 5, "君の水着はユニークな柄だな\nどれ、もっとよく見せたま…\nむっ、意外と良い身体…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1293005, 129301, 0, 5.7, 4, "おい同志、最近テレ女へ来る\n頻度が減っているんじゃないか？\n端的に言ってしまうが…寂しいぞ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1293006, 129331, 0, 0.0, 1, "ぼかあ筋金入りのインドア派だが\n屋外での開放的な読書というのも\nたまには悪くないものだな\n…ここが屋外か否かはさて置き", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1293007, 129331, 0, 0.0, 1, "彼方の白銀、此方の楽園、\nそしてトロピカル謎ポーション…\n気に入ったぞ、この露天書斎！\n後輩、永住権をもらってくるんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1293008, 129331, 0, 0.0, 1, "ぽーん！\nこちら喋る石のロゼッタです！\nユニ界一の文鎮として、今日も\nあるじの手帳を死守しています！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1293009, 129331, 0, 0.0, 1, "おい同志、足底腱膜を解してくれ\n足の裏は第二の心臓とも呼ばれ…\nふはっ、うひゃひゃ…ちょ待っ…\nんぎゃ！　あだだ…ぐ…ひぎぃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1293010, 129331, 0, 0.0, 1, "どうした同志、なにを呆けている\nぼくの顔に何か付いているかね\nなに？　水着が…似合ってる…？\n左様か…ふむ…ありがとう…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1293011, 129301, 0, 5.8, 2, "たしか都では、誕生日に物を\n贈る風習があったな\nこの謎ポーションをやろう\n飲んでみろ、飛ぶぞ", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1293012, 129331, 0, 0.0, 1, "やあ同志、まずは座りたまえよ\n誕生祝いに学びを贈ってやろう\nなに、遠慮することはないさ\n端的に言えば、君と話したいんだ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1294001, 129401, 0, 8.7, 5, "ああ、このグラサン？\nいや屋内つっても天井透明だし、\n陽射し眩しめかなって思って\nあと…まあ…カッコよくない？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1294002, 129401, 0, 6.0, 4, "アオイにこの水着見せたら、\nキャンプにも行けそうですねって\n言われたんだけど…え、最近の\nキャンプってそんなカンジなの？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1294003, 129401, 0, 9.0, 5, "この水着うち似合ってンだって\nさっきチエルが普通に言ってきて\n無駄にウザって返しちゃった\nちょっと嬉しかったんだけどね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1294004, 129401, 0, 9.4, 1, "あんたの水着、変わってんね\n何その柄、よく見してみ？\nいや近い近い、そんな近付かな…\nふーん、意外といい身体…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1294005, 129401, 0, 9.1, 4, "あんたさ、最近テレ女に来る回数\n減ってるくない？　いいけどさ、\n忙しいのは知ってるし…とはいえ\nもうちょっと都合つかんの？", 10, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1294006, 129431, 0, 0.0, 1, "あーも…チエルはしゃぎ過ぎ\n謎に水ぶっかけられたわ…\nえ、ソレあんたが使ったタオル？\nや、全然…借りるけど…ありがと", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1294007, 129431, 0, 0.0, 1, "ユニ先輩もああいう水着で静かに\n佇んでればちゃんと大人だよね\n端的に言ってセクシーってやつ？\nいや言わないよ、調子乗るもん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1294008, 129431, 0, 0.0, 1, "ちょー、その水筒うちンだよ、\nなに勝手に飲もうとしてんの\nあ、や、あんたが気にしないなら\nべつに…いいけど、それは…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1294009, 129431, 0, 0.0, 1, "あのさ、無意識なんだろうけど、\n人がラッシュガード脱いでるとこ\nまじまじ見んのやめてくんない\nや、なんか…謎にハズいんで…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1294010, 129431, 0, 0.0, 1, "きゃあっ！？　びっくりした…\nなに、いるなら声かけてよ\nほら、あンじゃん、心の準備とか\nあんまヘンな顔見られたくないし…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1294011, 129401, 0, 3.3, 7, "おたおめー。おばちゃんパイン\nあげるから。なんでって…いや\nかなりのパインフェチじゃなきゃ\n選ばんでしょ、その水着は", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1294012, 129431, 0, 0.0, 1, "あー、あのさ、暇だしメ団に道具\n借りてケーキ作ったんだけどさ\n今日あんた誕生日なんだって？\nあとでうちの部屋、食べに来なよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1295001, 129501, 0, 0.0, 2, "うちのガッコ女子校で良かった～\nかわいい水着選ぶのに露出度とか\n無用なノイズ気にしなくて済むし\nテレ女に男子いなくて本当最高！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1295002, 129501, 0, 10.3, 6, "ちぇる～ん☆　ちぇる　ちぇぱぱ\nちぇろりぽぴ　ちぇぱるれぱ？\nあ、やっぱり！　さっすが先輩\n分かってる～！　え…分かるの？", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1295003, 129501, 0, 7.1, 6, "というわけで、ちえるんでした！\n最後にチャンネル登録・高評価、\nよろしくお願いしまーす！\n…え？　カメラ回してなかった？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1295004, 129501, 0, 5.9, 1, "先輩、大変です！\nメリゾで売ってる飲食物どれを\nとっても美味しそう問題大勃発！\nただちに全額おごってください！", 11, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1295005, 129501, 0, 8.8, 3, "先輩もソレ、かわいい水着ですね\nその柄を違和感なく着こなす男子\n人類になかなか存在しないですよ\n分かり手のチョイスだ…やるな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1295006, 129531, 0, 0.0, 1, "メリゾ最高！　いい波きてます！\n始まりましたよ、チエルの夏が！\n…あれ、冬でしたっけ？\nまあどっちでもいんですけど！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1295007, 129531, 0, 0.0, 1, "きゃっは～！　た～のしぃ～！\n流石ちえるん、世界の美少女！\nどんな波でもかわいく映えて…\nうお…ちょ…待っ…んぎゃあー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1295008, 129531, 0, 0.0, 1, "アーッ！　先輩どいてどいて！\n何そんなとこ突っ立って…\nきゃああぁ！　…痛ったぁ～\nちょっ…どこ触ってるんですか！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1295009, 129531, 0, 0.0, 1, "正直『大きい』が『かわいい』を\n阻害しちゃう瞬間あるのがね…\nこんなきゃわ水着にミィコさんの\n第一声「でっっっ！」だもん、草", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1295010, 129531, 0, 0.0, 1, "あれ？　みんないなくなってる…\nえ、どします？　捜し行きます？\nいや、でも動かない方がいいか…\nじゃこのまま二人で遊びましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1295011, 129501, 0, 6.7, 1, "お誕生日おめでとうございます！\n誕プレは金のちぇると銀のちぇる\nどっちがいいです？\nま折角なんで両方あげときますね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1295012, 129531, 0, 0.0, 1, "ハピバ・すぷらーっしゅ！\n今日は可愛い後輩とメリゾで１日\n遊びたおせる権を差しあげます！\nや～ん！　そんな喜ぶなよ～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1296001, 129601, 0, 4.1, 7, "─私の、話？\n…どうしてそんなもの\n聞くんですか", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1296002, 129601, 0, 0.0, 7, "いつでも、\n辺りを警戒してます\nじゃないと、身、守れません…", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1296003, 129601, 0, 5.4, 4, "アルラウネは\nめずらしい種族、です\n─たくさん、狩られましたから", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1296004, 129601, 0, 10.6, 4, "媛も、ネアさんも…貴方も\nすごく、構ってきますよね\nちょっと─困ります", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1296005, 129601, 0, 9.6, 7, "あまり近くをうろうろ\nしないでください─\n反射的に攻撃してしまうので…\n忠告─です", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1296006, 129631, 0, 0.0, 1, "自分の身は、自分で守ります\nそのための毒も棘もある\nアルラウネ─ですから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1296007, 129631, 0, 0.0, 1, "花は好きです\n騙そうとしたり、襲ってきたり\nしないから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1296008, 129631, 0, 0.0, 1, "…………え？\nなんで見てるのか…？\n動くもの─観察する癖が\nあるだけです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1296009, 129631, 0, 0.0, 1, "媛は「貴方と関わって\n世界を知れ」と言います\nよく、わかりませんが…\n知ることばかりでは、あります", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1296010, 129631, 0, 0.0, 1, "私の力のこと、内緒ですよ\nもし誰かに言ったなら…\n貴方を倒さなくちゃ\nならない─から", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1296011, 129601, 0, 5.7, 6, "誕生日おめでとうございます\n…何ですか？　驚いた顔して…\nお祝いくらいは言いますよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1296012, 129631, 0, 0.0, 1, "貴方の誕生日を祝おうって─\n媛が宴を用意してます\n私も案内係くらいは…はい", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1297001, 129701, 0, 3.0, 1, "ジオ・ゲヘナは如何かえ？\n騎士殿には恩義多かれば\n懸念がございますれば\nどうぞおっしゃってくだしゃんせ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1297002, 129701, 0, 8.0, 1, "媛といえど、外へ出れば異邦者\n旅情を感じるものにありんす\n…玉座を狙う不逞の輩も\n今はおりませぬし喃", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1297003, 129701, 0, 9.9, 4, "モネは無礼をしておりませぬか？\n騎士殿が相手であれば\nあれもきっと懐きましょうぞ\n…恐らくは、でありんすが", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1297004, 129701, 0, 0.0, 2, "お前さまとの日々は、さぞ愉快な\n事ばかりでございましょうな\n行く先々で人助けをする姿…\n目に浮かびまする", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1297005, 129701, 0, 8.1, 2, "ジオ・ゲヘナの媛を務めまするは\n幾つもの奇縁と目的あってのこと\n幼気な稚児らを護るも目的の一つであり申す", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1297006, 129731, 0, 0.0, 1, "さぁさぁ皆の者、\n百鬼夜行と参ろうぞ\nこの地は煉獄、『ジオ・ゲヘナ』\n強き者のみが生き残る穢土なれば", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1297007, 129731, 0, 0.0, 1, "この賑わい、活力溢れたる様は\nランドソルにも引けは取りませぬ\nただ些とばかり、暑苦しくは\nあるやもしれず…フフ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1297008, 129731, 0, 0.0, 1, "鉄は硬きもの、されど溶けるもの\n千変万化、生々流転…\nなれど蛇の化生を好みたるは\nこの『装備』が故でありんしょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1297009, 129731, 0, 0.0, 1, "恐れることはありんせん\nお前さまはこの地を救いし英雄\nわっちらが牙を剝く道理など\n何処にもありませぬ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1297010, 129731, 0, 0.0, 1, "見やるのみでは興も醒めよう\nなれば騎士殿、望みの席を\n御用意差し上げ申す\nわっちの隣など如何かえ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1297011, 129701, 0, 6.9, 2, "今日は騎士殿が生まれた日\n宴の席を設けさせてありんす\nわっちらには恩義があります故\nこれくらいはさせておくんなんし", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1297012, 129731, 0, 0.0, 1, "お前さまの心意気に応える\n贈り物ができれば良かれど…\n頭を撫でてほしい？　…フフ\nほんに無欲でありんす喃", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1298001, 129801, 0, 4.6, 2, "なんだ、もしかしておまえも\n訓練に参加したいのか？\nはっ、いい度胸じゃねえか！\n望み通りしごいてやるよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1298002, 129801, 0, 4.6, 1, "厳しいだけじゃ新入りたちの\nモチベーションが続かねえ\nクッキーでも差し入れするか\nついでにおまえにも一つやるよ♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1298003, 129801, 0, 4.1, 4, "新入りに魔法を教えてほしいって\n言われたが…そっちはからきしだ\n仕方ねえ…魔法学園の教科書で\n一から勉強してみるとするか…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1298004, 129801, 0, 7.4, 2, "おまえはあたしの最初の教え子だ\nつまり、ある意味では\n新入りたちの先輩ってわけだ\nシャンとしろよ、先輩♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1298005, 129801, 0, 8.2, 5, "教官を続けてると\n早起きが習慣になっちまってな\n…あたしに朝起こしてほしい？\nバ、バカ！　自分で起きやがれ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1298006, 129831, 0, 0.0, 1, "おう、来たか…ゆっくりしてけよ\n今日も訓練は無事終わった\n今はオフでな。大したもてなしは\nできねえが歓迎するぜ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1298007, 129831, 0, 0.0, 1, "おまえにも一杯淹れてやるよ♪\n好きな砂糖とミルクの量は\n覚えてっからちょっと待ってろ…\nああ、ついでにケーキはどうだ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1298008, 129831, 0, 0.0, 1, "ずいぶん本が多いね、って？\n鍛え方だとかの勉強してんだよ\nあたしらしくねえが…\n教官として責任があるからな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1298009, 129831, 0, 0.0, 1, "教官不足が深刻でな…あ～疲れた\n…少し仮眠した方がいいって？\n…そうするか。おまえにだけは\n寝顔を見せられるからな…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1298010, 129831, 0, 0.0, 1, "なかなかいい部屋だろ？\n…ってよく考えりゃ、男を\n入れるのはおまえが初めて…！？\nそ、掃除するから一回外出ろ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1298011, 129801, 0, 6.9, 2, "誕生日おめでとさん！\n特別におまえ専用の\n訓練プログラムを用意してやった\nたっぷり楽しんでくれていいぜ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1298012, 129831, 0, 0.0, 1, "訓練でずいぶん靴がボロボロだな\n誕生日だし新しいの買ってやるよ\n遠慮なんてすんなよ？\nこれは教官命令だ、なんてな♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1299001, 129901, 0, 8.4, 7, "「壊し屋」が教え導くなんて\n冗談みたいですが、やるからには\n本気でやりますわ。たとえそれで\n壊れたとしても。クスクス…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1299002, 129901, 0, 7.6, 2, "戦闘訓練から試験勉強\nお菓子作りまで、なんでも教えて\n差し上げますわ。手取り足取り\nじっくりと…クスクス", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1299003, 129901, 0, 9.4, 6, "訓練に疲れて指一本動かなくても\n大丈夫です。私が調合した\n特製ドリンクで疲れなど…あら？\n遠慮などしなくていいのに…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1299004, 129901, 0, 6.7, 5, "あなた様のために特別な訓練を\n考えましたわ。早速参りましょう\n誰も追ってこれない遠くの地で\n二人だけの秘密のレッスンを…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1299005, 129901, 0, 7.0, 8, "人を鍛えるばかりではなく\n私自身も強くあらねばいけません\nあなた様に近づく全ての邪魔者を\n完全に叩き潰すためにも…！", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1299006, 129931, 0, 0.0, 1, "選びなさい。一生地を這い\n生きていくのか、それとも\nかすかな光を求めて私の下で\n地獄の日々を送るのかを", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1299007, 129931, 0, 0.0, 1, "面を上げなさい\nおや、口をきいていいと\n言った覚えはありませんが？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1299008, 129931, 0, 0.0, 1, "クスクス、なかなかいい顔ですわ\nでは、ご褒美に追加の訓練を\nいたしましょう。きっとさらに\nいい顔になりますわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1299009, 129931, 0, 0.0, 1, "私の下で鍛えられた身と心は\n全てあのおかたのために\n使いなさい。それこそが力ある者\nの使命なのですから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1299010, 129931, 0, 0.0, 1, "見ていてください、あなた様\nいずれあなた様の下に屈強な者\nたちが集う日のため、私も精進\nしてまいりますわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1299011, 129901, 0, 3.5, 5, "お誕生日おめでとうございます\nというわけで本日は特別講習\n「エリコの全て」をしっかりと\nお教えいたしますわ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1299012, 129931, 0, 0.0, 1, "あなた様の誕生日、それすなわち\n奇跡が顕現した日ということ\nなのでこの日を記念日にすべく\n色々手を打っていますわ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1300001, 130001, 0, 6.2, 2, "妹たちの婿にゃまだ頼りないねぇ\nアタシがビシバシしごいてやるよ\nなーに、遠慮はいらねぇさ\nボーヤはアタシの義弟だからね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1300002, 130001, 0, 2.8, 6, "飯食いに行くよ。ついてきな！\nアアン？　今手持ちがないだぁ？\nンなもん奢りに決まってんだろ\n宵越しの金は持たねぇ主義でね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1300003, 130001, 0, 7.9, 7, "どっちの婿になるか決めたかい？\n姉のアタシが言うのもなんだが、\n一世一代の大博打だねぇ♪\nさあ…腹括りな！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1300004, 130001, 0, 5.3, 3, "甘っちょろい男は好きじゃないが\nボーヤのことは嫌いじゃないねぇ\n…アン？　勘違いするなよ？\nもちろん妹の相手としてって話だ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1300005, 130001, 0, 5.5, 7, "ボーヤの心を奪うのはちょいと\n骨が折れるだろうねぇ…フフッ\n惚れた腫れたに興味はねぇが\n盗賊としては血が騒いじまうよ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1300006, 130031, 0, 0.0, 1, "バンディシャーク号に\n進めない道なんてないのさ\nどんな悪路も乗り越える快進撃を\n特等席で見せてやるよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1300007, 130031, 0, 0.0, 1, "嵐の前の静けさってやつだね\nこういう雰囲気は嫌いじゃない\n…さて、ここから見えるモンを\n根こそぎ奪ってやろうじゃないか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1300008, 130031, 0, 0.0, 1, "ボーヤにゃ特別に\nここからの景色を見せてやる\n妹たちにも内緒だよ？\nアタシのお気に入りさ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1300009, 130031, 0, 0.0, 1, "なんだい…こうやって隣に立つと\nボーヤ、存外背が高いじゃないか\n…あまり成長されると、妹に\nやるのがもったいなく感じるねぇ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1300010, 130031, 0, 0.0, 1, "なあボーヤ。世話になった礼に\nアンタが出した賽の目の数だけ\nいいコいいコをしてやるよ\nさあ、一世一代の運試しだ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1300011, 130001, 0, 4.4, 1, "今日はボーヤの誕生日だって？\n贈り物は何がいいんだい。\n希少な宝石でも高価な調度品でも\nなんでも盗ってきてやるよ♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1300012, 130031, 0, 0.0, 1, "義弟の年に一度の晴れ舞台！\nバンディシャーク号で\nド派手にバースデーパレードと\n洒落こもうじゃないか！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1301001, 130101, 0, 3.6, 5, "騎士さんはいつも\n助けてくれますよね…\nど、どうしてなんでしょう\nこんなだめだめな、わたしを…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1301002, 130101, 0, 5.3, 1, "誰かの助けになれるって\n素敵なこと、ですよね\n…姉さんたちといる時は、\nあんまりできないんですけど…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1301003, 130101, 0, 2.5, 2, "服がほつれちゃってますよ\nちょっと貸してください\nすぐ直しちゃいますから…\nこういうの慣れてるんです♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1301004, 130101, 0, 5.0, 4, "お食事がまだでしたら\nよければご一緒にいかがですか？\nあ…えと、もしお嫌なら、\n全然断っていただいても…！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1301005, 130101, 0, 0.0, 4, "いつも家族がご迷惑を\nおかけしてるので…\n何か、お詫びができたらと\n思うんですけど…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1301006, 130131, 0, 0.0, 1, "今日のカレーは自信作なんです\nたくさん作ったので\nお腹いっぱい食べてください\nお肉、多めにしておきますね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1301007, 130131, 0, 0.0, 1, "こうやって…誰かと一緒に\n料理するのっていいですね\n二人とも手伝ってくれないから\n実は…ちょっと憧れてたんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1301008, 130131, 0, 0.0, 1, "姉さんとフブキちゃんって\n料理の好みがまるで違うから…\n献立作りはよく悩んじゃいます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1301009, 130131, 0, 0.0, 1, "ハ、ハートのニンジンは…\n深い意味は、ないですよ？\nお腹に入れば、一緒ですしっ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1301010, 130131, 0, 0.0, 1, "騎士さんが見ててくれるだけで…\nちょっとだけ自信が持てるんです\nだから…叶うならですけど…\nまた、食べにきてもらえたら…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1301011, 130101, 0, 1.8, 2, "お、お誕生日っ、\nおめでとうございます…！\n大したものじゃないですけど…\nわたしからのプレゼント…です", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1301012, 130131, 0, 0.0, 1, "今日は奮発して、ちょっといい\nお肉を使っちゃいました♪\n騎士さんのお誕生日ですから\nお口に合えばいいですけど…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1302001, 130201, 0, 6.3, 1, "ザコお兄さ～ん♡\n…いひひっ、呼んでみただーけ♡\nザコって呼ばれてフツーに\n振り向くザコお兄さん面白～い♡", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1302002, 130201, 0, 3.4, 2, "次はナニ作ろっかな～♪\n…あっ、ザコお兄さんをつよつよ\nにするメカとかってどーお？\n火力装甲盛り盛りメカお兄さん♡", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1302003, 130201, 0, 8.2, 5, "なんでもハッキリしてたほうが\nわかりやすくていいよねー♡\nお兄さんもわかりやすくて好き♡\nザコってハッキリしてるから～♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1302004, 130201, 0, 8.1, 5, "勉強は好きだけどガッコはだる～\nザコ教師に教わることねーし？\nそんなのより、お兄さんを\nいぢってるほーが楽しーもん♡", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1302005, 130201, 0, 6.8, 2, "設計図書くのって楽しいけど\n頭使うから疲れるんだよね～\nとーぶんほきゅーきぼー♡\n甘いのはやくもってきてー♡", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1302006, 130231, 0, 0.0, 1, "きゃはははっ♡\nザコお兄さんみ～っけ♡\n今日はどうやって\nお兄さんで遊ぼうかな～♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1302007, 130231, 0, 0.0, 1, "どおどお？\nこの『フライングウィーゼル』\nちょーカッコイイでしょ～♡\n夜は目が光るし、自爆装置も…♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1302008, 130231, 0, 0.0, 1, "油断してるとバズーカで…\nどっかーん♡\n…いひひっ、うっそー♡\nびびった？　びびったぁ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1302009, 130231, 0, 0.0, 1, "お兄さんのコト、大好きだよぉ？\nからかうのが、だけどねー♡\n楽しーし気分いーし、\nアイデアが湧いてくるんだもん♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1302010, 130231, 0, 0.0, 1, "ザコお兄さんも乗ってみる？\n狭いけど、ぼくがザコお兄さんの\nヒザに座ればいけるっしょ♡\nぼくの専用シートにしてあげる♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1302011, 130201, 0, 0.0, 2, "はっぴーばーすでー！\nいひひっ、びっくりした？\nザコお兄さんの誕生日覚えてて\nえらいでしょ♪　ほめていーよ♡", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1302012, 130231, 0, 0.0, 1, "お誕生日おめでと～！\n誕プレはぁ～…ザコお兄さんを\nもっとザコザコにするメカ♡\nぽちっと～♡", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1307001, 130701, 0, 5.3, 7, "のんびりとお花を観賞する\nこれって大人の趣味ですよね\nそれでは大人な私があなたに\nお花の楽しみ方を教えましょう！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1307002, 130701, 0, 1.8, 2, "このお花は…なるほど！\n気になったお花をすぐに図鑑で\n調べる。これがお勉強という\nものですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1307003, 130701, 0, 6.4, 2, "この衣装、かわいいですが\n子どもっぽくないですか？\nアイドルみたい？　それは\nちょっと褒めすぎですよ…えへへ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1307004, 130701, 0, 7.9, 5, "え、私からいい匂いがする？\nさっきまでお花の世話をして\nいたからでしょうか…って\nもう！　かがないでください！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1307005, 130701, 0, 7.9, 5, "今日は山でお花を探します\nなのであなたが迷子にならない\nように、手を繋いであげます\nこ、これは特別なんですからね！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1307006, 130731, 0, 0.0, 1, "うららかな日和にきれいなお花畑\n心までポカポカしちゃいます\nいつまでもこんな幸せな時間が\n続くといいですね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1307007, 130731, 0, 0.0, 1, "この花冠、うまくできてると\n思いませんか？　えへへ\nあなたに一番最初に見せたくて\nがんばりました♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1307008, 130731, 0, 0.0, 1, "きれいなお花を見ていると\n気持ちが安らい、で、すぅ…はっ\nね、寝てませんよ！　そこまで\n気が緩んでませんから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1307009, 130731, 0, 0.0, 1, "花冠、上手に作れてますね\nどうせだれか女の人にあげ…え？\nわ、私にですか？\nあ、ありがとうございます…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1307010, 130731, 0, 0.0, 1, "このお花畑は私のとっておきの\n場所なんです。私とあなたしか\n知らない秘密の場所。絶対に\nぜーったいに内緒ですからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1307011, 130701, 0, 0.0, 2, "お誕生日おめでとうございます\nこれ、プレゼントのお花です\nお誕生日に合わせたお花を\nちゃんと用意したんですよ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1307012, 130731, 0, 0.0, 1, "お誕生日のケーキ、作って\nみたんです。食べられるお花を\nいっぱい散らしてみました\nどうぞめしあがれ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1308001, 130801, 0, 4.4, 2, "メイド服でもお仕事着でもない\n服を着るのって、なんだか\n少し、恥ずかしいですね…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1308002, 130801, 0, 5.4, 1, "えへへ、どうですか？\n花衣装を仕立て直したんです\nたくさん思い出の詰まった服♪\n大切にしますね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1308003, 130801, 0, 4.9, 2, "救護院にもみんなで植えた\nお花がたくさんあるんですよ\n今度見に来てくださいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1308004, 130801, 0, 6.7, 5, "この服装、よく似合ってるって\nお嬢さまに褒めていただきました\n…あの、あなたはメイド服と\nどちらがお好きでしょうか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1308005, 130801, 0, 6.0, 5, "あなたの側にいると、花たちも\nいつもより元気な気がします♪\n…もしかして、私の心が\n伝わっちゃってるのかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1308006, 130831, 0, 0.0, 1, "奇麗ですね…\n草花と、土と、暖かな風…\n春の匂いでいっぱいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1308007, 130831, 0, 0.0, 1, "ほら！　見てください！\nとっても奇麗に咲いてます！\nお嬢さまや、救護院のみんなにも\n見せてあげたいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1308008, 130831, 0, 0.0, 1, "花祭りやコンテストだけでなく\nあなたと過ごした日々…\nこの服には思い出が\nいっぱいなんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1308009, 130831, 0, 0.0, 1, "ええと…そんなにじっと\n見つめられると、照れちゃいます\nだからその…このお花を\n見つめてくれませんか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1308010, 130831, 0, 0.0, 1, "奇麗？　はい、私も奇麗な\nお花畑だと思います！　…て\nお、お花じゃなくて私ですか？\nじょ、冗談はよしてくださいぃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1308011, 130801, 0, 7.7, 1, "お誕生日おめでとうございます！\n今から、お花に囲まれてのお茶会\nなどいかがでしょう？　精一杯\nお祝いさせていただきます♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1308012, 130831, 0, 0.0, 1, "今日は、特別な日…\nあなたのお誕生日ですから\n日頃の感謝と私の気持ちを込めた\n特別な花でお祝いさせてください", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1309001, 130901, 0, 0.0, 2, "ヒーローさんのおかげでついに\nグランドリームパークがオープン\nできましたっ♪　感謝の気持ちに\n全財産の宝石をどうぞどうぞ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1309002, 130901, 0, 4.9, 2, "あなたと一緒なら、辛い時でも\n自然と笑顔になれちゃいます\nヒーローさんのお隣は\nわたしの夢の国、ですねっ♪", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1309003, 130901, 0, 6.8, 7, "ここを世界一のパークにしたい…\n世界で一番笑顔の溢れるパークに\n…なんて調子に乗っちゃいました\nすみませんですます～っ！", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1309004, 130901, 0, 6.6, 1, "アトラクションの待ち時間も\nヒーローさんと並んでると\nあっという間に感じちゃいます\nこれってなんででしょうか…？", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1309005, 130901, 0, 0.0, 4, "お、おかしいですます…\nゆったりしたアトラクションでも\nヒーローさんと一緒に乗ると\nドキドキしちゃって…？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1309006, 130931, 0, 0.0, 1, "見てください♪　見渡す限りの\nお客さんの笑顔ですます♪\nわたしの夢、ヒーローさんの\nおかげで叶っちゃいました～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1309007, 130931, 0, 0.0, 1, "ヒーローさん、冒険の合間にまた\nこのパークに寄ってくださいね…\nヒーローさんを笑顔にするため\nわたしはここでお待ちしてます♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1309008, 130931, 0, 0.0, 1, "ヒーローさんが宝石を受け取って\nくれないので考えました…あの\nわたしのこと貰ってくださいっ！\nはわっ、別に変な意味ではなく！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1309009, 130931, 0, 0.0, 1, "かばばん君かわいいですよね♪\nえ…クルルちゃんもかわいい…？\nほわぁぁっ！？　やめてください\nドキドキで倒れちゃいます～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1309010, 130931, 0, 0.0, 1, "ヒーローさん聞こえますかー！？\n…歓声で聞こえてないですよね…\nではでは…ヒーローさんっ！！\nわたしヒーローさんのことが─", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1309011, 130901, 0, 2.6, 8, "お誕生日おめでとうございます！\n今日という偉大な日を記念して\n宝石で作ったヒーローさん像を\nエントランスに飾りますですっ♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1309012, 130931, 0, 0.0, 1, "今日はヒーローさん誕生日記念の\n特別なパレードを開催しますっ♪\n楽しくて、夢と笑顔でいっぱいの\nすてきな一年にしましょうねっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1310001, 131001, 0, 7.6, 2, "うちが遊園地にかかわるなんて\n不思議な縁もあるもんやなぁ\n任されたからには、うちも精一杯\nお仕事してみせますえ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1310002, 131001, 0, 5.8, 2, "夢もワクワクもいっぱいで\nみんながキラキラしとる\nうち、ドリームパークも\nサーカスも好きやわぁ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1310003, 131001, 0, 9.5, 7, "この衣装、最初は恥ずかしいし\nうちに似合うかな思うてたけど…\n着てるうちに勇気が湧いてきたわ\n自信もって堂々とします", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1310004, 131001, 0, 2.8, 1, "王子はんも楽しんでってや\nみんなの楽しそうな姿こそが\nうちにとっては一番うれしくて\n幸せなことなんよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1310005, 131001, 0, 11.2, 2, "ショーを見る王子はん、\n目がキラキラしてて素敵やなぁ\n…うちも同じ顔してる？\nほんならお揃いやね♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1310006, 131031, 0, 0.0, 1, "ようこそ、ドリームパークへ！\n…なんやくすぐったいわぁ\nでも恥ずかしがってたらあかんね\nうちがみんなを笑顔にするんや", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1310007, 131031, 0, 0.0, 1, "マホマホサーカスは\nかわいいがいっぱいなんよ\nこの子らが上手くできたら\nいっぱい拍手して褒めたってな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1310008, 131031, 0, 0.0, 1, "ブランコの上って高いわぁ…\nでも怖くはあらへんよ\n一人じゃあらしまへんし\n王子はんも見てくれるさかい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1310009, 131031, 0, 0.0, 1, "演目はまだまだ盛りだくさんや\n王子はんの楽しむ姿を\nうちにもいっぱい見せて\nおくれやす♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1310010, 131031, 0, 0.0, 1, "王子はん、うちの演技する姿\n最後までじっくり見ていってな\nそのために今日までたくさん\n練習してきたんどす", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1310011, 131001, 0, 5.1, 1, "王子はん、誕生日おめでとさん♪\nうちからはお祝いとして\n特別なショーをプレゼントや\n楽しんでいっておくれやす♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1310012, 131031, 0, 0.0, 1, "今日は王子はんの誕生日やさかい\nうちがエスコートしますえ\n夢とメルヘンがいっぱいの時間を\n一緒にすごしまひょ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1311001, 131101, 0, 4.7, 5, "おっすー、騎士きゅん\n楽しんでるー？\nせっかくだし、二人で\n忘れられない夏にしよっか♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1311002, 131101, 0, 6.5, 2, "水着って何のために\nあると思うー？\n泳ぐため？　違いまーす\nぬるぬるするタメだよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1311003, 131101, 0, 5.2, 2, "日焼け止めより\nイイもの塗ってあげるねー\nべたっとタイプとぬるっとタイプ\nどっちのスライムが好きー？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1311004, 131101, 0, 5.9, 2, "クソ暑くても、好きぴといたら\n全然ムテキだわ～\nきゃわゆさは暑さも\n吹きとばすんだね～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1311005, 131101, 0, 9.2, 5, "騎士きゅんの笑顔って\n真夏の太陽なみに\nまぶしいし、マジ栄養だわー\nもっとおねえちゃんに見せて～♡", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1311006, 131131, 0, 0.0, 1, "騎士きゅん、こっちこっち～♪\n浮き輪もジュースもお城もあるし\nまだまだたっくさん遊ぼーねー\nとりま、ぬるぬるしてく～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1311007, 131131, 0, 0.0, 1, "なんか後ろの方で\nカリザきゅん騒いでんねー\n気になるし、後で\nランド探索しよっか～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1311008, 131131, 0, 0.0, 1, "どーする、もっかい海入る？\nスライムの浮き輪あるし\n騎士きゅんが気になるトコ\n連れてってあげるよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1311009, 131131, 0, 0.0, 1, "騎士きゅん、ちゃんと\n水分ホキューしてるー？\nネアおねえちゃんのジュース\nちゅっちゅしていいんだよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1311010, 131131, 0, 0.0, 1, "夕方のビーチで隣に好きぴって\nめっちゃエモなシチュじゃ～ん♪\nせっかくだし、もっとくっついて\nチルしよーよ？　ね～♡", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1311011, 131101, 0, 9.6, 1, "騎士きゅん、おたおめー！\n今日は、ネアおねえちゃんが\nアゲてくんでよろー\nとりま、プチョヘンザしとくー？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1311012, 131131, 0, 0.0, 1, "カリザきゅんランド招待券を\n騎士きゅんにプレゼントー♪\nもしかすっとバースデーパレード\nあるかもよー？　楽しみだね～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1312001, 131201, 0, 6.8, 1, "水鉄砲大会しませんか？\n濡れたら溶ける水着で\nバトルロワイヤル★\nきっと楽しいですよー？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1312002, 131201, 0, 10.8, 2, "水泳に、砂のお城、ビーチバレー\n海の家でお昼食べたり、\n夜の浜辺で花火も素敵ですよねー\nやりたいことがいっぱいです★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1312003, 131201, 0, 7.1, 5, "そんなに見つめちゃダメですよ★\n…なーんて、冗談ですよー\n騎士さんになら見せてあげます\nわたしの、どんなところでも…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1312004, 131201, 0, 3.0, 5, "人気者な騎士さんを独占★\n誰かに見つかって咎められないか\nハラハラドキドキして…\nイケナイことしてる気分です…♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1312005, 131201, 0, 5.7, 2, "忘れられない夏の思い出、\nたっくさん作りましょうね♪\n騎士さんのこと、もーっと好きに\nなりたいので…くすっ★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1312006, 131231, 0, 0.0, 1, "寒かったら言ってくださいね\nわたしのぺんぽこりんケープ\n貸してあげます★\nこれもイイコトですよね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1312007, 131231, 0, 0.0, 1, "キレイですねー、海\nぷかぷか浮いて、海の果てまで\n流されてみたいかも…ふふっ★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1312008, 131231, 0, 0.0, 1, "ばしゃばしゃばしゃーっ\nふふ、冷たくって気持ちいいー♪\nたまに子供っぽいことするのも\nちょっとアオハルですよね★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1312009, 131231, 0, 0.0, 1, "はい、あーん♪\n海で食べさせあいっこって、\nちょっとコイビトっぽいですね？\n「キュン★」ってしちゃいそー", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1312010, 131231, 0, 0.0, 1, "このままテレポーテーションで\n無人島に駆け落ちしちゃいますか\n…ふふ、冗談です、じょーだん★\n言ってみたかっただけですよー", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1312011, 131201, 0, 10.9, 1, "騎士さん、ハッピーバースデー♪\nお祝いに花火とかどうでしょう？\n打ち上げて、どっかーん！　って\nえっ、危ない？　…はーい★", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1312012, 131231, 0, 0.0, 1, "お誕生日おめでとうございます♪\n素敵な誕生日だったなって\n思ってもらえるように、たくさん\nイイコトしてあげますね★", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1316001, 131601, 0, 0.0, 5, "このように肌を見せるのは\nやはりどうしても抵抗が…\nですがこれも、修行の一環と\n思えば…うぅん…", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1316002, 131601, 0, 7.1, 2, "恥ずかしながら…\nわたくしは海に関しましても\n知らないことばかりなのです\n兄様、どうかひと夏のご教示を", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1316003, 131601, 0, 8.0, 1, "不思議と着心地がしっくり\nくるのです…もしかすると\n自分で選んだものよりも\nさすがの慧眼ですね、ミソラ様…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1316004, 131601, 0, 5.4, 7, "あまり見つめられますと、その\n気恥ずかしくて…ですが\nうれしくも感じてしまいます\nこれはなぜ、なのでしょう…？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1316005, 131601, 0, 6.7, 5, "未熟なわたくしとは違い\n兄様の水着姿はとても精悍です\n…はっ…！　し、失礼しました\nまじまじと見つめるなど…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1316006, 131631, 0, 0.0, 1, "ああっ、すっかり砂まみれに…！\nこのようなあられもない姿を\n兄様にお見せするだなんて…\n砂に埋もれて隠れたいです…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1316007, 131631, 0, 0.0, 1, "右往左往…翻弄されております\nスイカ割りという夏の遊びに…！\n本当にお子様からお年寄りまで\n嗜まれているのですか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1316008, 131631, 0, 0.0, 1, "どうか今一度、挑戦の機会を！\nなんとしても兄様に…！\nわたくし自ら割ったスイカを\n召し上がっていただきたいのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1316009, 131631, 0, 0.0, 1, "砂でしたら自分ではらえます\n兄様のお手を煩わせるわけには…\nダ、ダメです、兄様そんなところ\n足の裏はお許しください…っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1316010, 131631, 0, 0.0, 1, "スイカを狙う腕が震えていた…\nなんて、兄様はわたくしの迷いも\nすべてお見通しですね。…もしや\nわたくしの胸に秘めた想いも…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1316011, 131601, 0, 0.0, 2, "本日は兄様のお誕生日…！\n日頃の感謝を込めて精一杯\nおもてなしをいたします\nなんなりと仰ってくださいね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1316012, 131631, 0, 0.0, 1, "兄様のお誕生日という晴れの日…\nわたくしの成長した姿をご覧\nいただけるよう研鑽いたしました\nその、喜んでくださいますか…？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1317001, 131701, 0, 4.9, 2, "あたしが水着を着る羽目に\nなるとは思わなかったわ…\nこうなったら遠慮なく\n海を楽しませてもらうわよ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1317002, 131701, 0, 10.1, 6, "この背中の羽、目立ってるわよね\nどうしてかしら、水着着てると\n常にこうなっちゃうのよね\n…気合が入ってるから…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1317003, 131701, 0, 5.6, 1, "ちょっと待ちなさい\n海に入る前に、準備運動はした？\nあたしが近くにいるからには\nしっかりあんたを監督するからね", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1317004, 131701, 0, 8.0, 6, "こうして二人でいると\n昔を思い出してあんたの肩に\n乗りたくなるのよね…\n…きゃっ、肩車じゃないっての！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1317005, 131701, 0, 3.6, 2, "あたしが楽しんでるかって？\nあんたを見てるだけで十分よ\n気なんて遣わなくていいから\n楽しんでる顔をもっと見せなさい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1317006, 131731, 0, 0.0, 1, "はい、お疲れさま\nこれは夢かって？　違うわよ♪\nあたしとあんたの二人だけの時間\n楽しみましょ、最高の夏を", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1317007, 131731, 0, 0.0, 1, "そういえば二人きりで\n遊ぶなんて、久しぶりよね\n…覚えてなくてもいいわ\nあたしがちゃんと、覚えてるから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1317008, 131731, 0, 0.0, 1, "綺麗ね…あんたもそう思わない？\n…あたしばかり見てどうしたのよ\nああ、あたしに見とれちゃった？\nちょっと、なんとか言いなさいよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1317009, 131731, 0, 0.0, 1, "貴重なひと夏の海\nあたしと一緒でよかったわけ？\n…ふぅん。そ\nなら、忘れられない夏にしましょ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1317010, 131731, 0, 0.0, 1, "ねぇ…お願いがあるんだけど…\n今日だけでいいから\n…フィオって呼んでくれない？\n………………うん、ありがと♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1317011, 131701, 0, 0.0, 2, "はい、おめでと\n誕生日を海でお祝いなんて\nなんだか贅沢ね？\n遠慮なんてしなくていいわよ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1317012, 131731, 0, 0.0, 1, "あんたの誕生日ね、おめでとう\nあたし自身にとっても特別な日よ\nこれからも一緒に\n歩んでいけるといいわね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1318001, 131801, 0, 7.2, 2, "ふぅ、こうも暑いとかき氷なんか\n食べたくなっちゃうね～\n…え～、買ってきてくれるんだ？\n気が利くね～☆　ありがと♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1318002, 131801, 0, 8.4, 2, "せっかくのバカンスだし\nたまには羽を伸ばさないとね～\n文字通りの意味かって？　ふふ♪\nそうだね、翼も広げちゃおう☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1318003, 131801, 0, 0.0, 6, "気にせず日焼けできるのって\n若者の特権って感じで\nちょっとうらやましいな～\n私も思い切って焼いてみよっか？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1318004, 131801, 0, 7.9, 2, "オ・ト・ナの夏休み…\n君も一緒に過ごしちゃう？\nふふ…じゃ、何からしよっか♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1318005, 131801, 0, 3.4, 1, "くらくらしちゃうくらい夏だね～\n暑さのせいってことで\nパーっとなにもかも全部\n吹っ飛ばしちゃおっか…なんてね", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1318006, 131831, 0, 0.0, 1, "夏の日差しって強烈だけど\nそれがたまらなく好きなんだ～☆\n白い砂浜も青い海も…\n全身で夏を感じられてさ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1318007, 131831, 0, 0.0, 1, "日焼け止めって、こまめに\n塗り直さなきゃいけないのが\n面倒だよね～…\n君、手伝ってくれないかな～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1318008, 131831, 0, 0.0, 1, "そんな日の当たるとこだと\nのぼせちゃうよ？\nちょっと日陰で涼もっか♪\nほら、遠慮しないでおいで～☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1318009, 131831, 0, 0.0, 1, "この空と海の青さは印刷じゃ\n出せないね～…しょうがないから\n記憶にしっかり刻んでおこっか\n今日の思い出と一緒に♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1318010, 131831, 0, 0.0, 1, "お姉さんとひと夏の思い出、\n作っちゃおっか♪\nふふ、どんな想像したのかな\nちょっと聞かせてよ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1318011, 131801, 0, 5.6, 2, "誕生日おめでと～☆\nお祝いにBBQでもしよっか♪\nカヤちゃんとイノリちゃんも\n呼んでパーッとやっちゃおう♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1318012, 131831, 0, 0.0, 1, "ハッピーバースデー☆\nお祝いに海の見えるレストランを\n予約しておいたんだ～\n今夜、連れてってあげるね～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1319001, 131901, 0, 4.0, 2, "みんなで遊びに行くのって、\nすごーく贅沢ね！\n『さまぁばけぇしょん』を\nもっともっと楽しまなくちゃ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1319002, 131901, 0, 4.4, 2, "この格好じゃないと参加\nできない行事があるのかしら？\n一回きりかもしれないから\nみんなで行ってみましょう！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1319003, 131901, 0, 4.7, 2, "ランドソルの空気にも\nだいぶ馴染めてきてるわ\nみんなが優しくして\nくれるおかげね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1319004, 131901, 0, 7.3, 2, "夏の過ごし方、いろんな遊び、\nたくさんのことを教えてくれて…\nこの世界のみんなには、\nいくら感謝しても足りないわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1319005, 131901, 0, 9.4, 2, "私、来たのがこの世界でよかった\nかけがえのないお友だち、\n素敵な思い出、他にもたくさん\n宝物ができたんだもの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1319006, 131931, 0, 0.0, 1, "森の香りと息づかいを感じる…\nなんだか、私の体の中を\n風が通り抜けていくみたいで\nすごーく安心するわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1319007, 131931, 0, 0.0, 1, "きっとこの景色は、ずーっと\n今日みたいにきれいなままね\n何十年、ううん、何百年経っても\n同じように、色あせないで…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1319008, 131931, 0, 0.0, 1, "もしかして、精霊たちが\n挨拶してくれているのかしら？\n少しだけ、みんなの場所に\nお邪魔させてもらうわね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1319009, 131931, 0, 0.0, 1, "いろんな景色を見せてもらって、\n私の世界が広がっていくみたい\n次は何が起きるんだろうって\n今からすごーく楽しみね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1319010, 131931, 0, 0.0, 1, "このきれいな景色のことや、\nみんなで一緒に過ごした\nかけがえのない時間も…\nちゃんと、覚えておいてね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1319011, 131901, 0, 2.2, 2, "お誕生日おめでとう！\n今日はみんなで一緒に楽しく\n笑いながら過ごしましょう\nしてほしいことは何でも言ってね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1319012, 131931, 0, 0.0, 1, "たまたま出会えた私たちが、\nこうして誕生日のお祝いまで…\nびっくり仰天しちゃうわね！\nこれからもよろしくお願いします", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1320001, 132001, 0, 6.1, 2, "ボクの名前はエキドナ\n『強欲の魔女』と、\nそう名乗った方がいいかな？\nああ、ここでは誰も知らないか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1320002, 132001, 0, 3.6, 4, "やれやれ\n周囲の視線を感じるね\nボクが可憐な乙女だからとはいえ\nじろじろ見るのはいただけないな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1320003, 132001, 0, 3.3, 1, "未知の世界とはなんて心躍るんだ\n君に案内を頼んでもいいかな\nボクの好奇心を満たすものを\n見せてくれると嬉しいな", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1320004, 132001, 0, 10.1, 4, "海辺での遊戯を教えてほしい\n目隠しをして視覚を遮断し、\nこの棒で丸い果物をたたき割る？\n…もっと軽い棒はないのかい？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1320005, 132001, 0, 8.6, 2, "どうしたんだい？　ボクになにか\n聞きたいことでもあるのかな\n…この衣装がよく似合ってる？\n君は正直者だね。好ましく思うよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1320006, 132031, 0, 0.0, 1, "ようこそ、ボクの茶会へ\nまずは歓迎の意を表して\nとびきり美味しいお茶を淹れよう\n溢さずきちんと飲み干すんだよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1320007, 132031, 0, 0.0, 1, "これが海…眺めているだけなんて\nもったいないね\n海の底まで観察しに行くとしよう\n君も一緒に行くかい？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1320008, 132031, 0, 0.0, 1, "この衣装の感想を聞きたいな\nこう見えてボクも乙女だからね\n褒め言葉をもらえるかどうかで\n一喜一憂したりもするんだよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1320009, 132031, 0, 0.0, 1, "異なる世界で\nくつろぐのも悪くはないね\n君もボクのいた世界に来るといい\nそんな未来も実に興味深そうだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1320010, 132031, 0, 0.0, 1, "君はとんでもないお人よしだね\n魔女を前にして、大したものだよ\n改めて、君なら受けてくれるかな\n──ボクとの、契約を", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1320011, 132001, 0, 6.5, 1, "今日は君の誕生日じゃないかな？\nもちろん知ってるさ。視たからね\nボクからは喝采と言葉を贈るよ\n誕生日おめでとう", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1320012, 132031, 0, 0.0, 1, "めでたい日にボクからの贈り物だ\nこのお茶を飲むといい\nなに、悪い物は入っていないよ\n悪い物は……ね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1321001, 132101, 0, 4.4, 2, "メイドの給仕というものは\n時と場所を選びません\nたとえ陽の下、海の中…です！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1321002, 132101, 0, 1.7, 1, "夏は暑いですけど\nレムは嫌いではありません\nスバルくんの名前にも\n夏が入っていますから", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1321003, 132101, 0, 0.0, 1, "せっかくの機会ですから\n鉄球も夏の装いにしてみました\n威力はそのままですが\n清涼感が上がっていますよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1321004, 132101, 0, 6.6, 5, "涼しげな装いの姉様は素敵ですが\nジロジロ見るのは失礼ですよ\nレムのことも見てた、ですか…？\nからかってはいけませんよ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1321005, 132101, 0, 6.7, 2, "海に入る前の準備運動は\nしっかりとされましたか？\nよければお手伝いしますよ\n大丈夫です、加減はしますので", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1321006, 132131, 0, 0.0, 1, "見てください、姉様！\n空も海もこんなに青くて綺麗で…\n…レムの髪の毛みたい、ですか？\n姉様…ありがとうございます！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1321007, 132131, 0, 0.0, 1, "風が強くなってきましたね…\n少し心配ですが、こうして姉様と\n手を繋いでいると、それだけで\nレムは安心できるんです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1321008, 132131, 0, 0.0, 1, "これだけの海を凍らせれば\nかき氷がたくさん作れそうですね\n塩辛いのが難点ですが…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1321009, 132131, 0, 0.0, 1, "あっ！　スバルくんたちが\n手を振ってますよ…あれ？\nなんだか高度がだんだんと低く…\nほっ…持ち直しました…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1321010, 132131, 0, 0.0, 1, "この景色と穏やかな時間が\nいつまでも続くように…\nレムも誠心誠意\n努めたいと思います", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1321011, 132101, 0, 2.5, 1, "お誕生日おめでとうございます\n日頃の感謝を込めて\nレムが腕によりをかけた\nおいしい海鮮料理をご用意します", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1321012, 132131, 0, 0.0, 1, "親しい人のお祝いができる喜びは\n住んでいた世界が違っていても\n変わりありませんね\nお誕生日おめでとうございます！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1322001, 132201, 0, 2.9, 2, "新米錬金術師ノゾミ！\n…なんてね。アイドルと違って\n分からないことばかりだけど\n新鮮でワクワクしちゃうね！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1322002, 132201, 0, 4.2, 2, "この衣装、着てるだけで\n成功率が上がりそうでしょ？\nうん、気合入ってきた！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1322003, 132201, 0, 10.2, 6, "錬金術師ってちょっと魔法少女\nみたいじゃない？　呪文もあるし\n錬れば錬るほど、ヒッヒ…\n…うーん、ちょっと違うかも", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1322004, 132201, 0, 6.0, 1, "コーヒーとお茶菓子のいい匂い\nがキミから……クンクン♪\n仕事が終わったら一緒に\nカフェに行こう？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1322005, 132201, 0, 6.2, 7, "まだアイドルの時みたいな\n自信はないけど、キミの力に\nなれるようにがんばるよ！\n目指せ、凄腕錬金術師！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1322006, 132231, 0, 0.0, 1, "アイドルでも錬金術師でも\n私の望みは変わらない\nキミを笑顔にしたいんだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1322007, 132231, 0, 0.0, 1, "研究と研鑽が必要なのは\nアイドルと一緒だね\n錬金術、奥が深いなぁ\nハマっちゃいそうだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1322008, 132231, 0, 0.0, 1, "お客さんの注文の品、一つ一つ\n心を込めて作ってるんだ。キミの\n注文も受けてみたいな\n何か欲しいものはある？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1322009, 132231, 0, 0.0, 1, "新作の香水を作ってるんだけど\nキミ、隣で見ててくれない？\n傑作が生まれる予感がする…って\nちょ、それは近すぎだよ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1322010, 132231, 0, 0.0, 1, "つ、ついに完成しちゃった…\n彼の匂いの香水…さっそく\n嗅いでみ…ひゃあ！　キ、キミ\nいつからそこに！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1322011, 132201, 0, 5.3, 5, "お誕生日おめでとう！　これ、\nお手製のポーション詰め合わせ\nいつも元気でいてほしいからね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1322012, 132231, 0, 0.0, 1, "お誕生日のお祝いに、香水を\n作ってみたんだけど…え？\nいい匂いって、私が？　キミの\n方が断然いい匂いだよっ…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1323001, 132301, 0, 0.0, 10, "私の錬金術、試してみる？\n大丈夫、私は絶対失敗しないわ！\n…ただまあ、成功しすぎて\n違う効果が出ることはあるけど…", 10, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1323002, 132301, 0, 5.6, 2, "もっと一緒にいたいから、あなた\nそっくりの生き物を錬成したり…\nなんてっ、冗談よ。だって本物が\n目の前にいるんだもの", 9, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1323003, 132301, 0, 3.9, 10, "イロハちゃんとソノさんを\n元通りにする秘薬…\n私がきっと完成させてみせるから\nもう少しだけ待っててね…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1323004, 132301, 0, 9.8, 13, "あ、今日も会いに来てくれたっ♪\nこれだけ毎日一緒にいるのって\nもう夫婦ってことでいいわよね…\nきゃー、なんちゃってーっ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1323005, 132301, 0, 5.2, 2, "ダーリンは魅力的だから、\n恋のライバルもたくさんね…\nいつか錬金術みたいに\nこの恋も大成功させてみせるわ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1323006, 132331, 0, 0.0, 1, "…今回の錬金術、いつもより少し\n成功しすぎたみたいね…！\nって、ゴーレムの私とあなた！\nもうちょっと離れなさーいっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1323007, 132331, 0, 0.0, 1, "私とあなた…俯瞰で見ても\n結構お似合いじゃないかしら…？\n二人で街を歩けばきっとカップル\nだって思われちゃうわよね…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1323008, 132331, 0, 0.0, 1, "運命の王子さまに出会ってから\n錬金術の腕が上がってるのよね…\nこれが愛のパワーってやつかな？\n…私、まだまだ成長できそう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1323009, 132331, 0, 0.0, 1, "錬金術は自分のために使わない…\nこれは自分のためじゃないわ…！\nみんなを笑顔にするための\n錬金術の修行よ！　ほ、本当に！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1323010, 132331, 0, 0.0, 1, "ふふ…頭の天辺から爪先まで\n完璧にあの人そのものだわ…！\nま、これくらいの再現は楽勝よ\n毎晩夢であなたと逢ってるから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1323011, 132301, 0, 8.3, 2, "禁忌の錬金術で私そっくりの\n錬成生物を作ったらかわいがって\nくれるかしら？　…じゃなくて！\nお誕生日おめでとう、ダーリン♪", 9, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1323012, 132331, 0, 0.0, 1, "誕生日おめでとう…頬を染め呟く\n私を、あなたは優しく抱き寄せ…\nよし、お祝いシミュレーションは\n完璧だわ…ふふふ…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1324001, 132401, 0, 2.9, 10, "子どもは風の子、元気の子、\n外で遊ぶのが一番、でしょ？\nだから、お勉強とかお手伝いより\n好きに遊ばせるのがいいと思う！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1324002, 132401, 0, 4.5, 2, "イロハが肩もんであげると\nソノちゃんすっごい喜ぶの\n途中で面白い声上げるから\nイロハも肩もみ楽しいんだ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1324003, 132401, 0, 3.0, 4, "今日は何して遊ぼうかな～\nルイちゃんのコレクションが\nまた増えてないかチェック…は、\n昨日もうやったしなぁ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1324004, 132401, 0, 6.5, 8, "おじちゃんも一緒に稽古する？\nじゃあ、どっちが早く世界一に\nなれるか勝負しよっか！\nえへへ、燃えてきた～っ！", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1324005, 132401, 0, 4.7, 2, "今日はイロハがおじちゃんと\n遊んであげるよ。何しよっか？\n虫捕り？　釣り？　それとも\n稽古？　どれもオススメだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1324006, 132431, 0, 0.0, 1, "森はイロハのフィールドだよ！\nおっきな岩とか高い木とか\nぜーんぶ使って飛び回るの、\nアスレチックみたいだよねっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1324007, 132431, 0, 0.0, 1, "虫捕りはただの遊びじゃないよ\n虫の動き、考え、呼吸…\n全部を読んで網を振る真剣勝負\n…とそれっぽいこと言ってみた♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1324008, 132431, 0, 0.0, 1, "相手がどんなに強くても、\nイロハは全力で戦うだけだよ\nそれがイスルギ流の名を\n背負うってことだから…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1324009, 132431, 0, 0.0, 1, "ソノちゃんの体、虫捕りだと\nおっきいお胸が邪魔なんだよね…\nおじちゃんが支えてくれたら\n楽になるかな？　え、だめ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1324010, 132431, 0, 0.0, 1, "イロハは今が育ち盛り！\nよそ見してたらあっという間に\n成長しちゃうかもだよ\nだからずーっと見ててね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1324011, 132401, 0, 5.9, 6, "おじちゃんの誕生日のお祝いに\nお小遣いでケーキ買ったよ！\n遠慮なく食べてね！　あ、でも\nローソクはイロハが消したーい♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1324012, 132431, 0, 0.0, 1, "えへへ、誕生日おめでとう！\nプレゼントにクワガタあげるね！\nミヤマ、ヒラタ、どれにする？\n一押しはアルケスツヤクワガタ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1325001, 132501, 0, 14.7, 2, "今日もお疲れ～…って、こら\n頭を撫でようとしてくんなって…\nつい？　や、私の方が年上だから\n26歳の大人の女なんでよろしく", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1325002, 132501, 0, 10.4, 4, "いやー、すごいよこの身体\n朝めっちゃ早く目がさめるし\n寝起きも全然つらくないからね\nその分夜はすぐ眠くなるけど…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1325003, 132501, 0, 5.1, 2, "お店やってると手が欲しい時が\nあるんだよね～、ルイちゃんも\n君なら雇っていいって言ってるし\n正ギルドメンバーとして、どう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1325004, 132501, 0, 11.8, 7, "ハイ、クイズです\n私はイロハ？　それともソノ？\n正解者にはエナドリを～\nて、今のでバレバレかあ、残念", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1325005, 132501, 0, 7.0, 2, "例えば大人の私をデートに\n連れてくならどこ？\n…ほう、家でまったり\n君、分かってるね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1325006, 132531, 0, 0.0, 1, "お、ちゃんと働いてるみたいだね\n感心感心\nでもあんま根詰めないように\n省エネが一番だよ～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1325007, 132531, 0, 0.0, 1, "ん、もうこんな時間か\nちょっち休憩しよっかな\n君も付き合ってよ\nコーヒー淹れたげるからさ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1325008, 132531, 0, 0.0, 1, "さて、と…そろそろ定時だし\n私は上がっちゃうけど\nこのあとなんか食べにいく？\nお姉さんが奢ったげよっか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1325009, 132531, 0, 0.0, 1, "ん～、なに？　どしたん？\nもしかして見惚れてた？\nこのエロガキ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1325010, 132531, 0, 0.0, 1, "ふふっ…いや、なんかいつもより\n作業が捗るなーって思ってさ\n君が近くにいてくれるから…\nかもね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1325011, 132501, 0, 3.4, 2, "君、今日誕生日でしょ？\nよーし、私が奢るからさ\nミルクで乾杯しよ～よ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1325012, 132531, 0, 0.0, 1, "おー、君今日が誕生日なんだ\nそっか、これで年の差１つ\n縮まったのかぁ…あ、いや\nええと、おめでと", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1328001, 132801, 0, 9.2, 2, "クリスマスは素敵な行事ですね\nジオ・テオゴニアに持ち帰れば\n民たちがさぞ喜ぶことでしょう\n甘いケーキなどは特に…ふふっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1328002, 132801, 0, 5.6, 2, "ランドソルの冬は寒いですが\n色々なものが煌いていて…\nはあぁ…ほら、見てください\n白い吐息が綿菓子のよう…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1328003, 132801, 0, 7.4, 5, "時には黒い装いも良いですね\n普段よりも悪戯心が湧くようです\n少しだけ気恥ずかしいですが…\n騎士…その、似合っていますか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1328004, 132801, 0, 7.5, 5, "悪魔というのは大変ですね\n人をドキドキさせることを\n生業とするだなんて…\n騎士も…ドキドキしたいですか？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1328005, 132801, 0, 9.3, 6, "雪は冷たい…のですよね？\n一度足をつけてみたいのですが\nなかなか勇気が出なくて…\n騎士、支えていてくれますか？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1328006, 132831, 0, 0.0, 1, "あなたと二人、特別な一夜を\n過ごせることをうれしく思います\nクリスマスの楽しみ方…\nたくさん教えてくださいね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1328007, 132831, 0, 0.0, 1, "夜も更けてきましたね…\n聖夜の星に祈りましょう\nあなたの道行きに、これからも\n祝福があらんことを…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1328008, 132831, 0, 0.0, 1, "ブドウ朱はおいひいのですね…\nろれつ？　らいじょうぶです…\nほら、いつおろーりれひょう…？\n騎士…きいていまふか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1328009, 132831, 0, 0.0, 1, "少し体が、火照ってきました…\nブドウ朱のせいでしょうか\nそれともあなたが…\n側にいるから？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1328010, 132831, 0, 0.0, 1, "いつもと違う衣装を着るのは\n少し恥ずかしいですね\n天使のわたしと、悪魔のわたし…\nその…あなたは、どちらが…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1328011, 132801, 0, 0.0, 1, "クリスマスの悪魔として\nあなたの生誕をお祝いします\nとびっきり素敵な夢を\n見せて差し上げたいですから", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1328012, 132831, 0, 0.0, 1, "今日はあなたが生まれた特別な日\n甘いケーキとキャンドルで\n奇跡の一夜を共に過ごしましょう\nあなたに祝福がありますように…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1329001, 132901, 0, 8.4, 2, "クリスマスまでの夜、\n人々の夢に現れる一つの影！\nその正体は…クリスマスの悪魔に\nなった、イオちゃんよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1329002, 132901, 0, 0.0, 1, "この衣装、意外と寒くないのよ\nきっとクリスマスの悪魔の力が\n守ってくれてるおかげね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1329003, 132901, 0, 4.3, 2, "少し前はよく王宮騎士団の人に\n呼び止められてたけど、\n最近は会釈してくれるの\nみんな礼儀正しいのね～♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1329004, 132901, 0, 9.9, 1, "私ってなぜか人から力を\nもらうことが多いのよね～\n闇の力とか、悪魔の力とか、\nそれから…君の強化とか♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1329005, 132901, 0, 0.0, 2, "いいことしたら褒めてあげる♪\nいい子いい子、よく頑張りました\nいつも君のこと、見守ってるね♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1329006, 132931, 0, 0.0, 1, "眠れない夜は私を呼んで♪\nクリスマスの悪魔として、\n君にとびきりいい夢を\n見せてあ・げ・る♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1329007, 132931, 0, 0.0, 1, "雲の上で寝るのって\n子どもの頃からの憧れだったの♪\n夢の中で夢が叶っちゃった、ふふ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1329008, 132931, 0, 0.0, 1, "この夢の果てには\n何があるんでしょうね？\nふふ、確かめに行くときは\n君と一緒に行きたいな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1329009, 132931, 0, 0.0, 1, "君がそばにいてくれるだけで\n私の心はいつもぽかぽか♪\n君もそうだったらうれしいな♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1329010, 132931, 0, 0.0, 1, "本当にクリスマスプレゼントが\n私だったら、君はどうする？\n夢の中なら君のために\nどんなことだってしてあげるわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1329011, 132901, 0, 5.0, 2, "お誕生日おめでとう！\nお祝いのパーティーは盛大にね♪\nケーキはもちろん、\nショートケーキにしましょう♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1329012, 132931, 0, 0.0, 1, "ハッピーバースデー！\n誕生日プレゼントとして、\n私がいい夢を見せてあげる♪\nきっと寝覚めもばっちりよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1330001, 133001, 0, 6.1, 1, "暇なときは…絵を描いたりとか？\nと言っても、この世界は\nカラフルな絵の具がないので\nもっぱら水墨画ですが", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1330002, 133001, 0, 13.8, 1, "このサークレット何に見えます？\n実はこれ、一流の幽霊のみが\n装着を許された、伝説の三角の布\nリスペクトなんです…冗談ですが", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1330003, 133001, 0, 7.8, 2, "また温泉でも一緒に行きますか\n今度は男湯には侵入しませんよ\nはじめから混浴でどうです？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1330004, 133001, 0, 6.4, 7, "騎士氏といるとなんだか\n落ち着いてしまいますね\nこのまま健やかに\n成仏してしまいそうです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1330005, 133001, 0, 7.5, 6, "もしいつか騎士氏が死んだら\nレイスの嗜みを教えますよ\nまずは、そうですね\n二人で空中散歩とかどうですか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1330006, 133031, 0, 0.0, 1, "深海のように静寂に包まれた\nこの世界も悪くないでしょう？\n息遣いさえ聞こえる静寂…\n私の方は息してませんけど", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1330007, 133031, 0, 0.0, 1, "この槍はヴァイオレット姫を\n守るためにありますが…\n貴方のこともそれなりに\n守るのでご心配なく", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1330008, 133031, 0, 0.0, 1, "貴方にはレイスのプライドを\nぼっきぼきに折られましたよ…\n恨み晴らさでおくべきか…いつか\n騎士氏を絶叫させますので", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1330009, 133031, 0, 0.0, 1, "どれだけ動いても心拍数は一定…\nというか動いてないのですが\n貴方といると、なぜか心拍数が…\nいえ、何でもありません冗談です", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1330010, 133031, 0, 0.0, 1, "フフ、貴方と共に戦えること\nうれしく思いますよ、騎士氏\n無事に勝てたらハグでもしますか\nその時は、透けたりしませんから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1330011, 133001, 0, 0.0, 7, "ハッピーバースデー騎士氏\n幽霊やゾンビ、吸血鬼も呼んで\nじめじめした真っ暗闇の世界で\n盛大にお祝いいたししましょう", 7, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1330012, 133031, 0, 0.0, 1, "誕生祝いのケーキを用意しました\nフフ、下からロウソクの灯りに\n照らされた私の顔、怖いですよね\n…え、かわいい？　…そうですか", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1331001, 133101, 0, 5.3, 2, "ジオ・ニヴルヘルは他の世界より\n荒涼としておりますでしょう\nそれでも、わたくしにとっては\n愛しく、守るべき世界なのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1331002, 133101, 0, 6.0, 3, "臆病で不甲斐ないわたくしを\n支えてくださる方々がいる\n皆様の期待と献身に、わたくしは\n勇気をもって報いなくては…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1331003, 133101, 0, 9.2, 2, "胸元にハートをかたどる服装は\n騎士様の世界でも\nよく見られるもの…なのですわね\nわたくし、安心いたしました", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1331004, 133101, 0, 8.4, 5, "姫としてではなく、\nゾンビとしてでもなく、\nただわたくしを見てくれる\nそんな殿方は…騎士様だけですわ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1331005, 133101, 0, 0.0, 4, "強く在らねばならないと\n常日頃から心がけておりますが…\n挫けそうな時は、またわたくしに\n勇気をくださいますか？　騎士様", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1331006, 133131, 0, 0.0, 1, "今回の茶葉はわたくしの\nお気に入りを選びました\nジオ・ニヴルヘルでは貴重な\nやさしい味のする一杯ですわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1331007, 133131, 0, 0.0, 1, "騎士様の冒険譚は\nどれも胸が躍るようです\nどうか可能な限り聞かせて下さい\n屍人は時間だけはありますから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1331008, 133131, 0, 0.0, 1, "紅茶を一緒に楽しむたびに\n少しずつ心の距離が縮まっていく\nわたくしはそう信じておりますわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1331009, 133131, 0, 0.0, 1, "わたくしに憑いている\n動物霊たちも、騎士様のことが\n気に入ったようですね…♪\nこれも人徳のなせる業でしょうか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1331010, 133131, 0, 0.0, 1, "いつもはよくグレイスやルルィと\nお茶を楽しんでいたのです\n…同年代の男性をお招きするのは\nこれが初めてですけれど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1331011, 133101, 0, 8.5, 1, "お誕生日おめでとうございます\nささやかですが、祝宴の席を\n設けさせていただきました\nぜひ、一緒に祝わせてください", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1331012, 133131, 0, 0.0, 1, "今日は騎士様が生まれた日…\n新たな生命が芽吹いた、\n特別に尊い日に思えますね\nどうかその未来に、祝福を", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1332001, 133201, 0, 8.0, 2, "今年の抱負？　愚問だな\n私の心は常に「軍人たれ」だ\nまあ、軍人でも正月くらいは\nのんびりするけどな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1332002, 133201, 0, 4.1, 1, "着物というのも存外悪くないな\nニノンの気持ちが少しは分かるぞ\nどうだ、このままオーエドにでも\n繰り出そうか？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1332003, 133201, 0, 4.8, 1, "貴公、初詣に行ったらお守りも\n買うのだぞ。ご利益のほどは\n分からないが、一つ持つだけで\n随分と心強いものだ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1332004, 133201, 0, 12.0, 5, "こう寒いと温泉にでも行きたく\nなるな。なに、いい宿を知って\nいるだと？　そ、それは私と\n二人で…いや、何でもない…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1332005, 133201, 0, 10.4, 5, "正月を楽しく過ごすには沢山の\n準備が必要だが、一番大事なのは\nその正月を誰と過ごすかだと思う\nだからその、わ、分かるだろ…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1332006, 133231, 0, 0.0, 1, "日の出を拝んで参拝して屋台巡り\n正月とは実に楽しいものだな！\nこうなれば徹底的に正月を味わい\n尽くすぞ！　さあ、私に続け！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1332007, 133231, 0, 0.0, 1, "私はただ食べ歩きして浮かれてる\nわけではないぞ。自ら平和を享受\nすることで街と人々を守る決意を\n新たにしているのだ。本当だぞ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1332008, 133231, 0, 0.0, 1, "神殿、参拝客で溢れていたな\n神頼みはもちろん結構だが\n何かあればまずは私を頼れ\n必ず貴公の力となろう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1332009, 133231, 0, 0.0, 1, "何？　口元が汚れてる？\nこ、こら、これくらい自分で\n拭けるって、は、恥ずかしいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1332010, 133231, 0, 0.0, 1, "貴公と過ごす正月が、こんなにも\n楽しくて愛おしいとはな。来年も\nいや、願わくはこれから先ずっと\nこんな正月を送りたいものだな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1332011, 133201, 0, 6.2, 6, "誕生日おめでとう！　お祝いに\nとっておきの甘味処にご招待\nしよう。決して私が食べたい\nからではないぞ！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1332012, 133231, 0, 0.0, 1, "貴公、年を経るごとに顔つきが\nよくなっていくな。いつか同じ\n部隊で背中を預けて戦えたら…\nなんて想像をしてしまうぞ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1335001, 133501, 0, 5.1, 2, "こうして和服を着ていると、\nなんだか背筋が伸びる気分だわ\n一年の計は元旦にあり、だもの\n正月が明けてもきちんとしたいわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1335002, 133501, 0, 7.6, 9, "お祝い事は大切にしないとね\n特に子どもたちにとっては、\n特別な時間だと思うの\n大人が考えているよりずっとね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1335003, 133501, 0, 0.0, 2, "お正月の遊びなら任せて♪\n子どもの頃は貧しいなりに\nいろいろ工夫して\n遊んでたんだから", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1335004, 133501, 0, 0.0, 1, "あんたはこれから\nどんな一年を過ごしたい？\nやりたいことがあるなら\nあたしも手伝ってあげるわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1335005, 133501, 0, 6.1, 2, "新年の目標、決めておきましょ\nあたしは、そうね…\nあんたと楽しく過ごせたら、\nそれが一番かしら。ふふっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1335006, 133531, 0, 0.0, 1, "は～…\n普段はなかなかできないけど、\n横になって休むのって\nやっぱりいいものね～…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1335007, 133531, 0, 0.0, 1, "ここから見える庭がとっても\n素敵なのよ。ふふっ♪　ちょっと\nお行儀悪いけど、今だけだから\n満喫しましょ。ほら、あんたも♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1335008, 133531, 0, 0.0, 1, "この香り…温泉の匂いかしら\nあまりなじみがない分、\n今は旅先にいるんだって\n実感できるのよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1335009, 133531, 0, 0.0, 1, "二人きり、水入らずかぁ…\nほら、いつも一緒にいても\nこういう時間はあまりないでしょ\nだから…もう少し味わわせて", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1335010, 133531, 0, 0.0, 1, "こうやってごろごろする時間が\n本当に幸せなの。あんたを\nひとり占めした、最高に贅沢な\nお正月よ…なんてね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1335011, 133501, 0, 0.0, 2, "お誕生日おめでとう！\nうん、いい顔してるわね\nこれからの一年も\nきっといいことがあるわ♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1335012, 133531, 0, 0.0, 1, "誕生日を一緒に過ごす\n特別な相手に、あたしを\n選んでくれたってことかしら\nふふっ、ありがとうね…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1336001, 133601, 0, 4.5, 8, "アタシの魔眼で睨まれたら、\nみーんな石になっちゃうの！\nどう？　怖いでしょ！\nわかったならひれ伏しなさい！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1336002, 133601, 0, 5.2, 4, "アタシだって立派な\nジオ・ゲヘナの貴族…レディよ！\nそりゃ、お姉ちゃんたちに\n比べたらまだ半人前だけど…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1336003, 133601, 0, 6.2, 7, "メドゥシアナはアタシの眷属！\n良き相棒でもあり、\n友達でもあるわ\nすっごく頼もしいんだから！", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1336004, 133601, 0, 4.9, 5, "お姉ちゃんたち…どこに\n行っちゃったんだろう…\nあ！　い、今のは心細いとか\nそういうのじゃないから！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1336005, 133601, 0, 5.7, 1, "アンタはまぁまぁ優しいし？\n言うこと聞いてくれるし？\nま、そばにいてもいいわよ\n光栄に思いなさいよね！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1336006, 133631, 0, 0.0, 1, "どんな世界に行っても\nアタシたちは無敵なんだから！\nもっと高く、もっと速く─\n行くわよ、メドゥシアナ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1336007, 133631, 0, 0.0, 1, "このアタシのお姉ちゃん捜しを\n手伝わせてあげるんだから\n光栄に思いなさい！\nお～も～い～な～さ～い～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1336008, 133631, 0, 0.0, 1, "お姉ちゃん…\n絶対見つけてみせるわ…！\nそれまでに、今より\nもっと立派なアタシになる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1336009, 133631, 0, 0.0, 1, "アンタなら…メドゥシアナに\n乗せてあげてもいいわよ？\nた、たまにだけどね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1336010, 133631, 0, 0.0, 1, "ねぇ、これからも\nアタシを楽しませてよね？\n約束破ったら…\n石にしちゃうんだからっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1336011, 133601, 0, 2.5, 2, "誕生日？　しょうがないわねー\nメドゥシアナに乗せてあげるから\n世界一周でもしに行くわよ！", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1336012, 133631, 0, 0.0, 1, "アンタ、誕生日なんでしょ？\n蛇の抜け殻あげるわ！\nどう？　うれしいでしょ！\n…うれしいわよね？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1337001, 133701, 0, 3.4, 2, "故きを温ね新しきを知る…\n迷宮女王、再起動！\nって感じかな？\nさあ、力を貸すよ少年！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1337002, 133701, 0, 8.1, 6, "手早くどこででも食べられて、\nおまけに栄養面もばっちりな\n非常食を考えてるんだけど…\nクレープでの実現は厳しいか～？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1337003, 133701, 0, 8.8, 1, "世界が変わる…っていうか\n世界観を変えちゃってないかな？\nこの格好…ま、アタシの他にも\n例外はたくさんいるし今更か！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1337004, 133701, 0, 7.9, 2, "どんどん可能性を広げていく\nキミを見てると…アタシのした\n選択は間違いじゃなかったって\n思えるよ。ありがとう、少年", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1337005, 133701, 0, 0.0, 2, "少年、どんなに難しい局面でも\nアタシがついてるから安心して！\n立ち塞がる敵にお見舞いする、\n百発百中の援護射撃をご覧あれ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1337006, 133731, 0, 0.0, 1, "誰から相手してほしい？\n今日はすごく気分がいいんだ\nとっておきの大技も必殺技も\n大盤振る舞いで見せちゃおう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1337007, 133731, 0, 0.0, 1, "少年、みんなと一緒に\n心置きなく前に出ちゃって！\nアタシが援護するから\n背中は任せてくれていいよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1337008, 133731, 0, 0.0, 1, "どんな状況でも打開する道を\n最短で探査し駆け抜ける…\n迷宮の名を冠する女王は勿論、\n攻略にも詳しいってわけさ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1337009, 133731, 0, 0.0, 1, "後衛で背後を守る\nってのも悪くないもんだね！\n少年の成長が実感できるよ\nもうすっかり頼れる背中だ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1337010, 133731, 0, 0.0, 1, "昔、アタシの進む道を\n少年が導いてくれたように…\n今度はアタシが切り拓こう\nキミの進む先を、この光で！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1337011, 133701, 0, 9.5, 6, "お誕生日おめでとう少年！\n今日は好きなのを作ってあげよう\nまたクレープかって？　ご名答！\nあー…たまには他のがいいかな？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1337012, 133731, 0, 0.0, 1, "ハッピーバースデー！\n今日はいつもと趣向を変えて\nとっておきの手料理をご馳走だ！\n料理の腕には自信あるんだ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1338001, 133801, 0, 5.9, 2, "最適化のためのアバター変更に\n特に感慨はなかったのですが…\n今となっては多少の懐かしさを\n感じてしまいますね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1338002, 133801, 0, 7.7, 4, "この状態のグレゴールは今とは\n比較にならないほど強力ですが\nそれ以上に…比較できないほど\n燃費が悪いのが問題ですね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1338003, 133801, 0, 6.7, 2, "見た目は確かに過去の私ですが\n中身は今の進化した私です\n当然、あの頃よりも更に\n強いに決まっているでしょう", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1338004, 133801, 0, 2.5, 2, "ひらひらしてキレイ？\nふふっ、そうですか\n貴方の好みは\nこういう服装なのですね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1338005, 133801, 0, 3.0, 2, "このアバターについて知りたい？\nいい傾向ですね\n正しい好奇心は学習の質と量を\n高めますから", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1338006, 133831, 0, 0.0, 1, "あら、そう…ふふっ\nグレゴールもそう思うのですね\nよかったですね、貴方の事は\n気に入ってるみたいですよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1338007, 133831, 0, 0.0, 1, "この場所は完全に私の支配領域\nいうなれば妖精の女王の統べる\n常春の国ティル・ナ・ノーグ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1338008, 133831, 0, 0.0, 1, "七冠と呼ばれ始めた頃のことなど\nすっかり忘れていましたが…\nたまに振り返るのも良いでしょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1338009, 133831, 0, 0.0, 1, "この姿は普段より偉そう…？\nふふっ、そう思うのであれば\n足元に跪いてもよいのですよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1338010, 133831, 0, 0.0, 1, "この姿の時のグレゴールは\nいつもより甘えたがりなのです\nおや、貴方も甘えたいのですか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1338011, 133801, 0, 0.0, 2, "誕生日おめでとうございます\n…貴方にとっては私と出会った日\nこそが本当の誕生日と言えますね\nそちらもしっかりと祝うのですよ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1338012, 133831, 0, 0.0, 1, "誕生日の祝祭を始めましょう\n貴方が生まれ、私のものとなる\nこの必然を祝うために", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1339001, 133901, 0, 4.5, 2, "再びこの姿になれる日が来るとは\n夢にも思わなかったな！\nどれ、泡沫の夢と消える前に\n存分に愉しませてもらおうか☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1339002, 133901, 0, 6.7, 1, "罵倒の言葉も数あれど、\n最も多くワタシに向けられた\nフレーズは『悪魔』だったな\nフフ、お似合いだろう？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1339003, 133901, 0, 0.0, 1, "悪魔は邪悪な存在だが\n契約は決して違えぬものらしい\n思わず親近感を覚えてしまうな☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1339004, 133901, 0, 5.2, 1, "おいおい、そう警戒してくれるな\n取って食いやしないさ。それとも\n坊やは食われるのがお好みか？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1339005, 133901, 0, 5.8, 1, "ワタシの昔話が聞きたいのか？\n興味があるなら聞かせてやろう\n寝物語には悪くない♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1339006, 133931, 0, 0.0, 1, "正気で戦場は成り立たない！\nさあ、ワタシと狂気の舞踏会と\n洒落込もうじゃないか、坊や☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1339007, 133931, 0, 0.0, 1, "戦いは派手なほどいい☆\n多少の過負荷で悲鳴を上げる\nアラートなぞ捨て置け！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1339008, 133931, 0, 0.0, 1, "退屈予防にいくつか火種を\nバラ撒いておくか？\n祭りになればそれはそれで\n愉しめるだろうよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1339009, 133931, 0, 0.0, 1, "悪魔の誘惑というやつは\n甘く逆らいがたいものらしいぞ\nフフ、誘惑されてみるか、坊や？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1339010, 133931, 0, 0.0, 1, "使い道を過てば己の破滅さえも\n招きかねんほどの絶大な力だ！\n坊やのついでに世界までも\n守ってやれるだろうよ☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1339011, 133901, 0, 2.6, 2, "今日はオマエの誕生日だったか\nどれ、ケーキでも作ってやろう☆\n味は期待してくれて構わんぞ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1339012, 133931, 0, 0.0, 1, "ハッピーバースデー！\n誕生日パーティーは任せておけ\n決して退屈しない一日を\nオマエにプレゼントしてやろう☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1340001, 134001, 0, 6.2, 2, "おはよ♪\n今日はどこに行きたい？\nどこへだって案内するわよ\nあたしはガイド妖精なんだから", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1340002, 134001, 0, 3.1, 1, "ちっこい体って便利なのよね\nあんたの肩の上に乗って\n楽できるし、頭の上に乗って\n広い世界を見下ろせるし♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1340003, 134001, 0, 1.1, 3, "ひゃっ！？　こらーっ！\n羽をつまむんじゃないわよ！\n妖精サイズでも女の子なのよ？\nもっと丁寧に扱いなさい！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1340004, 134001, 0, 9.8, 2, "この姿だといろいろ思い出すわね\n今よりもお調子者なあんたに\n振り回されたり、振り回したり\n毎日が大騒ぎだったんだから♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1340005, 134001, 0, 11.0, 1, "行きたい場所があるの？\nどこ？　案内なら任せなさい♪\n…あたしの、好きなところに？\nなら…迷うことはないわね♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1340006, 134031, 0, 0.0, 1, "ようこそ、アストルムへ♪\nあたしは妖精のフィオ！\nあんたを導く、一番の相棒よ♪\nこれからも…よろしくね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1340007, 134031, 0, 0.0, 1, "あたしのあるべき姿はこれよね♪\nさぁ、一緒に冒険に出発よ！\nあたしがガイドしてあげるから\nなんでも聞きなさいっ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1340008, 134031, 0, 0.0, 1, "このメンバーで冒険してると\nなんだか…ううん、なんでもない\n行きましょ、願いを叶える旅に！\nどこまでもついていくわよっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1340009, 134031, 0, 0.0, 1, "この姿でいるとさ、あんたとの\n距離が近くなる気がするのよね\n一人の─\nあんただけの、妖精でいられる", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1340010, 134031, 0, 0.0, 1, "妖精のおまじないをしてあげる\nちょっと、目を閉じなさい\n…～～～フィオ、ぱーんちっ！\nダマされたわね？　…ふぅ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1340011, 134001, 0, 2.6, 5, "誕生日おめでとう♪\n長い付き合いだもの\n二人っきりのお祝いも\nいいものよね…♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1340012, 134031, 0, 0.0, 1, "ハッピーバースデー！\n優秀なガイド妖精は\n記念日を忘れたりしないのよ？\n妖精の祝福を受け取りなさいっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1343001, 134301, 0, 10.1, 1, "唱喚士の素質がある、なんて…\nまだ…信じられない、わ…\nひとを傷つけてきた、わたしが…\nひとを守るため、歌うなんて…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1343002, 134301, 0, 11.2, 4, "エルフの里に伝わる、精霊の舞を\n教えてもらったん、だけど…\nころびすぎて、コッコロちゃんに\n「諦めも肝心」って言われたわ…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1343003, 134301, 0, 11.8, 6, "精霊さんばかりかまってると…\nワイバーンがやきもち妬いちゃう\nみたいで…な、なんでかしら…？\nわたしが造ったのに…はぅ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1343004, 134301, 0, 14.7, 1, "どうしてこの服って\nこんなに薄着なのかしら…？\nマナを感じやすかったり…？\nなら仕方ない…の、かしら…ね？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1343005, 134301, 0, 18.0, 5, "同じお世話でも、あなたと\n精霊さんとでは、少し…違うわ…\nあなたをお世話、してる時は…\nド、ドキドキ…しちゃって…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1343006, 134331, 0, 0.0, 1, "ひとの声が、ずっとこわかった…\nでも今は…だれかの声に合わせて\n歌うことが…ふふっ…\nこんなにも、たのしい…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1343007, 134331, 0, 0.0, 1, "もっと色々な歌、歌いたいわ…\nカルミナみたいな\n明るくて、かわいい歌も…\n自信はないけど、そのうち…はい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1343008, 134331, 0, 0.0, 1, "チカちゃんの歌は…\nひとを包み込む、優しい、歌…\n本当に、すてき…ふふっ\n弟子になれて、うれしい…わ…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1343009, 134331, 0, 0.0, 1, "あなたとも、一緒に…\n歌ってみたい、わ…\nあなたとの、ハーモニー…\nたくさん、奏でていきたいから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1343010, 134331, 0, 0.0, 1, "ひとりでいるのも好きだけれど…\nみんなでいるのも好きになれたわ\nあ…もちろん、あなたと\nふたりでいるのも…好き、よ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1343011, 134301, 0, 0.0, 2, "お誕生日ケーキ…作ってみたの\n何度も失敗しちゃったけど\nやっとうまくいったから…その…\n…はい、あ～ん♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1343012, 134331, 0, 0.0, 1, "チカちゃんと、ね…\nたくさん、歌の練習、したの…\nあなたの誕生日に、とっておきの\n精霊の加護を贈るために…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1344001, 134401, 0, 8.8, 1, "いつも私を支えてくれて、\n時には導いてくれるあなたへ\n感謝を込めて歌います♪\n子守唄がいいですか…？", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1344002, 134401, 0, 5.8, 8, "歌は時々、言葉よりもたくさんの\n想いを伝えてくれます。うまく\n言えないことも、歌でなら\nあなたに伝えられるかも…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1344003, 134401, 0, 11.1, 6, "私ももっと修行して、みんなに\n頼られる、唱喚士になりたいです\nあなたには一番近くで見守って\nいてほしい…わがままですか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1344004, 134401, 0, 6.2, 9, "この衣装、布が少ないから\n心もとなくて…でもあなたが\nきれいだって言ってくれたから\n胸を張って着られます…♪", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1344005, 134401, 0, 6.4, 2, "肌にも模様を乗せることで精霊と\nより心を通わせられるんだとか\nふふっ…精霊とも、あなたとも\n距離が近づいた気がします…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1344006, 134431, 0, 0.0, 1, "教え、導く…言葉にするのは\n簡単ですが、実際は難しいですね\nそれでも、私の精一杯を\nランファさんに届けたいです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1344007, 134431, 0, 0.0, 1, "心を込めて歌います\n私たちの旋律と歌が\n精霊たちにも、あなたにも\n届きますように…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1344008, 134431, 0, 0.0, 1, "ランファさんの師匠として\nなによりうれしいのは、一緒に\n歌えるようになったことですね♪\nあなたも一緒に歌いませんか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1344009, 134431, 0, 0.0, 1, "あなたに見つめられるとだんだん\n頬が熱くなってきて…\nどんな舞台の上に立つよりも\n緊張してしまうんです…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1344010, 134431, 0, 0.0, 1, "あわわ…思いっきり歌うと\nあなたへの気持ちが隠せなく…っ\nき、聞いたらだめです…！\nだって恥ずかしいですから…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1344011, 134401, 0, 5.9, 9, "ハッピーバースデー！　ケーキを\n里の皆さんと一緒に用意しました\nろうそくを消す前には、お祝いの\n歌を歌いましょう♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1344012, 134431, 0, 0.0, 1, "お誕生日おめでとうございます\nあなたの生まれた日、全力で\nお祝いしますね。歌にケーキ\n膝枕で子守唄もしちゃいます…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1345001, 134501, 0, 4.1, 2, "クローチェ、空戦特化仕様の\nエアリアルモードですっ♪\nあなたのピンチにびゅーんと\n駆けつけますよっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1345002, 134501, 0, 3.3, 1, "このボディ、すっごく軽く\nなってるんですよっ♪\n試してみますっ？　抱き上げて\nみてくださいっ、ほらっ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1345003, 134501, 0, 0.0, 2, "アームを換えたら、ハグと握手が\nできるようになったんですよっ♪\n前よりも上手にナイトさんに\n触れられて、わたし、幸せですっ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1345004, 134501, 0, 6.9, 2, "ナイトさん、充填お願いしますっ\n飛んでる途中でエネルギー切れに\nなったら大変ですからねっ！\nこまめにいっぱい補給ですっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1345005, 134501, 0, 6.1, 2, "一緒にお出かけしましょうっ！\nナイトさんと一緒に見たい景色が\nいーっぱいあるんですっ♪\nずっと一緒にいてくださいねっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1345006, 134531, 0, 0.0, 1, "エアリアル、高機動モードっ！\n悪い魔物さんはみんな\n空の上から狙い撃ちですっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1345007, 134531, 0, 0.0, 1, "どんなに逃げてもロックオンっ！\nわたしのビームキャノンが\n逃がしませんよ～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1345008, 134531, 0, 0.0, 1, "どれだけ大きくて強い敵が\n相手でも、諦めませんよっ\n守りたいものがあるんですからっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1345009, 134531, 0, 0.0, 1, "空から街を見下ろしていると\nナイトさんの顔が思い浮かんで…\n絶対負けられないってパワーが\nぐんぐん湧いてくるんですっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1345010, 134531, 0, 0.0, 1, "帰ったらナイトさんに\nハグしてもらって、充填もっ♪\nわたしにとってはとびきりの\nご褒美なんですよっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1345011, 134501, 0, 3.9, 1, "ハッピーバースデーっ！\nえへへ、お誕生日って\n歌を歌うんですよねっ？\n精いっぱい歌わせてもらいますっ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1345012, 134531, 0, 0.0, 1, "お誕生日おめでとうございます！\nプレゼントはわたしですっ！\nナイトさんが喜んでくれたら、\nとってもとっても幸せですっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1346001, 134601, 0, 4.3, 2, "私がパイロットになったのは\n成り行きみたいなものですけど…\nそれでも全力でがんばります\n信じてくれるみなさんのために！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1346002, 134601, 0, 0.0, 4, "どれだけ強い機体でも、\nパイロットの腕が悪かったら\n宝の持ち腐れなんですよね…\nパイロット、責任重大ですっ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1346003, 134601, 0, 0.0, 2, "クローチェさんってすごいです\nいつも明るいムードメーカーで\n誰とでもすぐ仲良くなれて…\nい、いつか私もあんなふうに…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1346004, 134601, 0, 2.9, 7, "もしも団長さんがいなかったら…\nいやいや、そんなこと怖すぎて\n想像できませんよ\n世界が滅ぶみたいなものです！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1346005, 134601, 0, 7.3, 6, "あなたに伝えたいことは、\nまだまだたくさんありますけど…\n戦いが終わったらにしましょう\n…あれ、なんかフラグみたい？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1346006, 134631, 0, 0.0, 1, "一人乗りのコクピットですけど、\n今の私はぼっちじゃありません\n応援してくれる人たちが\nたくさんいますからねっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1346007, 134631, 0, 0.0, 1, "クローチェさんの熱い気持ちが\n文字通りダイレクトに\n伝わってきます…\nこれが人機一体の友情…！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1346008, 134631, 0, 0.0, 1, "コクピットに乗って戦ってると\nなんだか叫びたくなるんですよね\nえっ？　わりといつも叫んでる？\nそそそうでしょうか…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1346009, 134631, 0, 0.0, 1, "あの人が待っていてくれるから\n絶対に生きて帰ろうって気持ちが\n湧いてくるんです\nこうなった私は無敵ですよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1346010, 134631, 0, 0.0, 1, "どんなに強い敵にも、\n挫くことはできないんです\n大切な人を守りたいと思う、\n私たちのこの願いは…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1346011, 134601, 0, 7.3, 5, "お誕生日おめでとうございます！\nここはクローチェさんに頼んで\nギガンティックな祝砲でも\n撃っちゃいますか！　やり過ぎ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1346012, 134631, 0, 0.0, 1, "大切な人のお誕生日を\nいっしょに祝うことができる\nそんな平和な日常を守るのも、\n大事なことなんですっ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1347001, 134701, 0, 3.5, 1, "日々私のもとへ来るとは…\n人の子よ、良い心がけです♪\n特別にほーちゃん、と\n呼んでもいいですよ？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1347002, 134701, 0, 5.4, 8, "今日もお役目ご苦労でした\n温泉でもつかりにいきますか？\nと、鶏のだしは出ませんよ？\n出ませんからね？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1347003, 134701, 0, 8.8, 6, "絢爛にして神聖なる私\nそう、とってもえらいんです\nえっへん！\n…ほ、本当ですからね？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1347004, 134701, 0, 8.3, 8, "私の加護を受ければ、\n無病息災、食欲増進、\n恋愛成就間違いなし！\n…た、たぶん", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1347005, 134701, 0, 9.7, 4, "あなたは見どころがありますね\nと、特別に、私のそばに\n来ることをゆるしましょう！\nほ、ほら、もっと近くに～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1347006, 134731, 0, 0.0, 1, "我が名はホウオウ\n輪廻転生を繰り返すこと幾星霜\n浄化を司る神鳥とはこの私！\n…あっ、信じてませんね！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1347007, 134731, 0, 0.0, 1, "ジオ・ゲヘナに渦巻く闇の力、\n私の浄化の炎で払ってあげます！\n…あちっ、あ゛ぢぢっ！\nや、やりすぎましたぁ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1347008, 134731, 0, 0.0, 1, "鳳凰の羽は、我が力の象徴\nこの美しい羽毛をご覧なさい！\n神々しく、美しく輝いて…\nって、抜かないでくださいぃ～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1347009, 134731, 0, 0.0, 1, "だって私は鳳凰ですからっ！\nこのジオ・ゲヘナの空の覇者！\nいつか騎士さんにもあの景色を…\nぱたぱたぱた～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1347010, 134731, 0, 0.0, 1, "我が炎は、悪しき心のみを焼く…\nふふふっ。すごいでしょう？\nさぁ褒めなさいっ♪　騎士さんに\nたたえられるのは格別ですからね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1347011, 134701, 0, 9.2, 7, "誕生日、おめでとうございます！\n私をおんぶする権利、もしくは\nだっこする権利をあげましょう！\n…え？いらない？ぞん゛な゛ぁ゛", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1347012, 134731, 0, 0.0, 1, "誕生日のごちそうといえばチキン\nだそうですね\n…わ、私はちがいますからね？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1348001, 134801, 0, 8.5, 5, "この服、どうですか？\nちょっとワイルドすぎますか？\nえっ？　何着てもかわいいって…\nバ、バカぁ～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1348002, 134801, 0, 8.4, 5, "ファッションで大事なのはTPO\n時と場合で服も変えないと！\nでも騎士さんはいつも変わらない\nのが…な、なんでもないですっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1348003, 134801, 0, 5.4, 7, "いろんな世界の人たちに\nおしゃれすることのすばらしさ\nを知ってもらいたいです！\n仕立て屋も大繁盛！　なーんて♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1348004, 134801, 0, 3.4, 5, "おしゃれの醍醐味は個性を伸ばす\nことです！　騎士さんの個性は…\n優しい所…う、嘘ですよーだ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1348005, 134801, 0, 9.1, 3, "レイ様が好きな気持ちと、今\n私が抱えてる、この気持ち\n同じ大きさで綱引きしてて…もう\n全部騎士さんのせいなんだから！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1348006, 134831, 0, 0.0, 1, "こ、このエロガエルーっ！\n私の服食べるなーーーっ！！\nちょっと、騎士さん！\n見てないで助けてくださーい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1348007, 134831, 0, 0.0, 1, "昔似たような目に遭ったような…\n私ってば、こういう星の下に\n生まれついてたりします？\n違うって言ってください～～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1348008, 134831, 0, 0.0, 1, "ぐぬぬ…こんな魔物ごときにっ！\n私のデザインが…！　次は絶対\n破けない生地で作りますから！\n…え？　フラグじゃないですー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1348009, 134831, 0, 0.0, 1, "ううっ、こんな姿…\n誰かに見られたら泣いちゃいます\n騎士さんも、忘れてくださいね？\nお願いですから～～っ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1348010, 134831, 0, 0.0, 1, "たっ、大変だったけど素材は手に\n入ったし、これで服が作れます！\nあとは…その、騎士さん…着替え\n持ってきてくれますか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1348011, 134801, 0, 5.4, 5, "誕生日おめでとうございます！\nプレゼントに服を作ってあげます\n…ぺ、ペアルックとかじゃない\nですからね！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1348012, 134831, 0, 0.0, 1, "今日が騎士さんの生まれた日…\nつまり私の悩みの種が生まれた日\n…なーんて、冗談ですよ！\n今日はたくさんお祝いしましょ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1349001, 134901, 0, 7.1, 3, "ご覧あそばせ、今日もあたくしは\nお美しく輝いていますわ～♪\nそうは見えないというなら、\nきっと目が曇っておりますわよ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1349002, 134901, 0, 4.5, 2, "さて、今日も日課を始めましょう\nヲホホッ、無様ですわねネフィ！\n…これは、ネフィに勝った後\n土下座を命じるイメトレですわ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1349003, 134901, 0, 7.8, 7, "この辺りも全部、本来ならば\nあたくし様が統治しているはずの\n土地でしたのよっ\nう、嘘なんかじゃねぇですわ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1349004, 134901, 0, 6.2, 2, "馬ボーンときたら、相変わらず\n締まらないお顔をしてますわねぇ\nあたくし様の華麗なオーラを\n分けてあげてもよろしくてよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1349005, 134901, 0, 7.1, 1, "いずれあたくしはネフィを倒し、\n本来の地位を取り戻してみせます\n馬ボーンには、その美しき物語を\n語り継ぐお役目を命じますわ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1349006, 134931, 0, 0.0, 1, "ヲォ～ッホッホッホ！\nこれぞ全てを見下すポーズ！\n支配者たるあたくし様だけに\n許されたお姿をご覧あそばせ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1349007, 134931, 0, 0.0, 1, "剣闘士用に押しつけられた\nナマクラ刀？　関係ねぇですわ！\n真の最強は、どんな武器でも！\n敵をボコボコにできますのよ～！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1349008, 134931, 0, 0.0, 1, "ヲッホッホ、ヲ～ッホッホッホ、\nヲッホッホ！　思わず五七五で\n高笑いしてしまうくらい、\n感動でお胸が膨らんでますわ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1349009, 134931, 0, 0.0, 1, "あぁっ、最高の気分ですわ～！\nもうずっと馬ボーンと一緒に\nいたいくらいですわ～♪\n…変な意味じゃありませんわよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1349010, 134931, 0, 0.0, 1, "この力をあたくし様のモノに…\nいえ、馬ボーンを永遠に\nあたくし様の下僕にするしか！\n夢が広がりまくりですわ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1349011, 134901, 0, 14.9, 9, "今日は馬ボーンのお誕生日～？\nまぁどうしても祝ってほしいなら\n考えてあげなくもないですけど…\n…別にいい？　なんでですの！？", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1349012, 134931, 0, 0.0, 1, "馬ボーンのお誕生日プレゼントに\n素晴らしいものを用意しましたわ\nそう、あたくし様に仕える地位！\n涙を流してお喜びなさいな～♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1350001, 135001, 0, 3.0, 1, "騎士クン、暑くない…？\n涼みたくなったら\nわたしにいつでも言って\n防暑魔法の準備は、万全", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1350002, 135001, 0, 0.0, 1, "海の匂いって…有機的だね\nわたしの作る仮想領域とは違う\n複雑に濁っていて、独特で…\nだけど、命の営みを感じる匂い…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1350003, 135001, 0, 6.1, 2, "砂浜に刻んだ足跡も\nいつか波にさらわれてしまう…\nでも…わたしはずっと覚えてるよ\n二人で過ごした、この夏のこと…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1350004, 135001, 0, 0.0, 4, "わたしからの気持ちだけ\n騎士クンに受け取ってほしい…\n他の子からの好意は、忘れて、ね\n今、この時だけ、でも", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1350005, 135001, 0, 3.5, 2, "巡り巡って…夏…\n騎士クンとこうやって\n過ごせるなんて、\n思ってもみなかった…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1350006, 135031, 0, 0.0, 1, "月が満ちて季節は巡り、命は続く\nやっと分かってきた気がする\n生きるということ…その意味を\n騎士クンが、教えてくれたんだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1350007, 135031, 0, 0.0, 1, "月の光と波の音…\n他には何もない…\n騎士クンとこの世界で\n今だけは、二人きり…だね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1350008, 135031, 0, 0.0, 1, "騎士クンとのつながりが\nわたしを温めてくれる…\nだから…平気\nもう…寒くなんて、ない", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1350009, 135031, 0, 0.0, 1, "わたしに残っているものなんて\nほとんどないけれど\nこの身も、心も…力も\n全ては騎士クンのために…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1350010, 135031, 0, 0.0, 1, "暗い…光の届かない海底で、\n泡になったとしても…\nわたしの想いは…消えない\n騎士クンへの、この想いは…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1350011, 135001, 0, 2.6, 1, "お誕生日おめでとう\n騎士クンが生を受けた日を\nお祝いできること…\nそれが…とってもうれしい…", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1350012, 135031, 0, 0.0, 1, "騎士クンへの誕生日プレゼント…\nわたしにあげられるものなら\nなんでもあげる…夏の空も、海も\nだから、欲しいもの…教えて？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1351001, 135101, 0, 11.7, 6, "前はちょっと恥ずかしかったけど\n今はこの水着、気に入ってるの\n私も成長したのかしら。あ、その\n精神的に、っていう意味よ…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1351002, 135101, 0, 7.6, 2, "波の音って落ち着くわね\nずっと聴いていたい…\nもう少しだけ二人で\nゆっくりしましょう♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1351003, 135101, 0, 9.0, 1, "ねえ、アイスクリーム食べに\nいきましょう♪　私はやっぱり\nチョコミントで決まりね\nあなたはどのフレーバーにする？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1351004, 135101, 0, 6.1, 6, "涼みたい時はいつでも言ってね\n私がヒンヤリさせてあげる…\nあ、羽を触らせるとかじゃなくて\n氷魔法の話よ？　念のため…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1351005, 135101, 0, 9.1, 7, "こうして、何も考えずに\n二人で海を眺めているのも\n私はキライじゃないわ\n…ううん、むしろ好きかも…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1351006, 135131, 0, 0.0, 1, "さあ、私の手を取って！\n一緒に波に乗って\n景色を楽しみましょう\n私たちの夏は、これからよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1351007, 135131, 0, 0.0, 1, "この水着、実は気に入っているの\nシンプルで動きやすいし\nひまわりのワンポイントが\nかわいいし…あなたもそう思う？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1351008, 135131, 0, 0.0, 1, "このフロート、何だか\n兄さんに似ている気がして…\nつい買っちゃったの\n海で頼りになりそうだしね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1351009, 135131, 0, 0.0, 1, "ふふ、こうして滑っていると\n何だか飛んでいるみたい♪\nあなたが一緒ならどんな荒波でも\n乗り越えられる気がするわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1351010, 135131, 0, 0.0, 1, "あなたと同じ波の上にいると\n不思議な一体感を感じるわ\n楽しくてドキドキするような\nうれしい感覚…私は好きよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1351011, 135101, 0, 2.9, 7, "誕生日おめでとう！\nあなたのバースデイパーティーは\n私が精一杯プロデュースするわ\n楽しみにしててね♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1351012, 135131, 0, 0.0, 1, "ハッピーバースデイ♪\nあなたにプレゼントしたい\n景色があるの。さあ手を取って\n特別な一日を過ごしましょう♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1352001, 135201, 0, 0.0, 2, "ばーべきゅー\nうみのいえ\nやたい\nうみ、いいとこ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1352002, 135201, 0, 0.0, 7, "ぶぅ…\nすなはま、あつい…\nやきぶた\nちょくぜん", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1352003, 135201, 0, 3.4, 2, "うみでも、ぱとろる\nあ\nかいがら、はっけん\nきれい", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1352004, 135201, 0, 0.0, 2, "お兄。あそぼ\nまだ、あかるい。よ\nうみ、たのしい。よ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1352005, 135201, 0, 5.3, 2, "お兄の\nみずぎ\nぱいなぽー\nおいしそ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1352006, 135231, 0, 0.0, 1, "お兄も。ぷかぷか、する？\nきもちいい、たのしい\nながれ、ながされ\nぼうけん、はじまり", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1352007, 135231, 0, 0.0, 1, "（じー…）\nおさかな、たくさん…\nでも。まだこども\nまだがまん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1352008, 135231, 0, 0.0, 1, "ついんて\nかいすい。ぽたぽた\nお兄、ひだりついんて\nしぼって？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1352009, 135231, 0, 0.0, 1, "こおりまほー\nひえひえ、ぷれしあ\nお兄を、ぎゅ。ってして\nひやす。ぎゅ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1352010, 135231, 0, 0.0, 1, "すいぶんほきゅー、だいじ\nお兄、ドリンク。あげる\nおいしかった。よ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1352011, 135201, 0, 0.0, 2, "たんじょび、おめでと\nおさかな\nつってきた\nいっしょに、たべよ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1352012, 135231, 0, 0.0, 1, "お兄。はぴば\nすぐ、おとな。なるから\nまってて。ね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1353001, 135301, 0, 6.1, 2, "お兄さん、海に入る前に\n日焼け止めクリーム\n塗ってあげますね♪\nわたしも…あとでお願いします♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1353002, 135301, 0, 7.1, 1, "『ひと夏の経験』って言いますよね\nわたしもちょっと\n冒険しちゃおうかな…\nお兄さん、付き合ってくれます？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1353003, 135301, 0, 5.2, 1, "海で食べる焼きそばってすっごく\nおいしいですよね♪　…あ、\nお口にソースついてますよ～？\nふふ、はい。きれいになりました♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1353004, 135301, 0, 6.2, 2, "お兄さんに見つめられると\n恥ずかしくって赤くなっちゃう…\nだってお兄さんの視線\n夏の日差しよりも眩しいから…♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1353005, 135301, 0, 10.1, 1, "海を見てると落ち着くんですよね\n嫌なことや悲しいこと…全部波が\nさらってくれるような気がして…\nなーんて…ふふ、冗談ですよ", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1353006, 135331, 0, 0.0, 1, "うふふっ、それ発射～っ♪\nなんて…ちょっと子どもっぽい？\nこんなにはしゃいじゃったことも\n内緒ですよ、お兄さん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1353007, 135331, 0, 0.0, 1, "ほら、冷たくて気持ちいでしょ♪\nきゃっ、水かけないでください♪\nお兄さん手が大きいから\n勢い激しすぎますよ～っ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1353008, 135331, 0, 0.0, 1, "夏の日差しは強くて眩しいから\nその分影は暗く沈んで…なんだか\nいけないものに感じませんか…？\n…なんて、気のせいですよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1353009, 135331, 0, 0.0, 1, "お兄さんと二人っきり…\n緊張で汗かいちゃっても\n海の中ならバレなくていいです…\nふふ、なんでもないですよ～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1353010, 135331, 0, 0.0, 1, "この夏がずっと続けば…なんて\nわがままだってわかってます…\nだからせめて、今だけでも…\nねぇ、お兄さん…一緒に…っ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1353011, 135301, 0, 2.7, 2, "お誕生日おめでとうございます♪\n今日は忘れられないお誕生日に\nなりそうですね…\nあの夏の夕日みたいに…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1353012, 135331, 0, 0.0, 1, "お兄さんへの誕生日プレゼントは\nかわいい妹と海でバカンスです♪\n今日はお兄さんを独り占め\nしちゃいますから…えへへっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1354001, 135401, 0, 0.0, 7, "遊びとか─バカンスとか\nそんなの、時間の無駄では…\n私には─そう思えます", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1354002, 135401, 0, 9.0, 6, "青い空も、青い海も─\nジオ・ゲヘナには、ないです\nだから─忘れないよう、\n見ておこうかと", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1354003, 135401, 0, 7.0, 4, "夏だから、休む─\n奇妙な文化ですね\n季節なんて関係なく─\n休むなら、休めばいいのに", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1354004, 135401, 0, 6.1, 7, "貴方も、夏の正装なんですね\n─いえ、別に…\n珍しい格好だと思っただけです\n他には何も─", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1354005, 135401, 0, 7.6, 1, "貴方の考えてること、\nやっぱりよくわかりません\nでも─前よりは、ちょっと…\nほんの少しだけ…", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1354006, 135431, 0, 0.0, 1, "ハンモック─悪くないですね\n風に合わせて、静かに揺れて…\nスイカの、栽培みたいで\nこれもバカンスの楽しみ方─？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1354007, 135431, 0, 0.0, 1, "─心配しなくても、\n海に落ちたりしませんよ\n貴方みたいに、無邪気に\nはしゃいだりしませんし…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1354008, 135431, 0, 0.0, 1, "媛からの命令…どういう狙いか\nわからないことが多くて─\nいつかはわかるように\nなるんでしょうか─", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1354009, 135431, 0, 0.0, 1, "─必要以上に休むのは、\n性に合いませんが─\nまあ、たまには\n背も伸びるかもですし─", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1354010, 135431, 0, 0.0, 1, "─二人だと、狭いかな…\n…いえ、何でもないです\nただのおかしな思いつきなので\n気にしないでください", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1354011, 135401, 0, 15.2, 1, "誕生日、おめでとうございます\n─贈り物？　ありませんよ\n─一緒に過ごせるだけでいい？\nまあ、そんなのでよければ", 6, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1354012, 135431, 0, 0.0, 1, "今日は貴方の誕生日、ですか\nそれなら、ここの隣─\n少しだけ譲ってあげてもいいです\n─少しだけ、ですよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1355001, 135501, 0, 3.1, 1, "ようこそ、あたしたちの街へ！\nこっちの世界で知りたい事は\nあたしに何でも聞いてね！\nおいしい名物料理とか興味ない？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1355002, 135501, 0, 8.9, 9, "乾いた風が気持ちいいでしょ？\nこの街を散歩するには\n涼しさと動きやすさが重要っ♪\nあれ、今見とれちゃってた～？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1355003, 135501, 0, 3.5, 1, "騎士くん…その格好、暑くない？\nこっちで過ごしやすそうな服、\nあたしが見繕ってあげよっか？\nサラサリアの民族衣装とかさ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1355004, 135501, 0, 6.1, 8, "情熱の風が吹くこの国も…やっと\n新しい運命が動き出したんだね\nう～、体も心も熱くなってきた！\n騎士くんもどう？　燃えてる！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1355005, 135501, 0, 4.9, 2, "見知らぬ未来の道も\n一緒に歩けるなら楽しみだね♪\nこの足でどこまでも、行けるよ\nドキドキも心配も全部一緒に！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1355006, 135531, 0, 0.0, 1, "みんなの笑顔を守るためなら\n何があっても立ち上がる！\nたとえ誰が相手だって\nあたしは絶対負けないんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1355007, 135531, 0, 0.0, 1, "こーんな大舞台での試合だし、\n力いっぱいぶつかり合おうか！\n怪我させるかもなんて心配は\nいらないよ？　さあおいで！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1355008, 135531, 0, 0.0, 1, "あはは…滾ってきたよ！\nヒヨリちゃんやレイちゃん\nそして騎士くんと手に入れた未来\n心を燃やして生きてくよ～っ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1355009, 135531, 0, 0.0, 1, "騎士くんが応援してくれてる…\nこ～りゃみっともない所は\n見せらんないね！\nよーし、準備も覚悟も万端だっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1355010, 135531, 0, 0.0, 1, "どんなに離れてたって\n騎士くんと絆でつながってる…\nなら、あたしは絶対に負けない！\nそれを証明してみせる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1355011, 135501, 0, 6.2, 4, "騎士くん、誕生日おめでと～\nこれでやっと一緒に麦しゅわを…\nってまだダメなんだっけ？\nあ～早く来い…もっと先の未来！", 8, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1355012, 135531, 0, 0.0, 1, "今日の武闘会、頑張っちゃうよ！\n騎士くんも応援よろしくね～！\nキミへの誕生日プレゼントに\n優勝賞品ゲットしてくるからさ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1356001, 135601, 0, 0.0, 2, "よく来たな、私たちの街へ\nゆっくり休んでいってくれ\n私たちに未来をくれた恩人だ\n私も皆も心から歓迎するよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1356002, 135601, 0, 12.3, 4, "トーゴクは観光に訪れる者も多い\n名所案内？　私の弟子たちに\n頼んでみよう。彼らなら詳しいし\n私は、ほら…そういうのは苦手だ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1356003, 135601, 0, 5.2, 2, "街の人々を導くことが私の役目…\n今は、悪くないと思っている\n大変ではあるがやりがいも多い\nキミと出会えたこともその一つだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1356004, 135601, 0, 8.9, 5, "どうした、そんなにまじまじと…\n私は何か変な顔をしていたのか？\n…いや、考え事はしていないが\nぼうっとしているとよく聞かれる", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1356005, 135601, 0, 0.0, 2, "…あぁ、すまない。少しばかり\n感じ入っていたよ。この未来は\n戦いの末に皆で勝ち取ったもの\nなんだ…とな。大切にしなくては", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1356006, 135631, 0, 0.0, 1, "あぁ…いい夜だ\nたとえここが複製されただけの\n世界でも、月の美しさは同じ…\nキミもそう思うだろう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1356007, 135631, 0, 0.0, 1, "世界のため、人々のため、\n私は強くあらねばならなかった\nそうして手に入れた強さが、\n今は私の誇りになっているんだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1356008, 135631, 0, 0.0, 1, "真剣勝負の中でこそ、\n交わすことができる言葉がある…\nなに、声に出す必要はない\n刃は時に雄弁に、心を語るものだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1356009, 135631, 0, 0.0, 1, "剣をぶつけ合い火花を散らす…\nその輝きは一瞬のもの。だが\n互いの心に宿した火は、決して\n消えはしない。希望と同じだよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1356010, 135631, 0, 0.0, 1, "今こそこの剣に誓おう\nたとえ隔てられた世界に在っても\n私の心はキミと共にあると、\nかけがえのない仲間として…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1356011, 135601, 0, 0.0, 2, "今日はキミの誕生日か。方々で\n祝われただろうが、私からも\nおめでとう…そしてありがとう\nキミがいてくれて、よかった…", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1356012, 135631, 0, 0.0, 1, "誕生日か…今まで私はその\n価値を知ることもなかった\nだが今は、心からうれしく思うよ\nキミが生まれてくれて、よかった", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1357001, 135701, 0, 12.5, 5, "この服かわいいと思うけど\nちょっとセクシーすぎないかな…\n騎士くんはこういうの…好き？\nなら…いいけど…っ", 8, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1357002, 135701, 0, 12.7, 8, "ふー、暑いね騎士くん…そうだ！\nオアシスで水浴びしようよっ！\n冷たい水に浸かれば火照った体も\n…って二人っきりは恥ずかしい…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1357003, 135701, 0, 10.0, 7, "たん、たたたん、キメっ！\nえへへ、街の人に教えてもらった\nサラサリアのダンスだよ♪\n騎士くんも一緒に踊ろ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1357004, 135701, 0, 13.7, 8, "こんなに熱くなって赤くなって\n汗もいっぱいかいちゃうのは\nサラサリアの太陽のせいかな…？\nそれとも…な、なんでもないっ！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1357005, 135701, 0, 6.5, 2, "あ、あたしだって、いざとなれば\n積極的になるんだからね？\n…猫には狩猟本能があるってこと\n覚えておいてよ？　…えへへへ♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1357006, 135731, 0, 0.0, 1, "騎士くんと夜の空中散歩…♪\n幸せだな…この時間がずっと\n続けばってくらい…騎士くんも\n同じ気持ちだとうれしいなぁ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1357007, 135731, 0, 0.0, 1, "風が気持ちよくて景色もきれいで\n隣に騎士くんがいてくれて…\nうう…憧れが詰まりすぎてて\n心臓が飛び出しちゃいそう！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1357008, 135731, 0, 0.0, 1, "いつもより笑ったからかな…\nほっぺたが少し痛いや\nもしかして騎士くんも？\nえへへ、お揃いだねっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1357009, 135731, 0, 0.0, 1, "夜はちょっと冷えるね…\nいつもより肌がでてるからかな…\nえ！？　温めてくれる…っ！？\nそ…その一言で熱くなっちゃった", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1357010, 135731, 0, 0.0, 1, "言葉だけじゃ伝えきれないね、\n自分の気持ちって…だから\n手を繋いだりして、心の距離を\n少しでも近づけようとするんだね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1357011, 135701, 0, 10.8, 5, "お誕生日おめでとう、騎士くん！\n広場で盛大にお祝いしようねっ♪\n食べ物にダンスにそれから…\nまた、一緒に絨毯くんに乗ろ？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1357012, 135731, 0, 0.0, 1, "ハッピーバースデー♪　行きたい\nところがあったら言って！\n絨毯くんとあたしが、どこへでも\n連れていってあげるっ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1358001, 135801, 0, 2.6, 4, "あ、えっと、その…す、すまね\nひ、人と話すの慣れてねぇで…\nこういうとき、何話せばいいか\nよくわがんねぇんだ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1358002, 135801, 0, 8.1, 6, "あての周りにいる子たち…\nみんな、すげぇ子ばっかなんだよ\nあ、あてもみんなみてぇに、って\nいつも思ってっけど、難しいな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1358003, 135801, 0, 8.7, 5, "『久遠氷河』はあてらの土地…\n静かで涼しくて、いいとこだよ\nお、おめさも気に入ってくれたら\nうれしいな…なんて、えへへ…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1358004, 135801, 0, 9.7, 1, "お、おめさは、あての傍にいて…\nさ、寒かったりはしねぇか？\n…だ、大丈夫ならいいけど…\nその…無理はしねぇでくれな", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1358005, 135801, 0, 12.2, 7, "男の人と話すの、緊張するけど…\n騎士さならちょっと安心するの、\nなんでだろ…って、き、騎士さ？\nい…今の聞いてた？　ひえぇ…！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1358006, 135831, 0, 0.0, 1, "あのね、あて…三味線の弾き方は\nお母から教わったんだ…\nこの先どれだけ経ったって、\n絶対に忘れねぇ、宝物だよ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1358007, 135831, 0, 0.0, 1, "仁太坊、ハルちゃ、竹山さ…\nみんなも手伝ってくんろ\nあてが奏でる、精いっぱいの三味\n騎士さにも、聴いてほしいから…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1358008, 135831, 0, 0.0, 1, "雪の積もる音、氷の軋む音…\nここも賑やかな場所なんだな\n今まで気づかなかった音も、\n前より良く聴こえてくるよ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1358009, 135831, 0, 0.0, 1, "あては雪女、身も心も久遠の氷と\n共にある…そう思ってた\nでも…やっぱ求めちまうみてぇだ\nぽかぽかする、騎士さの温もり…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1358010, 135831, 0, 0.0, 1, "姫ちゃんやサツキちゃん…\nみんなに勇気を教えてもらった\nだからあても、いつか伝えるんだ\n氷に閉じ込めてた、この心を…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1358011, 135801, 0, 11.8, 5, "お、おめさの誕生日、かぁ…\nそんなめでてぇ日に、あてと\n一緒にいてくれるの…うれしいな\nあ、あてにもお祝いさせてくんろ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1358012, 135831, 0, 0.0, 1, "今日は騎士さの誕生日、だな\nあてからも…お祝いの曲、贈るよ\n雪女の一族に伝わる、大事な曲…\n…特別な相手にだけ聴かせるんだ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1359001, 135901, 0, 4.4, 1, "んだよ、妙にガンくれるな\n俺ばっかり見てっと\n足元がお留守になっちまうぜ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1359002, 135901, 0, 6.4, 1, "露出しすぎだぁ？\nゴテゴテ鎧う方が動きづれぇだろ\n「鬼に衣」って言うしな、昔から", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1359003, 135901, 0, 0.0, 1, "俺ら鬼のあいだじゃァ\nケジメは何より大事でな\n図体ばかりデカくても\nスジを通せねぇ奴ぁ三下だぜ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1359004, 135901, 0, 3.8, 2, "矢の傷痕が心配だって？\n騎士公に心配されるほど\nヤワな鍛え方してねェよ\n試してみっか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1359005, 135901, 0, 8.1, 2, "背負い込んでるモンが\n重いヤツはやっぱり強ぇよ\nけど騎士公、潰れちまいそうなら\n俺に半分寄越しな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1359006, 135931, 0, 0.0, 1, "おう、来たな騎士公\nここがジオ・ニヴルヘルは\n夜叉峠の鬼の村だ\n案内してやっから付いてきな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1359007, 135931, 0, 0.0, 1, "鬼族の若い衆はちっとばかし\n荒っぽいかもしれねェが、\n気のいいヤツらでな\n良くしてやってくれ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1359008, 135931, 0, 0.0, 1, "内輪揉めは拳固一発落としゃ済む\nが、外の脅威はそうはいかねェ\n何が来ようと、\n俺が通しゃしねェがな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1359009, 135931, 0, 0.0, 1, "長旅で疲れたろ\nちっと腰を落ち着けながら\n騎士公の土産話を聞かせてくれよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1359010, 135931, 0, 0.0, 1, "面が気になるって？\n気に入っちゃいるが、\n魔法の道具とかじゃねェよ\nほら、お前さんも被ってみっか", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1359011, 135901, 0, 0.0, 2, "誕生日おめでとさん\nひとまず一杯グイっといっとけ\nハレの日なんだ、遠慮するなよ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1359012, 135931, 0, 0.0, 1, "よお、騎士公。ちっとツラ貸せ\n村で一席宴があるんだ\nお前さん、誕生日だろ？\n俺たちが祝ってやるよ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1360001, 136001, 0, 0.0, 4, "生きている…\nそれだけでも幸運なのはわかって\nいるのです。それでも、求めて\nしまうわたしがいるのです", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1360002, 136001, 0, 8.0, 1, "記憶を失う前でしたら、きっと\nこの世界をご案内できたと思うの\nですが…あ、おいしい虫が獲れる\n場所なら、ご案内できます", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1360003, 136001, 0, 7.4, 2, "あなたと出会えたこと…\nそれは間違いなく、わたしに\nとっての幸せ。そしてできること\nなら、ずっとあなたのそばに…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1360004, 136001, 0, 10.5, 1, "なーちゃんと騎士さまは、\nとても大切な友達。友達の\nためなら、わたしはなんでもする\nと思います。えぇ、なんでも", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1360005, 136001, 0, 0.0, 1, "記憶については今はこのままで…\n過去の行いのことを思うと、\n記憶を取り戻したとき、わたしが\nどうなるか分かりませんので…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1360006, 136031, 0, 0.0, 1, "あぁ…ここは冷たい…\nそれに、何も思い出せません…\n誰か…いらっしゃいませんか\nわたしを救ってくれる方は…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1360007, 136031, 0, 0.0, 1, "寒い…一人でいるというのは、\nこんなにも寒いものなのですね\nぬくもりが欲しい…耐えがたい…\nあぁ…騎士さま…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1360008, 136031, 0, 0.0, 1, "お腹が空いて、空いて…\nほんとうに、苦しいです…\n…でも、本当に飢えているのは\n体より、心の方なのかも…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1360009, 136031, 0, 0.0, 1, "あたたかい光の先には、なにが\n待っているのでしょう…安全な\n場所から動くのは怖い…それでも\nこの光に惹かれてしまうのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1360010, 136031, 0, 0.0, 1, "騎士さまとお会いできなければ\nわたしの心は闇に囚われたまま…\n命がけでお守りし、お仕えします\n騎士さま…わたしの光", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1360011, 136001, 0, 0.0, 2, "お誕生日、おめでとうございます\nあなたが生まれた日にご一緒\nできたことを、うれしく思います", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1360012, 136031, 0, 0.0, 1, "誕生日…それはとても素敵な日\n本人にとっても、周りの人に\nとっても、きっと。わたしも、\nその輪の中に入れたら…", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1361001, 136101, 0, 4.8, 2, "私はサクラ！　今回も訳あって、\nランドソルでソロ活動中！\nこの素敵な街で、私の毎日、\nまた何かが変わるかもしれんね！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1361002, 136101, 0, 4.9, 7, "ゾンビは…アストルムでは\n珍しくなかと？　モ、モンスター\nやなかとよ！　倒しても経験値に\nならんばいっ。討伐せんで～！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1361003, 136101, 0, 8.7, 4, "はっ。ズ、ズレとる…\nどやんすどやんす…\nこ…これで…よしっと、ふう\nまた取れるとこやった、首が", 10, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1361004, 136101, 0, 4.8, 2, "騎士さんもサガば好きになって、\nそれからフランシュシュの…\n私のファンになってくれたら\nうれしかね、えへへ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1361005, 136101, 0, 0.0, 6, "騎士さん、「持っとる」って\n言われません？　私の知っとる\n人にどっか似とるっていうか…\n性格は違うとに…不思議やね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1361006, 136131, 0, 0.0, 1, "おはようございまーす！\nフランシュシュ１号のライブ\nですっ！　伝説が、ランドソルに\nやってきました！　聴いとって！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1361007, 136131, 0, 0.0, 1, "サガの街はよかとこやけど、\nランドソルも治安のよか！\nここで安心安全に\n生きていくとも素敵かもっ…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1361008, 136131, 0, 0.0, 1, "騎士さんのおかげで、\nライブは大成功の予感っ\n天気も最高やし、今すぐにも\n素敵なことが起こりそうっ…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1361009, 136131, 0, 0.0, 1, "騎士さんって、いつも\n女の子に囲まれとらん…？\nもしかしてアイドル向きかもっ\nん～、あの人に会わせたか～", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1361010, 136131, 0, 0.0, 1, "だめですよ、騎士さんっ\nみんなのハートに\n火ばつけたっちゃけん、\n責任ば取らんとっ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1361011, 136101, 0, 6.8, 2, "お誕生日おめでとうございます！\nサガでももちろん、こやんか日は\nメンバー皆でパーティーやけんっ\nえへへ…ワクワクしてきたと♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1361012, 136131, 0, 0.0, 1, "私、今日は騎士さんのために、\n心ば込めて歌うけんっ！\nハッピーバースデー、サガ！\nえへへ、縁起のよか歌ばいっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1362001, 136201, 0, 0.0, 2, "ごっはん～、ごっはん～♪\n今日の訓練はいつもより\nがんばったから、ごほーびに\nごはんいっぱいもらっちゃおっ！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1362002, 136201, 0, 6.2, 2, "ナーナね、かけっこは得意なの！\n「お逃げ足だけは一人前ですわ」\nって、前にカノンちゃんにも\nほめてもらったんだよ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1362003, 136201, 0, 6.3, 1, "ジオ・ゲヘナはきびしーとこだし\n怖い魔物がたくさんいるけど…\nお友だちもたくさんいるから、\n毎日楽しいんだ♪", 7, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1362004, 136201, 0, 9.4, 2, "甘い物はね、おいしいだけ\nじゃなくて、みんなで食べると\nいっぱいうれしくなるんだよ\nだから騎士ちゃんも、どーぞっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1362005, 136201, 0, 5.5, 2, "ナーナはまだまだダメダメで\nよわっぴぃウサギさんだけど…\n騎士ちゃんが応援してくれるから\nいっぱいがんばれちゃうんだ！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1362006, 136231, 0, 0.0, 1, "ぴえぇ～…びしょびしょだよぉ…\nあっ、でもこれなら、思いっきり\n水遊びしても怒られないかも？\nえへへ、ナーナあったまいい～♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1362007, 136231, 0, 0.0, 1, "んむ、おくちにお水が…あれ？\nこのお水…あまあまだぁ…\nえっ、これ全部あまあまなの？\nほわわわぁ～…ご、ごくり…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1362008, 136231, 0, 0.0, 1, "クルルちゃんはナーナのいとこで\n大切なお友だちなんだよっ♪\n住んでる世界は離れてても、\nココロはず～っと一緒なんだっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1362009, 136231, 0, 0.0, 1, "ナーナだけびっしょりなの、\nちょっと恥ずかしいかもぉ…\n騎士ちゃんも、どぼーんって\nしちゃお！　絶対たのしーよっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1362010, 136231, 0, 0.0, 1, "遊園地でいーっぱい遊んで\n甘いお池でパシャパシャして…\n毎日こんなだったらいーのになっ\nもちろん騎士ちゃんも一緒に♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1362011, 136201, 0, 8.9, 9, "騎士ちゃん、お誕生日なの？\nじゃあ今日のナーナのおやつ、\n騎士ちゃんにも半ぶ…\nぜ、ぜんぶあげちゃうっ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1362012, 136231, 0, 0.0, 1, "騎士ちゃんのお誕生日、\nナーナにもお祝いさせてねっ\n騎士ちゃんが笑顔になったら\nナーナだってうれしいもん♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1363001, 136301, 0, 0.0, 2, "メリークリスマス、騎士さん♪\n聖夜騎姫★チャーミングスカイが\nクリスマスの街をロマンチックに\nイイカンジに彩ってみせますよ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1363002, 136301, 0, 9.6, 2, "クリスマスらしく白でふわふわな\n衣装に着替えてみました★\n赤色はサンタさんとユイさんに\n譲ってあげようかな、みたいな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1363003, 136301, 0, 0.0, 1, "わたし、クリスマスは騎士さんの\n隣で過ごすって決めてたんです♪\n倍率高めの場所なのでワンチャン\n修羅場と化して楽しそうですし★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1363004, 136301, 0, 8.8, 2, "いいですか、騎士さん\nクリスマスを一緒に過ごすって\n乙女にとって特別なコトなんです\nそれぐらい好きってコトですよ★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1363005, 136301, 0, 8.2, 2, "イイコの靴下を枕元に吊るしたら\n悪い子なわたしでもプレゼント\n貰えますかね～？　それじゃあ\n騎士さん、靴下脱いでください★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1363006, 136331, 0, 0.0, 1, "あはっ★　クリスマスだからって\nダイタン過ぎましたかねー？\nでも、ふたりっきりの聖夜ですし\nイイコトしないとですよね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1363007, 136331, 0, 0.0, 1, "この状況、誰かに見られると\n勘違いされるかもですね♪\nもし修羅場になったら\nわたしを選んでくれますか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1363008, 136331, 0, 0.0, 1, "騎士さん、この服何点ですか？\n100点以下だったら\nいっそ脱いじゃおっかなーって\n…何点か、教えてください★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1363009, 136331, 0, 0.0, 1, "最近いっぱいイイコトしてたぶん\n反動が来ちゃったかもです★\n騎士さんも悪い子になるまで\nぜーったい逃がしませんよ♥", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1363010, 136331, 0, 0.0, 1, "メリークリスマス、騎士さん♪\nプレゼント、何がほしいですか？\nわたしのファーストキスなら\n今すぐプレゼントできますよ…★", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1363011, 136301, 0, 6.0, 1, "騎士さん、ハッピーバースデー★\nパーティーの用意は万端ですよ\nランファさんたちも待ってるので\n早速、テレポーテーションです★", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1363012, 136331, 0, 0.0, 1, "誕生日、おめでとうございます♪\n今日が特別な思い出になるように\nわたしがシゲキ的でキケンな\nひと時をプレゼントしますね♥", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1364001, 136401, 0, 7.9, 2, "騎士クンに見てもらいたくて\n精いっぱいおめかししてみたの\n似合ってる？　ふふ、うれしい！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1364002, 136401, 0, 6.4, 1, "街の飾りつけが変わってくると\nみんなそわそわしちゃうよね\nふふっ、わたしも同じだよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1364003, 136401, 0, 3.0, 2, "騎士クンと過ごすクリスマス…\nドキドキで体が熱くなりそう\nおかげで寒さも平気だよ", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1364004, 136401, 0, 6.3, 7, "騎士クン、最近気になることや、\n欲しいものはない？\n…ふ、深い意味はないよ？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1364005, 136401, 0, 10.1, 6, "わたし本当は、欲張りなの\n騎士クンとやりたいこと、\n少しずつ叶えていきたいんだ\n…いいかな？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1364006, 136431, 0, 0.0, 1, "雪のクリスマスって、いいよね\nふわふわの雪に背中を押されて\nいつもより大胆になれちゃうの♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1364007, 136431, 0, 0.0, 1, "わたし、いつもと雰囲気違う？\nえへへっ、うれしいな\n今日は騎士クンに見せたくて\nおめかししたの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1364008, 136431, 0, 0.0, 1, "騎士クン、いつもより頼もしくて\nカッコよく見えちゃう…\nこれもクリスマスの…ううん\nデートの魔法、かな…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1364009, 136431, 0, 0.0, 1, "冬の夜って、好き\nこんなにくっついてても、\nちっとも不自然じゃないし…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1364010, 136431, 0, 0.0, 1, "この時間が終わっても\n明日も明後日もその先も、\n騎士クンと一緒にいたいの…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1364011, 136401, 0, 3.8, 5, "騎士クン、お誕生日おめでとう♪\nパーティーなら任せて\nまごころこめて用意するから", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1364012, 136431, 0, 0.0, 1, "お誕生日おめでとう♪\nほら、星も街の明かりも、\nお祝いしてくれてる…\nって、ロマンチックすぎかな？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1365001, 136501, 0, 0.0, 2, "あけましておめでとう！　弟くん\nはい、お年玉♪　足りなかったら\n追加してあげるから、遠慮なく\nお姉ちゃんに甘えてねっ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1365002, 136501, 0, 2.9, 1, "お姉ちゃん特製おみくじ～♪\nなんと大吉より上があるんだよ！\nそれは…『弟吉』！　引いたら\n最高の一年になっちゃうよ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1365003, 136501, 0, 9.2, 2, "お正月の空気って、ワクワクして\nきちゃうよね♪　初詣、お年玉、\nおせちにお正月遊び…特別な\nイベントが多いからかもね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1365004, 136501, 0, 2.8, 2, "弟くん、寒くない？　いつでも\nどこでも、お姉ちゃんが温めて\nあげる！　体だけじゃなくて、\n弟くんの心もねっ♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1365005, 136501, 0, 2.7, 5, "着物のお姉ちゃんもいいでしょ？\n弟くんなら、もっと近くで見て\nいいんだぞ？　ほらほら～♪\n襟のところとか色っぽいでしょ♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1365006, 136531, 0, 0.0, 1, "弟くんが元気に過ごせますように\n私と弟くんがもっと一緒にいられ\nますように…本年も、どうか\nよろしくお願いします", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1365007, 136531, 0, 0.0, 1, "お正月はゆっくりしようね。\n年末の忙しさを忘れちゃうくらい\n食べて休んで…そして、\n甘やかしてあげるっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1365008, 136531, 0, 0.0, 1, "今年の抱負は弟くんを去年以上に\n甘やかすこと！　…実はほぼ毎年\n同じ抱負なんだけど、これが私の\n本音なんだもん仕方ないよね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1365009, 136531, 0, 0.0, 1, "まるで神前式をしてるみたい…♪\nそれも、相手は弟くん！\nたまには呼び方を変えるのもアリ\nかも？　たとえば…あ・な・た♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1365010, 136531, 0, 0.0, 1, "着物姿の弟くんもかっこいい…♪\nって、いけないいけない…初詣で\n来てるのに、弟くんを見ちゃう…\nはぁぁ…やっぱりかっこいい…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1365011, 136501, 0, 7.0, 1, "ハッピーバースデー♪　おせちを\n用意したから一緒に食べようね！\n縁起がいいものもいっぱいだよ\n数の子とかレンコンとか！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1365012, 136531, 0, 0.0, 1, "お誕生日おめでとう！\nプレゼントの前にお参りに行こ？\nこの一年と次の一年とその次の\n一年の、お礼とお願いをしにねっ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1371001, 137101, 0, 3.1, 2, "忍者とは忍ぶ者…\nなので、海では忍び装束よりも\n水着のほうが忍べマース！\nニンニンデース♪", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1371002, 137101, 0, 6.4, 6, "新しい印結びを見てクダサイ！\n指をこうして…ショーグン？\nなんで目を逸らすデス？\n目のやり場に困る…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1371003, 137101, 0, 8.4, 2, "ついにショーグンに見せる時が\n来たようデスね…\n海でしか使えない、とっておきの\n忍法・スイカ割りを！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1371004, 137101, 0, 10.5, 1, "たとえ火の中、海の中、どこでも\nショーグンのお供をいたしマス！\nチューシン、ニノンここにあり！\n…デス♪", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1371005, 137101, 0, 5.8, 7, "この夏、この海で！\nまた新しい忍法を編み出しマス！\nショーグンと天下統一のため、\n一肌脱ぎマスよー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1371006, 137131, 0, 0.0, 1, "すごいすごい、すごいデース！\n水がキラキラしてキレイで…\nショーグンのくれたこの景色…\n最高のゴホウビデス♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1371007, 137131, 0, 0.0, 1, "新たなる忍法を思いつきまシタ！\n水手裏剣の術！　シュババババッ\nこれでショーグンの夏を涼しく\n彩りマース♪　いかがデスか？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1371008, 137131, 0, 0.0, 1, "フーコーメービな隠れ里で\nショーグンと二人、忍術に興じる\nなんて素敵な夏の過ごし方…♪\n時を止める忍法が欲しいデス", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1371009, 137131, 0, 0.0, 1, "涼しくて気持ちいいデース♪\nショーグンも一緒に水浴び\nしまセンか！　水も滴るいい\nショーグン…見惚れそうデス…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1371010, 137131, 0, 0.0, 1, "水遊びの後は冷えないように\nお気をつけデスよ？　風邪は\nマンビョーの元デス。そうだ、\nこの後サウナで温まりまショウ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1371011, 137101, 0, 0.0, 2, "めでてえめでてえめでてえな♪\nショーグンがいい一年だったと\n思えるように、チューシンとして\nもっともーっと頑張るデスよー！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1371012, 137131, 0, 0.0, 1, "お誕生日おめでとうゴザル！\n今日のために新しく編み出した\n忍法をお披露目しちゃいマス！\nそれそれー♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1374001, 137401, 0, 4.9, 1, "真実の信奉者にしてシスター\nここに推参！　…なーんてね\nこの装い、心まで清らかになるよ\nふふ、似合ってるかな、助手くん", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1374002, 137401, 0, 4.4, 6, "このベール、耳が出せるのが\n機能的だね♪　え？　時々動く\nのがかわいいって？　いきなり\n何を言い出すんだいっ！？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1374003, 137401, 0, 10.6, 5, "シスターたるもの恋をしたり\n誰かと結ばれたりは禁忌なのかな\nそれはちょっと…すごく困るね…\nあ、ああいや、こっちの話さ", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1374004, 137401, 0, 4.8, 2, "悩みがあるなら、私の耳が\nとことん聞き届けるよ\n解決は二人で一緒にね。君の\n悩みは私の悩み。そうだろう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1374005, 137401, 0, 3.2, 4, "ずっしりと重いね、この本は\n抱いていると、真実に責任を持つ\n者としての実感が湧いてくるよ\n読破するまで何日かかるかな…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1374006, 137431, 0, 0.0, 1, "厳かな光の中で、誰かを想い祈る\nのは、自分自身の真実と向き合う\nことと同じく尊い時間だ。隣に\n君がいてくれるなら、なおさらね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1374007, 137431, 0, 0.0, 1, "なんてきれいな聖堂だろうね\nいつかこんな場所で君と永遠を\n誓い合うことができたなら…\nあ、いや、その…今のナシでっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1374008, 137431, 0, 0.0, 1, "こんな時よく思い出す\nいつも私を見守ってくれる\n大切な友達を。君も、ときどき\n思い出してくれたらうれしいな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1374009, 137431, 0, 0.0, 1, "現実がいつも残酷だとしても\n誰かの心を映す真実が\n優しいものであることを祈るよ\nこの鏡に誓って、ね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1374010, 137431, 0, 0.0, 1, "きっと私は、とても幸せな人間だ\n助手くん、君というパートナーが\nいつも背中を、心を支えてくれる\nこれ以上の祝福はないよ、ふふっ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1374011, 137401, 0, 7.9, 2, "祝福の日、来たれり。そう、君が\nこの世に生を受けた特別な…\nふふっ、堅苦しかったかな♪\nハッピーバースデー、助手くんっ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1374012, 137431, 0, 0.0, 1, "祈らせてほしい。この世界に君が\n生まれてきてくれたこと…\nそれがみんなに、そして私に\nとって、どれほどの幸いだったか", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1376001, 137601, 0, 0.0, 2, "新たな姿、いかがでしょうか？\n魔力による変身、時間の限られた\nものですが…主さまに尽くす心も\nよりパワーアップしておりますよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1376002, 137601, 0, 6.2, 2, "大人の姿を手に入れはしましたが\nそれで満足してはいけませんね\n見た目だけでなく内面も成長し、\n誇れる自分になれるよう励みます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1376003, 137601, 0, 9.9, 2, "ただ強くなりたいと願うだけでは\n本当の力は得られません…\n確かな願いとともに力を望むこと\nわたくしはそう学びました", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1376004, 137601, 0, 0.0, 2, "体が大きくなると、周りの景色も\nまるで違って見えるのですね\n素晴らしいのは、主さまのお顔が\nとても近くなったことです", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1376005, 137601, 0, 9.9, 7, "わたくしはこれまでと変わりなく\n主さまに寄り添い支えていきます\nそれは使命や責務ではなく…\nわたくし自身の望みですから", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1376006, 137631, 0, 0.0, 1, "『嚮導幼君』の権能は、\n理の破壊と再構成を行う禁断の力\nしかし世界を救うためならば\nこの上なく頼もしい力となります", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1376007, 137631, 0, 0.0, 1, "世界を巡る精霊たちの声が、\n以前よりもはっきりと聞こえます\n景色も音も、鮮やかになって…\nその美しさに感じ入るほどです…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1376008, 137631, 0, 0.0, 1, "七冠であるお父さまに比べれば\nわたくしは未熟な存在でしょう\nそれでも守れるものがあるならば\n戦うことに迷いなどありません…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1376009, 137631, 0, 0.0, 1, "この力はお父さまから継いだもの\nしかし力を目覚めさせたのは、\nわたくしが抱いた何よりの願い…\n主さまを愛する心に他なりません", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1376010, 137631, 0, 0.0, 1, "主さまがわたくしを信じ、\n運命さえも委ねてくださる…\nそれが何より心強いのです\nその想いに、必ず応えてみせます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1376011, 137601, 0, 0.0, 7, "本日は主さまの誕生日ですね\nぜひお祝いさせてくださいまし\nお料理、添い寝、ハグになでなで\n何でも用意しておりますので♪", 7, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1376012, 137631, 0, 0.0, 1, "お誕生日、おめでとうございます\nこれからも共に経験を積み重ね\nより大きく、さらに大きく\n成長をして参りましょう", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1379001, 137901, 0, 3.5, 2, "晴れ着姿の妹はどうですか～？\n気合い入れて作ったんですよ☆\n笑う門には福着たる、です！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1379002, 137901, 0, 5.0, 1, "お正月と言えば、やっぱり\nおみくじは欠かせませんよね！\n今年の運勢は…どんな結果でも\nお兄ちゃんと一緒なら大吉です！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1379003, 137901, 0, 2.5, 2, "今年はどんな年でしょうね？\n私は、お兄ちゃんとたくさん\n一緒にいられる年になって\nくれると思ってます☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1379004, 137901, 0, 6.8, 2, "羽子板、投げゴマ、福笑い…\n正月遊びはいっぱいありますよ！\nコンプリート目指して、\n私と遊びましょう、お兄ちゃん♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1379005, 137901, 0, 6.4, 4, "毎年書き初めをすると、なんでか\nみんな首を傾げるんですよね～\n私、何か変なことでも\n書いてるんでしょうか…？", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1379006, 137931, 0, 0.0, 1, "絵馬に書いた願い事、ですか？\nそれは、ええっと…秘密ですっ！\nきっと叶うって信じてるから、\nそしたら教えてあげますねっ♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1379007, 137931, 0, 0.0, 1, "きれいな雪景色ですねっ！\nお参りにも気合が入りますっ\nお願いは一つだけ、お兄ちゃんと\n一緒に…あっ内緒だったのに！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1379008, 137931, 0, 0.0, 1, "こうしてお正月から一緒に\n過ごせるなんて、今年の運勢は\n大吉決定です♪　お兄ちゃんも\n妹が充実した一年になりますね♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1379009, 137931, 0, 0.0, 1, "楽しいですね、初詣デート♪\nお参りして、おいしいもの食べて\nお正月遊びをしてこたつに入って\nずーっと一緒ですよ、お兄ちゃん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1379010, 137931, 0, 0.0, 1, "やっぱり冷えますね。絵馬を\n掛けたら、手を温めてあげます♪\n今年の私は頼りになる妹ですから\nどーんと頼ってくださいね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1379011, 137901, 0, 6.1, 1, "お誕生日おめでとうございます☆\n今年もいい年になりますように…\nいえっ、私がいい年にします！\nなんてったって頼れる妹ですし！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1379012, 137931, 0, 0.0, 1, "ハッピーバースデーお兄ちゃん！\nパーティーの準備はばっちりです\nケーキに餅つき、蠟燭に初詣♪\nいいとこどりして楽しみますよ！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1380001, 138001, 0, 9.6, 2, "食と文化の祭典、ランドソル万博\nジオ・二ヴルヘルも国を挙げ\n準備してまいりました。どうか\n皆様にご満足いただけますように", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1380002, 138001, 0, 6.7, 2, "姫として万博に参加するのは\n少しだけ緊張しますが…\n会場には騎士様がいてくださる…\n…ええ、きっと大丈夫ですわ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1380003, 138001, 0, 8.3, 1, "この装い、身のこなしに気を付け\nないと…見えてしまいそうで…\n…いえ、これはいわば正装です\n恥ずかしがってはなりませんね", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1380004, 138001, 0, 11.2, 2, "騎士様のおかげで民と国の矜持を\n守ることができました。あなたの\n優しさに頼ってばかりですが…\nいつかお返しできたら…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1380005, 138001, 0, 5.1, 5, "騎士様は、手作りのお菓子は\nお好きでしょうか？　…実は\nわたくし、お菓子作りにも興味が\nあって…あの、いかがですか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1380006, 138031, 0, 0.0, 1, "大いなる海神よ、イサナミよ\n今わたくしの祈りに応え、\n万物に慈悲をお与えください\nそしてこの手に、民を照らす力を", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1380007, 138031, 0, 0.0, 1, "海は太古より万物を育むと同時に\n数多の命を飲み込んできました\n生と死のあわい…その在り方は\n我が国に近いのかもしれませんね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1380008, 138031, 0, 0.0, 1, "あなたたちも、わたくしに\n力を貸してくれるのですか？\n…ふふ、ありがとうございます\nでは、共に参りましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1380009, 138031, 0, 0.0, 1, "騎士様も慈愛の心をお持ちの方\nその魂が曇ることないよう、\nわたくしがお守りいたします\nどんなときも、ずっと", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1380010, 138031, 0, 0.0, 1, "騎士様が水底に堕ちたときは\n必ずお助けすると誓います\nこれは姫としての責務ではなく\nわたくし個人の…約束、ですわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1380011, 138001, 0, 7.2, 1, "おめでとうございます、騎士様\nお誕生日祝いの料理をどうぞ\n数か月前から練習を重ねたので…\nほんの少しだけ、自信作です…♪", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1380012, 138031, 0, 0.0, 1, "騎士様、お誕生日おめでとう\nございます。これからもこの日を\n二人でお祝いできれば…などと、\n少々贅沢すぎるでしょうか…？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1381001, 138101, 0, 8.1, 1, "次は『ジオ・テオゴニア万博』を\n開催したいですね。国民もきっと\n歓迎してくれます。わたしも腕に\nよりをかけてお菓子を作りますよ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1381002, 138101, 0, 7.2, 7, "争いは好みませんが、国を負った\n勝負となれば話は違います…\nええ、つい熱くなってしまうのは\n副賞のためでは、決して…", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1381003, 138101, 0, 8.1, 2, "各世界の姫やシュヴァリエとの\n交流は、危機の中ではあっても\n胸躍る時間でした。他国の\n民たちとの交流も、同じように", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1381004, 138101, 0, 9.2, 2, "こうして、プリンセスとして立つ\nわたしの隣にあなたがいてくれて\nとても心強く感じます。ずっと\n一緒に…と、望んでしまいますね", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1381005, 138101, 0, 0.0, 5, "あなたにお菓子を食べてもらう\n時間は特別で、少し気恥ずかしく\n胸がドキドキしてしまうのです\n誰にも内緒ですよ…？", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1381006, 138131, 0, 0.0, 1, "ジオ・テオゴニア─\nわたしの愛するこの世界が、\nわたしに力をくれるのを感じます\nどんな敵からも護ってみせますよ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1381007, 138131, 0, 0.0, 1, "少し羽が硬くなりましたが…\nこれならクローチェのように\n素早く飛ぶことができますね\nさぁ、掴まってください騎士", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1381008, 138131, 0, 0.0, 1, "平和を脅かす者は誰であろうと\n許しません。ジオ・テオゴニアの\nプリンセスとして、皆のために\nこの力を振るいましょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1381009, 138131, 0, 0.0, 1, "ただ傲慢なだけなら、裸のプリン\nセスになっていたはず…ですが、\n助言をくれる存在がいるから、\nわたしはわたしでいられるのです", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1381010, 138131, 0, 0.0, 1, "時折、自分がひどく傲慢な者だと\n気づかされます。あなたを帰さな\nい方法を考えてしまって…大丈夫\n実行はしませんよ…今は、まだ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1381011, 138101, 0, 2.6, 1, "お誕生日おめでとうございます\nジオ・テオゴニアで採れた\nフルーツをたっぷり使った\nケーキを、どうぞ召し上がれ", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1381012, 138131, 0, 0.0, 1, "今日は騎士の誕生日という特別な\n日。一緒に空中散歩はどうですか\nジオ・テオゴニアの美しい風景を\nあなただけにプレゼントしますね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1382001, 138201, 0, 7.0, 2, "騎士殿や、此度の装い\n御覧じて如何に思し召すや？\nたまさかに異彩なる有様も\n興あるものでありんしょう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1382002, 138201, 0, 5.7, 2, "民草の意を統べるは\n言の葉ほどに易きは非ず\n稚児らの信じ奉る心延え\n無碍には致すまじく候", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1382003, 138201, 0, 0.0, 2, "わっちにとりて姿形は\n些事なれど、モネなどは些か\n心安からざる様子\nその様も愛らしゅうものですが喃", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1382004, 138201, 0, 6.4, 2, "此の身は不変の泥濘なれど\n世は儚く、移ろひゆく物\n願わくば泡沫の世の変はり\n騎士殿と愛でまほしく思いまする", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1382005, 138201, 0, 0.0, 2, "姿のみならず。わっちの胸中にも\n変転の兆しありけり。其れも皆\n騎士殿の御座しませばこそ\n実にも罪作りなる御方です喃", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1382006, 138231, 0, 0.0, 1, "移ろいて闇路を進み行き、\n光明を信じて揺るがず\n不退転たる我が矜持にて\n百鬼千妖万魔を束ね導かん", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1382007, 138231, 0, 0.0, 1, "眼を、心を\n民草と交え、偽りなく相対す\n而して初めて\n悟りに至ることもあり申す", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1382008, 138231, 0, 0.0, 1, "鬼神が如き猛き力に、\n仏が如き静寂なる心\n清濁共に呑み込みてこそ\n拓き得る道もありんしょう", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1382009, 138231, 0, 0.0, 1, "お前さまの真心こそが\n真に皆人を救い給ふ度に\n然る道筋もあるものかと\n驚嘆するばかりにて候", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1382010, 138231, 0, 0.0, 1, "騎士殿と共にあらば\n我が内に燃ゆる煉火、\n豈絶ゆる時は参り来ませぬ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1382011, 138201, 0, 11.1, 2, "此度は騎士殿の格別なる日\nお前さまらの習はしに従ひて\n寿ぎの詞を贈らせてくだしゃんせ\n『はっぴーばーすでー』…♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1382012, 138231, 0, 0.0, 1, "巷には『さぷらいず』なる\n戯れがあると聞き侍り\n騎士殿の生誕を祝う贈り物、\n匣に収めたわっちなどいかがかえ", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1801001, 180101, 0, 4.2, 2, "チェンジ、プリンセスフォーム！\n…くぅぅー！　やっぱり変身って\nすっごくワクワクするよね！\n騎士くん、一緒にポーズとって！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1801002, 180101, 0, 0.0, 4, "ううぅ…変身した後は\nお腹が減るって言ってたけど、\nほんとなんだね…\nご飯まだかなぁ…お腹空いたな", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1801003, 180101, 0, 7.8, 2, "この力で、世界中の人たちを\n助けにいくよ！　騎士くんも\n何かあったら言ってね？\n困った時はお互いさまさま♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1801004, 180101, 0, 11.0, 2, "えっと…騎士くん\n聞いていいかな？　このカッコ…\n…え？　似合ってる…？\nえへへっ、ほめられちゃった♪", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1801005, 180101, 0, 10.1, 2, "騎士くん、ちょっとほっぺ触るね\nどうどう？　肉球、\nいつもよりあったかいでしょ？\nこれぞ、プリンセス肉球だー♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1801006, 180131, 0, 0.0, 1, "うにゃにゃにゃにゃー！\nパワーが溢れて止まらない！\nこれがあたしと騎士くんの！\nプリンセスフォームの力だぁー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1801007, 180131, 0, 0.0, 1, "すごい力だよね…だけど、\nあたし、使いこなしてみせるよ！\n大切な人たちに、\nもう傷ついて欲しくないもん！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1801008, 180131, 0, 0.0, 1, "あたしの姿が変わっても、\n騎士くんはそのままなんだ…あ！\nこれと同じ服作る？　そしたら\n一緒にプリンセスフォームだよ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1801009, 180131, 0, 0.0, 1, "変身するとね？\n胸の奥が燃えてるみたいに\n熱くなるんだ…\n騎士くん、あたしに何かした？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1801010, 180131, 0, 0.0, 1, "この力は、騎士くんと一緒に\nいないと使えないんだよね？\nじゃあ、ずーっといちゃおっかな\n…じょ、冗談！　冗談だよー！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1801011, 180101, 0, 5.9, 1, "お誕生日おめでとう、騎士くん！\nお祝いになんでも食べて！\n串焼き…リンゴ飴…うーん、\nそうだ！　全部食べちゃおー！", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1801012, 180131, 0, 0.0, 1, "ハッピーバースデー、騎士くん！\nあたしにしてほしいこと、ない？\n今日は騎士くんのためだけに\n超頑張っちゃうよ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1802001, 180201, 0, 3.5, 7, "チェンジ・プリンセスフォーム…\nあれ！？　変身しちゃってる！？\n騎士クン！　今のはちょっと、\n言ってみたかっただけで…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1802002, 180201, 0, 9.5, 2, "変身したらお腹が減るけど\n何かを頑張った後のごはんって\n美味しいから、楽しみだよね\n今日は何を食べようかな…♪", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1802003, 180201, 0, 13.3, 7, "この力を使ってる時、騎士クンの\n格好は変わらないんだね…\nその…わたしがプリンセスなら\n王子様みたいに…なんでもない！", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1802004, 180201, 0, 5.3, 5, "騎士クンのためなら、わたしは\nどんなことが起きても平気だよ\n騎士クンと一緒にいられて、\n騎士クンが笑顔でいてくれたら…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1802005, 180201, 0, 3.3, 7, "騎士クンと二人でパワーアップ…\nううぅ～…すごくうれしくて、\n胸の奥の気持ちが溢れ出そう\nこれも力の影響なのかな…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1802006, 180231, 0, 0.0, 1, "たとえこの先に、何があっても…\n騎士クンを、みんなを守りたいの\nわたしの、全てをかけて！\nこの思いは…誰にも負けない！　", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1802007, 180231, 0, 0.0, 1, "これだけの大きな魔力、\n本当に使いこなせるのかな…\n…ううん、わたしはもう迷わない\nこの瞬間に、全力を出し切る！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1802008, 180231, 0, 0.0, 1, "戦うのは、わたしにまかせて！\n今の幸せも、未来の幸せも\n壊させたりしないよ…！\n今度こそ！　必ず！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1802009, 180231, 0, 0.0, 1, "騎士クンのあったかい力\n前よりもすごく強くなってる…\nわたしの気持ちも、同じだよ\n騎士クンの気持ちは…どう？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1802010, 180231, 0, 0.0, 1, "戦いや普段の時だけじゃなくて\nもっと色んな時間を騎士クンと\nずっっと一緒に過ごしたい…\n…まずは、食事に誘わなくちゃ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1802011, 180201, 0, 6.5, 5, "お誕生日おめでとう！\n今日はずっと騎士クンを\nお祝いしてあげたいの\nだから、そばにいてもいい…？", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1802012, 180231, 0, 0.0, 1, "騎士クン、お誕生日おめでとう！\n…お祝いの気持ちだけじゃなくて\nもう一つ、伝えたいことがあるの\n…やっぱり言えないよぉ～！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1803001, 180301, 0, 4.5, 2, "プリンセスフォーム…\nすごい力だね。ありがとう\nキミのおかげで私はまた\n一つ強くなれたよ", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1803002, 180301, 0, 3.8, 6, "チェンジ！　プリンセスフォー…\nキミ！？　いつからそこに！？\n今のはその、かけ声の練習で…\n…え？　もっと凛々しく？", 3, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1803003, 180301, 0, 4.6, 1, "変身できるからといって\n日々の鍛錬は欠かさないよ\nそうだ。キミも一緒にどう？\n特別メニューを組んであるんだ", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1803004, 180301, 0, 5.6, 6, "やっぱり私が着るには、\nヒラヒラしたものが多すぎる…\n似合ってる？　そうかな…\n…ねえキミ。全部聞いてたの？", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1803005, 180301, 0, 9.2, 6, "この姿でいると、まるでキミに\n抱き締められているようだ\nふふっ、冗談だよ…キミ？\nじ、実際にはしなくていいから！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1803006, 180331, 0, 0.0, 1, "見ていてくれ！\nこれこそ私の思いが…\n私たちの力が\n一つになった証だ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1803007, 180331, 0, 0.0, 1, "何度強敵が立ちはだかろうとも\n私は決して挫けない！\n世界の滅びを食い止めてでも\nキミのそばにいてみせる！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1803008, 180331, 0, 0.0, 1, "限界なんてものはない…\nキミとの絆が私に教えてくれたよ\nだから私は剣を振るい続ける！\nあらゆる困難を断ち切るために！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1803009, 180331, 0, 0.0, 1, "キミへの思いが強さになるなら\nもっと早く素直に\nなっておけばよかったよ…\nどんな思いか？　そ、それは…！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1803010, 180331, 0, 0.0, 1, "私とキミ…お互いへの気持ちを\nより強くするにはどうしたら…\n剣を交える以外の方法を\n見つけないといけないな", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1803011, 180301, 0, 7.6, 5, "今日はキミの誕生日だ\nプレゼントを贈らせてほしいから\n一緒に買い物へいかない？\n…変身を解いてからね", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1803012, 180331, 0, 0.0, 1, "誕生日おめでとう！\nキミを祝えて私もうれしいよ\n次の誕生日もまた\nキミの隣で祝えるといいな", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1804001, 180401, 0, 4.5, 1, "オイッスー☆\n一緒にお散歩、行きませんか？\n王女様なんて呼んじゃだめですよ\n今のわたしはペコリーヌです☆", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1804002, 180401, 0, 10.9, 2, "いつもと違うこの姿、\nあなたはどう思いますか？\n強そう？　う～ん、そっちですか\nかわいい？　えっへへ、それです", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1804003, 180401, 0, 7.1, 2, "王宮から抜けだして会っていると\nなんだかドキドキしますね♪\nお付きの護衛もいませんから\nあなたが守ってくださいね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1804004, 180401, 0, 4.2, 1, "わたしがごはんのことばかり\n考えてると思っていませんか？\n半分は正解ですけど、もう半分は\n…あなたのことですよ☆", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1804005, 180401, 0, 7.7, 6, "やっぱりあなたと囲む食卓が\n一番ですね☆　いっそのこと\n王宮で一緒に住みませんか？\nやっぱり、まずいですかね～？", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1804006, 180431, 0, 0.0, 1, "超全力全開☆\n私は守られるだけの王女様じゃ\nありませんよ？　この国と…\nあなたのために、戦います！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1804007, 180431, 0, 0.0, 1, "この姿、とっても強いんですけど\nその分お腹が減るんですよ～…\nでも、それってつまり…\nもっとごはんが食べられますね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1804008, 180431, 0, 0.0, 1, "力が溢れ出て、止まりません！\nこの力で、わたしの大切なものを\nぜ～んぶ守ってみせますね\n今度は何もなくしたりしません！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1804009, 180431, 0, 0.0, 1, "あなたの力をこれまでより\n強く感じます…温かくて、\n胸がキュンキュンしちゃいます…\nえへへ、やばいですね☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1804010, 180431, 0, 0.0, 1, "お腹が空きましたね！\nさぁ、わたしと一緒にごはんを\n食べに行きましょう♪\n今日も明日も仲良くごはんです☆", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1804011, 180401, 0, 8.1, 5, "あなたの誕生日となれば、\n国を挙げてお祝いしなきゃですね\nそれとも二人きりがいいですか？\n…えへへ、照れちゃいますね☆", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1804012, 180431, 0, 0.0, 1, "今日はあなたの誕生日ですね\nおめでとうございます！　\nそれと…ありがとうございます\nこうしてわたしと出会ってくれて", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1805001, 180501, 0, 6.1, 2, "姿形が変わっても\nわたくしはわたくしのまま…\nですが、これまで以上に\n主さまにお仕えいたします", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1805002, 180501, 0, 4.5, 2, "主さまが変身するところも\n見とうございますね\nわたくしに続いて\n掛け声をどうぞ。せーの…", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1805003, 180501, 0, 5.3, 6, "プリンセス耳かきに\nプリンセスふーふー…\nあ、いえ。この姿でのサポートは\n一味違ったものにしてみようかと", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1805004, 180501, 0, 5.5, 1, "この姿はまるでおとぎ話の\n妖精のようでございます\nいつも主さまの隣で\n舞っていたいものです", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1805005, 180501, 0, 2.7, 2, "『プリンセス・フォーム』…\nいつもより力が湧いてくるのは\n心の奥底で、主さまとの絆を\n強く感じるからでしょうか", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1805006, 180531, 0, 0.0, 1, "わたくしの願いは、ただ一つ…\n末永く主さまの隣にいることです\nどんな困難もこの力で乗り越えて\n必ずや叶えてみせます！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1805007, 180531, 0, 0.0, 1, "わたくしたちの旅がどんな結末を\n迎えようとも、主さまのために\n戦います。迷いはありません\nこの力が全ての証明でございます", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1805008, 180531, 0, 0.0, 1, "もっと強く、もっと高く…\n絆が生んだこの羽で\n主さまと未来に羽ばたきます\nいつまでも…どこまでも一緒に…", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1805009, 180531, 0, 0.0, 1, "主さまが傷つくところを\nこれ以上見たくはありません\nいつも笑っていていただくために\nわたくしはこの力でお守りします", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1805010, 180531, 0, 0.0, 1, "以前にも増して主さまを近くに\n感じています\n主さまも、わたくしを感じて\nおいででしょうか…？", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1805011, 180501, 0, 0.0, 2, "わたくしは今日という日を心から\nお待ちしておりました。主さまの\nお誕生日を、全身全霊をかけて\nお祝いいたしますね", 2, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1805012, 180531, 0, 0.0, 1, "お誕生日おめでとうございます\nなにか特別にしてほしいことは\nございますか？　遠慮せずに\nなんでもお申し付けくださいまし", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1806001, 180601, 0, 4.0, 1, "あたしもなれたのね\nプリンセスフォームに…！\nねえ、見てなさいよ？\nここからのあたし、すごいから！", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1806002, 180601, 0, 2.4, 2, "あたしの魔力にびっくりした？\nしょうがないわね～\n仕方がないから\n自慢させてあげるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1806003, 180601, 0, 3.6, 1, "プリンセスフォームって\n空を飛べるのよね？\nあんたにも付き合ってもらうわよ\n秘密の特訓！", 4, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1806004, 180601, 0, 6.7, 1, "あんたみたいな底抜けのお人よし\nあたしが守ってあげなきゃ\n誰が守ってくれるってのよ\nありがたく思いなさいよね♪", 5, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1806005, 180601, 0, 6.9, 2, "この力で自分の居場所を…ううん\nみんなの居場所を守りたいわ\n…こんな風に思えるように\nなったのは、あんたのおかげかも", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1806006, 180631, 0, 0.0, 1, "チェンジ・プリンセスフォーム！\nこの力で、あんたと一緒に\nどんな限界だって\n超えてみせるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1806007, 180631, 0, 0.0, 1, "あたしはもう迷わないし\nあきらめない！\nあんたがくれたこの力で\nどこまでもあがき続けてやるわ！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1806008, 180631, 0, 0.0, 1, "ふふ～ん♪\n優秀なあたしの完っ璧な大魔法で\nどんなやつだって\nぶっ飛ばしちゃうんだから！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1806009, 180631, 0, 0.0, 1, "プリンセスフォームって\n心の繋がり…なのよね？\nつまり、あんたとあたしの心が\n深く…って、こっち見んな！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1806010, 180631, 0, 0.0, 1, "未来は、変えていけるのよね…？\nあんたがいてくれたらもうあたし\n怖いものなんてないわ！\nお願い、力を貸して！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1806011, 180601, 0, 6.2, 3, "はいはい、誕生日おめでとう\nせっかくだしみんなで\nパーティーしましょうよ\n料理は魔物料理以外でね！", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1806012, 180631, 0, 0.0, 1, "今日ってあんたの誕生日よね\nなんかしてほしいこととかある？\nあんたのこと、思いっきり\n甘やかしてあげるわ♪", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 1, 1807001, 180701, 2, 3.4, 1, "今度一緒におでかけしようよ！\n空を飛んでたら、とっても素敵な\n場所を見つけたから、\nシオリンと一緒に行きたいんだ☆", 2, 2, 1, 118301, 2, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 2, 1807002, 180701, 2, 3.8, 2, "シオリンがおすすめしてた本を\n読んでたんだ☆\nわくわくドキドキな冒険の連続で\nすっごく面白いね！", 1, 2, 1, 118301, 2, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 3, 1807003, 180701, 2, 2.9, 1, "シオリンはしっかりもの\nだけど…もしもの時は\n私が守ってあげなきゃ！\nだって、お姉ちゃんだもん！", 6, 1, 1, 118301, 1, 7.0, 7.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 4, 1807004, 180701, 2, 5.8, 6, "お姉ちゃん、今日は一緒に\n少し遠くまで冒険しに行こう？\n体調は大丈夫。ふふっ…\nお姉ちゃん、心配性なんだから", 1, 2, 1, 118401, 2, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 5, 1807005, 180701, 2, 0.0, 1, "あれ？　お姉ちゃん、その本…\n読んでくれてたんだね\nふふっ、うれしいなぁ\n読み終わったら感想聞かせてね", 1, 6, 1, 118401, 6, 6.2, 6.2, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (1, 6, 1807006, 180701, 1, 0.0, 1, "お姉ちゃんに心配されるのって\n気恥ずかしいけど…実は少し\nうれしくて。でも迷惑かけるのは\nいやだから、もっと強くなるね", 1, 5, 1, 118401, 5, 8.4, 8.4, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1807007, 180731, 0, 0.0, 1, "シオリンと一緒なら\n私たちは無敵なのだ～☆\nそうだよね、シオリン♪", 1, 0, 1, 118301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1807008, 180731, 0, 0.0, 1, "合体必殺攻撃なのだ～☆\n二人でカッコよく、\nキメちゃおうね、シオリン♪", 1, 0, 1, 118301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1807009, 180731, 0, 0.0, 1, "シオリンとの絆とキミの優しさ！\nこの二つがあれば、私は\nどんな相手にだって\n絶対に負けないのだ～☆", 1, 0, 1, 118301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1807010, 180731, 0, 0.0, 1, "お姉ちゃんがいてくれるなら\nきっと、どんなことがあっても\n怖くないよ…！", 1, 0, 1, 118401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1807011, 180731, 0, 0.0, 1, "感じるよ、お姉ちゃんとの\nあったかくて力強い、絆の力…！\n私たちの全力、\n見ていてください…！", 1, 0, 1, 118401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1807012, 180731, 0, 0.0, 1, "お姉ちゃんとの姉妹の絆と\nあなたからもらった力…！\n二つをこの矢に込めて…\n放ちます…！", 1, 0, 1, 118401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 7, 1807013, 180701, 2, 6.4, 2, "お誕生日、おめでとう☆\nプレゼントはシオリンと一緒に\n選んだんだよ！　よろこんで\nもらえたらうれしいな♪", 1, 2, 2, 118301, 2, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1807014, 180731, 0, 0.0, 1, "今日は何の日か知ってる～？\nなんとなんと…！　キミの\nお誕生日なんだよ！　おめでと☆\nめいっぱいお祝いしちゃうよ～♪", 1, 0, 2, 118301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (5, 8, 1807015, 180701, 5, 0.0, 1, "ハッピーバースデー…！\nお祝いの品を用意したのですが\n…受け取っていただけますか？\n私の手作りの詩集、です", 1, 2, 2, 118401, 2, 7.9, 7.9, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1807016, 180731, 0, 0.0, 1, "今日は特別な日ですね\nだってあなたのお誕生日ですから\nこうして一緒にお祝いできて、\n私もうれしいです", 1, 0, 2, 118401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1808001, 180801, 3, 0.0, 1, "さっきから私たちをじっと見て\nどうかしましたか？　なんだか\n目つきがあやしい…やっぱり\nへんたいふしんしゃさんです！", 1, 0, 1, 120602, 1, 0.0, 5.1, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1808002, 180801, 6, 0.0, 1, "冒険に出かける前の予習復習は\nとても大事です。どんな場所か\n知っておけば、対処は簡単なので\n…怖いわけじゃ、ありません！", 1, 0, 1, 120602, 2, 0.0, 10.1, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1808003, 180801, 2, 0.0, 1, "あなたは一人だとなにをするか\n分からなくて心配ですから、\n私たちから離れちゃダメですよ\nほら、手をつないでください", 1, 0, 1, 120602, 1, 0.0, 7.9, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1808004, 180801, 0, 5.4, 1, "えへへ、おにいちゃん♪\n今日も一緒に遊ぼう～\nミソギちゃんとキョウカちゃんも\nさそって、おままごとしたいな♪", 2, 0, 1, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1808005, 180801, 0, 0.0, 2, "えへへ、みんな一緒だね～♪\nなんだかうれしくって、\nこころがうさぎさんみたく\nぴょんってしちゃうね～♪", 2, 0, 1, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1808006, 180801, 0, 5.5, 2, "冒険のおべんとうに、いっぱい\nにんじんさんを入れてきたんだ♪\nおにいちゃんにもあげるね～\nにんじんさんパワーでがんばろ♪", 1, 0, 1, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (1, 7, 1808007, 180801, 0, 0.0, 1, "にひひ♪　新しいイタズラ\n思いついちゃった！\nミミもキョウカもにいちゃんも\nみんなまとめて驚かせちゃお♪", 1, 2, 1, 120501, 0, 3.7, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 8, 1808008, 180801, 0, 0.0, 1, "わあ～い！　にいちゃんまた\nイタズラにひっかかったね♪\nどうだった？　今どんな気分？\nやっぱり楽しかった？　だよね♪", 1, 2, 1, 120501, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (2, 9, 1808009, 180801, 0, 0.0, 1, "ミソギたちがみんな揃ったら\nいつでもどんな場所でも\nサイッコーに楽しい冒険が\nできちゃうと思うんだ♪", 1, 1, 1, 120501, 0, 3.7, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1808010, 180831, 0, 0.0, 1, "私たち【リトルリリカル】の\n３人が力を合わせれば…！\n相手がどんなに強くても\n絶対に負けたりしません！", 1, 0, 1, 120602, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1808011, 180831, 0, 0.0, 1, "いこうミミ、ミソギ！\n私たちの絆の力を見せつけよう！\n【リトルリリカル】に\nできないことはないんだから！", 1, 0, 1, 120602, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1808012, 180831, 0, 0.0, 1, "あなたの力で強くつながった\n私たちの絆の三角形…！\nその底力を、見ていてください！", 1, 0, 1, 120602, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1808013, 180831, 0, 0.0, 1, "一人じゃ、怖くて泣いて\n逃げちゃうような時でも\nさんにん一緒なら、ミミ、\nいっぱいがんばれるよ♪", 1, 0, 1, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1808014, 180831, 0, 0.0, 1, "ミソギちゃんとキョウカちゃんと\nそれにおにいちゃん♪\nみんなの優しい力を感じるよ\nだから、今はなにも怖くないんだ♪", 1, 0, 1, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1808015, 180831, 0, 0.0, 1, "おにいちゃん、ミミのがんばる姿\nちゃんと見ててね～♪\nみんながいれば\nミミだってやればできるんだよ♪", 1, 0, 1, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1808016, 180831, 0, 0.0, 1, "すごい、これが３人の絆の力…！\n今のミソギたちなら\nどんなことでもできちゃいそう！\nなんだかワクワクしてきた！", 1, 0, 1, 120501, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1808017, 180831, 0, 0.0, 1, "ミミもキョウカも、怖かったら\nミソギの後に続いていいからね！\n…まあ、そんな心配いらないか！\n今なら怖いものなしだもんね！", 1, 0, 1, 120501, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 9, 1808018, 180831, 0, 0.0, 1, "まばたき禁止だよ、にいちゃん！\n今からイタズラじゃなくて、\nミソギたち３人の絆の力で\nにいちゃんを驚かせちゃうから！", 1, 0, 1, 120501, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 10, 1808019, 180801, 2, 0.0, 1, "プレゼントが欲しそうな顔を\nしていますね？　まったく…\nちゃんと用意してあげましたよ！\nお誕生日、おめでとうございます", 1, 0, 2, 120602, 1, 0.0, 5.6, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 10, 1808020, 180831, 0, 0.0, 1, "あの…な、なにも言わずにこれを\n受け取ってください…！　私の\n手作りカップケーキです。その…\nハッピーバースデー、です…♪", 1, 0, 2, 120602, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 11, 1808021, 180801, 0, 6.5, 2, "今日はおにいちゃんのお誕生日\nだよね。ちゃんと覚えてるよ～♪\nだってミミ、おにいちゃんのこと\n大好きだから♪　えへへ～", 1, 0, 2, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 11, 1808022, 180831, 0, 0.0, 1, "ミミ、今日はいっぱい踊るね\nおにいちゃんのお誕生日を\nいっぱいおいわいしたいから♪\nるんたったるんたった～♪", 1, 0, 2, 120402, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (1, 12, 1808023, 180801, 0, 0.0, 1, "クラッカー、パンパンパーン！\nえへへ、びっくりした？\nにいちゃんの誕生日祝いの\nサプライズだよ♪", 1, 2, 2, 120501, 0, 4.8, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 12, 1808024, 180831, 0, 0.0, 1, "ハッピーバースデーにいちゃん！\nプレゼントに特大のイタズラを\n準備しといたよ！　ちゃんと\n引っかかってよね！　にひひ♪", 1, 0, 2, 120501, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1809001, 180901, 0, 0.0, 1, "意気込みは十分ですわよ！\nサレンさんに情けないところは\n見せられませんもの", 1, 0, 1, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1809002, 180901, 0, 2.3, 2, "共に競い高め合う…\nそれこそが高貴な\n友情というものですわ！", 1, 0, 1, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1809003, 180901, 0, 0.0, 1, "いくらサレンさんが相手でも\nあなたさまに関することでしたら\n一歩も引く気はありませんわ！", 1, 0, 1, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1809004, 180901, 1, 0.0, 1, "アキノさんがいてくれると\nとっても心強いわ\n二人ならどんな相手にも\n勝てちゃいそうよ", 1, 0, 1, 121801, 2, 0.0, 3.8, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1809005, 180901, 2, 0.0, 1, "何かにつけて張り合ってくるのも\n最初は戸惑ったけれど…\n今ではアキノさんなりの親愛の\n表し方なのかなって思うわ", 1, 0, 1, 121801, 1, 0.0, 4.7, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1809006, 180901, 5, 0.0, 1, "あたしだってアキノさんに\n張り合うことだってあるわよ？\nべ、別にあんたがどうとかは\n関係ないけど…！", 1, 0, 1, 121801, 6, 0.0, 4.5, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1809007, 180931, 0, 0.0, 1, "サレンさんに恥ずかしい\nところは見せられませんわ！\n私が持てる全力で\nお相手いたします！", 1, 0, 1, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1809008, 180931, 0, 0.0, 1, "サレンさん、私の見せ場も\n残しておいてくださいね？\nかっこいいところを\nお見せしたいですから", 1, 0, 1, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1809009, 180931, 0, 0.0, 1, "私たちが揃えばできないことなど\nありませんわ！\n邪魔をするというなら\n覚悟してくださいまし♪", 1, 0, 1, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1809010, 180931, 0, 0.0, 1, "アキノさんに\n好敵手って呼んでもらえる以上\n後れを取るわけにはいかないわ！\nあたしの全力、見せてあげる！", 1, 0, 1, 121801, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1809011, 180931, 0, 0.0, 1, "まったく、アキノさんの勢いに\nついていくのは大変だわ…\nまぁ、その分あたしも\n強くなれるんだけど", 1, 0, 1, 121801, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1809012, 180931, 0, 0.0, 1, "あたしたちが揃えば\n向かうところ敵なしね♪\nおとなしくしてないと\nけがするわよ？", 1, 0, 1, 121801, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1809013, 180901, 0, 6.8, 1, "お誕生日おめでとうございます！\n私たち二人でたっぷりと\nお祝いさせていただきますわ！\nささ、こちらへどうぞ♪", 2, 0, 2, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1809014, 180931, 0, 0.0, 1, "おめでとうございます！\nあなたさまのお誕生日を\nサレンさんと二人で祝えるなんて\n光栄の極みですわ♪", 1, 0, 2, 121701, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1809015, 180901, 2, 0.0, 1, "誕生日おめでとう♪\n今日はアキノさんと二人で\nお祝いしてあげるわ\nほら、いきましょう？", 1, 0, 2, 121801, 1, 0.0, 5.6, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1809016, 180931, 0, 0.0, 1, "誕生日おめでとう\nあんたのおかげで\nもっと仲良くなれたから\n二人でお祝いさせてもらうわね♪", 1, 0, 2, 121801, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1810001, 181001, 0, 8.9, 6, "え？　私とグレアは\nいつも一緒にいるって？\nもちろん別行動だってするよ！\n最近だって…ええと、ええと…？", 1, 0, 1, 124301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1810002, 181001, 0, 4.3, 2, "グレアの体って\n他の人より温かいんだよね！\nだから寒い時は本を読む前に\n手を握ってもらうんだ～♪", 1, 0, 1, 124301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1810003, 181001, 5, 7.3, 2, "グレアって褒められるとすぐ\n顔が真っ赤になっちゃうんだ\nもっと褒めると涙目になって…\nそんな顔もかわいいんだよね♪", 1, 0, 1, 124301, 5, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1810004, 181001, 2, 0.0, 1, "時々、アンと一緒に\n『もしも』の話をするんだ…\nあなたも同じ学院に通ってたら\nきっと楽しいだろうなって", 1, 0, 1, 124401, 1, 0.0, 5.9, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1810005, 181001, 6, 0.0, 1, "アンったら…さっきからずっと\n魔導書を読んでるの…\n…え！？　寂しいなんてこと…！\n…ちょっとだけ、あるかも", 1, 0, 1, 124401, 4, 0.0, 5.3, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1810006, 181001, 2, 0.0, 1, "今日はピアノを弾きに\n行こうと思ってるんだ\n…その、アンと一緒に\n連弾する約束をしてて…", 1, 0, 1, 124401, 1, 0.0, 4.5, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1810007, 181031, 0, 0.0, 1, "感じるよ…\nグレアの温かくて優しい魔力を\nお願い、私に力を貸して…\n─英霊の槍よ、敵を貫け！", 1, 0, 1, 124301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1810008, 181031, 0, 0.0, 1, "一人では乗り越えられないことも\nグレアと一緒なら大丈夫！\nそう信じられる絆が\n私たちにはあるから！", 1, 0, 1, 124301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1810009, 181031, 0, 0.0, 1, "グレア、私の陽だまり…\nいつも私のそばにいてくれて\nありがとう。グレアがいるから\n私の魔法は光り輝くんだ！", 1, 0, 1, 124301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1810010, 181031, 0, 0.0, 1, "感じるよ…\nアンの純粋で真っすぐな魔力を…\n最後まで、そばを離れないから…\n─竜の炎よ、敵を燃やしつくして！", 1, 0, 1, 124401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1810011, 181031, 0, 0.0, 1, "私を導いてくれる光…アン\nどんな敵でも、私は負けない…\nアン、あなたと一緒だから\nそんな気持ちになれるの！", 1, 0, 1, 124401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1810012, 181031, 0, 0.0, 1, "昔の私は自分の力が怖かった…\nでも今は、どんな私を見ても\nアンが笑ってくれるから…だから\nもっと強くなりたいって願えた！", 1, 0, 1, 124401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1810013, 181001, 0, 4.6, 2, "誕生日おめでとう！\n私たちも友達として\nお祝いさせて！\nプレゼントもグレアと選んだの♪", 1, 0, 2, 124301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1810014, 181031, 0, 0.0, 1, "今日のためにグレアと\nピアノの練習をしたんだ！\n曲はもちろん…\n『ハッピーバースデー』だよ♪", 1, 0, 2, 124301, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1810015, 181001, 2, 0.0, 1, "アンと一緒に\nプレゼントを用意したんだ…\n今日は誕生日、おめでとう", 1, 0, 2, 124401, 2, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1810016, 181031, 0, 0.0, 1, "今日はおめでとう\nあなたの誕生日をアンと\nお祝いできてうれしい…\n…その、友達って感じがして…", 1, 0, 2, 124401, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1811001, 181101, 0, 5.1, 5, "私とリノちゃんがお揃いなんだし\n弟くんも着替えるべきだよねっ！\n…恰好よくタキシードとかどう？\nお姉ちゃん、隣を歩きたいな…♪", 2, 0, 1, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1811002, 181101, 0, 3.1, 5, "姉妹の力でパワーアップだよっ♪\nリノちゃんとお姉ちゃんの\n愛の力で、大好きな弟くんを\n守り抜いてみせるからね…！", 2, 0, 1, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1811003, 181101, 0, 6.9, 2, "弟くんお兄ちゃんっ♪\n今ね、姉妹の絆を深めるために\nリノちゃんになりきってるの♪\n似てるでしょ？　似てるよねっ？", 5, 0, 1, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1811004, 181101, 4, 0.0, 1, "お、弟くんっ、お姉ちゃんだぞ♪\n…いま、もっと絆を深めるために\nお姉ちゃんになりきってるんです\nわっ、笑わないでくださいよ～！", 1, 0, 1, 128201, 6, 0.0, 6.3, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1811005, 181101, 6, 0.0, 1, "この姿は私とお姉ちゃんの\n繋がりの証、なんですよね\n心強くて…力が湧いてきます\nこれがお姉ちゃん力…え、違う？", 1, 0, 1, 128201, 5, 0.0, 12.6, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1811006, 181101, 5, 0.0, 1, "お兄ちゃんっ、ちょっと私の手を\n引いて、歩いてもらえませんか？\n…こんなにきれいな衣装ですから\n特別感を味わいたくて…えへへ♪", 1, 0, 1, 128201, 2, 0.0, 5.5, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1811007, 181131, 0, 0.0, 1, "姉と妹のダブルアタックだぞ♪\nえへへ、覚悟してね弟くん！\nパワーアップした私たちからは\nもう逃げられないんだからっ♪", 1, 0, 1, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1811008, 181131, 0, 0.0, 1, "弟くんと、リノちゃんと、私\nこの繋がりは特別なものなんだ\nだから必ず、守ってみせるからね\nあなたたちの、お姉ちゃんとして", 1, 0, 1, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1811009, 181131, 0, 0.0, 1, "ねぇ…あとでお姉ちゃんと\n一緒にお出かけしない？\nこの衣装でデートしよっ♪\n…リノちゃんには内緒だよ♪", 1, 0, 1, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1811010, 181131, 0, 0.0, 1, "お兄ちゃん、あとで私とお出かけ\n…デート、しませんか？\nこの衣装、見せびらかしたくて…\nお姉ちゃんには秘密ですよっ♪", 1, 0, 1, 128201, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1811011, 181131, 0, 0.0, 1, "こうやってお揃いの衣装を\n着るの、仲良し姉妹って\n感じがしていいですよね！\nお兄ちゃんも揃えちゃいますっ？", 1, 0, 1, 128201, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1811012, 181131, 0, 0.0, 1, "お兄ちゃん、さぁどうぞ\n私とお姉ちゃんの間に♪\nえへへ、どうですか？\nこれが姉妹ホールドですっ♪", 1, 0, 1, 128201, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1811013, 181101, 0, 8.1, 2, "弟くんの生まれた日、それは\nお姉ちゃんとリノちゃんにとって\n運命の日、なんだよ…！\n盛大にお祝いしちゃおうねっ♪", 5, 0, 2, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 7, 1811014, 181131, 0, 0.0, 1, "お誕生日おめでとう♪\nリノちゃんと一緒に選んだ\nプレゼントだよっ♪　…それと\nお姉ちゃん一人からのもあるよ♪", 1, 0, 2, 128101, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1811015, 181101, 2, 0.0, 1, "お兄ちゃんがまた一つ\nお兄ちゃんになっちゃいましたね\n妹として誇らしいです！\n恰好いいですよ、お兄ちゃん！", 1, 0, 2, 128201, 1, 0.0, 4.1, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 8, 1811016, 181131, 0, 0.0, 1, "お誕生日おめでとうございます！\n私とお姉ちゃんでプレゼントを\n用意したんです、けど…実は\n私一人からのもあったり…えへへ", 1, 0, 2, 128201, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1812001, 181201, 0, 0.0, 2, "迎えに来てくれてありがとう\nもう二度と会えないって\n私自身覚悟してたから…\nこんな奇跡があるなんてね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1812002, 181201, 0, 5.4, 2, "正直、戻ってきた実感は\nまだあまりないのだけど…\nあなたの顔を見ると、\nやっぱりほっとするわね", 6, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1812003, 181201, 0, 6.3, 2, "今一番みんなとしたいのは\nやっぱり食べ歩きの旅かしら\n…そのためにも、頑張って\n世界を救わなきゃね", 10, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1812004, 181201, 0, 0.0, 2, "みんなが捜してくれたって聞いて\nすごくうれしかったの\n今度は私がこの力で\nみんなを守る番ね", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1812005, 181201, 0, 7.6, 1, "あなたといる時間は\n決して当たり前じゃないって\n改めて気づけたわ\nだからこそ、大事にしたいの", 2, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 1, 1812006, 181231, 0, 0.0, 1, "待っててくれて、信じてくれて\n本当にありがとう\nだから何度だって言うわ\n─ただいま！", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 2, 1812007, 181231, 0, 0.0, 1, "今の私には、こんなに綺麗で\n大きな翼があるんだもの\n今度は私が抱き留めてあげる", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 3, 1812008, 181231, 0, 0.0, 1, "…私、涙もろくなったみたい\nあなたの顔を見られただけで\n胸がいっぱいになってしまうの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 4, 1812009, 181231, 0, 0.0, 1, "もう一度この手を掴んでくれる？\nそうしたら、二度と離さないわ\nあなたは私の…大切な人だもの", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 5, 1812010, 181231, 0, 0.0, 1, "これがプリンセスフォーム…\nあなたとの絆の証なのね\n今まで以上に一緒に戦ってる\n気がして、勇気がもらえるわ", 1, 0, 1, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1812011, 181201, 0, 5.4, 2, "お誕生日おめでとう！\nまたお祝いできてうれしいわ\nとびきりのパーティーを開くから\n期待していてね", 10, 0, 2, 0, 0, 0.0, 0.0, 0);
INSERT INTO `v1_81aeb52b7250fd350340fa84ef662d745cc9952267b81e4d19ac41b73ca40206` VALUES (0, 6, 1812012, 181231, 0, 0.0, 1, "あなたたちがしてくれたように、\n私もケーキを作ってみたいの\n愛情いっぱいの特製ケーキ…\n完成したら食べてくれる？", 1, 0, 2, 0, 0, 0.0, 0.0, 0);
