INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (206200, 0, 1, 270, 0, 650, 3.5, "ギャングオッター", "【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。", 206200, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (206201, 0, 1, 270, 0, 650, 0.5, "ノーティビーバー", "【物理】中距離攻撃\n片っ端から木を削り倒すビーバー。\n削った木片は家作りだけでなく、\n家を守るための飛び道具となる。", 206201, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (206300, 0, 0, 270, 0, 1110, 10.0, "ルナポッド", "【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。", 206300, 0, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (206301, 0, 0, 270, 0, 1800, 10.0, "ルナポッド", "【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。", 206300, 0, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (206400, 0, 0, 270, 0, 100, 2.5, "リトルアイ", "【物理】近接攻撃\n一ツ目族の王に仕える、小さな召使い。\n王様がくれた金ピカ装備が、大のお気に入り。", 206400, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (206500, 0, 0, 300, 0, 100, 2.5, "コマンド・ウキキー", "【物理】近接攻撃\nウキキーがより成長した姿。幼少より大岩を投げ\n続けたその太腕は、絶大な打撃を繰り出す。", 206500, 1, 0, 0);
INSERT INTO `v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf` VALUES (206700, 0, 1, 270, 0, 200, 2.295, "ヒヨリのシャドウ", "ヒヨリの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理大ダメージを与え、\n　その周りの狭い範囲の敵すべてにも\n　物理中ダメージを与える。\n・目の前の敵１キャラに物理中ダメージを\n　与える。\n・目の前の敵１キャラに物理小ダメージを与え、\n　さらに自分の物理攻撃力を中アップさせる。", 206700, 1, 0, 0);
